INSERT INTO [t6037] VALUES (19,'الاستقساء','60','0','0');
INSERT INTO [t6037] VALUES (20,'إلى السماء الدنيا كذا الرواية وهو الوجه والقياس ورواه بعضهم إلى سماء الدنيا فيكون هذا من باب صلاة الأولى ومسجد الجامع وقوله من يدعوني من رواه هكذا جعل من استفهاما ونصب ما بعد الفاء كما قال الله عز وجل','65','0','0');
INSERT INTO [t6037] VALUES (21,'في جرير يطلع عليكم من هذا الفج خير ذي يمن عليه مسحه ملك من جمال وكان جرير من أجمل الناس وجها','66','0','0');
