INSERT INTO [t6037] VALUES (26,'عام الفتح والرهان والمراهنة المسابقة سمي رهانا لما يوضع فيها من الرهون يقال أرهنت في المخاطرة فإذا أردت غير المخاطرة قلت','96','0','0');
INSERT INTO [t6037] VALUES (27,'كتاب الحج','98','0','0');
INSERT INTO [t6037] VALUES (28,'لحرمه هذا هو المعروف والحرم هو الإحرام وقال قاسم في الدلائل لحرمه بكسر الحاء وأنكر الضم','100','0','0');
