INSERT INTO [t6037] VALUES (32,'للمغيرة رضي الله عنه وقد قال إني خطبت لو نظرت إليها فإنه أحرى أن يؤدم بينكما وقوله من أدم البيت الوجه فيه أن يقال الأدم الأول هو الذي يراد به الواحد والثاني يراد به الجمع وقد يجوز أن يكونا جميعا الذي يراد به','124','0','0');
INSERT INTO [t6037] VALUES (33,'كتاب الأقضية','130','0','0');
INSERT INTO [t6037] VALUES (34,'ضالة المؤمن حرق النار الحرق يتصرف في اللغة على أربعة معان فتارة يجعلونه النار بعينها وتارة يجعلونه إحراق النار ومنه الحديث الحرق والغرق شهادة وتارة يجعلونه الأثر الذي يكون في الثوب من دق القصار والكاد فإن كان من النار','135','0','0');
INSERT INTO [t6037] VALUES (35,'كتاب الجامع','137','0','0');
INSERT INTO [t6037] VALUES (36,'مسيحا لحسن وجهه والمسيح في اللغة الجميل الوجه والمسيح قطع الفضة وقيل سمي مسيحا لأنه مسح عند ولادته بالدهن وقيل المسيح الصديق وقيل هو معرب من مشلخة بالعبرانية وقيل سمي الدجال لعنه الله مسيحا لأن المسيح الذي لا عين له ولا','139','0','0');
INSERT INTO [t6037] VALUES (37,'الأيمن فالأيمن منصوب بفعل مضمر كأنه قال أعطوا الأيمن فالأيمن يقال أدمته بالقصر وآدمته بالمد لغتان ويقال لما يؤتدم به إدام وآدم وقد يكون أصله أدم بضم الدال ثم تسكن تخفيفا كما يقال عنق وعنق ويدل على أن الأدم','140','0','0');
INSERT INTO [t6037] VALUES (38,'دون الجمة وفوق الوفرة العرب تسمي الشعر الذي على الرأس رأسا لكونه على الرأس وثائر الرأس قائم الشعر','144','0','0');
INSERT INTO [t6037] VALUES (39,'أعطوا الركب أسنتها الركب جمع راكب وهي الإبل التي تركب وأصلها ركب ثم إن شئت سكنت تخفيفا كحمر وحمر وعنق وعنق وكذلك كل ضمتين تتالتا في كلمة فإن حذف الضمة الثانية جائز وواحد الركاب حمولة من غير لفظها وقيل ركوبة والأسنة جمع','147','0','0');
