INSERT INTO [t6227] VALUES (1,'الجزء ١','1','0','0');
INSERT INTO [t6227] VALUES (2,'مسألة','2','1','0');
INSERT INTO [t6227] VALUES (3,'عن الثوب يصيبه الدم من الحيضة فقال لتحته ثم لتقرضه بالماء ثم لتنضحه','2','1','0');
INSERT INTO [t6227] VALUES (4,'وثيابك فطهر فأمره بتطهير ثيابه فكأن عبد الله استحسن ذلك من قوله قال إسحق فرفعت رأسي فقلت أعز الله الأمير كذب هذا على الله وعلى رسوله أخبرنا وكيع حدثنا سفيان عن','4','1','0');
INSERT INTO [t6227] VALUES (5,'فقالت إني امرأة أطيل ذيلي وأمشي في المكان القذر فقالت أم سلمة قال','5','1','0');
INSERT INTO [t6227] VALUES (6,'يطهره ما بعده أم ولد إبراهيم لم يخرج حديثها في الصحيح وما رويناه أصح ثم هو محمول على النجاسة اليابسة التي تسقط عن الثوب بالسحب على الأرض وروي عن امرأة من بني عبد الأشهل قالت قلت يا رسول الله إن لنا طريقا إلى المسجد منتنة','6','1','0');
INSERT INTO [t6227] VALUES (7,'قال إذا وطئ أحدكم بنعليه في الأذى فإن التراب له طهور وخالفه أبو الأحوص محمد بن الهيثم القاضي عن ابن كثير عن الأوزاعي عن ابن','7','1','0');
INSERT INTO [t6227] VALUES (8,'إذا وطئ أحدكم بخفيه أو قال بنعليه الأذى فطهورهما التراب وخالفه أصحاب الأوزاعي في إقامة إسناده فروى العباس بن الوليد بن مزيد عن أبيه عن الأوزاعي قال أنبئت أن سعيد ابن أبي سعيد حدث عن أبيه عن أبي هريرة أن رسول الله','8','1','0');
INSERT INTO [t6227] VALUES (9,'إذا وطئ أحدكم بنعليه الأذى فإن التراب لهما طهور هكذا روي عن الأوزاعي عن محمد بن الوليد عن سعيد وهذا أيضا لا يعارض ما روينا فإن الطريق فيه ليس بواضح إلى سعيد وهو مرسل القعقاع لم يسمع من عائشة وما روينا إسناده متفق عليه وروي هذا الحديث','9','1','0');
INSERT INTO [t6227] VALUES (10,'قال له يا عمار ما نخامتك ولا دموع عينيك إلا بمنزلة الماء الذي في','11','1','0');
INSERT INTO [t6227] VALUES (11,'مسألة','12','1','0');
INSERT INTO [t6227] VALUES (12,'كل مسكر خمر وكل مسكر حرام أخرجه مسلم في الصحيح فثبت بهذا وقع اسم الخمر على النبيذ لكونه مسكرا وقد قال الله عز اسمه يأيها الذين آمنوا إنما الخمر والميسر والأنصاب والأزلام رجس من عمل الشيطان فاجتنبوه لعلكم تفلحون فأمر باجتنابه','14','1','0');
INSERT INTO [t6227] VALUES (13,'معك ماء قلت ليس معي ماء ولكن معي إداوة فيها نبيذ فقال النبي','15','1','0');
INSERT INTO [t6227] VALUES (14,'وهو خلاف القرآن قال الحاكم أبو عبد الله قد قيل أنه كان نباذا بالكوفة يعني أبا زيد قال البخاري أبو زيد الذي يروي الحديث عن ابن مسعود رجل مجهول لا يعرف','17','1','0');
INSERT INTO [t6227] VALUES (15,'قال ليلة الجن أمعك ماء قال لا قال أمعك نبيذ قال نعم فتوضأ به قال الحاكم أبو عبد الله هذا حديث تفرد به أبو سعيد مولى بني هاشم عن حماد بن سلمة وعلي بن زيد بن جدعان على الطريق وهو ممن أجمع الحفاظ على تركه وقال الدارقطني','19','1','0');
INSERT INTO [t6227] VALUES (16,'ذات ليلة فقال خذ معك إداوة فيها ماء فذكر حديثا طويلا في ليلة الجن إلى أن قال فلما أفرغت عليه من الأداوة إذا هو نبيذ فقلت يا رسول الله أخطأت بالنبيذ فقال تمرة حلوة وماء عذب قال الحاكم أبو عبد الله هذا حديث لم نكتبه من حديث','20','1','0');
INSERT INTO [t6227] VALUES (17,'تمرة طيبة وماء طهور','21','1','0');
INSERT INTO [t6227] VALUES (18,'ليلة الجن بنبيذ فتوضأ به وقال شراب طهور قال','22','1','0');
INSERT INTO [t6227] VALUES (19,'بنبيذ قال الدارقطني الثقفي الذي رواه عن ابن مسعود مجهول قيل اسمه عمرو وقيل عبد الله بن عمرو بن غيلان ومما يدل على بطلان جميع ما روي من ذلك عن عبد الله بن مسعود رضي الله عنه إقراره بأنه لم يكن ليلة الجن مع رسول الله','23','1','0');
INSERT INTO [t6227] VALUES (20,'وودت أني كنت معه وروي عن شعبة عن عمرو بن مرة قال سألت أبا عبيدة بن عبد الله أكان أبوك مع النبي','24','1','0');
INSERT INTO [t6227] VALUES (21,'ليلة الجن فمن قال إنه صحبه فيها فإنه يريد حين ذهب رسول الله','24','1','0');
INSERT INTO [t6227] VALUES (22,'ليلة الجن فقال ما صحبه منا أحد ولكنا فقدناه بمكة فطلبناه في الشعاب والأودية فقلت اغتيل استطير فبتنا بشر ليلة بات قوم فلما أصبحنا رأيناه مقبلا فقلنا يا رسول الله بتنا الليلة بشر ليلة بات بها قوم فقدناك فقال إنه أتاني داعي الجن','24','1','0');
INSERT INTO [t6227] VALUES (23,'تلك الليلة على اضطراب في الإسناد فإن في حديث سليمان التيمي عن أبي تميمة عن عبد الله وقيل عن أبي تميمة عن أبي عثمان النهدي عن عبد الله وعلى هذا الاضطراب لا تقوم بهم الحجة قال البيهقي رضي الله عنه قد تتبعت هذه الروايات فوجدتها على ما ذكر','25','1','0');
INSERT INTO [t6227] VALUES (24,'النبيذ وضوء من لم يجد الماء قال علي بن عمر كذا قال ووهم فيه المسيب بن واضح في موضعين في ذكر ابن عباس وفي ذكر النبي','26','1','0');
INSERT INTO [t6227] VALUES (25,'والمحفوظ من قول عكرمة غير مرفوع إلى النبي','27','1','0');
INSERT INTO [t6227] VALUES (26,'مسألة','29','1','0');
INSERT INTO [t6227] VALUES (27,'يقول إذا دبغ الإهاب فقد طهر خرج هذا مخرج الشرط والجزاء فقوله إذا دبغ شرط وقوله فقد طهر جزاء والجزاء لا يسبق الشرط كما يقال إذا دخلت الدار فأنت حر فما لم يدخل لا يعتق وروي عن أبي المليح الهذلي عن أبيه أن رسول الله','30','1','0');
INSERT INTO [t6227] VALUES (28,'عن جلود السباع أن تفرش قال الحاكم أبو عبد الله هذا حديث صحيح الإسناد فإن أبا المليح عامر بن أسامة بن عمير صحابي من بني لحيان مخرج حديثه في المسانيد وله','30','1','0');
INSERT INTO [t6227] VALUES (29,'نهى عن لبس جلود السباع وركوبها قال نعم وقد ورد النهي في جلد النمر خاصة عنده أيضا عن هناد عن وكيع عن أبي المعتمر عن ابن سيرين عن معاوية قال قال رسول الله','31','1','0');
INSERT INTO [t6227] VALUES (30,'وعنده أيضا عن أبي هريرة عن النبي','32','1','0');
INSERT INTO [t6227] VALUES (31,'قال دباغ الأديم','32','1','0');
INSERT INTO [t6227] VALUES (32,'دعا في غزوة تبوك بماء من عند امرأة فقالت ما عندي إلا في قربة ميتة فقال أليس دبغتها قالت بلى قال فإن ذكاتها دباغها','33','1','0');
INSERT INTO [t6227] VALUES (33,'جاء في غزوة تبوك أي على بيت فإذا فيه قربة معلقة فسأل الماء فقالوا يا رسول الله إنها ميتة قال دباغها طهورها في حديث ابن عباس وعائشة مفسرا فروى عنه قال ماتت شاة لميمونة فقال النبي','34','1','0');
INSERT INTO [t6227] VALUES (34,'يقول دباغ الأديم طهوره وروي عن سعيد بن أبي','34','1','0');
INSERT INTO [t6227] VALUES (35,'مسألة','35','1','0');
INSERT INTO [t6227] VALUES (36,'قال شر','35','1','0');
INSERT INTO [t6227] VALUES (37,'شر كسب وسماه خبثا في أخبار أخر سنرويها إن شاء الله تعالى في كتاب البيوع وروي عن ابن عباس رضي الله عنهما أن رسول الله','36','1','0');
INSERT INTO [t6227] VALUES (38,'بأرض جهينة وأنا غلام شاب أن لا تستمتعوا من الميتة بإهاب ولا عصب وقد روى فيه قبل موته','37','1','0');
INSERT INTO [t6227] VALUES (39,'عن جلود السباع والكلب من السباع فقد روينا في كتاب دلائل النبوة أن النبي','38','1','0');
INSERT INTO [t6227] VALUES (40,'وهذا محمول على غير جلد الكلب بدليل حديث رافع وغيره فإنه خاص وهذا عام والخاص يحكم على','38','1','0');
INSERT INTO [t6227] VALUES (41,'مسألة','39','1','0');
INSERT INTO [t6227] VALUES (42,'نهى عن ركوب النمار وفي صحيح مسلم عن ابن عباس أن النبي','39','1','0');
INSERT INTO [t6227] VALUES (43,'بمضيعة ذكر منها ما ينتفع به وهو الإهاب فلو كان الشعر والصوف والقرن بمثابة الإهاب لذكره إن شاء الله تعالى والمراد بالإهاب الجلد وحده يبينه ما اتفق البخاري ومسلم على صحته عن ابن عباس أن رسول الله','39','1','0');
INSERT INTO [t6227] VALUES (44,'هلا انتفعتم بجلدها فقالوا إنها ميتة فقال إنما حرم أكلها وقوله إنما حرم أكلها أي ما يكون مأكولا فأما ما تنازعنا فيه فغير مأكول وروي بإسناد ضعيف مرفوعا عن ابن عمر ادفنوا الأظفار والدم والشعر فإنه ميتة وروي في دفن الشعر والظفر','40','1','0');
INSERT INTO [t6227] VALUES (45,'من الميتة لحمها فأما الجلد والشعر والصوف فلا بأس به قال الدارقطني','41','1','0');
INSERT INTO [t6227] VALUES (46,'في متنه ورواه أيضا بالإسناد وتفسير الآية قل لا أجد في ما أوحي إلي محرما على طاعم يطعمه الآية قال الطاعم الأكل فأما السن والقرن والعظم والصوف والشعر والوبر والعصب فلا بأس به لأنه يغسل قال الدارقطني أبو بكر الهذلي متروك ورواه','42','1','0');
INSERT INTO [t6227] VALUES (47,'بمشط من عاج فرواه عمرو بن خالد الواسطي عن قتادة عن أنس وعمرو ضعيف وأما شعور الآدميين فإنها طاهرة في ظاهر مذهب الشافعي رضي الله عنه لكرامته ولوقع البلوى به وقد صح عن النبي','43','1','0');
INSERT INTO [t6227] VALUES (48,'مسألة','44','1','0');
INSERT INTO [t6227] VALUES (49,'قال إن الذي يشرب في آنية الفضة إنما يجرجر','44','1','0');
INSERT INTO [t6227] VALUES (50,'من شرب في إناء ذهب أو فضة أو إناء فيه شيء من ذلك فإنما يجرجر في بطنه نار جهنم أخرج الإسناد أبو الوليد والشيخ أبو الحسن الدارقطني في كتابيهما وروى عن ابن سيرين عن عمرة أنها قالت كنا مع عائشة فما زلنا بها حتى رخصت لنا في الحلى ولم','45','1','0');
INSERT INTO [t6227] VALUES (51,'نهى عن الشرب في آنية الذهب والفضة لم يشرب في القدح المفضض والله أعلم','46','1','0');
INSERT INTO [t6227] VALUES (52,'ولا يجوز الوضوء بغير النية وقال أبو حنيفة يجوز ودليلنا من طريق الخبر حديث عمر المتفق على صحته قال سمعت رسول الله','46','1','0');
INSERT INTO [t6227] VALUES (53,'الطهور شطر الإيمان والحمد لله تملأ الميزان وسبحان الله والحمد لله تملآن أو تملأ ما بين السموات والأرض والصلاة نور والصدقة برهان والصبر ضياء والقرآن حجة لك أو عليك كل الناس يغدو فبائع نفسه فمعتقها أو موبقها وما أمروا إلا','47','1','0');
INSERT INTO [t6227] VALUES (54,'لا وضوء لمن لم يذكر اسم الله عليه أنه الذي يتوضأ ويغتسل ولا ينوي وضوءا للصلاة ولا غسلا للجنابة وروي عن عبد الله بن المثنى الأنصاري حدثني بعض أهل بيتي عن أنس بن مالك أن رجلا من الأنصار من بني عمرو بن عوف قال يا رسول الله','48','1','0');
INSERT INTO [t6227] VALUES (55,'مسألة','49','1','0');
INSERT INTO [t6227] VALUES (56,'فعل هذا وإسناده قد احتجا بجميع رواته غير عامر بن شقيق قال الحاكم أبو عبد الله لا أعلم في عامر طعنا','49','1','0');
INSERT INTO [t6227] VALUES (57,'يتوضأ وعن أبي داود عن الربيع بنت معوذ صفة ثم غسل رجليه ثلاثا ثم قال رأيت رسول الله','50','1','0');
INSERT INTO [t6227] VALUES (58,'فعل هكذا رواه الحسن بن زياد اللؤلؤي عن أبي حنيفة ومسح برأسه ثلاثا وقد رواه أبو عوانة وزائد بن قدامة عن خالد ولم يذكر العدد كما ذكره أبو حنيفة ثم خالفه وروي عن ابن جريج عن محمد بن','51','1','0');
INSERT INTO [t6227] VALUES (59,'يتوضأ وعن أبي داود عن الربيع بنت معوذ صفة وضوء توضأه رسول الله','52','1','0');
INSERT INTO [t6227] VALUES (60,'مسألة','53','1','0');
INSERT INTO [t6227] VALUES (61,'يتوضأ فأخذ ماء لأذنيه خلاف الماء الذي مسح به رأسه وروى مالك عن نافع أن عبد الله بن','53','1','0');
INSERT INTO [t6227] VALUES (62,'توضأ فمسح باطن أذنيه وظاهرهما وقال وكان ابن مسعود يأمر بذلك وربما استدلوا بما روي عن النبي','54','1','0');
INSERT INTO [t6227] VALUES (63,'الأذنان من الرأس قال الحاكم أبو عبد الله هذا حديث يعرف بالمعمري وهو آخر ما ذكره موسى بن هارون في الإنكار عليه وقد سرقه منه الباغندي وغيره رواه أبو أحمد بن عربي عن محمد بن محمد الباغندي عن أبي كامل فذكره بنحوه وزاد قال أبو كامل لم أكتب','63','1','0');
INSERT INTO [t6227] VALUES (64,'مرسلا حدثنا به إبراهيم بن حماد حدثنا العباس بن يزيد حدثنا وكيع حدثنا ابن جريج حدثني سليمان بن موسى أن رسول الله','64','1','0');
INSERT INTO [t6227] VALUES (65,'رواه عاصم بن علي عن ابن جريج قال الدارقطني وهم علي بن عاصم في قوله عن أبي هريرة والذي قبله أصح وقد رواه الربيع بن بدر عن ابن جريج مثل حديث غندر مرفوعا مضمضوا واستنشقوا والأذنان من الرأس سئل ابن معين عن الربيع بن بدر فقال كان ضعيفا','65','1','0');
INSERT INTO [t6227] VALUES (66,'كما تقدم ذكري له والله أعلم وأما حديث أبي موسى فروي عن علي بن جعفر عن علي بن زياد الأحمر حدثنا عبد الرحيم','69','1','0');
INSERT INTO [t6227] VALUES (67,'تمضمضوا واستنشقوا والأذنان من الرأس قال الحاكم أبو عبد الله تفرد به محمد بن علاثة عن عبد الكريم وابن علاثة هو أبو اليسير القاضي محمد بن عبد الله بن علاثة الشامي ذاهب الحديث بمرة وله مناكير عن الأوزاعي وغيره من أئمة المسلمين فمنها','72','1','0');
INSERT INTO [t6227] VALUES (68,'قال الدارقطني','73','1','0');
INSERT INTO [t6227] VALUES (69,'توضأ ثلاثا ثلاثا ومسح برأسه وقال الأذنان من الرأس وكان','74','1','0');
INSERT INTO [t6227] VALUES (70,'قال وكان رسول الله','76','1','0');
INSERT INTO [t6227] VALUES (71,'أو أبي أمامة يعني قصة الأذنين قال قتيبة عن سنان بن ربيعة وروى الدارقطني عن ابن حشيش عن يوسف بن موسى عن سليمان بن حرب عن حماد عن سنان عن شهر عن أبي أمامة أنه وصف وضوء رسول الله','76','1','0');
INSERT INTO [t6227] VALUES (72,'توضأ بثلثي مد وجعل يدلك والأذنان من الرأس','78','1','0');
INSERT INTO [t6227] VALUES (73,'والحجاج لا يحتج بحديثه إن كان محفوظا عنه والطريق إليه سليم ولا','79','1','0');
INSERT INTO [t6227] VALUES (74,'وهذا هو الصواب وبغير ذلك لا تثبت الحجة عندنا وروي عن اليمان أبي حذيفة عن عمرة قالت سألت عائشة رضي الله عنه عن الأذنين قالت من الرأس وقالت كان رسول الله','82','1','0');
INSERT INTO [t6227] VALUES (75,'يتوضأ فذكر الحديث ثلاثا ثلاثا وقال ومسح برأسه وأذنيه مسحة واحدة وهذا لو صح فلا حجة لهم فيه إذ يجوز أنه عزل سبابتيه فمسح بها أذنيه كما روي في بعض الأخبار والحكاية حكاية حال وقد روي عن طلحة بن مصرف عن أبيه عن جده قال كان رسول','83','1','0');
INSERT INTO [t6227] VALUES (76,'توضأ فأنكر ذلك سفيان وعجب أن يكون جد طلحة لقي النبي','83','1','0');
INSERT INTO [t6227] VALUES (77,'مسألة','85','1','0');
INSERT INTO [t6227] VALUES (78,'فقال ارجع أحسن وضوءك فرجع ثم صلى أخرجه مسلم في صحيحه وروي هذا المتن بعينه من حديث أنس بن مالك بإسناد صحيح أن رجلا جاء إلى النبي','86','1','0');
INSERT INTO [t6227] VALUES (79,'ارجع فأحسن وضوءك رواته كلهم ثقات مجمع على عدالتهم وشاهده ما روى أبو داود بإسناده عن خالد عن بعض أصحاب النبي','86','1','0');
INSERT INTO [t6227] VALUES (80,'رأى رجلا يصلي في ظهر قدمه لمعة قدر الدرهم لم يصبها الماء فأمره النبي','86','1','0');
INSERT INTO [t6227] VALUES (81,'أن يعيد الوضوء والصلاة وهذا منقطع وروي ذلك عن عمر بن الخطاب رضي الله عنه موقوفا عن سفيان عن الأعمش عن أبي سفيان وعن جابر قال رأى عمر رجلا يتوضأ فبقي في رجله لمعة فقال أعد الوضوء وعن سفيان عن خالد الحذاء','87','1','0');
INSERT INTO [t6227] VALUES (82,'فقلت يا رسول الله إن أهلي تغار إذا وطئت جواري قال ولم تعلمهن بذلك قلت من قبل الغسل قال فإذا كان ذلك منك فاغسل رأسك عند أهلك فإذا حضرت الصلاة فاغسل سائر بدنك وفي هذا إن صح جواز تفريق الغسل إلا أنه غير معروف وفي إسناده ضعف وروي','89','1','0');
INSERT INTO [t6227] VALUES (83,'مسألة','90','1','0');
INSERT INTO [t6227] VALUES (84,'فلما دنا من الصفا قرأ إن الصفا والمروة من شعائر الله نبدأ بما بدأ الله به فبدأ بالصفا وفي حديث مسلم عن عدي بن حاتم قال خطب رجل عند رسول الله','90','1','0');
INSERT INTO [t6227] VALUES (85,'بئس الخطيب أنت قل ومن يعص الله ورسوله فقد غوى فإن عارضوا بما قد روي عن عبد الله بن يسار عن حذيفة قال قال رسول الله','90','1','0');
INSERT INTO [t6227] VALUES (86,'طاعة الله بفرض الله طاعته فلو اقتصر على رسول الله','91','1','0');
INSERT INTO [t6227] VALUES (87,'يتوضأ نحو وضوئي هذا ثم قال رسول الله','92','1','0');
INSERT INTO [t6227] VALUES (88,'فقال يا رسول الله كيف الطهور فدعا بماء في إناء فغسل كفيه ثلاثا ثم غسل وجهه ثلاثا ثم غسل ذراعيه ثلاثا ثم مسح برأسه فأدخل أصبعيه السباحتين في أذنيه ومسح بإبهاميه على ظاهر أذنيه وبالسباحتين باطن أذنيه ثم غسل رجليه ثلاثا ثم قال هذا الوضوء','93','1','0');
INSERT INTO [t6227] VALUES (89,'توضأ مرة مرة ثم قال هذا وضوء الصلاة الذي لا يقبل الله الصلاة إلا به ثم توضأ مرتين مرتين ثم قال هذا وضوء الأنبياء قبلي وضوء إبراهيم خليل الرحمن','94','1','0');
INSERT INTO [t6227] VALUES (90,'مسألة','98','1','0');
INSERT INTO [t6227] VALUES (91,'عندنا الذي كتبه لعمرو بن حزم قال هذا كتاب رسول الله حين بعثه إلى اليمن يفقه أهلها ويعلمهم السنة فذكر الحديث وقال فلا يمس أحد القرآن إلا وهو طاهر وروى الدارقطني في','98','1','0');
INSERT INTO [t6227] VALUES (92,'لا يمس القرآن إلا طاهرا وروي عن حكيم بن حزام أن رسول الله','99','1','0');
INSERT INTO [t6227] VALUES (93,'قال لا تمس القرآن إلا وأنت على طهر وروي عن علقمة قال كنا مع سلمان الفارسي في سفر فقضى حاجته فقلنا له توضأ حتى نسألك عن آية من القرآن فقال سلوني إني لست أمسه فقرأ علينا ما أردنا ولم يكن بيننا وبينه ماء قال الحاكم أبو عبد الله هذا','100','1','0');
INSERT INTO [t6227] VALUES (94,'مسألة','101','1','0');
INSERT INTO [t6227] VALUES (95,'يقضي الحاجة ويقرأ القرآن ويأكل اللحم ولم يكن يحجبه أو قال يحجزه عن قراءته شيء ليس الجنابة قال الحاكم أبو عبد الله هذا حديث صحيح الإسناد والشيخان لم يحتجا بعبد الله بن سلمة ومدار الحديث عليه وعبد الله بن سلمة غير مطعون فيه رواه','102','1','0');
INSERT INTO [t6227] VALUES (96,'أن يقرأ أحدنا القرآن وهو جنب وروي عن عمرو بن زريق عن زمعة عن سلمة بن وهرام عن عكرمة عن ابن عباس قال دخل عبد الله بن رواحة فذكر قصة وقال إن رسول الله','103','1','0');
INSERT INTO [t6227] VALUES (97,'مسألة','105','1','0');
INSERT INTO [t6227] VALUES (98,'قال لا تستقبلوا القبلة بغائط ولا بول ولا تستدبروها وفي رواية بنحو معناه وزاد ولكن شرقوا وغربوا قال فقدمنا الشام فوجدنا مراحيض قد بنيت قبل القبلة فكنا ننحرف عنها ونستغفر الله اتفق البخاري ومسلم على صحته وفي صحيح مسلم عن عبد','106','1','0');
INSERT INTO [t6227] VALUES (99,'قاعدا على لبنتين لحاجته مستقبل الشام مستدبر القبلة ورواه مسلم أيضا في حديث واسع عن ابن عمر وفيه فقال عبد الله ولقد رقيت على ظهر بيت فرأيت رسول الله','107','1','0');
INSERT INTO [t6227] VALUES (100,'أن نستقبل القبلة ببول فرأيته قبل أن يقبض بعام يستقبلها قال الترمذي سألت محمدا يعني البخاري عن هذا الحديث فقال هذا حديث صحيح رواه غير واحد عن محمد بن إسحق وروي عن خالد بن أبي الصلت قال كنت عند عمر بن عبد العزيز في خلافته وعنده عراك بن','108','1','0');
INSERT INTO [t6227] VALUES (101,'لما بلغه قول الناس في ذلك أمر بمقعدته فاستقبل بها القبلة والله أعلم','109','1','0');
INSERT INTO [t6227] VALUES (102,'والاستنجاء واجب لا يجوز تركه ولا يقع الوضوء عنه وإن كانت النجاسة يسيرة وقلنا أنه يعفى عن يسير النجاسة على أحد القولين وقال أبو حنيفة الاستنجاء سنة يجوز تركها إذا لم يرد على قدر الدرهم ودليلنا عليه من طريق الحديث الذي روي عن','109','1','0');
INSERT INTO [t6227] VALUES (103,'كل شيء حتى الخراءة فقال أجل قد نهانا أن نستقبل القبلة بغائط أو بول ونهانا ونهانا أن يستنجي أحدنا بأقل من ثلاثة أحجار ونهانا أن نستنجي برجيع أو عظم أخرجه مسلم في الصحيح وروى أبو داود عن أبي هريرة قال قال رسول الله','110','1','0');
INSERT INTO [t6227] VALUES (104,'قال إذا ذهب أحدكم إلى الغائط فليذهب معه بثلاثة أحجار يستطيب بهن فإنها تجزئ عنه وربما استدل أصحابهم بالحديث الذي رواه أبو داود عن إبراهيم بن موسى الرازي أخبرنا','110','1','0');
INSERT INTO [t6227] VALUES (105,'قال من اكتحل فليوتر من فعل فقد أحسن ومن لا فلا حرج ومن استجمر فليوتر ومن فعل فقد أحسن ومن لا فلا حرج ومن أكل فما تخلل فليلفظ ومن لاك لسانه فليبتلع من فعل فقد أحسن ومن لا فلا حرج ومن أتى الغائط فليستتر فإن لم يجد وإن يجمع','111','1','0');
INSERT INTO [t6227] VALUES (106,'أنه قال من توضأ فليستنثر ومن استجمر فليوتر وكذلك رواه','112','1','0');
INSERT INTO [t6227] VALUES (107,'الغائط فأمرني أن آتيه بثلاثة أحجار قال فوجدت حجرين والتمست الثالث فلم أجده فأخذت روثة فأتيت بها النبي','113','1','0');
INSERT INTO [t6227] VALUES (108,'في الاستنجاء بالأحجار الثلاثة قال ابن الشاذكوني ما سمعت بتدليس قط أعجب من هذا ولا أخفى قال أبو عبيدة لم يحدثني ولكن عبد الرحمن عن فلان عن فلان ولم يقل حدثني فجاز الحديث وسار قال البيهقي وذكر إبراهيم بن يوسف لا يجعله متصل','114','1','0');
INSERT INTO [t6227] VALUES (109,'ذهب لحاجته فأمر ابن مسعود أن يأتيه بثلاثة أحجار فجاءه بحجرين وبروثة فألقى الروثة وقال إنها ركس ائتني بحجر والله أعلم','115','1','0');
INSERT INTO [t6227] VALUES (110,'ولا عفو عن قدر الدرهم','115','1','0');
INSERT INTO [t6227] VALUES (111,'على قبرين فقال إنهما يعذبان وما يعذبان في كبير أما أحدهما فكان يمشي بالنميمة وأما الآخر فكان لا يستنزه من بوله قال فدعا بعسيب رطب فشقه باثنين ثم غرس على هذا واحد وعلى هذا واحدا ثم قال لعله أن يخفف عنهما ما لم ييبسا واتفقا على','116','1','0');
INSERT INTO [t6227] VALUES (112,'مسألة','118','1','0');
INSERT INTO [t6227] VALUES (113,'الرجل يخيل إليه الشيء في الصلاة فقال لا ينفتل حتى يسمع صوتا أو يجد ريحا وفي رواية عنه عن النبي','118','1','0');
INSERT INTO [t6227] VALUES (114,'قال لا وضوء إلا من صوت أو ريح أخرجه مسلم بمعناه فقال حتى تسمع صوتا أو تجد ريحا والله أعلم','118','1','0');
INSERT INTO [t6227] VALUES (115,'ومن استجمع نوم القلب والعين فعليه الوضوء سواء كان قائما أو','118','1','0');
INSERT INTO [t6227] VALUES (116,'قال إذا نعس أحدكم في صلاته فليرقد حتى يذهب عن عينه النوم فإن أحدكم إذا صلى وهو ناعس لعله يذهب يستغفر فيسب نفسه وأخرج البخاري عقيبه حديث أنس عن النبي','119','1','0');
INSERT INTO [t6227] VALUES (117,'الناعس في الصلاة بالانصراف ولو بقي فيها ببقاء الطهارة كما زعموا لما أمر بالانصراف إن شاء الله تعالى وروى الشافعي عن سفيان عن عاصم بن بهدلة عن زر قال أتيت صفوان بن عسال فقال ما جاء بك فقلت ابتغاء العلم قال إن الملائكة تضع','119','1','0');
INSERT INTO [t6227] VALUES (118,'فأتيتك أسألك هل سمعت من رسول الله','120','1','0');
INSERT INTO [t6227] VALUES (119,'يأمرنا إذا كنا سفرا أو مسافرين أن لا ننزع خفافنا ثلاثة أيام ولياليهن إلا من جنابة لكن من غائط وبول ونوم عاصم بن بهدلة قارئ أهل الكوفة وإن لم يخرج البخاري ومسلم حديثه في الصحيح لسوء حفظه فليس بساقط إذا وافق فيما يرويه الثقات ولم يخالف','120','1','0');
INSERT INTO [t6227] VALUES (120,'وكاء السه العينان فمن نام فليتوضأ وروي ذلك من حديث معاوية بن أبي سفيان قال قال النبي','121','1','0');
INSERT INTO [t6227] VALUES (121,'نام وهو ساجد حتى غط أو نفخ قلت يا رسول الله إنك قد نمت قال إن الوضوء لا يوجب حتى ينام مضطجعا فإنه إذا اضطجع استرخت مفاصله تفرد بآخر هذا الحديث أبو خالد يزيد بن عبد الرحمن الدالاني عن قتادة وأنكره عليه جميع أئمة أهل الحديث قال أبو','122','1','0');
INSERT INTO [t6227] VALUES (122,'محفوظا وقالت عائشة قال النبي','123','1','0');
INSERT INTO [t6227] VALUES (123,'كانوا ينتظرون العشاء فينامون أحسبه قال قعودا وعن ابن عمر أنه كان ينام قاعدا ويصلي ولا يتوضأ رواه الشافعي عن الثقة عن حميد عن أنس قال كان أصحاب رسول الله','125','1','0');
INSERT INTO [t6227] VALUES (124,'ينامون ثم يصلون ولا يتوضؤون وروى الشافعي عن مالك عن نافع عن ابن عمر أنه كان ينام قاعدا ثم يصلي ولا يتوضأ وروي في ذلك عن يزيد بن ثابت وأبي أمامة وأبي هريرة وروي فيه حديثان مسندان أحدهما عن حذيفة قال رقدت فاحتضنني رجل من','126','1','0');
INSERT INTO [t6227] VALUES (125,'إذا وضع أحدكم جنبه فليتوضأ والله أعلم','127','1','0');
INSERT INTO [t6227] VALUES (126,'وملامسة الرجل مع المرأة توجب الوضوء وقال أبو حنيفة لا توجب ودليلنا قول الله عز اسمه أو لامستم النساء واللمس باليد وغيرها داخل لوقوع اسمه عليه بدليل ما روي عن ابن عباس أن ماعزا جاء إلى النبي','127','1','0');
INSERT INTO [t6227] VALUES (127,'كل بني آدم أصاب من الزنا لا محالة فالعين زناها النظر واليد زناها اللمس والنفس تهوى وتحدث ويصدق ذلك ويكذبه الفرج وروي عن عائشة قالت قل يوم أو ما كان يوم إلا ورسول الله','128','1','0');
INSERT INTO [t6227] VALUES (128,'فجاءه رجل فقال يا رسول الله ما تقول في رجل أصاب من امرأة لا تحل له فلم يدع شيئا يصبه الرجل من امرأته إلا وقد أصابه منها إلا أنه لم يجامعها فقال توضأ وضوءا حسنا ثم قم فصل قال وأنزل الله عز وجل هذه الآية وأقم الصلاة طرفي','130','1','0');
INSERT INTO [t6227] VALUES (129,'قبل بعض نسائه ثم خرج إلى الصلاة ولم يتوضأ هذا حديث يشتبه فساده على كثير ممن ليس الحديث من شأنه ورواه إسنادا صحيحا وهو فاسد من وجهين أحدهما أن حبيب بن أبي ثابت لم يسمع من عروة بن الزبير فهو مرسل من هذا الوجه حكى ذلك يحيى بن سعيد قال','131','1','0');
INSERT INTO [t6227] VALUES (130,'يقبلني وهو على وضوء ثم يصلي وهذا أيضا فاسد','132','1','0');
INSERT INTO [t6227] VALUES (131,'كان يقبل وهو صائم وقال عنه غير عثمان أن النبي','134','1','0');
INSERT INTO [t6227] VALUES (132,'كان يتوضأ للصلاة ثم يقبل ولا يحدث وضوءا وروى حجاج بن أرطأة عن عمرو بن شعيب عن زينب عن عائشة أن رسول الله','134','1','0');
INSERT INTO [t6227] VALUES (133,'يقبل بعض نسائه ويصلي ولا يتوضأ رواة هذا الحديث إلى ابن أخي الزهري أكثرهم مجهولون ولا يجوز الاحتجاج بأخبار المجهولين وقد رواه غيره فخالفه فيه فروي عن سعيد بن بشير عن منصور بن زاذان عن الزهري عن أبي سلمة عن عائشة أنها قالت كان رسول','136','1','0');
INSERT INTO [t6227] VALUES (134,'كان يقبل وهو صائم كذلك رواه الحفاظ الثقات عن الزهري منهم معمر وعقيل عن أبي ذئب وقال مالك عن الزهري في القبلة الوضوء ولو كان ما رواه سعيد بن بشير صحيحا لما كان الزهري','137','1','0');
INSERT INTO [t6227] VALUES (135,'يقبل وهو صائم ثم يصلي ولا يتوضأ قال علي بن عمر هذا خطأ من وجوه لم يرو على هذا وإنما جاء به أنه أخطأ في إسناده ومتنه جميعا حيث روي عن الزهري عن أبي سلمة عن عروة عن عائشة وزاد في متنه ثم يصلي ولا يتوضأ والمحفوظ ما سبق ذكره','138','1','0');
INSERT INTO [t6227] VALUES (136,'كان يقبل وهو صائم وحاجب لم يكن له كتاب إنما كان يحدث من حفظه وروي من وجه آخر عن الحسن بن دينار عن هشام عن أبيه أن رجلا قال سألت عائشة رضي الله عنها عن الرجل يقبل امرأته أيعيد الوضوء فقالت قد كان رسول الله','139','1','0');
INSERT INTO [t6227] VALUES (137,'يقبل وهو صائم ثم لا يتوضأ قال علي بن عمر الدارقطني لا أعلم أحدا حدث به عن عاصم بن علي هكذا غير علي بن عبد العزيز هذا وهم من علي بن عبد العزيز','141','1','0');
INSERT INTO [t6227] VALUES (138,'كان يقبل بعض نسائه ثم لا يحدث وضوءا قال علي بن عمر قوله عبد الله بن غالب وهم وإنما أراد غالب بن عبيد الله وهو متروك وأبو سلمة الجهني وهو خالد بن','142','1','0');
INSERT INTO [t6227] VALUES (139,'ثم يخرج إلى الصلاة ولا يتوضأ قال الحاكم أبو عبد الله غالب هذا هو ابن عبيد الله العقيلي هو شيخ من أهل الجزيرة قد خلط في هذا الحديث من وجهين أخبرنا بالوجه الثاني وذكر إسنادا عن عمر بن أيوب الموصلي عن غالب بن عبيد الله عن نافع عن ابن','143','1','0');
INSERT INTO [t6227] VALUES (140,'أعطى معاوية سهما فقال له هاك هذا يا معاوية حتى توافيني به في الجنة وفي هذا غنية لمن تدبره وروي من وجه آخر عن سلمة بن صالح الكوفي عن محمد بن عبد الرحمن عن عطاء عن عائشة قالت توضأ رسول الله','144','1','0');
INSERT INTO [t6227] VALUES (141,'كان يقبل ولا يتوضأ هذا وهم والصحيح عن عبد الكريم عن عطاء من قوله وروي عن الوليد بن صالح حدثنا عبيد الله بن عمرو عن عبد الكريم الجزري عن عطاء عن عائشة أن النبي','146','1','0');
INSERT INTO [t6227] VALUES (142,'ذات ليلة فالتمست بيدي فوقعت يدي على قدميه وهما منصوبتان وهو ساجد وهو يقول اللهم إني أعوذ بمعافاتك من عقوبتك وأعوذ برضاك من سخطك وأعوذ بك منك رواه مسلم دون قوله وهو ساجد وروى عن حريث عن عامر عن','147','1','0');
INSERT INTO [t6227] VALUES (143,'كان يستدفئ بها بعد الغسل تفرد به حريث بن أبي مطر وهو ضعيف ضعفه ابن معين والبخاري وغيرهما والذي صح من الحديث الأول يحتمل أن يكون بينهما حائل من ثوب ثم أنه ورد في الملموس وكلامنا وقع في الملامس والله أعلم','148','1','0');
INSERT INTO [t6227] VALUES (144,'مسألة','149','1','0');
INSERT INTO [t6227] VALUES (145,'يقول إذا مس أحدكم ذكره فليتوضأ ورواه يحيى بن بكير عن مالك وزاد فليتوضأ وضوءه للصلاة وروى عن الزهري عن عبد الله بن أبي بكر بمعناه ورواه الأوزاعي عن عبد الله بن أبي بكر عن عروة عن بسرة عن النبي','150','1','0');
INSERT INTO [t6227] VALUES (146,'كما حدثني مروان عنها فدل ذلك على صحة الحديث وثبوته على شرط الشيخين وزال عنه الخلاف والشبهة وثبت سماع عروة بن بسرة هذا كله كلام الحاكم أبي عبد الله أخبرنا بذلك قال الحاكم فممن بين ما ذكرناه','151','1','0');
INSERT INTO [t6227] VALUES (147,'خمسة أحاديث غير هذا الحديث فقد ثبت بما ذكرناه اشتهار بسرة وارتفع عنها اسم الجهالة بهذه الروايات وقد روينا إيجاب الوضوء عن جماعة من الصحابة والصحابيات عن رسول الله','155','1','0');
INSERT INTO [t6227] VALUES (148,'قال من مس فرجه فليتوضأ قال الحاكم وهذا حديث صحيح وشاهده الحديث المشهور عن يزيد بن عبد الملك عن سعيد بن أبي سعيد المقبري عن أبي هريرة وروى عبد العزيز بن مقلاص عن الشافعي حدثنا عبد الله بن نافع عن يزيد بن عبد الملك النوفلي عن','156','1','0');
INSERT INTO [t6227] VALUES (149,'فذكر حديثا معناه أنه توضأ من مسه وعبد الله بن أبي جعفر الرازي هذا ضعيف وأيوب بن عتبة وروى ابن عدي عن ابن صاعد حدثنا عثمان بن معبد بن نوح حدثنا إسحاق الفروي حدثنا عبد الله بن عمر','157','1','0');
INSERT INTO [t6227] VALUES (150,'قال من مس ذكره فليتوضأ قال ابن عدي هذا الحديث بهذا الإسناد منكر وروى حفص بن عمر عن مالك بن أنس عن نافع عن ابن عمر أنه كان يتوضأ من مس الذكر ويذكر أن بسرة أخبرته أن رسول الله','158','1','0');
INSERT INTO [t6227] VALUES (151,'في مس الذكر فلم يدع الوضوء منه حتى مات قال وأخبرنا مسلم بن خالد عن ابن جريج عن عبد الواحد بن قيس عن ابن عمر أن النبي','159','1','0');
INSERT INTO [t6227] VALUES (152,'قال من مس فرجه فليتوضأ قال أبو عبد الله تفرد به أبو بكر بن أبي العوام عن عبد العزيز وروي من وجه آخر عن العلاء بن سليمان الرقي حدثنا الزهري عن سالم عن أبيه قال قال رسول الله','159','1','0');
INSERT INTO [t6227] VALUES (153,'من مس ذكره فليتوضأ قال ابن المديني لم أعلم لابن إسحاق إلا حديثين منكرين نافع عن ابن عمر عن النبي','160','1','0');
INSERT INTO [t6227] VALUES (154,'رواه إسحاق الحنظلي عن محمد بن بكر البرساني عن ابن جريج قال','160','1','0');
INSERT INTO [t6227] VALUES (155,'من مسه فرجه فليتوضأ أخطأ فيه هذا المصيصي حيث قال عن عائشة وإنما هو عن بسرة وروى عن الزهري عن عبد الله بن أبي بكر عن عروة ولم أسمع ذلك منه أنه كان يحدث عن بسرة أو زيد بن خالد وفي رواية عن ابن جريج عن الزهري عن عبد الله بن عروة','161','1','0');
INSERT INTO [t6227] VALUES (156,'إذا أفضى أحدكم بيده إلى ذكره فليتوضأ وزاد ابن نافع عن محمد بن عبد الرحمن بن ثوبان عن','162','1','0');
INSERT INTO [t6227] VALUES (157,'قال الشافعي رضي الله عنه وسمعت غير واحد من الحفاظ يرويه لا يذكرون فيه جابرا وأما حديث أبي أيوب فروي عن ابن أبي فروة عن الزهري عن عبد الله القاري عن خالد بن زيد عن أبي أيوب عن النبي','163','1','0');
INSERT INTO [t6227] VALUES (158,'أعاد الوضوء في مجلس فسألوه عن ذلك فقال إني حككت ذكري وقد قيل عن يونس عن ابن شهاب عن عمرو بن شعيب عن عروة عن عائشة','163','1','0');
INSERT INTO [t6227] VALUES (159,'من مس ذكره فليتوضأ وروي عن يحيى بن أبي كثير عن عروة عن عائشة قالت قال رسول الله','164','1','0');
INSERT INTO [t6227] VALUES (160,'عن عروة بن الزبير عن عائشة عن النبي','164','1','0');
INSERT INTO [t6227] VALUES (161,'أنها سمعت رسول الله','164','1','0');
INSERT INTO [t6227] VALUES (162,'من مس فرجه فليتوضأ هذا خطأ والصحيح رواية الجماعة عن هشام بن عروة عن أبيه عن بسرة وقد صحت الرواية عن سعد بن أبي وقاص وعبد الله بن عمر وعائشة أنهم كانوا يوجبون الوضوء من مس الذكر وروي عن عمر وابن عباس وروي عن أنس عن النبي','166','1','0');
INSERT INTO [t6227] VALUES (163,'عن الرجل يمس ذكره وهو في الصلاة فقال لا بأس إنما هو كبعض جسدك وروي من أوجه قريب من معناه عنه عن النبي','167','1','0');
INSERT INTO [t6227] VALUES (164,'وقيس','168','1','0');
INSERT INTO [t6227] VALUES (165,'أو سأله رجل فقال بينا أنا في الصلاة إذ ذهبت أحك فخذي فأصابت يدي ذكري فقال إنما هو منك وهذا إن صح فالظاهر أن إصابة يده ذكره إنما كانت بظهر الكف فإن حك الفخذ إنما يكون ببطن الأنامل والأظافير وأنما يلي ذكره حينئذ ظهر كفه وقال في','169','1','0');
INSERT INTO [t6227] VALUES (166,'في أول مقدمه المدينة حين كان يبني مسجده كذلك رواه حماد بن زيد عن محمد بن جابر فذكر قدومه عليه وهو يبني المسجد وسؤاله عن ذلك وقد روينا عن أبي هريرة وهو من آخرهم إسلاما عن النبي','170','1','0');
INSERT INTO [t6227] VALUES (167,'ثلاث سنين فصار حديث طلق منسوخا وربما استدلوا بما روي عن الفضل بن مختار عن الصلت بن دينار عن أبي عمر النهدي عن عمر بن الخطاب وعن فضل عن عبيد الله بن موهب عن عصمة بن مالك الخطمي وكان من أصحاب النبي','171','1','0');
INSERT INTO [t6227] VALUES (168,'وأنا أفعل ذلك الصلت بن دينار أو شعيب المجنون ضعيف كان ممن يشتم أصحاب رسول الله','171','1','0');
INSERT INTO [t6227] VALUES (169,'عن مس الذكر في الصلاة فقال هو منك إسناده ضعيف جعفر بن الزبير لا يحتج بحديثه وروي في ذلك عن عائشة مرفوعا ما أبالي إياه مسست أو أنفي فمنكر وقد روينا عن عائشة رضي الله عنها بخلافة وروى عن حاتم بن سليط عن رجل من بني حنيفة أنه','172','1','0');
INSERT INTO [t6227] VALUES (170,'أنه ترك الوضوء من مس الذكر كذا في هذه الرواية وأما في القديم في رواية','178','1','0');
INSERT INTO [t6227] VALUES (171,'خلافه وأما من أوجب الوضوء فيه فلا يكون يوجبه بالرأي وإنما يوجبه بالاتباع لأن الذي يعرف في الرأي لا وضوء فيه ولكن إنما اتبعنا فيه السنة والله أعلم','179','1','0');
INSERT INTO [t6227] VALUES (172,'مسألة','180','1','0');
INSERT INTO [t6227] VALUES (173,'قال لا وضوء إلا من صوت أو ريح هذا حديث ثابت اتفق البخاري ومسلم على إخراج معناه من حديث عبد الله بن زيد وأخرجه مسلم أيضا من حديث سهيل كما سبق في مسألة خروج الريح من القبل وروي عن محمد بن إسحاق عن صدقة بن يسار عن ابن جابر وهو عقيل بن','180','1','0');
INSERT INTO [t6227] VALUES (174,'غزوة ذات الرقاع من نخل فأصاب رجل من المسلمين امرأة رجل من المشركين فلما انصرف رسول الله','181','1','0');
INSERT INTO [t6227] VALUES (175,'دما فخرج يتبع أثر رسول الله فنزل رسول الله','181','1','0');
INSERT INTO [t6227] VALUES (176,'وأصحابه قد نزلوا إلى الشعب من الوادي فلما أن خرج الرجلان إلى فم الشعب قال الأنصاري للمهاجري أي الليل أحب إليك أن أكفيك أوله أو آخره قال بل اكفني أوله قال فاضطجع المهاجري فنام وقام الأنصاري يصلي وأتى زوج المرأة فلما رأى شخص','181','1','0');
INSERT INTO [t6227] VALUES (177,'بحفظه لقطع نفسي قبل أن أقطعها أو أنفذها قال الحاكم أبو عبد الله هذا حديث صحيح الإسناد وقد احتج مسلم بأحاديث محمد بن إسحاق فأما عقيل بن جابر الأنصاري فإنه أحسن حالا من أخويه محمد وعبد الرحمن وروي عن وهب بن جرير عن أبيه عن ابن إسحاق عن','182','1','0');
INSERT INTO [t6227] VALUES (178,'احتجم فصلى ولم يتوضأ ولم يزد على غسل محاجمه في إسناده صالح بن مقاتل قال الحاكم أبو عبد الله سألت الدارقطني عن صالح بن مقاتل بن صالح فقال يحدث عن أبيه ليس بالقوي واحتج الشافعي رحمه الله بأن ابن عمر عصر بثرة بوجهه فخرج منها دم','183','1','0');
INSERT INTO [t6227] VALUES (179,'من رعف أو قاء فإنه يتوضأ ويبني ما لم يتكلم هكذا رواه إسماعيل بن عياش وهو ممن لا تقوم به حجة عن ابن جريج ورواه أيضا مرة عن ابن جريج عن أبيه عن النبي','184','1','0');
INSERT INTO [t6227] VALUES (180,'وهو','184','1','0');
INSERT INTO [t6227] VALUES (181,'مرسلا كذلك رواه أصحاب ابن جريج الحفاظ عنه روى أبو عاصم عن ابن جريج عن أبيه قال قال','186','1','0');
INSERT INTO [t6227] VALUES (182,'إذا قاء أحدكم أو قلس أو وجد مذيا وهو في الصلاة فلينصرف وليتوضأ وليرجع فليبن على صلاته ما لم يتكلم قال علي بن عمر قال لنا أبو بكر سمعت محمد بن يحيى يقول هذا هو الصحيح عن ابن جريج وهو مرسل وأما حديث ابن أبي مليكة عن','187','1','0');
INSERT INTO [t6227] VALUES (183,'وقد سال من أنفي دم فقال أحدث وضوءا أحدث وضوءا قال علي بن عمر عمرو القرشي هذا هو عمرو بن خالد أبو خالد الواسطي متروك الحديث وقال أحمد بن حنبل ويحيى بن معين أبو خالد الواسطي كذاب وروي عن جعفر بن زياد الأحمر عن أبي خالد عن أبي هاشم','188','1','0');
INSERT INTO [t6227] VALUES (184,'من رعف في صلاته فليرجع فليتوضأ وليبن على صلاته قال علي بن عمر أبو بكر الداهري عبد الله بن حكيم متروك وقال ابن معين ليس حديثه بشيء وقال الجوزجاني كذاب مصرح ومنها ما روي عن بقية حدثنا يزيد بن خالد عن يزيد بن محمد عن عمرو بن','189','1','0');
INSERT INTO [t6227] VALUES (185,'إذا رعف أحدكم في صلاته فلينصرف فليغسل الدم ثم ليعد وضوءه ويستقبل صلاته وسليمان بن أرقم ضعيف قد مضى ذكر حاله وروي من وجه آخر عن عمر بن رباح البصري حدثنا عبد الله بن طاوس عن أبيه عن ابن عباس قال كان رسول الله','190','1','0');
INSERT INTO [t6227] VALUES (186,'قاء فأفطر قال فلقيت','191','1','0');
INSERT INTO [t6227] VALUES (187,'وضوءه وروي عن عبد الصمد بن عبد الوارث حدثنا أبي عن حسين بن ذكوان المعلم عن يحيى بن أبي كثير حدثني الأوزاعي عن يعيش بن الوليد بن هشام عن أبيه حدثني معدان بن أبي طلحة عن أبي الدرداء بمعناه وهذا إسناد مضطرب رواه عبد الوهاب عن هشام كما','192','1','0');
INSERT INTO [t6227] VALUES (188,'القلس حدث قال علي بن عمر الدارقطني سواء متروك ولم يروه عن زيد غيره وروى الشافعي عن مالك عن نافع عن ابن عمر أنه كان إذا رعف انصرف فتوضأ ثم رجع ولم يتكلم وهذا ثابت عن ابن','194','1','0');
INSERT INTO [t6227] VALUES (189,'بهذا اللفظ وقد روينا عنه أنه قال اغسل أثر المحاجم عنك وحسبك يدل ذلك على أن المراد من قوله الوضوء مما خرج أي من مخرج الحدث فيكون حجة عليهم وروي أيضا عن علي رضي الله عنه أن الوضوء مما خرج وليس مما دخل وروي عن معمر بن محمد بن','197','1','0');
INSERT INTO [t6227] VALUES (190,'احتجم فغسل موضع محاجمه وصب على رأسه قال ابن عدي سمعت ابن حماد يقول','197','1','0');
INSERT INTO [t6227] VALUES (191,'مسألة','198','1','0');
INSERT INTO [t6227] VALUES (192,'يعني شكي إليه الرجل يجد في صلاته شيئا قال لا ينصرف حتى يسمع صوتا أو يجد ريحا وثبت عن أبي هريرة عن النبي','198','1','0');
INSERT INTO [t6227] VALUES (193,'قال الضاحك في الصلاة والملتفت والمفقع أصابعه بمنزلة واحدة قال الحاكم أبو عبد الله هذا حديث مصري حسن','198','1','0');
INSERT INTO [t6227] VALUES (194,'قال من ضحك في صلاته يعيد الصلاة ولا يعيد الوضوء قال الحاكم تفرد به أبو فروة يزيد بن سنان الكبير عن الأعمش وغيره أوثق عندنا منه وكلهم ثقات إلا هذا الواحد من بينهم وكذا رواه أبو حامد أحمد بن علي بن الحسن المقري هذا عن أبي فروة','199','1','0');
INSERT INTO [t6227] VALUES (195,'الضحك ينقض الصلاة ولا ينقض الوضوء لكن رواته عن أبي خالد إبراهيم بن عثمان قاضي واسط أبو شيبة العبسي جد أبي بكر عبد الله بن محمد بن أبي شيبة غمزه شعبة ويحيى بن معين ورواه شعبة عن يزيد بن أبي خالد عن أبي سفيان وعن أبي خالد مولى جابر','200','1','0');
INSERT INTO [t6227] VALUES (196,'يصلي بنا فدخل رجل ضرير فوقع في حفرة فضحكنا فلما سلم رسول الله','201','1','0');
INSERT INTO [t6227] VALUES (197,'وأما قول الحسن بن عمارة عن خالد الحذاء عن أبي المليح عن أبيه فوهم قبيح وإنما رواه خالد الحذاء عن حفصة بنت سيرين عن النبي','203','1','0');
INSERT INTO [t6227] VALUES (198,'كذلك رواه عنه سعيد بن أبي عروبة ومعمر وأبو عوانة وسعيد بن بشير وغيرهم والحسن بن دينار متروك وروي من وجه آخر عن عمر بن قيس المكي عن عمرو بن عبيد عن الحسن عن عمران بن الحصين قال بينما نحن مع','203','1','0');
INSERT INTO [t6227] VALUES (199,'في صلاة في يوم ماطر إذ أقبل أعرابي يسعى يريد الصلاة فزلق فسقط في حفرة فيها ماء فضحك من كان خلف رسول الله','204','1','0');
INSERT INTO [t6227] VALUES (200,'من الصلاة أقبل علينا بوجهه فقال من قهقه منكم آنفا فليتوضأ وليعد الصلاة قال ابن معين عمرو بن قيس الكندي لقبه سندل وهو ضعيف وقال البخاري عمر بن قيس أخو حميد بن قيس المكي منكر الحديث ثم إن سلم منه فعمرو بن عبيد على الطريق وهو ضال غير','204','1','0');
INSERT INTO [t6227] VALUES (201,'الوضوء من الضحك في الصلاة وهذا هو المحفوظ عن الحسن مرسلا ورواه عبد الوهاب بن الضحاك عن إسماعيل بن','205','1','0');
INSERT INTO [t6227] VALUES (202,'قال لرجل ضحك أعد وضؤك قال ابن عدي ومحمد الخزاعي هذا هو من مجهولي مشايخ بقية ويقال عن بقية في هذا الحديث عن محمد بن راشد عن الحسن ومحمد بن راشد عن الحسن أيضا مجهول ورواه عبد الكريم أبي أمية عن الحسن عن أبي هريرة مرفوعا إذا','207','1','0');
INSERT INTO [t6227] VALUES (203,'وأحسن حالات سفيان بن محمد أن يكون وهم في الحديث على ابن وهب إن لم يكن تعمد ذلك في قوله عن الحسن عن أنس فقد رواه غير واحد عن ابن وهب عن يونس عن الزهري عن الحسن مرسلا عن النبي','209','1','0');
INSERT INTO [t6227] VALUES (204,'في حديث قال من كان منكم قهقه فليعد الوضوء والصلاة قال ابن عدي ورواه أبو يوسف ومكي بن إبراهيم المقري عن أبي حنيفة','210','1','0');
INSERT INTO [t6227] VALUES (205,'والله أعلم قال ابن عدي وهذا الذي ذكره ابن حماد غلط وذلك أنه قيل معبد الجهني فكيف يكون جهنيا أنصاريا ومعبد بن هوذه أنصاري وله حديث عن النبي','211','1','0');
INSERT INTO [t6227] VALUES (206,'ووهم فيه أبو حنيفة على منصور وإنما رواه منصور بن زاذان عن محمد بن سيرين عن معبد ومعبد هذا لا صحبة له ويقال أنه أول من تكلم في القدر من التابعين حدث به عن منصور عن ابن سيرين غيلان بن جامع وهشيم بن بشير وهما أحفظ من أبي حنيفة للإسناد','212','1','0');
INSERT INTO [t6227] VALUES (207,'فذكر قصة وقال فأمر رسول الله','214','1','0');
INSERT INTO [t6227] VALUES (208,'من ضحك أن يعيد الوضوء والصلاة قال علي بن عمر داود بن المحبر متروك الحديث وأيوب بن خوط ضعيف وقال محمد بن إسماعيل أيوب بن خوط البصري أبو أمية تركه ابن المبارك وقال داود بن محبر أبو سليمان قال أحمد شبه لا شيء لا يدري','215','1','0');
INSERT INTO [t6227] VALUES (209,'نحو الحديث قبله في الضحك في الصلاة وهذا خطأ إن لم يكن تعمده بعض رواته فقد رواه الثوري ويحيى القطان وجماعة من الثقات عن هشام عن حفصة عن أبي العالية عن النبي','216','1','0');
INSERT INTO [t6227] VALUES (210,'من ضحك منكم في الصلاة فليعد الوضوء والصلاة قال ابن عدي وهذا الحديث بهذا الإسناد ليس يرويه عن الأعمش غير أبي فروة حدثنا ابن أبي عصمة حدثنا أحمد بن أبي يحيى قال سمعت أحمد بن حنبل يقول أبو فروة يزيد بن سنان ضعيف وروى عن إبراهيم','217','1','0');
INSERT INTO [t6227] VALUES (211,'أمر رجلا ضحك في الصلاة أن يعيد الوضوء والصلاة قال الشافعي فلم نقبل هذا لأنه مرسل قال الشافعي يقولون نحابي ولو حابينا لحابينا الزهري إرسال الزهري ليس بشيء وذلك أنا نجده يروي عن سليمان بن أرقم قال الشافعي أخبرنا الثقة عن معمر عن','218','1','0');
INSERT INTO [t6227] VALUES (212,'في الصلاة فأمرهم أن يعيدوا الوضوء والصلاة قال ابن عدي وهذا الحديث إنما أرسله إبراهيم عن نفسه فأما الحديث فهو عن أبي العالية وذكر عن أبي هاشم الواسطي قال أنا حدثت إبراهيم عن أبي العالية قال ابن عدي حدثنا ابن أبي بكير حدثنا عباس','219','1','0');
INSERT INTO [t6227] VALUES (213,'أن أعرابيا ضحك في الصلاة فأمر النبي','221','1','0');
INSERT INTO [t6227] VALUES (214,'مرسلا ومدار الحديث على أبي العالية أخبرنا بصحة ذلك فذكر إسنادا إلى علي بن المديني قال قال لي عبد الرحمن بن مهدي هذا الحديث يدور على أبي العالية فقلت قد رواه الحسن مرسلا فقال حدثني حماد بن زيد عن حفص بن سليمان المنقري فقال أنا حدثت','221','1','0');
INSERT INTO [t6227] VALUES (215,'ولم يسم الرجل ولا ذكرا له صحبة أم لا ولم يصنع خالد شيئا وقد خالفه خمسة أثبات حفاظ وقولهم أولى بالصواب أخبرني بجميع هذا الكلام أبو عبد الرحمن السلمي عن علي بن عمر الحافظ ومراسيل أبي العالية الرياحي ليست بشيء فإنه كان يأخذ عن كل ضرب وروي','222','1','0');
INSERT INTO [t6227] VALUES (216,'في الضحك في الصلاة خبر قال أبو بكر بن إسحاق وأما حديث الحسن عن النبي','223','1','0');
INSERT INTO [t6227] VALUES (217,'ساقط وقد روينا عن سعيد عن قتادة عن الحسن أنه كان لا يرى على من ضحك في الصلاة وضوءا والله أعلم','223','1','0');
INSERT INTO [t6227] VALUES (218,'مسألة','224','1','0');
INSERT INTO [t6227] VALUES (219,'إنما الماء من ماء فإن ادعوا فيه النسخ بما روي عن الزهري عن سهل بن سعد عن أبي بن كعب إنما كانت الفتيا في الماء من الماء رخصة في أول الإسلام ثم نهى عنها قلنا إنما نسخ منه ترك الغسل بالتقاء الختانين دون خروج الماء فإما نطقه فغير','224','1','0');
INSERT INTO [t6227] VALUES (220,'مسألة','225','1','0');
INSERT INTO [t6227] VALUES (221,'كان إذا اغتسل من الجنابة بدأ فغسل يديه ثم توضأ كما يتوضأ للصلاة ثم يدخل أصابعه في الماء فيخلل بها أصول شعرة ثم يصب على رأسه ثلاث غرف بيده ثم يفيض الماء على جلده كله أخرجه البخاري في الصحيح من حديث مالك وأخرجه مسلم من حديث أبي معاوية','225','1','0');
INSERT INTO [t6227] VALUES (222,'وهو يغتسل من الجنابة فبدأ فغسل','225','1','0');
INSERT INTO [t6227] VALUES (223,'مسألة','226','1','0');
INSERT INTO [t6227] VALUES (224,'فقلت يا رسول الله إني امرأة أشد ضفر رأسي أفأنقضه لغسل الجنابة قال لا إنما يكفيك أن تحثي عليه ثلاث حثيات ثم تفيضين عليك الماء فتطهرين أو قال فإذا أنت قد طهرت واتفقا على حديث جبير بن مطعم أنهم ذكروا عند رسول الله','226','1','0');
INSERT INTO [t6227] VALUES (225,'أما أنا','226','1','0');
INSERT INTO [t6227] VALUES (226,'فذكر الحديث إلى أن قال كان آخر ذلك أن أعطي الذي أصابته الجنابة إناء من ماء قال اذهب فأفرغه عليك وروي عن أبي ذر قال قدمت على النبي','227','1','0');
INSERT INTO [t6227] VALUES (227,'جعل الاستنشاق والمضمضة ثلاثا فريضة وفي رواية قال المضمضة والاستنشاق للجنب ثلاثا فريضة قال علي بن عمر الدارقطني هذا باطل ولم يحدث به غير بركة هذا وهو يضع الحديث قال ابن عدي قال لي عبدان الأهوازي أغرب علي لخالد الحذاء حديثا فذكرت','228','1','0');
INSERT INTO [t6227] VALUES (228,'الاستنشاق في الجنابة ثلاثا معا هكذا','228','1','0');
INSERT INTO [t6227] VALUES (229,'إن تحت كل شعرة جنابة فاغسلوا الشعر وأنقوا البشرة وعن سفيان عن منصور عن إبراهيم قال كان يقال أنقوا البشر وبلوا الشعر يعني من الجنابة وعن عبد الوهاب بن عطاء عن سعيد عن قتادة عن الحسن عن أبي هريرة قال تحت كل','229','1','0');
INSERT INTO [t6227] VALUES (230,'تحت كل شعرة جنابة فبلوا الشعر وأنقوا البشرة فإنه ليس بثابت قال أبو داود الحارث حديثه منكر وقال البخاري الحارث بن وجيه الراسبي البصري سمع','230','1','0');
INSERT INTO [t6227] VALUES (231,'مرسلا وعن إبراهيم كان يقال وعن الحسن عن أبي هريرة موقوف من قوله والحسن لم يسمع من أبي هريرة أخبرنا بصحة ذلك وذكر إسنادا ثم أنه حمله على ما ظهر وروى أبو داود عن موسى بن إسماعيل حدثنا حماد أخبرنا عطاء بن السايب عن زاذان عن علي مرفوعا','231','1','0');
INSERT INTO [t6227] VALUES (232,'مسألة','232','1','0');
INSERT INTO [t6227] VALUES (233,'الرجل يخيل إليه الشيء في الصلاة فقال رسول الله','232','1','0');
INSERT INTO [t6227] VALUES (234,'لا يقطع الصلاة شيء وادرؤا ما استطعتم فإنما هو شيطان مجالد يحتاج إلى دعامة ومشاهده ما روي عن عمر بن عبد العزيز عن أنس بن مالك أن رسول الله','233','1','0');
INSERT INTO [t6227] VALUES (235,'قال من المسبح آنفا سبحان الله وبحمده قال أنا يا رسول الله إني سمعت أن الحمار يقطع الصلاة فقال رسول الله','233','1','0');
INSERT INTO [t6227] VALUES (236,'يقول لا يقطع الصلاة إلا الحديث وروى أبو داود عن أبي ذر قال اجتمعت غنيمة عند رسول الله','234','1','0');
INSERT INTO [t6227] VALUES (237,'فقال أبو ذر فسكت فقال ثكلتك أمك أبا ذر لأمك الويل فدعا لي بجارية سوداء فجاءت بعس فيه ماء فسترتني بثوب واستترت بالراحلة واغتسلت فكأني ألقيت عني جبلا فقال الصعيد الطيب وضوء المسلم ولو إلى عشر سنين فإذا وجدت الماء فأمسه','234','1','0');
INSERT INTO [t6227] VALUES (238,'مسألة','235','1','0');
INSERT INTO [t6227] VALUES (239,'مسألة','236','1','0');
INSERT INTO [t6227] VALUES (240,'وجعلت الأرض لنا مسجدا وترابها طهورا رواه مسلم في الصحيح عن حذيفة وفي الصحيحين عن عمران بن حصين رضي الله عنه أن رسول الله','236','1','0');
INSERT INTO [t6227] VALUES (241,'أن التراب هو الكافي في التطهير فمن نفض يديه حتى لم يبق عليهم غبار فقد اكتفى بغير التراب وخالف وصح وثبت عن سالم عن ابن عمر رضي الله عنهما أنه كان إذا تيمم ضرب بيديه ضربة فمسح بهم وجهه ثم ضرب بيديه ضربة أخرى ثم مسح بهما يديه إلى المرفقين','237','1','0');
INSERT INTO [t6227] VALUES (242,'إنما كان','237','1','0');
INSERT INTO [t6227] VALUES (243,'بكفيه الأرض ونفخ فيهما ثم مسح بهما وجهه وكفيه أخرج معناه البخاري ومسلم في الصحيح وهذا لا يخالف ما روينا إذ يجوز أنه بقي فيهما غبار التراب الذي جعله النبي','238','1','0');
INSERT INTO [t6227] VALUES (244,'مسألة','238','1','0');
INSERT INTO [t6227] VALUES (245,'في حديث حذيفة عند مسلم وجعلت لنا الأرض كلها مسجدا وجعلت ترتبها لنا طهورا والله أعلم','238','1','0');
INSERT INTO [t6227] VALUES (246,'مسألة','239','1','0');
INSERT INTO [t6227] VALUES (247,'في سفر فسمع مناديا ينادي قال الله أكبر قال شهادة الحق وذكر الحديث ثم قال انظروا فإنكم ستجدوه راعي معزى حضرته الصلاة فرأى لله عليه من الحق أن يتوضأ بالماء فإن لم يجد يتيمم ثم أذن وذكر الحديث وعن أبي أمامة قال قال رسول الله','239','1','0');
INSERT INTO [t6227] VALUES (248,'مسألة','240','1','0');
INSERT INTO [t6227] VALUES (249,'فقال يا عمرو صليت بأصحابك وأنت جنب فأخبرته بالذي منعني من الاغتسال وقلت إني سمعت الله جل ثناؤه يقول ولا تقتلوا أنفسكم إن الله كان بكم رحيما فضحك النبي','240','1','0');
INSERT INTO [t6227] VALUES (250,'سأل رسول الله','241','1','0');
INSERT INTO [t6227] VALUES (251,'إلى عمرو فسأله فأخبره بذلك وبالذي لقي من البرد فقال يا رسول الله إن الله تعالى يقول ولا تقتلوا أنفسكم ولو اغتسلت مت فضحك رسول الله','241','1','0');
INSERT INTO [t6227] VALUES (252,'مسألة','243','1','0');
INSERT INTO [t6227] VALUES (253,'يقول إن الحمى من فور جهنم فأبردوها بالماء فأمر المحموم باستعمال الماء وقد يتأذى إذا كان معها علة أخرى والله أعلم','243','1','0');
INSERT INTO [t6227] VALUES (254,'مسألة','244','1','0');
INSERT INTO [t6227] VALUES (255,'أخبر بذلك فقال قتلوه قتلهم الله ألا سألوا إذا لم يعلموا فإنما شفاء العي السؤال إنما كان يكفيه أن يتيمم ويعصر أو ويعصب شك موسى على جرحه خرقة ثم يمسح عليها ويغسل سائر جسده قال أبو بكر بن أبي داود هذه سنة تفرد بها أهل مكة','244','1','0');
INSERT INTO [t6227] VALUES (256,'وهو الصواب قال الدارقطني وقال ابن أبي حاتم سألت وأبا زرعة فقالا رواه ابن أبي العشرين عن الأوزاعي عن إسماعيل بن مسلم عن عطاء عن ابن عباس وأسند الحديث قال المصنف وفي الكتاب دلالة على وجوب التيمم على المريض ووجوب الغسل على الصحيح فوجب','245','1','0');
INSERT INTO [t6227] VALUES (257,'وفي المسح على الجبائر قولان أحدهما يجوز وهو مذهب أبي حنيفة وروي عن عمرو بن خالد عن زيد بن علي عن أبيه عن جده عن علي رضي الله عنه قال انكسر إحدى زندي فسألت','245','1','0');
INSERT INTO [t6227] VALUES (258,'فأمرني أن أمسح على الجبائر قال علي بن عمر عمرو بن خالد أبو خالد الواسطي متروك وقال وكيع كان عمرو بن خالد في جوارنا يضع الحديث فلما فطن له تحول إلى واسط وكذبه أحمد بن حنبل وابن معين وغيرهما وهذا الحديث يعرف به وقد سرقه عمر بن موسى','246','1','0');
INSERT INTO [t6227] VALUES (259,'فأمر به رسول الله','248','1','0');
INSERT INTO [t6227] VALUES (260,'فقدم سنده وهو ضعيف واضح ما روى في هذا الباب ما تقدم عن عطاء بن أبي رباح وإسناده مختلف فيه والله أعلم وصحيح عن ابن عمر أنه كان يمسح على العصابة ورواه العرزمي عن عطاء عن جابر نحو الرواية الأولى ببعض معناها في المسألة قبلها رواه عنه مرجى','248','1','0');
INSERT INTO [t6227] VALUES (261,'فقال ما لهم قتلوه قتلهم الله ثلاثا قد جعل الله التيمم أو الصعيد طهورا شك ابن عباس ثم أثبته بعد وبقريب من معناه روي بإسناد ضعيف عن أبي سعيد الخدري فإن صحت روايات هذا الحديث فقد أمر في بعضها بالتيمم وفي بعضها بغسل الصحيح منه وفي بعضها','249','1','0');
INSERT INTO [t6227] VALUES (262,'ولا يتيمم صحيح في المصر في حال وجود الماء لصلاة جنازة ولا غيرها وإن خاف فواتها وقال أبو حنيفة رحمه الله يتيمم إذا','249','1','0');
INSERT INTO [t6227] VALUES (263,'من نحو بئر جمل فلقيه رجل فسلم عليه فلم يرد رسول الله','250','1','0');
INSERT INTO [t6227] VALUES (264,'إذا فجئتك الجنازة وأنت على غير وضوء فتيمم قال ابن عدي هذا مرفوع غير محفوظ والله أعلم','252','1','0');
INSERT INTO [t6227] VALUES (265,'مسألة','253','1','0');
INSERT INTO [t6227] VALUES (266,'يتيمم بموضع يقال له مربد النعم وهو يرى بيوت المدينة قال الحاكم أبو عبد الله هذا حديث تفرد به عمرو بن محمد وهو صدوق وقد أوقفه يحيى بن سعيد الأنصاري وغيره عن نافع عن ابن عمرو وروى الشافعي عن ابن عيينة عن ابن عجلان عن نافع عن ابن عمر','253','1','0');
INSERT INTO [t6227] VALUES (267,'كان يخرج فيهريق الماء فيمسح بالتراب فأقول يا رسول الله إن الماء منك قريب فيقول ما أدري لعلي لا أبلغه رواية ابن لهيعة عن عبد الله بن هبيرة عن الأعرج عن حنش عنه وبما استدلوا بما روى الحارث الأعور عن علي قال إذا أجنب الرجل في السفر','254','1','0');
INSERT INTO [t6227] VALUES (268,'مسألة','255','1','0');
INSERT INTO [t6227] VALUES (269,'بالأبطح فجاءه بلال فآذنه بالصلاة قال فدعا بوضوء فتوضأ فجعل الناس يأتون وضوء رسول الله','255','1','0');
INSERT INTO [t6227] VALUES (270,'وأنا مريض لا أعقل فتوضأ فصب علي من وضوئه فأفقت فقلت يا رسول الله أني إنما يرثني الكلالة فكيف بالميراث فنزلت آية الفرض وروى معاذ قال رأيت رسول الله','255','1','0');
INSERT INTO [t6227] VALUES (271,'مسح رأسه ببلل لحيته وخالفه محمد بن يحيى الأزدي عن أبي داود فقال فيه كان النبي','256','1','0');
INSERT INTO [t6227] VALUES (272,'توضأ ومسح رأسه ببلل يديه ورواه مسدد عن أبي داود فقال إن النبي','256','1','0');
INSERT INTO [t6227] VALUES (273,'توضأ مرة مرة ومسح رأسه ببلل يديه وسليمان بن أرقم متروك والصحيح عن ابن عباس عن النبي','257','1','0');
INSERT INTO [t6227] VALUES (274,'توضأ ومسح رأسه من فضل يده ورواه إسماعيل بن عياش عن تمام عن الحسن عن أبي الدرداء قال رأيت رسول الله','257','1','0');
INSERT INTO [t6227] VALUES (275,'فقال يا رسول الله إني اغتسلت من جنابة فصليت الفجر فلما أصبحت رأيت في ذراعي قدر موضع الظفر لم يصبه الماء فقال رسول الله','258','1','0');
INSERT INTO [t6227] VALUES (276,'اغتسل ولمعة بين منكبيه لم يصبهما الماء فقال رسول الله','258','1','0');
INSERT INTO [t6227] VALUES (277,'اغتسل من الجنابة فبقيت لمعة في جسده فقيل له يا رسول الله هذه لمعة في جسدك لم يصبها الماء قال فأومأ إلى بلل شعره فبله به فأجزأه ذلك يحيى بن عنبسة هذا كان يتهم بوضع الحديث وإنما يروي عن إبراهيم من قوله في الوضوء إن كان في','259','1','0');
INSERT INTO [t6227] VALUES (278,'صلاة الصبح وقد اغتسل من جنابة فكان نكتة مثل الدرهم لم يصبها الماء فسلت شعره من الماء','259','1','0');
INSERT INTO [t6227] VALUES (279,'مرضي أن رسول الله','260','1','0');
INSERT INTO [t6227] VALUES (280,'اغتسل من الجنابة فرأى على عاتقه لمعة بهذا وقال فقال بشعره وهو رطب قال علي هذا مرسل وهو الصواب والله أعلم','260','1','0');
INSERT INTO [t6227] VALUES (281,'مسألة','261','1','0');
INSERT INTO [t6227] VALUES (282,'قال إذا شرب الكلب في إناء أحدكم فليغسله سبع مرات وفي رواية عند مسلم قال قال رسول الله','261','1','0');
INSERT INTO [t6227] VALUES (283,'طهور إناء أحدكم إذا ولغ الكلب فيه أن يغسله سبع مرات أولاهن بالتراب كذا رواه هشام بن حسان وأيوب وحبيب بن الشهيد عن ابن سيرين عن أبي هريرة قال أيوب أولاهن أو آخرهن بالتراب وعند مسلم أيضا عن عبد الله بن مغفل أن رسول الله','261','1','0');
INSERT INTO [t6227] VALUES (284,'رواه أبو داود عن محمد بن عبيد عن حماد بن','263','1','0');
INSERT INTO [t6227] VALUES (285,'مسألة','264','1','0');
INSERT INTO [t6227] VALUES (286,'أن يتوضأ فقالت امرأة من نسائه يا رسول الله قد توضأت من هذا فتوضأ النبي','265','1','0');
INSERT INTO [t6227] VALUES (287,'قال إنها ليست بنجس إنها من الطوافين عليكم والطوافات فجعل النبي','265','1','0');
INSERT INTO [t6227] VALUES (288,'إنها ليست بنجس إنما هي من الطوافين عليكم وقد رأيت رسول الله','266','1','0');
INSERT INTO [t6227] VALUES (289,'من إناء قد أصابت منه الهرة قبل ذلك وعن الليث حدثنا يعقوب بن إبراهيم الأنصاري عن عبد الله بن سعيد عن أبيه عن عروة عنها قالت كان رسول الله','266','1','0');
INSERT INTO [t6227] VALUES (290,'أنه كان يصغي إلى الهرة الإناء حتى تشرب منه ثم يتوضأ بفضلهما وأما الذي روي عن','266','1','0');
INSERT INTO [t6227] VALUES (291,'في الكلب يلغ في الإناء يغسل سبع مرات أولاهن أو أخراهن بالتراب والسنور مرة فهو في السنور من قول أبي هريرة فغلط فيه بعض الرواة فأدرجه في الحديث وقد بينه قرة عن ابن سيرين بيانا شافيا فقال عن رسول الله','267','1','0');
INSERT INTO [t6227] VALUES (292,'أنه سئل أتتوضأ بما أفضلت الحمر قال نعم وبما أفضلت السباع كلها ورواه أبو بكر بن زياد النيسابوري عن الربيع عن الشافعي عن ابن أبي حبيبة وهو إبراهيم بن إسماعيل بن أبي حبيبة عن داود بن الحصين عن أبيه عن جابر وروى الدارقطني عن محمد بن','268','1','0');
INSERT INTO [t6227] VALUES (293,'سئل عن الحياض التي بين المدينة ومكة وقالوا تردها السباع والكلاب والحمير فقال رسول الله','269','1','0');
INSERT INTO [t6227] VALUES (294,'ما أكل لحمه فلا بأس بسؤره وهذا إن سلم','270','1','0');
INSERT INTO [t6227] VALUES (295,'مسألة','271','1','0');
INSERT INTO [t6227] VALUES (296,'إذا سقط الذباب في إناء أحدكم فليغمسه كله ثم لينتزعه فإن في أحد جناحيه داء وفي الآخر شفاء رواه البخاري في الصحيح أجاب الشافعي عن هذا فقال وغمس الذباب في الإناء ليس بقتله والذباب لا يؤكل وروى بقية عن سعيد بن أبي سعيد عن','271','1','0');
INSERT INTO [t6227] VALUES (297,'يا سلمان كل طعام وشراب وقعت فيه دابة ليس لها دم فماتت فيه فهو حلال أكله وشربه ووضؤوه قال الدارقطني لم يروه غير بقية عن سعيد بن أبي سعيد الزبيدي وهو ضعيف وقد ذكرنا أن ما يرويه بقية من الضعفاء والمجهولين فليس بمقبول منه كيف وقد أجمعوا','272','1','0');
INSERT INTO [t6227] VALUES (298,'وحد الماء الذي لا ينجس جميعه بما يقع فيه ولا بغيره قلتان وقال أبو حنيفة ما لا يلتقي طرفاه وحده أصحابه بأنه إذا حرك لا يتحرك جانباه ودليلنا ما أخبرنا الحاكم أبو عبد الله حدثنا أبو العباس','272','1','0');
INSERT INTO [t6227] VALUES (299,'سئل عن الماء وما ينوبه من الدواب والسباع فقال رسول الله','273','1','0');
INSERT INTO [t6227] VALUES (300,'عن الماء وما ينوبه من الدواب والسباع فقال إذا كان الماء قلتين لم يحمل الخبث أخبرنا أبو عبد الله قال وهكذا رواه الشافعي رحمه الله في المبسوط عن الثقة وهو أبو أسامة بلا شك فيه أخبرنا أبو عبد الله حدثنا أبو العباس أخبرنا الربيع أخبرنا','275','1','0');
INSERT INTO [t6227] VALUES (301,'عن الماء وما ينوبه من الدواب والسباع فقال النبي','276','1','0');
INSERT INTO [t6227] VALUES (302,'يسأل عن الماء يكون بأرض الفلاة وما ينوبه من الدواب والسباع فقال رسول الله','276','1','0');
INSERT INTO [t6227] VALUES (303,'سئل عن الماء يكون بالفلاة وترده السباع والكلاب قال إذا كان الماء قلتين لا يحمل الخبث كذا قال السباع والكلاب وهو غريب وروى أبو داود حدثنا موسى بن إسماعيل حدثنا حماد حدثنا عاصم بن المنذر عن عبد الله بن عبد الله حدثني أبي أن رسول الله','278','1','0');
INSERT INTO [t6227] VALUES (304,'قال إذا كان الماء قلتين لم يحمل خبثا وفي هذا الحديث بقلال هجر قال ابن جريج وقد رأيت قلال هجر فالقلة تسع قربتين أو قربتين وشيأ وروى الدارقطني عن أبي بكر النيسابوري حدثنا أبو حميد حدثنا حجاج حدثنا ابن جريج أخبرني محمد يعني ابن يحيى','279','1','0');
INSERT INTO [t6227] VALUES (305,'قال فذكر حديث المعراج وفيه قال ودفعت إلى سدرة المنتهى وإذا أوراقها مثل أذان الفيول وإذا نبقها مثل قلال هجر وروى القاسم بن عبد الله عن محمد بن المنكدر عن جابر بن عبد الله قال قال رسول الله','280','1','0');
INSERT INTO [t6227] VALUES (306,'الماء طهور لا ينجسه شيء قال أبو داود قال قتيبة بن سعيد سألت قيم بئر بضاعة عن عمقها فقلت أكثر ما يكون فيها الماء قال إلى العانة قلت فإذا نقص قال دون العورة قال أبو داود قدرت بئر بضاعة بردائي مددته عليها ثم ذرعته فإذا','282','1','0');
INSERT INTO [t6227] VALUES (307,'في سفر فإذا نحن بنهر من ماء أو غدير فيه شاة ميتة فأمسكنا أيدينا فقال اشربوا وتوضئوا فإن الماء لا ينجسه شيء طريف هو ابن شهاب أبو سفيان السعدي ليس بقوي وروى عن عكرمة أن عمر ورد ماء مجنة فقيل له إن الكلاب قد ولغت فيه فقال إنما ولغت','283','1','0');
INSERT INTO [t6227] VALUES (308,'مرسلا وقد روي عن قتادة مختصرا وفيه إرسال وربما استدلوا بما روي عن ابن سيرين أن','284','1','0');
INSERT INTO [t6227] VALUES (309,'وقد يكون الدم ظهر فيها فنزحها إن كان فعل أو تنظيفا لا واجبا هذا الحديث لا يثبت كما ذكر الإمام الشافعي رضي الله عنه وذلك أن محمد بن سيرين لم يسمع من ابن عباس رضي الله عنهما ذكره ابن حنبل فهو مرسل والآخر فجابر في طريقه وهو ساقط بمرة لا','285','1','0');
INSERT INTO [t6227] VALUES (310,'مسألة','286','1','0');
INSERT INTO [t6227] VALUES (311,'في سفر فذكر الحديث إلى أن قال ثم أهويت لأنزع خفيه قال دعهما فإني أدخلتهما طاهرتين فمسح','286','1','0');
INSERT INTO [t6227] VALUES (312,'أنه رخص للمسافر ثلاثة أيام ولياليهن وللمقيم يوما وليلة إذا تطهر ولبس خفيه أن يمسح عليهما ورويناه في حديث صفوان بن عسال وغيره بمعناه والله أعلم','287','1','0');
INSERT INTO [t6227] VALUES (313,'مسألة','288','1','0');
INSERT INTO [t6227] VALUES (314,'في غزوة تبوك فمسح أعلى الخفين وأسفلهما والذي روي عن المغيرة وغيره من مسح رسول الله','288','1','0');
INSERT INTO [t6227] VALUES (315,'يمسح على ظاهر خفيه وفي هذا دلالة على','288','1','0');
INSERT INTO [t6227] VALUES (316,'مسألة','289','1','0');
INSERT INTO [t6227] VALUES (317,'قال يغتسل من أربع من الجنابة ويوم الجمعة ومن غسل الميت والحجامة','289','1','0');
INSERT INTO [t6227] VALUES (318,'قال من غسل الميت فليغتسل ومن حمله فليتوضأ قال الشافعي أخبرنا عمرو بن الهيثم الثقة عن شعبة عن أبي إسحاق عن ناجية بن كعب عن علي رضي الله عنه قال قلت يا رسول الله بأبي أنت وأمي إن أبي قد مات قال اذهب فواره قلت إنه كان مشركا','290','1','0');
INSERT INTO [t6227] VALUES (319,'مسألة','291','1','0');
INSERT INTO [t6227] VALUES (320,'فقالت إني امرأة أستحاض فلا أطهر أفأدع الصلاة قال إنما ذلك عرق وليست بالحيضة فإذا أقبلت الحيضة فدعي الصلاة وإذا أدبرت فاغسلي عنك الدم ثم صلي وأخرجه البخاري ومسلم في الصحيح وروى أبو داود من حديث فاطمة بنت أبي حبيش أنها كانت تستحاض','291','1','0');
INSERT INTO [t6227] VALUES (321,'الدم فقال لها امكثي قدر ما كانت تحبسك حيضتك ثم اغتسلي فكانت تغتسل عند كل صلاة وروى مالك في الموطأ عن نافع مولى ابن عمر عن سليمان بن يسار عن أم سلمة زوج النبي','292','1','0');
INSERT INTO [t6227] VALUES (322,'فاستفتت لها أم سلمة رسول الله','292','1','0');
INSERT INTO [t6227] VALUES (323,'مسألة','293','1','0');
INSERT INTO [t6227] VALUES (324,'أستفتيه وأخبره فوجدته في بيت أختي زينب فقلت يا رسول الله إني امرأة أستحاض حيضة كثيرة شديدة فما ترى فيها قد منعني الصلاة والصوم قال انعت لك الكرسف فإنه يذهب الدم قالت هو أكثر من ذلك قال فاتخذي ثوبا قال هو أكثر من ذلك إنما أثج','294','1','0');
INSERT INTO [t6227] VALUES (325,'إنما هذه ركضة من ركضات الشيطان فتحيضي ستة أو سبعة أيام في علم الله جل وعز ثم اغتسلي حتى إذا رأيت أنك قد طهرت واستنقأت فصلي ثلاثا وعشرين ليلة أو أربعا وعشرين ليلة وأيامها فإن ذلك يجزيك وكذلك فافعلي في كل شهر كما تحيض النساء وكما يطهرن ميقات','294','1','0');
INSERT INTO [t6227] VALUES (326,'مسألة','295','1','0');
INSERT INTO [t6227] VALUES (327,'مسألة','296','1','0');
INSERT INTO [t6227] VALUES (328,'في الحيض فذكر قصة طويلة إلى أن قال قلت أخبرنا أبو عصمة قال حدثني حميد عن أنس قال قال رسول الله','302','1','0');
INSERT INTO [t6227] VALUES (329,'قال الحيض ثلاثة أيام وخمسة وستة وسبعة وثمانية وعشرة فإذا زادت على العشرة فمستحاضة هذا إنما يعرف من','302','1','0');
INSERT INTO [t6227] VALUES (330,'لا يكون الحيض للجارية والثيب التي قد آيست من الحيض أقل من ثلاثة أيام ولا أكثر من عشرة أيام قال الدارقطني عبد الملك هذا رجل مجهول والعلاء هو ابن كثير وهو ضعيف الحديث ومكحول لم يسمع من أبي أمامة شيئا والله أعلم وقال البخاري','303','1','0');
INSERT INTO [t6227] VALUES (331,'الحيض عشر فما زاد فهي مستحاضة والنفساء أربعين فما زاد فهي مستحاضة إبراهيم بن زكريا فيه ضعف وسليمان بن عمرو النخعي رمي بالكذب روى عن يعقوب بن سفيان قال أبو داود النخغي سليمان بن عمر قدري رجل سوء كذاب كان يكذب معاوية قال إسحاق','304','1','0');
INSERT INTO [t6227] VALUES (332,'وحدثنا أبو طوالة عن أبي سعيد الخدري وجعفر بن محمد عن أبيه عن جده عن النبي','305','1','0');
INSERT INTO [t6227] VALUES (333,'أقل الحيض ثلاثة أيام وأكثره عشرة أيام قال علي بن عمر حماد بن منهال مجهول ومحمد بن أحمد بن أنس ضعيف وربما استدلوا بما روي عن هارون بن زياد القشيري عن الأعمش عن إبراهيم عن علقمة عن عبد الله قال الحيض ثلاث وأربع وخمس وست وسبع وثمان','306','1','0');
INSERT INTO [t6227] VALUES (334,'مسألة','307','1','0');
INSERT INTO [t6227] VALUES (335,'تقعد بعد نفاسها أربعين يوما أو أربعين ليلة أبو سهل هو كثير بن زياد البرساني ليس له ذكر في الكتابين الصحيحين وذكره أبو حاتم في كتاب المجروحين واستحب مجانبة ما انفرد به وقد وثقه البخاري من رواية أبي عيسى عنه وذكر أنه ليس لمسة إلا هذا','308','1','0');
INSERT INTO [t6227] VALUES (336,'تقعد في النفاس أربعين ليلة لا يأمرها النبي','309','1','0');
INSERT INTO [t6227] VALUES (337,'وقت للنساء في نفاسهن أربعين يوما أبو بلال الأشعري لا يحتج به وقال الدارقطني ضعيف وروي عن عمر بن هارون البلخي عن أبي بكر الهذلي عن الحسن أن امرأة عثمان ابن أبي العاص رضي الله عنه لما تعلت من نفاسها تزينت فقال عثمان ألم أخبرك أن رسول','309','1','0');
INSERT INTO [t6227] VALUES (338,'تنتظر النفساء أربعين ليلة فإن رأت الطهر قبل ذلك فهي طاهر وإن جاوزت الأربعين فهي بمنزلة المستحاضة تغتسل وتصلي فإن عليها الدم توضأت لكل صلاة عمرو بن الحصين ضعيف ومحمد بن علاثة متروك وقد ذكرنا في مسألة الأذنين ما يقع به الكفاية وروي عن','311','1','0');
INSERT INTO [t6227] VALUES (339,'يقول لا حيض دون ثلاثة أيام ولا حيض فوق عشرة مما زاد على ذلك فهي مستحاضة فما زاد تتوضأ بكل صلاة إلى أيام أقرائها ولا نفاس دون أسبوعين ولا نفاس فوق أربعين فإن رأت النفساء الطهر دون الأربعين صامت وصلت ولا يأتيها زوجها إلا بعد الأربعين','312','1','0');
INSERT INTO [t6227] VALUES (340,'وقت للنفساء أربعين يوما يحيى بن العلاء ضعيف','312','1','0');
INSERT INTO [t6227] VALUES (341,'عن ذلك فأمرها أن تمسك أربعين ليلة ثم تغتسل ثم تطهر فتصلي قال علي بن عمر عطاء بن عجلان متروك الحديث وروي عن سلام بن سلم عن حميد عن أنس قال قال رسول الله','313','1','0');
INSERT INTO [t6227] VALUES (342,'وقت النفساء أربعون ليلة إلا أن ترى الطهر قبل ذلك زيد العمي','313','1','0');
INSERT INTO [t6227] VALUES (343,'في ذلك فليس لأحد مع النبي','314','1','0');
INSERT INTO [t6227] VALUES (344,'أنه قال لعائشة وأم سلمة أنفست قالتا نعم فسمى رسول الله','314','1','0');
INSERT INTO [t6227] VALUES (345,'تحت الشجرة قال نفست امرأة له فرأت الطهر بعد عشرين يوما فاغتسلت ثم جاءت تدخل معه في لحافه فوجد مسها فقال من هذه قالت فلانة قال ما لك قالت لا إلا أني رأيت الطهر فاغتسلت فضربها برجله فأقامها عن فراشه وقال لا تغريني عن ديني حتى','315','1','0');
INSERT INTO [t6227] VALUES (346,'والمستحاضة تتوضأ لكل صلاة فريضة وقال أبو حنيفة أنها تتوضأ لوقت كل صلاة وتصلي بوضوئها ما شاءت من الفرائض','315','1','0');
INSERT INTO [t6227] VALUES (347,'فقالت إني أستحاض فلا أطهر فقال أحصي أيام حيضتك ثم اغتسلي وتوضئي لكل صلاة وإن قطر الدم على الحصير يقال إن عروة هذا ليس بابن الزبير إنما هو المزني وقد سبق ذكره في مسألة اللمس وقد وقفه حفص وروي عن عثمان بن سعد القرشي حدثنا ابن أبي','316','1','0');
INSERT INTO [t6227] VALUES (348,'قولي لها فلتدع الصلاة في كل شهر أيام قرؤها ثم لتغتسل في كل يوم غسلا واحدا ثم الطهور عند كل صلاة ولتنظف ولتحتشي فإنما هو داء عرض أو ركضة من','316','1','0');
INSERT INTO [t6227] VALUES (349,'قال لفاطمة إذا أقبلت أيام أقرائك فأمسكي عليك فإذا مضت فاغتسلي ثم اطهري لكل صلاة يعني الوضوء وروي عن الشعبي عن قمير امرأة مسروق عن عائشة رضي الله عنها أنها قالت المستحاضة تدع الصلاة أيام حيضها ثم تغتسل ثم تتوضأ عند كل صلاة وضؤا','317','1','0');
INSERT INTO [t6227] VALUES (350,'قال يحيى وجده اسمه دينار وقد قيل فيه عن عدي بن ثابت عن أبيه عن علي رضي الله عنه عن النبي','318','1','0');
INSERT INTO [t6227] VALUES (351,'أمر المستحاضة بالوضوء لكل صلاة وروي عن سمي مولى أبي بكر عن ابن المسيب قال تغتسل من ظهر إلى ظهر','318','1','0');
INSERT INTO [t6227] VALUES (352,'كتاب الصلاة','321','1','0');
INSERT INTO [t6227] VALUES (353,'مسألة','321','1','0');
INSERT INTO [t6227] VALUES (354,'بالصلاة فأمره فصلى وذكر الحديث وفيه ثم صلى العصر حتى كان ظل كل شيء بقدره مرة وفيه وأخر العصر إلى قدر ظله مرتين وفي أخره ثم قال ما بين هذين صلاة قال صالح بن كيسان وكان عطاء بن أبي رباح يحدث عن جابر بن عبد الله في وقت الصلاة نحو','322','1','0');
INSERT INTO [t6227] VALUES (355,'عن وقت الصلاة فقال صل معنا فصلى رسول الله','323','1','0');
INSERT INTO [t6227] VALUES (356,'أمني جبريل عليه السلام عند البيت مرتين فذكر الحديث بمعنى ما تقدم وهكذا رواه عبد العزيز بن','323','1','0');
INSERT INTO [t6227] VALUES (357,'بمعنى حديث هؤلاء وربما استدلوا بحديث ابن عمر في صحيح البخاري من حديث نافع عنه قال قال رسول الله','324','1','0');
INSERT INTO [t6227] VALUES (358,'إنما بقاؤكم فيمن سلف من الأمم كما بين من صلاة العصر إلى غروب الشمس أوتي أهل التوراة التوراة فعملوا حتى انتصف النهار ثم عجزوا فأعطوا قيراطا قيراطا ثم أوتي أهل الإنجيل الإنجيل فعملوا حتى صليت العصر ثم عجزوا فأعطوا قيراطا قيراطا ثم أوتيتم','325','1','0');
INSERT INTO [t6227] VALUES (359,'رواه البخاري في الصحيح بمعناه فهذا الحديث بمعنى ما رواه عبد الله بن عمر والقصد منه بيان إكمال المسلمين ما استعملوا له دون اليهود والنصارى فلذلك استحقوا بوعد الله تعالى ما استؤجروا عليه ولا دلالة لهم في هذا أو الرجوع في بيان الأوقات إلى','326','1','0');
INSERT INTO [t6227] VALUES (360,'مسألة','327','1','0');
INSERT INTO [t6227] VALUES (361,'فذكر المواقيت وقال فيه ثم جاءه للمغرب حين غابت الشمس وقتا واحدا نزل عنه فقال قم فصل المغرب قال الحاكم أبو عبد الله هذا حديث صحيح مشهور من حديث عبد الله بن المبارك وروي في ذلك عن أبي هريرة قال قال رسول الله','327','1','0');
INSERT INTO [t6227] VALUES (362,'فصلى به الصلوات في وقتين إلا المغرب وفي حديث أبي مسعود في المواقيت لم يذكرها إلا وقتا واحدا وروي عن عمرو بن شعيب عن أبيه عن جده عن النبي','328','1','0');
INSERT INTO [t6227] VALUES (363,'كان يصلي الظهر بالهاجرة ويصلي العصر والشمس حية ويصلي العصر إذا وجبت ويصلي العشاء إذا كثر الناس عجل وإذا قلوا أخر ويصلي الصبح بغلس فهذا فعل رسول الله','328','1','0');
INSERT INTO [t6227] VALUES (364,'صلوا كما رأيتموني أصلي رواه الشافعي عن عبد الوهاب الثقفي واتفقا على إخراجه في الصحيح من حديثه عن أيوب عن أبي قلابة حدثنا أبو سليمان مالك بن الحويرث قال قال لنا','328','1','0');
INSERT INTO [t6227] VALUES (365,'صلوا كما رأيتموني أصلي فإذا حضرت الصلاة فليؤذن لكم أحدكم وليؤمكم أكبركم وعلى هذا إجماع المسلمين عملا حتى لا تكاد توجد عند غروب الشفق جماعة في صلاة المغرب فصار ذلك كإجماعهم على أعداد ركعات الصلوات ورد بإسناد رواته كلهم ثقات عن','329','1','0');
INSERT INTO [t6227] VALUES (366,'وفي الصحيحين عن سلمة قال كنا نصلي مع رسول الله','330','1','0');
INSERT INTO [t6227] VALUES (367,'فينصرف أحدنا وإنه لينظر إلى مواقع نبله وكتب عمر بن الخطاب رضي الله عنه إلى عماله أن صلوا المغرب إذا غربت الشمس وربما استدلوا بما روى مسلم في الصحيح عن عبيد الله بن معاذ عن أبيه حدثنا شعبة عن قتادة سمع أبا أيوب عن عبد الله بن عمرو عن','330','1','0');
INSERT INTO [t6227] VALUES (368,'ومرتين لم يرفعه وكذلك قال يحيى بن أبي بكير عن شعبة وقال أبو عامر العقدي عن شعبة حدثني به مرتين مرة رفعه إلى النبي','331','1','0');
INSERT INTO [t6227] VALUES (369,'عن مواقيت الصلاة فقال له اشهد معنا الصلاة فأمر بلالا فأذن فذكر الحديث في المواقيت وذكر في صلاة المغرب أنه أمره بها من الغد قبل أن يقع الشفق وقال في','331','1','0');
INSERT INTO [t6227] VALUES (370,'أمني جبريل وذكر الحديث ويقال إنما أخذه عن قيس بن الربيع أخبرنا أبو صالح العنبري حدثنا يحيى بن منصور القاضي حدثنا أحمد بن سلمة حدثنا محمد بن بشار حدثنا حرمي بن عمارة حدثنا شعبة عن علقمة عن سليمان بن بريدة عن أبيه عن النبي','332','1','0');
INSERT INTO [t6227] VALUES (371,'في رواية قال ثم أخر المغرب حتى إذا كان عند سقوط الشفق وفي أخرى وصلى المغرب قبل أن يغيب الشفق في اليوم الثاني وروي عن محمد بن فضيل عن الأعمش عن أبي صالح عن أبي هريرة قال قال رسول الله','333','1','0');
INSERT INTO [t6227] VALUES (372,'إن للصلاة أولا وآخرا رواه الناس كلهم عن الأعمش عن مجاهد مرسل والله أعلم','334','1','0');
INSERT INTO [t6227] VALUES (373,'والشفق الذي يدخل بغروبه وقت العشاء هو الحمرة وقال','334','1','0');
INSERT INTO [t6227] VALUES (374,'يصليها لسقوط القمر لثالثه والشفق الذي هو البياض لا يغيب إلا بعد ذلك بزمان وقد صلاها قبله وروى في حديث عبد الله بن عمرو في المواقيت عن النبي','335','1','0');
INSERT INTO [t6227] VALUES (375,'مسألة','338','1','0');
INSERT INTO [t6227] VALUES (376,'فقال كان يصلي بنا وذكر الحديث وقال فيه وكان يصلي بنا العشاء لا يبالي أن يؤخرها إلى ثلث الليل أخرجه مسلم في الصحيح عن عبيد بن معاذ عن','338','1','0');
INSERT INTO [t6227] VALUES (377,'في المواقيت وفيه فأمره يعني من الغد فأقام العشاء حين ذهب ثلث الليل وفي آخره قال وقت صلاتكم ما بين ما أريتكم وعنده أيضا حديث أبي موسى في المواقيت عن رسول الله','339','1','0');
INSERT INTO [t6227] VALUES (378,'عند أبي داود أمني جبريل عليه السلام عند البيت مرتين وقال فيه وصلى بي العشاء إلى ثلث الليل وصلى بي الفجر فأسفر ثم التفت إلي فقال يا محمد هذا وقت الأنبياء من قبلك الوقت ما بين هذين الوقتين وروى البخاري في الصحيح من حديث عائشة رضي','339','1','0');
INSERT INTO [t6227] VALUES (379,'خاتما قال نعم أخر الصلاة صلاة العشاء ذات ليلة إلى شطر الليل فلما صلى أقبل علينا بوجهه فقال الناس قد صلوا ورقدوا وأنتم لن تزالوا في صلاة منذ انتظرتم الصلاة وكأني أنظر إلى وبيض خاتمه اتفقا على صحته معنى ولفظا وفي حديث عبد الله بن','340','1','0');
INSERT INTO [t6227] VALUES (380,'يصلي فذكر الوقت بتأخير العشاء إلى ثلث الليل ثم قال إلى شطر الليل وعند مسلم','340','1','0');
INSERT INTO [t6227] VALUES (381,'فقال كان لا يبالي بعد تأخيرها يعني العشاء إلى نصف الليل الحديث وروي عن المفضل بن عاصم أخبرنا داود بن أبي هند عن أبي نضرة عن أبي سعيد قال أخر رسول الله','341','1','0');
INSERT INTO [t6227] VALUES (382,'على أصحابه وهم ينتظرون العشاء فقال الناس صلوا ورقدوا وأنتم تنتظرونها أما أنكم في صلاة ما انتظرتموها ولولا ضعف الضعيف وكبر الكبير','341','1','0');
INSERT INTO [t6227] VALUES (383,'فالمصير إليها أولى وبالله التوفيق وهو أعلم','342','1','0');
INSERT INTO [t6227] VALUES (384,'والأذان لصلاة الصبح صحيح قبل الفجر وقال أبو حنيفة لا يصح ودليلنا ما أخرجه البخاري في الصحيح عن سالم عن أبيه أن','342','1','0');
INSERT INTO [t6227] VALUES (385,'قال إن بلالا ينادي بليل فكلوا واشربوا حتى ينادي ابن أم مكتوم وأخرجه مسلم أيضا في الصحيح عنه عن النبي','343','1','0');
INSERT INTO [t6227] VALUES (386,'لا يغرنكم نداء بلال ولا هذا البياض حتى ينفجر الفجر هكذا وأخرجاه أيضا من حديث ابن مسعود رضي الله عنه قال قال رسول الله','343','1','0');
INSERT INTO [t6227] VALUES (387,'قال إذا سمع أحدكم الأذان والإناء على يده فلا يضعه حتى يقضي حاجته منه والاستدلال بهذا واضح إلا أنه في بعض الروايات وكان المؤذنون يؤذنون إذا بزغ الفجر ويحتمل أنه أراد الأذان الثاني وأراد باليد الأذان الأول وربما استدلوا بما روى','344','1','0');
INSERT INTO [t6227] VALUES (388,'ارجع فناد إن العبد نام فقال هو عندي خطأ لم يتابع حماد بن سلمة على هذا إنما روى أن بلالا كان ينادي','344','1','0');
INSERT INTO [t6227] VALUES (389,'ما حملك على ذلك قال استيقظت وأنا وسنان فظننت أن الفجر قد طلع فأمره النبي','346','1','0');
INSERT INTO [t6227] VALUES (390,'وأمره أن ينادي أن العبد نام فوجد بلال وجدا شديدا قال الدارقطني وهم فيه عامر بن مدرك والصواب عن شعيب بن حرب عن عبد العزيز عن نافع عن مؤذن عمر عن عمر رضي الله عنه من قوله وروي عن أنس بن مالك ولا يصح وروي عن أبي يوسف القاضي عن','347','1','0');
INSERT INTO [t6227] VALUES (391,'حدثنا عثمان وذكر إسنادا عن عبد الوهاب حدثنا سعيد عن قتادة أن بلالا أذن ولم يذكر أنسا والمرسل أصح والله أعلم وروي عن محمد بن إسحق الأسدي حدثنا الربيع بن صبيح عن الحسن عن أنس بن مالك قال أذن بلال قبل الفجر فأمره النبي','348','1','0');
INSERT INTO [t6227] VALUES (392,'أن يرجع إلى مقامه فينادي أن العبد نام ورواه','348','1','0');
INSERT INTO [t6227] VALUES (393,'وهو يتسحر فقال لا تؤذن حتى ترى الفجر وهذا مرسل قال أبو داود شداد مولى عياض لم يدرك بلالا وروى الحسن بن عمارة عن طلحة بن مصرف عن سويد بن غفلة عن بلال قال أمرني رسول الله','349','1','0');
INSERT INTO [t6227] VALUES (394,'وكلاهما ضعيفان وروي عن سفيان عن سليمان التميمي عن أبي عثمان أن النبي','350','1','0');
INSERT INTO [t6227] VALUES (395,'ذكر معاني ناديته بالليل وذلك أولى بالقبول لأنه موصول وهذا مرسل والله أعلم وروي عن إسماعيل بن أبي خالد عن أبي إسحاق عن الأسود قال قالت لي عائشة كان رسول الله','350','1','0');
INSERT INTO [t6227] VALUES (396,'بالليل فقالت كان ينام أول الليل فإذا كان السحر أوتر ثم يأتي فراشه فإن كان له حاجة إلى أهله ألم بهم ثم ينام فإذا سمع النداء وما قالت الأذان وثب وما قالت قام فإن كان جنبا أفاض عليه الماء وما قالت اغتسل وإن لم يكن جنبا توضأ ثم خرج','351','1','0');
INSERT INTO [t6227] VALUES (397,'أنها أخبرته أن رسول الله','352','1','0');
INSERT INTO [t6227] VALUES (398,'مسألة','352','1','0');
INSERT INTO [t6227] VALUES (399,'قال من أدرك ركعة من الصبح قبل أن تطلع الشمس فقد أدرك الصبح ومن أدرك ركعة من العصر قبل أن تغرب الشمس فقد أدرك العصر والله الموفق وهو أعلم','353','1','0');
INSERT INTO [t6227] VALUES (400,'ودرك وقت العصر إذا أوجب العصر أوجب معها الظهر وقال أبو حنيفة لا يجب الظهر مع العصر ودليلنا كون وقت العصر وقتا للظهر في حال العذر فهو وقت لها في حال الضرورة وتلك الأخبار مخرجة في مسألة الجمع بين الصلاتين لعذر السفر والمطر وفي الصحيحين','353','1','0');
INSERT INTO [t6227] VALUES (401,'مسألة','354','1','0');
INSERT INTO [t6227] VALUES (402,'عن الرجل يغمى عليه فيترك الصلاة فذكر عنه أن ليس بشيء من ذلك قضاء إلا أن يغمى عليه في وقت صلاة فيفيق وهو في وقتها فيصليها واستدلوا بما روي عن السدي عن يزيد مولى عمار أن عمار بن ياسر رضي الله عنه أغمي عليه في الظهر والعصر والمغرب والعشاء','356','1','0');
INSERT INTO [t6227] VALUES (403,'مسألة','357','1','0');
INSERT INTO [t6227] VALUES (404,'الأذان الله أكبر الله أكبر أشهد أن لا إله إلا الله أشهد أن لا إله إلا الله أشهد أن محمدا رسول الله أشهد أن محمدا رسول الله مرتين ثم يعود يقول أشهد أن لا إله إلا الله أشهد أن لا إله إلا الله أشهد أن محمدا رسول الله أشهد أن','358','1','0');
INSERT INTO [t6227] VALUES (405,'مسألة','360','1','0');
INSERT INTO [t6227] VALUES (406,'بمكة وهو في قبة حمراء من أدم فخرج بلال فأذن فكنت أتتبع فمه ههنا وههنا قال ثم خرج النبي','360','1','0');
INSERT INTO [t6227] VALUES (407,'وهو في قبة حمراء بالأبطح فخرج إلينا بلال','361','1','0');
INSERT INTO [t6227] VALUES (408,'مسألة','362','1','0');
INSERT INTO [t6227] VALUES (409,'صلى المغرب والعشاء بالمزدلفة جميعا قال ابن أبي ذئب لم ينادني في كل واحدة منهما إلا بإقامة ولم يسبح','362','1','0');
INSERT INTO [t6227] VALUES (410,'وهو أيضا مخرج في الصحيح عند مسلم من حديث الثوري عن سلمة عن سعيد عن ابن عمر رضي الله عنهما أن النبي','363','1','0');
INSERT INTO [t6227] VALUES (411,'في هذا المكان وروي عن عبد الله بن مالك قال رأيت ابن عمر رضي الله عنهما صلى المغرب والعشاء بجمع بأذان وإقامة فقال له مالك بن خالد ما هذا يا أبا عبد الرحمن فقال صليت مع رسول الله','363','1','0');
INSERT INTO [t6227] VALUES (412,'بلال فأمره فأقام الظهر فصلاها فأحسن صلاتها كما كان يصليها في وقتها ثم أقام العصر فصلاها كذلك أيضا قال وذلك قبل أن ينزل صلاة الخوف فرجالا أو ركبانا رواة هذا الحديث كلهم ثقات وقد احتج','364','1','0');
INSERT INTO [t6227] VALUES (413,'بمعناه في هذه الصلوات وروي عن أبي جحيفة قال كان رسول الله','365','1','0');
INSERT INTO [t6227] VALUES (414,'وساق الحديث إلى أن قال ارتحلوا فارتحل النبي','365','1','0');
INSERT INTO [t6227] VALUES (415,'كان في سفر فنام عن الصبح حتى إذا طلعت الشمس فأمر فأذن فصلى ركعتين ثم انتظر حتى استعلت الشمس ثم أمره فأقام بهم وفي صحيح البخاري عن أبي قتادة قال سرينا مع رسول الله','365','1','0');
INSERT INTO [t6227] VALUES (416,'إن الله تعالى قبض أرواحكم حين شاء وردها إليكم ثم قال يا بلال قم فأذن للناس بالصلاة فتوضأ فلما ارتفعت الشمس','365','1','0');
INSERT INTO [t6227] VALUES (417,'في سفر فذكر الحديث في نومهم عن الصلاة وقال ثم أذن بلال فصلى ركعتين قبل الفجر ثم صلى الفجر وروى أبو داود حدثنا موسى بن إسماعيل أخبرنا أبان حدثنا معمر عن الزهري عن سعيد بن المسيب عن أبي هريرة هذا الخبر يعني حديث التعريس قال فقال رسول','366','1','0');
INSERT INTO [t6227] VALUES (418,'كان في سفر فقال من يكلؤنا الليلة لا يرقد عن صلاة الفجر فقال بلال أنا يا رسول الله فاستقبل مطلع الشمس فما أيقظهم إلا حر الشمس فقاموا فتوضئوا فأذن بلال فصلوا الركعتين ثم صلوا الفجر وروي عن علي المديني حدثنا سفيان قال ألقيناه من','367','1','0');
INSERT INTO [t6227] VALUES (419,'في سفر فأراد أن يعرس الحديث قال علي بن المديني فقلت لسفيان إن حماد بن سلمة يقول عن عمرو بن دينار عن نافع بن جبير عن أبيه قال سفيان لم أحفظ إلا عن نافع عن رجل قال محمد بن يحيى صار الحديث واهي عن جبير بن مطعم وروى أبو داود عن','367','1','0');
INSERT INTO [t6227] VALUES (420,'قال تنحوا عن هذا المكان قال ثم أمر بلالا فأذن ثم توضئوا وصلوا ركعتي الفجر ثم أمر بلالا فأقام الصلاة فصلى بهم','367','1','0');
INSERT INTO [t6227] VALUES (421,'في هذا الخبر قال فتوضأ يعني النبي','368','1','0');
INSERT INTO [t6227] VALUES (422,'فركع ركعتين غير عجل ثم قال لبلال أقم الصلاة ثم صلى وهو غير عجل وروي عن ابن عباس رضي الله عنهما قال كنا مع رسول الله','368','1','0');
INSERT INTO [t6227] VALUES (423,'مناديه فنادى كما كان ينادي وصلى ركعتي الفجر كما كان يصلي وصلى الغداة كما كان يصلي وعن عبد الله بن عمرو بن العاص رضي الله عنهما قال لما غزا رسول الله','368','1','0');
INSERT INTO [t6227] VALUES (424,'المزدلفة يجمع بين المغرب والعشاء بأذان واحد وإقامتين أخرجه مسلم في الصحيح عن أبي بكر بن أبي شيبة وإسحق بن إبراهيم أظنه عن حاتم بن إسماعيل عن جعفر بن محمد عن أبيه عن جابر وروى أبو داود قال حدثنا عبد الله بن مسلمة حدثنا سليمان بن','369','1','0');
INSERT INTO [t6227] VALUES (425,'يوم عرفة الحديث وقال فركبنا حتى جئنا جمعا ثم أذن للصلاة فصلى المغرب وروي عن خزيمة بن ثابت أن رسول الله','370','1','0');
INSERT INTO [t6227] VALUES (426,'بلالا فأذن وأقام فصلينا الظهر ثم أقام فصلينا العصر ثم أقام فصلينا المغرب ثم','370','1','0');
INSERT INTO [t6227] VALUES (427,'قم فناد بالصلاة مخرج في الصحيحين وفي حديث عبد الله بن زيد فقلت أتبيع الناقوس قال وما تصنع به فقلت ندعوا به إلى الصلاة وعن ابن عمر رضي الله عنهما إنما الأذان داع يدعو الناس إلى الصلاة ويحتمل أن يكون لحق الوقت إذ لو كان','372','1','0');
INSERT INTO [t6227] VALUES (428,'وروى أبو داود في مسنده عن عقبة عن ابن عامر رضي الله عنه قول يعجب ربك من راعي غنم في رأس شظية الجبل يؤذن بالصلاة ويصلي فيقول الله انظروا إلى عبدي هذا يؤذن ويقيم الصلاة ويخاف مني قد غفرت لعبدي وأدخلته جنتي واختلاف هذه الروايات','373','1','0');
INSERT INTO [t6227] VALUES (429,'مسألة','374','1','0');
INSERT INTO [t6227] VALUES (430,'مسألة','375','1','0');
INSERT INTO [t6227] VALUES (431,'إن أخا صداء هو أذن ومن أذن فهو يقيم وقد سبق ذكره في مسألة الأذان قبل طلوع الفجر وروى أبو داود حديث عبد الله بن زيد حين أري الأذان في المنام فأتى النبي','375','1','0');
INSERT INTO [t6227] VALUES (432,'مسألة','376','1','0');
INSERT INTO [t6227] VALUES (433,'يا بلال قم فناد بالصلاة وفي رواية عند مسلم فأمره بالقيام لأجل الأذان وروينا عن الحسن بن أبي محمد قال دخلت على أبي','376','1','0');
INSERT INTO [t6227] VALUES (434,'أمر بلالا في سفر فأذن على راحلته ثم نزلوا فصلوا ركعتين ثم أمره فأقام فصلى بهم الصبح وهذا مرسل وروي عن ابن عمر أنه كان يؤذن على راحلته والله أعلم','377','1','0');
INSERT INTO [t6227] VALUES (435,'والإقامة فرادى وقال أبو حنيفة إنها مثنى كالأذان ودليلنا حديث أنس قال أمر بلال أن يشفع الأذان ويوتر الإقامة اتفقنا على صحته وفي رواية عند البخاري أمر بلال أن','377','1','0');
INSERT INTO [t6227] VALUES (436,'لأنه قال ذكروا أن يعلموا وقت الصلاة بشيء فيعرفونه فذكروا أن يضربوا ناقوسا أو ينوروا نارا فأمر بلال أن يشفع الأذان وأن يوتر الإقامة ثم قد رواه أهل الحديث أبو زكريا يحيى بن معين عن عبد الوهاب الثقفي عن أيوب عن أبي قلابة عن أنس أن رسول الله','378','1','0');
INSERT INTO [t6227] VALUES (437,'وفيما ذكرنا كفاية فإن عارضوا ذلك بما روي عن أحمد بن يوسف عن عبد الرزاق عن معمر عن حماد عن إبراهيم عن الأسود قال كان بلال','378','1','0');
INSERT INTO [t6227] VALUES (438,'وأبي بكر رضي الله عنه فإرسال الخبر ظاهر ومنها أن حماد بن أبي سليمان وشريك بن عبد الله النخعي وعمران بن مسلم غير محتج بهم في الصحيح قال البيهقي وقد رواه غيره عن عبد الرزاق أخبرنا الثوري عن','379','1','0');
INSERT INTO [t6227] VALUES (439,'بمنى صوتين صوتين وأقام مثل ذلك قال الحاكم أبو عبد الله هذا حديث وهم فيه زياد بن عبد الله بن إدريس الأودي فإن هذا الحديث بعينه مخرج في الصحيح بهذا الإسناد وليس فيه ذكر الصوتين قال البيهقي زياد بن عبد الله البكائي كثير الوهم قال','381','1','0');
INSERT INTO [t6227] VALUES (440,'أنه كان خرج في غزوة أحد وكان حانت الصلاة صلاة الظهر والعدو وراءه فقال رسول الله','382','1','0');
INSERT INTO [t6227] VALUES (441,'وصحابته والتابعين وأتباعهم علم أن هذه الألفاظ من أول الحديث إلى آخره لا يشبهها وكعب بن عياض لم يسند عن رسول الله','382','1','0');
INSERT INTO [t6227] VALUES (442,'ورأيت بلالا يؤذن بين يدي رسول الله مثنى مثنى ويقيم فرادى','383','1','0');
INSERT INTO [t6227] VALUES (443,'بالناقوس يعمل ليضرب به للناس في الجمع بين الصلاة أطاف بي وأنا نائم رجل يحمل ناقوسا في يده فقلت له يا عبد الله أتبيع الناقوس فقال لي وما تصنع به قلت ندعو به إلى الصلاة قال أفأدلك على ما هو خير لك من ذلك قلت بلى قال تقول','384','1','0');
INSERT INTO [t6227] VALUES (444,'فلله الحمد قال محمد بن يحيى الذهلي ليس في أخبار عبد الله بن زيد في قصة الأذان خبر أصح من هذا يعني حديث محمد بن إسحق عن محمد بن إبراهيم التيمي عن محمد بن عبد الله بن زيد لأن محمدا سمع من أبيه وابن أبي ليلى لم يسمع من عبد الله بن','385','1','0');
INSERT INTO [t6227] VALUES (445,'نعم ما رأيت علمها بلالا فعلمها إياه وروى في ذلك المعنى من بينه الإقامة في أخبار مختلفة اللفظ والإسناد عن عبد الرحمن بن أبي ليلى قال حدثنا أصحاب محمد','385','1','0');
INSERT INTO [t6227] VALUES (446,'لا غيره فأما رواة العراقيون عن عبد الله بن زيد فغير ثابت من جهة النقل وقد خلطوا في أسانيدهم التي رووها عن عبد الله بن زيد في تثنية الأذان والإقامة جميعا وعبد الرحمن بن أبي ليلى لم يسمع من معاذ بن جبل ولا من زيد بن عبد ربه صاحب الأذان','386','1','0');
INSERT INTO [t6227] VALUES (447,'فأخبرته فقال علمهن بلالا قال فتقدمت فأمرني أن أقيم فأقمت قال الحاكم أبو عبد الله هذا الحديث وهنه مبين في إسناده ومتنه فأما إسناده فإن الحفاظ من أصحاب أبي العميس رووه عن أبي العميس عن زيد بن محمد بن عبد الله بن زيد وأما الوهم','387','1','0');
INSERT INTO [t6227] VALUES (448,'فأخبرته كيف رأيت الأذان فقال ألقهن على بلال فإنه أندى صوتا فلما أذن بلال تقدم عبد الله','387','1','0');
INSERT INTO [t6227] VALUES (449,'فأقام والعلة الجامعة لوهن حديث عبد الله بن زيد بن عبد ربه هذا أن عبد الله استشهد يوم أحد كما بلغنا ولا تنفك الرواية عنه من الإرسال أخبرنا أبو عبد الله الحافظ أخبرنا أبو إسماعيل بن محمد وذكر إسنادا إلى عبيد الله بن عمر قال دخلت ابنة','388','1','0');
INSERT INTO [t6227] VALUES (450,'رواية غير حديث الأذان وليس له راو غير المراسيل التي قدمنا ذكرها والآخر عبد الله بن زيد بن عاصم المازني عم عباد بن تميم المازني قد روى عنه عباد ابن تميم وواسع بن حبان ويحيى بن','388','1','0');
INSERT INTO [t6227] VALUES (451,'بلالا به','389','1','0');
INSERT INTO [t6227] VALUES (452,'أمره أن يثني الأذان ويفرد الإقامة وروي عن أبي هريرة قال أمر أبو محذورة أن يشفع','390','1','0');
INSERT INTO [t6227] VALUES (453,'علمه الأذان تسع عشرة كلمة والإقامة سبع عشرة كلمة وذكرها كذلك وهذا الحديث رواه هشام الدستوائي عن عامر الأحول يصف الأذان دون ذكر الإقامة وعدد الكلمات كما تقدم في مسألة الترجيع وهو مخرج في صحيح مسلم فأما حديث همام بن يحيى بهذا اللفظ فلم','391','1','0');
INSERT INTO [t6227] VALUES (454,'معنى ما حكى ابن جريج قال الشافعي رحمه الله سمعته يقيم يقول الله أكبر الله أكبر أشهد أن لا إله إلا الله أشهد أن محمدا رسول الله حي على الصلاة حي على الفلاح قد قامت الصلاة قد قامت الصلاة الله أكبر الله أكبر لا إله إلا الله','392','1','0');
INSERT INTO [t6227] VALUES (455,'بحديث الأذان والترجيع وفيه الصلاة خير من النوم في الأولى من الصبح وقال علمني الإقامة مرتين فذكرها مثنى مثنى في رواية أبي عاصم وفي رواية عبد الرزاق إذا قمت فقلها مرتين قد قامت الصلاة قد قامت الصلاة أسمعت فيحتمل أن يكون الأمر بالتكرار','393','1','0');
INSERT INTO [t6227] VALUES (456,'أبا محذورة الإقامة فرادى ثم اجتماع ولد أبي محذورة عن الإفراد من أذان رسول الله','394','1','0');
INSERT INTO [t6227] VALUES (457,'جدنا أبو محذورة وروي عن يحيى بن يحيى عن أبي الأحوص عن عبد العزيز بن رفيع أخبرني جدي عبد الملك بن أبي محذورة أن أذانه كان مثنى مثنى وإقامته واحدة','394','1','0');
INSERT INTO [t6227] VALUES (458,'أمره أن يؤذن لأهل مكة يقال أن هذا من معضلات شريك فإنه لم يعلم أن عبد العزيز لم يدرك أذان أبي محذورة فإنه ولد بعد ذلك بسنين والصحيح فيه رواية يحيى بن يحيى عن أبي الأحوص وهذا مما لا يخفى إن شاء الله على عالم وبصحة ذلك أخبرنا وذكر إسنادا','395','1','0');
INSERT INTO [t6227] VALUES (459,'وإقامته وهو الله أكبر الله أكبر وذكر الأذان بالترجيع قال والإقامة واحدة واحدة ويقول قد قامت الصلاة مرة واحدة قال وكان إذا لم يؤذن له بلال بالصلاة وينادي أن رسول الله','396','1','0');
INSERT INTO [t6227] VALUES (460,'ويرطن بعضهم إلى بعض فقال سعد بن عائذ فرقيت في عذق فأذنت فاجتمع الناس وكان ذلك أول ما أذن سعد فلما بلغ سعد إلى رسول الله','396','1','0');
INSERT INTO [t6227] VALUES (461,'أصبت يا سعد إذا لم تر بلالا معي فأذن فمسح','396','1','0');
INSERT INTO [t6227] VALUES (462,'رأسه وقال بارك الله فيك يا سعد إذا لم تر بلالا معي فأذن وأذن سعد لرسول الله','397','1','0');
INSERT INTO [t6227] VALUES (463,'أتى بلال إلى أبي بكر فقال يا خليفة رسول الله','397','1','0');
INSERT INTO [t6227] VALUES (464,'يقول أن أفضل عمل المؤمن الجهاد في سبيل الله قال فما تشاء يا بلال فقال أريد أن أربط نفسي في سبيل الله حتى أموت فقال له أبو بكر رضي الله عنه أنشدك الله يا بلال وحقي وحرمتي فقد كبرت سني واقترب أجلي فأقام بلال مع أبي بكر حتى هلك','397','1','0');
INSERT INTO [t6227] VALUES (465,'بقباء قال فدعا عمر سعدا فقال له الأذان إليك وإلى عقبك من بعدك','397','1','0');
INSERT INTO [t6227] VALUES (466,'فقال امش بها بين يدي كما كان يمشي بها بين يدي رسول الله','398','1','0');
INSERT INTO [t6227] VALUES (467,'مثنى مثنى والإقامة مرة مرة غير أن المؤذن إذا قال قد قامت الصلاة قال مرتين وعن أبي المثنى عن ابن عمر أنه كان يأمر المؤذن أن يشفع الأذان ويوتر الإقامة ليعلم المار الأذان من الإقامة أبو المثنى هو مسلم بن مهران القرشي مؤذن مسجد الجامع','398','1','0');
INSERT INTO [t6227] VALUES (468,'مثنى مثنى والإقامة واحدة واحدة وروي عن علي رضي الله عنه الإقامة مثنى مثنى وروى إفراد الإقامة عن جماعة من الصحابة رضي الله عنهم منهم عبد الله بن عمر روي أنه مر على مؤذن فقال أوتر الإقامة ومنهم عبد الله بن عباس روي عنه أن أذانه كان','399','1','0');
INSERT INTO [t6227] VALUES (469,'مسألة','401','1','0');
INSERT INTO [t6227] VALUES (470,'يدعوه إلى صلاة الغداة فقيل له أن النبي','402','1','0');
INSERT INTO [t6227] VALUES (471,'قال حفص فحدثني أهلي أن بلالا أتى رسول الله','402','1','0');
INSERT INTO [t6227] VALUES (472,'الأذان حرفا حرفا فذكره قال وكان يقول في الفجر الصلاة خير من النوم وذكر عن ابن سيرين عن أنس قال من السنة إذا قال المؤذن في أذان الفجر حي على الفلاح قال الصلاة خير من النوم والله أعلم','403','1','0');
INSERT INTO [t6227] VALUES (473,'وموضع التثويب قبل الفراغ من الأذان وقال أبو حنيفة في الروايتين عنه بين الأذان والإقامة ودليلنا من الخبر ما تقدم آنفا وروي عن ابن عمر رضي الله عنهما قال كان في الأذان الأول بعد الفلاح الصلاة خير من النوم الصلاة خير من النوم وما روي','403','1','0');
INSERT INTO [t6227] VALUES (474,'مسألة','404','1','0');
INSERT INTO [t6227] VALUES (475,'أي العمل أفضل قال الصلاة في أول وقتها قلت ثم أي قال الجهاد في سبيل الله قلت ثم أي قال بر الوالدين تابعه محمد بن بشار بندار عن عثمان بن عمر في هذه اللفظة وهو صحيح على شرط البخاري ومسلم لأن رواته متفق على عدالتهم','405','1','0');
INSERT INTO [t6227] VALUES (476,'أي العمل أحب إلى الله قال الصلاة لوقتها قلت ثم أي قال بر الوالدين قلت ثم أي قال الجهاد في سبيل الله ورواية عثمان بن عمر عن الحسن ابن مغول عن الوليد بن العيزار مقبولة فقد اتفقا على الاحتجاج به وهو ممن لا يشك فيه','406','1','0');
INSERT INTO [t6227] VALUES (477,'أي العمل أفضل قال الصلاة في أول وقتها وروي عن أبي هريرة عن النبي','406','1','0');
INSERT INTO [t6227] VALUES (478,'الصلاة لوقتها الآخر حتى قبضه الله عز وجل قال الحاكم أبو عبد الله هذا حديث صحيح على شرط الشيخين وروي عن علي رضي الله عنه أن رسول الله','407','1','0');
INSERT INTO [t6227] VALUES (479,'أول الوقت رضوان الله وآخر الوقت عفو الله قال ابن عدي هذا من الأحاديث التي يحدث بها بقية عن المجهولين لأن عبد الله مولى عثمان وعبد العزيز لا يعرفان وروي عن نافع مولى يوسف السلمي عن عطاء عن ابن عباس رضي الله عنهما','407','1','0');
INSERT INTO [t6227] VALUES (480,'أول الوقت رضوان الله وآخره عفو الله نافع هذا أبو هرمز ضعفه ابن معين وابن حنبل وغيرهما وروي عن يعقوب بن الوليد عن عبد الله بن عمر العمري عن نافع عن ابن عمر قال قال رسول الله','408','1','0');
INSERT INTO [t6227] VALUES (481,'من وجه يصح ولا عن أحد من الصحابة إنما الرواية فيه عن أبي جعفر محمد بن علي','408','1','0');
INSERT INTO [t6227] VALUES (482,'أول الوقت رضوان الله وأوسط الوقت رحمة الله وآخر الوقت عفو الله قال البيهقي إبراهيم بن زكريا هذا هو العجلي الضرير يكنى أبا إسحق حدث عن الثقات بالبواطيل قاله لنا الماليني عن أبي أحمد بن عدي وإبراهيم بن أبي محذورة هو إبراهيم بن','409','1','0');
INSERT INTO [t6227] VALUES (483,'أول الوقت رضوان الله وآخره عفو الله وإسناده فيما أظن أصح ما روي في هذا الباب والله أعلم وروي عن أبي هريرة عن النبي','410','1','0');
INSERT INTO [t6227] VALUES (484,'ليصلي الصبح فينصرفن النساء متلفعات بمروطهن ما يعرفن من الغلس وروي بإسناد رواته ثقات لكن هذا المتن به غريب عن أم سلمة كن النساء يشهدن الصبح مع رسول الله','410','1','0');
INSERT INTO [t6227] VALUES (485,'وزيد بن ثابت تسحرا فلما فرغا من سحورهما قام نبي الله','411','1','0');
INSERT INTO [t6227] VALUES (486,'واتفقا على صحة حديث جابر رضي الله عنه كان رسول الله','411','1','0');
INSERT INTO [t6227] VALUES (487,'الصبح بغلس ثم صلى مرة أخرى وأسفر بها ثم كانت صلاته بعد ذلك بغلس حتى مات ثم لم يعد إلى أن يسفر وروي عن الأوزاعي عن نهيك بن يريم قال حدثني مغيث بن سمي قال صليت مع ابن الزبير صلاة الفجر فصلى بغلس وكان يسفر بها فلما سلم قلت لعبد','412','1','0');
INSERT INTO [t6227] VALUES (488,'في ركب الحي فصلى بنا صلاة الصبح فجعلت أنظر إلى الذي إلى جنبي فما أكاد أعرفه أي من الغلس وربما استدلوا بما روي عن محمود بن','412','1','0');
INSERT INTO [t6227] VALUES (489,'أسفروا بصلاة الفجر فإنه أعظم للأجر أو أعظم لأجركم وفي رواية أسفروا بالصبح فإن ذلك أعظم لأجوركم أو قال للأجر وليس بمخرج في الصحيح ورواه أبو داود في السنن فقال أصبحوا بالصبح وفي إدارة رواته لألفاظه كالدلالة على أنه','413','1','0');
INSERT INTO [t6227] VALUES (490,'قال أصبحوا بالصبح وروي عن زيد عن','413','1','0');
INSERT INTO [t6227] VALUES (491,'قال وروي عن فليح بن سليمان عن عاصم بن عمر عن أبيه عن جده عن النبي','414','1','0');
INSERT INTO [t6227] VALUES (492,'أسفروا بصلاة الصبح حتى يرى القوم مواقع نبلهم وقيل عن هرير بن عبد الرحمن بن رافع عن جده وقيل عن رفاعة بن هرير بن عبد الرحمن بن رافع حدثني أبي عن جدي عن أبيه أن النبي','414','1','0');
INSERT INTO [t6227] VALUES (493,'قال أسفروا بالصبح أجر لكم وأيوب بن سيار ضعيف لا يحتج به قال ابن معين ليس بشيء وروي عن أبي الزاهرية عن أبي الدرداء عن النبي','415','1','0');
INSERT INTO [t6227] VALUES (494,'صلى الصلاة إلا لميقاتها إلا هاتين الصلاتين صلى المغرب والعشاء بمزدلفة جميعا وصلى الفجر قبل ميقاتها فقط فالمراد به والله أعلم غلس بها غاية التغليس يومئذ وقد كان','416','1','0');
INSERT INTO [t6227] VALUES (495,'كان يصلي صلاة العصر والشمس مرتفعة حية فيذهب الذاهب إلى العوالي فيأتيها والشمس مرتفعة وروى أبو داود عن يوسف بن موسى حدثنا جرير عن منصور','416','1','0');
INSERT INTO [t6227] VALUES (496,'صلاة العصر ثم ننحر الجزور فيقسم عشر قسم ثم يطبخ فنأكل لحما نضيجا قبل أن تغيب الشمس واتفقا على حديث عائشة رضي الله عنها أن رسول الله','417','1','0');
INSERT INTO [t6227] VALUES (497,'العصر فلما انصرف أتاه رجل من بني سلمة فقال يا رسول الله إنا نريد نحر جزور لنا ونحن نحب أن تحضرها قال نعم فانطلق وانطلقنا معه فوجدنا الجزور لم تنحر فنحرت ثم قطعت ثم طبخ منها ثم أكلنا قبل أن تغيب الشمس وفي صحيح مسلم عن','417','1','0');
INSERT INTO [t6227] VALUES (498,'التي كنا نصلي معه وعند مسلم عن العلاء بن عبد الرحمن أنه دخل على أنس بن مالك في داره بالبصرة حين انصرف من الظهر قال وداره بجنب المسجد قال فلما دخلنا عليه قال أصليتم العصر قلنا إنما انصرفنا الساعة من الظهر قال فصلوا العصر','418','1','0');
INSERT INTO [t6227] VALUES (499,'أن رسول الله','420','1','0');
INSERT INTO [t6227] VALUES (500,'يصلي الظهر بالهاجرة والعصر والشمس بيضاء نقية والمغرب إذا وجبت الشمس والعشاء أحيانا وأحيانا كان إذا رآهم قد اجتمعوا عجل وإذا رآهم أبطأوا أخر والصبح كان النبي','422','1','0');
INSERT INTO [t6227] VALUES (501,'كان يعجل بها وأن التأخير كان لاجتماع الناس لأنه أفضل وحديث ابن مسعود رضي الله عنه المتقدم سؤاله رسول الله','422','1','0');
INSERT INTO [t6227] VALUES (502,'يصليها لسقوط القمر الثالثة','422','1','0');
INSERT INTO [t6227] VALUES (503,'ذات ليلة بالعشاء حتى رقد الناس واستيقظوا ورقدوا واستيقظوا فقام عمر بن الخطاب فقال الصلاة فخرج نبي الله','423','1','0');
INSERT INTO [t6227] VALUES (504,'ذات ليلة ذهب عامة الليل حتى نام أهل المسجد ثم خرج فصلى وقال إنه لوقتها لولا أن أشق على أمتي وعنده أيضا عن ابن عمر قال مكثنا ليلة حتى ذهب ثلث الليل أو نصفه قال حين خرج يعني النبي','423','1','0');
INSERT INTO [t6227] VALUES (505,'قال لولا أن أشق على أمتي لأمرتهم بتأخير العشاء والسواك عند كل صلاة وعند أبي داود عن أبي سعيد رضي الله عنه قال صلينا مع رسول الله','423','1','0');
INSERT INTO [t6227] VALUES (506,'يصلي المكتوبة فذكر الحديث وفيه وكان يستحب أن يؤخر من صلاة العشاء التي تدعونها العتمة قال وكان يكره النوم قبلها والحديث بعدها وعند مسلم عن جابر بن سمرة رضي الله عنه قال كان رسول الله','424','1','0');
INSERT INTO [t6227] VALUES (507,'صلاة العشاء ثمان ليال فقال أبو بكر رضي الله عنه لو عجلت هذه الصلاة كان أمثل لقيامنا من الليل ففعل والله أعلم','424','1','0');
INSERT INTO [t6227] VALUES (508,'الجزء ٢','426','0','0');
INSERT INTO [t6227] VALUES (509,'مسألة','426','508','0');
INSERT INTO [t6227] VALUES (510,'خمس صلوات في اليوم والليلة قال هل علي غيرها قال لا إلا أن تطوع وذكر له رسول الله','426','508','0');
INSERT INTO [t6227] VALUES (511,'أفلح إن صدق وعند أبي داود عن القعنبي عن مالك وفي موطأ مالك عن يحيى بن سعيد عن محمد بن يحيى بن حبان عن ابن محيريز أن رجلا من بني كنانة يدعى المخدجي سمع رجلا بالشام أبا محمد يقول إن الوتر واجب قال المخدجي فرحت إلى عبادة بن الصامت','426','508','0');
INSERT INTO [t6227] VALUES (512,'خمس صلوات كتبهن الله على العباد فمن جاء بهن لم يضيع منهن شيئا استخفافا بحقهن كان له عند الله عهدا أن يدخله الجنة ومن لم يأت بهن فليس له عند الله عهد إن شاء عذبه وإن شاء أدخله الجنة سمعت محمد بن إبراهيم يقول أبو محمد الذي في','427','508','0');
INSERT INTO [t6227] VALUES (513,'إن الله وتر يحب الوتر فأوتروا يا أهل القرآن تابعه الحارث عن علي رضي الله عنه وعاصم بن ضمره ليس بساقط فيما لا يخالف الثقات وروى أبو جناب عن عكرمة عن ابن عباس أن رسول الله','428','508','0');
INSERT INTO [t6227] VALUES (514,'والمسلمون من بعده وليس بواجب وروى ابن عدي عن يعقوب بن عبد الله عن عيسى بن جارية عن جابر قال صلى بنا رسول الله','429','508','0');
INSERT INTO [t6227] VALUES (515,'فقلنا يا رسول الله اجتمعنا في المسجد ورجونا أن تخرج إلينا فقال إني كرهت أن يكتب عليكم الوتر ورواه غيره عن يعقوب عنه بمعناه وقال إني خفت إن خرجت أن يكتب عليكم الوتر أخرجه ابن خزيمة في كتابه من وجهين آخرين عن يعقوب القمي وقد صح','429','508','0');
INSERT INTO [t6227] VALUES (516,'فرواه الأوزاعي عن الزهري مرفوعا وتابعه على ذلك يونس بن يزيد من رواية حرملة عن ابن وهب عنه','430','508','0');
INSERT INTO [t6227] VALUES (517,'الوتر حق فمن لم يوتر فليس منا أبو المنيب العتكي لا يحتج بحديثه قال البخاري رحمه الله عبيد الله بن عبد الله أبو المنيب العتكي المروزي سمع ابن يزيد وعكرمة روى عنه زيد ابن الحباب وعلي بن الحسن عنده مناكير قال أبو قدامة أراد ابن','434','508','0');
INSERT INTO [t6227] VALUES (518,'أن الله زادكم صلاة وهي الوتر أحمد هذا هو ابن محمد بن مصعب بن بشر بن فضالة من أهل مرو وكان ممن يضع المتون للآثار ويقلب الأسانيد للأخبار ولعله قد قلب على الثقات أكثر من عشرة آلاف حديث قاله أبو حاتم في كتاب المجروحين وضعف أمره وروى','436','508','0');
INSERT INTO [t6227] VALUES (519,'إن الله زادكم صلاة إلى صلاتكم وهو الوتر قال أبو حاتم حماد بن قيراط من أهل نيسابور يقلب الأسانيد عن الثقات ويجئ عن الأثبات بالطامات لا يجوز الاحتجاج به وكان أبو زرعة الرازي يمرض القول فيه وهو الذي روى عن عبيد الله بن عمر عن نافع','437','508','0');
INSERT INTO [t6227] VALUES (520,'ولا من حديث ابن عمر وخارجه بن مصعب أيضا ليس بشيء جرحه','437','508','0');
INSERT INTO [t6227] VALUES (521,'إن الله زادكم صلاة هي خير لكم من الدنيا وما فيها وهي الوتر وهذا بهذا الإسناد باطل لم يحدث به عن مكي بن إبراهيم غير محمود بن محارب هذا وهو بنيسابور ولعله غلط في إسناده إن لم يعتمده أو غلط عليه محمد بن عبد الله بن المبارك والله أعلم','438','508','0');
INSERT INTO [t6227] VALUES (522,'بمعناه وزاد فقال أعرابي ما تقول قال ليس لك ولا لأصحابك وفي رواية غيره عنه فقال رسول الله','439','508','0');
INSERT INTO [t6227] VALUES (523,'مسألة','439','508','0');
INSERT INTO [t6227] VALUES (524,'لما دخل البيت دعا في نواحيه كلها ولم يصل فيه حتى خرج منه فلما خرج ركع ركعتين في قبل الكعبة وقال هذه القبلة وروي بإسناد ضعيف عن أبي هريرة مرفوعا ما بين المشرق والمغرب قبلة وروي عن أبي جعفر الرازي عن محمد بن عمرو عن أبي سلمة عن أبي','440','508','0');
INSERT INTO [t6227] VALUES (525,'مرسلا وروي عن علي وابن عباس رضي الله عنهم من قولهما والمراد به والله أعلم أهل المدينة ومن كانت قبلته على سبت أهل المدينة مما بين المشرق والمغرب يطلب قبلتهم ثم يطلب عينها فقد أخبرنا أبو عبد الله وذكر إسنادا عن نافع عن ابن عمر عن عمر مرفوعا','442','508','0');
INSERT INTO [t6227] VALUES (526,'مسألة','443','508','0');
INSERT INTO [t6227] VALUES (527,'في مسير أو سير فأصابنا غيم فتحرينا فاختلفنا في القبلة وصلى كل واحد منا على حدة فجعل كل واحد منا يخط بين يديه لنعلم أمكنتنا التي صلينا فيها فلما أصبحنا مطرنا فإذا نحن قد صلينا على غير القبلة فذكرنا ذلك للنبي','443','508','0');
INSERT INTO [t6227] VALUES (528,'سرية كنت فيها فأصابتنا ظلمة فلم نعرف القبلة فقال طائفة منا القبلة هاهنا فصلوا وقال بعضنا القبلة هاهنا وخطوا خطا فلما طلعت الشمس أصبحت تلك الخطوط لغير القبلة فقدمنا من سفرنا فأتينا النبي','445','508','0');
INSERT INTO [t6227] VALUES (529,'في سفر في ليلة مظلمة فلم ندر كيف القبلة فصلى كل رجل منا على خياله قال فلما أصبحنا ذكرنا ذلك للنبي','446','508','0');
INSERT INTO [t6227] VALUES (530,'يصلي وهو مقبل من مكة إلى المدينة على راحلته حيث كان وجهه قال وفيه نزلت فأينما تولوا فثم وجه الله وروي عن أحمد بن عبيد الله بن الحسن العنبري قال وجدت في كتاب أبي حدثنا عبد الملك العرزمي عن سعيد بن جبير عن ابن عمر رضي الله عنهما','447','508','0');
INSERT INTO [t6227] VALUES (531,'والمراهق إذا افتتح صلاة الوقت بشرط صحتها من طهارة الفعل والبدن واللباس والعقد بالنية ثم بلغ في خلالها فإنه يتم ما عقده ولا إعادة عليه وكذلك أن تحلل من هذه الصلاة التي عقدها على استكمال شرائطها ثم بلغ والوقت باق فلا إعادة عليه وقال أبو حنيفة','447','508','0');
INSERT INTO [t6227] VALUES (532,'مروا الصبيان لسبع سنين واضربوهم عليها في عشر وفرقوا بينهم في المضاجع والله أعلم','448','508','0');
INSERT INTO [t6227] VALUES (533,'ولا تنعقد الصلاة إلا بقول الله أكبر والله أكبر وقال أبو حنيفة تنعقد بجميع الأسماء والصفات المناسبة للتكبير وبالاسم دون الصفة دليلنا حديث أبي هريرة في الصحيحين في شأن الرجل الذي صلى فقال له رسول الله','448','508','0');
INSERT INTO [t6227] VALUES (534,'يستفتح بالتكبير وقد قال','449','508','0');
INSERT INTO [t6227] VALUES (535,'إذ جاء رجل فدخل المسجد فصلى فلما قضى صلاته جاء فسلم على رسول الله','449','508','0');
INSERT INTO [t6227] VALUES (536,'ارجع فصل فإنك لم تصل قال فرجع فصلى فجعلنا نرمق صلاته لا ندري ما يعيب منها فلما قضى صلاته جاء فسلم على رسول الله','449','508','0');
INSERT INTO [t6227] VALUES (537,'ارجع فصل فإنك لم تصل قال فرجع فصلى فجعلنا نرمق صلاته لا ندري ما يعيب منها فلما قضى صلاته جاء فسلم على رسول الله','449','508','0');
INSERT INTO [t6227] VALUES (538,'ارجع فصل فإنك لم تصل وذكر ذلك إما مرتين وإما ثلاثة فقال الرجل ما أدري ما عبت من','449','508','0');
INSERT INTO [t6227] VALUES (539,'إنها لا تتم صلاة أحدكم حتى يسبغ الوضوء كما أمره الله تبارك وتعالى يغسل وجهه ويديه إلى المرفقين ويمسح برأسه ورجليه إلى الكعبين ثم يكبر ويحمد الله ويمجده ويقرأ من القرآن ما أذن الله له فيه ثم يكبر فيركع ويضع كفيه على ركبتيه حتى تطمئن','450','508','0');
INSERT INTO [t6227] VALUES (540,'قال مفتاح الصلاة الطهور وإحرامها التكبير وتحليلها التسليم وروي عن أبي سعيد رضي الله عنه قال قال رسول الله','451','508','0');
INSERT INTO [t6227] VALUES (541,'مسألة','451','508','0');
INSERT INTO [t6227] VALUES (542,'إذا افتتح الصلاة ورفع يديه حتى تحاذي منكبيه وإذا أراد أن يركع وبعد ما يرفع من الركوع ولا يرفع بعد السجدتين ومعنى ذلك في الرفع عند الافتتاح رواه علي بن أبي طالب وأبو حميد الساعدي رضي الله عنهما عن رسول الله','452','508','0');
INSERT INTO [t6227] VALUES (543,'وما روي عن علي رضي الله عنه كلهم عن النبي','453','508','0');
INSERT INTO [t6227] VALUES (544,'كان إذا كبر رفع يديه حتى يحاذي بهما أذنيه وإذا رفع رأسه من','453','508','0');
INSERT INTO [t6227] VALUES (545,'كان إذا كبر رفع يديه حتى يجعلهما قريبا من أذنيه وإذا ركع صنع مثل ذلك وإذا رفع رأسه من الركوع حاذتا فروع أذنيه والله أعلم','454','508','0');
INSERT INTO [t6227] VALUES (546,'والسنة أن يضع اليمنى على اليسرى تحت صدره وفوق سرته وقال أبو حنيفة يضعهما تحت السرة وروي عن مؤمل عن سفيان عن عاصم بن كليب عن أبيه عن وائل قال صليت مع رسول الله','454','508','0');
INSERT INTO [t6227] VALUES (547,'مسألة','456','508','0');
INSERT INTO [t6227] VALUES (548,'إذا استفتح الصلاة كبر ثم قال وجهت وجهي للذي فطر السموات والأرض حنيفا وما أنا من المشركين إن صلاتي ونسكي ومحياي ومماتي لله رب العالمين لا شريك له وبذلك أمرت أنا أول المسلمين اللهم أنت الملك لا إله إلا أنت أنت ربي وأنا عبدك ظلمت نفسي','456','508','0');
INSERT INTO [t6227] VALUES (549,'إذا استفتح الصلاة قال سبحانك اللهم وبحمدك وتبارك اسمك وتعالى','457','508','0');
INSERT INTO [t6227] VALUES (550,'إذا افتتح الصلاة رفع يديه حذو منكبيه ثم يقول سبحانك اللهم وبحمدك وتبارك اسمك وتعالى جدك ولا إله غيرك قال الحاكم أبو عبد الله كان مالك بن أنس لا يرضى حارثة بن محمد وقال الدوري سمعت يحيى بن معين يقول حارثة بن أبي الرجال ضعيف وروي عن','458','508','0');
INSERT INTO [t6227] VALUES (551,'إذا قام من الليل كبر ثم يقول سبحانك اللهم وبحمدك وتبارك اسمك وتعالى جدك ولا إله غيرك ثم يقول لا إله إلا الله ثلاثا ثم يقول الله أكبر كبيرا ثلاثا أعوذ بالله السميع العليم من الشيطان الرجيم من همزه ونفخه ثم يقرأ قال أبو داود هذا الحديث','459','508','0');
INSERT INTO [t6227] VALUES (552,'وروي عن أنس بن مالك مرفوعا وليس بالقوي ورواه الحسين بن علي العجلي عن محمد بن الصلت عن أبي خالد الأحمر عن حميد عن أنس قال كان رسول الله','460','508','0');
INSERT INTO [t6227] VALUES (553,'وليس بالصحيح والصواب ما ذكرنا والله أعلم','461','508','0');
INSERT INTO [t6227] VALUES (554,'مسألة','462','508','0');
INSERT INTO [t6227] VALUES (555,'أنزلت علي آنفا سورة وذكر أنه قرأ بسم الله الرحمن الرحيم إنا أعطيناك الكوثر حتى ختمها وفي صحيح البخاري عن قتادة قال سئل أنس بن مالك كيف كانت قراءة رسول الله','462','508','0');
INSERT INTO [t6227] VALUES (556,'لا يعلم انقضاء السورة حتى تنزل بسم الله الرحمن الرحيم هذا حديث صحيح وروي عن سعيد بن جبير عن ابن عباس أن جبريل عليه السلام كان إذا جاء إلى النبي','463','508','0');
INSERT INTO [t6227] VALUES (557,'أنه قد ختم السورة وافتتح أخرى وروي عن عروة ابن الزبير عن نيار بن مكرم الأسلمي صاحب رسول الله','463','508','0');
INSERT INTO [t6227] VALUES (558,'يقرأ بسم الله الرحمن الرحيم الم غلبت الروم في أدنى الأرض وهم من بعد غلبهم سيغلبون وذكر الحديث','463','508','0');
INSERT INTO [t6227] VALUES (559,'قال فضل الله قريشا بسبع خلال أني فيهم وأن النبوة فيهم والحجابة والسقاية فيهم وأن الله نصرهم على الفيل وأنهم عبدوا الله عشر سنين لا يعبده غيرهم وأن الله أنزل فيهم سورة من القرآن ثم تلاها رسول الله','464','508','0');
INSERT INTO [t6227] VALUES (560,'يقطع قراءته بسم الله الرحمن الرحيم الحمد لله رب العالمين','464','508','0');
INSERT INTO [t6227] VALUES (561,'قرأ في الصلاة بسم الله الرحمن الرحيم فعدها آية الحمد لله رب العالمين آيتين الرحمن الرحيم ثلاث آيات مالك يوم الدين أربع آيات وقال هكذا إياك نعبد وإياك نستعين وجمع خمس أصابعه وروي بإسناد كلهم ثقات مجمع على عدالتهم محتج بهم في الصحيح عن','465','508','0');
INSERT INTO [t6227] VALUES (562,'أنه كان يقول الحمد لله رب العالمين سبع آيات إحداهن بسم الله الرحمن الرحيم وهي السبع المثاني والقرآن العظيم وهي أم القرآن وهي فاتحة الكتاب وعنه قال كان رسول الله','466','508','0');
INSERT INTO [t6227] VALUES (563,'يجهر ببسم الله الرحمن الرحيم قال الحاكم أبو عبد الله رواة هذا الحديث عن','466','508','0');
INSERT INTO [t6227] VALUES (564,'قال الحاكم أبو عبد الله رواة هذا الحديث عن آخرهم ثقات وروي عن ابن عباس رضي الله عنهما قال كان رسول الله','467','508','0');
INSERT INTO [t6227] VALUES (565,'لم يزل يجهر ببسم الله الرحمن الرحيم حتى قبض صلوات الله عليه وعن عائشة رضي الله عنها أن رسول الله','467','508','0');
INSERT INTO [t6227] VALUES (566,'سكتتان سكتة إذا قرأ بسم الله الرحمن الرحيم وسكتة إذا فرغ من القراءة فأنكر ذلك عمران بن حصين','467','508','0');
INSERT INTO [t6227] VALUES (567,'كيف تقرأ إذا افتتحت الصلاة قال قلت أقول الحمد لله رب','468','508','0');
INSERT INTO [t6227] VALUES (568,'لا أخرج من المسجد حتى أخبرك بآية أو سورة لم تنزل على نبي بعد سليمان غيري قال فمشى وتبعته حتى انتهى إلى باب المسجد فأخرج إحدى رجليه من أسكفة المسجد وبقيت الأخرى في المسجد فقلت بيني وبين نفسي بشيء قال فأقبل علي بوجهه قال بأي شيء','469','508','0');
INSERT INTO [t6227] VALUES (569,'يجهر ببسم الله الرحمن الرحيم في السورتين جميعا وروي عن ثوير عن أبيه عن علي رضي الله عنه أنه كان يجهر ببسم الله الرحمن الرحيم وروي ذلك من أوجه من حديث أهل البيت عن علي','469','508','0');
INSERT INTO [t6227] VALUES (570,'أمني جبريل عند الكعبة فجهر ببسم الله الرحمن الرحيم وروي عن ابن عمر قال صليت خلف النبي','470','508','0');
INSERT INTO [t6227] VALUES (571,'من ترك بسم الله الرحمن الرحيم فقد ترك آية من كتاب الله عز وجل وقد عد فيما عد علي من أم الكتاب بسم الله الرحمن الرحيم وروي عن موسى بن أبي حبيب الطائفي عن الحكم بن عمير وكان بدريا قال صليت خلف النبي','470','508','0');
INSERT INTO [t6227] VALUES (572,'فجهر في الصلاة ببسم الله الرحمن الرحيم وربما استدل أصحابنا من طريق الآثار بما روى الشافعي عن عبد المجيد عن ابن جريج أخبرني عبد الله بن عثمان بن خيثمة أن أبا بكر بن حفص بن عمر أخبره أن أنس بن مالك رضي الله عنه قال صلى معاوية','471','508','0');
INSERT INTO [t6227] VALUES (573,'على الجهر ببسم الله الرحمن الرحيم وعلى أن يقضوا ما فاتهم من صلاة الليل والنهار وعلى أن يقولوا في أبي بكر وعمر أحسن القول وعن علي بن موسى الرضا سئل الصادق عن الجهر بالتسمية فقال أحق ما جهر به الآية التي ذكرها الله تعالى وإذا','475','508','0');
INSERT INTO [t6227] VALUES (574,'وأبو بكر وعمر يفتتحون القراءة بالحمد لله رب العالمين قال الشافعي يعني أنهم يبدؤن بقراءة أم القرآن قبل أن يقرأ ما بعدها والله أعلم لا يعني أنهم يتركون بسم الله الرحمن الرحيم الآية السابعة فإن تركها أو بعضها لم تجزئه الركعة التي تركها فيها','476','508','0');
INSERT INTO [t6227] VALUES (575,'ومع أبي بكر وعمر وعثمان رضي الله عنهم فلم أسمع أحد منهم يقرأ بسم الله الرحمن الرحيم انفرد مسلم بإخراجهما ولم يخرجهما البخاري وإنما اتفقا على المعنى الأول فقط قال الدارقطني عقب حديث غندر هذا وكذلك رواه معاذ بن معاذ وحجاج بن محمد ومحمد','477','508','0');
INSERT INTO [t6227] VALUES (576,'وأبا بكر وعمر وعثمان رضي الله عنهم يفتتحون القراءة','478','508','0');
INSERT INTO [t6227] VALUES (577,'يستفتح بالحمد لله رب العالمين أو ببسم الله الرحمن الرحيم فقال إنك لتسألني عن شيء ما أحفظه وما سألني عنه أحد قبلك قلت أكان رسول الله','480','508','0');
INSERT INTO [t6227] VALUES (578,'وأبا بكر وعمر رضي الله عنهما كانوا يسرون ببسم الله الرحمن الرحيم وهذه الرواية تدل على أنهم كانوا يقرؤونها وهو توافق رواية من رواها عن قتادة في ترك الجهر إذا كان الاختلاف في ترك','480','508','0');
INSERT INTO [t6227] VALUES (579,'يقول الله قسمت الصلاة بيني وبين عبدي نصفين فإذا قال الحمد لله رب العالمين يقول الله حمدني عبدي الحديث قال الحليمي رحمه الله في ابتداء القسمة من قوله الحمد لله دليل يقطع أن بسم الله الرحمن الرحيم ليست الآية الأولى لأنه يجوز أن','481','508','0');
INSERT INTO [t6227] VALUES (580,'عن بسم الله الرحمن الرحيم فقال هو اسم من أسماء الله عز وجل','483','508','0');
INSERT INTO [t6227] VALUES (581,'مسألة','484','508','0');
INSERT INTO [t6227] VALUES (582,'قال إذا أمن الإمام فأمنوا فإنه من وافق تأمينه تأمين الملائكة غفر له ما تقدم من ذنبه قال ابن شهاب وكان رسول الله','484','508','0');
INSERT INTO [t6227] VALUES (583,'إذا قال آمين رفع بها صوته وفي رواية أن النبي','484','508','0');
INSERT INTO [t6227] VALUES (584,'خفض بها صوته وقال في إسناده أخبرني سلمة قال سمعت حجرا أبا العنبس قال سمعت علقمة بن وائل وقد سمعه من وائل أنه صلى مع رسول الله','485','508','0');
INSERT INTO [t6227] VALUES (585,'إذا فرغ من قراءة أم القرآن رفع صوته فقال آمين قال الحاكم أبو عبد الله هذا حديث صحيح وعن أبي داود عنه بمعناه وزاد بيانا فقال قال آمين حتى يسمع من يليه في الصف الأول وفي رواية عنه قال كان النبي','487','508','0');
INSERT INTO [t6227] VALUES (586,'وفيه سمعته وهي في صف النساء وروي في ذلك عن علي قال سمعت رسول الله','487','508','0');
INSERT INTO [t6227] VALUES (587,'كان إذا قرأ ولا الضالين رفع صوته بآمين وعن ابن عمر رضي الله عنهما أنه صلى بهم صلاة المغرب فلما فرغ من قراءة أم القرآن قال آمين ورفع بها صوته وعن نافع عنه أنه كان يؤمن وراء الإمام قال ورأى ذلك من السنة وعند أبي داود عن بلال أنه','488','508','0');
INSERT INTO [t6227] VALUES (588,'إذا أمن الإمام فأمنوا دلالة على أن الإمام يجهر بآمين من خلفه لا نعرف وقت تأمينه إلا بأن يسمع تأمينه ثم بينه ابن شهاب فقال وكان رسول الله','488','508','0');
INSERT INTO [t6227] VALUES (589,'لو لم يكن عندهم وعندنا علم إلا هذا الحديث الذي ذكرنا عن مالك رحمه الله ينبغي أن نستدل بأن النبي','489','508','0');
INSERT INTO [t6227] VALUES (590,'كان يقول آمين يرفع بها صوته ويحكي مده إياها وكان أبو هريرة يقول للإمام لا تسبقني بآمين وكان يؤذن له قال الشافعي أخبرنا مسلم بن خالد عن ابن جريج عن عطاء قال كنت أسمع الأئمة ابن الزبير ومن بعده يقولون آمين ومن خلفهم آمين حتى أن للمسجد','489','508','0');
INSERT INTO [t6227] VALUES (591,'مسألة','490','508','0');
INSERT INTO [t6227] VALUES (592,'في رفع اليدين عند الركوع وبعدما يرفع رأسه من الركوع قال سفيان حفظته عن الزهري كما أنك ها هنا قال علي هذا الحديث عندي حجة على الخلق كل من سمعه فعليه أن يعمل به لأنه في إسناده شيء قال علي لم أزل أعمل به منذ أنا صبي قال أبو سعيد وبه','490','508','0');
INSERT INTO [t6227] VALUES (593,'فقولوا بسنة رسول الله','491','508','0');
INSERT INTO [t6227] VALUES (594,'ورضي عنهم وقد ذكر الشافعي حديث أبي حميد الساعدي وفيه هذه الزيادة ثم قال في آخره وبه نقول وعند البخاري ومسلم عن أبي قلابة أنه رأى مالك بن الحويرث إذا صلى كبر ثم رفع يديه وإذا أراد أن يركع ما يرفع وإذا رفع رأسه من الركوع رفع يديه وحدث','491','508','0');
INSERT INTO [t6227] VALUES (595,'قال الربيع قلت للشافعي رحمه الله ما معنى رفع','491','508','0');
INSERT INTO [t6227] VALUES (596,'في صفة الصلاة وذكر فيه رفع اليدين عند الافتتاح وعند الركوع والرفع منه وعند القيام من الركعتين وفي آخره فقالوا جميعا صدق هكذا كان يصلي رسول الله','492','508','0');
INSERT INTO [t6227] VALUES (597,'منهم أبو قتادة وأبو هريرة ومحمد بن سلمة وأبو أسيد وسهل بن سعد وعن أبي موسى','492','508','0');
INSERT INTO [t6227] VALUES (598,'بأسانيد صحيحة محتج بها وقد سمعت الحاكم أبا عبد الله يقول لا نعلم سنة اتفق على روايتها عن رسول الله','493','508','0');
INSERT INTO [t6227] VALUES (599,'بالجنة ممن بعدهم من أكابر الصحابة على تفرقهم في البلاد غير هذه السنة قال البيهقي وهو كما قال أستاذنا أبو عبد الله رضي الله عنه فقد روى هذه السنة عن أبي بكر وعمر وعثمان وعلي وذكرنا في العشرة رضي الله عنهم ومعاذ بن جبل وزيد بن ثابت وأبي','493','508','0');
INSERT INTO [t6227] VALUES (600,'أنهم كانوا يرفعون أيديهم عند الركوع منهم أبو قتادة وأبو أسيد ومحمد بن سلمة وسهل بن سعد وعبد الله بن عمرو وعبد الله بن الزبير ووائل بن حجر الحضرمي ومالك بن الحويرث وأبو موسى وأبو حميد وروي ذلك عن جماعة لم يذكرهم البخاري وعلي بن أبي طالب رضي','494','508','0');
INSERT INTO [t6227] VALUES (601,'كأنما أيديهم مراوح في صلاتهم إذا ركعوا وإذا رفعوا رؤوسهم وعن البخاري قال ويروى أن عدة من أهل مكة وأهل الحجاز والعراق والشام واليمن أنهم كانوا يرفعون أيديهم عند الركوع وعند رفع الرأس منه منهم سعيد بن جبير وعطاء بن أبي رباح ومجاهد','495','508','0');
INSERT INTO [t6227] VALUES (602,'قلنا نعم فقام فلم يرفع يديه إلا في أول تكبيرة ثم لم يعد وروي عن عبد الله بن المبارك قال لم يثبت عندي حديث عبد الله بن مسعود رضي الله عنه أن رسول الله','496','508','0');
INSERT INTO [t6227] VALUES (603,'وقال الحاكم أبو عبد الله عن هذا الخبر مختصر من أصله وعاصم بن كليب لم يخرج حديثه في الصحيحين وذلك أنه كان يختصر الأخبار يؤديها على المعنى وهذه اللفظة لم تعد غير محفوظة في الخبر يريد والله','496','508','0');
INSERT INTO [t6227] VALUES (604,'في مسألة الحلي السواد ثم ذكر سندا عن علقمة والأسود عن عبد الله رضي الله عنه قال ألا أريكم صلاة رسول الله','497','508','0');
INSERT INTO [t6227] VALUES (605,'فلم يرفع يديه إلا مرة ولم يكن يرد قوله قول من قال رأيت رسول الله','497','508','0');
INSERT INTO [t6227] VALUES (606,'في هذا الحديث حتى لقد روي عنه أنه قال لكأني أنظر إلى اختلاف أصابع رسول الله','497','508','0');
INSERT INTO [t6227] VALUES (607,'وضع اليدين على الركبتين ولما أن سعدا قال كنا نفعله ثم أمرنا بالركب قلنا فكيف وسعك أن تترك خبر عبد الله الصحيح الذي حفظه عن رسول الله','498','508','0');
INSERT INTO [t6227] VALUES (608,'رووا ذلك وأثبتوه منهم وائل بن حجر وأبو حميد وابن عمر وغيرهم وليس يروى عن عبد الله رضي الله عنه أنه أنكر ذلك من فعل رسول الله','498','508','0');
INSERT INTO [t6227] VALUES (609,'ذكر فيه الرفع عند الركوع والرفع منه ذكر خبرا في أوهى مما ذكرنا وذكر حديثا رواه إسحاق بن أبي','498','508','0');
INSERT INTO [t6227] VALUES (610,'وأبي بكر وعمر رضي الله عنهما فلم يرفعوا أيديهم إلا عند افتتاح الصلاة قال الحاكم أبو عبد الله هذا إسناد مقلوب لا نعلم أحدا حدث من أصحاب حماد بن أبي سليمان عنه من المشورين بالأخذ عنه ولو كان محفوظا لبادر بروايته أبو حنيفة وسفيان الثوري','499','508','0');
INSERT INTO [t6227] VALUES (611,'ثم لم يقنعه ذلك إلى أن وصله بذكر أبي بكر وعمر رضي الله عنهما ذكر خبر ثالث قال الشافعي رحمه الله أخبرنا سفيان عن يزيد بن أبي زياد عن عبد الرحمن بن أبي ليلى عن البراء بن عازب قال رأيت رسول الله','500','508','0');
INSERT INTO [t6227] VALUES (612,'وتعارضني بيزيد بن أبي زياد ويزيد رجل ضعيف الحديث وحديثه مخالف للسنة قال فاحمر وجه سفيان فقال الأوزاعي كأنك كرهت ما قلنا قال الثوري نعم قال الأوزاعي قم بنا إلى المقام نلتعن أينا على الحق قال فتبسم الثوري لما رأى الأوزاعي قد احتد','501','508','0');
INSERT INTO [t6227] VALUES (613,'ونحن رافعوا أيدينا في الصلاة فقال','502','508','0');
INSERT INTO [t6227] VALUES (614,'قلنا بأيدينا السلام عليكم فقال رسول الله','503','508','0');
INSERT INTO [t6227] VALUES (615,'والوجه الرابع لوهن هذا الحديث أن شعبة بن الحجاج قال لم يسمع الحكم من مقسم إلا أربعة أحاديث وليس هذا الحديث منها والوجه الخامس أن في جميع هذه الروايات ترفع الأيدي في سبع مواطن وليس في رواية منها لا ترفع الأيدي إلا في سبع مواطن','504','508','0');
INSERT INTO [t6227] VALUES (616,'في الدعاء للصلاة وأمر بها ورفع اليدين في القنوت قال البيهقي ورواه ابن جريج فقال حديث عن مقسم وبذلك لا تثبت الحجة','504','508','0');
INSERT INTO [t6227] VALUES (617,'على هذا ورفع حماد يده حذو المنكبين أو نحو ذلك وهذا مجمل بين في رواية أخرى عن حماد بن سلمة عن بشر عن ابن عمر والله أن رفعكم أيديكم في السماء لبدعة يحلف عليها ثلاثا ما زاد رسول الله','505','508','0');
INSERT INTO [t6227] VALUES (618,'وفعل أمة من أصحاب محمد','505','508','0');
INSERT INTO [t6227] VALUES (619,'يديه فوق صدره في الدعاء قال أبو عبد الله الحاكم فهذا الحسين بن واقد على صدقه وإتقانه قد أتى بالمعنى الذي أشرنا إليه ذكر خبر سابع قال محمد بن إسماعيل البخاري والذي قال أبو بكر بن عياش عن حصين عن مجاهد ما رأيت ابن عمر رافعا يديه في','506','508','0');
INSERT INTO [t6227] VALUES (620,'يرفعهما عند الركوع فليس الظن بعلي رضي الله عنه أنه يختار فعله على فعل النبي','507','508','0');
INSERT INTO [t6227] VALUES (621,'بخلاف هذا كما رواه الناس عن عاصم ذكر خبر تاسع عن عبد الملك بن أبجر عن الزبير بن عدي عن إبراهيم عن الأسود قال رأيت عمر رضي الله عنه يرفع يديه في أول التكبير ثم لم يعد قال الحاكم أبو عبد الله هذه رواية شاذة لا تقوم بها الحجة ولا','508','508','0');
INSERT INTO [t6227] VALUES (622,'خلاف ما روى على حجر قال لا ولكن ذهب إلى أن قال ذلك لو كان روياه وفعلاه قلت وروى إبراهيم هذا عن علي وعبد الله نصا قال لا قلت فخفى عن إبراهيم شيء رواه علي وعبد الله أو فعلاه قال ما أشك في ذلك قلت فتدري لعلهما قد فعلاه فخفي','509','508','0');
INSERT INTO [t6227] VALUES (623,'شيئا فقال عدد من أصحاب النبي','510','508','0');
INSERT INTO [t6227] VALUES (624,'بأن لم يعلم إبراهيم قول علي وعبد الله قال فقال وائل أعرابي قلت أفرأيت قرثع الضبي وقزعه وسهم بن منجاب حين روى إبراهيم عنهم وروى عن','510','508','0');
INSERT INTO [t6227] VALUES (625,'شيئا قط أكثر منهم غير وائل ووائل أهل أن يقبل عنه ويروون عن إبراهيم أنه كان إذا ذكر عنده حديث وائل يقول لعله فعل ذلك مرة ثم تركه وفيما روينا عن عاصم بن كليب عن أبيه عن وائل إبطال هذا القول قال البيهقي ومما يدل على ذلك وذكر خبرا عن','511','508','0');
INSERT INTO [t6227] VALUES (626,'حتى لقي الله عز وجل وربما تعلقوا بخبر يروونه عن ابن عمر على رأيهم وذلك','511','508','0');
INSERT INTO [t6227] VALUES (627,'مسألة','512','508','0');
INSERT INTO [t6227] VALUES (628,'إذا قال سمع الله لمن','512','508','0');
INSERT INTO [t6227] VALUES (629,'إذا قام إلى الصلاة يكبر حين يقوم ثم يكبر حين يركع ثم يقول سمع الله لمن حمده حين يرفع صلبه من الركعة ثم يقول وهو قائم ربنا ولك الحمد ثم يكبر حين يهوي ساجدا ثم يكبر حين يقوم من الثنتين بعد الجلوس أخرجه البخاري ومسلم في الصحيح قال وروي','513','508','0');
INSERT INTO [t6227] VALUES (630,'إذا رفع رأسه من الركوع قال سمع الله لمن حمده اللهم ربنا لك الحمد ملء السموات وملء الأرض وملء ما شئت من شيء بعد أخرجه مسلم في الصحيح وأخرجه أيضا عن أبي سعيد الخدري أن رسول الله','513','508','0');
INSERT INTO [t6227] VALUES (631,'قال إذا قال الإمام سمع الله لمن حمده فقولوا اللهم ربنا لك الحمد فإنه من وافق قوله قول الملائكة غفر له ما تقدم من ذنبه أخرجه البخاري ومسلم في الصحيح ولا حجة لهم فيه لأنه أمر بأن يقول اللهم ربنا لك الحمد ونحن نقوله فأما إذا قال معه','514','508','0');
INSERT INTO [t6227] VALUES (632,'وجلسة الاستراحة بعد السجدة الثانية من الركعة الأولى والثالثة سنة وإذا قام اعتمد بيديه على الأرض وقال أبو حنيفة لا يجلس بل يصير من السجود إلى القيام من غير اعتماد ودليلنا حديث مالك بن الحويرث أنه رأى النبي','514','508','0');
INSERT INTO [t6227] VALUES (633,'يصلي قال أيوب فقلت لأبي قلابة كيف كانت يعني صلاته قال مثل صلاة شيخنا هذا يعني عمرو بن سلمة قال أيوب وكان ذلك الشيخ يتم التكبير وكان إذا رفع رأسه من السجدة الثانية جلس واعتمد على الأرض قال وأما الذي روى خالد بن إياس','515','508','0');
INSERT INTO [t6227] VALUES (634,'وقوله أولى من متابعة غيره وبالله التوفيق','516','508','0');
INSERT INTO [t6227] VALUES (635,'ويشير بالمسبحة في التشهد وقال أبو حنيفة يكره ذلك ودليلنا حديث ابن عمر في صلاة رسول الله','516','508','0');
INSERT INTO [t6227] VALUES (636,'كان إذا جلس في الصلاة وضع يديه على ركبتيه ورفع إصبعه التي تلي الإبهام فدعا بها ويده اليسرى على ركبته باسطها عليها وفي رواية عنه أيضا نحوه وقال وعقد ثلاثا وخمسين ثم يدعو وعنده أيضا عن عبد الله بن الزبير عن رسول الله','517','508','0');
INSERT INTO [t6227] VALUES (637,'أنه كان يشير في الصلاة رواه أنس وجابر وغيرهما عن النبي','518','508','0');
INSERT INTO [t6227] VALUES (638,'مسألة','519','508','0');
INSERT INTO [t6227] VALUES (639,'فذكروا صلاة رسول الله','519','508','0');
INSERT INTO [t6227] VALUES (640,'رأيته إذا كبر جعل يديه حذو منكبيه وإذا ركع أمكن يديه من ركبتيه ثم يقصر ظهره وإذا رفع رأسه استوى حتى تعود كل فقار مكانه فإذا سجد وضع يده غير مفترش ولا قابضها واستقبل بأطراف أصابع رجليه القبلة فإذا جلس في الركعتين جلس على رجله اليسرى وإذا','519','508','0');
INSERT INTO [t6227] VALUES (641,'كما كتبناه قبل هذا وأما حديث عائشة رضي الله عنها في صلاة النبي','520','508','0');
INSERT INTO [t6227] VALUES (642,'ثم جلس فافترش رجله اليسرى فإنه ورد في التشهد الأول بيان ذلك في خبر أبي حميد وعند البخاري في الصحيح عن عبد الله بن عبد الله أنه كان يرى عبد الله بن عمر يتربع في الصلاة إذا جلس ففعلته وأنا يومئذ حديث السن فنهاني عبد الله بن عمر وقال','520','508','0');
INSERT INTO [t6227] VALUES (643,'مسألة','521','508','0');
INSERT INTO [t6227] VALUES (644,'في الظهر والعصر فحزرنا قيامه في الركعتين الأوليين من الظهر قدر قراءة ألم تنزيل السجدة وحزرنا قيامه في الركعتين الأخريين قدر النصف من ذلك وحزرنا قيامه في الركعتين الأوليين من العصر على قدر قيامه في الركعتين الأخريين في الظهر وفي الأخريين من','521','508','0');
INSERT INTO [t6227] VALUES (645,'كان يقرأ في الركعتين الأوليين من الظهر والعصر بفاتحة الكتاب وسورة ويسمعنا الآية أحيانا أخرجه','522','508','0');
INSERT INTO [t6227] VALUES (646,'مسألة','523','508','0');
INSERT INTO [t6227] VALUES (647,'قال لا صلاة لمن لم يقرأ فيها بفاتحة الكتاب اتفق البخاري على صحته وعند مسلم أيضا عن أبي السائب أيضا عن أبي هريرة قال رسول الله','523','508','0');
INSERT INTO [t6227] VALUES (648,'يقول قال الله قسمت الصلاة بيني وبين عبدي نصفين وذكر الحديث','523','508','0');
INSERT INTO [t6227] VALUES (649,'قال لا صلاة إلا بقراءة قال أبو هريرة فما أعلن رسول الله','524','508','0');
INSERT INTO [t6227] VALUES (650,'أمره أن يخرج فنادى في الناس لا صلاة إلا بفاتحة الكتاب فما زاد وقال الحاكم أبو عبد الله هذا حديث صحيح لا غبار عليه فإن جعفر بن ميمون العبدي من ثقات البصريين ويحيى بن سعيد لا يحدث إلا عن الثقات وروى أبو نضرة عن أبي سعيد قال أمرنا','524','508','0');
INSERT INTO [t6227] VALUES (651,'في صلاة الفجر فقرا رسول الله','524','508','0');
INSERT INTO [t6227] VALUES (652,'بعض الصلوات التي يجهر فيها بالقراءة فالتبست عليه القراءة فلما انصرف أقبل علينا بوجهه فقال هل تقرأون إذا جهرت بالقراءة فقال بعضنا إنا لنصنع ذلك قال فلا وأنا أقول مالي أنازغ القرآن فلا تقرأوا بشيء من القرآن إذا جهرت إلا بأم القرآن','525','508','0');
INSERT INTO [t6227] VALUES (653,'إذا أنصت فإذا قرأ لم يقرأ فإذا أنصت قرأ وكان رسول الله','526','508','0');
INSERT INTO [t6227] VALUES (654,'إذا أنصت وإذا قرأ لم يقرأوا خلفه فإذا أنصت قرأوا وباقيه بمعناه قال وقد صحت الرواية عن أميري المؤمنين عمر بن الخطاب وعلي بن أبي طالب رضي الله عنهما أنهما كانا يأمرانا بالقراءة خلف الإمام وذكر عن يزيد بن شريك أنه سأل','526','508','0');
INSERT INTO [t6227] VALUES (655,'دون ذكر جابر في الإسناد وهما قصتان عند أبي حنيفة عن ابن أبي عائشة عن ابن شداد عن النبي','531','508','0');
INSERT INTO [t6227] VALUES (656,'من صلاة الظهر فقال من قرأ سبح اسم ربك الأعلى الحديث قال والدليل على ما ذكرته أن أبا عبد الرحمن عبد الله بن المبارك أحفظ الرواة عن أبي حنيفة وقد روي هذا الخبر في كتاب الصلاة عن أبي حنيفة فأرسله وقد تابعه زفر بن الهذيل على رواية','531','508','0');
INSERT INTO [t6227] VALUES (657,'فلما قضى صلاته قال تقرأون والإمام يقرأ قالوا إننا لنفعل قال فلا تفعلوا إلا أن يقرأ أحدكم في نفسه أم الكتاب قال الحاكم أبو عبد الله هكذا وجدته في كتابي عن','533','508','0');
INSERT INTO [t6227] VALUES (658,'ما يعارض جميع هذه الروايات ويدافعها وروي عنه أيضا من قوله وفعله رضي الله عنه والرواية الثالثة لهذا الحديث الواهي عن علي بن أبي طالب رضي الله عنه أن رجلا سأل النبي','537','508','0');
INSERT INTO [t6227] VALUES (659,'فلما سلم قال أيكم قرأ خلفي فقال رجل أنا يا رسول الله فقال ما لي أنازع القرآن إذا صلى أحدكم خلف الإمام فلينصت فإن قراءته له قراءة وصلاته له صلاة قال أبو عبد الله هذا حديث لم يكتبه إلا عن هذا الشيخ بهذا الإسناد ولا سمعنا أحدا من','538','508','0');
INSERT INTO [t6227] VALUES (660,'أنه قال اخلطتم على القرآن في الجهر بالقراءة خلفه فأما الموقوف فيما روي أنه استغنى في القراءة خلف الإمام قال أنصت للقرآن كما أمرت فإن في الصلاة شغلا وسيكفيك ذاك الإمام قال أبو عبد الله فقال هذا الفتوى من ابن مسعود رضي الله','539','508','0');
INSERT INTO [t6227] VALUES (661,'أن نقرأ بفاتحة الكتاب وما تيسر بإسناد صحيح وروي عنه أنه أفتى بذلك عن أبي نضرة قال سألت أبا سعيد الخدري عن القراءة خلف الإمام فقال اقرأ بفاتحة الكتاب والرواية السادسة لهذا الخبر الواهي عن أبي هريرة رضي الله','540','508','0');
INSERT INTO [t6227] VALUES (662,'بخلاف هذا أكثر وأشهر من أن يمكن ذكرها في هذا الموضع وروى خالد بن عبد الله عن عبد الرحمن بن إسحق عن سعيد المقبري عن أبي هريرة قال قال رسول الله','541','508','0');
INSERT INTO [t6227] VALUES (663,'فقال إني أكون أحيانا وراء الإمام فيقول اقرأ بها في نفسك يا فارسي ثم يكون عنده خلافه عن رسول الله','542','508','0');
INSERT INTO [t6227] VALUES (664,'قال إنما الإمام ليؤتم به فلا تختلفوا عليه فإذا كبر فكبروا فذكر الحديث وقال فيه وإذا قرأ فأنصتوا قال أبو عبد الله هذا الخبر وهم الراوي فيه لا خلاف أعرفه بين أهل النقل فيه والدليل الأول على وهنه أن أصحاب أبي هريرة الأنجم','543','508','0');
INSERT INTO [t6227] VALUES (665,'لا صلاة لمن لم يقرأ فيها بفاتحة الكتاب خلف الإمام قال البيهقي هذا وجدته في كتابي ورواته ثقات وكذلك رواه محمد بن صالح بن هاني وأبو إسحق إبراهيم بن محمد بن يحيى وأبو الطيب محمد بن أحمد الذهلي قال أبو الطيب لمحمد بن سليمان خلف الإمام','547','508','0');
INSERT INTO [t6227] VALUES (666,'صلى بأصحابه الظهر فقال أيكم قرأ سبح اسم ربك الأعلى فقال رجل أنا فقال رسول الله','548','508','0');
INSERT INTO [t6227] VALUES (667,'أن لا أقرأ خلف الإمام قال أبو عبد الله وهذا خبر من النوع الذي يقول أنه لا يسوى سماعه فلو صح مثله عن الثوري لما خفي ولما وقع الخلاف في صحته فنقول وبالله التوفيق أن عيسى بن جعفر قاضي الري ثقة ثبت لا يحتمل هذا الدنس فالراوي عنه لا يخلو','549','508','0');
INSERT INTO [t6227] VALUES (668,'أفي الصلاة قراءة قال نعم فقال رجل وجبت وكنت أدنى القوم إليه فقال وذكر رسول الله','553','508','0');
INSERT INTO [t6227] VALUES (669,'الإمام ضامن فما يصنع فاصنعوا قال أبو حاتم هذا صحيح لمن قال بالقراءة خلف الإمام وروي عن نفر من أصحاب رسول الله','555','508','0');
INSERT INTO [t6227] VALUES (670,'في الصلاة وأن رسول الله','556','508','0');
INSERT INTO [t6227] VALUES (671,'فيما جهر فيه رسول الله','556','508','0');
INSERT INTO [t6227] VALUES (672,'وإلى هذا ذهب الشافعي رضي الله عنه في القديم ثم رجع عنه في الجديد فأوجب القراءة خلف الإمام فيما جهر فيه الإمام وأسر وابن أكيمة مجهول وقوله فانتهى الناس عن القراءة فيما جهر فيه قال الزهري رواه الأوزاعي فميزه من قول أبي هريرة وجعله من قول','556','508','0');
INSERT INTO [t6227] VALUES (673,'من القراءة وما أمر به منها فقال لا تفعلوا يعني الجهر وما زاد على الفاتحة وليقرأ بفاتحة الكتاب لأنه لا صلاة إلا بها قال وقد مضى حديثه وشواهده وذكرنا سائر شواهده في كتاب القراءة خلف الإمام وبالله التوفيق','557','508','0');
INSERT INTO [t6227] VALUES (674,'ويقنت في صلاة الصبح بعد الركوع من الركعة الثانية وقال أبو حنيفة يكره القنوت فيها عن أبي هريرة أن النبي','557','508','0');
INSERT INTO [t6227] VALUES (675,'وكان أبو هريرة يقنت في الركعة الثانية من صلاة الصبح بعدما يقول سمع الله لمن حمده فيدعو للمؤمنين ويلعن الكفار وعند البخاري ومسلم عن محمد عن أنس أنه سئل هل قنت النبي','558','508','0');
INSERT INTO [t6227] VALUES (676,'كان يقنت في الصبح والمغرب وعنده عن خفاف بن إيماء بن رحضه الغفاري قال قال رسول الله','558','508','0');
INSERT INTO [t6227] VALUES (677,'يقنت في صلاة الصبح حتى فارق الدنيا قال أبو','558','508','0');
INSERT INTO [t6227] VALUES (678,'قنت في صلاة الصبح وروينا ذلك عن أبي بكر الصديق وعمر بن الخطاب وعثمان وعلي وأبي هريرة والبراء رضي الله عنهم وذكر بعض أسانيد ذلك ثم قال ودليلهم في المسألة حديث أبي عاصم الأحول قال سألت أنسا عن القنوت قبل الركوع قال إنما قنت رسول','559','508','0');
INSERT INTO [t6227] VALUES (679,'قنت شهرا يدعو على أحياء من أحياء العرب ثم تركه قلنا هذا حديث لا شك بصحته إلا أنه','560','508','0');
INSERT INTO [t6227] VALUES (680,'ذلك إلى أن فارق الدنيا وروينا عن الخلفاء الراشدين ثم عن أبي هريرة والبراء أنهم قنتوا في صلاة الصبح ولو كان متروكا لما بقوا وروي أنه كان يقنت فيدعو لعباس وسلمه والوليد وعلى مضر فأنزل الله عز وجل ليس لك من الأمر شيء وعند مسلم','560','508','0');
INSERT INTO [t6227] VALUES (681,'صلاة','560','508','0');
INSERT INTO [t6227] VALUES (682,'إلا شهرا واحدا ثم تركه بشر بن حرب متروك الحديث والصحيح عن ابن عمر ما رواه أبو الشعثاء وأبو الأسود وأبو مجلز أنه كان لا يرى القنوت وقال ما أحفظه عن','561','508','0');
INSERT INTO [t6227] VALUES (683,'لم يعلم سنة عملها غيره فعليه وعلى جميع المسلمين قبوله فهذا خليفة رسول الله','562','508','0');
INSERT INTO [t6227] VALUES (684,'في ميراث الجدة حتى أخبره المغيرة بذلك عن النبي','562','508','0');
INSERT INTO [t6227] VALUES (685,'قنت في صلاة الصبح وقال على ابن عمر في بمعناه وقال وقال هياج عن عنبسة عن ابن نافع عن أبيه عن صفية بنت أبي عبيد عن النبي','563','508','0');
INSERT INTO [t6227] VALUES (686,'قال ابن معين عبد الله بن نافع ضعيف وإن استدلوا بما روي عن ابن مسعود في إنكار القنوت عن','563','508','0');
INSERT INTO [t6227] VALUES (687,'قال أبو عبد الله هذا حديث ينفرد به محمد بن جابر السحيمي متروك الحديث بمره وقد صح عن ابن مسعود رضي الله عنه خلاف هذا وإن استدلوا بما روي عن سعيد بن جبير عن ابن عباس بأنه بدعة فإنه لا يثبت وعبد الله بن ميسرة راويه يقال له أبو ليلى ويقال','564','508','0');
INSERT INTO [t6227] VALUES (688,'والمختار من التشهد قول التحيات المباركات الصلوات الطيبات لله كذا رواه ابن عباس وقال أبو حنيفة المختار قوله التحيات لله والصلوات كذا رواه ابن مسعود رضي الله عنهم عن','564','508','0');
INSERT INTO [t6227] VALUES (689,'يعلمنا التشهد كما يعلمنا القرآن وكان يقول التحيات المباركات الصلوات الطيبات لله السلام عليك أيها النبي ورحمة الله وبركاته السلام علينا وعلى عباد الله الصالحين أشهد أن لا إله إلا الله وأشهد أن محمدا رسول الله أخرجه مسلم في الصحيح','565','508','0');
INSERT INTO [t6227] VALUES (690,'وأشاد إلى رواية ابن مسعود وأبي موسى وجابر وذكر تشهد عمر وابن عباس وعائشة رضي الله عنهم واختار رواية ابن عباس لأنه أكملها وفيه زيادة المباركات والله أعلم','566','508','0');
INSERT INTO [t6227] VALUES (691,'مسألة','567','508','0');
INSERT INTO [t6227] VALUES (692,'في التشهد وقال في آخره ثم يستخير أحدكم من الدعاء أعجبه إليه فيدعو به وعند مسلم عن ابن عباس عن النبي','567','508','0');
INSERT INTO [t6227] VALUES (693,'قال أقرب ما يكون العبد من ربه وهو ساجد فأكثروا الدعاء وعنه قال رسول الله','567','508','0');
INSERT INTO [t6227] VALUES (694,'قال سلوا الله حوائجكم البتة في صلاة الصبح وقد بقي في هذه المسألة أحاديث تركتها اختصارا','568','508','0');
INSERT INTO [t6227] VALUES (695,'ومن ذكر صلاة وهو في فريضة الوقت أتمها ثم قضى ما تذكر سواء كان الوقت متسعا أو ضيقا وقال أبو حنيفة عليه أن يبدأ بالفائتة ما لم يزد على صلاة يوم وليلة إذا كان الوقت موسعا ودليلنا من طريق الخبر ما روينا في حديث أوقات الصلوات ما بين هذين','568','508','0');
INSERT INTO [t6227] VALUES (696,'ووهم في رفعه فإن كان قد رجع عن رفعه فقد وفق للصواب ولفظة هذا الأثر قال من نسي صلاة فلم يذكرها إلا وهو مع الإمام فليصل مع الإمام فإذا فرغ من صلاته فليعد الصلاة التي نسي ثم ليعد الصلاة التي صلاها مع الإمام قال ثم يعارضه ما أخبرنا وذكره','569','508','0');
INSERT INTO [t6227] VALUES (697,'وأنا والله ما صليتها بعد قال فنزل إلى بطحان','569','508','0');
INSERT INTO [t6227] VALUES (698,'يوم الأحزاب شغلونا عن الصلاة الوسطى صلاة العصر ملأ الله بيوتهم وقبورهم نار ثم صلاها بين العشائين بين المغرب والعشاء فيحتمل أن يكون ما روي في حديث جابر فعله في يوم والذي في حديث علي رضي الله عنه فعله في يوم آخر والله أعلم وفي المسألة','570','508','0');
INSERT INTO [t6227] VALUES (699,'فلم يستيقظ حتى طلعت الشمس فقال رسول الله','570','508','0');
INSERT INTO [t6227] VALUES (700,'لقضاء صلاة الصبح لم يكن لتحل الصلاة بذهاب وقت الكراهة فإنما استيقظوا بعدما طلعت الشمس وذهب وقت الكراهة وحضور الشيطان لا يمنع الصلاة فقد خنق رسول الله','571','508','0');
INSERT INTO [t6227] VALUES (701,'مسألة','571','508','0');
INSERT INTO [t6227] VALUES (702,'قال التسبيح في الصلاة للرجال والتصفيق للنساء أخرجه البخاري ومسلم وعن سهل بن سعد الساعدي أن رسول الله','572','508','0');
INSERT INTO [t6227] VALUES (703,'ما لي أراكم أكثرتم التصفيق من يأته شيء في صلاته فليسبح فإنه إذا سبح التفت إليه وإنما التصفيق للنساء أخرجه البخاري ومسلم وروي عن أبي هريرة أنه قال قال رسول الله','572','508','0');
INSERT INTO [t6227] VALUES (704,'مسألة','572','508','0');
INSERT INTO [t6227] VALUES (705,'وذكر الحديث وقال فيه وإذا زوج أحدكم عبده أو أمته أو أجيره فلا تنظر الأمة إلى شيء من عورته فإن ما تحت السرة إلى ركبته','572','508','0');
INSERT INTO [t6227] VALUES (706,'يقبلها منه احتجوا بحديث عن ابن جريج عن النبي','573','508','0');
INSERT INTO [t6227] VALUES (707,'مسألة','573','508','0');
INSERT INTO [t6227] VALUES (708,'وهو يصلي فسلمت عليه فرد إشارة قال ولا أعلمه إلا قال ورد إشارة بأصبعه وروي عن ابن عمر في رد رسول الله','573','508','0');
INSERT INTO [t6227] VALUES (709,'مسألة','574','508','0');
INSERT INTO [t6227] VALUES (710,'وفخذي منكشفه وقال خمر عليك أو ما علمت أن الفخذ عورة لفظ أبي أويس عن مالك','574','508','0');
INSERT INTO [t6227] VALUES (711,'عندنا فرأى فخذي منكشفه والباقي سواء وبمعناه رواه أبي بكير وجماعة عن مالك وروي عن معمر عن الزهري عن عبد الرحمن بن جرهد عن أبيه أن النبي','575','508','0');
INSERT INTO [t6227] VALUES (712,'فمر على معمر وهو جالس عند داره بالسوق وفخذاه مكشوفتان فقال النبي','575','508','0');
INSERT INTO [t6227] VALUES (713,'يقول ما بين السرة إلى الركبة عورة وعن ابن عباس قال قال رسول الله','575','508','0');
INSERT INTO [t6227] VALUES (714,'وأنا كاشف فخذي فقال يا علي غط فخذيك فإنها من العورة ولا يصح احتجاجهم بقصة عثمان وكشف النبي','576','508','0');
INSERT INTO [t6227] VALUES (715,'كان في مكان فيه ماء قد كشف عن ركبتيه فلما أقبل عثمان غطاهما والذي روى عبد العزيز بن صهيب عن أنس في قصة خيبر ودخوله في زقاق خيبر وأن ركبته لتمس فخذ رسول الله','576','508','0');
INSERT INTO [t6227] VALUES (716,'قال لا حجة لهم فيه فإن انحسار الإزار عن فخذيه لم يكن بقصده وإنما حصره ضيق الزقاق وقد رواه حميد عن أنس فقال في إحدى','576','508','0');
INSERT INTO [t6227] VALUES (717,'ولم يذكر انكشاف الفخذ وفي الرواية الأخرى وأن قدمي لتمس قدم رسول الله','577','508','0');
INSERT INTO [t6227] VALUES (718,'مسألة','577','508','0');
INSERT INTO [t6227] VALUES (719,'أتصلي المرأة في درع وخمار ليس عليها إزار فقال إذا كان الدرع سابغا يغطي ظهور قدميها وعند أبي داود عن عائشة رضي الله عنها أن أسماء بنت أبي بكر رضي الله عنها دخلت على','577','508','0');
INSERT INTO [t6227] VALUES (720,'فقال يا أسماء إن المرأة إذا بلغت المحيض لم يصلح أن يرى منها إلا هذا وهذا وأشار إلى وجهه وكفيه قال أبو داود هذا مرسل خالد بن دريك لم يدرك عائشة رضي الله عنها وروي عن ابن عباس ولا يبدين زينتهن إلا ما ظهر منها قال ما في الكف','578','508','0');
INSERT INTO [t6227] VALUES (721,'وكلام المخطئ والناسي والجاهل بتحريمه في الصلاة لا يقطع الصلاة وقال أبو حنيفة يقطعها دليلنا من طريق الخبر ما روي عن أبي هريرة أن رسول الله','578','508','0');
INSERT INTO [t6227] VALUES (722,'أصدق ذو اليدين فقال الناس نعم فقام رسول الله','578','508','0');
INSERT INTO [t6227] VALUES (723,'وإجابتهم كانت واجبة عليهم في الصلاة وغيرها وذكر حديث أبي سعيد بن المعلى لما دعاه رسول الله','579','508','0');
INSERT INTO [t6227] VALUES (724,'ألم يقول الله عز وجل يا أيها الذين آمنوا استجيبوا لله وللرسول إذا دعاكم لما يحييكم وروي حديث ذو اليدين عن ابن عمر وعمران بن حصين وروي عن عطاء قال صلى بنا ابن الزبير صلاة المغرب فسلم في ركعتين ثم قام إلى الحجر يستلمه فسبحنا','579','508','0');
INSERT INTO [t6227] VALUES (725,'فذكر الحديث وقال بينا أنا مع رسول الله','580','508','0');
INSERT INTO [t6227] VALUES (726,'دعاني فبأبي هو وأمي ما رأيت معلما قبله ولا بعده أحسن تعليما منه والله ما ضربني ولا كهرني ولا سبني فقال إن صلاتنا هذه لا يصلح فيها شيء من كلام الناس إنما هو التسبيح والتكبير وتلاوة القرآن أخرجه مسلم في الصحيح وفيه دليل على أن','580','508','0');
INSERT INTO [t6227] VALUES (727,'بإعادة الصلاة فمن تكلم في صلاته جاهلا أو ساهيا مضت صلاته ومن تكلم عامدا استأنف الصلاة','580','508','0');
INSERT INTO [t6227] VALUES (728,'قال إن الله تجاوز عن أمتي الخطأ والنسيان وما استكرهوا عليه إسناده مستقيم ورواته ثقات واستدلوا بحديث عبد الله قال كنا نسلم على النبي','581','508','0');
INSERT INTO [t6227] VALUES (729,'إلى المدينة وقصة ذي اليدين برواية أبي هريرة كان بعد الهجرة وأبو هريرة من آخرهم إسلاما قال صحبت رسول الله','581','508','0');
INSERT INTO [t6227] VALUES (730,'وهو منطلق إلى بني المصطلق فأتيته وهو يصلي على بعيره فكلمته فقال بيده هكذا وأومأ زهير بيده ثم كلمته وأومأ زهير بيده نحو الأرض وأنا أسمعه يقرأ يومئ برأسه فلما فرغ قال ما فعلت في الذي أرسلتك له فإنه لم يمنعني أن أكلمك','582','508','0');
INSERT INTO [t6227] VALUES (731,'وفي رواية عنه بينما أنا أصلي مع رسول الله','583','508','0');
INSERT INTO [t6227] VALUES (732,'مسألة','583','508','0');
INSERT INTO [t6227] VALUES (733,'فذكر الحديث قال ثم نفخ في آخر سجوده وروي عن أبي بن نابل قال قلت لقدامة بن عبد الله بن عمار الكلابي صاحب رسول الله','583','508','0');
INSERT INTO [t6227] VALUES (734,'يقول لغلام لنا إذا سجدت يا رباح فترب وجهك قالوا معناه لا تنفخ موضع السجود وأبو حمزة ميمون الكوفي ضعيف في الحديث وروي من وجه آخر عنها قالت مر رسول الله','584','508','0');
INSERT INTO [t6227] VALUES (735,'يا رباح لا تنفخ فإن من نفخ فقد تكلم هذا أضعف من الأول ثم هو مجهول على نفخ يبلغ ثلاثة أحرف كقولك أف فما دونه فلا يكون كلاما حتى يشدد الفاء فيكون ثلاثة أحرف من التأفيف قال والنافخ لا يخرج الفاء في نغمته مشددة ولا يكاد يخرجها','584','508','0');
INSERT INTO [t6227] VALUES (736,'مسألة','585','508','0');
INSERT INTO [t6227] VALUES (737,'إذا وجد أحدكم في بطنه شيئا فيشكل عليه أخرج منه شيء أم لا فلا يخرج من المسجد حتى يسمع صوتا أو يجد ريحا أخرجه مسلم في','585','508','0');
INSERT INTO [t6227] VALUES (738,'إذا فسا أحدكم في الصلاة فلينصرف فليتوضأ وليعد صلاته واحتج البويطي والربيع بحديث النبي','586','508','0');
INSERT INTO [t6227] VALUES (739,'كان قول المسور وأشبههما لأني لا أعلم خلافا أن كل من ولي ظهره القبلة عامدا أعاد الصلاة والراعف مولى ظهره القبلة','586','508','0');
INSERT INTO [t6227] VALUES (740,'أولى أن نتبع ورواه عمران بن ظبيان عن حكيم بن سعد وليسا بالقويين عن سلمان رضي الله عنه','587','508','0');
INSERT INTO [t6227] VALUES (741,'وما أدركه المسبوق فهو أول صلاته وقال أبو حنيفة ما أدركه فهو آخر صلاته ودليلنا من طريق الخبر ما روي عن أبي','587','508','0');
INSERT INTO [t6227] VALUES (742,'إذا سمعتم الإقامة فامشوا إلى الصلاة وعليك السكينة فما أدركتم فصلوا وما فاتكم فأتموا أخرجه البخاري ومسلم في الصحيح وقال رسول الله','588','508','0');
INSERT INTO [t6227] VALUES (743,'وروي معنى فتواه عن علي وعمر وأبي الدرداء رضي الله عنهم وروي أنه قول سعيد ابن المسيب والأوزاعي وسعيد بن عبد العزيز رحمهم الله وكذلك عن عطاء والحسن رحمهما الله تعالى واحتجوا بالرواية الأخرى عن أبي هريرة عن رسول الله','588','508','0');
INSERT INTO [t6227] VALUES (744,'وأبو قتادة وأنس عن النبي','589','508','0');
INSERT INTO [t6227] VALUES (745,'مسألة','590','508','0');
INSERT INTO [t6227] VALUES (746,'فصلى ومحجن في مجلسه فقال له رسول الله','591','508','0');
INSERT INTO [t6227] VALUES (747,'إذا جئت فصل مع الناس وإن كنت قد صليت وفي كتاب السنن عن جابر بن يزيد بن الأسود عن أبيه قال صلينا مع النبي','591','508','0');
INSERT INTO [t6227] VALUES (748,'فجيء بهما ترعد فرائصهما فقال لهما ما منعكما أن تصليا مع الناس ألستما مسلمين قالا بلى يا رسول الله إنا كنا صلينا في رحالنا فقال إذا صليتما في رحالكما ثم أتيتما الإمام فصليا معه فإنها لكما نافلة ورواه يونس بن صعصعة عن زيد بن','591','508','0');
INSERT INTO [t6227] VALUES (749,'إذا جئت الصلاة فوجدت الناس فصل معهم فإن كنت قد صليت فليكن ذلك نافلة وهذه مكتوبة عن حميد قال قال أنس قدمنا مع أبي موسى فصلى بنا الغداة بالمربد ثم انتهينا إلى المسجد فأقيمت الصلاة وصلينا مع المغيرة بن شعبة رواته ثقات وروينا','592','508','0');
INSERT INTO [t6227] VALUES (750,'في هذا الخبر فقام أبو بكر رضي الله عنه فصلى وقد كان صلى مع النبي','592','508','0');
INSERT INTO [t6227] VALUES (751,'عن الصلاة بعد الصبح حتى تطلع الشمس وبعد العصر حتى تغرب الشمس وذلك منسوخ بما روينا فإن حديث يزيد بن الأسود وكان في حجة الوداع وقد روي عن ابن عمر مرفوعا لا تصلوا صلاة في يوم مرتين تفرد','592','508','0');
INSERT INTO [t6227] VALUES (752,'مسألة','593','508','0');
INSERT INTO [t6227] VALUES (753,'فقال صل قائما','593','508','0');
INSERT INTO [t6227] VALUES (754,'مسألة','594','508','0');
INSERT INTO [t6227] VALUES (755,'فافتتح البقرة فقلت يصلي بها في ركعة فمضى فقلت يركع بها ثم افتتح النساء فقرأها ثم افتتح آل عمران فقرأها يقرأ مترسلا إذا مر بآية فيها تسبيح سبح وإذا مر بسؤال سأل وإذا مر بتعوذ تعوذ ثم ركع فقال سبحان ربي العظيم','594','508','0');
INSERT INTO [t6227] VALUES (756,'في الليل التام بآل عمران والنساء فإذا مر بآية فيها دعا وترغب وإذا مر بآية فيها تخويف دعا واستعاذ قال وروينا في السنن عن عوف بن مالك الأشجعي قال قمت مع رسول الله','595','508','0');
INSERT INTO [t6227] VALUES (757,'كان إذا قرأ سبح اسم ربك الأعلى قال سبحان ربي الأعلى وعن موسى بن أبي عائشة','595','508','0');
INSERT INTO [t6227] VALUES (758,'من قرأ منكم والتين والزيتون فانتهى إلى آخرها أليس الله بأحكم الحاكمين فليقل وأنا على ذلك من الشاهدين ومن قرأ لا أقسم بيوم القيامة وانتهى إلى أليس ذلك بقادر على أن يحيى الموتى فليقبل بلى ومن قرأ','596','508','0');
INSERT INTO [t6227] VALUES (759,'مسألة','598','508','0');
INSERT INTO [t6227] VALUES (760,'والنجم فلم يسجد رواه مسلم في الصحيح ورواه البخاري دون القراءة مع الإمام وروي عن ابن عباس أن النبي','598','508','0');
INSERT INTO [t6227] VALUES (761,'لم يسجد في شيء من المفصل منذ حول إلى المدينة قال وبهذا اللفظ أخرجه أبو داود في السنن قال وقد ثبت عن النبي','598','508','0');
INSERT INTO [t6227] VALUES (762,'يدعها مرة ويسجدها أخرى وفي ذلك دلالة على أنها غير واجبة وروي عن أبي هريرة أن النبي','598','508','0');
INSERT INTO [t6227] VALUES (763,'بإعادته وروي عن عطاء بن يسار عن زيد بن ثابت أنه قرأ على رسول الله','599','508','0');
INSERT INTO [t6227] VALUES (764,'ولم يكن فرضا فيأمره النبي','599','508','0');
INSERT INTO [t6227] VALUES (765,'مسألة','600','508','0');
INSERT INTO [t6227] VALUES (766,'فسجد الرجل وسجد النبي','600','508','0');
INSERT INTO [t6227] VALUES (767,'فانتظر الرجل أن يسجد النبي','600','508','0');
INSERT INTO [t6227] VALUES (768,'كنت إماما فلو سجدت سجدت معك وقد روي','600','508','0');
INSERT INTO [t6227] VALUES (769,'مسألة','601','508','0');
INSERT INTO [t6227] VALUES (770,'أقرأه خمس عشرة سجدة في القرآن منها ثلاثة في المفصل وفي سورة الحج سجدتين وعن عقبة بن عامر قال قلت يا رسول الله في سورة الحج سجدتان قال نعم ومن يسجدهما فلا يقرأهما قال هو أيضا في السنن وأنا إنما أخرجته شاهدا وروى','602','508','0');
INSERT INTO [t6227] VALUES (771,'مسألة','603','508','0');
INSERT INTO [t6227] VALUES (772,'يسجد فيها أخرجه البخاري في الصحيح وعند أبي داود عن أبي سعيد الخدري أنه قال قرأ رسول الله','603','508','0');
INSERT INTO [t6227] VALUES (773,'إنما هي توبة نبي ولكن رأيتكم تشزنتم للسجود فنزل فسجد رواته ثقات وروى الشافعي عن ابن عيينة عن عبده عن ذر عن ابن مسعود رضي الله عنه أنه كان لا يسجد في ص ويقول إنما هو توبة نبي وروي عنه أنه قال لا تسجدوا في ص','603','508','0');
INSERT INTO [t6227] VALUES (774,'سجدها داود عليه السلام لتوبة ونحن نسجدها شكرا يعني ص هذا المحفوظ وقد روي من أوجه عن عمر بن ذر عن أبيه عن سعيد بن جبير عن ابن عباس من طرق معناه','604','508','0');
INSERT INTO [t6227] VALUES (775,'السجدة إذا كانت في آخر السورة وكان في الصلاة فالأولى أن يسجد ثم يقوم فيقرأ سورة أخرى وقال بعض العراقيين يركع ولا يسجد للتلاوة لنا ما روي عن أبي هريرة قال رأيت عمر بن الخطاب رضي الله عنه سجد في النجم في صلاة الفجر ثم استفتح','604','508','0');
INSERT INTO [t6227] VALUES (776,'مسألة','605','508','0');
INSERT INTO [t6227] VALUES (777,'سجد في صلاة الظهر ثم قام فيرون أنه قرأ سورة فيها سجدة','605','508','0');
INSERT INTO [t6227] VALUES (778,'مسألة','606','508','0');
INSERT INTO [t6227] VALUES (779,'نهى أن يصلي في سبعة مواطن المزبلة والمجزرة والمقبرة وقارعة الطريق والحمام ومعاطن الإبل وفوق ظهر بيت الله','606','508','0');
INSERT INTO [t6227] VALUES (780,'وعلى المرتد قضاء ما ترك من الصلوات أيام ردته إذا عاد إلى الإسلام وقال أبو حنيفة ليس عليه قضاؤها ودليلنا من طريق الخبر ما روي عن أنس رضي الله عنه أن النبي','606','508','0');
INSERT INTO [t6227] VALUES (781,'إذا رقد أحدكم عن الصلاة وغفل عنها فليصلها إذا ذكرها فإن الله عز وجل يقول وأقم الصلاة لذكري وهذا الذي ارتد غفل عن ذكر الله تعالى واتبع هواه قال الله تعالى ولا تطع من أغفلنا قلبه عن ذكرنا واتبع هواه فإذا عاد إلى ذكر','607','508','0');
INSERT INTO [t6227] VALUES (782,'وإذا أخذ المصحف في صلاته فقرأ منه فإن كان يتصفح الأوراق متواليا وزاد على ثلاثة أوراق بطلت صلاته وإن كان يتصفح ورقة ولم يوال ذلك أو والى ولكن كانت ثلاثا فما دونها لم تبطل صلاته وكذلك إذا كان مكتوبا على حائط فقرأه في الصلاة لم تبطل صلاته','607','508','0');
INSERT INTO [t6227] VALUES (783,'أنها كان يؤمها غلامها ذكوان في المصحف في رمضان والله أعلم','608','508','0');
INSERT INTO [t6227] VALUES (784,'من شك في صلاته فلم يدر أثلاثا صلى أم أربعا لم تفسد به صلاته وإن كان ذلك أول مرة وقال أبو حنيفة إذا كان ذلك أول مرة فإن صلاته تفسد دليلنا حديث أبي هريرة المخرج في الصحيحين قال إن أحدكم إذا قام يصلي جاءه الشيطان فلبس عليه حتى لا','608','508','0');
INSERT INTO [t6227] VALUES (785,'ويبنى على ما استيقن إذا وقع له هذا الشك سواء كان في المرة الأولى والثانية فصاعدا وقال أبو حنيفة إذا وقع له هذا الشك تحرى وبنى على أغلب ظنه دليلنا من طريق الخبر حديث','608','508','0');
INSERT INTO [t6227] VALUES (786,'إذا شك أحدكم في صلاته فلم يدر كم صلى ثلاثا أم أربعا فليطرح الشك وليبن على ما استيقن ثم يسجد سجدتين قبل أن يسلم فإن كان صلى خمسا شفعن له صلاته وإن كان صلى تماما لأربع كانتا ترغيما للشيطان وروي عن ابن عمر قال قال رسول','609','508','0');
INSERT INTO [t6227] VALUES (787,'قال إذا شك أحدكم في صلاته الحديث بمعناه قال محمد بن إسحق قال لي حسين بن عبد الله أسند لك مكحول هذا الحديث قلت ما سألته قال فإنه ذكر عن كريب عن ابن عباس عن عبد الرحمن بن عوف وحسين بن عبد الله هذا ليس بثقة والاعتماد على','610','508','0');
INSERT INTO [t6227] VALUES (788,'إنما أنا بشر أنسى كما تنسون فأيكم شك في صلاته فلينظر أحرى ذلك إلى الصواب فليتم عليه ويسجد سجدتين والأخذ بالصواب هو الأخذ باليقين قال الله تعالى فمن أسلم فأولئك تحروا رشدا والله أعلم','611','508','0');
INSERT INTO [t6227] VALUES (789,'وسجود السهو قبل السلام وقال أبو حنيفة بعده وروى مالك عن الزهري عن الأعرج عن ابن بحينه قال صلى بنا','611','508','0');
INSERT INTO [t6227] VALUES (790,'ركعتين من بعض الصلوات ثم قال فلم يجلس فقام الناس معه فلما قضى صلاته ونظرنا تسليمه كبر فسجد سجدتين وهو جالس قبل التسليم ثم سلم أخرجه البخاري ومسلم في الصحيح من حديثه وروي بمعناه عن معاوية عن أبي سعيد أن رسول الله','612','508','0');
INSERT INTO [t6227] VALUES (791,'فليسجد سجدتين وهو جالس وكذلك رواه ابن عيينة ومعمر والليث ورواه ابن أخي الزهري عن الزهري قال فليسجد سجدتين قل أن يسلم ثم يسلم وهو جالس','612','508','0');
INSERT INTO [t6227] VALUES (792,'صلى رسول الله','613','508','0');
INSERT INTO [t6227] VALUES (793,'أنه سجد للسهو قبل السلام وأنه أمر بذلك قبل السلام وروينا أنه سجد بعد السلام وأنه أمر به بعد السلام وكلاهما صحيحان وله شواهد يطول بذكرها الكتاب وفي ألفاظهما منع تأويل أحدهما والأخذ بالآخر فالأشبه والصواب جواز الأمرين جميعا وإلى هذا','613','508','0');
INSERT INTO [t6227] VALUES (794,'مسألة','614','508','0');
INSERT INTO [t6227] VALUES (795,'الظهر خمسا فقيل له أزيد في الصلاة قال وما ذاك قال صليت خمسا فسجد سجدتين بعدما سلم أخرجه البخاري في الصحيح','614','508','0');
INSERT INTO [t6227] VALUES (796,'ومن أسر فيما يجهر أو جهر فيما يسر فلا سجود عليه وقال أبو حنيفة إن فعله الإمام سجد للسهو دليلنا من طريق الخبر حديث أبي قتادة أن رسول الله','614','508','0');
INSERT INTO [t6227] VALUES (797,'قال لا سهو في الصلاة إلا لقيام عن جلوس أو جلوس عن قيام وسمع الصنابحي أبا بكر الصديق قرأ في الركعة الثالثة من المغرب بأم القرآن وهذه الآية ربنا لا تزغ قلوبنا بعد إذ هديتنا وهب لنا من لدنك رحمة إنك أنت الوهاب روينا إسناده في','615','508','0');
INSERT INTO [t6227] VALUES (798,'مسألة','616','508','0');
INSERT INTO [t6227] VALUES (799,'إذا شك أحدكم في صلاته فليلق الشك وليبن على اليقين وإن استيقن التمام سجد سجدتين فإن كانت صلاته تامة كانت الركعة نافلة والسجدتان وإن كانت ناقصة كانت الركعة تماما لصلاته والسجدتان ترغمان أنف الشيطان','616','508','0');
INSERT INTO [t6227] VALUES (800,'وسجود الشكر عند حادث النعمة سنة مؤكدة وقال أبو حنيفة إنه بدعة دليلنا ما روي عن بكار بن عبد العزيز بن','616','508','0');
INSERT INTO [t6227] VALUES (801,'كان إذا أتاه أمر يسر به خر ساجدا شكرا لله قال الحافظ أبو عبد الله بكار بن عبد العزيز صدوق عند الأئمة وهذا حديث صحيح ولهذا الحديث شواهد عن النبي','617','508','0');
INSERT INTO [t6227] VALUES (802,'خالد بن الوليد إلى أهل اليمن يدعوهم إلى الإسلام فذكر الحديث في بعث علي رضي الله عنه وإقبال خالد وإسلام همذان قال فكتب علي رضي الله عنه إلى رسول الله','617','508','0');
INSERT INTO [t6227] VALUES (803,'الكتاب خر ساجدا ثم رفع رأسه فقال السلام على همذان السلام على همذان وهذا حديث صحيح قد أخرج البخاري صدر الحديث ولم يسقه بتمامه وسجود الشكر ثابت على شرطه ومنها في حديث كعب بن مالك المخرج في الصحيحين في','617','508','0');
INSERT INTO [t6227] VALUES (804,'وتوبته الحديث الطويل وقال فيه فبينا أنا جالس على الحال الذي ذكر الله عز وجل منا قد ضاقت علي نفسي وضاقت علي الأرض بما رحبت سمعت صوت صارخ أوفى على جبل سلع يقول بأعلا صوته يا كعب بن مالك أبشر قال فخررت ساجدا وعرفت أن قد','618','508','0');
INSERT INTO [t6227] VALUES (805,'من مكة نريد المدينة فلما كان قريبا عزور نزل فرفع يديه فدعا الله ساعة ثم خر ساجدا فمكث طويلا ثم قام فرفع يديه ساعة ثم خر ساجدا ذكره ثلاثا قال إني سألت ربي وشفعت لأمتي فأعطاني ثلث أمتي فخررت لربي ساجدا شكرا ثم رفعت رأسي فسألت ربي','619','508','0');
INSERT INTO [t6227] VALUES (806,'أبصر رجلا له زمانة فسجد قال محمد بن عبيد الله وأن أبا بكر رضي الله عنه أتاه فتح فسجد وعنه أن أبا بكر لما أتاه فتح','619','508','0');
INSERT INTO [t6227] VALUES (807,'مر برجل به زمانة فنزل فسجد ثم مر به أبو بكر رضي الله عنه فنزل فسجد ثم مر عمر رضي الله عنه فنزل فسجد ومنها ما روي عن جابر قال كان رسول الله','620','508','0');
INSERT INTO [t6227] VALUES (808,'مسألة','620','508','0');
INSERT INTO [t6227] VALUES (809,'فذكر الحديث في سؤاله رسول الله','621','508','0');
INSERT INTO [t6227] VALUES (810,'من صلاته بأبي وأمي ما رأيت معلما قبله ولا بعده أحسن تعليما منه والله ما نهرني ولا سبني قال إن صلاتنا هذه لا يصلح فيها شيء من كلام الناس إنما هي التكبير والتسبيح وقراءة القرآن أخرجه مسلم في الصحيح قال وقد روينا عن عائشة أن النبي','621','508','0');
INSERT INTO [t6227] VALUES (811,'مسألة','621','508','0');
INSERT INTO [t6227] VALUES (812,'عن الصلاة في السفينة فقال كيف أصلي في السفينة قال صل فيها قائما إلا أن تخاف الغرق قال رواته ثقات','621','508','0');
INSERT INTO [t6227] VALUES (813,'قال ليصل قائما دليل في هذه المسألة وقد أخرجاه قبل هذا وروي عن عبيد الله بن أبي عتبة مولى لأنس سافرت مع أبي الدرداء وأبي سعيد الخدري وجابر بن عبد الله يصلي بنا إمامنا الفرض قائما في السفينة ونصلي خلفه قياما ولو شئنا لخرجنا','622','508','0');
INSERT INTO [t6227] VALUES (814,'ولا تصح صلاة من لم يقرأ بفاتحة الكتاب في كل ركعة منها إذا أحسنها وقال أبو حنيفة يقرأ فيها ما شاء من القرآن وتجزئه صلاته دليلنا ما عن عبادة بن الصامت قال قال رسول الله','622','508','0');
INSERT INTO [t6227] VALUES (815,'قال قال الله تعالى قسمت الصلاة بيني وبين عبدي وهو عند مسلم في الصحيح عن أبي السائب مولى هشام بن زهرة عن أبي هريرة وفي أوله قال رسول الله','623','508','0');
INSERT INTO [t6227] VALUES (816,'يقول قال الله جل ثناؤه قسمت الصلاة بيني وبين عبدي وذكر الحديث وذكر حديث أبي ابن كعب قال قال لي رسول الله','623','508','0');
INSERT INTO [t6227] VALUES (817,'أسمعناكم وما أخفاه منا أخفيناه منكم من قرأ بأم القرآن فقد أجزأت عنه ومن زاد فهو أفضل أخرجه مسلم في الصحيح','623','508','0');
INSERT INTO [t6227] VALUES (818,'قال أم القرآن عوض غيرها وليس غيرها منها عوض قال أبو عبد الله الحاكم رواة هذا الحديث أكثرهم أئمة وكلهم ثقات واستدلوا بحديث أبي هريرة أن النبي','624','508','0');
INSERT INTO [t6227] VALUES (819,'أن أنادي في المدينة أنه لا صلاة إلا بقراءة ولو بفاتحة الكتاب كذا رواه غير وهيب عن جعفر بن ميمون وخالفهم سفيان الثوري ويحيى بن سعيد القطان في آخرين عن جعفر فقال الثوري في روايته عنه لا صلاة إلا بقرآن بفاتحة الكتاب فما زاد وقال','625','508','0');
INSERT INTO [t6227] VALUES (820,'مسألة','626','508','0');
INSERT INTO [t6227] VALUES (821,'في قوله للرجل صلي فقال له رسول الله','626','508','0');
INSERT INTO [t6227] VALUES (822,'قال أقيموا الركوع والسجود فوالله إني لأراكم من بعدي وربما قال من بعد ظهري إذا ركعتم وسجدتم وعند مسلم عنه عن النبي','627','508','0');
INSERT INTO [t6227] VALUES (823,'وروي عن ابن مسعود قال قال رسول الله','627','508','0');
INSERT INTO [t6227] VALUES (824,'أسوأ الناس سرقة الذي يسرق من صلاته قالوا يا رسول الله كيف يسرق صلاته قال لا يتم ركوعها ولا سجودها رواته ثقات ورواه عبد الحميد بن أبي العشرين عن الأوزاعي عن يحيى عن أبي سلمة عن أبي هريرة عن عبد الرحمن بن شبل قال نهى رسول الله','628','508','0');
INSERT INTO [t6227] VALUES (825,'مسألة','629','508','0');
INSERT INTO [t6227] VALUES (826,'قال أمرت أن أسجد على سبعة أعظم على الجبهة وأشار بيده واليدين والركبتين وأطراف القدمين ولا أكف الثوب ولا الشعر أخرجه البخاري في الصحيح وأخرجه مسلم عن خباب قال شكونا إلى رسول الله','629','508','0');
INSERT INTO [t6227] VALUES (827,'شدة الرمضاء في مياهنا وأكفنا فلم يشكنا زكريا بن أبي زائدة مجمع على عدالته وكذلك الطريق إليه سديد والزيادة من الثقة مقبولة وحديث رفاعة مكن جبهتك من الأرض قد سبق ذكره في مسألة التكبير وذكر عن عياض بن عبد الله القرشي قال رأى رسول','629','508','0');
INSERT INTO [t6227] VALUES (828,'في شدة الحر فإذا لم يستطع أحدنا أن يمكن وجهه في رواية جبهته من الأرض بسط ثوبه فسجد عليه أخرجه البخاري ومسلم في الصحيح وهذا لا حجة لهم فيه لأنه يريد به ثوبا منفصلا عنه وبيانه فيما روينا وذكر عن أنس أيضا كنا نصلي مع رسول الله','630','508','0');
INSERT INTO [t6227] VALUES (829,'صلاة الظهر فآخذ قبضة من الحصى في كفي حتى تبرد وأضعها لجبهتي إذا سجدت من شدة الحر','630','508','0');
INSERT INTO [t6227] VALUES (830,'يسجد على ثوبه وهؤلاء الثلاث كلهم ضعاف وروى حسين بن عبد الله وهو ضعيف لا يحتج به عن عكرمة عن ابن عباس قال كان رسول الله','631','508','0');
INSERT INTO [t6227] VALUES (831,'كان يسجد على كور العمامة وروى عبد العزيز بن عبيد الله بن حمزة بن صهيب حديثا مرفوعا في السجود في أعلى الجبهة مع قصاص الشعر وفي رواية على قصاص الشعر قال علي بن عمر تفرد به وليس بالقوي وروى معناه أبو بكر بن أبي مريم وهو متروك الحديث','631','508','0');
INSERT INTO [t6227] VALUES (832,'يسجدون وأيديهم في ثيابهم ويسجد الرجل منهم على عمامته ويحتمل أن يكون أراد يسجد على عمامته وجبهته كما قلنا في المسح بالاحتياط لفرط السجود أولا والله أعلم','632','508','0');
INSERT INTO [t6227] VALUES (833,'ووضع اليدين والركبتين فرض في السجود كوضع الجبهة على أحد القولين وقال أبو حنيفة ليس بفرض دليلنا عن ابن عباس','632','508','0');
INSERT INTO [t6227] VALUES (834,'أن يسجد على سبعة يديه وركبتيه وأطراف أصابعه وجبهته ونهى أن يكف منها الشعر والثياب أخرجه مسلم وأخرجه البخاري كما سبق وروى مسلم في الصحيح عن العباس بن عبد المطلب أنه سمع رسول الله','633','508','0');
INSERT INTO [t6227] VALUES (835,'إذا سجدت فضع كفيك وارفع مرفقيك وعند أبي داود عن نافع عن ابن عمر رفعه قال إن اليدين تسجدان كما يسجد الوجه فإذا وضع أحدكم وجهه فليضع يديه وإذا رفعه فليرفعهما ودليل القول الثاني ما عن علي رضي الله عنه أن رسول الله','633','508','0');
INSERT INTO [t6227] VALUES (836,'مسألة','634','508','0');
INSERT INTO [t6227] VALUES (837,'في ناحية المسجد فصلى الحديث وفيه إذا قمت إلى الصلاة فأسبع الوضوء ثم استقبل القبلة ثم اقرأ ما تيسر معك من القرآن وقال في آخره ثم افعل ذلك في صلاتك كلها أخرجه البخاري ومسلم في الصحيح وفي رواية عندهما الحديث وقال في آخره ثم كذلك','634','508','0');
INSERT INTO [t6227] VALUES (838,'في حديث مالك بن الحويرث صلوا كما رأيتموني أصلي دليل في هذه المسألة وروى عاصم بن أبي النجود عن ذكوان عن عائشة رضي الله عنها أنها كانت تأمر بالقراءة بفاتحة الكتاب في الأخريين وعن جابر بن عبد الله رضي الله عنه مثله رواه وهب بن','634','508','0');
INSERT INTO [t6227] VALUES (839,'مسألة','635','508','0');
INSERT INTO [t6227] VALUES (840,'الحديث في التشهد وقال فيه فالتفت إلينا رسول الله','635','508','0');
INSERT INTO [t6227] VALUES (841,'قولوا التحيات لله وحديث ابن عباس رضي الله عنهما كان رسول الله','636','508','0');
INSERT INTO [t6227] VALUES (842,'خطب لنا وبين لنا سنتنا وعلمنا صلاتنا فإذا قمتم إلى الصلاة الحديث وقال فيه فإذا كان عند القعدة فليكن من أول قول أحدكم التحيات الصلوات الطيبات وذكرنا في التشهد أخرجه مسلم في الصحيح وفي رواية عنده الحديث وفيه فإذا كان عند القعود','636','508','0');
INSERT INTO [t6227] VALUES (843,'يعلمنا التشهد كما يعلمنا السورة من القرآن ويقول تعلموا فإنه لا صلاة إلا بتشهد وعنه رضي الله عنه قال التشهد تمام الصلاة وأدلتهم مخرجة في مسألة التحليل مجاب عنها والله أعلم','637','508','0');
INSERT INTO [t6227] VALUES (844,'مسألة','638','508','0');
INSERT INTO [t6227] VALUES (845,'فريضة في التشهد الأخير وركن من أركان الصلاة وقال أبو حنيفة إنها سنة دليلنا ما عن كعب بن عجرة أن النبي','638','508','0');
INSERT INTO [t6227] VALUES (846,'في مجلس سعد بن عبادة فقال له بشير بن سعد أمرنا الله أن نصلي عليك يا نبي الله فكيف نصلي عليك فسكت النبي','639','508','0');
INSERT INTO [t6227] VALUES (847,'قولوا اللهم صل على محمد وعلى آل محمد كما صليت على إبراهيم وبارك على محمد وعلى آل محمد كما باركت على إبراهيم في العالمين إنك حميد مجيد والسلام كما قد علمتم وروي عن فضالة بن عبيد الأنصاري أن رسول الله','639','508','0');
INSERT INTO [t6227] VALUES (848,'فانصرف فقال رسول الله','639','508','0');
INSERT INTO [t6227] VALUES (849,'ثم يدعو بما شاء قال الحاكم أبو عبد الله هذا حديث صحيح','639','508','0');
INSERT INTO [t6227] VALUES (850,'كان يقول لا صلاة لمن لم يصل على النبي','640','508','0');
INSERT INTO [t6227] VALUES (851,'في التشهد الأخير فليعد صلاته أو قال لا تجزئ صلاته فهذا عن الشعبي يبطل قولهم من العلماء لم يقولوا في هذه المسألة بوجوب الصلاة على النبي','640','508','0');
INSERT INTO [t6227] VALUES (852,'مسألة','641','508','0');
INSERT INTO [t6227] VALUES (853,'قلنا يعني الإشارة بأصبعه السبابة السلام عليكم قال لنا النبي','641','508','0');
INSERT INTO [t6227] VALUES (854,'وقالت في آخره كان يختم الصلاة بالتسليم وعنده عن سعد','641','508','0');
INSERT INTO [t6227] VALUES (855,'يسلم عن يمينه وعن يساره حتى أرى بياض خده اتفقا على حديث عتبان وفيه ثم سلم يعني رسول الله','642','508','0');
INSERT INTO [t6227] VALUES (856,'بالتسليم في حديث عبد الله في سجدتي السهو قال','642','508','0');
INSERT INTO [t6227] VALUES (857,'كان يسلم عن يمينه وعن يساره وعن عبد الله بن عمرو عن أبي سعيد وعن وائل الحضرمي وعن البراء بن عازب ثم قال وقد تابع عبد الله بن مسعود رضي الله عنه على تسليمتي رسول الله','642','508','0');
INSERT INTO [t6227] VALUES (858,'كان يسلم في الصلاة تسليمة واحدة تلقاء وجهه يميل إلى الشق الأيمن شيئا قال الحاكم أبو عبد الله هذا حديث صحيح تابعهم على ذلك أنس وسمرة وذكرهما ثم روى عن أبي سعيد أن رسول الله','643','508','0');
INSERT INTO [t6227] VALUES (859,'مفتاح الصلاة الوضوء وتحريمها التكبير وتحليلها التسليم وروي عن عبد الله قال مفتاح الصلاة التكبير وانقضاؤها بالتسليم إذا سلم الإمام فقم إن شئت واستدلوا بحديث روي عن معلي بن منصور الرازي عن يعقوب يعني أبا يوسف حدثنا أبو حنيفة','643','508','0');
INSERT INTO [t6227] VALUES (860,'أخذ بيد عبد الله فعلمه التشهد في الصلاة فقال قل التحيات لله وذكره ثم قال فإذا فعلت أو قلت هذا فقد قضيت صلاتك فإن شئت أن تقم فقم وإن شئت أن تقعد فاقعد قال أبو العباس ابن عبده ليس هذا من حديث أبي حنيفة إنما رواه أبو يوسف عن أبي','644','508','0');
INSERT INTO [t6227] VALUES (861,'والدليل على صحة ما قلناه أن أبا خيثمة قد أقر بالشك في ذلك الموضع من الخبر في الروايات كلها عنه فأما أبو النضر هاشم بن القاسم فإنه قال في حديثه قال أبو خيثمة حدثني من سمعه قال يعني أشهد أن لا إله إلا الله وأشهد أن محمدا عبده','645','508','0');
INSERT INTO [t6227] VALUES (862,'ما ليس من كلامه وهو قوله إذا فعلت هذا فقد قضيت صلاتك هذا إنما هو من قول عبد الله بن مسعود والدليل الواضح على صحة ما ذكره أبو علي الحافظ وأبو عبد الله رحمهما الله إن عبد الرحمن بن ثابت بن ثوبان قد روى هذا الخبر عن ابن الحر فميز','646','508','0');
INSERT INTO [t6227] VALUES (863,'وفصله شبابة عن زهير وحوله عن كلام ابن مسعود وقوله أشبه بالصواب من قول من أدرجه في حديث النبي','646','508','0');
INSERT INTO [t6227] VALUES (864,'ما ليس منه بالرجوع إلى قوله أولى والله أعلم قال دحيم ابن ثوبان ثقة وقال البخاري ابن ثوبان الشامي الزاهد وقال أبو علي الحافظ ابن ثوبان حافظ مميز من','647','508','0');
INSERT INTO [t6227] VALUES (865,'قلنا متابعة محمد بن أبان عن الحسن لشك زهير بن يزيد الحديث وهنا فإنه ليس ممن يقبل منه ما يوافق الأثبات فكيف ما يخالف فيه الثقات قال ابن معين محمد بن أبان الجعفي ضعيف قال البخاري ليس بالقوي فقد ظهر لمن وفق أن هذه الزيادة من قول','648','508','0');
INSERT INTO [t6227] VALUES (866,'وعلى آله','649','508','0');
INSERT INTO [t6227] VALUES (867,'والتحليل من الصلاة بالتسليم وفي ذلك غنية لمن تدبره ولم يعاند الحق وأهله ثم لو قابلنا اليقين بالشك وروايات الحفاظ والمقبولين بروايات الضعفاء والمجروحين وقبلنا هذه الروايات التي رويناها لهم وكأنه كان قبل أن شرع التشهد في الصلاة والصلاة','650','508','0');
INSERT INTO [t6227] VALUES (868,'إذا قعد في آخر صلاته قدر التشهد أقبل على الناس بوجهه وذلك قبل أن ينزل التسليم وروى عاصم بن ضمرة من قوله إذا قعد قدر التشهد فقد تمت صلاته وعاصم ليس بحجة وإن كنا نروي حديثه على طريق الاستئناس والاستشهادات قال علي بن سعيد النسوي','650','508','0');
INSERT INTO [t6227] VALUES (869,'مسألة','651','508','0');
INSERT INTO [t6227] VALUES (870,'وقص الحديث وفيه فتوضأ كما أمرك الله ثم تشهد فأقم ثم كبر فإن كان معك قرآن فاقرأ به وإلا فاحمد الله وكبره وهلله وقال فيه وإن انتقصت منه شيئا انتقصت من صلاتك وروي عن ابن أبي أوفى قال أتى النبي','651','508','0');
INSERT INTO [t6227] VALUES (871,'مسألة','652','508','0');
INSERT INTO [t6227] VALUES (872,'أتاه جبريل عليه السلام وهو عند أضاة بني غفار فقال إن الله يأمرك أنت وأمتك أن تقرأ القرآن على حرف فقال رسول الله','652','508','0');
INSERT INTO [t6227] VALUES (873,'مسألة','653','508','0');
INSERT INTO [t6227] VALUES (874,'حتى قام مقامه ثم ذكر أنه لم يغتسل فقال مكانكم فانصرف إلى منزله فاغتسل ثم خرج حتى قام مكانه ورأسه ينطف الماء ورواه عبد الله بن وهب عن يونس عن الزهري وقال فيه فأتى رسول الله','653','508','0');
INSERT INTO [t6227] VALUES (875,'كبر ورواه أبو بكر أن رسول الله','654','508','0');
INSERT INTO [t6227] VALUES (876,'في صلاة فكبر فكبرنا معه ثم أشار إلى القوم أي كما أنتم فلم نزل قياما حتى أتانا رسول الله','654','508','0');
INSERT INTO [t6227] VALUES (877,'كبر في صلاة من الصلوات ثم أشار بيديه امكثوا ثم رجع وعلى جلده أثر الماء وهذا المرسل لعطاء بن يسار يقوى بانضمام مرسل محمد بن سيرين عن النبي','655','508','0');
INSERT INTO [t6227] VALUES (878,'مرسلا وبإجماع الصحابة رضي الله عنهم فقد روينا عن عمر وعثمان رضي الله عنهما فعلهما في خلافتهما بمشهد من الصحابة وصلاتهما بهم من غير نكير من أحد منهم وروي أيضا عن ابن عمر رضي الله عنهما مثل ما قلنا وعند البخاري في الصحيح عن أبي هريرة قال','655','508','0');
INSERT INTO [t6227] VALUES (879,'صلى بالناس وهو جنب فأعاد وأعادوا قال علي بن عمر هذا مرسل وأبو جابر متروك الحديث قال الشافعي رحمه الله الرواية عن حرام بن عمر بن حرام ومن روى عن أبي جابر البياضي بيض الله عينيه وقال مالك لم يكن رضا وقال ابن معين كان كذابا','657','508','0');
INSERT INTO [t6227] VALUES (880,'وليس هو على وضوء فتمت للقوم وأعاد النبي','658','508','0');
INSERT INTO [t6227] VALUES (881,'أيما إمام صلى بالناس وهو جنب أو على غير وضوء فقد تمت صلاة القوم ويعيد الإمام','658','508','0');
INSERT INTO [t6227] VALUES (882,'وبول ما يؤكل لحمه وما لا يؤكل لحمه في النجاسة سواء ووجوب غسل الثوب والبدن منه وفرق أبو حنيفة بينهما فقال في بول ما يؤكل لحمه أن ما يصيب البدن أو الثوب فمعفو ما لم يكن','658','508','0');
INSERT INTO [t6227] VALUES (883,'انتهى إلى قبرين فقال إنهما ليعذبان وما يعذبان في كبير كان أحدهما يمشي بالنميمة وكان الآخر لا يتقي البول قال ثم أخذ جريدة فكسرها بقطعتين ثم قال عسى أن يخفف عنهما حتى ييبسا اتفقا على صحته وفي رواية عند البخاري فكان لا يستتر من','659','508','0');
INSERT INTO [t6227] VALUES (884,'وعنان فرسه في ذراعه فبال الفرس فانتضح عليه من بوله ولم ينصرف لذلك وهذا مرسل وفي إسناده ضعيف وروي عن أبي قتادة ما أكل لحمه فلا بأس بسلخه هذا موقوف وفي ضعف وروي عن بكير بن عبد الله بن الأشبح قال كان أصحاب رسول الله','660','508','0');
INSERT INTO [t6227] VALUES (885,'مسألة','661','508','0');
INSERT INTO [t6227] VALUES (886,'بابن لها صغير لم يأكل الطعام فأجلسه رسول الله','661','508','0');
INSERT INTO [t6227] VALUES (887,'فنضحه ولم','661','508','0');
INSERT INTO [t6227] VALUES (888,'كان يؤتى بالصبيان فينزل عليهم ويحنكهم فأتى بصبي فبال عليه فدعا بماء فأتبعه ولم يغسله اتفقا على صحته أيضا وعند أبي داود عن لبابة بنت الحارث رضي الله عنها قالت كان الحسين بن علي رضي الله عنهما في حجر رسول الله','662','508','0');
INSERT INTO [t6227] VALUES (889,'قال في بول الصبي ينضح بول الغلام ويغسل بول الجارية قال أبو عبد الله الحاكم هذا حديث صحيح فإن أبا الأسود الديلي يصح سماعه من علي رضي الله عنه وفي رواية','662','508','0');
INSERT INTO [t6227] VALUES (890,'أخرى وروى أبو داود عن الحسن عن أمه أنها أبصرت أم سلمة رضي الله عنها تصب على بول الغلام ما لم يطعم وإذا طعم غسلته وكانت تغسل بول الجارية فقد صح عن النبي','663','508','0');
INSERT INTO [t6227] VALUES (891,'مسألة','663','508','0');
INSERT INTO [t6227] VALUES (892,'أخرجه مسلم في الصحيح وأخرج أيضا عن الأسود عنها قالت إن كنت لأجده يعني المني في ثوب رسول الله','663','508','0');
INSERT INTO [t6227] VALUES (893,'يسلت المني من ثوبه بعرق الأذفر ثم يصلي فيه قال وقال القاسم قالت عائشة رضي الله عنها كان رسول الله','664','508','0');
INSERT INTO [t6227] VALUES (894,'وهو في الصلاة وفيما قبله ما يؤكده وروى إسحاق الأزرق عن شريك عن محمد بن أبي ليلى عن عطاء عن ابن عباس قال سئل النبي','664','508','0');
INSERT INTO [t6227] VALUES (895,'فركا فإن رأيته أغسله وإن لم يره فأنضحه يعني المني أخرجه مسلم في الصحيح وهو محمول على الاستحباب ونحن نستحب غسله وأول الخبر دليل على','665','508','0');
INSERT INTO [t6227] VALUES (896,'كان إذا أصاب ثوبه المني غسل ما أصاب منه ثوبه ثم خرج إلى الصلاة وأنا انظر إلى أثر البقع في ثوبه ذلك في موضع الغسل أخرجه البخاري في الصحيح وهذا ليس بخلاف لقولهما كنت أفرك المني من ثوبه ثم يصلي فيه ولا لحتها المني من ثوبه','666','508','0');
INSERT INTO [t6227] VALUES (897,'بعرق الأذخر ثم يصلي فيه لا يكون غسله','666','508','0');
INSERT INTO [t6227] VALUES (898,'مسألة','667','508','0');
INSERT INTO [t6227] VALUES (899,'اتركوه فتركوه حتى بال ثم أمر بدلو فصب عليه وأخرج البخاري في الصحيح عن أبي هريرة قال قام أعرابي فبال في المسجد فتناوله الناس فقال','667','508','0');
INSERT INTO [t6227] VALUES (900,'دعوه وأهرقوا على بوله سجلا من ماء أو ذنوبا من ماء فإنما بعثتم ميسرين ولم تبعثوا معسرين واستدلوا بما اعتد أبي داود عن عبد الله بن مغفل بن مقرن قال صلى أعرابي مع النبي','668','508','0');
INSERT INTO [t6227] VALUES (901,'خذوا ما بال عليه من التراب فألقوه وأهرقوا على مكانه ماء قال أبو داود وهو مرسل ابن معقل لم يدرك النبي','668','508','0');
INSERT INTO [t6227] VALUES (902,'مسألة','669','508','0');
INSERT INTO [t6227] VALUES (903,'بصب الماء عليه وذلك الأمر باق إلى أن يحصل المأمور به قال البخاري في الصحيح وقال أحمد بن شبيب وذكر حديث ابن عمر كانت الكلاب تقبل وتدبر في المسجد فلم يكونوا يرشون شيئا من ذلك كان هذا في ابتداء الإسلام ثم صار منسوخا بحديث أبي','669','508','0');
INSERT INTO [t6227] VALUES (904,'في إيجاب الغسل سبعا من ولوغ الكلب وقال أبو بكر الإسماعيلي معنى الخبر أن المسجد لم يكن يغلق عليها وكانت تتردد فيه وعساها كانت تبول إلا أن علم بولها فيه لم يكن عند النبي','670','508','0');
INSERT INTO [t6227] VALUES (905,'في بول الأعرابي بما أمر دل على أن بول ما سواه في حكم النجاسة واحد وأن اختلف غلظ نجاستها وسمعت واحدا من أصحابه سئل عن هذه المسألة فروي عن النبي','670','508','0');
INSERT INTO [t6227] VALUES (906,'وإنما يروى عن أبي قلابة وهو من التابعين أخبرناه أبو عبد الله الحافظ وذكر إسناده إليه والله أعلم','670','508','0');
INSERT INTO [t6227] VALUES (907,'مسألة','671','508','0');
INSERT INTO [t6227] VALUES (908,'ووجوه','671','508','0');
INSERT INTO [t6227] VALUES (909,'ولم يصنع القوم شيئا رجاء أن تترك لهم رخصة فخرج إليهم بعد فقال وجهوا هذه البيوت عن المسجد فإني لا أحل المسجد لحائض ولا جنب وزاد فيه بعض الرواة إلا لمحمد','672','508','0');
INSERT INTO [t6227] VALUES (910,'سدوا هذه الأبواب إلا باب أبي بكر وهذا أصح قال البيهقي وهذا أصح من حديث جسرة عن عائشة رضي الله عنها فمحمول على المكث فيه دون العبور بدليل الكتاب والله أعلم','672','508','0');
INSERT INTO [t6227] VALUES (911,'ويجوز له أن يصلي في الأوقات التي نهي عنها فيها صلاة لها','672','508','0');
INSERT INTO [t6227] VALUES (912,'نهى عن الصلاة بعد العصر حتى تغرب الشمس وعن الصلاة بعد الصبح حتى تطلع الشمس أخرجه مسلم في الصحيح وأخرج أيضا عن عقبة بن عامر ثلاث ساعات كان رسول الله','673','508','0');
INSERT INTO [t6227] VALUES (913,'قال من نسي صلاة فليصلها إذا ذكرها لا كفارة لها إلا ذلك وقد روى حفص بن أبي العطاف وهو ممن لا يحتج به عن أبي الزناد عن الأعرج عن','673','508','0');
INSERT INTO [t6227] VALUES (914,'قال إذا دخل أحدكم المسجد فليركع ركعتين قبل أن يجلس وفي حديث جابر الصحيح قدمت بالغداة فقال رسول الله','674','508','0');
INSERT INTO [t6227] VALUES (915,'فقالوا أقرأ عليها السلام منا جميعا وسلها عن الركعتين بعد العصر وقل إنا أخبرنا أنك تصلينها وقد بلغنا أن رسول الله','674','508','0');
INSERT INTO [t6227] VALUES (916,'نهى عنها ثم رأيته يصليهما أما حين صلاهما فإنه صلى العصر ثم دخل وعندي نسوة من بني حرام من الأنصار فصلاهما فأرسلت إليه الجارية فقلت قومي بجنبه فقولي له بقول أم سلمة يا رسول الله سمعتك تنهى عن هاتين الركعتين وأراك تصليهما فإن أشار بيده','674','508','0');
INSERT INTO [t6227] VALUES (917,'قضاهما بعد العصر بعد نهيه عن الصلاة في هذه الأوقات فلا يجوز دعوى النسخ فيه بأخبار النهي والذي روي عن ذكوان عن أم سلمة رضي الله عنها في هذه القصة أنها قالت أفنقضيهما يا رسول الله إذا فاتتا قال لا وهذه رواية ضعيفة والذي تدل','675','508','0');
INSERT INTO [t6227] VALUES (918,'إذا عمل عملا أثبته وليس لغير رسول الله','675','508','0');
INSERT INTO [t6227] VALUES (919,'يصلي صلاة الفجر فصلى معه فلما سلم قام فصلى ركعتي الفجر','675','508','0');
INSERT INTO [t6227] VALUES (920,'ما هاتان الركعتان فقال لم أكن صليتهما قبل الفجر فسكت ولم يقل شيئا قال أبو عبد الله الحاكم رحمه الله قيس بن قهد صحابي والطريق إليه صحيح قال وقد رواه محمد بن إبراهيم التيمي عن قيس وذكر إسناده إليه عن قيس بن قهد قال رأى رسول','676','508','0');
INSERT INTO [t6227] VALUES (921,'أصلاة الصبح مرتين فقال الرجل لم أكن صليت الركعتين اللتين قبلهما فصليتهما الآن فسكت عنه رسول الله','676','508','0');
INSERT INTO [t6227] VALUES (922,'قال من أدرك ركعة من الصبح قبل أن تطلع الشمس فقد أدرك الصبح ومن أدرك ركعة من العصر قبل أن تغرب الشمس فقد أدرك العصر أخرجه البخاري في الصحيح قال الشافعي رضي الله عنه والعلم يحيط أن المصلي ركعة من قبل طلوع الشمس والمصلي ركعة قبل','676','508','0');
INSERT INTO [t6227] VALUES (923,'قال إذا أدرك أول سجدة من صلاة الصبح قبل أن تطلع الشمس فليتم صلاته وروى مالك عن نافع عن ابن عمر كان يصلي على الجنائز بعد العصر وبعد الصبح إذا صليتا لوقتهما فإن عارضوا بما روي عن سالم أنه شهد أباه وشهد جنازة رافع بن خديج حين صلى','677','508','0');
INSERT INTO [t6227] VALUES (924,'رضي الله عنها حين صلوا الصبح وذكر يحيى بن معين أن يحيى القطان روى عن عنبسة الوزان عن أبي لبابة مروان عن أبي هريرة أنه صلى على جنازة والشمس على أطراف الحيطان وعن كعب بن مالك أنه سجد للشكر حين بشر بتوبة الله عليه وعلى صاحبه بعد صلاة الصبح','679','508','0');
INSERT INTO [t6227] VALUES (925,'وروى الشافعي عن سفيان بإسناده عن جبير بن مطعم أن رسول الله','679','508','0');
INSERT INTO [t6227] VALUES (926,'قال يا بني عبد المطلب أو يا بني عبد مناف لا تمنعوا أحدا يطوف بالبيت ويصلي فإنه لا صلاة بعد الصبح حتى تطلع الشمس ولا صلاة بعد العصر حتى تغرب الشمس إلا عند هذا البيت يطوفون ويصلون وعن مجاهد قال قدم علينا أبو ذر فأخذ بحلقة باب الكعبة ثم','680','508','0');
INSERT INTO [t6227] VALUES (927,'لم يدخل بيتها إلا صلاهما رواه البخاري في الصحيح روى الشافعي عن سفيان عن','680','508','0');
INSERT INTO [t6227] VALUES (928,'تقولون لا صلاة بعد العصر حتى تغرب الشمس فقال إن هذه البلدة بلدة ليست كغيرها وعن ابن أيمن عن أبيه طاف مع أبي سعيد الخدري قبل صلاة المغرب ثم ركع ركعتين وعن ابن أبي نجيح عن أبيه قال قدم علينا أبو سعيد الخدري فطاف بعد الصبح','681','508','0');
INSERT INTO [t6227] VALUES (929,'نهى عن الصلاة نصف النهار وحتى تزول الشمس إلا يوم الجمعة قال الشافعي رحمه الله تعالى والنهي عن الصلاة في هذه','682','508','0');
INSERT INTO [t6227] VALUES (930,'أنه كره الصلاة نصف النهار إلا يوم الجمعة وروي عن مجاهد عن أبي الخليل عنه قال أبو داود هذا مرسل أبو الخليل لم يسمع من أبي قتادة ومجاهد أكبر من أبي الخليل قال البيهقي رضي الله عنه واعتمادي في المسألة على ما أخبرنا أبو عبد الله وذكر','683','508','0');
INSERT INTO [t6227] VALUES (931,'قال من اغتسل يوم الجمعة واستاك ولبس أحسن ثيابه وتطيب بطيب إن وجده ثم جاء ولم يتخط الناس فصلى ما شاء الله أن يصلي فإذا خرج الإمام سكت فذلك كفارة إلى','683','508','0');
INSERT INTO [t6227] VALUES (932,'استحب التبكير إلى صلاة الجمعة في أخبار كثيرة وندبه إلى الصلاة بعد مجيئه المسجد إلى أن يخرج الإمام ثم ندبه الإمساك والإنصات للخطبة فدل ذلك على جواز فعل الصلاة نصف النهار إذ لو كان ممنوعا منه لما مده إلى الخروج الإمام ولما أطلق له جواز فعل','684','508','0');
INSERT INTO [t6227] VALUES (933,'بيده يقللها لفظ حديث البخاري وفي أخرى عند مسلم عنه قال قال رسول الله','684','508','0');
INSERT INTO [t6227] VALUES (934,'وهذا لسائر الأيام والله أعلم','684','508','0');
INSERT INTO [t6227] VALUES (935,'مسألة','685','508','0');
INSERT INTO [t6227] VALUES (936,'يصليهما بعد العصر فقالت كان يصليهما قبل العصر ثم إنه شغل عنها ونسيهما فصلاهما بعد العصر ثم أثبتها وكان إذا صلى أثبتها وروي عن أبي هريرة أن النبي','685','508','0');
INSERT INTO [t6227] VALUES (937,'كان إذا فاتته الصلاة من الليل من وجع أو غيره صلى من النهار ثنتي عشرة ركعة وصح عن ابن عمر أنه كان يعيد ركعتي الفجر بعد طلوع الشمس','685','508','0');
INSERT INTO [t6227] VALUES (938,'مسألة','686','508','0');
INSERT INTO [t6227] VALUES (939,'قال من أدرك ركعة من الصبح قبل أن تطلع الشمس فقد أدرك الصبح لفظ حديث البخاري وروي عنه عن رسول الله','686','508','0');
INSERT INTO [t6227] VALUES (940,'قال من صلى من صلاة الصبح ركعة قبل أن تطلع الشمس فطلعت فليصل إليها أخرى وعن همام قال سئل قتادة عن رجل صلى ركعة من صلاة الصبح ثم طلع قرن الشمس قال حدثني خلاس عن أبي رافع عن أبي هريرة عن','686','508','0');
INSERT INTO [t6227] VALUES (941,'قال يتمها رواة هذه الأحاديث كلهم ثقات وفتوى أبي هريرة رضي الله عنه بذلك بعد وفاة رسول الله','687','508','0');
INSERT INTO [t6227] VALUES (942,'عن صلاة الليل فقال رسول الله','688','508','0');
INSERT INTO [t6227] VALUES (943,'قالت كان رسول الله','688','508','0');
INSERT INTO [t6227] VALUES (944,'يقول ركعة من آخر الليل أخرجه مسلم في الصحيح قال البيهقي رحمه الله تعالى سمعت الشيخ الإمام أبا الطيب سهل بن محمد بن سليمان قدس الله روحه يستدل في جواز الوتر بركعة واحدة بقول النبي','688','508','0');
INSERT INTO [t6227] VALUES (945,'جعل ركعتي القاعد مثل ركعة يركعها القائم ولو لم تكن الركعة الواحدة صلاة لم تكن ركعتا القاعد التي جعلها النبي','689','508','0');
INSERT INTO [t6227] VALUES (946,'كان يقرأ في الركعتين التي يوتر بعدهما سبح اسم ربك الأعلى وقل يا أيها الكافرون ويقرأ في الوتر ب قل هو الله أحد وقل قل أعوذ برب الفلق وقل أعوذ برب الناس قال وقد روينا صحة الوتر بركعة واحدة عن أمير','689','508','0');
INSERT INTO [t6227] VALUES (947,'وفي رواية عنده أيضا في الصحيح عن ابن أبي مليكة قال قيل لابن عباس هل لك في أمير المؤمنين معاوية ما أوتر إلا بواحدة قال أصاب إنه فقيه قال الربيع بن سليمان سئل الشافعي رحمه الله عن الوتر أيجوز أن يوتر الرجل بواحدة ليس قبلها شيء','691','508','0');
INSERT INTO [t6227] VALUES (948,'كان يصلي بالليل إحدى عشرة ركعة يوتر منها بواحدة وأخبرنا مالك عن نافع أن ابن عمر كان يسلم','691','508','0');
INSERT INTO [t6227] VALUES (949,'وورد به الأثر','692','508','0');
INSERT INTO [t6227] VALUES (950,'كان يصلي من الليل ثلاث عشرة ركعة وكان يوتر بخمس سجدات لا يجلس بينهن حتى يجلس في الأخيرة ثم يسلم أخرجه مسلم في الصحيح وروى الشافعي بإسناده عنها أن النبي','693','508','0');
INSERT INTO [t6227] VALUES (951,'قال قام فصلى ركعتين ثم ركعتين حتى صلى ثمان ركعات ثم أوتر بخمس لم يجلس بينهن وروي عن حبيب المعلم قال قيل للحسن أن ابن عمر كان يسلم في الركعتين من الوتر فقال كان عمر رضي الله عنه أفقه منه كان ينهض في الثالثة بالتكبيرة وروي عن عطاء','693','508','0');
INSERT INTO [t6227] VALUES (952,'قالت كنا نعد لرسول الله','694','508','0');
INSERT INTO [t6227] VALUES (953,'ويدعوه ثم يسلم تسليمة يسمعنا ثم يصلي ركعتين بعدها يسلم وهو قاعد فتلك إحدى عشرة ركعة فلما أسن النبي','694','508','0');
INSERT INTO [t6227] VALUES (954,'إذا غلبه قيام الليل صلى من النهار اثنتي عشرة ركعة أخرجه مسلم في الصحيح','694','508','0');
INSERT INTO [t6227] VALUES (955,'من ثلاثة أوجه أحدها ما رواه الزهري عن عروة عنها في وتره','695','508','0');
INSERT INTO [t6227] VALUES (956,'بخمس لا يقعد ولا يسلم إلا في آخرهن الثالث ما رواه سعد بن هشام عنها والرواية عنه فيه متعارضة فإن ذهبنا إلى المرجح فحديث عروة بن الزبير أولى لأنه أعرف بحديث عائشة رضي الله عنها من سعد بن هشام وكما أظن لا يخفى هذا على عالم لقربه من عائشة','695','508','0');
INSERT INTO [t6227] VALUES (957,'لا توتروا بثلاث فتشبهوه بصلاة المغرب وأوتروا بسبع أو بخمس رواته كلهم ثقات وله شاهد آخر بإسناد صحيح وذكر بمعناه وزاد أوتروا بخمس أو سبع أو بتسع أو إحدى عشرة أو أكثر من ذلك والكوفيون لا يجيزون الوتر بأكثر من ثلاث ولا بأقل ويخالفون','696','508','0');
INSERT INTO [t6227] VALUES (958,'مسألة','698','508','0');
INSERT INTO [t6227] VALUES (959,'مسألة','699','508','0');
INSERT INTO [t6227] VALUES (960,'في صلاة الصبح قبل الركوع أو بعده قال بعد الركوع يسيرا أخرجه البخاري ومسلم في الصحيح وروى البخاري في الصحيح عن عاصم قال سألت أنس بن مالك عن القنوت فقال قد كان القنوت قلت قبل الركوع أو بعده قال قبله فقلت فإن فلانا أخبرني','699','508','0');
INSERT INTO [t6227] VALUES (961,'عهد فقنت','699','508','0');
INSERT INTO [t6227] VALUES (962,'شهرا يدعو عليهم وروي عن أبي جعفر الرازي عن عاصم بخلاف هذا قال قلت لأنس إنهم يقولون أن رسول الله','700','508','0');
INSERT INTO [t6227] VALUES (963,'بعد الركوع يدعو على حي من أحياء العرب وكان يقنت قبل ذلك قبل الركوع هكذا وجدته في كتابي ورواته كلهم ثقات وروي في حديث حميد الطويل عن أنس كلاهما قال قلت لأنس كيف كنتم تقنتون أقبل الركوع أو بعد قال كل ذلك يفعل قبل وبعد','700','508','0');
INSERT INTO [t6227] VALUES (964,'في صلاة الصبح فوجدنا آخر الأمرين من رسول الله','701','508','0');
INSERT INTO [t6227] VALUES (965,'قبل الركوع أو بعده قال بعده ولم يزد على ذلك وهو مخرج في الصحيحين وفي رواية ابن علية عن أيوب عن محمد قال ثم سئل بعد ذلك هل قنت رسول الله','701','508','0');
INSERT INTO [t6227] VALUES (966,'قنت في صلاة الصبح بعد الركوع وتابعهما على ذلك علي بن أبي طالب وعبد الله بن عمر وجابر بن عبد الله وخفاف بن إيماء والبراء بن عازب وعائشة رضي الله عنهم فرووا عن النبي','701','508','0');
INSERT INTO [t6227] VALUES (967,'القنوت إلى ما بعد الركوع ودعا على أحياء من أحياء العرب في قنوته شهرا ثم ترك الدعاء عليهم ولم يترك القنوت ولم يرده إلى ما بعد الركوع حتى فارق الدنيا وحديث أبي جعفر الرازي يدل عليه فإنه قال فكان يقنت قبل ذلك قبل الركوع فأخبر أن قنوته قبله','702','508','0');
INSERT INTO [t6227] VALUES (968,'في وتر إذا رفعت رأسي ولم يبق إلا السجود اللهم اهدني فيمن هديت وعافني فيمن عافيت وتولني فيمن توليت وبارك لي فيما أتيت وقني شر ما قضيت إنك تقضي ولا يقضى عليك إنه لا يذل من واليت تباركت ربنا وتعاليت قال أبو عبد الله الحاكم رحمه الله هذا','703','508','0');
INSERT INTO [t6227] VALUES (969,'لأنظر كيف يقنت فقنت قبل الركوع هذا حديث لم يكتبه إلا من حديث أبان وأبان متروك الحديث لا يحل الاحتجاج به وقد سبق ذكرنا له في كتاب الطهارة وقد روي عن','703','508','0');
INSERT INTO [t6227] VALUES (970,'يوتر سبح اسم ربك الأعلى وقل يا أيها الكافرون وقل هو الله أحد وكان يقنت قبل الركوع قال أبو الأعلى الحافظ لم يسنده غير مخلد يعني ابن يزيد إن كان حفظه وروى وكيع وابن المبارك وأبو نعيم وغيرهم عن الثوري عن زبيد','704','508','0');
INSERT INTO [t6227] VALUES (971,'قنت في الوتر قبل الركوع وروى عيسى بن يونس هذا الحديث أيضا عن فطر بن خليفة عن زبيد عن سعيد بن عبد الرحمن بن أبزي عن أبيه عن أبي عن النبي','705','508','0');
INSERT INTO [t6227] VALUES (972,'لم يذكر القنوت ولا ذكر أبيا وكذلك رواه عبد الأعلى ومحمد بن بشر العبدي وسماعهما بالكوفة مع عيسى بن يونس ولم يذكر القنوت ورواه أيضا هشام الدستوائي وشعبة عن قتادة لم يذكر القنوت وحديث زبيد رواه سليمان الأعمش وشعبة وعبد الملك بن أبي سليمان','705','508','0');
INSERT INTO [t6227] VALUES (973,'قنت قبل الركوع وروى عطاء بن مسلم الخفاف وهو ضعيف عن العلاء عن حبيب عن ابن عباس رضي الله عنهما قال نبئت عن خالتي ميمونة فذكر حديثا وقال في آخره قنت وركع','706','508','0');
INSERT INTO [t6227] VALUES (974,'ويقنت بالدعاء الذي علمه رسول الله','706','508','0');
INSERT INTO [t6227] VALUES (975,'قال كان يعلمنا هذا الدعاء اللهم اهدني فيمن هديت وعافني فيمن عافيت وتولني فيمن توليت وبارك لي في ما أعطيت وقني شر ما قضيت إنك تقضي ولا يقضى عليك وإنه لا يذل من واليت تباركت ربنا وتعاليت وفي رواية عنه قال علمني كلمات أقولهن في قنوت','707','508','0');
INSERT INTO [t6227] VALUES (976,'مسألة','708','508','0');
INSERT INTO [t6227] VALUES (977,'قال صلاة الليل مثنى مثنى','708','508','0');
INSERT INTO [t6227] VALUES (978,'صلاة الليل والنهار مثنى مثنى والوتر ركعة من آخر الليل قال الحاكم هذا حديث غريب بهذا الإسناد ولم يكتبه إلا عن هذا الشيخ ورواته كلهم ثقات ولا أعرف له علة وروي عن علي رضي الله عنه عن النبي','710','508','0');
INSERT INTO [t6227] VALUES (979,'قال الصلاة مثنى مثنى فيشهد في كل ركعتين ثم تضرع وتخشع وتمسكن وترفع يديك تقول تستقبل بهما وجهك وتقول يا رب يا رب فمن لم يفعل فهي خداج ورواه شعبة مخالف في إسناده وبعض لفظه وذكر أبو عيسى الترمذي أن البخاري قال تلك','710','508','0');
INSERT INTO [t6227] VALUES (980,'وكان يصلي أربعا قبل الظهر فسألته عن ذلك فقال إن أبواب السماء تفتح فما تغلق حتى يصلى الظهر قال قلت يا رسول الله أيسلم بينهن قال لا إلا في آخرهن ومداره عن ابن عبيدة الضبي وهو ضعيف قال','711','508','0');
INSERT INTO [t6227] VALUES (981,'من صلى أربعا قبل الظهر ثم رآني أكتبه فقال لا تكتبه لا تكتبه أما أنه من عتيق حديثه وقال ابن معين عبيدة الضبي ليس بشيء وقال البخاري ليس بالقوي وقد روي من وجه آخر عن أيوب ببعض معناه وليس فيه ذكر التسليم وروى عمرو بن هارون حدثنا عن','712','508','0');
INSERT INTO [t6227] VALUES (982,'مسألة','713','508','0');
INSERT INTO [t6227] VALUES (983,'في صلاة الصبح فصلى ركعتين قبل أن يصل إلى الصف فلما انصرف رسول الله','713','508','0');
INSERT INTO [t6227] VALUES (984,'مر برجل فكلمه بشيء لا يدري ما هو فلما انصرف أحطنا به ماذا قال لك رسول الله','713','508','0');
INSERT INTO [t6227] VALUES (985,'رجلا يصلي والمؤذن يقيم فقال أتصلي الصبح أربعا وروي عن ابن عباس قال كنت أصلي','713','508','0');
INSERT INTO [t6227] VALUES (986,'فقال أتصلي أربعا وعند أبي داود عن أبي هريرة قال قال رسول الله','714','508','0');
INSERT INTO [t6227] VALUES (987,'قال إذا أقيمت الصلاة فلا صلاة إلا المكتوبة وزاد إلا ركعتي الصبح وهذه الزيادة لا أصل لها عباد وحجاج ضعيفان وروى ما قالوه عن أبي الدرداء وعبد الله أنهما فعلاه وقد روينا عن عمر خلاف ذلك وعن ابنه مع ما روينا من السنة الصحيحة فهو أولى','714','508','0');
INSERT INTO [t6227] VALUES (988,'مسألة','715','508','0');
INSERT INTO [t6227] VALUES (989,'ثم يأتي فيؤم قومه أخرجه البخاري ومسلم في الصحيح وعند مسلم أيضا عنه كان معاذ رضي الله عنه يصلي مع النبي','715','508','0');
INSERT INTO [t6227] VALUES (990,'العشاء ثم ينصرف فيأتي قومه فيصلي بهم تلك الصلاة ورواه أبو عاصم عن ابن جريج عن عمرو بن دينار عن جابر أن معاذ رضي الله عنه كان يصلي مع رسول الله','715','508','0');
INSERT INTO [t6227] VALUES (991,'الحال في الإمامة ولو كان تفصيل بين أن يكون الإمام متطوعا أو مؤديا فرضا لاستفصل رسول الله','717','508','0');
INSERT INTO [t6227] VALUES (992,'صلاة الخوف فصلى رسول الله','717','508','0');
INSERT INTO [t6227] VALUES (993,'أربع ركعات وصلى بكل طائفة ركعتين أخرجه مسلم في الصحيح قال الشافعي والآخرة من هاتين للنبي','718','508','0');
INSERT INTO [t6227] VALUES (994,'المسجد والناس في صلاة العصر قد فرغوا من صلاة الظهر فصلوا مع النبي فلما فرغوا قال بعضهم لبعض كيف صنعتم قال أحدهم جعلتها الظهر ثم صليت العصر وقال الآخر جعلتها للعصر ثم صليت الظهر وقال الآخر جعلتها للمسجد ثم صليت الظهر والعصر فلم يعب','718','508','0');
INSERT INTO [t6227] VALUES (995,'مسألة','719','508','0');
INSERT INTO [t6227] VALUES (996,'قال لمعاذ أفتان أنت ولم يأمر الرجل بإعادة الصلاة التي أتمها لنفسه منفردا والله أعلم','719','508','0');
INSERT INTO [t6227] VALUES (997,'إذا صلى في بيته بصلاة الإمام في المسجد وبينهما حائل لم يجز وقال أبو حنيفة يصح ما لم يكن بينه وبين المسجد شارع أو نهر لنا حديث علي وعائشة وأنس رضي الله عنهم قال الشافعي قد صلى نسوة مع عائشة زوج النبي','719','508','0');
INSERT INTO [t6227] VALUES (998,'مرفوعا وروى الشافعي بإسناده عن صالح بن إبراهيم قال رأيت أنس بن مالك صلى الجمعة في بيوت حميد بن عبد الرحمن بن عوف يصلي بصلاة الإمام في المسجد وبين بيوت حميد والمسجد الطريق وروي أيضا بإسناده عن هشام بن عروة عن أبيه أنه كان يصلي','720','508','0');
INSERT INTO [t6227] VALUES (999,'مسألة','721','508','0');
INSERT INTO [t6227] VALUES (1000,'قال أمرت أن أقاتل الناس حتى يشهدوا أن لا إله إلا الله وأن محمدا رسول الله ويقيمون الصلاة ويؤتوا الزكاة فإذا فعلوا ذلك عصموا مني دمائهم وأموالهم إلا بحق الإسلام وحسابهم على الله أخرجه البخاري في الصحيح وعنده أيضا في الصحيح عن أنس بن','721','508','0');
INSERT INTO [t6227] VALUES (1001,'مسألة','722','508','0');
INSERT INTO [t6227] VALUES (1002,'مروا أبا بكر فليصل بالناس ففيه أن أبا بكر دخل في الصلاة وأن رسول الله','722','508','0');
INSERT INTO [t6227] VALUES (1003,'يصلي بالناس جالسا وأبو بكر قائما يقتدي بصلاة رسول الله','722','508','0');
INSERT INTO [t6227] VALUES (1004,'لما خرج للطهارة بقوا هم في الصلاة منفردين إلى أن رجع وعلقوا صلاتهم على صلاته والله أعلم','722','508','0');
INSERT INTO [t6227] VALUES (1005,'مسألة','723','508','0');
INSERT INTO [t6227] VALUES (1006,'إذا كنتم ثلاثة فليؤمكم أحدكم وليؤمكم أقرأوكم أخرجه مسلم احتج في كتاب البويطي في جواز إمامته وإن كان الاختيار أن لا يؤم إلا بالغ فحديث معاذ رضي الله عنه حين كان يصليها فصلى مع رسول الله','723','508','0');
INSERT INTO [t6227] VALUES (1007,'مسألة','724','508','0');
INSERT INTO [t6227] VALUES (1008,'قال صلاة الجماعة أفضل من صلاة الفذ بسبع وعشرين درجة وروي أن رسول الله','725','508','0');
INSERT INTO [t6227] VALUES (1009,'وروي عن عائشة أن رسول الله','725','508','0');
INSERT INTO [t6227] VALUES (1010,'مسألة','726','508','0');
INSERT INTO [t6227] VALUES (1011,'قال يا أهل مكة لا تقصروا الصلاة في أدنى من أربع برد من مكة إلى عسفان رواه عن إسماعيل بن عياش قال ولا اعتمده بأن','727','508','0');
INSERT INTO [t6227] VALUES (1012,'لا تسافر المرأة ثلاثا إلا ومعها ذو محرم ولا حجة لهم فيه فقد روي مسيرة يوم وروي مسيرة ليلة وروي مسيرة يومين وروي بريدا فعن أبي هريرة قال قال رسول الله','729','508','0');
INSERT INTO [t6227] VALUES (1013,'يقول لا يخلون رجلا بامرأة ولا تسافر امرأة إلا ومعها ذو محرم وأطلق ولم','729','508','0');
INSERT INTO [t6227] VALUES (1014,'إنما قصد بهذه الأخبار الحياطة على المرأة دون تحديد السفر بشيء من ذلك وروي عن الحسن في التقصير قال في ليلتين والله أعلم','730','508','0');
INSERT INTO [t6227] VALUES (1015,'والقصر في السفر مباح وليس بفرض وقال أبو حنيفة فرض السفر ركعتان وما زاد عليهما فهو تطوع وبناء المسألة لنا على الكتاب قال الله جل ثناؤه وإذا ضربتم في الأرض فليس عليكم جناح أن تقصروا من الصلاة وعن يعلى بن أمية قال قلت لعمر بن الخطاب','730','508','0');
INSERT INTO [t6227] VALUES (1016,'على أن القصر في السفر بلا خوف صدقة من الله والصدقة رخصة لا حتم من الله أن تقصروا وعن عائشة رضي الله عنها قالت كل ذلك فعل رسول الله','731','508','0');
INSERT INTO [t6227] VALUES (1017,'صام في السفر وأفطر وأتم وقصر وهو مرسل صحيح شاهد للمسند وقصر عثمان وأتم وقصرت عائشة وأتمت وأخرج البخاري ومسلم في الصحيح عن ابن عمر قال صليت مع النبي','731','508','0');
INSERT INTO [t6227] VALUES (1018,'في عمرة رمضان فأفطر رسول الله','732','508','0');
INSERT INTO [t6227] VALUES (1019,'في الحضر أربعا وفي السفر ركعتين وفي الخوف ركعة وعندهما في الصحيح عن ابن مسعود رضي الله عنه قال صليت مع رسول الله','732','508','0');
INSERT INTO [t6227] VALUES (1020,'يقول إذا تأهل الرجل ببلد فليصل صلاة مقيم وروي عن الزهري أن عثمان إنما صلى بمنى أربعا لأنه أجمع الإقامة','733','508','0');
INSERT INTO [t6227] VALUES (1021,'وسنة صاحبيه ولكنه حدث العام من الناس فخفت أن يستنوا فهو يؤكد قول الزهري وفي ذلك دلالة على أن صلاة السفر على ركعتين ثم هي كانت تتم وتأولت ما تأول عثمان رضي الله عنه وتأول عثمان رضي الله عنه في ذلك أنه رأى القصر رخصة والرخصة بخلاف','735','508','0');
INSERT INTO [t6227] VALUES (1022,'يصلي بمنى ركعتين وأبو بكر وعمر وعمثان ثم صلى عثمان بعد أربع وكان ابن عمر يصلي مع الإمام بصلاته وإذا صلى وحده صلى ركعتين وروي عن أبي مجلز قال سألت ابن عمر المسافر يدرك ركعتين من صلاة المقيمين أفتجزآنه قال بلى يصلي بصلاتهم وعن','736','508','0');
INSERT INTO [t6227] VALUES (1023,'مسألة','737','508','0');
INSERT INTO [t6227] VALUES (1024,'قال يمكث المهاجر بعد قضاء نسكه ثلاثا أخرجه البخاري ومسلم في الصحيح وعن عمر أنه أجلى أهل الذمة من الحجاز وضرب لمن يقدم منهم تاجرا مقام ثلاثة أيام قال الشافعي رحمه الله فأشبه ما وصفت أن يكون ذلك مقام السفر ما جاوزه مقام الإقامة','737','508','0');
INSERT INTO [t6227] VALUES (1025,'يقصر حتى أتى مكة فأقام بها عشرا يقصر حتى رجع فلا حجة لهم فيه وذلك لأن النبي','738','508','0');
INSERT INTO [t6227] VALUES (1026,'بمنى ثلاثا يقصر وقدم فأقام بمكة قبل خروجه إلى عرفة ثلاثا يقصر فلم يحسب اليوم الذي قدم فيه لأنه كان فيه سائرا ويصحبه ذلك في قدومه','738','508','0');
INSERT INTO [t6227] VALUES (1027,'وأصحابه لأربع خلون من العشر وهم مهلون الحج فأمرهم رسول الله','739','508','0');
INSERT INTO [t6227] VALUES (1028,'صلى الظهر يوم التروية بمنى وصلى العصر يوم النفر بالأبطح وفي حديث عائشة عندهما قالت فلما كان ليلة النفر قلت يا رسول الله إني لم أكن تطوفت بالبيت قال انطلقي مع أخيك إلى التنعيم فاعتمري وحاضت صفية ليلة النفر فقال لها رسول الله','739','508','0');
INSERT INTO [t6227] VALUES (1029,'مدلجا على العقبة','739','508','0');
INSERT INTO [t6227] VALUES (1030,'لم يمكث بموضع واحد أربعة أيام يقصر الصلاة ثم إن هذا ورد فيمن أقام بموضع ولم يجمع مكثا وكلامنا وقع فيمن أجمع مقام أربعة أيام وقد روينا عن علي رضي الله عنه بخلاف مذهبهم وعن ابن عمر قال إذا أزمعت بالإقامة ثنتي عشرة فأتم الصلاة وعن سالم','740','508','0');
INSERT INTO [t6227] VALUES (1031,'وإذا نوى مقام أكثر من أربعة عشر أو ثمانية عشر مع الخوف والحرب صار مقيما على أحد القولين وكذا إذا انتهى إلى بلد فأقام يقصر ما لم يجمع مكثا بها لا يجمع مقام أربع ولكنه أقام على شيء يراه بحج في اليوم واليومين فاستأخر به ذلك فلا يزال يقصر ما','740','508','0');
INSERT INTO [t6227] VALUES (1032,'أقام سبع عشرة بمكة يقصر الصلاة قال ابن عباس ومن أقام سبع عشرة قصر ومن أقام أكثر أتم كذا قال سبع عشرة وهو عند أبي داود وكذا في رواية وفي أخرى خمس عشرة وعند البخاري عن ابن عباس أقام رسول الله','741','508','0');
INSERT INTO [t6227] VALUES (1033,'بتبوك عشرين يوما يقصر الصلاة قال أبو داود','741','508','0');
INSERT INTO [t6227] VALUES (1034,'مرسلا وروي عن ابن أبي أنيسة أظنه يحيى عن أبي الزبير عن جابر قال غزوت مع رسول الله','742','508','0');
INSERT INTO [t6227] VALUES (1035,'تبوك فأقام بضع عشرة ليلة يصلي صلاة مسافر وهذا أشبه والله أعلم وروى الحسن بن عمارة وهو ضعيف مرفوعا عن ابن عباس أقام بخيبر أربعين يوما يصلي ركعتين وروي عن ابن عمر قال أقمت بأذربيجان ستة أشهر في أمارة عثمان فكنت أصلي ركعتين وعن أنس','742','508','0');
INSERT INTO [t6227] VALUES (1036,'مسألة','743','508','0');
INSERT INTO [t6227] VALUES (1037,'إذا عجل في السير جمع بين المغرب والعشاء وعند مسلم عن نافع عن ابن عمر أنه كان إذا جد به السير جمع بين المغرب والعشاء بعد أن يغيب الشفق ويذكر أن رسول الله','743','508','0');
INSERT INTO [t6227] VALUES (1038,'إذا جد به السير جمع بين المغرب والعشاء إلى ربع الليل اتفقت رواية عبد الله بن عمر وأيوب السختياني ويحيى بن سعيد الأنصاري وكذلك رواه عمر بن محمد بن زيد عن نافع على أن الجمع من ابن عمر كان بعد غيبوبة الشفق وروايتهم أولى من رواية محمد بن','744','508','0');
INSERT INTO [t6227] VALUES (1039,'بقوله كان رسول الله','745','508','0');
INSERT INTO [t6227] VALUES (1040,'إذا ارتحل قبل ترفع الشمس أخر الظهر إلى وقت العصر ثم ترك فجمع بينهما فإن زاغت الشمس قبل أن يرتحل صلى الظهر ثم ركب ورواه محمد بن سهل البخاري فزاد وإذا زاغت الشمس وهو في منزله صلى الظهر والعصر جميعا ثم ركب وعند مسلم في الصحيح عن أنس عن','745','508','0');
INSERT INTO [t6227] VALUES (1041,'إذا أراد أن يجمع بين الظهر والعصر في السفر','745','508','0');
INSERT INTO [t6227] VALUES (1042,'كان إذا كان في سفر فزالت الشمس صلى الظهر والعصر ثم ارتحل قال البيهقي وهذا تمام الحديث الأول لا أنه يخالفه والله أعلم وعند مسلم عن معاذ قال خرجنا مع رسول الله','746','508','0');
INSERT INTO [t6227] VALUES (1043,'عام تبوك فكان رسول الله','746','508','0');
INSERT INTO [t6227] VALUES (1044,'كان في غزوة تبوك إذا ارتحل قبل زيغ الشمس أخر الظهر حتى يجمعها إلى العصر فيصليهما جميعا وإذا ارتحل بعد زيغ الشمس صلى الظهر والعصر جميعا وكان إذا ارتحل قبل المغرب أخر المغرب حتى يصليها مع العشاء وإذا ارتحل بعد المغرب عجل العشاء فصلاها مع','746','508','0');
INSERT INTO [t6227] VALUES (1045,'غابت له الشمس بمكة فجمع بينها بسرف وقال أبو بكر بن إسحاق رحمه الله وبسرف من مكة تسعة أميال ولا خلاف أعلمه أنه لا ينهانا أن يسار من عند غروب الشفق تسعة أميال على شيء من الدواب فدل على أنه جمع بينهما بعد ذهاب بعض الليل وعند أبي داود عن هشام','747','508','0');
INSERT INTO [t6227] VALUES (1046,'ومن كتاب الجمعة','750','508','0');
INSERT INTO [t6227] VALUES (1047,'مسألة','750','508','0');
INSERT INTO [t6227] VALUES (1048,'مسجد عبد القيسي بجواثاء من البحرين وفي رواية عند أبي داود بجواثاء قرية من قرى البحرين وفي أخرى قرية من قرى','750','508','0');
INSERT INTO [t6227] VALUES (1049,'وروي عن ابن إسحاق حدثني محمد بن أبي أمامة بن سهل عن أبيه عن عبد الرحمن بن كعب يعني ابن مالك قال كنت قائد أبي حين ذهب بصره فإذا خرجت به إلى الجمعة فسمع الأذان صلى على أبي أمامة أسعد بن زرارة واستغفر له يعني فلبث كثيرا لا يسمع أذان','751','508','0');
INSERT INTO [t6227] VALUES (1050,'قال من ترك ثلاث جمع متهاونا طبع الله على قلبه وعنده عن طارق بن شهاب عن النبي','752','508','0');
INSERT INTO [t6227] VALUES (1051,'ولم يسمع منه شيئا وذكر البيهقي له إسنادا متصلا عن طارق عن أبي موسى فذكره بنحوه والله أعلم قال وروي عن عبد الله بن عبيد الله بن عتبة بن مسعود وهو من أكابر التابعين أنه قال كل قرية فيها أربعون رجلا فعليهم الجمعة ومثله عن عمر بن','752','508','0');
INSERT INTO [t6227] VALUES (1052,'والله أعلم','754','508','0');
INSERT INTO [t6227] VALUES (1053,'لم يذكرها الإمام تجب الجمعة على من كان خارج المصر يسمع النداء وقال أبو حنيفة لا يلزم إلا أهل البلد ودليلنا ما عند أبي داود في كتاب السنن عن عبد الله بن عمرو عن النبي','754','508','0');
INSERT INTO [t6227] VALUES (1054,'قال إنما الجمعة على من سمع النداء روي عن علي رضي الله عنه أنه قال لا صلاة لجار المسجد إلا في المسجد قيل ومن جار المسجد قال من أسمعه المنادي وعند مسلم في الصحيح عن ابن عمر وأبي هريرة أنهما سمعا رسول الله','755','508','0');
INSERT INTO [t6227] VALUES (1055,'ناس منهم وهو عندي فقال رسول الله','756','508','0');
INSERT INTO [t6227] VALUES (1056,'مسألة','756','508','0');
INSERT INTO [t6227] VALUES (1057,'قال على الخمسين جمعة وفي رواية الجمعة على الخمسين وليس على من دون الخمسين جمعة تفرد به جعفر وهو متروك الحديث وللأربعين تأثير فيما يقصد فيه الجماعة بدليل ما روي عن عائشة قالت سمعت رسول الله','757','508','0');
INSERT INTO [t6227] VALUES (1058,'وكنت آخر','757','508','0');
INSERT INTO [t6227] VALUES (1059,'لا يجتمع أربعون رجلا من المسلمين يدعون الله في أمر واحد إلا استجاب لهم حتى لو دعوا على جبل لأزالوه وفي الصحيحين قال كنا مع رسول الله','758','508','0');
INSERT INTO [t6227] VALUES (1060,'يقول ما من رجل مسلم يموت فيقوم على جنازته أربعون رجلا لا يشركون بالله شيئا إلا شفعهم الله فيه وعندهما عن جابر أن رسول الله','758','508','0');
INSERT INTO [t6227] VALUES (1061,'ليس معه إلا أربعين رجلا قال علي بن عمر لم يقل في هذا الحديث إلا أربعين رجلا غير علي بن عاصم عن حصين وخالفه أصحاب حصين فقالوا لم يبق مع النبي','759','508','0');
INSERT INTO [t6227] VALUES (1062,'مسألة','759','508','0');
INSERT INTO [t6227] VALUES (1063,'قال إذا دخل أحدكم المسجد فليركع ركعتين قبل أن يجلس','759','508','0');
INSERT INTO [t6227] VALUES (1064,'يخطب فقال أصليت قال لا قال قم فصل ركعتين وعند مسلم عن جابر قال جاء سليك الغطفاني والنبي','760','508','0');
INSERT INTO [t6227] VALUES (1065,'أصليت الركعتين فقال لا قال قم فصل ركعتين وتجوز فيهما وقال إذا جاء أحدكم والإمام يخطب فليركع ركعتين وليتجوز فيهما وروى عبيد بن محمد العبدي عن معتمر عن أبيه عن قتادة عن أنس قال دخل رجل من قيس المسجد ورسول الله','760','508','0');
INSERT INTO [t6227] VALUES (1066,'قم فاركع ركعتين وأمسك عن الخطبة حتى فرغ من صلاته قال علي بن عمر أسنده عبيد ووهم فيه الصواب عن معمر عن أبيه عن النبي','760','508','0');
INSERT INTO [t6227] VALUES (1067,'قال علي بن عمر هذا مرسل وأبو','760','508','0');
INSERT INTO [t6227] VALUES (1068,'يخطب فأومأ بيده إليه أن اجلس فجلس وقد رواه مالك بن إسماعيل الهروي عن شعبة بغير هذا اللفظ أن أباه ذكر أنه كان في الشمس ورسول الله','761','508','0');
INSERT INTO [t6227] VALUES (1069,'مسألة','761','508','0');
INSERT INTO [t6227] VALUES (1070,'قال إذا قلت لصاحبك أنصت يوم الجمعة والإمام يخطب فقد لغوت وروى','761','508','0');
INSERT INTO [t6227] VALUES (1071,'مسألة','763','508','0');
INSERT INTO [t6227] VALUES (1072,'يخطب يوم الجمعة قائما ثم يقعد ثم يقوم قال كما يفعلون اليوم وعند مسلم عن سماك قال نبأني جابر بن سمرة أن رسول الله','763','508','0');
INSERT INTO [t6227] VALUES (1073,'ولم يخطب إلا قائما قال وروي عن الشعبي قال أول من أحدث القعود على المنبر معاوية رضي الله عنه ويحتمل أنه إنما فعل ذلك لضعف أو مرض والله أعلم','764','508','0');
INSERT INTO [t6227] VALUES (1074,'والجلسة بين الخطبتين فريضة وقال أبو حنيفة هي سنة دلينا ما سبق ذكره من فعل رسول الله','764','508','0');
INSERT INTO [t6227] VALUES (1075,'يخطب يوم الجمعة خطبة واحدة قائما فلما ثقل خطب خطبتين وجلس بينهما يستريح والحسن بن عمارة ضعيف متروك الحديث والحكم لم يسمع من مقسم إلا أربعة أحاديث وهذا ليس منها وما روي عن أبي إسحاق قال رأيت عليا رضي الله عنه يخطب يوم الجمعة ثم لم','764','508','0');
INSERT INTO [t6227] VALUES (1076,'مسألة','765','508','0');
INSERT INTO [t6227] VALUES (1077,'يخطب الناس فيحمد الله ويثني عليه بما هو أهله ثم يقول من يهده الله فلا مضل له ومن يضلل فلا هادي له وخير الحديث كتاب الله وخير الهدي هدى محمد','765','508','0');
INSERT INTO [t6227] VALUES (1078,'يوم الجمعة وهو أيضا صحيح وعند مسلم عن جابر بن سمرة قال كانت للنبي','765','508','0');
INSERT INTO [t6227] VALUES (1079,'يوم الجمعة وهو يقرأ بها على المنبر في كل جمعة أخت عمرة هي أم هشام بنت حارثة بن النعمان وكأنها أخت عمرة بنت عبد الرحمن لأمها وعنده أيضا عن عبد الله بن محمد بن معن عن أبيه لحارثة بن النعمان قالت ما حفظت ق إلا من في رسول الله','766','508','0');
INSERT INTO [t6227] VALUES (1080,'واحدة ورواه محمد بن إسحاق عن عبد الله بن أبي بكر عن يحيى بن عبد الله عن أم هشام بنت حارثة بن النعمان وعن أبي داود عن الحكم بن حزن فذكر وفادته على رسول الله','766','508','0');
INSERT INTO [t6227] VALUES (1081,'فقام متكئا على عصا أو قوس فحمد الله وأثنى عليه كلمات خفيفات طيبات مباركات ثم قال أيها الناس إنكم لن تطيقوا أو لن تفعلوا كلما أمرتم به ولكن سددوا وأبشروا وعند مسلم عن ابن عباس أن ضمادا قدم مكة فذكر قصة فقال رسول الله','767','508','0');
INSERT INTO [t6227] VALUES (1082,'يخطب يوم الجمعة خطبتين بينهما جلسة وفي أكثر ما روينا أخبار على دوام فعل رسول الله','767','508','0');
INSERT INTO [t6227] VALUES (1083,'حتى فارق الدنيا والله أعلم وفي وجوب ذكر النبي','767','508','0');
INSERT INTO [t6227] VALUES (1084,'إلا كانت عليهم ترة يوم القيامة إن شاء أخذهم الله وإن شاء عفا عنهم وفي وجوب الوصية في الخطبة والموعظة روينا ما تيسر وكذلك التحميد وقراءة القرآن وكذلك الدعاء وورد فيه ما عند مسلم في الصحيح عن حصين عن عمارة بن رويبة رأى بشر بن مروان رافعا','768','508','0');
INSERT INTO [t6227] VALUES (1085,'ويوصي بتقوى الله ويقرأ شيئا من القرآن في الأولى ويحمد الله ويصلي على النبي','768','508','0');
INSERT INTO [t6227] VALUES (1086,'مسألة','769','508','0');
INSERT INTO [t6227] VALUES (1087,'قائم يخطب فاستقبل رسول الله','769','508','0');
INSERT INTO [t6227] VALUES (1088,'يديه ثم قال اللهم اغثنا اللهم اغثنا اللهم اغثنا قال أنس ولا والله ما نرى في السماء من السحاب ولا قزعة وما بيننا وبين سلع من بيت ولا دار قال فطلعت علينا من ورائه سحابة مثل','769','508','0');
INSERT INTO [t6227] VALUES (1089,'قائم يخطب فاستقبله قائما فقال يا رسول الله هلكت الأموال وانقطعت السبل فادع الله عز وجل يمسكها عنا قال فرفع رسول الله','770','508','0');
INSERT INTO [t6227] VALUES (1090,'يخطب فأقبل الحسن والحسين عليهما قميصان أحمران يعثران ويقومان فنزل فأخذهما فوضعهما بين يديه ثم قال صدق الله ورسوله أنما أموالكم وأولادكم فتنة رأيت ولدي هذين فلم أصبر حتى نزلت فأخذتهما ثم أخذ في خطبته قال','770','508','0');
INSERT INTO [t6227] VALUES (1091,'وهو يخطب قال فقلت يا رسول الله رجل غريب جاء يسأل عن دينه لا يدري ما دينه فأقبل النبي','771','508','0');
INSERT INTO [t6227] VALUES (1092,'أمر بغسل الجمعة وذكر الشافعي فيما بلغه عن أبي معاوية عن الأعمش عن منهال عن عباد أن عليا رضي الله عنه كان يخطب فجاء الأشعث وقد امتلأ المسجد وأخذوا مجالسهم فجعل يتخطى حتى دنا وقال غلبتنا عليك هذه الحمراء فقال علي رضي الله عنه ما بال هذه','771','508','0');
INSERT INTO [t6227] VALUES (1093,'أنخت راحلتي وحللت عيني فلبست حلتي فدخلت ورسول الله','772','508','0');
INSERT INTO [t6227] VALUES (1094,'فرماني الناس بالحدق فقلت لجليسي يا عبد الله هل ذكر رسول الله','772','508','0');
INSERT INTO [t6227] VALUES (1095,'يوم الجمعة فدخل يتخطى رقاب الناس فقال عبد الله رضي الله عنه جاء رجل يتخطى رقاب الناس يوم الجمعة والنبي','772','508','0');
INSERT INTO [t6227] VALUES (1096,'اجلس فقد آذيت قد روينا عن النبي','772','508','0');
INSERT INTO [t6227] VALUES (1097,'فخطب فقال أفلحت الوجوه فقالوا أفلح وجهك يا رسول الله فقال قتلتموه فقلنا نعم وما روى عن أبي الدرداء أو عن أبي ذر في سؤال أحدهم الآخر حين قرأ رسول الله','773','508','0');
INSERT INTO [t6227] VALUES (1098,'بذلك فقال صدق قال وهذا حديث مختلف في اسم الذي سأل واسم الذي سئل قد ذكرناه في كتاب المعرفة أن يكون المراد بقوله لا جمعة لك أي ليس لك أجر من استمع الخطبة وقوله إذا قلت لصاحبك أنصت والإمام يخطب فقد لغوت قال الشافعي رحمه الله في معناه','773','508','0');
INSERT INTO [t6227] VALUES (1099,'بكلامه فيدل على ما وصفت وأن الإنصات للإمام اختيار وإن قوله لغوت','773','508','0');
INSERT INTO [t6227] VALUES (1100,'مسألة','774','508','0');
INSERT INTO [t6227] VALUES (1101,'قال من أدرك ركعة من الصلاة فقد أدرك الصلاة أخرجه البخاري ومسلم في الصحيح فجعله رسول الله','774','508','0');
INSERT INTO [t6227] VALUES (1102,'من أدرك من الجمعة ركعة فليصل إليها أخرى وفي','774','508','0');
INSERT INTO [t6227] VALUES (1103,'من أدرك الإمام جالسا قبل أن يسلم فقد أدرك الصلاة قال علي بن عمر لم يروه هكذا غير نوح وهو ضعيف الحديث متروك أبو عصمة أقر على نفسه بالكذب وصدق في إقراره بذلك فإنه ظاهر في روايته ثم يعارضه ما هو أسلم طريقا منه عن ياسين بن معاذ عن','775','508','0');
INSERT INTO [t6227] VALUES (1104,'مسألة','776','508','0');
INSERT INTO [t6227] VALUES (1105,'كتاب صلاة الخوف','778','508','0');
INSERT INTO [t6227] VALUES (1106,'أخرجه البخاري ومسلم وذكر حديث أبي عياش الزرقي في صلاته','778','508','0');
INSERT INTO [t6227] VALUES (1107,'بذات الرقاع أخرجه مسلم في الصحيح وذكر حديث ابن عمر الذي عند مسلم وحديثه الذي عند البخاري وذكر حديث ابن عتبة عن ابن عباس وهو عند البخاري قال جميع هذه الروايات في كيفية صلاة الخوف صحيحة معمول بها على اختلافها باختلاف الخوف وإن','779','508','0');
INSERT INTO [t6227] VALUES (1108,'بذات الرقاع أو مثل صلاته ببطن','779','508','0');
INSERT INTO [t6227] VALUES (1109,'بعسفان والله أعلم','780','508','0');
INSERT INTO [t6227] VALUES (1110,'والصلاة جائزة مع شدة القتال والتحام الحرب رجالا وركبانا مستقبلي القبلة وغير مستقبلها بالإيماء ولا يجوز تأخيرها عن وقتها لأجل ذلك وقال أبو حنيفة يؤخر الصلاة عن وقتها ولا تصح مع المحاربة عن ابن جريج عن عبد الله بن كثير عن مجاهد قال','780','508','0');
INSERT INTO [t6227] VALUES (1111,'مثل قول مجاهد وزاد عن النبي','781','508','0');
INSERT INTO [t6227] VALUES (1112,'صلاة الخوف في بعض أيامه فقامت طائفة معه وطائفة بإزاء العدو فصلى بالذين معه ركعة ثم ذهبوا وجاء الآخرون فصلى بهم ركعة ثم قضت الطائفتان ركعة ركعة قال وقال ابن عمر فإذا كان خوف أكثر من ذلك فصلى راكبا أو قائما يومئ إيماء وروى الشافعي عن','781','508','0');
INSERT INTO [t6227] VALUES (1113,'وصححه البخاري رحمه الله تعالى وقد روى أبو داود بإسناده عن عبد الله بن أنيس قال بعثني','781','508','0');
INSERT INTO [t6227] VALUES (1114,'إلى خالد بن سفيان الهذلي وكان نحو عرنه وعرفات فقال اذهب فاقتله قال فرأيته وحضرت صلاة العصر فقلت إني لا أخاف أن يكون بيني وبينه ما أن أؤخر الصلاة فانطلقت أمشي وأصلي أومئ إيماء نحوه فلما دنوت منه قال من أنت قلت رجل من العرب بلغني','782','508','0');
INSERT INTO [t6227] VALUES (1115,'مسألة','783','508','0');
INSERT INTO [t6227] VALUES (1116,'أخذ حريرا فجعله في يمينه وأخذ ذهبا فجعله في شماله ثم قال إن هذين حرام على ذكور أمتي وعند البخاري في الصحيح في حديث حذيفة قال إن رسول الله','783','508','0');
INSERT INTO [t6227] VALUES (1117,'ومن كتاب العيدين','784','508','0');
INSERT INTO [t6227] VALUES (1118,'مسألة','784','508','0');
INSERT INTO [t6227] VALUES (1119,'نحو رواية نافع عن ابن عمر إلا أن الموقري متروك لا نقر به وروي بإسناد أمثل من حديث الموقري مرفوعا أيضا أنه','785','508','0');
INSERT INTO [t6227] VALUES (1120,'مثل ما روينا عن ابن عمر مرفوعا عن التكبير عند الغدو إلى المصلى','785','508','0');
INSERT INTO [t6227] VALUES (1121,'مسألة','786','508','0');
INSERT INTO [t6227] VALUES (1122,'نهى عن الصلاة بعد العصر حتى تغرب الشمس وعن الصلاة بعد الصبح حتى تطلع الشمس وروي عن سليمان التيمي قال رأيت أنس بن مالك والحسن بن أبي الحسن وجابر بن زيد وسعيد بن أبي الحسن يصلون قبل الإمام في العيد وعنه عن عبد الله الداناج قال','786','508','0');
INSERT INTO [t6227] VALUES (1123,'خرج يوم الفطر فصلى ركعتين لم يصل قبلهما ولا بعدهما أخرجه البخاري ومسلم في الصحيح وهكذا يجب للإمام أن لا يصلي قبلها للخبر فأما المأموم فمخالف للإمام فقد تنفل قوم قبل صلاة العيد وبعدها وآخرون قبلها وآخرون بعدها وآخرون تركوه كما يكونون','787','508','0');
INSERT INTO [t6227] VALUES (1124,'مسألة','788','508','0');
INSERT INTO [t6227] VALUES (1125,'كبر في العيد يوم الفطر سبعا وخمسا في الأولى سبع وفي الآخرة خمس سوى تكبيرة الصلاة ورأيت حكاية عن محمد بن إسماعيل البخاري رحمه الله أنه قال حديث عبد الله بن عبد الرحمن في هذا الباب صحيح وعن كثير بن عبد الله بن عمرو بن عوف عن أبيه عن','788','508','0');
INSERT INTO [t6227] VALUES (1126,'كان يكبر في الفطر والأضحى في الأول سبع تكبيرات وفي الآخرة خمس تكبيرات قبل القراءة وروى عنه عن أبي هريرة وعنه عن ابن عمر مرفوعا ولا يصح والموقوف على أبي هريرة لا شك في صحته وعن ابن عباس كان يكبر ثنتي عشرة تكبيرة سبعا في الأولى وخمسا في','789','508','0');
INSERT INTO [t6227] VALUES (1127,'كذلك رواه أبو إسحاق السبيعي وقال الثانية يقرأ ثم يكبر وعبد الرحمن بن ثابت ضعفه يحيى بن معين وقال وكان رجلا صالحا قال الشافعي رحمه الله في القديم وقال بعض الناس يكبر أربعا في الأولى بالتي يفتتح بها الصلاة ثم يقرأ ثم يكبر فيركع ثم يقوم','790','508','0');
INSERT INTO [t6227] VALUES (1128,'مع علمه وعلمهم به علمنا أنه لم يكبر خلاف تكبير رسول الله','791','508','0');
INSERT INTO [t6227] VALUES (1129,'علموه إياه وأنكروا عليه خلافه ولم يكن ذلك كفعل رجل في بلد كلهم يتعلم منه ليسوا كأهل المدينة وتكبير أبي هريرة عام لأنه بين ظهراني المهاجرين والأنصار وأهل العلم وروي عن الشافعي رحمه الله بإسناده أن النبي','791','508','0');
INSERT INTO [t6227] VALUES (1130,'مسألة','791','508','0');
INSERT INTO [t6227] VALUES (1131,'التكبير في الفطر سبع في الأولى وخمس في الآخرة والقراءة بعدهما كلتاهما وروي معنى ذلك عن أبي هريرة وابن عمر مرفوعا وعن أبي هريرة من فعله وقد تقدم استدلوا بما روى محمد بن معاوية النيسابوري عن ابن لهيعة عن خالد عن الزهري عن عروة عن','792','508','0');
INSERT INTO [t6227] VALUES (1132,'فقالت كان رسول الله','792','508','0');
INSERT INTO [t6227] VALUES (1133,'فقال كان رسول الله','793','508','0');
INSERT INTO [t6227] VALUES (1134,'مسألة','793','508','0');
INSERT INTO [t6227] VALUES (1135,'وعن جابر بن عبد الله قال مضت السنة أن يكبروا للصلاة في العيدين سبعا وخمسا بذكر الله ما بين كل تكبيرتين والله أعلم','793','508','0');
INSERT INTO [t6227] VALUES (1136,'ومبتدأ تكبير أيام التشريق بعد صلاة الظهر من يوم النحر إلى','793','508','0');
INSERT INTO [t6227] VALUES (1137,'أردفه من جمع إلى منى فلم يزل يلبي حتى رمى جمرة العقبة أخرجه البخاري ومسلم وعند مسلم عن بثينة عن النبي','794','508','0');
INSERT INTO [t6227] VALUES (1138,'قال كان يلبي الملبي فلا ينكر عليه ويكبر المكبر فلا ينكر عليه أخرجه البخاري ومسلم في الصحيح وروي عن عبيد بن عمير قال كان عمر بن الخطاب رضي الله عنه يكبر بعد صلاة الصبح من يوم عرفة إلى صلاة الظهر في آخر أيام التشريق وعن','795','508','0');
INSERT INTO [t6227] VALUES (1139,'كان يجهر في المكتوبات ببسم الله الرحمن الرحيم وكان يقنت في صلاة الفجر وكان يكبر من يوم عرفة صلاة الغداة ويقطعها صلاة العصر آخر أيام التشريق قال الحاكم أبو عبد الله لا أعلم في رواته منسوبا إلى الجرح والله أعلم','796','508','0');
INSERT INTO [t6227] VALUES (1140,'التكبير عندنا ثلاثا نسقا وعند أبي حنيفة ثنتين عند مسلم في الصحيح في حديث جابر الطويل في صفة الحج أنه','796','508','0');
INSERT INTO [t6227] VALUES (1141,'كان إذا استوى على بعيره خارجا إلى سفر كبر ثلاثا وعن ابن عمر أن رسول الله','797','508','0');
INSERT INTO [t6227] VALUES (1142,'من الابتداء بها مرتين وإن كان الكل واسعا وبالله التوفيق وروي عن عكرمة عن ابن عباس يكبر من غداة عرفة إلى آخر أيام النفر لا يكبر في المغرب الله أكبر الله أكبر الله أكبر ولله الحمد الله أكبر وأجل الله أكبر على ما هدانا ورواه الواقدي عنه','797','508','0');
INSERT INTO [t6227] VALUES (1143,'مسألة','798','508','0');
INSERT INTO [t6227] VALUES (1144,'أنه قال أيام التشريق أيام أكل وشرب وذكر الله وأنه','798','508','0');
INSERT INTO [t6227] VALUES (1145,'ومن كتاب صلاة الخسوف','800','508','0');
INSERT INTO [t6227] VALUES (1146,'مسألة','800','508','0');
INSERT INTO [t6227] VALUES (1147,'حين خسفت الشمس وعن ابن عباس أيضا','800','508','0');
INSERT INTO [t6227] VALUES (1148,'ابن عباس وابن عمر وجابر وعائشة وابن عمرو وأبي موسى رضي الله عنهم عن النبي','801','508','0');
INSERT INTO [t6227] VALUES (1149,'ما لم يثبته غيرهم والمثبت أولى قال الشافعي رحمه الله تعالى يحكى عن مناظره فقال روى بعضكم أن النبي','801','508','0');
INSERT INTO [t6227] VALUES (1150,'إن شاء الله وإذا كان عطاء بن يسار وصفوان بن عبد الله والحسن يروون عن ابن عباس خلاف ما روى سليمان الأحول كانت رواية ثلاثة أولى أن تقبل وعبد الله بن أبي بكر وزيد بن أسلم أكثر حديثا وأشهر بالعلم بالحديث من سليمان قال فقد روى عن ابن عباس','802','508','0');
INSERT INTO [t6227] VALUES (1151,'فعلها مرات وأن الجميع جائز وكأنه','803','508','0');
INSERT INTO [t6227] VALUES (1152,'مسألة','805','508','0');
INSERT INTO [t6227] VALUES (1153,'مسألة','805','508','0');
INSERT INTO [t6227] VALUES (1154,'والناس معه فقام قياما طويلا بنحو من سورة البقرة ثم ركع قال الشافعي رحمه الله لو سمعه ما قدر قراءته وقد روي عنه صريحا قال صليت مع رسول الله','806','508','0');
INSERT INTO [t6227] VALUES (1155,'وخطبته وروي بإسناد رواته ثقات عن عائشة في حديث كسوف الشمس وفيه فخرج رسول الله','806','508','0');
INSERT INTO [t6227] VALUES (1156,'جهر قال البخاري رحمه الله حديث عائشة رضي الله عنها في الجهر أصح من حديث سمرة قال البيهقي رحمه الله لكنه ليس بأصح من حديث ابن','806','508','0');
INSERT INTO [t6227] VALUES (1157,'بنحو من سورة البقرة قال الشافعي رحمه الله فيه دليل على أن لم يسمع ما قرأ لأنه لو سمعه لم يقدره بغيره قال وروي عن ابن عباس أنه قرأ قمت إلى جنب النبي','807','508','0');
INSERT INTO [t6227] VALUES (1158,'مسألة','807','508','0');
INSERT INTO [t6227] VALUES (1159,'إلى المصلى','807','508','0');
INSERT INTO [t6227] VALUES (1160,'يوما يستسقي فاستقبل القبلة وحول رداءه وصلى ركعتين فقرأ وجهر بالقراءة وروي عن طلحة بن يحيى قال أرسلني مروان إلى ابن عباس أسأله عن سنة الاستسقاء فقال سنة الاستسقاء سنة الصلاة في العيدين إلا أن رسول الله','808','508','0');
INSERT INTO [t6227] VALUES (1161,'يستسقي فصلى ركعتين بلا آذان ولا إقامة ثم خطبنا وحول وجهه نحو القبلة رافعا يديه ثم قلب رداءه فجعل الأيمن من الأيسر والأيسر على الأيمن رواة هذا الحديث كلهم ثقات استدلوا بحديث أنس في الرجل الذي دخل ورسول الله','809','508','0');
INSERT INTO [t6227] VALUES (1162,'فمطروا الحديث والاستسقاء عندنا على أنواع منها أن يدعو ولا يصلي ومنها أن يدعو خلال الخطبة مثل الجمعة والعيدين ولا يصلي لها ومنها أن يصلي لها ويخطب ويدعو كما يصلي في العيدين وهو أكملها والله أعلم','809','508','0');
INSERT INTO [t6227] VALUES (1163,'يستحب للإمام والمأموم إذا أراد الدعاء أن يقلب رداءه ويحوله وقال في القديم يجوز ولا يقلب وهو قول مالك وأحمد رحمهما الله تعالى وقال محمد يفعله الإمام دون المأموم وقال أبو حنيفة لا يفعله أحد لا الإمام ولا المأموم ودليلنا في ذلك من','809','508','0');
INSERT INTO [t6227] VALUES (1164,'مضى في المسألة آنفا وروي عن جابر أنه قال استسقى رسول الله','810','508','0');
INSERT INTO [t6227] VALUES (1165,'مسألة','810','508','0');
INSERT INTO [t6227] VALUES (1166,'أمرت أن أقاتل الناس حتى يشهدوا أن لا إله إلا الله وأن محمدا رسول الله ويقيموا الصلاة ويؤتوا الزكاة فإذا فعلوا عصموا مني دماءهم وأموالهم وحسابهم على الله وعندهما حديث أبي هريرة في مراجعة عمر أبا بكر في قتال مانعي الزكاة وفيه فقال أبو بكر','810','508','0');
INSERT INTO [t6227] VALUES (1167,'أباح دم المسلم الذي يشهد أن لا إله إلا الله وأن محمدا رسول الله بإحدى ثلاث فذكر منها كفره بعد إسلامه وفي بعض الروايات بعد إيمانه فيشبه أن يرتد بتركه للصلاة بعد أن كان يفعلها فقد سمى الله الصلاة إيمانا بقوله عز وجل وما كان الله ليضيع','811','508','0');
INSERT INTO [t6227] VALUES (1168,'يقول بين الرجل وبين الشرك والكفر ترك الصلاة وروي عن بريدة بن الحصيب قال قال رسول الله','811','508','0');
INSERT INTO [t6227] VALUES (1169,'لا يرون شيئا عن الأعمال تركه كفر غير الصلاة وفي الموطأ عن ابن شهاب عن عطاء بن يزيد عن عبيد الله بن عدي بن الخيار أنه حدثه عن رسول الله','812','508','0');
INSERT INTO [t6227] VALUES (1170,'أليس يشهد أن لا إله إلا الله وأن محمدا رسول الله قال بلى يا رسول الله ولا شهادة له فقال أليس يصلي قال ولا صلاة له فقال رسول الله','812','508','0');
INSERT INTO [t6227] VALUES (1171,'من كتاب الجنائز','814','508','0');
INSERT INTO [t6227] VALUES (1172,'مسألة','814','508','0');
INSERT INTO [t6227] VALUES (1173,'في قميصه وذكر عن أبي داود حديثا رواه عن علي رضي الله عنه أن النبي','814','508','0');
INSERT INTO [t6227] VALUES (1174,'مسألة','814','508','0');
INSERT INTO [t6227] VALUES (1175,'بعرفة','814','508','0');
INSERT INTO [t6227] VALUES (1176,'اغسلوه بماء وسدر وكفنوه في ثوبين أو قال في ثوبيه ولا تحنطوه ولا تخمروا رأسه فإن الله يبعثه يوم القيامة يلبي وما روي عن ابن عمر من خلاف ذلك فقد قال الشافعي رحمه الله ولعل ابن عمر رضي الله عنه لم يسمع هذا الحديث بل لا شك إن شاء الله','815','508','0');
INSERT INTO [t6227] VALUES (1177,'فليس لأحد خلافه والله أعلم','815','508','0');
INSERT INTO [t6227] VALUES (1178,'الزوج يغسل امرأته وقال أبو حنيفة لا يغسلها وروى الشافعي عن إبراهيم عن عمارة عن أم محمد بنت محمد بن','815','508','0');
INSERT INTO [t6227] VALUES (1179,'أوصت أن تغسلها إذا ماتت هي وعلي رضي الله عنه فغسلتها هي وعلي رضي الله عنهم هذا عجيب فإن أسماء كانت في ذلك الوقت عند أبي بكر وقد ثبت أنه لم يعلم بوفاة فاطمة بما في الصحيح أن عليا دفنها ليلا ولم يعلم أبا بكر فكيف يمكن أن تغسلها زوجته ولا','816','508','0');
INSERT INTO [t6227] VALUES (1180,'مسألة','817','508','0');
INSERT INTO [t6227] VALUES (1181,'حين أمرها أن تغسل ابنته قال ابدئي بميامنها ومواضع الوضوء وزاد البخاري منها والله أعلم','817','508','0');
INSERT INTO [t6227] VALUES (1182,'مسألة','818','508','0');
INSERT INTO [t6227] VALUES (1183,'قال فظفرنا رأسها وناصيتها وقرنيها ثلاث قرون وألقينا خلفها والله أعلم','818','508','0');
INSERT INTO [t6227] VALUES (1184,'يستحب أن يجعل في الغسلة الأخيرة كافورا وقال أبو حنيفة لا يستحب ذلك في حديث أم عطية أن رسول الله','818','508','0');
INSERT INTO [t6227] VALUES (1185,'مسألة','818','508','0');
INSERT INTO [t6227] VALUES (1186,'كفن في ثلاثة أثواب بيض سحولية ليس فيها قميص ولا عمامة وفي رواية عند مسلم سحولية من كرسف وفي أخرى عند البخاري سحولية كرسف استدلوا بما روى أبو داود عن ابن عباس قال كفن رسول الله','819','508','0');
INSERT INTO [t6227] VALUES (1187,'وقد مات في بيتها وبين سحرها ونحرها وأجابت عن قول من زعم أنه كفن','820','508','0');
INSERT INTO [t6227] VALUES (1188,'لكفنه فيها فباعها وتصدق بثمنها فكفتنا رضي الله عنها مؤنة الجواب عن ذلك ولله الحمد وقد روي من وجه آخر لا يصح أن رسول الله','820','508','0');
INSERT INTO [t6227] VALUES (1189,'في ثلاثة أثواب بيض ليس فيها قميص ولا عمامة وما روي في الحديث صحيح من','820','508','0');
INSERT INTO [t6227] VALUES (1190,'عبد الله بن أبي قميصه فإن ذلك عندنا جائز قال الشافعي رضي الله عنه فإن قمصه وعممه جاز ثم إنما فعله رسول الله','821','508','0');
INSERT INTO [t6227] VALUES (1191,'جازاه بذلك ويكفي في الترجيح أن ما اختاره الله تعالى لرسوله','821','508','0');
INSERT INTO [t6227] VALUES (1192,'مسألة','821','508','0');
INSERT INTO [t6227] VALUES (1193,'كان يجمع بين الرجلين من قتلى أحد وذكر الحديث وفيه يدفنهم بدمائهم ولم يصل عليهم ولم يغسلهم وروى عن أنس بن مالك أن شهداء أحد لم يغسلوا ودفنوا بدمائهم ولم يصل عليهم قال أبو عبد الله هذا حديث صحيح والاعتماد على حديث جابر فإنه الصحيح','822','508','0');
INSERT INTO [t6227] VALUES (1194,'خرج يوما فصلى على أهل أحد صلاته على الميت ثم انصرف إلى المنبر الحديث قلنا لا حجة لكم فيه لأنه','822','508','0');
INSERT INTO [t6227] VALUES (1195,'صلى على قتلى أحد بعد ثمان سنين كالمودع للأحياء والأموات','823','508','0');
INSERT INTO [t6227] VALUES (1196,'على قتلى أحد مع حمزة حتى صلى عليه اثنين وسبعين صلاة قائما رواه محمد بن إسحاق بن يسار عن رجل من أصحابه لم يسمه عن مقسم عن ابن عباس وهو رحمنا الله وإياه إذا روى عن مشهور منسوب ولا يذكر سماعه منه يكون فيه نظر لما اشتهر من تدليسه وروايته عن','823','508','0');
INSERT INTO [t6227] VALUES (1197,'على قتلى أحد فقال لم يصل على قتلى أحد وذكر بقية كلامه وروي عن أبي مالك مرسلا لا يصح ورواه إسماعيل بن عياش عن عبد الملك أو غيره وإسماعيل لا يحتج به في إسناده شك ورواه عبد العزيز بن أبي عمران أبو ثابت وعبد العزيز ضعيف قال ابن','824','508','0');
INSERT INTO [t6227] VALUES (1198,'لم يصل عليهم وقال زملوهم بكلومهم وأما حديث عقبة بن عامر قال فيه أنه','825','508','0');
INSERT INTO [t6227] VALUES (1199,'وقف على قبورهم ودعا لهم واستغفر لهم كما كان يدعو لغيرهم من الموتى حين علم قرب أجله كالمودع ولا يدل ذلك على نسخ الحكم في غير ما ورد فيه إلا على الوجه الذي حملنا الخبر عليه لم ينتسخ به ما ثبت من أحكامه فأما الذي روي عن شداد بن الهاد في','825','508','0');
INSERT INTO [t6227] VALUES (1200,'مسألة','826','508','0');
INSERT INTO [t6227] VALUES (1201,'مسألة','827','508','0');
INSERT INTO [t6227] VALUES (1202,'مسألة','828','508','0');
INSERT INTO [t6227] VALUES (1203,'مسألة','829','508','0');
INSERT INTO [t6227] VALUES (1204,'وأبا بكر وعمر رضي الله عنهما يمشون أمام الجنازة وفي بعض الروايات وعثمان وقد روي هذا الحديث مرسلا ووصله سفيان وتابعه منصور وبكر بن وائل وزياد بن سعد من رواية هشام عنهم وكلهم ثقات وكذلك روي عن عقيل بن خالد في بعض الروايات عنه وشعيب بن أبي','829','508','0');
INSERT INTO [t6227] VALUES (1205,'وروي عن الزهري عن أنس بن مالك أيضا وروى الشافعي عن مالك عن ابن المنكدر عن ربيعة بن عبد الله بن الهدير أنه رأى عمر بن الخطاب رضي الله عنه تقدم الناس أمام جنازة زينب بنت جحش وروى ذلك أيضا عن أبي قتادة وأبي أسيد وأبي هريرة وغيرهم رضي الله','830','508','0');
INSERT INTO [t6227] VALUES (1206,'أمام الجنازة وعن المغيرة عن رسول الله','831','508','0');
INSERT INTO [t6227] VALUES (1207,'عن المشي مع الجنازة فقال ما دون الجنب أن يكون خيرا يعجل إليه وإن يكن غير ذلك فبعدا لأهل الشر والجنازة متبوعة ولا','831','508','0');
INSERT INTO [t6227] VALUES (1208,'أن أمه توفيت وهي نصرانية وهو يحب أن يحضرها فقال اركب دابتك وسر أمامها فإنك إذا ركبت أمامها لم تتبعها هذا لا يثبت وأبو معشر نجيح ضعيف وروى يحيى بن سعيد القطان بسنده عن سهل بن سعد أن النبي','832','508','0');
INSERT INTO [t6227] VALUES (1209,'في المشي أمامها والأخذ به أولى وقد كانوا أئمة بموضع القدوة بهم فلا يفعلون في الظاهر إلا ما هو الأفضل عندهم والله أعلم','833','508','0');
INSERT INTO [t6227] VALUES (1210,'والولي أولى بالصلاة على الميت من الوالي وقال أبو حنيفة','833','508','0');
INSERT INTO [t6227] VALUES (1211,'يؤم القوم أقرؤهم لكتاب الله الحديث في ترتيب الأئمة وفيه ولا يؤمن الرجل في سلطانه وهذا ورد في الصلاة التي هي من الأمور العامة التي تكون ولايتها إلى الأئمة فأما الصلاة على الجنازة فإنها من الأمور الخاصة التي يكون الولي فيها أولى من','834','508','0');
INSERT INTO [t6227] VALUES (1212,'يقول إذا اجتمعت الجنازة والإمام فالإمام أولى من الوالي بالصلاة عليها وفيها إن مروان هو الذي صلى وهذا لا يصح فإن الحسين رضي الله عنه لا يصح له عن النبي','835','508','0');
INSERT INTO [t6227] VALUES (1213,'مسألة','835','508','0');
INSERT INTO [t6227] VALUES (1214,'ويكون مسندا وقد روينا عن رسول الله','836','508','0');
INSERT INTO [t6227] VALUES (1215,'مسألة','836','508','0');
INSERT INTO [t6227] VALUES (1216,'مسألة','837','508','0');
INSERT INTO [t6227] VALUES (1217,'أتى قبرا منبوذا فصفهم وتقدم فصلى عليه وكبر أربعا وعند أبي داود عن أبي هريرة إن امرأة سوداء أو رجلا كان يقم المسجد ففقده النبي','838','508','0');
INSERT INTO [t6227] VALUES (1218,'صلى على قبر امرأة بعدما دفنت وروى هذا الحديث عن زيد بن ثابت وأخيه يزيد بن ثابت وكان ممن شهد بدرا وعامر بن زمعة بمعنى حديث أبي هريرة وروى مذهبنا عن ابن عمر وعائشة وغيرهم من الصحابة رضي الله عنهم والله أعلم','838','508','0');
INSERT INTO [t6227] VALUES (1219,'مسألة','839','508','0');
INSERT INTO [t6227] VALUES (1220,'نعى للناس النجاشي في اليوم الذي مات فيه فخرج إلى المصلى وكبر أربع تكبيرات وعندهما أيضا عن جابر أن رسول الله','839','508','0');
INSERT INTO [t6227] VALUES (1221,'إن أخا لكم مات فقوموا فصلوا عليه يعني النجاشي وروي عنه بإسناد رواته ثقات أن النبي','839','508','0');
INSERT INTO [t6227] VALUES (1222,'من حديث الثقات عند مالك وإنما الصلاة دعاء للميت وهو إذا كان ملففا بيننا نصلي عليه فإنما يدعو له بالصلاة بوجه علمناه فكيف لا','839','508','0');
INSERT INTO [t6227] VALUES (1223,'مسألة','840','508','0');
INSERT INTO [t6227] VALUES (1224,'على ابني بيضاء في المسجد سهيل وأخيه وعنده أنها صلت على سعد بن أبي وقاص في المسجد وروى مالك عن نافع عن عبد الله بن عمر أنه صلى عمر بن الخطاب رضي الله عنه في','840','508','0');
INSERT INTO [t6227] VALUES (1225,'من صلى على جنازة في المسجد فلا شيء له قال صالح وأدركت رجالا ممن أدركوا النبي','841','508','0');
INSERT INTO [t6227] VALUES (1226,'على ابني بيضاء في المسجد سكتوا ولم يعارضوا بغيره والله أعلم وله الحمد','842','508','0');
INSERT INTO [t6227] VALUES (1227,'وسل الميت من قبل رأسه وقال العراقيون يوضع على شفير','842','508','0');
INSERT INTO [t6227] VALUES (1228,'سل من قبل رأسه وقال أخبرنا الثقات من أصحابنا أن قبر النبي','843','508','0');
INSERT INTO [t6227] VALUES (1229,'والمهاجرون والأنصار بين أظهرنا ينقل إلينا العامة عن العامة لا يختلفون في ذلك أن الميت يسل سلا ثم جاء آت من غير بلدنا يعلمنا كيف ندخل الميت ثم لم يرض حتى روى عن حماد عن إبراهيم أن رسول الله','843','508','0');
INSERT INTO [t6227] VALUES (1230,'من قبل رأسه وقال أخبرنا بعض أصحابنا عن أبي الزناد وربيعة وأبي النضر اختلاف بينهم في ذلك أن رسول الله','844','508','0');
INSERT INTO [t6227] VALUES (1231,'وحكى ابن المنذر سل الميت من قبل رجل القبر عن ابن عمر وأنس وعبد الله بن زيد الأنصاري واستدلوا بحديث ضعيف عن ابن عباس وعن ابن مسعود عن النبي','844','508','0');
INSERT INTO [t6227] VALUES (1232,'أدخل من قبل القبلة والله أعلم وله الحمد وبه الاستعانة','845','508','0');
INSERT INTO [t6227] VALUES (1233,'ذكر ما اختلف فيه الشافعي وأبو حنيفة رضي الله عنهما','846','508','0');
INSERT INTO [t6227] VALUES (1234,'من كتاب الزكاة','846','508','0');
INSERT INTO [t6227] VALUES (1235,'مسألة','846','508','0');
INSERT INTO [t6227] VALUES (1236,'مع غيره من الزكوات في كتابه الذي كتبه لأنس حين ولاه صدقة','846','508','0');
INSERT INTO [t6227] VALUES (1237,'فرواه عنه وهو في صحيح البخاري وروي ذلك أيضا عن ابن عمر في كتاب رسول الله','847','508','0');
INSERT INTO [t6227] VALUES (1238,'وعن الزهري عن أبي بكر بن محمد بن عمرو بن حزم عن أبيه عن جده عن النبي','847','508','0');
INSERT INTO [t6227] VALUES (1239,'وأبي بكر وعمر رضي الله عنهما في الصدقات وأما الشافعي رحمه الله فإنه قال في كتابه القديم روى هذا مجهول عن علي رضي الله عنه وأكثر الرواة عن ذلك المجهول يزعم أن الذي روى هذا غلط عليه وأن هذا ليس من حديثه نريد قوله في الاستئناف واستدل على هذا','849','508','0');
INSERT INTO [t6227] VALUES (1240,'كتبه لجده فقرأته فكان فيه ذكر ما يخرج من فرائض الإبل فنص الحديث إلى أن يبلغ عشرين ومائة فإذا كانت أكثر من ذلك فعد في كل خمسين حقه وما فضل فإنه يعاد إلى أول فريضة الإبل فهو منقطع بين أبي بكر إلى النبي','850','508','0');
INSERT INTO [t6227] VALUES (1241,'مسألة','852','508','0');
INSERT INTO [t6227] VALUES (1242,'على المسلمين فذكر الحديث على وفق ما قلنا به أخرجه البخاري في الصحيح والله أعلم','852','508','0');
INSERT INTO [t6227] VALUES (1243,'وفي أربعين من البقر مسنة وقال أبو حنيفة وفي خمسين سنة وربع سنة لنا عن معاذ أن رسول الله','852','508','0');
INSERT INTO [t6227] VALUES (1244,'أنه كتب إلى أهل اليمن قال فيه وفي كل ثلاثين باقورة تبيع جذع وفي كل أربعين باقورة بقرة وروى الشافعي أخبرنا مالك عن حميد بن قيس عن طاوس اليماني أن معاذ بن جبل رضي الله عنه أخذ من ثلاثين بقرة تبيعا ومن أربعين بقرة مسنة وأتى بما دون','853','508','0');
INSERT INTO [t6227] VALUES (1245,'قبل أن يقدم معاذ','853','508','0');
INSERT INTO [t6227] VALUES (1246,'مسألة','854','508','0');
INSERT INTO [t6227] VALUES (1247,'فقالا إنا رسولا رسول الله','854','508','0');
INSERT INTO [t6227] VALUES (1248,'أن نأخذ شافعا قلت فأي شيء تأخذان قالا عناقا جذعة أو ثنية وروى مالك بإسناده عن سفيان بن عبد الله أن عمر بن الخطاب رضي الله عنه بعث مصدقا وكان يعد على الناس بالسخل فقالوا أتعد علينا ولا تأخذ منه شيئا فلما قدم على عمر بن الخطاب رضي الله','855','508','0');
INSERT INTO [t6227] VALUES (1249,'مسألة','856','508','0');
INSERT INTO [t6227] VALUES (1250,'مسألة','856','508','0');
INSERT INTO [t6227] VALUES (1251,'لقاتلتهم على منعها والذي روى عن سويد بن غفلة قال سرت أو قال أخبرني من سار مع مصدق رسول الله','857','508','0');
INSERT INTO [t6227] VALUES (1252,'أن لا نأخذ من راضع لبن فإنما أراد به والله أعلم أن راضع اللبن لا يؤخذ في زكاة الغنم إذا بقي من الأمهات شيء فإذا لم يبق من الأمهات شيء جاز أخذها بدليل ما ذكرنا في حديث أبي بكر رضي الله عنه في العناق وروى الشافعي عن سفيان عن بشر بن عاصم','857','508','0');
INSERT INTO [t6227] VALUES (1253,'أنه قال في أربعين شاة شاة والله أعلم','858','508','0');
INSERT INTO [t6227] VALUES (1254,'والخلطة في باب الزكاة صحيحة تزيد بها الزكاة مرة وتنقص أخرى وقال أبو حنيفة لا حكم للخلطة ولا تتغير بها الزكاة لنا حديث أنس عن أبي بكر في فريضة رسول الله','858','508','0');
INSERT INTO [t6227] VALUES (1255,'مسألة','859','508','0');
INSERT INTO [t6227] VALUES (1256,'قال ليس فيما دون خمسة أوسق صدقة ولا فيما دون خمس ذود صدقة ولا فيما دون خمس أواق صدقة فدل على أنه إذا كان واحد منهما لحر مسلم ففيه الصدقة في المال نفسه لا في المالك وروى عن عبد الحميد عن ابن جرير عن ابن ماهك أن رسول الله','859','508','0');
INSERT INTO [t6227] VALUES (1257,'مسألة','862','508','0');
INSERT INTO [t6227] VALUES (1258,'فرض زكاة الفطر من رمضان على الناس صاعا من تمر أو صاعا من شعير على كل حر أو عبد ذكر أو أنثى من المسلمين وعندهما عن أبي سعيد قال كنا نخرج زكاة الفطر صاعا من طعام أو صاعا من شعير أو صاعا من تمر أو صاعا من أقط أو صاعا من','862','508','0');
INSERT INTO [t6227] VALUES (1259,'وورد به الخبر وروي بإسناد رواته ثقات عن معاذ أن رسول الله','863','508','0');
INSERT INTO [t6227] VALUES (1260,'به من أخذ الجنس في الصدقات وأخذ الدينار أو عدله معافر في الجزية فإن رد الصدقات على فقرائهم إلا أن ينقلها إلى المهاجرين بالمدينة الذين أكثرهم أهل فيء لا أهل صدقة ثم يعارضه ما روي عن أبي سعيد قال جاء رجال من أهل البادية إلى النبي','864','508','0');
INSERT INTO [t6227] VALUES (1261,'أبصر ناقة مسنة في أبل الصدقة فقال قاتل الله صاحب هذه الناقة فقال يا','864','508','0');
INSERT INTO [t6227] VALUES (1262,'رأى في إبل الصدقة فذكره مرسلا وضعف مجالدا وأما ما روى أبو معشر عن نافع عن ابن عمر قال فإذا انصرف رسول الله','865','508','0');
INSERT INTO [t6227] VALUES (1263,'عن شري الصدقات حتى تقبض','865','508','0');
INSERT INTO [t6227] VALUES (1264,'مسألة','866','508','0');
INSERT INTO [t6227] VALUES (1265,'لقاتلتهم عليه وعن بهز بن حكيم عن أبيه عن جده قال سمعت رسول الله','866','508','0');
INSERT INTO [t6227] VALUES (1266,'مسألة','867','508','0');
INSERT INTO [t6227] VALUES (1267,'قال ليس في عبد المسلم ولا في فرسه صدقة وفيما ذهبنا إليه إجماع الصحابة وعلى هذا إجماع التابعين رضي الله عنهم وذكر فيه آثارا عن عمر وأبي عبيدة وعلي وغيرهم رضي الله عنهم وعن ابن المسيب وعمر بن عبد العزيز واعتمادهم في المسألة على حديث','868','508','0');
INSERT INTO [t6227] VALUES (1268,'قال إن خالدا قد احتبس أدراعه وأعتده في سبيل الله فلا أراه يبخل بدفع الزكاة ويمتنع من أدائها وهو وإن تأخر دفعها فعن قريب يستدركها وأن خالدا قد احتبس أمواله حتى أدراعه وأعتده في سبيل الله فلا شيء عليه ويؤكده جمعه بين أدراعه','869','508','0');
INSERT INTO [t6227] VALUES (1269,'ليس على المسلم في فرسه صدقة وبالله التوفيق استدلوا بما روي عن يعلى بن أمية أن أخاه ابتاع فرسا أنثى بمائة قلوص فذكر الحديث وأن عمر قال خذ من كل فرس دينارا قال فضرب على الخيل دينارا دينارا قلنا في ثبوته كلام ونظر فإن ثبت فإنما','871','508','0');
INSERT INTO [t6227] VALUES (1270,'فقال علي رضي الله عنه هو حسن إن لم يكن جزية يؤخذون بها راتبة والله أعلم','872','508','0');
INSERT INTO [t6227] VALUES (1271,'والعشر لا يجب فيما دون خمسة أوسق وقال أبو حنيفة يجب في الكثير والقليل لنا في الصحيحين عن أبي سعيد عن رسول الله','872','508','0');
INSERT INTO [t6227] VALUES (1272,'لا صدقة في حب ولا تمر دون خمسة أوسق وعنده أيضا عن جابر عن رسول الله','873','508','0');
INSERT INTO [t6227] VALUES (1273,'أنه سن فيما سقت السماء والعيون أو ما كان عثريا العشر وفيما سقى بالنضج نصف العشر والقصد من هذا الخبر بيان ما يجب فيه العشر أو نصف العشر فإما بيان النصاب الذي يجب فيه العشر أو نصف العشر فهو مستفاد مما تقدم والله أعلم','873','508','0');
INSERT INTO [t6227] VALUES (1274,'مسألة','874','508','0');
INSERT INTO [t6227] VALUES (1275,'اخرصوها فخرصناها وخرصها رسول الله','874','508','0');
INSERT INTO [t6227] VALUES (1276,'المرأة عن حديقتها كم بلغ فقالت بلغت عشرة أوسق وروى الشافعي عن عبد الله بن نافع عن محمد بن صالح التمار عن ابن شهاب عن سعيد بن المسيب عن عتاب بن أسيد','874','508','0');
INSERT INTO [t6227] VALUES (1277,'قال في زكاة الكرم يخرص كما يخرص النخل ثم تؤدى زكاته زبيبا كما تؤدى زكاة النخل تمرا وبإسناده أن رسول الله','875','508','0');
INSERT INTO [t6227] VALUES (1278,'قال إذا خرصتم فدعوا الثلث وإن لم تدعوا الثلث فدعوا الربع قال أبو عبد الله الحاكم هذا حديث صحيح الإسناد وهو عند أبي داود بمعناه وقال إذا خرصتم فجدوا ودعوا الثلث وعنده عن عائشة كان النبي','875','508','0');
INSERT INTO [t6227] VALUES (1279,'قال ليهود خيبر حين افتتح خيبر أقركم على ما','875','508','0');
INSERT INTO [t6227] VALUES (1280,'يبعث عبد الله بن رواحة فيخرص عليهم ثم يقول إن شئتم فلكم وإن شئتم فلي فكانوا يأخذونه وروي عن سهل بن أبي حثمة أن عمر بن الخطاب بعثه على خرص التمر وقال إذا أتيت أرضا فاخرصها ودع لهم قدر ما يأكلون هذا إسناد متصل ورواته ثقات وأكثر ما','876','508','0');
INSERT INTO [t6227] VALUES (1281,'مسألة','877','508','0');
INSERT INTO [t6227] VALUES (1282,'أنه إنما آخذ الصدقة من الحنطة والشعير والزبيب والتمر قال الحاكم أبو عبد الله هذا حديث قد احتج به الشيخان بجميع رواته وموسى بن طلحة تابعي كبير لا ينكر له أن يدرك أيام معاذ بن جبل رضي الله عنه ورواه الثوري في الجامع بهذا الإسناد وزاد','877','508','0');
INSERT INTO [t6227] VALUES (1283,'قال فيما سقت السماء والبعل والسيل العشر وفيما سقي بالنضح نصف العشر وإنما يكون ذلك في التمر والحنطة والحبوب فأما القثاء والبطيخ والرمان والقصب فقد عفا عنه رسول الله','878','508','0');
INSERT INTO [t6227] VALUES (1284,'إلى اليمن يعلمان الناس أمر دينهم لا تأخذ الصدقة إلا من هذه الأربعة الشعير والحنطة والزبيب والتمر رواته ثقات وهو متصل فإن سماع أبي بردة بن أبي موسى صحيح من أبيه وذكر أخبارا وآثارا عن ابن عمر وعائشة وعلي وطلحة بن عبيد الله وعمر بن الخطاب','878','508','0');
INSERT INTO [t6227] VALUES (1285,'مسألة','879','508','0');
INSERT INTO [t6227] VALUES (1286,'فيما سقت السماء والأنهار والعيون أو كان بعلا العشر وفيما سقي بالسواقي والنضح فنصف','879','508','0');
INSERT INTO [t6227] VALUES (1287,'فيما سقت السماء على اليهود والنصارى وليس على المسلمين عشور وعنده عن محمد بن عبيد عن وكيع عن سفيان عن عطاء بن السائب عن جرير بن عبيد الله عن النبي','880','508','0');
INSERT INTO [t6227] VALUES (1288,'أنه قال في أهل الذمة لهم ما أسلموا عليه من أموالهم وعبيدهم أرضيهم وماشيتهم ليس عليهم فيه إلا صدقة والذي يؤخذ من أراضي العراق فإنما هو كرا أو ثمن لها على ما نذكره في تفصيل المذهب وليس بخراج وأصل هذا أن أرض السواد فتحت عنوة ثم وقفها','880','508','0');
INSERT INTO [t6227] VALUES (1289,'لا يجتمع على المسلم خراج وعشور وهذا باطل وصله ورفعه يحيى بن عنبسة متهم بالوضع قال ابن عدي الحافظ إنما يرويه أبو حنيفة عن حماد عن إبراهيم من قوله فرواه يحيى فأوصله إلى النبي','881','508','0');
INSERT INTO [t6227] VALUES (1290,'منعت العراق قفيزها','881','508','0');
INSERT INTO [t6227] VALUES (1291,'يكون في أمتي خليفة يحثي المال لا يعده الحديث أخرجه مسلم في الصحيح قال وفي كتاب الغريبين عن أبي عبيدة','883','508','0');
INSERT INTO [t6227] VALUES (1292,'ما وظف على أهل الذمة في هذه البلاد في زمن عمر رضي الله عنه من الجزية وأنهم إذا أسلموا سقطت عنهم تلك الجزية وقوله منعت معناه ستمنع بإسلامهم ما وظف عليهم وفي هذا المعنى يصير الخبر حجة لنا في سقوط الخراج والذي يكون على طريق الجزية عن أراضي','884','508','0');
INSERT INTO [t6227] VALUES (1293,'والدراهم والدنانير إذا بلغت نصابا كان فيما زاد على النصاب بحسابه قلت الزيادة أو كثرت وقال أبو حنيفة ما لم تبلغ الزيادة أربعين درهما ومن الذهب أربع مثاقيل فلا شيء فيها لنا ما في حديث أنس عن أبي بكر الصديق في الصدقات وفي آخره وفي','884','508','0');
INSERT INTO [t6227] VALUES (1294,'ليس فيما دون خمس أواق صدقة وليس فيما دون خمس ذود صدقة قال سفيان والوقية أربعون درهما ووجه الاستدلال من هذا أنا لو خليناه وقوله وفي الرقة ربع العشر لكنا نوجبه في قليلها وكثيرها إلا أن الشرع دل أن ليس ذلك فيما نقص عن خمس أواق','885','508','0');
INSERT INTO [t6227] VALUES (1295,'فذكر حديثا في الزكاة قال فيه من كل مائتي درهم خمسة دراهم فإن زاد على المائتين ففي كل أربعين درهما درهم وهذا لو ثبت لم يكن فيه حجة فكيف وإسحق بن بشر الكاهلي منسوب إلى الوضع والمثنى لا يحتج به وروي عن جعفر بن محمد عن أبيه عن النبي','886','508','0');
INSERT INTO [t6227] VALUES (1296,'مسألة','886','508','0');
INSERT INTO [t6227] VALUES (1297,'لا زكاة في مال حتى يحول عليه الحول وقد ذكرناه في مسألة الزكاة في المستفاد في أثناء الحول والله أعلم','887','508','0');
INSERT INTO [t6227] VALUES (1298,'ولا زكاة في الحلي المباح على أحد القولين وقال أبو حنيفة فيه الزكاة وهو القول الآخر فوجه قولنا لا زكاة فيه من طريق الأثر ما روى الشافعي عن مالك عن نافع عن ابن عمر أنه يحلي بناته وجواريه الذهب ثم لا يخرج منه الزكاة وروي عنه','887','508','0');
INSERT INTO [t6227] VALUES (1299,'ليس في الحلي زكاة وهذا لا أصل له مرفوعا والصحيح أنه موقوف على جابر وروى الشافعي عن مالك عن القاسم عن أبيه عن عائشة رضي الله عنها كانت بنات أخيها يتامى في حجرها لهن الحلي فلا تخرج منه الزكاة وروي عن أسماء بيت أبي بكر أنها','888','508','0');
INSERT INTO [t6227] VALUES (1300,'ومعها ابنة لها وفي يد ابنتها مسكتان غليظتان من ذهب فقال لها أتعطين زكاة هذا قالت لا قال أيسرك أن يسورك الله بهما يوم القيامة سوارين من نار قال فخلعتهما فألقتهما إلى النبي','889','508','0');
INSERT INTO [t6227] VALUES (1301,'بطوق فيه سبعون مثقالا من ذهب فقلت يا رسول الله خذ منه الفريضة فأخذ مثقالا وثلاثة أرباع مثقال قلت يا رسول الله أفي المال حق سوى الزكاة قال نعم ثم قرأ وآتى المال على حبه ذوي القربى واليتامى قال علي بن عمر أبو بكر الهذلي','890','508','0');
INSERT INTO [t6227] VALUES (1302,'فقالت إن لي حليا وأن زوجي خفيف ذات اليد وإن لي بني أخ أفيجزي عني أن أجعل زكاة الحلي فيهم قال نعم قال علي بن عمر هذا وهم والصواب عن إبراهيم عن عبد الله مرسل موقوف وروي عن يحيى بن أبي أنيسة عن حماد عن إبراهيم عن علقمة عن عبد الله','890','508','0');
INSERT INTO [t6227] VALUES (1303,'قال تصدقن ولو من حليكن فذكر الحديث وفيه أنها قالت لبلال سئل رسول الله','891','508','0');
INSERT INTO [t6227] VALUES (1304,'قال أخبرها أن لهما أجرين أجر القرابة وأجر الصدقة وما روي عن يعلى قال دخلت على النبي','891','508','0');
INSERT INTO [t6227] VALUES (1305,'مسألة','892','508','0');
INSERT INTO [t6227] VALUES (1306,'مسألة','893','508','0');
INSERT INTO [t6227] VALUES (1307,'مسألة','894','508','0');
INSERT INTO [t6227] VALUES (1308,'أنه فرض صدقة الفطر صاعا من شعير أو تمر عن الصغير والكبير والحر والمملوك وعند مسلم عن أبي هريرة عن رسول الله','894','508','0');
INSERT INTO [t6227] VALUES (1309,'أنه قال لا ثنيا','894','508','0');
INSERT INTO [t6227] VALUES (1310,'مسألة','895','508','0');
INSERT INTO [t6227] VALUES (1311,'ليس فيما دون خمس أواق صدقة وفي الصحيحين عن ابن عباس رضي الله عنهما قال قال رسول الله','895','508','0');
INSERT INTO [t6227] VALUES (1312,'ليس الغني عن كثرة العرض ولكن الغنى غنى النفس وعند البخاري في الصحيح عن السائب بن يزيد أنه سمع عثمان بن عفان خطيبا على منبر رسول الله','896','508','0');
INSERT INTO [t6227] VALUES (1313,'مسألة','897','508','0');
INSERT INTO [t6227] VALUES (1314,'مسألة','899','508','0');
INSERT INTO [t6227] VALUES (1315,'أقطع بلال بن الحارث','899','508','0');
INSERT INTO [t6227] VALUES (1316,'أخذ من المعادن القبلية الصدقة وفي الصحيحين عن أبي هريرة أن رسول الله','900','508','0');
INSERT INTO [t6227] VALUES (1317,'جمع في هذا الخبر بين ذكر المعدن والركازة وأضاف إلى كل واحد منهما حكمة فلو كان المعدن والركاز واحد في وجوب الخمس فيه لجمع بينهما في الحكم وأضاف إليهما إيجاب الخمس والله أعلم ورواه عبد الله بن سعيد المقبري عن أبيه عن جده عن أبي هريرة وزاد','900','508','0');
INSERT INTO [t6227] VALUES (1318,'أتاه رجل بخمس أواق من معدن فلم يأخذ منها شيئا رواه البيهقي موصلا عن ابن أبي ذئب عن المقبري عن أبي هريرة أن رجلا جاء بخمس أواق إلى رسول الله','901','508','0');
INSERT INTO [t6227] VALUES (1319,'بقطعة فضة فقال خذ مني زكاتها فقال من أين جئت بها قال من معدن قال لا بل نعطيك مثل ما جئت به ولا ترجع إليه ليس في هذا بيان مقدار ما جاء به وفيه ما دل على أنه لم يأخذ منها شيئا وفيه النهي عن الرجوع إليه وكأنه أحب التنزه عنه لما','901','508','0');
INSERT INTO [t6227] VALUES (1320,'لم يأخذ منها شيئا ولو كان فيها شيء لأخذه وهذا على قوله في رواية أبي عبد الرحمن والبويطي عنه أن الحول شرط في وجوب الزكاة فيه وأجاب الشافعي رحمه الله عن قولهم تقول العرب قد أركز المعدن فإن قال إنما يقال أركز المعدن عند الندرة تأتي منه','902','508','0');
INSERT INTO [t6227] VALUES (1321,'فذكر حديثا فيه طول في الضوال وغيرها وفيه وما كان في الطريق غير الميتاء والقرية غير المسكونة ففيه وفي الركاز الخمس قالوا فقوله','903','508','0');
INSERT INTO [t6227] VALUES (1322,'أنه سئل عن التمر المعلق فقال غرامته ومثله معه وجلد أو نكال فإذا أواه الجرين ففيه القطع وهو يقول غرامته فقط وليس مثله معه ويقول لا يقطع فيه إذا أواه الجرين رطبا والجرين يؤويه رطبا وروي في ضالة الإبل غرامتها ومثلها معها ويقول','903','508','0');
INSERT INTO [t6227] VALUES (1323,'مسألة','904','508','0');
INSERT INTO [t6227] VALUES (1324,'وليس فيما دون خمس أواق صدقة زاد في رواية عند البخاري من الورق وروي عن جابر بن عبد الله قال كنا عند رسول الله','905','508','0');
INSERT INTO [t6227] VALUES (1325,'ثم أتاه من قبل ركنه الأيمن فقال مثل ذلك فأعرض عنه ثم أتاه من ركنه الأيسر فأعرض عنه رسول الله','905','508','0');
INSERT INTO [t6227] VALUES (1326,'فحذفه بها فلو أصابته لأوجعته أو عقرته فقال رسول الله','905','508','0');
INSERT INTO [t6227] VALUES (1327,'مسألة','906','508','0');
INSERT INTO [t6227] VALUES (1328,'فأخبره بها وقال له خذ صدقتها فقال له النبي','906','508','0');
INSERT INTO [t6227] VALUES (1329,'بارك الله لك فيها إنما لم يأخذ منه','906','508','0');
INSERT INTO [t6227] VALUES (1330,'مسألة','907','508','0');
INSERT INTO [t6227] VALUES (1331,'فرض زكاة الفطر في رمضان على كل نفس من المسلمين حر أو عبد رجل أو امرأة صغيرا أو كبيرا صاعا من تمر أو صاعا من شعير أخرجه مسلم في الصحيح وعند أبي داود عن ابن أبي صعير عن أبيه قال قال رسول الله','907','508','0');
INSERT INTO [t6227] VALUES (1332,'مسألة','908','508','0');
INSERT INTO [t6227] VALUES (1333,'فرض زكاة الفطر من رمضان صاعا من تمر أو صاعا من شعير على كل حر أو عبد ذكر أو أنثى من المسلمين وعند أبي داود عن ابن عباس رضي الله عنهما قال فرض رسول الله','908','508','0');
INSERT INTO [t6227] VALUES (1334,'مسألة','909','508','0');
INSERT INTO [t6227] VALUES (1335,'بزكاة الفطر تؤدى قبل أن يخرج الناس إلى الصلاة قال فكان عبد الله يؤديها قبل ذلك باليوم وباليومين قال وكان عبد الله يؤديها عن كل مملوك له في أرضه وغير أرضه عن كل إنسان يعوله صغيرا أو كبيرا وعن رقيق امرأته وكان له مكاتب بالمدينة وكان لا يؤدي','909','508','0');
INSERT INTO [t6227] VALUES (1336,'مسألة','910','508','0');
INSERT INTO [t6227] VALUES (1337,'صاعا من طعام أو صاعا من تمر أو صاعا من شعير أو صاعا من أقط والطعام في هذا الحديث المراد به الحنطة بدليل تناولها من طريق العربية وبما روى أحمد بن حنبل في مسنده عن عياض بن عبد بن أبي سرح قال قال أبو سعيد وذكر عنده صدقة الفطر فقال لا أخرج','910','508','0');
INSERT INTO [t6227] VALUES (1338,'زكاة الفطر على كل صغير وكبير حرا ومملوكا صاعا من طعام أو صاعا من أقط أو صاعا من شعير أو صاعا من تمر أو صاعا من زبيب فلم نزل نخرجه حتى قدم معاوية حاجا أو معتمرا يكلم الناس على المنبر فكان فيما كلم به الناس أن قال إني أرى مدين من تمر الشام','911','508','0');
INSERT INTO [t6227] VALUES (1339,'أمر بزكاة الفطر صاعا من تمر أو صاعا من شعير قال عبد الله فجعل الناس عدله مدين من حنطة يعني معاوية ومن تابعه عليه بدليل حديث أبي سعيد الصريح في ذلك وبدليل ما روي عنه أيضا أن رسول الله','912','508','0');
INSERT INTO [t6227] VALUES (1340,'في زكاة الفطر نصف صاع من حنطة أو صاعا من تمر وذكر في إسناده اختلافا ثم قال وفي الجملة لا يصح هذا الحديث بهذا الإسناد إذ لو كان عند ابن عمر أن النبي','913','508','0');
INSERT INTO [t6227] VALUES (1341,'في زكاة الفطر فقال حديث بصري وإسناده مرسل رواه الحسن','915','508','0');
INSERT INTO [t6227] VALUES (1342,'أن نعطي صدقة الفطر نصف صاع حنطة وصاعا مما سواه أبان متروك الحديث قال حماد بن زيد جاءني إبان فقال أحب أن تكلم شعبة أن يكف عني قال فكلمته فكف عنه أياما فأتاني في بعض الليل فقال إنك سألتني أن أكف عن أبان وأنه لا يحل الكف عنه فإنه','916','508','0');
INSERT INTO [t6227] VALUES (1343,'صدقة الفطر على كل صغير وكبير حر وعبد صاعا من تمر أو صاعا من شعير أو نصف صاع من قمح هذا منكر عمر','916','508','0');
INSERT INTO [t6227] VALUES (1344,'بعث مناديا فذكر الحديث وقال فيه مدان من قمح وابن جريج لم يسمع من عمرو بن شعيب وربما استدلوا بما روى ابن لهيعة عن أبي الأسود عن فاطمة عن أسماء بنت أبي بكر قالت كنا نؤدي زكاة الفطر على عهد رسول الله','917','508','0');
INSERT INTO [t6227] VALUES (1345,'بالمد الذي يقتات به أهل البيت أو الصاع الذي تقتاتون به يفعل ذلك أهل المدينة كلهم وربما استدلوا بما روي عن عصمة بن مالك عن النبي','917','508','0');
INSERT INTO [t6227] VALUES (1346,'فذكر الحديث وقال فيه بنصف صاع من بر قال الدارقطني لم يروه بهذا الإسناد وهذه الألفاظ غير سليمان بن أرقم وهو متروك الحديث وروي عنه أيضا بالإسناد خلاف ذلك وربما استدلوا بما روي عن الحكم عن قيس بن سعد بن عبادة قال كنا نصوم عاشوراء','918','508','0');
INSERT INTO [t6227] VALUES (1347,'فذكر الحديث وقال فيه نصف صاع من بر وهو مرسل وجابر متروك والله أعلم بالصواب استدلوا بمرسل عن سعيد بن المسيب في ذلك قال الشافعي رحمه الله تعالى حديث مدين خطأ وهو كما قال فقد ثبت أن التعديل بمدين إنما كان بعد رسول الله','919','508','0');
INSERT INTO [t6227] VALUES (1348,'مسألة','919','508','0');
INSERT INTO [t6227] VALUES (1349,'الميزان على ميزان أهل مكة والمكيال مكيال أهل المدينة وعن أبي هريرة قال قالوا لرسول الله','920','508','0');
INSERT INTO [t6227] VALUES (1350,'اللهم بارك لنا في صاعنا ومدنا فذكر الحديث وعن إسحق بن سليمان الرازي قال قلت لمالك يا أبا عبد الله كم وزن صاع رسول الله','920','508','0');
INSERT INTO [t6227] VALUES (1351,'وقال الآخر حدثني أبي عن أمه أنها كانت تؤدي بهذا الصاع إلى رسول الله','920','508','0');
INSERT INTO [t6227] VALUES (1352,'وقال لي مالك لو جاءكم أمير فضرب مدا أصغر من مد النبي','921','508','0');
INSERT INTO [t6227] VALUES (1353,'قال أفلا ترى أن الأمر إنما يعود إلى مد النبي','921','508','0');
INSERT INTO [t6227] VALUES (1354,'قلت لهم ما حجتكم في ذلك فقالوا نأتيك بالحجة غدا فلما أصبحت أتاني نحو من خمسين شيخا من أبناء المهاجرين والأنصار مع كل رجل','921','508','0');
INSERT INTO [t6227] VALUES (1355,'فنظرت فإذا هي سواء فعيرته فإذا هو خمسة أرطال وثلث ينقصان معه يسير فرأيت أمرا قويا فقد تركت قول أبي حنيفة في الصاع وأخذت بقول أهل المدينة قال الحسين فحججت من عامي ذلك فلقيت مالك بن أنس فسألته عن الصاع فقال صاعنا هذا صاع رسول الله','922','508','0');
INSERT INTO [t6227] VALUES (1356,'أن أصوم ثلاثة أيام أو أنسك ما تيسر أو فرق من طعام بين ستة مساكين ورواه مجاهد عن ابن أبي ليلى وقال فيه وأطعم فرقا بين ستة مساكين والفرق ثلاثة آصع ومعهود أن الفرق ستة عشر رطلا قال أبو داود سمعت أحمد بن حنبل يقول الفرق ستة','922','508','0');
INSERT INTO [t6227] VALUES (1357,'في الغسل من الجنابة صاع والوضوء رطلين والصاع ثمانية أرطال قال الدارقطني لم يروه عن منصور غير صالح وهو ضعيف الحديث وقال البخاري صالح بن موسى منكر الحديث وروى ابن أبي ليلى وهو ضعيف عن عبد الكريم عن أنس قال كان رسول الله','923','508','0');
INSERT INTO [t6227] VALUES (1358,'يتوضأ بالمد ويغتسل بالصاع قال أبو داود حدثنا مسدد عن أمية بن خالد','923','508','0');
INSERT INTO [t6227] VALUES (1359,'بالمد الذي يقتات به أهل البيت أو الصاع الذي يقتاتون به يفعل ذلك أهل المدينة كلهم فدل ذلك على مخالفة صاع الزكاة والقوت صاع الغسل ثم قد روت عائشة رضي الله عنها أنها كانت تغتسل هي ورسول الله','924','508','0');
INSERT INTO [t6227] VALUES (1360,'الجزء ٣','926','0','0');
INSERT INTO [t6227] VALUES (1361,'كتاب الصيام','926','1360','0');
INSERT INTO [t6227] VALUES (1362,'ذكر ما اختلف فيه الشافعي وأبو حنيفة رضي الله عنهما من كتاب الصيام','926','1360','0');
INSERT INTO [t6227] VALUES (1363,'مسألة','926','1360','0');
INSERT INTO [t6227] VALUES (1364,'قال من لم يجمع الصيام قبل الفجر فلا صيام له وروى من وجه آخر عنها بمعناه إلا أنه قال مع الفجر ورواته ثقات وله شاهد بإسناد صحيح عنها أن النبي','927','1360','0');
INSERT INTO [t6227] VALUES (1365,'قال من لم يبيت الصيام قبل الفجر فلا صيام له قال علي بن عمر تفرد به عبد الله بن عباد عن الفضل بهذا الإسناد وكلهم ثقات وروى مالك عن نافع عن ابن عمر رضي الله عنهما كان يقول لا يصوم إلا من أجمع الصيام قبل الفجر','928','1360','0');
INSERT INTO [t6227] VALUES (1366,'في قرى الأنصار من أصبح صائما فليتم صومه ومن كان أصبح مفطرا فليتم صومه آخر يومه قالت فلم نزل نصومه بعد ونصومه صبياننا الحديث أخرجه البخاري ومسلم في الصحيح وأخرجا أيضا عن سلمة بن الأكوع رضي الله عنه أن رسول','929','1360','0');
INSERT INTO [t6227] VALUES (1367,'يقول إن هذا اليوم يوم عاشوراء ولم يكتب عليكم صيامه فمن شاء فليصم ومن شاء فليفطر','929','1360','0');
INSERT INTO [t6227] VALUES (1368,'يوم عاشوراء فقال النبي','930','1360','0');
INSERT INTO [t6227] VALUES (1369,'مسألة وصوم التطوع يصح بالنية بعد الزوال في أحد القولين وقال أبو حنيفة رحمه الله لا يصح وروي عن حذيفة رضي الله عنه أنه بدا له الصوم بعد ما زالت الشمس فصام قال الربيع وقال الشافعي حكاية عن أبي معاوية عن','931','1360','0');
INSERT INTO [t6227] VALUES (1370,'مسألة','932','1360','0');
INSERT INTO [t6227] VALUES (1371,'قال لا يتقدم أحدكم رمضان بصوم يوم ولا يومين إلا رجل كان يصوم صوما فليصم ذلك اليوم وأخرجه','932','1360','0');
INSERT INTO [t6227] VALUES (1372,'قال لا تستقبلوا الشهر استقبالا صوموا لرؤيته وأفطروا لرؤيته فإن حال بينكم وبين منظره سحابة أو قترة فأكملوا العدة ثلاثين قال الحافظ أبو عبد الله الحاكم هذا حديث صحيح الإسناد عن صلة بن زفر قال كنا عند عمار بن ياسر رضي','933','1360','0');
INSERT INTO [t6227] VALUES (1373,'قال إذا انتصف شعبان فلا تصوموا فقال العلاء اللهم إن أبي حدثني عن أبي هريرة رضي الله عنه عن النبي','934','1360','0');
INSERT INTO [t6227] VALUES (1374,'لا تصوموا حتى تروه ولا تفطروا حتى تروه وروي عن ابن مسعود رضي الله عنه أنه قال لأن أفطر يوما من رمضان ثم أقضيه أحب إلي من أن أزيد فيه ما ليس منه وروي أن الحسن وابن سيرين وقتادة كانوا إذا حال بينهم وبين الهلال','936','1360','0');
INSERT INTO [t6227] VALUES (1375,'قال له أو لغيره هل صمت من سرر شعبان شيئا قال لا قال فإذا أفطرت فصم يومين فقد حكينا عن الأوزاعي وسعيد بن عبد العزيز رحمهما الله أنهما قالا سره أوله وقيل عن الأوزاعي آخره والمراد به والله أعلم','937','1360','0');
INSERT INTO [t6227] VALUES (1376,'مسألة','938','1360','0');
INSERT INTO [t6227] VALUES (1377,'فقال إني رأيت الهلال يعني هلال رمضان فقال أتشهد أن لا إله إلا الله قال نعم قال أتشهد أن محمدا رسول الله قال نعم قال يا بلال أذن في الناس أن يصوموا غدا ورواه حماد بن سلمة عن سماك بن حرب عن عكرمة عن ابن عباس','939','1360','0');
INSERT INTO [t6227] VALUES (1378,'وأمر الناس بالصيام','939','1360','0');
INSERT INTO [t6227] VALUES (1379,'أجاز شهادة رجل على رؤية هلال رمضان وليس بمعتمد لأن راويه حفص بن عمر الأيلي وهو ضعيف','940','1360','0');
INSERT INTO [t6227] VALUES (1380,'مسألة','941','1360','0');
INSERT INTO [t6227] VALUES (1381,'أن نمسك لرؤيته فإن لم نره وشهد شاهدا عدل تمسكنا بشهادتهما فسأله الحسين بن الحارث من أمير مكة فقال لا أدري ثم لقيني بعد ذلك فقال هو الحارث بن حاطب أخو محمد بن حاطب ثم قال الأمير إن','942','1360','0');
INSERT INTO [t6227] VALUES (1382,'وأما بيده إلى رجل قال الحسين فقلت لشيخ إلى جنبي من هذا الذي أومأ إليه الأمير قال هذا عبد الله بن عمر رضي الله عنهما وصدق كان أعلم بالله منه فقال بذلك أمرنا رسول الله','943','1360','0');
INSERT INTO [t6227] VALUES (1383,'الناس فأفطروا عن شقيق قال جاءنا كتاب عمر رضي الله عنه ونحن بخانقين وقال في كتابه إن الأهلة بعضها أكبر من','944','1360','0');
INSERT INTO [t6227] VALUES (1384,'مسألة','945','1360','0');
INSERT INTO [t6227] VALUES (1385,'صائما صبح ثلاثين فرئي هلال شوال نهارا فلم يفطر حتى أمسى والله أعلم','947','1360','0');
INSERT INTO [t6227] VALUES (1386,'وإذا جامع امرأته في نهار رمضان فلا كفارة عليها في ظاهر المذهب وقال أبو حنيفة رحمه الله على كل واحد منهما كفارة','947','1360','0');
INSERT INTO [t6227] VALUES (1387,'فقال هلكت يا رسول الله قال وما أهلكك قال وقعت على امرأتي في رمضان الحديث قال أبو عبد الله الحاكم قال لي بعض فقهائنا قد روى هذا الحديث أبو ثور عن معلى بن منصور الرازي عن سفيان عن الزهري','948','1360','0');
INSERT INTO [t6227] VALUES (1388,'فقال هلكت وأهلكت قلت له هذا باطل لا أصل له فذكر سماعا له فيه عن أبي ثور فقلت اشهدوا علي أنه متى صح هذا عن أبي ثور كما قال فهو باطل والدليل على بطلانه أني قرأت في كتاب أبي بكر محمد بن أحمد بن باكويه الجلاث سماعه من محمد','949','1360','0');
INSERT INTO [t6227] VALUES (1389,'فقال أهلكت يا رسول الله وليس هذا بشيء فقد رواه غيره عن محمد بن يحيى هلكت ورواه أحمد بن حنبل وغيره عن عبد الرزاق هلكت وقد أقر محمد بن إسحاق بالتصحيف فقال بعد روايته','951','1360','0');
INSERT INTO [t6227] VALUES (1390,'أمره بالكفارة وفي بعض الروايات أمره أيضا بالقضاء ولم يأمر المرأة بذلك وهو أخبر عن حاله وحالها وفي الصحيحين أيضا عن عائشة رضي الله عنها بقريب من معنى حديث أبي هريرة رضي الله عنه وفيه مكان هلكت احترقت','952','1360','0');
INSERT INTO [t6227] VALUES (1391,'مسألة','953','1360','0');
INSERT INTO [t6227] VALUES (1392,'قال من أفطر يوما من رمضان من غير رخصة رخصها الله عز وجل لم يقض عنه ولو صام الدهر كله وروي عن الشعبي وسعيد بن جبير وجابر بن زيد وإبراهيم ألا كفارة على المفطر في رمضان واستدلوا بما روى مالك عن ابن شهاب عن حميد','954','1360','0');
INSERT INTO [t6227] VALUES (1393,'حتى بدت ثناياه ثم قال كله قال الشافعي رحمه الله وكان فطره بجماع أخرجه مسلم في الصحيح من حديث مالك ولم يخرجه البخاري من حديثه وكذلك رواه يحيى بن سعيد الأنصاري وابن جريج وهشام بن سعد وغيرهم عن','955','1360','0');
INSERT INTO [t6227] VALUES (1394,'فقال إنه احترق فسأله ما له فقال أصبت أهلي في رمضان فأتى النبي','958','1360','0');
INSERT INTO [t6227] VALUES (1395,'في كفارة الفطر بالأكل شيء والأخبار كلها وردت في الفطر بالجماع وكان على بن موسى القمي من أصحاب الرأي ويدعي المعرفة بالحديث زعم أن الخبر الذي أوجب الكفارة على المفسد لصيامه هو في الجماع فجعل علماؤنا الأكل بمنزلة الجماع روى','959','1360','0');
INSERT INTO [t6227] VALUES (1396,'فقال يا رسول الله إني وقعت على أهلي في رمضان وأنا صائم فذكر الحديث وروي عن ابن المسيب عن النبي','960','1360','0');
INSERT INTO [t6227] VALUES (1397,'وهو','960','1360','0');
INSERT INTO [t6227] VALUES (1398,'وروي بإسناد مظلم بإسناد عن مقاتل بن سليمان عن عطاء عن جابر رضي الله عنه عن النبي','961','1360','0');
INSERT INTO [t6227] VALUES (1399,'فقال يا رسول الله إني وقعت على امرأتي نهارا في شهر رمضان فقال أعتق رقبة وذكر الحديث ورواه محمد بن عبد الرحمن بن أبي ليلى عن عطاء عن جابر رضي الله عنه قال جاء رجل إلى النبي','962','1360','0');
INSERT INTO [t6227] VALUES (1400,'فقال إني أفطرت يوما من رمضان وذكر الحديث وهو متى ما صح كان واردا أيضا في المفطر بالجماع بدليل أنه قد روي عن عبد الله العمري ولوين بن حكيم عن نافع عن ابن عمر رضي الله عنهما مقيدا أتى رجل رسول الله','963','1360','0');
INSERT INTO [t6227] VALUES (1401,'فقال يا رسول الله أفطرت يوما من رمضان متعمدا فقال رسول الله','964','1360','0');
INSERT INTO [t6227] VALUES (1402,'أن يعتق رقبة أو يصوم شهرين أو يطعم ستين مسكينا وهذه اللفظة باطلة وأبو معشر ضعيف الحديث وقد سبق منه ما يكفي إن شاء الله تعالى وقد روي عنه من وجه آخر فقال فيه أفطرت والله أعلم','965','1360','0');
INSERT INTO [t6227] VALUES (1403,'والحامل والمرضع إذا خافتا على ولديهما أفطرتا وعليهما القضاء والفدية وقال أبو حنيفة رحمه الله عليهما القضاء ولا فدية ودليلنا من طريق الأثر ما روى الشافعي عن مالك رحمهما الله عن نافع عن ابن عمر رضي الله عنهما','965','1360','0');
INSERT INTO [t6227] VALUES (1404,'مسألة','967','1360','0');
INSERT INTO [t6227] VALUES (1405,'يقول لهلال رمضان إذا رأيتموه فأفطروا فإن غم عليكم فاقدروا له فثبت بهذا الخبر وجوب الصوم عليه في ذلك اليوم وكونه عنده من رمضان والمفسد لصومه في نهار رمضان بالجماع يلزمه الكفارة','967','1360','0');
INSERT INTO [t6227] VALUES (1406,'لم يسأل أوقع ذلك في مثل هذا اليوم أو بعده وروي الواقدي بسنده عن عائشة رضي الله عنها مرفوعا صومكم يوم تصومون وفطركم يوم تفطرون وروي عن ابن المنكدر عن عائشة مرفوعا عرفة يوم يعرف الإمام والأضحى يوم يضحي','968','1360','0');
INSERT INTO [t6227] VALUES (1407,'ومن كان عليه صوم رمضان فلم يقضه مع القدرة عليه حتى دخل عليه رمضان آخر قضى وكفر وقال أبو حنيفة رحمه الله يقضي ولا يكفر','968','1360','0');
INSERT INTO [t6227] VALUES (1408,'ولا يصح رواه إبراهيم بن نافع أبو إسحاق الجلاب عن عمر بن موسى بن','969','1360','0');
INSERT INTO [t6227] VALUES (1409,'مسألة','970','1360','0');
INSERT INTO [t6227] VALUES (1410,'قال من مات وعليه صيام صام عنه وليه وفي الصحيحين أيضا عن ابن عباس رضي الله عنهما قال جاءت امرأة إلى النبي','970','1360','0');
INSERT INTO [t6227] VALUES (1411,'إذ أتته امرأة فقالت إني تصدقت على أمي بجارية وإنها ماتت قال وجب أجرك وردها عليك الميراث قالت يا رسول الله إنه كان عليها صوم شهر أفأصوم عنها قال صومي عنها قالت يا رسول الله إنها لم تحج أفأحج عنها قال حجي','971','1360','0');
INSERT INTO [t6227] VALUES (1412,'أولى وعلمي أنه لو بلغه الخبر لصار إليه روي عن ابن عباس وعائشة رضي الله عنهم أنهما أفتيا بالكفارة دون ذكر الصوم عنه ومن قال من أصحابنا بذلك احتج به عنهما وهما رويا الحديث المرفوع في القضاء عن الميت فدل على أن المراد','972','1360','0');
INSERT INTO [t6227] VALUES (1413,'مسألة','973','1360','0');
INSERT INTO [t6227] VALUES (1414,'نهى عن صيام هذين اليومين أما أحدهما ف يوم فطركم من صيامكم وعيدكم وأما الآخر فيوم تأكلون فيه من نسككم وعن أبي سعيد الخدري رضي الله عنه قال نهى رسول الله','973','1360','0');
INSERT INTO [t6227] VALUES (1415,'لا نذر في معصية ولا فيما لا يملكه ابن آدم وعند البخاري عن حكيم بن أبي حمزة الأسلمي سمع رجلا يسأل عبد الله بن عمر رضي الله عنهما عن رجل نذر لا يأتي عليه يوم سماه إلا وهو صائم فيه فوافق ذلك يوم أضحى أو يوم فطر','974','1360','0');
INSERT INTO [t6227] VALUES (1416,'مسألة','975','1360','0');
INSERT INTO [t6227] VALUES (1417,'إن الله تجاوز لي عن أمتي الخطأ والنسيان وما استكرهوا عليه ووجه قوله أفطر روى أبو داود عن جابر بن عبد الله رضي الله عنهما قال قال عمر رضي الله عنه هششت فقبلت وأنا صائم فقلت يا رسول الله صنعت اليوم أمرا عظيما','976','1360','0');
INSERT INTO [t6227] VALUES (1418,'مسألة','977','1360','0');
INSERT INTO [t6227] VALUES (1419,'قال رفع القلم عن ثلاث عن النائم حنى يستيقظ وعن المجنون حتى يبرأ وعن الصبي حتى يعقل روى جرير بن حازم عن سليمان بن مهران عن أبي ظبيان عن ابن عباس رضي الله عنهما قال مر علي رضي الله عنه','977','1360','0');
INSERT INTO [t6227] VALUES (1420,'رفع القلم عن ثلاث النائم حتى يستيقظ وعن الصبي حنى يحتلم وعن المجنون حتى يفيق قال نعم فأمر بها فخلى عنها رواته ثقات إلا أن جريرا انفرد برفعه عن سليمان ورواه جماعة عن الأعمش موقوفا على علي رضي الله عنه','978','1360','0');
INSERT INTO [t6227] VALUES (1421,'وإسناده حسن والمحدثون يقولون أدرك الحسن البصري عليا رضي الله عنه وعند أبي داود عن أبي الضحى عن علي رضي الله عنه','978','1360','0');
INSERT INTO [t6227] VALUES (1422,'قال رفع القلم عن ثلاثة عن النائم حتى يستيقظ وعن الصبي حتى يحتلم وعن المجنون حتى يعقل والله أعلم','979','1360','0');
INSERT INTO [t6227] VALUES (1423,'ويكره السواك للصائم بعد العشى وقال أبو','979','1360','0');
INSERT INTO [t6227] VALUES (1424,'كل حسنة يعمل ابن آدم تكتب عشرا إلى سبعمائة ضعف إلا الصوم فإنه لي وأنا أجزي به وللصائم فرحتان فرحة عند فطره وفرحة يوم القيامة ولخلوف فم الصائم أطيب عند الله من ريح المسك والسواك يقطع هذا الخلوف وبمثله احتج أبو هريرة','980','1360','0');
INSERT INTO [t6227] VALUES (1425,'يتسوك وهو صائم وعاصم بن عبيد الله ضعيف الحديث قال البخاري منكر الحديث وروى مجالد عن الشعبي عن مسروق عن عائشة رضي الله عنها قالت قال رسول الله','981','1360','0');
INSERT INTO [t6227] VALUES (1426,'وفي ذلك','982','1360','0');
INSERT INTO [t6227] VALUES (1427,'مسألة','983','1360','0');
INSERT INTO [t6227] VALUES (1428,'ذات يوم يا عائشة هل عندك شيء قالت لا والله ما عندنا شيء قال إني صائم قالت فخرج رسول الله','984','1360','0');
INSERT INTO [t6227] VALUES (1429,'قلت يا رسول الله أهديت لنا هدية أو جاءنا زور وقد خبأت لك شيئا قال ما هو قلت حيس قال هاتيه فجئت به فأكل ثم قال قد كنت أصبحت صائما هكذا أخرجه مسلم في الصحيح عن أبي كامل الجحدري عن عبد الواحد وعن أبي بكر عن','984','1360','0');
INSERT INTO [t6227] VALUES (1430,'فقلت إنا خبأنا لك حيسا فقال أما إني كنت أريد الصوم ولكن قربيه كذا رواه المزني عنه ورواه الطحاوي عن المزني عنه فزاد سأصوم يوما مكانه قال الشافعي رحمه الله سمعت سفيان عامة مجالسه لا يذكر فيه سأصوم','985','1360','0');
INSERT INTO [t6227] VALUES (1431,'خرج عام الفتح فصام حتى بلغ كراع الغميم فقيل له إن الناس قد شق عليهم الصيام وإنما ينظرون فيما فعلت فدعا بقدح من ماء بعد العصر فشرب والناس ينظرون فأفطر بعض الناس وصام بعضهم فبلغه أن أناسا صاموا فقال أولئك العصاة وعند','986','1360','0');
INSERT INTO [t6227] VALUES (1432,'آخى بين سلمان وأبي الدرداء قال فجاءه سلمان يزوره فإذا أم الدرداء مبتذلة فقال ما شأنك يا أم الدرداء قالت إن أخاك يقوم الليل ويصوم النهار فجاءه أبو الدرداء فرحبه وقرب إليه طعاما وقال له سلمان قال أطعم إني صائم','987','1360','0');
INSERT INTO [t6227] VALUES (1433,'بالذي أمره سلمان فقال له رسول الله','988','1360','0');
INSERT INTO [t6227] VALUES (1434,'وأم هانئ عن يمينه فجاءت الوليدة بإناء فيه شراب فناوله فشرب ثم ناول أم هانئ فشربت منه فقالت يا رسول الله لقد أفطرت وكنت صائمة فقال لها كنت تقضين شيئا قالت لا قال فلا يضرك إن كان تطوعا قولها يوم فتح مكة','988','1360','0');
INSERT INTO [t6227] VALUES (1435,'قال الصائم بالخيار ما بينه وبين نصف النهار وعون بن عمارة فيه نظر وجعفر بن الزبير متروك وروى ابن فديك عن ابن أبي حميد عن إبراهيم بن عبيد الله ابن رفاعة الأنصاري أن أبا سعيد الخدري رضي الله عنه صنع طعاما فدعا رسول','989','1360','0');
INSERT INTO [t6227] VALUES (1436,'تكلف لك أخوك وصنع لك طعاما فأفطر وصم يوما غيره إن أحببت','989','1360','0');
INSERT INTO [t6227] VALUES (1437,'فأمرهما أن يفطرا وقال اقضيا يوما مكانه وكذلك روي عن حجاج بن أرطاة عن الزهري والحجاج لم ير الزهري ولم يسمع منه وذكر عروة في هذا الحديث وهم وإنما روي عن الزهري عن عائشة مرسلا وكذا رواه مالك في الموطأ وسفيان بن','992','1360','0');
INSERT INTO [t6227] VALUES (1438,'فبدرتني حفصة فذكرت ذلك له فقال اقضيا يوما مكانه وقال أبو عيسى الترمذي سألت محمد بن إسماعيل البخاري عن هذا الحديث فقال لا يصح حديث الزهري عن عروة عن عائشة قال الشافعي رحمه الله أخبرنا مسلم بن خالد عن ابن','993','1360','0');
INSERT INTO [t6227] VALUES (1439,'فقال اقضياه قال الحافظ أبو عبد الله سلامة وأم سلمة إن لم يكن زوج النبي','999','1360','0');
INSERT INTO [t6227] VALUES (1440,'دخل على حفصة وأم','999','1360','0');
INSERT INTO [t6227] VALUES (1441,'وأصحابه فقال رجل من القوم إني صائم فقال رسول الله','1000','1360','0');
INSERT INTO [t6227] VALUES (1442,'مسألة','1002','1360','0');
INSERT INTO [t6227] VALUES (1443,'يقول من صام رمضان ثم أتبعه ستا من شوال فذاك صيام الدهر ورويناه أيضا في كتاب السنن من حديث ثوبان وجابر','1002','1360','0');
INSERT INTO [t6227] VALUES (1444,'فلا معنى للكراهة بأنه زيادة على الفرض فإنه إنما يصومهن تطوعا لا بنية الفرض وقد فصل بينه وبين الفرض بإفطار يوم العيد والله أعلم','1003','1360','0');
INSERT INTO [t6227] VALUES (1445,'وصيام أيام التشريق لا يجوز بحال وقال في القديم يجوز صيامها للمتمتع وقال أبو حنيفة رحمه الله لا يجوز فوجه قولنا لا يجوز بحال ما في صحيح مسلم عن نبيشة','1003','1360','0');
INSERT INTO [t6227] VALUES (1446,'أيام التشريق أيام أكل وشرب وذكر لله وعن كعب بن مالك رضي الله عنه أن رسول الله','1004','1360','0');
INSERT INTO [t6227] VALUES (1447,'يوم عرفة ويوم النحر وأيام التشريق عيدنا أهل الإسلام وهن أيام أكل وشرب قال أبو عبد الله الحاكم هذا حديث صحيح وروى الشافعي عن مالك رحمهما الله وهو عند أبي داود عن القعنبي عن مالك عن يزيد بن الهاد عن أبي مرة مولى أم هانئ','1005','1360','0');
INSERT INTO [t6227] VALUES (1448,'البيضاء في شعب الأنصار وهو يقول يا أيها الناس إن رسول الله','1006','1360','0');
INSERT INTO [t6227] VALUES (1449,'وروى جعفر بن محمد عن أبيه عن علي بن أبي طالب رضي الله عنه قال يصوم بعد أيام التشريق إذا فاته الصوم والله أعلم','1007','1360','0');
INSERT INTO [t6227] VALUES (1450,'الاعتكاف بغير صوم يصح وقال أبو حنيفة رحمه الله لا يصح','1007','1360','0');
INSERT INTO [t6227] VALUES (1451,'قال إني نذرت أن أعتكف ليلة في الجاهلية فقال له النبي','1008','1360','0');
INSERT INTO [t6227] VALUES (1452,'فأمره أن يفي به فدخل المسجد تلك الليلة فلما أصبح إذ السبي يسعون ويقولون أعتقنا رسول الله','1008','1360','0');
INSERT INTO [t6227] VALUES (1453,'يوم الجعرانة يا رسول الله إن علي يوما أعتكفه فقال النبي','1008','1360','0');
INSERT INTO [t6227] VALUES (1454,'بعد إسلامه فقال أوف بنذرك وقوله ويصوم ليس بمحفوظ وسعيد تفرد به وهو غير مقبول منه لمخالفته الثقات وروي عن ابن عباس رضي الله عنهما أن النبي','1009','1360','0');
INSERT INTO [t6227] VALUES (1455,'أنه أراد أن يعتكف العشر الأواخر من رمضان فذكر الحديث وفي آخره ثم أحر الاعتكاف إلى العشر الأول من شوال قال أبو بكر الإسماعيلي فيه دليل على جواز الاعتكاف بغير صوم لأن أول شوال يوم الفطر ولا يجوز صومه','1010','1360','0');
INSERT INTO [t6227] VALUES (1456,'كان يعتكف العشر الأواخر من رمضان حتى توفاه الله ثم اعتكف أزواجه من بعده','1011','1360','0');
INSERT INTO [t6227] VALUES (1457,'وأنه من كلام الزهري ومن أدرجه في الحديث فقد وهم والله أعلم وروى أبو داود عن وهب عن خالد عن عبد الرحمن عن الزهري عن عروة عن عائشة رضي الله عنها أنها قالت السنة على المعتكف أن لا يعود المريض ولا يشهد الجنازة ولا يمس','1012','1360','0');
INSERT INTO [t6227] VALUES (1458,'كتاب الحج','1014','1360','0');
INSERT INTO [t6227] VALUES (1459,'ذكر ما اختلف فيه الشافعي وأبو حنيفة رضي الله عنهما من كتاب الحج وما ورد فيه من خبر أو أثر','1014','1360','0');
INSERT INTO [t6227] VALUES (1460,'مسألة','1014','1360','0');
INSERT INTO [t6227] VALUES (1461,'مهلين بالحج ومعنا النساء','1014','1360','0');
INSERT INTO [t6227] VALUES (1462,'مسألة','1016','1360','0');
INSERT INTO [t6227] VALUES (1463,'في قول الله تبارك وتعالى ولله على الناس','1016','1360','0');
INSERT INTO [t6227] VALUES (1464,'مرسلا وكذلك رواه يونس بن عبيد عن الحسن مرسلا روي في ذلك عن ابن عمر عن النبي','1017','1360','0');
INSERT INTO [t6227] VALUES (1465,'فجاءته امرأة من خثعم تستفتيه فجعل الفضل ينظر إليها وتنظر إليه فجعل رسول الله يصرف وجه الفضل إلى الشق الآخر فقالت يا رسول الله إن فريضة الله على عباده في الحج أدركت أبي شيخا كبيرا لا يستطيع أن يثبت على الراحلة أفأحج عنه','1018','1360','0');
INSERT INTO [t6227] VALUES (1466,'أتاه رجل فقال إن أبي شيخ كبير أدرك الإسلام ولم يحج ولا يستمسك','1018','1360','0');
INSERT INTO [t6227] VALUES (1467,'احجج عن أبيك قال أبو عبد الله الحاكم هذا حديث صحيح وروي عن زيد بن علي بن الحسين عن أبيه عن عبد الله بن أبي رافع عن علي بن أبي طالب أن امرأة من خثعم شابة قالت يا رسول الله إن أبي شيخ كبير أدركته فريضة الله على','1019','1360','0');
INSERT INTO [t6227] VALUES (1468,'ومن مات بعد وجوب الحج عليه قضي عنه وإن لم يوص وقال أبو حنيفة رحمه الله لا يصح قضاء الحج عنه إذا لم يوص به إلا لابنه فإن حج عنه رجوت أن يجزئه ودليلنا من طريق الخبر ما في صحيح البخاري عن ابن عباس رضي الله عنهما أن','1019','1360','0');
INSERT INTO [t6227] VALUES (1469,'فأتته امرأة فقالت يا رسول الله إني كنت تصدقت بوليدة على أمي فماتت وبقيت الوليدة قال قد وجب أجرك ورجعت إليك في الميراث قالت إنها ماتت وعليها صوم شهر فقال صومي عن أمك قالت إنها لم تحج قال فحجي عن أمك','1020','1360','0');
INSERT INTO [t6227] VALUES (1470,'مسألة','1020','1360','0');
INSERT INTO [t6227] VALUES (1471,'سمع رجلا يقول لبيك عن شبرمة قال من شبرمة قال أخ لي أو قريب لي قال حججت عن نفسك قال لا قال حج عن نفسك ثم عن شبرمة قال يحيى بن معين أثبت الناس سماعا من سعيد عبدة بن سليمان','1021','1360','0');
INSERT INTO [t6227] VALUES (1472,'رجلا يلبي عن نبيشة فقال أيها الملبي هذه عن نبيشة واحجج عن نفسك قال علي بن عمر تفرد الحسن بن عمارة وهو متروك الحديث والمحفوظ عن ابن عباس رضي الله عنهما حديث شبرمة ويقال إن الحسن رجع عنه إلى الصواب فحدث','1023','1360','0');
INSERT INTO [t6227] VALUES (1473,'مسألة','1024','1360','0');
INSERT INTO [t6227] VALUES (1474,'سئل عن قول الله تعالى من استطاع إليه سبيلا فقيل وما السبيل قال الزاد والراحلة قال الحاكم أبو عبد الله هذا حديث صحيح','1025','1360','0');
INSERT INTO [t6227] VALUES (1475,'مرسلا وفي الصحيحين عن ابن عمر رضي الله عنهما أن رسول الله','1026','1360','0');
INSERT INTO [t6227] VALUES (1476,'فجاءه رجلان أحدهما يشكو العيلة والآخر يشكو قطع السبيل قال فقال لا يأتي عليك إلا قليل حتى تخرج المرأة من الحيرة إلى مكة بغير خفير وذكر باقي الحديث وروى إسرائيل عن سعد الطائي في هذا الحديث فإن طالت بك حياة لترين','1026','1360','0');
INSERT INTO [t6227] VALUES (1477,'يوشك أن تخرج الظعينة من الحيرة إلى مكة بلا مجوزه قال وقد كان ذلك والله عزيزا في الجاهلية واستدلوا بما في الصحيحين عن ابن عباس رضي الله عنهما قال سمعت رسول الله','1027','1360','0');
INSERT INTO [t6227] VALUES (1478,'فأخبرت أن أبا سعيد يخبر عن رسول الله','1027','1360','0');
INSERT INTO [t6227] VALUES (1479,'يقول لا تسافر المرأة سفرا ثلاثة أيام أو تحجج إلا ومعها زوجها وهذا اللفظ أو تحجج ليس بمحفوظ وراويه جابر الجعفي وهو كذاب كما سبق ذكره والله أعلم','1028','1360','0');
INSERT INTO [t6227] VALUES (1480,'الحج موسع الوقت يجوز تأخيره بعد وجوبه وقال العراقيون إنه مضيق الوقت يكون مؤخره عن سنة وجوبه مفرطا','1028','1360','0');
INSERT INTO [t6227] VALUES (1481,'على الحج وتخلف عليه السلام بالمدينة بعد منصرفه من تبوك لا محاربا ولا مشغولا بشيء وتخلف أكثر المسلمين قادرين على الحج وأزواج رسول الله','1029','1360','0');
INSERT INTO [t6227] VALUES (1482,'الفرض ولا ترك المتخلفين عنه ولم يحج عليه السلام بعد فرض الحج إلا حجة الإسلام وهي حجة الوداع وفي الصحيحين من حديث كعب بن عجرة أن رسول الله','1029','1360','0');
INSERT INTO [t6227] VALUES (1483,'أن يطعم فرقا بين ستة مساكين أو يصوم ثلاثة أيام أو ينسك شاة','1029','1360','0');
INSERT INTO [t6227] VALUES (1484,'سنة سبع معتمرا عمرة القضاء فأقام بمكة ثلاثة أيام ثم انصرف ولو أراد الحج لأمكنه ذلك إن شاء الله ثم خرج في شهر رمضان سائرا إلى مكة عام الفتح وذلك في سنة ثمان فأقام بها سبع عشرة ثم خرج منها واستعمل عليها عتاب بن أسيد ولو','1030','1360','0');
INSERT INTO [t6227] VALUES (1485,'خرج في رمضان فصام وصام الناس معه وذلك على رأس ثمان سنين ونصف من مقدم رسول الله','1031','1360','0');
INSERT INTO [t6227] VALUES (1486,'مكة لبضع عشرة خلت من شهر رمضان وعنده أيضا في الصحيح عن أبي هريرة أن أبا بكر الصديق رضي الله عنهما بعثه في الحجة التي أمره رسول الله','1031','1360','0');
INSERT INTO [t6227] VALUES (1487,'أقام بالمدينة تسع سنين لم يحج ثم حج وهو في صحيح مسلم في الحديث الطويل في صفة الحج والله أعلم','1032','1360','0');
INSERT INTO [t6227] VALUES (1488,'والإحرام بالحج في غير أشهر الحج لا يصح وقال أبو حنيفة رحمه الله أساء وتجزئه روى أبو بكر محمد بن إسحاق بن خزيمة حدثنا محمد بن العلاء أبو كريب حدثنا أبو خالد الأحمر عن شعبة عن الحكم','1032','1360','0');
INSERT INTO [t6227] VALUES (1489,'لرجموه وروى الشافعي أخبرنا مسلم وسعيد بن سالم عن ابن جريج عن أبي الزبير أنه سمع جابر بن عبد الله رضي الله عنهما سئل عن الرجل أيهل بالحج قبل أشهر الحج فقال لا وروي عن عطاء قال إنما قال الله تعالى الحج','1035','1360','0');
INSERT INTO [t6227] VALUES (1490,'ولا يكره فعل العمرة في وقت وحكى عن أبي حنيفة','1035','1360','0');
INSERT INTO [t6227] VALUES (1491,'عام حجة الوداع فمنا من أهل بعمرة ومنا من أهل بحجة وعمرة ومنا من أهل بالحج الحديث فالقارن معتمر في هذه الأيام وأي فرق بين إفراده عمرة وبين قرانه بينها وبين حجة وما روي عن عائشة رضي الله عنها قالت','1036','1360','0');
INSERT INTO [t6227] VALUES (1492,'مسألة','1037','1360','0');
INSERT INTO [t6227] VALUES (1493,'وفيه دلالة ظاهرة فإنه أخبر عن وجوبها وصوبه عمر وبين أنه مهتد بما يراه من وجوبها لسنة النبي','1038','1360','0');
INSERT INTO [t6227] VALUES (1494,'مهلين بالحج فذكر الحديث وفيه فجاء سراقة بن مالك بن جعشم فقال يا رسول الله أرايت عمرتنا هذه لعامنا هذا أم للأبد فقال لا بل للأبد وحكى مسلم بن الحجاج عن أحمد بن حنبل أنه لا يعلم في إيجاب العمرة حديثا أجود','1038','1360','0');
INSERT INTO [t6227] VALUES (1495,'فقال يا رسول الله العمرة واجبة قال لا وأن تعتمر خير لك','1039','1360','0');
INSERT INTO [t6227] VALUES (1496,'من هذا الوجه وخالفه عبد الملك بن جريج وغيره فرووه عن ابن المنكدر عن جابر رضي الله عنه من قوله وهو الصواب وحجاج ليس ممن يقبل منه ما ينفرد به من الروايات لسوء حفظه وقلة مراعاته لما يحدث به وكثرة تدليسه فكيف إذا خالف','1040','1360','0');
INSERT INTO [t6227] VALUES (1497,'قال الحج جهاد والعمرة تطوع ولا تقوم به الحجة لأن أبا صالح ماهان وقيل عبد الرحمن بن قيس ليس له ذكر في الصحيح وهو مرسل ونحن لا نحتج بالمراسيل إذا لم تخالف المسانيد فكيف إذا خالفتها وقد وهم بعضهم فرفعه','1041','1360','0');
INSERT INTO [t6227] VALUES (1498,'مسألة','1042','1360','0');
INSERT INTO [t6227] VALUES (1499,'وأصح ذلك حديث عمرة عن عائشة رضي الله عنها قالت خرجنا لخمس ليال بقين من ذي القعدة ولا نرى إلا الحج وإنما أحرم رسول الله','1043','1360','0');
INSERT INTO [t6227] VALUES (1500,'لا يذكر حجا ولا عمرة فلما قدمنا يعني مكة أمرنا أن نحل وذكر باق الحديث ورواه طاووس عن النبي','1043','1360','0');
INSERT INTO [t6227] VALUES (1501,'أفرد الحج وكأنه','1044','1360','0');
INSERT INTO [t6227] VALUES (1502,'إلا ما هو أفضل وعنده عنها رضي الله عنها أيضا قالت خرجنا مع رسول الله','1044','1360','0');
INSERT INTO [t6227] VALUES (1503,'بحج وأهل به ناس معه وفي الصحيحين عنها رضي الله عنها خرجنا مع رسول الله','1044','1360','0');
INSERT INTO [t6227] VALUES (1504,'بالحج فأما من أهل بالحج أو جمع بين الحج والعمرة فلم يحلوا حتى كان يوم النحر','1044','1360','0');
INSERT INTO [t6227] VALUES (1505,'بالمدينة تسع سنين لم يحج ثم أذن للناس بالحج فقدم الناس المدينة ليخرجوا معه فانطلق رسول الله','1045','1360','0');
INSERT INTO [t6227] VALUES (1506,'بين أظهرنا ينزل عليه القرآن وهو يعرف تأويله وإنما يفعل ما أمر به فقدمنا مكة فلما طاف رسول الله','1045','1360','0');
INSERT INTO [t6227] VALUES (1507,'يوم ساق البدن وقد أهلوا بالحج مفردا فقال رسول الله','1045','1360','0');
INSERT INTO [t6227] VALUES (1508,'بالحج خالصا وحده وذكر باقي الحديث بمعناه وفيه البيان الواضح أن النبي','1046','1360','0');
INSERT INTO [t6227] VALUES (1509,'بالحج مفردا وروي بإسناد صحيح عن عبد الرحمن بن الأسود عن أبيه قال حججت مع أبي بكر رضي الله عنه فجرد ومع عمر رضي الله عنه فجرد ومع عثمان رضي الله عنه فجرد وعن عمر رضي الله عنه قال افصلوا بين حجكم','1046','1360','0');
INSERT INTO [t6227] VALUES (1510,'وقال في الهدي جزور أو بقرة أو شاة أو شركا في دم وعند مسلم عن ابن عباس رضي الله عنهما عن النبي','1048','1360','0');
INSERT INTO [t6227] VALUES (1511,'أراد أصحابه الذين حلوا واستمتعوا إذ ثبت أنه','1048','1360','0');
INSERT INTO [t6227] VALUES (1512,'وهذا يومئذ كافر يعني معاوية بالعرش يعني بيوت مكة وروى الشافعي رحمه الله عن مالك عن ابن شهاب عن محمد بن عبد الله بن الحارث بن نوفل أنه سمع سعد بن أبي وقاص والضحاك بن قيس رضي الله عنهما عام حج معاوية بن أبي سفيان','1049','1360','0');
INSERT INTO [t6227] VALUES (1513,'وصنعناها معه وعندها عن ابن عمر رضي الله عنهما قال تمتع رسول الله','1050','1360','0');
INSERT INTO [t6227] VALUES (1514,'فأهل بالعمرة ثم أهل بالحج وتمتع الناس مع رسول الله','1050','1360','0');
INSERT INTO [t6227] VALUES (1515,'مكة قال للناس من كان منكم أهدى فإنه لا يحل له شيء حرم منه حتى يقضي حجة ومن لم يكن منكم أهدى فليطف بالبيت وبالصفا والمروة وليقصر وليحلل ثم ليهل بالحج وذكر الحديث في طواف رسول الله','1050','1360','0');
INSERT INTO [t6227] VALUES (1516,'ما شأن الناس حلوا ولم تحلل أنت من عمرتك فقال إني لبدت رأسي وقلدت هديي فلا أحل حتى أنحر وعند مسلم عن إبراهيم بن أبي موسى الأشعري عن أبي موسى رضي الله عنه أنه كان يفتي بالمتعة فقال له رجل رويدك ببعض فتياك','1051','1360','0');
INSERT INTO [t6227] VALUES (1517,'فقال لي بم أهللت قال قلت لبيك إهلالا كإهلال النبي','1052','1360','0');
INSERT INTO [t6227] VALUES (1518,'فإنه لم يحل حتى نحر الهدي وعند مسلم عن أبي نضرة قال كان ابن عباس رضي الله','1052','1360','0');
INSERT INTO [t6227] VALUES (1519,'فلما قام عمر بن الخطاب رضي الله عنه قال إن الله عز وجل كان يحل لنبيه','1053','1360','0');
INSERT INTO [t6227] VALUES (1520,'فقال عثمان رضي الله عنه أجل ولكنا كنا خائفين وعنده أيضا عن أبي ذر رضي الله عنه قال لا يصلح المتعتان إلا لنا خاصة يعني متعة النساء ومتعة الحج','1053','1360','0');
INSERT INTO [t6227] VALUES (1521,'يحتمل أن يكون مراده بذلك إذنه فيه وأمره من لم يكن معه هدي بدليل ما مضى من الأحاديث في الإفراد والله أعلم وعند أبي داود عن سعيد بن المسيب أن رجلا من أصحاب النبي','1054','1360','0');
INSERT INTO [t6227] VALUES (1522,'في مرضه الذي قبض فيه ينهى عن العمرة قبل الحج إنما أخرجت هذه الأحاديث لتعلم اختلاف الناس في حج','1054','1360','0');
INSERT INTO [t6227] VALUES (1523,'لأربع أو خمس مضين من ذي الحجة فدخل علي وهو غضبان فقلت من أغضبك أدخله الله النار فقال وما شعرت أني أمرت الناس بأمر فهم يترددون ولو أني كنت استقبلت من أمري ما استدبرت ما سقت الهدي معي حتى أشتريه وأحل كما حلوا وقد','1055','1360','0');
INSERT INTO [t6227] VALUES (1524,'لم يكن متمتعا بالعمرة إلى الحج إذ لو كان كذلك لما قال هذا لكنه أمر أصحابه بفسخ الإحرام بالعمرة قطعا لعادة الناس في كراهة الإحرام بالعمرة في أشهر الحج ثم لما رآهم يكرهون ذلك قال لو استقبلت من أمري ما استدبرت ما سقت الهدي','1056','1360','0');
INSERT INTO [t6227] VALUES (1525,'أن يجعلوها عمرة فتعاظم ذلك عندهم فقالوا يا رسول الله أي الحل قال الحل كله يعني يحلون من كل شيء أخرجه البخاري ومسلم في الصحيح ذكر حجج من اختار القران ورآه أفضل عند أبي داود عن ابن حنبل عن هشيم عن يحيى بن أبي','1056','1360','0');
INSERT INTO [t6227] VALUES (1526,'يلبي بالحج والعمرة جميعا يقول لبيك عمرة وحجا لبيك عمرة وحجا وفي رواية يحيى بن يحيى عن هشيم بمثله إلا أنه قال سمعت رسول الله','1057','1360','0');
INSERT INTO [t6227] VALUES (1527,'يصرخون بذلك وهم كل من جمع منهم بين الحج والعمرة وقد ذكرنا هذا في كتاب السنن مشروحا حديث معاذ بن معاذ عن شعبة عن مسلم القري سمع ابن عباس رضي الله عنهما يقول أهل النبي','1057','1360','0');
INSERT INTO [t6227] VALUES (1528,'ولا من ساق الهدي من','1057','1360','0');
INSERT INTO [t6227] VALUES (1529,'بالحج وذكر الحديث','1058','1360','0');
INSERT INTO [t6227] VALUES (1530,'بالحج وعن حميد عن بكر عن أنس رضي الله عنه قال سمعت النبي','1059','1360','0');
INSERT INTO [t6227] VALUES (1531,'يقول لبيك حجا وعمرة أخرجه البخاري ومسلم في الصحيح وروى الثقات عن زيد بن أسلم وغيره أن رجلا أتى ابن عمر','1059','1360','0');
INSERT INTO [t6227] VALUES (1532,'قال ألم تأت العام الأول قال بلى ولكن أنس بن مالك رضي الله عنه زعم أنه قرن قال ابن عمر إن أنس بن مالك كان يدخل على النساء وهن مكشفات الرؤوس وإني كنت تحت ناقة رسول الله','1060','1360','0');
INSERT INTO [t6227] VALUES (1533,'قال حجة واحدة واعتمر عمرته التي صده المشركون عن البيت وعمرنه حين صالحوه فدخل من العام المقبل وعمرته في ذي القعدة حين قسم غنيمة حنين وعمرته في حجته أخرجه البخاري في الصحيح ومسلم قال مع حجته وروي ذلك في حديث','1060','1360','0');
INSERT INTO [t6227] VALUES (1534,'حجتين قبل أن يهاجر وحج بعد ما هاجر حجة قرن معها عمرة وكذا رواه غير واحد عن زيد بن الحباب وكأنه أراد بذلك والله أعلم إن صح الجمع بينهما في سنة واحدة لا في عقد واحد فقد روينا عن جعفر بن محمد عن أبيه عن جابر رضي الله','1061','1360','0');
INSERT INTO [t6227] VALUES (1535,'أربعا وقال والرابعة التي قرن مع حجته وهذا أيضا لا يرضاه أهل العلم بالحديث فقد رواه ابن عيينة عن عمرو ابن دينار عن عكرمة أن النبي','1062','1360','0');
INSERT INTO [t6227] VALUES (1536,'أهل بالحج وهو عند أبي داود وعند مسلم عن حميد بن هلال عن مطرف قال قال','1062','1360','0');
INSERT INTO [t6227] VALUES (1537,'جمع بين حج وعمرة ثم لم ينه عنه حتى مات ولم ينزل فيه قرآن يحرمه وقد كان بسلم على حتى اكتويت فترك ثم تركت الكي فعاد وعند البخاري عن قتادة عن مطرف عن عمران رضي الله عنه قال تمتعنا مع رسول الله','1063','1360','0');
INSERT INTO [t6227] VALUES (1538,'اعتمر طائفة من أهله في العشر ورواه أبو رجاء العطاردي عن عمران في المتعة هكذا اختلفوا عنه وقصده من جميع ذلك بيان جواز العمرة في','1063','1360','0');
INSERT INTO [t6227] VALUES (1539,'يقول وهو بوادي العقيق أتاني الليلة آت فقال صل في هذا الوادي المبارك وقل عمرة في حجة فيكون ذلك والله أعلم إذنا في إدخال العمرة على الحج لا أنه','1064','1360','0');
INSERT INTO [t6227] VALUES (1540,'لقول أحد من الناس وعند أبي داود عن البراء رضي الله عنه قال كنت مع علي رضي الله عنه حين أمره رسول الله','1065','1360','0');
INSERT INTO [t6227] VALUES (1541,'فقال لي رسول الله','1065','1360','0');
INSERT INTO [t6227] VALUES (1542,'قال إني قد سقت الهدي وقرنت قوله وقرنت ليس بمذكور في الروايات الواردة في الصحيحين عن جابر وأنس رضي الله عنهما في قدوم علي رضي الله عنه من اليمن وجعل العلة في امتناعه من التحلل كون الهدي معه والقارن لا يحل من','1065','1360','0');
INSERT INTO [t6227] VALUES (1543,'هل تعلمون أن رسول الله','1065','1360','0');
INSERT INTO [t6227] VALUES (1544,'مسألة','1066','1360','0');
INSERT INTO [t6227] VALUES (1545,'في حجة الوداع بالعمرة إلى الحج وذكر الحديث وقال فيه عن النبي','1066','1360','0');
INSERT INTO [t6227] VALUES (1546,'وسبعة إذا رجع إلى أهله والله تعالى أعلم','1067','1360','0');
INSERT INTO [t6227] VALUES (1547,'ويبتدئ بالتلبية إذا انبعثت به راحلته في قوله الجديد وقال أبو حنيفة رحمه الله المختار أن يبتدئ به في مجلس صلاته بعد فراغه من الركعتين وهو قوله القديم فوجه الجديد ما عند البخاري عن ابن عمر رضي الله عنهما','1067','1360','0');
INSERT INTO [t6227] VALUES (1548,'أهل حين استوت به راحلته قائمة وأخرجه مسلم عنه بمعناه وعندهما عن أنس رضي الله عنه قال صلى رسول الله','1068','1360','0');
INSERT INTO [t6227] VALUES (1549,'إذا أخذ الطريق الفزع أهل إذا استقبلت به راحلته وإذا أخذ طريق أحد أهل إذا أشرف على جبل بيداء ووجه قوله القديم ما روى خصيف عن سعيد بن جبير عن','1068','1360','0');
INSERT INTO [t6227] VALUES (1550,'أهل في دبر صلاته حين فرغ من ركعتين وما مضى أصح إسنادا لأن خصيفا تكلموا فيه وقد تابعه الواقدي في ذلك عن ابن عباس رضي الله عنهما ومتابعة الواقدي لا تغني والأحاديث الواردة فيه عن ابن عمر رضي الله عنهما وغيره أسانيدها','1069','1360','0');
INSERT INTO [t6227] VALUES (1551,'والتلبية ليست بواجبة في الإحرام ولا هي شرط فيها وقال أبو حنيفة رحمه الله التلبية شرط في صحة الإحرام أو ما يقوم مقامها من سوق للهدي وروي عن الشعبي عن عروة بن مضرس بن أوس بن حارثة بن لام رضي الله عنه أنه حج على عهد','1069','1360','0');
INSERT INTO [t6227] VALUES (1552,'قال يا رسول الله أتعبت نفسي وأنضبت راحلتي فهل لي من حج فقال رسول الله','1070','1360','0');
INSERT INTO [t6227] VALUES (1553,'لم يذكرها الإمام والاقتصار على تلبية رسول الله','1071','1360','0');
INSERT INTO [t6227] VALUES (1554,'لبيك اللهم لبيك لبيك لا شريك لك لبيك إن الحمد والنعمة لك والملك لا شريك لك وكان عبد الله بن عمر رضي الله عنهما يزيد فيها لبيك لبيك وسعديك والخير بيديك لبيك والرغباء إليك والعمل وعند أبي داود عن جابر','1072','1360','0');
INSERT INTO [t6227] VALUES (1555,'يسمع فلا يقول لهم شيئا وروي عن ابن عباس رضي الله عنهما كان من تلبية رسول الله','1072','1360','0');
INSERT INTO [t6227] VALUES (1556,'مسألة','1072','1360','0');
INSERT INTO [t6227] VALUES (1557,'فقال يا رسول الله ما تأمرنا أن نلبس من الثياب في الإحرام فقال رسول الله','1073','1360','0');
INSERT INTO [t6227] VALUES (1558,'نهى النساء في إحرامهن عن القفازين والنقاب وما مس الورس والزعفران من الثياب ولتلبس بعد ذلك ما أحبت من ألوان الثياب معصفرا أو حزا أو حليا أو سراويلا أو قميصا أو خفا وروى مالك عن نافع أن عبد الله بن عمر رضي الله','1073','1360','0');
INSERT INTO [t6227] VALUES (1559,'ليس على المرأة حرم إلا في وجهها قال الحاكم أبو عبد الله هكذا رواه أيوب عن عبيد الله مسندا مرفوعا وحاله عند أئمة أهل النقل بخلاف ما عدله به عبد الله بن رجاء رحمه الله قال ابن معين شيخ يماني ضعيف وروى مخلد','1074','1360','0');
INSERT INTO [t6227] VALUES (1560,'مسألة','1076','1360','0');
INSERT INTO [t6227] VALUES (1561,'بميت مات محرما أن يكشف عن رأسه دون وجهه ولا يقرب طيبا ويكفن في ثوبيه اللذين مات فيهما قال ابن المنذر فيه أي المحرم يغطي وجهه ورخص سعد بن أبي وقاص وجابر بن عبد الله روى عن ابن الزبير يغشى المحرم وجهه بثوبه حتى شعر','1077','1360','0');
INSERT INTO [t6227] VALUES (1562,'مسألة','1078','1360','0');
INSERT INTO [t6227] VALUES (1563,'بعرفات فقال من لم يجد الإزار فليلبس السراويل ومن لم يجد النعلين فليلبس الخفين أخرجه البخاري ومسلم في الصحيح ولم يرخص في حديث ابن عمر إلا في النعلين قال وليقطعهما أسفل من الكعبين وفي أكثر الروايات أن ذلك كان','1078','1360','0');
INSERT INTO [t6227] VALUES (1564,'من لم يجد إزارا فليلبس سراويل ومن لم يجد نعلين فليلبس خفين والله أعلم','1079','1360','0');
INSERT INTO [t6227] VALUES (1565,'وإذا غسل المحرم رأسه بالسدر والخطمي لم تلزمه الفدية وقال أبو حنيفة رحمه الله عليه الفدية فيه حديث ابن عباس رضي الله عنهما في المحرم والله أعلم','1079','1360','0');
INSERT INTO [t6227] VALUES (1566,'مسألة','1080','1360','0');
INSERT INTO [t6227] VALUES (1567,'ادهن بزيت غير مقتت وهو محرم وفي رواية ابن عباس بدل ابن عمر والله أعلم','1080','1360','0');
INSERT INTO [t6227] VALUES (1568,'وإن تطيب ناسيا فلا فدية عليه وقال أبو حنيفة رحمه الله عليه الفدية','1080','1360','0');
INSERT INTO [t6227] VALUES (1569,'بالجعرانة فأتاه رجل وعليه مقطع يعني جبة وهو متضمخ بالخلوق فقال يا رسول الله إني أحرمت بالعمرة وهذه على فقال رسول الله','1081','1360','0');
INSERT INTO [t6227] VALUES (1570,'ما كنت صانعا في حجتك فاصنعه في عمرتك أخرجه البخاري ومسلم في الصحيح ورواه نوح بن حبيب القومسي فقال فيه فأتى بالرجل فقال أما الجبة فاخلعها وأما الطيب فاغسله ثم أحدث إحراما قال أبو عبد الرحمن السلمي لا أعلم','1081','1360','0');
INSERT INTO [t6227] VALUES (1571,'إن الله تجاوز عن أمتي الخطأ والنسيان وروى ابن جريج عن عطاء من تطيب ناسيا أو لبس ناسيا','1083','1360','0');
INSERT INTO [t6227] VALUES (1572,'مسألة','1084','1360','0');
INSERT INTO [t6227] VALUES (1573,'يقول لا ينكح','1084','1360','0');
INSERT INTO [t6227] VALUES (1574,'أن المحرم لا ينكح ولا ينكح ولا يخطب','1086','1360','0');
INSERT INTO [t6227] VALUES (1575,'تزوج ميمونة وهو محرم وبنى بها وهو حلال وماتت وهو بسرف أخرجه البخاري في الصحيح وروى عمرو بن دينار عن أبي الشعثاء عن عبد الله بن','1089','1360','0');
INSERT INTO [t6227] VALUES (1576,'نكح وهو محرم قال عمرو فحدثت ابن شهاب حديث أبي الشعثاء فقال حدثني يزيد ابن الأصم أن النبي','1090','1360','0');
INSERT INTO [t6227] VALUES (1577,'تزوجها وهو حلال قال وكانت خالتي وخالة ابن عباس وأخرجه أبو داود عن ميمون بن مهران عن يزيد بن الأصم ابن أخت ميمونة عن ميمونة رضي الله عنها قالت تزوجني رسول الله','1090','1360','0');
INSERT INTO [t6227] VALUES (1578,'نكح ميمونة وهو محرم ما نكحها رسول الله','1091','1360','0');
INSERT INTO [t6227] VALUES (1579,'تزوج وهو محرم قال أبو عيسى الترمذي رحمه الله سألت محمد بن إسماعيل البخاري رحمه الله عن هذا الحديث فقال يرون هذا عن ابن أبي مليكة مرسل ذكر عائشة فيه وهم والصواب إرساله كذا يقول أهل العلم بالحديث والله أعلم','1091','1360','0');
INSERT INTO [t6227] VALUES (1580,'مرسلا هكذا رواه جرير عن مغيرة مرسلا قال أبو علي الحافظ كلا الروايتين عن أبي عوانة خطأ والمحفوظ حديث جرير قال محمد بن عبد الله بن عمار سمعت عبد الرحمن بن مهدي يقول في حديث ابن مسعود رضي الله عنه لا بأس أن','1092','1360','0');
INSERT INTO [t6227] VALUES (1581,'مسألة','1093','1360','0');
INSERT INTO [t6227] VALUES (1582,'يستلمهما أخرجه البخاري ومسلم في الصحيح وحديث ابن عباس رضي الله عنهما لم أر رسول الله','1093','1360','0');
INSERT INTO [t6227] VALUES (1583,'لا يدع أن يستلم الركن اليماني والحجر في كل طوافه والله أعلم','1094','1360','0');
INSERT INTO [t6227] VALUES (1584,'ولا يصح الطواف إلا بما تصح به الصلاة من الطهارة وقال أبو حنيفة رحمه الله إن طاف بغير طهارة أجزأه ويجبر بدم ودليلنا من طريق الخبر ما روي عن موسى بن أعين عن عطاء بن السائب عن طاووس عن ابن عباس رضي الله عنهما عن النبي','1094','1360','0');
INSERT INTO [t6227] VALUES (1585,'يرمي على راحلته يوم النحر يقول لنا خذوا عني مناسككم فإني لا أدري لعلي لا أحج بعد حجتي هذه أخرجه مسلم في الصحيح بنحوه وعند البخاري عن عائشة رضي الله عنها قدمت وأنا حائض فشكوت ذلك إلى رسول الله','1096','1360','0');
INSERT INTO [t6227] VALUES (1586,'مسألة','1096','1360','0');
INSERT INTO [t6227] VALUES (1587,'فطاف بالبيت سبعا وصلى خلف المقام ركعتين ثم خرج إلى الصفا وقال قال الله تعالى لقد كان لكم في رسول الله أسوة حسنة أخرجه البخاري ومسلم وعن جابر رضي الله عنه قال قال رسول الله','1097','1360','0');
INSERT INTO [t6227] VALUES (1588,'من وراء الحجر وعند البخاري عن أبي السفر عن ابن عباس رضي الله','1097','1360','0');
INSERT INTO [t6227] VALUES (1589,'طاف من ورائه ولا يقولن أحدكم الحطيم وفي حديث عائشة رضي الله عنها في الصحيحين عن رسول الله','1098','1360','0');
INSERT INTO [t6227] VALUES (1590,'مسألة','1099','1360','0');
INSERT INTO [t6227] VALUES (1591,'طاف في حجة الوداع على بعيره يستلم الركن بمحجن أخرجه البخاري ومسلم من حديث الزهري عن عبيد الله بن عبد الله عنه وروى يزيد بن أبي زياد عن عكرمة عن ابن عباس رضي الله عنهما أن رسول الله','1099','1360','0');
INSERT INTO [t6227] VALUES (1592,'في حجة الوداع على راحتله بالبيت وبالصفا والمروة ليراه الناس وليشرف وليسألوه فإن الناس غشوه أخرجه مسلم في الصحيح وأخرج أيضا عن عائشة رضي الله عنها قالت طاف رسول الله','1100','1360','0');
INSERT INTO [t6227] VALUES (1593,'إني لم أطف طواف الخروج فقال إذا أقمت الصلاة فطوفي من وراء الناس على بعيرك والله أعلم','1100','1360','0');
INSERT INTO [t6227] VALUES (1594,'والسعي بين الصفا والمروة في الحج والعمرة ركن مفروض لا','1100','1360','0');
INSERT INTO [t6227] VALUES (1595,'أنه كان إذا طاف في الحج والعمرة أول ما يقدم سعى ثلاثة أطواف ومشى أربعا ثم يصلي سجدتين ويطوف بين الصفا والمروة أخرجه البخاري ومسلم في الصحيح وقد روينا عن النبي','1101','1360','0');
INSERT INTO [t6227] VALUES (1596,'الطواف بينهما وليس لأحد أن يترك الطواف بينهما كذا في رواية الزهري عن عروة وفي رواية هشام عن أبيه قالت ما أتم الله حج امرئ ولا عمرته لم يطف بين الصفا والمروة أخرجه مسلم في الصحيح','1101','1360','0');
INSERT INTO [t6227] VALUES (1597,'فطاف في بالبيت سبعا وصلى خلف المقام ركعتين وطاف بالصفا والمروة سبعا وقال لقد كان لكم في رسول الله أسوة حسنة قال عمرو فسألنا جابرا رضي الله عنه فقال لا تقربها حتى تطوف بين الصفا والمروة رواه البخاري عن','1102','1360','0');
INSERT INTO [t6227] VALUES (1598,'وهو يسعى بين الصفا والمروة فرأيته يسعى وأن مئزره ليدور من شدة السعي حتى لا أقول إني لأرى ركبته وسمعته يقول اسعوا فإن الله عز وجل كتب عليكم السعي ورواه يونس بن محمد ومعاذ بن هانئ عن ابن المؤمل في هذا','1103','1360','0');
INSERT INTO [t6227] VALUES (1599,'القارن يكفيه طواف واحد وسعي واحد وقال أبو حنيفة','1103','1360','0');
INSERT INTO [t6227] VALUES (1600,'عام حجة الوداع فذكر الحديث وقال في آخره قالت وأما الذين كانوا جمعوا بين الحج والعمرة فإنما طافوا طوافا واحدا أخرجه البخاري ومسلم في الصحيح وعند مسلم عنها رضي الله عنها أنها حاضت بسرف وطهرت بعرفة فقال لها رسول','1104','1360','0');
INSERT INTO [t6227] VALUES (1601,'قال لها طوافك بالبيت وبين الصفا والمروة يكفيك لحجك وعمرتك قال الشافعي وكان سفيان ربما قال عن عطاء عن عائشة وربما قال عن عطاء أن النبي','1104','1360','0');
INSERT INTO [t6227] VALUES (1602,'قال يكفيك طواف واحد بعد المعرف لحجك وعمرتك وفي حديث ابن عمر حين خرج إلى الحج عام نزل الحجاج بابن الزبير قال ثم انطلق يهل بهما جميعا حتى قدم','1104','1360','0');
INSERT INTO [t6227] VALUES (1603,'أخرجه البخاري ومسلم في الصحيح وروى الثقات عن نافع عن ابن عمر رضي الله عنهما أن رسول الله','1105','1360','0');
INSERT INTO [t6227] VALUES (1604,'ولا أصحابه بين الصفا والمروة إلا طوافا واحدا طوافه الأول أخرجه مسلم في الصحيح استدلوا بما روى الحسن بن عمارة عن الحكم عن مجاهد قال خرج ابن عمر رضي الله عنهما يهل بعمرة وهو يتخوف أيام نجدة أن يحبس عن البيت فلما سار','1105','1360','0');
INSERT INTO [t6227] VALUES (1605,'فعل قال علي بن عمر حفص بن أبي داود ضعيف وابن أبي ليلى ردئ الحفظ كثير الوهم وروي بإسناد آخر ولا يصح لأن راويه الحسن بن عمارة ورواه عيسى بن عبد الله بن محمد بن عمر بن علي حدثني أبي عن أبيه عن جده عن علي رضي','1106','1360','0');
INSERT INTO [t6227] VALUES (1606,'فعل ذلك ومثل ذلك لا يصح وروى علي بن عمر عن ابن صاعد عن ابن زنبور عن','1107','1360','0');
INSERT INTO [t6227] VALUES (1607,'طاف طوافين وسعى قال وسعى سعيين قال لنا ابن صاعد خالف محمد بن يحيى غيره في هذه الرواية قال الشيخ أبو الحسن يقال إن محمد بن يحيى حدث بهذا من حفظه فوهم في متنه والصواب بهذا الإسناد أن النبي','1109','1360','0');
INSERT INTO [t6227] VALUES (1608,'لعمرته وحجته طوافين وسعى سعيين وأبو بكر وعمر وعلي وابن مسعود رضي الله عنهم قال علي بن عمر أبو بردة هذا عمرو بن يزيد ضعيف ومن دونه في الإسناد ضعفاء والله أعلم','1110','1360','0');
INSERT INTO [t6227] VALUES (1609,'المؤذنون بعرفة يؤذنون في حال ما يخطب الإمام الخطبة الثانية وقال أبو حنيفة رحمه الله قبل الخطبة الأولى ولنا حديث جابر رضي الله عنه الطويل في صفه الحج عند مسلم وفيه فركب يعني رسول الله','1110','1360','0');
INSERT INTO [t6227] VALUES (1610,'قال ثم أذن بلال ثم أقام فصلى الظهر ثم أقام فصلى العصر ولم يصل بينهما شيئا وروى الشافعي أخبرنا إبراهيم بن محمد وغيره عن جعفر بن محمد عن أبيه عن جابر في حجة الإسلام قال فراح النبي','1111','1360','0');
INSERT INTO [t6227] VALUES (1611,'في الخطبة الثانية ففرغ من الخطبة وبلال من الأذان ثم أقام بلال فصلى الظهر ثم أقام فصلى العصر والله أعلم','1111','1360','0');
INSERT INTO [t6227] VALUES (1612,'وتؤدي صلاة الجمع بالمزدلفة بأذان وإقامتين وقال أبو حنيفة رحمه الله بأذان وإقامة ولنا ما في حديث جابر رضي الله عنه قال حتى أتى المزدلفة فجمع بين المغرب والعشاء بأذان واحد وإقامتين ولم يسبح بينهما شيئا وهو عند','1111','1360','0');
INSERT INTO [t6227] VALUES (1613,'مسألة','1112','1360','0');
INSERT INTO [t6227] VALUES (1614,'قال عليكم بحصى الخذف الذي يرمى به الجمرة وروي عن أبي العالية قال قال لي ابن عباس رضي الله عنهما غداة العقبة هات القط لي حصيات من حصى الخذف فلما وضعن في يده قال بأمثال هؤلاء بأمثال هؤلاء وإياكم والغلو في الدين','1112','1360','0');
INSERT INTO [t6227] VALUES (1615,'إلا ما قام دليله ولهم في ذلك أثر عن سفيان بن حرب قال رأيت سكينة بنت الحسن ترمي الجمرة ورجل يناولها حصاة حصاة كلما رمت بحصاة كبرت ورمت بست وسقطت واحدة فرمت بخاتمها والله أعلم','1113','1360','0');
INSERT INTO [t6227] VALUES (1616,'ورمي جمرة العقبة جائز في النصف الأخير من ليلة النحر وقال أبو حنيفة رحمه الله لا يجوز قبل طلوع الفجر من يوم النحر ودليلنا من طريق الخبر حديث أسماء رضي الله عنها في','1113','1360','0');
INSERT INTO [t6227] VALUES (1617,'أذن للظعن لفظ حديث مسلم وروي عن عائشة رضي الله عنها قالت أرسل رسول الله','1114','1360','0');
INSERT INTO [t6227] VALUES (1618,'رواه أبو داود في كتاب السنن بمعناه وروي عن زينب بنت أبي سلمة عن أم سلمة أن رسول الله','1114','1360','0');
INSERT INTO [t6227] VALUES (1619,'لم يكن معها بمكة','1114','1360','0');
INSERT INTO [t6227] VALUES (1620,'أغيلمة عبد المطلب على حمرات ثم جعل يلطح أفخاذنا ويقول أبيني لا ترموا الجمرة حتى تطلع الشمس اللطح الضرب اللين','1115','1360','0');
INSERT INTO [t6227] VALUES (1621,'كان يأمر نساءه وثقله من صبيحة جمع أن يفيضوا مع أول الفجر بسواد وأن لا يرموا الجمرة إلا مصلين رواته ثقات وفي صحيح مسلم عن جابر رضي الله عنه رمى رسول الله','1116','1360','0');
INSERT INTO [t6227] VALUES (1622,'مسألة','1116','1360','0');
INSERT INTO [t6227] VALUES (1623,'بمنى للناس يسألونه فجاء رجل فقال يا رسول الله لم أشعر فحلقت قبل أن أنحر قال اذبح ولا حرج ثم جاءه آخر فقال لم أشعر فنحرت قبل أن أرمي قال ارم ولا حرج فما سئل رسول الله','1117','1360','0');
INSERT INTO [t6227] VALUES (1624,'قيل له في الذبح والحلق والرمي والتقديم والتأخير قال لا حرج وعند البخاري عنه أيضا أن النبي','1117','1360','0');
INSERT INTO [t6227] VALUES (1625,'مسألة','1118','1360','0');
INSERT INTO [t6227] VALUES (1626,'من جمع إلى منى أن النبي','1118','1360','0');
INSERT INTO [t6227] VALUES (1627,'مسألة','1119','1360','0');
INSERT INTO [t6227] VALUES (1628,'لإحرامة قبل أن يحرم ولحلة قبل أن يطوف بالبيت وسنة رسول الله','1120','1360','0');
INSERT INTO [t6227] VALUES (1629,'مسألة','1120','1360','0');
INSERT INTO [t6227] VALUES (1630,'بينا هو يخطب يوم النحر فقام إليه رجل فقال كنت أحسب يا رسول الله أن كذا وكذا قبل كذا وكذا ثم قال آخر فقال كنت أحسب يا رسول الله أن كذا وكذا لهؤلاء الثلاث فقال رسول الله','1120','1360','0');
INSERT INTO [t6227] VALUES (1631,'يوم النحر فقال أي يوم هذا قلنا الله ورسوله أعلم فسكت حتى ظننا أنه سيسميه بغير اسمه قال أليس يوم النحر قلنا بلى وذكر باقي الحديث والله أعلم','1120','1360','0');
INSERT INTO [t6227] VALUES (1632,'مسألة','1121','1360','0');
INSERT INTO [t6227] VALUES (1633,'يخطب بين أوسط أيام التشريق ونحن عند راحلته وهي خطبة رسول الله','1121','1360','0');
INSERT INTO [t6227] VALUES (1634,'مسألة','1121','1360','0');
INSERT INTO [t6227] VALUES (1635,'رمى جمرة العقبة أول يوم ضحى وأما بعد ذلك فبعد الزوال وروى طلحة بن عمرو عن ابن أبي مليكة عن ابن عباس رضي الله عنهما إذا انتضح النهار من يوم النفر الآخر فقد حل الرمي والصدر وطلحة بن عمرو ضعيف والله أعلم','1122','1360','0');
INSERT INTO [t6227] VALUES (1636,'للصبي حج وقال أبو حنيفة رحمه الله لا حج للصبي','1122','1360','0');
INSERT INTO [t6227] VALUES (1637,'وروي عنه رضي الله عنه أنه قال حججنا مع رسول الله','1123','1360','0');
INSERT INTO [t6227] VALUES (1638,'وأنا غلام وفي رواية عنده عنه حج بي مع رسول الله','1123','1360','0');
INSERT INTO [t6227] VALUES (1639,'بعبد الله بن عباس رضي الله عنهما قبل بلوغه ففي الصحيحين عنه رضي الله عنهما جئت راكبا على أتان وقد ناهزت الحلم فإذا النبي','1124','1360','0');
INSERT INTO [t6227] VALUES (1640,'مع الثقل من جمع بليل فصلينا ورمينا قبل أن يأتي الناس وروى الشافعي عن سعيد بن سالم عن مالك بن مغول عن أبي السفر قال قال ابن عباس رضي الله عنهما أيها الناس أسمعوني ما تقولون وافهموا ما أقول لكم أيما مملوك حج به أهله','1124','1360','0');
INSERT INTO [t6227] VALUES (1641,'إذا حج الصبي فهي له حجة حتى يعقل وإذا عقل فعليه حجة أخرى قال أبو عبد الله الحاكم هذا حديث صحيح قال البيهقي رحمه الله وأظن أن شيخنا حمل حديث عفان وغيره على حديث يزيد فهذا الحديث إنما رواه أصحاب شعبة عنه موقوفا','1125','1360','0');
INSERT INTO [t6227] VALUES (1642,'وإذا جامع المحرم قبل الحل الأول فسد حجه وعليه بدنة وقال أبو حنيفة رحمه الله إذا كان قبل الوقوف بعرفة فسد حجه وعليه شاة وإن كان بعد الوقوف لم يفسد حجه وعليه بدنة قال الله تعالى الحج أشهر معلومات فمن فرض فيهن الحج','1125','1360','0');
INSERT INTO [t6227] VALUES (1643,'اقضيا نسككما واهديا هديا في هذا الحديث وعليكما حجة أخرى وهذا يتأكد بآثار الصحابة رضي الله عنهم كما سنذكرها عقيب هذا المرسل روى الثقات عن عمرو بن شعيب عن أبيه أن رجلا أتى عبد الله بن عمرو رضي الله عنهما يسأله','1126','1360','0');
INSERT INTO [t6227] VALUES (1644,'مسألة','1127','1360','0');
INSERT INTO [t6227] VALUES (1645,'قال من لم يدرك الحج فعليه دم ويجعلها عمرة وعليه الحج من قابل وعنه عن نافع عن ابن عمر مثله وروى الشافعي رحمه الله عن مالك عن يحيى بن سعيد أخبرني سليمان بن يسار أن أبا أيوب خرج حاجا حتى إذا كان بالنازية من طريق','1128','1360','0');
INSERT INTO [t6227] VALUES (1646,'مسألة','1131','1360','0');
INSERT INTO [t6227] VALUES (1647,'دخل مكة عام الفتح وعلى رأسه مغفر وعند مسلم عن جابر رضي الله عنه أن رسول الله','1132','1360','0');
INSERT INTO [t6227] VALUES (1648,'قال لا تجوز المواقيت إلا بإحرام خصيف ليس بالقوي وروى إسماعيل بن مسلم عن عطاء عن ابن عباس رضي الله عنهما فوالله ما دخلها رسول الله','1134','1360','0');
INSERT INTO [t6227] VALUES (1649,'دخلها بغير إحرام والله أعلم','1134','1360','0');
INSERT INTO [t6227] VALUES (1650,'وإذا قلنا إنه يلزمه دخولها بنسك فلم يفعل فلا قضاء عليه وقال أبو حنيفة رحمه الله عليه القضاء فيحتاج أن يدخلها بحج أو عمرة إلا أن يحج من سنته فيدخل القضاء في حجة الإسلام لنا حديث الأقرع بن حابس والله أعلم','1134','1360','0');
INSERT INTO [t6227] VALUES (1651,'مسألة','1135','1360','0');
INSERT INTO [t6227] VALUES (1652,'يقول الحج عرفة من أدرك عرفة قبل أن يطلع الفجر فقد أدرك الحج وتم حجه والله أعلم','1135','1360','0');
INSERT INTO [t6227] VALUES (1653,'وما له مثل من النعم من الصيد يجزى بمثله وقال أبو حنيفة رحمه الله بقيمته','1135','1360','0');
INSERT INTO [t6227] VALUES (1654,'في الضبع يصيبه المحرم كبشا نجديا وجعله من الصيد قال الشافعي رحمه الله أخبرنا مالك وسفيان عن أبي الزبير عن جابر أن عمر بن الخطاب رضي الله عنه قضى في الأرنب بعناق وقضى في الغزال بعنز وفي اليربوع بجفرة وروى','1136','1360','0');
INSERT INTO [t6227] VALUES (1655,'مسألة','1140','1360','0');
INSERT INTO [t6227] VALUES (1656,'قال لحم صيد البر لكم حلال ما لم تصيدوه أو يصاد لكم','1140','1360','0');
INSERT INTO [t6227] VALUES (1657,'وذكرت أني لم أكن أحرمت وأني إنما اصطدته لك فأمر النبي','1141','1360','0');
INSERT INTO [t6227] VALUES (1658,'مسألة','1142','1360','0');
INSERT INTO [t6227] VALUES (1659,'مسألة','1142','1360','0');
INSERT INTO [t6227] VALUES (1660,'مسألة','1143','1360','0');
INSERT INTO [t6227] VALUES (1661,'مسألة','1144','1360','0');
INSERT INTO [t6227] VALUES (1662,'مسألة','1144','1360','0');
INSERT INTO [t6227] VALUES (1663,'قال مالك هو عبد الرحمن بن عوف رضي الله عنه على رجلين أوطيا ظبيا فقتلاه بشاة','1144','1360','0');
INSERT INTO [t6227] VALUES (1664,'مسألة','1145','1360','0');
INSERT INTO [t6227] VALUES (1665,'قال خمس من الدواب ليس على المحرم في قتلهن جناح الغراب والحدأة والعقرب والفأرة والكلب العقور','1145','1360','0');
INSERT INTO [t6227] VALUES (1666,'خمس من الدواب كلها فاسق يقتلن في الحرم الغراب والحدأة والكلب العقور والعقرب والفأرة أخرجه من حديث عروة وأخرجه مسلم من حديث ابن المسيب عنها وقال فيه الحية بدل العقرب ومن حديث القاسم عنها أربع فواسق','1146','1360','0');
INSERT INTO [t6227] VALUES (1667,'فذكر الحية بدل الغراب وروى أبو داود عن ابن حنبل أخبر هشيم أخبرنا يزيد بن أبي زياد حدثنا عبد الرحمن بن أبي نعيم البجلي عن أبي سعيد الخدري رضي الله عنه أن النبي','1147','1360','0');
INSERT INTO [t6227] VALUES (1668,'مسألة','1147','1360','0');
INSERT INTO [t6227] VALUES (1669,'المدينة حرام ما بين عير إلى ثور فمن أحدث فيها حدثا أو آوى محدثا فعليه لعنة الله والملائكة والناس أجمعين لا يقبل منه عدل ولا صرف الحديث أخرجه البخاري ومسلم في الصحيح وعند أبي داود عن علي رضي الله عنه في هذه القصة','1148','1360','0');
INSERT INTO [t6227] VALUES (1670,'طلع له أحد فقال له هذا جبل يحبنا ونحبه اللهم إن إبراهيم حرم مكة وإني أحرمك ما بين لابتيها','1148','1360','0');
INSERT INTO [t6227] VALUES (1671,'المدينة قال نعم هي حرام حرمها الله ورسوله لا يختلى خلاها فمن فعل ذلك فعليه لعنة الله والملائكة والناس أجمعين وعن عبد الله بن زيد رضي الله عنه عن رسول الله','1149','1360','0');
INSERT INTO [t6227] VALUES (1672,'ما بين لابتيها حرام ورواه عبد الرحمن بن مهدي عن مالك فقال فيه لو رأيت الظباء بالمدينة ما ذعرتها إن رسول الله','1149','1360','0');
INSERT INTO [t6227] VALUES (1673,'ما بين لابتيها وذلك عندنا في أديم حولاني وعنده أيضا عن عبد الرحمن بن أبي سعيد الخدري عن أبيه رضي الله عنه أنه سمع رسول الله','1150','1360','0');
INSERT INTO [t6227] VALUES (1674,'إلى المدينة فقال إنها حرم آمن وعنده أيضا عن جابر رضي الله عنه قال قال رسول الله','1151','1360','0');
INSERT INTO [t6227] VALUES (1675,'قال إني أحرم ما بين لابتي المدينة أن يقطع عضاهها أو يقتل صيدها وروى في تحريم المدينة عن زيد بن ثابت وأبي قتادة الأنصاري رضي الله عنهما وفي ذلك إبطال قول من زعم أن ذلك ليس بعام فقد رويناه عن أحد عشر نفسا عن النبي','1151','1360','0');
INSERT INTO [t6227] VALUES (1676,'فلم يرد إليهم شيئا وفي الصحيحين عن أنس رضي الله عنه قال كان ابن لأم سليم رضي الله عنها يقال له أبو عمير كان النبي','1152','1360','0');
INSERT INTO [t6227] VALUES (1677,'مازح صبيا وفيه أنه لم ينه عن لعب','1152','1360','0');
INSERT INTO [t6227] VALUES (1678,'وذلك أنه لم يأمره برفع اليد عنه وحين مات لم يوجب فيه الجزاء وقد دللنا على أن حرم المدينة كحرم مكة وأما تحريم وج من الطائف فما روى فيه عن النبي','1153','1360','0');
INSERT INTO [t6227] VALUES (1679,'من لية قال الحميدي مكان بالطائف حتى إذا كنا عند السدرة وقف رسول الله','1154','1360','0');
INSERT INTO [t6227] VALUES (1680,'مسألة','1154','1360','0');
INSERT INTO [t6227] VALUES (1681,'بالحديبية البقرة عن سبعة والبدنة عن سبعة وفي صحيح البخاري عن الزهري عن عروة عن المسور ومروان يصدق حديث كل واحد منهما صاحبه قالا خرج رسول الله','1155','1360','0');
INSERT INTO [t6227] VALUES (1682,'نحر بها هديه وأن أصحابه لما رأوه نحر قاموا فنحروا وفي ذلك دليل على أنه','1155','1360','0');
INSERT INTO [t6227] VALUES (1683,'وأبو داود يقول حدثنا محمد بن كثير أخبرنا سفيان عن هشام','1155','1360','0');
INSERT INTO [t6227] VALUES (1684,'بعث معه بهدي فقال إن عطب فانحره ثم أصبغ نعله في دمه ثم خل بينه وبين الناس وليس لهم حجة فيه وفي صحيح مسلم عن ابن عباس رضي الله عنهما قال بعث رسول الله','1156','1360','0');
INSERT INTO [t6227] VALUES (1685,'الذي بويع فيه تحت الشجرة قال وإنما ذهبنا إلى أنه نحر في الحل لأن الله تعالى يقول هم الذين كفروا وصدوكم عن المسجد الحرام والهدي معكوفا أن يبلغ محله والحرم كله محله عند أهل العلم','1156','1360','0');
INSERT INTO [t6227] VALUES (1686,'مسألة','1158','1360','0');
INSERT INTO [t6227] VALUES (1687,'على ضباعة بنت الزبير فقال لها أردت الحج قالت والله ما أجدني إلا وجعة فقال لها حجي واشترطي وقولي اللهم محلي حيث حبستني وكانت تحت المقداد أخرجه البخاري ومسلم في الصحيح ورواه مسلم أيضا عن ابن عباس رضي الله','1159','1360','0');
INSERT INTO [t6227] VALUES (1688,'وروي الاشتراط في الحج عن ابن عمر وابن مسعود وعائشة وأم سلمة رضي الله عنهم من قولهم ولو كان يحل بالمرض لم يكن للاشتراط معنى والله أعلم واستدلوا بما روى عن عكرمة مولى ابن عباس رضي الله عنهما عن الحجاج بن عمرو','1160','1360','0');
INSERT INTO [t6227] VALUES (1689,'خاصة وفي هذا الحديث دلالة عن أبي ذر رضي الله عنه على أن فسخ الحج إلى العمرة لا يجوز لأحد بعد النبي','1161','1360','0');
INSERT INTO [t6227] VALUES (1690,'مسألة','1161','1360','0');
INSERT INTO [t6227] VALUES (1691,'قال ليس لها أن تنطلق إلا بإذن زوجها ولا يحل للمرأة أن تسافر ثلاث ليال إلا ومعها ذو محرم تحرم عليه وروي عن حسان بالقول الثاني احتج بما روينا عن النبي','1162','1360','0');
INSERT INTO [t6227] VALUES (1692,'مسألة','1163','1360','0');
INSERT INTO [t6227] VALUES (1693,'مسألة','1164','1360','0');
INSERT INTO [t6227] VALUES (1694,'قال إذا كان يوم الجمعة كان على كل باب من أبواب المسجد ملائكة يكتبون الناس الأول فالأول فالمهجر إلى الصلاة كالمهدي كبشا حتى ذكر الدجاجة والبيضة فإذا جلس الإمام طووا الصحف واجتمعوا للخطبة وعندها عنه فيه فمثل المهجر','1165','1360','0');
INSERT INTO [t6227] VALUES (1695,'إشعاره البدنة مسنون وقال أبو حنيفة رحمه الله إنه مكروه لنا حديث عائشة رضي الله عنها قالت فتلت قلائد هدي رسول الله','1165','1360','0');
INSERT INTO [t6227] VALUES (1696,'أخرجاه في الصحيح وعند البخاري عن المسور بن مخرمة رضي الله عنه أن رسول الله','1166','1360','0');
INSERT INTO [t6227] VALUES (1697,'لما أتى ذا الحليفة أشعر بدنته من جانب سنامها الأيمن ثم سلت عنها الدم وفي رواية ثم أماط عنها الدم وأهل بالحج وروى مالك عن نافع أن عبد الله بن عمر رضي الله عنهما كان يشعر بدنه من الشق الأيسر إلا أن تكون صعابا تنفر','1166','1360','0');
INSERT INTO [t6227] VALUES (1698,'مسألة','1167','1360','0');
INSERT INTO [t6227] VALUES (1699,'فيبعث بها ثم يمكث حلالا وفي صحيح مسلم عنها قالت أهدى رسول الله','1167','1360','0');
INSERT INTO [t6227] VALUES (1700,'حلال لم يحرم منه شيء والله أعلم','1167','1360','0');
INSERT INTO [t6227] VALUES (1701,'إذا نذر هديا بعينه لم يكن له أن يصرفه إلى غيره أو يعطى قيمته وقال أبو حنيفة رحمه الله فيما حكى عنه إن له ذلك','1167','1360','0');
INSERT INTO [t6227] VALUES (1702,'فقال يا رسول الله إني أهديت نجيبا فأعطيت بها ثلاث مائة دينار أفأبيعها وأشتري بثمنها بدنة قال لا انحرها إياها والله سبحانه وتعالى أعلم وله الحمد والمنة ومنه التوفيق والعصمة آخر كتاب العبادات','1168','1360','0');
INSERT INTO [t6227] VALUES (1703,'كتاب البيوع','1170','1360','0');
INSERT INTO [t6227] VALUES (1704,'ذكر ما اختلف فيه الشافعي وأبو حنيفة رحمهما الله تعالى من كتاب البيوع مما ورد فيه خبر أو أثر','1170','1360','0');
INSERT INTO [t6227] VALUES (1705,'مسألة','1170','1360','0');
INSERT INTO [t6227] VALUES (1706,'عن بيع الحصاة وعن بيع الغرر أخرجه مسلم في الصحيح وعن أبي داود عن عبد الله بن عمرو رضي الله عنهما قال قال رسول الله','1170','1360','0');
INSERT INTO [t6227] VALUES (1707,'عن بيع الغائب كله من كل شيء يديره الناس بينهم وروى الشافعي عن الثقة قال البيهقي أحسبه إسماعيل بن علية عن أيوب عن يوسف بن ماهك عن حكيم بن حزام قال نهاني رسول الله','1171','1360','0');
INSERT INTO [t6227] VALUES (1708,'مسألة','1172','1360','0');
INSERT INTO [t6227] VALUES (1709,'قال المتبايعان كل واحد منهم بالخيار على صاحبه ما لم يتفرقا إلا بيع الخيار أخرجه البخاري في الصحيح ومسلم واللفظ له','1172','1360','0');
INSERT INTO [t6227] VALUES (1710,'قال إذا تبايع الرجلان فكل واحد منهما بالخيار ما لم يفترقا وكانا جميعا أو يخير أحدهما الآخر فتبايعا على ذلك فقد وجب البيع وإن تفرقا بعد أن تبايعا ولم يترك واحد منهما البيع فقد وجب البيع وعندهما أيضا عن حكيم بن حزام','1173','1360','0');
INSERT INTO [t6227] VALUES (1711,'من اشترى بيعا فوجب له هو بالخيار ما لم يفارقه صاحبه فإن فارقه فلا خيار له قال أبو عبد الله هذا حديث صحيح وروي في ذلك عن أبي هريرة وسمرة بن جندب وجابر','1173','1360','0');
INSERT INTO [t6227] VALUES (1712,'مسألة','1175','1360','0');
INSERT INTO [t6227] VALUES (1713,'أنه نهى عن بيع الغرر وفي حديث عائشة رضي الله عنها عن رسول الله','1175','1360','0');
INSERT INTO [t6227] VALUES (1714,'في','1175','1360','0');
INSERT INTO [t6227] VALUES (1715,'ولم نجاوزه أما حديث المصراة فهو في مسألة بيعها وأما حديث حبان بن منقذ رضي الله عنه فغروي عن ابن عمر رضي الله عنهما قال كان حبان بن منقذ رجلا ضعيفا وكان قد سفع في رأسه مأمومة فجعل رسول الله','1176','1360','0');
INSERT INTO [t6227] VALUES (1716,'بع وقل لا خلابة وكنت أسمعه يقول لا خذابة فكان يشتري الشيء ويجئ به أهله فيقولون هذا غال فيقول إن رسول الله','1176','1360','0');
INSERT INTO [t6227] VALUES (1717,'مسألة','1177','1360','0');
INSERT INTO [t6227] VALUES (1718,'من مات وترك عليه دينا فدينه على الله ورسوله ومن مات وترك شيئا فهو للوارث والله أعلم','1177','1360','0');
INSERT INTO [t6227] VALUES (1719,'مسألة','1178','1360','0');
INSERT INTO [t6227] VALUES (1720,'الذهب بالورق ربا إلا هاء وهاء والبر بالبر ربا إلا هاء وهاء والتمر بالتمر ربا إلا','1178','1360','0');
INSERT INTO [t6227] VALUES (1721,'مثل حديث مالك وقال حتى يأتي خازني من الغابة أخرجه البخاري من حديث مالك وأخرجه مسلم من حديث ابن عيينة عن أبي قلابة قال كنت بالشام في حلقة فيها مسلم بن يسار فجاء أبو الأشعث قال قالوا أبو الأشعث فجلس فقلت','1179','1360','0');
INSERT INTO [t6227] VALUES (1722,'ينهى عن بيع الذهب بالذهب والفضة بالفضة والبر بالبر والشعير بالشعير والتمر بالتمر والملح بالملح إلا سواء بسواء عينا بعين فمن زاد أو ازداد فقد أربى فرد الناس ما أخذوا أخرجه مسلم في الصحيح وعن عبادة رضي الله عنه','1180','1360','0');
INSERT INTO [t6227] VALUES (1723,'الذهب بالذهب والفضة بالفضة والبر بالبر والشعير بالشعير والتمر','1180','1360','0');
INSERT INTO [t6227] VALUES (1724,'التمر بالتمر والحنطة بالحنطة والشعير بالشعير والملح بالملح مثلا بمثل يدا بيد فمن زاد أو استزاد فقد أربى إلا ما اختلفت ألوانه قوله إلا ما اختلفت ألوانه أراد به في جواز التفاضل لا في التفرق عن المجلس قبل','1181','1360','0');
INSERT INTO [t6227] VALUES (1725,'يقول الطعام بالطعام مثلا بمثل وعن ابن مسعود رضي الله عنه عن رسول الله','1186','1360','0');
INSERT INTO [t6227] VALUES (1726,'أنه قال والملح بالملح مثلا بمثل يدا بيد','1186','1360','0');
INSERT INTO [t6227] VALUES (1727,'بعث أخا بني عدي الأنصاري فاستعمله على خيبر فقدم بتمر جنيب فقال رسول الله','1187','1360','0');
INSERT INTO [t6227] VALUES (1728,'لا تفعلوا ولكن مثلا بمثل أو تبيعوا هذا وتشتروا بثمنه من هذا وكذلك الميزان رواه مسلم والبخاري في الصحيح وأخرجه من حديث مالك دون قوله وكذلك الميزان والأشبه أن يكون ذلك من قول أبي سعيد لما خالفه فيه ابن عباس','1187','1360','0');
INSERT INTO [t6227] VALUES (1729,'أربيت إذا أردت ذلك فبع تمرك بسلعة ثم اشتر بسلعتك أي تمر شئت','1187','1360','0');
INSERT INTO [t6227] VALUES (1730,'قال ردوه ردوه التمر بالتمر والحنطة بالحنطة والشعير بالشعير والذهب بالذهب والفضة بالفضة يدا بيد مثلا بمثل ليس فيه زيادة ولا نقصان فمن زاد أو نقص فقد أربى في كل ما يكال أو يوزن','1188','1360','0');
INSERT INTO [t6227] VALUES (1731,'في الربا وروي في حديث عبادة وأنس بن مالك ولا يصح رواه أبو بكر ابن عياش عن الربيع بن صبيح عن الحسن عن عبادة وأنس رضي الله عنهما عن النبي','1189','1360','0');
INSERT INTO [t6227] VALUES (1732,'بلفظ غير هذا اللفظ وروي عن محمد بن يعلى الكوفي عن الربيع عن ابن سيرين عن ابن الصامت وعن أنس رضي الله عنهما عن النبي','1189','1360','0');
INSERT INTO [t6227] VALUES (1733,'قال الورق بالورق والذهب بالذهب والتمر بالتمر والبر بالبر والشعير بالشعير والملح بالملح عينا بعين أو قال وزنا بوزن وقال أحدهما ولم يقله الآخر ولا بأس بالدينار بالورق اثنين بواحد يدا بيد ولا بأس بالبر','1190','1360','0');
INSERT INTO [t6227] VALUES (1734,'وهو وهم والصواب أنه من قول ابن المسيب مع أنه لا حجة لهم فيه والله أعلم','1191','1360','0');
INSERT INTO [t6227] VALUES (1735,'مسألة','1192','1360','0');
INSERT INTO [t6227] VALUES (1736,'أمره أن يجهز جيشا فنفذت الإبل فأمره أن يأخذ في قلاص الصدقة فكان يأخذ البعير بالبعيرين إلى إبل الصدقة وله شاهد صحيح عن عمرو بن شعيب عن أبيه عن جده عن عبد الله بن عمرو رضي الله عنهما مرفوعا نحوه ذكرناه في كتاب السنن','1192','1360','0');
INSERT INTO [t6227] VALUES (1737,'أنه نهى عن بيع الحيوان بالحيوان نسيئة هذا حديث رواته ثقات إلا أن أهل العلم بالحديث اختلفوا في','1193','1360','0');
INSERT INTO [t6227] VALUES (1738,'عن بيع الحيوان بالحيوان نسيئة كل من روى هذا الحديث عن الثوري وذكر ابن عباس رضي الله عنه في إسناده فقد وهم وذلك لأن الثوري إنما رواه مرسلا وذكره من حديث الفريابي عنه مرسلا وقال وهو','1194','1360','0');
INSERT INTO [t6227] VALUES (1739,'مرسلا قال أبو عيسى الترمذي سألت محمد بن إسماعيل البخاري رحمه الله عن هذا الحديث فقال قد روى داود بن عبد الرحمن العطار عن معمر وقال عن ابن عباس وقال الناس عن عكرمة عن النبي','1195','1360','0');
INSERT INTO [t6227] VALUES (1740,'عن بيع الحيوان بالحيوان نسيئة فهذا غير ثابت عن رسول الله','1196','1360','0');
INSERT INTO [t6227] VALUES (1741,'نهى عن بيع الحيوان بالحيوان نسيئة تفرد به محمد بن دينار إنما يرويه الناس عن زياد بن جبير عن النبي','1196','1360','0');
INSERT INTO [t6227] VALUES (1742,'قال عثمان بن سعيد سمعت يحيى بن معين يقول يزيد بن مروان الخلال كذاب قال أبو سعيد وقد أدركت أبا يزيد هذا وهو ضعيف قريب مما قال يحيى وقد روي عنه أيضا عن مالك عن الزهري عن سهل نهى رسول الله','1197','1360','0');
INSERT INTO [t6227] VALUES (1743,'مرسلا في النهي عن بيع اللحم بالحيوان وروى إسحاق الحنظلي عن محمد بن بكر البرساني عن ابن أبي عروبة عن قتادة قال سألت ابن عباس رضي الله عنهما عن شاة بشاتين إلى الحبال فقال سأل رجل عمر بن الخطاب رضي الله عنه فقال عمر','1197','1360','0');
INSERT INTO [t6227] VALUES (1744,'قبض قبل أن يفسرها لنا فدعوا الربا والريبة وروى بحر بن كثير السقا عن أبي الزبير عن جابر نهى رسول الله','1198','1360','0');
INSERT INTO [t6227] VALUES (1745,'مسألة','1198','1360','0');
INSERT INTO [t6227] VALUES (1746,'عام خيبر بقلادة فيها خرز معلقة بذهب ابتاعها','1198','1360','0');
INSERT INTO [t6227] VALUES (1747,'لا حتى يميز بينه وبينها قال إنما أردت الحجارة قال لا حتى يميز بينهما قال فرده حتى ميز بينهما أخرجه مسلم في الصحيح وأما الذي رواه الليث عن سعيد بهذا الإسناد عن فضالة أنه اشترى يوم خيبر قلادة باثني عشر دينارا','1199','1360','0');
INSERT INTO [t6227] VALUES (1748,'في الموضعين جميعا برد البيع حتى يفصل الذهب ونحن نقول بهما جميعا والله أعلم','1199','1360','0');
INSERT INTO [t6227] VALUES (1749,'مسألة','1200','1360','0');
INSERT INTO [t6227] VALUES (1750,'أنه قال لا تبيعوا الثمر حتى يبدو صلاحه ولا تبيعوا الثمر بالتمر اتفقا على صحته وعند مسلم عنه أن النبي','1200','1360','0');
INSERT INTO [t6227] VALUES (1751,'سئل عن شراء التمر بالرطب فقال رسول الله','1201','1360','0');
INSERT INTO [t6227] VALUES (1752,'عن بيع الرطب بالتمر نسيئة قال الدارقطني خالفه مالك وإسماعيل بن أمية والضحاك بن عثمان وأسامة بن زيد ورووه عن عبد الله بن يزيد ولم يقولوا','1201','1360','0');
INSERT INTO [t6227] VALUES (1753,'سئل عن رطب بتمر فقال أينقص الرطب فقالوا نعم فقال لا يباع رطب بيابس وروي عن عكرمة عن ابن عباس رضي الله عنهما أن النبي','1202','1360','0');
INSERT INTO [t6227] VALUES (1754,'عن التمر الجاف بالرطب وعن هشام بن عروة عن أبيه عن عائشة رضي الله عنها قالت سألت رسول الله','1202','1360','0');
INSERT INTO [t6227] VALUES (1755,'مسألة','1203','1360','0');
INSERT INTO [t6227] VALUES (1756,'لا تبيعوا الذهب بالذهب ولا الفضة بالفضة ولا البر بالبر ولا الشعير بالشعير ولا الملح بالملح ولا التمر بالتمر إلا مثلا بمثل سواء بسواء عينا بعين استدلوا بما روي عن سماك بن حرب عن سعيد بن جبير عن ابن عمر رضي الله','1203','1360','0');
INSERT INTO [t6227] VALUES (1757,'وهو في بيت حفصة أو قال حين خرج من بيت حفصة فقلت يا رسول الله رويدك أسألك إني أبيع الإبل بالبقيع فأبيع بالدنانير وآخذ الدراهم وأبيع بالدراهم وآخذ الدنانير فقال لا بأس أن تأخذهما بسعر يومها ما لم تفترقا وبينكما','1204','1360','0');
INSERT INTO [t6227] VALUES (1758,'فسألته عن ذلك فقال إذا بايعت الرجل بالذهب','1204','1360','0');
INSERT INTO [t6227] VALUES (1759,'فيه حكم الصرف فلم يجز العقد إلا بعد التقابض في المجلس وهذه الأحاديث وردت في بيع الإبل بالدراهم أو الدنانير في الذمة ولذلك جاز أخذ العوض عنها لاستقرارها في الذمة والحديث مشكوك في رفعه رواه جماعة غير سماك موقوفا على ابن','1205','1360','0');
INSERT INTO [t6227] VALUES (1760,'وبيع اللحم بالحيوان غير جائز وقال أبو حنيفة رحمه الله يجوز دليلنا ما روي عن قتادة عن الحسن عن سمرة أن النبي','1205','1360','0');
INSERT INTO [t6227] VALUES (1761,'نهى عن بيع اللحم بالحيوان ورواه يزيد بن مروان عن مالك عن الزهري عن سهل بن سعد عن النبي','1206','1360','0');
INSERT INTO [t6227] VALUES (1762,'نهى أن يباع حي بميت قال فسألت عن ذلك الرجل فأخبرت عنه خيرا وعن ابن أبي يحيى عن صالح مولى التوأمة عن ابن عباس رضي الله عنهما عن أبي بكر الصديق رضي الله عنه أنه كره بيع الحيوان باللحم والله أعلم','1207','1360','0');
INSERT INTO [t6227] VALUES (1763,'وإذا اشترى نخيلا مثمرة ولم تكن مؤبرة كان الثمر للمشتري بغير شرط وقال أبو حنيفة رحمه الله الثمرة','1207','1360','0');
INSERT INTO [t6227] VALUES (1764,'قال من باع نخلا قد أبرت فثمرتها للبائع إلا أن يشترط المبتاع اتفقا على صحته قال الشافعي رحمه الله فإذا جعل النبي','1208','1360','0');
INSERT INTO [t6227] VALUES (1765,'مسألة','1208','1360','0');
INSERT INTO [t6227] VALUES (1766,'نهى عن بيع الثمرة حتى يبدو صلاحها نهى البائع والمبتاع وعندهما عن أنس رضي الله عنه أن رسول الله','1208','1360','0');
INSERT INTO [t6227] VALUES (1767,'أرأيت إذا منع الله الثمرة فبم يأخذ أحدكم مال أخيه وعند البخاري عن جابر رضي الله عنه نهى رسول الله','1209','1360','0');
INSERT INTO [t6227] VALUES (1768,'عن المزابنة والمحاقلة والمخابرة وعن بيع الثمرة حتى تشقح وعندها واللفظ للبخاري عن شعبة قال عمرو أخبرني عن أبي البختري قال سألت ابن عمر رضي الله عنهما عن السلم في النخل قال نهى عن بيع النخل حتى يبدو صلاحها','1209','1360','0');
INSERT INTO [t6227] VALUES (1769,'لا تبيعوا الثمار حتى تبدو صلاحها وروي عن أنس رضي الله عنه أن رسول الله','1210','1360','0');
INSERT INTO [t6227] VALUES (1770,'مسألة','1210','1360','0');
INSERT INTO [t6227] VALUES (1771,'نهى عن بيع الغرر وعن بيع الحصاة أخرجه مسلم في الصحيح وروى الثقات عن أنس رضي الله عنه أن رسول الله','1211','1360','0');
INSERT INTO [t6227] VALUES (1772,'إنه أجاز بيع القمح في سنبله إذا أبيض فقال أما هذا فغرر لأنه يحول دونه فلا يرى فإن ثبت الخبر عن النبي','1211','1360','0');
INSERT INTO [t6227] VALUES (1773,'أجزناها كما أجازها وكان خاصا مستخرجا من عام لأن رسول الله','1211','1360','0');
INSERT INTO [t6227] VALUES (1774,'نهى عن بيع النخل حتى تزهو وعن السنبل حتى يبيض ويأمن العاهة أخرجه مسلم في الصحيح عن علي بن حجر عن ابن علية عنه ذكر السنبل في هذا الحديث مما تفرد به أيوب فقد رواه كافة أصحاب نافع عنه دونه ورواه سالم عن ابن عمر رضي الله','1212','1360','0');
INSERT INTO [t6227] VALUES (1775,'في النهي عن بيع الحب حنى يشتد وفي رواية حتى يفرك فإن كان بخفض الراء فهو كقوله حتى تشتد ويكون موافقا لرواية أيوب وإن كان يفرك بنصب الراء اقتضى تنقيته عن السنبل والأشبه أن يكون بالخفض لرواية من رواه حتى يشتد','1212','1360','0');
INSERT INTO [t6227] VALUES (1776,'مسألة','1213','1360','0');
INSERT INTO [t6227] VALUES (1777,'لو بعت من أخيك تمرا فأصابته جائحة فلا تحل لك أن تأخذ منه شيئا بم تأخذ مال أخيك بغير حق أخرجه مسلم في الصحيح وروى الشافعي رحمه الله عن سفيان عن حميد عن سليمان عن جابر رضي الله عنه أن رسول الله','1213','1360','0');
INSERT INTO [t6227] VALUES (1778,'مثله ووجه قولنا لا توضع الجائحة وتكون من مال المشتري حديث أبي سعيد رضي الله عنه قال أصيب رجل في عهد رسول الله','1214','1360','0');
INSERT INTO [t6227] VALUES (1779,'تصدقوا عليه فتصدقوا عليه فلم يبلغ ذلك وفاء دينه فقال رسول الله','1214','1360','0');
INSERT INTO [t6227] VALUES (1780,'فعالجه وأقام عليه حتى تبين له النقصان فسأل رب الحائط أن يضع فحلف ألا يفعل فذهبت أم المشتري إلى رسول الله','1215','1360','0');
INSERT INTO [t6227] VALUES (1781,'تألى أن لا يفعل خيرا فسمع بذلك رب المال فأتى إلى رسول الله','1216','1360','0');
INSERT INTO [t6227] VALUES (1782,'صوت خصوم بالباب عالية أصواتهم وإذا أحدهم يستوضع الآخر ويسترفقه في شيء وهو يقول والله لا أفعل فخرج النبي','1217','1360','0');
INSERT INTO [t6227] VALUES (1783,'مسألة','1217','1360','0');
INSERT INTO [t6227] VALUES (1784,'رخص لصاحب العرية أن يبيعها بخرصها من التمر كذا عند مسلم وعند البخاري رحمه الله رخص','1217','1360','0');
INSERT INTO [t6227] VALUES (1785,'أن تباع العرايا بخرصها تمرا وروى الزهري عن سالم عن أبيه عن رسول الله','1218','1360','0');
INSERT INTO [t6227] VALUES (1786,'أنه رخص بعد ذلك في العرية بالرطب أو التمر ولم يرخص في غير ذلك أخرجه البخاري ومسلم في الصحيح وفيه البيان الواضح أن الرخصة في بيع العرايا كانت بعد تحريم الربا ويدل على ذلك حديث سهل بن أبي خيثمة رضي الله عنه في','1218','1360','0');
INSERT INTO [t6227] VALUES (1787,'نهى أن يباع التمر بالتمر قال ذلك الربا ذلك المزابنة إلا أنه أرخص في بيع العرية النخلة والنخلتين يأخذها أهل البيت بخرصها تمرا يأكلونها رطبا وعندهما عن جابر رضي الله عنه أن رسول الله','1219','1360','0');
INSERT INTO [t6227] VALUES (1788,'رخص في العرايا ما دون خمسة أوسق أو في خمسة أوسق شك داود بن الحصين وروى عن موسى بن عقبة عن نافع عن ابن عمر عن زيد بن ثابت رضي الله عنهم أن رسول الله','1219','1360','0');
INSERT INTO [t6227] VALUES (1789,'أرخص في العرايا بخرصها تمرا قال يحيى العرية أن يشتري الرجل تمرا لنخلات لطعام أهله رطبا بخرصها تمرا والله أعلم','1220','1360','0');
INSERT INTO [t6227] VALUES (1790,'وبيع العقار قبل القبض غير جائز وقال أبو حنيفة رحمه الله إنه جائز ودليلنا من طريق الخبر ما روي عن حكيم بن حزام رضي الله عنه قال قلت يا رسول الله إني أشتري بيوعا فما يحل لي مما يحرم علي فقال إذا بعت بيعا','1220','1360','0');
INSERT INTO [t6227] VALUES (1791,'لعتاب بن أسيد إني بعثتك إلى أهل الله وأهل مكة فانههم عن بيع ما لم يقبضوا أو ربح ما لم يضمنوا وعن قرض وبيع وعن شرطين في بيع وعن بيع وسلف تفرد به يحيى بن صالح وروى الشافعي عن سفيان عن عمرو بن دينار عن طاوس عن ابن','1221','1360','0');
INSERT INTO [t6227] VALUES (1792,'مسألة','1221','1360','0');
INSERT INTO [t6227] VALUES (1793,'قال من ابتاع طعاما فلا يبيعه حتى يستوفيه أخرجه البخاري ومسلم في الصحيح وعند مسلم عنه قال كنا في زمان رسول الله','1222','1360','0');
INSERT INTO [t6227] VALUES (1794,'إذا اشتروا الطعام جزافا أن يبيعوه في مكانه حتى يحولوه وعند أبي داود عن طاوس عن ابن عباس رضي الله عنهما قال قال رسول الله','1222','1360','0');
INSERT INTO [t6227] VALUES (1795,'عن بيع','1222','1360','0');
INSERT INTO [t6227] VALUES (1796,'يقول إذا ابتعت طعاما فلا تبيعه حتى تستوفيه والله أعلم','1223','1360','0');
INSERT INTO [t6227] VALUES (1797,'ومن اشترى شاة فوجدها مصراة كان له ردها بعد ما حلبها وبرد معها صاعا من تمر وقال أبو حنيفة رحمه الله يرجع بأرش عيب التصرية ولا يرد لنا حديث أبي هريرة رضي الله عنه أن رسول الله','1223','1360','0');
INSERT INTO [t6227] VALUES (1798,'عن تلقي الركبان أو كما قال أخرجه البخاري في الصحيح وقد روي مرفوعا والصحيح أنه هكذا وروي في ذلك أحاديث وفي بعضها قال ردها معها مثل أو مثلي لبنها قمحا ومن روى التمر أكثر وأحفظ فهو أولى على أن من أصحابنا','1224','1360','0');
INSERT INTO [t6227] VALUES (1799,'وإن اشترى ماشية فنتجت في يده أو أشجارا فأثمرت ثم ظهر منها على عيب فله أن يردها بالعيب ويكون النتاج والثمرة له وقال أبو حنيفة رحمه الله له أرش العيب وليس له الرد','1224','1360','0');
INSERT INTO [t6227] VALUES (1800,'قضى أن الخراج بالضمان قال الشافعي رحمه الله أخبرني من لا أتهم عن ابن أبي ذئب قال أخبرني مخلد بن خفاف قال ابتعت غلاما فاستعملته ثم ظهرت منه على عيب فخاصمت فيه إلى عمر بن عبد العزيز','1225','1360','0');
INSERT INTO [t6227] VALUES (1801,'قضى في مثل هذا أن الخراج بالضمان فعجلت إلى عمر فأخبرته ما أخبرني عروة عن عائشة عن رسول الله','1226','1360','0');
INSERT INTO [t6227] VALUES (1802,'فأرد قضاء عمر أنفذ سنة رسول الله','1226','1360','0');
INSERT INTO [t6227] VALUES (1803,'قال الخراج بالضمان وروي عن مسلم بن خالد عن هشام عن أبيه أن رجلا اشترى من رجل غلاما في زمان النبي','1226','1360','0');
INSERT INTO [t6227] VALUES (1804,'الخراج','1226','1360','0');
INSERT INTO [t6227] VALUES (1805,'الغلة بالضمان قال أبو عبد الله هذا حديث صحيح الإسناد قال الدارمي قلت ليحيى بن معين فالزنجي يعني مسلم بن خالد فقال ثقة وقد رواه أبو عيسى الترمذي عن أبي سلمة يحيى بن خلف البصري عن عمر بن علي المقدمي عن','1227','1360','0');
INSERT INTO [t6227] VALUES (1806,'قضى أن الخراج بالضمان','1227','1360','0');
INSERT INTO [t6227] VALUES (1807,'مسألة','1228','1360','0');
INSERT INTO [t6227] VALUES (1808,'أنه قال الخراج بالضمان ويخالف في البكر بالإجماع وبما روينا عن علي رضي الله عنه وقول علي رضي الله عنه عندنا هو في البكر لئلا يكون مخالفا لما روينا من السنة وافتضاض البكر نقصان أدخله فيما اشتراه فيمنعه ذلك من','1230','1360','0');
INSERT INTO [t6227] VALUES (1809,'ويملك العبد بالتمليك على أحد القولين وقال أبو حنيفة رحمه الله إنه لا يملك وهو ظاهر المذهب في صحيح مسلم عن ابن عمر رضي الله عنهما أن','1230','1360','0');
INSERT INTO [t6227] VALUES (1810,'قال من باع عبدا وله مال فماله للبائع إلا أن يشترط المبتاع وفي حديث سلمان رضي الله عنه أنه جاءه','1231','1360','0');
INSERT INTO [t6227] VALUES (1811,'مسألة','1231','1360','0');
INSERT INTO [t6227] VALUES (1812,'يقول المسلم أخو المسلم ولا يحل لمسلم إن باع من أخيه بيعا فيه عيب أن لا يبينه له قال أبو عبد الله هذا حديث صحيح وروي عن وائلة بن الأسقع رضي الله عنه قال سمعت رسول الله','1233','1360','0');
INSERT INTO [t6227] VALUES (1813,'مسألة','1233','1360','0');
INSERT INTO [t6227] VALUES (1814,'إلا أن يتوب فقيل له أثبت هذا الحديث عن عائشة فقال أبو إسحاق رواه عن امرأته قيل فتعرف امرأته بشيء يثبت به حديثها فما علمته قال شيئا فقلت له ترد حديث بسرة بنت صفوان مهاجرة معروفة بالفضل أن تقول حديث امرأة وتحتج بحديث','1234','1360','0');
INSERT INTO [t6227] VALUES (1815,'مسألة','1235','1360','0');
INSERT INTO [t6227] VALUES (1816,'قضى باليمين على المدعى عليه اتفقا على صحته وروي عن أبي العميس عن عبد الرحمن بن قيس بن محمد بن الأشعث بن قيس عن أبيه عن جده قال اشترى','1236','1360','0');
INSERT INTO [t6227] VALUES (1817,'يقول إذا اختلف البيعان وليس بينهما بينة فهو ما يقول رب السلعة أو يتتاركان أخرجه أبو داود في كتاب السنن وقال أبو عبد الله هذا حديث صحيح الأسناد وروى الشافعي عن سعيد بن سالم عن ابن جريج أن إسماعيل بن أمية','1237','1360','0');
INSERT INTO [t6227] VALUES (1818,'في مثل هذا فأمر للبائع أن يستخلف ثم يخير المبتاع إن شاء أخذ وإن شاء ترك رواه أحمد بن حنبل عنه حديث أبي عبيدة هذا إن كان سعيد بن سالم حفظ عبد الملك بن عمير في إسناده فهو أيضا جيد لا بأس به إلا أنه مرسل أبو عبيدة لم','1238','1360','0');
INSERT INTO [t6227] VALUES (1819,'إذا اختلف البيعان ولم يكن بينهما بينة فهو بما يقول رب السلعة أو يتتاركان وكذلك رواه معن بن عبد الرحمن المسعودي عن أخيه القاسم وهو مرسل القاسم بن عبد الرحمن لم يدرك جده وقد رواه إسماعيل بن عياش عن عقبة عن محمد بن عبد','1239','1360','0');
INSERT INTO [t6227] VALUES (1820,'قال وهب قال سمعت رسول الله','1240','1360','0');
INSERT INTO [t6227] VALUES (1821,'إذا اختلف البيعان فالقول ما قال البائع فإذا استهلك فالقول قول ما قال المشتري وإسماعيل بن عياش وابن أبي ليلى والحسن بن عمارة كلهم ضعفاء قد سبق ذكرهم والله أعلم','1240','1360','0');
INSERT INTO [t6227] VALUES (1822,'وإذا اشترى عبدا بشرط أن يعتقه صح الشراء على أحد القولين وقال أبو حنيفة رحمه الله لا يصح ودليلنا من طريق الخبر حديث بريرة رضي الله عنها في الصحيحين','1240','1360','0');
INSERT INTO [t6227] VALUES (1823,'اشترطي لهم الولاء معناه اشترطي عليهم الولاء قال الله عز وجل أولئك لهم اللعنة يعني عليه اللعنة واستقصينا هذه المسألة في الولاء والعتق والله أعلم','1241','1360','0');
INSERT INTO [t6227] VALUES (1824,'ولا يجوز بيع مال الغير دون إذنه موقوفا على إجازته وقال أبو حنيفة رحمه الله إنه يجوز ووافقنا في الشراء للغير أنه لا يجوز بغير إذنه ودليلنا من طريق الخبر حديث أبي هريرة رضي الله عنه أن النبي','1241','1360','0');
INSERT INTO [t6227] VALUES (1825,'من ابتاع طعاما فلا يبيعه حتى يستوفيه قال ابن عباس رضي الله عنهما وأحسب أن كل شيء مثله اتفقا على صحته','1241','1360','0');
INSERT INTO [t6227] VALUES (1826,'أنه لما بعث عتاب بن أسيد إلى أهل مكة قال أخبرهم أنه لا يجوز بيعان في بيع ولا تبع ما لم تملك ولا سلف وبيع ولا شرطين في بيع وأما الحديث الذي يروي عن عروة البارقي أن النبي','1242','1360','0');
INSERT INTO [t6227] VALUES (1827,'بشاة ودينار فدعا النبي','1242','1360','0');
INSERT INTO [t6227] VALUES (1828,'يقول الخير معقود بنواصي الخيل إلى يوم القيامة والحديث الذي روي عن أبي الحصين عن شيخ من أهل المدينة عن حكيم بن حزام أن رسول الله','1243','1360','0');
INSERT INTO [t6227] VALUES (1829,'فتصدق به النبي','1243','1360','0');
INSERT INTO [t6227] VALUES (1830,'مسألة','1243','1360','0');
INSERT INTO [t6227] VALUES (1831,'إبل قال أبو رافع فأمرني النبي','1244','1360','0');
INSERT INTO [t6227] VALUES (1832,'فقال أعطه إياه فإن خير عباد الله أحسنهم قضاء مخرج في صحيح مسلم وعن أبي هريرة رضي الله عنه قال كان لرجل على النبي','1244','1360','0');
INSERT INTO [t6227] VALUES (1833,'إن خياركم أحسنكم قضاء اتفقا على صحته وعنه أن رجلا تقاضى رسول الله','1244','1360','0');
INSERT INTO [t6227] VALUES (1834,'دعوه فإن لصاحب الحق مقالا فاشتروا له بعيرا فأعطوه إياه قالوا لا نجد إلا أفضل من','1244','1360','0');
INSERT INTO [t6227] VALUES (1835,'مسألة','1245','1360','0');
INSERT INTO [t6227] VALUES (1836,'عام الفتح جاءه العباس بن عبد المطلب بأبي سفيان ابن حرب فأسلم بمر الظهران فقال له العباس يا رسول الله إن أبا سفيان رجل يحب هذا الفخر فلو جعلت له شيئا قال نعم من دخل دار أبي سفيان فهو آمن ومن أغلق بابه فهو آمن فنسب','1248','1360','0');
INSERT INTO [t6227] VALUES (1837,'مسألة','1249','1360','0');
INSERT INTO [t6227] VALUES (1838,'قال ذاكرت رسول الله','1250','1360','0');
INSERT INTO [t6227] VALUES (1839,'جالسا عند الركن فذكر الحديث ذكرناه في غير موضع في السنن وكذلك قول عمر رضي الله عنه لا تحل التجارة في شيء لا يحل أكله وشربه والله أعلم','1250','1360','0');
INSERT INTO [t6227] VALUES (1840,'ولا يجوز بيع الكلب ولا تجب قيمته على قاتله وقال','1250','1360','0');
INSERT INTO [t6227] VALUES (1841,'نهى عن ثمن الكلب ومهر البغي وحلوان الكاهن اتفقا على صحته وعن عون بن أبي جحيفة قال اشترى أبي عبدا حجاما فأمر بمحاجمه فكسرت وقال إن رسول الله','1251','1360','0');
INSERT INTO [t6227] VALUES (1842,'كسب الحجام خبيث وكسب البغي خبيث وثمن الكلب خبيث أخرجه مسلم في الصحيح','1251','1360','0');
INSERT INTO [t6227] VALUES (1843,'عن ثمن الكلب وإن جاء يطلب ثمن الكلب فاملأ كفه ترابا وعنده عن أبي هريرة رضي الله عنه قال قال رسول الله','1252','1360','0');
INSERT INTO [t6227] VALUES (1844,'عن ثمن الكلب والسنور وفي الصحيحين عن ابن عباس عن عمر رضي الله عنهم أن رسول الله','1252','1360','0');
INSERT INTO [t6227] VALUES (1845,'جالسا عند الركن قال فرفع بصره إلى السماء فضحك فقال لعن الله اليهود ثلاثا إن الله حرم عليهم الشحوم فباعوها وأكلوا أثمانها وإن الله عز وجل إذا حرم على قوم أكل شيء حرم عليهم ثمنه','1252','1360','0');
INSERT INTO [t6227] VALUES (1846,'عن ثمن الكلب والهر إلا الكلب المعلم ورواه عبد الله بن موسى عن حماد بالشك في رفعه ورواه سويد بن عمرو عن حماد موقوفا على جابر رضي الله عنه وهو الصواب وفي الجملة لا يعارض بهذه الرواية ما روينا من الأحاديث الصحيحة','1253','1360','0');
INSERT INTO [t6227] VALUES (1847,'ثلاث كلهن سحت كسب الحجام سحت ومهر الزانية سحت وثمن الكلب سحت إلا كلبا ضاريا وتابعه الوليد بن عبد الله بن أبي رباح عن عمه عطاء وهما ضعيفان وروي عن حماد بن سلمة عن قيس عن عطاء عن أبي هريرة رضي الله عنه نهى عن','1254','1360','0');
INSERT INTO [t6227] VALUES (1848,'مسألة','1256','1360','0');
INSERT INTO [t6227] VALUES (1849,'المدينة وهم يسلفون في التمر السنة والسنتين والثلاث فقال رسول الله','1256','1360','0');
INSERT INTO [t6227] VALUES (1850,'المدينة وهم يسلفون في الثمار السنتين والثلاث فقال رسول الله أسلموا في الثمار في كيل معلوم إلى أجل معلوم أخرجه البخاري في الصحيح وعن محمد بن أبي مجالد قال اختلف أبو برزة وعبد الله بن شداد في السلم فأرسلوني إلى','1257','1360','0');
INSERT INTO [t6227] VALUES (1851,'في البر والشعير والزبيب والتمر إلى قوم ما هو عندهم قال وسألنا ابن أبزى فقال مثل ذلك أخرجه البخاري في الصحيح استدلوا بحديث حكيم بن حزام لا تبع ما ليس عندك وإنما ورد ذلك في بيع الأعيان وقد أخرجته في هذا','1258','1360','0');
INSERT INTO [t6227] VALUES (1852,'بيع الأعيان والله أعلم وعبر بالسلف أو السلم عن البيع وبيع عين الثمرة قبل ظهورها لا تجوز والسلم المتعلق بالعين لا يجوز بالإجماع والله أعلم وعن أبي البختري قال سألت ابن عمر رضي الله عنهما عن السلم في النخل فقال','1259','1360','0');
INSERT INTO [t6227] VALUES (1853,'ويصح السلم حالا كما يصح مؤجلا وقال أبو حنيفة رحمه الله إنه لا يصح روي عن عائشة رضي الله عنها أن النبي','1259','1360','0');
INSERT INTO [t6227] VALUES (1854,'تمره فدفعه إليه قال أبو عبد الله هذا حديث صحيح وروى هذا الحديث الإمام أبو سهل محمد بن سليمان الصعلوكي بإسناده عنها قالت ابتاع رسول الله','1260','1360','0');
INSERT INTO [t6227] VALUES (1855,'إلى منزله فالتمس التمر فلم يجده فقال للأعرابي لتفقه يا عبد الله إنا ابتعنا منك جزورا بوسق من تمر الذخيرة فالتمسناه فلم نجده قال الأعرابي واغدراه قالت فوجزه الناس وقالوا تقول هذا لرسول الله','1260','1360','0');
INSERT INTO [t6227] VALUES (1856,'دعوه فإن لصاحب الحق مقالا قالت فلما لم يفهم عنه أرسل إلى خولة بنت حكيم أن أقرضينا من تمر الذخيرة حتى يكون عندنا فنقضيك فقالت نعم فأرسل رسولا يأخذه وقال للأعرابي انطلق معه حتى يؤتيك فانطلق الأعرابي فأخذ التمر ثم','1260','1360','0');
INSERT INTO [t6227] VALUES (1857,'أولئك خيار عباد الله يوم القيامة الموفون الطيبون قال الأستاذ أبو سهل رحمه الله هذا الحديث يجمع فنونا من الأحكام وعيونا من آداب الإسلام منها جواز ابتياع الشريف من المشروف والمتبوع من التابع ومنها حسن التجارة','1261','1360','0');
INSERT INTO [t6227] VALUES (1858,'ورد المدينة وهم يسلفون إلى آجال متفاوتة وأوقات غير معلومة فنهاهم النبي','1261','1360','0');
INSERT INTO [t6227] VALUES (1859,'في كيل معلوم قصدا منه إلى أن يكون المقدار والمخبر عن الكمية معلوما لا على وجه إفراد الكيل بالجواز دون الوزن وكل مكيل بالوزن جائز اتفاقا وإجماعا لأن الوزن أحصر وبالكمية أخبر فلهذا أجاز جوازه كذلك الحلول فإن قيل','1262','1360','0');
INSERT INTO [t6227] VALUES (1860,'الأعرابي إلى منزله للوفاء ومنها الرئيس الكريم الكبير والجليل الخطير يلتمس في منزله ما خلفه فيه فإن لم يجده لم يعقبه بعتب ألا ترى إلى عائشة رضي الله عنها تقول فالتمس التمر فلم يجده ومنها أن لا عتب على الإنسان','1263','1360','0');
INSERT INTO [t6227] VALUES (1861,'إنما أنا بشر أغضب كما تغضبون وكما قال','1263','1360','0');
INSERT INTO [t6227] VALUES (1862,'فقال ما كان من أمر دينكم فإلى وما كان من أمر دنياكم فإليكم أو كما قاله ومنها أنه','1263','1360','0');
INSERT INTO [t6227] VALUES (1863,'ومنها أن المبتاع يعرض لبائعه بالاستقالة والإقالة إذا لم يجد قضاء وهو قوله','1264','1360','0');
INSERT INTO [t6227] VALUES (1864,'فإن لصاحب الحق مقالا وهو الذي قال له','1265','1360','0');
INSERT INTO [t6227] VALUES (1865,'أنه قال أد إلى من ائتمنك ولا تخن من خانك إذ معنى هذا القول ألا يقابل الأخذ بمثله بعد وصوله إلى حقه وتناول ماله من تحت يده فيده في العدوى عليه والتزامه للخروج من حقه إذ اليد يعبر بها عن القدرة والقوة والبسط والجارحة','1265','1360','0');
INSERT INTO [t6227] VALUES (1866,'لتفقه يا عبد الله هذا يدل على حسن التكرار وصواب التقدير ومحل التحرير بترديد الكلام وإعادة النظام لتفاوت الناس في الأفهام فمن بين يدرك اللحظة من اللفظة من بين قوم لا يعرفون لا إلا ب لا سمعت الشيخ أبا الفضل محمد','1266','1360','0');
INSERT INTO [t6227] VALUES (1867,'قال لهم دعوه فلما عاد إلى قوله عادوا إلى زجره ولم يكن النهي الأول ناهيا عن هذه الحالة لأنه لو نهاهم عنها لكانوا عصاة بارتكاب النهي وحاشاهم أن يكونوا بهذه الصفة ولو كانوا للحقتهم الموعظة والمزجرة قال البيهقي رحمه','1266','1360','0');
INSERT INTO [t6227] VALUES (1868,'المدينة وهم يسلفون السنتين والثلاث فقال رسول الله','1267','1360','0');
INSERT INTO [t6227] VALUES (1869,'مسألة','1267','1360','0');
INSERT INTO [t6227] VALUES (1870,'أن تباشر المرأة المرأة في ثوب واحد لأجل أن تصفها لزوجها حتى كأنه ينظر إليها ونهى إذا كنا ثلاثة أن يتناجى اثنان دون واحد أجل أن يحزنه حتى يختلط بالناس وفيه دليل على أن الحيوان يضبط بالصفة خلاف ما زعم أبو حنيفة رحمه الله من أنه','1268','1360','0');
INSERT INTO [t6227] VALUES (1871,'ذكر ما اختلف فيه الشافعي وأبو حنيفة رضي الله عنهما من كتاب الرهن والتفليس والحجر والصلح والحوالة والضمان والشركة والوكالة والإقرار والعارية والغصب والمساقاة وإحياء الموات والوقف والهبة واللقطة مما ورد فيه','1272','1360','0');
INSERT INTO [t6227] VALUES (1872,'مسألة','1272','1360','0');
INSERT INTO [t6227] VALUES (1873,'عن الخمر تتخذ خلا قال لا','1272','1360','0');
INSERT INTO [t6227] VALUES (1874,'فقال ما فعلت شاتكم فقلت ماتت قال أفلا انتفعتم بإهابها قلت إنها ميتة قال فإن دباغها يحل كما يحل الخل الخمر قال أبو عبد الله الحاكم تفرد به الفرج بن فضالة عن يحيى والفرج ممن لا يحتج بحديثه ولم يصح تحليل خل الخمر','1273','1360','0');
INSERT INTO [t6227] VALUES (1875,'ما أفقر بيت من أدم فيه خل وخير خلكم خل خمركم','1274','1360','0');
INSERT INTO [t6227] VALUES (1876,'في النهي عن تخليل الخمر ولم يزل أهل حرم رسول الله','1275','1360','0');
INSERT INTO [t6227] VALUES (1877,'قال ثم قدمت بعد موت مالك فذكرت لهم فلم ينكر علي وروي عن أم حراش قالت أتيت عليا رضي الله عنه يصطبغ في خل خمر هذا وإن ثبت فهم يسمون الخل المتخذ من العنب خل خمر وذلك شائع بينهم وروي عن مسربل العبدي عن أمه عن','1275','1360','0');
INSERT INTO [t6227] VALUES (1878,'وزيادات الرهن المنفصلة الحادثة بعد عقد الرهن تخلص','1275','1360','0');
INSERT INTO [t6227] VALUES (1879,'لا يغلق الرهن له غنمه وعليه غرمه قال علي بن عمر هذا إسناد حسن متصل وقال أبو عبد الله الحاكم هذا حديث صحيح وعن أبي هريرة رضي الله عنه قال قال رسول الله','1276','1360','0');
INSERT INTO [t6227] VALUES (1880,'قال لبن الدر يحلب بنفقته إذا كان مرهونا والظهر يركب إذا كان مرهونا وعلى الذي يركب ويحلب النفقة كذلك رواه ابن المبارك ويحيى القطان عن زكريا بن أبي زائدة عن الشعبي عنه ورواه هشيم وسفيان بن حبيب عن زكريا وزاد في','1276','1360','0');
INSERT INTO [t6227] VALUES (1881,'قال الرهن مركوب ومحلوب قال أبو عبد الله هذا إسناد صحيح كذا رواه أبو معاوية وأبو عوانة عن الأعمش عن أبي صالح عن أبي هريرة رضي الله عنه ورواه وكيع عنه فوقفه على أبي هريرة رضي الله عنه احتجوا بما روي عن عمرو بن','1278','1360','0');
INSERT INTO [t6227] VALUES (1882,'والرهن أمانة في يد المرتهن إذا تلف لم يكن عليه ضمان وقال أبو حنيفة رحمه الله إنه مضمون بالدين بقدر قيمته قال الشافعي رحمه الله أخبرنا محمد بن إسماعيل بن أبي فديك عن ابن أبي ذئب عن ابن شهاب عن سعيد بن المسيب أن رسول','1278','1360','0');
INSERT INTO [t6227] VALUES (1883,'مثله أو مثل معناه وقد روينا من حديث زياد بن سعد عن الزهري متصلا وهذا حديث حسن وروي عن مالك مرفوعا متصلا والمحفوظ عنه ما في الموطأ','1279','1360','0');
INSERT INTO [t6227] VALUES (1884,'الرهن بما فيه قال أبو حازم الحافظ تفرد به حسان بن إبراهيم الكرماني قال البيهقي رحمه الله وهو منقطع بين عمرو بن دينار وأبي هريرة واستدلوا بما روي عن حميد عن أنس رضي الله عنه مرفوعا بمثله قال','1280','1360','0');
INSERT INTO [t6227] VALUES (1885,'للمرتهن ذهب حقك هكذا رواه ابن المبارك عنه مرة وقيل عنه عن عطاء عن الحسن عن النبي','1281','1360','0');
INSERT INTO [t6227] VALUES (1886,'فيما يكون مثبتا عنده وقيل عن عطاء يترادان الفضل وكل ذلك يخالف ما روي عنه في حديث الفرس وقد يكون الفرس أقل قيمة من الحق وأكثر منه ومثله ولم يرو أنه سأل عن قيمة الفرس وروي في هذه القصة أنه قال الرهن بما فيه وهم','1281','1360','0');
INSERT INTO [t6227] VALUES (1887,'مسألة','1282','1360','0');
INSERT INTO [t6227] VALUES (1888,'من أدرك ماله بعينه عند رجل قد أفلس أو','1282','1360','0');
INSERT INTO [t6227] VALUES (1889,'إذا أفلس الرجل فوجد غريمه متاعه بعينه فهو أحق به من سائر الغرماء وفي أخرى عنده أيضا عنه عن النبي','1283','1360','0');
INSERT INTO [t6227] VALUES (1890,'قال إذا أفلس الرجل فوجد الرجل عنده سلعته بعينها فهو أحق بها وروي عن أبي المعتمر عن عمرو بن خلدة الزرقي وكان قاضي المدينة حينا قال جئنا أبا هريرة رضي الله عنه في صاحب لنا قد أفلس فقال هذا الذي قضى فيه رسول الله','1283','1360','0');
INSERT INTO [t6227] VALUES (1891,'خالف عثمان وعليا رضي الله عنهما في ذلك وروى إسماعيل بن عياش عن ابن عقبة عن الزهري عن أبي بكر عن أبي هريرة رضي الله عنه عن رسول الله','1284','1360','0');
INSERT INTO [t6227] VALUES (1892,'لا تنكح المرأة على عمتها ولا خالتها فأخذنا جميعا به ولم يثبت عن النبي','1285','1360','0');
INSERT INTO [t6227] VALUES (1893,'مسألة','1285','1360','0');
INSERT INTO [t6227] VALUES (1894,'على معاذ بن جبل رضي الله عنه ماله وباعه في دين كان عليه وفي رواية قال كان معاذ بن جبل رضي الله عنه شابا حليما سمحا من أفضل شباب قومه ولم يكن يمسك شيئا فلم يزل يدان حتى أغرقه ماله كله في الدين فأتى النبي','1286','1360','0');
INSERT INTO [t6227] VALUES (1895,'فباع لهم رسول الله','1286','1360','0');
INSERT INTO [t6227] VALUES (1896,'حتى باع فيه غنيمة له وروى مالك عن عمر بن عبد الرحمن بن دلاف عن أبيه أن رجلا من جهينة كان يشتري الرواحل فيغال بها ثم يسرع السير فيسبق الحاج فأفلس فرفع أمره إلى عمر بن الخطاب رضي الله عنه فقال أما بعد أيها','1287','1360','0');
INSERT INTO [t6227] VALUES (1897,'يجوز الحجر على البالغ العاقل بالسفه وبالدين مع التفليس','1287','1360','0');
INSERT INTO [t6227] VALUES (1898,'إن الله تبارك وتعالى حرم عليكم عقوق الأمهات ووأد البنات ومنعا وهات وكره لكم قيل وقال وكثرة السؤال وإضاعة المال اتفقا على صحته وعن كعب بن مالك أن رسول الله','1289','1360','0');
INSERT INTO [t6227] VALUES (1899,'مسألة','1290','1360','0');
INSERT INTO [t6227] VALUES (1900,'عرضه يوم أحد للقتال قال وأنا ابن أربع عشرة سنة فلم يجزني ثم قال عرضني يوم الخندق وأنا ابن خمس عشرة سنة فأجازني قال نافع فقدمت على عمر بن عبد العزيز وهو إذ ذاك خليفة فحدثته هذا الحديث فقال إن هذا الحد بين الصغير','1290','1360','0');
INSERT INTO [t6227] VALUES (1901,'مسألة','1292','1360','0');
INSERT INTO [t6227] VALUES (1902,'فجاء على حمار فقال رسول الله','1293','1360','0');
INSERT INTO [t6227] VALUES (1903,'مسألة','1293','1360','0');
INSERT INTO [t6227] VALUES (1904,'الصلح بين المسلمين جائز إلا صلحا حرم حلالا أو أحل حراما وقال','1294','1360','0');
INSERT INTO [t6227] VALUES (1905,'كل معروف صدقة وما أنفق الرجل على أهله ونفسه كتب له صدقة وما وقى به المرء عرضه كتب له به صدقه وما أنفق المؤمن من نفقة فإن خلفها على الله والله ضامن إلا ما كان في بنيان أو معصية فقلت لمحمد بن المنكدر وما وقى به الرجل عرضه','1295','1360','0');
INSERT INTO [t6227] VALUES (1906,'مسألة','1296','1360','0');
INSERT INTO [t6227] VALUES (1907,'قال البينة على المدعي رواه الشافعي عن مسلم بن خالد عنه وعن أبي هريرة رضي الله عنه أن رسول الله','1296','1360','0');
INSERT INTO [t6227] VALUES (1908,'في شيء ليس لواحد منهما بينة فقضى به رسول الله','1297','1360','0');
INSERT INTO [t6227] VALUES (1909,'في حظار بينهم فأرسل حذيفة رضي الله عنه يقضي بينهم فقضى للذي وجد معاقد القمط تليه ثم رجع فأخبر النبي','1297','1360','0');
INSERT INTO [t6227] VALUES (1910,'أصبت أو قال أحسنت قال على بن عمر لم يروه غير دهثم وهو ضعيف سئل يحيى بن معين عن حديثه فقال دهثم كان كوفيا لا يكتب حديثه','1297','1360','0');
INSERT INTO [t6227] VALUES (1911,'مسألة','1298','1360','0');
INSERT INTO [t6227] VALUES (1912,'قال مطل الغني ظلم وإذا أتبع أحدكم على مليء فليتبع اتفقا على صحته','1298','1360','0');
INSERT INTO [t6227] VALUES (1913,'مسألة','1300','1360','0');
INSERT INTO [t6227] VALUES (1914,'بجنازة رجل من الأنصار ليصلي عليها فقال هل عليه دين قالوا لا قال هل ترك شيئا قالوا لا فصلى عليه وأتي بجنازة فقال هل عليه دين قالوا نعم قال هل ترك شيئا قالوا لا قال صلوا على صاحبكم قال أبو قتادة','1300','1360','0');
INSERT INTO [t6227] VALUES (1915,'أما إني لم أنوه باسمك إلا لخير إن فلانا لرجل منهم مأسور بدينه فلو رأيت أهله ومن يتحرون بأمره قاموا فقضوا عنه وفي هذين الحديثين بيان جواز إنشاء الضمان على الميت بعد موته وإن لم يضمنه الضامن عنه في حال حياته بخلاف مذهب','1301','1360','0');
INSERT INTO [t6227] VALUES (1916,'شركة المفاوضة باطلة وقال أبو حنيفة رحمه الله إنها جائزة ودليلنا من طريق الخبر ما في حديث بريرة المتفق على صحته عن عائشة رضي الله عنها قالت ثم قام رسول الله','1301','1360','0');
INSERT INTO [t6227] VALUES (1917,'وفيه قال رسول الله','1302','1360','0');
INSERT INTO [t6227] VALUES (1918,'ولا تزر وازرة وزر أخرى وهم في شركة المفاوضة يؤاخذون الشريك بجناية شريكه احتجوا بما روي عن أبي عبيدة عن عبد الله قال اشتركت أنا وعمار وسعد فيما نصيب يوم بدر قال فجاء سعد بأسيرين ولم أجئ أنا وعمار بشيء هذا','1302','1360','0');
INSERT INTO [t6227] VALUES (1919,'والكفالة ببدن من عليه المال صحيحة في أحد القولين وهو قول أبي حنيفة رحمه الله وباطلة في القول الآخر','1302','1360','0');
INSERT INTO [t6227] VALUES (1920,'مسألة','1303','1360','0');
INSERT INTO [t6227] VALUES (1921,'أي العمل أفضل فقال إيمان بالله وجهاد في سبيله قلت أي الرقاب أفضل قال أغلاها ثمنا وأنفسها عند أهلها قال قلت فإن لم أفعل قال تعين صانعا أو تصنع لأخرق قال قلت فإن لم أفعل قال تدع الناس من الشر فإنها','1304','1360','0');
INSERT INTO [t6227] VALUES (1922,'الكبر فتكلما في أمر صاحبهما وذكر الحديث اتفقا على صحته وروي عن محمد بن إسحاق عن جهم بن أبي جهم عن عبد الله بن جعفر قال كان علي بن أبي طالب رضي الله عنه يكره الخصومة وكان إذا كانت الخصومة وكل فيها عقيل بن أبي طالب','1305','1360','0');
INSERT INTO [t6227] VALUES (1923,'مسألة','1306','1360','0');
INSERT INTO [t6227] VALUES (1924,'إياكم والظن فإن الظن أكذب الحديث ولا تجسسوا ولا تحسسوا ولا تناجشوا ولا تحاسدوا ولا تباغضوا ولا تدابروا وكونوا عبدا الله إخوانا اتفقا على صحته عن أبي هريرة رضي الله عنه واستدلوا بما روى نوح بن دراج عن جعفر بن','1306','1360','0');
INSERT INTO [t6227] VALUES (1925,'مسألة','1307','1360','0');
INSERT INTO [t6227] VALUES (1926,'في ابن أمة زمعة فقال سعد يا رسول الله أوصاني أخي إذا قدمت مكة أن أنظر إلى ابن أمة زمعة فاقبضه فإنه ابني فقال عبد بن زمعة أخي وابن أمة أبي ولد على فراش أبي فرأى شبها بينا بعتبة فقال هو لك يا عبد بن زمعة','1307','1360','0');
INSERT INTO [t6227] VALUES (1927,'هو لك وهو أخوك يا عبد بن زمعة وفيه ثم قال واحتجبي منه يا سودة لما رأى من شبه عتبة بن أبي وقاص أخرجه البخاري في الصحيح فقال وقال ليث حدثني يونس فذكر معناه فذكر هذه اللفظة استدلوا بما روى جرير عن منصور عن','1308','1360','0');
INSERT INTO [t6227] VALUES (1928,'مسألة','1309','1360','0');
INSERT INTO [t6227] VALUES (1929,'قال على اليد ما أخذت حتى تؤدي ثم إن','1309','1360','0');
INSERT INTO [t6227] VALUES (1930,'استعار منه أدراعا يوم حنين فقال أغصب يا محمد قال لا بل عارية مضمونة وكذا رواه جعفر بن محمد عن أبيه أن صفوان بن أمية أعار ورواه عاصم بن عمر بن قتادة عن عبد الرحمن بن جابر عن أبيه رضي الله عنه عن النبي','1310','1360','0');
INSERT INTO [t6227] VALUES (1931,'قال يا صفوان هل عندك سلاح قال عارية أم غصبا قل لا بل عارية فأعاره بين الثلاثين إلى الأربعين درعا وغزا رسول الله','1311','1360','0');
INSERT INTO [t6227] VALUES (1932,'لصفوان إنا قد فقدنا من أدراعك أدراعا فهل نغرم لك قال لا يا رسول الله لأن في قلبي اليوم ما لم يك يومئذ ورواه أبو داود أيضا عن مسدد عن أبي الأحوص عن ابن رفيع عن عطاء عن أناس من آل صفوان فذكره بمعناه','1311','1360','0');
INSERT INTO [t6227] VALUES (1933,'استعار قصعة فضاعت فأمرهم النبي','1312','1360','0');
INSERT INTO [t6227] VALUES (1934,'استعار قصعة فضاعت فضمنها لهم سويد بن عبد العزيز ربما يهم في الشيء وما سبق أصح والصحيح رواية يحيى ين سعيد وابن عليه عن حميد أخرجها البخاري رحمه الله وفيها بيان أن القصة في غير العارية وروي ضمان العارية عن ابن عباس','1312','1360','0');
INSERT INTO [t6227] VALUES (1935,'قال ليس على المستعبر غير المغل ضمان ولا على المستودع غير المغل ضمان قال علي بن عمر عمرو وعبيدة ضعيفان وإنما يروي ذلك عن شريح القاضي غير مرفوع والله أعلم','1313','1360','0');
INSERT INTO [t6227] VALUES (1936,'والقطع والضمان يجتمعان وقال العراقيون إذا قطع السارق لم يضمن وإذا ضمن أولا لم يقطع قال الله عز وجل والسارق والسارقة فاقطعوا أيديهما جزاء بما كسبا نكالا من الله والله عزيز حكيم وقال الله عز وجل فمن','1313','1360','0');
INSERT INTO [t6227] VALUES (1937,'فقال يا أسامة تشفع في حد من حدود الله ثم قام النبي','1314','1360','0');
INSERT INTO [t6227] VALUES (1938,'ولكنهم لما لم يجدوا لها عنه محيصا بتشفيع أسامة بن زيد فلم ينفعهم ذلك أيضا وروى الشافعي عن مالك عن ابن شهاب عن صفوان بن عبد الله أن صفوان بن أمية رضي الله عنه قيل له من لم يهاجر هلك فقدم صفوان المدينة فنام في','1314','1360','0');
INSERT INTO [t6227] VALUES (1939,'بقطع يده فقال صفوان إني لم أرد هذا هو عليه صدقة فقال رسول الله','1314','1360','0');
INSERT INTO [t6227] VALUES (1940,'مثل حديث مالك','1314','1360','0');
INSERT INTO [t6227] VALUES (1941,'يقطع اليد في ربع دينار فصاعدا وروى الشافعي عن مالك عن هشام بن عروة عن أبيه عن يحيى بن عبد الرحمن بن حاطب أن رقيقا لحاطب سرقوا ناقة لرجل من مزينة وانتحروها فرفع ذلك إلى عمر بن الخطاب رضي الله عنه فأمر كثير بن الصلت أن يقطع','1315','1360','0');
INSERT INTO [t6227] VALUES (1942,'قال لا غرم على السارق إذا أقيم عليه الحد قال هذا حديث تفرد به المفضل وهو مرسل المسور أخو سعيد بن إبراهيم لم يدرك جده عبد الرحمن بن عوف قال أبو صالح عبد الغفار بن داود قلت للمفضل يا أبا معاوية إنما هو','1316','1360','0');
INSERT INTO [t6227] VALUES (1943,'مسألة','1318','1360','0');
INSERT INTO [t6227] VALUES (1944,'يقول من ظلم قيد شبر من أرض طوقه يوم القيامة من سبع أرضين اتفقا على صحته وعن عروة بن الزبير أن أروى ادعت على سعد بن زيد رضي الله عنه أنه أخذ شيئا من أرضها فخاصمته إلى مروان فقال سعيد أنا كنت آخذ من أرضها شيئا بعد','1318','1360','0');
INSERT INTO [t6227] VALUES (1945,'قال سمعت رسول الله','1318','1360','0');
INSERT INTO [t6227] VALUES (1946,'قال من أخذ شبرا من الأرض بغير حقه طوقه من سبع أرضين وفي الباب عن سعيد بن زيد رضي الله عنه أيضا مرفوعا أخرجه البخاري ومسلم في الصحيح وعن علي رضي الله عنه مرفوعا لعن الله من غير منار الأرض والله أعلم','1319','1360','0');
INSERT INTO [t6227] VALUES (1947,'وإذا غصب ساحة من الأرض فبنى عليها أخذ بنقض','1319','1360','0');
INSERT INTO [t6227] VALUES (1948,'قال لا يحل لامرئ أن يأخذ عصا أخيه بغير طيب نفسه وذلك لشدة ما حرم الله عز وجل مال المسلم على المسلم وهو في السنن أتم وفي حديث عبد الله بن السائب بن يزيد عن أبيه عن جده رضي الله عنهما عن النبي','1320','1360','0');
INSERT INTO [t6227] VALUES (1949,'رواية عمرو بن يثربي فيقوى احتجوا بما روي عن الشافعي رحمه الله أن مالكا رحمه الله أخبره عن عمرو بن يحيى المازني عن أبيه أن رسول الله','1321','1360','0');
INSERT INTO [t6227] VALUES (1950,'قال لا ضرر ولا ضرار من ضار ضره الله ومن شاق شق الله عليه ثم يقول المغصوب منه يستضر بذلك وهو أولى بأن ينظر له قال رسول الله','1322','1360','0');
INSERT INTO [t6227] VALUES (1951,'من أحيا أرضا ميتة فهي له وليس لعرق ظالم حق قال مالك بن أنس رحمه الله العرق الظالم كل ما أخذ واحتفر وغرس بغير حق','1322','1360','0');
INSERT INTO [t6227] VALUES (1952,'مسألة','1323','1360','0');
INSERT INTO [t6227] VALUES (1953,'أموالكم عليكم حرام ولكل غادر لواء يوم','1323','1360','0');
INSERT INTO [t6227] VALUES (1954,'بمنى وفيها فإن دماءكم وأموالكم وأعراضكم وأنسابكم عليكم حرام كحرمة يومكم هذا في شهركم هذا في بلدكم هذا ألا هل بلغت قلنا نعم قال اللهم أشهد فليبلغ الشاهد الغائب فإنه رب مبلغ يبلغه من هو أوعى له','1324','1360','0');
INSERT INTO [t6227] VALUES (1955,'مسألة','1324','1360','0');
INSERT INTO [t6227] VALUES (1956,'فقرأهن علينا وقال حرمت التجارة في الخمر اتفقا على صحته وفي الحديث الصحيح عن ابن عباس رضي الله عنهما أن النبي','1325','1360','0');
INSERT INTO [t6227] VALUES (1957,'أمر بإراقة خمر اليتامى ولم يأذن في تخليلها ولو كان إلى بيعها سبيل بوجه لم يأمر بإهلاك مال اليتيم وإذا لم يجز بيعها لم يجز تضمينها وأما ما روي عن سفيان عمن سمع ابن عباس رضي الله عنهما يقول دخلت على عمر رضي الله عنه','1325','1360','0');
INSERT INTO [t6227] VALUES (1958,'قال لعن الله اليهود حرمت عليهم الشحوم أن يأكلوها فجملوها فباعوها فأكلوا أثمانها قال سفيان يقول لا تأخذ في ضريبتهم الخمر ولا الخنزير ولكن خلوا بينهم وبين بيعها فخذوا أثمانها في جزيتهم فهذا حديث منقطع وليس فيه','1326','1360','0');
INSERT INTO [t6227] VALUES (1959,'إذا غصب شيئا فغيره أو طعاما فأكله لم يملكه وعليه رد ما بقى منه ناقصا ويغرم قيمة النقصان وقال أبو حنيفة رحمه الله يملكه على تفصيل يذكره في مذهبه ويغرم قيمته','1326','1360','0');
INSERT INTO [t6227] VALUES (1960,'خطب الناس في حجة الوداع وذكر الحديث وفيه ولا يحل لامرئ من مال أخيه إلا ما أعطاه عن طيب نفس ولا تظالموا ولا ترجعوا بعدي كفارا يضرب بعضكم رقاب بعض وروينا في حديث عمرو بن يثربي رضي الله عنه أنه قال شهدت خطبة النبي','1327','1360','0');
INSERT INTO [t6227] VALUES (1961,'طعاما فدعته وأصحابه قال فذهب بي أبي معه فلم يأكلوا حتى رأوا رسول الله','1328','1360','0');
INSERT INTO [t6227] VALUES (1962,'لقمته رمى بها ثم قال إني لأجد طعم لحم شاة ذبحت بغير إذن صاحبها فقالت يا رسول الله أخي وأنا من أعز الناس عليه ولو كان خيرا منها لم يغير علي وعلي أن أرضيه بأفضل منها فأبى أن يأكل منها وأمر بالطعام للأسارى فهذه','1328','1360','0');
INSERT INTO [t6227] VALUES (1963,'مسألة','1329','1360','0');
INSERT INTO [t6227] VALUES (1964,'مسألة','1329','1360','0');
INSERT INTO [t6227] VALUES (1965,'مسألة','1330','1360','0');
INSERT INTO [t6227] VALUES (1966,'الشفعة في كل ما لم يقسم فإذا وقعت الحدود وطرقت الطرق فلا شفعة أخرجه البخاري في الصحيح وأخرج مسلم عنه قال قضى رسول الله','1332','1360','0');
INSERT INTO [t6227] VALUES (1967,'فقال يا سعد ابتع مني بيتي في دارك فقال سعد والله لا ابتاعهما فقال المسور والله لتبتاعنهما فقال سعد لا أزيد على أربعة آلاف منجمة أو قال مقطعة فقال أبو رافع والله لقد أعطيت بها خمسمائة دينار ولولا أني','1333','1360','0');
INSERT INTO [t6227] VALUES (1968,'الجار أحق بشفعة جاره ينتظر بها وإن كان غائبا إذا كان طريقهما واحدا قال أبو عيسى سألت محمد بن إسماعيل عن هذا الحديث فقال لا أعلم أحد رواه عن عطاء غير عبد الملك بن أبي سليمان وهو حديث عبد الملك الذي تفرد به ويروى عن','1334','1360','0');
INSERT INTO [t6227] VALUES (1969,'في كون حق الشفعة على الفور وليس بثابت وروي عن ابن البيلماني عن أبيه عن ابن عمر رضي الله عنهما مرفوعا لا شفعة لشريك على شريكه إذا سبقه بالشراء والشفعة كحل العقال وابن البيلماني ضعيف جدا وليس هذا بثابت وروي عن','1335','1360','0');
INSERT INTO [t6227] VALUES (1970,'مسألة','1336','1360','0');
INSERT INTO [t6227] VALUES (1971,'مسألة','1336','1360','0');
INSERT INTO [t6227] VALUES (1972,'عامل أهل خيبر على شطر ما يخرج منها من ثمر وزرع أخرجه البخاري ومسلم في الصحيح وفي صحيح مسلم عنه أن النبي','1337','1360','0');
INSERT INTO [t6227] VALUES (1973,'شطر ثمرها وعنده أيضا عنه قال لما فتحت خيبر سألت يهود رسول الله','1337','1360','0');
INSERT INTO [t6227] VALUES (1974,'نقركم فيها على ذلك ما شئنا فكانوا فيها كذلك على عهد رسول الله','1337','1360','0');
INSERT INTO [t6227] VALUES (1975,'الخمس والله سبحانه وتعالى أعلم','1337','1360','0');
INSERT INTO [t6227] VALUES (1976,'مسألة','1338','1360','0');
INSERT INTO [t6227] VALUES (1977,'مسألة','1339','1360','0');
INSERT INTO [t6227] VALUES (1978,'مسألة','1340','1360','0');
INSERT INTO [t6227] VALUES (1979,'أنه قال من عمر أرضا ليست لأحد فهو أحق بها قال عروة قضى بذلك عمر بن الخطاب رضي الله عنه في خلافته رواه البخاري والله أعلم','1340','1360','0');
INSERT INTO [t6227] VALUES (1980,'ومن أحيا أرضا ميتة فهي له أذن له الإمام في إحيائها أو لم يأذن وقال أبو حنيفة رحمه الله لا يملكها إلا بإذن الإمام','1340','1360','0');
INSERT INTO [t6227] VALUES (1981,'قال من أحيا أرضا ميتة فهي له وليس لعرق ظالم حق وعن عروة قال أشهد أن رسول الله','1341','1360','0');
INSERT INTO [t6227] VALUES (1982,'الذين جاؤونا بالصلوات عنه وروى عنه عن عائشة مرفوعا وعند أبي داود عن أحمد بن حنبل بإسناده عن الحسن عن سمرة عن النبي','1341','1360','0');
INSERT INTO [t6227] VALUES (1983,'قال من أحيا أرضا ميتة فهي له وليس لعرق ظالم حق قال وأخبرنا مالك عن ابن شهاب عن سالم عن أبيه أن عمر','1341','1360','0');
INSERT INTO [t6227] VALUES (1984,'قال غزوت مع رسول الله','1342','1360','0');
INSERT INTO [t6227] VALUES (1985,'جعل الناس شركاء فيما لم يكن ملكا لأحد ولم يشترط في الانتفاع بها إذن السلطان وكذلك إحياء الموات الذي ليس بملك لأحد يجوز دون إذن السلطان إذ هو في معناه والله أعلم','1342','1360','0');
INSERT INTO [t6227] VALUES (1986,'ليس للذمي أن يحيي الموات في دار الإسلام وقال أبو حنيفة رحمه الله له ذلك إذا أذن له الإمام موتان الأرض لله ولرسوله ثم لكم من بعد والله تعالى أعلم','1342','1360','0');
INSERT INTO [t6227] VALUES (1987,'مسألة','1343','1360','0');
INSERT INTO [t6227] VALUES (1988,'يقول لا حمى إلا لله ولرسوله قال يعني ابن شهاب وبلغنا أن رسول الله','1343','1360','0');
INSERT INTO [t6227] VALUES (1989,'لا حمى إلا لله ولرسوله أي على مثل ما حمى عليه رسوله','1345','1360','0');
INSERT INTO [t6227] VALUES (1990,'مسألة','1345','1360','0');
INSERT INTO [t6227] VALUES (1991,'فقال يا رسول الله إني أصبت أرضا بخيبر لم أصب مالا قط أنفس منه فكيف تأمر ني به قال إن شئت حبست أصلها وتصدقت بها فتصدق بها عمر رضي الله عنه أن لا يباع أصلها ولا يوهب ولا يورث في الفقراء والقربى والرقاب وفي سبيل','1345','1360','0');
INSERT INTO [t6227] VALUES (1992,'في أن يتصدق بماله الذي بثمغ فقال النبي','1346','1360','0');
INSERT INTO [t6227] VALUES (1993,'فذكر الحديث وقال فيه عن النبي','1346','1360','0');
INSERT INTO [t6227] VALUES (1994,'حبس الأصل وسبل الثمرة','1346','1360','0');
INSERT INTO [t6227] VALUES (1995,'بالواد بيدي لم أهلكها فإنه مع ثمغ على سنته التي أمرت بها وإن شاء ولي ثمغ اشترى من ثمره رقيقا لعمله كتب معيقيب وشهد عبد الله بن الأرقم بسم الله الرحمن الرحيم هذا ما أوصى به عبد الله عمر أمير المؤمنين أن أحدث به حدث أن','1348','1360','0');
INSERT INTO [t6227] VALUES (1996,'عمر بن الخطاب رضي الله عنه على الصدقة وذكر الحديث وأما خالد فإنكم تظلمون خالدا وقد احتبس أدراعه وأعتده في سبيل الله وفي هذا دلالة على جواز وقف المنقولات خلاف قول محمد بن الحسن إنه لا يجوز قال أبو بكر عبد','1349','1360','0');
INSERT INTO [t6227] VALUES (1997,'رضي الله عنها تصدقت بمالها على بني هاشم وبني المطلب وأن عليا رضي الله عنه تصدق عليهم وأدخل معهم غيرهم واستدلوا بما روي عن أبي بكر بن حزم أن عبد الله بن زيد بن عبد ربه جاء إلى رسول الله','1351','1360','0');
INSERT INTO [t6227] VALUES (1998,'عليهما فورثه ابنهما بعدهما','1351','1360','0');
INSERT INTO [t6227] VALUES (1999,'ميراثا قال على بن عمر هذا مرسل لأن عبد الله بن زيد عبد ربه توفي في خلافة عثمان ولم يدركه أبو بكر بن حزم ورواه بشير بن محمد بن عبد الله بن زيد وهذا أيضا مرسل بشير لم يدرك جده عبد الله بن زيد قاله لي أبو عبد الرحمن','1352','1360','0');
INSERT INTO [t6227] VALUES (2000,'جاء بإطلاق الحبس قال مالك الذي جاء محمد','1353','1360','0');
INSERT INTO [t6227] VALUES (2001,'ولا يصح قال علي بن عمر لم يسنده غير ابن لهيعة عن أخيه وهما ضعيفان والمشهور عن شريح قوله لا حبس عن فرائض الله فإن استدلوا به من وجه آخر وقالوا حكم شريح فيما بين الصحابة وسكوتهم على ذلك دليل على موافقتهم إياه على','1354','1360','0');
INSERT INTO [t6227] VALUES (2002,'مسألة','1355','1360','0');
INSERT INTO [t6227] VALUES (2003,'مسألة','1355','1360','0');
INSERT INTO [t6227] VALUES (2004,'قال لا تعمروا ولا ترقبوا فمن أعمر شيئا أو أرقبه فهو في سبيل الميراث وروى أبو داود بسنده عن زيد بن ثابت رضي الله عنه قال قال رسول الله','1356','1360','0');
INSERT INTO [t6227] VALUES (2005,'العمرى جائزة لمن أعمرها والرقبى جائزة لمن أرقبها والله تعالى أعلم','1356','1360','0');
INSERT INTO [t6227] VALUES (2006,'وليس لأحد أن يرجع فيما وهب وأقبضه إلا الوالد فيما','1356','1360','0');
INSERT INTO [t6227] VALUES (2007,'فقال إني أعطيت ابن عمرة بنت رواحة عطية وأمرتني أن أشهدك يا رسول الله قال أعطيت سائر ولدك مثل هذا قال لا قال فاتقوا الله واعدلوا بين أولادكم وقال فرجع فرد عطيته رواه البخاري في الصحيح وعن حميد بن عبد الرحمن','1357','1360','0');
INSERT INTO [t6227] VALUES (2008,'فقال إني نحلت ابني هذا غلاما كان لي فقال رسول الله','1358','1360','0');
INSERT INTO [t6227] VALUES (2009,'فارجعه اتفقا على صحته قال الشافعي رحمه الله تعالى حديث النعمان حديث ثابت وبه نأخذ وفيه دلالة على أمور منها حسن الأدب في أن لا يفضل رجل أحدا من ولده على أحد في نحل فيعرض في قلب المفضل عليه شيء يمنعه من بره لأن','1358','1360','0');
INSERT INTO [t6227] VALUES (2010,'لا ينبغي وفي رواية لا يحل لأحد أن يعطي عطية فيرجع فيها إلا','1358','1360','0');
INSERT INTO [t6227] VALUES (2011,'ولد الرجل من كسبه وقال أنت ومالك لأبيك فهو من عمومه إلا ما قام دليله وعند أبي داود عن عمرو بن شعيب عن أبيه عن جده أن رجلا أتى النبي','1359','1360','0');
INSERT INTO [t6227] VALUES (2012,'قال العائد في هبته كالعائد في قيئه وروي بإسناد لا تقوم به حجة عن سمرة رضي الله عنه مرفوعا قال إذا كانت الهبة لذي رحم محرم لم يرجع فيها وروي عن عمر رضي الله عنه قال من وهب هبة لوجه الله فذلك له ومن وهب','1360','1360','0');
INSERT INTO [t6227] VALUES (2013,'في الحديث الذي قدمنا ذكره وهو الوالد ومن في معناه وروى جابر الجعفي وهو متروك عن علي رضي الله عنه قال من وهب هبة لغير ذي رحم فلم يثب فيها فهو أحق بها وبالله التوفيق وهو أعلم بالصواب','1361','1360','0');
INSERT INTO [t6227] VALUES (2014,'ويصح هبة المشاع وقال أبو حنيفة رحمه الله لا يصح هبة ما ينقسم عن حابر رضي الله عنه قال أتيت النبي','1361','1360','0');
INSERT INTO [t6227] VALUES (2015,'فأمر بلالا أن يزيد له فوزن فأرجح فما زال بعض تلك الدراهم معي حتى أصبت يوم الحرة','1361','1360','0');
INSERT INTO [t6227] VALUES (2016,'خرج يريد مكة وهو محرم حتى إذا كان بالروحاء إذا حمار وحشي عقير فذكر لرسول الله','1362','1360','0');
INSERT INTO [t6227] VALUES (2017,'فقال يا رسول الله شأنكم بهذا الحمار فأمر رسول الله','1362','1360','0');
INSERT INTO [t6227] VALUES (2018,'أمر رجلا يثبت عنده لا يريبه أحد من الناس حتى يجاوزه وروى مسلم البطين أن حسين بن علي رضي الله عنهما ورث مواريث فتصدق بها قبل أن يقسم فأجيزت وروى عن قتادة عن النضر بن أنس قال نحلني أنس نصف داره فقال أبو بردة إن سرك','1363','1360','0');
INSERT INTO [t6227] VALUES (2019,'للغني أكل اللقطة بعد حول التعريف وقال العراقيون','1363','1360','0');
INSERT INTO [t6227] VALUES (2020,'يسأله عن اللقطة فقال أعرف عفاصها ووكاءها ثم عرفها سنة فإن جاء صاحبها وإلا فشأنك بها اتفقا على صحته وعنه قال سئل رسول الله','1364','1360','0');
INSERT INTO [t6227] VALUES (2021,'فيها مائة دينار فأتيت بها رسول الله','1364','1360','0');
INSERT INTO [t6227] VALUES (2022,'بمعنى حديث زيد ابن خالد وزاد فيه فإن جاء باغيها فعرف عفاصها وعددها فادفعها إليه قال أبو داود هذه الزيادة التي زاد حماد ليست بمحفوظة وعند أبي داود عن بلال بن يحيى العبسي عن علي رضي الله عنه أنه التقط دينارا','1365','1360','0');
INSERT INTO [t6227] VALUES (2023,'فقال رسول الله','1366','1360','0');
INSERT INTO [t6227] VALUES (2024,'وأكل علي وفاطمة رضي الله عنهما فلما كان بعد ذلك أتت امرأة تنشد الدينار قال رسول الله','1366','1360','0');
INSERT INTO [t6227] VALUES (2025,'أن يعرفه فعرفه فلم يجد له باغيا فأنفقه ثم وجد باغيه فغرمه رسول الله','1366','1360','0');
INSERT INTO [t6227] VALUES (2026,'بتعريفها ثم إنه احتاج إليها فلشدة حاجته إليها أمره رسول الله','1366','1360','0');
INSERT INTO [t6227] VALUES (2027,'بدله وإنما أذن له في إنفاقه قبل مضي سنة التعريف لشدة حاجته إليه مع أنه يحتمل أن يكون','1366','1360','0');
INSERT INTO [t6227] VALUES (2028,'فإن عارضوا بروايتهم عن عامر عن أبيه عن عبد الله رضي الله عنه أنه اشترى جارية فذهب صاحبها فتصدق بثمنها وقال اللهم عن صاحبها فإن كره فلي وعلي الغرم ثم قال هكذا نفعل باللقطة قال الشافعي رحمه الله فخالفوا','1368','1360','0');
INSERT INTO [t6227] VALUES (2029,'وسنة النبي','1369','1360','0');
INSERT INTO [t6227] VALUES (2030,'مسألة','1370','1360','0');
INSERT INTO [t6227] VALUES (2031,'ما لك ولها دعها فإن معها حذاءها','1370','1360','0');
INSERT INTO [t6227] VALUES (2032,'حتى احمرت وجنتاه أو احمر وجهه وقال ما لك ولها معها حذاؤها وسقاؤها حتى يأتيها ربها وعند أبي داود عن المنذر بن جرير قلا كنت مع جرير بالبوازيج فجاء الراعي بالبقر وفيها بقرة ليست منها فقال له جرير ما هذه قال','1371','1360','0');
INSERT INTO [t6227] VALUES (2033,'إياك وضالة المسلم فإنها حرق النار','1372','1360','0');
INSERT INTO [t6227] VALUES (2034,'ولا يستحق الجعل في رد الآبق من العبيد والإماء والضوال من البهائم إلا بالشرط وقال أبو حنيفة رحمه الله إن رد العبد من مسيرة ثلاثة أيام وكانت قيمته أربعين أو اكثر استحق أربعين روي عن الشعبي عن علي رضي الله عنه في جعل الآبق','1372','1360','0');
INSERT INTO [t6227] VALUES (2035,'مسألة','1373','1360','0');
INSERT INTO [t6227] VALUES (2036,'قال رفع القلم عن ثلاثة عن الصبي حتى يحتلم وعن المعتوه حتى يفيق وعن النائم حتى يستيقظ ورويناه عن علي بن أبي طالب رضي الله عنه عن النبي','1376','1360','0');
INSERT INTO [t6227] VALUES (2037,'فمرض فأتاه النبي','1377','1360','0');
INSERT INTO [t6227] VALUES (2038,'عند رأسه فقال أسلم فنظر الغلام إلى أبيه وهو عنده فقال أطع أبا القاسم فأسلم فخرج رسول الله','1377','1360','0');
INSERT INTO [t6227] VALUES (2039,'خاطبه بالإسلام ألا ترى أن أباه قال أطع أبا القاسم وإذا كان مخصوصا بالإسلام كان مخصوصا بصحته نحو ما نقول في أمير المؤمنين علي رضي الله عنه قال الشافعي رحمه الله فإن احتج محتج بأن علي بن أبي طالب رضي الله عنه','1377','1360','0');
INSERT INTO [t6227] VALUES (2040,'بصبي من هودج فقالت ألهذا حج قال نعم ولك أجر وقد رأينا الصغير يرى الصلاة فيصلي وهو غير عالم بأن الصلاة عليه وهو غير عارف بالإيمان فعلى ذلك كان أمر علي رضي الله عنه كان أول من صلى وذلك أنه رأى النبي','1378','1360','0');
INSERT INTO [t6227] VALUES (2041,'مكث بمكة خمس عشرة سنة وأن عليا رضي الله عنه قتل وهو ابن ثلاث وستين سنة روى ذلك مسلم في الصحيح عن ابن عباس رضي الله عنهما قال أقام رسول الله','1379','1360','0');
INSERT INTO [t6227] VALUES (2042,'فيكون مقامه بمكة بعد الوحي ثمان سنين فيكون علي رضي الله عنه في قول من قال قتل وهو ابن ثلاث وستين سنة على رأس أربعين من مهاجر النبي','1379','1360','0');
INSERT INTO [t6227] VALUES (2043,'بالإسلام والصلاة فهو أحد شيئين إما أن يكون خصه بالخطاب لما صار من أهل التمييز والمعرفة دون سائر الصغار ليكون ذلك كرامة له ومنقبة فلما توجه عليه الخطاب والدعوة صحت منه الإجابة وسائر الصغار لا يتوجه عليهم الخطاب والدعوة فلا يصح','1380','1360','0');
INSERT INTO [t6227] VALUES (2044,'قط وقتل وهو ابن بضع وستين سنة وهذه الرواية أولى فولد الرجل أعرف به والله أعلم','1381','1360','0');
INSERT INTO [t6227] VALUES (2045,'الجزء ٤','1382','0','0');
INSERT INTO [t6227] VALUES (2046,'كتاب الفرائض','1382','2045','0');
INSERT INTO [t6227] VALUES (2047,'أرحم أمتي بأمتي أبو بكر وأشدهم في دين الله عمر وأصدقهم حياء عثمان وأعلمهم بالحلال والحرام معاذ بن جبل وأعلمهم بما أنزل الله علي أبي بن كعب وأفرضهم زيد بن ثابت وأمين هذه الأمة أبو عبيدة بن الجراح والله الموفق','1382','2045','0');
INSERT INTO [t6227] VALUES (2048,'ذوو الأرحام لا يرثون إرث ذوي النسب وقال العراقيون إنهم يرثون','1382','2045','0');
INSERT INTO [t6227] VALUES (2049,'وأبو بكر في بني سلمة فوجدني لا أعقل فدعا بماء فتوضأ فرش علي منه فأفقت فقلت كيف أصنع في مالي يا رسول الله فنزلت يوصيكم الله في أولادكم للذكر مثل حظ الأنثيين وفي رواية عندهما أيضا عنه دخل علي رسول الله','1383','2045','0');
INSERT INTO [t6227] VALUES (2050,'عن كيفية الميراث فأنزل الله تعالى الآيات فيه وبين حصة كل واحد منهم فلا','1383','2045','0');
INSERT INTO [t6227] VALUES (2051,'إن الله قد أعطى كل ذي حق حقه ولا نجد لهم في الكتاب العزيز حقا مذكورا وروي أن عمر رضي الله عنه كان كتب كتابا في شأن العمة ليسأل عنها ويستخبر ثم محاه بالماء وقال لو رضيك الله لأقرك وإنه كان يقول للعمة تورث ولا ترث','1384','2045','0');
INSERT INTO [t6227] VALUES (2052,'لا مطلقا هكذا ألا ترى أن الزوج يرث أكثر مما يرث ذوو الأرحام ولا رحم له أولا ترى أن ابن العم البعيد يرث المال كله ولا يرث الخال والخال أقرب رحما منه وذكر أيضا توريث الموالي دون ذوي الأرحام ولا رحم لهم قال فإنما','1386','2045','0');
INSERT INTO [t6227] VALUES (2053,'في حديث فيه والخال وارث من لا وارث له يرث ماله ويعقل عنه فليس بالقوي رواه راشد بن سعد وأبو عامر عبد الله بن لحي الهوزني وهما ممن لم يحتج بهما الشيخان رحمهما الله وهو حديث مختلف فيه رواه إسماعيل بن عياش وإسناده ضعيف','1386','2045','0');
INSERT INTO [t6227] VALUES (2054,'كان يقول الله ورسوله مولى من لا مولى له والخال وارث من لا وارث له وعبد الرحمن بن الحارث وحكيم بن حكيم غير محتج بهما في الصحيح وروي عن عمرو بن مسلم عن طاوس عن عائشة رضي الله عنها قالت الله ورسوله مولى من لا مولى له والخال','1387','2045','0');
INSERT INTO [t6227] VALUES (2055,'مرسلا وروي عن شريك عن ليث عن ابن المنكدر عن أبي هريرة رضي الله عنه قال ورث رسول الله','1388','2045','0');
INSERT INTO [t6227] VALUES (2056,'وروي عن عبد الله بن جعفر المدني عن عبد الله بن دينار عن ابن عمر رضي الله عنهما قال أقبل رسول الله','1388','2045','0');
INSERT INTO [t6227] VALUES (2057,'بمعناه وعن عطاء بن يسار عن أبى سعيد الخدري رضي الله عنه كذلك احتجوا بما روي عن الحجاج بن أرطأة عن الزهري أن رسول الله','1389','2045','0');
INSERT INTO [t6227] VALUES (2058,'الخالة والدة مرسلان وإسنادهما ضعيف وروي عن محمد بن إسحاق عن يعقوب بن عقبة عن','1389','2045','0');
INSERT INTO [t6227] VALUES (2059,'أنه سأل عاصم بن عدي عن ثابت بن الدحداح وتوفي هل تعلمون له نسبا فيكم فقالوا لا إنما هو أني قال فقضى رسول الله','1390','2045','0');
INSERT INTO [t6227] VALUES (2060,'عن وارثه وجوابهم له دلالة على أنه لم يكن له وارث وأن رسول الله','1391','2045','0');
INSERT INTO [t6227] VALUES (2061,'بميراثه فقال انظروا هل من وارث فالتمسوه فلم يجدوا له وارثا فأخبر به النبي','1391','2045','0');
INSERT INTO [t6227] VALUES (2062,'ادفعوه إلى أكبر خزاعة وهذا إنما دفعه إليه على طريق المصلحة لا بالتوريث فكذلك في قصة ابن الدحداح إن كان صحيحا والله أعلم ولا حجة لهم في حديث سعد بن أبي وقاص بقوله ولا يرثني إلا ابنة لي فإنه إنما أراد أن يتصدق بأكثر','1391','2045','0');
INSERT INTO [t6227] VALUES (2063,'مسألة','1396','2045','0');
INSERT INTO [t6227] VALUES (2064,'شيئا فارجعي حتى أسأل الناس فسأل الناس فقال له المغيرة بن','1396','2045','0');
INSERT INTO [t6227] VALUES (2065,'أعطاها السدس فقال أبو بكر رضي الله عنه هل معك غيرك فقام محمد بن مسلمة الأنصاري فقال مثل ما قال المغيرة فأنفذ لها أبو بكر رضي الله عنه ثم جاءت الجدة الأخرى إلى عمر رضي الله عنه تسأله ميراثها فقال ما لك في كتاب','1397','2045','0');
INSERT INTO [t6227] VALUES (2066,'وجماعة من التابعين رضي الله عنهم أنهم ورثوا ثلاث جدات مع الحديث المنقطع الذي روي عن النبي','1399','2045','0');
INSERT INTO [t6227] VALUES (2067,'خلاف ذلك إلا ما روينا عن سعد بن أبي وقاص رضي الله عنه مما لا يثبت عند أهل المعرفة بالحديث إسناده وهذا الحديث المنقطع روي عن إبراهيم وعبد الرحمن بن يزيد وفسراه بأنهن ثنتين من قبل الأب وواحدة من قبل الأم وروي أيضا عن الحسن','1399','2045','0');
INSERT INTO [t6227] VALUES (2068,'مسألة','1400','2045','0');
INSERT INTO [t6227] VALUES (2069,'مسألة','1402','2045','0');
INSERT INTO [t6227] VALUES (2070,'لو كنت متخذا خليلا لاتخذته يعني أبا بكر رضي الله عنه جعل الجد أبا أخرجه البخاري في الصحيح','1405','2045','0');
INSERT INTO [t6227] VALUES (2071,'مسألة','1406','2045','0');
INSERT INTO [t6227] VALUES (2072,'قال لا يرث المسلم الكافر ولا الكافر المسلم وهو عند مسلم أيضا بمعناه وعند أبي داود عن يزيد بن البراء عن أبيه قال لقيت عمي ومعه راية فقلت أين تريد قال بعثني رسول الله','1406','2045','0');
INSERT INTO [t6227] VALUES (2073,'لا يرث المسلم الكافر كما تركوا به قول معاذ ومعاوية رضي الله عنهما وغيرهما في توريث المسلم من اليهودي يعني أنهم معنا لم يخصوا خبر أسامة بقول معاذ وغيره فكذلك وجب أن لا يخصوه بقول علي رضي الله عنه حتى يحملوا الخبر على','1408','2045','0');
INSERT INTO [t6227] VALUES (2074,'فهو أولى والله أعلم','1409','2045','0');
INSERT INTO [t6227] VALUES (2075,'والإخوة والأخوات للأب والأم يشاركون الإخوة والأخوات للأم في ثلثهم في مسألة المشركة وقال العراقيون إنهم لا يشاركونهم روي عن وهب بن منبه عن مسعود بن الحكم الثقفي قال أتي عمر ابن الخطاب رضي الله عنه في امرأة تركت زوجها وأمها','1409','2045','0');
INSERT INTO [t6227] VALUES (2076,'مسألة','1411','2045','0');
INSERT INTO [t6227] VALUES (2077,'قال ألحقوا الفرائض بأهلها فما بقي فهو لأولى رجل ذكر فجعل ما فضل لعصبة المتوفى دون عصبة أمه وولد الملاعنة لا عصبة له من قبل أبيه فالمسلمون له عصبة وعندهما عن أبي هريرة رضي الله عنه عن رسول الله','1412','2045','0');
INSERT INTO [t6227] VALUES (2078,'قال المرأة تحرز ثلاث مواريث عتيقها ولقطتها وولدها الذي لاعنت','1412','2045','0');
INSERT INTO [t6227] VALUES (2079,'ميراث ابن الملاعنة لأمه ولورثتها من بعدها وهذا مرسل ورواه من حديث عيسى بن موسى أبي محمد القرشي وفيه نظر ولم يثبت من عدالته ما يوجب قبول خبره عن العلاء عن عمرو بن شعيب عن أبيه عن جده','1413','2045','0');
INSERT INTO [t6227] VALUES (2080,'بولد الملاعنة قال قضى به رسول الله','1414','2045','0');
INSERT INTO [t6227] VALUES (2081,'كتاب الوصايا','1416','2045','0');
INSERT INTO [t6227] VALUES (2082,'مسألة','1416','2045','0');
INSERT INTO [t6227] VALUES (2083,'حين أنزل الله تعالى وأنذر عشيرتك الأقربين قال يا معشر قريش اشتروا أنفسكم من الله لا أغني عنكم من الله شيئا يا بني عبد مناف لا أغني عنكم من الله شيئا يا عباس بن عبد المطلب لا أغني عنك من الله شيئا يا صفية عمة رسول','1416','2045','0');
INSERT INTO [t6227] VALUES (2084,'سليني ما شئت لا أغني عنك من الله شيئا فثبت','1416','2045','0');
INSERT INTO [t6227] VALUES (2085,'أمر أبا طلحة أن يجعل الأرض التي جعلها لله في قرابته فقسمها بين حسان بن ثابت وأبي بن كعب من حديث أنس بن مالك قال أبو داود وبلغني عن الأنصاري محمد بن عبيد الله قال أبو طلحة يزيد بن سهل بن الأسود بن حرام بن عمرو بن زيد بن مناة','1417','2045','0');
INSERT INTO [t6227] VALUES (2086,'مسألة','1418','2045','0');
INSERT INTO [t6227] VALUES (2087,'الثلث والثلث كثير ولم يفرق بين أن يوصي به لقاتله أو غيره وفي الصحيحين عن ابن عمر رضي الله عنهما أن رسول الله','1418','2045','0');
INSERT INTO [t6227] VALUES (2088,'مسألة','1419','2045','0');
INSERT INTO [t6227] VALUES (2089,'قال أوصاني جبريل بالجار إلى أربعين دارا عشرة من ههنا وعشرة من ههنا وعشرة من ههنا وعشرة من ههنا وإنما يعرف من حديث الزهري عن النبي','1419','2045','0');
INSERT INTO [t6227] VALUES (2090,'مسألة','1420','2045','0');
INSERT INTO [t6227] VALUES (2091,'مسألة','1421','2045','0');
INSERT INTO [t6227] VALUES (2092,'مسألة','1421','2045','0');
INSERT INTO [t6227] VALUES (2093,'كتاب قسم الفيء والغنيمة','1423','2045','0');
INSERT INTO [t6227] VALUES (2094,'مسالة','1423','2045','0');
INSERT INTO [t6227] VALUES (2095,'فيعطيك سلبه فقال رسول الله','1423','2045','0');
INSERT INTO [t6227] VALUES (2096,'بدلالة قوله عز وجل يسئلونك عن الأنفال قل الأنفال لله والرسول فكان','1424','2045','0');
INSERT INTO [t6227] VALUES (2097,'عن صاحبه فقيل ابن الأكوع قال له سلبه أجمع والقصة عند البخاري أيضا عنه وقال فيها ثم قتله قال فنفله رسول الله','1424','2045','0');
INSERT INTO [t6227] VALUES (2098,'قال من قتل قتيلا فله سلبه وفي رواية قال رسول الله','1424','2045','0');
INSERT INTO [t6227] VALUES (2099,'فيما بين أصحابه أن السلب يكون للقاتل في كل غزوة غزاها رسول الله','1425','2045','0');
INSERT INTO [t6227] VALUES (2100,'وقصة صفية بنت عبد المطلب رضي الله عنها وقتلها اليهودي وقولها لحسان اسلبه فاستلبه وأنه لم يمنعني أن أستلبه إلا أنه رجل وقصة قتل علي بن أبي طالب رضي الله عنه عمرو بن ود ثم أقبل علي رضي الله عنه نحو رسول الله','1425','2045','0');
INSERT INTO [t6227] VALUES (2101,'فقال يا خالد ما منعك أن تعطيه سلبه قال استكثرته يا رسول الله قال ادفعه وقال فيه إن رسول الله','1427','2045','0');
INSERT INTO [t6227] VALUES (2102,'بأبي هو وأمي شيء لم يجز تركه قال ولم يستثن النبي','1427','2045','0');
INSERT INTO [t6227] VALUES (2103,'مسألة','1427','2045','0');
INSERT INTO [t6227] VALUES (2104,'قال الله عز وجل واعلموا أنما غنمتم من شئ فأن لله خمسة وللرسول الآية','1427','2045','0');
INSERT INTO [t6227] VALUES (2105,'خيبر بين أصحابه ففي صحيح البخاري عن أسلم أنه سمع عمر بن الخطاب رضي الله عنه يقول أما والذي نفسي بيده لولا أن أترك آخر الناس ببانا ليس لهم شيء ما فتحت علي قرية إلا قسمتها كما قسم رسول الله','1428','2045','0');
INSERT INTO [t6227] VALUES (2106,'بين الغانمين هي ما فتحها عنوة دون ما فتح صلحا بدليل ما روي عن محمد بن إسحاق عن الزهري وغيره قالوا بقيت بقية من أهل خيبر يحصوا فسألوا رسول الله','1428','2045','0');
INSERT INTO [t6227] VALUES (2107,'خالصة لأنه لم يوجف عليها بخيل ولا ركاب وعمر رضي الله عنه حين رأى وقف السواد استطاب أنفس الغانمين كما استطاب رسول الله','1428','2045','0');
INSERT INTO [t6227] VALUES (2108,'أيما قرية أتيتموها فأقمتم فيها فسهمكم فيها وأيما قرية عصت الله ورسوله فإن خمسها لله ولرسوله ثم هي لكم رواه مسلم في الصحيح ورواه محمد بن رافع وغيره عن عبد الرزاق وقال سهمك فيها فيحتمل أن يكون المراد به ما','1429','2045','0');
INSERT INTO [t6227] VALUES (2109,'إذا شرط الإمام قبل القتال من أخذ شيئا فهو له فمن أخذ شيئا يكون غنيمة وفيه قول آخر إنه جائز وهو قول ابي حنيفة رحمه الله','1429','2045','0');
INSERT INTO [t6227] VALUES (2110,'حين التقى الناس ببدر نفل كل امرئ ما أصاب وذكر باقي الحديث في اختلافهم ونزول الآية في الأنفال قال البيهقي رحمه الله تعالى وقد روي في حديث داود بن أبي هند عن عكرمة عن ابن عباس رضي الله عنهما في شرط النبي','1430','2045','0');
INSERT INTO [t6227] VALUES (2111,'المدينة بعثنا في ركب فذكر الحديث وفيه قال وكان الفيء إذ ذاك من أخذ شيئا فهو له ثم ذكر الحديث في بعثه','1431','2045','0');
INSERT INTO [t6227] VALUES (2112,'بعد ذلك في مغازيه والله أعلم','1431','2045','0');
INSERT INTO [t6227] VALUES (2113,'وللإمام أن يمن على البالغين من الأسرى وأن يفاديهم بأسرى المسلمين وقال العراقيون ليس له ذلك وهذا أيضا خلاف الكتاب والسنة','1431','2045','0');
INSERT INTO [t6227] VALUES (2114,'خيلا قبل نجد فجاءت برجل من بني حنيفة يقال له ثمامة بن أثال فربطوه بسارية من سواري المسجد فخرج إليه رسول الله','1432','2045','0');
INSERT INTO [t6227] VALUES (2115,'بإطلاقه ثم إسلامه وفي صحيح البخاري عن جبير بن مطعم رضي الله عنه عن النبي','1433','2045','0');
INSERT INTO [t6227] VALUES (2116,'وأصحابه من جبال التنعيم عند صلاة الفجر ليقتلوهم فأخذهم رسول الله','1433','2045','0');
INSERT INTO [t6227] VALUES (2117,'رجلا من بني عقيل وكانت ثقيف قد أسرت رجلين من أصحاب النبي','1433','2045','0');
INSERT INTO [t6227] VALUES (2118,'بالرجلين اللذين أسرتهما ثقيف','1433','2045','0');
INSERT INTO [t6227] VALUES (2119,'فبعث بها إلى أهل مكة ففادى بها أسيرا كان في أيديهم وفي الصحيحين حديث جابر رضي الله عنه في الرجل الذي اخترط سيف رسول الله','1434','2045','0');
INSERT INTO [t6227] VALUES (2120,'الله فشام السيف فجلس فلم يعاقبه رسول الله','1434','2045','0');
INSERT INTO [t6227] VALUES (2121,'في الاسارى يوم بدر إن شئتم قتلتموهم وإن شئتم فاديتموهم واستمتعتم بالفداء واستشهد منكم بعدتهم وعن ابن إسحاق قال وكان ممن ترك رسول الله','1434','2045','0');
INSERT INTO [t6227] VALUES (2122,'وأبو عزة عمر بن عبد الله','1434','2045','0');
INSERT INTO [t6227] VALUES (2123,'مسألة','1435','2045','0');
INSERT INTO [t6227] VALUES (2124,'قسم غنائم بدر بشعب من شعاب الصفراء قريب من بدر وقسم غنائم بني المصطلق على مياههم وغنائم هوازن في ديارهم وغنائم خيبر بخيبر وذلك في مغازي ابن إسحاق مذكور رويناه في كتاب السنن وقد أعاد الشافعي رحمه الله تعالى هذه المسألة في','1435','2045','0');
INSERT INTO [t6227] VALUES (2125,'مسألة','1436','2045','0');
INSERT INTO [t6227] VALUES (2126,'أنه قسم للفرس سهمين وللراجل سهم وعند البخاري عنه عن نافع عن ابن عمر رضي الله عنهما قال قسم رسول الله','1436','2045','0');
INSERT INTO [t6227] VALUES (2127,'أسهم للرجل ولفرسه ثلاثة أسهم سهما له وسهمين لفرسه فقد شفى أبو معاوية بروايته هذه عن عبيد الله وهو حافظ ثقة حجة وكذلك رواه سفيان الثوري الإمام عنه وقد وهم بعض الرواة لهذا الحديث فروه بخلاف هذه الرواية الصحيحة','1436','2045','0');
INSERT INTO [t6227] VALUES (2128,'أسهم للرجل ولفرسه ثلاثه أسهم سهما له وسهمين لفرسه جعل للفارس سهمين وللراجل سهما قال الرمادي كذا يقول ابن نمير قال لنا النيسابوري هذا عندي وهم من ابن أبي شيبة أو من الرمادي لأن أحمد بن حنبل وعبد الرحمن بن بشر','1437','2045','0');
INSERT INTO [t6227] VALUES (2129,'أربعة نفر ومعنا فرس فأعطى كل إنسان منا سهما وأعطى الفرس سهمين استدلوا بما روي عن أبي حازم مولى أبي رهم عن أبي رهم قال حضرت انا وأخي حنينا ومعنا فرسان فأسهم النبي','1441','2045','0');
INSERT INTO [t6227] VALUES (2130,'أعطاهما ستة أسهم أربعة لفرسيهما وسهمان لهما وروي في الثلاثة الأسهم للفرس وصاحبه عن عمر وعلي رضي الله عنهما والله أعلم','1441','2045','0');
INSERT INTO [t6227] VALUES (2131,'مسألة','1442','2045','0');
INSERT INTO [t6227] VALUES (2132,'قال فقلنا يا رسول الله أعطيت بني عبد المطلب وتركتنا وإنما نحن وهم بمنزلة واحدة فقال رسول الله','1442','2045','0');
INSERT INTO [t6227] VALUES (2133,'لبني عبد شمس ولا لبني عبد نوفل وروى هذا الحديث بعضهم فأدرج فيه أن ابا بكر لم يكن يعطي قربى رسول الله','1442','2045','0');
INSERT INTO [t6227] VALUES (2134,'يعطيهم وكان عمر رضي الله عنه يعطيهم منه وعثمان رضي الله عنه بعده ورواه','1442','2045','0');
INSERT INTO [t6227] VALUES (2135,'يزيد بن معاوية وأهله وعند أبي داود أن نجدة حين حج في فتنة ابن الزبير أرسل إلى ابن عباس يسأله عن سهم ذي القربى ويقول لمن تراه قال ابن','1443','2045','0');
INSERT INTO [t6227] VALUES (2136,'قسمه لهم رسول الله','1444','2045','0');
INSERT INTO [t6227] VALUES (2137,'فقلت يا رسول الله إن رأيت توليني حقنا من هذا الخمس في كتاب الله فأقسمه حياتك حتى لا ينازعني أحد بعدك فافعل قال ففعل ذلك قال فقسمته حياة رسول الله','1444','2045','0');
INSERT INTO [t6227] VALUES (2138,'في قسمة سهم ذي القربى بينهم ويدل على استحقاق ذوي القربى هذا السهم بنص كتاب الله','1444','2045','0');
INSERT INTO [t6227] VALUES (2139,'عليا رضي الله عنه إلى خالد بن الوليد لقبض الخمس فأخذ منه جارية فأصبح ورأسه يقطر قال خالد لبريدة ترى ما يصنع هذا قال وكنت أبغض عليا فذكرت ذلك لرسول الله','1445','2045','0');
INSERT INTO [t6227] VALUES (2140,'إياه حقهم من الخمس وفيه دلالة على أن الله تعالى جعل لهم هذا السهم على جهة الاستحقاق وكان ذلك موكولا إلى رأي النبي','1445','2045','0');
INSERT INTO [t6227] VALUES (2141,'في ذلك ولما احتج له بان له في الخمس أكثر من ذلك والعجب أن هذا القائل استدل فقال لو كان هذا السهم لهم على جهة الاستحقاق ما جاز للنبي','1445','2045','0');
INSERT INTO [t6227] VALUES (2142,'إنما بعث مبينا لأمته ما أراد الله تعالى بكتابه عاما أو خاصا وليس هذا أول عموم ورد في الكتاب فبين رسول الله','1445','2045','0');
INSERT INTO [t6227] VALUES (2143,'في هذا الحكم على مجرد البيان حتى ذكر علته','1445','2045','0');
INSERT INTO [t6227] VALUES (2144,'ما روينا عنه في الأخبار الثابتة أنهم لم يفارقوه في جاهلية ولا إسلام وقد أعطى','1446','2045','0');
INSERT INTO [t6227] VALUES (2145,'بلا حجة بقول من زعم أن فرض الزكاة رفع برفع النبي','1446','2045','0');
INSERT INTO [t6227] VALUES (2146,'مسالة','1446','2045','0');
INSERT INTO [t6227] VALUES (2147,'بعد وفاته قولان أحدهما إنه مردود على الأصناف المذكورين في الآية وهو قول أبي حنيفة رحمه الله تعالى والقول الثاني إنه يصرف في المصالح وهو الصحيح الذي اختاره الشافعي رحمه الله مذهبا لنفسه روي عن عبادة بن الصامت رضي','1446','2045','0');
INSERT INTO [t6227] VALUES (2148,'أم أهله قال لا بل أهله قالت فما بال الخمس فقال إني سمعت رسول الله','1447','2045','0');
INSERT INTO [t6227] VALUES (2149,'أعلم ثم رجعت قال البيهقي رحمه الله تعالى هكذا رواه الوليد بن جميع وليس بالقوي وإنما احتج أبو بكر بما رواه وغيره عن النبي','1447','2045','0');
INSERT INTO [t6227] VALUES (2150,'كتاب قسم الصدقات','1448','2045','0');
INSERT INTO [t6227] VALUES (2151,'مسألة','1448','2045','0');
INSERT INTO [t6227] VALUES (2152,'قال لا تحل الصدقة لغني ولا لذي مرة قوي وروي عن أبي هريرة رضي الله عنه مثله وقال ولا لذي مرة سوي وروي عن عبيد الله بن عدي بن الخيار عن رجلين قالا أتينا رسول الله','1448','2045','0');
INSERT INTO [t6227] VALUES (2153,'في حجة الوداع وقال فرآنا جلدين فقال إن شئتما والله أعلم','1449','2045','0');
INSERT INTO [t6227] VALUES (2154,'لا يجوز صرف شيء من الصدقات الواجبة إلى الكفار وقال أبو حنيفة رحمه الله يجوز صرف صدقة الفطر والكفارات إلى فقراء أهل الذمة لنا حديث ابن عباس رضي الله عنهما في وصية رسول الله','1449','2045','0');
INSERT INTO [t6227] VALUES (2155,'أن الصدقة الواجبة عليهم مردودة على فقرائهم فخص','1449','2045','0');
INSERT INTO [t6227] VALUES (2156,'مسألة','1450','2045','0');
INSERT INTO [t6227] VALUES (2157,'فقال السلام عليك يا غلام بني عبد المطلب فقال وعليك قال إني رجل من أخوالك من ولد بني سعد بن بكر وإني رسول قومي إليك ووافدهم فذكر الحديث إلى أن قال فإنا قد وجدنا في كتابك وأمرتنا رسلك أن نأخذ من حواشي أموالنا فنضعه في','1450','2045','0');
INSERT INTO [t6227] VALUES (2158,'مسألة','1451','2045','0');
INSERT INTO [t6227] VALUES (2159,'لا تحل صدقة لغني إلا لخمسة لعامل عليها أو مسكين تصدق عليه منه فأهداها لغني أو لرجل اشتراها بماله أو غارم أو غاز في سبيل الله عز وجل وروي عنه عن معمر عن زيد بإسناده ومعناه ورواه القعنبي عن مالك عن زيد عن عطاء أن رسول','1451','2045','0');
INSERT INTO [t6227] VALUES (2160,'قال أقم يا قبيصة حتى تأتينا الصدقة فنأمر لك بها ثم قال يا قبيصة إن المسألة لا تحل إلا لأحد ثلاثة رجل تحمل حمالة فحلت له المسألة حتى يصيبها ثم يمسك ورجل أصابته جائحة فاجتاحت ماله فحلت له المسألة فسأل حتى يصيب قواما','1452','2045','0');
INSERT INTO [t6227] VALUES (2161,'ولا يجوز صرف الصدقات إلى صنف واحد من جملة الأصناف الثمانية مع وجودهم وقال العراقيون إن ذلك جائز ودليلنا قوله تعالى إنما الصدقات للفقراء الآية روى أبو داود عن زياد بن الحارث الصدائي قال أتيت','1452','2045','0');
INSERT INTO [t6227] VALUES (2162,'فبايعته فذكر حديثا طويلا فأتاه رجل فقال أعطني من الصدقة فقال له رسول الله','1453','2045','0');
INSERT INTO [t6227] VALUES (2163,'لمعاذ حين بعثه إلى اليمن فإذا صلوا فأخبرهم أن الله قد افترض عليهم زكاة في أموالهم تؤخذ من غنيهم فترد على فقيرهم قال البيهقي رحمه الله تعالى يشبه أن يكون المقصود من هذا الخبر تفرقة الصدقات في الموضع الذي تؤخذ فيه دون نقلها إلى','1453','2045','0');
INSERT INTO [t6227] VALUES (2164,'مسألة','1455','2045','0');
INSERT INTO [t6227] VALUES (2165,'ليس المسكين بهذا الطواف الذي يطوف على الناس فترده اللقمة واللقمتان والتمرة والتمرتان قالوا فمن المسكين يا رسول الله قال الذي لا يجد غنى يغنيه ولا يفطن له فيتصدق عليه ولا يسأل الناس شيئا لفظ حديث مسلم وعند البخاري','1456','2045','0');
INSERT INTO [t6227] VALUES (2166,'ليس المسكين الذي ترده التمرة والتمرتان واللقمة واللقمتان إنما المسكين الذي يتعفف اقرؤوا إن شئتم لا يسئلون الناس إلحافا ومن يتعفف وليس له بعض كفاية كان قاتل نفسه كما ذكرنا قيل دل على أن المسكين هو الذي له بعض الغنى','1457','2045','0');
INSERT INTO [t6227] VALUES (2167,'انه كان يقول اللهم أحيني مسكينا وتوفني','1457','2045','0');
INSERT INTO [t6227] VALUES (2168,'بعض الكفاية فدل على أن المسكين من له بعض الكفاية استدلوا بقوله جل جلاله أو مسكينا ذا متربة قالوا وصفه بشدة الحال والالتزاق بالتراب من البؤس والفاقة قلنا لم يقتصر فيه على اسم المسكين حتى قرنه بما دل','1458','2045','0');
INSERT INTO [t6227] VALUES (2169,'مسألة','1460','2045','0');
INSERT INTO [t6227] VALUES (2170,'لمعاذ حين بعثه إلى اليمن ومنه فإذا فعلوا فأخبرهم أن الله قد فرض عليهم زكاة تؤخذ من أغنيائهم فترد على فقرائهم وعندهما في رواية فأخبرهم أن عليهم صدقة تؤخذ من أغنيائهم فترد على فقرائهم وعند أبي داود حدثنا نصر بن علي','1460','2045','0');
INSERT INTO [t6227] VALUES (2171,'ووضعناها حيث كنا نضعها على عهد رسول الله','1461','2045','0');
INSERT INTO [t6227] VALUES (2172,'ووجوه أصحابه صدقة طيء جئت بها أخرجه مسلم في الصحيح من حديث أبي عوانة عن المغيرة عن عامر عن الشعبي عنه وروي عن هشيم بن مجالد عن الشعبي بمعناه إلا أنه قال وإن أول صدقة بيضت وجوه أصحاب رسول الله','1461','2045','0');
INSERT INTO [t6227] VALUES (2173,'لعام أول كما أتيتك بها','1461','2045','0');
INSERT INTO [t6227] VALUES (2174,'هذه صدقات قومنا وإتيان عدي بن حاتم والزبرقان بن بدر وغيرهما بالصدقات إلى المدينة محمول على ما فضل عن أهاليهم في موضع الصدقة ويحتمل أن يكون من حولهم لم يسلموا بعد أو ارتدوا في زمان أبي بكر وكانت المدينة أقرب دارا أو نسبا','1462','2045','0');
INSERT INTO [t6227] VALUES (2175,'به من أخذ الجنس في الصدقات وأخذ الدينار أو عدله معافر ثياب اليمن في الجزية وأن يرد الصدقات على فقرائهم لا أن ينقلها إلى المهاجرين بالمدينة الذين أكثرهم أهل فيء لا أهل صدقة والله أعلم فإن قيل كيف يجوز حمله على الجزية وقد','1463','2045','0');
INSERT INTO [t6227] VALUES (2176,'مسألة','1466','2045','0');
INSERT INTO [t6227] VALUES (2177,'أنه قال لا تحل الصدقة إلا لأحد ثلاثة فذكر الرجل الذي يحمل حمالة ورجل أصابته جائحة فاجتاحت ماله فحلت له المسألة حتى يصيب قواما من عيش أو قال سدادا من عيش ثم يمسك وما سواهن من المسألة يا قبيصة سحت وقد تقدم قبل','1467','2045','0');
INSERT INTO [t6227] VALUES (2178,'قال من سأل منكم وله أوقية أو عدلها فقد سأل إلحافا وروينا عن سهل بن حنظلية عن النبي','1468','2045','0');
INSERT INTO [t6227] VALUES (2179,'علم ما يغني كل واحد منهم فجعل غناه به وذلك لأن الناس يختلفون في قدر كفايتهم فمنهم من يغنيه خمسون درهما لا يغنيه أقل ومنهم من يغنيه أربعون درهما لا يغنيه أقل منها ومنهم من له كسب يدر عليه كل يوم ما يغديه ويعشيه ولا عيال له','1468','2045','0');
INSERT INTO [t6227] VALUES (2180,'مسألة','1469','2045','0');
INSERT INTO [t6227] VALUES (2181,'عن ذلك فقال رسول الله','1469','2045','0');
INSERT INTO [t6227] VALUES (2182,'مسألة','1469','2045','0');
INSERT INTO [t6227] VALUES (2183,'وهو يقسم نعم الصدقة فسألناه فصعد فينا البصر','1469','2045','0');
INSERT INTO [t6227] VALUES (2184,'فذكر رجلا قال لأتصدقن الليلة بصدقة فخرج فوضع صدقته في يد غني الحديث وقال فيه فقيل له أما صدقتك فقد قبلت ولعل الغني يعتبر فينفق مما أعطاه الله عز وجل مخرج في الصحيحين وحديث معن بن يزيد كان أبي خرج بدنانير','1470','2045','0');
INSERT INTO [t6227] VALUES (2185,'مسألة','1471','2045','0');
INSERT INTO [t6227] VALUES (2186,'بأخي ليحنكه فرأيته يسم شاة وفي الصحيحين أيضا عنه قال رأيت في يد رسول الله','1471','2045','0');
INSERT INTO [t6227] VALUES (2187,'حمارا موسوم الوجه فأنكر ذلك قال فوالله لا أسمه إلا بأقصى شيء من','1471','2045','0');
INSERT INTO [t6227] VALUES (2188,'كتاب النكاح','1474','2045','0');
INSERT INTO [t6227] VALUES (2189,'مسألة','1474','2045','0');
INSERT INTO [t6227] VALUES (2190,'لا نكاح إلا بولي كذا رواه عبد الرحمن بن مهدي الإمام ووكيع ويحيى بن آدم وهاشم بن القاسم وعبيد الله بن موسى وعبد الله بن رجاء عن إسرائيل بن يونس وهو ثقة متفق على عدالته وقد حكموا لحديثه هذا بالصحة عن علي بن المديني','1475','2045','0');
INSERT INTO [t6227] VALUES (2191,'قال نعم هكذا روياه ولكنهم كانوا يحدثون الحديث فيرسلونه فإذا قيل لهم عمن فيسندونه قال البيهقي رحمه الله وقد أسنداه في رواية النعمان بن عبد السلام وغيره عنهما قال أبو عبد الله الحافظ النعمان بن','1476','2045','0');
INSERT INTO [t6227] VALUES (2192,'قال أبو عبد الله الحافظ لا أعلم خلافا بين أئمة هذا العلم في عدالة يونس بن أبي إسحاق وأن سماعه من أبي بردة مع أبيه صحيح ثم لم يختلف عن يونس في وصل هذا الإسناد ففيه الدليل الواضح أن الخلاف الذي وقع على أبيه فيه من جهة أصحابه لا من','1477','2045','0');
INSERT INTO [t6227] VALUES (2193,'وقد وصل هذا الحديث عن أبي بردة نفسه أبو حصين عثمان بن عاصم الثقفي وروي عن عبد الرزاق عن ابن جريج أن سليمان بن موسى أخبره أن ابن شهاب أخبره أن عروة بن الزبير أخبره أن عائشة','1478','2045','0');
INSERT INTO [t6227] VALUES (2194,'يقول أيما امرأة نكحت بغير إذن مواليها فنكاحها باطل فنكاحها باطل فنكاحها باطل ولها المهر بما أصابها فإن اشتجروا فالسلطان ولي من لا ولي له تابعه أبو عاصم الضحاك بن مخلد على ذكر سماع ابن جريج من سليمان وسماع سليمان من الزهري','1479','2045','0');
INSERT INTO [t6227] VALUES (2195,'أيما امرأة نكحت بغير إذن وليها فنكاحها باطل ثلاثا ورواه ابن وهب عن ابن جريج وقال فيه لا تنكح امرأة بغير أمر وليها فإن نكحت فنكاحها باطل ثلاث مرات فإن أصابها فلها مهر مثلها بما أصاب منها فإن اشتجروا فالسلطان ولي من','1480','2045','0');
INSERT INTO [t6227] VALUES (2196,'لا نكاح إلا بولي وروي عن الحسن بن سفيان حدثنا مسلم الحرمي حدثنا مخلد بن حسين عن هشام بن حسان عن ابن سيرين عن','1482','2045','0');
INSERT INTO [t6227] VALUES (2197,'لا تزوج المرأة المراة ولا تزوج المرأة نفسها إن البغية التي تزوج نفسها قال الحسن وسألت يحيى بن معين عن رواية مخلد عن هشام فقال ثقة فذكرت له هذا الحديث قال نعم قد كان عندنا شيخ يرفعه عن مخلد تابعه عبد السلام بن','1483','2045','0');
INSERT INTO [t6227] VALUES (2198,'أشد في النكاح بغير ولي من علي بن أبي طالب رضي الله عنه كان يضرب فيه وروى الشافعي رحمه الله عن مسلم عن ابن خيثم عن ابن جبير عن ابن عباس رضي الله عنهما قال لا نكاح إلا بولي مرشد وشاهدي عدل وروي عن شيبان عن قتادة عن','1485','2045','0');
INSERT INTO [t6227] VALUES (2199,'ميمونة رضي الله عنها ومالك بن سليمان متروك','1486','2045','0');
INSERT INTO [t6227] VALUES (2200,'قال الأيم أحق بنفسها من وليها والبكر تستأذن في نفسها وإذنها في صماتها إنما جعلها رسول الله','1488','2045','0');
INSERT INTO [t6227] VALUES (2201,'لتستأمر النساء في أبضعهن قالت قلت يا رسول الله إنهن يستحيين قال الأيم أحق بنفسها والبكر تستأمر وسكاتها إقرارها وروي أن عليا رضي الله عنه أجاز نكاح امرأة زوجتها أمها وأبوها غائب وقد روينا عنه رضي الله عنه أنه','1488','2045','0');
INSERT INTO [t6227] VALUES (2202,'مسألة','1490','2045','0');
INSERT INTO [t6227] VALUES (2203,'قال الأيم أحق بنفسها من وليها والبكر تستأذن في نفسها وإذنها صماتها ورواه بعضهم فقال والبكر تستأذن أبوها قال أبو داود أبوها ليس بمحفوظ','1490','2045','0');
INSERT INTO [t6227] VALUES (2204,'قال ليس للولي مع الثيب أمر واليتيمة تستأمر وصمتها إقرارها رواته ثقات وفي هذا الخبر دليل على أن المراد بالبكر التي تستأذن اليتيمة يزوجها غير الأب والجد بعد بلوغها بإذنها ويقنع منها بالسكوت حينئذ في الإذن في ظاهر المذهب','1491','2045','0');
INSERT INTO [t6227] VALUES (2205,'تستأمر اليتيمة في نفسها فإن سكتت فهو إذنها وإن أبت فلا جواز عليها وروي عن أبي بردة عن أبيه عن رسول الله','1491','2045','0');
INSERT INTO [t6227] VALUES (2206,'فرد نكاحها وفي الصحيحين عن عائشة رضي الله عنها أن رسول الله','1492','2045','0');
INSERT INTO [t6227] VALUES (2207,'وأنا ابنة سبع وبنى بي وأنا ابنة تسع قال الشافعي رحمه الله ولو كانت إذا بلغت أحق بنفسها أشبه أن لا يجوز ذلك عليها قبل بلوغها كما قلنا في المولود يقتل أبوه يحبس قاتله حتى يبلغ فيقيد أو يعفو واستدلوا بما روي عن جرير عن','1492','2045','0');
INSERT INTO [t6227] VALUES (2208,'فذكرت له أن أباها زوجها وهي كارهة فخيرها رسول الله','1493','2045','0');
INSERT INTO [t6227] VALUES (2209,'رد نكاح بكر وثيب أنكحهما أبوهما وهما كارهتان قال الدارقطني رحمه الله تعالى هذا وهم والصواب عن يحيى عن المهاجر عن عكرمة مرسل وهم فيه الذماري على الثوري وليس بقوي متصل','1493','2045','0');
INSERT INTO [t6227] VALUES (2210,'ففرق بينهما يقال إن هذا وهم والصواب عن الأوزاعي عن إبراهيم بن مرة عن عطاء عن النبي','1494','2045','0');
INSERT INTO [t6227] VALUES (2211,'مرسلا قال الدارقطني الصحيح مرسل وقول شعيب وهم حدثنا دعلج حدثنا الخضر بن داود حدثنا الأثرم قال ذكرت لأبي عبد الله يعني أحمد بن حنبل رحمه الله حديث شعيب بن إسحاق عن','1494','2045','0');
INSERT INTO [t6227] VALUES (2212,'فقال حدثناه أبو المغيرة عن الأوزاعي عن عطاء مرسلا مثل هذا عن جابر رضي الله عنه كالمنكر أن يكون وروى عن ابن جريج عن أبي الزبير عن جابر رضي الله عنه حديثا مرفوعا في رد نكاح امرأتين بكر وثيب زوجهما أبوهما بكره منهما وروي عن','1495','2045','0');
INSERT INTO [t6227] VALUES (2213,'فقال لا تنكحها وهي كارهة قال البيهقي رحمه الله تعالى عندي أن هذا وهم من القطعي أو غيره والصواب عن عبد الرحمن بن القاسم عن أبيه عن عبد الرحمن ومجمع ابني يزيد بن جارية عن خنساء بغير هذا اللفظ ثم ذكر حديث مالك عنه','1496','2045','0');
INSERT INTO [t6227] VALUES (2214,'مسألة','1497','2045','0');
INSERT INTO [t6227] VALUES (2215,'لا تنكح امرأة بغير أمر وليها فإن نكحت فنكاحها باطل وقد سبق ذكرنا له أيضا وعند أبي داود عن ابن عمر رضي الله عنهما عن النبي','1497','2045','0');
INSERT INTO [t6227] VALUES (2216,'إذا تزوج العبد بغير إذن سيده كان عاهرا','1497','2045','0');
INSERT INTO [t6227] VALUES (2217,'وأمهرها عنه أربعة آلاف درهم وبعث بها إلى رسول الله','1498','2045','0');
INSERT INTO [t6227] VALUES (2218,'قلنا بل كان النبي','1498','2045','0');
INSERT INTO [t6227] VALUES (2219,'فاذكري ذلك له فجاء نبي الله','1499','2045','0');
INSERT INTO [t6227] VALUES (2220,'مسألة','1500','2045','0');
INSERT INTO [t6227] VALUES (2221,'قال أيما امرأة نكحت بغير إذن وليها وشاهدي عدل فنكاحها باطل الحديث وفي رواية قالت قال رسول الله','1500','2045','0');
INSERT INTO [t6227] VALUES (2222,'لا نكاح إلا بولي وشاهدي عدل وروي عن الحسن أن رسول الله','1502','2045','0');
INSERT INTO [t6227] VALUES (2223,'فإن أكثر أهل العلم يقول به ونقول الفرق بين النكاح والسفاح الشهود وهو ثابت عن ابن عباس رضي الله عنهما وغيره من أصحاب النبي','1502','2045','0');
INSERT INTO [t6227] VALUES (2224,'قال لا نكاح إلا بولي وشاهدي عدل وابن محرر متروك','1502','2045','0');
INSERT INTO [t6227] VALUES (2225,'لا نكاح إلا بولي وخاطب وشاهدي عدل قال ابن عدي المغيرة بن موسى في نفسه ثقة وروي عن سعيد بن المسيب والحسن أن عمر رضي الله عنه قال لا نكاح إلا بولي وشاهدي عدل إسناده صحيح وابن المسيب رحمه الله راوية عمر رضي','1503','2045','0');
INSERT INTO [t6227] VALUES (2226,'لا نكاح إلا بولي وشهود قالوا رجل وامرأتان شهود وهذا لا يصح ومندل بن علي ضعيف وروي عن الحجاج بن أرطأة عن عطاء عن عمر رضي الله عنه قال أجاز رسول الله','1504','2045','0');
INSERT INTO [t6227] VALUES (2227,'مسألة','1504','2045','0');
INSERT INTO [t6227] VALUES (2228,'لا نكاح إلا بإذن ولي مرشد أو سلطان ورواه الطبراني أيضا عن ابن حنبل ومحمد بن عبدوس السراج عن القواريري قال الطبراني لم يرو هذا الحديث عن سفيان مرفوعا إلا بشر وابن مهدي والحربي تفرد به القواريري عبيد الله بن','1505','2045','0');
INSERT INTO [t6227] VALUES (2229,'لا نكاح إلا بولي وشاهدي عدل فإن أنكحها ولي مسخوط عليه فنكاحها باطل وروى الشافعي رحمه الله عن مسلم بن خالد وسعيد بن سالم عن ابن جريج عن عبد الله بن عثمان بن خثيم عن سعيد عن مجاهد عن ابن عباس رضي الله عنهما قال لا نكاح إلا','1506','2045','0');
INSERT INTO [t6227] VALUES (2230,'ولا يزوج البكر الصغيرة إلا أبوها أو جدها وقال أبو حنيفة رحمه الله يزوجها الأخ وابن الأخ والعم وابن العم ومن هو ولي تزويجها بعد بلوغها وكذا عنده الثيب الصغيرة وعندنا','1506','2045','0');
INSERT INTO [t6227] VALUES (2231,'الأيم أحق بنفسها من وليها وقوله تستأمر اليتيمة في نفسها وقوله لا تنكح الثيب حتى تستأمر ولا البكر حتى تستأذن وكل ذلك قد ذكرناه فأغنى عن إعادته واستدل أصحابنا أيضا بما روي عن ابن إسحاق حدثني عمر بن','1507','2045','0');
INSERT INTO [t6227] VALUES (2232,'هي يتيمة ولا تنكح إلا بأذنها فانتزعت مني والله بعدما ملكتها فزوجوها المغيرة تابعه ابن أبي ذئب عن عمر بن حسين مختصرا ورويناه بإسناده في السنن الكبرى ولنا أيضا حديث محمد بن عمرو عن أبي سلمة عن أبي هريرة وحديث أبي بردة عن','1508','2045','0');
INSERT INTO [t6227] VALUES (2233,'يعني في عمرة القضية خرج بها علي بن أبي طالب رضي الله عنه وقال النبي','1509','2045','0');
INSERT INTO [t6227] VALUES (2234,'سلمة بن أبي سلمة فكان النبي','1509','2045','0');
INSERT INTO [t6227] VALUES (2235,'فإنه كان','1510','2045','0');
INSERT INTO [t6227] VALUES (2236,'مسألة','1510','2045','0');
INSERT INTO [t6227] VALUES (2237,'لا نكاح إلا بولي وخاطب وشاهدي عدل رواه بعضهم من حديث أبي سلمة عن أبي هريرة مرفوعا أيضا لا يحل نكاح إلا بأربعة ولي ومنكح وخاطب وشاهدين بإسناد ضعيف','1510','2045','0');
INSERT INTO [t6227] VALUES (2238,'مسألة','1512','2045','0');
INSERT INTO [t6227] VALUES (2239,'من أصابته مصيبة فليقل إنا لله وإنا إليه راجعون اللهم عندك أحتسب مصيبتي فأجرني فيها وأبدلني بها خيرا منها قلت في نفسي ومن خير من أبي سلمة ثم قلتها فلما انقضت عدتها بعث إليها رسول الله','1512','2045','0');
INSERT INTO [t6227] VALUES (2240,'فزوجه قال أصحابنا نكاح النبي','1512','2045','0');
INSERT INTO [t6227] VALUES (2241,'إنما يجعل ولايتها إليه فزوجها منه هذا وعمر بن أبي سلمة كان ابن عمها من البعد وكان عصبة لها','1512','2045','0');
INSERT INTO [t6227] VALUES (2242,'تزوج أم سلمة بعد وقعة بدر وأنه','1513','2045','0');
INSERT INTO [t6227] VALUES (2243,'مسألة','1513','2045','0');
INSERT INTO [t6227] VALUES (2244,'تخيروا لنطفكم وأنكحوا الأكفاء وأنكحوا إليهم والله اعلم','1514','2045','0');
INSERT INTO [t6227] VALUES (2245,'ولا يعقد النكاح بلفظ الهبة والبيع وما أشبههما من ألفاظ عقود التمليك وقال أبو حنيفة رحمه الله ينعقد لنا من الكتاب العزيز قول الله عز وجل وامرأة مؤمنة إن وهبت نفسها للنبي إن اراد النبي أن يستنكحها خالصة لك من دون','1514','2045','0');
INSERT INTO [t6227] VALUES (2246,'قال فاتقوا الله في النساء فإنكم','1514','2045','0');
INSERT INTO [t6227] VALUES (2247,'فوهبت نفسها له المتفق على صحته وفيه في رواية قتيبة عن يعقوب بن عبد الرحمن وعبد العزيز بن أبي حازم عن أبي حازم عن سهل بن سعد رضي الله عنه أنه قال للرجل الذي زوجه بها اذهب فقد زوجتكها بما معك من القرآن أخرجاه عنه وأخرجا','1515','2045','0');
INSERT INTO [t6227] VALUES (2248,'قد ملكتكها إلا ويروي عن ذلك الثقة من وجه آخر قد ملكتها بكاف واحد والله أعلم وبالإجماع لا ينعقد النكاح بقوله ملكتها فدل أن قوله ملكتها أو ملكتكها إن كان محفوظا صدر بعد لفظ التزويج خبرا عن ملكه بضعها بالتزويج','1516','2045','0');
INSERT INTO [t6227] VALUES (2249,'والزنا لا يحرم الحلال ولا يوقع تحريم المصاهرة وقال العراقيون إنه يوقع تحريم المصاهرة وبناء المسألة لنا على كتاب الله تعالى والمعاني وقد صح ذلك عن عبد الله بن عباس رضي الله عنهما ولا يصح عن أحد من الصحابة رضي الله عنهم','1516','2045','0');
INSERT INTO [t6227] VALUES (2250,'من وجه ليس بساقط عن ابن عمر رضي الله عنهما عنه','1517','2045','0');
INSERT INTO [t6227] VALUES (2251,'إذا نظر الرجل إلى فرج المرأة حرمت عليه أمها وابنتها وروايته في موضع آخر عن أم هانئ وهذا منقطع بين الحجاج وأم هانئ أو بين ابن هاني والنبي','1518','2045','0');
INSERT INTO [t6227] VALUES (2252,'مسألة','1519','2045','0');
INSERT INTO [t6227] VALUES (2253,'مسألة','1521','2045','0');
INSERT INTO [t6227] VALUES (2254,'أمسك أربعا وفارق سائرهن رواه الشافعي رحمه الله عن الثقة قال الربيع أحسبه إسماعيل بن إبراهيم عنه وروي عن سعيد بن أبي عروبة عنه وتابعهما على روايته عن معمر من البصريين يزيد بن زريع ومحمد بن جعفر غندر وهؤلاء','1522','2045','0');
INSERT INTO [t6227] VALUES (2255,'فصح الحديث بذلك وقد روي عن أيوب بن أبي تميمة السختياني عن نافع وسالم عن ابن عمر رضي الله عنهما متصلا رواه عنه سرار بن مجشر قال أبو علي الحافظ تفرد به سرار وهو بصري ثقة قال أبو عبد الله رواة هذا الحديث كلهم ثقات','1523','2045','0');
INSERT INTO [t6227] VALUES (2256,'استفسر واستفصل حال عقودهم كانت قبل التحريم أو بعده والله أعلم','1524','2045','0');
INSERT INTO [t6227] VALUES (2257,'وإذا أسلم أحدهما بعد الدخول توقف النكاح على انقضاء العدة وقال العراقيون إذا اختلف بينهما الدين والدار انقطع النكاح بينهما قال الشافعي رحمه الله فإن قال قائل ما دل على ذلك قيل له أسلم أبو سفيان بن حرب رضي الله عنه بمر','1524','2045','0');
INSERT INTO [t6227] VALUES (2258,'بمكة وهرب عكرمة إلى اليمن وهي دار حرب وصفوان يريد اليمن وهي دار حرب ثم رجع','1525','2045','0');
INSERT INTO [t6227] VALUES (2259,'وشهد حنينا والطائف مشركا وامرأته مسلمة واستقرا على النكاح يعني حين أسلم صفوان قال ابن شهاب وكان بين إسلام صفوان وامرأته نحوا من شهر وروى مالك رحمه الله عنه أن أم حكيم بنت الحارث بن هشام وكانت تحت عكرمة بن أبي جهل','1526','2045','0');
INSERT INTO [t6227] VALUES (2260,'وثب إليه فرحا وما عليه رداؤه حتى بايعه فثبتا على نكاحهما ذلك','1526','2045','0');
INSERT INTO [t6227] VALUES (2261,'والمؤمنين كانوا مشركي أهل حرب يقاتلهم ويقاتلونه ومشركي أهل عهد لا يقاتلهم ولا يقاتلونه فكان إذا هاجرت امرأة من الحرب لم تخطب حتى تحيض وتطهر فإذا طهرت حل لها النكاح فإن هاجر زوجها قبل أن تنكح ردت إليه وهذا يدل على أن','1527','2045','0');
INSERT INTO [t6227] VALUES (2262,'رد ابنته إلى أبي العاص بمهر جديد ونكاح جديد فقد قال الدارقطني رحمه الله هذا لا يثبت وحجاج لا يحتج به والصواب حديث ابن عباس وبلغني عن أبي عيسى الترمذي أنه قال سألت عنه البخاري فقال حديث ابن عباس في هذا الباب أصح من حديث عمرو','1528','2045','0');
INSERT INTO [t6227] VALUES (2263,'على أبي العاص بأن قال علم عبد الله بن عمرو بتحريم الله جل جلاله رجوع المؤمنات إلى الكفار فلم يكن ذلك عنده ألا بنكاح جديد أما ابن عباس فلم يعلم بتحريم الله المؤمنات إلى الكفار حين علم برد زينب على أبي العاص رضوان الله عليهما','1529','2045','0');
INSERT INTO [t6227] VALUES (2264,'ابنته على أبي العاص وإن اشتبه ذلك عليه في وقت نزولها أفيشتبه ذلك عليه حيث روى هذا الخبر بعد وفاة رسول الله','1529','2045','0');
INSERT INTO [t6227] VALUES (2265,'مسألة','1530','2045','0');
INSERT INTO [t6227] VALUES (2266,'نهى عن الشغار والشغار أن يزوج الرجل ابنته على أن يزوجه ابنته وليس بينهما صداق وأخرجه مسلم أيضا عن جابر وعن أبي هريرة رضي الله عنهما','1531','2045','0');
INSERT INTO [t6227] VALUES (2267,'وروي بإسناد رواته ثقات عن أنس رضي الله عنه عن النبي','1532','2045','0');
INSERT INTO [t6227] VALUES (2268,'في حديث وائل بن حجر والله أعلم','1532','2045','0');
INSERT INTO [t6227] VALUES (2269,'والمنكوحة ترد بالعيوب الخمسة وقال أبو حنيفة','1532','2045','0');
INSERT INTO [t6227] VALUES (2270,'أنا قد بايعناك فارجع أخرجه مسلم في الصحيح وأخرجه البخاري عن أبي هريرة رضي الله عنه قال قال رسول الله','1535','2045','0');
INSERT INTO [t6227] VALUES (2271,'لا يورد ممرض على مصح وفي ذلك دلالة على أن قوله لا عدوى أراد به على الوجه','1535','2045','0');
INSERT INTO [t6227] VALUES (2272,'أخذ بيد مجذوم فوضعها في قصعة فقال كل بسم الله وتوكلا عليه ففيه بيان توهم لحوق ضرر بمخالطته لكنه احتمله ثقة بالله وتوكلا عليه والله أعلم','1536','2045','0');
INSERT INTO [t6227] VALUES (2273,'وإذا أعتقت الأمة تحت حر فلا خيار لها في فسخ النكاح وقال أبو حنيفة رحمه الله لها خيار الفسخ في صحيح مسلم عن عبد الرحمن بن القاسم عن أبيه عن عائشة رضي الله عنها أنها اشترت بريرة من أناس من الأنصار واشترطوا الولاء فقال رسول','1536','2045','0');
INSERT INTO [t6227] VALUES (2274,'وكان زوجها عبدا','1536','2045','0');
INSERT INTO [t6227] VALUES (2275,'ولو كان حرا ما خيرها وعند أبي داود عنها أن بريرة عتقت وهي عند مغيث عبد لآل أبي أحمد فخيرها رسول الله','1537','2045','0');
INSERT INTO [t6227] VALUES (2276,'إن أعتقتهما فابدئي بالرجل قبل المرأة يشبه أن يكون إنما أراد بالبداءة','1538','2045','0');
INSERT INTO [t6227] VALUES (2277,'كتاب الصداق','1540','2045','0');
INSERT INTO [t6227] VALUES (2278,'مسألة','1540','2045','0');
INSERT INTO [t6227] VALUES (2279,'وبه أثر صفرة فسأله رسول الله','1540','2045','0');
INSERT INTO [t6227] VALUES (2280,'كم سقت إليها قال زنة نواة من ذهب فقال له رسول الله','1540','2045','0');
INSERT INTO [t6227] VALUES (2281,'وأنه','1541','2045','0');
INSERT INTO [t6227] VALUES (2282,'قد زوجتكها بما معك من القرآن بعد ان قال له التمس ولو خاتما من حديد لتصدقها إياه وعند أبي داود عن جابر رضي الله عنه أن النبي','1541','2045','0');
INSERT INTO [t6227] VALUES (2283,'وأبي بكر رضي الله عنه حتى نهانا عمر رضي الله عنه في شأن عمرو بن حريث فقد مضت الدلالة عن رسول الله','1541','2045','0');
INSERT INTO [t6227] VALUES (2284,'عن صداق النساء فقال هو ما اصطلح عليه أهلوهم واستدلوا بما روى داود بن يزيد الأودي عن الشعبي عن علي رضي الله عنه لا صداق دون عشرة دراهم داود بن يزيد ضعيف في الحديث وهذا مما أنكر عليه قال أحمد بن حنبل لقن غياث بن','1543','2045','0');
INSERT INTO [t6227] VALUES (2285,'مسألة','1545','2045','0');
INSERT INTO [t6227] VALUES (2286,'فأخبره بما كان فقال رسول الله','1545','2045','0');
INSERT INTO [t6227] VALUES (2287,'الرجل على ما معه من القرآن في الصحيحين وفي رواية عند مسلم أنه','1546','2045','0');
INSERT INTO [t6227] VALUES (2288,'فقالت يا رسول الله رأ في رأيك الحديث فقال رسول الله','1546','2045','0');
INSERT INTO [t6227] VALUES (2289,'فلأسألنه فأتيته فقلت يا رسول الله رجل أهدى إلي قوسا ممن كنت أعلمه الكتاب','1546','2045','0');
INSERT INTO [t6227] VALUES (2290,'إن أحق ما أخذتم عليه أجرا كتاب الله عز وجل وابن عباس رضي الله عنهما من أواخر من حمل الحديث عن رسول الله','1547','2045','0');
INSERT INTO [t6227] VALUES (2291,'فذكر الحديث وقال ونهى عن التعليم بالأجر ونهى عن الأذان بالأجر ونهى عن الإمامة بالأجرة وهذا منقطع','1549','2045','0');
INSERT INTO [t6227] VALUES (2292,'فداءهم أن يعملوا أولاد الأنصار الكتابة قال فجاء غلام من الأنصار يبكي يوما إلى أبيه فقال له ابوه ما شأنك قال ضربني معلمي قال الخبيث يطلب بدحل بدر والله لا تأتينه أبدا وهذا يعلم الخير والله أعلم','1550','2045','0');
INSERT INTO [t6227] VALUES (2293,'وإن مات زوج المفوضة قبل فرض مهرها والدخول بها لم','1550','2045','0');
INSERT INTO [t6227] VALUES (2294,'قضى في بروع بنت واشق وروي عن علقمة قال أتى عبد الله في امرأة توفي عنها زوجها ولم يفرض لها صداقا ولم يدخل بها فترددوا إليه ولم يزالوا به حتى قال إني سأقول برأيي لها صداق نسائها لا وكس ولا شطط وعليها العدة','1552','2045','0');
INSERT INTO [t6227] VALUES (2295,'بأبي وأمي أنه قضى في بروع بنت واشق ونكحت بغير مهر فمات زوجها فقضى لها بمهر نسائها وقضى لها بالميراث فإن كان يثبت عن النبي','1553','2045','0');
INSERT INTO [t6227] VALUES (2296,'ولا في القياس ولا في شيء في قوله إلا طاعة الله بالتسليم له وإن كان لا يثبت عن النبي','1553','2045','0');
INSERT INTO [t6227] VALUES (2297,'لا يوهن الحديث فإن أسانيد هذه الروايات صحيحة وفي بعضها إن جماعة من أشجع شهدوا بذلك فبعضهم سمى هذا وبعضهم سمى آخر وكلهم ثقة ولولا الثقة بمن رواه عن النبي','1554','2045','0');
INSERT INTO [t6227] VALUES (2298,'مسأله','1554','2045','0');
INSERT INTO [t6227] VALUES (2299,'ولي عقدة النكاح الزوج وابن لهيعة ضعيف لا يحتج به والله أعلم','1556','2045','0');
INSERT INTO [t6227] VALUES (2300,'مسألة','1557','2045','0');
INSERT INTO [t6227] VALUES (2301,'قال من كشف امرأة فنظر إلى عورتها فقد وجب الصداق زاد ابن لهيعة دخل بها أو لم يدخل وابن صالح وابن لهيعة لا يحتج بحديثهما فإن سلم منهما فهو مرسل وروى الشافعي عن مالك عن ابن سعيد عن ابن المسيب أن عمر بن الخطاب رضي','1558','2045','0');
INSERT INTO [t6227] VALUES (2302,'مسألة','1560','2045','0');
INSERT INTO [t6227] VALUES (2303,'فقال لزوجها متعها قال لا أجد ما أمتعها قال فإنه لا بد من المتاع قال متعها ولو نصف صاع من تمر وفاطمة هذه هي بنت قيس وقصتها مشهورة في','1563','2045','0');
INSERT INTO [t6227] VALUES (2304,'كتاب القسم','1566','2045','0');
INSERT INTO [t6227] VALUES (2305,'مسألة','1566','2045','0');
INSERT INTO [t6227] VALUES (2306,'لما تزوج أم سلمة رضي الله عنها أقام عندها ثلاثا ثم قال ليس بك على أهلك هوان إن شئت سبعت لك وإن سبعت لك سبعت لنسائي','1566','2045','0');
INSERT INTO [t6227] VALUES (2307,'حين تزوج أم سلمة رضي الله عنها واصبحت عنده قال أما ليس بك على أهلك هوان إن شئت سبعت عندك وسبعت عندهن وإن شئت ثلثت عندك ودرت فقالت ثلث وروي عن أنس رضي الله عنه قال قال رسول الله','1567','2045','0');
INSERT INTO [t6227] VALUES (2308,'في التثليث كقوله في التسبيع فلما قال','1567','2045','0');
INSERT INTO [t6227] VALUES (2309,'مسألة','1567','2045','0');
INSERT INTO [t6227] VALUES (2310,'إذا أراد سفرا اقرع بين نسائه وأيتهن خرج سهمها خرج بها والله أعلم','1568','2045','0');
INSERT INTO [t6227] VALUES (2311,'كتاب الخلع','1570','2045','0');
INSERT INTO [t6227] VALUES (2312,'مسألة','1570','2045','0');
INSERT INTO [t6227] VALUES (2313,'الخلع تطليقة هذا منقطع وإبراهيم بن يزيد ليس بقوي وروى الشافعي رحمه الله عن مالك عن هشام بن عروة عن أبيه عن جهمان مولى الأسلميين عن أم بكرة الأسلمية أنها اختلعت من زوجها عبد الله بن أسيد ثم أتيا عثمان بن عفان رضي الله عنه في ذلك','1571','2045','0');
INSERT INTO [t6227] VALUES (2314,'جعل الخلع تطليقه بائنة وعباد لا يحتج به وكيف يصح هذا ومذهب عكرمة وابن عباس خلافه وإن يثبت فأراد به إذا نوى طلاقا أو ذكره والقصد منه قطع الرجعة والله أعلم','1572','2045','0');
INSERT INTO [t6227] VALUES (2315,'المختلعة لا يلحقها الطلاق وقال العراقيون يلحقها صريح الطلاق دون كنايته','1572','2045','0');
INSERT INTO [t6227] VALUES (2316,'مسألة','1574','2045','0');
INSERT INTO [t6227] VALUES (2317,'لا طلاق من قبل نكاح وروي عن جابر رضي الله عنه قال سمعت رسول الله','1575','2045','0');
INSERT INTO [t6227] VALUES (2318,'لا طلاق إلا بعد نكاح قال ابن صاعد هذا حديث غريب لا أعرف له علة وروي هذا الحديث عن أبي بكر الصديق وعلي بن أبي طالب وابن عباس ومعاذ وزيد وأبي سعيد وعمران وأبي موسى وأبي هريرة والمسور وعائشة رضي الله','1575','2045','0');
INSERT INTO [t6227] VALUES (2319,'بهذا النفي عدم وقوعه بعد الملك حتى يفيد الخبر وبالله التوفيق والله أعلم بالصواب','1577','2045','0');
INSERT INTO [t6227] VALUES (2320,'كتاب الطلاق والرجعة والإيلاء','1578','2045','0');
INSERT INTO [t6227] VALUES (2321,'مسألة','1578','2045','0');
INSERT INTO [t6227] VALUES (2322,'قال فقال ليراجعها فإذا طهرت فليطلقها قال يعني ابن سيرين فقلت له يعني ابن عمر يحتسب بها قال فمه وهو في الصحيحين أيضا من حديث يونس بن جبير عن ابن عمر بمعناه وقال فقلت لابن عمر رضي الله عنهما فاحتسب بها قال','1578','2045','0');
INSERT INTO [t6227] VALUES (2323,'عن ذلك فقال مره فليراجعها فإذا طهرت فليطلقها أو يمسك قال وقرأ يا أيها النبي إذا طلقتم النساء فطلقوهن لقبل عدتهن وكان مجاهد يقرؤها هكذا','1579','2045','0');
INSERT INTO [t6227] VALUES (2324,'فتغيظ فيه رسول الله','1580','2045','0');
INSERT INTO [t6227] VALUES (2325,'ليراجعها ثم يمسكها حتى تطهر ثم تحيض فتطهر فإن بدا له أن يطلقها فليطلقها طاهرا قبل أن يمسها فتلك العدة كما أمر الله عز وجل وفي رواية أخرى عندهما عن نافع عنه بمعناه وزاد فتلك العدة التي أمر الله أن تطلق لها النساء','1580','2045','0');
INSERT INTO [t6227] VALUES (2326,'أمره أن يراجعها حتى تطهر ثم إن شاء طلق وإن شاء أمسك فإن كانت الرواية الأخرى عن سالم ثم عن نافع وابن دينار في أمره بأن يراجعها حتى تطهر ثم تحيض ثم تطهر محفوظة قال الشافعي رحمه الله هذا يحتمل أن يكون إنما أراد بذلك','1581','2045','0');
INSERT INTO [t6227] VALUES (2327,'وفيهما أيضا حديث عائشة رضي الله عنها جاءت امرأة رفاعة إلى النبي','1582','2045','0');
INSERT INTO [t6227] VALUES (2328,'أتحل للأول قال لا حتى يذوق عسيلتها كما ذاق الأول وفي صحيح مسلم عن عروة عن فاطمة بنت قيس قالت يا رسول الله زوجي طلقني ثلاثا الحديث فقد ذكر عنده','1582','2045','0');
INSERT INTO [t6227] VALUES (2329,'بعث مبينا فلما لم يفعل ثبت بذلك أنه مباح وليس بحرام','1582','2045','0');
INSERT INTO [t6227] VALUES (2330,'مسألة','1584','2045','0');
INSERT INTO [t6227] VALUES (2331,'فقال يا رسول الله إني طلقت امرأتي سهيمة البتة ووالله ما أردت إلا واحدة فقال رسول الله','1585','2045','0');
INSERT INTO [t6227] VALUES (2332,'فطلقها الثانية في زمان عمر والثالثة في زمان عثمان رضي الله عنهم وعند أبي داود عن محمد بن يونس عن عبد الله بن الزبير عن الشافعي بنحوه وروى سفيان عن حماد عن إبراهيم عن عمر بن الخطاب رضي الله عنه أنه كان يقول في الخلية','1585','2045','0');
INSERT INTO [t6227] VALUES (2333,'مسألة','1586','2045','0');
INSERT INTO [t6227] VALUES (2334,'إنما الأعمال بالنيات وإنما لكل امرئ ما نوى وحديث ركانة الذي تقدم وروي عن أبي هريرة رضي الله عنه عن رسول الله','1586','2045','0');
INSERT INTO [t6227] VALUES (2335,'مسألة','1586','2045','0');
INSERT INTO [t6227] VALUES (2336,'فاخترناه فلم يكن ذلك طلاقا وروي عن أبي إسحاق قال دخلت أنا وأبو السفر على أبي جعفر فسألته عن التخيير عن رجل خير امرأته فاختارت نفسها قال فليست بشيء قلنا فإن أناسا يروون عن علي رضي الله عنه أنه قال إن اختارت زوجها','1588','2045','0');
INSERT INTO [t6227] VALUES (2337,'مسألة','1589','2045','0');
INSERT INTO [t6227] VALUES (2338,'والمؤمنين إذا حرموا شيئا مما أحل الله تعالى أن يكفروا عن أيمانهم بإطعام عشرة مساكين أو كسوتهم أو تحرير رقبة وليس في ذلك الطلاق وفي الصحيحين عنه قال إذا حرم الرجل عليه امرأته فهي يمين يكفرها وقال لقد كان لكم في رسول الله','1590','2045','0');
INSERT INTO [t6227] VALUES (2339,'مسالة','1592','2045','0');
INSERT INTO [t6227] VALUES (2340,'كانت له أمة يطؤها فلم تزل به حفصة حتى جعلها على نفسه حراما فأنزل الله عز وجل هذه الآية يا أيها النبي لم تحرم ما أحل الله لك تبتغي مرضات أزواجك الآية قال أبو عبد الله هذا حديث صحيح فالآية في قول أكثر المفسرين نزلت في','1592','2045','0');
INSERT INTO [t6227] VALUES (2341,'آلى وحرم فوجوب الكفارة تعلق بالإيلاء لا بالتحريم وروي ذلك عنه عن عائشة رضي الله عنها والمرسل أصح وإلى مثل ذلك ذهب قتادة وهو أن النبي','1593','2045','0');
INSERT INTO [t6227] VALUES (2342,'حرم أم إبراهيم قال أنت علي حرام والله لا أمسك فأنزل الله عز وجل في ذلك ما أنزل وأما حديث عائشة في الصحيحين أن رسول الله','1593','2045','0');
INSERT INTO [t6227] VALUES (2343,'يشرب من شراب عند سودة من العسل فدخل على عائشة رضي الله عنها فقالت إني أجد منك ريحا ثم دخل على حفصة رضي الله عنها فقالت إني أجد منك ريحا فقال إني أراه من شراب شربته عند سودة والله لا أشربه فنزلت هذه الآية يا','1594','2045','0');
INSERT INTO [t6227] VALUES (2344,'مسألة','1595','2045','0');
INSERT INTO [t6227] VALUES (2345,'تجاوز الله عن أمتي الخطأ والنسيان وما استكرهوا عليه قال أبو عبد الله الحاكم هذا حديث صحيح وروي عن مالك عن نافع عن ابن عمر رضي الله عنهما أن النبي','1596','2045','0');
INSERT INTO [t6227] VALUES (2346,'وضع عن أمتي الخطأ والنسيان وما استكرهوا عليه قال أبو عبد الله تفرد به الوليد بن مسلم عن مالك وهو غريب صحيح وروي عن عائشة رضي الله عنها عن رسول الله','1596','2045','0');
INSERT INTO [t6227] VALUES (2347,'فليس فيه أن المشركين أكرهوهما على اليمين والعهد وكان هذا في ابتداء الإسلام قبل ثبوت الأحكام واليوم فلو حلف على ترك قتال المشركين فإنا نأمره بأن يحنث نفسه ويقاتل المشركين فإن قتالهم خير مما حلف عليه من تركه واستدلوا','1601','2045','0');
INSERT INTO [t6227] VALUES (2348,'مسالة','1601','2045','0');
INSERT INTO [t6227] VALUES (2349,'قال فيم أطهرك فقال من الزنا فسأل النبي','1604','2045','0');
INSERT INTO [t6227] VALUES (2350,'لم يسنه وفي الصحيحين عن أنس رضي الله عنه أن النبي','1607','2045','0');
INSERT INTO [t6227] VALUES (2351,'ثلاث جدهن جد وهزلهن جد ومن زال عقله بسكر أو غيره لم يكن له جد ولا هزل فلا يقع طلاقه كالمجنون وكذلك قول علي رضي الله عنه كل الطلاق جائز إلا طلاق','1607','2045','0');
INSERT INTO [t6227] VALUES (2352,'مسألة','1608','2045','0');
INSERT INTO [t6227] VALUES (2353,'كتب إلى شريح بخمس جاء بهن يعني عروة وإذا طلق الرجل امرأته ثلاثا وهو مريض ترثه ما دامت في عدتها فالرواية لهذا الحديث كما ترى وأما حديث أشعث عن الشعبي أن عثمان رضي الله عنه طلق امرأته وهو محصور ثلاثا فورثها علي رضي','1611','2045','0');
INSERT INTO [t6227] VALUES (2354,'وإذا طلق امرأته طلقة أو طلقتين ثم عادت إليه بعد ما أصابت','1611','2045','0');
INSERT INTO [t6227] VALUES (2355,'مسألة','1614','2045','0');
INSERT INTO [t6227] VALUES (2356,'أو عبد مكاتب تحته امرأة حرة فطلقها اثنتين ثم أراد أن يراجعها فأمره أزواج النبي','1615','2045','0');
INSERT INTO [t6227] VALUES (2357,'مسألة','1619','2045','0');
INSERT INTO [t6227] VALUES (2358,'مسألة','1621','2045','0');
INSERT INTO [t6227] VALUES (2359,'كلهم يقول يوقف المولي قال الشافعي رحمه الله فأقل بضعة عشر أن يكونوا ثلاثة عشر وهو يقول من الأنصار وروي عن سهيل بن أبي صالح عن أبيه قال سألت اثني','1621','2045','0');
INSERT INTO [t6227] VALUES (2360,'عن الرجل يولي قالوا ليس عليه شيء حتى تمضي أربعة أشهر فيوقف فإن فاء وإلا طلق وروى الشافعي رحمه الله عن سفيان عن مسعر عن حبيب بن أبي ثابت عن طاووس أن عثمان بن عفان رضي الله عنه كان يوقف المولي روى القاسم بن محمد أن','1622','2045','0');
INSERT INTO [t6227] VALUES (2361,'كتاب الظهار','1626','2045','0');
INSERT INTO [t6227] VALUES (2362,'مسألة','1626','2045','0');
INSERT INTO [t6227] VALUES (2363,'مسألة','1627','2045','0');
INSERT INTO [t6227] VALUES (2364,'قال إن الله تجاوز لأمتي ما لم تعمل أو تتكلم به مما حدثت به أنفسها وهذا دليل على أن لا اعتبار بالعزم على الوطء وذكر حديث خويلة وأوس واستفتائها النبي','1627','2045','0');
INSERT INTO [t6227] VALUES (2365,'فاسأليه الحديث وذكر حديثا عن ابن عباس رضي الله عنهما أن رجلا ظاهر من امرأته فرأى خلخالها في القمر فأعجبته فوقع عليها فأتى النبي','1628','2045','0');
INSERT INTO [t6227] VALUES (2366,'أمسك حتى تكفر رواه أبو داود بمعناه والله أعلم','1628','2045','0');
INSERT INTO [t6227] VALUES (2367,'مسألة','1629','2045','0');
INSERT INTO [t6227] VALUES (2368,'فقلت يا رسول الله أن جارية لي كانت ترعى غنما لي ففقدت شاة من الغنم فسألتها عنها فقالت أكلها الذئب فأسفت عليها وكنت من بني آدم فلطمت وجهها وعلي رقبة أفأعتقها فقال لها','1629','2045','0');
INSERT INTO [t6227] VALUES (2369,'أين الله فقالت في السماء فقال من أنا فقالت أنت رسول الله','1630','2045','0');
INSERT INTO [t6227] VALUES (2370,'مسألة','1633','2045','0');
INSERT INTO [t6227] VALUES (2371,'قال له فأطعم وسقا من تمر بين ستين مسكينا والله أعلم','1634','2045','0');
INSERT INTO [t6227] VALUES (2372,'وقدر ما يؤدي الواحد من المساكين من طعام الكفارة مد وقال العراقيون نصف صاع من بر أو صاع من شعير أو تمر روى الأوزاعي عن الزهري عن حميد بن عبد الرحمن عن أبي هريرة رضي الله عنه قال قال رجل يا رسول الله هلكت قال ويحك','1634','2045','0');
INSERT INTO [t6227] VALUES (2373,'بتمر فأعطاه إياه وهو قريب من خمسة عشر صاعا قال تصدق بهذا وروي عن نافع عن ابن عمر رضي الله عنهما أنه كان إذا كفر يمينه أطعم عشرة مساكين لكل مسكين مد وهو بالمد الأول وروي عن عكرمة عن ابن عباس رضي الله عنهما قال لكل','1635','2045','0');
INSERT INTO [t6227] VALUES (2374,'أعطاه خمسة عشر صاعا من شعير إطعام ستين مسكينا وهذا حديث مختلف فيه فتارة يقول ستون صاعا وتارة يقول ثلاثون صاعا وتارة خمسة عشر صاعا وأسانيدها ليست بالقوية ولا تقاوم حديث المجامع في شهر رمضان في الصحة وإذا','1636','2045','0');
INSERT INTO [t6227] VALUES (2375,'مسألة','1637','2045','0');
INSERT INTO [t6227] VALUES (2376,'كتاب اللعان','1638','2045','0');
INSERT INTO [t6227] VALUES (2377,'مسألة','1638','2045','0');
INSERT INTO [t6227] VALUES (2378,'بشريك بن سحماء فقال النبي','1638','2045','0');
INSERT INTO [t6227] VALUES (2379,'مسألة','1639','2045','0');
INSERT INTO [t6227] VALUES (2380,'يمينا فقال لولا الأيمان لكان لي ولها شأن وقال لهلال بن أمية رضي الله عنه احلف بالله ولا فرق بين الحر والعبد والمسلم والذمي في اليمين واستدلوا بما روي عن عثمان بن عبد الرحمن الزهري عن عمرو بن شعيب عن أبيه عن جده مرفوعا','1639','2045','0');
INSERT INTO [t6227] VALUES (2381,'أحكاما توافق أقاويلنا وتخالف أقاويلكم يرويها عنه الثقات ويسندها إلى النبي','1641','2045','0');
INSERT INTO [t6227] VALUES (2382,'خالفتم أكثرها فأنتم غير منصفين إن احتججتم بروايته وهو ممن لا يثبت روايته قال ولما ذكر الله تعالى اللعان على كل زوج جاز طلاقه ولزمه الفرض وكذلك زوجة لزمها الفرض','1641','2045','0');
INSERT INTO [t6227] VALUES (2383,'يا عتاب بن أسيد إني قد بعثتك إلى أهل مكة فذكر الحديث فيه أربعة ليس بينهم ملاعنة اليهودية والنصرانية تحت المسلم والعبد عند الحرة والحر عند الأمة وهذا الإسناد بهذا الحديث باطل ويحيى الإيلي أحاديثه غير','1642','2045','0');
INSERT INTO [t6227] VALUES (2384,'والفرقة تقع بلعان الزوج دون تفريق القاضي والتحريم الواقع به لا يرتفع بتكذيب الزوج الملاعن نفسه وقال العراقيون الفرقة لا تقع إلا بلعانها وتفريق القاضي وإذا كذب الملاعن نفسه ارتفع التحريم الواقع باللعان','1642','2045','0');
INSERT INTO [t6227] VALUES (2385,'قال ابن شهاب فكانت تلك سنة المتلاعنين كذا في رواية مالك عن الزهري عنه وروي عن الأوزاعي عن الزبيدي عن الزهري عنه في قصة المتلاعنين قال فتلاعنا ففرق رسول الله','1643','2045','0');
INSERT INTO [t6227] VALUES (2386,'فمضت السنة بعده في المتلاعنين قال أن يفرق بينهما ثم لا يجتمعان أبدا وفي الصحيحين عن ابن عباس رضي الله عنهما قال فرق رسول الله','1643','2045','0');
INSERT INTO [t6227] VALUES (2387,'قال المتلاعنان إذا تفرقا لا يجتمعان قال الشافعي رحمه الله وإذا أكمل الزوج الشهادة والالتعان فقد زال فراش امرأته ولا تحل له أبدا بحال وإن أكذب نفسه لم تعد إليه وإنما قلت هذا لأن رسول الله','1644','2045','0');
INSERT INTO [t6227] VALUES (2388,'إذا ألحق الولد بأمه أنه نفاه عن أبيه وإن نفيه عن أبيه بيمينه والتعانه لا بيمين أمه على كذبه ينفيه ولما قال','1644','2045','0');
INSERT INTO [t6227] VALUES (2389,'إلا أن تكذب نفسك أو تفعل كذا كما قال الله عز وجل في المطلق الثالثة حتى تنكح زوجا غيره وبسط الكلام فيه','1644','2045','0');
INSERT INTO [t6227] VALUES (2390,'وانتفى من ولدها ففرق رسول الله','1645','2045','0');
INSERT INTO [t6227] VALUES (2391,'أنه فرق بين المتلاعنين وتفريق النبي','1645','2045','0');
INSERT INTO [t6227] VALUES (2392,'مسألة','1646','2045','0');
INSERT INTO [t6227] VALUES (2393,'مسألة','1646','2045','0');
INSERT INTO [t6227] VALUES (2394,'مسألة','1647','2045','0');
INSERT INTO [t6227] VALUES (2395,'من مارية جاريته كاد يقع في نفس النبي','1647','2045','0');
INSERT INTO [t6227] VALUES (2396,'قال الولد للفراش وللعاهر الحجر وفي الصحيحين عن عائشة رضي الله عنها قالت اختصم سعد وعبد بن زمعة رضي الله عنهما في غلام فقال سعد هذا يا رسول الله ابن أخي عتبة عهد إلي أنه ابنه انظر إلى شبهه وقال عبد بن زمعة أخي','1647','2045','0');
INSERT INTO [t6227] VALUES (2397,'مسألة','1649','2045','0');
INSERT INTO [t6227] VALUES (2398,'كتاب العدد','1651','2045','0');
INSERT INTO [t6227] VALUES (2399,'مسألة','1651','2045','0');
INSERT INTO [t6227] VALUES (2400,'عن ذلك فقال له رسول الله','1651','2045','0');
INSERT INTO [t6227] VALUES (2401,'يا أيها النبي إذا طلقتم النساء فطلقوهن لقبل عدتهن قال الشافعي رحمه الله تعالى فأخبر رسول الله','1652','2045','0');
INSERT INTO [t6227] VALUES (2402,'فتلك العدة التي أمر الله أن تطلق لها النساء أي فيها كقولهم كتبت هذا الكتاب لخمس خلون من الشهر أي في وقت خلا فيه من','1652','2045','0');
INSERT INTO [t6227] VALUES (2403,'فشكت إليه الدم فقال لها رسول الله','1655','2045','0');
INSERT INTO [t6227] VALUES (2404,'أن تدع الصلاة أيام أقرائها وهذا وهم من ابن عيينة ليس هذا في حديث الحفاظ عن الزهري قال أبو داود لم يسمع قتادة عن عروة شيئا وذكر روايات في معناه في المستحاضة فقال ورواه قال البيهقي رحمه الله وشيء من هذه الروايات','1655','2045','0');
INSERT INTO [t6227] VALUES (2405,'أنهم قالوا هو أحق بها ما لم تغتسل من الحيضة الثالثة وذهب الشافعي رحمه الله في القديم إلى أن العراقيين خالفوا الإجماع في هذه المسألة لأن الذين قالوا إن الأقراء الحيض زعموا أنها لا تبرأ حتى تغتسل من الحيضة الثالثة ويحل لها الصلاة','1657','2045','0');
INSERT INTO [t6227] VALUES (2406,'مسألة','1658','2045','0');
INSERT INTO [t6227] VALUES (2407,'مسألة','1660','2045','0');
INSERT INTO [t6227] VALUES (2408,'يخصف نعله فجعل جبينه يعرق وجعل عرقه يتولد نورا فبهت فنظر إلى رسول الله','1661','2045','0');
INSERT INTO [t6227] VALUES (2409,'وقبل بين عيني وقال جزاك الله يا','1661','2045','0');
INSERT INTO [t6227] VALUES (2410,'ولم يقل إن غبر هذا لا يكون وروي عن الليث عن بكر بن عبد الله عن أم علقمة عن عائشة رضي الله عنها زوج النبي أنها سئلت عن الحامل ترى الدم أتصلي فقالت لا تصلي حتى يذهب عنها الدم وروي عن يحيى بن سعيد عن عمرة عن عائشة رضي','1662','2045','0');
INSERT INTO [t6227] VALUES (2411,'مسألة','1664','2045','0');
INSERT INTO [t6227] VALUES (2412,'مسألة','1665','2045','0');
INSERT INTO [t6227] VALUES (2413,'تسأله أن ترجع','1665','2045','0');
INSERT INTO [t6227] VALUES (2414,'أن أرجع إلى أهلي فإن زوجي لم يتركني في مسكن يملكه قالت فقال رسول الله','1666','2045','0');
INSERT INTO [t6227] VALUES (2415,'ولا يصح روى عن محبوب بن محرز التميمي عن أبي مالك النخعي عن عطاء عن أبي عبد الرحمن عن علي رضي الله عنه مرفوعا أمر المتوفى عنها زوجها أن تعتد في غير بيتها إن شاءت قال الدارقطني لم يسنده غير أبي مالك النخعي وهو ضعيف','1668','2045','0');
INSERT INTO [t6227] VALUES (2416,'مسألة','1670','2045','0');
INSERT INTO [t6227] VALUES (2417,'لا تحد المرأة فوق ثلاثة أيام إلا على زوجها فإنها تحد عليه أربعة أشهر وعشرا فخض','1670','2045','0');
INSERT INTO [t6227] VALUES (2418,'مسألة','1670','2045','0');
INSERT INTO [t6227] VALUES (2419,'يقول لا يحل لامرأة تؤمن بالله واليوم الآخر أن تحد على ميت فوق ثلاث ليال إلا على زوج أربعة أشهرا وعشرا','1670','2045','0');
INSERT INTO [t6227] VALUES (2420,'بمثله سواء والصغيرة داخلة فيه لأنها مؤمنة بالله تعالى وباليوم الآخر وروي عن أم سلمة رضي الله عنها زوج النبي','1671','2045','0');
INSERT INTO [t6227] VALUES (2421,'أنه قال المتوفى عنها زوجها لا تلبس المعصفر من الثياب ولا الممشق ولا الحلي ولا تختضب ولا تكتحل وليس فيه تفصيل بين مسلمة وذمية وصغيرة وروي عن ابن عمر رضي الله عنهما قال المتوفى عنها زوجها لا تكتحل ولا تتطيب ولا تختضب','1671','2045','0');
INSERT INTO [t6227] VALUES (2422,'مسألة','1672','2045','0');
INSERT INTO [t6227] VALUES (2423,'مسألة','1674','2045','0');
INSERT INTO [t6227] VALUES (2424,'مسألة','1675','2045','0');
INSERT INTO [t6227] VALUES (2425,'مسألة','1677','2045','0');
INSERT INTO [t6227] VALUES (2426,'في أم الولد إذا توفي عنها سيدها عدتها أربعة أشهر وعشرا قال الدارقطني قبيصة لم يسمع من عمرو والصواب لا تلبسوا علينا موقوفا','1677','2045','0');
INSERT INTO [t6227] VALUES (2427,'عدة أم الولد حيضة وروي عن سويد عن عبد العزيز وهو غير قوي عن سعيد عن عطاء أن مارية رضي الله عنها اعتدت بثلاث حيض وهو مرسل وسويد غير قوي والله أعلم ورواية الجماعة عن عطاء مذهبه دون الرواية وروي بإسناد فيه ضعف عن','1678','2045','0');
INSERT INTO [t6227] VALUES (2428,'وتزويج الأمة التي يطؤها لا يصح إلا بأن يستبرئها بحيضة وروي عن أبي حنيفة رحمه الله أنه قال يصح','1678','2045','0');
INSERT INTO [t6227] VALUES (2429,'أنه قال لا يحل لأحد يؤمن بالله واليوم الآخر أو من كان يؤمن بالله واليوم الآخر فلا يسقي ماءه ولد غيره وفي رواية لا يحل لامرئ يؤمن بالله واليوم الآخر أن يسقي ماءه زرع غيره ولا يحل لامرئ يؤمن بالله واليوم الآخر أن يقع على','1679','2045','0');
INSERT INTO [t6227] VALUES (2430,'كتاب الرضاع','1680','2045','0');
INSERT INTO [t6227] VALUES (2431,'مسألة','1680','2045','0');
INSERT INTO [t6227] VALUES (2432,'وهن مما يقرأ من القرآن وعنده أيضا عنها قالت قال رسول الله','1680','2045','0');
INSERT INTO [t6227] VALUES (2433,'لا تحرم المصة ولا المصتان أو الرضعة أو الرضعتان وفي صحيح البخاري عن عروة عن عائشة وأم سلمة رضي الله عنهما أن أبا حذيفة بن عتبة كان قد تبنى سالما وأنكحه بنت أخيه وهو مولى لامرأة من الأنصار كما تبنى رسول الله زيدا','1681','2045','0');
INSERT INTO [t6227] VALUES (2434,'وقلن لعائشة رضي الله عنها وما ندري لعلها كانت رخصة من رسول الله','1681','2045','0');
INSERT INTO [t6227] VALUES (2435,'فإنما الرضاعة من','1681','2045','0');
INSERT INTO [t6227] VALUES (2436,'في كتبها فلم يأذن له فيها وأما رضاعة الكبير فهى عند غير عائشة رضي الله عنها','1684','2045','0');
INSERT INTO [t6227] VALUES (2437,'مسألة','1685','2045','0');
INSERT INTO [t6227] VALUES (2438,'كتاب النفقات','1688','2045','0');
INSERT INTO [t6227] VALUES (2439,'مسألة','1688','2045','0');
INSERT INTO [t6227] VALUES (2440,'حث على الصدقة فجاء رجل فقال عندي دينار قال أنفقه على نفسك قال عندي آخر قال أنفقه على ولدك قال عندي آخر قال أنفقه على زوجتك قال عندي آخر قال أنفقه على خادمك قال عندي آخر','1688','2045','0');
INSERT INTO [t6227] VALUES (2441,'والإعسار بنفقة الزوجة يوجب لها خيار الفسخ للنكاح وقال','1688','2045','0');
INSERT INTO [t6227] VALUES (2442,'أنه قال خير الصدقة ما كان عن ظهر غنى واليد العليا خير من اليد السفلى وابدأ بمن تعول قال ومن أعول يا رسول الله قال امرأتك تقول أطعمني وإلا فارقني خادمك يقول أطعمني واستعملني ولدك يقول إلى من تتركني','1689','2045','0');
INSERT INTO [t6227] VALUES (2443,'مسألة','1690','2045','0');
INSERT INTO [t6227] VALUES (2444,'قال لها لا نفقة لك إلا أن تكوني حاملا','1690','2045','0');
INSERT INTO [t6227] VALUES (2445,'وروي عن ابن المسيب أنه إنما نقلها أنها استطالت على أحمائها بلسانها وفي صحيح البخاري عن القاسم وسليمان أن يحيى بن سعيد بن العاص طلق ابنة عبد الرحمن بن الحكم ألبتة فأنقلها عبد الرحمن فأرسلت عائشة رضي الله عنها إلى مروان وهو','1691','2045','0');
INSERT INTO [t6227] VALUES (2446,'لا سكنى لك ولا نفقة فقلت له ما تركنا من حديث فاطمة حرفا ولو كان ما حدثتم عنها كما حدثتم كان على ما قلنا وعلى خلاف ما قلتم قال وكيف قلت قلت أما حديثنا فصحيح على وجهه أن النبي','1692','2045','0');
INSERT INTO [t6227] VALUES (2447,'وغيره من أهل العلم قال فاذكرها قلت قال الله عز وجل لا تخرجوهن من بيوتهن الآية وأخبرنا عبد العزيز','1692','2045','0');
INSERT INTO [t6227] VALUES (2448,'قضى أن تعتد المبتوتة حيث شاءت في غير بيت زوجها ثم ساق الكلام إلى أن قال وما يعلم في كتاب الله ذكر نفقة إنما في كتاب الله ذكر السكنى ثم ذكر حديث ابن المسيب وقول مروان لعائشة وقد تقدم روى الشافعي رحمه الله عن','1696','2045','0');
INSERT INTO [t6227] VALUES (2449,'مسألة','1697','2045','0');
INSERT INTO [t6227] VALUES (2450,'خير غلاما بين أبيه وأمه وفي رواية أخرى عن أبي هريرة رضي الله عنه بيان أنها كانت مطلقة وأنه اختار أمه وعند أبي داود عن رافع بن سنان رضي الله عنه أنه أسلم وأبت امرأته أن تسلم فأتت النبي','1698','2045','0');
INSERT INTO [t6227] VALUES (2451,'لرافع اقعد ناحية وقال لامرأته أقعدي ناحية وأقعد الصبية بينهما ثم قال ادعواها فمالت الصبية إلى أمها فقال النبي','1698','2045','0');
INSERT INTO [t6227] VALUES (2452,'قال أنت أحق به ما لم تنكحي فجعلها أحق به ما لم تنكح ولم يعتبر ما اعتبروه وروى الشافعي رحمه الله عن سفيان بسنده أن عمر خير غلاما بين أبيه وأمه وعنه عن يونس بن عبد الله عن عمارة الجرمي قال خيرني علي رضي الله عنه بين','1699','2045','0');
INSERT INTO [t6227] VALUES (2453,'كتاب الجراح','1700','2045','0');
INSERT INTO [t6227] VALUES (2454,'مسألة','1700','2045','0');
INSERT INTO [t6227] VALUES (2455,'شيء سوى القرآن قال لا والذي فلق الحبة وبرأ النسمة إلا أن يعطي الله عبدا فهما في كتابه أو ما في الصحيفة قلت وما في الصحيفة قال العقل وفكاك الأسير ولا يقتل مسلم بكافر عن قيس بن عباد قال انطلقت أنا والأشتر إلى علي','1700','2045','0');
INSERT INTO [t6227] VALUES (2456,'عهدا لم يعهده إلى الناس عامة فقال لا إلا ما في كتابي هذا فأخرج كتابا فإذا فيه المؤمنون تتكافأ دماؤهم وهم يد على من سواهم ويسعى بذمتهم أدناهم ألا لا يقتل مؤمن بكافر ولا ذو عهد في عهده الحديث قال الشافعي','1701','2045','0');
INSERT INTO [t6227] VALUES (2457,'فحديث عمران رضي الله عنه قال قال رسول الله','1703','2045','0');
INSERT INTO [t6227] VALUES (2458,'كتابان فذكر أحدهما قال وفي الآخر المؤمنون تتكافأ دماؤهم ويسعى بذمتهم أدناهم لا يقتل مسلم بكافر ولا ذو عهد في عهده وذكرنا فيه ابن وهب هو عبيد الله بن عبد الرحمن بن وهب ومالك هو ابن أبي الرجال وأبو الرجال هو محمد بن','1704','2045','0');
INSERT INTO [t6227] VALUES (2459,'عام الفتح فذكر الخطبة فيها لا يقتل مؤمن بكافر رواه أبو داود عنه في كلام غيره من غير ذكر خطبة وفيه ولا ذو عهد في عهده وروى الشافعي رحمه الله حديثا مرسلا عن عطاء وطاووس','1704','2045','0');
INSERT INTO [t6227] VALUES (2460,'قال يوم الفتح لا يقتل مؤمن بكافر وقال وهذا عام عند أهل المغازي أن رسول الله','1705','2045','0');
INSERT INTO [t6227] VALUES (2461,'مسندا من حديث عمرو بن شعيب وحديث عمران بن حصين واستدلوا بما روى عمار بن مطر عن إبراهيم بن محمد الأسلمي عن ربيعة بن أبي عبد الرحمن عن ابن البيلماني عن ابن عمر رضي الله عنهما أن رسول الله','1705','2045','0');
INSERT INTO [t6227] VALUES (2462,'فقال أنا أحق من أوفى بذمته ثم أمر به فقتل كذا رواه أبو عبيد القاسم بن محمد بن سلام وغيره عن إبراهيم بن محمد بن أبي يحيى مرسلا ورواه الثوري وغيره عن ربيعة عن ابن البيلماني عن النبي','1706','2045','0');
INSERT INTO [t6227] VALUES (2463,'قتل مسلما بمعاهد وهو مرسل منكر وقال ابن المديني حديث ابن البيلماني أن النبي','1707','2045','0');
INSERT INTO [t6227] VALUES (2464,'قتل مؤمنا بكافر قلنا أفرايت لو كنا نحن وأنت نثبت المنقطع لحسن الظن بمن روى فروي حديثان أحدهما','1707','2045','0');
INSERT INTO [t6227] VALUES (2465,'به فلو كان ثابتا كنت أنت قد خالفت الحديثين حديثنا وحديث ابن البيلماني قال والذي قتله عمرو بن أمية قبل بني النظير وقبل الفتح بزمان وخطبة النبي','1708','2045','0');
INSERT INTO [t6227] VALUES (2466,'دهرا وأنت إنما تأخذ العلم من بعد ليس لك به مثل معرفة أصحابنا وعمرو قتل اثنين وداهما النبي','1708','2045','0');
INSERT INTO [t6227] VALUES (2467,'عمرا على أن قال قتلت رجلين لهما مني عهد لأدينهما','1708','2045','0');
INSERT INTO [t6227] VALUES (2468,'شيئا ثم يخالفه وروي بسند ضعيف عن الشعبي قال قتل رجل من المسلمين رجلا من أهل الحيرة نصرانيا فقتله به عمر رضي الله عنه وهذا مع ضعف رواته منقطع ورواه النخعي وزاد فيه فكتب عمر رضي الله عنه بعد ذلك إن كان الرجل يقتل','1709','2045','0');
INSERT INTO [t6227] VALUES (2469,'ونهوه عن قتله فجعل ديته ألف دينار وقال الشافعي رحمه الله قلت هذا من حديث من يجهل فإن كان غير ثابت فدع الاحتجاج به وإن كان ثابتا فقد زعمت أنه أراد قتله فمنعه أناس من أصحاب رسول الله','1710','2045','0');
INSERT INTO [t6227] VALUES (2470,'مجمعون لا يقتل مسلم بكافر فكيف خالفتهم والله أعلم','1710','2045','0');
INSERT INTO [t6227] VALUES (2471,'مسألة','1711','2045','0');
INSERT INTO [t6227] VALUES (2472,'الولاء لمن أعتق أي لا ولاء لمن لم يعتق وكذلك قوله الحر بالحر ولا يقتل بالعبد قاله لي الأستاذ أبو طاهر الريادي وروي الثوري عن يونس عن الحسن قال لا يقتل الحر بالمملوك وعن ابن جريج قال لا يقتل به','1711','2045','0');
INSERT INTO [t6227] VALUES (2473,'قال لا يقتل حر بعبد وروي بإسناد آخر والله أعلم وروي عن علي رضي الله عنه من السنة أن لا يقتل مسلم بكافر ولا حر بعبد رواه جابر الجعفي فإن سلم منه فسائر رواته ثقات وروي عن عمرو بن شعيب عن أبيه عن جده أن أبا بكر وعمر','1712','2045','0');
INSERT INTO [t6227] VALUES (2474,'مسألة','1713','2045','0');
INSERT INTO [t6227] VALUES (2475,'القصاص قال البيهقي وروي فيه عن ابن عباس وزيد بن ثابت رضي الله عنهم والرواية من طريق عبد الرحمن بن أبي الزناد عن أبيه عن الفقهاء السبعة منهم ورويناه عن الزهري وغيره وعن إبراهيم قال القصاص بين الرجل والمرأة في','1714','2045','0');
INSERT INTO [t6227] VALUES (2476,'إنما لم يجعل عليه شيئا لأنه التزم أرش جنايته فأعطاه من عنده متبرعا وقد حمله الخطابي على أن الجاني كان حرا وكانت الجناية خطأ وكانت عاقلته فقراء فلم يجعل عليهم شيئا إما لفقرهم وإما لأنهم لا يعقلون الجناية الواقعة على العبد إن','1714','2045','0');
INSERT INTO [t6227] VALUES (2477,'مسألة','1715','2045','0');
INSERT INTO [t6227] VALUES (2478,'مسألة','1716','2045','0');
INSERT INTO [t6227] VALUES (2479,'أن يرض رأسه وروي عن أبي سعيد رضي الله عنه بينما رسول الله','1716','2045','0');
INSERT INTO [t6227] VALUES (2480,'تعال فاستقد فقال بل عفوت يا رسول الله وعن زياد بن علاقة عن مرداس بن عروة رضي الله عنه قال رمى رجل من الحي أخا لي فقتله ففر فوجدناه فانطلقنا به إلى رسول الله','1716','2045','0');
INSERT INTO [t6227] VALUES (2481,'فأقادهم به وروي عن البراء رضي الله عنه عن النبي','1717','2045','0');
INSERT INTO [t6227] VALUES (2482,'قال ألا إن في قتيل العمد الخطأ بالسوط والعصا مائة من الإبل مغلظة منها أربعون خلفة في بطونها أولادها وليس في هذا الخبر أن كل قتيل حصل بالسوط والعصا يكون قتل عمد خطأ وإنما فيه أن القتل بالسوط والعصا إذا كان عمد خطأ تكون فيه','1718','2045','0');
INSERT INTO [t6227] VALUES (2483,'فقضى رسول الله','1719','2045','0');
INSERT INTO [t6227] VALUES (2484,'إنما هذا من إخوان الكهان من أجل سجعه قلنا هذا القتل كان خطأ ألا ترى أنه جعل الدية على العاقلة ثم قد روى أبو داود عن ابن عباس عن عمر رضي الله عنهم أنه سأل عن قضية النبي','1719','2045','0');
INSERT INTO [t6227] VALUES (2485,'بغرة وأن تقتل والله أعلم','1720','2045','0');
INSERT INTO [t6227] VALUES (2486,'والواجب بقتل العمد القصاص أو الدية فيخير بينهما ولي الدم في أحد القولين وقال أبو حنيفة رحمه الله القصاص وحده والدية تجب بالصلح بتراضيهما قال الله تعالى كتب عليكم القصاص في القتلى إلى قوله فمن عفى له من','1720','2045','0');
INSERT INTO [t6227] VALUES (2487,'عام الفتح فيها ومن قتل له قتيل فهو بخير النظرين إما أن يعطي الدية وأما أن يقاد أهل القتيل وعند البخاري إما أن يؤدي وأما أن يقاد وروي عن علقمة بن وائل الحضرمي عن أبيه قال جيء بالقاتل إلى رسول الله','1721','2045','0');
INSERT INTO [t6227] VALUES (2488,'أتعفو قال لا قال أتأخذ الدية قال لا قال أتقتل قال نعم قال فاذهب فلما ذهب دعاه فقال أما إنك إن عفوت عنه فإنه يبوء بإثمك وإثمه وإثم صاحبك فعفا عنه فأرسله فرأيته وهو يجر نسعه استدلوا بحديث أنس','1721','2045','0');
INSERT INTO [t6227] VALUES (2489,'فأمر بالقصاص فقال أنس بن النضر رضي الله عنه يا رسول الله أتكسر ثنية الربيع والذي بعثك بالحق لا تكسر ثنيتها فقال النبي','1722','2045','0');
INSERT INTO [t6227] VALUES (2490,'إن من عباد الله من لو أقسم على الله لأبره وهذا لا يخالف ما رواه الشافعي رحمه الله عن ابن أبي فديك عن ابن أبي ذئب عن سعيد المقبري عن أبي شريح الكعبي رضي الله عنه أن رسول الله','1722','2045','0');
INSERT INTO [t6227] VALUES (2491,'يقول من أصيب بدم أو خبل فهو بالخيار بين إحدى ثلاث فإن أراد الرابعة فخذوا على يديه بين أن يقتص أو يعفو أو يأخذ العقل فإن قبل من ذلك شيئا ثم عدا بعد ذلك فله النار وذاك لأن كتاب الله القصاص إلا أن يعفو عنه ولي الدم وليس إذا لم','1723','2045','0');
INSERT INTO [t6227] VALUES (2492,'القصاص وفيه قال فما زالت حتى قبلوا الدية وروى إسماعيل بن مسلم وهو ضعيف عن عمرو بن دينار عن طاووس عن ابن عباس رضي الله عنهما مرفوعا قال العمد قود إلا أن يعفو ولي المقتول والله أعلم','1724','2045','0');
INSERT INTO [t6227] VALUES (2493,'وعلى شريك الأب القصاص في قتل الولد عمدا وقال أبو حنيفة رحمه الله إنه لا قصاص عليه روى الشافعي عن مالك عن يحيى عن ابن المسيب أن عمر بن الخطاب رضي الله عنه قتل نفرا خمسة أو سبعة برجل قتلوه قتل غيلة وقال لو تمالأ عليه','1724','2045','0');
INSERT INTO [t6227] VALUES (2494,'مسألة','1725','2045','0');
INSERT INTO [t6227] VALUES (2495,'أمر برض رأس اليهودي الذي رض رأس الجارية وفي صحيح مسلم عن أنس رضي الله عنه حديث العكليين الذين','1725','2045','0');
INSERT INTO [t6227] VALUES (2496,'أعينهم وفي صحيح مسلم عنه أن رسول الله','1726','2045','0');
INSERT INTO [t6227] VALUES (2497,'من بدل دينه فاقتلوه ولا تعذبوا بعذاب الله ورواه أبو داود بمعناه وزاد فبلغ ذلك عليا فقال ويح أم ابن عباس','1727','2045','0');
INSERT INTO [t6227] VALUES (2498,'مسألة','1728','2045','0');
INSERT INTO [t6227] VALUES (2499,'أن يمتثل من الجارح حتى يبرأ المجروح','1728','2045','0');
INSERT INTO [t6227] VALUES (2500,'يستقيد فقيل له حتى تبرأ فأبى وعجل فاستقاد قال فعنتت رجله وبرئت رجل المستقاد منه فأتى النبي','1730','2045','0');
INSERT INTO [t6227] VALUES (2501,'مثله وعن معتمر عن أيوب عم عمرو بن شعيب قال قال رسول الله','1730','2045','0');
INSERT INTO [t6227] VALUES (2502,'أن يقص من المجروح حتى ينتهي كذا رواه محمد بن حمدان عن ابن جريج ذكرناه في السنن الكبير وروي من وجه آخر عن ابن عباس رضي الله عنهما وجأ رجل فخذ رجل فجاء إلى النبي','1731','2045','0');
INSERT INTO [t6227] VALUES (2503,'فقال شلت رجلي قال أخذت حقك والله أعلم','1731','2045','0');
INSERT INTO [t6227] VALUES (2504,'قال عام الفتح من قتل له قتيل فهو بخير النظرين إن أحب أخذ العقل وإن أحب فله القود استدلوا بما في الصحيحين من حديث أبي شريح العدوي أنه قال لعمرو بن أسيد وهو يبعث البعوث إلى مكة ائذن لي أيها الأمير أحدثك قولا قام به رسول','1734','2045','0');
INSERT INTO [t6227] VALUES (2505,'فقام خطيبا وهو مسند ظهره إلى الكعبة فقال إن','1734','2045','0');
INSERT INTO [t6227] VALUES (2506,'عندما قتل عاصم بن ثابت وخبيب رضي الله عنهما بقتل أبي سفيان في داره بمكة غيلة إن قدر عليه وهذا في الوقت الذي كانت محرمة فدل على أنها لا تمنع أحدا من شيء وجب عليه وإنما يمنع من أن ينصب عليها الحرب كما ينصب على غيرها وروي عن ابن','1735','2045','0');
INSERT INTO [t6227] VALUES (2507,'مسألة','1736','2045','0');
INSERT INTO [t6227] VALUES (2508,'بقوله في كل سن خمس من الإبل ولم يفرق بين سوداء وبيضاء والله أعلم','1736','2045','0');
INSERT INTO [t6227] VALUES (2509,'كتاب الديات','1738','2045','0');
INSERT INTO [t6227] VALUES (2510,'مسألة','1738','2045','0');
INSERT INTO [t6227] VALUES (2511,'وداه بمائة من إبل الصدقة ولا مدخل لبني المخاض في فرائض الصدقات وإنما وداه بدية الخطأ حيث لم يثبت العمد ألا ترى أن دية','1738','2045','0');
INSERT INTO [t6227] VALUES (2512,'فوددنا أن لو كان ما روياه عن ابن مسعود من بني اللبون كما روياه غير ان مذهب ابن مسعود رضي الله عنه في ذلك مشهور في بني المخاض وقد حكاه ابن المنذر في الخلافيات عن ابن مسعود رضي الله عنه كذلك والله أعلم وروي بنو اللبون الذكور عن','1740','2045','0');
INSERT INTO [t6227] VALUES (2513,'في دية الخطأ عشرون حقة وعشرون جذعة وعشرون ابنة مخاض وعشرون ابنة لبون وعشرون ابن مخاض ذكر تابعه عبد الرحيم بن سليمان عن حجاج بن أرطأة وليس هذا الحديث بثابت وقد كفانا إمامنا أبو الحسن الدارقطني','1741','2045','0');
INSERT INTO [t6227] VALUES (2514,'في الخطأ أخماسا عشرون جذاع وعشرون بنات لبون وعشرون بني لبون وعشرون بنات مخاض','1743','2045','0');
INSERT INTO [t6227] VALUES (2515,'دية الخطأ أخماسا لم يزيدوا على هذا ورواه يحيى بن زكريا بن أبي زائدة عن حجاج واختلف عنه فرواه شريح بن يونس بموافقة عبد الرحيم وعبد الواحد وخالفه أبو هشام الرفاعي فرواه عنه بموافقة أبي معاوية ومن تابعه لم يفسرها فيشبه','1744','2045','0');
INSERT INTO [t6227] VALUES (2516,'ويقوي هذا أيضا اختلاف عبد الواحد وعبد الرحيم والأموي عنه ووجه آخر وهو أنه قد روي عن النبي','1745','2045','0');
INSERT INTO [t6227] VALUES (2517,'في دية الخطأ ثلاثين حقة وثلاثين جذعة وعشرين بنات لبون وعشرين بني لبون ذكور وهذا مرسل إسحاق لم يسمع من عبادة ورواه محمد بن راشد عن سليمان بن موسى عن عمرو بن شعيب عن أبيه عن جده عن النبي','1745','2045','0');
INSERT INTO [t6227] VALUES (2518,'مسألة','1747','2045','0');
INSERT INTO [t6227] VALUES (2519,'خطب يوم الفتح بمكة فكبر ثلاثا ثم قال لا إله إلا الله وحده صدق وعده ونصر عبده وهزم الأحزاب وحده الحديث وفيه ألا إن دية شبه الخطأ شبه العمد ما كان بالسوط والعصا مائة من الإبل منها أربعون في بطونها أولادها وقول زيد','1747','2045','0');
INSERT INTO [t6227] VALUES (2520,'أولى والله ولي التوفيق وهو أعلم','1748','2045','0');
INSERT INTO [t6227] VALUES (2521,'دية قتل الخطأ في الحرم والأشهر الحرم ولذي الرحم المحرم','1748','2045','0');
INSERT INTO [t6227] VALUES (2522,'قال ليس لقاتل شيء وروي عن مجاهد أن عمر رضي الله عنه قضى فيمن قتل في الحرم أو في الشهر الحرام أو هو محرم بالدية وثلث الدية','1749','2045','0');
INSERT INTO [t6227] VALUES (2523,'في الدية بمائة من الإبل فذكر الحديث ثم ذكر تقويم عمر بن الخطاب رضي الله عنه الدية باثني عشر ألف درهم ثم قال ويزاد ثلث الدية في الشهر الحرام وفيه انقطاع بين إسحاق وعبادة ولكنه إذا انضم إلى رواية مجاهد عن عمر فيما اتفقا عليه من','1750','2045','0');
INSERT INTO [t6227] VALUES (2524,'والأصل في الدية الإبل وحدها ولا يجوز العدول عنها مع وجودها إلى غيرها على قوله في الجديد وقال أبو حنيفة','1750','2045','0');
INSERT INTO [t6227] VALUES (2525,'مائة بعير لكل بعير أوقية فذلك أربعة آلاف فلما كان عمر رضي الله عنه غلت الإبل ورخصت الورق فجعل عمر أوقيتين أوقيتين فلذلك ثمانية آلاف ثم لم تزل الإبل تغلو وترخص الورق حتى جعلها اثني عشر ألفا من الورق وألف دينار ومن البقر مائتي','1751','2045','0');
INSERT INTO [t6227] VALUES (2526,'يقيم الإبل على أهل القرى أربعمائة دينار أو عدلها من الورق ويقسمها على أثمان الإبل فإذا غلت رفع في قيمتها وإذا هانت نقص من ثمنها على أهل القرى الثمن ما كان ورواه محمد بن راشد عن سليمان بن موسى عن عمرو بن شعيب عن أبيه عن جده موصولا','1752','2045','0');
INSERT INTO [t6227] VALUES (2527,'على أهل الإبل مائة من الإبل وعلى أهل البقر مائتي بقرة وعلى أهل الشاة ألفي شاة وعلى أهل الحلل مائتي حلة وعلى أهل الطعام شيئا لا أحفظه والله سبحانه الموفق والملهم للصواب','1752','2045','0');
INSERT INTO [t6227] VALUES (2528,'مسألة','1753','2045','0');
INSERT INTO [t6227] VALUES (2529,'جعل الدية اثني عشر ألفا عن أنس رضي الله عنه قال قال رسول الله','1754','2045','0');
INSERT INTO [t6227] VALUES (2530,'أنه قضى بالدية اثني عشر ألف درهم وزعم أنه نزل فيه وما نقموا إلا أن أغناهم الله ورسوله من فضله قال الشافعي رحمه الله فقلت لمحمد أفتقول إن الدية اثنا عشر ألف درهم وزن ستة قال لا قلت فمن أين زعمت إذ كنت أعلم','1757','2045','0');
INSERT INTO [t6227] VALUES (2531,'مسألة','1758','2045','0');
INSERT INTO [t6227] VALUES (2532,'مائة من الإبل وفي هذا دلالة على أن الدية كانت تختلف حيث قيدوه بالرجل المسلم الحر ولو كانت دية غيره كديته لم يكن لتقييدهم فائدة والله أعلم وروى أبو داود عن ابن إسحاق عن عمرو بن شعيب عن أبيه عن جده عن النبي','1763','2045','0');
INSERT INTO [t6227] VALUES (2533,'فرض على كل مسلم حر قتل رجلا من أهل الكتاب أربعة آلاف','1763','2045','0');
INSERT INTO [t6227] VALUES (2534,'جعل دية المعاهد كدية المسلم قال علي بن عمر عثمان هو الوقاصي متروك الحديث وروي عن أبي سعد عن عكرمة عن ابن عباس رضي الله عنهما قال جعل رسول الله','1764','2045','0');
INSERT INTO [t6227] VALUES (2535,'أنه ودى ذميا دية مسلم','1764','2045','0');
INSERT INTO [t6227] VALUES (2536,'وأبي بكر وعمر وعثمان وعلي رضي الله عنهم دية المسلم واليهودي والنصراني سواء فلما استخلف معاوية صار دية اليهودي والنصراني على النصف من دية المسلم فلما استخلف عمر بن عبد العزيز رد الأمر إلى القضاء الأول هذا باطل بهذا الإسناد فبركة','1765','2045','0');
INSERT INTO [t6227] VALUES (2537,'مسألة','1766','2045','0');
INSERT INTO [t6227] VALUES (2538,'مسألة','1767','2045','0');
INSERT INTO [t6227] VALUES (2539,'في جنين امرأة من بني لحيان سقط ميتا بغرة عبد أو','1767','2045','0');
INSERT INTO [t6227] VALUES (2540,'دية المقتول على عاقلة القاتلة وبرأ زوجها قال فقال عاقلة المقتول ميراثها لنا قال رسول الله','1768','2045','0');
INSERT INTO [t6227] VALUES (2541,'فقضى فيه على عاقلتها بالدية وكانت حاملا فقضى في الجنين بغرة فقال بعض عصبتها أندي من لا طعم الحديث قال الشافعي رحمه الله ولم أعلم مخالفا في أن العاقلة العصبة وهم القرابة من قبل الأب قال رحمه الله وقد قضى عمر على','1768','2045','0');
INSERT INTO [t6227] VALUES (2542,'مسألة','1769','2045','0');
INSERT INTO [t6227] VALUES (2543,'على العاقلة ولا ديوان حتى كان الديوان في زمان عمر رضي الله عنه حين كثر المال وقد مضى الحديث في قضاء رسول الله','1769','2045','0');
INSERT INTO [t6227] VALUES (2544,'مسألة','1769','2045','0');
INSERT INTO [t6227] VALUES (2545,'قال لهم حين أنكرت يهود قتل عبد الله بن سهل تحلفون وتستحقون دم صاحبكم قالوا لا قال فيحلف يهود قالوا ليسوا بمسلمين فوداه رسول الله','1770','2045','0');
INSERT INTO [t6227] VALUES (2546,'استحقوا صاحبكم أو قال قتيلكم بأيمان خمسين منكم قالوا أمر لم نشهده فقال فتبرئكم يهود بأيمان خمسين منهم قالوا قوم كفار فوداه رسول الله','1770','2045','0');
INSERT INTO [t6227] VALUES (2547,'مقتل عبد الله فقال لهم تحلفون خمسين يمينا فتستحقون صاحبكم أو قاتلكم قالوا كيف نحلف ولم نشهد قال فتبرئكم اليهود بخمسين يمينا قالوا وكيف نقبل أيمان قوم كفار فلما رأى ذلك رسول الله','1770','2045','0');
INSERT INTO [t6227] VALUES (2548,'قال لهم تأتوني بالبينة على من قتل قالوا ما لنا بينة قال فيحلفون لكم ولم يسم أحدا منهم قال البيهقي رحمه الله وهذا يحتمل أن لا يخالف رواية يحيى وكأنه أراد بالبينة إيمان المدعين مع اللوث كما فسره','1771','2045','0');
INSERT INTO [t6227] VALUES (2549,'طالبهم بالبينة فلم يكن لهم بينة فقال استحقوا بخمسين قسامة وعن عمرو بن شعيب أيضا بمعنى ذلك وروى ابن إسحاق عن محمد بن إبراهيم عن عبد الرحمن بن بجيد قال والله ما هكذا كان الشأن ولكن سهلا أوهم ما قال رسول الله','1772','2045','0');
INSERT INTO [t6227] VALUES (2550,'وروى أبو داود خبرا منقطعا عن الزهري عن أبي سلمة وسليمان بن يسار ورجال من الأنصار أن رسول الله','1773','2045','0');
INSERT INTO [t6227] VALUES (2551,'من الأنصار أن النبي','1773','2045','0');
INSERT INTO [t6227] VALUES (2552,'بين ناس من الأنصار في قتيل ادعوه على اليهود وروي بألفاظ أخر فالرواية عنه مختلفة منها المنقطع والمجمل والمفسر وحديث سهل صحيح متصل مفسر','1773','2045','0');
INSERT INTO [t6227] VALUES (2553,'مسألة','1776','2045','0');
INSERT INTO [t6227] VALUES (2554,'نفر من بني سليم فقالوا إن صاحبا لنا أوجب قال فليعتق رقبة يقد الله بكل عضو منه عضوا منه من النار وفي رواية في صاحب لنا قد أوجب النار بالقتل رواه أبو داود والله أعلم','1776','2045','0');
INSERT INTO [t6227] VALUES (2555,'ونفس السحر ليس بكفر ولا يقتل ما لم يقتل سحره عمدا','1776','2045','0');
INSERT INTO [t6227] VALUES (2556,'لا يحل دم امرئ مسلم يشهد أن لا إله إلا الله وأني رسول الله إلا بإحدى ثلاث الثيب الزاني والنفس بالنفس والتارك لدينه المفارق للجماعة وفيهما حديث أسامة رضي الله عنه بعثنا رسول الله','1777','2045','0');
INSERT INTO [t6227] VALUES (2557,'فقال من لك ب لا إله إلا الله يوم القيامة فقلت يا رسول الله إنما قالها مخافة السلاح والقتل قال أفلا شققت عن قلبه حتى تعلم قالها من أجل ذلك أم لا من لك ب لا إله إلا الله يوم القيامة قال فما زال يقول حتى وددت أني','1777','2045','0');
INSERT INTO [t6227] VALUES (2558,'على أحد هذه المعاني وهو حديثها في الصحيحين أن النبي','1779','2045','0');
INSERT INTO [t6227] VALUES (2559,'مسألة','1779','2045','0');
INSERT INTO [t6227] VALUES (2560,'مسألة','1780','2045','0');
INSERT INTO [t6227] VALUES (2561,'مسألة','1781','2045','0');
INSERT INTO [t6227] VALUES (2562,'من بدل دينه فاقتلوه وفي الصحيحين حديث أبي موسى رضي الله عنه في بعث','1781','2045','0');
INSERT INTO [t6227] VALUES (2563,'إياه إلى اليمن وفيه ثم أتبعه معاذا رضي الله عنه فلما قدم قال له إنزل وألقي له وسادة فإذا عنده رجل موثق قال ما هذا قال كان يهوديا فأسلم ثم راجع دينه دين السوء فتهود قال لا أجلس حتى يقتل قضاء الله ورسوله قال نعم','1782','2045','0');
INSERT INTO [t6227] VALUES (2564,'مسألة','1783','2045','0');
INSERT INTO [t6227] VALUES (2565,'فقتلها فقال النبي','1784','2045','0');
INSERT INTO [t6227] VALUES (2566,'فقتلها خالد بن الوليد وقال ابن عمر والزهري وإبراهيم تقتل المرتدة وروي من وجه ليس بالقوي من حديث ابن المنكدر عن جابر خبر مرفوع في ذلك وكذلك أيضا من حديث الزهري عن عروة عن عائشة رضي الله عنها قال الشافعي رحمه الله','1784','2045','0');
INSERT INTO [t6227] VALUES (2567,'مقتولة فأنكر رسول الله','1785','2045','0');
INSERT INTO [t6227] VALUES (2568,'ولا رواه شعبة وروي عن جلاس عن علي رضي الله عنه المرتدة تستأنى ولا تقتل قال لي شعبة قال لي أيوب لا ترو عن خلاس شيئا قال علي بن عمر خلاس عن علي لا يحتج به لضعفه وقد روي عن علي رضي الله عنه بخلافه وروى','1786','2045','0');
INSERT INTO [t6227] VALUES (2569,'مسألة','1786','2045','0');
INSERT INTO [t6227] VALUES (2570,'كتاب الحدود','1788','2045','0');
INSERT INTO [t6227] VALUES (2571,'مسألة','1788','2045','0');
INSERT INTO [t6227] VALUES (2572,'فرجما ورواه البخاري ومسلم في الصحيح وعند مسلم قصة أخرى عن البراء بن عازب رضي الله عنه في يهودي زنى وفيها فأمر يعني رسول الله','1790','2045','0');
INSERT INTO [t6227] VALUES (2573,'رجلا من أسلم ورجلا من اليهود وامرأة وروي عن جابر بن سمرة رضي الله عنه أن رسول الله','1790','2045','0');
INSERT INTO [t6227] VALUES (2574,'مخيرا ثم نزلت وأن احكم بينهم بما أنزل الله ولا تتبع أهواءهم فأمر النبي','1791','2045','0');
INSERT INTO [t6227] VALUES (2575,'إنما حكم عليهما بالرجم بحكم الإسلام وأنه لم يجعل الإسلام من شرائط الإحصان خلاف قول من زعم أنه','1791','2045','0');
INSERT INTO [t6227] VALUES (2576,'اليهوديين وهو لا يخالف رسول الله','1792','2045','0');
INSERT INTO [t6227] VALUES (2577,'عن ذلك فنهاه وقال إنها لا تحصنك قال علي أبو بكر بن أبي مريم ضعيف وعلي بن أبي طلحة لم يدرك كعبا ورواه بقية بن الوليد عن بعض مشائخه المجهولين أخبرنا أبو سبأ عتبة بن تميم عن علي عن كعب وروى إبراهيم بن أبي حية عن هشام','1793','2045','0');
INSERT INTO [t6227] VALUES (2578,'مسألة','1793','2045','0');
INSERT INTO [t6227] VALUES (2579,'برجم ماعز ولم يحضره وأمر أنيسا أن يأتي امرأة فإن اعترفت رجمهما ولم يقل أعلمني لأحضرها ولم أعلمه أمر برجم أحد فحضره ولو كان حضور الإمام حقا حضر رسول الله','1794','2045','0');
INSERT INTO [t6227] VALUES (2580,'ما دل على أن حضور الإمام ليس بشرط وذكر أنه','1794','2045','0');
INSERT INTO [t6227] VALUES (2581,'مسألة','1795','2045','0');
INSERT INTO [t6227] VALUES (2582,'يأمر فيمن زنى ولم يحصن بجلد مائة وتغريب عام زاد البخاري قال ابن شهاب وأخبرني عروة بن الزبير أن عمر رضي الله عنه غرب ثم لم تزل تلك السنة وعند البخاري عن أبي هريرة رضي الله عنه عن رسول الله','1795','2045','0');
INSERT INTO [t6227] VALUES (2583,'جلد الذي زنى مائة وغربه عاما','1795','2045','0');
INSERT INTO [t6227] VALUES (2584,'والبكر جلد مائة ونفي سنة وروى مالك عن نافع عن صفية بنت أبي عبيد أن أبا بكر الصديق رضي الله عنه أتي برجل وقع على جارية بكر فأحبلها ولم يكن أحصن فأمر به أبو بكر رضي الله عنه فجلد الحد ثم نفي إلى فدك وروي عن ابن إدريس عن','1796','2045','0');
INSERT INTO [t6227] VALUES (2585,'والتغريب عنه صحيح في سائر الروايات وعن أبي بكر وعمر صحيح وفي هذه الرواية وغيرها وروي الجلد والتغريب عن علي وعن أبي بن كعب رضي الله عنهما','1796','2045','0');
INSERT INTO [t6227] VALUES (2586,'سئل عن الأمة إذا زنت ولم تحصن قال إن زنت فاجلدوها ثم إن زنت فاجلدوها ثم إن زنت فاجلدوها ثم بيعوها ولو بضفير قال ابن شهاب لا أدري أبعد الثالثة أو الرابعة وروي عن أبي أمامة بن سهل بن حنيف قال أتت امرأة إلى','1798','2045','0');
INSERT INTO [t6227] VALUES (2587,'فأتي به يحمل وهو ضرير مقعد فاعترف فضربه رسول الله','1798','2045','0');
INSERT INTO [t6227] VALUES (2588,'في الابتداء بجلد الثيب مع الرجم فاستدللنا بسكوته عن الحد في حديث أنس على نسخ الجلد كذلك استدللنا بسكوته عن النفي في حديث الأمة على نسخ النفي قلنا تركه في حديث الأمة ذكره لا يدل على النسخ لأمور','1799','2045','0');
INSERT INTO [t6227] VALUES (2589,'في الأمة على ذكر الجلد يشبه أن يكون اكتفى بما سبق منه من ذكره والله أعلم والعجب أن قائل هذا يدعي المعرفة بالآثار ثم يجعل تركه القول فيما تقدم من الأخبار نفي البكر كتركنا جميعا القول بحديث رواه محمد بن عبد العزيز الواسطي عن إسماعيل بن','1800','2045','0');
INSERT INTO [t6227] VALUES (2590,'مسألة','1801','2045','0');
INSERT INTO [t6227] VALUES (2591,'قال واغد يا أنيس على امرأة هذا فإن اعترفت فارجمها فغدا عليها فاعترفت فأمر بها رسول الله','1801','2045','0');
INSERT INTO [t6227] VALUES (2592,'عنه حتى أقر أربع مرات فلأنه لم يتبين له صحة إقراره بصريح الزنا ألا تراه استفسر منه صريح الزنا بعد وجود العدد في إقراره وحين فسر إقراره نظر هل به جنة أو سكر فلما تبين له صحة إقراره أمر برجمه فإذا','1801','2045','0');
INSERT INTO [t6227] VALUES (2593,'مسألة','1802','2045','0');
INSERT INTO [t6227] VALUES (2594,'قال من وجدتموه يعمل عمل قوم لوط فاقتلوا الفاعل والمفعول به وعند أبي داود عن ابن جريج عن ابن جشم عن سعيد بن جبير ومجاهد عن ابن عباس رضي الله عنهما في البكر يؤاخذ على اللوطية قال يرجم','1802','2045','0');
INSERT INTO [t6227] VALUES (2595,'يعني ما تقدم وروي عن ابن المنكدر وصفوان بن سليم قصة في شأن رجل ينكح كما تنكح المرأة أن أبا بكر رضي الله عنه جمع الناس فسألهم عن ذلك فكان من أشدهم قولا يومئذ علي رضي الله عنه قال يرى أن نحرقه بالنار فاجتمع رأي أصحاب','1803','2045','0');
INSERT INTO [t6227] VALUES (2596,'من أتى بهيمة فاقتلوه واقتلوها معه قال قلت له ما شأن البهيمة قال ما أراه قال ذلك إلا أنه كره أن يؤكل لحمها وقد عمل بها ذلك العمل استدلوا بما روى أبو حنيفة رحمه الله عن عاصم عن أبي رزين عن ابن عباس رضي الله عنهما','1805','2045','0');
INSERT INTO [t6227] VALUES (2597,'رجم رجلا أتى بهيمة ويرجح مذهبنا في اتيان البهائم بقول الحسن بن علي وجابر بن زيد والحكم والحسن وعطاء والله أعلم','1806','2045','0');
INSERT INTO [t6227] VALUES (2598,'ومن نكح ذات محرم له ووطئها عالما حد وقال أبو حنيفة رحمه الله لا يحد وهذا خلاف الكتاب والسنة ولنا عموم الخبر في وجوب الحد على الزاني مطلقا وروي عن ابن عباس رضي الله عنهما قال قال رسول الله','1806','2045','0');
INSERT INTO [t6227] VALUES (2599,'إذ جاء ركب أو فوارس معهم فجعل الأعراب يلوذون بي لمنزلتي','1806','2045','0');
INSERT INTO [t6227] VALUES (2600,'فانتهوا إلينا فاطافوا بقبة فاستخرجوا رجلا فضربوا عنقه فسألت عن قصته فقيل وجد يعرس بامرأة أبيه إسناده صحيح وجاء من يدعي تسوية الأخبار على مذهبه وحمل ذلك على أنه','1807','2045','0');
INSERT INTO [t6227] VALUES (2601,'إلى رجل نكح امرأة أبيه فأمرني أن أضرب عنقه وآخذ ماله وبحديث معاوية بن قرة عن أبيه أن النبي','1807','2045','0');
INSERT INTO [t6227] VALUES (2602,'ادرؤوا الحدود ما استطعتم عن المسلمين فإن وجدتم للمسلم مخرجا فخلوا سبيله فإن الإمام لأن يخطئ في العفو خير له من أن يخطئ في العقوبة','1808','2045','0');
INSERT INTO [t6227] VALUES (2603,'بإسناد شبه لا شيء وروي عن عبد الله ومعاذ وعقبة بن عامر رضي الله عنهم موقوفا كما ذكرنا إسنادها في السنن والله أعلم','1809','2045','0');
INSERT INTO [t6227] VALUES (2604,'ويحد الرجل أمته إذا زنت وقال أبو حنيفة رحمه الله ليس له ذلك إلا أن يكون سلطانا لنا ما في الصحيحين عن أبي هريرة رضي الله عنه سمعت رسول الله','1809','2045','0');
INSERT INTO [t6227] VALUES (2605,'زنت فأمرني أن أجلدها فأتيتها فإذا هي حديث عهد بالنفاس فخشيت إن أنا','1809','2045','0');
INSERT INTO [t6227] VALUES (2606,'فأخبرته فقال أحسنت وروي عن أبي جميلة عنه قال قال رسول الله','1810','2045','0');
INSERT INTO [t6227] VALUES (2607,'إذا زنت إماؤكم فأقيموا عليهن الحدود أحصن أو لم يحصن قال الشافعي رحمه الله وهم يخالفون هذا إلى غير فعل أحد علمته من أصحاب النبي ونحن نقول به أخبرنا سفيان عن عمرو عن الحسن بن محمد بن علي رضي الله عنه أن فاطمة رضي الله','1810','2045','0');
INSERT INTO [t6227] VALUES (2608,'حجة قال لا قلنا فلم يحتج به وليس عن ابن عباس رضي الله عنهما بمعروف أيضا','1811','2045','0');
INSERT INTO [t6227] VALUES (2609,'مسألة','1812','2045','0');
INSERT INTO [t6227] VALUES (2610,'قال القطع في ربع دينار فصاعدا وفي رواية تقطع يد السارق في ربع دينار فصاعدا رواه الشافعي رحمه الله من حديث ابن عيينة عن الزهري فقال القطع في ربع دينار فصاعدا وكذلك رواه إسحاق الحنظلي عنه في أحد الموضعين من مسنده','1812','2045','0');
INSERT INTO [t6227] VALUES (2611,'فيحتمل أن يكون ذلك لأنها قومت ما قطع','1813','2045','0');
INSERT INTO [t6227] VALUES (2612,'يقطع فيه وقيمته عند غيرها أكثر من ربع دينار قال البيهقي رحمه الله تعالى ولو كان أصل الحديث على هذا اللفظ فعائشة رضي الله عنها عند أهل العلم بحالها كانت أعلم بالله وأفقه في دين الله وأخوف من الله وأشد إتقانا في الرواية','1813','2045','0');
INSERT INTO [t6227] VALUES (2613,'كما رواه عن يونس وكلهم حفاظ ثقات وفي ذلك دلالة على أن أصل الحديث عن لفظ رسول الله','1815','2045','0');
INSERT INTO [t6227] VALUES (2614,'فيحتمل أن يكونا محفوظين بأن يقطع','1815','2045','0');
INSERT INTO [t6227] VALUES (2615,'مثل معنى رواية الجماعة وكلها مخرج في الصحيح رواية سليمان وأبي بكر عند مسلم والأخرى عند البخاري وأظنها لم تقع للطحاوي ولو وقعت له فما أدري بما كان يعللها فإنه علل حديث سليمان بأن مخرمة بن بكير بن الأشج الذي رواه عن أبيه عن','1816','2045','0');
INSERT INTO [t6227] VALUES (2616,'فيقتصر الرواة تارة على فتواها ومرة على روايتها وأما رواية عبد الله بن أبي بكر فإنه روى عن عمرة قصة المولاتين اللتين خرجتا مع عائشة رضي الله عنها والعبد الذي سرق','1817','2045','0');
INSERT INTO [t6227] VALUES (2617,'وروى همام عن قتادة عن الزهري عن عروة عن عائشة رضي الله عنها قالت قال رسول الله','1818','2045','0');
INSERT INTO [t6227] VALUES (2618,'في أقل من ثمن المجن جحفة أو ترس وكلاهما ذو ثمن فهشام إنما رواه في رجل سرق قدحا فأتى به عمر بن عبد العزيز فقال قال أبي إنه لا تقطع اليد في الشيء التافه','1818','2045','0');
INSERT INTO [t6227] VALUES (2619,'في أدنى من ثمن مجن حجفة أو ترس رواه مسلم في الصحيح ثم قيمة المجن غير مذكورة في هذه الرواية وقد ذكرتها عمرة عن عائشة في رواية ابن إسحاق عن يزيد بن حبيب عن بكير الأشج عن ابن يسار عن عمرة قالت قيل لعائشة رضي الله عنها ما ثمن المجن','1819','2045','0');
INSERT INTO [t6227] VALUES (2620,'قطع رجلا سرق ترسا من صفة النساء ثمنه ثلاثة دراهم قال الشافعي رحمه الله حديث ابن عمر موافق لحديث عائشة رضي الله عنهم لأن ثلاثة دراهم في عهد النبي','1819','2045','0');
INSERT INTO [t6227] VALUES (2621,'في أدنى من ثمن جحفة أو ترس وكل واحد منهما يومئذ ذو ثمن زاد مسلم وأن يد السارق لم تقطع في عهد رسول','1821','2045','0');
INSERT INTO [t6227] VALUES (2622,'يقوم عشرة دراهم ورواه أبو داود من حديث ابن نمير عن ابن إسحاق فقال قطع رسول الله','1821','2045','0');
INSERT INTO [t6227] VALUES (2623,'إلا في ثمن المجن وكان ثمن المجن يومئذ دينارا قال الشافعي رحمه الله أخبرنا ابن عيينة عن حميد الطويل قال سمعت قتادة يسأل أنس بن مالك عن القطع فقال حضرت أبا بكر الصديق رضي الله عنه قطع سارقا في شيء ما يسوى ثلاثة دراهم أو','1822','2045','0');
INSERT INTO [t6227] VALUES (2624,'شبيها بقولنا قلت أتعرف أيمن أما أيمن الذي روى عنه عطاء فرجل حدث لعله أصغر من عطاء روى عنه عطاء حديثا عن ربيع ابن امرأة كعب عن كعب يعني في الوضوء قال الشافعي فهذا منقطع قال فقد روينا عن شريك عن مجاهد عن أيمن','1822','2045','0');
INSERT INTO [t6227] VALUES (2625,'يوم حنين قبل مولد مجاهد قال فقد روينا عن عمرو بن شعيب عن عبد الله بن عمرو رضي الله عنهما أن النبي','1823','2045','0');
INSERT INTO [t6227] VALUES (2626,'دينارا قلت هذا رأي من عبد الله بن عمرو في رواية عمرو بن شعيب والمجان قديما وحديثا تبلغ ثماني عشرة ومائة ودرهمين فإذا قطع رسول الله','1823','2045','0');
INSERT INTO [t6227] VALUES (2627,'قطع سارقا في خمسة دراهم وهذا أقرب أن يكون صحيحا عن عبد الله في حديث المسعودي عن القاسم عن عبد الله قال وكيف لم يأخذ بهذا قلنا هذا حديث لا يخالف حديثنا إذا قطع في ثلاثة دراهم قطع في خمسة قال فقد روينا عن عمر','1824','2045','0');
INSERT INTO [t6227] VALUES (2628,'مسألة','1825','2045','0');
INSERT INTO [t6227] VALUES (2629,'لعن الله السارق يسرق الحبل فتقطع يده ويسرق البيضة فتقطع يده وروي عن عمرو بن شعيب عن عبد الله بن عمرو أن رجلا من مزينة أتى رسول الله','1826','2045','0');
INSERT INTO [t6227] VALUES (2630,'لا قطع في ثمر ولا كثر','1826','2045','0');
INSERT INTO [t6227] VALUES (2631,'حين قال لا قطع في تمر ولا كثر على مثل ما سئل عنه وكان حيطان المدينة ليس عليها حيطان ولأنه يقول فإذا آواه الجرين والمراح ففيه القطع واحتج بحديث عثمان في الأترجة وقد مضى والله أعلم','1827','2045','0');
INSERT INTO [t6227] VALUES (2632,'ولو وهبت منه السرقة لم يدرء عنه بذلك الحد وقال أبو حنيفة رحمه الله سقط القطع في الصحيحين حديث عائشة رضي الله عنها أن قريشا أهمهم شأن المخزومية التي سرقت فقالوا من يكلم فيها رسول الله','1827','2045','0');
INSERT INTO [t6227] VALUES (2633,'فكلمه فقال رسول الله','1827','2045','0');
INSERT INTO [t6227] VALUES (2634,'أقيلوا ذوي الهيئات عثراتهم إلا الحدود وروى مالك عن ابن شهاب عن صفوان بن عبد الله بن صفوان أن صفوان بن أمية رضي الله عنه قيل له من لم يهاجر هلك فقدم المدينة فنام في المسجد وتوسد رداءه فجاء سارق فأخذ رداءه فأخذ صفوان','1828','2045','0');
INSERT INTO [t6227] VALUES (2635,'أن تقطع يده فقال صفوان إني لم أرد هذا هو عليه صدقة فقال رسول الله','1828','2045','0');
INSERT INTO [t6227] VALUES (2636,'فأمر به ليقطع فأتيته فقلت أتقطعه من أجل ثلاثين درهما أنا أبيعه وأنسئه ثمنها قال فهلا كان هذا قبل أن تأتيني به وروى أبو داود من حديث عمرو بن شعيب عن عبد الله بن عمرو رضي الله عنهما أن رسول الله','1829','2045','0');
INSERT INTO [t6227] VALUES (2637,'مسألة','1829','2045','0');
INSERT INTO [t6227] VALUES (2638,'يا أبا ذر قلت لبيك يا رسول الله وسعديك قال كيف أنت إذا أصاب الناس موت يكون البيت فيه بالوصيف يعني القبر قلت الله ورسوله أعلم أو ما خار الله لي ورسوله قال عليك بالصبر أو قال تصبر قال أبو داود قال حماد بن أبي','1830','2045','0');
INSERT INTO [t6227] VALUES (2639,'مسألة','1831','2045','0');
INSERT INTO [t6227] VALUES (2640,'قال في السارق غن سرق فاقطعوا يده ثم إن سرق فاقطعوا رجله ثم إن سرق فاقطعوا يده ثم إن سرق فاقطعوا رجله وفي رواية حرملة والمزني عنه قال أخبرني عبد الله بن نافع عن محمد بن أبي حميد عن محمد بن المنكدر عن جابر بن عبد الله رضي','1834','2045','0');
INSERT INTO [t6227] VALUES (2641,'فقال اقتلوه فقالوا يا رسول الله إنما سرق فقال اقطعوه فقطع وذكر مثل ذلك إلى الرابعة فأتي به الخامسة فقال اقتلوه فقتلناه والقتل منسوخ كهو في حد الشرب والقطع ثابت وروى أبو داود أيضا عن الحارث بن عبد الله بن','1834','2045','0');
INSERT INTO [t6227] VALUES (2642,'لم ير بلوغه في المرات الأربع أو لم ير سرقته بلغت ما يوجب القطع ثم رأى ذلك في المرات الأخر فقطع وهذا مرسل يقوي المرسل قبله ويقوي قول من وافقه من الصحابة رضي الله عنهم وروي عن معاذ بن عبد الله الجهني رضي الله عنه صاحب رسول','1835','2045','0');
INSERT INTO [t6227] VALUES (2643,'من سرق فاقطعوا يده الحديث بنحو حديث أبي هريرة وروى الشافعي رحمه الله عن مالك عن عبد الرحمن بن القاسم عن أبيه أن رجلا من أهل اليمن أقطع اليد والرجل قدم على أبي بكر رضي الله عنه فشكا إليه أن عامل اليمن ظلمه وكان يصلي من الليل','1835','2045','0');
INSERT INTO [t6227] VALUES (2644,'وعن صفية بنت أبي عبيد أن رجلا سرق مقطوعة يده ورجله فأراد أبو بكر أن يقطع رجله ويدع يده ينتفع بها فقال عمر لا والذي نفسي بيده فأمر به أبو بكر فقطعت يده وعن عكرمة عن ابن عباس رضي الله عنهما شهدت عمر قطع يدا بعد يد','1836','2045','0');
INSERT INTO [t6227] VALUES (2645,'وأبي بكر في دار الهجرة وعمر يراه ويشير به وروي عنه أنه قطع أيضا وهو كما قال الشافعي رحمه الله تعالى فإن عبد الله بن سلمة غير محتج به والله أعلم','1837','2045','0');
INSERT INTO [t6227] VALUES (2646,'مسألة','1838','2045','0');
INSERT INTO [t6227] VALUES (2647,'لا يحل دم رجل يشهد أن لا إله إلا الله وأني رسول الله إلا بإحدى ثلاثة نفر النفس بالنفس والثيب الزاني والتارك لدينه المفارق للجماعة والردء لم يقتل ولم يزن ولم يكفر','1839','2045','0');
INSERT INTO [t6227] VALUES (2648,'الجزء ٥','1841','0','0');
INSERT INTO [t6227] VALUES (2649,'كتاب الاشربة','1841','2648','0');
INSERT INTO [t6227] VALUES (2650,'مسألة','1841','2648','0');
INSERT INTO [t6227] VALUES (2651,'فحمد الله وأثنى عليه ثم قال أما بعد فإن الخمر نزل تحريمها وهي من خمسة من العنب والتمر والبر والشعير والعسل والخمر ما خامر العقل وثلاث وددت أن رسول الله','1841','2648','0');
INSERT INTO [t6227] VALUES (2652,'ولو كان لنهى عنه ألا ترى أنه قد عم الأشربة كلها فقال الخمر ما خامر العقل قال البخاري وقال حجاج عن حماد أبي حسان مكان العنب الزبيب وقوله الخمر ما خامر العقل في آخر الحديث مسند فافهمه وفي الصحيحين عن','1842','2648','0');
INSERT INTO [t6227] VALUES (2653,'عن البتع فقال كل شراب أسكر فهو حرام زاد مسلم في رواية والبتع نبيذ العسل وفي أخرى عند البخاري سئل رسول الله','1843','2648','0');
INSERT INTO [t6227] VALUES (2654,'كل شراب أسكر فهو حرام اتفقا على صحة حديث أبي موسى فقلت يا رسول الله يصنع عندنا شراب من العسل يقال له البتع وشراب من الشعير يقال المزر وهما يسكران فقال النبي','1844','2648','0');
INSERT INTO [t6227] VALUES (2655,'قال كل مسكر خمر وكل مسكر حرام وعند أبي داود عن جابر بن عبد الله رضي الله عنه قال قال رسول الله','1844','2648','0');
INSERT INTO [t6227] VALUES (2656,'قال أنهى عن قليل ما أسكر كثيره رواته ثقات وعن عائشة رضي الله عنها قالت سمعت رسول الله','1844','2648','0');
INSERT INTO [t6227] VALUES (2657,'قال كل مسكر خمر وما أسكر كثيره فقليله حرام وعن ابن عمر رضي الله عنهما عن رسول الله','1845','2648','0');
INSERT INTO [t6227] VALUES (2658,'فقلت يا رسول الله إنا بأرض باردة نعالج فيها عملا شديدا وإنا نتخذ شرابا من هذا القمح نتقوى به قال هل يسكر قلت نعم قال فاجتنبوه قلت فإن الناس غير تاركيه قال فإن لم يتركوه فقاتلوهم وعند مسلم في الصحيح عن أبي','1845','2648','0');
INSERT INTO [t6227] VALUES (2659,'عن الباذق ما أسكر فهو حرام قال الشراب الطيب لا الحرام الخبيث وعند أبي داود عن مالك بن أبي مريم قال دخل علينا عبد الرحمن ابن غنيم فتذاكرنا الطلاء فقال حدثني أبو مالك الأشعري رضي الله عنه أنه سمع رسول الله','1846','2648','0');
INSERT INTO [t6227] VALUES (2660,'أنه قال الخمر من هاتين الشجرتين وفي رواية أخرى هي خمسة لم يفرق بين المطبوخ والني وفي صحيح مسلم عن أبي هريرة رضي الله عنه سمعت رسول الله','1846','2648','0');
INSERT INTO [t6227] VALUES (2661,'يقول إن الخمر من العصير والزبيب والتمر والحنطة والشعير والذرة وإني أنهاكم عن كل مسكر وهذان الحديثان لا يتنافيان قال أبو سليمان الخطابي في حديث أبي هريرة معناه أن معظم ما يتخذ من الخمر إنما هو من النخلة والعنبة','1847','2648','0');
INSERT INTO [t6227] VALUES (2662,'بقدحي هذا الشراب كله العسل والنبيذ والماء واللبن وهذا لا حجة لهم فيه وصفة النبيذ الذي كان يشربه','1848','2648','0');
INSERT INTO [t6227] VALUES (2663,'في سقا يوكى أعلاه وله عزلا ننبذ غدوة فيشربه عشاء وننبذه عشاء فيشربه غدوة عنده عن ابن عباس رضي الله عنهما جاءه قوم فسألوه عن بيع الخمر واشترائه والتجارة فيه قال لا يصلح بيعها ولا اشتراؤها ولا التجارة فيه','1848','2648','0');
INSERT INTO [t6227] VALUES (2664,'في سفر فرجع من سفره وناس من أصحابه قد انتبذوا نبيذا لهم في نقيرة وحناتم ودباء فأمر بها فأهرقت فأمر بسقا فجعل فيه زبيب وماء فكان ينبذ له من الليل فيصبح فيشربه يومه ذلك وليلته التي يستقبل ومن الغد حتى يمسي فإذا أمسى','1849','2648','0');
INSERT INTO [t6227] VALUES (2665,'كان يصوم فتحينت قطرة نبيذ صنعته في دباء ثم أتيته به فإذا هو ينشى فقال اضرب بهذا الحائط فإن هذا شراب من لا يؤمن بالله واليوم الآخر واستدلوا بما روي عن سلام عن القاسم بن عبد الرحمن عن أبيه عن أبي بردة رضي الله عنه أن النبي','1850','2648','0');
INSERT INTO [t6227] VALUES (2666,'كنت نهيتكم عن الأشربة في ظروف الأدم فاشربوا في كل وعاء غير أن لا تشربوا مسكرا وفي رواية فاشربوا في الأسقية كلها ولا تشربوا مسكرا','1850','2648','0');
INSERT INTO [t6227] VALUES (2667,'من حديث ابن مسعود ولا يصح روي عن فرقد السبحي عن جابر بن يزيد عن مسروق عن عبد الله رضي الله عنهم قال بينما نحن نزول مع النبي','1851','2648','0');
INSERT INTO [t6227] VALUES (2668,'كل مسكر حرام وروى عنه عطاء وطاوس ومجاهد ما أسكر كثيره فقليله حرام وكذلك روي عن','1852','2648','0');
INSERT INTO [t6227] VALUES (2669,'عن النبيذ حلال أو حرام فقال حلال قال علي بن عمر عبد العزيز بن أبان متروك الحديث وروي عن يحيى بن يمان العجلي عن سفيان بالإسناد أن النبي','1854','2648','0');
INSERT INTO [t6227] VALUES (2670,'وقال الأشجعي وعبدة عن سفيان الكلبي عن أبي صالح عن المطلب وروي عن يزيد بن أبي زياد عن عكرمة عن ابن عباس رضي الله عنهما فذكر قصة فيها طواف النبي','1855','2648','0');
INSERT INTO [t6227] VALUES (2671,'وشربه لم يذكر ما ذكره يزيد وإنما تعرف هذه الزيادة من رواية الكلبي كما مضى وزاد يزيد شربه منه قبل خلطه بالماء وهو بخلاف سائر الرواة وروى عن الشيباني عن عبد الملك بن نافع بن أخي القعقاع عن ابن عمر رضي الله عنهما فذكر','1856','2648','0');
INSERT INTO [t6227] VALUES (2672,'أنه قال لوفد عبد القيس لا تشربوا في نقير ولا مقير ولا دباء ولا حنتم ولا مزادة ولكن اشربوا في سقاء أحدكم غير مسكر فإن خشي شدته فليصب عليه الماء','1857','2648','0');
INSERT INTO [t6227] VALUES (2673,'أنه أمر فيه بإراقته ولو كان إلى إصلاحه بالماء سبيل لما أمر بإراقته والله أعلم وروي بإسناد لا يحتج به عن أبي سعيد مرفوعا لينبذ أحدكم في سقائه فإن خشي سكره فليكسره بالماء ورأيت في حديث يحيى بن أبي كثير عن ثمامة بن كلاب عن أبي','1858','2648','0');
INSERT INTO [t6227] VALUES (2674,'قال إذا دخل أحدكم على أخيه المسلم فأطعمه فليأكل من طعامه ولا يسأله وإن سقاه شرابا فليشرب من شرابه ولا يسأله عنه وإن خشي منه فليكسره بالماء المراد والله أعلم إذا خشي أن يبلغ حد الإسكار فليكسره بالماء حتى لا يشتد','1859','2648','0');
INSERT INTO [t6227] VALUES (2675,'وأصحاب النبي','1860','2648','0');
INSERT INTO [t6227] VALUES (2676,'وعن أصحابه والتابعين فلم يعبؤا به وأذكر عن إبراهيم فنكسوا رؤوسهم وروى الكلبي عن أبي صالح عن ابن عباس رضي الله عنهما قال قال رسول الله','1860','2648','0');
INSERT INTO [t6227] VALUES (2677,'كان إذا حمض عليهم النبيذ كسروه بالماء لما روى عبد الله بن المبارك قال قال عبيد الله بن عمر لأبي حنيفة في النبيذ فقال أبو حنيفة أخذناه من قبل أبيك قال أي شيء هو قال إذا رابكم فاكسروه بالماء قال عبد الله العمري إذا','1862','2648','0');
INSERT INTO [t6227] VALUES (2678,'عن عمر رضي الله عنه أسانيدها غير قوية فإجراء ما روينا على ظاهرها وحمل ما رووا على الأمر بالكسر بالماء إذا خشي شدته قبل أن يشتد على تخفيف حموضته أو حارته كما حمله المتقدمون عليه أولى وبالله التوفيق وروي عن سليمان التيمي في','1863','2648','0');
INSERT INTO [t6227] VALUES (2679,'وحد الشرب أربعون وقال أبو حنيفة رحمه الله','1863','2648','0');
INSERT INTO [t6227] VALUES (2680,'أتي برجل شرب الخمر فضربه بجريدتين نحوا من أربعين ثم صنع أبو بكر مثل ذلك فلما كان عمر رضي الله عنه استشار الناس فيه فقال له عبد الرحمن بن عوف أخف الحدود ثمانون ففعل قوله فضربه بجريدتين نحوا من أربعين أراد به أن','1864','2648','0');
INSERT INTO [t6227] VALUES (2681,'وأبي بكر وعمر وبين ما أشار به عبد الرحمن خلاف وروى البخاري في الصحيح عن السائب بن يزيد قال كنا نؤتي بالشارب في عهد رسول الله','1864','2648','0');
INSERT INTO [t6227] VALUES (2682,'أربعين وجلد أبو بكر أربعين وجلد عمر ثمانين وكل سنة وهذا أحب إلي وعنده في رواية ببعض معناه عنه وقال فيه ضرب رسول الله','1865','2648','0');
INSERT INTO [t6227] VALUES (2683,'يعني بالأيدي والنعال والعصي قالوا وكانوا في خلافة أبي بكر أكثر منه في عهد النبي','1865','2648','0');
INSERT INTO [t6227] VALUES (2684,'فكان يجلدهم أربعين حتى توفي ثم كان عمر رضي الله عنه من بعده فجلدهم كذلك أربعين حتى أتي برجل فذكر قصة ثم قال قال عمر فماذا ترون قال علي بن أبي طالب إنه إذا شرب سكر وإذا سكر هذى وإذا هذى افترى وعلى المفتري','1865','2648','0');
INSERT INTO [t6227] VALUES (2685,'لم يسنه وإنما أراد لم يسن ما وراء الأربعين إلى الثمانين وهو ما زادوا على حده على وجه التعزير فأما الأربعون بالجريد والنعال وأطراف الثياب فهو حد ثابت عن النبي','1866','2648','0');
INSERT INTO [t6227] VALUES (2686,'ثم في عهد أبي بكر رضي الله عنه وتوخيه في ذلك فعل رسول الله','1866','2648','0');
INSERT INTO [t6227] VALUES (2687,'مسألة','1866','2648','0');
INSERT INTO [t6227] VALUES (2688,'ومعه','1866','2648','0');
INSERT INTO [t6227] VALUES (2689,'فقام إليه بمشقص أو بمشاقص قال فكأني انظر إلى رسول الله','1867','2648','0');
INSERT INTO [t6227] VALUES (2690,'قال لو أن امرأ اطلع عليك بغير إذن فخذفته بحصاة ففقأت عينه ما كان عليك جناح وعند مسلم عنه عن رسول الله','1867','2648','0');
INSERT INTO [t6227] VALUES (2691,'قال من اطلع على قوم بغير إذنهم فرموه وأصابوا','1867','2648','0');
INSERT INTO [t6227] VALUES (2692,'مسألة','1868','2648','0');
INSERT INTO [t6227] VALUES (2693,'لم يسنه وإنما أراد والله أعلم أن رسول الله','1868','2648','0');
INSERT INTO [t6227] VALUES (2694,'فمن مات منه وديته إما قال في بيت المال وإما قال على عاقلة الإمام أشك يعني الشافعي وإنما أراد والله أعلم إذا مات فيما أحدثوه من الزيادة على الأربعين على وجه التعزير قال الشافعي رحمه الله وبلغنا أن عمر','1869','2648','0');
INSERT INTO [t6227] VALUES (2695,'الختان واجب وقال أبو حنيفة رحمه الله سنة دليلنا ما اتفقنا على صحته عن أبي هريرة رضي الله عنه قال قال رسول الله','1869','2648','0');
INSERT INTO [t6227] VALUES (2696,'فأسلم فقال النبي','1870','2648','0');
INSERT INTO [t6227] VALUES (2697,'لا تنهكي فإن ذلك أحظى للمرأة وأحب للبعل وروي عن ابن عباس رضي الله عنهما قال لا تقبل صلاة','1870','2648','0');
INSERT INTO [t6227] VALUES (2698,'وأمر به فيكون واجبا بدليل ما مضى وقد روي هذا مرفوعا ولا يصح رفعه وروي عن الحجاج بن أرطأة من حديث أسامة وأبي أيوب مرفوعا والحجاج لا يحتج به والله أعلم','1871','2648','0');
INSERT INTO [t6227] VALUES (2699,'قضى على أهل الحوائط حفظها بالنهار وعلى أهل الماشية ما أفسدت ماشيتهم بالليل وروى قتادة عن الشعبي أن شريحا رفعت إليه شاة أصابت غزلا فقال إن كان بليل فقد ضمنته وإن كان بنهار فلا ضمان عليكم','1873','2648','0');
INSERT INTO [t6227] VALUES (2700,'قال جرح العجماء جبار والبئر جبار والمعدن جبار وفي الركاز الخمس اتفقا على صحته قالوا فهذا يدل على أن حديث البراء صار منسوخا قال الشافعي رحمه الله فأخذنا به يعني بحديث البراء أيضا لثبوته واتصاله','1874','2648','0');
INSERT INTO [t6227] VALUES (2701,'مسألة','1874','2648','0');
INSERT INTO [t6227] VALUES (2702,'أنه قال العجماء جرحها جبار','1875','2648','0');
INSERT INTO [t6227] VALUES (2703,'قال الرجل جبار','1876','2648','0');
INSERT INTO [t6227] VALUES (2704,'كتاب السير','1879','2648','0');
INSERT INTO [t6227] VALUES (2705,'مسألة','1879','2648','0');
INSERT INTO [t6227] VALUES (2706,'قال من قتل عصفورا فما فوقها بغير حقها سأله الله عز وجل عن قتله قيل يا رسول الله وما حقها قال أن يذبحها فيأكلها ولا يقطع رأسها فيرمي بها','1879','2648','0');
INSERT INTO [t6227] VALUES (2707,'عن المصبورة وفي حديث أنس رضي الله عنه نهى رسول الله','1880','2648','0');
INSERT INTO [t6227] VALUES (2708,'قال الشافعي رحمه الله لا أعلمه مما يثبت عند الإفراد ولا أعلمه مشهورا عند عوام أهل العلم بالمغازي قال البيهقي رحمه الله الحفاظ يتوقون ما ينفرد به ابن إسحاق وإن صح فلعل جعفرا رضي الله عنه لم يبلغه','1881','2648','0');
INSERT INTO [t6227] VALUES (2709,'والغنيمة لمن شهد الوقعة أو كان ردأ لهم فأما من لم يحضرها ولم يكن ردأ لهم فلا حق له فيها وقال أبو حنيفة رحمه الله الغنيمة لكل من دخل دار الحرب قبل قسمتها روى طارق بن شهاب الأحمس قال غزت بنو عطارد ماء البصرة','1881','2648','0');
INSERT INTO [t6227] VALUES (2710,'بعث أبان بن سعيد على سرية من المدينة قبل نجد فقدم أبان بن سعيد وأصحابه على رسول الله','1882','2648','0');
INSERT INTO [t6227] VALUES (2711,'اجلس يا أبان ولم يقسم لهم رسول الله','1882','2648','0');
INSERT INTO [t6227] VALUES (2712,'حين','1882','2648','0');
INSERT INTO [t6227] VALUES (2713,'مسألة','1883','2648','0');
INSERT INTO [t6227] VALUES (2714,'من حنين بعث أبا عامر على جيش أوطاس فلقي دريد بن الصمة فقتل دريد وهزم الله تعالى أصحابه وذكر باقي ما في الحديث وفي آخره فلما رجعت إلى رسول الله','1883','2648','0');
INSERT INTO [t6227] VALUES (2715,'وجنبيه فأخبرته خبرنا وخبر أبي عامر وروى أبو داود عن سمرة رضي الله عنه قال قال رسول الله','1884','2648','0');
INSERT INTO [t6227] VALUES (2716,'قال انطلقوا بسم الله وبالله وعلى ملة رسول الله ولا تقتلوا شيخا فانيا ولا طفلا ولا صغيرا ولا امرأة ولا تغلوا وضموا غنائمكم وأصلحوا وأحسنوا إن الله يحب المحسنين وروي عن عكرمة عن ابن عباس رضي الله عنهما أن','1884','2648','0');
INSERT INTO [t6227] VALUES (2717,'مسألة','1885','2648','0');
INSERT INTO [t6227] VALUES (2718,'مسألة','1886','2648','0');
INSERT INTO [t6227] VALUES (2719,'وحديث عبادة بن الصامت رضي الله عنه أن رسول الله','1886','2648','0');
INSERT INTO [t6227] VALUES (2720,'مسألة','1887','2648','0');
INSERT INTO [t6227] VALUES (2721,'ثم انفلتت المرأة فركبت الناقة فأتت المدينة فعرفت ناقة النبي','1887','2648','0');
INSERT INTO [t6227] VALUES (2722,'قال بئسما جزيتها أن أنجاك الله عليها لا نذر في معصية الله ولا فيما لا يملك ابن آدم أخرجه مسلم في الصحيح وعند البخاري عن ابن عمر رضي الله عنهما قال ذهب فرس له فأخذه العدو فظهر عليهم المسلمون فرد عليه في زمن رسول','1887','2648','0');
INSERT INTO [t6227] VALUES (2723,'وعند أبي داود عن نافع عن ابن عمر رضي الله عنهما أن غلاما لابن عمر أبق إلى العدو فظهر عليه المسلمون فرده رسول الله','1888','2648','0');
INSERT INTO [t6227] VALUES (2724,'فذكر ذلك له فقال رسول الله','1888','2648','0');
INSERT INTO [t6227] VALUES (2725,'وقبيصة لم يدرك عمر رضي الله عنه والله أعلم','1889','2648','0');
INSERT INTO [t6227] VALUES (2726,'من دخل دار أبي سفيان فهو آمن ومن أغلق عليه داره فهو آمن ومن دخل المسجد فهو آمن قال فافترق الناس إلى دورهم وإلى المسجد وعنده عن وهب قال سألت جابرا هل غنموا يوم الفتح شيئا قال لا','1891','2648','0');
INSERT INTO [t6227] VALUES (2727,'لما دخل مكة سرح الزبير بن العوام وأبا عبيدة بن الجراح وخالد بن الوليد على الخيل وقال يا أبا هريرة اهتف بالأنصار قال اسلكوا هذا الطريق فلا يشرفن لكم أحد إلا أمنتموه فنادى مناد لا قريش بعد اليوم فقال رسول الله','1892','2648','0');
INSERT INTO [t6227] VALUES (2728,'وصلى خلف المقام ثم أخذ بجنبتي الباب فخرجوا فبايعوا النبي','1892','2648','0');
INSERT INTO [t6227] VALUES (2729,'مسألة','1892','2648','0');
INSERT INTO [t6227] VALUES (2730,'بعث يوم حنين جيشا إلى أوطاس فلقوا عدوا فقاتلوهم وظهروا عليهم فأصابوا لهم سبايا فكأن أناسا من أصحاب رسول الله','1892','2648','0');
INSERT INTO [t6227] VALUES (2731,'أن توطأ حامل حتى تضع أو حائل حتى تحيض قال لنا ابن صاعد وما قال لنا في هذا الإسناد أحد عن ابن عباس رضي الله عنهما إلا الغامدي والله أعلم','1893','2648','0');
INSERT INTO [t6227] VALUES (2732,'والتفريق بين الأمة وولدها قبل سن التمييز في الملك بالبيع والقسمة باطل وقال أبو حنيفة رحمه الله إذا فرق بينهما في','1893','2648','0');
INSERT INTO [t6227] VALUES (2733,'عن ذلك ورد البيع والله أعلم','1894','2648','0');
INSERT INTO [t6227] VALUES (2734,'كتاب الجزية','1895','2648','0');
INSERT INTO [t6227] VALUES (2735,'مسألة','1895','2648','0');
INSERT INTO [t6227] VALUES (2736,'أمرت أن أقاتل الناس حتى يقولوا لا إله إلا الله الحديث','1895','2648','0');
INSERT INTO [t6227] VALUES (2737,'أخذها من مجوس هجر وروى الشافعي رحمه الله عن سفيان عن أبي سعيد بن المرباز عن نصر بن عاصم فذكر قصة فيها عن علي رضي الله عنه أنا أعلم الناس بالمجوس كان لهم علم يعلمونه وكتاب يدرسونه وإن ملكهم سكر فوقع على ابنته أو أخته','1896','2648','0');
INSERT INTO [t6227] VALUES (2738,'إذا بعث أميرا على سرية أو جيش أوصاه بتقوى الله وقال إذا لقيت عدوك من المشركين فادعهم إلى','1896','2648','0');
INSERT INTO [t6227] VALUES (2739,'كان يقاتل كفار قريش بنفسه ويقاتل أهل الكتاب بنفسه وبأصحابه وما بلغنا أنه دعا كفار قريش إلى إعطاء الجزية والله أعلم','1897','2648','0');
INSERT INTO [t6227] VALUES (2740,'وأقل الجزية دينار والغني والفقير فيه سواء وقال أبو حنيفة رحمه الله هي على ثلاث مراتب على الفقير دينار وعلى المتوسط ديناران وعلى الغني أربعة دنانير وروى في ذلك خبر مرسل عن إبراهيم عن مسروق قالا قال معاذ بعثني','1897','2648','0');
INSERT INTO [t6227] VALUES (2741,'كان لأهل ذمة اليمن على دينار كل سنة وروى مالك عن نافع عن سالم مولى عمر أن عمر رضي الله عنه ضرب الجزية على أهل الذهب أربعة دنانير وعلى أهل الورق أربعين درهما ومع ذلك أرزاق المسلمين وضيافة ثلاثة أيام والله أعلم','1898','2648','0');
INSERT INTO [t6227] VALUES (2742,'مسألة','1899','2648','0');
INSERT INTO [t6227] VALUES (2743,'كتاب الصيد والذبائح','1900','2648','0');
INSERT INTO [t6227] VALUES (2744,'مسألة','1900','2648','0');
INSERT INTO [t6227] VALUES (2745,'في صيد الكلب إذا أرسلت كلبك وذكرت اسم الله عليه فكل وإن أكل منه وكل ما ردت يدك وعن عمرو بن شعيب عن أبيه عن جده أن أعرابيا يقال','1900','2648','0');
INSERT INTO [t6227] VALUES (2746,'إن كان لك كلاب مكلبة فكل ما أمسكن عليك قال وإن أكل منه قال وإن أكل منه وقال مالك حدثني من سمع نافعا يقول قال عبد الله رضي الله عنه وإن أكل أو لم يأكل وعن مالك أنه بلغه أن سعد بن أبي وقاص رضي','1901','2648','0');
INSERT INTO [t6227] VALUES (2747,'عن الكلب قلت إنا قوم نصيد بهذه الكلاب فقال إذا أرسلت كلابك المعلمة فذكرت اسم الله عليها فكل مما أمسكن عليك وإن قتلن إلا أن يأكل الكلب فإن أكل فلا تأكل فإني أخاف أن يكون إنما أمسك على نفسه وإن خالطها كلاب من غيرها فلا تأكل','1902','2648','0');
INSERT INTO [t6227] VALUES (2748,'مسألة','1903','2648','0');
INSERT INTO [t6227] VALUES (2749,'سموا الله عليه وكلوا كذا رواه البخاري عن أبي أشعث عن محمد بن عبد الرحمن قال وتابعه الدراوردي عن هشام وكذلك رواه أبو خالد الأحمر ومحاضر ومسلمة بن قعنب ويونس وحاتم وعبد الرحمن بن سليمان وأسامة بن حفص عن هشام','1903','2648','0');
INSERT INTO [t6227] VALUES (2750,'سموا عليها ثم كلوا قال البيهقي رحمه الله كذا وجدته في كتابي في الثاني من الثالث والعشرين من الفوائد الكبير وروي عن أبي العشراء الدارمي واسمه أسامة بن مالك بن فهطم وقال بعضهم عطارد بن برز عن أبيه أنه قال','1904','2648','0');
INSERT INTO [t6227] VALUES (2751,'عن المعراض فقال إذا أصبت بحده وذكرت اسم الله فكل وإذا أصبت بعرضه فإنه وقيذ فلا تأكل قلت يا رسول الله أرسل كلبي وأجد معه كلبا آخر قد أخذ لا أدري أيهما أخذ قال فلا تأكل فإنما سميت على كلبك ولم تسم على غيره','1905','2648','0');
INSERT INTO [t6227] VALUES (2752,'مسألة','1905','2648','0');
INSERT INTO [t6227] VALUES (2753,'كل','1906','2648','0');
INSERT INTO [t6227] VALUES (2754,'وإن رميت بسهمك فاذكر اسم الله وإن غاب عنك يوما فلم تجد فيه إلا أثر سهمك فكل إن شئت وإن وجدته غريقا في الماء فلا تأكل قال أصحابنا إنما أراد به إذا أصاب السهم مقتله بمرأى منه ثم غاب عنه وذلك يحل أكله وقد رواه عباد','1907','2648','0');
INSERT INTO [t6227] VALUES (2755,'أرمي بسهمي فأصيب فلا اقدر عليه إلا بعد يوم أو يومين قال إن قدرت عليه وليس به أثر ولا خدش إلا رميتك فكل وإن وجدت به أثرا غير رميتك فلا تأكل أو قال فلا تطعمه وعند مسلم عن أبي ثعلبة رضي الله عنه عن النبي','1908','2648','0');
INSERT INTO [t6227] VALUES (2756,'مسألة','1908','2648','0');
INSERT INTO [t6227] VALUES (2757,'يا أبا ثعلبة كل ما ردت عليك قوسك وكلبك المعلم ويدك فكل ذكي وغير ذكي','1908','2648','0');
INSERT INTO [t6227] VALUES (2758,'المدينة والناس يحتون أسنام الإبل ويقطعون أليات الغنم فقال النبي','1909','2648','0');
INSERT INTO [t6227] VALUES (2759,'مسألة','1909','2648','0');
INSERT INTO [t6227] VALUES (2760,'فقلت يا رسول الله إنا نلقى العدو غدا وليس معنا مدى فقال رسول الله','1909','2648','0');
INSERT INTO [t6227] VALUES (2761,'إنا نرجو أو نخشى أن نلقى العدو وليس معنا مدى أفنذبح بالقصب فقال رسول الله','1910','2648','0');
INSERT INTO [t6227] VALUES (2762,'مسألة','1910','2648','0');
INSERT INTO [t6227] VALUES (2763,'في البحر هو الطهور ماؤه والحل ميتته','1910','2648','0');
INSERT INTO [t6227] VALUES (2764,'فقال رزق أخرجه الله أطعمونا إن كان معكم فأتاه بعضو فأكله وروي عن ابن عباس عن أبي بكر الصديق رضي الله عنهم عنه قال كل السمكة الطافية وروي عن زيد بن أسلم عن عبد الله بن عمر رضي الله عنهما قال السمك ذكي','1911','2648','0');
INSERT INTO [t6227] VALUES (2765,'وقد قيل عن النبي','1912','2648','0');
INSERT INTO [t6227] VALUES (2766,'وعبد الرحمن بن زيد متروك الحديث وروي عن ابن أبي أويس عن عبد الرحمن وأسامة وعبد الله بن زيد بن أسلم عن أبيهم مسندا أيضا وروى أبو أحمد الزبيري عن الثوري عن أبي الزبير عن جابر','1912','2648','0');
INSERT INTO [t6227] VALUES (2767,'كتاب الضحايا','1915','2648','0');
INSERT INTO [t6227] VALUES (2768,'مسألة','1915','2648','0');
INSERT INTO [t6227] VALUES (2769,'إذا دخل العشر فأراد أحدكم أن يضحي فلا يمس من شعره ولا بشره شيئا أخرج معناه مسلم في الصحيح وروي عن ابن عباس رضي الله عنهما أن رسول الله','1915','2648','0');
INSERT INTO [t6227] VALUES (2770,'صلى للناس يوم النحر فلما فرغ من خطبته وصلاته دعا بكبش فذبحه هو بنفسه وقال بسم الله والله أكبر اللهم عني وعمن لم يضح من أمتي وعن أبي مسعود الأنصاري رضي الله عنه إني لأترك الأضحى وإني موسر كراهة أن يرى جيراني وأهل','1916','2648','0');
INSERT INTO [t6227] VALUES (2771,'قال يا أيها الناس إن على أهل كل بيت في كل عام أضحية وعتيرة أتدرون ما العتيرة هذه التي يقول الناس الرجبية وروي عن عبد الله بن شريح عن عبد الله القتباني عن الأعرج عن أبي هريرة رضي الله عنه مرفوعا من وجد سعة فلم يذبح','1917','2648','0');
INSERT INTO [t6227] VALUES (2772,'مسألة','1918','2648','0');
INSERT INTO [t6227] VALUES (2773,'وذلك حين حلت الصلاة وقدر خطبتين خفيفتين إذا كان هذا فقد حل الذبح لكل أحد حيث كان وقال أبو حنيفة رحمه الله يعتبر فعل الصلاة وفي صحيح مسلم عن جابر رضي الله عنه قال قال رسول الله','1918','2648','0');
INSERT INTO [t6227] VALUES (2774,'إن أول ما نبدأ به في يومنا هذا أن نصلي ثم ترجع','1918','2648','0');
INSERT INTO [t6227] VALUES (2775,'يوم النحر يقول من ذبح قبل أن يصلي فليعد مكانها ومن لم يذبح فليذبح وعند مسلم عنه شهدت الأضحى مع رسول الله','1919','2648','0');
INSERT INTO [t6227] VALUES (2776,'مسألة','1919','2648','0');
INSERT INTO [t6227] VALUES (2777,'مسألة','1920','2648','0');
INSERT INTO [t6227] VALUES (2778,'قال ذكاة الجنين ذكاة أمه وعن أبي سعيد الخدري رضي الله عنه قال سألت رسول الله','1920','2648','0');
INSERT INTO [t6227] VALUES (2779,'مسألة','1921','2648','0');
INSERT INTO [t6227] VALUES (2780,'قال نعم قال أبو عيسى الترمذي سألت محمد بن إسماعيل عن هذا الحديث فقال هو حديث صحيح وعن عطاء عن جابر رضي الله عنه قال قال رسول الله','1922','2648','0');
INSERT INTO [t6227] VALUES (2781,'نهى عن أكل كل ذي ناب من السباع وفي صحيح مسلم عن ابن عباس رضي الله عنهما نهى رسول الله','1922','2648','0');
INSERT INTO [t6227] VALUES (2782,'مسألة','1923','2648','0');
INSERT INTO [t6227] VALUES (2783,'بيت ميمونة زوج النبي','1923','2648','0');
INSERT INTO [t6227] VALUES (2784,'يده فقال بعض النسوة اللاتي في بيت ميمونة زوج النبي','1923','2648','0');
INSERT INTO [t6227] VALUES (2785,'بما يريد أن يأكل فقالوا هو ضب فرفع يده فقلت أحرام هو قال لا ولكنه لم يكن بأرض قومي فأجدني أعافه قال فاجتررته فأكلته ورسول الله','1923','2648','0');
INSERT INTO [t6227] VALUES (2786,'فيهم سعد فذهبوا يأكلون من لحم فنادتهم امرأة من بعض أزواج النبي','1923','2648','0');
INSERT INTO [t6227] VALUES (2787,'كلوا وأطعموا فإنه حلال أو قال لا بأس به ولكنه ليس من طعام قومي','1923','2648','0');
INSERT INTO [t6227] VALUES (2788,'أقطا وسمنا وأضبا فأكل رسول الله','1924','2648','0');
INSERT INTO [t6227] VALUES (2789,'ولو كان حراما ما أكل على مائدته وفي الصحيحين عن ابن عمر رضي الله عنهما سئل النبي','1924','2648','0');
INSERT INTO [t6227] VALUES (2790,'وروى إسماعيل بن عياش عن ضمضم عن شريح عن أبي راشد عن عبد الرحمن بن شبل رضي الله عنه أن رسول الله','1924','2648','0');
INSERT INTO [t6227] VALUES (2791,'مسألة','1925','2648','0');
INSERT INTO [t6227] VALUES (2792,'وعند مسلم أن رسول الله','1925','2648','0');
INSERT INTO [t6227] VALUES (2793,'عن الحمار الأهلي وعنده عن أسماء بنت أبي بكر رضي الله عنهما قالت أكلنا لحم فرس على عهد النبي','1925','2648','0');
INSERT INTO [t6227] VALUES (2794,'فأكلناه','1925','2648','0');
INSERT INTO [t6227] VALUES (2795,'نهى عن أكل لحوم الخيل كذا رواه محمد بن عمر الواقداني عن ثور إلا أنه قال نهى يوم خيبر عن أكل لحوم الخيل قال الدارقطني حدثنا أبو سهل قال سمعت محمد بن هارون يقول لا يعرف صالح بن يحيى ولا أخوه إلا بجده وهذا ضعيف','1926','2648','0');
INSERT INTO [t6227] VALUES (2796,'نهى يوم خيبر عن كل ذي ناب من السباع وعن كل ذي مخلب من الطير وعن المجثمة وعن لحوم الخيل وعن لحوم الحمر الأهلية وأن توطأ الحبالى حتى يضعن ما في بطونهن والله أعلم','1927','2648','0');
INSERT INTO [t6227] VALUES (2797,'لا يجوز بيع الزيت النجس وقال أبو حنيفة رحمه الله يجوز وفي صحيح البخاري عن ميمونة رضي الله عنها أن النبي','1927','2648','0');
INSERT INTO [t6227] VALUES (2798,'إذا وقعت الفأرة في السمن فإن كان جامدا فألقوها وما حولها وإن كان مائعا فلا تقربوه وروي عن ابن عمر رضي الله عنهما مرفوعا في السمن المائع والودك قال فانتفعوا به ولا تأكلوه وعن أبي موسى رضي الله عنه في الفأرة','1928','2648','0');
INSERT INTO [t6227] VALUES (2799,'ومن اضطر إلى الميتة حل له أن يتناول منها مقدار الشبع في أحد القولين وقال المزني وأبو حنيفة رحمه الله لا يحل له منها إلا قدر ما يسد الرمق روي عن جابر بن سمرة رضي الله عنه أن رجلا كانت له','1928','2648','0');
INSERT INTO [t6227] VALUES (2800,'فذكر له ذلك فقال أعندكم ما يغنيكم قال لا قال فكلوها وكانت قد ماتت قال فأكلنا من ودكها ولحمها وشحمها نحوا من عشرين يوما ثم لقي صاحبها فقال له ألا كنت نحرتها قال إني كنت استحييت منك رواه أبو داود الطيالسي عن شريك','1929','2648','0');
INSERT INTO [t6227] VALUES (2801,'كتاب السبق والرمي','1931','2648','0');
INSERT INTO [t6227] VALUES (2802,'مسألة','1931','2648','0');
INSERT INTO [t6227] VALUES (2803,'قال لا سبق إلا في نصل أو حافر أو خف','1931','2648','0');
INSERT INTO [t6227] VALUES (2804,'سابق بين الخيل التي قد أضمرت من الحفياء وكان أمدها ثنية الوداع وسابق بين الخيل التي لم تضمر من الثنية إلى مسجد بني زريق وأن عبد الله بن عمر فيمن سابق بها وروي عن أبي هريرة رضي الله عنه من أدخل فرسا بين فرسين ولا يأمن أن','1932','2648','0');
INSERT INTO [t6227] VALUES (2805,'على فرس له يقال لها سبخة فجاءت سابقة فهش لذلك أعجبه','1932','2648','0');
INSERT INTO [t6227] VALUES (2806,'قال نعم والله لقد راهن على فرس له يقال لها سبخة فجاءت سابقة فبهش لذلك وأعجبه والله أعلم','1933','2648','0');
INSERT INTO [t6227] VALUES (2807,'كتاب الأيمان والنذور','1935','2648','0');
INSERT INTO [t6227] VALUES (2808,'مسألة','1935','2648','0');
INSERT INTO [t6227] VALUES (2809,'أنه قال من حلف بملة غير الإسلام كاذبا فهو كما قال ومن قتل نفسه بشيء عذب به يوم القيامة في نار جهنم ولعن المؤمن كقتله ومن رمى مؤمنا بكفر فهو كقتله','1935','2648','0');
INSERT INTO [t6227] VALUES (2810,'من حلف باللات والعزى فليقل لا إله إلا الله ولم ينسبه إلى الكفر والله أعلم','1936','2648','0');
INSERT INTO [t6227] VALUES (2811,'والكفارة واجبة في يمين الغموس وقال أبو حنيفة رحمه الله لا كفارة قال الله عز وجل لا يؤاخذكم الله باللغو في أيمانكم ولكن','1936','2648','0');
INSERT INTO [t6227] VALUES (2812,'قال هو كلام الرجل في بيته كلا والله بل والله وروى الشافعي رحمه الله عن مالك عن هشام عن أبيه عن عائشة رضي الله عنها أنها قالت لغو اليمين قول الإنسان لا والله وبلى والله أخرجه البخاري في الصحيح من حديث هشام عن','1937','2648','0');
INSERT INTO [t6227] VALUES (2813,'فسأل رسول الله','1938','2648','0');
INSERT INTO [t6227] VALUES (2814,'بلى قد فعلت ولكن غفر لك بإخلاص قول لا إله إلا الله وروى أبو حنيفة عن يحيى بن أبي كثير عن مجاهد وعكرمة عن أبي هريرة رضي الله عنه قال قال رسول الله','1939','2648','0');
INSERT INTO [t6227] VALUES (2815,'مسألة','1939','2648','0');
INSERT INTO [t6227] VALUES (2816,'في نفر من الأشعريين أستحمله فقال والله لا أحملكم فذكر الحديث إلى أن قال فأتينا النبي','1940','2648','0');
INSERT INTO [t6227] VALUES (2817,'أنه قال من حلف على يمين فرأى خيرا منها فليكفر عن يمينه وليفعل وعنده عن عدي بن حاتم رضي الله عنه قال قال رسول الله','1940','2648','0');
INSERT INTO [t6227] VALUES (2818,'يا عبد الرحمن لا تسأل الإمارة فإنك إن أعطيتها عن مسألة وكلت إليها وإن أعطيتها عن غير مسألة أعنت عليها وإذا','1940','2648','0');
INSERT INTO [t6227] VALUES (2819,'قال له يا عبد الرحمن إذا حلفت على يمين فرأيت غيرها خيرا منها فكفر عن يمينك ثم ائت الذي هو خير وعند البخاري عن عائشة رضي الله عنها قال كان أبو بكر إذا حلف لا يحنث حتى نزلت الكفارة قالت فقال أبو بكر لا أحلف','1941','2648','0');
INSERT INTO [t6227] VALUES (2820,'مسألة','1942','2648','0');
INSERT INTO [t6227] VALUES (2821,'مسألة','1943','2648','0');
INSERT INTO [t6227] VALUES (2822,'أخذ كسرة من خبز شعير فوضع عليها تمرة وقال هذه أدام هذه فأكلها','1943','2648','0');
INSERT INTO [t6227] VALUES (2823,'مسالة','1944','2648','0');
INSERT INTO [t6227] VALUES (2824,'بأثكال النخل في الزنا وروي عن أبي أمامة بن سهل بن حنيف عن أبيه قال أخذ رسول الله','1944','2648','0');
INSERT INTO [t6227] VALUES (2825,'من الأنصار أنه اشتكى رجل منهم حتى أضنى فذكر قصة في زناه قال فأمر رسول الله','1944','2648','0');
INSERT INTO [t6227] VALUES (2826,'مسألة','1945','2648','0');
INSERT INTO [t6227] VALUES (2827,'قال كفارة النذر كفارة يمين وعند أبي داود عن كريب عن ابن عباس رضي الله عنهما أن رسول الله','1945','2648','0');
INSERT INTO [t6227] VALUES (2828,'يقول لا يمين عليك ولا نذر في معصية الرب ولا في قطيعة الرحم ولا فيما لا تملك وروى الدارقطني بسنده عن أبي رافع أن مولاته أرادت أن تفرق بينه وبين امرأته فقالت هي يوما يهودية وهي يوما نصرانية وكل مملوك لها حر وكل مال','1946','2648','0');
INSERT INTO [t6227] VALUES (2829,'مسألة','1947','2648','0');
INSERT INTO [t6227] VALUES (2830,'قال من نذر أن يطيع الله فليطعه ومن نذر أن يعصي الله فلا يعصه رواه البخاري في الصحيح عن أبي عاصم وأبي نعيم عن مالك بإسناده ومعناه وروى الشافعي رحمه الله عن عبد الوهاب بن عبد المجيد عن أبي أيوب عن أبي قلابة عن أبي المهلب عن','1948','2648','0');
INSERT INTO [t6227] VALUES (2831,'قال لا نذر في معصية الله وكفارته كفارة يمين وهو موافق لفتوى ابن عباس وروي عن ابن عباس رضي الله عنهما أنه قال لرجل نذر أن ينحر نفسه وهم يفعل ذلك ويحل لقد أردت أن تحل ثلاث خصال أن تحل بلدا حراما وأن تقطع رحما','1949','2648','0');
INSERT INTO [t6227] VALUES (2832,'مسألة','1950','2648','0');
INSERT INTO [t6227] VALUES (2833,'رأى رجلا يهادي بين ابنيه فسأل عنه فقالوا نذر أن يمشي فقال إن الله عز وجل لغني عن تعذيب هذا نفسه وأمره أن يركب وفي الصحيحين أيضا عن عقبة بن عامر رضي الله عنه قال نذرت أختي أن تمشي إلى بيت الله فأمرتني أن','1951','2648','0');
INSERT INTO [t6227] VALUES (2834,'فيه فإذا نذر أحدكم أن يحج ماشيا فليهد هديا وليركب وفي هذا نظر فالمحدثون يقولون الحسن لم يسمع من عمران فهو مرسل وروي عن جعفر بن عون عن يحيى بن سعيد عن عبيد الله بن زحر عن أبي سعيد الرعيني عن عبد الله بن مالك عن عقبة بن','1952','2648','0');
INSERT INTO [t6227] VALUES (2835,'مسألة','1954','2648','0');
INSERT INTO [t6227] VALUES (2836,'قال لا تشدوا الرحال إلا إلى ثلاثة مساجد المسجد الحرام ومسجدي هذا والمسجد الأقصى أخرجه مسلم في الصحيح بلفظه ومعناه وروي عن طلق بن حبيب أن فرعة قال لابن عمر إني نذرت أن أخرج إلى بيت المقدس قال إنما تشد الرحال إلى','1954','2648','0');
INSERT INTO [t6227] VALUES (2837,'شأنك إذن وروي نحوه عن عبد الرحمن بن عوف عن النبي','1955','2648','0');
INSERT INTO [t6227] VALUES (2838,'كتاب القاضي','1957','2648','0');
INSERT INTO [t6227] VALUES (2839,'مسألة','1957','2648','0');
INSERT INTO [t6227] VALUES (2840,'من سمع رجلا ينشد ضالة في المسجد فليقل لا ردها الله عليك فإن المساجد لم تبن لهذا','1959','2648','0');
INSERT INTO [t6227] VALUES (2841,'وقال إنما بنيت المساجد لما بنيت له وفي الصحيحين عن أنس رضي الله عنه قال جاء أعرابي إلى المسجد فبال في المسجد فقال أصحاب النبي','1960','2648','0');
INSERT INTO [t6227] VALUES (2842,'لا تزرموه فلما فرغ دعا به النبي','1960','2648','0');
INSERT INTO [t6227] VALUES (2843,'وهو على المنبر يقول جنبوا مساجدكم صبيانكم ومجانينكم وخصوماتكم ورفع أصواتكم وسل سيوفكم وإقامة حدودكم واعمروها في الجمع واتخذوا على أبواب مساجدكم مطاهر والله أعلم','1960','2648','0');
INSERT INTO [t6227] VALUES (2844,'مسألة','1961','2648','0');
INSERT INTO [t6227] VALUES (2845,'إذا حكم الحاكم فاجتهد فقرنه بالاجتهاد وروى أبو داود الطيالسي عن شعبة عن أبي عون الثقفي قال سمعت الحارث بن عمر يحدث عن أصحاب معاذ من أهل حمص','1962','2648','0');
INSERT INTO [t6227] VALUES (2846,'لما بعث معاذا إلى اليمن قال له كيف تقضي إذا عرض لك قضاء قال أقضي بكتاب الله قال فإن لم تجده في كتاب الله قال أقضي بسنة رسول الله','1963','2648','0');
INSERT INTO [t6227] VALUES (2847,'قال أجتهد رأيي ولا آلو قال فضرب بيده في صدري وقال الحمد لله الذي وفق رسول رسول الله','1963','2648','0');
INSERT INTO [t6227] VALUES (2848,'إلى اليمن قاضيا فقلت يا رسول الله ترسلني وأنا حديث السن ولا علم لي بالقضاء فقال إن الله جل ثناؤه سيهدي قلبك ويثبت لسانك فإذا جلس بين يديك الخصمان فلا تقضين حتى تسمع من الآخر كما سمعت من الأول فإنه أحرى أن يتبين لك','1963','2648','0');
INSERT INTO [t6227] VALUES (2849,'يقول إذا حكم الحاكم فاجتهد فأصاب فله أجران وإذا حكم الحاكم فاجتهد فأخطأ فله أجر وعن أبي بكر بن عمرو بن حزم هكذا حدثني أبو سلمة عن أبي هريرة رضي الله عنه عن النبي','1964','2648','0');
INSERT INTO [t6227] VALUES (2850,'قال القضاة ثلاثة واحد في الجنة واثنان في النار فأما الذي في الجنة فرجل عرف الحق فقضى به ورجل عرف الحق وجار في الحكم ورجل قضى للناس على جهل فهما في النار وروي عن معاوية بن صالح عن أشياخهم أن عمر بن الخطاب رضي الله عنه','1964','2648','0');
INSERT INTO [t6227] VALUES (2851,'مسألة','1965','2648','0');
INSERT INTO [t6227] VALUES (2852,'بعد ما كدت أن ألحق بأصحاب الجمل وأقاتل معهم قال بلغ رسول الله','1965','2648','0');
INSERT INTO [t6227] VALUES (2853,'يا معشر النساء أكثرن الاستغفار فإني رأيتكن أكثر أهل النار فقالت امرأة منهن وما لنا يا رسول الله أكثر أهل النار قال تكثرن اللعن وتكفرن العشير وما من ناقصات عقل ودين أغلب لذي اللب منكن قالت يا رسول الله وما نقصان العقل','1965','2648','0');
INSERT INTO [t6227] VALUES (2854,'مسألة','1966','2648','0');
INSERT INTO [t6227] VALUES (2855,'إن أبا سفيان رجل شحيح أعلي جناح أن آخذ من ماله قال خذي ما يكفيك وولدك بالمعروف وحديث أبي قلابة عند عمر بن عبد العزيز في شأن القسامة وقال حدثنا أنس بن مالك رضي الله عنه وذكر حديث القوم من عكل وعرينة في صحيح','1968','2648','0');
INSERT INTO [t6227] VALUES (2856,'مسألة','1969','2648','0');
INSERT INTO [t6227] VALUES (2857,'جلب خصم عند بابها فخرج عليهم فقال إنكم تختصمون إلي وإنما أنا بشر ولعل بعضكم أن يكون أعلم بحجته من بعض فأقضي له بما أسمع وأظنه صادقا فمن قضيت له بشيء من حق أخيه فإنها قطعة من النار فليأخذها أو ليذرها وفي الصحيحين عن','1973','2648','0');
INSERT INTO [t6227] VALUES (2858,'من حلف على يمين ليستحق بها مالا هو فيها فاجر لقي الله تعالى وهو عليه غضبان فأنزل الله تعالى تصديق ذلك ثم قرأ هذه الآية إن الذين يشترون بعهد الله وأيمانهم ثمنا قليلا إلى آخر الآية وفي صحيح','1974','2648','0');
INSERT INTO [t6227] VALUES (2859,'فأتيته فقال إن الله صدقك وعذرك ونزل هم الذين يقولون لا تنفقوا على من عند رسول الله حتى ينفضوا الآية واتفقا على حديث جابر بن عبد الله رضي الله عنه في هذا الباب وفيه فقال عمر رضي','1974','2648','0');
INSERT INTO [t6227] VALUES (2860,'أن رسول الله','1975','2648','0');
INSERT INTO [t6227] VALUES (2861,'ليقض ثمن فرسه فأسرع رسول الله','1975','2648','0');
INSERT INTO [t6227] VALUES (2862,'إن كنت مبتاعا هذا الفرس فابتعه وإلا بعته فقام رسول الله','1975','2648','0');
INSERT INTO [t6227] VALUES (2863,'أو ليس قد ابتعت منك قال لا والله ما بعتكه قال بلى ابتعته منك فطفق الناس يلوذون برسول الله','1975','2648','0');
INSERT INTO [t6227] VALUES (2864,'على خزيمة فقال بم تشهد فقال بتصديقك فجعل رسول الله','1975','2648','0');
INSERT INTO [t6227] VALUES (2865,'أرسلت إلى أبي بكر رضي الله عنه تسأله','1975','2648','0');
INSERT INTO [t6227] VALUES (2866,'مما أفاء الله عليه بالمدينة وفدك وما بقي من خمس خيبر فقال أبو بكر رضي الله عنه إن رسول الله','1976','2648','0');
INSERT INTO [t6227] VALUES (2867,'عن حالها التي كانت عليها في عهد رسول الله','1976','2648','0');
INSERT INTO [t6227] VALUES (2868,'وذكر باقي الحديث والله أعلم','1976','2648','0');
INSERT INTO [t6227] VALUES (2869,'كتاب الشهادات','1977','2648','0');
INSERT INTO [t6227] VALUES (2870,'مسألة','1977','2648','0');
INSERT INTO [t6227] VALUES (2871,'لكل غادر لواء يوم القيامة يعرف به وقال ولا نكاح إلا بولي وشاهدي عدل وقال أيما امرأة نكحت بغير إذن وليها فنكاحها باطل وقال من فعل في أمرنا ما لا يجوز فهو رد وفي الصحيحين حديث أم سلمة رضي الله عنها أن','1978','2648','0');
INSERT INTO [t6227] VALUES (2872,'فأقبل معه عبد بن زمعة فقال سعد يا رسول الله هذا ابن أخي عهد إلي أنه ابنه قال عبد يا رسول الله هذا أخي ابن زمعة وولد على فراشه فنظر النبي','1978','2648','0');
INSERT INTO [t6227] VALUES (2873,'هو لك يا عبد بن زمعة من أجل أنه ولد على فراش أبيه وقال النبي','1979','2648','0');
INSERT INTO [t6227] VALUES (2874,'وفي الصحيحين عن عائشة رضي الله عنها قالت قال رسول الله','1979','2648','0');
INSERT INTO [t6227] VALUES (2875,'قال من فعل في أمرنا ما لا يجوز فهو رد وروى الدارقطني عن أبي جعفر محمد بن سليمان النعماني حدثنا عبد الله بن عبد الصمد بن أبي خداش حدثنا عيسى بن يونس حدثنا عبيد الله ابن أبي حميد عن أبي المليح الهذلي قال كتب عمر رضي الله عنه','1979','2648','0');
INSERT INTO [t6227] VALUES (2876,'مسألة','1983','2648','0');
INSERT INTO [t6227] VALUES (2877,'أنه قال يا معشر النساء تصدقن وأكثرن من الاستغفار فإني رأيتكن أكثر أهل النار فقالت امرأة منهن جزلة وما لنا يا رسول الله أكثر أهل النار قال تكثرن اللعن وتكفرن العشير ما رأيت من ناقصات عقل ودين أغلب لذي لب منكن قال يا','1983','2648','0');
INSERT INTO [t6227] VALUES (2878,'مسألة','1984','2648','0');
INSERT INTO [t6227] VALUES (2879,'التائب من الذنب كمن لا ذنب له وقال عمر رضي الله عنه لأبي بكرة تب تقبل شهادتك وروى الشافعي رحمه الله عن ابن عيينة قال سمعت الزهري يقول زعم أهل العراق أن شهادة القاذف لا تجوز فأشهد لأخبرني سعيد بن المسيب','1985','2648','0');
INSERT INTO [t6227] VALUES (2880,'ويزيد ضعيف لا يحتج به ويحيى بن سعيد هذا هو الفارسي متروك وعبد الأعلى ضعيف قال ذلك الدارقطني رحمه الله والله أعلم','1986','2648','0');
INSERT INTO [t6227] VALUES (2881,'وشهادة الكافر عندنا مردودة في جميع الأحوال وقال أبو حنيفة رحمه الله شهادة أهل الذمة فيما بينهم مقبولة','1986','2648','0');
INSERT INTO [t6227] VALUES (2882,'لا','1987','2648','0');
INSERT INTO [t6227] VALUES (2883,'فإنها تجوز على غيرهم قال أبو عبد الرحمن شاذان فسألت عن هذا الشيخ بعد أصحابنا فزعم أنه عمر بن راشد الحنفي تابعه الحسن بن موسى عن عمر بن راشد ورواه علي بن الجعدي عن عمر بن راشد إلا أنه قال وحدثه عن أبي هريرة أحسبه','1988','2648','0');
INSERT INTO [t6227] VALUES (2884,'مسألة','1989','2648','0');
INSERT INTO [t6227] VALUES (2885,'قضى بشاهد ويمين قال الحاكم أبو عبد الله رحمه الله تعالى وقد تعرض لهذا الحديث بعض المخالفين ممن ليس من صناعته معرفة الصحيح من السقيم واحتج فيه بما روي عن يحيى بن معين سمعت أبا العباس يقول سمعت العباس الدوري يقول قال يحيى بن','1992','2648','0');
INSERT INTO [t6227] VALUES (2886,'اغسلوه بماء وسدر ولا تخمروا رأسه فإن الله يبعثه يوم القيامة وهو يلبي ولا يبعد أن يكون له عن عمرو غير هذا وليس من شرط قبول الأخبار كثرة رواية الراوي عمن روى عنه وإذا روى الثقة عمن لا ينكر سماعه منه حديثا واحدا وجب قبوله','1993','2648','0');
INSERT INTO [t6227] VALUES (2887,'قضى باليمين مع الشاهد ورواه كذلك عنه أبو حذيفة ورواه أبو داود في كتاب السنن من حديث عبد الرزاق عن محمد بن مسلم بإسناده ومعناه وخالفهما خالد بن يزيد العمري عن محمد بن مسلم فرواه عنه عن عمرو عن طاوس عن ابن عباس وتابعه على ذلك','1994','2648','0');
INSERT INTO [t6227] VALUES (2888,'قضى باليمين مع الشاهد قال عبد العزيز فذكرت ذلك لسهيل قال أخبرني ربيعة وهو عندي ثقة أني حدثته إياه ولا أحفظه قال عبد العزيز وقد أصاب سهيلا علة أذهبت بعض عقله ونسي بعض حديثه وكان سهيل يحدث عن ربيعة عنه عن أبيه','1995','2648','0');
INSERT INTO [t6227] VALUES (2889,'قضى باليمين مع الشاهد وعن عبد الله بن نافع عن المغيرة بإسناده ومتنه قال الحاكم أبو عبد الله فحدثني محمد بن يعقوب أخبرنا أبو الجهم حدثنا أحمد بن أبي الحواري قال سمعت يحيى بن معين يقول محمد بن مبارك رجل أهل الشام بعد أبي','1996','2648','0');
INSERT INTO [t6227] VALUES (2890,'قضى باليمين مع الشاهد أخبرنا أبو عبد الله الحافظ حدثنا يوسف بن يعقوب حدثنا عبد الله بن محمد حدثنا إسحاق بن إبراهيم أخبرنا عبد الوهاب بن عبد المجيد الثقفي فذكره بإسناده نحوه أخبرنا الإمام أبو إسحاق الإسفرايني أخبرنا محمد بن محمد بن','1997','2648','0');
INSERT INTO [t6227] VALUES (2891,'قضى بشهادة رجل واحد مع يمين صاحب الحق وقضى به علي رضي الله عنه بالعراق وكذلك رواه حسين بن زيد عن جعفر بن محمد عن أبيه عن جده عن علي بن أبي طالب رضي الله عنه عن رسول الله','1998','2648','0');
INSERT INTO [t6227] VALUES (2892,'قضى باليمين مع الشاهد وكذلك رواه ربيعة بن أبي عبد الرحمن وخالد بن أبي كريمة','1998','2648','0');
INSERT INTO [t6227] VALUES (2893,'مرسلا وكأنه رواه مرة متصلا ومرة مرسلا والله أعلم وروى أبو داود حدثنا أحمد بن عبده حدثنا عمار بن شعيب بن عبد الله بن الزبيب العنبري حدثنا أبي قال سمعت جدي الزبيب يقول بعث رسول الله','1999','2648','0');
INSERT INTO [t6227] VALUES (2894,'فركبت فسبقتهم إلى النبي','1999','2648','0');
INSERT INTO [t6227] VALUES (2895,'هل لكم بينة على أنكم أسلمتم قبل أن تؤخذوا في هذه الأيام قلت نعم قال من بينتك قلت سمرة رجل من بني العنبر ورجل آخر سماه له فشهد الرجل وأبى سمرة أن يشهد فقال نبي الله','1999','2648','0');
INSERT INTO [t6227] VALUES (2896,'اذهبوا فقاسموهم أنصاف الأموال ولا تمسوا ذراريهم لولا أن الله عز وجل لا يحب ضلالة العمل ما ذريناكم عقالا قال الزبيب فدعتني أمي فقالت هذا الرجل أخذ زريبتي فانصرفت إلى نبي الله','2000','2648','0');
INSERT INTO [t6227] VALUES (2897,'قائمين فقال ما تريد بأسيرك فأرسلته من يدي فقام نبي الله','2000','2648','0');
INSERT INTO [t6227] VALUES (2898,'سيف الرجل فأعطانيه وقال للرجل اذهب فزده آصعا من طعام قال فزادني آصعا من شعير وروي عن إسماعيل بن أبي أويس حدثني أبي عن ربيعة عن سعيد بن عمرو بن شرحبيل يعني ابن سعيد بن سعد بن عبادة الأنصاري عن أبيه عن جده أن النبي','2000','2648','0');
INSERT INTO [t6227] VALUES (2899,'قضى باليمين مع الشاهد وروي عن سليمان بن بلال عن ربيعة عن إسماعيل بن عمرو بن قيس بن سعد بن عبادة عن أبيه أنهم وجدوا في كتاب سعد أن رسول الله','2001','2648','0');
INSERT INTO [t6227] VALUES (2900,'باليمين مع الشاهد قال نعم وقضى به علي بين أظهركم قال مسلم قال جعفر في الدين وروي عن كلثوم بن زياد قال أدركت سليمان بن حبيب والزهري يقضيان بذلك قال كلثوم وكان أبو ثابت بن سليمان بن','2001','2648','0');
INSERT INTO [t6227] VALUES (2901,'شاهداك أو يمينه أخبرنا أبو عمرو الأديب أخبرنا الشيخ أبو بكر أحمد بن إبراهيم','2002','2648','0');
INSERT INTO [t6227] VALUES (2902,'شاهداك أو يمينه فنقله عن الشاهدين إلى يمين خصمه بلا ذكر رجل وامرأتين والله أعلم','2003','2648','0');
INSERT INTO [t6227] VALUES (2903,'مسألة','2004','2648','0');
INSERT INTO [t6227] VALUES (2904,'قال من حلف على منبري هذا بيمين آثمة تبوأ مقعده من النار وعن مالك رحمه الله عن داود بن الحصين أنه سمع أبا غطفان بن طريف المري قال اختصم زيد بن ثابت وابن مطيع إلى مروان بن الحكم في دار فقضى باليمين على زيد بن ثابت على','2004','2648','0');
INSERT INTO [t6227] VALUES (2905,'قال ثلاثة لا يكلمهم الله ولا ينظر إليهم رجل حلف على سلعته لقد أعطي بها أكثر مما أعطي وهو كاذب ورجل حلف على يمين كاذبة بعد العصر ليقتطع بها مال امرئ مسلم ورجل منع فضل ماء فيقول الله تعالى له اليوم أمنعك فضلي كما منعت فضل ما','2005','2648','0');
INSERT INTO [t6227] VALUES (2906,'فذهب عبد الرحمن يتكلم فقال له رسول الله','2008','2648','0');
INSERT INTO [t6227] VALUES (2907,'وروى مسلم في الصحيح عن عمرو الناقد عن سفيان عن يحيى بن سعيد بشير بن يسار عن سهل بن أبي حيثمة فذكر هذه القصة قال النبي','2009','2648','0');
INSERT INTO [t6227] VALUES (2908,'من عنده كذا رواه سفيان بن عيينة عن يحيى بن سعيد فيبدأ بقوله فتبرئكم يهود والجماعة عن يحيى بن سعيد بدؤوا بقوله تحلفون خمسين يمينا للأنصاريين كما سبق ذكرنا له والله أعلم وروى الشافعي رحمه الله أخبرنا سفيان بن','2009','2648','0');
INSERT INTO [t6227] VALUES (2909,'بمثله كذا يرويه الإمام الشافعي رحمه الله عن ابن عيينة وهو الصواب وحديث اللعان دليل في هذه المسألة وقد سبق ذكره في كتاب اللعان وروي عن الليث بن سعد عن نافع عن ابن عمر رضي الله عنهما أن النبي','2010','2648','0');
INSERT INTO [t6227] VALUES (2910,'فأتاه خصمان فقال أحدهما وهو ابن القيس بن عابس الكندي وخصمه ربيعة يا رسول الله إن هذا أجر على أرضي في الجاهلية فقال هي أرضي أزرعها فقال لك بينة قال لا قال لك يمينه قال إنه ليس يبالي ما حلف عليه قال ليس','2011','2648','0');
INSERT INTO [t6227] VALUES (2911,'للحضرمي ألك بينة قال لا قال فلك يمينه قال فقال ليس لك منه إلا ذلك','2011','2648','0');
INSERT INTO [t6227] VALUES (2912,'شاهداك أو يمينه كما سبق ذكرنا له وليس فيه ليس لك إلا ذلك وإنما هو في حديث وائل وقال فيه ألك بينة ولم يقل شاهداك فافهمه وفي الصحيحين عن ابن عباس رضي الله عنهما أن النبي','2012','2648','0');
INSERT INTO [t6227] VALUES (2913,'قال إذا ادعت المرأة طلاق زوجها فجاءت على ذلك بشاهد عدل استحلف زوجها فإن حلف بطلت شهادة الشاهد وإن نكل فنكوله بمنزلة شاهد آخر وجاز طلاقه والله أعلم وشهادة العدو على العدو غير مقبولة وحكي عن أبي','2012','2648','0');
INSERT INTO [t6227] VALUES (2914,'رد شهادة والخائنة وذي الغمر على أخيه ورد شهادة القانع لأهل البيت وأجازها لغيرهم وعنه قال قال رسول الله','2013','2648','0');
INSERT INTO [t6227] VALUES (2915,'مسألة','2013','2648','0');
INSERT INTO [t6227] VALUES (2916,'مسألة','2014','2648','0');
INSERT INTO [t6227] VALUES (2917,'كتاب الدعوى','2015','2648','0');
INSERT INTO [t6227] VALUES (2918,'مسألة','2015','2648','0');
INSERT INTO [t6227] VALUES (2919,'فأقام كل واحد منهما شاهدين فقضى به النبي','2015','2648','0');
INSERT INTO [t6227] VALUES (2920,'فبعث كل واحد منهما شاهدين فقسمه النبي','2015','2648','0');
INSERT INTO [t6227] VALUES (2921,'في بعير ليس لواحد منهما بينة فقضي به رسول الله','2016','2648','0');
INSERT INTO [t6227] VALUES (2922,'في بعير ليس لواحد منهما بينة فقضي به رسول الله','2016','2648','0');
INSERT INTO [t6227] VALUES (2923,'ليس لواحد منهما بينة فقال النبي','2016','2648','0');
INSERT INTO [t6227] VALUES (2924,'مسألة','2017','2648','0');
INSERT INTO [t6227] VALUES (2925,'يوما مسرورا وأسارير وجهه تبرق فقال ألم تسمعي ما قال مجزز المدلجي ورأى زيدا وأسامة قد غطيا رؤوسهما وبدت أقدامهما فقال إن هذه الأقدام بعضها من بعض وفي رواية أن النبي','2017','2648','0');
INSERT INTO [t6227] VALUES (2926,'وهو الصادق المصدوق أن أحدكم يجمع خلقه في بطن أمه أربعين يوما ثم يكون علقة مثل ذلك ثم يكون مضغة مثل ذلك ثم يبعث الله الملك فينفخ فيه الروح ثم يؤمر بأربع كلمات يكتب رزقه وعمله وأجله وشقي هو أم سعيد فوالذي لا إله غيره إن','2020','2648','0');
INSERT INTO [t6227] VALUES (2927,'حتى بدت أضراسه أو نواجذه','2020','2648','0');
INSERT INTO [t6227] VALUES (2928,'ولا قوله طيبا وروي عن قتادة عن سعيد بن المسيب أن رجلين اشتركا في طهر امرأة فحملت منهما فولدت ولدا فارتفعا إلى عمر رضي الله عنه فدعا ثلاثة من القافة فدعوا بتراب فوطئ فيه الرجلان والغلام ثم قال لأحدهما انظر','2021','2648','0');
INSERT INTO [t6227] VALUES (2929,'كتاب العتق والولاء والمدبر والكتابة','2023','2648','0');
INSERT INTO [t6227] VALUES (2930,'مسألة','2023','2648','0');
INSERT INTO [t6227] VALUES (2931,'فقال ليس لله شريك فأجاز النبي','2023','2648','0');
INSERT INTO [t6227] VALUES (2932,'مسألة','2024','2648','0');
INSERT INTO [t6227] VALUES (2933,'من أعتق شركا له في عبد فكان له مال يبلغ ثمن العبد قوم عليه قيمة عدل فأعطي شركاؤه حصصهم وعتق عليه العبد وإلا فقد عتق منه ما عتق وفي الصحيحين أيضا عنه قال قال رسول الله','2024','2648','0');
INSERT INTO [t6227] VALUES (2934,'قال من أعتق شقصا له في مملوك فكان للذي يعتق منهما نصيبه مبلغ ثمنه من عتق كله ورواه أيوب عن نافع عن ابن عمر رضي الله عنهما عن','2024','2648','0');
INSERT INTO [t6227] VALUES (2935,'قال من أعتق نصيبا له في مملوك أو شركا من عبد فكان له من المال ما يبلغ قيمة بقية العبد فقد عتق قال نافع وإلا فقد عتق منه ما عتق قال أيوب لا أدري أشيء قاله نافع أو هو في الحديث وفي رواية بمعناه إلا أنه قال','2025','2648','0');
INSERT INTO [t6227] VALUES (2936,'واتفقا على حديث سالم عن أبيه يبلغ به النبي','2026','2648','0');
INSERT INTO [t6227] VALUES (2937,'في المملوك بين الرجلين فيعتق أحدهما نصيبه قال يضمن وعنده أيضا عن النبي','2026','2648','0');
INSERT INTO [t6227] VALUES (2938,'فضمنه إياه وكان له قريبا من مائتي شاة فباعها فأعطاها صاحبه تفرد به','2026','2648','0');
INSERT INTO [t6227] VALUES (2939,'مرسلا وفي الصحيحين عن سعيد بن أبي عروبة عن قتادة عن النضر بن أنس عن بشير بن نهيك عن أبي هريرة رضي الله عنه أن النبي','2027','2648','0');
INSERT INTO [t6227] VALUES (2940,'بقية ثمنه قال وكان قتادة يقول إن لم يكن له مال استسعى العبد','2027','2648','0');
INSERT INTO [t6227] VALUES (2941,'يقولون إذا أعتق الرجل العبد بينه وبين الرجل فهو ضامن إن كان موسرا وإن كان معسرا سعى بالعبد صاحبه في نصف قيمته غير مشقوق عليه وهذا لا يصح وراوية الحجاج ولا يعرف به والله أعلم','2028','2648','0');
INSERT INTO [t6227] VALUES (2942,'من أعتق ستة أعبد له في مرض موته ولا مال له غيرهم أقرع بينهم فيحكم بعتق اثنين ورق أربعة وقال أبو حنيفة رحمه الله يعتق من كل واحد ثلثه ولا يقرع ودليلنا ما في صحيح مسلم عن أبي المهلب عن عمران بن الحصين أن رجلا من','2028','2648','0');
INSERT INTO [t6227] VALUES (2943,'فقال فيه قولا شديدا ثم دعاهم فجزأهم ثلاثة أجزاء فأقرع بينهم فأعتق اثنين وأرق أربعة وعنده أيضا عن محمد بن سيرين عن عمران بن حصين أن رجلا كان له ستة أعبد ولم يكن له مال غيرهم فأعتقهم عند موته فرفع ذلك إلى النبي','2029','2648','0');
INSERT INTO [t6227] VALUES (2944,'بينهم فأعتق ثلثهم وأرق ثلثيهم وقال لو علمنا ما صلينا عليه أو ما دفن في مقابرنا حديث ابن سيرين وأبي المهلب عن عبد الرحمن بن عمرو متصل حكم مسلم وغيره من أئمة أهل الحديث بصحته فأما حديث الحسن عن عمران بن حصين فالمحدثون','2029','2648','0');
INSERT INTO [t6227] VALUES (2945,'ثلثه وأمر أن يسعى في الثلثين وهذا مرسل والله أعلم','2030','2648','0');
INSERT INTO [t6227] VALUES (2946,'ولا يعتق عليه أخوه وقال أبو حنيفة رحمه الله يعتق ووافقنا في بني الأعمام أنهم يعتقون عليه بحق الملك فبناء المسألة لنا على المعاني وربما استدل أصحابنا بما في الصحيحين عن سعد بن مرجانة صاحب علي بن الحسين قال قال','2030','2648','0');
INSERT INTO [t6227] VALUES (2947,'من ملك ذا رحم محرم فهو حر يقال في هذا الحديث من وجهين أحدهما أن حماد بن سلمة تفرد به هكذا وخالفه سعيد بن أبي عروبة فرواه عن قتادة عن الحسن من قوله عن قتادة عن عمر رضي الله عنه ورواه أبو داود عن مسلم بن إبراهيم','2031','2648','0');
INSERT INTO [t6227] VALUES (2948,'من ملك ذا رحم محرم فهو حر وقال أبو عيسى سألت البخاري عن هذا الحديث فلم يعرفه عن الحسن عن سمرة إلا من حديث حماد بن سلمة وقال البيهقي رحمه الله تعالى والحديث تفرد به حماد بن سلمة وخالفه غيره ثم شك هو أيضا فيه','2031','2648','0');
INSERT INTO [t6227] VALUES (2949,'من ملك ذا رحم محرم فهو حر لو كان هذا الحديث عن سعيد الثوري عن عبد الله بن دينار عن عبد الله بن عمر رضي الله عنهما صحيحا لكان كالأخذ باليد ولما خفي على المتقدمين من أصحاب سفيان الذين هو حفاظ حديثه لكنه تفرد به ضمرة بن ربيعة عن','2032','2648','0');
INSERT INTO [t6227] VALUES (2950,'من ملك ذا رحم محرم فهو حر وروى العرزمي عن أبي النضر عن أبي صالح عن ابن عباس رضي الله عنهما قال جاء رجل يقال له صالح بأخيه فقال يا رسول الله إني أريد أن أعتق أخي هذا فقال إن الله أعتقه حين ملكته قال','2033','2648','0');
INSERT INTO [t6227] VALUES (2951,'ولا ولاء بغير جهة الإعتاق وقال أبو حنيفة رحمه الله','2033','2648','0');
INSERT INTO [t6227] VALUES (2952,'إنما الولاء لمن أعتق وعنده أيضا عن ابن عباس رضي الله عنهما في قوله والذين عقدت أيمانكم فآتوهم نصيبهم قال كان المهاجرون حين قدموا المدينة يرث المهاجر الأنصاري دون ذي رحمه للأخوة التي آخى','2034','2648','0');
INSERT INTO [t6227] VALUES (2953,'أن يؤتيه نصيبه زاد في رواية غير أحمد فما أسلم حتى حمل على الإسلام بالسيف وروي عن تميم الداري قال سألت رسول الله','2035','2648','0');
INSERT INTO [t6227] VALUES (2954,'هو أولى الناس بمحياه ومماته وروى معاوية بن يحيى الصدفي عن القاسم الشامي عن أبي أمامة رضي الله عنه قال قال رسول الله','2035','2648','0');
INSERT INTO [t6227] VALUES (2955,'فقال إن فلانا أسلم على يدي قال هو مولاك فإذا مت فأوص له والله أعلم','2036','2648','0');
INSERT INTO [t6227] VALUES (2956,'وبيع المدبر جائز وقال أبو حنيفة رحمه الله لا يجوز دليلنا من طريق الخبر ما في الصحيحين عن جابر بن عبد الله دبر رجل من الأنصار عبدا له لم يكن له مال غيره فباعه رسول الله','2036','2648','0');
INSERT INTO [t6227] VALUES (2957,'فقال من يشتريه مني فاشتراه نعيم بن عبد الله بثمان مائة درهم فدفعها إليه قال سمعت جابر بن','2036','2648','0');
INSERT INTO [t6227] VALUES (2958,'فقال من يشتريه مني فاشتراه منه نعيم بن عبد الله بثمان مائة درهم فدفع إليه ثمنه وفي صحيح مسلم عنه أن رجلا من الأنصار أعتق غلاما عن دبر وكان محتاجا فذكر ذلك لرسول الله','2037','2648','0');
INSERT INTO [t6227] VALUES (2959,'أنت أحوج إليه ثم قال من يشتريه فقال نعيم بن عبد الله أنا فاشتراه فأخذ النبي','2037','2648','0');
INSERT INTO [t6227] VALUES (2960,'فقال ألك مال غيره فقال لا فقال من يشتره مني فاشتراه نعيم بن النحام العدوي بثمان مائة درهم فجاء بها إلى رسول الله','2037','2648','0');
INSERT INTO [t6227] VALUES (2961,'من يشتريه فاشتراه نعيم بن النحام قال عمرو سمعت جابرا يقول عبدا قبطيا مات عام أول في أمارة ابن الزبير زاد أبو الزبير يقال له يعقوب وروى الدارقطني أخبرنا أبو عمرو يوسف بن يعقوب حدثنا إبراهيم بن عبد العزيز المقوم حدثنا','2038','2648','0');
INSERT INTO [t6227] VALUES (2962,'كان','2038','2648','0');
INSERT INTO [t6227] VALUES (2963,'باع مدبرا فأتيت أبا جعفر محمد بن علي فقلت له إن عطاء بن أبي رباح حدثنا عن جابر بن عبد الله رضي الله عنه أن رسول الله','2039','2648','0');
INSERT INTO [t6227] VALUES (2964,'مرسلا أخبرناه أبو','2039','2648','0');
INSERT INTO [t6227] VALUES (2965,'خدمة المدبر قال أبو بكر لم أجد فيه حديثا غير هذا وأبو جعفر وإن كان من الثقات فإن حديثه مرسل أخبرني أبو عبد الرحمن أخبرنا علي بن عمر حدثنا أبو محمد بن صاعد حدثنا محمد بن المثنى حدثنا عبد الوهاب الثقفي قال سمعت يحيى بن','2040','2648','0');
INSERT INTO [t6227] VALUES (2966,'قال المدبر لا يباع ولا يوهب وهو حر من الثلث قال الدارقطني لم يسنده غير عبيدة بن حسان وهو ضعيف وإنما هو عن ابن عمر موقوف من قوله حدثنا أبو بكر النيسابوري حدثنا محمد بن يحيى حدثنا أبو النعمان حدثنا حماد بن زيد عن أيوب','2041','2648','0');
INSERT INTO [t6227] VALUES (2967,'ولا تجوز الكتابة على أقل من نجمين وقال أبو حنيفة','2041','2648','0');
INSERT INTO [t6227] VALUES (2968,'عن الغرر وليس الغرر بأكثر من بيع ماله بماله لكن الله تعالى جوزه بهذه الآية ووردت الأخبار والآثار بذلك ولم يسمع أن أحدا من السلف كاتب عبده كتابة حالة وإنما كاتبوهم بذكر الأجل فيه فصار إجماعهم على ذلك بيانا للآية فلا يجوز حالا','2042','2648','0');
INSERT INTO [t6227] VALUES (2969,'مسألة','2043','2648','0');
INSERT INTO [t6227] VALUES (2970,'فذكرت ذلك له فقال اغرس واشترط لهم فإذا أردت أن تغرس فآذني فآذنته فجاء فجعل يغرس إلا واحدة غرستها بيدي فعلقن جميعا إلا الواحدة والله أعلم','2043','2648','0');
INSERT INTO [t6227] VALUES (2971,'وإذا مات المكاتب وقد بقي عليه شيء من مال الكتابة مات','2043','2648','0');
INSERT INTO [t6227] VALUES (2972,'مسألة','2046','2648','0');
INSERT INTO [t6227] VALUES (2973,'قال يؤدي المكاتب بحصة ما أدى دية حر وما بقي دية عبد وبه عن حماد عن أيوب عن عكرمة عن النبي','2049','2648','0');
INSERT INTO [t6227] VALUES (2974,'يؤدي المكاتب بقدر ما أدى رواية عكرمة عن علي رضي الله عنه مرسل والله تعالى أعلم تم الكتاب والحمد لله رب العالمين وصلى الله على سيدنا محمد وعلى آله وصحبه أجمعين وسلم','2049','2648','0');
INSERT INTO [t6227] VALUES (2975,'الخاتمة','2051','2648','0');
INSERT INTO [t6227] VALUES (2976,'إذا اجتهد الحاكم فأصاب فله أجران وإذا اجتهد وأخطأ فله أجر متفق عليه ولهذا كان الصحابة رضوان الله عليهم يعلمون أن المصيب واحد قال شيخ الإسلام ابن تيمية قال ابن مسعود لما سئل عن مسألة أقول فيها برأيي فإن كان صوابا','2052','2648','0');
INSERT INTO [t6227] VALUES (2977,'حضرا ولا سفرا سأل عن ميراث الجدة فأخبره المغيرة بن شعبة ومحمد بن مسلمة بقضاء رسول الله','2052','2648','0');
INSERT INTO [t6227] VALUES (2978,'سنوا بهم سنة أهل الكتاب وكذلك عثمان بن عفان رضي الله عنه لم يكن يعلم أن المتوفي عنها زوجها تعتد في بيت الموت حتى أخبرته الفريعة بنت مالك بذلك','2052','2648','0');
INSERT INTO [t6227] VALUES (2979,'فيها ثم لو فرض فليس كل ما في الكتب يعلمه عالم بل الذين كانوا قبل جمع هذه الدواوين أعلم بالسنة من المتأخرين لأن كثيرا مما بلغهم وصح عندهم قد لا يبلغنا إلا عن مجهول أو بإسناد منقطع أو لا يبلغنا بالكلية فكانت دواوينهم في صدورهم التي','2053','2648','0');
INSERT INTO [t6227] VALUES (2980,'وأن التعصب والتقليد قد يكون من عوامل الاختلاف عند بعض الناس ومنها أسباب اختلاف الفقهاء للدكتور عبد المحسن التركي وذكر فيه من أسباب اختلاف الفقهاء القراءة الشاذة ومنها الخلاف بين العلماء وموقفنا منه للشيخ محمد بن صالح العثيمين','2054','2648','0');
INSERT INTO [t6227] VALUES (2981,'ولا يقولون على الله سبحانه بلا علم فكان منهم مجتهد له مأخذ شرعي فيما يعتقده ولا يحل لأحد أن يتصدى للفتوى إلا أن يكون بصيرا بخلاف العلماء وقد تحققت فيه شروط الفتوى منها الأمانة والتقوى والعلم والعدالة وقد كان السلف يتدافعون','2055','2648','0');
INSERT INTO [t6227] VALUES (2982,'رضوان الله عليهم لأنهم أعلم بالتنزيل ثم ما قرره علماء الأمة من التابعين ومن تبعهم من سلف صالح المؤمنين يحمله من كل خلف عدوله ينفون عنه تحريف الجاهلين وتأويل المبطلين وتنطع الهالكين وهذا الكتاب يمثل أنموذجا صادقا لهذه الحقيقة','2056','2648','0');
