INSERT INTO [t6336] VALUES (1,'الجزء ١','1','0','0');
INSERT INTO [t6336] VALUES (2,'بسم الله الرحمن الرحيم','2','1','0');
INSERT INTO [t6336] VALUES (3,'شرح مقدمة الكتاب','30','1','0');
INSERT INTO [t6336] VALUES (4,'حيث ابتدأ بها في كتابته إلى الملوك وغيرهم واقتداء بقوله','32','1','0');
INSERT INTO [t6336] VALUES (5,'فيما رواه أبو هريرة وغيره أنه قال كل أمر ذي بال لا يبدأ فيه بالحمد لله فهو أقطع وفي رواية بحمد الله وفي رواية بالحمد وفي رواية ببسم الله الرحمن كما تقدم وفي رواية فهو أجذم وفي رواية لا يبدأ فيه','34','1','0');
INSERT INTO [t6336] VALUES (6,'قال قال الله تعالى قسمت الصلاة بيني وبين عبدي نصفين','43','1','0');
INSERT INTO [t6336] VALUES (7,'والعلوم النافعة والأعمال الصالحة ويأتي معنى الإلهام عقب فصل الأعيان المنتفع بها ومعنى الفهم قريبا وفهم مضعف للفورية والتكثير قوله والصلاة ثلثنا بذكر الصلاة عليه صلوات لله وسلامه عليه تترى إلى يوم القيامة','63','1','0');
INSERT INTO [t6336] VALUES (8,'عن جبريل عليه السلام عن رب العالمين ذكره النووي وغيره ويدل على ذلك ذكره معه في التشهد والخطب والتأذين وغيرها وأمر الله تعالى المؤمنين بالصلاة والسلام عليه وأخبر أنه وملائكته يصلون عليه في الآية الكريمة وأدنى مراتب الأمر','64','1','0');
INSERT INTO [t6336] VALUES (9,'وصلى صلاة دعا انتهى قال ابن القيم في جلاء الأفهام أصل الصلاة لغة يرجع إلى','64','1','0');
INSERT INTO [t6336] VALUES (10,'إذا دعي أحدكم إلى الطعام فليجب فإن كان صائما فليصل أي فليدع على الصحيح انتهى وقال السهيلي معنى الصلاة حيث تصرفت يرجع إلى الحنو','65','1','0');
INSERT INTO [t6336] VALUES (11,'قال من صلى علي صلاة صلى الله عليه عشرا وفي غير مسلم سبعين وظاهره الاقتصار على الصلاة وهذا أظهر','77','1','0');
INSERT INTO [t6336] VALUES (12,'بلسانه فيردفه بالتسليم هذا محل الخلاف في الكراهة فيما يظهر لي وهو ظاهر الآية فإن قيل قوله تعالى صلوا عليه وسلموا تسليما الأحزاب يشمل الفعل أيضا قلت أصل وضع الأمر للقول وإطلاقه على الفعل مجاز','79','1','0');
INSERT INTO [t6336] VALUES (13,'أنا سيد ولد آدم ولا فخر ومنها ما خصه الله تعالى به في الدنيا والآخرة ففي الدنيا كونه بعث إلى الناس كافة وغيره مما لا يحصى وفي الآخرة اختصاصه بالشفاعة والأنبياء تحت لوائه','80','1','0');
INSERT INTO [t6336] VALUES (14,'أوتيت فواتح الكلم وخواتمه وجوامعه رواه أحمد وفي رواية واختصر لي الحديث اختصارا فبعثه الله تعالى بجوامع الكلم وخصه ببدائع الحكم وفي الصحيحين من حديث أبي هريرة بعثت بجوامع الكلم قال الزهري جوامع الكلم','81','1','0');
INSERT INTO [t6336] VALUES (15,'أنه قال فضلت على من قبلي بست ولا فخر فذكر منها وأوتيت جوامع الكلم وهذا مما لا يحتاج إلى إطالة ولا تقرير قوله وأعلم كونه أعلم خلق الله من المتفق عليه بين الأمة من غير توقف لأن من تتبع مجاري أحواله','82','1','0');
INSERT INTO [t6336] VALUES (16,'أرجح الناس عقلا وأفضلهم رأيا وفي رواية أخرى فوجدت في جميعها أن الله تعالى لم يعط جميع الخلق من بدء الدنيا إلى انقضائها من العقل في جنب عقله','83','1','0');
INSERT INTO [t6336] VALUES (17,'لكثرة خصاله المحمودة أي ألهم الله تعالى أهله ذلك لما علم من خالصه المحمودة قاله ابن فارس وقالت أمة سماه الله بذلك','83','1','0');
INSERT INTO [t6336] VALUES (18,'وباسمه وكان كل واحد منهم قد خلف امرأته حاملا فنذر كل واحد منهم إن ولد له ولد أن يسميه محمدا ففعلوا ذلك','88','1','0');
INSERT INTO [t6336] VALUES (19,'لكن لم يكن محمدا حتى كان أحمد حمد ربه فنبأه وشرفه فلذلك تقدم اسم أحمد على اسم محمد','89','1','0');
INSERT INTO [t6336] VALUES (20,'تالية لحمد الله وذلك لأنه','101','1','0');
INSERT INTO [t6336] VALUES (21,'رزقنا الله محبتهم واتباع آثارهم ولا جعل في قلوبنا غلا للذين آمنوا نكتة إنما جمعنا بين الآل والأصحاب مخالفة للمبتدعة لأنهم يوالون الآل فقط وأهل السنة يوالون الآل والأصحاب وجمعنا العلم بقولنا العلوم وإن كان العلم جنسا','103','1','0');
INSERT INTO [t6336] VALUES (22,'فإن كان يقولها في خطبه وشبهها رواه عنه الخمسة وثلاثون صحابيا ذكر الحافظ الرهاوي اثنين وثلاثين في كتابه الأربعين وذكر رواية كل واحد منهم بالأسانيد وزاد ابن منده في مستخرجه ثلاثة','106','1','0');
INSERT INTO [t6336] VALUES (23,'أوتيت جوامع الكلم واختصر لي الكلام اختصارا وهو مرادنا واختصاره مشاهد بالعيان كذلك إذ لو كان مطولا لكان أوسع وأكبر حجما وإنما اختصرناه لمعان منها لئلا يحصل الملل بالإطالة ومنها ليحفظ فإن حفظ المختصر أيسر','123','1','0');
INSERT INTO [t6336] VALUES (24,'الكلام على المقدمة','138','1','0');
INSERT INTO [t6336] VALUES (25,'يتلقى من جبرائيل فلا حاجة فيه إلى الاستدلال وقيل بالاستدلالي أي أن علمهم استدلالي قال بعض الأصوليين علمهم استدلالي لأنه يعلمون الشيء على حقيقته أي على ما هو به وحقائق الأحكام تابعة لأدلتها وعللها فكما','168','1','0');
INSERT INTO [t6336] VALUES (26,'إذا أدوا الجزية فلهم ما لنا وعليهم ما علينا','170','1','0');
INSERT INTO [t6336] VALUES (27,'في قوله تعالى فاعلم أنه لا إله إلا الله محمد وقيل ككون الإجماع دليلا والقياس حجة ومن ثم لا حاجة إلى زيادة ما تقدم','175','1','0');
INSERT INTO [t6336] VALUES (28,'فيما جاء به عنه ويتوقف صدقه على دلالة المعجزة ولهذا ذكرت في هذا المختصر من أصول الدين بعض المتعلق بأصول الفقه كل مسألة في مكانها المتعلق بها وقد ذكره الأصوليون ضمنا لأجل التعلق المذكور الثاني استمداده من العربية وذلك','190','1','0');
INSERT INTO [t6336] VALUES (29,'قوله فصل','192','1','0');
INSERT INTO [t6336] VALUES (30,'وما به الإرشاد هو كتاب الله وسنة رسوله','195','1','0');
INSERT INTO [t6336] VALUES (31,'وإلى علماء الأمة لم يخرج شيء من أحكام المسلمين والإسلام عنها قوله والدليل القرآن قال الفخر أبو محمد إسماعيل البغدادي هذا دليل على أن الدليل حقيقة قول الله تعالى','207','1','0');
INSERT INTO [t6336] VALUES (32,'قوله فصل','217','1','0');
INSERT INTO [t6336] VALUES (33,'تعرف إلى الله في الرخاء يعرفك في الشدة','238','1','0');
INSERT INTO [t6336] VALUES (34,'قوله فصل','247','1','0');
INSERT INTO [t6336] VALUES (35,'قوله فصل','254','1','0');
INSERT INTO [t6336] VALUES (36,'ألا إن في الجسد مضغة إذا صلحت صلح الجسد كله ألا وهي القلب وقد دللنا أيضا على أن العقل بعضالعلوم الضرورية والعلوم الضرورية لا تكون إلا في القلب ومع هذا له اتصال بالدماغ قاله التميمي من أصحابنا وغيره من الأصحاب','263','1','0');
INSERT INTO [t6336] VALUES (37,'قال للنساء أليس شهادة إحداكن مثل نصف شهادة الرجل قلن بلى قال فذلك من نقصان عقلها وخالف ابن عقيل والأشعرية والمعتزلة وقاله الماوردي في الغريزي لا التجربي وحمل الطوفي الخلاف على ذلك','266','1','0');
INSERT INTO [t6336] VALUES (38,'قوله فصل','269','1','0');
INSERT INTO [t6336] VALUES (39,'قوله فصل','279','1','0');
INSERT INTO [t6336] VALUES (40,'من الأنبياء المرسلين إنما كان مبعوثا لقومه خاصة فهو مبعوث بلسانهم ومحمد','279','1','0');
INSERT INTO [t6336] VALUES (41,'أصدق كلمة قالها شاعر كلمة لبيد ألا كل شيء ما خلا الله باطل فسمى ذلك كله كلمة وهو مجاز مهمل في عرف النحاة فقيل هو من تسمية الشيء باسم بعضه وقيل لما ارتبط أجزاء الكلام بعضها ببعض حصل له بذلك وحدة فشابه بها الكلمة','308','1','0');
INSERT INTO [t6336] VALUES (42,'عن الله تعالى أعلم أمته أنه كلام الله تعالى لا كلام غيره وهذا يبطل قول من قال من المتأخرين إن الكلام يقال بالاشتراك على اللفظ والمعنى فيقال لهم إذا كان كل منهما يسمى كلاما','313','1','0');
