INSERT INTO [t6336] VALUES (53,'أيما رجل وجد ماله عند رجل قد أفلس فصاحب المتاع أحق بمتاعه قال القاضي','404','1','0');
INSERT INTO [t6336] VALUES (54,'أتحلفون وتستحقون دم صاحبكم وقد يقال إنه من مجاز الحذف أي بدل دمه ومنها المدح في صورة الذم وعكسه نحو ما أشعره قاتله الله ونحو قوله تعالى ذق إنك أنت العزيز الكريم الدخان ويمكن دخولهما','416','1','0');
INSERT INTO [t6336] VALUES (55,'قوله فصل','437','1','0');
INSERT INTO [t6336] VALUES (56,'في فرس أبي طلحة لماركبه وجدناه بحرا ونحوه قوله وفي إسناد خلافا لقوم يكون المجاز في الإسناد على الصحيح وعليه المعظم وكثير من أصحابنا فيجري فيه وإن لم يكن في لفظي المسند والمسند إليه تجوز وذلك بأن يسند الشيء','445','1','0');
