INSERT INTO [t6336] VALUES (773,'إنها من الطوافين عليكم فيعتبر الأمر في كل طائف','3447','726','0');
INSERT INTO [t6336] VALUES (774,'من أعتق شركا له في عبد وكان له مال يبلغ ثمن العبد قوم عليه قيمة عدل الحديث فإنا نقطع بعدم اعتبار الشارع الذكورة والأنوثة فيه ومثل قوله','3452','726','0');
