INSERT INTO [t6336] VALUES (777,'عهد إلينا فيهن عهدا ننتهي إليه الجد والكلالة وأبواب من أبواب الربا وصح عن ابن عمر أجرؤكم على الجد أجرؤكم على جهنم وصح عن ابن المسيب عن عمر وعلي ورواه سعيد في سننه','3484','726','0');
INSERT INTO [t6336] VALUES (778,'بيان على وفقه مع عموم الحاجة إليه في زمانه وعموم الحاجة إلى خلافه هل يعمل بذلك القياس فيه خلاف وذكر له صورا منها ضمان الدرك وهو مثال للشق الثاني من المسألة ومنها وهو مثال الأول صلاة الإنسان على من مات من المسلمين في مشارق','3510','726','0');
INSERT INTO [t6336] VALUES (779,'للذي أراد الانتفاء من ولده لمخالفة لونه لعله نزعه عرق وهذا قياس لجواز مخالفة الولد للوالد في','3519','726','0');
INSERT INTO [t6336] VALUES (780,'قوله فصل','3522','726','0');
INSERT INTO [t6336] VALUES (781,'قوله فصل القوادح','3538','726','0');
INSERT INTO [t6336] VALUES (782,'رخص في السلم أما مخالفة الإجماع فكقول حنفي لا يجوز للرجل أن يغسل امرأته لأنه يحرم النظر إليها كالأجنبية فيقال هذا فاسد الاعتبار لمخالفة الإجماع السكوتي وهو أن عليا غسل فاطمة','3548','726','0');
INSERT INTO [t6336] VALUES (783,'فدل على أن رتبة القياس بعد النص ولأن الظن المستفاد من النص أقوى من الظن المستفاد من القياس','3549','726','0');
INSERT INTO [t6336] VALUES (784,'بخلاف غيرهما فإن','3552','726','0');
INSERT INTO [t6336] VALUES (785,'بالتوراة والإنجيل المبدلين لكن نحتج به على أهل الكتاب لتصديقهم به انتهى قوله وإن نقض أحدهما علة الآخر بأصل نفسه لم يجز عند أصحابنا والشافعية وقيل بلى وقال الشيخ هو كقياسه على أصل نفسه لو نقض المعترض أو المستدل علة','3612','726','0');
INSERT INTO [t6336] VALUES (786,'في الأصح ولا برخصة ثابتة على خلاف مقتضى القياس ولا بموضوع استحسان عند أصحابنا والشافعية وعند الشيخ تنتقض المستنبطة إن لم يبين مانعا قال ابن مفلح وفي قبول النقض بالمنسوخ وبخاص بالنبي','3614','726','0');
INSERT INTO [t6336] VALUES (787,'وكان حينئذ موزونا ومنها المطالبة بكون وصف المعارض مؤثرا يقال ولم قلت إن الكيل مؤثر وهذا إنما يسمع من المستدل إذا كان مثبتا للعلية بالمناسبة أو الشبه حتى يحتاج المعارض في معارضته إلى بيان مناسبة أو شبه بخلاف ما إذا أثبته','3626','726','0');
INSERT INTO [t6336] VALUES (788,'الخال وارث من لا وارث له','3667','726','0');
INSERT INTO [t6336] VALUES (789,'بلا كيف ولا شرح ولا يقال','3693','726','0');
INSERT INTO [t6336] VALUES (790,'الجزء ٨','3731','0','0');
INSERT INTO [t6336] VALUES (791,'باب الاستدلال','3731','790','0');
INSERT INTO [t6336] VALUES (792,'قوله باب الاستدلال','3733','790','0');
INSERT INTO [t6336] VALUES (793,'فليس بحرام مثال آخر صيد المحرم إما حلال أو حرام لكنه حرام لأنه نهي عنه فليس بحلال وقد تقدم بيان ذلك في أوائل الشرح فليراجع ولهذا تفاريع كثيرة ليست مقصودة تركناها خوف الإطالة','3735','790','0');
INSERT INTO [t6336] VALUES (794,'قوله فصل','3747','790','0');
INSERT INTO [t6336] VALUES (795,'أمامة في الصلاة وكانت بحيث لا تحترز عن نجاسة قلت وهذا لا يطرد في مذهبنا ولا في مذهبهم ويأتي قبيل التقليد هل يلزم نافي الحكم الدليل عليه أم لا قوله وليس استصحاب حكم الإجماع في محل الخلاف حجة عند','3756','790','0');
INSERT INTO [t6336] VALUES (796,'قوله فصل','3761','790','0');
INSERT INTO [t6336] VALUES (797,'هل يكون شرعنا لنا حتى يستدل به في أحكام شرعنا إذا لم نجد له دليلا يقرره ولا ورد ما ينسخه أو ليس بشرع لنا حتى يأتي في شرعنا ما يقرر ذلك الحكم فيه قولان يأتيان قريبا قوله يجوز تعبد نبي بشريعة نبي قبله عقلا ومنعه قوم','3761','790','0');
INSERT INTO [t6336] VALUES (798,'قبل البعثة متعبدا في الفروع بشرع من قبله مطلقا عند القاضي والحلواني وغيرهما وأومأ إليه أحمد وقيل معين فقيل أدم أو نوح أو إبراهيم اختاره ابن عقيل والمجد والبغوي وابن كثير وجمع أو موسى أو عيسى ومنع الحنفية','3762','790','0');
INSERT INTO [t6336] VALUES (799,'واحد منهم فيتناوله عموم الدعوة ثم اختلفوا على هذا القول هل كان متعبدا بشرع معين أو لا فيه قولان ثم اختلف القائل بأنه متعبد بشرع معين في المعين فقيل آدم صلوات الله وسلامه عليه ولم يذكره في جمع الجوامع','3763','790','0');
INSERT INTO [t6336] VALUES (800,'في أمور منها انصراف همم الناس عن أمر دينه والبحث عنه انتهى قال ابن مفلح وغيره وجه المنع أنه لو كان متعبدا بشرع لخالط أهله عادة رد باحتمال مانع','3768','790','0');
INSERT INTO [t6336] VALUES (801,'على ما كان عليه قومه عند الأئمة قال أحمد من زعمه فقول سوء قال ابن مفلح ولم يكن','3770','790','0');
INSERT INTO [t6336] VALUES (802,'ممن أمر أن يقتدى بهم رواه البخاري','3774','790','0');
INSERT INTO [t6336] VALUES (803,'قضى بالقصاص في السن وقال كتاب الله القصاص وإنما هذا في التوراة وسياق قوله تعالى فاعتدوا عليه في غيره ولهذا لم يفسر له وللترمذي والنسائي عن عمران أن رجلا عض يد رجل فنزعها من فيه فوقعت ثنيتاه فقال','3776','790','0');
INSERT INTO [t6336] VALUES (804,'إلى التوراة في الرجم','3777','790','0');
INSERT INTO [t6336] VALUES (805,'قوله فصل','3782','790','0');
INSERT INTO [t6336] VALUES (806,'أنه قال نحن نحكم بالظاهر والله يتولى السرائر كما استدل به البيضاوي وغيره لكنه حديث لا يعرف لكن رواه الحافظ أبو طاهر إسماعيل بن علي بن إبراهيم بن أبي القاسم الجنزوي في كتابه إدارة الأحكام في','3785','790','0');
INSERT INTO [t6336] VALUES (807,'وأصل حديثهما في الصحيحين فقال المقضي عليه قضيت علي والحق لي فقال رسول الله','3786','790','0');
INSERT INTO [t6336] VALUES (808,'قوله فصل','3791','790','0');
INSERT INTO [t6336] VALUES (809,'أصحابي كالنجوم بأيهم اقتديتم اهتديتم رواه عثمان الدارمي وابن عدي','3799','790','0');
INSERT INTO [t6336] VALUES (810,'بالظن رد يمنع ذلك كخبر الواحد','3806','790','0');
INSERT INTO [t6336] VALUES (811,'لئلا يكون كاتما للعلم رد يحتمل أنه نقله ولم يبلغنا أو ظن نقل غيره له فاكتفى بذلك الغير عن نقله أو كره الرواية قلت كل هذه الاحتمالات بعيدة بل يقال لا يلزم أنه إذا روى ذلك وكان توقيفا أن يصرح برفعه قوله','3807','790','0');
INSERT INTO [t6336] VALUES (812,'لا يلزم الأخذ به ونقل المروذي ينظر ما كان عن النبي','3808','790','0');
INSERT INTO [t6336] VALUES (813,'قوله فصل','3812','790','0');
INSERT INTO [t6336] VALUES (814,'فهو ثابت بالسنة أو في زمانهم من غير إنكار فهو إجماع وإلا فهو مردود','3821','790','0');
INSERT INTO [t6336] VALUES (815,'قوله فصل','3828','790','0');
INSERT INTO [t6336] VALUES (816,'خمس مائة ألف حديث انتخبت منه ما ضمنته كتابي السنن جمعت فيه أربعة آلاف وثمانمائة حديث ذكرت الصحيح وما يشبهه ويقاربه ويكفي المسلم لدينه من ذلك أربعة أحاديث','3834','790','0');
INSERT INTO [t6336] VALUES (817,'الرجل يخيل إليه أنه يجد الشيء في الصلاة قال لا ينصرف حتى يسمع صوتا أو يجد ريحا متفق عليه ولمسلم إذا وجد أحدكم في بطنه شيئا فأشكل عليه أخرج منه شيء فلا يخرجن من المسجد حتى يسمع صوتا أو يجد ريحا','3837','790','0');
INSERT INTO [t6336] VALUES (818,'البينة على المدعي واليمين على المدعى عليه وفي رواية على من أنكر قوله والضرر يزال','3839','790','0');
INSERT INTO [t6336] VALUES (819,'لا ضرر ولا ضرار وفي رواية ولا إضرار بزيادة همزة في أوله وألف بين الراءين وقد علل أصحابنا بذلك في مسائل كثيرة جدا وقد تقدم قريبا أن أبا داود قال الفقه يدور على خمسة أحاديث منها قوله','3840','790','0');
INSERT INTO [t6336] VALUES (820,'ويضع عنهم إصرهم والأغلال التي كانت عليهم الأعراف وقال تعالى لا يكلف الله نفسا إلا وسعها البقرة وقال تعالى في دعائهم ولا تحمل علينا إصرا كما حملته على الذين من قبلنا البقرة','3842','790','0');
INSERT INTO [t6336] VALUES (821,'لهند خذي ما يكفي وولدك بالمعروف','3847','790','0');
INSERT INTO [t6336] VALUES (822,'لحمنة بنت جحش تحيضي في علم الله ستا أو سبعا كما تحيض النساء وكما يطهرن لميقات حيضهن وطهرهن رواه الترمذي وصححه الحاكم وحديث أم سلمة أن امرأة كانت تهراق الدم على عهد رسول الله','3848','790','0');
INSERT INTO [t6336] VALUES (823,'فقال لتنظر عدد الليالي والأيام التي كانت تحيضهن من الشهر قبل أن يصيبها ذلك فلتترك الصلاة رواه أبو داود والنسائي وابن خزيمة وابن حبان في صحيحيهما','3848','790','0');
INSERT INTO [t6336] VALUES (824,'من عمل عملا ليس عليه أمرنا فهو رد رواه مسلم بهذا اللفظ فإنه دليل على اعتبار ما المسلمون عليه إما من جهة الأمر الشرعي أو من جهة العادة المستقرة لشمول قوله ليس عليه أمرنا ذلك ومنها حديث المكيال مكيال أهل المدينة والوزن','3849','790','0');
INSERT INTO [t6336] VALUES (825,'على أهل الحائط حفظها بالنهار وعلى أهل المواشي حفظها بالليل رواه أبو داود وصححه جماعة','3850','790','0');
INSERT INTO [t6336] VALUES (826,'التضمين على ما جرت به العادة وقيل وينبني على هذه القاعدة ما اعتمده إمامنا وأصحابنا في أقل سن الحيض للمرأة وأقل الحيض والطهر وأكثرهما وثمن المثل وكفء النكاح وأكثر مدة الحمل وأقلها وسن اليأس ومهر المثل وضابط كل فعل','3851','790','0');
INSERT INTO [t6336] VALUES (827,'باب الاجتهاد','3857','790','0');
INSERT INTO [t6336] VALUES (828,'قوله باب الاجتهاد','3859','790','0');
INSERT INTO [t6336] VALUES (829,'فإنه لا يسمى في العرف فقيها ولعدم الإذن فيه إلا أن يقال المراد بالحد اجتهاد الفقيه لا مطلق الاجتهاد قاله البرماوي','3860','790','0');
INSERT INTO [t6336] VALUES (830,'في الأحاديث ليعرف المراد من ذلك وما يتعلق بهما من تخصيص أو تعميم وأن يعرف أيضا شروط المتواتر والآحاد ليقدم ما يجب تقديمه عند التعارض وأن يعرف الصحيح من الحديث والضعيف سندا ومتنا ليطرح الضعيف حيث لا يكون في فضائل الأعمال','3869','790','0');
INSERT INTO [t6336] VALUES (831,'على ما هو الراجح وإن جاز غيره في كلام العرب قال الطوفي ويشترط أن يعرف من النحو واللغة ما يكفيه في معرفة ما يتعلق بالكتاب والسنة من نص وظاهر ومجمل وحقيقة ومجاز وعام وخاص ومطلق ومقيد ودليل الخطاب ونحوه كفحوى الخطاب','3870','790','0');
INSERT INTO [t6336] VALUES (832,'وبما جاء به من الشرع المنقول كل بدليله من جهة الجملة لا من جهة التفصيل','3871','790','0');
INSERT INTO [t6336] VALUES (833,'وأصول الإيمان لجاز له الاجتهاد في الفروع قال والقدر الواجب من ذلك اعتقاد جازم إذ به يصير مسلما والإسلام شرط المفتي لا محالة قال الطوفي قلت المشترط في الاجتهاد بالجملة معرفة كل ما يتوقف حصول ظن الحكم الشرعي عليه سواء','3872','790','0');
INSERT INTO [t6336] VALUES (834,'قوله فصل','3880','790','0');
INSERT INTO [t6336] VALUES (835,'في أمر الدنيا ووقع إجماعا قاله ابن مفلح وذلك لقصته','3883','790','0');
INSERT INTO [t6336] VALUES (836,'فظاهر قوله تعالى وشاورهم في الأمر آل عمران وطريق المشاورة الاجتهاد','3889','790','0');
INSERT INTO [t6336] VALUES (837,'أبكي للذي عرض علي أصحابك من أخذهم الفداء فأنزل الله تعالى ما كان لنبي أن يكون له أسرى حتى يثخن في الأرض الأنفال وأيضا عفا الله عنك لم أذنت لهم التوبة قال في الفنون هو من أعظم دليل لرسالته','3890','790','0');
INSERT INTO [t6336] VALUES (838,'أن ينزل ببدر دون الماء قال له إن كان هذا بوحي فنعم وإن كان الرأي والمكيدة فأنزل بالناس','3894','790','0');
INSERT INTO [t6336] VALUES (839,'لا يسألني الله عن سنة أحدثتها فيكم لم يأمرني بها واحتج أبو القاسم بن مندة في ذم من فعل','3896','790','0');
INSERT INTO [t6336] VALUES (840,'يجوز أن يتعبد بالاجتهاد قال البرماوي قال أكثرهم إذا اجتهد يكون دائما مصيبا وليس كغيره في أنه تارة يصيب في نفس الأمر وتارة يخطيء بل اجتهاده لا يخطيء أبدا لعصمته ولمنصب النبوة عن الخطأ في الاجتهاد قال القاضي في العدة','3898','790','0');
INSERT INTO [t6336] VALUES (841,'يتصرف بالفتيا والتبليغ والقضاء والإقامة','3899','790','0');
INSERT INTO [t6336] VALUES (842,'أتاه رجلان يختصمان في مواريث وأشياء قد درست فقال إني إنما أقضي بينكم برأيي فيما لم ينزل علي فيه وله أيضا','3900','790','0');
INSERT INTO [t6336] VALUES (843,'آثارها مختلفة فإقامة الحدود وترتيب الجيوش وغير ذلك من منصب الإمام وليس لأحد','3900','790','0');
INSERT INTO [t6336] VALUES (844,'من أحيا أرضا ميتة فهي له','3901','790','0');
INSERT INTO [t6336] VALUES (845,'لهند بنت عتبة امرأة أبي سفيان بن حرب خذي من ماله ما يكفيك ويكفي ولدك بالمعروف قال الشافعي هو تصرف بالفتوى فمن ظفر بجنس حقه أو بغير جنسه عند التعذر جاز أن يستوفي منه حقه وهذا أحد القولين للموفق فإنه تارة قطع بأنه فتوى وتارة','3902','790','0');
INSERT INTO [t6336] VALUES (846,'من قتل قتيلا فله سلبه','3903','790','0');
INSERT INTO [t6336] VALUES (847,'قوله فصل','3905','790','0');
INSERT INTO [t6336] VALUES (848,'عقلا عند الأكثر وخالف أبو الخطاب وغيره ويجوز شرعا ووقع ذكره في العدة والواضح والطوفي وأكثر الشافعية ومنعه في المجرد وأبو الفرج وقوم مع القدرة وقوم مطلقا وابن حامد وجمع لمن بحضرته وقيل أو قريبا منه','3905','790','0');
INSERT INTO [t6336] VALUES (849,'وأمته في أنه كان يجوز له أن يجتهد ويحكم بالقياس من جهة العقل وقال بعضهم لا يجوز ذلك عقلا ثم قال لنا أنه إذا جاز أن يتعبد غيره بالنص تارة وبالاجتهاد أخرى جاز أن يتعبد هو بذلك وليس في العقل ما يحيله في حقه ويصححه في حقنا','3906','790','0');
INSERT INTO [t6336] VALUES (850,'ويأتي في الدليل على المسألة وأما الثاني وهو الوقوع ففيه أيضا مذاهب أحدها وهو الأصح أنه وقع وسيأتي الحوادث بذلك والثاني أنه لم يقع إذ لو وقع لاشتهر والثالث أنه لم يقع بين الحضور والرابع الوقف','3910','790','0');
INSERT INTO [t6336] VALUES (851,'في غزوة حنين إنه قتل قتيلا فقال رجل صدق وسلبه عندي فأرضه في حقه فقال أبو بكر لا ها الله إذا لا يعمد إلى أسد من أسد الله يقاتل عن الله ورسوله فيعطيك سلبه فقال صدق متفق عليه والمعروف لغة لا ها الله ذا أي يميني وقيل','3911','790','0');
INSERT INTO [t6336] VALUES (852,'إليه فجاء فقال نزل','3911','790','0');
INSERT INTO [t6336] VALUES (853,'رجلان فقال لعمرو بن العاص اقض بينهما فقال وأنت هنا يا رسول الله قال نعم','3912','790','0');
INSERT INTO [t6336] VALUES (854,'قوله فصل','3913','790','0');
INSERT INTO [t6336] VALUES (855,'تفترق أمتي على ثلاث وسبعين فرقة فرقة ناجية والباقي في النار وقد تقدم قريبا الخلاف بين العلماء في تكفير المبتدعة وقد ذكر هنا أقول تقشعر منها الجلود وتنفر','3919','790','0');
INSERT INTO [t6336] VALUES (856,'فإنا نعلم قطعا أن النبي','3924','790','0');
INSERT INTO [t6336] VALUES (857,'قوله فصل','3926','790','0');
INSERT INTO [t6336] VALUES (858,'كان إذا أمر أميرا على جيش أو سرية قال إذا حاصرت أهل حصن وأرادوك أن تنزلهم على حكم الله فلا تنزلهم على حكم الله ولكن أنزلهم على حكمك فإنك لا تدري تصيب منهم حكم الله أم لا','3939','790','0');
INSERT INTO [t6336] VALUES (859,'قوله فصل','3949','790','0');
INSERT INTO [t6336] VALUES (860,'تعارضا وكذا جزم الآمدي يمتنع العمل بأحدهما لاحتمال رجوعه كنصين وإن علم أسبقهما فالصحيح من المذهب أن الثاني مذهبه وهو ناسخ للأول وعليه الأكثر منهم أبو الخطاب في التمهيد والموفق في الروضة والقاضي في العدة','3954','790','0');
INSERT INTO [t6336] VALUES (861,'قوله فصل','3957','790','0');
INSERT INTO [t6336] VALUES (862,'قوله فصل','3965','790','0');
INSERT INTO [t6336] VALUES (863,'قوله فصل','3981','790','0');
INSERT INTO [t6336] VALUES (864,'قوله فصل','3989','790','0');
INSERT INTO [t6336] VALUES (865,'وما تفرع عن ذلك من إجماع أو قياس وغيرهما من الاستدلالات وطرقها بالاجتهاد ولو من النبي','3989','790','0');
INSERT INTO [t6336] VALUES (866,'وقاله الشافعي وأكثر أصحابه وجمهور أهل الحديث فيكون حكمه من جملة المدارك الشرعية فإذا قال هذا حلال عرفنا أن الله تعالى في الأزل حكم بحله أو هذا حرام أو نحو ذلك لا أنه ينشيء الحكم لأن ذلك من خصائص الربوبية قال ابن','3990','790','0');
INSERT INTO [t6336] VALUES (867,'قوله فصل','3996','790','0');
INSERT INTO [t6336] VALUES (868,'باب التقليد','4003','790','0');
INSERT INTO [t6336] VALUES (869,'قوله باب التقليد','4005','790','0');
INSERT INTO [t6336] VALUES (870,'وإلى المفتي وإلى الإجماع والقاضي إلى العدول ليس بتقليد ولو سمي تقليدا ساغ وفي المقنع المشهور أن أخذه بقول المفتي تقليد وهو أظهر وقدمه في آداب المفتي في الإجماع أيضا وقيل والقاضي قال الشيخ تقي الدين في المسودة','4007','790','0');
INSERT INTO [t6336] VALUES (871,'والإجماع واحترز بالنافي عن قبول القاضي البينة ونحو ذلك وما قاله الرافعي أرجح ومثلوا على قول الآمدي وابن الحاجب ذلك بأخذ العامي والمجتهد بقول مثله كما تقدم قال العضد فلا يكون الرجوع إلى الرسول','4009','790','0');
INSERT INTO [t6336] VALUES (872,'بالمعجز والإجماع بما تقدم في حجيته وقول الشاهد والمفتي بالإجماع','4009','790','0');
INSERT INTO [t6336] VALUES (873,'قوله فصل','4011','790','0');
INSERT INTO [t6336] VALUES (874,'عن الكلام في القدر ورواه الترمذي من حديث أبي هريرة من رواية صالح المري وهو ضعيف ورواه أحمد','4020','790','0');
INSERT INTO [t6336] VALUES (875,'يخاصمونه في القدر قالوا لو كان فعله الصحابة ونقل كالفروع رد هو كذلك لئلا يلزم نسبتهم إلى الجهل وهو باطل لأنه غير ضروري ولم ينقل لعدم الحاجة قالوا لو كان أنكرت على العامة تركه','4022','790','0');
INSERT INTO [t6336] VALUES (876,'طلب العلم فريضة على كل مسلم','4026','790','0');
INSERT INTO [t6336] VALUES (877,'قوله فصل','4029','790','0');
INSERT INTO [t6336] VALUES (878,'قوله فصل','4053','790','0');
INSERT INTO [t6336] VALUES (879,'لا تزال طائفة من أمتي ظاهرين إلى آخره قال ابن بطال لأن أمته آخر الأمم وعليها تقوم الساعة وإن ظهرت أشراطها وضعف الدين فلا بد أن يبقى من أمته من يقوم به قال فإن قيل قال النبي','4055','790','0');
INSERT INTO [t6336] VALUES (880,'قال لا تزال طائفة من أمتي ظاهرين على الحق لا يضرهم من خالفهم قيل وأين هم يا رسول الله قال ببيت المقدس أو أكناف بيت المقدس انتهى','4056','790','0');
INSERT INTO [t6336] VALUES (881,'لا تزال طائفة من أمتي ظاهرين على الحق رد الخبر الأول أدل على المقصود ولو تعارضا سلم الأول وأيضا التفقه فرض كفاية ففي تركه اتفاق الأمر على باطل رد منعه الآمدي إن أمكن تقليد العصر السابق ثم فرض عند إمكانه فإذا','4060','790','0');
INSERT INTO [t6336] VALUES (882,'لا يخلو عصر من حجة لله وذكره القاضي أيضا وقوله لا تزال طائفة من أمتي ظاهرين على الحق حتى يردوا علي فلا يصح','4061','790','0');
INSERT INTO [t6336] VALUES (883,'ينبغي أن تكون ألفا أو ألفا ومائتين وذكر القاضي أن ابن شاقلا اعترض عليه به فقال إن كنت لا أحفظ فإني أفتي بقول من يحفظ أكثر منه قال القاضي لا يقتضي هذا أنه كان يقلد أحمد لمنعه الفتيا بلا علم قال بعض أصحابنا ظاهره','4071','790','0');
INSERT INTO [t6336] VALUES (884,'أصحابي كالنجوم بأيهم اقتديتم اهتديتم وفيهم الأفضل من غيره وأيضا العامي لا يمكنه الترجيح لقصوره ولو كلف بذلك لكان تكليفا بضرب من الاجتهاد لكن زيف ابن الحاجب بأن ذلك يظهر بالتسامع ورجوع العلماء إليه وغيره لكثرة','4078','790','0');
INSERT INTO [t6336] VALUES (885,'في كل أمره ونهيه وهو خلاف الإجماع وتوقف أيضا في جوازه وقال أيضا إن خالفه لقوة الدليل أو زيادة علم أو تقوى فقد','4081','790','0');
INSERT INTO [t6336] VALUES (886,'قوله فصل','4089','790','0');
INSERT INTO [t6336] VALUES (887,'ينهى عن قيل وقال وإضاعة المال وكثرة السؤال وفي لفظ إن الله كره لكم ذلك متفق عليهما وفي حديث اللعان وكره','4096','790','0');
INSERT INTO [t6336] VALUES (888,'وأصحابه','4106','790','0');
INSERT INTO [t6336] VALUES (889,'باب ترتيب الأدلة والتعادل والتعارض والترجيح','4111','790','0');
INSERT INTO [t6336] VALUES (890,'قوله باب ترتيب الأدلة والتعادل والتعارض والترجيح','4113','790','0');
INSERT INTO [t6336] VALUES (891,'المشهود لهم بالخيرية في قوله خير القرون قرني ثم الذين يلونهم فإن فرض في عصر واحد إجماعان فالثاني باطل لأن كل من اجتهد من المتأخر فقوله باطل لمخالفته الإجماع السابق فإن كان أحد الإجماعين مختلفا فيه والآخر متفق عليه فالمتفق','4117','790','0');
INSERT INTO [t6336] VALUES (892,'عن الصلاة بعد الصبح والعصر بقوله','4121','790','0');
INSERT INTO [t6336] VALUES (893,'ورضي رسول الله','4126','790','0');
INSERT INTO [t6336] VALUES (894,'في البحر هذا الطهور ماؤه الحل ميتته رواه أبو داود وغيره مع قوله تعالى قل لا أجد في ما أوحي إلي محرما إلى قوله أو لحم خنزير الأنعام فكل من الآية والحديث يتناول خنزير','4126','790','0');
INSERT INTO [t6336] VALUES (895,'على ما رواه الجماعة عن النبي','4138','790','0');
INSERT INTO [t6336] VALUES (896,'على كتابة قاله الجرجاني وابن عقيل والمجد والآمدي وقال أحمد سواء فيحتمل في الحجة ويحتمل لا ترجيح كالقاضي وابن البنا وما سمع منه على ما سكت عنه مع حضوره','4158','790','0');
INSERT INTO [t6336] VALUES (897,'على فعله وثالثتها سواء وما لا تعم به البلوى في الآحاد وما لم ينكره المروي عنه','4159','790','0');
INSERT INTO [t6336] VALUES (898,'ينطق بالفصيح وبالأفصح فلا فرق بين ثبوتهما عنه والكلام في سوى ذلك','4174','790','0');
INSERT INTO [t6336] VALUES (899,'ما اجتمع الحلال والحرام إلا غلب الحرام لأنه أحوط','4178','790','0');
INSERT INTO [t6336] VALUES (900,'البيت قال بلال','4181','790','0');
INSERT INTO [t6336] VALUES (901,'لم يصل في البيت لأني كنت معه فيه ولم يغب على نظري طرفة عين فيه ولم أره صلى فيه أو قال أخبرني رسول الله','4183','790','0');
INSERT INTO [t6336] VALUES (902,'لا ضرر ولا ضرار في الإسلام','4198','790','0');
INSERT INTO [t6336] VALUES (903,'في البحر الحل ميتته فإنه عام في ميتة البحر حتى خنزيره مع قوله تعالى قل لا أجد في ما أوحي إلي محرما على طاعم يطعمه إلا أن يكون ميتة أو دما مسفوحا أو لحم خنزير الأنعام يتناول خنزير البحر فتعارض عموم الكتاب والسنة','4202','790','0');
INSERT INTO [t6336] VALUES (904,'عليكم بسنتي وسنة الخلفاء الراشدين من بعدي عضوا عليها بالنواجذ ولأن الظاهر أنهم لم يتركوا النص الآخر إلا لحجة عندهم فلذلك قدم','4206','790','0');
INSERT INTO [t6336] VALUES (905,'اقتدوا باللذين من بعدي أبي بكر وعمر قال أيوب السختياني إذا بلغك اختلاف عن النبي','4207','790','0');
INSERT INTO [t6336] VALUES (906,'ظاهرا وباطنا كامتناعه من الصلاة حتى قال علي هما علي وأنه ابتداء ضمان','4211','790','0');
INSERT INTO [t6336] VALUES (907,'في الظاهر والباطن على ما يتضمن إصابته في الظاهر والباطن على ما يتضمن إصابته في الظاهر فقط فالأول مقدم ومرجح لأنه بعيد عن الخطأ وهو اللائق به وبحاله','4215','790','0');
