INSERT INTO [t6362] VALUES (118,'من ذا الذي منا إذا أشباههم إن كنت ذا علم وذا عرفان قال الخوارج للرسول اعدل فلم تعدل وما ذي قسمة الديان','574','57','0');
INSERT INTO [t6362] VALUES (119,'ماذا بعدل في العبارة وهي موهمة التحيز وانتقال مكان','575','57','0');
INSERT INTO [t6362] VALUES (120,'قال الناظم رحمه الله تعالى وكذلك قلت بأن منه ينزل القرآن تنزيلا من الرحمن كان الصواب بأن يقال نزوله من لوحه او من محل ثان','576','57','0');
INSERT INTO [t6362] VALUES (121,'أي يقول الجهمي للرسول إنك تقول أين الله والاين ممتنع على الله تعالى ومحال وليس بممكن والصواب ان تقول من الله كما يسأل الملكان في القبر الميت فيقولان من ربك وما دينك ومن نبيك','576','57','0');
INSERT INTO [t6362] VALUES (122,'قال الناظم رحمه الله تعالى','576','57','0');
INSERT INTO [t6362] VALUES (123,'قالوا لنا هذا دليل أنه فوق السما بأوضح البرهان فالناس طرا إنما يدعونه من فوق هذي فطرة الرحمن لا يسألون القبلة العليا ولكن يسألون الرب ذا الاحسان قالوا وما كانت إشارته إلى غير الشهيد منزل الفرقان أتراه أمسى للسما مستشهدا حاشاه من تحريف','577','57','0');
INSERT INTO [t6362] VALUES (124,'قال الناظم رحمه الله تعالى','577','57','0');
INSERT INTO [t6362] VALUES (125,'لو لم تقل فوق السماء ولم تشر باشارة حسية ببيان وسكت عن تلك الأحاديث التي قد صرحت بالفوق للديان وذكرت أن الله ليس بداخل فينا ولا هو خارج الأكوان كنا أنتصفنا من أولي التجسيم بل كانوا لنا أسرى عبيد هوان لكن منحتهم سلاحا كلما شاؤوا لنا','578','57','0');
INSERT INTO [t6362] VALUES (126,'قال الناظم رحمه الله تعالى','580','57','0');
INSERT INTO [t6362] VALUES (127,'قال الناظم رحمه الله تعالى','580','57','0');
INSERT INTO [t6362] VALUES (128,'واخو الجهالة نسبة للفظ والمعنى فنسب العالم الرباني','580','57','0');
INSERT INTO [t6362] VALUES (129,'ورأيت أكوابا هناك كثيرة مثل النجوم لوارد ظمآن ورأيت حوض الكوثر الصافي الذي لا زال يشخب فيه ميزابان ميزاب سنته وقول إلهه وهما مدى الايام لا ينيان والناس لا يردونه إلا من الالآف أفرادا ذوو إيمان وردوا عذاب مناهل أكرم بها ووردتم أنتم','582','57','0');
INSERT INTO [t6362] VALUES (130,'قال الناظم رحمه الله تعالى','583','57','0');
INSERT INTO [t6362] VALUES (131,'فصل','585','57','0');
INSERT INTO [t6362] VALUES (132,'ومن العجائب قولهم لمن اقتدى بالوحي من أثر ومن قرآن حشوية يعنون حشوا في الوجود وفضله في أمة الانسان ويظن جاهلهم بانهم حشو ارب العباد بداخل الاكوان إذ قولهم فوق العباد وفي السماء الرب ذو الملكوت والسلطان ظن الحمير بأن في للظرف','585','57','0');
INSERT INTO [t6362] VALUES (133,'قال الناظم','588','57','0');
INSERT INTO [t6362] VALUES (134,'فصل','590','57','0');
INSERT INTO [t6362] VALUES (135,'في بيان عداوتهم في تلقيب أهل القرآن والحديث بالمجسمة وبيان أنهم اولى بكل لقب خبيث كم ذا مشبهة مجسمة نوا بتة مشبة جاهل فتان أسماء سميتم بها أهل الحديث وناصري القرآن والايمان سميتموهم أنتم وشيوخكم بهتا بها من غير ما سلطان وجعلتموها سبة','590','57','0');
INSERT INTO [t6362] VALUES (136,'سموه تجسيما وتشبيها فلسنا جاحديه لذلك الهذيان','591','57','0');
INSERT INTO [t6362] VALUES (137,'قال الناظم رحمه الله تعالى بل بيننا فرق لطيف بل هو الفرق العظيم لمن له عينان','591','57','0');
INSERT INTO [t6362] VALUES (138,'فكلامه فيما لديكم لا حقيقة تحته تبدو الى الأذهان في ذكر آيات العلو وسائر الأوصاف وهي القلب للقرآن بل قول رب الناس ليس حقيقة فيما لديكم يا أولي العرفان وإذا جعلتم ذا مجازا صح ان ينفى على الأطلاق والامكان وحقائق الألفاظ بالعقل انتفت فيما','592','57','0');
INSERT INTO [t6362] VALUES (139,'فصل','594','57','0');
INSERT INTO [t6362] VALUES (140,'في بيان مورد أهل التعطيل وانهم تعرضوا بالقلوط عن مورد السلسبيل يا وارد القلوط ويحك لو ترى ماذا على شفتيك والاسنان أو ما ترى آثارها في القلب والنيات والاعمال والاركان لو طاب منك الورد طابت كلها أنى تطيب موارد الانتان يا وارد القلوط طهر','594','57','0');
INSERT INTO [t6362] VALUES (141,'والله ليس بأصعب الوردين بل هو أسهل الوردين للظمآن','595','57','0');
INSERT INTO [t6362] VALUES (142,'فصل','595','57','0');
INSERT INTO [t6362] VALUES (143,'وبوده لو كان شيء غير ذا لكن مخافة صاحب السلطان','596','57','0');
INSERT INTO [t6362] VALUES (144,'قال الناظم رحمه الله تعالى فلقد أتانا عن كبير فيهم وهو الحقير مقالة الكفران لو كان يمكني وليس بممكن لحككت من ذا المصحف العثماني ذكر استواء الرب فوق العرش لكن ذاك ممتنع على الانسان','597','57','0');
INSERT INTO [t6362] VALUES (145,'قال الناظم رحمه الله تعالى والله لولا هيبة الاسلام والقرآن والأمراء والسلطان لأتوا بكل مصيبة ولدكدكوا ال إسلام فوق قواعد الاركان فلقد رأيتم ما جرى لأئمة ال إسلام من محن على الازمان لا سيما لما استمالوا جاهلا ذا قدرة في الناس مع سلطان','598','57','0');
INSERT INTO [t6362] VALUES (146,'قال الناظم رحمه الله تعالى فيرى عمائم ذات أذناب على تلك الفشور طويلة الأردان ويرى هيولي لا تهول لمبصر وتهول أعمى في ثياب جبان فإذا أصاخ بسمعة ملؤوه من كذب وتلبيس ومن بهتان فيرى ويسمع فشرهم وفشارهم يا محنة العينين والآذان','600','57','0');
INSERT INTO [t6362] VALUES (147,'قال الناظم رحمه الله تعالى فلقد رأينا من فريق منهم أمرا تهد له قوى الإيمان من سبهم أهل الحديث ودينهم أخذ الحديث وترك قول فلان يا أمة غضب الإله عليهم الأجل هذا تشتموا بهوان تبا لكم إذ تشتمون زوامل ال إسلام حزب الله والقرآن وسببتموهم','601','57','0');
INSERT INTO [t6362] VALUES (148,'قال الناظم رحمه الله تعالى قوم أقامهم الإله لحفظ هذا الدين من ذي بدعة شيطان وأقامهم حرسا من التبديل والتحريف والتتميم والنقصان يزك على الاسلام بل حصن له يأوي اليه عساكر الفرقان فهم المحك فمن يرى متنقصا لهم فزنديق خبيث جنان إن تتهمه','602','57','0');
INSERT INTO [t6362] VALUES (149,'قال الناظم رحمه الله تعالى ذكروه فوق منابر وبسكة رقموا اسمه في ظاهر الأثمان والأمر والنهي المطاع لغيره ولمهتد ضربت بذا مثلان يا للعقول أيستوي من قال بالقرآن والآثار والبرهان ومخالف هذا وفطرة ربه الله أكبر كيف يستويان بل فطرة الله','604','57','0');
INSERT INTO [t6362] VALUES (150,'فصل','606','57','0');
INSERT INTO [t6362] VALUES (151,'يا ويلهم ولوا نتائج فكرهم وقضوا بها قطعا على القرآن ورذالهم ولو إشارات ابن سينا حين ولوا منطق اليونان وانظر إلى نص الكتاب مجدلا وسط العرين ممزق اللحمان بالطعن بالاجمال والاضمار والتخصيص والتأويل بالبهتان والاشتراك وبالمجاز وحذف ما','607','57','0');
INSERT INTO [t6362] VALUES (152,'إن غاب ثابت عنه أقوال الرسو ل همالهم دون الوري حكمان فأتاهم مالم يكن في ظنهم في حكم جنكسخان ذي الطغيان','608','57','0');
INSERT INTO [t6362] VALUES (153,'فعلوا بملته وسنته كما فعلوا بأمته من العدوان والله ما أنقادوا لجنكسخان حتى أعرضوا عن محكم القرآن والله ما ولوه إلا بعد عز ل الوحى عن علم وعن إيقان عزلوه عن سلطانه وهو اليقين المستفاد لنا من السلطان هذا ولم يكف الذي فعلوه حتى تمموا','608','57','0');
INSERT INTO [t6362] VALUES (154,'هذا وقد عضهوه أن نصوصه معزولة عن أمرة الإيقان لكن غايتها الظنون وليته ظنا يكون مطابقا ببيان لكن ظواهر لا يطابق ظنها ما في الحقيقة عندنا بوزان إلا إذا ما أولت فمجازها بزيادة فيها أو النقصان أو بالكناية واستعارات وتشبيه وأنواع','609','57','0');
INSERT INTO [t6362] VALUES (155,'قال الناظم رحمه الله تعالى هذا وقولهم خلاف الحسن والمعقول والمنقول والبرهان مع كونه أيضا خلاف الفطرة ال أولى وسنة ربنا الرحمن والله قد فطر العباد على التفا هم بالخطاب لمقصد التبيان كل يدل على الذي في نفسه بكلامه من اهل كل لسان فترى','612','57','0');
INSERT INTO [t6362] VALUES (156,'قال الناظم رحمه الله تعالى لو أنكم والله عاملتم بذا أهل العلوم وكتبهم بوزان فسدت تصانيف الوجود بأسرها وغدت علوم الناس ذات هوان هذا وليسوا في بيان علومهم مثل الرسول ومنزل القرآن والله لو صح الذي قد قلتم قطعت سبيل العلم والايمان','614','57','0');
INSERT INTO [t6362] VALUES (157,'قال الناظم رحمه الله تعالى هذا ومن بهتانهم أن اللغا ت أتت بنقل الفرد والوحدان فانظر الى الألفاظ في جريانها في هذه الأخبار والقرآن أتظنها تحتاج نقلا مسندا متواترا او نقل ذي وحدان ام قد جرت مجرى الضروريات لا تحتاج نقلا وهي ذات بيان','616','57','0');
INSERT INTO [t6362] VALUES (158,'قال الناظم رحمه الله تعالى ومن المصائب قول قائلهم بأن الله أظهر لفظه بلسان وخلافهم فيه كثير ظاهر عربي وضع ذاك ام سرياني','616','57','0');
INSERT INTO [t6362] VALUES (159,'وإذا هم اختلفوا بلفظة مكة فيه لهم قولان معروفان أفبينهم خلف بان مرادهم حرم الإله وقبلة البلدان وإذا هم اختلفوا بلفظة أحمد فيدلهم قولان مذكوران أفبينهم خلف بأن مرادهم منه رسول الله ذو البرهان ونظير هذا ليس يحصر كثرة يا قوم فاستحيوا','617','57','0');
INSERT INTO [t6362] VALUES (160,'يرمونهم كذبا بكل عظيمة حاشاهم من إفك ذي بهتان','618','57','0');
INSERT INTO [t6362] VALUES (161,'فصل','619','57','0');
INSERT INTO [t6362] VALUES (162,'في تنزيه أهل الحديث والشريعة عن الألقاب القبيحة الشنيعة فرموهم بغيا بما الرامي به أولى ليدفع عنه فعل الجاني يرمي البريء بما جناه مباهتا ولذاك عند الغر يشتبهان سموهم حشوية ونوابتا ومجسمين وعابدي أوثان وكذاك أعداء الرسول وصحبه وهم','619','57','0');
INSERT INTO [t6362] VALUES (163,'فصل','620','57','0');
INSERT INTO [t6362] VALUES (164,'في نكتة بديعة تبين ميراث الملقبين من المشركين والموحدين والملقبين الأولى بفتح القاف والثانية بكسرها هذا وثم لطيفة عجب سأبديها لكم يا معشر الاخوان فاسمع فذاك معطل ومشبه واعقل فذاك حقيقة الانسان لا بد أن يرث الرسول وضده في الناس طائفتان','620','57','0');
INSERT INTO [t6362] VALUES (165,'فأتى الألى ورثوهم فرموا بها وراثه بالبغي والعدوان هذا يحقق إرث كل منهما فاسمع وعه يامن له أذنان والآخرون أولو النفاق فأضمروا شيئا وقالوا غيره بلسان وكذا المعطل مضمر تعطيله قد أظهر التنزيه للرحمن هذي مواريث العباد تقسمت بين','621','57','0');
INSERT INTO [t6362] VALUES (166,'قال الناظم رحمه الله تعالى هذا وثم لطيفة أخرى بها سلوان من قد سب بالبهتان تجد المعطل لاعنا لمجسم ومشبه لله بالإنسان','621','57','0');
INSERT INTO [t6362] VALUES (167,'فصل','623','57','0');
INSERT INTO [t6362] VALUES (168,'في بيان اقتضاء التجهم والجبر والارجاء للخروج عن جميع ديانات الانبياء واسمع وعه سرا عجيبا كان مكتوما من الأقوام منذ زمان فأذعته بعد اللتيا والتي نصحا وخوف معرة الكتمان جيم وجيم ثم جيم معهما مقرونة مع احرف بوزان فيها لدى الأقوام طلسم متى','623','57','0');
INSERT INTO [t6362] VALUES (169,'لا فاعل أبدا ولا هو قادر كالميت أدرج داخل الأكفان والأمر والنهي اللذان توجها فهما كأمر العبد بالطيران وكأمره الأعمى بنقط مصاحف أو شكلها حذرا من الالحان','625','57','0');
INSERT INTO [t6362] VALUES (170,'ومطيع أمر الله مثل مطيع ما يقضي به وكلاهما عبدان','625','57','0');
INSERT INTO [t6362] VALUES (171,'فانظر إلى ما قادت الجيم التي للجبر من كفر ومن بهتان','625','57','0');
INSERT INTO [t6362] VALUES (172,'فتكون حقا مؤمنا وجميع ذا وزر عليك وليس بالكفران','626','57','0');
INSERT INTO [t6362] VALUES (173,'فصل في جواب الرب تبارك وتعالى يوم القيامة إذا سئل المعطل والمثبت عن قول كل واحد منهما وسل المعطل ما تقول إذا أتى فئتان عند الله تختصمان إحداهما حكمت على معبودها بعقولها وبفكرة الأذهان سمته معقولا وقالت إنه أولى من المنصوص بالبرهان','629','57','0');
INSERT INTO [t6362] VALUES (174,'وكذلك قلنا ما لفعلك حكمة من أجلها خصصته بزمان','629','57','0');
INSERT INTO [t6362] VALUES (175,'وهو الذي أدت إليه عقولنا لما وزنا الوحي بالميزان إن كان ذلكم الجواب مخلصا فامضوا عليه يا ذوي العرفان تالله ما بعد البيان لمنصف إلا العناد ومركب الخذلان','631','57','0');
INSERT INTO [t6362] VALUES (176,'فصل','633','57','0');
INSERT INTO [t6362] VALUES (177,'يا أيها الباغي على أتباعه بالظلم والبهتان والعدوان قد حملوك شهادة فاشهد بها إن كنت مقبولا لدى الرحمن واشهد عليهم إن سألت بأنهم قالوا إله العرش والأكوان فوق السماوات العلى حقا على العرش استوى سبحان ذي السلطان والأمر ينزل منه ثم يسير','633','57','0');
INSERT INTO [t6362] VALUES (178,'هو قول رب العالمين حقيقة لفظا ومعنى ليس يفترقان واشهد عليهم أنه سبحانه قد كلم المولود من عمران','633','57','0');
INSERT INTO [t6362] VALUES (179,'واشهد عليهم أنهم قد أثبتوا الأسماء والأوصاف للديان وكذلك الأحكام أحكام الصفا ت وهذه الأركان للإيمان','634','57','0');
INSERT INTO [t6362] VALUES (180,'قال الناظم رحمه الله تعالى','636','57','0');
INSERT INTO [t6362] VALUES (181,'قال الناظم رحمه الله تعالى','637','57','0');
INSERT INTO [t6362] VALUES (182,'قال الناظم رحمه الله تعالى واشهد عليهم أنهم حملوا النصو ص على الحقيقة لا المجاز الثاني','638','57','0');
INSERT INTO [t6362] VALUES (183,'قال الناظم رحمه الله تعالى','641','57','0');
INSERT INTO [t6362] VALUES (184,'فهناك انتم أكفر الثقلين من انس وجن ساكني النيران','641','57','0');
INSERT INTO [t6362] VALUES (185,'قال الناظم رحمه الله تعالى','641','57','0');
INSERT INTO [t6362] VALUES (186,'فصل في عهود المثبين مع رب العالمين','656','57','0');
INSERT INTO [t6362] VALUES (187,'ولأجعلن لحومهم ودماءهم في يوم نصرك أعظم القربان ولأحملن عليهم بعساكر ليس تفر إذا التقى الزحفان بعساكر الوجهين والفطرات والمعقول والمنقول بالاحسان','657','57','0');
INSERT INTO [t6362] VALUES (188,'فصل','659','57','0');
INSERT INTO [t6362] VALUES (189,'أنا تحملنا الشهادة بالذي قلتم نؤديها لدى الرحمن ما عندكم في الأرض قرآن كلا م الله حقا يا أولي العدوان كلا ولا فوق السماوات العلى رب يطاع بواجب الشكران كلا ولا في القبر أيضا عندكم من مرسل والله عند لسان هاتيك عورات ثلاث قد بدت','659','57','0');
INSERT INTO [t6362] VALUES (190,'قال الناظم رحمه الله تعالى','663','57','0');
INSERT INTO [t6362] VALUES (191,'ولأجل هذا رام ناصر قولكم ترقيعه يا كثرة الخلقان قال الرسول بقبره حي كما قد كان فوق الارض والرجمان من فوقه أطباق ذاك التراب واللبنات قد عرضت على الجدران لو كان حيا في الضريح حياته قبل الممات بغير ما فرقان ما كان تحت الأرض بل من فوقها','663','57','0');
INSERT INTO [t6362] VALUES (192,'قال الناظم رحمه الله تعالى','664','57','0');
INSERT INTO [t6362] VALUES (193,'قال الناظم رحمه الله تعالى','665','57','0');
INSERT INTO [t6362] VALUES (194,'فصل','669','57','0');
INSERT INTO [t6362] VALUES (195,'فان احتججتم بالشهيد بأنه حي كما قد جاء في القرآن والرسل أكمل حالة منه بلا شك وهذا ظاهر التبيان فلذاك كانوا بالحياة أحق من شهدائنا بالعقل والبرهان وبأن عقد نكاحه لم ينفسخ فنساؤه في عصمة وصيان ولأجل هذا لم يحل لغيره منهن واحدة','669','57','0');
INSERT INTO [t6362] VALUES (196,'معنى هذه الأبيات أن القائلين بحياة الرسل في القبور احتجوا بأشياء منها الشهداء فانهم أحياء بنص القرآن كما قال تعالى ولا تحسبن الذين قتلوا في سبيل الله أمواتا بل أحياء عند ربهم يرزقون آل عمران والرسل أكمل من الشهداء بغير شك فهم احق بالحياة من','670','57','0');
INSERT INTO [t6362] VALUES (197,'فصل','671','57','0');
