INSERT INTO [t6362] VALUES (490,'قال الناظم رحمه الله تعالى يا حبذا ذاك الخلاف فانه عين الوفاق لطاعة الرحمن أو ما علمت بأن أعداء الرسو ل عليه عابوا الخلف بالبهتان لشيوخهم ولما عليه قد مضى أسلافهم في سالف الأزمان ما العيب الا في خلاف النص لا رأي الرجال وفكرة','924','57','0');
INSERT INTO [t6362] VALUES (491,'ومصرح أن الذي قد قاله اهل الحديث وعسكر القرآن','925','57','0');
INSERT INTO [t6362] VALUES (492,'قال الناظم لكنه قد قال إن كلامه معنى يقوم بربنا الرحمن في القول خالفناه نحن وأنتم في الفوق والأوصاف للديان لو كان نفس خلافنا كفرا وكا ن خلافكم هو مقتضى الايمان هذا وخالفتم لنص حين خا لفنا لرأي الجهم ذي البهتان والله ما لكم جواب','932','57','0');
INSERT INTO [t6362] VALUES (493,'فصل','934','57','0');
INSERT INTO [t6362] VALUES (494,'لما تحيزتم الى الاشياخ وانحازوا إلى المبعوث بالقرآن نسبوا اليه دون كل مقالة أو حالة أو قائل ومكان هذا انتساب أولي التفرق نسبة من اربع معلومة التبيان','934','57','0');
INSERT INTO [t6362] VALUES (495,'ما ضرهم والله بغضكم لهم إذ وافقوا حقا رضى الرحمن يا من يعاديهم لأجل مآكل ومناصب ورياسة الاخوان تهنيك هاتيك العداوة كم بها من حسرة ومذلة وهوان ولسوف تجني غيها والله عن قرب وتذكر صدق ذي الايمان فاذا تقطعت الوسائل وانتهت تلك','935','57','0');
INSERT INTO [t6362] VALUES (496,'فالغرس دلب كله وهو الذي يسقي ويحفظ عند اهل زمان فالغرس في تلك الحضارة شارب فضل المياه مصاوة البستان لكنما البلوى من الحطاب قطاع الغراس وعاقر الحيطان بالفوس يضرب في اصول الغرس كي يجتثها ويظن ذا إحسان ويظل يحلف كاذبا لم اعتمد في ذا','937','57','0');
INSERT INTO [t6362] VALUES (497,'فصل','938','57','0');
INSERT INTO [t6362] VALUES (498,'والهجرة الأخرى الى المبعوث بالإسلام والايمان والاحسان','938','57','0');
INSERT INTO [t6362] VALUES (499,'يا هجرة طالت مسافتها على من خص بالحرمان والخذلان يا هجرة طالت مسافتها على كسلان منخوب الفؤاد جهان','939','57','0');
INSERT INTO [t6362] VALUES (500,'نار هي النور المبين ولم يكن ليراه إلا من له عينان','939','57','0');
INSERT INTO [t6362] VALUES (501,'لكن رضيتم بالاماني وابتليتم بالحظوظ ونصرة الاخوان بل غركم ذاك الغرور وسولت لكم النفوس وساوس الشيطان ونبذتم عسل النصوص وراءكم وقنعتم بقطارة الأذهان','940','57','0');
INSERT INTO [t6362] VALUES (502,'فهما بحكم الحق أولى منهما سبحانك اللهم ذا السبحان حتى أذا انكشف الغطاء وحصلت أعمال هذا الخلق في الميزان واذا انجلى هذا الغبار وصار ميدان السباق تناله العينان','940','57','0');
INSERT INTO [t6362] VALUES (503,'وهناك يعلم مؤثر الآراء والشطحات والهذيان والبطلان أي البضائع قد أضاع وما الذي منها تعوض في الزمان الفاني','941','57','0');
INSERT INTO [t6362] VALUES (504,'لو شاء كان الناس شيئا واحدا ما فيهم من تائه حيران','941','57','0');
INSERT INTO [t6362] VALUES (505,'قال الناظم وسل العياذ من اثنتين هما اللتا ن بهلك هذا الخلق كافلتان شر النفوس وسيىء الأعمال ما والله أعظم منهما شران ولقد أتى هذا التعوذ منهما في خطبة المبعوث بالقرآن','942','57','0');
INSERT INTO [t6362] VALUES (506,'في ظهور الفرق المبين بين دعوة الرسل ودعوة المعطلين والفرق بين الدعوتين فظاهر جدا لمن كانت له أذنان','943','57','0');
INSERT INTO [t6362] VALUES (507,'ولأي شيء بالغوا بالوصف بالاثبات دون النفي كل زمان ولأي شيء أنتم بالغتم في النفي والتعطيل بالقفزان فجعلتم نفي الصفات مفصلا تفصيل نفي العيب والنقصان وجعلتم الاثبات أمرا مجملا عكس الذي قالوه بالبرهان','945','57','0');
INSERT INTO [t6362] VALUES (508,'ثم قال رحمه الله تعالى فسلوهم بسؤال كتبهم التي جاؤوا بها عن علم هذا الشان وسلوهم هل ربكم في أرضه أو في السماء وفوق كل مكان أم ليس من ذا كله شيء فلا هو داخل أو خارج الاكوان فالعلم والتبيان والنصح الذي فيهم يبين الحق كل بيان لكنما','946','57','0');
INSERT INTO [t6362] VALUES (509,'فصل','946','57','0');
INSERT INTO [t6362] VALUES (510,'يا رب قد قالوا بأن مصاحف الاسلام ما فيها من القرآن إلا المداد وهذه الأوراق والجلد الذي قد سل من حيوان','949','57','0');
INSERT INTO [t6362] VALUES (511,'إن اليقين قواطع عقلية ميزانها هو منطق اليونان هذا دليل الرفع منه وهذه أعلامه في آخر الأزمان يا رب من أهلوه حقا كي يرى أقدامهم منا على الاذقان أهلوه من لا يرتضي منه بديلا فهو كافيهم بلا نقصان وهو الدليل لهم وهاديهم الى الايمان','951','57','0');
INSERT INTO [t6362] VALUES (512,'فصل','952','57','0');
INSERT INTO [t6362] VALUES (513,'أي لا تسموا أهل الحديث والسنة مشبهة فانكم أهل التشبيه لأنكم شبهتم الرحمن بالجامدات في عدم الكلام ان الذي نزل الامين به على قلب الرسول الواضح البرهان هو قول ربي اللفظ والمعنى جميعا اذ هما أخوان مصطحبان لا تقطعوا رحما تولى وصلها الرحمن تنسلخوا','953','57','0');
INSERT INTO [t6362] VALUES (514,'الرق بفتح الراء الورق والله أكبر من على العرش استوى لكنه استولى على الأكوان','954','57','0');
INSERT INTO [t6362] VALUES (515,'والله أكبر من يخاف جلاله أملاكه من فوقهم ببيان','954','57','0');
INSERT INTO [t6362] VALUES (516,'أي إن أنواع العلو ثابتة لله سبحانه وهي علو القهر وعلو القدر وعلو الذات فبذاته خلق السماوات العلى ثم استوى بالذات فافهم ذان فضمير فعل الاستواء يعود للذات التي ذكرت بلا فرقان','954','57','0');
INSERT INTO [t6362] VALUES (517,'هذا اشارة الى قول من قال من المعطلة إن من أشار بأصبعه الى السماء وإن الله تعالى فوق خلقه تقطع اصبعه والله اكبر ظاهر ما فوقه شيء وشأن الله أعظم شان والله اكبر عرشه وسع السما والارض والكرسي ذا الاركان وكذلك الكرسي قد وسع الطبا ق السبع','955','57','0');
INSERT INTO [t6362] VALUES (518,'اشارة الى قول الجهمية وأتباعهم إنه تعالى لا داخل العالم ولا خارجه فان ذلك صفة المعدوم الله أكبر هتكت أستاركم وبدت لمن كانت له عينان','956','57','0');
INSERT INTO [t6362] VALUES (519,'والله أكبر من له الأسماء وال أوصاف كاملة بلا نقصان','956','57','0');
INSERT INTO [t6362] VALUES (520,'والله أكبر جل عن شبه الجما د كقول ذي التعطيل والكفران هم شبهوه بالجماد وليتهم قد شبهوه بكامل ذي شان الله اكبر جل عن شبه العبا د فذان تشبيهان ممتنعان الله أكبر واحد صمد وكل الشأن في صمدية الرحمن نفت الولادة والأبوة عنه والكفء الذي هو','956','57','0');
INSERT INTO [t6362] VALUES (521,'في تلازم التعطيل والشرك واعلم بأن الشرك والتعطيل مذ كانا هما لا شك مصطحبان أبدا فكل معطل هو مشرك حتما وهذا واضح التبيان فالعبد مضطر إلى من يكشف البلوى ويغني فاقة الانسان','957','57','0');
INSERT INTO [t6362] VALUES (522,'إحدى الطوائف مشرك بالهه فاذا دعاه دعا الها ثاني','958','57','0');
INSERT INTO [t6362] VALUES (523,'هو جاحد للرب يدعو غيره شركا وتعطيلا له قدمان هذا وثالث هذه الأقسام خير الخلق ذاك خلاصة الانسان يدعو الاله الحق لا يدعو سوا هـ قط في الاكوان والأزمان يدعوه في الرغبات والرهبات والحالات من سر ومن اعلان','958','57','0');
INSERT INTO [t6362] VALUES (524,'فهما إذا أخوان مصطحبان لا يتفارقان وليس ينفصلان فمعطل الأوصاف ذو شرك كذا ذو الشرك فهو معطل الرحمن أو بعض أوصاف الكمال له فحقق ذا ولا تسرع إلى النكران','959','57','0');
INSERT INTO [t6362] VALUES (525,'فصل','960','57','0');
INSERT INTO [t6362] VALUES (526,'إن المعطل جاحد للذات او لكمالها هذان تعطيلان متضمنا للقدح في نفس الالو هة كم بذاك القدح من نقصان والشرك فهو توسل مقصوده الزلفى من الرب العظيم الشان بعبادة المخلوق من حجر ومن بشر ومن قبر ومن أوثان فالشرك تعظيم بجهل من قيا س الرب','960','57','0');
