INSERT INTO [t6362] VALUES (549,'قال الناظم رحمه الله تعالى هذا وأولهم دخولا خير خلق الله من قد خص بالقرآن','1000','57','0');
INSERT INTO [t6362] VALUES (550,'وكذلك المملوك حين يقوم بالحقين سباق بغير توان','1002','57','0');
INSERT INTO [t6362] VALUES (551,'فصل','1003','57','0');
INSERT INTO [t6362] VALUES (552,'تقدم كلام على مضمون هذه الابيات وذكرنا الأوجه العشرة في تفضيل الجنتين الأوليين من كلام الناظم سبحان من غرست يداه جنة الفردوس عند تكامل البنيان ويداه أيضا أتقنت لبنائها فتبارك الرحمن أعظم بان هي في الجنان كآدم وكلاهما تفضيله من أجل هذا الشان','1009','57','0');
INSERT INTO [t6362] VALUES (553,'والساعة الأخرى إلى هذي السما ويقول هل من تائب ندمان أو داع او مستغفر أو سائل أعطيه إني واسع الاحسان','1012','57','0');
INSERT INTO [t6362] VALUES (554,'هذا الحديث بطوله وسياقه وتمامه في سنة الطبراني','1012','57','0');
INSERT INTO [t6362] VALUES (555,'فصل','1013','57','0');
INSERT INTO [t6362] VALUES (556,'وقصورها من لؤلؤ وزبرجد أو فضة أو خالص العقيان وكذاك من در وياقوت به نظم البناء بغاية الاتقان والطين مسك خالص أو زعفرا ن جابذا أثران مقبولان','1013','57','0');
INSERT INTO [t6362] VALUES (557,'فصل','1015','57','0');
INSERT INTO [t6362] VALUES (558,'فصل','1016','57','0');
INSERT INTO [t6362] VALUES (559,'ثنتان خالص حقه سبحانه وعبيده أيضا لهم ثنتان','1016','57','0');
INSERT INTO [t6362] VALUES (560,'فصل','1017','57','0');
INSERT INTO [t6362] VALUES (561,'يغشى الجميع فلا يشاهد بعضهم بعضا وهذا لاتساع مكان فيها مقاصير بها الأبواب من ذهب ودر زين بالمرجان وخيامها منصوبة برياضها وشواطيء الأنهار ذي الجريان ما في الخيام سوى التي لو قابلت للنيرين لقلت منكسفان لله هاتيك الخيام فكم بها','1017','57','0');
INSERT INTO [t6362] VALUES (562,'فصل','1018','57','0');
INSERT INTO [t6362] VALUES (563,'فصل','1019','57','0');
INSERT INTO [t6362] VALUES (564,'القنو واحد الأقناء والعيدان جمع عيدانه وهي النخل الطوال بل ذللت تلك القطوف فكيفما شئت انتزعت بأسهل الامكان ولقد أتى خبر بأن الساق من ذهب رواه الترمذي ببيان','1024','57','0');
INSERT INTO [t6362] VALUES (565,'فصل','1026','57','0');
INSERT INTO [t6362] VALUES (566,'يا لذة الاسماع لأتتعوضي بلذاذة الأوتار والعيدان أو ما سمعت سماعهم فيها غناء الحور بالاصوات والألحان واها لذياك السماع فانه ملئت به الأذنان بالاحسان واها لذياك السماع وطيبه من مثل أقمار على أغصان واها لذياك السماع فكم به','1026','57','0');
INSERT INTO [t6362] VALUES (567,'والله إن سماعهم في القلب والإيمان مثل السم في الأبدان والله ما انفك الذي هو دأبه أبدا من الإشراك بالرحمن فالقلب بيت الرب جل جلاله حبا وإخلاصا مع الاحسان فاذا تعلق بالسماع أصاره عبدا لكل فلانة وفلان','1029','57','0');
INSERT INTO [t6362] VALUES (568,'فصل','1035','57','0');
INSERT INTO [t6362] VALUES (569,'أنهارها في غير أخدود جرت سبحان ممسكها عن الفيضان من تحتهم تجري كما شاؤوا مفجرة وما للنهر من نقصان عسل مصفى ثم خمر ثم أنهار من الالبان والله ما تلك المواد كهذه لكن هما في اللفظ مجتمعان هذا وبينهما يسير تشابه وهو اشتراك قام بالأذهان','1035','57','0');
INSERT INTO [t6362] VALUES (570,'فصل','1037','57','0');
INSERT INTO [t6362] VALUES (571,'وطعامهم ما تشتهيه نفوسهم ولحوم طير ناعم وسمان وفواكه شتى بحسب مناهم يا شيعة كملت لذي الأيمان لحم وخمر والنسا وفواكه والطيب مع روح ومع ريحان وصحافهم ذهب يطوف عليهم بأكف خدام من الولدان','1037','57','0');
INSERT INTO [t6362] VALUES (572,'فصل','1040','57','0');
INSERT INTO [t6362] VALUES (573,'يسقون فيها من رحيق ختمه بالمسك أوله كمثل الثاني من خمرة لذت لشاربها بلا غول ولا داء ولا نقصان والخمر في الدنيا فهذا وصفها تغتال عقل الشارب السكران وبها من الأدواء ما هي أهله ويخاف من عدم لذي الوجدان فنفى لنا الرحمن أجمعها عن','1040','57','0');
INSERT INTO [t6362] VALUES (574,'فصل','1043','57','0');
INSERT INTO [t6362] VALUES (575,'فصل','1044','57','0');
INSERT INTO [t6362] VALUES (576,'فصل في فرشهم وما يتبعها','1047','57','0');
INSERT INTO [t6362] VALUES (577,'فصل','1049','57','0');
INSERT INTO [t6362] VALUES (578,'والحلي أصفى لؤلؤ وزبرجد وكذاك أسورة من العقيان ما ذاك يختص الاناث وانما هو للاناث كذاك للذكران التاركين لباسه في هذه الدنيا لأجل لباسه بجنان أو ما سمعت بأن حليتهم الى حيث انتهاء وضوئهم بوزان وكذا وضوء ابي هريرة كان قد فازت به','1049','57','0');
INSERT INTO [t6362] VALUES (579,'واحفظ حدود الرب لا تتعدها وكذاك لا تجنح الى النقصان وانظر الى فعل الرسول تجده قد أبدى المراد وجاء بالتبيان ومن استطاع يطيل غرته فمو قوف على الراوي هو الفوقاني فأبو هريرة قال ذا من كيسه فغدا يميزه اولو العرفان ونعيم الراوي له قد','1050','57','0');
INSERT INTO [t6362] VALUES (580,'فصل','1051','57','0');
INSERT INTO [t6362] VALUES (581,'يا من يطوف الكعبة الحصن التي حفت بذاك الحجر والاركان ويظل يسعى دائما حول الصفا ومحسر مسعاه لا العلمان ويروم قربان الوصال على منى والخيف يحجبه عن القربان فلذ تراه محرما أبدا ومو ضع حله منه فليس بدان يبغي التمتع مفردا عن حبه','1051','57','0');
INSERT INTO [t6362] VALUES (582,'كالزبد لينا في نعومة ملمس أصداف در دورت بوزان والصدر متسع على بطن لها حفت به خصران ذات ثمان وعليه أحسن سرة هي مجمع الخصرين قد غارت من الأعكان حق من العاج استدار وحوله حبات مسك جل ذو الاتقان','1060','57','0');
INSERT INTO [t6362] VALUES (583,'قال الناظم رحمه الله تعالى','1063','57','0');
INSERT INTO [t6362] VALUES (584,'أقدامها من فضة قد ركبت من فوقها ساقان ملتفان الساق مثل العاج ملموم يرى مخ العظام وراءه بعيان والريح مسك والجسوم نواعم واللون كالياقوت والمرجان وكلامها يسبي العقول بنغمة زادت على الأوتار والعيدان وهي العروب بشكلها وبدلها','1063','57','0');
INSERT INTO [t6362] VALUES (585,'فصل','1065','57','0');
INSERT INTO [t6362] VALUES (586,'لكن دراجا أبا السمح الذي فيه يضعفه أولو الاتقان هذا وبعضهم يصح عنه في التفسير كالمولود من حبان فحديثه دون الصحيح وإنه فوق الضعيف وليس ذا إتقان يعطى المجامع قوة المائة التي اجتمعت لأقوى واحد الانسان لا أن قوته تضاعف هكذا إذ قد يكون','1065','57','0');
INSERT INTO [t6362] VALUES (587,'فصل وإذا بدت في حلة من لبسها وتمايلت كتمايل النشوان تهتز كالغصن الرطيب وحمله ورد وتفاح على رمان وتبخترت في مشيها ويحق ذا ك لمثلها في جنة الحيوان','1069','57','0');
INSERT INTO [t6362] VALUES (588,'فصل في ذكر الخلاف بين الناس هل تحبل نساء أهل الجنة أم لا والناس بينهم خلاف هل بها حبل وفي هذا لهم قولان فنفاه طاووس وابراهيم ثم مجاهد وهم أولو العرفان وروى العقبلي الصدوق أبو رزين صاحب المبعوث بالقرآن أن لا توالد في الجنان رواه تعليقا','1072','57','0');
INSERT INTO [t6362] VALUES (589,'فالنفي للمعهود في الدنيا من الايلاد والاثبات نوع ثاني والله خالق نوعنا من أربع متقابلات كلها بوزان ذكر وأنثى والذي هو ضده وكذلك من أنثى بلا ذكران والعكس أيضا مثل حوا أمنا هي أربع معلومة التبيان وكذاك مولود الجنان يجوز أن يأتي بلا','1075','57','0');
INSERT INTO [t6362] VALUES (590,'فصل','1076','57','0');
INSERT INTO [t6362] VALUES (591,'ويرونه سبحانه من فوقهم رؤيا العيان كما يرى القمران هذا تواتر عن رسول الله لم ينكره إلا فاسد الايمان','1076','57','0');
INSERT INTO [t6362] VALUES (592,'وعليه أصحاب الرسول وتابعو هم بعدهم تبعية الاحسان','1077','57','0');
INSERT INTO [t6362] VALUES (593,'قال الناظم رحمه الله تعالى','1082','57','0');
INSERT INTO [t6362] VALUES (594,'فصل','1090','57','0');
INSERT INTO [t6362] VALUES (595,'او ما سمعت بأنه سبحانه حقا يكلم حزبه بجنان فيقول جل جلاله هل أنتم راضون قالوا نحن ذو رضوان أم كيف لا نرضى وقد أعطيناه مالم ينله قط من انسان هل تم شيء غير ذا فيكون أفضل منه نسأله من المنان فيقول أفضل منه رضواني فلا يغشاكم سخط من','1090','57','0');
INSERT INTO [t6362] VALUES (596,'فصل','1092','57','0');
INSERT INTO [t6362] VALUES (597,'أو ما سمعت بشأنهم يوم المزيد وأنه شأن عظيم الشان هو يوم جمعتنا ويوم زيارة الرحمن وقت صلاتنا وأذان والسابقون إلى الصلاة هم الألى فازوا بذاك السبق بالاحسان سبق بسبق والمؤخر هاهنا متأخر في ذلك الميدان والأقربون إلى الامام فهم أولو','1092','57','0');
INSERT INTO [t6362] VALUES (598,'فصل','1095','57','0');
INSERT INTO [t6362] VALUES (599,'فصل','1096','57','0');
INSERT INTO [t6362] VALUES (600,'فيقول جل جلاله له قوموا الى ما قد ذخرت لكم من الاحسان','1096','57','0');
INSERT INTO [t6362] VALUES (601,'فصل','1098','57','0');
INSERT INTO [t6362] VALUES (602,'فاذا هم رجعوا الى اهليهم بمواهب حصلت من الرحمن قالوا لهم اهلا ورحبا ما الذي أعطيتم من ذا الجمال الثاني والله لا ازددتم جمالا فوق ما كنتم عليه قبل هذا الآن قالوا وأنتم والذي أنشأكم قد زدتم حسنا على الاحسان لكن يحق لنا وقد كنا','1098','57','0');
INSERT INTO [t6362] VALUES (603,'فصل','1098','57','0');
INSERT INTO [t6362] VALUES (604,'فصل','1100','57','0');
INSERT INTO [t6362] VALUES (605,'أو ما سمعت بذبحه للموت بين المنزلين كذبح كبش الضان حاشا لذا الملك الكريم وانما هو موتنا المحتوم للانسان والله ينشىء منه كبشا املحا يوم المعاد يرى لنا بعيان ينشي من الاعراض أجساما كذا بالعكس كل قابل الامكان','1100','57','0');
INSERT INTO [t6362] VALUES (606,'فالموت ينشيه لنا في صورة خلاقة حتى يرى بعيان','1104','57','0');
INSERT INTO [t6362] VALUES (607,'في نفسه وبنشأة أخرى بقد رة خالق الأعراض والألوان','1104','57','0');
INSERT INTO [t6362] VALUES (608,'وكذلك الأعراض يقلب ربها أعيانها والكل ذو إمكان','1104','57','0');
INSERT INTO [t6362] VALUES (609,'فمكذب ومؤول ومحير ما ذاق طعم حلاوة الايمان','1104','57','0');
INSERT INTO [t6362] VALUES (610,'فثنى لنا العطفين منه تكبرا وتبخترا في حلة الهذيان','1104','57','0');
INSERT INTO [t6362] VALUES (611,'فصل في أن الجنة قيعان وان اغراسها الكلم الطيب والعمل الصالح أو ما سمعت بأنها القيعان فاغرس ما تشاء بذا الزمان الفاني وغراسها التسبيح والتكبير والتحميد والتوحيد للرحمن تبا لتارك غرسه ماذا الذي قد فاته من مدة الامكان يا من يقر بذا ولا يسعى','1106','57','0');
INSERT INTO [t6362] VALUES (612,'فصل في أقامة المأتم على المتخلفين عن رفقة السابقين المأتم كمقعد كل مجتمع في حزن او فرح او خاص بالنساء قاله في القاموس بالله ما عذر أمريء هو مؤمن حقا بهذا ليس باليقظان بل قلبه في رقدة فاذا استفا ق فلبسه هو حلة الكسلان','1108','57','0');
INSERT INTO [t6362] VALUES (613,'واسأله نورا هاديا يهديك في طرق المسير اليه كل أوان والله ما خوفي الذنوب فانها لعلى طريق العفو والغفران لكنما أخشى انسلاخ القلب عن تحكيم هذا الوحي والقرآن ورضى بآراء الرجال وحرصها لا كان ذاك بمنة الرحمن','1111','57','0');
INSERT INTO [t6362] VALUES (614,'فصل في زهد أهل العلم والايمان وايثارهم الذهب الباقي على الخزف الفاني لكن ذا الايمان يعلم ان هذا كالظلال وكل هذا فاني كخيال طيف ما استتم زيارة الا وصبح رحيله باذان','1115','57','0');
INSERT INTO [t6362] VALUES (615,'قال صلى الله عليه وسلم ما الدنيا في الآخرة الا كما يجعل أحدكم اصبعه في اليم فلينظر بم يرجع وكذلك مثلها بظل الدوح في وقت الحرور لقائل الركبان','1116','57','0');
INSERT INTO [t6362] VALUES (616,'هل فيك معتر فيسلو عاشق بمصارع العشاق كل زمان لكن على تلك العيون غشاوة وعلى القلوب أكنة النسيان وأخو البصائر حاضر متيقظ متفرد عن زمرة العميان يسمو الى ذاك الرفيق الأرفع الأعلى وخلى اللعب للصبيان والناس كلهم فصبيان وإن بلغوا سوى','1117','57','0');
INSERT INTO [t6362] VALUES (617,'فصل في رغبة قائلها إلى من يقف عليها من اهل العلم والايمان ان يتجرد لله ويحكم عليها بما يوجبه الدليل والبرهان فان رأى حقا قبله وحمد الله عليه وإن رأى باطلا عرف به وأرشد اليه يا أيها القاري لها اجلس مجلس الحكم الأمين أتى له الخصمان واحكم هداك الله','1119','57','0');
INSERT INTO [t6362] VALUES (618,'قوله متفيهق قال في القاموس تفيهق في كلامه تنطع وتوسع كأنه ملأ به فمه قوله متضلع تضلع امتلأ شبعا أو ريا حتى بلغ الماء أضلاعه قاله في القاموس قوله ذو ضلع قال في القاموس ضلع كمنع مال وجنف جار وفلانا ضرب ضلعه وضلع السيف كفرح اعوج والضالع الجائر','1120','57','0');
INSERT INTO [t6362] VALUES (619,'هذا كما قال الشيخ نصر المنبجي لبيبرس الجاشنكير إن هذا يخشى على الدولة منه كما جرى لابن التومرت صاحب المغرب يعني شيخ الاسلام رحمه الله تعالى واستنصروا بمحاضر وشهادة قد اصلحت بالرفق والاتقان','1121','57','0');
INSERT INTO [t6362] VALUES (620,'أي إذا قدح قادح في شهودكم فلا تلتفتوا لقوله ولا تصغوا له وقولوا الاصل في الناس العدالة ونحو ذلك ثبتت على الحكام بل حكموا بها فالطعن فيها ليس ذا إمكان من جاء يقدح فيهم فليتخذ ظهرا كمثل حجارة الصوان وإذا هو استعداهم فجوابهم أتردها بعداوة','1122','57','0');
INSERT INTO [t6362] VALUES (621,'فصل في حال العدو الثاني أو حاسد قد بات يغلي صدره بعداوتي كالمرجل الملآن لو قلت هذا البحر قال مكذبا هذا السراب يكون بالقيعان أو قلت هذي الشمس قال مباهتا الشمس لم تطلع إلى ذا الآن أو قلت قال الله قال رسوله غضب الخبيث وجاء بالكتمان','1122','57','0');
INSERT INTO [t6362] VALUES (622,'هذا العدو الثالث وهو الجاهل المقلد للعدوين اللذين تقدما وهما الجاهل المتمعلم والحاسد قوله قال اسمعوا الخ المراد بالرجلين الجاهل والحاسد','1123','57','0');
INSERT INTO [t6362] VALUES (623,'فصل في حال العدو الرابع هذا ورابعهم وليس بكلبهم حاشا الكلاب الآكلي الانتان خنزير طبع في خليقة ناطق متسوف بالكذب والبهتان','1124','57','0');
INSERT INTO [t6362] VALUES (624,'غازان من ملوك التتار ثم أخذ الناظم في التشكي من عدم نفاق بضاعته هذه وان العلماء الذين هم أهل لها قد سافروا عن','1124','57','0');
INSERT INTO [t6362] VALUES (625,'ما كل منقوش لديه أصفر ذهبا يراه خالص العقيان وكذا الزجاج ودرة الغواص في تمييزه ما إن هما مثلان','1125','57','0');
INSERT INTO [t6362] VALUES (626,'فصل في توجه أهل السنة الى رب العالمين ان ينصر دينه وكتابه ورسوله وعباده المؤمنين هذا ونصر الدين فرض لازم لا للكفاية بل على الأعيان بيد وإما باللسان فإن عجز ت فبالتوجه والدعا بجنان ما بعد ذا والله للايمان حبة خردل يا ناصر الايمان بحياة','1126','57','0');
INSERT INTO [t6362] VALUES (627,'وصلى الله على محمد عبده ورسوله وخيرته من خلقه وأمينه على وحيه وسفيره بينه وبين عباده وسلم','1129','57','0');
