INSERT INTO [t6446] VALUES (1,'الجزء ١','1','0','0');
INSERT INTO [t6446] VALUES (2,'بسم الله الرحمن الرحيم','1','1','0');
INSERT INTO [t6446] VALUES (3,'فقطع به العذر فبلغ الرسول وبالغ واجتهد وجاهد وبين للأمة السبيل وشرع لهم الطريق لئلا يقولوا ما جاءنا من بشير ولا نذير ولينذر من كان حيا ويحق الحق على الكافرين وإلى الله أرغب في حسن التوفيق لما يقرب إليه من صواب القول والفعل','1','1','0');
INSERT INTO [t6446] VALUES (4,'باب في التوحيد','3','1','0');
INSERT INTO [t6446] VALUES (5,'كان يقول اللهم لك أسلمت وبك آمنت وعليك توكلت وإليك أنبت وبك خاصمت أعوذ بعزتك لا إله إلا أنت أن تضلني أنت الحي الذي لا تموت والجن والإنس يموتون','3','1','0');
INSERT INTO [t6446] VALUES (6,'كان يدعو فيقول أشهد أن لا إله إلا أنت وحدك لا شريك لك لك الملك ولك الحمد وأنت على كل شيء قدير وأشهد أن محمدا عبدك ورسولك وأشهد أن وعدك حق ولقاءك حق والساعة آتية لا ريب فيها وأنك تبعث من في القبور','4','1','0');
INSERT INTO [t6446] VALUES (7,'لقد دعا الله باسمه الذي إذا دعي به أجاب وإذا سئل به أعطى','4','1','0');
INSERT INTO [t6446] VALUES (8,'بعث رجلا على سرية فكان يقرأ لأصحابه في صلاتهم فيختم بقل هو الله أحد فلما رجعوا ذكروا ذلك لرسول الله','5','1','0');
INSERT INTO [t6446] VALUES (9,'أخبروه أن الله يحبه','6','1','0');
INSERT INTO [t6446] VALUES (10,'بعث معاذا إلى اليمن فقال إنك تأتي أهل كتاب فادعهم إلى شهادة أن لا إله إلا الله وأن','6','1','0');
INSERT INTO [t6446] VALUES (11,'سمع رجلا يقول اللهم إني أسألك بأنك لا إله إلا أنت الأحد الصمد الذي لم تلد ولم تولد ولم يكن لك كفوا أحد فقال','7','1','0');
INSERT INTO [t6446] VALUES (12,'لقد دعا الله باسمه الذي إذا دعى به أجاب وإذا سئل به أعطى','8','1','0');
INSERT INTO [t6446] VALUES (13,'يقول من وحد الله وفي رواية مروان عن أبي مالك من قال لا إله إلا الله وكفر بما يعبد من دون الله حرم ماله ودمه وحسابه على الله','8','1','0');
INSERT INTO [t6446] VALUES (14,'فصل','9','1','0');
INSERT INTO [t6446] VALUES (15,'في الإيمان بصفات الله والفرق بين صفات الخالق والمخلوق','9','1','0');
INSERT INTO [t6446] VALUES (16,'موافقة لكتاب الله عز وجل فنقلها الخلف عن السلف قرنا بعد قرن من لدن الصحابة والتابعين إلى عصرنا هذا على سبيل إثبات الصفات لله والمعرفة والإيمان به والتسليم لما أخبر الله به في تنزيله وبينه الرسول عن كتابه مع اجتناب التأويل والجحود وترك','9','1','0');
INSERT INTO [t6446] VALUES (17,'غير زائلة عنه ولا كائنة دونه فمن جحد صفة من صفاته بعد الثبوت كان بذلك جاحدا ومن زعم أنها محدثه لم تكن ثم كانت على أي معنى تأوله دخل في حكم التشبيه بالصفات التي هي محدثه في المخلوق زائلة بفنائه غير باقية وذلك أن الله عز وجل امتدح نفسه','10','1','0');
INSERT INTO [t6446] VALUES (18,'وبين مراد الله فيما أظهر لعباده من ذكر نفسه وأسمائه وصفاته فقال كتب ربكم على نفسه الرحمة وقال النبي','11','1','0');
INSERT INTO [t6446] VALUES (19,'بيانا لقوله','11','1','0');
INSERT INTO [t6446] VALUES (20,'لأن المجاوز','11','1','0');
INSERT INTO [t6446] VALUES (21,'وأن أسامي الخلق وصفاتهم وافقتها في الاسم وباينتها في جميع المعاني لحدوث خلقه وفنائهم وأزلية الخالق وبقائه وبما أظهر من صفاته ومنع استدراك كيفيتها فقال ليس كمثله شيء وهو السميع البصير وإنما ذكرنا هذا الفصل لئلا يتعلق','13','1','0');
INSERT INTO [t6446] VALUES (22,'الذين نقلوا دين الله وأحكامه وبلغوا','13','1','0');
INSERT INTO [t6446] VALUES (23,'فصل','14','1','0');
INSERT INTO [t6446] VALUES (24,'وعن مالك عن أبي الزناد عن الأعرج عن أبي هريرة رضي الله عنه أن رسول الله','14','1','0');
INSERT INTO [t6446] VALUES (25,'بإنكار ذلك','15','1','0');
INSERT INTO [t6446] VALUES (26,'ليسألنكم الناس عن كل شيء حتى يسألوكم هذا الله خلق كل شيء فمن خلق الله','16','1','0');
INSERT INTO [t6446] VALUES (27,'يأتي الشيطان أحدكم فيقول من خلق كذا من خلق كذا حتى يقول من خلق ربك فإذا بلغه فليستعذ بالله ولينته قال الشيخ الإمام رحمه الله أمر رسول الله','16','1','0');
INSERT INTO [t6446] VALUES (28,'فصل','17','1','0');
INSERT INTO [t6446] VALUES (29,'في ترك التفكير في شأن الرب عز وجل','17','1','0');
INSERT INTO [t6446] VALUES (30,'على ناس من أصحابه وهم يتفكرون في خلق الله فقال فيم تتفكرون قال نتفكر فيما خلق الله قال فلا تتفكروا في الله ولكن تفكروا فيما خلق الله وروي عن ابن عمر رضي الله عنه عن','17','1','0');
INSERT INTO [t6446] VALUES (31,'فصل','17','1','0');
INSERT INTO [t6446] VALUES (32,'في الاجتناب من المحدثات','17','1','0');
INSERT INTO [t6446] VALUES (33,'من أحدث في أمرنا ما ليس منه فهو رد قال الشيخ الإمام رحمه الله أنكر السلف الكلام في الجواهر والأعراض وقالوا لم يكن على عهد الصحابة والتابعين رضي الله عن','18','1','0');
INSERT INTO [t6446] VALUES (34,'فصل','21','1','0');
INSERT INTO [t6446] VALUES (35,'في ذكر من عاب الكلام وذمه من الأئمة','21','1','0');
INSERT INTO [t6446] VALUES (36,'فصل','25','1','0');
INSERT INTO [t6446] VALUES (37,'في ذكر الفرقة الناجية','25','1','0');
INSERT INTO [t6446] VALUES (38,'ليأتين على أمتي ما أتى على بني إسرائيل حذو النعل بالنعل حتى لو كان فيهم من يأتي أمه علانية لكان في أمتي من يفعل ذلك إن بني إسرائيل افترقوا على اثنتين وسبعين ملة وتزيدون عليها ملة كلها في النار إلا واحدة فقالوا يا رسول الله وما هي','26','1','0');
INSERT INTO [t6446] VALUES (39,'ليأتين على أمتي ما أتى على بني إسرائيل وذكر الحديث وفي آخره قيل من هم يا رسول الله قال من كان على مثل ما أنا عليه اليوم وأصحابي','27','1','0');
INSERT INTO [t6446] VALUES (40,'إن بني إسرائيل تفرقت على إحدى وسبعين فرقة وإن أمتي تفترق على اثنين وسبعين فرقة كلها في النار إلا واحدة وهي الجماعة وفي رواية يعقوب بن سفيان وإن أمتي ستفترق على كذا وسبعين فرقة وفي روايته فقيل يا رسول الله وما هذه الواحدة','28','1','0');
INSERT INTO [t6446] VALUES (41,'افترقت اليهود على إحدى وسبعين فرقة فواحدة في الجنة وسبعون في النار وافترقت النصارى على اثنتين وسبعين فرقة فواحدة في الجنة وإحدى وسبعون في النار والذي نفسي بيده لتفترقن أمتي على ثلاث وسبعين فرقة فواحدة في الجنة وثنتان وسبعون في النار','28','1','0');
INSERT INTO [t6446] VALUES (42,'وولاة الأمر من بعده سننا الأخذ بها تصديق لكتاب الله عز وجل واستكمال لطاعته وقوة على دين الله عز وجل ليس لأحد تغييرها ولا تبديلها ولا النظر في رأي من خالفها فمن اقتدى بما سنوا اهتدى ومن استبصر بها مبصر ومن خالفها واتبع غير سبيل المؤمنين','29','1','0');
INSERT INTO [t6446] VALUES (43,'فصل','30','1','0');
INSERT INTO [t6446] VALUES (44,'في النهي عن طلب كيفية صفات الله عز وجل','30','1','0');
INSERT INTO [t6446] VALUES (45,'فقال قل يا أيها الناس إني رسول الله إليكم جميعا الذي','32','1','0');
INSERT INTO [t6446] VALUES (46,'فصل','33','1','0');
INSERT INTO [t6446] VALUES (47,'في ذكر أسماء الله تعالى وصفاته','33','1','0');
INSERT INTO [t6446] VALUES (48,'سمع رجلا يقول اللهم إني أسألك بأنك لا إله إلا أنت الأحد الصمد الذي لم تلد ولم تولد ولم يكن لك كفوا أحد فقال رسول الله','34','1','0');
INSERT INTO [t6446] VALUES (49,'قال سيد الاستغفار أن يقول العبد اللهم أنت ربي لا إله إلا أنت خلقتني وأنا عبدك وأنا على عهدك ووعدك ما استطعت أعوذ بك من شر ما صنعت أبوء لك بنعمتك وأبوء لك بذنبي فاغفر لي فإنه لا يغفر الذنوب إلا أنت فإن قالها بعدما يصبح موقنا','36','1','0');
INSERT INTO [t6446] VALUES (50,'قال إذا أراد الله تعالى خلق عبد فجامع الرجل المرأة طار ماؤه في كل عضو وعرق فإذا كان','36','1','0');
INSERT INTO [t6446] VALUES (51,'قال لما صور الله تعالى آدم في الجنة تركه ما شاء الله أن يتركه فجعل إبليس يطيف به وينظر إليه فلما رآه أجوف علم أنه خلق لا يتمالك ومن أسماء الله تعالى الحي القيوم الدائم القائم قال أهل العلم معنى الحي حياة لا تشبه حياة','37','1','0');
INSERT INTO [t6446] VALUES (52,'كان يقول اللهم لك أسلمت وبك آمنت وعليك توكلت وإليك أنبت وبك خاصمت أعوذ بعزتك لا','37','1','0');
INSERT INTO [t6446] VALUES (53,'يدعو يا حي يا قيوم ومن أسماء الله تعالى الأول والآخر والظاهر والباطن وهي صفة معرفة ذاته قال أهل العلم معنى الأول هو الأول بالأولية وهو خالق أول الأشياء ومعنى الآخر هو الآخر الذي لا يزال آخرا دائما باقيا الوارث لكل شيء','38','1','0');
INSERT INTO [t6446] VALUES (54,'تسأله خادما فقال لها النبي','39','1','0');
INSERT INTO [t6446] VALUES (55,'كان يعلمهم الاستخارة كما يعلمهم السورة من القرآن يقول إذا هم أحدكم بالأمر فليركع ركعتين من غير الفريضة ثم ليقل اللهم إني أستخيرك بعلمك وأستقدرك بقدرتك وأسألك من فضلك العظيم فإنك تقدر ولا أقدر وتعلم ولا أعلم وأنت علام الغيوب','40','1','0');
INSERT INTO [t6446] VALUES (56,'عن ذراري المشركين فقال الله أعلم بما كانوا عاملين','41','1','0');
INSERT INTO [t6446] VALUES (57,'قال بعض العلماء أول فرض فرضه الله تعالى على خلقه معرفته','41','1','0');
INSERT INTO [t6446] VALUES (58,'اسمه تعالى الله قال الله تعالى الله خالق كل شيء وبين أهل اللغة اختلاف هل هو اسم موضوع أو مشتق فروي عن الخليل أنه اسم علم ليس بمشتق فلا يجوز حذف الألف واللام منه كما يجوز من','42','1','0');
INSERT INTO [t6446] VALUES (59,'عن شيء فكان يعجبنا أنه يأتيه الرجل من أهل البادية فيسأله ونحن نسمع فأتاه رجل منهم فقال يا محمد أتانا رسولك فزعم أنك تزعم أن الله أرسلك قال صدق قال فمن خلق السماء قال الله قال فمن خلق الأرض قال الله قال فمن نصب','43','1','0');
INSERT INTO [t6446] VALUES (60,'قال الله أنا الرحمن خلقت الرحم وشققت لها اسما من اسمي وهذا الخبر يدل على أن جميع أفعال الله تعالى مشتقة من أسمائه بخلاف المخلوق مثل الرازق والخالق تقدم أسماؤه على أفعاله وأسماء المخلوقين مشتقة من أفعالهم وأما الرحيم','45','1','0');
INSERT INTO [t6446] VALUES (61,'إن الله تعالى خلق مائة رحمة فمنها رحمة بها يتراحم الخلق وأخر تسعة وتسعين ليوم القيامة ومن أسماء الله تعالى السميع البصير خلق الإنسان صغيرا لا يسمع فإن سمع لم يعقل ما يسمع فإذا عقل ميز بين المسموعات فأجاب عن الألفاظ بما','46','1','0');
INSERT INTO [t6446] VALUES (62,'ليسألنكم الناس عن كل شيء حتى يسألوكم هذا الله خلق كل شيء فمن خلق الله فإن','48','1','0');
INSERT INTO [t6446] VALUES (63,'أنه كان يدعو بهؤلاء الكلمات اللهم أنت الأول فلا شيء قبلك وأنت الآخر فلا شيء بعدك أعوذ بك من شر كل دابة ناصيتها بيدك وأعوذ بك من الإثم والكسل ومن عذاب القبر ومن عذاب النار ومن فتنة الغنى ومن فتنة الفقر وأعوذ بك من المأثم','49','1','0');
INSERT INTO [t6446] VALUES (64,'قرأ ذات يوم على المنبر هذه الآية وما قدروا الله حق قدره والأرض جميعا قبضته يوم القيامة والسماوات مطويات بيمينه الآية وقال رسول الله','50','1','0');
INSERT INTO [t6446] VALUES (65,'فقلنا قد عرفنا كيف نسلم عليك فكيف نصلي عليك فقال قولوا اللهم صل على محمد وعلى آل محمد كما صليت على إبراهيم إنك حميد مجيد اللهم بارك على محمد وعلى آل محمد كما باركت على إبراهيم إنك حميد مجيد قال بعض العلماء الحميد المحمود','54','1','0');
INSERT INTO [t6446] VALUES (66,'كان يدعو إذا تهجد من الليل اللهم لك الحمد أنت نور السماوات والأرض ومن فيهن ولك الحمد أنت ضياء السموات والأرض ومن فيهن ولك الحمد أنت الحق ووعدك حق ولقاؤك حق والجنة حق والنار حق والنبيون حق ومحمد حق','55','1','0');
INSERT INTO [t6446] VALUES (67,'بمنى على جمل أحمر تحته رحل رث ولم يكن ضرب ولا طرد فقلت يا أمير المؤمنين إنه بهلول المجنون قال قد عرفت قال قل وأوجز فقال هب أنك قد ملكت الأرض طرا ودان لك العباد فكان ماذا ألست تصير في قبر ويحثو عليك ترابه هذا','58','1','0');
INSERT INTO [t6446] VALUES (68,'بأربع فقال إن الله تعالى لا ينام ولا ينبغي له أن ينام يخفض القسط ويرفعه يرفع إليه عمل الليل قبل النهار وعمل النهار قبل الليل حجابه النور لو كشفها لأحرقت سبحات وجهه كل شيء أدركه بصره قال أبو عبيدة هذه الآية أن بورك من في','61','1','0');
INSERT INTO [t6446] VALUES (69,'إذا آوى إلى فراشه وضع يده اليمنى تحت خده الأيمن ويقول رب قني عذابك يوم تبعث عبادك ومن أسمائه الرقيب هكذا رواه أبو عبد الله من رواية صفوان ابن صالح في أسماء الله التسعة والتسعين ورواه جعفر الفريابي عن صفوان ابن صالح فجعل','62','1','0');
INSERT INTO [t6446] VALUES (70,'في سفر فكان الناس إذا صعدوا أو انحدروا رفعوا أصواتهم بالتسبيح والتهليل فقال النبي','63','1','0');
INSERT INTO [t6446] VALUES (71,'فصل','65','1','0');
INSERT INTO [t6446] VALUES (72,'لله تسعة وتسعون اسما غير واحدة من أحصاها دخل الجنة إنه وتر يحب الوتر قال زهير فبلغنا أن غير واحد من أهل العلم قال إن أولها أن يفتتح بلا إله إلا الله وحده لا شريك له له الملك وله الحمد بيده الخير وهو على كل شيء قدير لا إله إلا','66','1','0');
INSERT INTO [t6446] VALUES (73,'فمن سمى الله تعالى بغير ما','67','1','0');
INSERT INTO [t6446] VALUES (74,'أو زاد في صفاته صفة لم يسم بها نفسه أو رسول','68','1','0');
INSERT INTO [t6446] VALUES (75,'لا مانع لما أعطيت ولا معطي لما منعت فهو تعالى يملك المنع والعطاء يعطي تفضلا ويمنع ابتلاء لا راد لما أراد واسمه تعالى النور قيل معناه لا منور لأبصار العيون وأبصار القلوب غيره وقيل معناه هادي الخلق إلى مصالحهم','68','1','0');
INSERT INTO [t6446] VALUES (76,'في المسجد إذ دخل رجل فصلى ركعتين ثم قال اللهم إني أسألك بأن لك الحمد لا إله إلا أنت المنان بديع السموات والأرض يا ذا الجلال والإكرام يا حي يا قيوم فقال رسول الله','71','1','0');
INSERT INTO [t6446] VALUES (77,'يا أبا بكر ألست تمرض ألست تحزن ألست تصيبك اللأواء قال بلى قال فتلك بتلك','74','1','0');
INSERT INTO [t6446] VALUES (78,'له بالجنة يقول هذا فما نصنع نحن وروي عن النبي','75','1','0');
INSERT INTO [t6446] VALUES (79,'فقال أنت سيد قريش فقال السيد الله فقال أنت أفضلنا فيها قولا وأعظمنا فيها طولا فقال النبي','76','1','0');
INSERT INTO [t6446] VALUES (80,'قال لا تقولوا للمنافق سيدنا فإنكم إذا قلتم ذلك أسخطتم ربكم قيل السيد المحتاج إليه والمحتاج إليه بالإطلاق هو الله ليس للملائكة ولا الإنس ولا الجن غنية عنه لو لم يوجدهم لم يوجدوا ولو لم يبقهم بعد الإيجاد لم يكن لهم بقاء ولو','77','1','0');
INSERT INTO [t6446] VALUES (81,'حين كادته الشياطين فقال تحدرت الشياطين من الجبال والأودية يريدون رسول الله','79','1','0');
INSERT INTO [t6446] VALUES (82,'فلما رآهم رسول الله','79','1','0');
INSERT INTO [t6446] VALUES (83,'إن الله عز وجل صنع كل صانع وصنعته قيل الصنع الاختراع والتركيب ومن أسمائه الفاطر قال الله عز وجل الحمد لله فاطر السماوات والأرض وقيل الفاطر فاتق المرتتق من السماء والأرض قال الله عز وجل أولم ير الذين','81','1','0');
INSERT INTO [t6446] VALUES (84,'يا سيد الناس وديان العرب وفي رواية عبد العزيز بن الحصين عن أيوب وهشام بن حسان عن ابن سيرين أسماء ليست في رواية أبي الزناد عن الأعرج منها البادي والكافي والدائم والمولي والنصير والمحيط والمبين والفاطر والعلام والمليك','85','1','0');
INSERT INTO [t6446] VALUES (85,'فصل','88','1','0');
INSERT INTO [t6446] VALUES (86,'ذكره بعض العلماء','88','1','0');
INSERT INTO [t6446] VALUES (87,'وما رميت إذ رميت ولكن الله رمى وجعل مخادعة المنافقين المؤمنين مخادعته فقال يخادعون الله والذين آمنوا وجعل محاربتهم إياهم محاربته فقال إنما جزاء الذين يحاربون الله ورسوله وتولى الذب عنهم حين قالوا إنما نحن','89','1','0');
INSERT INTO [t6446] VALUES (88,'باب','91','1','0');
INSERT INTO [t6446] VALUES (89,'متواترة في صفات الله تعالى موافقة لكتاب الله تعالى نقلها السلف على سبيل الإثبات والمعرفة والإيمان به والتسليم وترك التمثيل والتكييف وأنه عز وجل أزلي بصفاته وأسمائه التي وصف بها نفسه أو وصفه الرسول','91','1','0');
INSERT INTO [t6446] VALUES (90,'وبين مراد الله فيما أظهر لعباده من ذكر نفسه وأسمائه وصفاته وكان ذلك مفهوما عند العرب غير محتاج إلى تأويله فقال تعالى كتب ربكم على نفسه الرحمة','92','1','0');
INSERT INTO [t6446] VALUES (91,'قال الله تعالى إني حرمت الظلم على نفسي','92','1','0');
INSERT INTO [t6446] VALUES (92,'بيانا لقوله إن الله كتب كتابا على نفسه فهو عنده إن رحمتي تغلب غضبي فبين مراد الله تعالى فيما أخبر عن نفسه تعالى وبين أن نفسه قديم غير فان بفناء الخلق وأن ذاته لا يوصف إلا بما وصف تعالى ووصفه النبي','93','1','0');
INSERT INTO [t6446] VALUES (93,'فصل','93','1','0');
INSERT INTO [t6446] VALUES (94,'في بيان ذكر الذات','93','1','0');
INSERT INTO [t6446] VALUES (95,'عشرة منهم خبيب الأنصاري عينا فأسروهم فلما أرادوا قتل خبيب فذكر الحديث قال الزهري وأخبرني عبيد الله بن عياض أن بنت الحارث أخبرته أنه حين أراد المشركون قتل خبيب في أبيات له ما أبالي حين أقتل مسلما على أي شق كان في الله مصرعي','94','1','0');
INSERT INTO [t6446] VALUES (96,'يستعيذ بوجه الله من النار والفتن كلها ويسأل به','95','1','0');
INSERT INTO [t6446] VALUES (97,'من سألكم بوجه الله فأعطوه','95','1','0');
INSERT INTO [t6446] VALUES (98,'في قوله تعالى للذين أحسنوا الحسنى وزيادة قال النظر إلى وجه ربهم','95','1','0');
INSERT INTO [t6446] VALUES (99,'كان يقول في دعائه وأسألك لذة النظر إلى وجهك','96','1','0');
INSERT INTO [t6446] VALUES (100,'الكلام في صفات الله عز وجل ما جاء منها في كتاب الله أو روي بالأسانيد الصحيحة عن رسول الله','96','1','0');
INSERT INTO [t6446] VALUES (101,'فصل','99','1','0');
INSERT INTO [t6446] VALUES (102,'تكلمه في جانب البيت ما أسمع ما تقول فأنزل الله عز وجل قد سمع الله قول التي تجادلك في زوجها الآية','100','1','0');
INSERT INTO [t6446] VALUES (103,'هل أتي عليك يوم كان أشد عليك من يوم أحد فقال لقد لقيت من قومك وكان أشد ما لقيت منهم يوم العقبة أني عرضت نفسي على ابن عبد ياليل بن عبد كلال فلم يجبني إلى ما','100','1','0');
INSERT INTO [t6446] VALUES (104,'بل أرجو أن يخرج الله من أصلابهم من يعبد الله لا شريك له وقال ابن يوسف لا يشرك به شيئا','101','1','0');
INSERT INTO [t6446] VALUES (105,'ذكر ما امتدح الله عز وجل به من الرؤية والنظر إلى خلقه','102','1','0');
INSERT INTO [t6446] VALUES (106,'فقلنا حدثنا ما سمعت رسول الله','102','1','0');
INSERT INTO [t6446] VALUES (107,'فقال','102','1','0');
INSERT INTO [t6446] VALUES (108,'كما جاءت قال وحدثنا أبو الشيخ حدثنا عبد الرحمن بن أبي حاتم قال سمعت أحمد ابن سنان يقول المشبهة الذين غلوا فجاوزوا الحديث فأما الذين قالوا بالحديث فلم يزيدوا على ما سمعوا فهؤلاء أهل السنة والمتمسكون بالصواب والحق وليس','103','1','0');
INSERT INTO [t6446] VALUES (109,'فصل','104','1','0');
INSERT INTO [t6446] VALUES (110,'في إثبات رؤية الله لخلقه','104','1','0');
INSERT INTO [t6446] VALUES (111,'والمؤمنين وإن كان اسم الرؤية يقع على الجميع وقال تعالى يأبت لم تعبد ما لا يسمع ولا يبصر جل وتعالى عن أن يشبه صفة شيء من خلقه صفته أو فعل أحد من خلقه فعله فالله تعالى يرى ما تحت الثرى وما تحت الأرض السابعة السفلى وما في السموات','104','1','0');
INSERT INTO [t6446] VALUES (112,'انسب لنا ربك فأنزل الله قل هو الله أحد الله الصمد قال الصمد الذي لم يلد ولم يولد لأنه ليس شيء يولد إلا سيموت وليس شيء يموت إلا سيورث وإن الله تعالى لا يموت ولا يورث ولم يكن له كفوا أحد قال لم يكن له شبيه ولا عدل','105','1','0');
INSERT INTO [t6446] VALUES (113,'فصل','106','1','0');
INSERT INTO [t6446] VALUES (114,'في سفر فجعل الناس يجهرون بالتكبير ويرفعون أصواتهم فقال يا أيها الناس أربعوا على أنفسكم إنكم لا تدعون أصم ولا غائبا إنكم تدعون سميعا قريبا وهو معكم وقال عائشة رضي الله عنها في الحديث الذي ذكرناه سبحان الذي وسع سمعه','106','1','0');
INSERT INTO [t6446] VALUES (115,'ثلاثة لا ينظر الله إليهم يوم القيامة ولا يكلمهم الله ولا يزكيهم ولهم عذاب أليم رجل عنده فضل ماء منعه من ابن السبيل ورجل حلف على سلعة بعد العصر كاذبا فصدقه كاذبا واشتراها ورجل بايع إماما لا يبايعه إلا للدنيا فإن أعطاه وفي وإن لم','107','1','0');
INSERT INTO [t6446] VALUES (116,'قال إن الذي يجر ثوبه من الخيلاء لا ينظر الله إليه يوم القيامة','107','1','0');
INSERT INTO [t6446] VALUES (117,'قال الله تعالى الذي يراك حين تقوم وتقلبك في الساجدين يعني تقلبه في أصلاب الأنبياء من آبائك الساجدين مثل إبراهيم ونوح عليهم السلام قال عبد الله بن عباس رضي الله عنه من نبي إلى نبي حتى ابتعثه الله عز','107','1','0');
INSERT INTO [t6446] VALUES (118,'عن الإحسان فقال أن تعبد الله كأنك تراه فإن لم تكن تراه فإنه يراك','108','1','0');
INSERT INTO [t6446] VALUES (119,'فصل','109','1','0');
INSERT INTO [t6446] VALUES (120,'في إثبات اليد لله تعالى صفة له','109','1','0');
INSERT INTO [t6446] VALUES (121,'ذكر البيان من سنة النبي','109','1','0');
INSERT INTO [t6446] VALUES (122,'إن موسى عليه السلام قال يا رب أين أبونا الذي أخرجنا ونفسه من','109','1','0');
INSERT INTO [t6446] VALUES (123,'فحج آدم موسى عليهما السلام قال أبو الشيخ حكى إسماعيل بن زرارة قال سمعت أبا زرعة الرازي يقول المعطلة النافية الذين ينكرون صفات الله عز وجل التي وصف','110','1','0');
INSERT INTO [t6446] VALUES (124,'ويكذبون بالأخبار الصحاح التي جاءت عن رسول الله','111','1','0');
INSERT INTO [t6446] VALUES (125,'من غير تمثيل ولا تشبيه إلى التشبيه فهو معطل ناف ويستدل عليهم بنسبتهم إياهم إلى التشبيه أنهم معطلة نافية كذلك كان أهل العلم يقولون منهم عبد الله بن المبارك ووكيع بن الجراح','111','1','0');
INSERT INTO [t6446] VALUES (126,'قال يجتمع المؤمنون يوم القيامة فيهتمون لذلك اليوم فيقولون لو استشفعنا على ربنا عز وجل حتى يريحنا من مكاننا هذا فيأتون آدم عليه السلام فيقولون يا آدم أنت أبو البشر خلقك الله بيده','111','1','0');
INSERT INTO [t6446] VALUES (127,'فيأتوني فأنطلق معهم فأستأذن على ربي فيؤذن لي فإذا رأيت ربي وقعت له ساجدا فيدعني ما شاء الله أن يدعني ثم يقول يا محمد ارفع رأسك وسل تعطه واشفع تشفع فأرفع رأسي فأحمد ربي بمحامد علمنيها ثم أحد لهم حدا ثانيا فأدخلهم الجنة ثم أرجع','113','1','0');
INSERT INTO [t6446] VALUES (128,'قال من تصدق بعدل تمرة من كسب طيب ولا يصعد إلى الله إلا الطيب فإن الله عز وجل يقبلها بيمينه ثم يربيها لصاحبها كما يربي أحدكم فلوه حتى يكون مثل الجبل قال أبو عبد الله رواه أبو النضر عن عبد الرحمن وأخرجه البخاري وقال تابعه','114','1','0');
INSERT INTO [t6446] VALUES (129,'قال إن العبد ليتصدق بالتمرة من الكسب الطيب فيضعها في حقها فيقبلها الله بيمينه ثم لا يبرح يربيها أحسن ما يربي أحدكم فلوه حتى يكون مثل الجبل أو أكبر وفي رواية سعيد بن يسار عن أبي هريرة رضي الله عنه ولا يعطي إلا الله وفي رواية','114','1','0');
INSERT INTO [t6446] VALUES (130,'فصل','115','1','0');
INSERT INTO [t6446] VALUES (131,'قال قلت قوم سوء قال ليس من صاحب بدعة تحدثه عن رسول الله','115','1','0');
INSERT INTO [t6446] VALUES (132,'فهو على شفا هلكة أخبرنا أحمد أنا هبة الله أنا علي بن عمر بن إبراهيم نا عثمان ابن أحمد نا عبد الكريم بن الهيثم نا سعيد بن المغيرة الصياد نا مخلد ابن الحسين قال قال لي الأوزاعي يا أبا محمد إذا بلغك عن رسول الله','115','1','0');
INSERT INTO [t6446] VALUES (133,'كان مبلغا عن ربه','115','1','0');
INSERT INTO [t6446] VALUES (134,'إنكم تنظرون إلى ربكم كما تنظرون إلى القمر ليلة البدر فقال رجل في مجلسه يا أبا خالد ما معنى هذا الحديث فغضب وحرد وقال ما أشبهك بصبيغ وأحوجك إلى مثل ما فعل به ويلك من يدري كيف هذا ومن يجوز له أن يجاوز هذا القول الذي جاء به','117','1','0');
INSERT INTO [t6446] VALUES (135,'فصل','118','1','0');
INSERT INTO [t6446] VALUES (136,'وما أجمع عليه الصحابة','118','1','0');
INSERT INTO [t6446] VALUES (137,'يقوم في خطبته يحمد الله ويثني عليه بما هو له أهل ثم يقول من يهده الله فلا مضل له ومن يضلل فلا هادي له وإن أصدق الحديث كتاب الله وأحسن الهدى هدى محمد وشر الأمور محدثاتها وكل محدثة بدعة وكل بدعة ضلالة وكل ضلالة في النار ومذهب أهل','119','1','0');
INSERT INTO [t6446] VALUES (138,'قال إن الله تعالى يبسط يده بالليل ليتوب مسيء النهار ويبسط يده بالنهار ليتوب مسيء الليل حتى تطلع الشمس من مغربها','120','1','0');
INSERT INTO [t6446] VALUES (139,'قال يد الله ملأى لا يغيضها نفقة سحاء الليل والنهار وقال أرأيتم ما أنفق منذ خلق السموات والأرض فإنه لم ينقض ما في يده وكان عرشه على الماء وبيده الميزان يخفض ويرفع قال أبو زرعة المعطلة النافية الذين ينكرون صفات الله عز وجل','120','1','0');
INSERT INTO [t6446] VALUES (140,'في الصفات ويتأولونها بآرائهم','120','1','0');
INSERT INTO [t6446] VALUES (141,'من غير تمثيل ولا تشبيه إلى التشبيه فهو معطل ناف ويستدل عليهم بنسبتهم إياهم إلى التشبيه أنهم معطلة نافية كذلك كان أهل العلم يقولون منهم عبد الله بن المبارك ووكيع ابن الجراح','121','1','0');
INSERT INTO [t6446] VALUES (142,'قال إن الله تبارك وتعالى ينزل إلى السماء الدنيا حيث يبقى ثلث الليل أو نصف الليل فيقول تبارك وتعالى من يستغفرني فاغفر له من يدعوني فأجيب من يسألني فأعطيه ثم يبسط يديه فيقول من يقرض الغني غير عدوم ولا ظلوم','121','1','0');
INSERT INTO [t6446] VALUES (143,'قال إذا كان ثلث الليل الآخر ينزل الله إلى السماء الدنيا ثم يبسط يده فيقول من يسألني فأعطيه حتى يطلع الفجر','122','1','0');
INSERT INTO [t6446] VALUES (144,'باب','123','1','0');
INSERT INTO [t6446] VALUES (145,'واصبر نفسك مع الذين يدعون ربهم بالغداة والعشي يريدون وجهه وقال ولله المشرق والمغرب فأينما تولوا فثم وجه الله وقال للذين يريدون وجه الله وقال إنما نطعمكم لوجه الله وقال إلا ابتغاء وجه ربه الأعلى','123','1','0');
INSERT INTO [t6446] VALUES (146,'أعوذ بوجهك أو من تحت أرجلكم قال أعوذ بوجهك أو يلبسكم شيعا قال هذه أهون','124','1','0');
INSERT INTO [t6446] VALUES (147,'دعا يوم خرج إلى الطائف فقال فيه اللهم إني أعوذ بنور وجهك الذي أضاءت له السموات','124','1','0');
INSERT INTO [t6446] VALUES (148,'فينا بأربع فقال إن الله لا ينام ولا ينبغي له أن ينام يرفع القسط وبخفضه يرفع إليه عمل الليل قبل النهار وعمل النهار قبل الليل حجابة النار أو النور لو كشفها لأحرقت سبحات وجهه كل شيء أدركه بصره قال مجاهد بين الملائكة وبين العرش','125','1','0');
INSERT INTO [t6446] VALUES (149,'المثبتين لله عز وجل من صفاته ما وصف الله به نفسه في محكم تنزيله المثبت بين الدفتين وعلى لسانه نبيه','126','1','0');
INSERT INTO [t6446] VALUES (150,'ونحن نقول وعلماؤنا جميعا إن لمعبودنا عز وجل وجها كما أعلمنا الله في محكم تنزيله ووصفه بالجلال والإكرام وحكم له بالبقاء وهو محجوب عن أبصار أهل الدنيا لا يراه بشر ما دام في الدنيا ووجه ربنا قديم لم يزل باق لا يزال فنفي عنه الفناء','126','1','0');
INSERT INTO [t6446] VALUES (151,'بالمؤمنين رءوف رحيم فإن كان علماء الآثار الذين يصفون الله بما وصف به نفسه في كتابه وعلى لسان نبيه','127','1','0');
INSERT INTO [t6446] VALUES (152,'واحتقارهم لهم وتسميتهم إياهم حشوية وجهلة وظاهرة ومشبهة اعتقادا منهم في أخبار رسول الله','128','1','0');
INSERT INTO [t6446] VALUES (153,'فصل','129','1','0');
INSERT INTO [t6446] VALUES (154,'في التغليظ في معارضة الحديث بالرأي والمعقول','129','1','0');
INSERT INTO [t6446] VALUES (155,'فصل','130','1','0');
INSERT INTO [t6446] VALUES (156,'يخرج من المدينة راجلا ومحمد بن إسماعيل هو البخاري على أثره ينظر كلما رفع النبي','131','1','0');
INSERT INTO [t6446] VALUES (157,'فصل','131','1','0');
INSERT INTO [t6446] VALUES (158,'من سألكم بوجه الله فأعطوه أخبرنا أبو عمرو أنا والدي أنا أحمد بن محمد بن عبد السلام نا خير ابن موفق نا أحمد بن عبد الرحمن القرشي قال جاء يوسف بن عمر إلى عمي عبد الله بن وهب فقال له يا أبا محمد أخبرني عن الجنة التي خلق','131','1','0');
INSERT INTO [t6446] VALUES (159,'فصل','132','1','0');
INSERT INTO [t6446] VALUES (160,'آخر في ذم الأئمة لعلم الكلام','132','1','0');
INSERT INTO [t6446] VALUES (161,'أو عن أصحابه أو عن التابعين فأما غير ذلك فالكلام فيه غير محمود قال وحدثنا أبو الشيخ قال حكى أبو زرعة قال كان الشافعي يكره الكلام كله ولم يضع كتاب الكلام وقال آخر صاحب الكلام إلى الزندقة قال أبو الشيخ وحكى المزني','133','1','0');
INSERT INTO [t6446] VALUES (162,'باب','135','1','0');
INSERT INTO [t6446] VALUES (163,'الدليل من الكتاب والأثر على أن الله تعالى لم يزل متكلما آمرا ناهيا بما شاء لمن شاء من خلقه موصوفا بذلك','135','1','0');
INSERT INTO [t6446] VALUES (164,'بيان ذلك من الأثر والفرق بين القول والعلم والإرادة والفعل','137','1','0');
INSERT INTO [t6446] VALUES (165,'عن الله عز وجل أنه قال يا عبادي إني حرمت الظلم على نفسي وجعلته محرما فيما بينكم فلا تظالموا يا عبادي إنكم الذين تخطئون بالليل والنهار وأنا أغفر الذنوب ولا أبالي فاستغفروني اغفر لكم يا عبادي كلكم جائع إلا من أطعمته فاستطعموني','137','1','0');
INSERT INTO [t6446] VALUES (166,'فصل','138','1','0');
INSERT INTO [t6446] VALUES (167,'أن الله عز وجل إذا تكلم بالوحي سمعه أهل السموات','139','1','0');
INSERT INTO [t6446] VALUES (168,'من الأنصار أنهم بينا هم جلوس ليلة مع رسول الله','139','1','0');
INSERT INTO [t6446] VALUES (169,'ماذا كنتم تقولون في الجاهلية إذا رمي بمثل هذا قالوا الله ورسوله أعلم كنا نقول ولد','139','1','0');
INSERT INTO [t6446] VALUES (170,'فإنها لا ترمى لموت أحد ولا لحياته ولكن ربنا إذا قضى أمرا سبح حملة العرش ثم سبح أهل السماء الذين يلونهم حتى يبلغ التسبيح أهل السماء الدنيا قال الذين يلون حملة العرش ماذا قال ربكم فيخبرونهم فيسبح أهل السموات حتى يبلغ الخبر أهل هذه','140','1','0');
INSERT INTO [t6446] VALUES (171,'قال فمز يدي وقال هداك الله فاثبت قال فأتيت أصحابي فوالله لكأنما شهدوني وإياه قال فأخذوني فألقوا على وجهي قطيفة فجعلوا يغموني بها وجعلت أمارسهم قال فأفلت عريانا ما علي قشرة فأتيت على حبشية فأخذت قناعها من رأسها قال','142','1','0');
INSERT INTO [t6446] VALUES (172,'وهو الصادق المصدوق إن خلق أحدكم يجمع في بطن أمه أربعين ليلة ثم يكون علقة مثل ذلك ثم يكون مضغة مثل ذلك ثم يبعث الله إليه ملكا بأربع كلمات فيقول اكتب أجله ورزقه وعمله وشقي أو سعيد فإن الرجل ليعمل بعمل أهل الجنة حتى ما يكون بينه','143','1','0');
INSERT INTO [t6446] VALUES (173,'في المنام ومعه أبو بكر وعمر وعلي رضي الله عنهم ورجل كان يكنى أبا يعقوب الحضرمي أصابه في وجهه ذاك الريح الخبيث فقلت يا أبا يعقوب ها هنا فقال رسول الله','144','1','0');
INSERT INTO [t6446] VALUES (174,'وهو الصادق المصدوق يعني ذكر الحديث بتمامه قال','144','1','0');
INSERT INTO [t6446] VALUES (175,'بجويرية وهي في ذكر ثم مر بها قريبا من نصف النهار فقال لها ما زلت بعدها هنا فقال إلا أعلمك كلمات سبحان الله عدد خلقه أعادها ثلاث مرات سبحان الله رضي نفسه ثلاث مرات سبحان الله زنة عرشه ثلاث مرات سبحان الله مداد كلماته ثلاث مرات','144','1','0');
INSERT INTO [t6446] VALUES (176,'بيان أن كلام الله غير مخلوق قال سبحان الله عدد خلقه ورضى نفسه وزنة عرشة ومداد كلماته ولو كانت كلمات الله من خلقه لما فرق بينهما ألا ترى حين ذكر العرش الذي هو مخلوق ذكره بلفظة لا تقع على العدد فقال زنة عرشه والوزن غير','145','1','0');
INSERT INTO [t6446] VALUES (177,'قال إن صلاتنا هذه لا يصلح فيها شيء من كلام الناس إنما هو ذكر وتسبيح وتلاوة القرآن','149','1','0');
INSERT INTO [t6446] VALUES (178,'يعرض نفسه على الناس بالموقف يقول ألا رجل يحملني إلى قومه فإن قريشا قد منعوني أن أبلغ كلام ربي وقال الله تعالى وإن أحد من المشركين استجارك فأجره حتى يسمع كلام الله حدثنا أبو علي بن إبراهيم نا محمد بن هشام البحتري نا','149','1','0');
INSERT INTO [t6446] VALUES (179,'سمعه من جبرائيل عليه السلام وأصحاب النبي','150','1','0');
INSERT INTO [t6446] VALUES (180,'مخلوقا وأدى النبي','150','1','0');
INSERT INTO [t6446] VALUES (181,'باب','151','1','0');
INSERT INTO [t6446] VALUES (182,'ما ورد في كتاب الله عز وجل من بيان أن القرآن كلام الله غير مخلوق','151','1','0');
INSERT INTO [t6446] VALUES (183,'يقول','152','1','0');
INSERT INTO [t6446] VALUES (184,'فصل','154','1','0');
INSERT INTO [t6446] VALUES (185,'قال يأمر الله إسرافيل بنفخة الصعقة فإذا هم خامدون وجاء ملك الموت فقال يا رب قد مات أهل السماء والأرض إلا من شئت فيقول من بقي وهو أعلم قال يا رب بقيت أنت الحي الذي لا يموت وبقي حملة عرشك وبقي جبريل وميكائيل وبقيت أنا','154','1','0');
INSERT INTO [t6446] VALUES (186,'فصل','155','1','0');
INSERT INTO [t6446] VALUES (187,'أبو بكر الصديق ثم عمر الفاروق ثم عثمان ذو النورين ثم علي الرضا رضي الله عنهم أجمعين فإنهم الخلفاء الراشدون المهديون بويع كل واحد منهم يوم بويع وليس أحد أحق بالخلافة منه وأن رسول الله','156','1','0');
INSERT INTO [t6446] VALUES (188,'أمهات المؤمنين الطاهرات وأن معاوية بن أبي سفيان كاتب وحي الله وأمينة ورديف رسول الله','156','1','0');
INSERT INTO [t6446] VALUES (189,'إنكم سترون ربكم عز وجل كما ترون القمر ليلة البدر لا تضارون في رؤيته قال الله عز وجل وجوه يومئذ ناضرة إلى ربها ناظرة وإن عذاب القبر حق وضغطه القبر حق وأن منكرا ونكيرا هما ملكان يأتيان الناس في قبورهم يسألان عن ربهم','157','1','0');
INSERT INTO [t6446] VALUES (190,'حق ما بين طرفيه كما بين عدن إلى عمان أباريقه عدد نجوم السماء وماؤه أحلى من العسل وأشد بياضا من اللبن من شرب منه لا يظمأ أبدا وأن الشفاعة لرسول الله','158','1','0');
INSERT INTO [t6446] VALUES (191,'حبيبا قريبا وأن الدجال ودابة الأرض ويأجوج ومأجوج وطلوع الشمس من مغربها حق','159','1','0');
INSERT INTO [t6446] VALUES (192,'عرج بروحه وبدنه في ليلة واحدة إلى السماء فرأى الجنة والنار والملائكة والأنبياء صلوات الله عليهم وأسري به من المسجد الحرام إلى المسجد الأقصى ثم عرج به فرأى ربه عز وجل بعينه وقلبه فكان قاب قوسين أو أدنى قال الله عز وجل ما زاغ','160','1','0');
INSERT INTO [t6446] VALUES (193,'بأمر الله تبارك وتعالى قال الله عز وجل وأطيعوا الله والرسول وقال من يطع الرسول فقد أطاع الله وقال ما آتاكم الرسول فخذوه وما نهاكم عنه فانتهوا فأمر الله عز وجل بالبلاغ فقال يا أيها الرسول بلغ ما أنزل إليك','161','1','0');
INSERT INTO [t6446] VALUES (194,'من أولي الأمر أبو بكر ثم عمر ثم عثمان ثم علي رضي الله عنهم ثم الأكابر فالأكابر من العشرة وغيرهم من الصحابة الذين أبان رسول الله','161','1','0');
INSERT INTO [t6446] VALUES (195,'السنة عن الله عز وجل وأخذ الصحابة عن رسول الله','162','1','0');
INSERT INTO [t6446] VALUES (196,'بالاقتداء بهم ثم أشار الصحابة إلى التابعين بعدهم مثل سعيد بن المسيب وعلقمة بن وقاص والأسود والقاسم','162','1','0');
INSERT INTO [t6446] VALUES (197,'اجتمع الأئمة على أن تفسيرها قراءتها قالوا أمروها كما جاءت وما ذكر الله في القرآن مثل قوله عز وجل هل ينظرون إلا أن يأتيهم الله في ظل من الغمام وقوله عز وجل وجاء ربك والملك صفا صفا كل ذلك بلا كيف ولا تأويل','167','1','0');
INSERT INTO [t6446] VALUES (198,'فصل','168','1','0');
INSERT INTO [t6446] VALUES (199,'في فضائل الأثر ومتبعيه','168','1','0');
INSERT INTO [t6446] VALUES (200,'فإن الله عز وجل قال وما آتاكم الرسول فخذوه وما نهاكم عنه فانتهوا قال وأنا أبو محمد نا الحسن بن محمد نا أبو زرعة نا الربيع نا الشافعي نا ابن سماك بن الفضل الشهابي حدثني ابن أبي ذئب بحديث عن رسول الله','169','1','0');
INSERT INTO [t6446] VALUES (201,'وتقول تأخذ به نعم آخذ به وذلك الفرض علي وعلى من سمعه إن الله تبارك وتعالى اختار محمدا','169','1','0');
INSERT INTO [t6446] VALUES (202,'فصل','169','1','0');
INSERT INTO [t6446] VALUES (203,'علم لمحبة الله تعالى به يستوجبون محبة الله تعالى ومغفرته قوله تعالى قل إن كنتم تحبون الله فاتبعوني يحببكم الله','169','1','0');
INSERT INTO [t6446] VALUES (204,'قوله عز وجل واعتصموا بحبل الله جميعا قال وأنا أبو محمد ابن حيان نا محمد بن يحيى المروزي نا عاصم بن علي نا المسعودي عن مجالد عن الشعبي عن ثابت بن قطبة قال خطبنا عبد الله بن مسعود رضي الله عنه فقال يا أيها الناس','170','1','0');
INSERT INTO [t6446] VALUES (205,'يقول لا تزال طائفة من أمتي يقاتلون على الحق ظاهرين إلى يوم القيامة قال أبو عبد الله البخاري يعني أهل الحديث','171','1','0');
INSERT INTO [t6446] VALUES (206,'قال لا يزال ناس من أمتي ظاهرين حتى يأتيهم أمر الله وهم ظاهرون قال وأخبرنا أبو محمد بن حيان نا محمد بن الفضل بن الخطاب نا أبو حاتم قال سمعت أحمد بن سنان وذكر حديث النبي','171','1','0');
INSERT INTO [t6446] VALUES (207,'فقال إن لم يكونوا أصحاب الحديث فلا أدري من هم','171','1','0');
INSERT INTO [t6446] VALUES (208,'ذكر النظر في الحديث والأثر وما فيه من الخير والبركة','172','1','0');
INSERT INTO [t6446] VALUES (209,'فصل','173','1','0');
INSERT INTO [t6446] VALUES (210,'من غير أن يقال كيف','173','1','0');
INSERT INTO [t6446] VALUES (211,'قال ومما نعتقد أن لله عز وجل عرشا وهو على العرش والعرش مخلوق من ياقوتة حمراء وعلمه تعالى محيط بكل مكان ما تسقط من ورقة لا يعلمها ولا حبة في ظلمات الأرض ولا رطب ولا يابس إلا في كتاب مبين ومن قال العرش ملك أو الكرسي ليس','174','1','0');
INSERT INTO [t6446] VALUES (212,'فصل','175','1','0');
INSERT INTO [t6446] VALUES (213,'في ذكر الأهواء المذمومة','175','1','0');
INSERT INTO [t6446] VALUES (214,'يدعو بهؤلاء الدعوات اللهم جنبني منكرات الأخلاق والأهواء والأدواء','175','1','0');
INSERT INTO [t6446] VALUES (215,'إن مما أخشى عليكم بعدي بطونكم وفروجكم ومضلات الأهواء','176','1','0');
INSERT INTO [t6446] VALUES (216,'يقول بئس العبد عبد هوى يضله بئس العبد عبد رغب يذله','176','1','0');
INSERT INTO [t6446] VALUES (217,'لا يؤمن أحدكم حتى يكون هواه تبعا لما جئت به','177','1','0');
INSERT INTO [t6446] VALUES (218,'قال إن إبليس قال أهلكتهم بالذنوب وأهلكوني بالاستغفار فلما رأيت ذلك أهلكتهم بأهوائهم يحسبون أنهم مهتدون ولا يستغفرون','177','1','0');
INSERT INTO [t6446] VALUES (219,'قال لعائشة يا عائشة إن الذين فرقوا دينهم وكانوا شيعا إنهم أصحاب البدع والأهواء وأصحاب الضلالة من هذه الأمة','178','1','0');
INSERT INTO [t6446] VALUES (220,'ما تحت ظل السماء إله يعبد من دون الله أعظم عند الله من هوى متبع','178','1','0');
INSERT INTO [t6446] VALUES (221,'يوما فذكر أن أهل الكتاب قبلكم تفرقوا على سبعين فرقة في الأهواء كلها في النار إلا واحدة وهي الجماعة ألا وإنه','178','1','0');
