INSERT INTO [t6446] VALUES (232,'في ذكر ابتداء الوحي وصفته وأنه أنزل عليه','188','1','0');
INSERT INTO [t6446] VALUES (233,'وهو ابن أربعين سنة فمكث بمكة ثلاث عشرة سنة يوحى إليه ثم أمر بالهجرة فهاجر عشر سنين ومات وهو ابن ثلاث وستين سنة','189','1','0');
INSERT INTO [t6446] VALUES (234,'فقال يا رسول الله كيف يأتيك الوحي فقال رسول الله','189','1','0');
INSERT INTO [t6446] VALUES (235,'إذا تكلم الله تعالى بالوحي سمع أهل السماء للسماء صلصلة كجر السلسلة على الصفا فيصعقون فلا يزالون حتى يأتيهم جبريل عليه السلام فإذا جاءهم جبريل فزع عن قلوبهم فيقولون يا جبريل ماذا قال ربكم فيقول الحق وهو العلي الكبير','190','1','0');
INSERT INTO [t6446] VALUES (236,'إذا تكلم الله عز وجل بالوحي سمع أهل السموات لذلك صلصلة كصلصلة الحديد على الصفا فيصعقون فإذا فزع عن قلوبهم قالوا ماذا قال ربكم قالوا الحق وهو العلي الكبير','190','1','0');
INSERT INTO [t6446] VALUES (237,'فصل','191','1','0');
INSERT INTO [t6446] VALUES (238,'القرآن كلام الله المنزل','191','1','0');
INSERT INTO [t6446] VALUES (239,'يعرض نفسه على الناس بالموقف فقال ألا رجل يحملني إلى قومه فإن قريشا قد منعوني أن أبلغ كلام ربي عز وجل','192','1','0');
INSERT INTO [t6446] VALUES (240,'وكل حدثني طائفة من الحديث قالت عائشة رضي الله عنها ولشأني في نفسي كان أحقر من أن يتكلم الله في بأمر يتلى','192','1','0');
INSERT INTO [t6446] VALUES (241,'قال كلام الله تعالى مدرك مسموع بحاسة الأذن فتارة يسمع من الله تعالى وتارة يسمع من التالي فالذي يسمعه من الله تعالى من يتولى خطابه بنفسه بلا واسطة ولا ترجمان كمحمد','192','1','0');
INSERT INTO [t6446] VALUES (242,'ولو كنا سامعين من الله تعالى لكان الكل كليم الجبار ولم يختص موسى عليه السلام بذلك ولو كنا','193','1','0');
INSERT INTO [t6446] VALUES (243,'فصل','194','1','0');
INSERT INTO [t6446] VALUES (244,'قال الله تعالى طه ما أنزلنا عليك القرآن لتشقى وقال المر تلك آيات الكتاب والذي أنزل إليك وقال المص كتاب أنزل إليك','194','1','0');
INSERT INTO [t6446] VALUES (245,'لجبريل عليه السلام ما يمنعك أن تزورنا أكثر ما تزورنا فنزلت ما نتنزل إلا بأمر ربك قال أبو عبد الله أخبرنا عبد الرحمن بن عبد الله الجوان بمكة نا علي ابن عبد العزيز نا محمد بن عبد الله الرقاشي نا يزيد بن زريع عن','195','1','0');
INSERT INTO [t6446] VALUES (246,'فصل','196','1','0');
INSERT INTO [t6446] VALUES (247,'يدل على أن الله تعالى إذا أراد أن يحدث أمرا سمعه حملة العرش ثم يسمعه أهل كل سماء حتى يبلغ الخبر أهل السماء الدنيا','196','1','0');
INSERT INTO [t6446] VALUES (248,'إذ رمي بنجم فاستنار فقال لهم رسول الله','196','1','0');
INSERT INTO [t6446] VALUES (249,'إنها لم ترم لموت أحد ولا لحياته ولكن ربنا عز وجل إذا قضى أمرا سبحت حملة العرش ثم سبحه أهل السماء الذين يلونهم حتى يبلغ التسبيح أهل السماء الدنيا ثم يقول الذين يلون حملة العرش ماذا قال ربكم عز وجل فيستخبر أهل السموات بعضهم بعضا','196','1','0');
INSERT INTO [t6446] VALUES (250,'فصل','197','1','0');
INSERT INTO [t6446] VALUES (251,'في بيان أن الله عز وجل يكلم عباده المؤمنين يوم القيامة','197','1','0');
INSERT INTO [t6446] VALUES (252,'ما منكم من أحد إلا سيكلمه الله عز وجل يوم القيامة ليس بينه وبينه ترجمان','197','1','0');
INSERT INTO [t6446] VALUES (253,'إلا أبشرك يا جابر أن الله عز وجل أحيا أباك فكلمه كفاحا فقال له عبدي تمن علي فقال تردني إلى الدنيا فأقاتل في سبيلك فأقتل مرة أخرى فقال إني قضيت أنهم إليها لا يرجعون قال أهل اللغة كفاحا أي مقابلة قال صاحب الغربيين','198','1','0');
INSERT INTO [t6446] VALUES (254,'قال لحسان لا تزال مؤيدا بروح القدس ما كافحت عن رسول الله','198','1','0');
INSERT INTO [t6446] VALUES (255,'فصل','199','1','0');
INSERT INTO [t6446] VALUES (256,'في إثبات النداء صفة لله عز وجل','199','1','0');
INSERT INTO [t6446] VALUES (257,'قال إذا أحب الله عبدا نادى جبريل عليه السلام فقال إني أحب عبدي فلانا فأحبوه قال فينوه بها جبريل في حمله العرش فيسمع أهل السماء لغط حملة العرش فيحبه أهل السماء السابعة ثم سماه سماء حتى ينزل إلى السماء الدنيا','199','1','0');
INSERT INTO [t6446] VALUES (258,'فصل','200','1','0');
INSERT INTO [t6446] VALUES (259,'فضحك فقال هل تدرون مما أضحك قلنا الله ورسوله أعلم قال من مخاطبة العبد ربه عز وجل يقول يا رب ألم تجرني من الظلم قال يقول بلى قال فإني لا أجيز على نفسي إلا شاهدا مني قال فيقول كفى بنفسك اليوم عليك شهيدا وبالكرام','200','1','0');
INSERT INTO [t6446] VALUES (260,'قال يقول الله يوم القيامة يا ابن آدم ألم أحملك على الخيل والإبل وأزوجك النساء وجعلتك ترأس وتربع قال فيقول بلى قال فيقول الله فأين شكر ذلك قوله تربع أي تأخذ ربع الغنيمة وكان أهل الجاهلية يأخذ الرئيس منهم ربع الغنيمة','201','1','0');
INSERT INTO [t6446] VALUES (261,'وابن طاوس عن أبيه عن أبي هريرة رضي الله عنه عن النبي','201','1','0');
INSERT INTO [t6446] VALUES (262,'فلو كنت ثم لأريتكم قبره بجنب الطريق تحت الكثيب الأحمر قوله رمية بحجر أي بمقدار رمية بحجر أراد أن يدفن هناك قال أبو عبد الله قوله فقأ عينه مما سكت عنه رواة الآثار ورووا هذا الحديث على التصحيح','202','1','0');
INSERT INTO [t6446] VALUES (263,'فصل','203','1','0');
INSERT INTO [t6446] VALUES (264,'في وجوب طاعة الله','203','1','0');
INSERT INTO [t6446] VALUES (265,'فكانوا الدعاة إلى الله والأدلاء على طاعته يبشر الأول الآخر ويصدق الآخر الأول كل نبي يدعو إلى ما أمر الله عز وجل به وشرع له فافترض الله عز وجل على العباد طاعتهم وجعل حجته على عبادة حتى كان آخرهم محمد','203','1','0');
INSERT INTO [t6446] VALUES (266,'رسالات ربه وبالغ في النصيحة حتى توفاه الله عز وجل فندبنا الله عز وجل إلى طاعة نبيه','204','1','0');
INSERT INTO [t6446] VALUES (267,'بأمر الله عز وجل ووجب على العباد طاعة العلماء الذين أمر الله عز وجل بطاعتهم في قوله عز وجل أطيعوا الله وأطيعوا الرسول وأولي الأمر منكم وأولوا الأمر هم أولوا العلم وأولوا الخير والفضل الذين دل عليهم رسول الله','204','1','0');
INSERT INTO [t6446] VALUES (268,'أصحاب رسول الله','204','1','0');
INSERT INTO [t6446] VALUES (269,'أبو بكر الصديق ثم عمر بن الخطاب ثم عثمان ابن عفان ثم علي بن أبي طالب رضي الله عنه ثم الأكابر فلم يخرج النبي','204','1','0');
INSERT INTO [t6446] VALUES (270,'اقتدوا بالذين من بعدي أبي بكر وعمر','204','1','0');
INSERT INTO [t6446] VALUES (271,'فإلى أبي بكر','204','1','0');
INSERT INTO [t6446] VALUES (272,'فقال أصحابي كالنجوم بأيهم اقتديتم اهتديتم','206','1','0');
INSERT INTO [t6446] VALUES (273,'أصحابه الذين أشار إليهم وأمر الأمة بطاعتهم ولم يمت كبير أحد من أصحاب النبي','207','1','0');
INSERT INTO [t6446] VALUES (274,'إلى أصحابه رضي الله عنهم وأصحابه إلى التابعين رحمهم الله والتابعين إلى تابعي التابعين كذلك يشير الأول إلى الآخر وينتحل الآخر الأول لا يزال كذلك حتى تقوم الساعة وفي الحديث لا تزال طائفة من أمتي على الحق ظاهرين لا يضرهم من خالفهم','207','1','0');
INSERT INTO [t6446] VALUES (275,'إلى أصحابه وأصحابه إلى التابعين والتابعون إلى من بعدهم حتى بلغ دهرنا هذا وكذلك حتى يبلغ الساعة يشير الأول إلى الآخر وينتحل الآخر الأول ويصدق بعضهم بعضا دينا قيما ظاهرا قال عز وجل ليظهره على الدين كله فأظهر الله عز وجل','208','1','0');
INSERT INTO [t6446] VALUES (276,'ومثل مالك عن الزهري عن سعيد بن المسيب عن زيد بن ثابت عن النبي','208','1','0');
INSERT INTO [t6446] VALUES (277,'كل هؤلاء في زمانهم ونظرائهم في زمانهم قد أشار النبي','208','1','0');
INSERT INTO [t6446] VALUES (278,'فصل','208','1','0');
INSERT INTO [t6446] VALUES (279,'في ذكر مجيء جبريل عليه السلام بالوحي وما يلقاه رسول الله','208','1','0');
INSERT INTO [t6446] VALUES (280,'يعالج من التنزيل شدة فكان يحرك شفتيه فقال ابن عباس رضي الله عنه أنا أحركهما لك كما رأيت رسول الله','209','1','0');
INSERT INTO [t6446] VALUES (281,'بعد ذلك إذا أتاه جبريل عليه السلام استمع فإذا انطلق جبريل عليه السلام قرأه النبي','210','1','0');
INSERT INTO [t6446] VALUES (282,'يعالج من التنزيل شدة فكان يحرك شفتيه فأنزل الله عز وجل لا تحرك به لسانك لتعجل به إن علينا جمعه وقرانه قال نجمعه في قلبك ثم تقرأه فإذا قرأناه فاتبع قرانه يقول استمع وأنصت ثم إن علينا بيانه قال فكان النبي','210','1','0');
INSERT INTO [t6446] VALUES (283,'فصل','211','1','0');
INSERT INTO [t6446] VALUES (284,'في بيان أن القرآن وحي من الله عز وجل جاء به جبريل عليه السلام إلى رسول الله','211','1','0');
INSERT INTO [t6446] VALUES (285,'قال الله تعالى إن هو إلا وحي يوحى علمه شديد القوى وقال وكذلك أوحينا إليك روحا من أمرنا وقال وكذلك أوحينا إليك قرآنا عربيا وقال قل أوحي إلي أنه استمع نفر من الجن وقال نحن نقص عليك أحسن','211','1','0');
INSERT INTO [t6446] VALUES (286,'فصل','212','1','0');
INSERT INTO [t6446] VALUES (287,'في النهي عن الخصومات في الدين ومجانبة أهل الخصومات','212','1','0');
INSERT INTO [t6446] VALUES (288,'في حجة الوداع حين خطب الناس فقال','215','1','0');
INSERT INTO [t6446] VALUES (289,'بينهما ولم يذكر في أثر كتاب الله وسنة نبيه','215','1','0');
INSERT INTO [t6446] VALUES (290,'وما توفى رسول الله','215','1','0');
INSERT INTO [t6446] VALUES (291,'في كراهية ذلك أن قال ذورني ما تركتكم فإنما هلك الذي من قبلكم بسؤالهم واختلافهم على أنبيائهم فإذا نهيتكم عن شيء فاجتنبوه وإذا أمرتكم بشيء فأتوا منه ما استطعتم فأي أمر أكف لمن يعقل عن التنقيب من هذا ولم يبلغ الناس يوم قيل لهم','216','1','0');
INSERT INTO [t6446] VALUES (292,'كالذي جاء به موسى عليه السلام يعتبر بعضه ببعض ويشبه بعضه بعضا ومن أجهل وأضل وأقل معرفة بحق الله ورسوله وبنور الإسلام وبرهانه ممن قال لا أقبل سنة ولا أمرا مضى من أمر المسلمين حتى يكشف له عيبه وأعرف أصوله ولم يقل ذلك بلسانه','217','1','0');
INSERT INTO [t6446] VALUES (293,'فصل','218','1','0');
INSERT INTO [t6446] VALUES (294,'في الرد على الجهمية الذي أنكروا صفات الله عز وجل وسموا أهل السنة مشبهة وليس قول أهل السنة أن لله وجها ويدين وسائر ما أخبر الله تعالى به عن نفسه موجبا تشبيهه بخلقه','218','1','0');
INSERT INTO [t6446] VALUES (295,'خلق الله آدم على صورته','218','1','0');
INSERT INTO [t6446] VALUES (296,'فهو حق قول الله حق وقول رسوله حق والله أعلم بما يقول ورسوله','219','1','0');
INSERT INTO [t6446] VALUES (297,'فصل','219','1','0');
INSERT INTO [t6446] VALUES (298,'ينزل الله كل ليلة إلى السماء الدنيا رواه ثلاثة وعشرون من الصحابة سبعة عشر رجلا وست نساء','220','1','0');
INSERT INTO [t6446] VALUES (299,'ما من قلب إلا وهو بين أصبعين من أصابع الرحمن فهذا وأمثاله مما صح نقله عن رسول الله','220','1','0');
INSERT INTO [t6446] VALUES (300,'في صفة الجنة يقول الله تعالى أعددت لعبادي الصالحين ما لا عين رأت ولا أذن سمعت ولا خطر على قلب بشر','222','1','0');
INSERT INTO [t6446] VALUES (301,'ثلاثة لا ينظر الله إليهم يوم القيامة ولا يكلمهم ولا يزكيهم ولهم عذاب أليم رجل عنده فضل ماء منعه من ابن السبيل ورجل حلف على سلعة بعد العصر كاذبا فصدقه واشتراها ورجل بايع إماما لا يبايعه إلا للدنيا فإن أعطاه وفى وإن لم يعطه لم يف له','222','1','0');
INSERT INTO [t6446] VALUES (302,'ثلاثة لا يكلمهم الله','222','1','0');
INSERT INTO [t6446] VALUES (303,'ثلاثة لا ينظر الله إليهم فذكر نحو معناه','223','1','0');
INSERT INTO [t6446] VALUES (304,'قال إن الذي يجر ثوبه من الخيلاء لا ينظر الله إليه يوم القيامة','223','1','0');
INSERT INTO [t6446] VALUES (305,'عن الإحسان فقال أن تعبد الله كأنك تراه فإن لم تكن تراه فإنه يراك','223','1','0');
INSERT INTO [t6446] VALUES (306,'لم تحل الغنائم لمن كان قبلنا ذلك بأن الله رأى ضعفنا وعجزنا فطيبها لنا','224','1','0');
INSERT INTO [t6446] VALUES (307,'قال لما نزلت الم غلبت الروم في أدنى الأرض إلى آخر الآيتين خرج رسول الله','224','1','0');
INSERT INTO [t6446] VALUES (308,'ومن أصحابه فدل على أنها ليست قول البشر واستدلوا بما روي عن جابر رضي الله عنه','225','1','0');
INSERT INTO [t6446] VALUES (309,'يعرض نفسه على الناس بالموقف فيقول هل رجل يحملني إلى قومه فإن قريشا قد منعوني أن أبلغ كلام ربي وعندهم لم يكن مبلغا لكلام ربه وإنما بلغ تلاوة كلامه ولأن المسلمين إذا سمعوا قراءة القارئ قالوا هذا كلام الله واستدلوا بما قدمناه من حديث','225','1','0');
INSERT INTO [t6446] VALUES (310,'فصل','226','1','0');
INSERT INTO [t6446] VALUES (311,'في ذكر الأهواء المذمومة','226','1','0');
INSERT INTO [t6446] VALUES (312,'تلا هذه الآية هو الذي أنزل عليك الكتاب منه آيات محكمات هن أم الكتاب وأخر متشابهات فأما الذين في قلوبهم','226','1','0');
INSERT INTO [t6446] VALUES (313,'فخط خطا هكذا أمامه فقال هذا سبيل الله وخطا عن يمينه وخطا عن شماله وقال هذه سبل الشيطان ثم وضع يده في الخط الأوسط ثم تلا هذه الآية وأن هذا صراطي مستقيما فاتبعوه ولا تتبعوا السبل فتفرق بكم عن سبيله ذلكم وصاكم به لعلكم','227','1','0');
INSERT INTO [t6446] VALUES (314,'قال قال رسول الله','227','1','0');
INSERT INTO [t6446] VALUES (315,'قال إن الشيطان قد أيس أن يعبده المصلون ولكن بالتحريش بينهم قال أهل اللغة حرشت بين القوم أي أغربت بينهم وألقيت العداوة فيهم','228','1','0');
INSERT INTO [t6446] VALUES (316,'يكون أقوام يتجارى بهم تلك الأهواء كما يتجارى الكلب بصحابه فلا يبقى فيه مفصل إلا دخله قال الشيخ الكلب بفتح اللام من قولهم كلب كلب وهو الذي يأخذه شبه الجنون فإذا عقر إنسان كلب فيقال رجل كلب','228','1','0');
INSERT INTO [t6446] VALUES (317,'ما تحت ظل السماء إله يعبد من دون الله أعظم عند الله من هوى متبع','229','1','0');
INSERT INTO [t6446] VALUES (318,'لتتبعن سنن من كان قبلكم','229','1','0');
INSERT INTO [t6446] VALUES (319,'ستتبعون سنن من كان قبلكم باعا بباع وذراعا بذراع وشبرا بشبر حتى لو دخلوا جحر ضب لدخلتم فيه قالوا يا رسول الله اليهود والنصارى قال فمن إذا','229','1','0');
INSERT INTO [t6446] VALUES (320,'إلى حنين ونحن حديثوا عهد بكفر وكانوا أسلموا يوم الفتح قال فمررنا بشجرة فقلنا يا رسول الله اجعل لنا ذات أنواط كما لهم ذات أنوط وكان للكفار سدرة يعكفون حولها ويعلقون بها أسلحتهم يدعونها ذات أنواط قال فلما قلنا ذلك للنبي','230','1','0');
INSERT INTO [t6446] VALUES (321,'لا تقوم الساعة حتى تضطرب إليات نساء دوس حول ذي الخلصة وهو','230','1','0');
INSERT INTO [t6446] VALUES (322,'فصل','231','1','0');
INSERT INTO [t6446] VALUES (323,'في ذكر قول النبي','231','1','0');
INSERT INTO [t6446] VALUES (324,'عليكم هديا قاصدا فإنه من يغالب هذا الدين يغلبه يقال ما أحسن هدي فلان أي طريقته والقاصد المتوسط ليس بالغالي ولا المقصر','231','1','0');
INSERT INTO [t6446] VALUES (325,'إياكم والغلو فإنما هلك من كان قبلكم بالغلو في الدين','232','1','0');
INSERT INTO [t6446] VALUES (326,'ثلاثة لا تسأل عنهم رجل فارق الجماعة','232','1','0');
INSERT INTO [t6446] VALUES (327,'فينا فقال من أراد بحبوحة الجنة فعليه بالجماعة فإن الشيطان مع الفذ قال أهل اللغة بحبوحة الجنة وسطها والفذ الفرد','233','1','0');
INSERT INTO [t6446] VALUES (328,'قال بعض العلماء في البيان عن تشبيه المعتزلة والجهمية ومن يذهب مذهبهم وأن أصحاب الحديث ليسوا بمشبهة قال إن الله تعالى لا يشاء المعاصي لعباده ثم يعاقبهم عليها لأن الحكيم العاقل من المخلوقين لا يجوز هذا ولأن هذا داخل في باب الظلم','233','1','0');
INSERT INTO [t6446] VALUES (329,'فصل','234','1','0');
INSERT INTO [t6446] VALUES (330,'فصل','235','1','0');
INSERT INTO [t6446] VALUES (331,'فصل','236','1','0');
INSERT INTO [t6446] VALUES (332,'في ذم الأهواء وأهل البدع','236','1','0');
INSERT INTO [t6446] VALUES (333,'يقول في آخر الزمان كذابون يأتونكم بما لم تسمعوا أنتم ولا آباؤكم','237','1','0');
INSERT INTO [t6446] VALUES (334,'إن لكل شيء آفة تهلكه وإن آفة هذا الدين الأهواء قال وحدثنا الطبراني نا محمد بن الحسن بن كيسان نا أبو حذيفة نا سفيان عن حبيب بن أبي ثابت عن خالد بن سعد أن حذيفة رضي الله عنه لما حضرته الوفاة دخل عليه أبو مسعود الأنصاري','237','1','0');
INSERT INTO [t6446] VALUES (335,'فإياكم وما ابتدع فإن ما ابتدع ضلالة قال وحدثنا الطبراني نا عبد الله ابن أحمد بن حنبل قال وجدت في كتاب أبي حدثني المفضل بن غسان الغلابي حدثني رجل من بني عدي قال قال عنبسة بن سعيد ما ابتدع رجل بدعة إلا غل صدره على المسلمين','238','1','0');
INSERT INTO [t6446] VALUES (336,'فصل','239','1','0');
INSERT INTO [t6446] VALUES (337,'في بيان التوحيد والتشبيه','239','1','0');
INSERT INTO [t6446] VALUES (338,'فصل','241','1','0');
INSERT INTO [t6446] VALUES (339,'في بيان الأمور التي يكون بها الرجل إماما في الدين وأن أهل الكلام ليسوا من العلماء','241','1','0');
INSERT INTO [t6446] VALUES (340,'مميزا بين صحيحها وسقيمها ومتصلها ومنقطعها ومراسيلها ومسانيدها ومشاهيرها وغرائبها وبأحاديث الصحابة رضي الله عنه ثم يكون ورعا صاينا','241','1','0');
INSERT INTO [t6446] VALUES (341,'فإذا جمع هذه الخلال فحينئذ يجوز أن يكون إماما في المذاهب وجاز أن يجتهد وأن يعتمد عليه في دينه وفتاويه وإذا لم يكن جامعا لهذه الخلال لم يجز أن يكون إماما في المذهب وأن يقلده الناس في فتاويه قال بعض العلماء عقيب مثل هذا','242','1','0');
INSERT INTO [t6446] VALUES (342,'وأحاديث الصحابة رضي الله عنه كالزهري ومالك بن أنس ويحيى بن سعيد وعبد الرحمن بن مهدي وأحمد بن حنبل ويحيى بن معين قالوا لا وهم لا يقولون بالحديث قلنا هؤلاء من أهل الزهد والعبادة كالحسن البصري وفضيل بن عياض وإبراهيم','245','1','0');
INSERT INTO [t6446] VALUES (343,'وقول الصحابة رضوان الله عليهم وقول العلماء والفقهاء بعدهم ممن يبني مذهبه ودينه على كتاب الله تعالى وسنة رسوله','245','1','0');
INSERT INTO [t6446] VALUES (344,'كيف لا يأمن أن يكون متبعا للشيطان أعاذنا الله من متابعة الشيطان','245','1','0');
INSERT INTO [t6446] VALUES (345,'فصل','246','1','0');
INSERT INTO [t6446] VALUES (346,'في النهي عن مناظرة أهل البدع وجدالهم والاستماع إلى أقوالهم','246','1','0');
INSERT INTO [t6446] VALUES (347,'ما ضل قوم بعد هدى كانوا عليه إلا أتو الجدال ثم قرأ ما ضربوه لك إلا جدلا بل هم قوم خصمون قال وأخبرنا هبة الله أنا الحسين بن علي بن زنجويه القطان القزويني نا سليمان بن يزيد المعدل نا علي بن محمد بن عبد الله ابن المبارك','246','1','0');
INSERT INTO [t6446] VALUES (348,'فصل','249','1','0');
INSERT INTO [t6446] VALUES (349,'أمرت أن أقاتل الناس حتى يقولوا لا إله إلا الله فدل أنه الداعي إلى الإيمان وعندهم أن الداعي إلى الإيمان هو العقل وجاء الكتاب مؤيدا لهذا قال الله تعالى قل يا أيها الناس إني رسول الله إليكم جميعا الذي له ملك السماوات والأرض','251','1','0');
INSERT INTO [t6446] VALUES (350,'قال والله لولا الله ما اهتدينا ولا تصدقنا ولا صلينا فهذه الدلائل دلت أن الله تعالى هو المعرف إلا أنه','252','1','0');
INSERT INTO [t6446] VALUES (351,'من ذكر عذاب القبر وسؤال منكر ونكير والحوض والميزان والصراط وصفات الجنة وصفات النار وتخليد الفريقين فيهما أمور لا ندرك حقائقها بعقولنا وإنما ورد الأمر بقبولها والإيمان بها فإذا سمعنا شيئا من أمور الدين وعقلناه وفهمناه','256','1','0');
INSERT INTO [t6446] VALUES (352,'بمنة وفضله','257','1','0');
INSERT INTO [t6446] VALUES (353,'ومما يدل على أن الله عز وجل لم يزل متكلما وأن الكلمة والكلمات من كلامه قوله عز وجل ولقد سبقت كلمتنا لعادنا المرسلين وقوله ولولا كلمة سبقت من ربك وقوله حقت كلمة ربك وقال وتمت كلمة ربك صدقا وعدلا','257','1','0');
INSERT INTO [t6446] VALUES (354,'يعوذ حسنا وحسينا أعيذكما بكلمات الله التامة من كل شيطان وهامة ومن كل عين لامة وكان يقول كان أبوكما يعوذ بها إسماعيل وإسحاق عليهما السلام','258','1','0');
INSERT INTO [t6446] VALUES (355,'قال لرجل من أسلم لو قلت حين نمت أعوذ بكلمات الله التامات من شر ما خلق لم يضرك وفي رواية القعقاع بن حكيم عن أبي صالح عن أبي هريرة رضي الله عنه أنه قال جاء رجل إلى النبي','259','1','0');
INSERT INTO [t6446] VALUES (356,'أما إنك لو قلت حين أمسيت أعوذ بكلمات الله من شر ما خلق لم يضرك وفي رواية يعقوب بن عبد الله بن أبي صالح لو أنك قلت حين أمسيت أعوذ بكلمات الله التامات من شر ما خلق','259','1','0');
INSERT INTO [t6446] VALUES (357,'يقول إذا نزل أحدكم منزلا فليقل أعوذ بكلمات الله التامات من شر ما خلق فإنه لا يضره شيء حتى يرتحل منه','259','1','0');
INSERT INTO [t6446] VALUES (358,'علم جويرية بنت الحارث فقال قولي سبحان الله عدد ما خلق سبحان الله رضى نفسه سبحان الله زنة عرشه سبحان الله مداد كلماته','259','1','0');
INSERT INTO [t6446] VALUES (359,'فصل','260','1','0');
INSERT INTO [t6446] VALUES (360,'في أن القرآن نزل بلغة العرب','260','1','0');
INSERT INTO [t6446] VALUES (361,'عبر كلام الله بالعربية وأن موسى عليه السلام عبر كلام الله بالعبرانية وأن عيسى عليه السلام غبر كلام الله بالسريانية قال الله عز وجل ولو نزلناه على بعض الأعجمين فقرأه عليهم ما كانوا به مؤمنين وقال ولو جعلناه قرآنا','260','1','0');
INSERT INTO [t6446] VALUES (362,'فصل','262','1','0');
INSERT INTO [t6446] VALUES (363,'في بيان وجوب تعظيم السنة وأنها مفسرة للقرآن','262','1','0');
INSERT INTO [t6446] VALUES (364,'عن الخذف وقال إنه لا يصاد بها صيد ولا ينكى بها عدو ولكن يفقأ العين ويكسر السن فقال رجل بينه وبينه نسب ما هذا فقال أحدثك عن رسول الله','262','1','0');
INSERT INTO [t6446] VALUES (365,'سن لنا كيف كنا نركع كيف كنا نسجد كيف كنا نعطي زكاة أموالنا ثم قال','263','1','0');
INSERT INTO [t6446] VALUES (366,'قال لا شغار في الإسلام والشغار أن تكون المرأة الحرة مهرا لأخرى تكون للرجل قريبة فيقول أنكحك وتنكحي بغير صداق فذلك هو الشغار فهل تجد هذا في القرآن ولعمري إنه في القرآن قال الله تعالى وآتوا النساء صدقاتهن نحلة','263','1','0');
INSERT INTO [t6446] VALUES (367,'فقال رجل دعنا من هذه الأحاديث وحدثنا عن القرآن فغضب عمران رضي الله عنه وقال ويحك أمرنا الله في القرآن بأن نحج فهل تجد في القرآن أن نطوف سبعا بالبيت والصفا والمروة كذا كذا قال لا قال فرض الله عز وجل علينا خمس صلوات','264','1','0');
INSERT INTO [t6446] VALUES (368,'فصل','265','1','0');
INSERT INTO [t6446] VALUES (369,'ذكره بعض حنابلة بغداد','265','1','0');
INSERT INTO [t6446] VALUES (370,'فلولا أن ما تلاه النبي','266','1','0');
INSERT INTO [t6446] VALUES (371,'تعاهدوا القرآن فهو أشد تفصيا من صدور الرجال من النعم من عقلها','267','1','0');
INSERT INTO [t6446] VALUES (372,'إن الذي ليس في جوفه شيء من القرآن كالبيت الخرب فأثبت النبي','267','1','0');
INSERT INTO [t6446] VALUES (373,'وكان يعرض نفسه على الناس في الموقف هل من رجل يحملني إلى قومه فإن قريشا منعوني أن أبلغ كلام ربي','267','1','0');
INSERT INTO [t6446] VALUES (374,'من أراد أن يسمع القرآن غضا كما أنزل فليسمعه من ابن أم عبد','267','1','0');
INSERT INTO [t6446] VALUES (375,'أن يسافر بالقرآن إلى أرض العدو وهذه الأخبار تلقتها العلماء بالقبول وحكمت بصحتها فحصلت معلومة وخاطر أبو بكر رضي الله عنه أي راهن قوما من أهل مكة فقرأ عليهم القرآن فقالوا هذا من كلام صاحبك فقال ليس بكلامي ولا كلام صاحبي','268','1','0');
INSERT INTO [t6446] VALUES (376,'وفي قول أبي بكر رضي الله عنه ليس بكلامي ولا كلام صاحبي إنما هو كلام','268','1','0');
INSERT INTO [t6446] VALUES (377,'لا تأكلني فإني مسمومة ولأنه إن جاز أن يقال إنه لم يتكلم بحرف وصوت لأنه يؤدي إلى إثبات الأدوات وجب أن لا يثبت له العلم لأنه لا يوجد في الشاة علم إلا علم ضرورة أو علم استدلال وعلم الله يخرج عن هذين القسمين','270','1','0');
INSERT INTO [t6446] VALUES (378,'فصل','271','1','0');
INSERT INTO [t6446] VALUES (379,'ذكر الصحابة والتابعين الذين قالوا القرآن كلام الله غير مخلوق','271','1','0');
INSERT INTO [t6446] VALUES (380,'فصل','273','1','0');
INSERT INTO [t6446] VALUES (381,'ذكر التابعين من أهل مكة والمدينة والكوفة والبصرة الذين قالوا القرآن كلام الله','273','1','0');
INSERT INTO [t6446] VALUES (382,'ابن عباس وجابر رضي الله عنهما وذكر جماعة قال هبة الله وقد لقي عمرو بن دينار من تقدم ذكره من الصحابة وممن جالس من التابعين ولقيهم وأخذ منهم من علماء مكة من علية التابعين عبيد بن عمير وعطاء وطاوس ومجاهد وسعيد بن جبير','274','1','0');
INSERT INTO [t6446] VALUES (383,'فصل','277','1','0');
INSERT INTO [t6446] VALUES (384,'في بيان أن كلام الله لا مثل له','277','1','0');
INSERT INTO [t6446] VALUES (385,'فصل','282','1','0');
INSERT INTO [t6446] VALUES (386,'في تثبيت خبر الواحد من قول علماء السلف','282','1','0');
INSERT INTO [t6446] VALUES (387,'أنه قال نضر الله امرءا سمه مقالتي فحفظها ووعاها وأداها فلما ندب','283','1','0');
INSERT INTO [t6446] VALUES (388,'لا ألفين أحدكم متكئا على أريكته يأتيه الأمر من أمري مما نهيت عنه أو أمرت به فيقول لا ندري ما وجدنا في كتاب الله اتبعناه ففي هذا دليل على','283','1','0');
INSERT INTO [t6446] VALUES (389,'إذا أخبر به واحد ثقة لزم اتباعه ووقع العلم به وإن لم يوجد له في كتاب الله نص حكم','284','1','0');
INSERT INTO [t6446] VALUES (390,'قد أنزل عليه قرآن وأمر أن يستقبل الكعبة فاستقبلوها وكانت وجوههم إلى الشام فاستداروا إلى الكعبة وأهل قباء أهل سابقة وفقه وعلم وقد كانوا على قبلة فرض الله عليهم استقبالهم ولم يكن لهم أن يدعوا فرض الله تعالى في القبلة إلا بما بجب','284','1','0');
INSERT INTO [t6446] VALUES (391,'لا يجوز لقال لهم رسول الله','284','1','0');
INSERT INTO [t6446] VALUES (392,'وتقدم الصحبة بالموضع الذي لا بنكره عالم وقد كان الشراب عندهم حلالا فجاءهم آت فأخبرهم بتحريم الخمر فقبلوا منه وكسروا الجرار وأراقوا الخمر ولم يقل أنس ولا هم ولا واحد منهم نحن على تحليلها حتى نلقى رسول الله','285','1','0');
INSERT INTO [t6446] VALUES (393,'إليكم يأمركم أن تثبتوا على','285','1','0');
INSERT INTO [t6446] VALUES (394,'أصحابه بتحريم لحوم الحمر الأهلية فقبلوه وكفؤوا القدور ومما يدل عليه أيضا أن النبي فرق عمالا إلى عشائرهم وأمرهم بأوامر قبلوها منهم وانتهوا إليه وعملوا بها لعلمهم بحقيقة ذلك وبعث معاذ بن جبل رضي الله عنه إلى اليمن وأمره','286','1','0');
INSERT INTO [t6446] VALUES (395,'بتحويل القبلة وتحريم الشراب وغير ذلك لما كان','286','1','0');
INSERT INTO [t6446] VALUES (396,'لوقوع الصدق في خبره عندهم حقيقة والخبر إذا صح كان كالمشاهدة فإن قيل أخبار الآحاد كالشهادات والشهادة لا يجوز أن يقطع على مغيبها بالإجماع قيل الشهادة تخالف أداء الحديث في مواضع منها أنها لا تقبل لابن الشاهد ولا لأبيه وأمه وليس','287','1','0');
INSERT INTO [t6446] VALUES (397,'قال بعض العلماء الوجوه التي يعرف بها إعجاز القرآن نقض العادة المألوفة ونقض العادة بالقرآن يعرف من وجوه','287','1','0');
INSERT INTO [t6446] VALUES (398,'فصل','289','1','0');
INSERT INTO [t6446] VALUES (399,'ومن أعجاز القرآن الإخبار بالمغيبات','289','1','0');
INSERT INTO [t6446] VALUES (400,'وكانت فارس بخلاف هذه الصورة وملكهم كسرى مزق كتاب النبي','290','1','0');
INSERT INTO [t6446] VALUES (401,'بأمر الله إلى المباهلة بتحقيق اللعنة وتعجيل الهلاك على الكاذبين فوعدوه ذلك ومضوا وتشاوروا فقال قائلهم والله لئن باهلتموه ليضطر من الوادي عليكم ومضى رسول الله','291','1','0');
INSERT INTO [t6446] VALUES (402,'والذي نفسي بيده لو باهلوا لأضرم الله عليهم الوادي نارا ومن هذا الباب قوله عز وجل إذا جاء نصر الله والفتح إلى آخر السورة وهذه بشارة بفتح مكة وعلو الكلمة وظهور الدعوة فأتاح الله هذا الفتح لرسوله','291','1','0');
INSERT INTO [t6446] VALUES (403,'بالنصر والتمكين فأنجزه الله وأحسن فيه الصنع وله الحمد وقوله سبحانه سبحان الذي أسرى بعبده الآية والوصول في ليلة واحدة من مكة إلى مسجد بيت المقدس من المعجزات وقد أخبرهم النبي','292','1','0');
INSERT INTO [t6446] VALUES (404,'ومما يدل على أن الله تعالى كلم آدم عليه السلام قوله تعالى اسكن أنت وزوجك الجنة','293','1','0');
INSERT INTO [t6446] VALUES (405,'لما خلق الله آدم ونفخ فيه من روحه عطس فإذن الله بحمده فحمد الله فقال له ربه رحمك ربك بيان آخر يدل على أن الله عز وجل كلم الملائكة قبل خلق آدم عليه السلام فقال إني خالق بشرا من طين فإذا سويته ونفخت فيه من روحي فقعوا','293','1','0');
INSERT INTO [t6446] VALUES (406,'أن موسى عليه السلام قال يا رب آدم الذي أخرجنا ونفسه من الجنة فأراه الله آدم فقال أنت آدم أبونا قال نعم قال الذي نفخ الله فيك من روحه وعلمكم الأسماء كلها وأمر الملائكة فسجدوا لك أراه قال وأسكنك جنته وخلقك بيده قال','294','1','0');
INSERT INTO [t6446] VALUES (407,'فحج آدم موسى ذكر بيان آخر يدل على أن الله عز وجل يكلم عباده المؤمنين بالرضا','295','1','0');
INSERT INTO [t6446] VALUES (408,'ما منكم من أحد إلا سيكلمه الله ليس بينه وبينه ترجمان فينظر أيمن منه فلا يرى إلا ما قدم من عمله وينظر أيسر منه فلا يرى إلا ما قدم من عمله وينظر بين يديه فلا يرى إلا النار تلقاء وجهه فاتقوا النار ولو بشق تمرة بيان آخر يدل','295','1','0');
INSERT INTO [t6446] VALUES (409,'إن لله ملائكة سيارة فضلا يلتمسون مجالس الذكر فإذا أتوا على قوم يذكرون الله جلسوا فأظلوهم بأجنحتهم ما بينهم وبين السماء الدنيا فإذا قاموا عرجوا إلى ربهم فيقول وهو أعلم من أين جئتم فيقولون جئنا من عند عباد لك يسبحونك ويحمدونك','296','1','0');
INSERT INTO [t6446] VALUES (410,'لجابر بن عبد الله إن الله أحيا أباك فكلمه كفاحا','297','1','0');
INSERT INTO [t6446] VALUES (411,'فقال إن أرواحهم في أجواف طير خضر لها قناديل معلقة بالعرش تسرح في الجنة حيث تشاء ثم تأوي إلى تلك القناديل فاطلع عليهم ربهم اطلاعة فقال هل تشتهون شيئا فأزيدكموه قالوا وما نشتهي ونحن نسرح في الجنة حيث نشاء فلما رأوا أنهم لا يتركون','297','1','0');
INSERT INTO [t6446] VALUES (412,'يقول إن الله ليدعو يوم القيامة الجنة فتأتي بزخرفها وزينتها فيقول أين عبادي الذين قاتلوا في سبيلي وقتلوا وأوذوا وجاهدوا في سبيلي ادخلوا الجنة فيدخلونها بغير عذاب ولا حساب وتأتي الملائكة فيسجدون فيقولون ربنا نحن نسبح بحمدك الليل','298','1','0');
INSERT INTO [t6446] VALUES (413,'ومن إعجاز القرآن صنيعه بالقلوب وتأثيره في النفوس فإنك لا تسمع كلاما غير القرآن منظوما ولا منثورا إذا قرع السمع خلص له إلى القلب من اللذة والحلاوة في حال ومن الروعة والمهابة في أخرى ما يخلص منه إليه تستبشر به النفوس وتنشرح له','298','1','0');
INSERT INTO [t6446] VALUES (414,'من رجال العرب وفتاكها أقبلوا إليه يريدون اغتياله وقتله فسمعوا آيات من القرآن فلم يلبثوا حين وقعت في مسامعهم أن يتحولوا عن رأيهم الأول وأن يركنوا إلى مسالمته ويدخلوا في دينه وصارت عدواتهم موالاة وكفرهم إيمانا خرج عمر بن الخطاب رضي','299','1','0');
INSERT INTO [t6446] VALUES (415,'آيات من حم السجدة فلما أقبل عتبة وأبصره الملأ من قريش قالوا قد أقبل أبو الوليد بغير الوجه الذي ذهب به ولما قرأ رسول الله','299','1','0');
INSERT INTO [t6446] VALUES (416,'فصل','300','1','0');
INSERT INTO [t6446] VALUES (417,'ما رآه المسلمون حسنا فهو عند الله حسن وما رآه المسلمون قبيحا فهو عند الله قبيح وهذا مما رآه المسلمون حسنا فهو مستحسن عند الله والبدعة على وجهين بدعة قبيحة وبدعة حسنة','301','1','0');
INSERT INTO [t6446] VALUES (418,'ومن أقوال الصحابة رضي الله عنهم أنا أمرنا بالاتباع وندبنا إليه ونهينا عن الابتداع وزجرنا عنه وشعار أهل السنة اتباعهم السلف الصالح وتركهم كل ما هو مبتدع محدث وقد روينا عن سلفنا أنهم نهوا عن هذا النوع من العلم وهو علم','303','1','0');
INSERT INTO [t6446] VALUES (419,'فصل','306','1','0');
INSERT INTO [t6446] VALUES (420,'يدل على أن الله عز وجل كلم ملك الموت ويكلمه إذا شاء وكلم الرحم لما خلقه','306','1','0');
INSERT INTO [t6446] VALUES (421,'لما خلق الله آدم فضل من طينه فخلق منه الرحم فقامت','306','1','0');
INSERT INTO [t6446] VALUES (422,'يقول يدخل الملك على النطفة بعدما تستقر في الرحم أربعين يوما فيقول يا رب ماذا أشقي أم سعيد قال فيقول الله عز وجل ويكتبان ثم يقول يا رب أذكر أم أنثى فيقول الله ويكتبان يعني ثم يقول رزقه وعمله وأثره ومصيبته ثم تطوى له','307','1','0');
INSERT INTO [t6446] VALUES (423,'أنه قال يؤتى بأنعم الناس كان في الدنيا يوم القيامة فيقول أصبغوه صبغة في النار فيقول يا ابن آدم هل أصبت نعيما قط هل أصبت قرة عين هل رأيت سرورا قط فيقول لا وعزتك ما رأيت خيرا ولا سرورا ولا قرة عين قط فيقال ردوه','307','1','0');
INSERT INTO [t6446] VALUES (424,'فصل','308','1','0');
INSERT INTO [t6446] VALUES (425,'في أن القرآن المكتوب في المصاحف هو حقيقة كلام الله','308','1','0');
INSERT INTO [t6446] VALUES (426,'وتحدى به النبي','308','1','0');
INSERT INTO [t6446] VALUES (427,'إلى الصحابة رضوان الله عليهم حسب ما سمعه من جبريل عليه السلام ونقله السلف إلى الخلف قرنا بعد قرن والدليل على أن القرآن موجود في المصاحف نهي النبي','308','1','0');
INSERT INTO [t6446] VALUES (428,'أن يسافر به إلى أرض العدو لأن','308','1','0');
INSERT INTO [t6446] VALUES (429,'أن يسافر بالقرآن إلى أرض العدو مخافة أن يناله العدو ومن الدليل على ذلك أيضا نهى النبي','309','1','0');
INSERT INTO [t6446] VALUES (430,'أنه قال لا يقر الجنب والحائض شيئا من القرآن','309','1','0');
INSERT INTO [t6446] VALUES (431,'المحدث عن مسه','310','1','0');
INSERT INTO [t6446] VALUES (432,'لعمرو بن حزم لا تمس القرآن إلا على طهر','310','1','0');
INSERT INTO [t6446] VALUES (433,'قال الخطابي عصمنا الله وإياك من الأهواء المضلة والآراء المغوية والفتن المحيرة ورزقنا وإياك الثبات على السنة والتمسك بها ولزوم الطريقة المستقيمة التي درج عليها السلف وانتهجها بعدهم صالحوا الخلف وجنبنا وإياك مداحض البدع وبنيات','310','1','0');
INSERT INTO [t6446] VALUES (434,'ولسننه المأثورة عنه وردوها على وجوهها وأساءوا في نقلتها القالة ووجهوا عليهم الظنون ورموهم بالتزيد ونسبوهم إلى ضعف المنة وسوء المعرفة بمعاني ما يروونه من الحديث والجهل بتأويله ولو سلكوا سبيل القصد ووقفوا عندما انتهى بهم التوقيف','312','1','0');
INSERT INTO [t6446] VALUES (435,'إليهم بشيرا ونذيرا وداعيا إلى الله بإذنه وسراجا منيرا وقال له يا أيها الرسول بلغ ما أنزل إليك من ربك وإن لم تفعل فما بلغت رسالته وقال','315','1','0');
INSERT INTO [t6446] VALUES (436,'شيئا من أمور الدين قواعده وأصوله وشرائعه وفصوله إلا بينه وبلغه على كماله وتمامه ولم يؤخر بيانه عن وقت الحاجة إليه إذ لا خلاف بين فرق الأمة أن تأخير البيان عن وقت الحاجة لا يجوز بحال ومعلوم أن أمر التوحيد وإثبات الصانع لا تبرح','315','1','0');
INSERT INTO [t6446] VALUES (437,'فصل','316','1','0');
INSERT INTO [t6446] VALUES (438,'ذكر الآيات الدالة على وحدانية الله تعالى وأنه خالق الخلق','316','1','0');
INSERT INTO [t6446] VALUES (439,'قال لما صور آدم في الجنة تركه ما شاء أن يتركه فجعل إبليس يطيف به وينظر إليه فلما رآه أجوف علم أنه خلق لا يتمالك','317','1','0');
INSERT INTO [t6446] VALUES (440,'في الجمعة هل ذكر لكم منها فقال الله أعلم إن الله خلق آدم يوم الجمعة بعد العصر خلقه من أديم الأرض كلها فسمي آدم ألا ترى أن من ولده الأسود والأحمر والخبيث والطيب ثم عهد إليه فنسي فسمي الإنسان فبالله إن غابت الشمس من ذلك اليوم','318','1','0');
INSERT INTO [t6446] VALUES (441,'إن المرأة خلقت من ضلع لن تستقيم لك على طريقة فإن ذهبت تقيمها كسرتها وإن استمتعت بها استمتعت بها وفيها عوج','318','1','0');
INSERT INTO [t6446] VALUES (442,'قال لما خلق الله تعالى آدم انتزع ضلعا من أضلاعه فخلق منه حواء','318','1','0');
INSERT INTO [t6446] VALUES (443,'وهو الصادق المصدوق إن خلق أحدكم يجمع في بطن أمه أربعين ليلة ثم يكون علقة مثل ذلك ثم يكون مضغة مثل ذلك ثم يبعث الله تعالى إليه ملكا بأربع كلمات فيقول اكتب أجله ورزقه وشقي أو سعيد وإن الرجل ليعمل بعمل أهل الجنة حتى ما يكون بينه','320','1','0');
INSERT INTO [t6446] VALUES (444,'قال كنت قاعدا عند رسول الله','321','1','0');
INSERT INTO [t6446] VALUES (445,'أجل إن أهلي سموني محمدا فقال جئتك لأسألك عن واحدة لا يعلمها إلا نبي أو رجل أو رجلان قال هل ينفعك إن أخبرتك فقال أسمع بأذني فقال سل عما بدا لك قال من أين يكون شبه الولد فقال رسول الله','321','1','0');
INSERT INTO [t6446] VALUES (446,'لقد سألني حين سألني وما عندي منه علم حتى أنبأني الله تعالى','321','1','0');
INSERT INTO [t6446] VALUES (447,'عن الولد ينزع إلى أبيه أو أمه فقال أخبرني جبريل عليه السلام آنفا فقال إذا سبق ماء الرجل نزعه وإذا سبق ماء المرأة نزعها قوله نزعه أي أشبهه','322','1','0');
INSERT INTO [t6446] VALUES (448,'قال إن الله تعالى إذا أراد خلق عبد فجامع الرجل المرأة طار ماؤه في كل عضو وعرق منها فإذا كان يوم السابع جمعه الله ثم أحضره كل عرق له دون آدم في أي صورة ما شاء ركبه','322','1','0');
INSERT INTO [t6446] VALUES (449,'في كفه ثم وضع عليه إصبعه السبابة ثم قال يقول الله تعالى أنى تعجزني يا ابن آدم وقد خلقتك من مثل هذه حتى إذا سويتك وعدلتك مشيت بين بردين وللأرض منك وئيد ثم جمعت ومنعت','322','1','0');
INSERT INTO [t6446] VALUES (450,'يقول في الإنسان ثلاثمائة وستون مفصلا وعليه أن يتصدق عن كل مفصل في كل يوم صدقة','323','1','0');
INSERT INTO [t6446] VALUES (451,'قال خلق كل إنسان من بني آدم على ستين وثلاثمائة مفصل فمن كبر الله وحمد الله وهلل الله واستغفر الله وعزل حجرا عن طريق الناس أو عزل شوكة عن طريق الناس أو أمر بمعروف أو نهى عن منكر عدد الستين والثلاثمائة السلامى فإنه يمشي وقد زحزح نفسه','324','1','0');
INSERT INTO [t6446] VALUES (452,'لا ومقلب القلوب ذكر آية أخرى تدل على وحدانية الخالق وأن الأرواح بيده في حال الموت والحياة والنوم والانتباه قال الله عز وجل مخبرا عن قدرته على ذلك الله يتوفى الأنفس حين موتها والتي لم تمت في منامها الآية','325','1','0');
INSERT INTO [t6446] VALUES (453,'طرقه وفاطمة فقال ألا تصلون فقلت يا رسول الله إنما أنفسنا بيد الله إذا شاء أن يبعثنا بعثنا فانصرف رسول الله','326','1','0');
INSERT INTO [t6446] VALUES (454,'قال لبلال اكلأ لنا الليلة فلما كان في وجه الصبح ناموا حتى ضربتهم الشمس فقال النبي','326','1','0');
INSERT INTO [t6446] VALUES (455,'قال قال الله كذبني عبدي ولم يكن له أن يكذبني وشتمني ولا ينبغي له أن يشتمني فأما تكذيبه إياي فقوله لن يعيدني كالذي بدأني وليس آخر الخلق أهون علي أن أعيده من أوله فقد كذبني أن قال هذا وأما شتمه إياي فيقول اتخذ الله ولدا وأنا','327','1','0');
INSERT INTO [t6446] VALUES (456,'فصل','328','1','0');
INSERT INTO [t6446] VALUES (457,'فيما ذكر في اللفظية','328','1','0');
INSERT INTO [t6446] VALUES (458,'إلى سوق عكاظ وقد حيل بين الشياطين وبين خبر السماء وأرسلت عليهم الشهب فرجعت الشياطين فقالوا حيل بيننا وبين خبر السماء فتوجهوا نحو تهامة إلى رسول الله','329','1','0');
INSERT INTO [t6446] VALUES (459,'قل أوحي إلي فهذا هو الأصل في أن اللفظ بالقرآن هو القرآن لأن الجن إنما سمعوا لفظ النبي','330','1','0');
INSERT INTO [t6446] VALUES (460,'قيل يا رسول الله إن أمتك ستفتتن من بعدك قالوا وما المخرج من ذلك فذكر الحديث وقال وهو الفصل ليس بالهزل وهو الذي سمعته الجن فلم تناه أن قالوا إنا سمعنا قرآنا عجبا يهدي إلى الرشد لا يخلق على كثرة الرد ولا تنقضي عجائبه','330','1','0');
INSERT INTO [t6446] VALUES (461,'فصل في الواقفة','331','1','0');
INSERT INTO [t6446] VALUES (462,'فصل','335','1','0');
INSERT INTO [t6446] VALUES (463,'فيما روي من كلام الرب تبارك تعالى','335','1','0');
INSERT INTO [t6446] VALUES (464,'فقال مالي أراك مهتما قلت يا رسول الله قتل أبي وترك دينا وعيالا فقال ألا أخبرك ما كلم الله قط أحدا إلا من وراء حجاب وإنه كلم أباك كفاحا فقال يا عبدي سلني أعطك قال أسألك أن تردني إلى الدنيا فأقتل فيك ثانية فقال إنه','335','1','0');
INSERT INTO [t6446] VALUES (465,'قيل المكافحة المضاربة تلقاء الوجه','336','1','0');
INSERT INTO [t6446] VALUES (466,'خزائن الله الكلام إذا أراد شيئا يقول له كن فيكون','336','1','0');
INSERT INTO [t6446] VALUES (467,'قال وحدثنا محمد بن معمر نا يعلى بن عبيد نا موسى بن المسيب عن شهر ابن','336','1','0');
INSERT INTO [t6446] VALUES (468,'قال إن الله عز وجل يقول عطائي كلام وعذابي كلام إذا أردت شيئا إنما أقول له كن فيكون','337','1','0');
INSERT INTO [t6446] VALUES (469,'قال بعض الحنابلة القرآن كلام الله منزل غير مخلوق منه بدا وإليه يعود تكلم به في القدم بحرف وصوت حرف يكتب وصوت يسمع ومعنى يعلم وقالت المعتزلة القرآن مخلوق وقالت الأشعرية كلام الله ليس بحرف ولا صوت وإنما هو معنى قائم','337','1','0');
INSERT INTO [t6446] VALUES (470,'أنزله عليه ويقال لهم قوله من ذكر من للتبعيض وهذا يدل أن ثم ذكرا قديما وعندهم ليس ثم ذكر قديم','338','1','0');
INSERT INTO [t6446] VALUES (471,'ليس بكلام الله لم تحصل الاستجارة لهم ولأنه قال يريدون أن يبدلوا كلام الله فلا يجوز أن يكون كلاما لم يصل إليهم لأن ما لم يصل إليهم لا يتأتى لهم تبديله فلم يبق إلا أن يكون الحرف والصوت ولأنه قال تعالى فلما أتاها نودي من','339','1','0');
INSERT INTO [t6446] VALUES (472,'فصل','341','1','0');
INSERT INTO [t6446] VALUES (473,'أنه قال إن صلاتنا هذه لا يصلح فيها شيء من كلام الناس إنما هي قراءة القرآن فلو كانت القراءة غير المقروء لكان التقدير لا يصلح فيها شي من كلام الناس إنما هي كلام الناس وهذا ما لا فائدة فيه ولآن الأمة أجمعت','341','1','0');
INSERT INTO [t6446] VALUES (474,'لا يعذب الله قلبا وعى القرآن','343','1','0');
INSERT INTO [t6446] VALUES (475,'تعاهدوا القرآن فلهو أشد تفصيا من صدور الرجال من النعم من عقلها','343','1','0');
INSERT INTO [t6446] VALUES (476,'باب مسائل الإيمان','345','1','0');
INSERT INTO [t6446] VALUES (477,'الإيمان في الشرع عبارة عن جميع الطاعات الباطنة والظاهرة','345','1','0');
INSERT INTO [t6446] VALUES (478,'الإيمان بضع وسبعون شعبة وفي رواية بضع وستون شعبة أفضلها شهادة أن لا إله إلا الله وأدناها إماطة الأذى من الطريق والحياء شعبة من الإيمان ولأن المكره على الإيمان يصح دخوله فيه فلو كان الإيمان يختص القلب لم يصح دخوله','347','1','0');
INSERT INTO [t6446] VALUES (479,'ويجوز الزيادة والنقصان في الإيمان وزيادته بفعل الطاعات ونقصانه بتركها وفعل المعاصي خلافا لمن قال الإيمان معرفة القلب وتصديقه وهما عرضان من الأعراض والزيادة والنقصان لا تجوز على الأعراض','347','1','0');
INSERT INTO [t6446] VALUES (480,'قال الإيمان يزيد وينقص وروي عن ابن عباس وأبي هريرة وأبي الدرداء رضي الله عنه الإيمان يزيد وينقص وإذا كان الإيمان عبارة عن جميع الطاعات فإذا أخل ببعضها وارتكب المنهيات فقد أخل ببعض أفعاله فجاز أن يوصف بالنقصان والزيادة','348','1','0');
INSERT INTO [t6446] VALUES (481,'ولا يتساوى إيمان جميع المكلفين من الملائكة والأنبياء ومن دونهم من الشهداء والصديقين بل يتفاضلون بقدر رتبهم في الطاعات خلافا لمن قال الإيمان هو التصديق بالقلب وإنما يقع التفاضل في العلم بأصناف أدلته وقد ذكرنا أن الطاعات من الإيمان','348','1','0');
INSERT INTO [t6446] VALUES (482,'مسألة','349','1','0');
INSERT INTO [t6446] VALUES (483,'أعطى رهطا وترك رجلا منهم فقال سعد يا رسول الله أعطيتهم وتركت فلانا ووالله إني لأراه مؤمنا فقال رسول الله','350','1','0');
INSERT INTO [t6446] VALUES (484,'يقول اللهم حبب إلي الإسلام والإيمان وقد ذكرنا أن الإيمان عبارة عن جميع الطاعات والإسلام عبارة عن الشهادتين مع طمأنينة القلب وإذا كان كذلك وجب الفرق بينهما','351','1','0');
INSERT INTO [t6446] VALUES (485,'ويكره لمن حصل منه الإيمان أن يقول أنا مؤمن حقا ومؤمن عند الله ولكن يقول أنا مؤمن أرجو أو مؤمن إن شاء الله أو يقول آمنت بالله','351','1','0');
INSERT INTO [t6446] VALUES (486,'مسألة','353','1','0');
INSERT INTO [t6446] VALUES (487,'فصل','353','1','0');
INSERT INTO [t6446] VALUES (488,'في ذكر الأدلة على الفرق بين معنى الإسلام والإيمان','353','1','0');
INSERT INTO [t6446] VALUES (489,'إلا يثبت القدر ثم إني حججت أنا وحميد بن عبد الرحمن الحميري فلما قضينا حجنا قلت نأتي المدينة فنلقى أصحاب رسول الله','354','1','0');
INSERT INTO [t6446] VALUES (490,'أن موسى لقي آدم عليه السلام فقال يا آدم أنت خلقك الله بيده وأسجد لك الملائكة وأسكنك الجنة فوالله لولا ما فعلت ما دخل أحد من ذريتك النار قال فقال يا موسى أنت الذي اصطفاك الله برسالاته وبكلامه تلومني فيما قد كان كتب علي قبل أن أخلق','355','1','0');
INSERT INTO [t6446] VALUES (491,'فقال يا رسول الله أدنو منك قال نعم قال فجاء حتى وضع يده على ركبته فقال ما الإسلام قال تقيم الصلاة وتؤتي الزكاة وتصوم رمضان وتحج البيت قال فإذا فعلت ذلك فقد أسلمت قال نعم قال صدقت قال فجعل الناس يتعجبون','355','1','0');
INSERT INTO [t6446] VALUES (492,'عمر فقال تدري من الرجل الذي أتاكم قال فإنه جبريل عليه السلام أتاكم يعلمكم دينكم رواه مسلم في الصحيح من رواية عبد الله بن بريدة عن يحيى ابن يعمر بزيادة ألفاظ ونقصان ألفاظ وليس فيه فإذا فعلت ذلك فقد آمنت','356','1','0');
INSERT INTO [t6446] VALUES (493,'ذات يوم إذ طلع رجل شديد بياض الثياب شديد سواد','356','1','0');
INSERT INTO [t6446] VALUES (494,'وأسند ركبته إلى ركبته ووضع كفيه على فخذية ثم قال يا محمد أخبرني عن الإسلام قال الإسلام أن تشهد أن لا إله إلا الله وأن محمدا رسول الله وتقيم الصلاة وتؤدي الزكاة وتصوم رمضان وتحج البيت إن استطعت إليه سبيلا قال صدقت قال','357','1','0');
INSERT INTO [t6446] VALUES (495,'قال الإيمان سبعون بابا أو اثنان وسبعون أرفعه لا إله إلا الله وأدناه إماطة الأذى عن الطريق والحياء شعبة من الإيمان','358','1','0');
INSERT INTO [t6446] VALUES (496,'فسأله ما الإسلام فقال ما ذكرناه وسأله ما الإيمان فقال ما وصفنا وقال قائلون الإسلام هو أن يقول المرء إما طائعا وإما كارها فإن كان طائعا فاعتقد قلبه ما أقر بلسانه فقد كمل إيمانه وإن لم يصدق القلب قوله باللسان فليس','360','1','0');
INSERT INTO [t6446] VALUES (497,'وذكر أصحابه رضي الله عنه فقال لو أنفق أحدكم مثل أحد ذهبا ما بلغ مد أحدهم ولا نصيفه ثم فضل بعضهم على بعض وقد فضل الله بعض النبيين بعضهم على بعض فقال عز وجل تلك الرسل فضلنا بعضهم على بعض وقال لا يستوي','363','1','0');
INSERT INTO [t6446] VALUES (498,'باب','365','1','0');
INSERT INTO [t6446] VALUES (499,'في الرد على الجهمية والمعتزلة','365','1','0');
INSERT INTO [t6446] VALUES (500,'فصل','365','1','0');
INSERT INTO [t6446] VALUES (501,'فصل','366','1','0');
INSERT INTO [t6446] VALUES (502,'في إثبات صفة المحبة والفرق بينها وبين الإرادة','366','1','0');
INSERT INTO [t6446] VALUES (503,'يقول إن الله يحب العبد التقي الخفي الغني العفيف','368','1','0');
INSERT INTO [t6446] VALUES (504,'إن فيك خلقين يجبهما الله الحلم والحياء قلت أقديما كانا في أو حديثا قال لا بل قديما قلت الحمد لله الذي جبلني على خلقين يحبهما الله','369','1','0');
INSERT INTO [t6446] VALUES (505,'للحسن بن علي رضي الله عنه اللهم إني أحبه فأحبه وأحب من يحبه','369','1','0');
INSERT INTO [t6446] VALUES (506,'يقول في الأنصار لا يحبهم إلا مؤمن ولا يبغضهم إلا منافق من أحب الأنصار أحبه الله ومن أبغضهم أبغضه الله','369','1','0');
INSERT INTO [t6446] VALUES (507,'إن الله يحب أن تؤتى رخصه كما يكره أن تؤتى معصيته','370','1','0');
INSERT INTO [t6446] VALUES (508,'قال إن الله عز وجل يقول لأهل الجنة يا أهل الجنة فيقولون لبيك ربنا وسعديك والخير بيديك فيقول هل رضيتم فيقولون وما لنا لا نرضى وقد أعطيتنا ما لم تعط أحدا من خلقكم فيقول الله عز وجل أعطيكم أفضل من ذلك قالوا وأي شيء','370','1','0');
INSERT INTO [t6446] VALUES (509,'وهما منتصبتان وهو ساجد وهو يقول اللهم إني أعوذ بمعافاتك من عقوبتك وأعوذ برضاك من سخطك وأعوذ بك منك لا أحصي ثناء عليك أنت كما أثنيت على نفسك','371','1','0');
INSERT INTO [t6446] VALUES (510,'يقول إن أحدكم ليتكلم بالكلمة من رضوان الله عز وجل ما يظن أن تبلغ ما بلغت فيكتب الله عز وجل له بها رضوانه إلى يوم يلقاه وإن أحدكم ليتكلم بالكلمة من سخط الله ما يظن أن تبلغ الذي بلغت فيكتب الله عز وجل عليه بها سخطه إلى يوم','371','1','0');
INSERT INTO [t6446] VALUES (511,'اشتد غضب الله تعالى على قوم فعلوا برسول الله','373','1','0');
INSERT INTO [t6446] VALUES (512,'في سبيل الله','373','1','0');
INSERT INTO [t6446] VALUES (513,'قال آخر من يدخل الجنة رجل يمشي على الصراط وهو يمشي مرة ويكبو مرة وتسفعه النار فإذا جاوزها التفت إليها فقال تبارك الذي نجاني منك أعطاني الله شيئا ما أعطاه أحدا من الأولين والآخرين وترفع له شجرة فيقول أي رب أدنني من','375','1','0');
INSERT INTO [t6446] VALUES (514,'ثم قال ألا تسألوني مم ضحكت قالوا مم ضحكت يا رسول الله قال من ضحك رب العالمين عز وجل حيث قال أتستهزئ بي وأنت رب العالمين فيقول إني لا أستهزئ بك ولكني على ما أشاء قدير','376','1','0');
INSERT INTO [t6446] VALUES (515,'قال يا رسول الله مرني بما أحببت ولا أعصي لك أمرا فعجب لذلك النبي','376','1','0');
INSERT INTO [t6446] VALUES (516,'يعوده في الشتاء في برد وغيم فلما انصرف قال لأهله إني لا أرى طلحة إلا قد حدث فيه الموت فآذنوني به حتى أشهده وأصلي عليه وعجلوه فلم يبلغ النبي','376','1','0');
INSERT INTO [t6446] VALUES (517,'فإني أخاف عليه اليهود أن يصاب في سيي فأخبر النبي','377','1','0');
INSERT INTO [t6446] VALUES (518,'إن الله عز وجل يضحك من رجلين يقتل أحدهما الآخر فيدخلهما الله الجنة قيل كيف ذاك قال يكون أحدهما كافرا فيقتل الآخر ثم يسلم فيغزو في سبيل الله فيقتل','377','1','0');
INSERT INTO [t6446] VALUES (519,'قال ضحك الله عز وجل من قنوط عباده وقرب غيره','378','1','0');
INSERT INTO [t6446] VALUES (520,'قال عجب ربنا من رجلين رجل قام عن وطائه ولحافه ومن حبه وأهله إلى صلاته فيقول الله عز وجل انظروا إلى عبدي قام من وطائه ولحافه ومن حبه وأهله إلى صلاته رغبة فيما عندي وشفقة مما عندي ورجل غزا في سبيل الله عز وجل فانهزم فعلم ما عليه في','378','1','0');
INSERT INTO [t6446] VALUES (521,'قال يعجب ربكم عز وجل من راعي غنم في شظية يؤذن للصلاة ويقيم قال أهل اللغة شظية الجبل حرفه النادر منه','379','1','0');
INSERT INTO [t6446] VALUES (522,'يقول عجب ربنا من رجال يقادون إلى الجنة في السلاسل','379','1','0');
INSERT INTO [t6446] VALUES (523,'قال إن الله عز وجل ليعجب من الشاب ليست له صبوة','379','1','0');
INSERT INTO [t6446] VALUES (524,'قال إن الله عز وجل ليعجب من مداعبة المرء زوجته فيكتب لهما بذلك أجرا ويجعل لهما بذلك رزقا','380','1','0');
INSERT INTO [t6446] VALUES (525,'روى يوسف بن موسى قال سمعت أبا عبد الله يعني أحمد بن حنبل يقول لا تشبهوا الله بخلقه ليس كمثله شيء وهو السميع البصير وقال أبو يعلى أنكر أحمد رحمة الله عليه التشبيه وقال أئمة أصحاب الحديث في أخبار الصفات أمروها كما','380','1','0');
INSERT INTO [t6446] VALUES (526,'في الصفات بأسانيد صحاح فهو حق وقال أحمد في رواية حنبل يضحك الله ولا نعلم كيف ذلك إلا بتصديق الرسول','381','1','0');
INSERT INTO [t6446] VALUES (527,'إن الله يضع السموات على إصبع','382','1','0');
INSERT INTO [t6446] VALUES (528,'الصلاة والصيام والزكاة والحج وإنما عرفنا الله بهذه الأحاديث وقال عبد الرحمن بن مهدي وذكر عنده أن الجهمية ينفون أحاديث الصفات ويقولون الله أعظم من أن يوصف بشيء من هذا فقال عبد الرحمن ابن مهدي قد هلك قوم من وجه التعظيم','384','1','0');
INSERT INTO [t6446] VALUES (529,'فصل','386','1','0');
INSERT INTO [t6446] VALUES (530,'في إثبات الفرح صفة لله عز وجل','386','1','0');
INSERT INTO [t6446] VALUES (531,'قال سمعت رسول الله','386','1','0');
INSERT INTO [t6446] VALUES (532,'وأنا فلم أسمعه هكذا وأخبرنا أبو عمرو أنا والدي أبو عبد الله أنا علي بن الحسن نا أبو حاتم الرازي نا أبو الوليد وحسن بن الربيع قال أبو عبد الله وأخبرنا محمد بن سعد نا محمد بن أيوب نا سعيد ابن منصور','387','1','0');
INSERT INTO [t6446] VALUES (533,'كيف تقولون بفرح رجل انقلتت راحلته بأرض قفر تجر زمامها ليس بها طعام ولا شراب وله عليها طعام وشراب فذهب في طلبها حتى شق عليه فمرت بجذل شجرة فتعلق زمامها فوجدها معلقة قلنا شديدا يا رسول الله قال فوالله لله أشد فرحا بتوبة عبده','388','1','0');
INSERT INTO [t6446] VALUES (534,'قال ينزل ربنا عز وجل كل ليلة إلى سماء الدنيا حين يبقى ثلث الليل الآخر فيقول من يدعوني فأستجيب له من يسألني فأعطيه من يستغفرني فأغفر له','389','1','0');
INSERT INTO [t6446] VALUES (535,'إذا مضى شطر من الليل أو ثلثاه ينزل الله عز وجل إلى سماء الدنيا فيقول لا أسأل عن عبادي غيري من هذا الذي يسألني أعطيه من ذا الذي يدعوني أستجيب له من ذا الذي يستغفر لي أغفر له حتى ينفجر الصبح','389','1','0');
INSERT INTO [t6446] VALUES (536,'فصل','390','1','0');
INSERT INTO [t6446] VALUES (537,'في كراهية التأويل','390','1','0');
INSERT INTO [t6446] VALUES (538,'فصل','392','1','0');
INSERT INTO [t6446] VALUES (539,'في معنى المحكم والمتشابه','392','1','0');
INSERT INTO [t6446] VALUES (540,'جارية هذا المجرى ومنزلة هذا التنزيل قال الأنباري ذهب جماعة من أصحاب رسول الله','392','1','0');
INSERT INTO [t6446] VALUES (541,'أنزل القرآن على أربعة أحرف ثم قال ومتشابه لا يعلمه إلا الله قال أهل التفسير معنى آمنا به صدقنا به ولم يقل علمناه قالوا ولأنه إذا كانت الواو عاطفة في المشاركة في العلم احتاج الكلام إلى إضمار والإضمار ترك حقيقة','393','1','0');
INSERT INTO [t6446] VALUES (542,'فصل','394','1','0');
INSERT INTO [t6446] VALUES (543,'في الرد على من أنكر عذاب القبر','394','1','0');
INSERT INTO [t6446] VALUES (544,'كان يتعوذ من عذاب القبر','395','1','0');
INSERT INTO [t6446] VALUES (545,'قبره يعني سعد بن معاذ فاحتبس فيه فلما خرج قيل يا رسول الله ما حسبك قال ضم سعد في القبر فدعوت الله فكشف عنه','395','1','0');
INSERT INTO [t6446] VALUES (546,'ضم في القبر ضمة حتى صار مثل الشعر فدعوت الله أن يرفه عنه','396','1','0');
INSERT INTO [t6446] VALUES (547,'قميصه فألبسه إياها واضطجع معها في قبرها فلما سوى عليه التراب قالوا يا رسول الله رأيناك صنعت شيئا لم تصنعه بأحد قال إني ألبستها قميصي لتلبس من ثياب الجنة واضطجعت معها في قبرها ليخفف عنها من ضغطه القبر','396','1','0');
INSERT INTO [t6446] VALUES (548,'رقية ابنته جلس عند القبر فتربد وجهه وتغير ثم سري عنه فقال له أصحابه رأينا وجهك قد تغير فسري عنك فقال ذكرت ابنتي وضعفها وعذاب القبر فدعوت الله ففرج عنها وأيم الله لقد ضمت ضمة سمعها ما بين الخافقين','397','1','0');
INSERT INTO [t6446] VALUES (549,'أنا عند عقر حوضي أذود الناس عنه لأهل اليمن إني لأضربهم بعصاي وإنه ليغت فيه ميزابان من الجنة أحدهما من ورق والآخر من ذهب طوله ما بين بصرى وصنعاء أو ما بين أيلة ومكة أو من مقامي هذا إلى عمان قال أهل اللغة عقر الحوض مؤخره وعقر','398','1','0');
INSERT INTO [t6446] VALUES (550,'حوضي مسيرة شهر ما بين زواياه سواء كيزانه كنجوم السماء ماؤه أبيض من الورق وأحلى من العسل وريحه أطيب من المسك من شرب منه لم يظمأ بعده أبدا','399','1','0');
INSERT INTO [t6446] VALUES (551,'حوضي من عدن إلى عمان ماؤه أحلى من العسل وأطيب من المسك وأبيض من اللبن أكوابه كعدد نجوم السماء من شرب منه شربة لم يظمأ بعدها أبدا وأول الناس ورودا عليه فقراء المهاجرين الشعث رؤوسا الدنس ثيابا الذين لا ينكحون المنعمات','400','1','0');
INSERT INTO [t6446] VALUES (552,'قال إن لكل نبي حوضا يتباهون أيهم أكثر واردة وإني أرجو أن أكون أكثرهم واردة','400','1','0');
INSERT INTO [t6446] VALUES (553,'أبصر نهرا في الجنة قيل هذا الكوثر قال فأصبح وحدث به الناس فقال منافق لصاحب له سله فوالله ما رأينا نهرا قط إلا على شطه نبات فما نبته قال قضبان الذهب الرطب مستعلية عليه تظله قالوا إنا لم نر نباتا إلا وله ثمر فما ثمره','401','1','0');
INSERT INTO [t6446] VALUES (554,'شعار المسلمين يوم القيامة على الصراط اللهم سلم سلم','402','1','0');
INSERT INTO [t6446] VALUES (555,'قال يحبس أهل الجنة بعدما يجاوزون الصراط فليقتصن بعضهم من بعض مظالم تظالموها في دار الدنيا حتى إذا هذبوا ونقوا أذن لهم في دخول الجنة قال قتادة قال أبو عياش ما نشبهه بهم إلا أهل جمعة انصرفوا من جمعتهم قال قتادة إن أحدهم لأهدى','403','1','0');
INSERT INTO [t6446] VALUES (556,'قال إن آخر من يدخل الجنة رجل يمشي على الصراط مرة ويكبو مرة وتسعفه النار مرة فإذا جاوز التفت إليها فقال','403','1','0');
INSERT INTO [t6446] VALUES (557,'ما الصراط فقال رسول الله','404','1','0');
INSERT INTO [t6446] VALUES (558,'قال إني قائم أنتظر أمتي تعبر الصراط إذ جاءني عيسى بن مريم عليه السلام فقال يا محمد هذه الأنبياء قد جاءت يسألون أن يجتمعوا إليك وتدعو الله أن يفرق بين جميع الأمم إلى حيث شاء الله تدعون الله لغم ما هم فيه فالخلق ملجمون في العرق','404','1','0');
INSERT INTO [t6446] VALUES (559,'فصل','405','1','0');
INSERT INTO [t6446] VALUES (560,'في ذكر شفاعة رسول الله','405','1','0');
INSERT INTO [t6446] VALUES (561,'يدخل من أهل هذه القبلة النار ما لا يحصي عددهم إلا الله عز وجل بما عصوا الله عز وجل واجترؤوا علىمعصيته وخالفوا طاعته فيؤذن لي في الشفاعة فأثني على الله ساجدا كما أثنى عليه قائما فيقال يا محمد ارفع رأسك وقل تسمع واشفع تشفع','406','1','0');
INSERT INTO [t6446] VALUES (562,'في الشفاعة فقال أنس حدثنا محمد رسول الله','406','1','0');
INSERT INTO [t6446] VALUES (563,'فأوتى فأقول أنا لها فأنطلق فاستأذن على ربي عز وجل فيؤذن لي عليه فيقيمني فأقوم ويلهمني محامد لا أقدر عليها الآن فأحمده بتلك المحامد ثم أخر له ساجدا فيقال يا محمد أرفع رأسك وقل نسمع وسل تعطه واشفع تشفع فأقول أي رب أمتي','407','1','0');
INSERT INTO [t6446] VALUES (564,'ما أزال أشفع إلى ربي عز وجل فيشفعني ويشفعني حتى أقول يا رب شفعني فيمن قال لا إله إلا الله فقال هذه ليست لك ولا لأحد إنما هي لي وعزتي وحلمي ورحمتي لا أدع في النار من قال لا إله إلا الله','408','1','0');
INSERT INTO [t6446] VALUES (565,'قال إن الناس يحتبسون يوم القيامة ما شاء الله أن يحتبسوا فيهم المؤمنين فيقولون انظروا من يشفع لنا إلى ربنا عز وجل فليرحنا من منزلنا هذا فيأتون آدم عليه السلام فيقولون اسفع لنا إلى ربنا عز وجل فليرحنا من منزلنا فيستقرون الأنبياء','409','1','0');
INSERT INTO [t6446] VALUES (566,'فصل','410','1','0');
INSERT INTO [t6446] VALUES (567,'في إثبات الميزان','410','1','0');
INSERT INTO [t6446] VALUES (568,'قال تنصب الموازين يوم القيامة فيؤتى بأهل الصلاة وأهل الصيام وأهل الصدقة وأهل الحج فيوفون بالميزان ويؤتى بأهل البلاء فلا ينصب لهم ميزان ولا ينشر لهم ديوان ويصب الأجر عليهم صبا بغير حساب','411','1','0');
INSERT INTO [t6446] VALUES (569,'قال قال رسول الله','411','1','0');
INSERT INTO [t6446] VALUES (570,'ما يبكيك فقالت يا رسول الله هل تذكرون أهليكم يوم القيامة فقال رسول الله أما في ثلاث مواطن فلا يذكر أحد أحدا عند الميزان حتى يعلم أيخف ميزانه أم يثقل وعند الكتاب حين يقول هاؤوم اقرؤوا كتابيه حتى يعلم أين يقع كتابه أفي يمينه','412','1','0');
INSERT INTO [t6446] VALUES (571,'ثلاث مواطن تذهل كل نفس فيهن حين رمى إلى كل نفس بكتابة حتى ينظر أبيمينه يأخذ كتابه أم بشماله وعند الموازين حتى ينظر أيرجح أم يخف وعند جسر جهنم يمر به الرجل أسرع من البرق ومن الريح ومن الطير','412','1','0');
INSERT INTO [t6446] VALUES (572,'إن أثقل شيء في الميزان الخلق الحسن','413','1','0');
INSERT INTO [t6446] VALUES (573,'يقول إن الله خلق أرواح العباد قبل العباد بألفي عام فما تعارف منها ائتلف وما تناكر منها اختلف قال أبو عبد الله بيان آخر يدل على أن الأرواح مخلوقة وأنها جنود مجندة','413','1','0');
INSERT INTO [t6446] VALUES (574,'يقول الأرواح جنود مجندة فما تعارف منها ائتلف وما تناكر منها اختلف قال أبو عبد الله قال محمد بن نصر ولا خلاف بين المسلمين في أن الأرواح التي في آدم وبنيه وعيسى ومن سواء من بني آدم كلها مخلوقة الله خلقها وانشأها وكونها','414','1','0');
INSERT INTO [t6446] VALUES (575,'فصل','416','1','0');
INSERT INTO [t6446] VALUES (576,'في الرد على من ينكر أن الريح مخلوقة','416','1','0');
INSERT INTO [t6446] VALUES (577,'قال إن الله عز وجل خلق في الجنة ريحا بعد الريح بسبع سنين ومن دونها باب مغلق وإنما يأتيكم الروح من خلل ذلك الباب ولو فتح ذلك الباب لا ذرت ما بين السماء والأرض من شيء وهي عند الله عز وجل الأزيب وهي فيكم الجنوب','416','1','0');
INSERT INTO [t6446] VALUES (578,'اطلعت في الجنة فرأيت أكثر أهلها الفقراء واطلعت في النار فرأيت أكثر أهلها النساء','417','1','0');
INSERT INTO [t6446] VALUES (579,'رأيت عمرو بن عامر بن لحي يجر قصبة في النار وكان أول من سيب السوائب','418','1','0');
INSERT INTO [t6446] VALUES (580,'إن أحدكم إذا مات عرض عليه مقعده بالغداة والعشي إن كان من أهل الجنة فمن أهل الجنة وإن كان من أهل النار فمن أهل النار حتى يبعثه الله يوم القيامة يقال له هذا مقعدك','418','1','0');
INSERT INTO [t6446] VALUES (581,'أنه رأى جهنم','418','1','0');
INSERT INTO [t6446] VALUES (582,'لما خلق الله الجنة أرسل جبريل عليه السلام إليها فقال انظر إليها وما أعد الله لأهلها فيها فجاء فنظر إليها وما أعد الله عز وجل لأهلها فيها فرجع فقال وعزتك لا يسمع بها أحد إلا دخل فيها فأمر بها فحفت بالمكارة فقال وعزتك لقد خفت أن لا','419','1','0');
INSERT INTO [t6446] VALUES (583,'أبردوا بالصلاة أو قال انتظروا فإن شدة الحر في فيح جهنم','419','1','0');
INSERT INTO [t6446] VALUES (584,'اشتكت النار إلى ربها فقالت يا رب أكل بعضي بعضا فأذن لها بنفسين نفس في الشتاء ونفس في الصيف','420','1','0');
INSERT INTO [t6446] VALUES (585,'قال دخلت الجنة فإذا أنا بنهر حافتاه خيام اللؤلؤ فضربت بيدي في حومة الماء فإذا مسك أذفر قلت يا جبريل ما هذا قال الكوثر الذي أعطاكه الله أو أعطاك ربك','420','1','0');
INSERT INTO [t6446] VALUES (586,'دخلت الجنة فسمعت فيها قراءة فقلت من هذا قالوا حارثة بن النعمان كذلكم البر كذلك البر','421','1','0');
INSERT INTO [t6446] VALUES (587,'يتعوذ من عذاب القبر قال ابن أبي داود هذه أم خالد بنت خالد بن سعيد بن العاص روت عن النبي','421','1','0');
INSERT INTO [t6446] VALUES (588,'فخرج بجنازتها وخرجنا معه فرأيناه كئيبا حزينا ثم دخل النبي','422','1','0');
INSERT INTO [t6446] VALUES (589,'كيف أنت إذا كنت في أربعة أذرع في ذراعين ورأيت منكرا ونكيرا قال قلت يا رسول الله وما منكر ونكير قال فتانا القبر يبحثان الأرض بأنيابهما ويطآن في أشعارهما أصواتهما كالرعد القاصف وأبصارهما كالبرق الخاطف معهما مرزبة لو اجتمع عليها أهل','422','1','0');
INSERT INTO [t6446] VALUES (590,'كيف أنت يا عمر إذا كنت من الأرض في أربعة أذرع في ذراعين ورأيت منكرا ونكيرا قلت يا رسول الله وما منكر ونكير قال فتانا القبر يبحثان الأرض بأنيابهما ويطآن في أشعارهما أصواتهما كالرعد القاصف وأبصارهما كالبرق الخاطف معهما مرزية لو','423','1','0');
INSERT INTO [t6446] VALUES (591,'يصلح بينهم وهو متكئ على رجل قال فبينما هم كذلك إذ التفت إلى قبر فقال لا دريت فقال له الرجل بأبي أنت وأمي والله ما أرى قربك أحدا فلمن قلت لا دريت فقال إني مررت بقبر وهو يسئل عني فقال لا أدري فقلت لا دريت','423','1','0');
INSERT INTO [t6446] VALUES (592,'قال لولا أن لا تدافنوا لدعوت الله عز وجل أن يسمعكم عذاب القبر','424','1','0');
INSERT INTO [t6446] VALUES (593,'ويحتج بقوله تعالى يريدون أن يخرجوا من النار وما هم بخارجين منها وقوله كلما أرادوا أن يخرجوا منها من غم أعيدوا فيها وليس لهم في ذلك حجة إنما هذا في الكفار','424','1','0');
INSERT INTO [t6446] VALUES (594,'يقول الله عز وجل يريدون أن يخرجوا من النار وما هم بخارجين منها الآية فانتهرني أصحابه وكان أحلمهم فقال','425','1','0');
INSERT INTO [t6446] VALUES (595,'قال يخرج قوم من النار ولا نقول كما يقول أهل حروراء','426','1','0');
INSERT INTO [t6446] VALUES (596,'يقول إن أناسا يخرجون من النار بذنوب أصابوها من أهل التوحيد فيجعلون على نهر من أنهار الجنة فيرش عليهم أهل الجنة','426','1','0');
INSERT INTO [t6446] VALUES (597,'فصل','427','1','0');
INSERT INTO [t6446] VALUES (598,'في بيان أن السحر له حقيقة','427','1','0');
INSERT INTO [t6446] VALUES (599,'قال اجتنبوا السبع الموبقات قيل يا رسول الله وما هن قال الشرك بالله والسحر وقتل النفس التي حرم الله إلا بالحق وأكل الربا وأكل مال اليتيم والتولي يوم الزحف وقذف المحصنات الغافلات المؤمنات','428','1','0');
INSERT INTO [t6446] VALUES (600,'أصابه شيء حتى كان يرى أنه يأتي النساء ولا يأتيهن فانتبه من نومه فقال يا عائشة إن الله تعالى أفتاني فيما استفتيته آتاني آتيان فقعد أحدهما عند رأسي والآخر عند رجلي فقال أحدهما للآخر ما بال الرجل قال مطبوب قال ومن طبه','428','1','0');
INSERT INTO [t6446] VALUES (601,'هذه البئر التي رأيتها كأن ماءها نقاعة الحناء وكأن نخلها رؤوس الشياطين قالت عائشة رضي الله عنها فقلت له تعني ألا تتنشر قال أما أنا فقد شفاني الله وأكره أن أثير على أحد يعني شرا قالت ونزلت قل أعوذ برب الفلق من شر ما خلق','429','1','0');
INSERT INTO [t6446] VALUES (602,'فوافقه مغتما فقال يا محمد ما هذا الغم الذي أراه في وجهك قال الحسن والحسين أصابتهما عين فقال يا محمد صدق بالعين فإن العين حق ثم قال أفلا عوذتهما بهؤلاء الكلمات قال وما هن يا جبريل قال قل اللهم ذا السلطان العظيم ذا المن','430','1','0');
INSERT INTO [t6446] VALUES (603,'فقاما يلعبان بين يديه فقال النبي','431','1','0');
INSERT INTO [t6446] VALUES (604,'فصل','431','1','0');
INSERT INTO [t6446] VALUES (605,'في بيان أن إبليس والجن هم خلق من خلق الله','431','1','0');
INSERT INTO [t6446] VALUES (606,'قال الجن على ثلاثة أثلاث فثلث لهن أجنحة يطيرون في الهواء وثلث حيات وكلاب وثلث يحلون ويظعنون','432','1','0');
INSERT INTO [t6446] VALUES (607,'أن أحتفظ بزكاة رمضان وأتاني آت من الليل فجعل يحثو من الطعام فأخذته فقلت لأرفعنك إلى رسول الله','432','1','0');
INSERT INTO [t6446] VALUES (608,'يا أبا هريرة ما فعل أسيرك الليلة قال يا نبي الله زعم أنه محتاج وحاله شديدة فرحمته قال أما إنه قد كذبك وسيعود فلما كان الليلة الثانية رصده فجاء فأخذه فقال لأرفعنك إلى رسول الله','432','1','0');
INSERT INTO [t6446] VALUES (609,'يا أبا هريرة ما فعل أسيرك الليلة قال يا رسول الله شكى حاجة وعيالا وإني رحمته فخليت سبيله قال أما إنه قد كذبك وسيعود فلما كان في الليلة الثالثة رصده فجاء فأخذه فقال لأرفعنك إلى رسول الله','432','1','0');
INSERT INTO [t6446] VALUES (610,'ما فعل أسيرك الليلة قال يا نبي الله علمني كلمات زعم أن الله ينفعني بها قال وما هي قال أمرني أن أقرأ آية الكرسي من أولها إلى آخرها فإنه لن يزال علي من الله حافظا ولا يقربني شيطان حتى أصبح قال أما إنه قد صدقك وهو','433','1','0');
INSERT INTO [t6446] VALUES (611,'فصل','434','1','0');
INSERT INTO [t6446] VALUES (612,'في الرد على من ينكر معراج النبي','434','1','0');
INSERT INTO [t6446] VALUES (613,'قال الله عز وجل سبحان الذي أسرى بعبده ليلا وقال والنجم إذا هوى ما ضل صاحبكم وما غوى إلى قوله فكان قاب قوسين أو أدنى','434','1','0');
INSERT INTO [t6446] VALUES (614,'حدثهم عن ليلة أسري به قال بينما أنا في الحطيم وربما قال قتادة في الحجر مضطجعا فجعل يقول لصاحب الأوسط بين الثلاثة قال فأتاني فقد وقال قتادة فشق ما بين هذه إلى هذه قال قتادة فقلت للجارود وهو إلى جنبي ما يعني قال من ثغرة نحره','435','1','0');
INSERT INTO [t6446] VALUES (615,'حين أسري به في ليلة المسرى وأنه رفع إلى الأفق الأعلى ثم زيد في مرتبته فبلغ به السدرة المنتهى ثم زيد به في رفعته بلغ به إلى أن دنى من ربه عز وجل حتى صار أقرب إلى ربه عز وجل من القدر الذي ذكر في التنزيل فأوحى إلى عبده ما أوحى','438','1','0');
INSERT INTO [t6446] VALUES (616,'قال أتيت يعني بالبراق وهي دابة أبيض طويل فوق الحمار ودون البغل يضع حافره عند منتهى طرفه قال فركبته حتى أتيت بيت المقدس قال فربطته بالحلقة التي يربط بها الأنبياء قال ثم دخلت المسجد فصليت فيه ركعتين ثم خرجت فجاءني جبريل','439','1','0');
INSERT INTO [t6446] VALUES (617,'قد رجعت إلى ربي عز وجل وقد استحييت رواه مسلم في الصحيح عن شيبان رواية أبي ذر رضي الله عنه','441','1','0');
INSERT INTO [t6446] VALUES (618,'قال فرج سقف بيتي وأنا بمكة فنزل جبريل فرج صدري ثم غسله من ماء زمزم ثم جاء بطست من ذهب ممتلىء حكمة وإيمانا فأفرغها في صدري ثم أطبقه ثم أخذ بيدي فعرج بي إلى السماء فلما جئنا السماء الدنيا قال جبريل لخازن السماء الدنيا افتح','441','1','0');
INSERT INTO [t6446] VALUES (619,'بإدريس قال مرحبا بالنبي الصالح والأخ الصالح فقلت من هذا قال هذا إدريس قال ثم مررت بعيسى فقال مرحبا بالنبي الصالح والأخ الصالح قال قلت من هذا قال هذا عيسى ابن مريم قال ثم مررت بموسى فقال مرحبا بالنبي الصالح والأخ','442','1','0');
INSERT INTO [t6446] VALUES (620,'ففرض الله عز وجل على أمتي خمسين صلاة قال فرجعت بذلك حتى أمر بموسى فقال موسى ماذا فرض ربك على أمتك قال قلت فرض عليهم خمسين صلاة فقال لي موسى فراجع ربك فإن أمتك لا تطيق ذلك قال فراجعت ربي عز وجل فقال هي خمس وهي خمسون لا','442','1','0');
INSERT INTO [t6446] VALUES (621,'فصل','443','1','0');
INSERT INTO [t6446] VALUES (622,'في اعتراض المبتدعة وغيرهم على حديث المعراج','443','1','0');
INSERT INTO [t6446] VALUES (623,'من مسجد الكعبة وفي حديث مالك بن صعصعة قال قال النبي بينا أنا نائم عند البيت بين النائم واليقظان قالوا رويتم أنه شق صدره وغسل بماء زمزم وإنما كان ذلك في حال صغره وحين كان عند حليمة ظئره قالوا ورويتم في بعض','443','1','0');
INSERT INTO [t6446] VALUES (624,'بينا أنا جالس إذ جاء جبريل فوكز بين كتفي فقمت إلى شجرة مثل وكري الطير فقعد في أحدهما وقعدت في الآخر فسمت وارتفعت حتى سدت الخافقين ولو شئت أن أمس السماء لمسست قالوا ورويتم في حديث أبي هارون العبدي أنه رأى أباه آدم عليه السلام في','444','1','0');
INSERT INTO [t6446] VALUES (625,'وكان مقامه أقرب من مقامه ومحله أعظم من محله ورويتم أن موسى عليه السلام بعدما رجع لم يطأ امرأة قط ولم يرو ذلك عن النبي','445','1','0');
INSERT INTO [t6446] VALUES (626,'كان بالإسراء من المسجد الحرام إلى المسجد الأقصى والعروج إنما كان بعد ذلك فلو أخبر النبي','445','1','0');
INSERT INTO [t6446] VALUES (627,'عام الفيل ودفع إلى ظئره فلم يزل عندهم خمس سنين ثم ردوه إلى أمه فأخرجته أمه إلى أخواله بالمدينة بعد سنة فتوفيت أمه بالأبواء وردته أم أيمن حاضنته إلى مكة بعد موت آمنة وكفله عبد المطلب فتوفي عبد المطلب وهو ابن ثمان سنين وكفله أبو طالب وخرج','446','1','0');
INSERT INTO [t6446] VALUES (628,'فصل','447','1','0');
INSERT INTO [t6446] VALUES (629,'ليلة الإسراء كان في بيت أم هانئ وهو بين الصفا والمروة ومن','447','1','0');
INSERT INTO [t6446] VALUES (630,'تربى فيه فأضيف إليه ومن روى أنه أسري به من المسجد الحرام ومسجد الكعبة فإنما أراد به الحرم الذي هو مسجد فيضاف إلى الكعبة فأضاف الكل إلى الحرم والحرم قد يجوز أن يطلق عليه اسم المسجد الحرام قال الله تعالى جعل الله الكعبة البيت','448','1','0');
INSERT INTO [t6446] VALUES (631,'صلى ببيت المقدس بالأنبياء وفي السماء الرابعة بالملائكة فليس هذا بمختلف وأما ما رويتم أنه لقي إدريس في السماء السادسة وفي رواية في السماء الرابعة فالمشهور في ذلك السماء الرابعة وقولهم كيف يجوز أن يؤذن للروح الخبيث ليعرج به في','449','1','0');
INSERT INTO [t6446] VALUES (632,'في ذلك الوقت بالتثبيت لأنه لو يخصه بذلك لما أطاق رؤية العجائب وقولهم رجع من عند موسى عليه السلام إلى ربه عز وجل لما أخبره بأنه رد إلى خمس صلوات قيل هذا في حديث شريك وفي غيره من الأخبار أنه قال قد رجعت إلى ربي حتى استحييت','450','1','0');
INSERT INTO [t6446] VALUES (633,'وقال الحسن غشي كل ورقة ملك وقيل غشيها الملائكة كأنهم فراش من ذهب وقولهم تبرقع موسى لما كلمه الرب عز وجل ولم يتبرقع المصطفى','451','1','0');
INSERT INTO [t6446] VALUES (634,'ألبس باطنه نورا رفقا بأمته وقول القائل ما معنى ربطه البراق بحلقة الباب','451','1','0');
INSERT INTO [t6446] VALUES (635,'أن يعلمه بلالا وجعل ذلك علما لدخول وقت الصلاة','453','1','0');
INSERT INTO [t6446] VALUES (636,'ربه تعالى','453','1','0');
INSERT INTO [t6446] VALUES (637,'وعليهما','455','1','0');
INSERT INTO [t6446] VALUES (638,'رأى ربه عز وجل قال وأخبرنا أبو سعيد النقاش أنا سليمان بن أحمد نا علي بن رستم نا محمد بن عصام عن أبيه عن سفيان عن ابن جريج عن عطاء عن ابن عباس رضي الله عنه أن محمد','455','1','0');
INSERT INTO [t6446] VALUES (639,'رأيت ربي عز وجل','456','1','0');
INSERT INTO [t6446] VALUES (640,'فإن قيل كيف يجوز أن يرى بالعين الفانية القديم الباقي يقال له لما جاز أن يسمع موسى بالأذن الفانية كلام القديم الباقي جاز أن يرى محمد','456','1','0');
INSERT INTO [t6446] VALUES (641,'ربه عز وجل بابن عباس وأنس بن مالك وأبي ذر وكعب قال كعب إن الله عز وجل قسم رؤيته وكلامه بين موسى ومحمد صلى الله عليهما فرآه محمد مرتين وكلمه موسى مرتين قال محمد بن إسحاق أكثر ما في هذا الباب أن عائشة وأبا ذر وابن عباس وأنس','456','1','0');
INSERT INTO [t6446] VALUES (642,'يقول لم أر ربي وإنما تأولت قوله لا تدركه الأبصار وقوله وما كان لبشر أن يكلمه الله إلا وحيا أو من وراء حجاب وقوله لا تدركه الأبصار يحتمل أن يكون معناه نفي الإدراك وقال بعضهم نحن لا نقول إنا نرى','457','1','0');
INSERT INTO [t6446] VALUES (643,'فصل','457','1','0');
INSERT INTO [t6446] VALUES (644,'بيان الصحيح من أحاديث المعراج','457','1','0');
INSERT INTO [t6446] VALUES (645,'فصل','458','1','0');
INSERT INTO [t6446] VALUES (646,'في أن الإسراء والمعراج كانا يقظة لا مناما','458','1','0');
INSERT INTO [t6446] VALUES (647,'إلى بيت المقدس ورأى الدجال في صورته رؤيا عين لا رؤيا منام','459','1','0');
INSERT INTO [t6446] VALUES (648,'في حديث المعراج رأيت كذا ورأيت كذا وقال أهل اللغة رأيت في اليقظة رؤية ورؤيا مثل قربة وقربى وروي عن ابن عباس رضي الله عنه أنه قال في قوله عز وجل وما جعلنا الرؤيا التي أريناك إنها رؤية عين ويقظة لا رؤية منام قال بعض','460','1','0');
INSERT INTO [t6446] VALUES (649,'فصل','461','1','0');
INSERT INTO [t6446] VALUES (650,'في بيان معنى المعراج','461','1','0');
INSERT INTO [t6446] VALUES (651,'قيل في التفسير تعرج الملائكة إلى السماء من صخرة ببت المقدس قال خالد بن معدان الصخرة أقرب إلى السماء بستة عشر ميلا وقالوا وهو بالأفق الأعلى يعني فوق السماء السابعة قال بعض العلماء في هذه الآيات دلالة على أن الله على كل شيء','461','1','0');
INSERT INTO [t6446] VALUES (652,'فصل','462','1','0');
INSERT INTO [t6446] VALUES (653,'حكمة تسمية البراق براقا','462','1','0');
INSERT INTO [t6446] VALUES (654,'ليلة أسري به','463','1','0');
INSERT INTO [t6446] VALUES (655,'الجزء ٢','464','0','0');
INSERT INTO [t6446] VALUES (656,'ذكر الأحاديث الصحيحة والمشهورة في الباب','468','655','0');
INSERT INTO [t6446] VALUES (657,'وهو الصادق المصدوق قال إن أحدكم ليجمع خلقه في بطن أمة أربعين يوما أو قال أربعين ليلة ثم يكون علقة ثم يكون مضغة مثل ذلك ثم يرسل الله إليه ملكا فيؤمر','468','655','0');
INSERT INTO [t6446] VALUES (658,'يقول يدخل الملك على النطفة بعدما تستقر في الرحم أربعين يوما فيقول يا رب ماذا أشقي أم سعيد قال فيقول الله عز وجل ويكتبان ثم يقول يا رب أذكر أم أنثى فيقول الله ويكتبان رزقه وعمله وأثره ومصيبته ثم تطوى له الصحف','469','655','0');
INSERT INTO [t6446] VALUES (659,'يقول إذا استقرت النطفة في الرحم أربعين أو خمسا وأربعين ليلة دخل عليها الملك فيقول أي رب أشقي أو سعيد فيقول الرب ويكتب الملك قال فيقول يا رب أذكر أم أثنى فيقول الرب ويكتب الملك قال فيقول يا رب أجله','470','655','0');
INSERT INTO [t6446] VALUES (660,'إن لله ملكا موكلا بالرحم فيقول أي رب نطفة أي رب علقة أي رب مضغة أي رب شقي أو سعيد فما الأجل فما الرزق فيكتب كذلك في بطن أمه','472','655','0');
INSERT INTO [t6446] VALUES (661,'كل ميسر للذي خلق له فقال سراقة ما كنت بأحق بالاجتهاد مني الآن','473','655','0');
INSERT INTO [t6446] VALUES (662,'وثبتت الحجة عليهم فقلت بل شيء قضي عليهم ومضى عليهم فقال أفلا يكون ظلما ففزعت من ذلك فزعا شديدا وقلت كل شيء خلق الله','473','655','0');
INSERT INTO [t6446] VALUES (663,'فقال أرأيت ما يعمل الناس اليوم ويكدحون اليوم أشيء قضي عليهم ومضى من قدر قد سبق أو فيما يستقبلونه مما أتاهم به نبيهم','474','655','0');
INSERT INTO [t6446] VALUES (664,'فصل','475','655','0');
INSERT INTO [t6446] VALUES (665,'قالوا فما تقول لربك إذا لقيته قال أقول اللهم تركتني فيهم ثم قبضتني إليك وأنت فيهم فإن شئت أصلحتهم وإن شئت أفسدتهم وأخبرنا أبو المظفر أنا أبو علي الشافعي نا أبو الحسن بن فراس نا عبد الرحمن المقري نا جدي نا سفيان عن','476','655','0');
INSERT INTO [t6446] VALUES (666,'فصل','477','655','0');
INSERT INTO [t6446] VALUES (667,'بعثت داعيا ومبلغا وليس إلي من الهدى شيء وخلق أبليس مزينا وليس إليه من الضلالة شيء','477','655','0');
INSERT INTO [t6446] VALUES (668,'نا أبو حاتم محمد بن إدريس نا إسماعيل ابن أبي أويس حدثني أبي عن جعفر بن محمد عن أبيه أنه سأل جابر ابن عبد الله رضي الله عنه عن خطبة رسول الله','478','655','0');
INSERT INTO [t6446] VALUES (669,'يحمد الله ويثني عليه بما هو له أهل ثم يقول من يهده الله فلا مضل له ومن يضلل فلا هادي له إن أحسن الحديث كتاب الله وخير الهدي هدى محمد وكل بدعة ضلالة ثم يذكر الساعة','478','655','0');
INSERT INTO [t6446] VALUES (670,'لا تجالسوا أصحاب القدر ولا تفاتحوهم','479','655','0');
INSERT INTO [t6446] VALUES (671,'يقول كتب الله تعالى مقادير الخلائق كلها قبل أن يخلق السموات والأرض بخمسين ألف سنة وعرشه على الماء','483','655','0');
INSERT INTO [t6446] VALUES (672,'في جنازة صبي من الأنصار فقالت عائشة رضى الله عنها طوبى له عصفور من عصافير الجنة فقال رسول','483','655','0');
INSERT INTO [t6446] VALUES (673,'وما يدريك يا عائشة إن الله عز وجل خلق الجنة وخلق لها أهلا وهم في أصلاب آبائهم وخلق النار وخلق لها أهلا وهم في أصلاب آبائهم وفي رواية سفيان عن طلحة بن يحيى إن الله تعالى خلق الجنة وخلق لها أهلا خلقها لهم في أصلاب آبائهم','484','655','0');
INSERT INTO [t6446] VALUES (674,'وفي يده كتابان فقال أتدرون ما هذان','484','655','0');
INSERT INTO [t6446] VALUES (675,'بيديه فنبذهما ثم قال فرغ ربكم من العباد فريق في الجنة وفريق في السعير قال أبو عيسى حديث حسن صحيح وأبو قبيل اسمه يحيى بن هانئ','485','655','0');
INSERT INTO [t6446] VALUES (676,'قال الإمام أبو المظفر وأما أهل القدر احتجوا بحديثين','485','655','0');
INSERT INTO [t6446] VALUES (677,'قال كل مولود يولد على الفطرة فأبواه يهودانه وينصرانه ويمجسانه كما تنتج البهيمة بهيمة جمعاء هل تحسون فيها جدعاء ثم يقول أبو هريرة اقرءوا إن شئتم فطرة الله التي فطر الناس عليها','486','655','0');
INSERT INTO [t6446] VALUES (678,'فسمعه يقول إن الله عز وجل أمرني أن أعلمكم مما جهلتم من دينكم يومكم هذا وإن كل مال نحلته عبدي فهو له حلال وإني خلقت عبادي حنفاء كلهم وإنه أتتهم الشياطين فاجتالتهم عن دينهم وحرمت عليهم ما أحللت لهم وأمرتهم أن يشركوا بي ما لم أنزل','486','655','0');
INSERT INTO [t6446] VALUES (679,'سئل عن أطفال المشركين فقال الله أعلم بما كانوا عاملين يذهب إلى أنهم إنما يولدون على ما يصيرون إليه من إسلام أو كفر فمن كان في علم الله أنه يصير مسلما فإنه يولد على الفطرة ومن كان علمه فيه أن يموت كافرا ولد على الكفر ولا مزيد على','489','655','0');
INSERT INTO [t6446] VALUES (680,'خلقت عبادي حنفاء فهو والله أعلم إشارة إلى المعرفة الغريزية التي هي مركبة فيهم قال وقد ذكر بعض أهل العلم أن الفطرة ها هنا هي الفطرة الغريزية التي هي موجودة في كل إنسان فإن كل أحد يرجع إلى غريزته عرف خالقه وذلك معنى قوله','492','655','0');
INSERT INTO [t6446] VALUES (681,'له الجنة بكل حال فلما أحال الحكم على ما علم الله تعالى من أعمالهم أن لو أدركوا وقت العمل عرفنا أن المعني بالفطرة ما قلنا من المعرفة التي لا يكون فيها ثواب ولا بعدمها عقاب','493','655','0');
INSERT INTO [t6446] VALUES (682,'فصل','494','655','0');
INSERT INTO [t6446] VALUES (683,'قال إن الله تعالى كتب على ابن آدم حظه من الزنا أدرك ذلك لا محالة فزنا العينين النظر وزنا اللسان','494','655','0');
INSERT INTO [t6446] VALUES (684,'يقول اللهم لولا أنت ما اهتدينا ولا تصدقنا ولا صلينا فثبت الأقدام إن لاقينا إن الأولى قد بغوا علينا','495','655','0');
INSERT INTO [t6446] VALUES (685,'يقوله بعد الصلاة قال لا إله إلا الله وحده لا شريك له اللهم لا مانع لما أعطيت ولا معطي لما منعت ولا ينفع ذا الجد منك الجد','496','655','0');
INSERT INTO [t6446] VALUES (686,'أنها أخبرته أنها سألت نبي الله','496','655','0');
INSERT INTO [t6446] VALUES (687,'أنه كان عذابا يبعثه الله على من يشاء فجعله الله رحمة للمؤمنين وليس من عبد يقع في الطاعون فيمكث في بلده صابرا محتسبا يعلم أنه لن يصيبه إلا ما كتب له إلا كان له مثل أجر شهيد','496','655','0');
INSERT INTO [t6446] VALUES (688,'لا يأتي ابن آدم النذر بشيء لم أكن قد قدرته له ولكنه يلقيه النذر إلى ما قدرته له أستخرج به من البخيل يؤتيني عليه ما لم يكن آتاني من قبل','496','655','0');
INSERT INTO [t6446] VALUES (689,'لا','496','655','0');
INSERT INTO [t6446] VALUES (690,'جف القلم بما أنت لاق فاختص على ذلك أو ذر قال وحدثنا أبو حفص نا أحمد بن عبد الرحمن نا عمي أخبرني يونس عن الزهري قال أبو حفص وحدثنا محمد بن المثنى نا يعمر بن بشر نا ابن المبارك نا الأوزاعي حدثني الزهري','497','655','0');
INSERT INTO [t6446] VALUES (691,'أنه قال ما بعث الله من نبي ولا استخلف من خليفة إلا كانت له بطانتان بطانة تأمره بالخير وتحضه عليه وبطانة تأمره بالشر وتحضه عليه فالمعصوم من عصم الله','498','655','0');
INSERT INTO [t6446] VALUES (692,'فقال يا غلام ألا أعلمك كلمات لعل الله أن ينفعك بهن قال قلت بلى فداك أبي وأمي قال احفظ الله يحفظك احفظ الله تجده أمامك تعرف إلى الله في الرخاء يعرفك في الشدة إذا سألت فسل الله وإذا استعنت فاستعن بالله فقد جف القلم بما هو','499','655','0');
INSERT INTO [t6446] VALUES (693,'في حاجة فلم تقض إلا قال لي يا أنس لو قدر شيء لكان','499','655','0');
INSERT INTO [t6446] VALUES (694,'قال حاج آدم موسى فقال موسى يا آدم أنت أبونا وأخرجتنا من الجنة فقال آدم يا موسى أتلومني على أمر قدره الله علي قبل أن يخلقني بأربعين سنة قال فحج آدم موسى عليهما السلام','500','655','0');
INSERT INTO [t6446] VALUES (695,'احتج آدم وموسى عليهما السلام فقال موسى أنت آدم الذي خلقه الله بيده ونفخ فيه من روحه وأسجد له ملائكته وأسكنه جنته ثم أخرجتنا أراه قال من الجنة فقال له آدم أنت موسى الذي اصطفاه الله برسالاته وقربه نجيا وكلمة تكليما','500','655','0');
INSERT INTO [t6446] VALUES (696,'فحج آدم عليه السلام موسى إبطال حكم الظاهر ولا إسقاط العمل الواجب ولكن معناه ترجيح أحد الأمرين وتقديم رتبة العلة على السبب','508','655','0');
INSERT INTO [t6446] VALUES (697,'فحج آدم موسى هذا السبيل وقد ظهر هذا المعنى في قصة آدم عليه السلام قال الله تعالى وإذ قال ربك للملائكة إني جاعل في الأرض خليفة فهذا تصريح بين الملأ من الملائكة أنه خلق آدم للأرض ليكون خليفة فيها ثم قال','509','655','0');
INSERT INTO [t6446] VALUES (698,'على موسى عليه السلام فقال فحج آدم موسى وقال الحسن البصري أخرج الله آدم من الجنة قبل أن يدخله فيها يريد المعنى الذي ذكرناه','510','655','0');
INSERT INTO [t6446] VALUES (699,'يقول إن الله لو عذب أهل سماواته وأهل أرضه لعذبهم غير ظالم ولو رحمهم كانت رحمته خيرا لهم من أعمالهم ولو كان لك جبل أحد أو مثل أحد ذهبا فأنفقته في سبيل الله ما قبله الله منك','511','655','0');
INSERT INTO [t6446] VALUES (700,'فصل','513','655','0');
INSERT INTO [t6446] VALUES (701,'بملازمته فالحسنة ما أصابوا من القتل والسبي والغنائم من الكفار والسيئة ما أصيب منهم من القتل والجرح ونحن إن جعلنا أفعال العباد من الله خلقا ومشيئة وتقديرا فهي من العباد فعل وكسب وبهذا المعنى صحت إضافة الأفعال إلى العباد وتحققت','514','655','0');
INSERT INTO [t6446] VALUES (702,'ما منكم من أحد ينجيه عمله قالوا ولا أنت يا رسول الله قال ولا أنا إلا أن يتغمدني الله برحمته وقالوا إذا جعلنا أفعال العباد مخلوقة لله تعالى لم يمكن تحقيق أفعال العباد وأعمالهم لأن الفعل الواحد لا يتصور من فاعلين','516','655','0');
INSERT INTO [t6446] VALUES (703,'فصل','519','655','0');
INSERT INTO [t6446] VALUES (704,'عمن مات وهو يشربها فأنزل الله تعالى وما كان الله ليضل قوما بعد إذ هداهم وأما قوله تعالى وما منع الناس أن يؤمنوا إذ جاءهم الهدى إلا أن قالوا فالآية خرجت على بيان أن الأعدل والأبلغ أن يبعث إلى كل خلق من جنسهم فأكثر','520','655','0');
INSERT INTO [t6446] VALUES (705,'باب في ذكر الوعد والوعيد','522','655','0');
INSERT INTO [t6446] VALUES (706,'قال من وعده الله على عمله ثوابا فهو منجزه له ومن أوعده على عمله عقابا فهو فيه بالخيار قال وحدثنا أبو الشيخ نا محمد بن حمزة نا أحمد بن الخيل نا الأصمعي قال جاء عمرو بن عبيد إلى عمرو بن العلاء فقال يا','523','655','0');
INSERT INTO [t6446] VALUES (707,'فحين جاءه تائبا عفا عنه وكان في عفوه دليل أن إطلاقه القول كان باستثناء وإن لم يكن ذلك مسموعا منه لأن ذلك لو لم يكن كذلك كان يؤدي إلى الكذب وحاشى رسول الله','525','655','0');
INSERT INTO [t6446] VALUES (708,'فصل','526','655','0');
INSERT INTO [t6446] VALUES (709,'باب في بيان استواء الله عز وجل على العرش','532','655','0');
INSERT INTO [t6446] VALUES (710,'اجعلوها في سجودكم قال أهل السنة الله فوق السماوات لا يعلوه خلق من خلقه ومن الدليل على ذلك أن الخلق يشيرون إلى السماء بأصابعهم ويدعونه ويرفعون إليه أبصارهم','533','655','0');
INSERT INTO [t6446] VALUES (711,'فصل في بيان أن العرش فوق السماوات وأن الله عز وجل فوق العرش','534','655','0');
INSERT INTO [t6446] VALUES (712,'قال لما قضى الله عز وجل الخلق كتب في كتاب عنده فوق العرش إن رحمتي غلبت غضبي','535','655','0');
INSERT INTO [t6446] VALUES (713,'جالس إذ مرت سحابة','535','655','0');
INSERT INTO [t6446] VALUES (714,'هل تدرون ما اسم هذه قالوا نعم هذه السحابة فقال رسول الله','536','655','0');
INSERT INTO [t6446] VALUES (715,'والعنان قالوا والعنان فقال رسول الله','536','655','0');
INSERT INTO [t6446] VALUES (716,'يقول كتب الله مقادير الخلائق قبل أن يخلق السماوات والأرض بخمسين ألف سنة قال وعرشه على الماء','536','655','0');
INSERT INTO [t6446] VALUES (717,'فعقلت ناقتي بالباب ثم دخلت فأتاه نفر من بني تميم فقال اقبلوا البشرى يا بني تميم قالوا قد بشرتنا فأعطنا فجاءه نفر من أهل اليمن فقال اقبلوا البشرى يا أهل اليمن إذ لم يقبلها إخوانكم بنو تميم قالوا يا رسول الله أتيناك لنتفقه في','537','655','0');
INSERT INTO [t6446] VALUES (718,'فصل','538','655','0');
INSERT INTO [t6446] VALUES (719,'الملائكة يتعاقبون فيكم ملائكة بالليل وملائكة بالنهار ويجتمعون في صلاة الفجر وصلاة العصر ثم يعرج إليه الذين فيكم فيسألهم ربهم وهو أعلم بهم كيف تركتم عبادي قالوا تركناهم وهم يصلون وأتيناهم وهم يصلون','539','655','0');
INSERT INTO [t6446] VALUES (720,'بعث إلى سعد ابن معاذ في أمر بني قريظة فجاء سعد على حمار قد كادت رجلاه تثقلان الأرض فلما رآه النبي','539','655','0');
INSERT INTO [t6446] VALUES (721,'إن هؤلاء قد رضوا بحكمك فاحكم فيهم فقال أحكم فيهم أن يقتل مقاتلهم وأن يسبى ذراريهم فقال النبي','539','655','0');
INSERT INTO [t6446] VALUES (722,'بذهب فقسمها فقال رجل كنا نحن أحق بهذا فبلغ ذلك النبي','540','655','0');
INSERT INTO [t6446] VALUES (723,'لله ملائكة سيارة يبتغون مجالس الذكر فإذا وجدوا مجلسا فيه ذكر الله عز وجل قعدوا معهم وحف بعضهم بعضا بأجنحتهم حتى يملئوا ما بينهم وبين السماء الدنيا فإذا تفرقوا صعدوا إلى السماء','540','655','0');
INSERT INTO [t6446] VALUES (724,'فصل','541','655','0');
INSERT INTO [t6446] VALUES (725,'في جنازة رجل من الأنصار قال فانتهينا إلى القبر ولما يلحد قال فجلس رسول الله','542','655','0');
INSERT INTO [t6446] VALUES (726,'حتى يجلس عند رأسه فيقول أيتها النفس الطيبة','542','655','0');
INSERT INTO [t6446] VALUES (727,'فيقولان له وما علمك فيقول قرأت كتاب الله عز وجل فآمنت به وصدقته قال فينادي مناد من السماء أن صدق عبدي فأفرشوه من','543','655','0');
INSERT INTO [t6446] VALUES (728,'حتى يجلس عند رأسه فيقول أيتها النفس الخبيثة أخرجي إلى سخط الله وغضبه قال فتنطوي في جسده قال فلينتزعها كما ينتزع السفود من الصوف المبلول فتقطع منها العروق والعصب قال فيأخذها فإذا أخذها لم يدعوها في يده طرفة عين حتى','544','655','0');
INSERT INTO [t6446] VALUES (729,'لا تفتح لهم أبواب السماء ولا يدخلون الجنة حتى يلج الجمل في سم الخياط قال فيقول الله عز وجل اكتبوا كتابه في سجين في الأرض السفلى قال فيطرح على وجهه طرحا ثم قرأ رسول الله','545','655','0');
INSERT INTO [t6446] VALUES (730,'فانتهينا إلى القبر ولم يلحد ووضعت الجنازة فجلس رسول الله','547','655','0');
INSERT INTO [t6446] VALUES (731,'روحه من جسده رشحا فإذا صارت إلى ملك الموت ابتدرها الملكان فأخذاها منه فحنطاها بحنوط من الجنة وكفناها بكفن من الجنة ثم عرجا بها إلى السماء فيفتح لها أبواب السماء ويستبشر الملائكة بها وتقول لمن هذه الروح الطيبة التي فتحت لها أبواب','547','655','0');
INSERT INTO [t6446] VALUES (732,'منها خلقناكم وفيها نعيدكم ومنها نخرجكم تارة أخرى وإذا وضع المؤمن في لحده فتح له باب عند رجليه فيقال له انظر إلى ما أعد الله لك من الثواب ويفتح له باب عند رأسه إلى النار فيقال له انظر إلى ما صرف الله عنك من العذاب ثم يقال','548','655','0');
INSERT INTO [t6446] VALUES (733,'إذا وضع المؤمن في لحده تقول له الأرض إن كنت لحبيبا إلي وأنت على ظهري فكيف إذ صرت اليوم في بطني سأريك ما أصنع بك فينفسخ له قبره مد بصره','548','655','0');
INSERT INTO [t6446] VALUES (734,'إذا وضع الكافر في قبره أتاه منكر ونكير فأجلساه فيقولان له من ربك فيقول لا أدري فيقولان له لا دريت فيضربانه ضربة فيصير رمادا ثم يعاد فيجلس فيقولان ما دينك فيقول لا أدري فيقولان لا دريت فيضربانه ضربة فيصير رمادا','548','655','0');
INSERT INTO [t6446] VALUES (735,'قال إن الميت تحضره الملائكة فإذا كان الرجل الصالح قال اخرجي أيتها النفس الطيبة كانت في الجسد الطيب اخرجي حميدة وأبشري بروح وريحان ورب غير غضبان قال فلا يزال يقال لها ذلك حتى تخرج ثم يعرج بها إلى السماء ويستفتح لها','549','655','0');
INSERT INTO [t6446] VALUES (736,'فصل','550','655','0');
INSERT INTO [t6446] VALUES (737,'قال لحصين ما تعبد قال عشرة آلهة قال وما هم وأين هم قال تسعة منهم في الأرض وواحد في السماء قال فمن لحاجتك قال الذي في السماء قال فمن لطلبتك قال الذي في السماء قال','550','655','0');
INSERT INTO [t6446] VALUES (738,'قال إن الميت تحضره الملائكة فإذا كان الرجل الصالح قيل اخرجي أيتها النفس الطيبة كانت في جسد طيب أخرجي حميدة وأبشري بروح وريحان ورب غير غضبان قال فيقولون ذلك حتى تخرج فإذا خرجت عرجت إلى السماء فيستفتح لها فيقال من هذا','551','655','0');
INSERT INTO [t6446] VALUES (739,'فأخبرته فعظم علي ذلك فقلت يا رسول الله ألا أعتقها قال ائتني بها فأتيت بها رسول الله','552','655','0');
INSERT INTO [t6446] VALUES (740,'فقلت إن لي جارية كانت ترعى لي غنما فجئتها ففقدت شاة من الغنم فسألتها عنها فقالت أكلها الذئب فأسفت وكنت من بني آدم فلطمت وجهها وعلي رقبة أفأعتقها فقال لها رسول الله','553','655','0');
INSERT INTO [t6446] VALUES (741,'من مسجد الكعبة أنه جاءه ثلاثة نفر قبل أن يوحى إليه وهو نائم في المسجد الحرام فقال أولهم أهو هو فقال وسطهم هو خيرهم وقال آخرهم خذوا خيرهم فكانت تلك فلم يرهم حتى جاءوا ليلة أخرى فيما يرى قلبه والنبي','554','655','0');
INSERT INTO [t6446] VALUES (742,'قالوا وقد بعث إليه قال نعم قالوا فمرحبا به وأهلا ثم','555','655','0');
INSERT INTO [t6446] VALUES (743,'فصل','558','655','0');
INSERT INTO [t6446] VALUES (744,'قال يحيى بن عمار فكل مسلم من أول العصر إلى عصرنا هذا إذا دعا الله سبحانه رفع يديه إلى السماء والمسلمون من عهد النبي','559','655','0');
INSERT INTO [t6446] VALUES (745,'ولا نتفكر في ذلك ولا نسلط عليه الوهم والخاطر والوسواس وتعلم حقا يقينا أن كل ما تصور في همك ووهمك من كيفية أو تشبيه فالله سبحانه بخلافه وغيره نقول هو بذاته على العرش وعلمه محيط بكل شيء','560','655','0');
INSERT INTO [t6446] VALUES (746,'أنهم بينما هم جلوس ليلة مع النبي','562','655','0');
INSERT INTO [t6446] VALUES (747,'إذ رمي بنجم فاستنار فقال لهم رسول الله','562','655','0');
INSERT INTO [t6446] VALUES (748,'إنها لم ترم لموت أحد ولا لحياته ولكن ربنا عز وجل إذا قضى أمرا سبحت حملة العرش ثم سبحه أهل السماء الذين يلونهم حتى يبلغ التسبيح أهل السماء الدنيا ثم يقول الذين يلون','562','655','0');
INSERT INTO [t6446] VALUES (749,'فصل','563','655','0');
INSERT INTO [t6446] VALUES (750,'فقال رسول الله','565','655','0');
INSERT INTO [t6446] VALUES (751,'فقال يا رسول الله علمني الكلمتين اللتيين وعدتني قال قل اللهم ألهمني رشدي وأعذني من شر نفسي','565','655','0');
INSERT INTO [t6446] VALUES (752,'ربنا الله الذي في السماء تقدس اسمك أمرك في السماء والأرض اغفر لنا حوبنا وخطايانا أنت رب الطيبين فأنزل شفاء من شفائك ورحمة من رحمتك على هذا الوجع فيبرأ وأمره أن يرقيه بها فرقاه بها فبرأ','566','655','0');
INSERT INTO [t6446] VALUES (753,'قال بعض علماء السنة حكى ابن فورك في مجرد قوله إن استواء الله عز وجل على العرش عند أبي الحسن من صفات الأفعال وكذلك المجيء في قوله عز وجل وجاء ربك والملك صفا صفا وقوله','566','655','0');
INSERT INTO [t6446] VALUES (754,'أنه','569','655','0');
INSERT INTO [t6446] VALUES (755,'بإيمانها حين قالت إن الله في السماء وتحكم الجهمية بكفر من يقول ذلك','569','655','0');
INSERT INTO [t6446] VALUES (756,'قال لنا الإمام أبو المظفر السمعاني رحمه الله قالوا جعلتم','569','655','0');
INSERT INTO [t6446] VALUES (757,'وقوله نفس الحجة فكيف يكون هذا قبول قول الغير من غير حجة فإن المسلمين لهم الدلائل السمعية على نبوة رسول الله','570','655','0');
INSERT INTO [t6446] VALUES (758,'ولا من الصحابة رضي الله عنه وكذلك من التابعين بعدهم وكيف يجوز أن يخفى عليهم أول الفرائض وهم صدور هذه الأمة والسفراء بيننا وبين رسول الله','572','655','0');
INSERT INTO [t6446] VALUES (759,'كان يدعو الكفار إلى الإسلام والشهادتين','572','655','0');
INSERT INTO [t6446] VALUES (760,'لمعاذ حين بعثه إلى اليمن ادعهم إلى شهادة أن لا إله إلا الله','573','655','0');
INSERT INTO [t6446] VALUES (761,'أمرت أن أقاتل الناس حتى يقولوا لا إله إلا الله ومثل هذا كثير ولم يرو أنه دعاهم إلى النظر والاستدلال وإنما يكون حكم الكافر في الشرع أنه يدعى إلى الإسلام فإن أبى وسأل النظرة والإمهال لا يجاب إلى ذلك ولكنه إما أن يسلم أو','573','655','0');
INSERT INTO [t6446] VALUES (762,'لا يدخل الجنة إلا نفس مسلمة وهذا حديث ثابت لا شك فيه ومما يدل على صحة ما ذهبنا إليه من أن الدين طريقه الاتباع أنا إذا سلكنا طريق الإنصاف وطرحنا المكابرات من جانب فلا بد من الانقياد لما قلناه لأن المقصود في الابتداء إذا كان','575','655','0');
INSERT INTO [t6446] VALUES (763,'فصل','576','655','0');
INSERT INTO [t6446] VALUES (764,'وهو محتبيء ببردة لها طرائق حمر فقلت السلام عليك يا رسول الله قال وعليك قلت إنا معشر أهل البادية قوم منا الجفاء فعلمني كلمات ينفعني الله بهن قال أدن ثلاثا فقال أعد علي فقلت إنا معشر أهل البادية قوم منا','577','655','0');
INSERT INTO [t6446] VALUES (765,'وينتهون فيه إليه ويمرون الخبر الصحيح الوارد بذكره على ظاهره ويكلون علمه إلى الله عز وجل وكذلك يثبتون ما أنزله عز اسمه في كتابه من ذكر المجيء والإتيان المذكورين في قوله عز وجل هل ينظرون إلا أن يأتيهم الله في ظلل من الغمام','578','655','0');
INSERT INTO [t6446] VALUES (766,'أقر به أهل السنة وقبلوا الخير وأثبتوا النزول على ما قاله رسول الله','580','655','0');
INSERT INTO [t6446] VALUES (767,'من مكة فجعلوا يستأذنون النبي','582','655','0');
INSERT INTO [t6446] VALUES (768,'ما بال شق الشجرة الذي يلي رسول الله','582','655','0');
INSERT INTO [t6446] VALUES (769,'فحمد الله وأثنى عليه وكان إذا حلف قال والذي نفسي بيده أشهد عند الله ما منكم من أحد يؤمن بالله واليوم الآخر ثم يسدد إلا سلك به في الجنة ولقد وعدني ربي أن يدخل من أمتي الجنة سبعين ألفا بغير حساب ولا عذاب وإني لأرجو أن لا يدخلوها','582','655','0');
INSERT INTO [t6446] VALUES (770,'فصل في بيان أن التكليف إيقاع الكلفة على المكلف والكلفة المشقة والتكليف نوعان تكليف ما هو معهود ممكن وتكليف ما هو غير معهود ولا ممكن فأما الذي لا يمكن ولا يعهد مثله فكتكليف المكلف أن يرد الشيء الماضي كرد أمس الذاهب وكتكليف','583','655','0');
INSERT INTO [t6446] VALUES (771,'فصل','590','655','0');
INSERT INTO [t6446] VALUES (772,'قال ما أحد أصبر على أذى يسمعه من الله إنهم يدعون له ولدا وهو يرزقهم ويعافيهم','592','655','0');
INSERT INTO [t6446] VALUES (773,'في غزاة فجلعنا لا نصعد شرفا ولا نهبط واديا إلا رفعنا أصواتنا بالتكبير فدنا منا رسول الله','592','655','0');
INSERT INTO [t6446] VALUES (774,'فصل','593','655','0');
INSERT INTO [t6446] VALUES (775,'فصل','595','655','0');
INSERT INTO [t6446] VALUES (776,'يقول الخوارج كلاب أهل النار','596','655','0');
INSERT INTO [t6446] VALUES (777,'يذكر هؤلاء الخوارج قال سمعته وأشار نحو المشرق يخرج منه قوم يقرءون القرآن بألسنتهم لا يعدو تراقيهم يمرقون من الدين كما يمرق السهم من الرمية','596','655','0');
INSERT INTO [t6446] VALUES (778,'فصل','597','655','0');
INSERT INTO [t6446] VALUES (779,'في خلق الروح قبل الجسد لأنه لم يوافق نظرهم وأصولهم','598','655','0');
INSERT INTO [t6446] VALUES (780,'في خلق العقل قبل الخلق وإنما ردوا هذه الأخبار لأن العقل عندهم عرض والعرض لا يقوم بنفسه فردوا الأخبار بهذا الطريق وكذلك ردوا الخبر الذي روي عن النبي','599','655','0');
INSERT INTO [t6446] VALUES (781,'ومثل هذا كثير ولهذا قال بعض السلف إن أهل الكلام أعداء الدين لأن اعتمادهم على حدسهم وظنونهم وما يؤدي إليه نظرهم وفكرهم ثم يعرضون عليه الأحاديث فما وافقه قبلوه وما خالفه ردوه','599','655','0');
INSERT INTO [t6446] VALUES (782,'في خطبة الوداع وفي مقامات له شتى وبحضرة عامة أصحابه ألا هل بلغت وكان فيما أنزل إليه وأمر بتبليغه أمر التوحيد وبيانه بطريقه فلم يترك النبي','600','655','0');
INSERT INTO [t6446] VALUES (783,'لم يدعهم في هذه الأمور إلى الاستدلال بالأعراض والجواهر وذكر ماهيتها ولا يمكن لأحد من الناس أن يروي في ذلك عنه ولا عن أحد من الصحابة رضى الله عنهم من هذا النمط حرفا واحدا فما فوقه لا في طريق تواتر ولا آحاد فعلمنا أنهم ذهبوا','601','655','0');
INSERT INTO [t6446] VALUES (784,'فصل','603','655','0');
INSERT INTO [t6446] VALUES (785,'قسم قسما فأعطى ناسا ومنع الآخرين فقلت يا رسول الله أعطيت فلانا وفلانا ومنعت فلانا وهو مؤمن فقال لا تقل مؤمن قل مسلم قال ابن شهاب قالت الأعراب آمنا قل لم تؤمنوا ولكن قولوا أسلمنا وفي رواية قال الزهري نرى','604','655','0');
INSERT INTO [t6446] VALUES (786,'إذا دخل المقابر قال السلام على أهل الديار من المؤمنين والمسلمين قال أبو الشيخ ذكر حدود الإيمان وأعلاها وأدناها وحقوقها وشعبها','605','655','0');
INSERT INTO [t6446] VALUES (787,'الإيمان بضع وسبعون بابا أعلاها شهادة أن لا إله إلا الله وأدناها رفع الأذى عن الطريق','606','655','0');
INSERT INTO [t6446] VALUES (788,'بلغنا','607','655','0');
INSERT INTO [t6446] VALUES (789,'قال الإيمان بضع وستون أو سبعون جزءا أولها شهادة أن لا إله إلا الله وأدناها إماطة الأذى عن الطريق والحياء شعبة من الإيمان وقال الله عز وجل شرع لكم من الدين ما وصى به نوحا والذي أوحينا إليك وما وصينا به إبراهيم وموسى','607','655','0');
INSERT INTO [t6446] VALUES (790,'بشهادة أن لا إله إلا الله فلما صدق به المؤمنون زادهم الصلاة فلما صدقوا به زادهم الزكاة فلما صدقوا بها زادهم الصيام فلما صدقوا به زادهم الحج فلما صدقوا به زادهم الجهاد ثم أكمل لهم دينهم فقال اليوم أكملت لكم دينكم','608','655','0');
INSERT INTO [t6446] VALUES (791,'فصل','610','655','0');
INSERT INTO [t6446] VALUES (792,'من الأحاديث','610','655','0');
INSERT INTO [t6446] VALUES (793,'وأما محض الإيمان','614','655','0');
INSERT INTO [t6446] VALUES (794,'وأما صدق الإيمان وبره','614','655','0');
INSERT INTO [t6446] VALUES (795,'وأما لباسه','614','655','0');
INSERT INTO [t6446] VALUES (796,'وأما حلاوته','614','655','0');
INSERT INTO [t6446] VALUES (797,'قال ثلاث من كن فيه وجد حلاوة الإيمان أن يكون الله ورسوله أحب إليه مما سواهما وأن يحب العبد لا يحبه إلا لله وأن يكره أن يرجع في الكفر بعد إذ أنقذه الله منه كما يكره أن يلقى في النار','614','655','0');
INSERT INTO [t6446] VALUES (798,'قال الطهور شطر الإيمان وفي رواية إسباغ الوضوء شطر الإيمان','614','655','0');
INSERT INTO [t6446] VALUES (799,'وأما نصف الإيمان','615','655','0');
INSERT INTO [t6446] VALUES (800,'فصل فيما يفسد الإيمان','615','655','0');
INSERT INTO [t6446] VALUES (801,'إن سوء الخلق يفسد إيمان العبد كما يفسد الصبر الطعام','616','655','0');
INSERT INTO [t6446] VALUES (802,'يقول ليس المؤمن الذي يشبع وجاره جائع إلى جانبه','616','655','0');
INSERT INTO [t6446] VALUES (803,'لا يجتمع الإيمان والكفر في قلب أبدا ولا يجتمع الصدق والكذب أبدا ولا يجتمع الخيانة والأمانة جميعا','616','655','0');
INSERT INTO [t6446] VALUES (804,'لا إيمان لمن لا أمانة له ولا دين لمن لا عهد له والذي نفس محمد بيده لا يستقيم إيمان عبد حتى يستقيم لسانه ولا يستقيم لسانه حتى يستقيم قلبه ولا يدخل الجنة من خاف جاره بوائقه قيل يا رسول الله ما بوائقه قال غشمه وظلمه وأيما رجل','617','655','0');
INSERT INTO [t6446] VALUES (805,'يطبع المؤمن على كل خلق ليس الخيانة والكذب','617','655','0');
INSERT INTO [t6446] VALUES (806,'ليس المؤمن بالطعان ولا اللعان ولا الفاحش ولا البذيء أخبرناه أحمد بن عبد الغفار أنا أبو بكر بن أبي نصر نا أبو الشيخ نا إبراهيم بن شريك الأسدي نا أحمد ابن يونس نا أبو بكر بن عياش عن الحسن بن عمرو عن محمد ابن عبد','618','655','0');
INSERT INTO [t6446] VALUES (807,'أنه قال في خطبة الوداع ألا أخبركم بالمؤمن قالوا بلى يا رسول الله قال من أمنه الناس على أموالهم وأنفسهم والمسلم من سلم الناس من لسانه ويده والمجاهد من جاهد نفسه في طاعة الله والمهاجر من هجر الخطايا والذنوب','618','655','0');
INSERT INTO [t6446] VALUES (808,'فصل في بيان خطأ من أنكر أن يكون في المصحف القرآن','619','655','0');
INSERT INTO [t6446] VALUES (809,'أنه نهى أن يسافر بالقرآن إلى أرض العدو مخافة أن يناله العدو فسمى المصحف قرآنا ولأن الله قال ولو نزلنا عليك كتابا في قرطاس فلمسوه بأيديهم فأبان أن المكتوب في القراطيس وعلى الجدار والبساط وغيرها قرآن يقع اللمس عليه','622','655','0');
INSERT INTO [t6446] VALUES (810,'فصل في بيان أن المتلو والمكتوب والمسوع من القرآن كلام الله عز وجل الذي نزل به جبريل عليه السلام من عند الله عز وجل على قلب محمد','624','655','0');
INSERT INTO [t6446] VALUES (811,'قال الله تعالى الحمد لله الذي أنزل على عبده الكتاب وقال تعالى هو الذي أنزل عليك الكتاب منه آيات محكمات وقال وكذلك أنزلناه قرآنا عربيا وقال فأجره حتى يسمع كلام الله','624','655','0');
INSERT INTO [t6446] VALUES (812,'يعرض نفسه بالموقف ويقول إن قريشا قد منعوني أن أبلغ كلام ربي','625','655','0');
INSERT INTO [t6446] VALUES (813,'إذا عطس رجل من القوم إلى جنبي فقلت يرحمك الله فرماني القوم بأبصارهم فقلت واثكل أمياه مالي أراكم تنظرون إلي وأنا اصلي فجعلوا يضربون بأيديهم على أفخاذهم يصمتوني فلما رأيت ذلك سكت فلما قضى رسول الله','625','655','0');
INSERT INTO [t6446] VALUES (814,'يسري على كتاب الله في ليلة فلا يبقى في الأرض منه آية وتبقى طوائف من الناس الشيخ الكبير والعجوز الكبيرة يقولون أدركنا آباءنا على هذه الكلمة لا إله إلا الله فنحن نقولها','626','655','0');
INSERT INTO [t6446] VALUES (815,'قال إني تارك فيكم الثقلين أولهما كتاب الله فيه الهدى والنور فحث على كتاب الله ورغب فيه','626','655','0');
INSERT INTO [t6446] VALUES (816,'فقال إن هذا القرآن سبب طرفه بيد الله وطرفه بأيديكم فتمسكوا به فإنكم لن تضلوا ما إن تمسكتم به','627','655','0');
INSERT INTO [t6446] VALUES (817,'فصل في الدليل على أن رسول الله','628','655','0');
INSERT INTO [t6446] VALUES (818,'والدليل على ذلك قول الله عز وجل يأيها الرسول بلغ ما أنزل إليك من ربك فسماه رسول قبل أن يبلغ الرسالة والله تعالى لا يقول المجاز','628','655','0');
INSERT INTO [t6446] VALUES (819,'كنت رسولا وآدم بين الروح والجسد ويؤيد هذا قوله عز وجل حكاية عن عيسى عليه السلام إني رسول الله إليكم مصدقا لما بين يدي من التوراة ومبشرا برسول يأتي من بعدي اسمه أحمد فسماه الله تعالى رسولا من قبل أن وجد وقبل أن ولد','628','655','0');
INSERT INTO [t6446] VALUES (820,'عند الله وفي علم الله قبل أن وجد حقيقة وكانت الرسالة موجودة معه حقيقة كان رسولا حاملا للرسالة وما لم يخرج عن عهدة الرسالة بتبليغها بتمامها لم يزل عنه اسم الرسالة والدليل على ذلك قوله عز وجل كما أرسلنا فيكم رسولا منكم يتلوا','629','655','0');
INSERT INTO [t6446] VALUES (821,'قل يا أيها الناس إني رسول الله إليكم جميعا وهذا خطاب لكافة الناس من كان في عصر النبي','629','655','0');
INSERT INTO [t6446] VALUES (822,'من لدن بعثه الله إلى يوم القيامة رسول حقيقة','629','655','0');
INSERT INTO [t6446] VALUES (823,'ويدل عليه أيضا أن أداء الرسالة يكون مرة بالكلام والخطاب ومرة بالإخبار والكتاب يوصله الرسول إلى من أرسل إليه من غير أن يتكلم بها أو يخاطب المرسل إليه بالرسالة كما يفعله البريد والفيج ومثال ذلك من أرسل رسولا بكتاب إلى قوم وأمره','630','655','0');
INSERT INTO [t6446] VALUES (824,'فصل في دلائل نبوة النبي','631','655','0');
INSERT INTO [t6446] VALUES (825,'كان يخطب إلى جذع فلما اتخذ المنبر تحول إليه فحن الجذع فأتى النبي','632','655','0');
INSERT INTO [t6446] VALUES (826,'كان يخطب إلى جذع قبل أن يتخذ المنبر فلما اتخذ المنبر تحول إليه فحن الجذع فاحتضنه فسكن فقال لو لم أحتضنه لحن إلى يوم القيام قال الشيخ هذا إسناد صحيح على شرط مسلم','632','655','0');
INSERT INTO [t6446] VALUES (827,'كان يقوم يوم الجمعة فيسند ظهره إلى جذع منصوب في','632','655','0');
INSERT INTO [t6446] VALUES (828,'على المنبر خار الجذع كخوار الثور حتى ارتج المسجد لخواره حزنا على النبي','633','655','0');
INSERT INTO [t6446] VALUES (829,'من المنبر فالتزمه وهو يخور فلما التزمه رسول الله','633','655','0');
INSERT INTO [t6446] VALUES (830,'رواية جابر بن عبد الله رضي الله عنه','633','655','0');
INSERT INTO [t6446] VALUES (831,'يقوم إلى جذع نخلة فيخطب قبل أن يصنع المنبر فلما وضع المنبر صعده فحن الجذع حتى سمعنا حنينه فأتاه رسول الله','633','655','0');
INSERT INTO [t6446] VALUES (832,'رواية أبي سعد الخدري رضي الله عنه','633','655','0');
INSERT INTO [t6446] VALUES (833,'يخطب إلى جذع فأتاه رجل رومي فقال أصنع لك منبرا تخطب عليه فصنع له منبره هذا الذي ترون قال فلما قام عليه يخطب حن الجذع حنين الناقة إلى ولدها فنزل إليه رسول الله','634','655','0');
INSERT INTO [t6446] VALUES (834,'فصل في حديث خروج الماء من بين أصابع رسول الله','634','655','0');
INSERT INTO [t6446] VALUES (835,'وليس معنا ماء فقال لنا رسول الله','634','655','0');
INSERT INTO [t6446] VALUES (836,'بركات وأنتم ترونها تخويفا لقد رأيتنا مع رسول الله','635','655','0');
INSERT INTO [t6446] VALUES (837,'رواية أنس رضي الله عنه','636','655','0');
INSERT INTO [t6446] VALUES (838,'أتى بإناء فيه ماء ما يغمر أصابعه أو لا يكاد يغمر أصابعه شك سعيد فجعل الناس يتوضأون وجعل الماء ينبع من بين أصابعه قال قلنا لأنس كم كنتم قال زهاء ثلثمائة','636','655','0');
INSERT INTO [t6446] VALUES (839,'فدعا بتور فيه ماء فوضع كفه فيه فقال خذوا بسم الله','636','655','0');
INSERT INTO [t6446] VALUES (840,'ضعيفا أعرف فيه الجوع فهل عندك من شيء قالت نعم فأخرجت أقراصا من شعير ثم أخرجت خمارا لها فلفت الخبز ببعضه ثم دسته تحت ثوبي وردتني ببعضه ثم أرسلتني إلى رسول الله','638','655','0');
INSERT INTO [t6446] VALUES (841,'في المسجد ومعه الناس فقمت عليهم فقال لي رسول الله','638','655','0');
INSERT INTO [t6446] VALUES (842,'لمن معه قوموا قال فانطلق وانطلقت بين أيديهم حتى جئت أبا طلحة فأخبرته فقال أبو طلحة يا أم سليم قد جاء رسول الله بالناس وليس عندنا من الطعام ما نطعمهم قالت الله ورسوله أعلم قال فانطلق أبو طلحة حتى يلقى رسول الله','638','655','0');
INSERT INTO [t6446] VALUES (843,'وأبو طلحة معه حتى دخلا فقال رسول الله','638','655','0');
INSERT INTO [t6446] VALUES (844,'ففت وعصرت أم سليم يعني عكة لها ثم قال رسول الله','638','655','0');
INSERT INTO [t6446] VALUES (845,'فإذا أبو ذر فسلمت وجلست إليه فذكر عثمان فقال لا أقول أبدا إلا خيرا ثلاث مرات لشيء رأيته من رسول الله','639','655','0');
INSERT INTO [t6446] VALUES (846,'لأتعلم منه فمر بي فاتبعته حتى انتهى إلى موضع قد سماه','639','655','0');
INSERT INTO [t6446] VALUES (847,'سبع أو تسع حصيات فسبحن حتى سمعت لهن حنينا كحنين النحل ثم وضعهن فخرسن ثم أخذهن فوضعهن في يد أبي بكر فسبحن حتى سمعت لهن حنينا كحنين النحل ثم وضعهن فخرسن ثم تناولهن فوضعهن في يد عمر فسبحن حتى سمعت لهن حنينا كحنين النحل ثم وضعهن','640','655','0');
INSERT INTO [t6446] VALUES (848,'كان الكتاب الأول ينزل من باب واحد على حرف واحد ونزل القرآن من سبعة أبواب على سبعة أحرف زاجر وآمر وحلال وحرام ومحكم ومتشابه نهيتم عنه واعتبروا بأمثاله واعملوا بمحكمة وآمنوا بمتشابهه وقولا آمنا به كل من عند ربنا','642','655','0');
INSERT INTO [t6446] VALUES (849,'مجلسا ما جلست مجلسا أغبط عندي منه خرج رسول الله','643','655','0');
INSERT INTO [t6446] VALUES (850,'عن الساعة فأمسك عن الجواب حتى نزلت يسألونك عن الساعة أيان مرساها فيم أنت من ذكراها وقال يسألونك كأنك حفي عنها قل إنما علمها عند الله وقال إنما علمها عند ربي','644','655','0');
INSERT INTO [t6446] VALUES (851,'فصل','644','655','0');
INSERT INTO [t6446] VALUES (852,'لا يزالون يسألون حتى يقال لأحدكم هذا الله خلقنا فمن خلق الله','644','655','0');
INSERT INTO [t6446] VALUES (853,'يقول إن الله عز وجل قال كذبني ابن آدم ولم ينبغ له أن يكذبني وشتمني ابن آدم وما ينبغ له أن يشتمني فأما تكذيبه إياي فقوله لن يعيدني كما بدأني وليس أول الخلق أهون علي من إعادته وأما شتمه إياي فقوله اتخذ الله ولدا وأنا الله','645','655','0');
INSERT INTO [t6446] VALUES (854,'فصل','647','655','0');
INSERT INTO [t6446] VALUES (855,'يقول الله تبارك وتعالى العظمة إزاري والكبرياء ردائي فمن نازعني واحدا منهما قذفته في النار','648','655','0');
INSERT INTO [t6446] VALUES (856,'قال من قرأ حرفا من كتاب الله كتب له عشر حسنات أما إني لا أقول ألم حرف ولكن ألف ولام وميم ثلاثون حسنة','649','655','0');
INSERT INTO [t6446] VALUES (857,'قال من قرأ حرفا من القرآن فله به حسنة والحسنة بعشر أمثالها لا أقول ألم حرف ولكن ألف حرف ولام حرف وميم حرف','649','655','0');
INSERT INTO [t6446] VALUES (858,'خيركم من تعلم القرآن وعلمه وفضل القرآن على سائر الكلام كفضل الله على خلقه وذلك أنه منه','649','655','0');
INSERT INTO [t6446] VALUES (859,'يعوذ الحسن والحسين ويقول أعيذكما بكلمات الله التامة من كل شيطان وهامة ومن كل عين لامة وقال كان أبي إبراهيم عليه السلام يعوذ به ابنيه إسماعيل وإسحاق','650','655','0');
INSERT INTO [t6446] VALUES (860,'علم جويرية بنت الحارث فقال قولي سبحان الله عدد ما خلق من شيء سبحان الله زنة عرشه سبحان الله مداد كلماته وقالت الأشعرية كلام الله واحد وقالوا ما بين اللوحين حكاية عن كلام الله وعبارة عنه','650','655','0');
INSERT INTO [t6446] VALUES (861,'إن الله عز وجل ناجى موسى عليه السلام بمائة ألف وأربعين ألف كلمة كلها وصايا','651','655','0');
INSERT INTO [t6446] VALUES (862,'مما جاء به جبريل الأمين حكاية القرآن فجهمهم أبو عبد الله أحمد بن محمد ابن حنبل وتابعه على تجهيمهم علماء الأمصار طرا أجمعون لا خلاف بين أهل الأثر في ذلك','654','655','0');
INSERT INTO [t6446] VALUES (863,'أجمع المسلمون أن القرآن كلام الله وإذا صح أنه كلام الله صح أنه صفة لله تعالى وأنه عز وجل موصوف به وهذه الصفة لازمة لذاته تقول العرب زيد متكلم فالمتكلم صفة له إلا أن حقيقة هذه الصفة الكلام وإذا كان كذلك كان القرآن كلام','654','655','0');
INSERT INTO [t6446] VALUES (864,'فصل','660','655','0');
INSERT INTO [t6446] VALUES (865,'أنزل عليه ولأنه قال ما يأتيهم من ذكر ومن للتبعيض وهذا يدل على أن ثم ذكرا قديما وعندهم ليس ثم ذكر قديم ولأن الله تعالى قال إنما قولنا لشيء إذا أردناه أن نقول له كن فيكون فأخبر تعالى أنه كون الأشياء بكن فلو كانت كن','660','655','0');
INSERT INTO [t6446] VALUES (866,'قوله تعالى إنا أنزلناه قرآنا عربيا','661','655','0');
INSERT INTO [t6446] VALUES (867,'نجوما في ثلاث وعشرين سنة قال العلماء لم يبعث الله عز وجل نبيا إلا ومعه معجزة تدل على صدق قوله من جنس ما قومه عليه فعيسى','661','655','0');
INSERT INTO [t6446] VALUES (868,'بعث في زمان السحرة والكهنة وكانت العصا معجزته ابتلعت حبالهم وعصيهم ولم تطل ولم تقصر ولم يكبر بطنها فلما عجزوا عن ذلك مع معرفتهم بالسحر استدلوا على أنه رسول الله ونبيا','662','655','0');
INSERT INTO [t6446] VALUES (869,'مرسل من عند الله والقرآن معجزته السابقة الأولة والنبوة ثبتت بالمعجزة الأولة والمعجزة الثانية والثالثة كانت تأكيدا للأوله وروي عن عطية بن قيس أنه قال ما تكلم العباد بكلام أحب إلى الله تعالى من كلامه ولا رفع إليه كلام أحب إليه','662','655','0');
INSERT INTO [t6446] VALUES (870,'آية فأثبتها في مصحفي فلما كان الليل جئت حتى أقرأها فلم أقدر على قراءتها فعدت إلى المصحف فوجدت مكان الآية أبيض فأخبرت بذلك النبي','663','655','0');
INSERT INTO [t6446] VALUES (871,'ليس بكلام الله لم تحصل الاستجارة لهم وقال تعالى يريدون أن يبدلوا كلام الله ولا يخلو إما أن يكون كلاما وصل إليهم أو كلاما لم يصل إليهم ولا يجوز أن يكون كلاما لم يصل إليهم لأن ما لا يصل إليهم لا يتأتى تبديله فثبت أنه وصل','664','655','0');
INSERT INTO [t6446] VALUES (872,'ولو كانت كلام البشر لم يتوعدهم بالنار','665','655','0');
INSERT INTO [t6446] VALUES (873,'أعطوا أعينكم حظها من العبادة قيل يا رسول الله وما حظها من العبادة قال النظر في المصحف ولأنا إذا كتبنا القرآن فالمنظور إليه الحرف وهو قائم بمحل وهو الحبر فإذا محي الحبر لم ينظر إلى الحرف لزوال المحل الذي قام به','666','655','0');
INSERT INTO [t6446] VALUES (874,'القلب الذي ليس فيه شيء من القرآن كالبيت الخرب','667','655','0');
INSERT INTO [t6446] VALUES (875,'تعاهدوا القرآن فلهو أشد تفصيا من صدور الرجال من النعم من عقلها','667','655','0');
INSERT INTO [t6446] VALUES (876,'أنه قال لا يعذب الله قلبا وعى القرآن','667','655','0');
INSERT INTO [t6446] VALUES (877,'قال البخاري باب كلام الرب مع الأنبياء وغيرهم يوم القيامة','667','655','0');
INSERT INTO [t6446] VALUES (878,'قال أني لأعلم آخر أهل النار خروجا من النار وآخر أهل الجنة دخولا الجنة رجل يخرج من النار حبوا فيقول الله تعالى له اذهب فادخل الجنة فيأتيها فيخيل إليه أنها ملأي فيقول الله عز وجل اذهب فادخل الجنة فإن لك مثل الدنيا وعشرة','668','655','0');
INSERT INTO [t6446] VALUES (879,'ما منكم من أحد إلا سيكلمه الله عز وجل ليس بينه وبينه ترجمان فينظر عن يمينه فلا يرى إلا ما قدم وينظر عن شماله فلا يرى إلا ما قدم وينظر بين يديه فيرى النار فمن استطاع أن يقي وجهه النار ولو بشق تمرة يعني فليفعل وفي رواية','668','655','0');
INSERT INTO [t6446] VALUES (880,'في الشفاعة فقال أنس حدثنا محمد','669','655','0');
INSERT INTO [t6446] VALUES (881,'قال فاؤتى فأقول أنا لها فأنطلق فأستأذن على ربي فيؤذن لي عليه فأقوم بين يديه مقاما ويلهمني الله محامد لا أقدر عليها الآن فأحمده بتلك المحامد ثم أخر له ساجدا فيقال لي يا محمد ارفع رأسك وقل يسمع لك واشفع تشفع وسل تعطه','670','655','0');
INSERT INTO [t6446] VALUES (882,'يذكر في النجوى قال سمعت النبي','672','655','0');
INSERT INTO [t6446] VALUES (883,'إن الله تعالى إذا أحب عبدا نادى جبريل إن الله قد أحب','672','655','0');
INSERT INTO [t6446] VALUES (884,'قال يتعاقبون فيكم ملائكة بالليل وملائكة بالنهار ويجتمعون في صلاة العصر وصلاة الفجر ثم يعرج الذين باتوا فيكم فيسألهم وهو أعلم كيف تركتم عبادي فيقولون تركناهم وهم يصلون وأتيناهم وهم يصلون','673','655','0');
INSERT INTO [t6446] VALUES (885,'قال الله تعالى يؤذيني ابن آدم يسب الدهر وأنا الدهر بيدي الأمر أقلب الليل والنهار','673','655','0');
INSERT INTO [t6446] VALUES (886,'قال يقول الله تعالى الصوم لي وأنا أجزي به','673','655','0');
INSERT INTO [t6446] VALUES (887,'قال الله تعالى أنفق أنفق عليك','673','655','0');
INSERT INTO [t6446] VALUES (888,'ينزل ربنا عز وجل كل ليلة إلى السماء الدنيا حين يبقى ثلث الليل الآخر فيقول من يدعوني فأستجيب له','674','655','0');
INSERT INTO [t6446] VALUES (889,'ووعدك الحق وقولك الحق','674','655','0');
INSERT INTO [t6446] VALUES (890,'بينما أيوب يغتسل عريانا خر عليه رجل جراد من ذهب فجعل يحثي في ثوبه فناداه ربه يا أيوب ألم أكن أغنيك عما ترى قال بلى يا رب ولا غني بي عن بركتك','674','655','0');
INSERT INTO [t6446] VALUES (891,'قال خلق الله الخلق فلما فرغ منه قامت الرحم قال مه قالت هذا مقام العائذ بك من القطيعة قال ألا ترضين أن أصل من وصلك وأقطع من قطعك قالت بلى يا رب قال فذلك لك','674','655','0');
INSERT INTO [t6446] VALUES (892,'إذا نزل عليه جبريل بالوحي يحرك به لسانه فيشتد ذلك عليه','676','655','0');
INSERT INTO [t6446] VALUES (893,'مختف بمكة فكان إذا صلى بأصحابه رفع صوته بالقرآن فإذا سمع المشركون سبوا القرآن ومن أنزله ومن جاء به','676','655','0');
INSERT INTO [t6446] VALUES (894,'فصل في كلام الرب مع أهل الجنة','677','655','0');
INSERT INTO [t6446] VALUES (895,'قال إن الله يقول لأهل الجنة يا أهل الجنة فيقولون لبيك ربنا وسعديك والخير في يديك فيقول هل رضيتم فيقولون وما لنا لا نرضى يا رب وقد أعطيتنا ما لم تعط أحدا من خلقك فيقول ألا أعطيكم أفضل من ذلك فيقولون يا رب وأي شيء','677','655','0');
INSERT INTO [t6446] VALUES (896,'قال يوما وهو يحدث وفيمن عنده رجل من أهل البادية إن رجلا من أهل الجنة استأذن ربه في الزرع فقال له ربه أولست فيما شئت قال بلى ولكني أحب أن أزرع قال فيقول الله ازرع قال فيبذر حبه نباته واستواؤه واستحصاده ويكون أمثال','678','655','0');
INSERT INTO [t6446] VALUES (897,'فصل','678','655','0');
INSERT INTO [t6446] VALUES (898,'ورواه الثقات والأئمة وأسندوه خلفهم عن سلفهم إلى رسول الله','679','655','0');
INSERT INTO [t6446] VALUES (899,'ومناقب أصحابه وأخبار الأنبياء المتقدمين عليهم السلام وكذلك أخبار الرقائق والعظات وما أشبه ذلك مما يكثر عده وذكره وهذه الأشياء كلها علمية لا عملية وإنما تروى لوقوع علم السامع بها فإذا قلنا إن خبر الواحد لا يجوز أن يوجب العلم','681','655','0');
INSERT INTO [t6446] VALUES (900,'أدى هذا الدين إلى الواحد فالواحد من أصحابه ليؤدوه إلى الأمة ونقلوا عنه فإذا لم يقبل قول الراوي لأنه واحد رجع هذا العيب إلى المؤدي نعوذ بالله من هذا القول الشنيع والاعتقاد القبيح ويدل عليه أن الأمر مشتهر في أن النبي','682','655','0');
INSERT INTO [t6446] VALUES (901,'بعث إلى الناس كافة وكثير من الأنبياء بعثوا إلى قوم دون قوم وإنما قصد بإرسال الرسل إلى هؤلاء الملوك والكتاب إليهم بث الدعوة في جميع الممالك ودعا الناس عامة إلى دينه على حسب ما أمره الله بذلك فلو لم يقع العلم بخبر الواحد في أمور','683','655','0');
INSERT INTO [t6446] VALUES (902,'بإرسال الواحد من أصحابه منها','683','655','0');
INSERT INTO [t6446] VALUES (903,'بعث عليا لينادي في موسم الحج بمنى ألا لا يحجن بعد العام مشرك ولا يطوفن بالبيت عريان ومن كان بينه وبين رسول الله','683','655','0');
INSERT INTO [t6446] VALUES (904,'مبسوط العذر في قتالهم وقتلهم','683','655','0');
INSERT INTO [t6446] VALUES (905,'وسيرته لم يخف عليه ما ذكرنا وما يرد هذا إلا معاند مكابر ولو أنك وضعت في قلبك أنك سمعت الصديق أو الفاروق أو غيرهما من وجوه الصحابة رضى الله عنهم يروي لك حديثا عن النبي','685','655','0');
INSERT INTO [t6446] VALUES (906,'ولا فعلوا هم بأنفسهم ذلك وقد نقلوا هذا الدين إلينا كما نقل إليهم وأدوا على ما أدي إليهم وكانوا في صدق العناية والاهتمام بهذا الشأن بما يجل عن الوصف ويقصر دونه الذكر وإذا وقف المرء على هذا من شأنهم وعرف حالهم وخبر صدقهم وورعهم','686','655','0');
INSERT INTO [t6446] VALUES (907,'قال حين سئل عن الفرقة الناجية قال ما أنا عليه وأصحابي يعني من كان على ما أنا عليه وأصحابي فلا بد من تعرف ما كان عليه رسول الله','687','655','0');
INSERT INTO [t6446] VALUES (908,'وأصحابه إلى أهل النقل والرواية لأنهم عنوا بهذا الشأن واستغلوا بحفظه','687','655','0');
INSERT INTO [t6446] VALUES (909,'ولم يقف أحد على سنته وطريقته فإن قال قائل إن أهل الفقه مجمعون على قول الفقهاء وطريق كل واحد منهم في الفروع وأهل النحو مجمعون على طريق البصريين والكوفيين في النحو وكذلك أهل الكلام مجمعون على طريق واحد منهم من متقدميهم وسلفهم','688','655','0');
INSERT INTO [t6446] VALUES (910,'واتبعنا طريقته ومن كان على غير ما نحن عليه كان على غير طريقته فلم يجز اعتبار هذا الذي تنازعنا فيه بما قلتم الجواب أن كل فريق من المبتدعة إنما يدعي أن الذي يعتقده هو ما كان عليه رسول الله','688','655','0');
INSERT INTO [t6446] VALUES (911,'غير أن الطرق تفرقت بهم بعد ذلك وأحدثوا في الدين ما لم يأذن به الله ورسوله فزعم كل فريق أنه هو المتمسك بشريعة الإسلام وأن الحق الذي قام به رسول الله','688','655','0');
INSERT INTO [t6446] VALUES (912,'وأخذه أصحاب رسول الله','689','655','0');
INSERT INTO [t6446] VALUES (913,'ولا طريق إلى معرفة ما دعا إليه رسول الله','689','655','0');
INSERT INTO [t6446] VALUES (914,'ورضي عنهم اختلفوا في أحكام الدين فلم يفترقوا ولم يصيروا شيعا لأنهم لم يفارقوا الدين ونظروا فيما أذن لهم فاختلفت أقوالهم وآراؤهم في مسائل كثيرة مثل مسألة الجد والمشتركة','692','655','0');
INSERT INTO [t6446] VALUES (915,'وأصحابه ولا يجوز لنا الإعراض عن نقلها وروايتها وبيانها لتفرق الناس في ذلك كما في أصل الإسلام والدعاء إلى التوحيد وإظهار الشهادتين وقد ظهر بما قدمنا وذكرنا بحمد الله ومنه أن الطريق المستقيم مع أهل الحديث وأن الحق ما نقلوه','695','655','0');
INSERT INTO [t6446] VALUES (916,'عليكم بسنتي ومن رغب عن سنتي فليس مني ثم لعن تارك سنته على ما روي أنه قال','696','655','0');
INSERT INTO [t6446] VALUES (917,'فأخذوها من معادنها وجمعوها من مظانها وحفظوها فاغتبطوا بها ودعوا إلى اتباعها وعابوا من خالفها فكثرت عندهم وفي أيديهم حتى اشتهروا بها كما اشتهر البزاز ببزه والتمار بتمره والعطار بعطره ثم رأينا قوما انسلخوا من حفظها ومعرفتها','698','655','0');
INSERT INTO [t6446] VALUES (918,'فيها تقليدا لمن أمر الله بتقليده','698','655','0');
INSERT INTO [t6446] VALUES (919,'فهذا الذي قلناه سمة ظاهره وعلامة بينة تشهد لأهل السنة باستحقاقها وعلى أهل الأهواء في تركها والعدول عنها ولا نحتاج في هذا إلى شاهد أبين من هذا ولا إلى دليل أضوأ من هذا فإن قالوا إن لكل فريق من الأهواء وأصحاب الآراء حججا','699','655','0');
INSERT INTO [t6446] VALUES (920,'أو بحديث مرسل ضعيف على حديث متصل قوي ومن ها هنا امتاز أهل اتباع السنة من غيرهم لأن صاحب السنة لا يألو أن يتبع من السنن أقواها ومن الشهود عليها أعدلها وأتقاها وصاحب الهوى كالغريق يتعلق بكل عود ضعيف أو قوي فإذا رأيت الحاكم لا يقبل من','699','655','0');
INSERT INTO [t6446] VALUES (921,'الصادقة فيغلط جهال الناس بهذه الدعوى وما احتج مبتدع في رد آثار رسول الله','700','655','0');
INSERT INTO [t6446] VALUES (922,'شرقا وغربا وبرا وبحرا وارتحل في الحديث الواحد فراسخ واتهم أباه وأدناه في خبر يرويه عن النبي','701','655','0');
INSERT INTO [t6446] VALUES (923,'وأشهر من الشمس برأي دخيل واستحسان ذميم وظن فاسد ونظر مشوب بالهوى فانظر وفقك الله للحق أي الفريقين أحق بأن ينسب إلى اتباع السنة واستعمال الأثر الفرقة الأولى أم الثانية فإذا قضيت بين هذين بوافر لبك وصحيح نظرك وثاقب فهمك فليكن','701','655','0');
INSERT INTO [t6446] VALUES (924,'فصل ومن مذهب أهل السنة أن المؤمنين يرون الله تبارك وتعالى بأبصارهم يوم القيامة','702','655','0');
INSERT INTO [t6446] VALUES (925,'فنظر إلى القمر ليلة البدر فقال إنكم سترون ربكم عيانا كما ترون هذا لا تضامون في رؤيته فإن استطعتم أن لا تغلبوا على صلاة قبل طلوع الشمس وقبل غروبها وقرأ وسبح بحمد ربك قبل طلوع الشمس وقبل غروبها أخرجه البخاري في الصحيح','704','655','0');
INSERT INTO [t6446] VALUES (926,'إنكم ستعاينون ربكم','704','655','0');
INSERT INTO [t6446] VALUES (927,'إنكم سترون ربكم كما ترون هذا القمر ليلة البدر لا تضامون في رؤيته فانظروا لا تغلبوا على صلاة قبل طلوع الشمس وقبل غروبها','705','655','0');
INSERT INTO [t6446] VALUES (928,'يجمع المؤمنون يوم القيامة فيقولون لو استشفعنا إلى ربنا فأراحنا من مكاننا هذا فيأتون آدم عليه السلام فذكر الحديث إلى أن قالوا إئتوا محمدا عبدا قد غفر الله له ما تقدم من ذنبه وما تأخر فيأتوني حتى استأذن على ربي فيؤذن لي فإذا','705','655','0');
INSERT INTO [t6446] VALUES (929,'قال جنتان من فضة آنيتهما وما فيهما وجنتان من ذهب آنيتهما وما فيهما وما بين أن ينظروا إلى ربهم تبارك وتعالى إلا رداء الكبرياء على وجهه في جنة عدن','706','655','0');
INSERT INTO [t6446] VALUES (930,'إذا دخل أهل الجنة الجنة نودوا يا أهل الجنة إن لكم عند الله موعدا لم تروه قال فيقولون ما هو ألم يبيض وجوهنا ويزحزحنا عن النار ويدخلنا الجنة قال فيكشف الحجاب عز وجل فينظرون إليه فوالله ما أعطاهم الله شيئا هو أحب إليهم مما هم','707','655','0');
INSERT INTO [t6446] VALUES (931,'بينا أهل الجنة في نعيمهم إذ طلع عليهم ربهم عز وجل فيرفعون رؤسهم فإذا ربهم قد أشرف عليهم فيقول السلام عليكم أهل الجنة فذلك قوله سلام قولا من رب رحيم فينظر إليهم ثم يحتجب عنهم ويبقى نور من نوره في منازلهم','708','655','0');
INSERT INTO [t6446] VALUES (932,'ثم انصرف قال فتبعه رجل قال عطاء أبي الذي تبعه ولكن كره أن يقول فسأله عن الدعاء فقال اللهم إني أسألك بعلمك الغيب وقدرتك على الخلق أحيني ما علمت الحياة لي خيرا وتوفني إذا كانت لي الوفاة خيرا اللهم أسألك كلمة الحلم','710','655','0');
INSERT INTO [t6446] VALUES (933,'علمه','710','655','0');
INSERT INTO [t6446] VALUES (934,'فصل','712','655','0');
INSERT INTO [t6446] VALUES (935,'من الصحابة حديث الرؤية ثلاث وعشرون نفسا منهم علي وأبو هريرة وأبو سعيد الخدري وجرير وأبو موسى وصهيب وجابر وابن عباس وأنس وعمار بن ياسر وأبي بن كعب وابن مسعود وزيد بن ثابت وحذيفة وعبادة وأبو أمامة وعدي بن حاتم','712','655','0');
INSERT INTO [t6446] VALUES (936,'وعليهم','713','655','0');
INSERT INTO [t6446] VALUES (937,'ذكر عبد الرحمن بن أبي حاتم نا أبي عبد الرحمن المقري قال سمعت الحسين بن محمد الطنافسي قال سمعت وكيعا يقول يراه المؤمنون في الجنة ولا يراه إلا المؤمنون وذكر يحيى بن المغيرة قال كنا عند جرير بن عبد الحميد فذكر له حديث ابن سابط','713','655','0');
INSERT INTO [t6446] VALUES (938,'فصل','717','655','0');
INSERT INTO [t6446] VALUES (939,'أسألك لذة النظر إلى وجهك والنبي','717','655','0');
INSERT INTO [t6446] VALUES (940,'فصل','719','655','0');
INSERT INTO [t6446] VALUES (941,'رأى ربه ليلة المعراج وكانت رؤيا يقظة لا رؤيا منام','719','655','0');
INSERT INTO [t6446] VALUES (942,'فصل','720','655','0');
INSERT INTO [t6446] VALUES (943,'والطريق إليه الطريق إلى سنته فلما استحلى الباطل سدت الأبواب بينه وبينه فعظم في عيني وقبلت رأسه وخرجت فلما رجعت إلى المنزل قال لي الشيخ ما كان منك فقصصت عليه القصة وقلت له إنه لكما قلت وحي يوحى إليه فوجم','723','655','0');
INSERT INTO [t6446] VALUES (944,'فصل','724','655','0');
INSERT INTO [t6446] VALUES (945,'وهم يد على من سواهم أي يعاون بعضهم بعضا على من سواهم من الكفار وهذا أيضا لا يجوز لأنه لا يجوز أن يقال لما خلقت بنصرتي وقد تكون اليد بمعنى الملك والتصرف يقال هذه الدار في يد فلان أي في تصرفه وملكه وهذا أيضا لا','728','655','0');
INSERT INTO [t6446] VALUES (946,'وكيفيته مجهوله وكذلك القول في جميع الصفات يجب الإيمان به ويترك الخوض في تأويله وإدراك كيفيته','730','655','0');
INSERT INTO [t6446] VALUES (947,'قال علماء السلف أول ما افترض الله على عباده الإخلاص وهو معرفة الله والإقرار به وطاعته بما أمر ونهى وأول الفرض شهادة أن لا إله إلا الله وحده لا شريك له وأن محمدا عبده ورسوله','730','655','0');
INSERT INTO [t6446] VALUES (948,'حق وخير الناس بعد رسول الله','732','655','0');
INSERT INTO [t6446] VALUES (949,'وعلى طلحة والزبير وعائشة وعمار ابن ياسر وعمرو بن العاص وأصحاب الجمل وصفين القاتلين','732','655','0');
INSERT INTO [t6446] VALUES (950,'فصل','734','655','0');
INSERT INTO [t6446] VALUES (951,'لا يحل شيء منه لصغير ولا كبير والمزامير والطنابير حرام واللعب بالنرد حرام والقمار والميسر حرام ولا يجوز مجالسة أهل المعاصي الذين ظهر فسقهم ولا مجالسة أهل البدع الذين ظهرت بدعهم ولا يجوز دخول الحمام إلا بمئزر والحب في الله','735','655','0');
INSERT INTO [t6446] VALUES (952,'إلى آخر عصابة تقاتل الدجال','737','655','0');
INSERT INTO [t6446] VALUES (953,'والإيمان بأن الله تعالى على عرشه استوى كما شاء وعلمه بكل مكان لا يخفى عليه شيء ومن صفة أهل السنة الأخذ بكتاب الله عز وجل وبأحاديث رسول الله','737','655','0');
INSERT INTO [t6446] VALUES (954,'وترك الرأي والابتداع والقرآن الذي في الأرض هو القرآن الذي في السماء بعينه مكتوب في اللوح المحفوظ ولا يحل للجنب والحائض والنفساء مسه ولا قراءته ولا يمسه من ليس على وضوء ولا بأس أن يقرأ والمعراج حق أسري بالنبي','737','655','0');
INSERT INTO [t6446] VALUES (955,'ونرجو لأهل القبلة الجنة ونرغب في شهود جنازته وعيادته ومن حلف بالقرآن فعليه بكل آية كفارة يمين ومن حلف بوجه الله أو بعلم الله فهو يمين','738','655','0');
INSERT INTO [t6446] VALUES (956,'فصل','739','655','0');
INSERT INTO [t6446] VALUES (957,'بإحسان عمله ويخشى عليه بذنب اكتسبه والإمساك عما شجر بين أصحاب النبي','739','655','0');
INSERT INTO [t6446] VALUES (958,'فصل في بيان أن القاتل عمدا له توبة','740','655','0');
INSERT INTO [t6446] VALUES (959,'في قوله فجزاؤه جهنم قال هو جزاؤه إن جازاه','742','655','0');
INSERT INTO [t6446] VALUES (960,'في بيان أن المسلمين لا يضرهم الذنوب إذا ماتوا عن توبة عنها من غير إصرار وإن ماتوا عن غير توبة فأمرهم إلى الله عز وجل إن شاء عذبهم وإن شاء غفر لهم وقال محمد بن سيرين لا نعلم أحدا من أصحاب محمد','742','655','0');
INSERT INTO [t6446] VALUES (961,'قال يقول ربكم عز وجل ابن آدم إن تأتيني بقراب الأرض خطيئة بعد أن لا تشرك بي شيئا جعلت قرابها مغفرة ولا أبالي','743','655','0');
INSERT INTO [t6446] VALUES (962,'قال من جاء يعبد الله لا يشرك به شيئا ويقيم الصلاة ويؤتي الزكاة ويصوم رمضان فإن له الجنة','743','655','0');
INSERT INTO [t6446] VALUES (963,'ونحن في مجلس بايعوني على أن لا تشركوا بالله شيئا ولا تسرقوا ولا تزنوا ولا تقتلوا أولادكم ولا تأتوا ببهتان تفترونه بين أيديكم وأرجلكم ولا تعصوني في معروف فمن وفى منكم فأجره على الله ومن أصاب من ذلك شيئا فستره الله في','744','655','0');
INSERT INTO [t6446] VALUES (964,'لأصحابه ما تقولون في رجل قتل في سبيل الله قالوا الله ورسوله أعلم قال رسول الله','744','655','0');
INSERT INTO [t6446] VALUES (965,'مذنب والله غفور رحيم','744','655','0');
INSERT INTO [t6446] VALUES (966,'ليجيئن ناس من أمتي بذنوب أمثال الجبال فيغفرها الله لهم ويضعها على اليهود والنصارى قال فحدثت به عمر بن عبد العزيز فقال آلله أنت سمعته من أبيك يحدث به عن النبي','745','655','0');
INSERT INTO [t6446] VALUES (967,'فصل','747','655','0');
INSERT INTO [t6446] VALUES (968,'إن الله لا يغفر أن يشرك به ويغفر ما دون ذلك لمن يشاء وإني ادخرت دعوتي شفاعة لأهل الكبائر من أمتي يوم القيامة','748','655','0');
INSERT INTO [t6446] VALUES (969,'إلى قوله الذي كذب وتولى','749','655','0');
INSERT INTO [t6446] VALUES (970,'وكان توعده فقال أنبئت أن رسول الله أوعدني والعفو عند رسول الله مأمول','751','655','0');
INSERT INTO [t6446] VALUES (971,'إذا حدثتم الناس عن ربهم فلا تحدثوهم بما يفزعهم ويشق عليهم','752','655','0');
INSERT INTO [t6446] VALUES (972,'ليلة أسري بي مررت على جبرائيل في الملأ الأعلى كالحلس البالي من خشية الله تعالى','752','655','0');
INSERT INTO [t6446] VALUES (973,'انسب لنا ربك فأنزل الله قل هو الله أحد الله الصمد لم يلد ولم يولد لأنه ليس شيء يلد أو يولد إلا سيموت وليس يموت إلا يورث وإن الله تبارك وتعالى لا يموت ولا يورث ولم يكن له كفوا أحد قال ليس له شبيه ولا مثل','752','655','0');
INSERT INTO [t6446] VALUES (974,'إن موسى بن عمران عليه السلام كان يمشي ذات يوم في الطريق فناداه الجبار يا موسى فالتفت يمينا وشمالا ولم ير أحدا ثم ناداه الثانية يا موسى بن عمران فالتفت يمينا وشمالا ولم ير أحدا وارتعدت فرائصه ثم نودي الثالثة يا موسى بن عمران','753','655','0');
INSERT INTO [t6446] VALUES (975,'رجلا من أصحابه إلى رأس من المشركين يدعوه إلى الله فقال المشرك هذا الذي يدعوني إليه من ذهب أو فضة أو من نحاس فتعاظم مقالته في صدر رسول رسول الله فرجع إلى رسول الله','754','655','0');
INSERT INTO [t6446] VALUES (976,'في الطريق لا يدري فرجع إلى النبي','754','655','0');
INSERT INTO [t6446] VALUES (977,'إن الله قد أهلك صاحبك بعدك ونزلت على رسول الله','754','655','0');
INSERT INTO [t6446] VALUES (978,'فصل في الوسوسة في أمر الرب عز وجل','755','655','0');
INSERT INTO [t6446] VALUES (979,'فقال يا رسول الله إني أحدث نفسي من أمر الرب تبارك وتعالى لأن أخر من السماء أحب إلي من أن أتكلم به قال ذاك محض الإيمان','755','655','0');
INSERT INTO [t6446] VALUES (980,'فقال يا رسول الله إني أجد في صدري الشيء لأن أكون حمما أحب إلي من أن أتكلم به فقال رسول الله','755','655','0');
INSERT INTO [t6446] VALUES (981,'قال إن سألكم الناس عن ذلك فقولوا الله كان قبل كل شيء والله خالق كل شيء والله كائن بعد كل شيء','756','655','0');
INSERT INTO [t6446] VALUES (982,'فإذا قالوا ذلك فقل الله أحد الله الصمد لم يلد ولم يولد ولم يكن له كفوا أحد ثم ليتفل عن يساره وليستعيذ من الشيطان','756','655','0');
INSERT INTO [t6446] VALUES (983,'يقول إن الناس يجلسون يوم القيامة على قدر رواحهم إلى الجمعات الأول ثم الثاني ثم الثالث ثم الرابع وما رابع أربعة ببعيد','757','655','0');
INSERT INTO [t6446] VALUES (984,'قال أبو عبد الله بن منده رحمه الله ذكر الآيات التي تدل على وحدانية الخالق من تقلب أحوال العبد وأنه المدبر لذلك من حال الصحة والمرض والموت والحياة والنوم والانتباه والفقر والغنى والعجز والقدرة','757','655','0');
INSERT INTO [t6446] VALUES (985,'قال إن الله تعالى خالق كل صانع وصنعته','758','655','0');
INSERT INTO [t6446] VALUES (986,'قال إن الله صانع ما شاء لا مكره له','759','655','0');
INSERT INTO [t6446] VALUES (987,'قال لا تواصلوا قالوا فإنك تواصل قال إني لست كأحدكم إني أبيت يطعمني ربي ويسقيني','759','655','0');
INSERT INTO [t6446] VALUES (988,'إن الذي أنزل الداء أنزل الدواء','759','655','0');
INSERT INTO [t6446] VALUES (989,'فصل','760','655','0');
INSERT INTO [t6446] VALUES (990,'يقول ما من قلب إلا وهو بين أصبعين من أصابع الرحمن إن شاء أقامه وأن شاء أزاغه وكان رسول الله','760','655','0');
INSERT INTO [t6446] VALUES (991,'يكثر أن يقول يا مقلب القلوب ثبت قلبي على دينك ومن مذهب أهل السنة الإيمان بجميع ما ثبت عن النبي','761','655','0');
INSERT INTO [t6446] VALUES (992,'فصل','763','655','0');
INSERT INTO [t6446] VALUES (993,'فصل','765','655','0');
INSERT INTO [t6446] VALUES (994,'فصل','766','655','0');
INSERT INTO [t6446] VALUES (995,'فصل','767','655','0');
INSERT INTO [t6446] VALUES (996,'أن تعارض بشيء من المقاييس أو تنفي بتأويل القرآن','767','655','0');
INSERT INTO [t6446] VALUES (997,'قال لا أعرفن ما بلغ أحدكم عني حديث من حديثي قد أمرت فيه أو نهيت وهو متكيء على أريكته فيقول لا حاجة لي به هذا القرآن ما وجدنا فيه اتبعناه وما لم نجد فيه لم نتبعه','768','655','0');
INSERT INTO [t6446] VALUES (998,'في الكلمة من الأشياء مما يكتب قبله في الكتب يعني فيفسرها النبي','769','655','0');
INSERT INTO [t6446] VALUES (999,'ونهى عنه هو عن الله وفي كتاب الله فتأولوا فيه قول الله عز وجل وما آتاكم الرسول فخذوه وما نهاكم عنه فانتهوا وذكر حديث عبد الله بن مسعود رضي الله عنه في','769','655','0');
INSERT INTO [t6446] VALUES (1000,'وهو في كتاب الله وقرأ وما آتاكم الرسول فخذوه وما نهاكم عنه فانتهوا','769','655','0');
INSERT INTO [t6446] VALUES (1001,'نهى عن النقير والمقير والدباء والحنتم قال الدارمي يقول ابن عباس رضي الله عنه ليس النقير والمقير والدباء والحنتم بمنصوصات في كتاب الله عز وجل وهي داخلة في قوله وما آتاكم الرسول فخذوه وما نهاكم عنه فانتهوا وفي','770','655','0');
INSERT INTO [t6446] VALUES (1002,'فقال رجل من أهل الكوفة إن الله يقول كذا وكذا فغضب سعيد غضبا شديدا','771','655','0');
INSERT INTO [t6446] VALUES (1003,'بكتاب الله عز وجل كان رسول الله','772','655','0');
INSERT INTO [t6446] VALUES (1004,'السنة يعلمه إياها تفسيرا للقرآن قال الدارمي في قول يحيى بن أبي كثير السنة قاضية على القرآن وليس القرآن بقاض على السنة يعني أن السنة تفسر القرآن والقرآن أصول محكمة مجملة لا تفسر السنة والسنة تفسرها وتبين حدودها ومعانيها','772','655','0');
INSERT INTO [t6446] VALUES (1005,'عن الحذف وقال إنه لا يصطاد صيدا','772','655','0');
INSERT INTO [t6446] VALUES (1006,'وتهاون به لا أكلمك أبدا','773','655','0');
INSERT INTO [t6446] VALUES (1007,'لن يستكمل مؤمن إيمانه حتى يكون هواه تبعا لما جئتكم به','773','655','0');
INSERT INTO [t6446] VALUES (1008,'فصل','774','655','0');
INSERT INTO [t6446] VALUES (1009,'فيترك رأيه ويرجع إلى حديث رسول الله','774','655','0');
INSERT INTO [t6446] VALUES (1010,'أنه نهى عنه','775','655','0');
INSERT INTO [t6446] VALUES (1011,'قضى في مثل هذا أن الخراج بالضمان فعجلت إلى عمر فأخبرته بما أخبرني عروة عن عائشة عن النبي','775','655','0');
INSERT INTO [t6446] VALUES (1012,'أن أراد قضاء عمر وأنفذ سنة رسول الله','775','655','0');
INSERT INTO [t6446] VALUES (1013,'قال عام الفتح من قتل له قتيل فهو بخير النظرين إن أحب أخذ العقل وإن أحب فله القود وقيل لابن أبي ذئب أتأخذ به يا أبا الحارث فضرب صدر السائل وصاح به صياحا كثيرا وقال أحدثك عن رسول الله','776','655','0');
INSERT INTO [t6446] VALUES (1014,'فقد تقطعت من بين يديه أسباب الهدى ولقي حجة فتنته وتلك أبلغ الشرور في القلوب عقوبة وما أرى امريء في شيء سبقه إليه رسول الله','776','655','0');
INSERT INTO [t6446] VALUES (1015,'قال','776','655','0');
INSERT INTO [t6446] VALUES (1016,'من أفعاله وأمره ونهيه وسنن وأمر الله بالاقتداء برسوله فيها غير أن بعضها ألزم من بعض وبعضها أيسر من بعض قال وما آتاكم الرسول فخذوه وما نهاكم عنه فانتهوا وقال فليحذر الذين يخالفون عن أمره أن تصيبهم فتنة أو يصيبهم عذاب','777','655','0');
INSERT INTO [t6446] VALUES (1017,'ومنها ما هو فضيلة وأدب الاختيار فيها الأخذ بها على كل حال وتركها من غفلة أو سهو أو عجز من غير استخفاف بها وتهاون فالرجاء أن لا تبعة على صاحبها في الدنيا ولا في الأخرة مثل','777','655','0');
INSERT INTO [t6446] VALUES (1018,'فهو الضال الممقوت في الدنيا المعذب في الآخرة إلا أن يعفو الله عنه ومن قال قال رسول الله','778','655','0');
INSERT INTO [t6446] VALUES (1019,'فإنما يقبل عن الله ومن رد عليه فإنما يرده على الله قال الله تبارك وتعالى من يطع الرسول فقد أطاع الله وقال إن الذين يبايعونك إنما يبايعون الله','778','655','0');
INSERT INTO [t6446] VALUES (1020,'مع كتاب الله عز وجل مقام البيان عن الله عز وجل وليس شيء من سنن رسول الله','779','655','0');
INSERT INTO [t6446] VALUES (1021,'يهدي إلى صراط مستقيم وقول من قال يعرض السنة على القرآن فإن وافقت ظاهره وإلا استعملنا ظاهر القرآن وتركنا الحديث فهذا جهل وقد قص الله علينا أن ننتهي إلى سنة نبيه وليس لنا معها من الأمر شيء إلا التسليم لها واتباعها ولا تعرض','779','655','0');
INSERT INTO [t6446] VALUES (1022,'لا تزال طائفة من أمتي قائمة بالحق ظاهرين على من سواهم إلى يوم القيامة فقال الطائفة دون الألف وسيبلغ هذا الأمر إلى أن لا يبلغ عدد المتمسكين بما كان عليه رسول الله','779','655','0');
INSERT INTO [t6446] VALUES (1023,'فصل','780','655','0');
INSERT INTO [t6446] VALUES (1024,'فصل','782','655','0');
INSERT INTO [t6446] VALUES (1025,'في جوف الليل وهو على بئر بدر ينادي يا أبا جهل بن هشام يا عتبة ابن ربيعة يا شيبة بن ربيعة يا أمية بن خلف ألا هل وجدتم ما وعد ربكم حقا','785','655','0');
INSERT INTO [t6446] VALUES (1026,'فصل','786','655','0');
INSERT INTO [t6446] VALUES (1027,'فقال يا أبا ذر أتدري فيما انتطحتا فقلت لا قال لكن آلله يدري ويقضي بينهما يوم القيامة','787','655','0');
INSERT INTO [t6446] VALUES (1028,'لتؤدن الحقوق إلى أهلها يوم القيامة حتى يقاد الشاة الجلحاء من الشاة القرناء','788','655','0');
INSERT INTO [t6446] VALUES (1029,'روي عن مجاهد في قوله يتوفاكم ملك الموت قال حويت له الأرض فجعلت له مثل الطست ينال منها حيث يشاء وعن يحيى بن خلاد قال لما أراد الله أن يخلق آدم بعث جبريل فقال ائت الأرض فاقبض منها قبضة أخلق منها خلقا وأعيده فيها','788','655','0');
INSERT INTO [t6446] VALUES (1030,'يأتي ملك الموت إلى الجبار يوم القيامة فيقول يا رب قد مات أهل السموات والأرض إلا من شئت فيقول الله عز وجل وهو أعلم فمن بقي فيقول يا رب بقيت أنت الحي الذي لا تموت وبقي حملة عرشك وبقي جبريل وميكائيل وأنا فيقول الله تعالى','789','655','0');
INSERT INTO [t6446] VALUES (1031,'باب في فضائل الصحابة رضوان الله عليهم','792','655','0');
INSERT INTO [t6446] VALUES (1032,'فضل أبي بكر الصديق رضي الله عنه','793','655','0');
INSERT INTO [t6446] VALUES (1033,'فقال ها هنا رجل خيره الله تعالى بين أن يعيش في الدنيا متى شاء أو الآخرة فاختار الآخرة وقال في حديث آخر فبكى أبو بكر رضي الله عنه وقال فديناك بآبائنا وأمهاتنا قال فتعجبنا له وقال الناس انظروا إلى هذا الشيخ يخبر','793','655','0');
INSERT INTO [t6446] VALUES (1034,'هو المخير وكان أبو بكر أعلمنا به','793','655','0');
INSERT INTO [t6446] VALUES (1035,'فقالت كان المشركون قعودا في المسجد الحرام فتذكروا رسول الله','794','655','0');
INSERT INTO [t6446] VALUES (1036,'المسجد فقاموا إليه وكان إذا سألوه عن شيء صدقهم فقالوا ألست تقول في آلهتنا كذا وكذا قال بلى قال فتشبثوا به بأجمعهم فأتى الصريخ إلى أبي بكر رضي الله عنه فقيل له أدرك صاحبك فخرج أبو بكر رضي الله عنه ودخل المسجد فوجد رسول','794','655','0');
INSERT INTO [t6446] VALUES (1037,'وأقبلوا على أبي بكر قالت فرجع إلينا فجعل لا يمس شيئا من غدائه إلا جاء معه وهو يقول تباركت يا ذا الجلال والإكرام أخبرنا أبو المظفر أنا أبو علي نا ابن فراس نا الديبلي نا المخزومي نا ابن عيينة عن هشام بن عروة عن أبيه أن','794','655','0');
INSERT INTO [t6446] VALUES (1038,'قال أقبل عقبة بن أبي معيط ورسول الله','796','655','0');
INSERT INTO [t6446] VALUES (1039,'ثم قال أتقتلون رجلا أن يقول ربي الله وقد جاءكم بالبينات من ربكم','796','655','0');
INSERT INTO [t6446] VALUES (1040,'في الغار فمر رجل من المشركين فقلت يا رسول الله لو نظر تحت قدمه لأبصرنا تحت قدمه قال ما ظنك باثنين الله ثالثهما','796','655','0');
INSERT INTO [t6446] VALUES (1041,'إذ أقبل أبو بكر رضي الله عنه آخذا بطرف ثوبه حتى أبدى عن ركبته فلما رآه رسول الله','797','655','0');
INSERT INTO [t6446] VALUES (1042,'فقال يا رسول الله إنه كان بيني وبين عمر شيء فاسرعت إليه ثم إني ندمت على ما كان مني فسألته أن يغفر لي فأبى علي فتبعته البقيع كله حتى تحرز مني بداره فأقبلت إليك فقال رسول الله','797','655','0');
INSERT INTO [t6446] VALUES (1043,'فأقبل عمر إلى رسول الله','797','655','0');
INSERT INTO [t6446] VALUES (1044,'يتمعر حتى أشفق أبو بكر رضي الله عنه أن يكون من رسول الله','797','655','0');
INSERT INTO [t6446] VALUES (1045,'يا أيها الناس إن الله بعثني إليكم فقلتم كذبت وقال أبو بكر صدقت وواساني بنفسه وماله فهل أنتم تاركو لي صاحبي فهل أنتم تاركو لي صاحبي فهل أنتم تاركوا لي صاحبي قال فما أوذي بعدها قوله غامر أي خاصم وقوله يتمعر أي','798','655','0');
INSERT INTO [t6446] VALUES (1046,'في اليوم الذي بديء فيه فقلت وا رأساه فقال وددت أن ذاك كان وأنا حي فهيأتك ودفنتك فقلت غيرى كأني بك في ذلك اليوم عروسا ببعض نسائك قال أنا وا رأساه ادعي لي أباك وأخاك حتى أكتب لأبي بكر كتابا فإني أخاف أن يقول قائل ويتمنى','798','655','0');
INSERT INTO [t6446] VALUES (1047,'إذا أنت مرضت قدمت أبا بكر قال لست أنا الذي أقدمه ولكن الله يقدمه','799','655','0');
INSERT INTO [t6446] VALUES (1048,'جالس وعنده أبو بكر رضي الله عنه','799','655','0');
INSERT INTO [t6446] VALUES (1049,'إلى أبي بكر فقال يا أبا بكر هذا جبريل يقرئك من الله السلام ويقول لك أراض أنت عني في فقرك هذا أم ساخط قال فبكى أبو بكر رضي الله عنه وقال أعلى ربي أغضب أنا عن ربي راض أنا عن ربي راض','800','655','0');
INSERT INTO [t6446] VALUES (1050,'أنه قال من أنفق زوجين في سبيل الله نودي من الجنة يا عبد الله هذا خير فهلم فمن كان من أهل الصلاة دعي من باب الصلاة ومن كان من أهل الزكاة','800','655','0');
INSERT INTO [t6446] VALUES (1051,'نعم وأرجوا أن تكون منهم قوله من ضرورة أي من ضرر وخسران','801','655','0');
INSERT INTO [t6446] VALUES (1052,'صعد أحدا وأبو بكر وعمر وعثمان فرجف بهم فقال نبي الله','801','655','0');
INSERT INTO [t6446] VALUES (1053,'في بعض أسفاره حتى إذا','801','655','0');
INSERT INTO [t6446] VALUES (1054,'على التماسه وأقام الناس معه وليسوا على ماء وليس معهم ماء فأتى الناس إلى أبي بكر رضي الله عنه فقالوا ما ترى ما صنعت عائشة أقامت برسول الله','802','655','0');
INSERT INTO [t6446] VALUES (1055,'واضع رأسه على فخذي قد نام فقال حبست الناس وليسوا على ماء وليس معهم ماء قالت فعاتبني وقال ما شاء الله أن يقول وجعل يطعن بيده في خاصرتي فلا يمنعني من التحرك إلا مكان رسول الله','802','655','0');
INSERT INTO [t6446] VALUES (1056,'حتى أصبح على غير ماء فأنزل الله تعالى آية التيمم فتيمموا صعيدا طيبا فقال أسيد بن حضير ما هي بأول بركتكم يا آل أبي بكر فقالت فبعثنا البعير الذي كنت عليه فوجدنا العقد تحته','802','655','0');
INSERT INTO [t6446] VALUES (1057,'فصل','805','655','0');
INSERT INTO [t6446] VALUES (1058,'تسأله فقال لها ارجعي إلي','805','655','0');
INSERT INTO [t6446] VALUES (1059,'تساله شيئا فقال لها تعودين فقالت يا رسول الله إن جئت ولم أجدك كأنها تعرض بالموت قال إن جئت ولم تجديني فأتي أبا بكر فهو الخليفة بعدي أخبرناه سليمان بن إبراهيم أنا أبو بكر بن مردوية نا أحمد بن كامل نا أحمد بن','806','655','0');
INSERT INTO [t6446] VALUES (1060,'من يلي الأمر بعدك قال أبو بكر','807','655','0');
INSERT INTO [t6446] VALUES (1061,'فصل','808','655','0');
INSERT INTO [t6446] VALUES (1062,'وما كان الله ليجمعهم على الكذب قال وأخبرنا أبو بكر بن مردوية نا محمد بن أحمد بن إبراهيم نا أحمد ابن إسحاق الجوهري نا الزبير بن بكار نا مطرف بن عبد الله المدني عن مالك بن أنس قال قال لي هارون الرشيد أمير المؤمنين يا مالك','809','655','0');
INSERT INTO [t6446] VALUES (1063,'فصل','809','655','0');
INSERT INTO [t6446] VALUES (1064,'أمشي أمام أبي بكر فقال يا أبا الدرداء أتمشي أمام من هو خير منك في الدنيا والأخرة ما طلعت الشمس ولا غربت على أحد بعد النبيين والمرسلين أفضل من أبي بكر','810','655','0');
INSERT INTO [t6446] VALUES (1065,'إن الله يتجلى للناس عامة ويتجلى لأبي بكر خاصة','810','655','0');
INSERT INTO [t6446] VALUES (1066,'إلى المسجد وأبو بكر عن يمينه وعمر عن يساره فقال هكذا نبعث يوم القيامة','810','655','0');
INSERT INTO [t6446] VALUES (1067,'فصل','811','655','0');
INSERT INTO [t6446] VALUES (1068,'إن أمن الناس علينا في صحبته وذات يده أبو بكر الصديق فحبه وشكره وحفظه واجب على أمتي أخبرنا أبو المظفر السمعاني نا عبد الصمد بن علي الهاشمي نا الدراقطني نا أبو بكر بن أبي سعيد البزاز نا محمد بن القاسم بن خلاد أبو العيناء','812','655','0');
INSERT INTO [t6446] VALUES (1069,'فصل','813','655','0');
INSERT INTO [t6446] VALUES (1070,'يدخل الجنة رجل لا يبقى فيها أهل دار ولا غرفة إلا قالوا مرحبا مرحبا إلينا إلينا فقال أبو بكر رضي الله عنه يا رسول الله ما توى هذا الرجل في ذلك اليوم قال أجل يا أبا بكر وأنت هو قيل توى ضاع وخسر وروي ما','813','655','0');
INSERT INTO [t6446] VALUES (1071,'ما صحب المرسلين أجمعين ولا صاحب ياسين يعني نفسه أفضل من أبي بكر الصديق','813','655','0');
INSERT INTO [t6446] VALUES (1072,'فصل','814','655','0');
INSERT INTO [t6446] VALUES (1073,'قالت خرج أبو بكر الصديق رضي الله عنه يريد رسول الله','814','655','0');
INSERT INTO [t6446] VALUES (1074,'إني رسول الله أدعو إلى الله فلما فرغ رسول الله','814','655','0');
INSERT INTO [t6446] VALUES (1075,'وكانوا تسعة وثلاثين رجلا ألح أبو بكر على رسول الله','815','655','0');
INSERT INTO [t6446] VALUES (1076,'حتى ظهر رسول الله','815','655','0');
INSERT INTO [t6446] VALUES (1077,'جالس وكان أول خطيب دعا إلى الله عز وجل وإلى رسوله وثار المشركون على أبي بكر وعلى المسلمين يضربونهم في نواحي المسجد ضربا شديدا ووطئ أبو بكر وضرب ضربا شديدا ودنا منه الفاسق عتبه بن ربيعة فجعل يضربه بنعلين مخصوفين ويحرفهما لوجهه','815','655','0');
INSERT INTO [t6446] VALUES (1078,'فنالوه بألسنتهم وعذلوه وقالوا لأم الخير بنت صخر انظري أن تطعميه شيئا أو تسقيه إياه فلما خلت به والحت جعل يقول ما فعل رسول الله قالت والله ما لي علم بصاحبك قال اذهبي إلى أم جميل بنت الخطاب فسليها عنه فخرجت حتى جاءت أم جميل','816','655','0');
INSERT INTO [t6446] VALUES (1079,'قال فانكب عليه فقبله وأكب عليه المسلمون ورق رسول الله','817','655','0');
INSERT INTO [t6446] VALUES (1080,'ثم دعاها إلى الله فأسلمت فأقاموا مع رسول الله','817','655','0');
INSERT INTO [t6446] VALUES (1081,'لعمر بن الخطاب رضى الله عنه وأبي جهل بن هشام فأصبح عمر وكانت الدعوة يوم الأربعاء فأسلم عمر يوم الخميس فكبر رسول الله','817','655','0');
INSERT INTO [t6446] VALUES (1082,'فقال ما يجلسك بأبي أنت وأمي فوالله ما بقي مجلس كنت أجلس فيه بالكفر إلا أظهرت الإيمان غير هائب ولا خائف فخرج رسول الله','818','655','0');
INSERT INTO [t6446] VALUES (1083,'إلى دار الأرقم وعمر رضي الله عنه معه','818','655','0');
INSERT INTO [t6446] VALUES (1084,'أن خير هذه الأمة بعد نبيها أبو بكر ثم عمر ثم عثمان فيبلغ النبي','821','655','0');
INSERT INTO [t6446] VALUES (1085,'أبا بكر فصلى بالناس وإني لشاهد غير غائب وإني لصحيح غير مريض ولو شاء أن يقدمني لقدمني فرضينا لدنيانا من رضيه الله ورسوله لديننا','822','655','0');
INSERT INTO [t6446] VALUES (1086,'قال سعيد بن المسيب فجاء بكلمة لم يأت بها أحد منهم','823','655','0');
INSERT INTO [t6446] VALUES (1087,'أخبرنا سليمان بن إبراهيم أخبرنا أبو بكر بن مردوية حدثنا محمد ابن أحمد بن محمد بن علي حدثنا أبو عوانة موسى بن يوسف حدثنا محمد ابن علي بن خلف العطار حدثنا محمد بن عمرو حدثنا الحارث الخصاف عن جعفر الأحمر عن أبي الجارود قال قالت','823','655','0');
INSERT INTO [t6446] VALUES (1088,'فصل في قول جعفر بن محمد رضي الله عنه','824','655','0');
INSERT INTO [t6446] VALUES (1089,'فصل قول أبي جعفر محمد بن علي رضي الله عنه','825','655','0');
INSERT INTO [t6446] VALUES (1090,'فصل قول علي بن الحسين رضي الله عنه','825','655','0');
INSERT INTO [t6446] VALUES (1091,'فقال كمنزلتهما اليوم هما ضجيعاه','826','655','0');
INSERT INTO [t6446] VALUES (1092,'أخبرنا أبو المظفر أخبرنا أبو الغنايم أخبرنا الدارقطني حدثنا محمد ابن مخلد حدثنا أحمد بن علي بن زياد حدثنا علي بن الجعد أخبرنا زهير ابن معاوية عن أبيه قال كان لي جار يزعم أن جعفر بن محمد يبرأ من أبي بكر وعمر رضي الله عنهم','826','655','0');
INSERT INTO [t6446] VALUES (1093,'الصديق فمن لم يسمه الصديق فلا صدق الله قوله في الدنيا والآخرة وقال الحسن بن محمد بن الحنفية يا أهل الكوفة اتقوا الله ولا تقولوا في أبي بكر وعمر ما ليسا له بأهل إن أبا بكر الصديق رضي الله عنه كان مع رسول الله','828','655','0');
INSERT INTO [t6446] VALUES (1094,'أمره بأمره فلم ينفذه فكفى هذا إزراء على علي رضي الله عنه ومنقصة أن يزعم أن رسول الله','829','655','0');
INSERT INTO [t6446] VALUES (1095,'فصل في مناقب عمر بن الخطاب رضي الله عنه','829','655','0');
INSERT INTO [t6446] VALUES (1096,'فقال له النبي','830','655','0');
INSERT INTO [t6446] VALUES (1097,'كان جبريل يذاكرني أمر عمر بن الخطاب فقلت يا جبريل اذكر لي فضائل عمر وماذا له عند الله فقال لو جلست معك ما جلس نوح في قومه ما بلغت فضائل عمر وليبكين الإسلام بعد موتك يا محمد على عمر','830','655','0');
INSERT INTO [t6446] VALUES (1098,'ما من آدمي إلا وفي سرته من تربته التي خلق منها وإني أنا وأبو بكر وعمر خلقنا من تربة واحدة وفيها نعود','831','655','0');
INSERT INTO [t6446] VALUES (1099,'ما من مولود يولد إلا وقد ثري عليه من تراب حفرته قال أبو عاصم ما نجد فضله لأبي بكر وعمر أنبل من هذا الحديث لأن طينتيهما من طينة رسول الله','831','655','0');
INSERT INTO [t6446] VALUES (1100,'يقول إذا جمع الله الأولين والآخرين يوم القيامة في صعيد واحد سمعوا صوت مناد يهتف من نحو العرش إلا لا يرفعن أحد كتابه قبل أبي بكر وعمر رضي الله عنه','832','655','0');
INSERT INTO [t6446] VALUES (1101,'أنه قال بينا أنا نائم إذ رأيتني في الجنة وإذ امرأة توضأ إلى جانب قصر فقلت لمن هذه قالوا لعمر بن الخطاب فذكرت غيرة عمر فوليت مدبرا قال','832','655','0');
INSERT INTO [t6446] VALUES (1102,'ثم قال عمر بأبي أنت يا رسول الله أعليك أغار','833','655','0');
INSERT INTO [t6446] VALUES (1103,'قال بينا أنا نائم رأيت الناس يعرضون علي وعليهم قمص منها ما يبلغ الثدي ومنا ما يبلغ دون ذلك وعرض علي عمر بن الخطاب وعليه قميص يجره قالوا فماذ أولت ذلك يا رسول الله قال الدين','833','655','0');
INSERT INTO [t6446] VALUES (1104,'فصل','833','655','0');
INSERT INTO [t6446] VALUES (1105,'يقول لو كان بعدي نبي لكان عمر','834','655','0');
INSERT INTO [t6446] VALUES (1106,'قال إن الحق نزل على قلب عمر ولسانه','834','655','0');
INSERT INTO [t6446] VALUES (1107,'فقال أقرئ عمر السلام وأخبره أن رضاه عز وغضبه حكم','835','655','0');
INSERT INTO [t6446] VALUES (1108,'مناقب عثمان بن عفان رضي الله عنه','836','655','0');
INSERT INTO [t6446] VALUES (1109,'من يحفر بئر رومة فله الجنة فحفرها عثمان وقال من جهز جيش العسرة فله الجنة فجهزه عثمان رضي الله عنه','836','655','0');
INSERT INTO [t6446] VALUES (1110,'دخل حائطا وقال احفظ الباب فجاء رجل فاستأذن قال ائذن له وبشره بالجنة فخرجت فإذا أبو بكر ثم جاء رجل فاستأذن قال أئذن له وبشره بالجنة فخرجت فإذا عمر ثم جاء رجل يستأذن فلبث رسول الله','837','655','0');
INSERT INTO [t6446] VALUES (1111,'مضطجعا في بيتي كاشفا عن فخذيه أو ساقيه فاستأذن أبو بكر رضي الله عنه فأذن له وهو كذلك يتحدث ثم استأذن عمر فأذن له وهو كذلك يتحدث ثم استأذن عثمان فجلس رسول الله','837','655','0');
INSERT INTO [t6446] VALUES (1112,'إلى عثمان بن عفان فقال شبيه إبراهيم صلوات الله عليه إن الملائكة لتستحي منه','838','655','0');
INSERT INTO [t6446] VALUES (1113,'قال','838','655','0');
INSERT INTO [t6446] VALUES (1114,'في طريق من طرق المدينة قال كيف تصنعون في فتنة تكون في أقطار الأرض كأنها صياصي بقر قالوا فنصنع ماذا يا نبي الله قال عليكم بهذا وأصحابه قال فأسرعت حتى عطفت إلى الرجل قلت هذا يا نبي الله قال هذا فإذا عثمان بن','839','655','0');
INSERT INTO [t6446] VALUES (1115,'وهو في منزل أم سلمة فلم يصادفه فلما جاء رسول الله','841','655','0');
INSERT INTO [t6446] VALUES (1116,'فأكله فاستطابه قال من بعث بهذا فقالت عثمان فرفع يديه إلى السماء وقال اللهم إن عثمان يترضاك فارض عنه','841','655','0');
INSERT INTO [t6446] VALUES (1117,'كنيته أبو الحسن','841','655','0');
INSERT INTO [t6446] VALUES (1118,'وهو عنه راض','841','655','0');
INSERT INTO [t6446] VALUES (1119,'غاضب يوما','841','655','0');
INSERT INTO [t6446] VALUES (1120,'يبتغيه فلم يجده في البيت فقال لفاطمة فأين ابن عمك قالت خرج آنفا مغضبا فأمر رسول الله','842','655','0');
INSERT INTO [t6446] VALUES (1121,'يمسح التراب عن ظهره ويقول اجلس أبا تراب','842','655','0');
INSERT INTO [t6446] VALUES (1122,'أبا بكر بن أبي قحافة الصديق برايته إلى بعض حصون خيبر فرجع ولم يكن فتح وقد جهد فقال رسول الله','842','655','0');
INSERT INTO [t6446] VALUES (1123,'علي بن أبي طالب رضي الله عنه وهو أرمد فتفل في عينينه ثم قال خذ هذه الراية فامض بها حتى يفتح الله عليك قال','842','655','0');
INSERT INTO [t6446] VALUES (1124,'فصل','843','655','0');
INSERT INTO [t6446] VALUES (1125,'يقول من أحب عليا فقد أحبني ومن أحبني فقد أحب الله عز وجل ومن أبغض عليا فقد أبغض الله عز وجل','843','655','0');
INSERT INTO [t6446] VALUES (1126,'فصل في الحث على حب الصحابة رضوان الله عليهم ونسمي محاسنهم والترحم عليهم والاستغفار لهم والكف عن مساوئهم','844','655','0');
INSERT INTO [t6446] VALUES (1127,'كلهم سنة','845','655','0');
INSERT INTO [t6446] VALUES (1128,'أن رسول الله','845','655','0');
INSERT INTO [t6446] VALUES (1129,'فقد برئ من النفاق','846','655','0');
INSERT INTO [t6446] VALUES (1130,'لا تذكروا مساويء أصحابي فتختلف قلوبكم عليهم واذكروا محاسن أصحابي حتى تتألف قلوبكم عليهم','846','655','0');
INSERT INTO [t6446] VALUES (1131,'فإن الله عز وجل قد أمر بالاستغفار لهم وهو يعلم أنهم سيقتتلون','846','655','0');
INSERT INTO [t6446] VALUES (1132,'فسبوهم','847','655','0');
INSERT INTO [t6446] VALUES (1133,'الله الله في أصحابي ولا تتخذوهم غرضا من بعدي من أحبهم فقد أحبني ومن أبغضهم فقد أبغضني ومن آذاهم فقد آذاني ومن آذاني فقد أذى الله فيوشك أن يأخذه','847','655','0');
INSERT INTO [t6446] VALUES (1134,'إن الله اختارني واختار لي أصحابا فجعل منهم وزراء وأنصارا وأصهارا فمن سبهم فعليه لعنة الله والملائكة والناس أجمعين لا يقبل الله منه يوم القيامة صرفا ولا عدلا','847','655','0');
INSERT INTO [t6446] VALUES (1135,'بسوء فاتهمه على الإسلام','848','655','0');
INSERT INTO [t6446] VALUES (1136,'لسبع ونقلت إليه بنت تسع سنين وتزوجني بكرا لم يشرك في أحد وكنت أحب الناس إليه وابنه أحب الناس إليه ونزلت في آيات كادت الأمم أن تهلك فيهن ورأيت جبريل عليه السلام ولم يره احد من نسائه غيري وقبض في بيتي ولم يله أحد إلا الملك','849','655','0');
INSERT INTO [t6446] VALUES (1137,'أن يتزوجني ولقد تزوجني بكرا وما تزوج بكرا غيري وقد قبض وإن رأسه لفي حجري ولقد قبر في بيتي ولقد حفت الملائكة بيتي وإن كان الوحي لينزل عليه في أهله فيتفرقون عنه وإن كان الوحي لينزل وأنا معه في لحافه وإني','849','655','0');
INSERT INTO [t6446] VALUES (1138,'بتزويجي وتزوجني بكرا ولم يتزوج بكرا غيري وقبض رأسه في حجري وقبر في بيتي وحفت الملائكة بيتي وكان ينزل الوحي فيتفرق عنه أهله وأنا في لحافه وأنا ابنة خليفته وصديقه ونزل عذري من السماء أو في القرآن وجعلت طيبه لطيب ووعدت مغفرة','850','655','0');
INSERT INTO [t6446] VALUES (1139,'هون على الموت إنني رأيت أنك زوجتي في الجنة','851','655','0');
INSERT INTO [t6446] VALUES (1140,'قال لها إن جبريل يقرئك السلام قالت وعليه السلام ورحمة الله وبركاته','851','655','0');
INSERT INTO [t6446] VALUES (1141,'عائشة زوجتي في الدنيا والآخرة','851','655','0');
INSERT INTO [t6446] VALUES (1142,'قال وحدثنا أبو الشيخ حدثنا إبراهيم بن محمد بن الحسن حدثنا سلم بن جنادة حدثنا أبي عن الأعمش عن معاوية بن إسحاق عن موسى بن إسحاق عن موسة بن طلحة قال جاورت عائشة رضى الله عنها ستة أشهر فما سمعت مريضا ولا ثكلى كان أشد تأوها','852','655','0');
INSERT INTO [t6446] VALUES (1143,'فصل في فضل معاوية رضي الله عنه','853','655','0');
INSERT INTO [t6446] VALUES (1144,'ما قال قال لي يا معاوية إن ملكت فأحسن','854','655','0');
INSERT INTO [t6446] VALUES (1145,'وضوءه فرفع رأسه إلي وقال أما إنك ستلي أمر أمتي من بعدي فإذا كان ذلك فاقبل من محسنهم وتجاوز عن مسيئهم فما زلت أرجوها حتى قمت مقامي هذا أخبرنا أبو المظفر حدثنا منصور بن محمد اليمانكتي حدثنا أبو محمد عبد الواحد بن الحسن','854','655','0');
INSERT INTO [t6446] VALUES (1146,'يذكر معاوية فقال اللهم اجعله هاديا مهديا واهد به','855','655','0');
INSERT INTO [t6446] VALUES (1147,'فصل في أبواب من السنة','856','655','0');
INSERT INTO [t6446] VALUES (1148,'باب في التمسك بالسنة','856','655','0');
INSERT INTO [t6446] VALUES (1149,'من أحيا سنتي فقد أحبني ومن أحبني كان معي في الجنة','856','655','0');
INSERT INTO [t6446] VALUES (1150,'من يعش حقا بلسانه جرى له أجره حتى يأتي الله يوم القيامة فيوفيه إياه وعن سعيد بن جبير في قوله وعمل صالحا ثم اهتدى قال لزوم السنة والجماعة','856','655','0');
INSERT INTO [t6446] VALUES (1151,'باب في اجتناب البدع والأهواء','858','655','0');
INSERT INTO [t6446] VALUES (1152,'فصل','859','655','0');
INSERT INTO [t6446] VALUES (1153,'أول ما يذهب من الناس العلم قالوا يا رسول الله أيذهب القرآن قال يذهب الذين يعلمونه ويبقى قوم لا يعلمونه فيتناولونه عن أهوائهم وقال الضحاك بن مزاحم كان أولكم يتعلمون الورع ويأتي عليكم زمان يتعلم فيه الكلام','859','655','0');
INSERT INTO [t6446] VALUES (1154,'فصل','860','655','0');
INSERT INTO [t6446] VALUES (1155,'وهم القائلون إن الله واحد ليس كمثله شيء وهو السميع البصير ولا يشاركه شيء من الموجودات بوجه من','860','655','0');
INSERT INTO [t6446] VALUES (1156,'وأجمعت عليه الأمة أو أجمعت الأمة على تسميته به ولا يوصف إلا بما وصف به نفسه أو وصفه به رسوله','861','655','0');
INSERT INTO [t6446] VALUES (1157,'فصل','862','655','0');
INSERT INTO [t6446] VALUES (1158,'عليكم بسنتي ومن رغب عن سنتي فليس مني','862','655','0');
INSERT INTO [t6446] VALUES (1159,'فأخذوها من معادنها وجمعوها من مظانها وحفظوها ودعوا إلى اتباعها وعابوا من خالفها وكثرت عندهم وفي أيديهم حتى اشتهروا بها كما اشتهر البزاز ببزه والتمار بتمره والعطار بعطره ورأينا قوما تنكبوا معرفتها واتباعها وطعنوا فيها وزهدوا الناس','863','655','0');
INSERT INTO [t6446] VALUES (1160,'لا يتساويان فإن من اتبع قول الرسول','864','655','0');
INSERT INTO [t6446] VALUES (1161,'فصل في بيان الرحمة التي يتراحم بها الخلق مخلوقة','864','655','0');
INSERT INTO [t6446] VALUES (1162,'يقول جعل الله الرحمة مائة جزء فأمسك عنده تسعة وتسعين وأنزل في الأرض جزءا فمن ذلك الجزء يتراحم الخلق حتى ترفع الفرس حافرها عن ولدها خشية أن تصيبه','864','655','0');
INSERT INTO [t6446] VALUES (1163,'فصل في بيان أن بني آدم خير من الملائكة','865','655','0');
INSERT INTO [t6446] VALUES (1164,'فصل في بيان أن الدجال يخرج لا محالة وقالت الجهمية الدجال كل رجل خبيث','867','655','0');
INSERT INTO [t6446] VALUES (1165,'ليس هو بالذي يضرك','867','655','0');
INSERT INTO [t6446] VALUES (1166,'ما بعث نبي إلا أنذر أمته الأعور الكذاب ألا إنه اعور وإن ربكم ليس بأعور بين عينيه مكتوب كافر','868','655','0');
INSERT INTO [t6446] VALUES (1167,'فصل في بيان منع الخروج على أولي الأمر','869','655','0');
INSERT INTO [t6446] VALUES (1168,'قال سيليكم بعدي ولاة فيليكم البر ببره والفاجر بفجوره فاسمعوا لهم وأطيعوا في كل ما وافق الحق وصلوا وراءهم فإن أحسنوا فلهم وإن أساءوا فلكم وعليهم','870','655','0');
INSERT INTO [t6446] VALUES (1169,'قال عليكم بالطاعة في منشطك ومكرهك ويسرك وزاد بعضهم وعسرك وأثرة عليك','870','655','0');
INSERT INTO [t6446] VALUES (1170,'فصل في ذكر شفاعة النبي','871','655','0');
INSERT INTO [t6446] VALUES (1171,'أنا أول من تنشق عنه الأرض وأول شافع وأول مشفع','871','655','0');
INSERT INTO [t6446] VALUES (1172,'قال رأيت ما تلقى أمتي بعدي فأحزنني وشق ذلك علي من سفك دماء بعضهم بعضا فسألته يوليني شفاعة فيهم يوم القيامة ففعل','872','655','0');
INSERT INTO [t6446] VALUES (1173,'أعطيت الشفاعة وهي نائلة من لم يشرك بالله شيئا','872','655','0');
INSERT INTO [t6446] VALUES (1174,'قال يجتمع المؤمنون يوم القيامة فيهتمون بذلك فيقولون لو استشفعنا إلى ربنا عز وجل فيريحنا من مقامنا هذا فيأتون آدم فيقولون أنت أبونا خلقك الله بيده','872','655','0');
INSERT INTO [t6446] VALUES (1175,'يجمع الأولون والآخرون في صعيد واحد ففظع الناس','874','655','0');
INSERT INTO [t6446] VALUES (1176,'فيشفع لكم إلى ربكم فانطلق فآتى جبريل فيأتي جبريل ربه عز وجل فيقول إئذن له وبشره بالجنة قال فانطلق فأخر له ساجدا قدر جمعه ثم يقول الله تبارك وتعالى ارفع رأسك وقل يسمع واشفع تشفع قال أذهب لأقع ساجدا قال فيأخذ جبريل بقبعيه فيفتح','875','655','0');
INSERT INTO [t6446] VALUES (1177,'فصل','876','655','0');
INSERT INTO [t6446] VALUES (1178,'ونهيه صغيره وكبيره بلا معارض له يعرفه من حديثه أو ناسخ له ثم قال قال رسول الله','876','655','0');
INSERT INTO [t6446] VALUES (1179,'في أدنى من أمره ونهيه عظيم فمن قبل عن النبي','876','655','0');
INSERT INTO [t6446] VALUES (1180,'مع','876','655','0');
INSERT INTO [t6446] VALUES (1181,'يخالف كتاب الله لأن الله عز وجل أعلم خلقه أن رسول الله','877','655','0');
INSERT INTO [t6446] VALUES (1182,'من الأمر شيء إلا الاتباع والتسليم ولا يعرض على قياس ولا غيره وكل ما سواها من قول الآدميين تبع لها ولا عذرا لأحد يتعمد ترك السنة ويذهب إلى غيرها لأنه لا حجة لقول أحد مع رسول الله','877','655','0');
INSERT INTO [t6446] VALUES (1183,'شيء ووجد فيها عن أصحابه رضي الله عنهم فهم الأئمة بعده والحجة اعتبارا بكتاب الله وبأخبار رسول الله','877','655','0');
INSERT INTO [t6446] VALUES (1184,'كان فيهم الأمراء والخلفاء والعلماء والفقهاء','877','655','0');
INSERT INTO [t6446] VALUES (1185,'بإصابة الحق وأقربهم إلى التوفيق لما يقرب إلى رضاه وكذلك وصفهم الرسول','878','655','0');
INSERT INTO [t6446] VALUES (1186,'مثل أصحابي مثل النجوم في السماء يهتدي بها فبأيهم أخذتم بقوله اهتديتم','878','655','0');
INSERT INTO [t6446] VALUES (1187,'عليكم بما عرفتم من سنتي وسنة الخلفاء الراشدين المهديين بعدي فحض على اتباع الخلفاء الراشدين وأجمل في','879','655','0');
INSERT INTO [t6446] VALUES (1188,'ونهيه لأنهم وإن كانوا كالنجوم فليسوا مع النبي','880','655','0');
INSERT INTO [t6446] VALUES (1189,'معهم كمثل الشمس مع النجوم إذا طلعت لم يبد معها كوكب وقد روي فيما ذكر النهي عن رسول الله','880','655','0');
INSERT INTO [t6446] VALUES (1190,'أمر أو نهي وقد حدثت حوادث بعد النبي','880','655','0');
INSERT INTO [t6446] VALUES (1191,'خلافها فهم لنا كالنجوم التي يقتدي بها في السماء','880','655','0');
INSERT INTO [t6446] VALUES (1192,'فاصطفاه لنفسه وبعثه برسالته ثم نظر في قلوب العباد بعد قلبه فوجد أصحابه خير قلوب العباد فجعلهم وزراء نبيه يقاتلون عن دينه فما رآه المؤمنون حسنا فهو عند الله حسن وما رآه المؤمنون سيئا فهو عند الله سيء قال إبراهيم النخعي لو لم','880','655','0');
INSERT INTO [t6446] VALUES (1193,'شيء لزمنا الأخذ به فإن لم نجد عنه ووجدنا عن الصحابة فكذلك فإذا جاء قول التابعين زاحمناهم قال أبو يوسف إذا جاءكم عن رسول الله','881','655','0');
INSERT INTO [t6446] VALUES (1194,'لمعاذ وما أباح عمر رضي الله عنه لشريح وما أباح ابن مسعود رضي الله عنه لأهل العلم','881','655','0');
INSERT INTO [t6446] VALUES (1195,'قال لمعاذ حين أراد يبعثه إلى اليمن قال بما تقضي قال بكتاب الله قال فإن لم تجده في كتاب الله قال فبسنة رسول الله','881','655','0');
INSERT INTO [t6446] VALUES (1196,'قال اجتهد رأيي قال الحمد لله الذي وفق رسول الله لما يرضى رسول الله','881','655','0');
INSERT INTO [t6446] VALUES (1197,'فاقض بها فإن جاءك شيء ليس في كتاب الله ولا فيه سنة رسول الله','882','655','0');
INSERT INTO [t6446] VALUES (1198,'ولم يتكلم فيه أحد قبلك فاختر أي الأمرين شئت إن شئت أن تجتهد رأيك فتقدم وإن شئت أن تتأخر فتأخر ولا أرى التأخر إلا خيرا لك','882','655','0');
INSERT INTO [t6446] VALUES (1199,'ولم يقض به الصالحون فليجتهد','882','655','0');
INSERT INTO [t6446] VALUES (1200,'فصل في مذهب أهل السنة','883','655','0');
INSERT INTO [t6446] VALUES (1201,'وكلتا يديه يمين والصفات التي وردت بها الأخبار مثل الكف والقدم والأصبع فإنه يجب إطلاق القول بها على ما ورد بها الخبر من غير أن يصور ذلك في الفكر أو تخيل أو','884','655','0');
INSERT INTO [t6446] VALUES (1202,'رسول الله وخيرته من خلقه وبعثه بشيرا ونذيرا وأن تصرفاته وأفعاله كلها حجة لنا ودلالة ويعتقدون أن الجنة والنار خلقتا للبقاء ولا يفنيان أبدا والمؤمنون كلهم يرون الله بغير حجاب ويكلمهم بلا ترجمان ويؤمنون بملائكة الله','885','655','0');
INSERT INTO [t6446] VALUES (1203,'فصل في النهي عن سب الأمراء والولاة وعصيانهم','886','655','0');
INSERT INTO [t6446] VALUES (1204,'أن لا تسبوا أمراءكم ولا تعصوهم واصبروا واتقوا الله عز وجل فإن الأمر قريب','886','655','0');
INSERT INTO [t6446] VALUES (1205,'فصل','887','655','0');
INSERT INTO [t6446] VALUES (1206,'كان ملك الموت يأتي الناس عيانا فأتى موسى فذهب بعينه فعرج إلى ربه عز وجل فقال بعثتني إلى موسى فلطمني فذهب بعيني فلولا كرامته عليك لشققت عليه قال ارجع إلى عبدي فقل له ليضع يده على ثور فله بكل شعرة وارت كفه سنة يعيشها فأتاه','887','655','0');
INSERT INTO [t6446] VALUES (1207,'في صورة دحية الكلبي ومرة في صورة أعرابي ومرة أخرى وقد سد بجناحيه ما بين الأفق وقال تعالى فأرسلنا إليها روحنا فتمثل لها بشرا سويا قيل إن جبريل تصور بصورة رجل وهذه الصور التي تنتقل تمثيلات فاللطمة أذهبت العين الذي هي تمثيل','888','655','0');
INSERT INTO [t6446] VALUES (1208,'فصل','889','655','0');
INSERT INTO [t6446] VALUES (1209,'فصل في فضل توقير الأمير','889','655','0');
INSERT INTO [t6446] VALUES (1210,'خمس من فعل واحدة منهن كان حقا على الله أن يكرمه من عاد','889','655','0');
INSERT INTO [t6446] VALUES (1211,'يقول يكون بعدي سلطان فمن أراد ذله ثغر في الإسلام ثغره ليس يسدها إلى يوم القيامة','890','655','0');
INSERT INTO [t6446] VALUES (1212,'قال بعض العلماء من تعرض لكشف ما طوى الله علمه عن خلقه أو لم يسلم ما لم يبلغه فهمه ووهمه وأنكر حكما من أحكامه أو عدم الإخلاص في قلبه والسكون إلى جميع ما نطق به كتابه أنه حق وصدق وأن ما علم من ذلك فبفضل من الله وما لم يعلمه أكثر فهو','890','655','0');
INSERT INTO [t6446] VALUES (1213,'فصل في بيان أن الله عز وجل عرض على نبيه','891','655','0');
INSERT INTO [t6446] VALUES (1214,'قال إن الله رفع لي الدنيا فأنا أنظر إليها وإلى ما هو كائن فيها إلى يوم القيامة كما أنظر إلى كفي جلاه لي','891','655','0');
INSERT INTO [t6446] VALUES (1215,'قال عرضت','891','655','0');
INSERT INTO [t6446] VALUES (1216,'قال إن الله زوى لي الأرض حتى رأيت مشارقها ومغاربها وأعطاني الكنزين الأحمر والبيض وإن ملك أمتي سيبلغ ما زوي لي منها','892','655','0');
INSERT INTO [t6446] VALUES (1217,'قام فحمد الله وأثنى عليه فقال ما من شيء كنت لم أره إلا قد رأيته في مقامي هذا حتى الجنة والنار','892','655','0');
INSERT INTO [t6446] VALUES (1218,'فقام قياما طويلا فلما انجلت الشمس قالوا يا رسول الله رأيناك تناولت شيئا من مكانك هذا ثم رأيناك تقعقت قال إني رأيت الجنة فتناولت منها عنقودا فلو أخذته لأكلتم منه ما بقيت الدنيا وإني رأيت النار فلم أركاليوم منظرا أفظع منه وإن','893','655','0');
INSERT INTO [t6446] VALUES (1219,'لقد عرضت علي الجنة والنار آنفا في عرض هذا الحائط وأنا أصلي فلم أر كاليوم في الخير والشر','893','655','0');
INSERT INTO [t6446] VALUES (1220,'فقال عرضت علي الأمم فجعل النبي يمر ومعه النفر من قومه والنبيان يمران وليس معهما أحد والنبي يمر ومعه الرهط إلى أن مر سواد عظيم قال قلت هذه أمتي فقيل هذا موسى وقومه ولكن انظر نحو الأفق فإذا سواد عظيم قد ملأ الأفق قيل','894','655','0');
INSERT INTO [t6446] VALUES (1221,'فقال هم الذين لا يسترقون ولا يكتوون ولا يتطيرون وعلى ربهم يتوكلون فقام عكاشة بن محصن الأسدي فقال أنا منهم يا رسول الله فقال أنت منهم وقال رجل آخر فقال أنا منهم يا رسول الله فقال سبقك بها عكاشة','894','655','0');
INSERT INTO [t6446] VALUES (1222,'يدل على هذا قوله تعالى والله خلقكم وما تعلمون وقوله تعالى','894','655','0');
INSERT INTO [t6446] VALUES (1223,'ليس لك من الأمر شيء أي ليس لك فيما تتصرف فيه من الأمر والأعمال والقدرة عليها شيء ولا يمكنك أن تخلق عملا أو فعلا من نفسك بل ما تفعله وتعمله مخلوقا بخلق الله تعالى إياه ومفعوله بقدرته وتوفيقه لا حول ولا قوة إلا بالله','895','655','0');
INSERT INTO [t6446] VALUES (1224,'فصل في ذكر آية تدل على وحدانية الله تعالى في خلق الشمس والقمر','897','655','0');
INSERT INTO [t6446] VALUES (1225,'قال يوما أتدرون أين تذهب هذه الشمس قالوا الله ورسوله أعلم قال إن هذه تجري حتى تنتهي إلى مستقرها تحت العرش فتخر ساجدة فلا تزال كذلك حتى يقال لها ارتفعي ارجعي من حيث جئت فترجع فتصبح طالعة من مطلعها ثم تجري حتى تنتهي إلى','897','655','0');
INSERT INTO [t6446] VALUES (1226,'إذ رمي بنجم فاستنار فقال لهم رسول الله','898','655','0');
INSERT INTO [t6446] VALUES (1227,'إنها لا ترمي لموت أحد ولا لحياته ولكن ربنا إذا قضى أمرا سبحت حملة العرش ثم سبحت أهل ثم سبح أهل السماء الذين يلونهم ثم سبح أهل السماء الذين يلونهم حتى يبلغ التسبيح أهل السماء الدنيا ثم يقول الذين يلون حملة العرش ماذا قال ربكم','898','655','0');
INSERT INTO [t6446] VALUES (1228,'أنه قال إن الزمان قد استدار كهيئته بعد خلق الله السماوات والأرض السنة اثنا عشر شهرا فيها أربعة حرم ثلاثة ذو القعدة وذو الحجة والمحرم ورجب مضر الذي بين جماد وشعبان وهكذا رواه الثقفي عن أيوب ولم يسم ابن أبي بكرة وسماه ابن عون','899','655','0');
INSERT INTO [t6446] VALUES (1229,'قال بينما رجل بفلاة إذا سمع رعدا في سحاب فسمع فيه كلاما اسق حديقة فلان باسمه فجاء ذلك السحاب إلى حرة فأفرغ ما فيه من الماء ثم جاء إلى ذناب شرج فانتهى إلى شرجة فاستوعب الماء ومشى الرجل مع السحاب حتى انتهى إلى رجل قائم في حديقة','900','655','0');
INSERT INTO [t6446] VALUES (1230,'إذا كان يوم ذو الريح والغيم عرف ذلك في وجهه وأقبل وأدبر فإذا أمطرت سر به وذهب عنه ذلك قالت فسألته فقال إني خشيت أن يكون عذابا سلط على أمتي ويقول إذا رأى المطر رحمة','901','655','0');
INSERT INTO [t6446] VALUES (1231,'قال لما خلق الله الأرض جعلت تميد فخلق الجبال عليها فاستقرت فعجبت الملائكة فقالت يا رب هل من خلقك أشد من الجبال قال الحديد قالت يا رب هل من خلقك أشد من الحديد قال نعم النار قالت يا رب هل من خلقك أشد من النار قال نعم','902','655','0');
INSERT INTO [t6446] VALUES (1232,'ما يكفر رجل رجلا إلا باء به أحدهما فإن كان كافرا إنه لكما قال وإن كان مسلما فقد كفر بتكفيره إياه','903','655','0');
INSERT INTO [t6446] VALUES (1233,'إن أخوف ما أخاف عليكم رجل آتاه الله علما بالقرآن حتى إذا عرف الإسلام ورأى عليه بهجته اخترط سيفه فضرب به جاره ورماه بالكفر قالوا يا رسول الله أيهما أولى بالكفر الرامي أم المرمي قال بل الرامي','903','655','0');
INSERT INTO [t6446] VALUES (1234,'أخوف ما أخاف على أمتي رجل قد قرأ القرآن حتى إذا لم يبق عليه ألف ولا واو رمى جاره بالكفر وخرج عليه بالسيف قال قلت يا رسول الله أيهما أولى بالكفر الرامي أو المرمي قال الرامي','904','655','0');
INSERT INTO [t6446] VALUES (1235,'لا يرمي رجل رجلا بفسق أو كفر إلا أتت على صاحبه إن لم يكن كذلك','904','655','0');
INSERT INTO [t6446] VALUES (1236,'لو عذب الله أهل سماواته وأهل أرضه بدم امرئ مسلم لعذبهم وهو غير ظالم لهم ولو لقي رجل ربه بدم أهل السماوات والأرض أرجا له من أن يقول لأخيه المسلم يا كافر قال أحمد بن منصور سمع مني هذا الحديث بندار بن حسين فقال هذا تأكيد','905','655','0');
INSERT INTO [t6446] VALUES (1237,'من صلى صلاتنا واستقبل قبلتنا وأكل ذبيحتنا فذلك المسلم له ذمة الله وذمة رسوله','906','655','0');
INSERT INTO [t6446] VALUES (1238,'فصل في مسالة المعدوم والموجود','907','655','0');
INSERT INTO [t6446] VALUES (1239,'فصل','910','655','0');
INSERT INTO [t6446] VALUES (1240,'فصل','911','655','0');
INSERT INTO [t6446] VALUES (1241,'أتى أبو بكر رضي الله عنه فصعد المنبر فحمد الله وأثنى عليه وقال إن كان محمد إلهكم الذي يعبد فإن إلهكم قد مات وإن كنتم تعبدون إلهكم الذي في السماء فإن إلهكم حي لا يموت ثم تلا وما محمد إلا رسول قد خلت من قبله الرسل','911','655','0');
INSERT INTO [t6446] VALUES (1242,'لما أراد أن يوجه معاذا إلى اليمن استشار أناسا من أصحابه فيهم أبو بكر وعمر وعثمان وعلي وطلحة والزبير وأسيد بن حضير فقال أبو بكر لولا أنك تستشيرنا ما تكلمنا قال','912','655','0');
INSERT INTO [t6446] VALUES (1243,'إن الله تعالى يكره من فوق سمائه أن يخطئ أبو بكر أو قال أن يخطئ أبو بكر','912','655','0');
INSERT INTO [t6446] VALUES (1244,'أنا أعظم نسائك عليك','912','655','0');
INSERT INTO [t6446] VALUES (1245,'إن الله أنكحني من السماء','913','655','0');
INSERT INTO [t6446] VALUES (1246,'قال أهل السنة لا نرى أحدا مال إلى هوى أو بدعة إلا وجدته متحيرا ميت القلب ممنوعا من النطق بالحق وقالوا الكلام في الرب عز وجل بدعة لأنه لا يجوز أن يتكلم في الرب عز وجل إلا بما وصف به نفسه في القرآن وما بينه رسوله','913','655','0');
INSERT INTO [t6446] VALUES (1247,'فصل','914','655','0');
INSERT INTO [t6446] VALUES (1248,'فصل في قصة الدجال','915','655','0');
INSERT INTO [t6446] VALUES (1249,'ذات يوم فكان آخر خطبته ما يحدثنا عن الدجال ويحذرنا فكان قوله يا أيها الناس إنه لم تكن فتنة في الأرض أعظم من فتنة الدجال وإن الله لم يبعث نبيا قط إلا حذر أمته وأنا آخر الأنبياء وأنتم آخر الأمم وهو خارج فيكم لا محالة فإن يخرج','915','655','0');
INSERT INTO [t6446] VALUES (1250,'فصل','917','655','0');
INSERT INTO [t6446] VALUES (1251,'فصل','917','655','0');
INSERT INTO [t6446] VALUES (1252,'يصلون صلاة الجمعة والأعياد خلف أئمة الجور والصلاة معه سنة قائمة في تركها معهم هلكة قال النبي','918','655','0');
INSERT INTO [t6446] VALUES (1253,'فصل','918','655','0');
INSERT INTO [t6446] VALUES (1254,'فصل','919','655','0');
INSERT INTO [t6446] VALUES (1255,'فقيل له ما هذا فقال من الله العلم وعلى رسوله البلاغ وعلينا التسليم قال أهل السنة أداء الفرائض وأعمال الجوارح من الطاعات هي من الإيمان وللنبيين شفاعة يوم القيامة وللصديقين والصالحين ولله عز وجل تفضل كثير على من يشاء','919','655','0');
INSERT INTO [t6446] VALUES (1256,'وترك معارضتها بكيف ولم والكلام والخصومات في الدين والجدال محدث وهو يوقع الشك في القلوب ويمنع من معرفة الحق والصواب وليس العلم بكثرة الرواية وإنما هو الاتباع والاستعمال يقتدى بالصحابة والتابعين وإن كان قليل العلم ومن','920','655','0');
INSERT INTO [t6446] VALUES (1257,'وإذا طلق الرجل امرأته ثلاثا فقد حرمت عليه لا تحل له حتى تنكح زوجا غيره والشراء والبيع في أسواق المسلمين حلال بحكم الكتاب والسنة ومتعة النساء حرام إلى يوم القيامة','920','655','0');
INSERT INTO [t6446] VALUES (1258,'فصل يدل على أن العين حق','921','655','0');
INSERT INTO [t6446] VALUES (1259,'لو قلت إن شيئا يسبق القدر لقلت العين تسبق القدر','921','655','0');
INSERT INTO [t6446] VALUES (1260,'أكثر من يموت من أمتي بعد كتاب الله وقضائه وقدره بالأنفس يعني العين','922','655','0');
INSERT INTO [t6446] VALUES (1261,'إن الله عز وجل بفضله وكرمه أنزل كتابه فكشف به الحيرة وأتم به الحجة علينا ولم يفرط في شيء فيه حتى يحوجنا إلى استعمال الرأي والعقل قال تعالى اتبعوا المرسلين اتبعوا من لا يسألكم أجرا وهم مهتدون وقال لقد كان لكم في رسول','922','655','0');
INSERT INTO [t6446] VALUES (1262,'خطا ثم قال هذا سبيل الله وخط خطوطا عن يمينه وعن شماله فقال','922','655','0');
INSERT INTO [t6446] VALUES (1263,'السنة لأمته وأوضحها لأصحابه فمن خالف أصحاب رسول الله','923','655','0');
INSERT INTO [t6446] VALUES (1264,'فصل','923','655','0');
INSERT INTO [t6446] VALUES (1265,'يحكي عن موسى عليه السلام على المنبر قال وقع في نفس موسى عليه السلام هل ينام الله عز وجل فأرسل الله عز وجل إليه ملكلا فأرقه ثلاثا وأعطاه قارورتين في كل يد قارورة وأمره أن يتحفظ بهما فجعل ينام وتكاد يداه تلتقيان فيحبس أحداهما','924','655','0');
INSERT INTO [t6446] VALUES (1266,'إلى الطائف ماشيا على قدميه فدعاهم إلى الله عز وجل فلم يجيبوه فأتى ظل شجرة فصلى تحتها ركعتين ثم قال اللهم إليك أشكو ضعفي وهواني على الناس أرحم الراحمين أنت أرحم الراحمين إلى من تكلني إلى بعيد يتجهمني أم إلى قريب ملكته أمري','924','655','0');
INSERT INTO [t6446] VALUES (1267,'بأربع فقال إن الله عز وجل لا ينام ولا ينبغي له أن ينام يخفض القسط ويرفعه يرفع إليه عمل الليل قبل النهار وعمل النهار قبل الليل حجابه النار لو كشفها لأحرقت سبحات وجهه كل شيء أدركه بصره','925','655','0');
INSERT INTO [t6446] VALUES (1268,'من استعاذ بالله عز وجل فأعيذوه ومن سأل بوجه الله فأعطوه','925','655','0');
INSERT INTO [t6446] VALUES (1269,'ملعون من سأل بوجه الله وملعون من سئل بوجه الله فرد سائله','926','655','0');
INSERT INTO [t6446] VALUES (1270,'علمه وأمره أن يتعاهد به أهله كل صباح لبيك اللهم لبيك وسعديك والخير في يديك ومنك وبك وإليك اللهم ما قلت من قول أو نذرت من نذر أو خلقت من خلق فمشيئتك بين يديه ما شئت كان وما لم تشأ لم يكن ولا حول ولا قوة إلا بالله والله على كل','926','655','0');
INSERT INTO [t6446] VALUES (1271,'فقام رجل فاتبعه وهو عطاء بن السائب فسأله عن الدعاء','927','655','0');
INSERT INTO [t6446] VALUES (1272,'فصل','928','655','0');
INSERT INTO [t6446] VALUES (1273,'وإذا رأيت الذين يخوضون في آياتنا فأعرض عنهم فأمره بالإعراض عنهم ثم أمر نبيه','928','655','0');
INSERT INTO [t6446] VALUES (1274,'فقد كفانا الله','928','655','0');
INSERT INTO [t6446] VALUES (1275,'لا تسافروا بالقرآن إلى أرض العدو ولو اراد بالقرآن الحبر لنهاهم عن السفر بالمحابر','931','655','0');
INSERT INTO [t6446] VALUES (1276,'لجابر حين شكا إليه رمد العين انظر بالمصحف أراد به النظر إلى القرآن الذي أنزله الله تعالى شفاء للمسلمين','931','655','0');
INSERT INTO [t6446] VALUES (1277,'من قرأ القرآن نظرا فله بختمه ألفا درجة ومن قرأ حفظا فله ألف درجة فولا أن يصح النظر لم يكن لتضعيف الثواب معنى وصح أنه أراد به النظر إلى كلامه الذي هو مخلوق','931','655','0');
INSERT INTO [t6446] VALUES (1278,'فصل','932','655','0');
INSERT INTO [t6446] VALUES (1279,'وقال فأجره حتى يسمع كلام الله وقال تعالى لا تحرك به لسانك لتعجل به فهو على عرشه وكلامه يجري على ألسنتنا وهو محفوظ في قلوبنا مكتوب مرئي كما قال إن الله تعالى لا كيفية له فكذلك كلامه لا مثل له وصفاته لا','933','655','0');
INSERT INTO [t6446] VALUES (1280,'فصل','934','655','0');
INSERT INTO [t6446] VALUES (1281,'أو أجمع عليه المسلمون سأل أبو العباس الناشيء أبا إسحاق الزجاج فقال الله عز وجل الرحيم ويقول فلان رحيم كيف نفرق بينهما وإنما الرحمة رقة فكيف جاز أن يوصف الله عز وجل بها فحاد','934','655','0');
INSERT INTO [t6446] VALUES (1282,'فصل','936','655','0');
INSERT INTO [t6446] VALUES (1283,'وإلى قول الأئمة فإن لم نجد ذلك في كتاب الله ولا في سنة رسوله','936','655','0');
INSERT INTO [t6446] VALUES (1284,'وما قاله أحد من الصحابة والتابعين وأول من تلكم به الحسين الكرابيسي فأنكر عليه أحمد ابن حنبل قوله أشد الإنكار ونهى عن مجالسته فمات مهجورا فلم ينتفع بعلمه ومن الدليل على بطلان قولهم من كتاب الله عز وجل قوله تعالى وإن أحد من','936','655','0');
INSERT INTO [t6446] VALUES (1285,'فقالوا إنا سمعنا قرآنا عجبا يهدي إلى الرشد فامنا به فآمنت طائفة من أهل الإسلام أن يقولوا قرآن إن هذا للعجب العجيب وأما بيان ذلك من قول النبي','937','655','0');
INSERT INTO [t6446] VALUES (1286,'يعرض نفسه على الناس بالموقف فيقول ألا رجل يحملني إلى قومه فإن قريشا منعوني أن أبلغ كلام ربي قال ولم يقل أن أبلغ حكاية كلام ربي','937','655','0');
INSERT INTO [t6446] VALUES (1287,'إن هذه الصلاة لا يصلح فيها شيء من كلام الناس قال أبو عثمان من أمر السنة على نفسه قولا وفعلا نطق بالحكمة ومن أمر الهوى على نفسه نطق بالبدعة لأن الله تعالى يقول وإن تطيعوه تهتدوا','937','655','0');
INSERT INTO [t6446] VALUES (1288,'فصل','938','655','0');
INSERT INTO [t6446] VALUES (1289,'ما ضل قوم بعد هدى كانوا عليه إلا أوتوا الجدل ثم قرأ ما ضربوه لك إلا جدلا','938','655','0');
INSERT INTO [t6446] VALUES (1290,'فصل','939','655','0');
INSERT INTO [t6446] VALUES (1291,'فصل','940','655','0');
INSERT INTO [t6446] VALUES (1292,'فلا معنى للمعارضة وقد صح أنه قال','941','655','0');
INSERT INTO [t6446] VALUES (1293,'أنه قال','942','655','0');
INSERT INTO [t6446] VALUES (1294,'والحق أن الحديث إذا صح عن النبي','942','655','0');
INSERT INTO [t6446] VALUES (1295,'فصل','943','655','0');
INSERT INTO [t6446] VALUES (1296,'كان متعبدا بشريعة من كان قبله من الأنبياء خلافا لمن قال لم يكن متعبدا دليلنا قوله تعالى أولئك الذين هدى الله فبهداهم اقتداه فذكر الله أنبياءه إبراهيم وإسماعيل وإسحاق وغيرهم وأخبر أنه هداهم وأمرنا باتباعهم فيما','943','655','0');
INSERT INTO [t6446] VALUES (1297,'فصل','944','655','0');
INSERT INTO [t6446] VALUES (1298,'الزيادة في النص ليست بنسخ','944','655','0');
INSERT INTO [t6446] VALUES (1299,'فصل في بيان أن الأرواح بيد الله في حالة الموت والحياة والنوم والانتباه','946','655','0');
INSERT INTO [t6446] VALUES (1300,'طرقه وفاطمة فقال ألا تصلون فقلت يا رسول الله','946','655','0');
INSERT INTO [t6446] VALUES (1301,'حين قلت له ذلك وهو يضرب فخذه ويقول وكان الإنسان أكثر شيء جدلا','946','655','0');
INSERT INTO [t6446] VALUES (1302,'مرجعه من خيبر فقال من يحفظ علينا صلاتنا فقال بلال أنا قال فما استيقظوا إلا بحر الشمس فقال رسول الله','947','655','0');
INSERT INTO [t6446] VALUES (1303,'فصل في بيان أن الله الممرض والمداوي والشافي','947','655','0');
INSERT INTO [t6446] VALUES (1304,'قال بلى قال قل اللهم رب الناس أذهب البأس وأشف أنت الشافي لا شافي إلا أنت شفاء لا يغادر سقما','947','655','0');
INSERT INTO [t6446] VALUES (1305,'فصل في قول النبي','948','655','0');
INSERT INTO [t6446] VALUES (1306,'قال يعني لعمر إن الله ينهاكم أن تحلفوا بآبائكم من كان حالفا فليحلف بالله أو ليسكت','949','655','0');
INSERT INTO [t6446] VALUES (1307,'وقال إنها شرك','949','655','0');
INSERT INTO [t6446] VALUES (1308,'لا ينظر الله يوم القيامة إلى من جر إزاره بطرا','950','655','0');
INSERT INTO [t6446] VALUES (1309,'من جر ثيابه خيلاء لم ينظر الله إليه يوم القيامة فقال أبو بكر رضي الله عنه أي رسول الله إن أحد شقي إزاري يسترخي إلا أن أتعاهد ذلك منه فقال لست ممن يصنعه خيلاء','950','655','0');
INSERT INTO [t6446] VALUES (1310,'فصل','953','655','0');
INSERT INTO [t6446] VALUES (1311,'في أخباره الصحيحه عند أهل النقل والسلف المشهورين بالسنة المعروفين بالصدق والعدالة وجميع آيات الصفات التي في القرآن والأخبار الصحاح في الصفات التي نقلها أهل الحديث واجب على جميع المسلمين أن يؤمنوا بها ويسلموها ويتركوا السؤال فيه','953','655','0');
INSERT INTO [t6446] VALUES (1312,'في أخباره مثل قوله','954','655','0');
INSERT INTO [t6446] VALUES (1313,'حق وصعود أرواح المؤمنين إليه حق وغير ذلك مما صح عنه وثبت فعلى العبد أن يؤمن بجميع ذلك ولا يؤوله تأويل المخالفين ولا يمثله تمثيل الممثلين ولا يزيد فيه ولا ينقص عنه ولا يفسر منه إلا ما فسره السلف ويمره على ما أمروا ويقف حيث','955','655','0');
INSERT INTO [t6446] VALUES (1314,'فصل','956','655','0');
INSERT INTO [t6446] VALUES (1315,'فسار ذلك اليوم إلى الليل فلما كان الليل نزل وعسكر الناس ونام هو وأبو طلحة زوج أم أنس وفلان وفلان أربعة فتوسد النبي','956','655','0');
INSERT INTO [t6446] VALUES (1316,'عند راحلته فذهبوا يلتمسون النبي','956','655','0');
INSERT INTO [t6446] VALUES (1317,'كنت نائما حيث وأنتم فسمعت في نومتي دويا كدوي الرحا أو هزيزا كهزيز الرحا ففزعت في منامي فوثبت','956','655','0');
INSERT INTO [t6446] VALUES (1318,'والأربعة حتى استقبله عشرة فقالوا أين كان نبينا نبي الرحمه','957','655','0');
INSERT INTO [t6446] VALUES (1319,'فحدثهم بالذي حدث القوم فنادوا بأجمعهم أي جعلنا الله فداك اجعلنا ممن تشفع لهم يوم القيامة ثم نادى ثلاثا إني أشهد الله وأشهد من سمع أن شفاعتي لمن يموت لا يشرك بالله شيئا قالها ثلاثا','957','655','0');
INSERT INTO [t6446] VALUES (1320,'في هذه الآية شيئا ربما يود الذين كفروا لو كانوا مسلمين قال نعم سمعته يقول يخرج الله','957','655','0');
INSERT INTO [t6446] VALUES (1321,'يخرج ضبارة من النار قد كانوا فحما فقال بثوهم في الجنة ورشوا عليهم من الماء فينبتون كما تنبت الحبة في حميل السيل فقال رجل من القوم يا رسول الله كأنما كنت من أهل البادية','958','655','0');
INSERT INTO [t6446] VALUES (1322,'قال بعض علماء أهل السنة أما بعد فإني وجدت جماعة من مشايخ السلف وكثيرا ممن تبعهم من الخلف من عليهم المعتمد في أبواب الديانة وبهم القدوة في استعمال السنة قد أظهروا اعتقادهم وما انطوت عليه ضمائرهم في','958','655','0');
INSERT INTO [t6446] VALUES (1323,'من صفاته جل جلاله بنقل العدول والأسانيد المتصلة التي اجتمع عليها أهل المعرفة بالنقل أنها صحيحة ثابتة عن نبي الله','963','655','0');
INSERT INTO [t6446] VALUES (1324,'ولا نكيف صفات الله عز وجل ولا نفسرها تفسير أهل التكييف والتشبيه ولا نضرب لها الأمثال بل نتلقاها بحسن القبول تصديقا ونطلق ألفاظها تصريحا كما قال الله عز وجل في كتابه وكما قال رسول الله','963','655','0');
INSERT INTO [t6446] VALUES (1325,'كما سمعنا ولا نقول في صفات الله كما قالت الجهمية والمعطلة بل نثبت صفات الله تعالى بإيمان وتصديق قال الأوزاعي أقروا أحاديث رسول الله','963','655','0');
INSERT INTO [t6446] VALUES (1326,'فصل يتعلق باعتقاد أهل السنة ومذهبهم','964','655','0');
INSERT INTO [t6446] VALUES (1327,'وطاعة أولي الأمر واجبة وهي من أوكد السنن ورد بها الكتاب والسنة ولا طاعة لمخلوق في معصية الخالق','964','655','0');
INSERT INTO [t6446] VALUES (1328,'فصل','965','655','0');
INSERT INTO [t6446] VALUES (1329,'والخوارج تبرأوا من عثمان وعلي رضي الله عنه وقالوا نكفر أهل الكبائر وأن من لم يقل بقولهم فهو كافر والقدرية يزعمون أن ليس لله في كفر العباد ومعاصي العباد صنع والجهمية لا يصفون الله بالسمع والبصر والاستواء على العرش','965','655','0');
INSERT INTO [t6446] VALUES (1330,'فصل الدليل على أن القرآن منزل وهو ما يقرأه القارئ خلافا لمن يقول كلام الله ليس بمنزل ولا حرف ولا صوت','966','655','0');
INSERT INTO [t6446] VALUES (1331,'فصل فيما روي عن النبي','967','655','0');
INSERT INTO [t6446] VALUES (1332,'فصل في ذهاب العلم','969','655','0');
INSERT INTO [t6446] VALUES (1333,'أول ما يذهب من الناس العلم قالوا يا رسول الله أيذهب القرآن قال يذهب الذين يعلمونه ويبقى قوم لا يعلمونه فيتأولونه على أهوائهم','969','655','0');
INSERT INTO [t6446] VALUES (1334,'إنهم كانوا أبر هذه الأمة قلوبا وأعمقها علما وأقلها تكلفا وأقومها هديا وأحسنها أخلاقا اختارهم الله عز وجل لصحبه نبيه','970','655','0');
INSERT INTO [t6446] VALUES (1335,'إذ قال هذا أوان العلم أن يرفع قلنا يا رسول الله يرفع العلم وعندنا كتاب الله عز وجل وقد قرأناه وعلمناه نساءنا وصبياننا فذكر ضلال أهل الكتابين اليهود والنصارى ثم قال ذهابه بذهاب أوعيته قال شداد بن أوس صدوق عوف وأول ما','970','655','0');
INSERT INTO [t6446] VALUES (1336,'قال لا تقوم الساعة حتى يقبض العلم ويكثر الكذب وتظهر الفتن وقال الضحاك بن مزاحم كان أولكم يتعلمون الورع ويأتي عليكم زمان يتعلم فيه الكلام وقال أبو هلال قلت لقتادة ألا تعجب من محمد بن سيرين يتورع في الفتيا ويعبر','971','655','0');
INSERT INTO [t6446] VALUES (1337,'قال عارف الحق كعامله','973','655','0');
INSERT INTO [t6446] VALUES (1338,'قال يا ابن مسعود هل تدري أي المؤمنين','973','655','0');
INSERT INTO [t6446] VALUES (1339,'فصل في الرؤية','975','655','0');
INSERT INTO [t6446] VALUES (1340,'ليلة البدر فقال إنكم ترون ربكم يوم القيامة كما ترون هذا لا تضامون في رؤيته','976','655','0');
INSERT INTO [t6446] VALUES (1341,'فصل','977','655','0');
INSERT INTO [t6446] VALUES (1342,'قال سفيان الثوري ديننا دين العجائز والصبيان قالوا وقد قال الله عز وجل وإذا رأيت الذين يخوضون في آياتنا فأعرض عنهم حتى يخوضوا في حديث غيره كيف يجتريء عاقل على المراء والجدال بعد قول الله عز وجل ما يجادل في','977','655','0');
INSERT INTO [t6446] VALUES (1343,'فصل','978','655','0');
INSERT INTO [t6446] VALUES (1344,'وهم الذين ذكرهم الله عز وجل في كتابه إنما يريد الله ليذهب عنكم الرجس أهل البيت ويطهركم تطهيرا وقال عز وجل قل لا أسألكم عليه أجرا إلا المودة في القربى','978','655','0');
INSERT INTO [t6446] VALUES (1345,'إني تارك فيكم الثقلين كتاب الله وعترتي فمن عترته فاطمة بنت محمد','978','655','0');
INSERT INTO [t6446] VALUES (1346,'فصل','978','655','0');
INSERT INTO [t6446] VALUES (1347,'فصل','979','655','0');
INSERT INTO [t6446] VALUES (1348,'ثلاثة لا ينظر الله إليهم يوم القيامة الإمام الكذاب والشيخ الزاني والعائل المزهو وفي رواية أسامة عاق لوالديه ومدمن خمر ومنان بما أعطى','979','655','0');
INSERT INTO [t6446] VALUES (1349,'لا ينظر الله إلى امرأة لا تعرف حق زوجها وهي لا تستغني عنه مذهب أهل السنة أنه لا يجوز وصف الله تعالى بأنه راء بصير وقال ابن فورك لا يجوز وصفه بأنه ناظر نظرا هو رؤية لأنه لا يجوز أن نثبت له إلا ما وصف بها نفسه أو وصفه رسوله','980','655','0');
INSERT INTO [t6446] VALUES (1350,'وقال تعالى ويستخلفكم في الأرض فينظر كيف تعملون فوصف نفسه بالنظر','980','655','0');
INSERT INTO [t6446] VALUES (1351,'قال إن الله عز وجل لا ينظر إلى صوركم وألوانكم ولكن إنما ينظر إلى قلوبكم وأعمالكم','980','655','0');
INSERT INTO [t6446] VALUES (1352,'فصول مستخرجه من كتب السنة','981','655','0');
INSERT INTO [t6446] VALUES (1353,'فصل من كتاب الرد على أهل الأهواء لأبي زرعة الرازي','981','655','0');
INSERT INTO [t6446] VALUES (1354,'ونحن نذكر الفقر ونتخوفه فقال الفقر تخافون والذي نفسي بيده لتصبن عليكم الدنيا صبا حتى لا يزيغ قلب أحد منكم إلا هيه وأيم الله لأتركنكم على البيضاء ليلها ونهارها سواء قال أبو الدرداء صدق رسول الله','981','655','0');
INSERT INTO [t6446] VALUES (1355,'نفاق وقال أيوب السختياني إذا حدثت الرجل بالسنة فقال دعنا من هذا وحدثنا القرآن فاعلم أنه ضال','982','655','0');
INSERT INTO [t6446] VALUES (1356,'فيقوم أحدهم فينفض ثوبه يقول لا إلا القرآن وما يعمل من القرآن بحرف وعن مالك بن أنس عن ربيعة بن أبي عبد الرحمن قال إن الله تبارك وتعالى أنزل القرآن وترك فيه موضع السنة','982','655','0');
INSERT INTO [t6446] VALUES (1357,'فصل من كتاب السنة لعبد الله بن أحمد بن حنبل رحمه الله','983','655','0');
INSERT INTO [t6446] VALUES (1358,'فصل','984','655','0');
INSERT INTO [t6446] VALUES (1359,'وعنده عائشة فقال لي كيف أنت وقوم كذا وكذا قلت الله ورسوله أعلم قال قوم يخرجون من قبل المشرق يقرأون القرآن لا يجاوز تراقيهم يمرقون من الدين كما يمرق السهم من الرمية فيهم رجل مخدج اليد كأن يده ثدي حبشية أنشدكم الله هل','984','655','0');
INSERT INTO [t6446] VALUES (1360,'فيك مثل من عيسى أبغضه يهود حتى بهتوا أمه وأحبه النصارى حتى أنزلوه بالمنزلة التي ليس لها ثم قال علي رضي الله عنه هلك في رجلان محب مفرط ومبغض مفرط يقرظني بما ليس في ومبغض يحمله شنآني على أن ينتهي','985','655','0');
INSERT INTO [t6446] VALUES (1361,'يقولون يا أمير المؤمنين أفنكذبهم وقد حج الناس وقطع ورجم أيكون هذا إلا خليفة قلت لأبي من احتج بحديث عبيدة','986','655','0');
INSERT INTO [t6446] VALUES (1362,'في الخلافة ثلاثون قال سفينة فخذ سنتي أبو بكر وعشر عمر وثنتي عشرة عثمان وست علي','987','655','0');
INSERT INTO [t6446] VALUES (1363,'مسجد المدينة جاء النبي','987','655','0');
INSERT INTO [t6446] VALUES (1364,'ثلاثون سنة قال سفينة فأتمهما علي رضى الله عنه ثلاثين','987','655','0');
INSERT INTO [t6446] VALUES (1365,'لا صلاة إلا بقراءة فاتحة الكتاب سواء أن كان المرء إماما أو مأموما لحديث عبادة بن الصامت رضي الله عنه عن النبي','987','655','0');
INSERT INTO [t6446] VALUES (1366,'مبين ذلك في حديث الزهري عن سعيد بن المسيب','988','655','0');
INSERT INTO [t6446] VALUES (1367,'قال لعبد الله بن زيد ألقه على بلال فإنه أندى صوتا منك وأما ما روي في حديث أبي محذورة','988','655','0');
INSERT INTO [t6446] VALUES (1368,'فصل','990','655','0');
INSERT INTO [t6446] VALUES (1369,'قال حسبنا كتاب الله فاعلم أنه صاحب بدعة وإذا رأيت الرجل إذا قيل له لم لا تكتب الحديث يقول العقل أولى فاعلم أنه صاحب بدعة وإذا رأيته يمدح الفلسفة والهندسة ويمدح الذين ألفوا الكتب فيها فاعلم أنه ضال وإذا رأيت الرجل يسمي أهل','991','655','0');
INSERT INTO [t6446] VALUES (1370,'ومن السنة العقد على النساء الثيب اللاتي لا أولياء لهن إلى الإمام لا إلى أنفسهن وأن كل شراب يسكر كثيرة فقليله حرام سواء اتخذ من','991','655','0');
INSERT INTO [t6446] VALUES (1371,'إن الشمس تغرب في كل ليلة فتقع تحت العرش ساجدة فتستأذن لها بالطلوع من مطلعها فإذا قربت القيامة تستأذن فيؤذن لها بالطلوع من مغربها','992','655','0');
INSERT INTO [t6446] VALUES (1372,'قال بعض العلماء الدين لا يدرك بالعقل والعقل نوعان غريزي واكتسابي فالغريزي ما يكون موجودا مع المولود كعقله للارتضاع وأكل الطعام وضحكه مما يسره وبكائه مما لا يهواه وامتناعه مما يضره كل هذا يعقله بالعقل الغريزي','992','655','0');
INSERT INTO [t6446] VALUES (1373,'بالمشاورة في الأمر مع تمام عقله ووفور رأيه فقال وشاروهم في الأمر أي لا تتكل على عقلك وحده فدل هذا على ما قلناه قال بعض العلماء لا يوصف الله بكونه عاقلا ويوصف بكونه عالما فدل أن العلم أقوى من العقل','996','655','0');
INSERT INTO [t6446] VALUES (1374,'قال أهل السنة الكف عن مساويء أصحاب محمد','996','655','0');
INSERT INTO [t6446] VALUES (1375,'فصل','997','655','0');
INSERT INTO [t6446] VALUES (1376,'فصل','998','655','0');
INSERT INTO [t6446] VALUES (1377,'فصل','998','655','0');
INSERT INTO [t6446] VALUES (1378,'فصل','999','655','0');
INSERT INTO [t6446] VALUES (1379,'فصل','999','655','0');
INSERT INTO [t6446] VALUES (1380,'فصل','1000','655','0');
INSERT INTO [t6446] VALUES (1381,'فصل','1000','655','0');
INSERT INTO [t6446] VALUES (1382,'شق صدره فأخرج منه حظ الشيطان ثم أعيد مكانه معجزة له خاصة دون البشر إذ البشر لو فعل ذلك بهم ماتوا','1000','655','0');
INSERT INTO [t6446] VALUES (1383,'ولا نعارض سنة النبي','1000','655','0');
INSERT INTO [t6446] VALUES (1384,'فصل','1001','655','0');
INSERT INTO [t6446] VALUES (1385,'وقالت لا تصح وسموا أصحاب الحديث حشوية وقالوا الخبر يدخله الصدق والكذب وكل ما تردد بين الصدق والكذب فهو شك وتأولت أسماء الله تعالى وصفاته وقالت إن الله لا يشاء المعاصي ولا يقدرها على العبد ونفت حديث النزول وحديث القدم','1001','655','0');
INSERT INTO [t6446] VALUES (1386,'فصل','1002','655','0');
INSERT INTO [t6446] VALUES (1387,'إلى السماء ليلة المعراج حتى رأى ما في السموات من الأنبياء والملائكة ورأى ربه عز وجل ولم يكن ذلك نوم بل كان في يقظة إذ لو كان في النوم لاستوى فيه معه','1002','655','0');
INSERT INTO [t6446] VALUES (1388,'المتأول إذا أخطأ وكان من أهل عقد الإيمان نظر في تأويله فإن كان قد تعلق بأمر يفضي به إلى خلاف بعض كتاب الله أو سنة يقطع بها العذر أو إجماع فإنه يكفر ولا يعذر لأن الشبهة التي يتعلق بها من هذا ضعيفة لا يقوى قوة يعذر بها لأن ما شهد له','1002','655','0');
INSERT INTO [t6446] VALUES (1389,'سباب المسلم فسوق وقتاله كفر','1003','655','0');
INSERT INTO [t6446] VALUES (1390,'فصل','1004','655','0');
INSERT INTO [t6446] VALUES (1391,'في واحد منهم القدوة ولم يقاتلوا وأشكل الأمر إذ لم يبين في آية ولا سنة ولا إجماع من الأمة فامسكنا عن الدماء أن نقول فيها شيئا لاختلاف أصحاب رسول الله','1004','655','0');
INSERT INTO [t6446] VALUES (1392,'في ذلك قول بين','1005','655','0');
INSERT INTO [t6446] VALUES (1393,'أعطاني سيفا وقال قاتل به المشركين فإذا اقتتل المسلمون فأت به فاكسره ثم الزم بيتك حتى تأتيك منية قاضية أو يد خاطئة فقال علي رضي الله عنه دعوه وفي ذلك دليل على أنه أشكل عليه الأمر فأمسك واتبع وصية رسول الله','1006','655','0');
INSERT INTO [t6446] VALUES (1394,'آخى بينه وبين علي رضي الله عنه قال يوم صفين أيها الناس اتهموا الرأي فإنا ما حملنا أسيافنا هذه على عواتقنا في أمر إلا أسهل بنا إلى أمر نعرفه إلا أمرنا هذا ففي هذا دليل أنه رأى أنه لا يحل له أن يهريق الدماء على الإشكال ومما يدل','1007','655','0');
INSERT INTO [t6446] VALUES (1395,'فصول مستخرجة من كتب السنة','1008','655','0');
INSERT INTO [t6446] VALUES (1396,'فقال بعضهم ألم يقل الله كذا وقال بعضهم ألم يقل الله كذا وقال فسمع ذلك رسول الله','1010','655','0');
INSERT INTO [t6446] VALUES (1397,'عن النبي','1011','655','0');
INSERT INTO [t6446] VALUES (1398,'إنكم لن ترجعوا إلى الله بشيء أفضل مما خرج منه يعني القرآن','1011','655','0');
INSERT INTO [t6446] VALUES (1399,'اتقوا الله معشر القراء وخذوا طريق من كان قبلكم فوالله لئن أسبقتم لقد سبقتم بعيدا ولئن تركتموه شمالا ويمينا لقد ضللتم ضلالا بعيدا قال وإنما تركت ذكر الأسانيد لما تقدم من اليمين التي حلفت بها بما علمه أمير المؤمنين لولا ذلك','1012','655','0');
INSERT INTO [t6446] VALUES (1400,'أو عن الصحابة رضى الله عنهم أو عن التابعين فأما غير ذلك فإن الكلام فيه غير محمود','1013','655','0');
INSERT INTO [t6446] VALUES (1401,'فصل','1014','655','0');
INSERT INTO [t6446] VALUES (1402,'لا أنه علم أنه كان مخطئا وذلك أنه يوم النهروان أظهر السرور بقتال الخوارج وقال','1014','655','0');
INSERT INTO [t6446] VALUES (1403,'لمن قتل هؤلاء','1014','655','0');
INSERT INTO [t6446] VALUES (1404,'في قتال أصحاب الجمل كما كان عنده في قتال أهل النهروان وقال','1015','655','0');
INSERT INTO [t6446] VALUES (1405,'وصلى أبو بكر وثلث عمر ثم خبطتنا فتنة فهي ما شاء الله وليس في هذا دليل على أنه كان في شبهة من أمره بل كان مصيبا عند نفسه ولا يرى ما يحدث','1015','655','0');
INSERT INTO [t6446] VALUES (1406,'خبر في ذلك لاحتج به عليهم ولو أخبرهم بخبر في ذلك كان مصدقا غير مكذب ولكن كره ذلك','1015','655','0');
INSERT INTO [t6446] VALUES (1407,'فصل في ذكر يزيد وحاله','1016','655','0');
INSERT INTO [t6446] VALUES (1408,'يقول ينصب لكل غادر لواء يوم القيامة وإنا قد بايعنا هذا الرجل على بيعة الله ورسوله ولا أعلم عذرا أعظم من أن يبايع رجل على بيع الله ورسوله ثم ينصب له القتال وإني والله لا أعلم أحدا منكم خلعه ولا تابع في هذا الأمر إلا كانت الفيصل بيني','1016','655','0');
INSERT INTO [t6446] VALUES (1409,'لو رآهم بهذه الصورة فبكى بكاء شديدا أهل الدار حتى علت أصواتهم ثم قال فكوا عنهم الغل وفك الغل بيده من عنق علي بن الحسين وأمر بحملهم إلى الحمام وغسلهم وأمر بضرب القباب عليهم وأمرهم بالمطبخ وكساهم وأخرج لهم جوائز كثيرة','1018','655','0');
INSERT INTO [t6446] VALUES (1410,'فصل','1020','655','0');
INSERT INTO [t6446] VALUES (1411,'فإنه أحب الناس إلى الناس فصل رحمه وارفق به وداره يصلح لك أمرك وما جرى بين علي وبين معاوية رضي الله عنهم فقال السلف من السنة السكوت عما شجر بين أصحاب النبي','1020','655','0');
INSERT INTO [t6446] VALUES (1412,'فصل','1021','655','0');
INSERT INTO [t6446] VALUES (1413,'وأمه هند أكلت كبد حمزة ومعاوية قاتل عليا ويزيد قتل الحسين والجواب عن ذلك أن قتال أبي سفيان إنما كان قبل إسلامه وإسلامه قد هدم ما كان قبله قال الله تعالى قل للذين كفروا إن ينتهوا يغفر لهم ما قد سلف وقال النبي','1021','655','0');
INSERT INTO [t6446] VALUES (1414,'فأسلمت وبايعت ونزل قوله تعالى فبايعهن واستغفر لهن الله فاستغفر لها النبي','1022','655','0');
INSERT INTO [t6446] VALUES (1415,'الطائف وفقئت عينه في سبيل الله وفقئت عينه الأخرى يوم اليرموك وكان ينادي يا نصر الله اقترب','1022','655','0');
INSERT INTO [t6446] VALUES (1416,'ومن مذهب أهل السنة التورع في المآكل والمشارب والمناكح والتحرز من الفواحش والقبائح والتحريض على التحاب في الله عز وجل واتقاء الجدال والمنازعة في أصول الدين ومجانبة أهل الأهواء والضلالة وهجرهم ومباينتهم والقيام بوفاء العهد والأمانة','1022','655','0');
INSERT INTO [t6446] VALUES (1417,'فصل','1024','655','0');
INSERT INTO [t6446] VALUES (1418,'قال','1025','655','0');
INSERT INTO [t6446] VALUES (1419,'مسألة','1027','655','0');
INSERT INTO [t6446] VALUES (1420,'فصل','1028','655','0');
INSERT INTO [t6446] VALUES (1421,'قال إن من أشراط الساعة أن يفتح القول ويخزن الفعل وتوضع','1028','655','0');
