INSERT INTO [t6449] VALUES (1,'بسم الله الرحمن الرحيم','1','0','0');
INSERT INTO [t6449] VALUES (2,'وأولاهم بالإمامة بعده أبو بكر الصديق رضي الله عنه ثم عمر بن الخطاب رضي الله عنه ومنهم من يقول أبو بكر ثم عمر ثم علي رضي الله عنهم ومنهم من يقول أبو بكر ثم عمر ثم عثمان ووقف ومنهم من يقول أبو بكر ثم عمر ثم عثمان ثم','2','0','0');
INSERT INTO [t6449] VALUES (3,'علي بن أبي طالب رضي الله عنه وهم الإمامية وكل هذه الفرق مقلد فيما انتحل سلفا يحتج به ممن يخالفهم ويعاديه واستعنت الله تعالى وأودعت هذا الجزء بيان الأصوب من النحل والأقوم من المقالات والملل وأجمع في ذلك','3','0','0');
INSERT INTO [t6449] VALUES (4,'وثبت عن الرسول','4','0','0');
INSERT INTO [t6449] VALUES (5,'بالخصال الحميدة والفضائل الكريمة قال الله تبارك وتعالى والسابقون الأولون من المهاجرين والأنصار والذين اتبعوهم بإحسان رضي الله عنهم ورضوا عنه وقال تبارك وتعالى لقد رضي الله عن المؤمنين إذ يبايعونك تحت الشجرة الآية','4','0','0');
INSERT INTO [t6449] VALUES (6,'خلافة أمير المؤمنين أبي بكر الصديق رضي الله عنه وأرضاه','8','0','0');
INSERT INTO [t6449] VALUES (7,'خير أمتي القرن الذي أنا فيه ثم الذين يلونهم رواه شعبة والثوري وجرير عن منصور','8','0','0');
INSERT INTO [t6449] VALUES (8,'خير أمتي القرن الذي بعثت فيهم ثم الذين يلونهم ثم الذين يلونهم ثم يأتي قوم من بعد ينذرون ولا يوفون ويخونون ولا يؤتمنون ويشهدون ولا يستشهدون ويفشو فيهم السمن','9','0','0');
INSERT INTO [t6449] VALUES (9,'عن خير الناس قال أنا ومن معي قيل ثم من قال الذين على الأثر','10','0','0');
INSERT INTO [t6449] VALUES (10,'خير الناس قرني ثم الذين يلونهم ثم الذين يلونهم الحديث فلم تنكر فرقة من هذه الفرق المدائح التي مدح الله بها أصحاب رسول الله','10','0','0');
INSERT INTO [t6449] VALUES (11,'وأن الصحابة هم خير الأمم فيقال للإمامية الطاعنين على المهاجرين والأنصار اجتماعهم على تقدمة','10','0','0');
INSERT INTO [t6449] VALUES (12,'من كنت مولاه فعلي مولاه','13','0','0');
INSERT INTO [t6449] VALUES (13,'مولاه فعلي والمؤمنين مواليه دليل ذلك قول الله تبارك وتعالى والمؤمنين والمؤمنات بعضهم أولياء بعض وقال تعالى والذين كفروا بعضهم أولياء بعض والولي والموالي في كلام العرب واحد والدليل عليه قوله تبارك وتعالى','14','0','0');
INSERT INTO [t6449] VALUES (14,'لعلي رضي الله عنه وحث على محبته وترغيب في ولايته لما ظهر من ميل المنافقين ب عليه وبغضهم له وكذلك قال','16','0','0');
INSERT INTO [t6449] VALUES (15,'فقال رسول الله','16','0','0');
INSERT INTO [t6449] VALUES (16,'أخبر أن كل هؤلاء القبائل موالي الله ورسوله','16','0','0');
INSERT INTO [t6449] VALUES (17,'أنه قال لعلي أنت مني بمنزلة هارون من موسى قيل له كذلك نقول في استخلافه على المدينة في حياته بمنزلة هارون من موسى وإنما خرج هذا القول له من النبي','17','0','0');
INSERT INTO [t6449] VALUES (18,'فذكر له قولهم فقال','17','0','0');
INSERT INTO [t6449] VALUES (19,'يقول لعلي أما ترضى أن تكون مني بمنزلة هارون بن موسى إلا أنه لا نبي بعدي','17','0','0');
INSERT INTO [t6449] VALUES (20,'عليا في أهله حين غزا غزاة تبوك فقال بعض الناس ما منعه أن يخرج إلا أنه كره صحبته فبلغ ذلك عليا عليه السلام فقال لرسول الله','18','0','0');
INSERT INTO [t6449] VALUES (21,'يا بن أبي طالب أما ترضى أن تنزل مني بمنزلة هارون من موسى فإن قال الطاعن لم يرد استخلافه على المدينة قيل له هل شاركه في النبوة كما شارك هارون موسى فإن قال نعم كفر وإن قال لا قيل له فهل كان أخاه في النسب فإن قال','18','0','0');
INSERT INTO [t6449] VALUES (22,'فصعد المنبر وقال أي أهل الأرض تعلمون أكرم على الله قالوا أنت قال فإن العباس مني وأنا منه اهـ','19','0','0');
INSERT INTO [t6449] VALUES (23,'كان في مغزى له فلما فرغ من القتال فقال وهل تفقدون من أحد لكنني أفقد جليبيبا فوجوده عند سبعة قد قتلهم وقتلوه فأخبر النبي','20','0','0');
INSERT INTO [t6449] VALUES (24,'حتى حفر له فما كان له سرير إلا ذراعي النبي','20','0','0');
INSERT INTO [t6449] VALUES (25,'قيل له قد شاركه عثمان بن عفان وغيره رضي الله عنهما في هذا الأمر فإن عثمان كان ختنه على ابنتيه وابن العاص بن الربيع على ابنته','20','0','0');
INSERT INTO [t6449] VALUES (26,'لأدفعن الراية إلى رجل يحب الله ورسوله ويحبه الله ورسوله قيل قد شاركه في هذه الفضيلة عدة من الصحابة منهم أبو بكر وعمر وعثمان وزيد وأسامة والحسن والحسين وعائشة رضي الله عنهم','21','0','0');
INSERT INTO [t6449] VALUES (27,'قال','21','0','0');
INSERT INTO [t6449] VALUES (28,'فيها ومن يجترئ عليه إلا أسامة بن زيد حب رسول الله','22','0','0');
INSERT INTO [t6449] VALUES (29,'إلا أبوها رضي الله عنه وأرضاه','23','0','0');
INSERT INTO [t6449] VALUES (30,'بعثه على جيش ذات السلاسل فلما أتيته قلت أي الناس أحب إليك قال عائشة قلت من الرجال قال أبوها قال ثم عد رجالا','23','0','0');
INSERT INTO [t6449] VALUES (31,'والحسن والحسين على عاتقة وهو يقول اللهم إني أحبه فأحبه','24','0','0');
INSERT INTO [t6449] VALUES (32,'فقلت يا رسول الله هذا علي والعباس بالباب يريدان الدخول عليك قال تدري ما جاء بهما قلت لا والله يا رسول الله ما أدري ما جاء بهما قال ولكني قد علمت ما جاء بهما أبين لهما إيذن لهما فدخلا عليه فقال علي يا رسول الله جئناك','24','0','0');
INSERT INTO [t6449] VALUES (33,'لزيد بن حارثة إنه لمن أحب الناس إلي بعده وهذه فضيلة له رضي الله عنهم فإن احتج المعاند بأنه استحق الخلافة لأنه كان أولهم إسلاما طولب','25','0','0');
INSERT INTO [t6449] VALUES (34,'فإن كنت تحتج بالأخبار فإذا تعارضت الأخبار سقطت','26','0','0');
INSERT INTO [t6449] VALUES (35,'قال سمعت عمرو بن عبسه السلمي قال أتيت رسول الله','26','0','0');
INSERT INTO [t6449] VALUES (36,'وهو نازل بعكاظ فقلت من معك','26','0','0');
INSERT INTO [t6449] VALUES (37,'أول ما بعث وهو يومئذ مستخف فقلت فمن معك على هذا الأمر قال حر وعبد يعني أبا بكر وبلال','27','0','0');
INSERT INTO [t6449] VALUES (38,'فقلت يا رسول الله من أسلم معك قال حر وعبد','27','0','0');
INSERT INTO [t6449] VALUES (39,'وعهد إليه وأنه القاضي لدينه والقائم بعهده المنجز موعده وما شاكله من موضوعاتهم وأباطيلهم قيل له قد روي من الوجوه المرتضى خلافه وذلك','28','0','0');
INSERT INTO [t6449] VALUES (40,'أوصى قال لا قلت فكتب على المسلمين أو أمر المسلمين بالوصية ولم يوص قال أوصى بكتاب الله قال فقال هزيل أبو بكر كان يتأمر على وصي رسول الله','29','0','0');
INSERT INTO [t6449] VALUES (41,'وفي البيت رجال قال رسول الله','30','0','0');
INSERT INTO [t6449] VALUES (42,'فقال النبي','30','0','0');
INSERT INTO [t6449] VALUES (43,'أن يكتب لهم الكتاب لاختلافهم ولغطهم','31','0','0');
INSERT INTO [t6449] VALUES (44,'بشيء فقال ما هو إلا كتاب الله وفهم يؤتيه الله من شاء في الكتاب','33','0','0');
INSERT INTO [t6449] VALUES (45,'أولى وأحرى فقد فاتته يوم الخندق','34','0','0');
INSERT INTO [t6449] VALUES (46,'ولو جاز لأحد لكان رسول الله','34','0','0');
INSERT INTO [t6449] VALUES (47,'قال يوم الأحزاب ملأ الله بيوتهم وقبورهم نارا كما شغلونا عن صلاتنا الوسطى حتى آبت الشمس رواه شتير بن شكل ويحيى بن الجزار وغيرهما','35','0','0');
INSERT INTO [t6449] VALUES (48,'يوم الخندق شغلونا عن صلوات فأمر رسول الله','35','0','0');
INSERT INTO [t6449] VALUES (49,'ما كدت أن أصلي حتى كادت أن تغرب قال فقال رسول الله','35','0','0');
INSERT INTO [t6449] VALUES (50,'أحسبه قال إلى بطحان لنتوضأ للصلاة وتوضأنا لها فصلى','35','0','0');
INSERT INTO [t6449] VALUES (51,'في سفر فقال لو عرسنا فمال إلى شجرة فنزل فقال احفظوا علينا صلاتنا فنمنا فما أيقظنا إلا حر الشمس فانتبهنا','36','0','0');
INSERT INTO [t6449] VALUES (52,'فسرنا ليلة حتى إذا كان في آخر الليلة قبيل الصبح وقعنا تلك الوقعة ولا وقعة أحلى عند المسافر منها فما أيقظنا إلا حر الشمس فكان أول ما استيقظ بلال ثم فلان ثم فلان ثم عمر وكان رسول الله','36','0','0');
INSERT INTO [t6449] VALUES (53,'قال لعلي أنت خيرهم وأفضلهم وأنت الخليفة من بعدي وما في معناه قيل له كذلك روي عن علي رضي الله عنه أن النبي','37','0','0');
INSERT INTO [t6449] VALUES (54,'أنه قال أبو بكر خير خلق الله','37','0','0');
INSERT INTO [t6449] VALUES (55,'آخى بين الصحابة فاختار عليا فقال له أنت أخي في الدنيا والآخرة قيل له هذه الفضيلة لا توجب الخلافة ولو كانت هذه توجب الخلافة كانت لها الأبوة أخص وأوجب وقد قال ذلك لعمه العباس فقال هو أبي','38','0','0');
INSERT INTO [t6449] VALUES (56,'احفظوني في العباس وأنه عم الرجل صنو أبيه وأما قوله لأبي بكر رضي الله عنه أخي وصاحبي','39','0','0');
INSERT INTO [t6449] VALUES (57,'لو اتخذت خليلا لاتخذت أبا بكر خليلا ولكنه أخي','39','0','0');
INSERT INTO [t6449] VALUES (58,'قال لو كنت متخذا خليلا دون أبي بكر لاتخذت أبا بكر خليلا ولكنه أخي في الدين وصاحبي في الغار فإن أبا بكر كان ينزله منزلة الوالد فإن أحق ما اقتدينا به قول أبي بكر رضي الله عنه فإن احتج بقوله','40','0','0');
INSERT INTO [t6449] VALUES (59,'يقول للأنصار','40','0','0');
INSERT INTO [t6449] VALUES (60,'يقول من أحب الأنصار أحبه الله ومن أبغض الأنصار أبغضه الله وذكر فيه كلاما فإن احتج بشجاعته رضوان الله عليه وأنه كان من أشد القوم بأسا وأربطهم جأشا قيل له الشجاعة وإن حيز بها الفضل فليست حجة لاستحقاق الخلافة فلقد كان في','41','0','0');
INSERT INTO [t6449] VALUES (61,'لأن كنت أحسنت القتال لقد أحسنه سهل بن حنيف وأبو دجانة سماك بن خرشة','42','0','0');
INSERT INTO [t6449] VALUES (62,'يقول يومئذ يعني يوم أحد أوجب طلحة','43','0','0');
INSERT INTO [t6449] VALUES (63,'أخذ سيفه وأصحابه حوله فقال من يأخذ هذا السيف فليسطر أيديهم يقول هذا أنا وهذا أنا فقال من يأخذه بحقه قال فأحجم القوم فقال سماك أبو دجانة أنا آخذه بحقه فدفعه إليه رسول الله','43','0','0');
INSERT INTO [t6449] VALUES (64,'لأن أراني الله قتالا ليرين الله ما أصنع فلما كان يوم أحد انهزم أصحاب رسول الله','44','0','0');
INSERT INTO [t6449] VALUES (65,'يأبى الله والمؤمنين إلا أبا بكر رضي الله عنه وأرضاه','45','0','0');
INSERT INTO [t6449] VALUES (66,'في مرضه الذي مات فيه ادعوا لي عبد الرحمن بن أبي بكر الصديق رضي الله عنه اكتب كتابا لا يختلف بعدي ثم قال دعه معاذ الله أن يختلف المؤمنون في أبي بكر','45','0','0');
INSERT INTO [t6449] VALUES (67,'في اليوم الذي بدىء فيه فقال ادعي لي أباك وأخاك حتى أكتب لأبي بكر كتابا فإني','45','0','0');
INSERT INTO [t6449] VALUES (68,'وأنا عنده في نفر من المسلمين دعاه بلال إلى الصلاة فقال مروا من يصلي بالناس فخرجت فإذا عمر بن الخطاب رضي الله عنه في الناس وكان أبو بكر رضي الله عنه غائبا فقلت يا عمر قم فصل بالناس فقام فلما كبر سمع رسول الله','46','0','0');
INSERT INTO [t6449] VALUES (69,'فأين أبو بكر يأبى الله والمؤمنين ذلك فبعث إلى أبي بكر فجاء بعد أن صلى عمر تلك الصلاة فصلى الناس قال عبد الله بن زمعة فقال عمر ويحك ماذا صنعت بي يا بن زمعة والله ما ظننت حين أمرتني إلا أن رسول الله','46','0','0');
INSERT INTO [t6449] VALUES (70,'يأبى الله والناس إلا أبا بكر وقوله عليه السلام إن أمن الناس علي في صحبته وذات يده أبو بكر رضي الله عنه','47','0','0');
INSERT INTO [t6449] VALUES (71,'خطب بالناس فقال إن أمن الناس علي في صحبته وماله أبو بكر ولو كنت متخذا خليلا من الناس لاتخذت أبا بكر ولكن أخوة في الإسلام ومودته لا يبقين في المسجد باب إلا سد إلا باب أبي بكر','47','0','0');
INSERT INTO [t6449] VALUES (72,'قبل موته بخمس يقول قد كان لي فيكم خليل ولو كنت متخذا خليلا من أمتي لاتخذت أبا بكر خليلا ومنها قوله','48','0','0');
INSERT INTO [t6449] VALUES (73,'فسألته حاجة فقال لها ترجعين فقالت يا رسول الله إن رجعت فلم أجدك قال إن رجعت فلم تجديني يعني الموت إيتي أبا بكر رضي الله عنه ومنها قوله','48','0','0');
INSERT INTO [t6449] VALUES (74,'إلى الأنصار ليصلح بينهم فحصرت العصر فقال بلال لأبي بكر رضي الله عنه قد حضرت الصلاة ولك من رسول الله','49','0','0');
INSERT INTO [t6449] VALUES (75,'بعدما فرغ فقال أصليتم قالوا نعم قال ومن صلى بكم قالوا أبا بكر الصديق رضي الله عنه قال أحسنتم لا ينبغي لقوم فيهم أبو بكر رضي الله عنه أن يؤمهم أحد غيره رواه أحمد بن بشير الكوفي عن عيسى بن ميمون','49','0','0');
INSERT INTO [t6449] VALUES (76,'لا ينبغي لقوم يكون بينهم أبو بكر أن يؤمهم غيره ومنها قوله','49','0','0');
INSERT INTO [t6449] VALUES (77,'اقتدوا باللذين من بعدي يشير','49','0','0');
INSERT INTO [t6449] VALUES (78,'نحوه','50','0','0');
INSERT INTO [t6449] VALUES (79,'أن يطع الناس أبا بكر رضي الله عنهما يرشدوا','50','0','0');
INSERT INTO [t6449] VALUES (80,'في سفر فقال إنكم لا تدركون الماء غدا فانطلق سرعان الناس يريدون الماء ولزمت رسول الله','50','0','0');
INSERT INTO [t6449] VALUES (81,'راحلته فنعس فنمنا فما أيقظنا إلا حر الشمس فقال أصبح الناس وقد فقدوا نبيهم فقال بعضهم إن نبي الله','50','0','0');
INSERT INTO [t6449] VALUES (82,'لم يكن يسبقكم إلى الماء ويخلفكم وإن يطع الناس أبا بكر وعمر يرشدوا قالها ثلاثا وللصديق رضي الله عنه مناقب مشهورة وفضائل معدودة واكتفينا ها هنا','50','0','0');
INSERT INTO [t6449] VALUES (83,'طائعين مطيعين مبايعين له مقرين بفضله وقدره','51','0','0');
INSERT INTO [t6449] VALUES (84,'يقول إن هذا الأمر في قريش لا يعاديهم أحد إلا أكبه الله في النار على وجهه ما أقاموا الدين','51','0','0');
INSERT INTO [t6449] VALUES (85,'الناس تبع لقريش في الخير والشر','52','0','0');
INSERT INTO [t6449] VALUES (86,'لا يزال هذا الأمر في قريش ما بقي في الناس اثنان ويقول بإصبعه هكذا اثنان ولو كان الإمامة في غير قريش سائرة مع ما أخبر النبي','52','0','0');
INSERT INTO [t6449] VALUES (87,'إن يطيعوا أبا بكر وعمر رضي الله عنهما يرشدوا وأن يقتدوا باللذين من بعدي أبي بكر وعمر رضي الله عنهما في نظائر لذلك مما قد سمعوه واستقر ذلك عندهم','56','0','0');
INSERT INTO [t6449] VALUES (88,'في ذلك أو تخلفه عن رأي رآه من عند نفسه ثم رأى بعد ذلك أن الحق والصواب في مبايعته فبايعه وهذا أولى به وأليق بدينه وعلمه رضي الله عنه','57','0','0');
INSERT INTO [t6449] VALUES (89,'فلم يروا أن عقد عمار وسهل يوجب عليهم بيعة لأحد إلا بعد اختيار وتشاور واجتماع المسلمين لا يسعهم أن يتخلفوا عنه إذا وجدوا شرائط الخلافة كمسابقة غيرهم إلى البيعة وإنما بايعوا عن علم ورأي واختيار ومشورة واستحقاق من بايعوا له وإن سوغت','61','0','0');
INSERT INTO [t6449] VALUES (90,'لا تخيروني على الأنبياء ولا يقولن أحدكم أنا خير من يونس بن متى وكقوله رحم الله أخي يوسف لو لبثت في السجن ثم جاءني الداعي لأسرعت وكقوله نحن أحق بالشك من إبراهيم كل ذلك إنما قاله','64','0','0');
INSERT INTO [t6449] VALUES (91,'قال إن أبا بكر رضي الله عنه صاحب رسول الله','65','0','0');
INSERT INTO [t6449] VALUES (92,'في كذا في كذا ثلاثا فمن قال غير ذلك حل عليه ما حل على المقرى','66','0','0');
INSERT INTO [t6449] VALUES (93,'فقال عوف بن مالك كذبتم والله لقد كان أبو بكر أطيب من ريح المسك وإني لمثله كغيري','66','0','0');
INSERT INTO [t6449] VALUES (94,'أبو بكر وبعده عمر رضي الله عنهما','66','0','0');
INSERT INTO [t6449] VALUES (95,'ألا أخبركم بخير الناس أبو بكر ثم بعد أبي بكر عمر رضي الله عنهما','67','0','0');
INSERT INTO [t6449] VALUES (96,'خير الناس رسول الله ثم أبو بكر ثم عمر عليهم السلام','67','0','0');
INSERT INTO [t6449] VALUES (97,'أبو بكر وعمر وعثمان رضي الله عنهم وأما سكوت من سكت عند ذكر','67','0','0');
INSERT INTO [t6449] VALUES (98,'وهم متوافرون يغني عن الاحتجاج بالأخبار في أمره والتطويل في شأنه فإن احتج بأن مبايعة علي رضي الله عنه كانت عن تقية قيل له قد احتججت فيما سلف من كلامك أنه قعد عن بيعته ستة أشهر فلو كانت على تقية لما أمهل ساعة فكيف وبقي ستة أشهر','69','0','0');
INSERT INTO [t6449] VALUES (99,'خلافة أمير المؤمنين عمر بن الخطاب رضي الله عنه وأرضاه','70','0','0');
INSERT INTO [t6449] VALUES (100,'من تفخيمه وجلالة ما ذكر من مناقبه في كمال علمه وتمام قوته وصائب الهامه وفراسته وما قرن بشأنه من السكينة وغير ذلك من ورعه وخوفه وزهده ورأفته بالمؤمنين وغلظته وفظاظته على المنافقين والكافرين وأخذه بالحزم والحياطة وحسن الرعاية','74','0','0');
INSERT INTO [t6449] VALUES (101,'أقضاكم علي فلو ثبت لكان فيه لنا الحجة عليك فإن قال كيف قيل لأن في هذا الخبر وأفرضكم زيد وأعلمكم بالحلال والحرام معاذ وأقرأكم لكتاب الله تعالى أبي فكيف يكون أعلم وغيره أفرض وأعلم بالحلال والرام وأقرأ لكتاب الله تبارك وتعالى منه','74','0','0');
INSERT INTO [t6449] VALUES (102,'وهو يحدث أصحابه وهو يقول كيف أنتم وقد ذهب أوان العلم قال قلت يا رسول الله وكيف يذهب أوان العلم ونحن نقرأ القرآن ونعلمه أنباءنا إلى يوم القيامة قال فقال النبي','75','0','0');
INSERT INTO [t6449] VALUES (103,'وسلك مسلكك فيما احتججت به ما كان محتكرا عليه وإنما وجه هذا الحديث عندنا أن زياد بن لبيد من فقهاء أهل المدينة وعلمائهم لا أنه افقه رجل بها وأعلمه ولو ثبت الحديث الذي اعتللت به كان وجهه مثله وبقي ما تأولناه في حديث زياد بن لبيد','75','0','0');
INSERT INTO [t6449] VALUES (104,'فشخص ببصره إلى السماء ثم قال هذا أوان يختلس العلم من الناس حتى لا يقدرون منه على شيء فقال زياد بن لبيد الأنصاري وكيف يختلس منا وقد قرأنا القرآن فوالله لنقرأنه ولنعرفنه نساءنا وأنباءنا فقال ثكلتك أمك يا زياد إن كنت لأعدك من فقهاء','76','0','0');
INSERT INTO [t6449] VALUES (105,'ذكر الأحاديث التي ذكرناها في تفضيل عمر رضي الله عنه وأرضاه','76','0','0');
INSERT INTO [t6449] VALUES (106,'أحب إلي أن ألقى الله بصحيفته منك رواه أبو معشر المديني عن نافع عن ابن عمر','78','0','0');
INSERT INTO [t6449] VALUES (107,'يقول ذهبت أنا وأبو بكر وعمر ودخلت أنا وأبو بكر وعمر وخرجت أنا وأبو بكر وعمر وإن كنت لأظن أن يجعلك الله معهما','78','0','0');
INSERT INTO [t6449] VALUES (108,'أبو بكر ثم عمر رضي الله عنهما','79','0','0');
INSERT INTO [t6449] VALUES (109,'أبو بكر ثم عمر ولو شئت أن أذكر الثالث لذكرته وما قاله فيه عبد الله بن مسعود رضي الله عنه','79','0','0');
INSERT INTO [t6449] VALUES (110,'ذكر دعوة النبي','83','0','0');
INSERT INTO [t6449] VALUES (111,'اللهم أعز الدين بعمر بن الخطاب رضي عنه أو بأبي جهل بن هشام فجعل الله دعوة رسوله لعمر رضي الله عنه فبنى عليه ملك الدين وهدم به الأوثان','83','0','0');
INSERT INTO [t6449] VALUES (112,'قال اللهم أعز الإسلام بعمر أبو بأبي جهل بن هشام فأصبح عمر بن الخطاب رضي الله عنه فغدا على رسول الله','83','0','0');
INSERT INTO [t6449] VALUES (113,'قال اللهم أعز الإسلام بعمر بن الخطاب رضي الله عنه خاصة','84','0','0');
INSERT INTO [t6449] VALUES (114,'من كمال دينه','84','0','0');
INSERT INTO [t6449] VALUES (115,'بينما أنا نائم رأيت الناس يعرضون علي وعليهم قمص منها ما يبلغ الثدي ومنها ما يبلغ دون ذلك ومر علي عمر بن الخطاب رضي','84','0','0');
INSERT INTO [t6449] VALUES (116,'ذكر ما أخبر به النبي','85','0','0');
INSERT INTO [t6449] VALUES (117,'يقول بين أنا نائم إذ رأيت قدحا أتيت به فشربت منه لبنا حتى أني لأرى الذي يجري في أضافري ثم أعطيت فضله عمر بن الخطاب رضي الله عنه قالوا ما أولت ذلك يا رسول الله قال العلم','85','0','0');
INSERT INTO [t6449] VALUES (118,'ذكر ما أخبر به النبي','86','0','0');
INSERT INTO [t6449] VALUES (119,'قال رأيت ابن أبي قحافة ينزع ذنوبا أو ذنوبين وفي نزعه ضعف ويغفر الله له ثم قام عمر بن الخطاب رضي الله عنه فنزع فاستحالت غربا فلما أن ما ينزع من حتى ضرب الناس بعطن ورواه الزهري عن سعيد بن المسيب عن أبي هريرة رضي','86','0','0');
INSERT INTO [t6449] VALUES (120,'ما ذكر','87','0','0');
INSERT INTO [t6449] VALUES (121,'إنه كان فيمن خلا من الأمم قبلكم أناس محدثون وأن يكن من أمتي منهم أحد هو عمر بن الخطاب رضي الله عنه','87','0','0');
INSERT INTO [t6449] VALUES (122,'من رسوخ إيمانه زيادة لعلو شأنه','87','0','0');
INSERT INTO [t6449] VALUES (123,'بينما رجل يسوق بقرة إذ عيى فركبها فقالت أنا لم أخلق لهذا إنما خلقت لحراثة الأرض فقال من حول رسول الله','87','0','0');
INSERT INTO [t6449] VALUES (124,'فإني آمنت به أنا وأبو بكر وعمر رضي الله عنهما وليسا في المجلس','87','0','0');
INSERT INTO [t6449] VALUES (125,'ما ذكر','88','0','0');
INSERT INTO [t6449] VALUES (126,'وعنده نساء يستكثرنه ويكلمنه عالية أصواتهن فلما استأذن عمر رضي الله عنه قمن يبتدرن الحجاب فقال رسول الله','88','0','0');
INSERT INTO [t6449] VALUES (127,'فقلت له يا رسول الله إني قد حمدت ربي بمحامد ومدح وإياك فقال إن ربك يحب الحمد فجعلت أنشده فاستأذن رجل طويل أصلع فقال لي رسول الله','88','0','0');
INSERT INTO [t6449] VALUES (128,'ثم خرج ففعل ذلك مرتين أو ثلاثا فقال يا رسول الله من هذا الذي أسكتني له فقال هذا عمر بن الخطاب هذا رجا لا يحب الباطل','89','0','0');
INSERT INTO [t6449] VALUES (129,'إن رضاه يثبت العدل وغضبه يفضي إلى العز','89','0','0');
INSERT INTO [t6449] VALUES (130,'فقال اقرئ على عمر رضي الله عنه السلام وأعلمه أن رضاه عدل وغضبه عز','89','0','0');
INSERT INTO [t6449] VALUES (131,'قال إن الله جعل الحق على لسان عمر وقلبه','89','0','0');
INSERT INTO [t6449] VALUES (132,'لما نزل الله تعالى به القرآن وذلك نحو','92','0','0');
INSERT INTO [t6449] VALUES (133,'قال إن الله جعل الحق على لسان عمر رضي الله عنه وقلبه فلم يزل أمره رضي الله عنه مستوثقا وعامة الصحابة رضي الله عنهم أجمعين لرأيه متبعا يشفي الله تعالى به صدور أوليائه ويغيظ الكفار وأعداءه','93','0','0');
INSERT INTO [t6449] VALUES (134,'فجمع الرهط المرضيين الذين رفع الله أعلامهم وأمرهم بالشورى والاختيار والمسلمون بأجمعهم قد عرفوا فضل أهل الشورى وأنهم أعلام الدين ومصابيح الهدى فلم ينكر ذلك أحد من رأيه وفعله وقد كان بقي من أهل بدر والعقبة وجلة الصحابة العدد الكثير فرضوا','94','0','0');
INSERT INTO [t6449] VALUES (135,'خلافة الإمام أمير المؤمنين عثمان بن عفان رضي الله عنه وأرضاه','95','0','0');
INSERT INTO [t6449] VALUES (136,'لأن الفضل الذي حازه أهل بدر في شهود بدر طاعة الرسول','97','0','0');
INSERT INTO [t6449] VALUES (137,'للقيام على ابنته فكان في أجل فرض لطاعته لرسول الله','97','0','0');
INSERT INTO [t6449] VALUES (138,'من غزاة تبوك فلما دنوا من المدينة قال إن بالمدينة لأقواما ما قطعتم من واد ولا سرتم من مسير إلا كانوا معكم فيه قالوا يا رسول الله بالمدينة قال نعم خلفهم العذر','98','0','0');
INSERT INTO [t6449] VALUES (139,'فمرضت فقال له','98','0','0');
INSERT INTO [t6449] VALUES (140,'لك أجر رجل شهد بدرا وسهمه','99','0','0');
INSERT INTO [t6449] VALUES (141,'وقد ضرب رسول الله','99','0','0');
INSERT INTO [t6449] VALUES (142,'فيها بسهم فقد شهد وأما بيعة الرضوان فلأجل عثمان رضي الله عنه وقعت هذه المبايعة وذلك أن النبي','100','0','0');
INSERT INTO [t6449] VALUES (143,'بقتله فبايع رسول الله','100','0','0');
INSERT INTO [t6449] VALUES (144,'عثمان فأرسله إلى أهل مكة فبايع لعثمان إحدى يديه على الأخرى فقال الناس هنيئأ لأبي عبد الله','100','0','0');
INSERT INTO [t6449] VALUES (145,'ببيعة الرضوان كان عثمان رسول رسول الله','101','0','0');
INSERT INTO [t6449] VALUES (146,'إن عثمان في حاجة الله وحاجة رسوله فضرب إحدى يديه على الأخرى فكان يد رسول الله','101','0','0');
INSERT INTO [t6449] VALUES (147,'بعثه إلى الأحزاب ليوادعوه ويسالموه وأن رسول الله','101','0','0');
INSERT INTO [t6449] VALUES (148,'حين لم ينص على خلافة أبي بكر مع علمه','101','0','0');
INSERT INTO [t6449] VALUES (149,'سكت عن النص على أبي بكر لجهل كان منه بمكانه فقد قال عظيما وهو الذي يقول عليه السلام يأبا الله والمؤمنون إلا أبا بكر رضي الله عنه وقوله للمرأة إن لم تجديني فأت أبا بكر مع غيره من الأدلة والبيان في أمره والدليل على أن عمر رضي الله عنه كان','102','0','0');
INSERT INTO [t6449] VALUES (150,'تكلموا في عثمان رضي الله عنه قيل له إن الإجتماع عليه بالفضيلة والمنقبة والسابقة قد ثبت ولا سبيل إلى إزالة ذلك إلا بمثله من الاجتماع ويلزم من تكلم فيه بعد الاجتماع النقض حتى يأتي بحجة يقم بها قوله ويثبته على غير معارضة ولا خلل','104','0','0');
INSERT INTO [t6449] VALUES (151,'لا نتهمك واجمع القرآن','105','0','0');
INSERT INTO [t6449] VALUES (152,'فإن اعتل بتوليته الوليد بن عقبة وأنه سكر فصلى الصبح أربعا','105','0','0');
INSERT INTO [t6449] VALUES (153,'بعض الناس على الصدقة ففسق فأنزل الله تعالى فيه إن جاءكم فاسق بنبأ فتبينوا الآية فلا يلحقه في ذلك إلا ما لحق رسول الله','106','0','0');
INSERT INTO [t6449] VALUES (154,'ولي عمر بن الخطاب قدامة بن مظعون دلكم فشرب الخمر متأولا فأمر عمر رضي الله عنه بحده وقدامة من أولي السابقة والفضل من أهل بدر فلم يلحق عمر من فعله شيئا بعد أن حده ولذلك عثمان رضي الله عنه قد أقام الحد على الوليد بن عقبة','106','0','0');
INSERT INTO [t6449] VALUES (155,'جلد أربعين وجلد أبو بكر رضي الله عنه أربعين وجلد عمر رضي الله عنه ثمانين وكل سنة','106','0','0');
INSERT INTO [t6449] VALUES (156,'ابن اللتبية واستعمله على الصدقات فجاء بمال وسواد كثير لم يدفعه إلى رسول الله','107','0','0');
INSERT INTO [t6449] VALUES (157,'وأخذ ما معه وولى علي بن أبي طالب المختار بن أبي المداين فأتاه بصرة فقال هذا من أجور المؤمنات فقال علي قاتله الله لو','107','0','0');
INSERT INTO [t6449] VALUES (158,'وإن الذي حمل عثمان رضي الله عنه على الاتمام أنه بلغه أن قوما من الأعراب ممن شهدوا معه الصلاة بمنى رجعوا إلى قومهم فقالوا الصلاة ركعتان كذلك صليناها مع أمير المؤمنين عثمان بن عفان رضي الله عنه بمنى فالأجل ذلك صلى أربعا ليعلمهم','108','0','0');
INSERT INTO [t6449] VALUES (159,'أنه جمع بينهما وكان ابنه عبد الله يخالفه ويقول سنة رسول الله','109','0','0');
INSERT INTO [t6449] VALUES (160,'وإقامته على الإحرام حين دخل مكة معتمرا حتى فرغ من إقامة المناسك ولم يعدوا ذلك خلافا من عمر رضي الله عنه ولم يظهروا إنكارا عليه ولو كان ذلك موضع الإنكار لأنكروه ولما تابعوه على رأيه فإن عاد للطعن عليه بأنه أمر للناس بالعطاء من مال','109','0','0');
INSERT INTO [t6449] VALUES (161,'غنائم حنين في المؤلفة قلوبهم يوم الجعرانة وترك الأنصار لما رأى من المصلحة حتى قال قائلهم تقسم غنائمنا في الناس وسيوفنا تقطر من دمائهم فكان الذي دعاهم إلى الإنكار على ما فعل رسول الله','110','0','0');
INSERT INTO [t6449] VALUES (162,'من المصلحة فيما قسم وكان أعظم من إنكار من أنكر على عثمان رضي الله عنه لأن مال المؤلفة من الغنيمة فلا يلزم عثمان رضي الله عنه من إنكار من أنكر عليه شيئا إلا ما لزم رسول الله','110','0','0');
INSERT INTO [t6449] VALUES (163,'فإن قال قائل إنما الذي أعطى رسول الله','110','0','0');
INSERT INTO [t6449] VALUES (164,'لم أنكرتم إنما أعطيتهم من مال الله ألا تراه','110','0','0');
INSERT INTO [t6449] VALUES (165,'إلى بيوتكم قالوا رضينا','110','0','0');
INSERT INTO [t6449] VALUES (166,'يعطي رجلا من قريش المائة من الإبل كل رجل منهم فقالوا غفر الله لرسول الله','110','0','0');
INSERT INTO [t6449] VALUES (167,'بمقالتهم فأرسل إلى الأنصار فجمعهم في قبة من آدم ولم يدع معهم أحدا غيرهم فلما اجتمعوا جاءهم رسول الله','110','0','0');
INSERT INTO [t6449] VALUES (168,'إنما أعطي رجالا حدثاء عهد بكفر أتألفهم وقال لهم أفلا ترضون أن يذهب الناس بالأموال وترجعون برسول الله','110','0','0');
INSERT INTO [t6449] VALUES (169,'إنكم ستجدون بعدي أثرة شديدة فاصبروا حتى تلقوا الله ورسوله وإني فرطكم على الحوض قال أنس فلم يصبروا فإذا طعن وقال ضرب عمارا قيل له هذا غير ثابت عنه ولو ثبت ذلك فللأئمة أن يؤدبوا رعيتهم إذا رأى واجبا لهم فإن كان ذلك ظلما','111','0','0');
INSERT INTO [t6449] VALUES (170,'على رجل ردعة من صفرة فهوى إلى بطنه بخشبة في يده فأصاب صدره فجرحه فقال النبي','111','0','0');
INSERT INTO [t6449] VALUES (171,'فيضعف أن يكلمه عند الناس فأخذ بوقا بزمام ناقته فاقل رسول الله','112','0','0');
INSERT INTO [t6449] VALUES (172,'فقمت إليه فأخذت برأسه فكببته على أنفه فكأنما كان غزو','112','0','0');
INSERT INTO [t6449] VALUES (173,'يوما قسما فقال له رجل إن هذه القسمة ما أريد بها وجه الله قال فأتيت النبي','114','0','0');
INSERT INTO [t6449] VALUES (174,'يوم حنين وهو يقسم تبرا فقال يا محمد إعدل قال ويحك ومن يعدل إذا لم أعدل','115','0','0');
INSERT INTO [t6449] VALUES (175,'قد ولى زيد بن ثابت بن حارثة فطعن بعض الناس في إمرته حتى قام خطيبا منكرا عليهم فيما طعنوا عليه وقالوا فيه وفي أسامة ابنه رضي الله عنهما','117','0','0');
INSERT INTO [t6449] VALUES (176,'وبعث بعثا وأمر عليهم أسامة بن زيد فطعن الناس في إمرته فقام رسول الله','117','0','0');
INSERT INTO [t6449] VALUES (177,'قال وأحسبه لم يدعهم يخرجون من المدينة حتى مات وقال مالك حبس أبا هريرة وأبا ذر وابن مسعود وغيرهم حتى قتل وقال ما هذه الأحاديث التي تحدثونها عن رسول الله','121','0','0');
INSERT INTO [t6449] VALUES (178,'إلا حديث كان يذكر على عهد عمر رضي الله عنه فإن عمر رضي الله عنه كان رجلا يخيف الناس في الله فإن احتجوا بما روى الأعمش عن إبراهيم عن همام عن حذيفة قال لا يلي بعد عمر رضي الله عنه إلا صغير أبتر مولى الحق اسنه قيل لهم إنما','121','0','0');
INSERT INTO [t6449] VALUES (179,'فأما إذا قال من ذاته فهو رأي يخطئ فيه ويصيب فإن احتجوا برواية الروافض وعلمائهم أن حذيفة وعمارا رضي الله عنهما روي عنهما قالا في كافر أو أن طلحة فيمن حصره وأن عليا أعان على قتله ومالا وأن الناس خذلوا','122','0','0');
INSERT INTO [t6449] VALUES (180,'ذرب لساني فقال أين أنت من الاستغفار إني لأستغفر الله تعالى كل يوم مائة مرة وأما طلحة رضي الله عنه','123','0','0');
INSERT INTO [t6449] VALUES (181,'لا أستحي ممن تستحيه الملائكة وإني لأستحي من الله تعالى أن أبايع وعثمان قتيل على وجه الأرض لم يدفن بعد','125','0','0');
INSERT INTO [t6449] VALUES (182,'وما كان منه من خروجه من البصرة وتنكبه عن الحجاز وتباعده من المدينة عن بيعة علي ماذا أيضا حقا وصوابا وهذا ما لا يقوله وإن كان بعض ما يفعله حقا وبعضه خطأ فالاحتجاج بقوله في حال الرضا أولى مما يقوله في حال الغضب فلو اتبعتم في أمره','130','0','0');
INSERT INTO [t6449] VALUES (183,'مما لكم فيه حجة قيل له','130','0','0');
INSERT INTO [t6449] VALUES (184,'لم أقم سمعت رسول الله','131','0','0');
INSERT INTO [t6449] VALUES (185,'وهو جالس في ظل دومة وعنده كاتب يملي عليه مم قال يا بن حوالة كيف أنت إذ نشأت فتنة فذكرها قلت لا أدري ما خار الله ورسوله قال فمر برجل متقنع فقال هذا وأصحابه يومئذ على الحق فأتيته فأخذت بمنكبه وأقبلت بوجهه على رسول الله','131','0','0');
INSERT INTO [t6449] VALUES (186,'فتنة فحذر منها فقالوا يا رسول الله فما تأمر من أدركها منا قال عليكم بابن وأصحابه يعني عثمان ابن عفان رضي الله عنه','132','0','0');
INSERT INTO [t6449] VALUES (187,'أحدا أو حراء ومعه أبو بكر وعمر وعثمان رضي الله عنهم فرجت الجبل فقال أثبت أحد فإن عليك نبي وصديق وشهيدان','132','0','0');
INSERT INTO [t6449] VALUES (188,'كان على صخرة حراء فتحركت فقال إسكني فما عليك إلا نبي أو صديق أو شهيد وكان عليها رسول الله','133','0','0');
INSERT INTO [t6449] VALUES (189,'قلت بلى قالت كنت قاعدة أنا وحفصة يوما عنده فأقبل عثمان فجلس إليه فأقبل إليه بوجهه وحدثه قالت فسمعته يقول يا عثمان إن الله عز وجل مقمصك قميصا فإن أرادوك على خلعه فلا تخلعه يقول ذلك ثلاث مرات فهذه الأحاديث دالة على أن أحدا من','133','0','0');
INSERT INTO [t6449] VALUES (190,'وأبي بكر وعمر رضي الله عنهما واجتمع أفاضل الصحابة والمشهود لهم بالجنة على تقديمه وتوليته وإمامته لا يلزمه إلا ما اجتمعوا عليه إنه مشى فيه مما لا يمكن لعثمان فيه تأويلا وأما أن يدفع عثمان عن أن يفعل ويفرط منه فلا لا سيما ومن كان','134','0','0');
INSERT INTO [t6449] VALUES (191,'ما نسب الله إليهم من القدر العظيم والسوابق القديمة والمناقب الثواب الجزيل والمحاسن المشهورة المذكورة وقد قص الله تعالى علينا في كتابه أحوال أنبيائه وأصفيائه وأضاف إليهم بعض أفعالهم فقال تعالى وعصى آدم ربه فغوى','134','0','0');
INSERT INTO [t6449] VALUES (192,'قال ما من ولد آدم أحد إلا وقد عمل خطيئة أو هم بها ليس يحيى بن زكريا','135','0','0');
INSERT INTO [t6449] VALUES (193,'يصلي حتى ترم قدماه أو قيل ساقاه فقال له أليس قد غفر الله لك ما تقدم من ذنبك وما تأخر فيقول أفلا أكون عبدا شكورا','135','0','0');
INSERT INTO [t6449] VALUES (194,'حتى تورمت قدماه فقيل له أليس قد غفر الله لك ما تقدم من ذنبك وما تأخر قال أفلا أكون عبدا شكورا','135','0','0');
INSERT INTO [t6449] VALUES (195,'بحضرة العدد وكذلك عفى عن حاطب بن أبي بلتعة حين كتب إلى المشركين يخبرهم بشأن رسول الله','136','0','0');
INSERT INTO [t6449] VALUES (196,'من الحدود على غير واحد من الصحابة من قطع السارق ورجم المعترف بالزنا ماعزا وأتي بالنعيمان سكران فأمر بجلده وكان نعيمان من أهل بدر وكل هذا مغفورا له ومسكوتا عنه لما أولاهم الله تعالى من السوابق الكريمة والمناقب العظيمة وشكر لهم وأثنى','137','0','0');
INSERT INTO [t6449] VALUES (197,'لأناس من أصحابه في الغضب فينطلق ناس ممن سمع ذلك من حذيفة فيأتون سلمان فيذكرون له قول حذيفة فيقول سلمان هو أعلم بما يقول فيرجعون إلى حذيفة فيقولون ذكرنا قولك لسلمان فما صدقك ولا كذابك فأتى حذيفة سلمان وهو في مبقلة فقال لسلمان ما','138','0','0');
INSERT INTO [t6449] VALUES (198,'كان يغضب فيقول في الغضب لأناس من أصحابه ويرضى فيقول في الرضا لأناس من أصحابه أما تنتهي حذيفة حتى تورث رجالا حب رجالا ورجالا بغض رجال وحتى توقع اختلافا وفرقة ولقد علمت أن رسول الله','138','0','0');
INSERT INTO [t6449] VALUES (199,'يدعو رافعا يديه يقول اللهم إنما أنا بشر فلا تعاقبني أيما رجل من المؤمنين آذيته وشتمته فلا تعاقبني به ورواه الأعمش عن أبي الضحى عن مسروق عن عائشة رضي الله عنها','139','0','0');
INSERT INTO [t6449] VALUES (200,'اليتيمة في البيت فقال آنت هيه لقد كبرت لاكبر سنك فرجعت إلى أم سليم تبكي قال فضحك رسول الله','139','0','0');
INSERT INTO [t6449] VALUES (201,'ورواه أبو الزناد عن الأعرج عن أبي هريرة ورواه الأعمش عن أبي سفيان عن جابر وعمرو بن سليم عن أبي سعيد','139','0','0');
INSERT INTO [t6449] VALUES (202,'وأبو بكر وعمر رضي الله عنهما من أنفسهما وإنما يقتد ممن فعل ما ليس له أن يفعل وثبت عن الرسول','140','0','0');
INSERT INTO [t6449] VALUES (203,'سيد الأولين والآخرين يقول إنما أنا بشر مثلكم فلا يتتبع هفوات أصحاب رسول الله','140','0','0');
INSERT INTO [t6449] VALUES (204,'وفي غيبته فيبلغه من الله تعالى ذلك الخصام والسباب في حال الغضب والموجدة أشياء فلا يأخذهم به ولا يعيب ذلك عليهم بل يأمرهم بالعفو ويحضهم على التآلف ويطفي ثائرة الغضب وثورة البشرية وذلك مثل ما جرى بين السيدين سعد بن معاذ وسعد بن عبادة','140','0','0');
INSERT INTO [t6449] VALUES (205,'حتى سكتوا ما كان بين العباس وعلي وهما كبيرا أصحاب رسول الله','141','0','0');
INSERT INTO [t6449] VALUES (206,'إذا ذكر أصحابي فأمسكوا','142','0','0');
INSERT INTO [t6449] VALUES (207,'ووفاء للمسلمين ورغبة وحذرا من أن يسن لهم ما لم يأمره الله تعالى به رغبة في الشهادة التي أكرمه الله بها وقد','144','0','0');
INSERT INTO [t6449] VALUES (208,'قال الله تبارك وتعالى وعد الله الذين آمنوا منكم وعملوا الصالحات ليتسخلفنهم في الأرض الآية فبان للمسلمين ما مكن الله تعالى به نبيه','145','0','0');
INSERT INTO [t6449] VALUES (209,'ما عنده فقبضه إليه بعد كمال الدين به وتمام النعمة عليه وأداء ما حمله من الرسالة وإبلاغه صابرا محتسبا صلوات الله عليه وبركاته ثم قام مقامه الصديق رضي الله عنه وأرضاه فقام مقامه في إقامة الحق وحفظ الدين وصيانة أهله فقاتل من ارتد من العرب','145','0','0');
INSERT INTO [t6449] VALUES (210,'بعده على استخلاف عثمان بن عفان رضي الله عنه وأرضاه من غير اختلاف ولا تنزع مكن له في الأرض فتح الله تعالى به أقاص الأرض فنعم المؤمنون في أيامه لرأفته بهم وخزي في ديارهم الكفار لغلظته عليهم حتى أتته الشهادة التي بشره الله تعالى بها على','146','0','0');
INSERT INTO [t6449] VALUES (211,'واجترؤا على حرمة من صحبه بتأويله ورأيه وسيفه في الإفساد والتفرقة بين المسلمين رأس الفتنة وقادة الأباطيل يرون أنهم أفضل ممن اختاره الله لصحبة الرسول','146','0','0');
INSERT INTO [t6449] VALUES (212,'وهو','146','0','0');
INSERT INTO [t6449] VALUES (213,'المدينة حرام ما بين عير إلى ثور فمن أحدث فيها حدثا أو آوى محدثا فعليه لعنة الله والملائكة والناس أجمعين ولا يقبل الله منه صرفا ولا عدلا وذمة المسلمين واحدة يسعى بها أدناهم فكانت اللعنة التي لحقتهم من رسول الله','147','0','0');
INSERT INTO [t6449] VALUES (214,'ليلة فصلى حتى إذا كان الفجر قال لرسول الله','148','0','0');
INSERT INTO [t6449] VALUES (215,'ثم قال سألت ربي أن لا يهلك أمتي بالسنة فأعطانيها وسألته أن لا يهلكهم بالغرق فأعطانيها وسألته أن لا يلبسهم شيعا ويذيق بعضهم بأس بعض فمنعنيها','148','0','0');
INSERT INTO [t6449] VALUES (216,'أعوذ بوجهك الكريم قال أو من تحت أرجلكم قال النبي','148','0','0');
INSERT INTO [t6449] VALUES (217,'بخمس وعشرين سنة فالبسوا شيعا وأذيق بعضهم بأس بعض وكان الحسن رحمه الله فيما روى أبو الحسن القزاز عن حميد عنه يقول أكرم الله أن يرى نبيه عليه السلام في أمته ما يكره يعني قوله فإما نذهبن بك فإنا منهم منتقمون وأما قوله','149','0','0');
INSERT INTO [t6449] VALUES (218,'قال تدور رحى المسلمين على خمس أو ست أو سبع وثلاثين سنة فإن تهلكوا فسبيل من هلك وأن يقم لهم دينهم يقوم سبعين عاما فقال عمر يا رسول الله','149','0','0');
INSERT INTO [t6449] VALUES (219,'مما بقي رواه الثوري عن منصور','150','0','0');
INSERT INTO [t6449] VALUES (220,'قال تدور رحى المسلمين على خمس وثلاثين أو ست وثلاثين سنة فإن هلكوا فسبيل من هلك وإن بقوا بقي لهم دينهم سبعين سنة فصار أمرهم إلى ما قال حذيفة لما أن قتلوا برى بالسيف لم تحجوا معا ولم تصلوا معا ولم تقاتلوا جميعا أبدا فالاختلاف','150','0','0');
INSERT INTO [t6449] VALUES (221,'مقامي فيكم فقال أكرموا أصحابي ثم الذين يلونهم ثم الذين يلونهم ثم يفشو الكذب حتى يحلف الرجل ولم يستحلف ويشهد الرجل ولم يستشهد فمن أراد بحبوحة الجنة فليلزم الجماعة فإن الشيطان مع الواحد وهو من الاثنين أبعد','151','0','0');
INSERT INTO [t6449] VALUES (222,'من سره بحبوحة الجنة فليلزم الجماعة رواه معمر وإسرائيل والحسين بن واقد في آخرين عن عبد الملك بن عمير عن عبد الله بن الزبير بن عمر','151','0','0');
INSERT INTO [t6449] VALUES (223,'قام فينا كمقامي فيكم وقال أيها الناس اتقوا الله في أصحابي ثم الذين يلونهم ثم الذين يلونهم ثم يفشو الكذب وشهادات الزور حتى يحلف الرجل من غير أن يستخلف ويشهد الرجل من غير أن يستشهد فمن سره أن يحلل بحبوحة الجنة فيلزم الجماعة فإن الشيطان مع','152','0','0');
INSERT INTO [t6449] VALUES (224,'من فارق الجماعة شبرا فارق الإسلام','152','0','0');
INSERT INTO [t6449] VALUES (225,'إنها ستكون وهنات وهنات فمن جاءكم يفرق أمر هذه الأمة وهم جميع فاقتلوه','153','0','0');
INSERT INTO [t6449] VALUES (226,'وأصحابه بملازمتهم هم الصحابة والتابعون من العلماء لا الجماعة الفسقة الجهلة الظلمة المنتهكون لحرمة أصحاب رسول الله','153','0','0');
INSERT INTO [t6449] VALUES (227,'خلافة أمير المؤمنين علي عليه السلام','154','0','0');
INSERT INTO [t6449] VALUES (228,'فقال الخلافة في أمتي ثلاثون ثم يكون ملكا ثم قال سفينة إمسك عليك خلافة أبي بكر وعمر رضي الله عنهما ثلاثة عشر سنة وستة أشهر وخلافة عثمان رضي الله عنه اثنا عشر سنة ثم خلافة علي مكملة الثلاثين','154','0','0');
INSERT INTO [t6449] VALUES (229,'قال الخلافة في أمتي بعدي ثلاثون سنة فكان أمير المؤمنين علي رضي الله عنه ممن زين الله من الخلافة ولم يزين بالخلافة أمسك عن قتال من تمر د عن بيعته كما امتنع الصديق رضي الله عنه عن مقاتلته حين تخلف عن بيعته إلى أن بايع','155','0','0');
INSERT INTO [t6449] VALUES (230,'قال تفترق أمتي فرقتين فيمرق من بينهما مارقة تقتلها أولى الطائفتين بالحق','155','0','0');
INSERT INTO [t6449] VALUES (231,'في حديث ذكر فيه قوما يخرجون على فرقة من الناس يقتلهم أقرب الطائفين من الحق فتولى علي رضي الله عنه قتلهم لأن خروجهم كان بعد الجمل بين علي ومعاوية لا بين علي وطلحة والزبير رضي الله عنهم فلما اختلفت الصحابة كان على الذين سبقوا إلى','156','0','0');
INSERT INTO [t6449] VALUES (232,'بل ازداد به ارتفاعا لمعرفته بفضل من قدمه على نفسه إذ كان ذلك موجودا في الأنبياء والرسل عليهم السلام قال الله تعالى تلك الرسل','156','0','0');
INSERT INTO [t6449] VALUES (233,'ووقوفه وفي سهم ذوي القربى وغير ذلك من أحكامهم لم يخالفهم في شيء منه مع قوله رضي الله عنه اقضوا كما كنتم تقضون حتى يكون للناس إمام جماعة أو أموت كما مات أصحابي فهذا القول يدل على رجوعه عن بيع أمهات الأولاد فإن طعن طاعن على ما','159','0','0');
INSERT INTO [t6449] VALUES (234,'من هذه الأخبار قيل إن هذا الطعن كبير على الأعلام من الصحابة وأعلام الدين والهدى فإن قالوا لم تصل هذه الأخبار إليهم قيل لهم فما الذي حملكم على الطعن عليهم ولا تعلمون عن رسول الله','160','0','0');
INSERT INTO [t6449] VALUES (235,'وذهابها عنهم في قربهم من رسول الله','160','0','0');
INSERT INTO [t6449] VALUES (236,'من علي وطلحة والزبير وغيرهم من أكابر الصحابة وسادة العلماء منهم فإن قالوا ولم اقتتلوا بأي حجة احتجوا في القتال قيل له أما من كتاب الله عز وجل فإن الله عز وجل أمر بقتال أهل','160','0','0');
INSERT INTO [t6449] VALUES (237,'أمرت أن أقاتل الناس حتى يقولوا لا إله إلا الله فإن قالوها عصموا مني دماءهم وأموالهم إلا بحقها فأعلم عليه السلام أن ثم حقوقا تستباح بها الدماء والأموال من ذلك قتال أهل البغي وقتال الخوارج وقتال اللصوص ورجم الزاني المحصن','161','0','0');
INSERT INTO [t6449] VALUES (238,'أنه قال','162','0','0');
INSERT INTO [t6449] VALUES (239,'يقول من قتل دون ماله مظلوما فله الجنة','162','0','0');
INSERT INTO [t6449] VALUES (240,'قال من قتل دون ماله فهو شهيد','163','0','0');
INSERT INTO [t6449] VALUES (241,'قال من قتل دون ماله فهو شهيد ومن قتل دون أهله فهو شهيد ومن قتل دون دمه فهو شهيد فجعل','163','0','0');
INSERT INTO [t6449] VALUES (242,'في الدماء والأموال والأعراض في التحريم فإذا كان له أن يقاتل عن نفسه فكذلك يباح له أن يقاتل عن ماله وإنها نهى رسول الله','164','0','0');
INSERT INTO [t6449] VALUES (243,'من ذلك ألا ترى أن رسول الله','164','0','0');
INSERT INTO [t6449] VALUES (244,'واجتمع المسلمون على أبي بكر فسمعت وأطعت ثم حضر أبو بكر قلت رأى أنه لا يعدلها عني فولاها عمر فسمعت وأطعت ثم أن عمر أصيب فظننت أنه لا يعدلها عني فجعلها في ستة أنا منهم فولوها عثمان فسمعت وأطعت ثم إن عثمان قتل فجاؤا فبايعوني طائعين غير','165','0','0');
INSERT INTO [t6449] VALUES (245,'وأما طلحة والزبير رضي الله عنهما فيريان أن الذب عن النفس والمال شهادة وكان طلحة يقول بايعت كارها واللج علي فرأى بأن الأشتر','165','0','0');
INSERT INTO [t6449] VALUES (246,'فيما اختلفوا فيه واجتهدوا فيه من الرأي مأجورون ومحمودون وإن كان الحق مع بعضهم دون الكل ولا يغضب من قال بقول بعضهم وترك قول بعض وأنه عنده مصيب الحق الذي أمر به من طريق الرأي للإجتهاد','166','0','0');
INSERT INTO [t6449] VALUES (247,'يقول إذا حكم الحاكم فاجتهد فأصاب فله أجران وإن حكم فاجتهد فأخطأ فله أجر واحد قال فحدثت بهذا الحديث أبا بكر بن محمد بن عمرو بن حزم فقال هكذا حدثني أبو سلمة بن عبد الرحمن عن أبي هريرة','166','0','0');
INSERT INTO [t6449] VALUES (248,'الذين هم أصلنا في القدوة بهم في النظر والاجتهاد أولى من أن يطعن عليهم لما فازوا به من السوابق والمناقب وليس لعقود من قعد عنهم وإمساكهم عن القتال حجة للطاعن عليهم فإن من أمسك عن القتال وقعد عن الخروج مع إحدى الطائفتين حجة إذ لم','167','0','0');
INSERT INTO [t6449] VALUES (249,'وشهادته عليهم فاستعظموا إسلال السيوف والخروج على المشهود له بالجنة والشهادة وكيف يحكم لإحدى الطائفتين على الأخرى فكلاهما شهيد ولا يكون شهيدا من يستحيل دمه','167','0','0');
INSERT INTO [t6449] VALUES (250,'على حراء فتزعزع بهم الجبل فقال رسول الله','167','0','0');
INSERT INTO [t6449] VALUES (251,'وأبو بكر وعمر وعثمان وعلي وطلحة والزبير وسعد وعبد الرحمن بن عوف وسعيد','167','0','0');
INSERT INTO [t6449] VALUES (252,'شهيد يمشي على وجه الأرض','168','0','0');
INSERT INTO [t6449] VALUES (253,'لكل نبي حواري في الجنة وحواري الزبير','168','0','0');
INSERT INTO [t6449] VALUES (254,'يقول طلحة والزبير جاراي في الجنة فالإمساك عن ذكر أصحاب رسول الله','169','0','0');
INSERT INTO [t6449] VALUES (255,'بإكرام أصحابه وأوصى بحقهم وصيانتهم وإجلالهم','169','0','0');
INSERT INTO [t6449] VALUES (256,'قام فينا مقامي فيكم فقال احفظوني في أصحابي ثم الذين يلونهم ثم الذين يلونهم ثم الذين يلونهم ثلاثا','170','0','0');
INSERT INTO [t6449] VALUES (257,'احفظوني في أصحابي فمن حفظني فيهم كان عليه من الله حافظ ومن لم يحفظني فيهم تخلى الله عنه ومن تخلى الله عنه أوشك أن يأخذه','170','0','0');
INSERT INTO [t6449] VALUES (258,'إذا رأيتم الذين يسبون أصحابي فالعنوهم هم شرار أمتي أجرأهم على أصحابي','170','0','0');
INSERT INTO [t6449] VALUES (259,'إذا رأيتم الذين يسبون أصحابي فالعنوهم','170','0','0');
INSERT INTO [t6449] VALUES (260,'إذا ذكر أصحابي فامسكوا','171','0','0');
INSERT INTO [t6449] VALUES (261,'تأبلوا عليهم القلوب ولا تذكروا مساويهم فتحرشوا الناس عليهم','171','0','0');
INSERT INTO [t6449] VALUES (262,'بأن يعفو عن أصحابه ويستغفر لهم ويخفض لهم الجناح قال تعالى ولو كنت فظا غليظ القلب لانفضوا من حولك فاعف عنهم واستغفر لهم وشاورهم في الأمر','171','0','0');
INSERT INTO [t6449] VALUES (263,'وصحابته والإسلام والمسلمين','172','0','0');
INSERT INTO [t6449] VALUES (264,'فبعثه في رسالته وانتخبه بعلمه ثم نظر في قلوب الناس بعده له أصحابا فجعلهم أنصار دينه ووزراء نبيه فما رآه المؤمنين حسنا فهو عند الله حسن وما رآه المؤمنون قبيحا فهو عند الله عز وجل قبيح','172','0','0');
INSERT INTO [t6449] VALUES (265,'الله ألله في أصحابي لا تتخذوهم غرضا بعدي فمن أحبهم فبحبي أحبهم ومن أبغضهم فببغضي أبغضهم ومن أذاهم فقد أذاني ومن أذاني فقد أذى الله ومن آذ الله يوشك أن يأخذه','172','0','0');
INSERT INTO [t6449] VALUES (266,'إحفظوني في أصحابي وأصهاري فمن حفظني فيهم حفظه الله في الدنيا والآخرة ومن لم يحفظني في أصحابي وأصهاري تخلى الله تعالى منه ومن تخلى الله منه أوشك أن يأخذه فإن قال قائل فقد نازع عليا رضي الله عنه غير طلحة والزبير وعائشة رضي الله','173','0','0');
INSERT INTO [t6449] VALUES (267,'لقوله أوصيكم في أصحابي خيرا لا سيما إذا كان متأولا وإن كان في تأويله غير مصيب نقدي في ذلك بكبار الصحابة الذين شاهدوا حربهم فكفوا وقعدوا لإشكال ذلك عليهم فإذا كان لهم في قربهم منهم ومشاهدتهم لهم أن يكفوا ويعقدوا فنحن في تأخرنا','173','0','0');
INSERT INTO [t6449] VALUES (268,'ودعوته','173','0','0');
INSERT INTO [t6449] VALUES (269,'إياه لمعصيته فأرجو له عفو الله بدعاء رسول الله','174','0','0');
INSERT INTO [t6449] VALUES (270,'قد بعثه الله يدعو في صلاته لأمته ويستغفر لهم لأحيائهم وأمواتهم فلو كان كل دعوة مجابة لما كان أحد من أمته معذبا أو دخل النار وكذلك نوح وإبراهيم عليهما السلام دعوا لمن تبعهما من المؤمنين والمؤمنات قال الله تبارك وتعالى مخبرا عن نوح','174','0','0');
INSERT INTO [t6449] VALUES (271,'على وجهين فوجه يلعن قوما على مآثم ارتكبوها كلعنة عليه الصلاة والسلام للواصلة والواشمة فهذا جائز عفو الله تعالى فيه لأنه من حقوقه وأما لعنته عليه السلام لمن ظلم الله فيه أحدا بل ينتقم من الظالم للمظلوم ولا يعفو عنه','174','0','0');
INSERT INTO [t6449] VALUES (272,'على معنيين أحدهما في غير غضب يريد بذلك إعلام أمته بعظم ما عظم الله والتحذير مما حذر الله منه كلعنته من أكل الرباء ومن أحدث حدثا أو آوى محدثا ومن ادعى إلى غير أبيه ومن سب أصحابه إلى غير ذلك لعن فاعليها في حال الرضاء تأكيدا لما','175','0','0');
INSERT INTO [t6449] VALUES (273,'يوم أحد وأمر الرسول','176','0','0');
INSERT INTO [t6449] VALUES (274,'لا ترجعوا بعدي كفارا فإذا تواجه المسلمان بسيفيهما وما في معناه لأنهم أول من أحدث هذه الأشياء وهذا ما لا يقوله مسلم معظم حرمة الصحابة ومعتقد تفضيلهم وسابقتهم والله أعلم قال الناسخ تم الكتاب والحمد لله رب العالمين','177','0','0');
