INSERT INTO [t6534] VALUES (123,'٣٢ - إذا دخل في الصلاة متيمما، فوجد الماء في الصلاة بطلت، واستأنفها','136','91','0');
INSERT INTO [t6534] VALUES (124,'٣٣ - إذا [وجد ماء] يكفي بعض طهره لزمه استعماله، ثم تيمم للباقي','137','91','0');
