INSERT INTO [t6534] VALUES (126,'٣٥ - إذا نوى بتيممه الحدثين، ثم أحدث حدثا أصغر، بطل تيممه للأصغر، دون الأكبر','139','91','0');
INSERT INTO [t6534] VALUES (127,'٣٦ - إذا انقطع دم الحائض فتيممت لإباحة وطئها، ثم أحدثت لم يمنع ذلك وطأها بالتيمم','140','91','0');
INSERT INTO [t6534] VALUES (128,'٣٧ - صوف الميتة وشعرها طاهر','140','91','0');
INSERT INTO [t6534] VALUES (129,'٣٨ - إذا تخللت الخمرة بنفسها طهرت','141','91','0');
INSERT INTO [t6534] VALUES (130,'٣٩ - لا يطهر شيء من النجاسات بالاستحالة','142','91','0');
INSERT INTO [t6534] VALUES (131,'٤٠ - يطهر بول الغلام الذي لم يأكل الطعام بنضحه','142','91','0');
INSERT INTO [t6534] VALUES (132,'٤١ - الحيض يمنع وجوب الصلاة','144','91','0');
INSERT INTO [t6534] VALUES (133,'٤٢ - يلزم المرأة نقض شعرها لغسل الحيض','144','91','0');
INSERT INTO [t6534] VALUES (134,'٤٣ - إذا انقطع دم الحائض صح الصوم منها','145','91','0');
INSERT INTO [t6534] VALUES (135,'٤٤ - لأقل الحيض حد','146','91','0');
INSERT INTO [t6534] VALUES (136,'كتاب الصلاة','147','0','0');
INSERT INTO [t6534] VALUES (137,'٤٥ - يكفر المسلم المكلف بترك الصلاة من غير عذر معتقدا وجوبها في الصحيح من المذهب','147','136','0');
INSERT INTO [t6534] VALUES (138,'٤٦ - إذا اشتبهت عليه القبلة في السفر أجزأه أن يصلي مرة واحدة بالاجتهاد، ولا يلزمه أن يصلي إلى أربع جهات','148','136','0');
INSERT INTO [t6534] VALUES (139,'٤٧ - إذا اشتبهت القبلة فصلى أربع صلوات إلى أربع جهات أجزأته كلها، مع القطع بكونه صلى ثلاثا إلى غير القبلة','149','136','0');
INSERT INTO [t6534] VALUES (140,'٤٨ - إذا خفي عليه وقت الصلاة فتحرى وصلى، فبان قبله لم يجزئه','150','136','0');
INSERT INTO [t6534] VALUES (141,'٤٩ - إذا صلى المسافر فرضا في سفينة لزمه التوجه إلى القبلة في صلاته كلها','151','136','0');
INSERT INTO [t6534] VALUES (142,'٥٠ - إذا وهب سترة للصلاة عادمها لم يلزمه قبولها','151','136','0');
INSERT INTO [t6534] VALUES (143,'٥١ - يجوز الأذان للفجر قبل دخول وقتها','152','136','0');
INSERT INTO [t6534] VALUES (144,'٥٢ - إذا صلى على منديل، فرأى نجاسة موضع سجوده، فغطاها بطرفه الطاهر، وسجد علبها صحت صلاته','153','136','0');
INSERT INTO [t6534] VALUES (145,'٥٣ - لا تصح الصلاة في معاطن الإبل، وهي التي تقيم فيها','153','136','0');
INSERT INTO [t6534] VALUES (146,'٥٤ - إذا صلى ناسيا لحدثه لم تصح الصلاة','154','136','0');
INSERT INTO [t6534] VALUES (147,'٥٥ - لا تصح إمامة الأخرس بناطق، ولا بمثله نص عليه وتصح إمامة الأمي بمثله','155','136','0');
INSERT INTO [t6534] VALUES (148,'٥٦ - العاصي بسفره لا يقصر، ولا يجمع، ولا يفطر، ولا يزيد على مسح مقيم','157','136','0');
INSERT INTO [t6534] VALUES (149,'٥٧ - إذا ذكر صلاة سفر في سفر آخر جاز له قصرها','157','136','0');
INSERT INTO [t6534] VALUES (150,'٥٨ - إذا دخل المسافر بلدا له فيه زوجة، أو تزوج فيه، ولم ينو إقامة يصير بها في حكم المقيم، لم يجز له القصر','158','136','0');
INSERT INTO [t6534] VALUES (151,'٥٩ - إذا جمع بين صلاتين في وقت أولاهما لم يفرق بينهما إلا بقدر الإقامة والوضوء','159','136','0');
INSERT INTO [t6534] VALUES (152,'٦٠ - إذا جمع بينهما في وقت أولاهما، وفرق بينهما بوضوء، ثم بان أنه صلى الأولى محدثا بطلتا','160','136','0');
INSERT INTO [t6534] VALUES (153,'٦١ - إذا صلى الصبي ظهر يوم الجمعة، ثم بلغ قبل أن تصلى الجمعة، لزمه فرض الجمعة','160','136','0');
INSERT INTO [t6534] VALUES (154,'كتاب الزكاة','162','0','0');
INSERT INTO [t6534] VALUES (155,'٦٢ - إذا أخرج عن خمس من الابل بعيرا لم يجزئه','162','154','0');
INSERT INTO [t6534] VALUES (156,'٦٣ - إذا ملك تسعا وثلاثين شاة، ثم نتجت سخلة قبل حلول الحول، فلا زكاة فيها حتى يحول الحول عليها وهي كاملة','163','154','0');
INSERT INTO [t6534] VALUES (157,'٦٤ - إذا نوى علف السائمة لم ينقطع بها حكم السوم','163','154','0');
INSERT INTO [t6534] VALUES (158,'٦٥ - إذا اشترى الخباز ملحا ليخبز به خبزا يبيعه، فحال الحول وقيمته نصاب، وجب عليه زكاة قيمته','164','154','0');
INSERT INTO [t6534] VALUES (159,'٦٦ - إذا كانت له جارية للخدمة، فنواها للتجارة، لم تصر للتجارة ما لم يبعها','165','154','0');
INSERT INTO [t6534] VALUES (160,'٦٧ - إذا ملك بالوصية عروضا، ونواها حال تملكها للتجارة، صارت للتجارة وزكاها','167','154','0');
INSERT INTO [t6534] VALUES (161,'٦٨ - إذا خرصت الثمار على أربابها، وجب على الخارص أن يترك لهم الثلث أو الربع','168','154','0');
INSERT INTO [t6534] VALUES (162,'٦٩ - إذا سرق المال أو غصب، ثم عاد إلى مالكه، لم تجب زكاته لما مضى','169','154','0');
INSERT INTO [t6534] VALUES (163,'٧٠ - لا تجب زكاة المال المغصوب والضال','170','154','0');
INSERT INTO [t6534] VALUES (164,'٧١ - إذا حال الحول على عبيد للتجارة وجب زكاة فطرتهم مع زكاة قيمتهم','170','154','0');
INSERT INTO [t6534] VALUES (165,'٧٢ - نقصان النصاب في أثناء الحول شهرا أو شهرين يمنع وجوب الزكاة','171','154','0');
INSERT INTO [t6534] VALUES (166,'٧٣ - ما زاد على نصاب النقدين تجب الزكاة فيه بحسابه وإن قل، وكذا الزروع والثمار','171','154','0');
INSERT INTO [t6534] VALUES (167,'٧٤ - إذا ورث جماعة أموالا فيها زكوي، فحصل لكل منهم نصاب، أو حصل النصاب لجماعتهم، وجبت الزكاة فيه قبل القسمة','172','154','0');
INSERT INTO [t6534] VALUES (168,'٧٥ - إذا كان نصيب الغانمين بعد الخمس نصابا زكويا، وشروط الخلطة موجودة جرى في حول الزكاة','173','154','0');
INSERT INTO [t6534] VALUES (169,'٧٦ - إذا ملك عقارا قيمته نصاب فأكثر، لم تجب زكاة قيمته، سواء كان للسكنى أو للكراء','174','154','0');
INSERT INTO [t6534] VALUES (170,'٧٧ - إذا كاتب عبده على نصاب، فحال الحول عليه ولم يقبضه، فلا زكاة حتى يحول الحول بعد القبض','174','154','0');
INSERT INTO [t6534] VALUES (171,'٧٨ - يلزم العامل في المساقاة والمزارعة زكاة حصته قبل القسمة','176','154','0');
INSERT INTO [t6534] VALUES (172,'٧٩ - ما لا يشترط له الحول كالزروع والمعدن، لا تثنى عليه الزكاة','176','154','0');
INSERT INTO [t6534] VALUES (173,'٨٠ - يشترط حول الحول في وجوب زكاة الماشية، والنقدين، وعروض التجارة','177','154','0');
INSERT INTO [t6534] VALUES (174,'٨١ - تصرف الزكاة إلى الغزاة الذين لا حق لهم في الديوان مع الغنى، ولا يجوز صرفها إلى من يحج إلا مع الفقر','178','154','0');
INSERT INTO [t6534] VALUES (175,'٨٢ - أجرة كيال الزروع والثمار ليعلم قدرها تخرج من سهم العامل','178','154','0');
INSERT INTO [t6534] VALUES (176,'٨٣ - لا يلزم الإنسان فطرة زوجته الناشز','179','154','0');
INSERT INTO [t6534] VALUES (177,'كتاب الصيام','180','0','0');
INSERT INTO [t6534] VALUES (178,'٨٤ - لا يصح صيام الواجب إلا بنية من الليل','180','177','0');
INSERT INTO [t6534] VALUES (179,'٨٥ - إذا نوى الصوم ليلا، ثم فعل ما ينافيه، لم تنفسخ نيته ما لم يفسخها','181','177','0');
INSERT INTO [t6534] VALUES (180,'٨٦ - يثبت هلال الصوم بقول عدل','181','177','0');
INSERT INTO [t6534] VALUES (181,'٨٧ - إذا صام الناس بشهادة عدلين ثلاثين يوما، ولم يروا هلال شوال لغيم أفطروا','182','177','0');
INSERT INTO [t6534] VALUES (182,'٨٨ - إذا أدركه الفجر مجامعا، فعليه القضاء والكفارة','183','177','0');
INSERT INTO [t6534] VALUES (183,'٨٩ - قد بان أنه يفسد صومه بالنزع، فلو قال: إن وطئتك فأنت طالق، فأولج طلقت، فإذا نزع لم يلزمه مهر ولا حد','184','177','0');
INSERT INTO [t6534] VALUES (184,'٩٠ - إذا أكل شاكا في طلوع الفجر لم يقض','185','177','0');
INSERT INTO [t6534] VALUES (185,'٩١ - إذا أكل معتقدا بقاء النهار، فبان [أن] الشمس قد غابت، فصومه صحيح','185','177','0');
INSERT INTO [t6534] VALUES (186,'٩٢ - إذا نوى الإفطار أفطر، فإن عاد فنوى الصوم أجزأه في النفل، دون الفرض','186','177','0');
INSERT INTO [t6534] VALUES (187,'٩٣ - إذا تلبس بنفل صوم أو صلاة لم يلزمه إتمامه، ولا قضاؤه إن أفسده','187','177','0');
INSERT INTO [t6534] VALUES (188,'٩٤ - إذا خافت الحامل والمرضع على ولديهما أفطرتا وقضتا، وأطعمتا مسكينا عن كل يوم','190','177','0');
INSERT INTO [t6534] VALUES (189,'٩٥ - إذا عجز الشيخ الكبير والمريض الذي لا يرجى برؤه عن الاطعام لم يسقط عنهما','191','177','0');
INSERT INTO [t6534] VALUES (190,'٩٦ - إذا جامع المسافر الصائم، ولم ينو بجماعه الفطر لزمته الكفارة','191','177','0');
INSERT INTO [t6534] VALUES (191,'٩٧ - لا يصام عن الميت صوم رمضان، ويطعم عنه مسكين لكل يوم','192','177','0');
INSERT INTO [t6534] VALUES (192,'٩٨ - يجوز للمسافر التطوع بالصلاة من غير كراهة','193','177','0');
INSERT INTO [t6534] VALUES (193,'٩٩ - إذا نوى الصوم، ثم أغمي عليه جميع يومه، لم يصح صومه','194','177','0');
INSERT INTO [t6534] VALUES (194,'١٠٠ - اذا اشتبهت الأشهر على الأسير تحرى وصام، وقد أجزأه، إلا إذا تبين صومه قبل الشهر فلا يجزئه','195','177','0');
INSERT INTO [t6534] VALUES (195,'١٠١ - إذا نذر صوم يوم الخميس، أو الصلاة فيه لم يجزئه قبله','196','177','0');
INSERT INTO [t6534] VALUES (196,'١٠٢ - إذا نذر اعتكاف عشرة أيام متتابعة، لم يلزمه اعتكاف ليلة أول يوم منها، ولا الليالي المتخللة في وجه','197','177','0');
INSERT INTO [t6534] VALUES (197,'١٠٣ - إذا نذر اعتكاف العشر الأخير من شهر كذا، أجزأه اعتكاف ما بعد العشرين الأولين، تاما كان أو ناقصا','197','177','0');
INSERT INTO [t6534] VALUES (198,'كتاب الحج','199','0','0');
INSERT INTO [t6534] VALUES (199,'١٠٤ - لا يجوز للمرأة أن تنشيء السفر للحج إلا بمحرم','199','198','0');
INSERT INTO [t6534] VALUES (200,'١٠٥ - ليس للرجل منع امرأته من حجة الإسلام','200','198','0');
INSERT INTO [t6534] VALUES (201,'١٠٦ - إذا أحرم بحجتين أو عمرتين انعقد بواحدة','200','198','0');
INSERT INTO [t6534] VALUES (202,'١٠٧ - يصح إدخال الحج على العمرة، وهو أن يحرم بالعمرة وحدها، ثم بالحج قبل الطواف','201','198','0');
INSERT INTO [t6534] VALUES (203,'١٠٨ - يحرم على المحرم صيد البر، دون صيد البحر','202','198','0');
INSERT INTO [t6534] VALUES (204,'١٠٩ - إذا قتل المحرم البراغيث لم يلزمه شيء','202','198','0');
INSERT INTO [t6534] VALUES (205,'١١٠ - يحرم على المحرم عقد النكاح','204','198','0');
INSERT INTO [t6534] VALUES (206,'١١١ - إذا قتل المحرم صيدا بعد صيد لزمه جزاء كل واحد منهما، أخرج عن الأول أو لم يخرج','204','198','0');
INSERT INTO [t6534] VALUES (207,'١١٢ - إذا قتل جماعة صيدا لزمهم جزاء واحد إن كان مالا','206','198','0');
INSERT INTO [t6534] VALUES (208,'١١٣ - لا تحرم خطبة المحرمة','207','198','0');
INSERT INTO [t6534] VALUES (209,'١١٤ - إذا خرج في عينه شعر يؤلمه فقطعه، فلا فدية','207','198','0');
INSERT INTO [t6534] VALUES (210,'١١٥ - إذا حبس حلال حمامة في الحل فماتت، ومات فراخها في الحرم بحبسها ضمن الفراخ دونها','208','198','0');
INSERT INTO [t6534] VALUES (211,'١١٦ - إذا قطع حلال غصنا في الحل أصله في الحرم ضمنه','208','198','0');
INSERT INTO [t6534] VALUES (212,'١١٧ - إذا أتلف غصنا في الحل أصله في الحرم ضمنه','209','198','0');
INSERT INTO [t6534] VALUES (213,'١١٨ - إذا قتل المحرم في الحرم حماما مصوتا، ضمنه غير مصوت','209','198','0');
INSERT INTO [t6534] VALUES (214,'١١٩ - إذا قبل المحرم زوجته لشهوة، لزمته شاة','210','198','0');
INSERT INTO [t6534] VALUES (215,'١٢٠ - إذا أرسل المحل كلبه على صيد في الحل فطارده الكلب إلى الحرم، فقتله فيه، لم يضمن','211','198','0');
INSERT INTO [t6534] VALUES (216,'١٢١ - إذا تحلل من عمرته ووطئ، ثم أحرم بالحج وكمله ووطئ، ثم ذكر أنه طاف أحد الطوافين بغير طهارة ولم يعلم أيهما','212','198','0');
INSERT INTO [t6534] VALUES (217,'١٢٢ - قد تقدم: أنه إذا ذكر أنه طاف لأحد النسكين غير طاهر لم يجزئاه عن الفرض، وعليه القضاء','214','198','0');
INSERT INTO [t6534] VALUES (218,'١٢٣ - إذا حج عن نفسه، ثم أراد أن يعتمر عن غيره، أو حج عن غيره ثم أراد أن يعتمر عن نفسه','214','198','0');
INSERT INTO [t6534] VALUES (219,'١٢٤ - إذا أحرم مطلقا ثم عين تمتعا، أو إفرادا أو قرانا جاز','216','198','0');
INSERT INTO [t6534] VALUES (220,'١٢٥ - إذا أحرم مبهما صرفه إلى أي الأنساك شاء إن كان في أشهر الحج، وإلا انعقد بعمرة','217','198','0');
INSERT INTO [t6534] VALUES (221,'١٢٦ - الطهارة شرط في حجة الطواف','217','198','0');
INSERT INTO [t6534] VALUES (222,'١٢٧ - إحرام الصبي بالصلاة ينعقد بغير إذن وليه','218','198','0');
INSERT INTO [t6534] VALUES (223,'١٢٨ - إذا أفسد العبد حجه بالوطء، ثم عتق قبل فوات الوقوف، أجزأه قضاؤه من قابل عن حجة الإسلام','219','198','0');
INSERT INTO [t6534] VALUES (224,'١٢٩ - إذا استنيب في حجة عن غيره، فبدأ فاعتمر عن نفسه، ثم حج عن الغير ضمن جميع ما أنفق؛ لأنه صرف سفره إلى نسك عن نفسه، فإن كان المنوب [عنه] ميتا وقعت الحجة عنه، وإن كان حيا وقعت عن النائب','220','198','0');
INSERT INTO [t6534] VALUES (225,'١٣٠ - اذا أتلف صيدا ماخضا ضمنه بمثله، ولا يخرجه، بل يقومه ماخضا، ثم يشتري بالقيمة طعاما يتصدق به ذكره في المجرد','220','198','0');
INSERT INTO [t6534] VALUES (226,'١٣١ - اذا أتلف الذمي صيدا في الحرم ضمن','221','198','0');
INSERT INTO [t6534] VALUES (227,'١٣٢ - اذا أحصر المحرم بعدو تحلل','222','198','0');
INSERT INTO [t6534] VALUES (228,'كتاب البيع','224','0','0');
INSERT INTO [t6534] VALUES (229,'١٣٣ - إذا قال: بعتك هذه الصبرة كل قفيز بدرهم، على أن أنقصك قفيزا، وهما يعلمان كيلها صح','224','228','0');
INSERT INTO [t6534] VALUES (230,'١٣٤ - إذا قال: بعتك هذه الصبرة كل قفيز بدرهم، على أن أنقصك قفيزا، وهما يعلمان قفزانها، فالبيع صحيح، وإن جهلاها لم يصح','224','228','0');
INSERT INTO [t6534] VALUES (231,'١٣٥ - إذا باعه قفيزا من صبرة مجهولة الكيل صح','225','228','0');
INSERT INTO [t6534] VALUES (232,'١٣٦ - إذا باعه صبرة يجهلان كيلها صح','225','228','0');
INSERT INTO [t6534] VALUES (233,'١٣٧ - إذا باعه صبرة يجهلان كيلها، واستثنى منها جزءا مشاعا، كالثلث ونحوه صح','226','228','0');
INSERT INTO [t6534] VALUES (234,'١٣٨ - إذا باعه الصبرة واستثنى منها قفيزا معلوما، وهما يجهلان كيلها لم يصح، ولو علماه صح','226','228','0');
INSERT INTO [t6534] VALUES (235,'١٣٩ - إذا قال: بعتك هذه الصبرة كل قفيز بدرهم، صح','227','228','0');
INSERT INTO [t6534] VALUES (236,'١٤٠ - إذا قال: بعتك جاريتي فلانة، ولم يرها ولم توصف له، لم يصح','227','228','0');
INSERT INTO [t6534] VALUES (237,'١٤١ - إذا قال: بعتك عبدي بكذا، ولم يعرفه المشتري برؤية، ولا صفة، لم يصح','228','228','0');
INSERT INTO [t6534] VALUES (238,'١٤٢ - إذا باعه قفيزا من صبرة يجهلان قفزانها صح','228','228','0');
INSERT INTO [t6534] VALUES (239,'١٤٣ - إذا باعه جريبا من ضيعة يعلمان مبلغ جربانها صح','229','228','0');
INSERT INTO [t6534] VALUES (240,'١٤٤ - إذا قال: بعتك نصف داري مما يلي دارك لم يصح','229','228','0');
INSERT INTO [t6534] VALUES (241,'١٤٥ - قال القاضي في المجرد: وكذا إذا باعه من الدار عشرة من هنا إلى حيث ينتهي الذرع، فإنه لا يصح','230','228','0');
INSERT INTO [t6534] VALUES (242,'١٤٦ - إذا باعه مائة شاة بمائة شاة إلا شاة لم يصح','230','228','0');
INSERT INTO [t6534] VALUES (243,'١٤٧ - إذا باعه ثمرة بأربعة آلاف إلا بقدر ألف صح','230','228','0');
INSERT INTO [t6534] VALUES (244,'١٤٨ - إذا باعه الأصل دون الثمرة، فسقيها على مالكها','231','228','0');
INSERT INTO [t6534] VALUES (245,'١٤٩ - اذا باعه رطبة بشرط القطع، فأجرته على المشتري','231','228','0');
INSERT INTO [t6534] VALUES (246,'١٥٠ - إذا باعه جارية واستثنى حملها، بطل البيع والاستثناء','232','228','0');
INSERT INTO [t6534] VALUES (247,'١٥١ - إذا باع حيوانا مأكولا، واستثنى رأسه وأطرافه، جاز','232','228','0');
INSERT INTO [t6534] VALUES (248,'١٥٢ - إذا باع شاة على أنها حامل لم يصح','233','228','0');
INSERT INTO [t6534] VALUES (249,'١٥٣ - يجوز بيع المدبر','234','228','0');
INSERT INTO [t6534] VALUES (250,'١٥٤ - يجوز بيع الجاني مطلقا','234','228','0');
INSERT INTO [t6534] VALUES (251,'١٥٥ - إذا تبايعا عينا غائبة بالصفة، فوجدها المشتري على غير الصفة فله الخيار، لا المطالبة بعين بدلها على تلك الصفة','236','228','0');
INSERT INTO [t6534] VALUES (252,'١٥٦ - إذا اشترى جارية لم يجز له وطؤها، حتى يستبرئها','237','228','0');
INSERT INTO [t6534] VALUES (253,'١٥٧ - إذا اشترى من إنسان دارا، وهي في يد ثالث، فادعاها لنفسه، ولم يقم البائع بينة بها، ففسخ القاضي العقد، ورد الثمن على المشتري، ثم ملك المشتري الدار بهبة أو ميراث، أو صدقة أو غيره، لم يلزمه تسليمها إلى بائعها','238','228','0');
INSERT INTO [t6534] VALUES (254,'١٥٨ - اذا اشترى دارا فاستحقت، رجع على البائع بالدرك','239','228','0');
INSERT INTO [t6534] VALUES (255,'١٥٩ - إذا اشترى ما لم يره وصححناه، ثبت له خيار الرؤية، فلو أجازه قبلها، لم يسقط خياره','239','228','0');
INSERT INTO [t6534] VALUES (256,'١٦٠ - إذا قال: بعتكه بمائة دينار إلا درهما، أو بمائة درهم إلا دينارا لم يصح','240','228','0');
INSERT INTO [t6534] VALUES (257,'١٦١ - إذا تشاح المشتري والبائع في تسليم الثمن والمثمن، فإن كان الثمن عينا، نصب عدد يقبض منهم، ثم يقبضهما','241','228','0');
INSERT INTO [t6534] VALUES (258,'١٦٢ - إذا اشترى أمة شراء فاسدا، ثم وطئها لزمه مهر المثل لبائعها','241','228','0');
INSERT INTO [t6534] VALUES (259,'١٦٣ - إذا باعه صبرة لم يره باطنها صح','242','228','0');
INSERT INTO [t6534] VALUES (260,'١٦٤ - إذا دخل الماء أرض إنسان، وفيه سمك، فنصب الماء وبقي السمك، لم يملكه صاحب الأرض إلا بأخذه','243','228','0');
INSERT INTO [t6534] VALUES (261,'١٦٥ - إذا اشترى غلاما صانعا فنسي صنعته، ثم وجد به عيبا فله رده، ولا يلزمه أن يرد معه شيئا لأجل نسيان الصنعة','243','228','0');
INSERT INTO [t6534] VALUES (262,'١٦٦ - إذا باع شيئا واشترط نفعه، كسكنى الدار سنة ونحوه صح','244','228','0');
INSERT INTO [t6534] VALUES (263,'١٦٧ - إذا باع أمة، واستثنى خدمتها ونحوها من أعمالها مدة معلومة، جاز','246','228','0');
INSERT INTO [t6534] VALUES (264,'١٦٨ - إذا ملك عبده مالا ثم باعه، فالمال للبائع إلا أن يشترطه المبتاع رواية واحدة','246','228','0');
INSERT INTO [t6534] VALUES (265,'١٦٩ - إذا اشترى الواحد شيئين صفقة، فوجد بأحدهما عيبا فليس له إلا ردهما، أو إمساكهما وأرش العيب','247','228','0');
INSERT INTO [t6534] VALUES (266,'١٧٠ - إذا اشترى دابة بشرط الخيار، فركبها ليختبرها، فهو على خياره','248','228','0');
INSERT INTO [t6534] VALUES (267,'١٧١ - إذا اشترى أمة على أنها كتابية، فبانت مسلمة، فله الخيار','248','228','0');
INSERT INTO [t6534] VALUES (268,'١٧٢ - إذا اشترى شيئا فوجد به عيبا، فاختار إمساكه والمطالبة بأرش عيبه، قوم صحيحا ومعيبا، ونسب تفاوت القيمتين إلى الثمن الذي اشتري به، ورجع به','249','228','0');
INSERT INTO [t6534] VALUES (269,'١٧٣ - إذا تبايعا ذهبا بورق عينا بعين، فوجد أحدهما فيما اشتراه عيبا من جنسه، لم يكن له البدل','250','228','0');
INSERT INTO [t6534] VALUES (270,'١٧٤ - إذا كان لانسان على آخر دينار دينا، فأحال به على من له عليه دينار دينا، فصارف به المحال للمحال عليه جاز نص عليه في رواية بكر بن محمد عن أبيه','251','228','0');
INSERT INTO [t6534] VALUES (271,'١٧٥ - إذا أسلف الأعمى في كر حنطة صح','252','228','0');
INSERT INTO [t6534] VALUES (272,'١٧٦ - إذا أسلم في عبد وجارية، وذكر شروط العلم صح','252','228','0');
INSERT INTO [t6534] VALUES (273,'١٧٧ - إذا اشترط أجود الطعام لم يصح السلم','253','228','0');
INSERT INTO [t6534] VALUES (274,'١٧٨ - إذا أسلم إلى رجل في عبد، وقبض عند المحل عبدا، ثم أحضر عبدا','253','228','0');
INSERT INTO [t6534] VALUES (275,'١٧٩ - قد تقرر: أنه إذا قال البائع: ليس المبيع هذا المردود كان القول قوله، إذا كان رد المشتري للسلعة للعيب','254','228','0');
INSERT INTO [t6534] VALUES (276,'١٨٠ - إذا باع بدراهم معينة وقبضها، ثم أحضر دراهم وادعى: أنها التي باع بها وهي معيبة، فأنكر المشتري كونها التي اشترى بها, ولا بينة لواحد منهما، فالقول قول المشتري مع يمينه','255','228','0');
INSERT INTO [t6534] VALUES (277,'١٨١ - لا يجوز لرب السلم التصرف فيه، ولا أخذ البدل عنه قبل قبضه، فإن فعل لم يصح','256','228','0');
INSERT INTO [t6534] VALUES (278,'١٨٢ - إذا تقايلا السلم جاز تفرقهما قبل قبض رأس ماله','257','228','0');
INSERT INTO [t6534] VALUES (279,'١٨٣ - يجوز بيع خل العنب بخل التمر، متساويا ومتفاضلا','258','228','0');
INSERT INTO [t6534] VALUES (280,'١٨٤ - إذا أقرضه دراهم، وقال: إن مت فأنت في حل، لم يصح','259','228','0');
INSERT INTO [t6534] VALUES (281,'١٨٥ - إذا قال لرجل: اكفل عني، ولك ألف لم يصح','259','228','0');
INSERT INTO [t6534] VALUES (282,'١٨٦ - إذا اشترى إنسان نصف عبد بخمسين، وآخر النصف الآخر بمائة، ثم باعاه مساومة بثلاثمائة، فهي بينهما نصفين، رواية واحدة','260','228','0');
INSERT INTO [t6534] VALUES (283,'١٨٧ - إذا كان له في ذمة رجل دراهم أو دنانير، فاشترى بها عرضا من غيره لم يصح نص عليه','261','228','0');
INSERT INTO [t6534] VALUES (284,'١٨٨ - إذا كان له على رجل ألف، وقال له: أسلمت إليك ألفا في أكرار [حنطة]، وأراد جعل الألف مال السلم لم يصح','261','228','0');
INSERT INTO [t6534] VALUES (285,'١٨٩ - إذا استدان العبد غير المأذون له، ثم ورثه رب الدين فأعتقه، لم يسقط دينه','262','228','0');
INSERT INTO [t6534] VALUES (286,'١٩٠ - إذا استدان المأذون له تعلقت ديونه بذمة سيده','263','228','0');
INSERT INTO [t6534] VALUES (287,'كتاب الرهن','265','0','0');
INSERT INTO [t6534] VALUES (288,'١٩١ - إذا أعتق الراهن عبده المرهون نفذ، موسرا كان أو معسرا نص عليه','265','287','0');
INSERT INTO [t6534] VALUES (289,'١٩٢ - كلما جاز أخذ الرهن به، جاز أخذ الضمين إلا ثلاثة مواضع: ضمان عهدة المبيع، وضمان ما لم يجب، وضمان مال الكتابة،','266','287','0');
INSERT INTO [t6534] VALUES (290,'١٩٣ - إذا جنى العبد المرهون على أجنبي بما يوجب القصاص في النفس فاقتص، لم يلزم الراهن دفع قيمة رهن مكانه','267','287','0');
INSERT INTO [t6534] VALUES (291,'١٩٤ - لا يصح رهن العبد المرهون','268','287','0');
INSERT INTO [t6534] VALUES (292,'١٩٥ - لا يصح رهن العبد المرهون بحق آخر، لا من مرتهنه، ولا من غيره','268','287','0');
INSERT INTO [t6534] VALUES (293,'١٩٦ - لا يصح رهن المرهون عند مرتهنه بحق آخر','269','287','0');
INSERT INTO [t6534] VALUES (294,'١٩٧ - إذا أقر الراهن أنه باع المرهون قبل رهنه، أو وهبه، أو أنه كان ملك فلان وأنا غصبته، فصدقه المقر له، وأنكر المرتهن صح إقراره، وبطل الرهن، ويبقى الدين بغير رهن في وجه','269','287','0');
INSERT INTO [t6534] VALUES (295,'١٩٨ - يجوز رهن (الأمة دون ولدها)، ورهنه دونها','270','287','0');
INSERT INTO [t6534] VALUES (296,'١٩٩ - إذا حدث للمرهونة ولد تعلق به الرهن','270','287','0');
INSERT INTO [t6534] VALUES (297,'٢٠٠ - إذا باعه شيئا بشرط رهن أو كفيل معين، فجاءه بغيرهما لم يلزمه القبول','271','287','0');
INSERT INTO [t6534] VALUES (298,'٢٠١ - إذا امتنع الراهن من علف الرهن وسقيه، أجبر عليه','272','287','0');
INSERT INTO [t6534] VALUES (299,'٢٠٢ - إذا قال الراهن للمرتهن: رهنت عندك بحقك هذه العين، فإن جئتك بحقك، وإلا فالرهن لك بحقك، لم يصح الشرط، وفي صحة الرهن روايتان، فإن قلنا: لا يصح، كان المرهون أمانة في يد المرتهن إلى','272','287','0');
INSERT INTO [t6534] VALUES (300,'٢٠٣ - إذا كان عليه لرجل ألف، فقال له: أقرضني ألفا على أن أرهن عندك بالألفين فرسي هذه ففعل، ففي صحة الرهن روايتان','273','287','0');
INSERT INTO [t6534] VALUES (301,'٢٠٤ - إذا وطئ الراهن جاريته المرهونة بغير إذن المرتهن، فإن كانت ثيبا لم يلزمه بوطئها شيء','274','287','0');
INSERT INTO [t6534] VALUES (302,'٢٠٥ - إذا شرطا في الرهن أن يكون منافع المرهون للمرتهن، وكان الرهن بقرض بطل الشرط نص عليه','274','287','0');
INSERT INTO [t6534] VALUES (303,'كتاب الحجر','276','0','0');
INSERT INTO [t6534] VALUES (304,'٢٠٦ - يجوز للحاكم قسمة مال المفلس بين غرمائه، وإن لم يثبتوا أنه لا غريم له سواهم','276','303','0');
INSERT INTO [t6534] VALUES (305,'٢٠٧ - إذا وجد عين ماله عند المفلس أخذه بشرطه','277','303','0');
INSERT INTO [t6534] VALUES (306,'٢٠٨ - إذا اقترض المفلس المحجور عليه، لم يشارك مقرضه بقية الغرماء','278','303','0');
INSERT INTO [t6534] VALUES (307,'٢٠٩ - إذا تقرر هذا، فإنه لا يقدم حق المجني عليه، بل يشارك، كما تقدم','278','303','0');
INSERT INTO [t6534] VALUES (308,'٢١٠ - إذا اختلعت المحجور عليها لسفه على مال صح، ولم يلزمها حالا، ولا مآلا','279','303','0');
INSERT INTO [t6534] VALUES (309,'كتاب الصلح','280','0','0');
INSERT INTO [t6534] VALUES (310,'٢١١ - إذا صالحه على ألف حالة، بخمسمائة حالة، جاز الصلح','280','309','0');
INSERT INTO [t6534] VALUES (311,'٢١٢ - لا يصح الصلح عن الشفعة بمال، وتسقط الشفعة','281','309','0');
INSERT INTO [t6534] VALUES (312,'٢١٣ - يصح الصلح عن دم العمد بأكثر من الدية','281','309','0');
INSERT INTO [t6534] VALUES (313,'٢١٤ - إذا أتلف عليه متقوما، لم يجز أن يصالح عنه بأكثر من قيمته من جنسها','282','309','0');
INSERT INTO [t6534] VALUES (314,'٢١٥ - قد تقرر: أنه إذا صالح عن متلف متقوم بأكثر من قيمته من جنسها لم يجز','282','309','0');
INSERT INTO [t6534] VALUES (315,'كتاب الكفالة والضمان والحوالة','284','0','0');
INSERT INTO [t6534] VALUES (316,'٢١٦ - لا تصح الكفالة ببدن من عليه حد، سواء كان لله أو لآدمي','284','315','0');
INSERT INTO [t6534] VALUES (317,'٢١٧ - إذا برئ المدين برئ ضامنه، ولا عكس','284','315','0');
INSERT INTO [t6534] VALUES (318,'٢١٨ - إذا ضمن رجل عن آخر ألفا، فدفع إلى رب المال بها عرضا يساوي خمسمائة، لم يرجع الضامن على المضمون عنه بأكثر من قيمة السلعة','285','315','0');
INSERT INTO [t6534] VALUES (319,'٢١٩ - إذا كفل اثنان ببدن إنسان، فرده أحدهما إلى المكفول له، لم يبرأ الآخر','286','315','0');
INSERT INTO [t6534] VALUES (320,'٢٢٠ - إذا كفل اثنان ببدن إنسان، على أن كل واحد منهما كفيل لصاحبه صح، وكان لصاحب الحق مطالبة أيهم شاء، وأي الكفيلين سلم المكفول به برئ هو والكفيل الآخر','287','315','0');
INSERT INTO [t6534] VALUES (321,'٢٢١ - إذا كان لانسان على اثنين ألف، على كل واحد منهما خمسمائة','288','315','0');
INSERT INTO [t6534] VALUES (322,'٢٢٢ - إذا كان له على شخص دين، ولآخر عليه مثله في الجنس والصفة، والحلول والتأجيل، فقال: أحلتك بدينك على فلان صح','288','315','0');
INSERT INTO [t6534] VALUES (323,'٢٢٣ - إذا أحال الضامن المضمون له بما ضمنه على من له عليه دين، فقبل صح','290','315','0');
INSERT INTO [t6534] VALUES (324,'٢٢٤ - يعتبر في الحوالة رضا المحيل','290','315','0');
INSERT INTO [t6534] VALUES (325,'٢٢٥ - إذا اشترى شيئا فأحال البائع عليه بالثمن، ثم وجد بالمبيع عيبا فرده، لم تبطل الحوالة، بل يطالب المحتال المشتري بالثمن، ويرجع به هو على البائع','291','315','0');
INSERT INTO [t6534] VALUES (326,'كتاب الوكالة','293','0','0');
INSERT INTO [t6534] VALUES (327,'٢٢٦ - إذا وكله في بيع ثوب أو دابة، وسلمه إليه، فتعدى فيه، بأن لبس الثوب، وركب الدابة، لم تبطل الوكالة','293','326','0');
INSERT INTO [t6534] VALUES (328,'٢٢٧ - إذا وكله في بيع عبد بمائة في سوق بعينه، فباعه بمائة في سوق آخر صح','294','326','0');
INSERT INTO [t6534] VALUES (329,'٢٢٨ - إذا سلم اليه ألف درهم، ووكله أن يشتري له بعينها عبدا، فاشتراه في الذمة، لم يصح البيع في حق الموكل في الصحيح من المذهب، ويلزم الوكيل','294','326','0');
INSERT INTO [t6534] VALUES (330,'٢٢٩ - إذا وكله في شراء جارية بعينها فاشتراها، ثم اختلفا، فقال الوكيل: اشتريتها بعشرين بأمرك، وقال الموكل: ما أذنت لك أن تشتريها بأكثر من عشرة، فالقول قول الموكل مع يمينه، ذكره القاضي في المجرد','295','326','0');
INSERT INTO [t6534] VALUES (331,'٢٣٠ - الوكيل في الخصومة غير وكيل في القبض','296','326','0');
INSERT INTO [t6534] VALUES (332,'٢٣١ - إذا وكله في شراء ثوب بعينه، فاشترى غيره في الذمة، لا بعين مال الموكل، ولم يجز الموكل لم يبطل، ولزم الشراء الوكيل','296','326','0');
INSERT INTO [t6534] VALUES (333,'٢٣٢ - إذا ادعى: أنه وكيل في استيفاء حق على زيد فصدقه، لم يلزمه دفع الحق إليه','297','326','0');
INSERT INTO [t6534] VALUES (334,'٢٣٣ - قد تقرر: أنه لا يلزم الغريم الدفع إلى مدعي الوكالة، وإن صدقه','298','326','0');
INSERT INTO [t6534] VALUES (335,'٢٣٤ - قد ثبت أنه لا يلزم مصدق مدعي الوكالة الدفع إليه، لكن لو دفع إليه جاز، فلو سلمه إليه، فحضر صاحب الحق، فأنكر الوكالة، وحلف، فإن كان المدفوع دينا فله الرجوع به على الدافع فقط','298','326','0');
INSERT INTO [t6534] VALUES (336,'٢٣٥ - إذا ادعى الوكيل دينا ثابتا لموكله، فادعى الغريم أن موكله عزله، وشهد بذلك ابنا الموكل، حكم بشهادتهما','299','326','0');
INSERT INTO [t6534] VALUES (337,'٢٣٦ - إذا وكله إنسان في شراء عبد بثمن بعينه سلمه إليه، ثم حجر على الموكل لفلس، بطلت الوكالة','300','326','0');
INSERT INTO [t6534] VALUES (338,'٢٣٧ - إذا وكل زوجته وطلقها، لم تبطل الوكالة','300','326','0');
INSERT INTO [t6534] VALUES (339,'كتاب الشركة','301','0','0');
INSERT INTO [t6534] VALUES (340,'٢٣٨ - إذا اشترى أحد شريكي العنان شيئا للشركة بثمن ليس لشريكه مال من جنسه بغير إذن شريكه، وقع الشراء له، دون الشريك','301','339','0');
INSERT INTO [t6534] VALUES (341,'٢٣٩ - قلت: وقد ذكرنا: أنه لا يجوز أن يشتري للشركة بثمن ليس معه من جنسه، ومثلنا ذلك','302','339','0');
INSERT INTO [t6534] VALUES (342,'٢٤٠ - إذا كان اثنان مشتركين في قفيز حنطة، فقال إنسان لأحدهما: أشركني فيه، ففعل، ولم يجز شريكه، صار القابل شريكا بنصف النصف','302','339','0');
INSERT INTO [t6534] VALUES (343,'٢٤١ - إذا كان عبد مشترك بين رجلين، فشركا فيه معا ثالثا، صار العبد بينهم أثلاثا','303','339','0');
INSERT INTO [t6534] VALUES (344,'٢٤٢ - إذا شرط رب المال للعامل الثلث، وسكت عن الباقي جاز، وكان الباقي لرب المال','303','339','0');
INSERT INTO [t6534] VALUES (345,'٢٤٣ - إذا شرط للعامل في المضاربة نفقة جاز','304','339','0');
INSERT INTO [t6534] VALUES (346,'٢٤٤ - يجوز للمضارب أن يبتاع المعيب والسليم','305','339','0');
INSERT INTO [t6534] VALUES (347,'٢٤٥ - إذا اشترى المضارب من يعتق على رب المال بغير إذنه صح، وعتق، ولزم المضارب قيمته لرب المال','306','339','0');
INSERT INTO [t6534] VALUES (348,'٢٤٦ - إذا شرط للعامل في المضاربة كثر من أجرة مثله في مرضه صح، وحسبت الزيادة من رأس المال','306','339','0');
INSERT INTO [t6534] VALUES (349,'٢٤٧ - إذا قال العامل: ربحت ألفا، ثم قال: غلطت، أو نسيت، لم يقبل','307','339','0');
INSERT INTO [t6534] VALUES (350,'٢٤٨ - إذا كان أربعة، لأحدهم دكان، ولآخر رحى، ولآخر بغل','307','339','0');
INSERT INTO [t6534] VALUES (351,'كتاب الإقرار','309','0','0');
INSERT INTO [t6534] VALUES (352,'٢٤٩ - إذا أقر في مرض موته لوارث بدين، فأجازه بقية الورثة قبل موته لم يصح، حتى يجيزوه بعده','309','351','0');
INSERT INTO [t6534] VALUES (353,'٢٥٠ - إذا قال: أعطيتني ألف درهم وديعة فهلكت، فقال المالك: بل غصبتنيها، فالقول قول المقر مع يمينه، ولا ضمان عليه','309','351','0');
INSERT INTO [t6534] VALUES (354,'٢٥١ - إذا قال: له علي شيء، صح وألزم تفسيره','310','351','0');
INSERT INTO [t6534] VALUES (355,'٢٥٢ - إذ قال: له علي ألف إذا جاء رأس الشهر صح','311','351','0');
INSERT INTO [t6534] VALUES (356,'٢٥٣ - إذا قال عارف العربية: له على درهم غير دانق، برفع غير، لزمه درهم تام','311','351','0');
INSERT INTO [t6534] VALUES (357,'٢٥٤ - إذا قال: له على كذا وكذا درهما، لزمه درهمان','312','351','0');
INSERT INTO [t6534] VALUES (358,'٢٥٥ - إذا قال: له على درهم، بل درهمان، لزمه درهمان فقط','314','351','0');
INSERT INTO [t6534] VALUES (359,'٢٥٦ - إذا أقر في مرض موته لبعض ورثته بمال، لم يصح إقراره','315','351','0');
INSERT INTO [t6534] VALUES (360,'٢٥٧ - إذا قال: هذه الدار لفلان، ولي منها هذا البيت، قبل منه','315','351','0');
INSERT INTO [t6534] VALUES (361,'٢٥٨ - إذا قال: على له مع كل درهم درهم، لزمه درهمان من غير زيادة','316','351','0');
INSERT INTO [t6534] VALUES (362,'٢٥٩ - إذا كانت في يده عين فادعاها زيد وعمرو، فأقر بها لزيد، ثم لعمرو، لزمه تسليمها إلى زيد، وقيمتها لعمرو','317','351','0');
INSERT INTO [t6534] VALUES (363,'٢٦٠ - إذا قال: له في ميراث أبي ألف، كان مقرا بالألف دينا على تركة أبيه، يستوفى منها','317','351','0');
INSERT INTO [t6534] VALUES (364,'٢٦١ - إذا كان في يد شخص عبد محكوم له بملكه فأقر السيد: أنه ملك لزيد، فصدقه زيد، فقال العبد: بل أنا ملك عمرو، حكم به لزيد، ولم يلتفت إلى قول العبد، فإن أنكر ذلك زيد بقي على ملك سيده','318','351','0');
INSERT INTO [t6534] VALUES (365,'٢٦٢ - إذا أقر بألف درهم وأطلق','318','351','0');
INSERT INTO [t6534] VALUES (366,'٢٦٣ - إذا كان لرجل أمة محكوم له بملكها، فانتقلت إلى غيره، واختلفا في سبب انتقالها، فقال الذي انتقلت إليه: زوجتنيها بألف، فقال: بل بعتكها بالألف، فكل منهما يدعي على الآخر عقدا ينكره، ولا بينة، فعلى منكر الابتياع اليمين، دون منكر التزويج، وهو المالك الأول','319','351','0');
INSERT INTO [t6534] VALUES (367,'٢٦٤ - إذا ثبت هذا، فإن الأمة إذا حلف منكر شرائها حكمنا ببطلان البيع والنكاح، وترد إلى مالكها، لكن لا يجوز له وطؤها','320','351','0');
INSERT INTO [t6534] VALUES (368,'٢٦٥ - قلت: وهذا الذي ذكرناه من رد الأمة إلى مالكها الأول إنما يكون بتقدير أن مدعي الزوجية لم يستولدها','321','351','0');
INSERT INTO [t6534] VALUES (369,'٢٦٦ - فإن كان اختلافهما في المسألة المتقدمة بعد أن استولدها مدعي الزوجية، وحلف على عدم الشراء فقد قررنا: أن الجارية لا ترد إلى مالكها الأول، فإذا ثبت ذلك، فإنها لا تقر في يد مدعي الزوجية، ولا يجوز له وطؤها في الحكم','322','351','0');
INSERT INTO [t6534] VALUES (370,'٢٦٧ - فإن ماتت هذه الجارية، استوفى مدعي بيعها الثمن من تركتها إن كان مستولدها حيا','322','351','0');
INSERT INTO [t6534] VALUES (371,'٢٦٨ - إذا أقر بنسب صغير أو مجنون مجهول النسب، وكان مثله يولد لمثله، لحقه','323','351','0');
INSERT INTO [t6534] VALUES (372,'٢٦٩ - إذا كان له جاريتان، لكل منهما ولد، فقال سيدهما: أحد هذين الولدين ولدي، ولا زوج لواحدة منهما، ولم يكن السيد قد أقر بوطء واحدة منهما، ثم مات ولم يبين، ولم توجد قافة، أو وجدوا وأشكل عليهم وعلى ورثته، أقرع بينهم، فمن قرع ثبتت حريته، دون نسبه','324','351','0');
INSERT INTO [t6534] VALUES (373,'٢٧٠ - قد قررنا: أنه تثبت حرية أحدهما بالقرعة، دون نسبه، فعلى هذا لا يرث، ولا يوقف له سهم، ولا يقرع بينهما لتبيين الوارث','325','351','0');
INSERT INTO [t6534] VALUES (374,'٢٧١ - إذا أقر العبد الماذون له في التجارة بمال وأطلق، صح إقراره في قدر ما أذن له فيه، فإن لم يف ما بيده من المال بالدين لزم سيده ما فضل','325','351','0');
INSERT INTO [t6534] VALUES (375,'كتاب العارية','327','0','0');
INSERT INTO [t6534] VALUES (376,'٢٧٢ - إذا أعاره أرضا ولم يقدر مدتها جاز','327','375','0');
INSERT INTO [t6534] VALUES (377,'٢٧٣ - يلزم المستعير مؤنة رد العارية','327','375','0');
INSERT INTO [t6534] VALUES (378,'٢٧٤ - لا يجوز للمستعير أن يعير، ولا يؤجر','328','375','0');
INSERT INTO [t6534] VALUES (379,'٢٧٥ - إذا أعاره شيئا، ثم وهبه منه، بطلت العارية','328','375','0');
INSERT INTO [t6534] VALUES (380,'٢٧٦ - إذا اختلف مالك الدابة وراكبها، فقال المالك: أجرتكها، وقال الراكب: بل أعرتنيها، ولا بينة، أخذ بقول القابض مع يمينه، ولا أجرة، ذكره ابن عقيل','329','375','0');
INSERT INTO [t6534] VALUES (381,'٢٧٧ - إذا أعاره أرضا لغرس أو بناء مدة معلومة، وشرط عليه قلعه عند انقضائها، لم يغرم المعير نقص البناء والغرس بالقلع','330','375','0');
INSERT INTO [t6534] VALUES (382,'٢٧٨ - يضمن الغاصب المغصوب بكثر القيمتين: من قيمته يوم غصبه، ويوم تلفه','331','375','0');
INSERT INTO [t6534] VALUES (383,'٢٧٩ - إذا استعار دابة مدة فانقضت، ولم ينتفع بها بعد المدة، فعليه ضمانها','332','375','0');
INSERT INTO [t6534] VALUES (384,'٢٨٠ - إذا غصب ثوبا مصبوغا فبله، فنقصت قيمته، وجب رده وأرش نقصه','333','375','0');
INSERT INTO [t6534] VALUES (385,'٢٨١ - إذا غصب جارية ضمن منفعتها بالغصب','333','375','0');
INSERT INTO [t6534] VALUES (386,'٢٨٢ - إذا اشترى أمتين، فتسرى بإحداهما، وزوج الأخرى','334','375','0');
INSERT INTO [t6534] VALUES (387,'٢٨٣ - إذا اشترى أمة فاستولدها، ثم استحقت، فلمالكها أخذها ومهرها وقيمة أولادها، ويرجع على الغاصب بقيمة الأولاد','335','375','0');
INSERT INTO [t6534] VALUES (388,'٢٨٤ - إذا غصب مثليا فاتلفه وأعوز مثله، لزمه قيمته يوم قبضها','336','375','0');
INSERT INTO [t6534] VALUES (389,'٢٨٥ - إذا غصب صاعين عصيرا قيمتهما دينار، فغلاهما حتى بقي منهما صاع قيمته دينار فرده، لم يلزمه معه شيء آخر','337','375','0');
INSERT INTO [t6534] VALUES (390,'٢٨٦ - إذا غصب خشبة فبنى عليها، نقض بناؤه، وردت إلى مالكها','338','375','0');
INSERT INTO [t6534] VALUES (391,'٢٨٧ - إذا تعدى بالبناء على جدار جاره، فأقره الجار على تعديه، أو عفى عن مطالبته بهدمه، ثم عاد وطالبه بذلك، فله المطالبة بنقض البناء','338','375','0');
INSERT INTO [t6534] VALUES (392,'٢٨٨ - إذا اشترى أرضا وزرعها وأخذ الزرع، ثم بانت مغصوبة، فلا شيء لمالكها في الزرع','339','375','0');
INSERT INTO [t6534] VALUES (393,'٢٨٩ - إذا قال الغاصب: كان المغصوب معيبا، فأنكر المالك ولا بينة، فالقول قول المالك','340','375','0');
INSERT INTO [t6534] VALUES (394,'٢٩٠ - إذا زرع الغاصب الأرض لم يكن لمالكها إجباره على قلعه، بل يخير: بين إبقائه بالأجرة، وبين أخذه بقيمته','340','375','0');
INSERT INTO [t6534] VALUES (395,'٢٩١ - إذا غصب عبدا صغيرا فقتل عنده قتيلا، فرده على مالكه، ففداه، أو دفعه في الدية، رجع السيد على الغاصب بالأقل','340','375','0');
INSERT INTO [t6534] VALUES (396,'٢٩٢ - إذا غصب عبدا فزادت قيمته لسمن أو تعلم صنعة، ثم هزل أو نسي الصنعة، فعاد إلى قيمته الأولى، فالزيادة مضمونة على الغاصب','341','375','0');
INSERT INTO [t6534] VALUES (397,'٢٩٣ - لا تضمن زيادة السعر مع بقاء العين','342','375','0');
INSERT INTO [t6534] VALUES (398,'٢٩٤ - إذا غصب عبدا قيمته مائة فخصاه، فصارت قيمته مائتين فرده، لزمه مع رده مائة، وهي قيمته وقت الغصب لأجل الخصاء','342','375','0');
INSERT INTO [t6534] VALUES (399,'٢٩٥ - إذا غصب ثوبا فباعه، فقطعه المشتري، وهو جاهل بالغصب، فضمان أرش القطع عليه، دون الغاصب، فإذا كرمه لم يرجع على الغاصب','343','375','0');
INSERT INTO [t6534] VALUES (400,'٢٩٦ - إذا حل زقا فيه سمن جامد، فسأل بالشمس ضمنه','344','375','0');
INSERT INTO [t6534] VALUES (401,'٢٩٧ - إذا أخرج جناحا إلى ملك غيره، أو إلى الطريق، فطالبه الجار أو بعض مستحقي الاستطراق بإزالته، وأشهد بذلك، فباع المتعدي ملكه، فوقع الجناح، فأتلف مالا، لزمه ضمانه','345','375','0');
INSERT INTO [t6534] VALUES (402,'٢٩٨ - إذا اشترى عبدا فأعتقه، فادعى إنسان: أنه عبده، فصدقه البائع والمشترى، لم يقبل قولهم على العبد، ولم يحكم برقه','346','375','0');
INSERT INTO [t6534] VALUES (403,'٢٩٩ - إذا باع المشتري الشقص المشفوع، نقض الشفيع، وأخذ بالشفعة','347','375','0');
INSERT INTO [t6534] VALUES (404,'٣٠٠ - إذا اشترى شقصا مشفوعا، فقبل أن يؤخذ بالشفعة تلف بعضه بفعل آدمي، أخذ الباقي بقسطه من الثمن','348','375','0');
INSERT INTO [t6534] VALUES (405,'٣٠١ - إذا باع شقصين من دارين، فللشفيع أن يأخذ أحدهما، ويترك الآخر','349','375','0');
INSERT INTO [t6534] VALUES (406,'٣٠٢ - إذا كانت دار لثلاثة: اشترى أحدهم نصيب آخر منهم، كان المبيع بين المشتري والشريك نصفين، فإن عفا الشريك للمشتري صح، وإن عفا المشتري لم يصح','349','375','0');
INSERT INTO [t6534] VALUES (407,'٣٠٣ - إذا أسقط الشفيع حقه من الشفعة قبل البيع لم يسقط','350','375','0');
INSERT INTO [t6534] VALUES (408,'٣٠٤ - إذا صالح المشتري الشفيع على ترك الشفعة بعوض لم يصح','350','375','0');
INSERT INTO [t6534] VALUES (409,'٣٠٥ - دار لثلاثة، باع اثنان منهم نصيبهما من اثنين في حالة واحدة، فللشفيع أخذ الجميع، وله العفو عن أحدهما، وأخذ الآخر','350','375','0');
INSERT INTO [t6534] VALUES (410,'٣٠٦ - إذا أقر مالك الشقص المشفوع: أنه باعه من فلان، فأنكر فلان، وجبت الشفعة','351','375','0');
INSERT INTO [t6534] VALUES (411,'كتاب المساقاة','353','0','0');
INSERT INTO [t6534] VALUES (412,'٣٠٧ - إذا شرط في عقد المساقاة أن تكون أجرة الأجراء الذين يستعين بهم العامل من الثمرة وسطا، لم يصح','353','411','0');
INSERT INTO [t6534] VALUES (413,'٣٠٨ - إذا قال: ساقيتك على هذا البستان بالنصف، على أن أساقيك الآخر بالثلث لم يصح فيهما','353','411','0');
INSERT INTO [t6534] VALUES (414,'٣٠٩ - بقر الدولاب على رب الأرض وبقر الحرث على العامل','354','411','0');
INSERT INTO [t6534] VALUES (415,'٣١٠ - إذا أخذ رب البذر مثل بذره، وقسما الباقي لم يصح','354','411','0');
INSERT INTO [t6534] VALUES (416,'كتاب الإجارة','357','0','0');
INSERT INTO [t6534] VALUES (417,'٣١١ - إذا استأجر أرضا للزرع فغرقت، وتلف زرعه، انفسخت الإجارة فيما بقي من المدة','357','416','0');
INSERT INTO [t6534] VALUES (418,'٣١٢ - لا يضمن الأجير الخاص، وهو: المقدر نفعه في [الزمن، ما تلف بفعله ويضمن الأجير المشترك، وهو: المقدر نفعه] بالعمل، ما تلف بفعله','357','416','0');
INSERT INTO [t6534] VALUES (419,'٣١٣ - إذا استأجر أجيرا مشتركا - وقد ذكرناه - فعمل في بيت المستأجر، فتلف العمل بعد فراغه، فله الأجرة','358','416','0');
INSERT INTO [t6534] VALUES (420,'٣١٤ - إذا استأجر عبدا فمات، انفسخت الإجارة فيما بقي','359','416','0');
INSERT INTO [t6534] VALUES (421,'٣١٥ - إذا استأجر دابة بعينها ليركبها مسافة معلومة، فله أن يركبها مثله','359','416','0');
INSERT INTO [t6534] VALUES (422,'٣١٦ - إذا دفع ثوبا إلى [خياط ليخيطه، وكان] يعرف بأخذ الأجرة على ذلك فله الأجرة، وإن لم يعقد معه عقدا، [ولا ذكر له أجرة','360','416','0');
INSERT INTO [t6534] VALUES (423,'٣١٧ - [العين المستأجرة أمانة في يد المستأجر] لا يضمنها بالقبض','361','416','0');
INSERT INTO [t6534] VALUES (424,'٣١٨ - إذا استأجر راعيا يرعى له غنما مدة معلومة، ولم يعين الغنم ولا عددها صح، وكان عليه أن يرعى ما جرت عادة الواحد برعيه، ولو تلف بعضها رعى بدلها، وإن بقيت منها شاة استحق جميع الأجرة برعيها، ويرعى سخالها على العرف والعادة','361','416','0');
INSERT INTO [t6534] VALUES (425,'٣١٩ - إذا استأجره لحمل الخمر للشرب لم يصح','362','416','0');
INSERT INTO [t6534] VALUES (426,'٣٢٠ - إذا أجر المسلم نفسه من الذمي للخدمة لم يصح','363','416','0');
INSERT INTO [t6534] VALUES (427,'٣٢١ - إذا أذن المؤجر للمستأجر أن يعمر في الدار المؤجرة (شيئا عينه)، ويحتسب له به من أجرة الدار ففعل جاز، وبرئ المستأجر مما أنفق على الدار','363','416','0');
INSERT INTO [t6534] VALUES (428,'٣٢٢ - [إذا] ظهر له في معدن عرق من أحد النقدين، فقال: [لرجل استأجرتك على أن تستخرجه] بدينار لم يصح','364','416','0');
INSERT INTO [t6534] VALUES (429,'٣٢٣ - [إذا أجر أمته ثم با] عها لآخر عقيب الإجارة، والمشتري عالم بالإجارة لم تنفسخ، ولو فسخاها كانت منفعة العين للبائع، دون المشتري','364','416','0');
INSERT INTO [t6534] VALUES (430,'٣٢٤ - إذا استأجر دارا، ثم اشتراها لم تنفسخ الإجارة، فيستوفي المنافع بحكم الإجارة إلى أن تنقضي مدتها، ثم بحكم الملك','365','416','0');
INSERT INTO [t6534] VALUES (431,'كتاب الوقف','367','0','0');
INSERT INTO [t6534] VALUES (432,'٣٢٥ - إذا وقف على غير معين كرجل لم يصح','367','431','0');
INSERT INTO [t6534] VALUES (433,'٣٢٦ - يجب التعديل في عطية الأقارب على حسب مواريثهم','367','431','0');
INSERT INTO [t6534] VALUES (434,'٣٢٧ - لا يصح الوقف على الحمل','368','431','0');
INSERT INTO [t6534] VALUES (435,'٣٢٨ - وهذه قاعدة كبيرة وهي: أنه يغتفر في الاستدامة ما لا يغتفر في الابتداء','368','431','0');
INSERT INTO [t6534] VALUES (436,'٣٢٩ - إذا قال: وقفت على أولادي، ثم على أولادهم، ثم على أولادهم وأولاد أولادهم، ترتبت البطون الثلاثة، واشترك الباقون','369','431','0');
INSERT INTO [t6534] VALUES (437,'٣٣٠ - إذا قال: وقفت على أولادي، ثم على ولد ولدي','369','431','0');
INSERT INTO [t6534] VALUES (438,'٣٣١ - الوقف على غير معين كالفقراء والمساجد لا يفتقر إلى قبول','370','431','0');
INSERT INTO [t6534] VALUES (439,'٣٣٢ - إذا وقف أمة فحبلت وولدت، فولدها وقف معها','370','431','0');
INSERT INTO [t6534] VALUES (440,'٣٣٣ - إذا أهدى الغائب شيئا مع رسول، فمات المهدي أو المهدى إليه قبل وصولها، بطل حكمها وعادت إلى المهدي، أو وارثه، وكذا الهبة','372','431','0');
INSERT INTO [t6534] VALUES (441,'٣٣٤ - يقبل الأب الهبة لولده الصغير','373','431','0');
INSERT INTO [t6534] VALUES (442,'٣٣٥ - إذا أعمره دارا فقال: هي لك عمرك ونحوه، كانت له ولورثته بعده','373','431','0');
INSERT INTO [t6534] VALUES (443,'٣٣٦ - إذا قال: جعلت هذه الدار لك عمرك، صارت له ولوارثه بعده','374','431','0');
INSERT INTO [t6534] VALUES (444,'٣٣٧ - يجوز للأب الرجوع في ما وهبه ولده','375','431','0');
INSERT INTO [t6534] VALUES (445,'٣٣٨ - يجوز للأب أن يأخذ من مال ولده ما شاء مع الحاجة وعدمها، ما لم يجحف به','375','431','0');
INSERT INTO [t6534] VALUES (446,'٣٣٩ - إذا وهب بشرط ثواب معلوم صح، وكان بيعا','376','431','0');
INSERT INTO [t6534] VALUES (447,'٣٤٠ - يصح قبول العبد المحجور عليه للهبة نص عليه، وهي لسيده','376','431','0');
INSERT INTO [t6534] VALUES (448,'كتاب اللقطة','378','0','0');
INSERT INTO [t6534] VALUES (449,'٣٤١ - إذا وجد غير الإمام ضالة ممتنعة عن صغار السباع، كالإبل والبقر والخيل، لم يجز له أخذها، فإن أخذها ضمنها','378','448','0');
INSERT INTO [t6534] VALUES (450,'٣٤٢ - إذا جعل رب اللقطة لرادها عليه جعلا، فردها الملتقط لأجل الجعل، لم يستحقه','379','448','0');
INSERT INTO [t6534] VALUES (451,'٣٤٣ - إذا أخذ اللقطة معتقدا كتمانها، فهي مضمونة عليه','379','448','0');
INSERT INTO [t6534] VALUES (452,'٣٤٤ - إذا رد الآبق من غير شرط الجعل، استحق عن رده دينارا، أو اثني عشر درهما','380','448','0');
INSERT INTO [t6534] VALUES (453,'٣٤٥ - إذا قال: من رد علي عبدي فله دينار، فرده ثلاثة لم يستحقوا أكثر من دينار، ويكون بينهم أثلاثا','381','448','0');
INSERT INTO [t6534] VALUES (454,'٣٤٦ - إذا قال: من رد عبدي فله دينار، فرده إنسان فهرب منه في بعض الطريق، لم يستحق الجعل','381','448','0');
INSERT INTO [t6534] VALUES (455,'٣٤٧ - إذا ترك دابة بمهلكة من الأرض، فأحياها إنسان بعلفها ملكها','382','448','0');
INSERT INTO [t6534] VALUES (456,'٣٤٨ - إذا اصطاد سمكة فوجد في بطنها لؤلؤة غير مثقوبة، أو غيرها مما يخرج من البحر، كالعنبر، فهو للصياد','383','448','0');
INSERT INTO [t6534] VALUES (457,'٣٤٩ - إذا اشترى سمكة فوجد في بطنها لؤلؤة غير مثقوبة، فهي للصياد','383','448','0');
INSERT INTO [t6534] VALUES (458,'٣٥٠ - إذا وجد مشتري السمكة في بطنها لؤلؤة غير مثقوبة فهي للصياد','384','448','0');
INSERT INTO [t6534] VALUES (459,'٣٥١ - إذا التقط العبد لقطة فأنفقها قبل تعريفها بحول، تعلقت برقبته','384','448','0');
INSERT INTO [t6534] VALUES (460,'٣٥٢ - إذا التقط الفاسق لقطة أقرت في يده','385','448','0');
INSERT INTO [t6534] VALUES (461,'٣٥٣ - يجوز للذمي إحياء موات في دار الإسلام، ويملكها بذلك نص عليه','386','448','0');
INSERT INTO [t6534] VALUES (462,'٣٥٤ - إذا أحيا حظيرة للماشية كفاه لتملكها حائط عليها','386','448','0');
INSERT INTO [t6534] VALUES (463,'٣٥٥ - إذا ملك بالاحياء أو بغيره أرضا، فظهر فيها معدن، فإنه يملك ذلك بملكها','387','448','0');
INSERT INTO [t6534] VALUES (464,'٣٥٦ - إذا أحيا المسلم مواتا من دار الحرب ملكه','388','448','0');
INSERT INTO [t6534] VALUES (465,'٣٥٧ - إذا فضل من ماء الإنسان شيء عن زرعه وحيوانه، لزمه بذله لبهائم غيره','389','448','0');
INSERT INTO [t6534] VALUES (466,'٣٥٨ - إذا طلب المودع الوديعة في حالة لا يمكن دفعها إليه، كحالة الصلاة، أو في الحمام، وما أشبه ذلك، فتلفت قبل زوال العذر أو بعده، وقبل إمكان الرد، فلا ضمان عليه','390','448','0');
INSERT INTO [t6534] VALUES (467,'٣٥٩ - إذا اختلف المودع والمودع في الرد، فالقول قول المودع','390','448','0');
INSERT INTO [t6534] VALUES (468,'٣٦٠ - إذا أودعه خاتما وقال: البسه في الخنصر، فلبسه في البنصر، فلا ضمان عليه','391','448','0');
INSERT INTO [t6534] VALUES (469,'٣٦١ - إذا طولب بوديعة فقال: مالك عندي وديعة، أو ما تستحق علي وديعة، ثم أقر بوديعة، وادعى تلفها من حرز فلا ضمان','392','448','0');
INSERT INTO [t6534] VALUES (470,'٣٦٢ - إذا قال له: احفظ وديعتي في هذا البيت، ولا تدخله أحدا، فأدخل إليه أناسا، فسرقها بعضهم ضمنها المودع','392','448','0');
INSERT INTO [t6534] VALUES (471,'كتاب الوصايا','394','0','0');
INSERT INTO [t6534] VALUES (472,'٣٦٣ - إذا [قال]: وصيت لفلان بشاة من غنمي، ولا غنم له، لم يصح في أصح الوجهين','394','471','0');
INSERT INTO [t6534] VALUES (473,'٣٦٤ - إذا أوصى لزيد بشيء، ثم لعمرو به، فليس رجوعا، وهو بينهما','394','471','0');
INSERT INTO [t6534] VALUES (474,'٣٦٥ - اذا أوصى لرجل برقبة عبد، ولآخر بمنفعته، فقتل العبد، اشتري بقيمته رقبة تقوم مقامه','395','471','0');
INSERT INTO [t6534] VALUES (475,'٣٦٦ - يصح قبول الوصي الوصية قبل موت الموصي وبعده','396','471','0');
INSERT INTO [t6534] VALUES (476,'٣٦٧ - تصح الوصية مع تأخير قبولها إلى ما بعد الموت','396','471','0');
INSERT INTO [t6534] VALUES (477,'٣٦٨ - إذا رد الموصى له الوصية قبل موت الموصي، ثم قبلها بعد موته صح','397','471','0');
INSERT INTO [t6534] VALUES (478,'٣٦٩ - إذا مات الموصى له قبل موت الموصي، بطلت','397','471','0');
INSERT INTO [t6534] VALUES (479,'٣٧٠ - قد تقرر: أنه إذا مات الموصى له بعد الموصي وقبل القبول قام وارثه مقامه','398','471','0');
INSERT INTO [t6534] VALUES (480,'٣٧١ - إذا أوصى بعتق عبده سالم، وقيمته ثلث ماله، ثم أوصى بعتق عبده','398','471','0');
INSERT INTO [t6534] VALUES (481,'٣٧٢ - إذا كان جميع ماله مائتي درهم وعبدا قيمته مائة، فأوصى لرجل بالعبد، ولآخر بمائة، فمع الإجازة يأخذ كل واحد ما وصي له به، ومع الرد لكل منهما نصف ما وصي له به','399','471','0');
INSERT INTO [t6534] VALUES (482,'٣٧٣ - إذا قال: وصيت لما تحمل هذه الجارية بكذا، لم تصح الوصية','400','471','0');
INSERT INTO [t6534] VALUES (483,'٣٧٤ - إذا أوصى بثمرة نخلته فاحتاجت إلى سقي، لم يجبر الموصى له على سقيها؛ لأنه غير مالك للنخلة، ولا الوارث؛ لأنه غير مالك للثمرة','401','471','0');
INSERT INTO [t6534] VALUES (484,'٣٧٥ - إذا أوصى لإنسان بعبد من عبيده مبهم، فقتل العبيد كلهم بعد موت الموصي، فللموصى له قيمة أحدهم بالقرعة','401','471','0');
INSERT INTO [t6534] VALUES (485,'٣٧٦ - إذا قال: وصيت لفلان بفهد من مالي، ولا فهد في ملكه، صحت','402','471','0');
INSERT INTO [t6534] VALUES (486,'٣٧٧ - إذا أوصى له بقوس استحق وترها','402','471','0');
INSERT INTO [t6534] VALUES (487,'٣٧٨ - إذا أوصى لزيد بمائة، ولخالد بتمام الثلث على المائة، ولبكر بثلث ماله، وكل ماله أربعمائة وخمسون','403','471','0');
INSERT INTO [t6534] VALUES (488,'٣٧٩ - إذا أوصى لإنسان بثلث ماله فتلف ماله أو لم يكن له مال، ثم اكتسب، استحق الموصى له ثلثه','404','471','0');
INSERT INTO [t6534] VALUES (489,'٣٨٠ - أذا أوصى له بعبد من عبيده مبهم، فله أحدهم بتعيين الورثة','405','471','0');
INSERT INTO [t6534] VALUES (490,'٣٨١ - إذا أوصى بمعين من ماله قيمته قدر الثلث صحت الوصية، سواء رضي الورثة أو سخطوا','405','471','0');
INSERT INTO [t6534] VALUES (491,'٣٨٢ - إذا أوصى لرجل بثلث ماله، ولآخر بثلثه أيضا، ولم يجز الورثة، فالثلث بينهما نصفين، ولو رد أحدهما الوصية وقبل الآخر، استحق جميع الثلث','406','471','0');
INSERT INTO [t6534] VALUES (492,'٣٨٣ - إذا تبرع في مرض موته بأكثر من الثلث لجماعة دفعة، ولا عتق فيه، ولم يجز الورثة، تحاص الجميع في الثلث، وأدخل النقص على كل بقدر حصته من التبرع','407','471','0');
INSERT INTO [t6534] VALUES (493,'٣٨٤ - إذا أعتق في مرضه عبدين بكلمة واحدة، قيمة كل منهما قدر الثلث، فلم يجز الورثة، أقرع بينهما، فمن قرع عتق','408','471','0');
INSERT INTO [t6534] VALUES (494,'٣٨٥ - إذا أعتق أمته حاملا تبعها حملها في العتق، فإن لم يخرجا من الثلث عتق من الأم بقدر الثلث، وعتق من الولد مثل ذلك، ولا يقرع بين الحمل وأمه إذا كانت قيمتهما متساوية','408','471','0');
INSERT INTO [t6534] VALUES (495,'٣٨٦ - إذا أعتق أم ولده فهي من رأس ماله','409','471','0');
INSERT INTO [t6534] VALUES (496,'٣٨٧ - إذا أوصى لانسان بمائة، ثم أوصى أن يتصدق على فلان بمائة، ثم أوصى أن يباع عبده من فلان ويحابا بمائة، ومقدار ثلث ماله مائة، تحاصوا في ثلث ماله، ولم يقدم أحدهم على صاحبه','410','471','0');
INSERT INTO [t6534] VALUES (497,'٣٨٨ - إذا أوصى لعبده بجزء مشاع كالثلث صح، وعتق إن خرج من الثلث، وإن كانت قيمته أقل من الثلث استحق فاضله، وإن لم يخرج منه عتق منه بمقدار الثلث','410','471','0');
INSERT INTO [t6534] VALUES (498,'٣٨٩ - إذا أوصى لعبده بدابة، أو ثوب لم يصح','411','471','0');
INSERT INTO [t6534] VALUES (499,'٣٩٠ - إذ أوصى بثلث ماله، اعتبر ماله عند الموت','411','471','0');
INSERT INTO [t6534] VALUES (500,'٣٩١ - إذا باع في مرضه كر حنطة لا مال له سواه، قيمته اثنا عشر دينارا، بكر شعير قيمته ستة دنانير، فقد حابا بنصف ماله، وليس له المحاباة بأكثر من الثلث، فللورثة الرجوع على المشتري بسدس الكر الحنطة','412','471','0');
INSERT INTO [t6534] VALUES (501,'٣٩٢ - إذا دبر عبدين لا يخرجان من الثلث، فلم يجز الورثة، فأقرع بينهما، فخرج من وقع عليه سهم الحرية مستحقا، بطل عتقه، وعتق الآخر كله إن خرج من الثلث','412','471','0');
INSERT INTO [t6534] VALUES (502,'٣٩٣ - إذا دبر عبدا قيمته تخرج من الثلث، فحكم بعتقه بموت سيده، ثم خرج نصفه مستحقا بطل العتق، ولم يقوم هذا النصف على الميت','413','471','0');
INSERT INTO [t6534] VALUES (503,'٣٩٤ - يجوز للوصي في مال اليتيم أن يأكل مع الفقر من ماله بمقدار عمله إذا كان شغله به يمنعه من التكسب لكفايته','414','471','0');
INSERT INTO [t6534] VALUES (504,'كتاب النكاح','415','0','0');
INSERT INTO [t6534] VALUES (505,'٣٩٥ - إذا تزوج العبد بغير إذن سيده، وقلنا: يقف على إجازته، فأعتقه، جاز النكاح','415','504','0');
INSERT INTO [t6534] VALUES (506,'٣٩٦ - قد ذكرنا: أنه إذا أعتقه جاز النكاح','415','504','0');
INSERT INTO [t6534] VALUES (507,'٣٩٧ - إذا أذن لعبده أن يتزوج، ويكون الصداق رقبته، وكانت الزوجة أمة صح','416','504','0');
INSERT INTO [t6534] VALUES (508,'٣٩٨ - اذا زوج أمته، ثم أعتقها، ثبت لها الخيار في الفسخ عبدا كان زوجها أو حرا، على رواية','417','504','0');
INSERT INTO [t6534] VALUES (509,'٣٩٩ - إذا ترافع الزوجان إلى حاكم فأقرا بالزوجية، ثم تجاحداها واختلفا في','417','504','0');
INSERT INTO [t6534] VALUES (510,'٤٠٠ - للأب أن يزوج عبد ابنه الصغير من أمته، ويزوج أمته من غير عبده','418','504','0');
INSERT INTO [t6534] VALUES (511,'٤٠١ - إذا أعتقا أمة لم يجز لأحدهما تزويجها بغير إذن الآخر','419','504','0');
INSERT INTO [t6534] VALUES (512,'٤٠٢ - قد تقدم: أن أحد المعتقين لا يجوز له الانفراد بتزويج المعتقة','419','504','0');
INSERT INTO [t6534] VALUES (513,'٤٠٣ - يجوز للأب تزويج ابنه الصغير بحرة','420','504','0');
INSERT INTO [t6534] VALUES (514,'٤٠٤ - سكوت البكر رضى','420','504','0');
INSERT INTO [t6534] VALUES (515,'٤٠٥ - إذا زوجت المرأة أمتها لم يصح','421','504','0');
INSERT INTO [t6534] VALUES (516,'٤٠٦ - إذا قال السيد لأمته بحضرة شاهدين: أعتقتك وجعلت عتقك صداقك، انعقد النكاح','422','504','0');
INSERT INTO [t6534] VALUES (517,'٤٠٧ - إذا قال لأمته: أعتقتك على أن تتزوجي بي، وعتقك صداقك فأبت، لزمها قيمة نفسها لسيدها','423','504','0');
INSERT INTO [t6534] VALUES (518,'٤٠٨ - إذا ادعت امرأة أن فلانا زوجها فأنكر، فشهد شاهدان بذلك، فردت شهادتهما لفسق أو غيره، لم يجز لأحدهما أن يتزوجها','424','504','0');
INSERT INTO [t6534] VALUES (519,'٤٠٩ - يملك السيد إجبار أمته البالغة على النكاح','424','504','0');
INSERT INTO [t6534] VALUES (520,'٤١٠ - إذا زوج أمته أو بنته المجبرة بعبده الصغير، جاز له أن يتولى طرفي العقد','425','504','0');
INSERT INTO [t6534] VALUES (521,'٤١١ - يجوز للعبد تزوج جارية ابنه','425','504','0');
INSERT INTO [t6534] VALUES (522,'٤١٢ - يحرم على الأب الحر تزوج جارية ابنه','426','504','0');
INSERT INTO [t6534] VALUES (523,'٤١٣ - إذا تزوج رجل امرأة، وابنه بنتها، فزفت زوجة كل منهما إلى الآخر جهلا وخطأ، وعلم الوافى منهما أولا، انفسخ بوطء الأول نكاح كل منهما، وحرمت زوجته عليه على التأبيد، ولزمه للتي وطئها مهر مثلها، ويلزم الواطى أولا لزوجته أولا نصف ما سمى لها','426','504','0');
INSERT INTO [t6534] VALUES (524,'٤١٤ - إذا اشترى أمة، ثم اشترى أختها لأمها، ثم لأبيها لم يجز له الجمع بينها وبين إحدى أختيها','427','504','0');
INSERT INTO [t6534] VALUES (525,'٤١٥ - بنت الربيبة محرمة كأمها','428','504','0');
INSERT INTO [t6534] VALUES (526,'٤١٦ - إذا عقد نكاحا على امرأة، وعقد عقدا آخر على ثنتين، وعقدا آخر على ثلاث، وجهل الأول، صح نكاح المفردة دون غيرها','428','504','0');
INSERT INTO [t6534] VALUES (527,'٤١٧ - إذا كان له زوجتان: مسلمة وبهودية، فقال لليهودية: أسلمت، وللمسلمة: ارتددت، فكذبتاه، انفسخ نكاحهما إن كان قبل الدخول','429','504','0');
INSERT INTO [t6534] VALUES (528,'٤١٨ - إذا أسلم مجوسي له زوجة مجوسية دخل بها، لم يجز أن يتزوج أختها المسلمة حتى تنقضي عدتها','429','504','0');
INSERT INTO [t6534] VALUES (529,'٤١٩ - إذا أسلمت زوجة الكافر بعد الدخول، فلها نفقة العدة','430','504','0');
INSERT INTO [t6534] VALUES (530,'٤٢٠ - إذا أسلم الكافر وتحته إماء قد دخل بهن، وقلنا: الفرقة تقف على انقضاء العدة، فأسلمت واحدة منهن بعد إسلامه، ثم عتقت ثم أسلمن، كان له أن يختار منهن كلهن','430','504','0');
INSERT INTO [t6534] VALUES (531,'٤٢١ - إذا أسلم وتحته خمس نسوة، فطلق إحداهن، كان اختيارا لها','431','504','0');
INSERT INTO [t6534] VALUES (532,'٤٢٢ - إذا قال لموليته: أريد أن أزوجك فلانا، فقالت: غيره أولى، لم يكن إذنا','432','504','0');
INSERT INTO [t6534] VALUES (533,'٤٢٣ - إذا أرادت المرأة أن تتزوج بعنين، أو مجبوب، لم يكن لوليها منعها','432','504','0');
INSERT INTO [t6534] VALUES (534,'٤٢٤ - إذا اختارت المرأة فسخ نكاحها لأجل عيب زوجها قبل الدخول والخلوة، سقط مهرها','433','504','0');
INSERT INTO [t6534] VALUES (535,'٤٢٥ - إذا فسخ نكاح الزوجة بعد ما دخل بها، لزمه المهر','434','504','0');
INSERT INTO [t6534] VALUES (536,'٤٢٦ - إذا عتقت الأمة تحت عبد فوطئها، سقط خيارها إن كانت بالغة عاقلة','435','504','0');
INSERT INTO [t6534] VALUES (537,'٤٢٧ - إذا مكنته من وطئها مدعية للجهل بعتقها، ومثلها يجهله، لم يسقط خيارها','435','504','0');
INSERT INTO [t6534] VALUES (538,'٤٢٨ - إذا عتقت الصغيرة تحت عبد، لم يملك أبوها اختيار الفسخ، حرا كان أو عبدا','436','504','0');
INSERT INTO [t6534] VALUES (539,'٤٢٩ - إذا عتقت الأمة تحت عبد، فخيارها على الفور على ما اختاره في المجرد','436','504','0');
INSERT INTO [t6534] VALUES (540,'٤٣٠ - يجوز للمعتقة تحت عبد أن تفسخ بنفسها، ولا تحتاج إلى حاكم','437','504','0');
INSERT INTO [t6534] VALUES (541,'٤٣١ - إذا شرط الزوج في عقد نكاحها: أن يطأها ليلا صح','438','504','0');
INSERT INTO [t6534] VALUES (542,'٤٣٢ - إذا أصدقها عبدا فزاد زيادة متصلة، كسمن وتعلم صنعة، ثم طلق قبل الدخول لم يرجع بنصفه زائدا إلا برضاها، فإن أبت لزمها نصف قيمته وقت العقد رواية واحدة','439','504','0');
INSERT INTO [t6534] VALUES (543,'٤٣٣ - الزيادة في الصداق بعد تمام عقد النكاح تلحق','440','504','0');
INSERT INTO [t6534] VALUES (544,'٤٣٤ - إذا زوجها الأب بدون مهر المثل صح، رضيت أو سخطت','441','504','0');
INSERT INTO [t6534] VALUES (545,'٤٣٥ - إذا تزوج امرأة فارتدت قبل الدخول، سقط مهرها','441','504','0');
INSERT INTO [t6534] VALUES (546,'٤٣٦ - إذا أصدقها شاة حائلا فحملت، ثم طلقها قبل الدخول، فردت نصفها عليه، أجبر على قبوله','442','504','0');
INSERT INTO [t6534] VALUES (547,'٤٣٧ - إذا أصدقها أمة فحملت، وولدت في ملكها، ثم طلقها قبل الدخول والولد طفل، رجع بنصف قيمتها، دون نصفها','442','504','0');
INSERT INTO [t6534] VALUES (548,'٤٣٨ - إذا تزوج امرأة نكاحا فاسدا، وأصدقها جارية معينة فقبضتها، وأعتقتها قبل الدخول لم يصح، وبعده يصح','444','504','0');
INSERT INTO [t6534] VALUES (549,'٤٣٩ - إذا طلقت الصغيرة قبل الدخول، فلأبيها العفو عن نصف صداقها','444','504','0');
INSERT INTO [t6534] VALUES (550,'٤٤٠ - إذا وهبت زوجها مهرها، ثم وجد ما يسقطه أو ينصفه، رجع عليها بعوضه في أصح الروايتين','445','504','0');
INSERT INTO [t6534] VALUES (551,'٤٤١ - قد قدمنا أنها إذا وهبت زوجها المهر، ثم وجد ما يسقطه أو ينصفه، فإنه يرجع عليها بعوضه','446','504','0');
INSERT INTO [t6534] VALUES (552,'٤٤٢ - للمرأة منع نفسها حتى تقبض مهرها الحال','446','504','0');
INSERT INTO [t6534] VALUES (553,'٤٤٣ - إذا زوج أم ولده، ثم أوصى لها بمهرها الحال في ذمة الزوج صحت الوصية، فإذا عتقت بموت سيدها ملكته، ولم يكن لها منع نفسها حتى تقبضه','447','504','0');
INSERT INTO [t6534] VALUES (554,'٤٤٤ - إذا أصدقها خشبا فشقته أبوابا فزادت قيمته، ثم طلقها قبل الدخول، لم يكن له الرجوع بنصفه، ولو بذلت له النصف بزيادته، لم يلزمه قبوله','447','504','0');
INSERT INTO [t6534] VALUES (555,'٤٤٥ - إذا تزوج من لا يوطء مثلها، لزمه تسليم مهرها إن طلبت','448','504','0');
INSERT INTO [t6534] VALUES (556,'٤٤٦ - إذا تزوجها على دار لغيره يشتريها، ويسلمها إليها صح، ولزمه ذلك','448','504','0');
INSERT INTO [t6534] VALUES (557,'٤٤٧ - إذا تزوجها على عبد مطلق يشتريه لها، صحت التسمية اختاره القاضي، وقال: لها الوسط','450','504','0');
INSERT INTO [t6534] VALUES (558,'٤٤٨ - إذا قال: أصدقتك هذا الحر، وجب مهر المثل','450','504','0');
INSERT INTO [t6534] VALUES (559,'٤٤٩ - وكذا لو أصدقها خمرا، وجب مهر المثل','451','504','0');
INSERT INTO [t6534] VALUES (560,'٤٥٠ - إذا أصدقها أمها، وهي جائزة التصرف، صح، وعتقت أمها عقب العقد','451','504','0');
INSERT INTO [t6534] VALUES (561,'٤٥١ - إذا زوج ابنه الصغير وضمن المهر وقضاه، فبلغ الابن فطلق قبل الدخول، فالراجع للابن','451','504','0');
INSERT INTO [t6534] VALUES (562,'٤٥٢ - إذا تعيب المهر بيد الزوج بفعله، ثم طلق قبل الدخول خيرت: بين رده والمطالبة بنصف قيمته، وبين أخذ نصفه ناقصا، وتضمنه','452','504','0');
INSERT INTO [t6534] VALUES (563,'٤٥٣ - إذا تزوجها على خنزير صح، ووجب مهر المثل','453','504','0');
INSERT INTO [t6534] VALUES (564,'٤٥٤ - إذا تزوج امرأة نكاحا فاسدا ووطئها مرارا، أو اشترى جارية فوطئها مرارا واستحقت، ولم يكن عالما، [أو وطئ مكاتبته مرارا]، أو وطئ جارية ولده مرارا لم يلزمه إلا مهر واحد','455','504','0');
INSERT INTO [t6534] VALUES (565,'٤٥٥ - إذا وطئ ذات محرمه بعقد فاسد، أو شبهة عقد غير عالم بذلك، فلها المهر، سواء كان تحريمها لنسب أو سبب؛ لأنه أتلف عليها منفعة بضعها فلزمه قيمته، كالأجنبي','456','504','0');
INSERT INTO [t6534] VALUES (566,'كتاب الخلع','458','0','0');
INSERT INTO [t6534] VALUES (567,'٤٥٦ - إذا قال لزوجته: خالعتك بألف، فأنكرت، بانت منه، والقول قولها مع يمينها في العوض','458','566','0');
INSERT INTO [t6534] VALUES (568,'٤٥٧ - إذا قال: خالعتك على هذا الثوب المروي، فبان هرويا صح، وله الخيار: بين إمساكه، ورده وأخذ قيمة مروي','458','566','0');
INSERT INTO [t6534] VALUES (569,'٤٥٨ - إذا خالعها على مروي بعينه فأعطته، فبان هرويا فله رده، والمطالبة بقيمة المروي','459','566','0');
INSERT INTO [t6534] VALUES (570,'٤٥٩ - إذا خالعها على مروي معين، فبان هرويا صح، وللزوج إمساكه إن شاء','460','566','0');
INSERT INTO [t6534] VALUES (571,'كتاب الطلاق','461','0','0');
INSERT INTO [t6534] VALUES (572,'٤٦٠ - يجوز طلاق الحامل والآيسة والصغيرة','461','571','0');
INSERT INTO [t6534] VALUES (573,'٤٦١ - إذا قال لزوجته: يدك أو رجلك طالق طلقت','461','571','0');
INSERT INTO [t6534] VALUES (574,'٤٦٢ - إذا قال: أنا منك طالق لم تطلق','462','571','0');
INSERT INTO [t6534] VALUES (575,'٤٦٣ - إذا قال: أنت بائن، ونوى الطلاق طلقت','462','571','0');
INSERT INTO [t6534] VALUES (576,'٤٦٤ - إذا وهب زوجته لأهلها، ونوى الطلاق، طلقت','463','571','0');
INSERT INTO [t6534] VALUES (577,'٤٦٥ - إذا قال لزوجته: تجرعي، ونوى الطلاق، طلقت','463','571','0');
INSERT INTO [t6534] VALUES (578,'٤٦٦ - إذا قال لزوجته: أنت واحدة بالرفع، ونوى الثلاث، طلقت ثلاثا','464','571','0');
INSERT INTO [t6534] VALUES (579,'٤٦٧ - إذا استثنى في الطلاق ونوى الاستثناء من أول إيقاع الطلاق إلى آخره، ووصله بآخر يمينه صح استثناؤه','465','571','0');
INSERT INTO [t6534] VALUES (580,'٤٦٨ - إذا قال لمدخول بها: أنت طالق ثلاثا إلا طلقة، طلقت طلقتين','470','571','0');
INSERT INTO [t6534] VALUES (581,'٤٦٩ - إذا قال: أنت طالق ثلاثا، واستثنى بقلبه واحدة طلقت ثلاثا، ظاهرا وباطنا على ظاهر كلام الخرقي','470','571','0');
INSERT INTO [t6534] VALUES (582,'٤٧٠ - إذا قال: أنت طالق ثم قال: إن فعلت كذا، دين، ولم يقبل منه في الحكم نص عليه','471','571','0');
INSERT INTO [t6534] VALUES (583,'٤٧١ - إذا قال: أنت طالق طلقتين ونصفا إلا طلقة، طلقت ثلاثا في أحد الوجهين','472','571','0');
INSERT INTO [t6534] VALUES (584,'٤٧٢ - إذا قال لزوجتيه: إحداكما طالق غدا، فجاء الغد وهما زوجتاه، طلقت إحداهما بالقرعة','472','571','0');
INSERT INTO [t6534] VALUES (585,'٤٧٣ - إذا قال لغير مدخول بها: إن دخلت الدار فأنت طالق، وكرره ثلاثا، فدخلت، طلقت ثلاثا','473','571','0');
INSERT INTO [t6534] VALUES (586,'٤٧٤ - إذا قال لغير مدخول بها: إن دخلت الدار فأنت طالق، ثم طالق، ثم طالق، لم تطلق حتى تدخل الدار، فتطلق واحدة','473','571','0');
INSERT INTO [t6534] VALUES (587,'٤٧٥ - إذا قال لغير مدخول بها: إن دخلت الدار فأنت طالق، ثم طالق، ثم طالق، فدخلت، لم تطلق إلا واحدة','474','571','0');
INSERT INTO [t6534] VALUES (588,'٤٧٦ - إذا قال لزوجته وهي طاهر طهرا لم يجامعها فيه: إن حضت فأنت طالق، فهذا طلاق محرم','475','571','0');
INSERT INTO [t6534] VALUES (589,'٤٧٧ - إذا قال لها: إذا طلقتك فأنت طالق، ثم قال: إن دخلت الدار فأنت طالق فدخلت، طلقت طلقتين','475','571','0');
INSERT INTO [t6534] VALUES (590,'٤٧٨ - إذا قال لعبده: إذا طلقت زوجتي فأنت حر، ثم قال لها: إن دخلت فأنت طالق، لم يعتق حتى تدخل الدار، فإذا دخلت طلقت وعتق','476','571','0');
INSERT INTO [t6534] VALUES (591,'٤٧٩ - إذا قال لعبده: إن دخلت الدار فأنت حر، ثم قال: إن أعتقت عبدي فأنت طالق، فدخل الدار، عتق ولم تطلق','476','571','0');
INSERT INTO [t6534] VALUES (592,'٤٨٠ - إذا قال لها: إن دخلت الدار فأنت طالق، ثم قال: إن طلقتك فأنت طالق، فدخلت الدار، لم تطلق إلا واحدة','477','571','0');
INSERT INTO [t6534] VALUES (593,'٤٨١ - إذا قال لمدخول بها: كلما وقع عليك طلاقي فأنت طالق، ثم قال لها: أنت طالق، أو علقه بصفة غير المعاوضة فوجدت، طلقت ثلاثا','477','571','0');
INSERT INTO [t6534] VALUES (594,'٤٨٢ - إذا قال لمدخول بها: كلما حلفت بطلاقك فأنت طالق، ثم قال: إن دخلت الدار فأنت طالق، إن كلمت أمك فأنت طالق، إن لبست ثوبك فأنت طالق، طلقت ثلاثا','478','571','0');
INSERT INTO [t6534] VALUES (595,'٤٨٣ - إذا قال لغير مدخول بها: إن حلفت بطلاقك فأنت طالق، ثم أعاد ثانيا وثالثا لم تطلق إلا واحدة، فإن عاد فتزوجها، ثم قال لها: إن حلفت بطلاقك فأنت طالق، أو: إن دخلت الدار فأنت طالق، طلقت ثانية في الحال من غير أن تدخل الدار','479','571','0');
INSERT INTO [t6534] VALUES (596,'٤٨٤ - إذا قال لزوجته: إن حلفت بطلاقك فأنت طالق، ثم قال: إذا طلعت الشمس، أو قدم الحاج فأنت طالق، لم تطلق','479','571','0');
INSERT INTO [t6534] VALUES (597,'٤٨٥ - إذا قال لها: إن كنت حاملا بذكر فأنت طالق، وإن ولدت أنثى فأنت طالق، فولدت ذكرا ثم أنثى، لم تطلق إلا واحدة','480','571','0');
INSERT INTO [t6534] VALUES (598,'٤٨٦ - إذا قال: إن ولدت ذكرا فأنت طالق، وإن ولدت أنثى فأنت طالق، فولدت ذكرا، ثم أنثى، ثم ذكرا، طلقت طلقتين','481','571','0');
INSERT INTO [t6534] VALUES (599,'٤٨٧ - إذا قال: إن ولدت ذكرا فأنت طالق، وإن ولدت أنثى فأنت طالق، فولدتهما معا، ثم ولدت ذكرا أو أنثى، طلقت طلقتين','481','571','0');
INSERT INTO [t6534] VALUES (600,'٤٨٨ - إذا قال: إن ولدت أنثى فأنت طالق طلقة، وإن ولدت ذكرين فأنت طالق طلقتين، فولدت ذكرين معا، أو واحدا بعد واحد، ثم ولدت أنثى، طلقت طلقتين','482','571','0');
INSERT INTO [t6534] VALUES (601,'٤٨٩ - إذا قال لها: أنت طالق مع موتي، لم تطلق','482','571','0');
INSERT INTO [t6534] VALUES (602,'٤٩٠ - إذا قال: أنت طالق قبل قدوم زيد بشهر، فقدم قبل كمال شهر، لم تطلق','483','571','0');
INSERT INTO [t6534] VALUES (603,'٤٩١ - إذا قال: أنت طاق أمس، يريد الإيقاع مسندا إلى ذلك الوقت، طلقت في الحال، لا في الزمان الماضي','484','571','0');
INSERT INTO [t6534] VALUES (604,'٤٩٢ - إذا قال: أنت طالق قبل أن أتزوج بك، طلقت في الحال','485','571','0');
INSERT INTO [t6534] VALUES (605,'٤٩٣ - إذا قلنا: يصح تعليق الطلاق قبل النكاح، فقال: كل امرأة أتزوجها فهي طالق إن كلمت فلانا، فتزوج امرأة، ثم كلم فلانا، ثم تزوج أخرى، طلقت التي تزوجها قبل كلامه لا بعده','485','571','0');
INSERT INTO [t6534] VALUES (606,'٤٩٤ - إذا قال: كل امرأة أتزوجها طالق، فتزوج امرأة، فطلقت، ثم عاد فتزوجها، لم تطلق','486','571','0');
INSERT INTO [t6534] VALUES (607,'٤٩٥ - إذا قال: المرأة التي أتزوجها طالق ثلاثا، ثم تزوج، طلقت ثلاثا','487','571','0');
INSERT INTO [t6534] VALUES (608,'٤٩٦ - إذا قال لزوجته: كلما ولدت ولدا فأنت طالق، فولدت ثلاثة معا، طلقت طلقتين، وتنقضي عدتها بوضع الثالث','487','571','0');
INSERT INTO [t6534] VALUES (609,'٤٩٧ - إذا قال: إن ولدت ذكرا فأنت طالق طلقة، وإن ولدت أنثى فأنت طالق طلقتين، فولدت ثلاثة في بطن: ذكرا ثم أنثى ثم ذكرا، أو أنثى ثم ذكرا ثم ذكرا، طلقت ثلاثا','488','571','0');
INSERT INTO [t6534] VALUES (610,'٤٩٨ - إذا قال لأربع: كلما ولدت واحدة منكن فصواحباتها طوالق، فولدت اثنتان معا، ثم اثنتان معا، طلقت الوالدتان أولا كل واحدة منهما ثلاثا','489','571','0');
INSERT INTO [t6534] VALUES (611,'٤٩٩ - إذا قال: إن كنت حاملا بذكر فأنت طالق، فوضعت أنثى وذكرا، طلقت','489','571','0');
INSERT INTO [t6534] VALUES (612,'٥٠٠ - إذا قال: إن كنت حاملا بذكر فأنت طالق طلقة، وإن كنت حاملا بأنثى فأنت طالق طلقتين، فولدتهما معا طلقت ثلاثا','489','571','0');
INSERT INTO [t6534] VALUES (613,'٥٠١ - إذا قال لها، ولها ضرة: كلما طلقت ضرتك فأنت طالق، ثم قال مثله لضرتها، ثم قال: أنت طالق للمخاطبة أولا، طلقت طلقتين، وضرتها طلقة','490','571','0');
INSERT INTO [t6534] VALUES (614,'٥٠٢ - إذا قال: إن بدأتك بالكلام فأنت طالق، فقالت: إن بدأتك بالكلام فعبدي حر، ثم كلمها وكلمته، لم يقع طلاق ولا عتق','490','571','0');
INSERT INTO [t6534] VALUES (615,'٥٠٣ - إذا قال: كلما أكلت رمانة فأنت طالق، وكلما أكلت نصف رمانة فأنت طالق، فأكلت رمانة، طلقت ثلاثا','491','571','0');
INSERT INTO [t6534] VALUES (616,'٥٠٤ - إذا حلف لا يلبس ثوبا هو لابسه، أو لا يركب دابة هو راكبها، فاستدام ذلك، حنث','491','571','0');
INSERT INTO [t6534] VALUES (617,'٥٠٥ - إذا قال: إذا قعدت فأنت طالق، وهي قاعدة، فاستدامته، طلقت','492','571','0');
INSERT INTO [t6534] VALUES (618,'٥٠٦ - إذا حلف لا يشرب له الماء من العطش، ناويا قطع المنة، حنث بكل ما له فيه منة','492','571','0');
INSERT INTO [t6534] VALUES (619,'٥٠٧ - إذا شهد اثنان بالطلاق، واختلفا في وقته، ثبت','493','571','0');
INSERT INTO [t6534] VALUES (620,'٥٠٨ - إذا قال لمدخول بها: متى أو أي وقت لم تدخلي الدار فأنت طالق، فمضى زمن يمكن الدخول فيه ولم تدخل، طلقت واحدة، ولا يقع عليها غيرها','493','571','0');
INSERT INTO [t6534] VALUES (621,'٥٠٩ - إذا قال: أنت طالق إن دخلت الدار - بكسر الهمزة - فدخلت بعد اليمين، طلقت','494','571','0');
INSERT INTO [t6534] VALUES (622,'٥١٠ - إذا قال: إن كلمتك ودخلت دارك فأنت طالق، ففعل أحدهما لم تطلق، وإن فعلهما طلقت واحدة','495','571','0');
INSERT INTO [t6534] VALUES (623,'٥١١ - إذا قال: أنت طالق إلى شهر، ونوى من الآن إلى شهر، طلقت في الحال، ولم يرتفع بعد الشهر','495','571','0');
INSERT INTO [t6534] VALUES (624,'٥١٢ - إذا قال: أنت طالق في اليوم، وفي الغد، وفي بعد الغد، طلقت ثلاثا في ثلاثة أيام','496','571','0');
INSERT INTO [t6534] VALUES (625,'٥١٣ - إذا قال: أنت طالق في الغد طلقت في أوله، فإن قال: أردت آخره، قبل منه في الحكم على رواية، ودين','497','571','0');
INSERT INTO [t6534] VALUES (626,'٥١٤ - إذا قال: أنت طالق اليوم إذا جاء غد، لم تطلق','498','571','0');
INSERT INTO [t6534] VALUES (627,'٥١٥ - إذا تزوج امرأة واشترى عبدا في يوم واحد، ثم قال لها: أنت طالق أمس، وله: أنت حر أمس، عتق ولم تطلق إذا أراد بقوله: طلاق زوجها قبله','499','571','0');
INSERT INTO [t6534] VALUES (628,'٥١٦ - إذا قال: أنت طالق نصف طلقة، وثلث طلقة، وسدس طلقة، طلقت ثلاثا','499','571','0');
INSERT INTO [t6534] VALUES (629,'٥١٧ - إذا قال لغير مدخول بها: أنت طالق طلقة قبل طلقة، لم تطلق إلا واحدة','500','571','0');
INSERT INTO [t6534] VALUES (630,'٥١٨ - إذا قال لها: أنت طالق إن كلمت زيدا حتى يقدم عمرو، فكلمته قبل قدومه طلقت، ولو كلمته بعده، لم تطلق','502','571','0');
INSERT INTO [t6534] VALUES (631,'٥١٩ - إذا أتى بصريح العتق في الطلاق، فقال لها: أنت حرة، ونوى الطلاق وقع','502','571','0');
INSERT INTO [t6534] VALUES (632,'٥٢٠ - إذا حلف لا يفعل شيئا ففعله ناسيا، وكانت يمينه طلاقا أو عتاقا، حنث','503','571','0');
INSERT INTO [t6534] VALUES (633,'كتاب الرجعة','505','0','0');
INSERT INTO [t6534] VALUES (634,'٥٢١ - لا يجوز للعبد أن يتزوج بغير إذن سيده، فلو أذن له فتزوج، ثم طلق، فقياس المذهب: أنه يملك ارتجاعها بغير إذن سيده قاله السامري','505','633','0');
INSERT INTO [t6534] VALUES (635,'٥٢٢ - إذا قال للرجعية: قد راجعتك إن شئت، فقالت: قد شئت لم تحصل الرجعة','506','633','0');
INSERT INTO [t6534] VALUES (636,'٥٢٣ - إذا قال: راجعتك للمحبة أو للإهانة، ونوى: من أجل محبتي لك، أو لما لحقك من الإهانة بالطلاق، صحت الرجعة','506','633','0');
INSERT INTO [t6534] VALUES (637,'٥٢٤ - لا تصح الرجعة في الردة رواية واحدة','507','633','0');
INSERT INTO [t6534] VALUES (638,'٥٢٥ - إذا وطئ مطلقته الرجعية بشبهة، وقد بقي من عدتها قرؤ واحد، لزمها استئناف العدة ثلاثة قروء، وله ارتجاعها في القرء الأول، دون القرئين الآخرين','508','633','0');
INSERT INTO [t6534] VALUES (639,'كتاب الإيلاء','509','0','0');
INSERT INTO [t6534] VALUES (640,'٥٢٦ - الايلاء في النكاح الفاسد لا يتعلق به حكم الإيلاء في الصحيح','509','639','0');
INSERT INTO [t6534] VALUES (641,'٥٢٧ - إذا قال لأربع زوجات: والله لا وطئتكن، لم يكن موليا من الجميع','509','639','0');
INSERT INTO [t6534] VALUES (642,'٥٢٨ - إذا قال: والله لا جامعتك إلا جماع سوء، ونوى الدبر، أو دون الفرج، فهو مول','510','639','0');
INSERT INTO [t6534] VALUES (643,'٥٢٩ - إذا قال: إن وطئتك فلله علي صوم شهر، فهل يكون موليا؟ فيه روايتان','511','639','0');
INSERT INTO [t6534] VALUES (644,'٥٣٠ - إذا فاء المولي بعد المدة تخلص من','511','639','0');
INSERT INTO [t6534] VALUES (645,'٥٣١ - إذا امتنع المولي من الفيئة والطلاق، طلق الحاكم عليه','512','639','0');
INSERT INTO [t6534] VALUES (646,'كتاب الظهار','513','0','0');
INSERT INTO [t6534] VALUES (647,'٥٣٢ - إذا قال لها: أنت علي كظهر أمي، وقال: لم أرد به الظهار، لم يقبل، وكان ظهارا','513','646','0');
INSERT INTO [t6534] VALUES (648,'٥٣٣ - إذا قال لأجنبية: أنت علي كظهر أمي، صار مظاهرا، لا يحل له وطؤها إن تزوجها حتى يكفر','514','646','0');
INSERT INTO [t6534] VALUES (649,'٥٣٤ - إذا قالت لزوجها: أنت علي كظهر أبي، فليست مظاهرة في الصحيح عنه','514','646','0');
INSERT INTO [t6534] VALUES (650,'٥٣٥ - إذا قال لأجنبية: والله لا وطئتك سنة، ثم تزوجها، فإذا تزوجها قبل انقضاء السنة لزمته الكفارة إلا أنه لا يكون موليا بحيث يؤجل، كاليمين بعد العقد','516','646','0');
INSERT INTO [t6534] VALUES (651,'٥٣٦ - إذا قال لأجنبية: أنت علي كظهر أمي، ونوى في تلك الحال، صار مظاهرا','517','646','0');
INSERT INTO [t6534] VALUES (652,'٥٣٧ - إذا قال لزوجته: أنت علي حرام، صار مظاهرا','517','646','0');
INSERT INTO [t6534] VALUES (653,'٥٣٨ - إذا قال لزوجته: أنت علي كظهر أمي، ونوى به الطلاق، لم يكن طلاقا','518','646','0');
INSERT INTO [t6534] VALUES (654,'٥٣٩ - إذا وجبت على إنسان كفارة ظهار، فقال له آخر: أعتق عبدك عن كفارتك على أن علي عشرة دراهم ففعل، ثم قال: لا أريدها، لم يصح الرد، ولم يقع العتق عن الكفارة','519','646','0');
INSERT INTO [t6534] VALUES (655,'٥٤٠ - إذا قال لها: إن ظاهرت من فلانة الأجنبية فأنت علي كظهر أمي، ثم قال لفلانة ذلك، صار مظاهرا من زوجته','519','646','0');
INSERT INTO [t6534] VALUES (656,'٥٤١ - إذا أعتق في كفارة الظهار مقطوع الخنصر والبنصر من يد واحدة، لم يجزئه','520','646','0');
INSERT INTO [t6534] VALUES (657,'٥٤٢ - إذا أعتق مقطوع الابهام، لم يجزئه','520','646','0');
INSERT INTO [t6534] VALUES (658,'٥٤٣ - لا يجوز دفع الطعام في الكفارة إلى مكاتب','521','646','0');
INSERT INTO [t6534] VALUES (659,'٥٤٤ - يجزئ إطعام الخبز في الكفارة','521','646','0');
INSERT INTO [t6534] VALUES (660,'٥٤٥ - إذا تخلل صوم الكفارة وطء، لزمه استئنافه','522','646','0');
INSERT INTO [t6534] VALUES (661,'٥٤٦ - إذا وطئ المظاهر منها ليلا أو نهارا ناسيا قبل تمام الصوم، انقطع التتابع في إحدى الروايتين','523','646','0');
INSERT INTO [t6534] VALUES (662,'٥٤٧ - إذا كفر من جنسين فأعتق نصف رقبة، وصام شهرا، لم يجزئه','524','646','0');
INSERT INTO [t6534] VALUES (663,'٥٤٨ - إذا لزمته كفارة الظهار وهو معسز عن الرقبة، أجزأه الصوم، فلو تكلف العتق أجزأه','525','646','0');
INSERT INTO [t6534] VALUES (664,'كتاب القذف','526','0','0');
INSERT INTO [t6534] VALUES (665,'٥٤٩ - إذا قال لزوجته: يا زانية أنت طالق ثلاثا لزمه الحد، وله إسقاطه باللعان','526','664','0');
INSERT INTO [t6534] VALUES (666,'٥٥٠ - إذا قال لأجنبي: لست بولد فلانة، لم يكن قاذفا لأمه','526','664','0');
INSERT INTO [t6534] VALUES (667,'٥٥١ - إذا قال لزوجته: يا زانية، فقالت: بل أنت زان، لزم كلا منهما الحد للآخر','527','664','0');
INSERT INTO [t6534] VALUES (668,'٥٥٢ - إذا قال لها: يا زانية، فقالت: بك زنيت، لم يلزم واحدا منهما الحد','527','664','0');
INSERT INTO [t6534] VALUES (669,'٥٥٣ - إذا قذف زوجته، وأقام عليها البينة بالزنا فحدت، ثم قذفها قاذف، لم يلزمه حد القذف','528','664','0');
INSERT INTO [t6534] VALUES (670,'٥٥٤ - إذا قال لها: زنيت ببعير أو حمار، فلا حد','529','664','0');
INSERT INTO [t6534] VALUES (671,'٥٥٥ - إذا اعترف بالولد ثم نفاه لحق به، ولم ينتف عنه بلعان ولا غيره','530','664','0');
INSERT INTO [t6534] VALUES (672,'٥٥٦ - إذا قال لولده: لست بولدي، فليس صريحا في قذف أمه','530','664','0');
INSERT INTO [t6534] VALUES (673,'٥٥٧ - إذا قال لإنسان: لست بولد فلان، فعليه حد القذف نص عليه','531','664','0');
INSERT INTO [t6534] VALUES (674,'٥٥٨ - لا يصح اللعان بالعجمية لمن يحسن العربية','531','664','0');
INSERT INTO [t6534] VALUES (675,'كتاب العدد','533','0','0');
INSERT INTO [t6534] VALUES (676,'٥٥٩ - إذا طلقها وقد بقي من طهرها لحظة، احتسب بها قرءا على قولنا: الأقراء الأطهار','533','675','0');
INSERT INTO [t6534] VALUES (677,'٥٦٠ - إذا قلنا: الأقراء الأطهار، فطلقت معتادة، احتسب بما بقي من طهرها قرءا','533','675','0');
INSERT INTO [t6534] VALUES (678,'٥٦١ - إذا قلنا: الأقراء الحيض، فطلقها حائضا، لم يحتسب تلك الحيضة قرءا','534','675','0');
INSERT INTO [t6534] VALUES (679,'٥٦٢ - إذا اتفق الزوجان على أن وقت الطلاق يوم الجمعة، واختلفا في وقت وضعها للحمل، فقال: وضعت يوم الخميس، فعليك عدة الأقراء، ولي الرجعة، فقالت: بل يوم السبت فانقضت العدة، ولا رجعة لك، فالقول قولها','535','675','0');
INSERT INTO [t6534] VALUES (680,'٥٦٣ - إذا طلقها رجعيا، ثم أبانها، بنت على رواية واحدة','535','675','0');
INSERT INTO [t6534] VALUES (681,'٥٦٤ - إذا تزوجت زوجة المفقود بعد التربص، ثم قدم زوجها ومات، فعليها عدة الوفاة من حين فراق الثاني؛ لأنها فراش له','536','675','0');
INSERT INTO [t6534] VALUES (682,'٥٦٥ - يحرم وطء المستبرأة ودواعيه إلا المسبية، فإن دواعيه تباح فيها','536','675','0');
INSERT INTO [t6534] VALUES (683,'٥٦٦ - إذا حاضت الجارية في يد بائعها قبل قبض المشتري، لم يحصل الاستبراء بذلك في رواية','537','675','0');
INSERT INTO [t6534] VALUES (684,'٥٦٧ - إذا وطئ الرجل زوجته ثم طلقها، ثم أتت بولد لأكثر مدة الحمل، وقبل انقضاء عدتها، لحقه نسبه','538','675','0');
INSERT INTO [t6534] VALUES (685,'٥٦٨ - إذا اعتدت الصغيرة بالشهور، ثم حاضت بعدها وقبل التزوج، لم يلزمها الاعتداد به','538','675','0');
INSERT INTO [t6534] VALUES (686,'كتاب الرضاع','540','0','0');
INSERT INTO [t6534] VALUES (687,'٥٦٩ - إذا كان لرجل زوجة صغيرة، فأرضعتها زوجة أبيه - بلبن ثاب من حمل منه - الرضاع المحرم، انفسخ نكاحها','540','686','0');
INSERT INTO [t6534] VALUES (688,'٥٧٠ - إذا أرضعت حماته زوجة أخرى له صغيرة، فإن كانت بنتها باقية انفسخ نكاحهما','540','686','0');
INSERT INTO [t6534] VALUES (689,'٥٧١ - إذا ادعت امرأة أن زوجها أخوها من الرضاع، فأنكر، فشهدت به امرأة ثقة، فرق بينهما','541','686','0');
INSERT INTO [t6534] VALUES (690,'٥٧٢ - إذا استبرأ أم ولده وهي ترضع بلبن ولده، وزوجها بصبي حر، فأرضعته بذلك اللبن، لم تحرم على سيدها على التأبيد','541','686','0');
INSERT INTO [t6534] VALUES (691,'٥٧٣ - إذا أرضعت زوجة رجل زوجة له أخرى صغيرة، لزمها نصف مهرها المسمى','542','686','0');
INSERT INTO [t6534] VALUES (692,'٥٧٤ - إذا كان له خمس أمهات أولاد لهن لبن منه، فأرضعن به طفلا كل واحدة رضعة، لم يصر ولدا لواحدة منهن، وصار السيد أباه ذكره ابن حامد','542','686','0');
INSERT INTO [t6534] VALUES (693,'٥٧٥ - إذا كان له زوجتان صغيرتان، وزوجتان كبيرتان، ولم يدخل بواحدة منهما، فأرضعت كل واحدة من الكبيرتين واحدة من الصغيرتين الرضاع المحرم، انفسخ نكاح الجميع، وحرمت الكبيرتان عليه أبدا','543','686','0');
INSERT INTO [t6534] VALUES (694,'٥٧٦ - إذا ارتضع صبي وصبية من أجنبية رضاعا محرما، جاز له التزويج بأمها من النسب، دون أمها من الرضاع','544','686','0');
INSERT INTO [t6534] VALUES (695,'كتاب النفقات','545','0','0');
INSERT INTO [t6534] VALUES (696,'٥٧٧ - يلزم المكاتب نفقة ولده من أمته','545','695','0');
INSERT INTO [t6534] VALUES (697,'٥٧٨ - إذا دفع إلى زوجته نفقة سنة، ثم طلقها أو ماتت، فله الرجوع عليها بقسط ما بقي من المدة في إحدى الروايتين','545','695','0');
INSERT INTO [t6534] VALUES (698,'٥٧٩ - إذا اختلف الزوجان في قبض المهر والنفقة، وكانت الزوجة حرة فـ[ـلها] الخصومة فيهما','546','695','0');
INSERT INTO [t6534] VALUES (699,'٥٨٠ - تعود نفقة المرتدة بعودها إلى الإسلام في غيبة زوجها','547','695','0');
INSERT INTO [t6534] VALUES (700,'٥٨١ - إذا أعسر بنفقة زوجته الأمة، فلسيدها خيار الفسخ دونها','547','695','0');
INSERT INTO [t6534] VALUES (701,'٥٨٢ - إذا أعسر بنفقة زوجته، فلها خيار الفسخ','548','695','0');
INSERT INTO [t6534] VALUES (702,'٥٨٣ - إذا كان له على زوجته دين، فأراد محاسبتها به من نفقتها، جاز مع يسارها، لا مع إعسارها','549','695','0');
INSERT INTO [t6534] VALUES (703,'٥٨٤ - إذا سكنت المعتدة دارا بغير إذن الزوج، لم ترجع عليه بأجرتها','549','695','0');
INSERT INTO [t6534] VALUES (704,'٥٨٥ - لا تسقط نفقة الزوجة بمضي المدة','550','695','0');
INSERT INTO [t6534] VALUES (705,'٥٨٦ - إذا رضيت زوجة المعسر بالمقام معه بلا نفقة وهي حرة، فلها ذلك','550','695','0');
INSERT INTO [t6534] VALUES (706,'٥٨٧ - تجب نفقة الزوجة مع الإعسار','550','695','0');
INSERT INTO [t6534] VALUES (707,'٥٨٨ - إذا أخذت المرأة نفقة شهر، فلم تنفقها حتى جاء الشهر الثاني، فلها المطالبة لنفقته','551','695','0');
INSERT INTO [t6534] VALUES (708,'٥٨٩ - إذا منعت نفسها لتقبض صداقها، فلها النفقة','551','695','0');
INSERT INTO [t6534] VALUES (709,'٥٩٠ - زوجة المفقود إذا اعتدت بعد التربص، فلها النفقة من مال زوجها','552','695','0');
INSERT INTO [t6534] VALUES (710,'٥٩١ - يلزمه نفقة زوجته الهرمة التي لا يمكن التمتع بها','553','695','0');
INSERT INTO [t6534] VALUES (711,'٥٩٢ - إذا كانت الزوجة صغيرة لا يوطأ مثلها، فلا نفقة لها','553','695','0');
INSERT INTO [t6534] VALUES (712,'٥٩٣ - لا تجب نفقة الأقارب مع اختلاف الدين','554','695','0');
INSERT INTO [t6534] VALUES (713,'٥٩٤ - إذا سافرت المرأة بإذن زوجها في حاجة لها، فلا نفقة لها، ولا قسم','554','695','0');
INSERT INTO [t6534] VALUES (714,'كتاب الجنايات','555','0','0');
INSERT INTO [t6534] VALUES (715,'٥٩٥ - إذا اشترك في القتل عامد ومخطئ، لم يجب القصاص على العامد','555','714','0');
INSERT INTO [t6534] VALUES (716,'٥٩٦ - لا يقتل والد بولده رواية واحدة','555','714','0');
INSERT INTO [t6534] VALUES (717,'٥٩٧ - إذا قتل ذمي مسلما، ثم أسلم، قتل به','556','714','0');
INSERT INTO [t6534] VALUES (718,'٥٩٨ - إذا ادعى رجلان نسب لقيط متساويان في البينة أو عدمها','557','714','0');
INSERT INTO [t6534] VALUES (719,'٥٩٩ - إذا قتل زوجته ولها ابن منه، وآخر من غيره، لم يكن لابنها من غيره أن يقتص منه','557','714','0');
INSERT INTO [t6534] VALUES (720,'٦٠٠ - إذا قتل أحد الابنين أباهما، والآخر أمهما، وهي في زوجية الأب، وجب القصاص على قاتل الأم، دون قاتل الأب','558','714','0');
INSERT INTO [t6534] VALUES (721,'٦٠١ - أربعة أخوة: قتل الثاني الأول، والثالث الرابع، فإن القصاص يجب على الثالث، دون الثاني','559','714','0');
INSERT INTO [t6534] VALUES (722,'٦٠٢ - إذا قطع إنسان أصبع آخر عمدا، فطلب المجني عليه القصاص قبل الاندمال، فله ذلك','559','714','0');
INSERT INTO [t6534] VALUES (723,'٦٠٣ - إذا قطع أصبع رجل، ثم يد آخر، فاختار الأول القصاص، اقتص له، ويكون للثاني الخيار: بين دية يده كاملة، وبين الاقتصاص في اليد، وأخذ دية الأصبع الناقصة','560','714','0');
INSERT INTO [t6534] VALUES (724,'٦٠٤ - إذا قطع مسلم يد ذمي فأسلم، ثم سرت إلى نفسه، فلا قود على القاطع، بل عليه دية مسلم','561','714','0');
INSERT INTO [t6534] VALUES (725,'٦٠٥ - الاعتبار في وجوب القصاص بحال الجناية','561','714','0');
INSERT INTO [t6534] VALUES (726,'٦٠٦ - إذا اشترك جماعة في قتل مكافئ لهم عمدا، فعفى وليه عن القصاص، لزم كل واحد منهم دية كاملة','562','714','0');
INSERT INTO [t6534] VALUES (727,'٦٠٧ - إذا قطع أذنيه فذهب سمعه، لزمه ديتان، ولم تدخل دية السمع في دية الأذنين','562','714','0');
INSERT INTO [t6534] VALUES (728,'٦٠٨ - إذا جنى على أذن غيره فشلت وجبت حكومة','563','714','0');
INSERT INTO [t6534] VALUES (729,'٦٠٩ - إذا قال حر لحر: شجني، ففعل، لم يلزمه شيء في أصح الروايتين','563','714','0');
INSERT INTO [t6534] VALUES (730,'٦١٠ - إذا جرح حر حرا عمدا، فقال المجروح: عفوت عن هذه الجناية وما يحدث منها فمات، صح عفوه، ولا شيء لورثته','564','714','0');
INSERT INTO [t6534] VALUES (731,'٦١١ - إذا قال حر لحر: شجني [فشجه]، لم يلزمه قصاص رواية واحدة','565','714','0');
INSERT INTO [t6534] VALUES (732,'٦١٢ - إذا قتل رجلا ثم قطع يد آخر أو بالعكس، فاختار أولياؤهما القصاص قطع، ثم قتل','565','714','0');
INSERT INTO [t6534] VALUES (733,'٦١٣ - تغلظ دية العمد بصفتها، وهي أسنان الإبل، لا بزيادة العدد','566','714','0');
INSERT INTO [t6534] VALUES (734,'٦١٤ - تغلظ الدية بالحرم والإحرام والشهر الحرام، فتجب ديتان، ولا تداخل','566','714','0');
INSERT INTO [t6534] VALUES (735,'٦١٥ - الموضحة في الرأس والوجه فيها مقدر','567','714','0');
INSERT INTO [t6534] VALUES (736,'٦١٦ - إذا أوضحه في موضعين من رأسه، ولم يخرق الحاجز بينهما، فأرش ذلك عشر من الإبل','568','714','0');
INSERT INTO [t6534] VALUES (737,'٦١٧ - إذا قطع الذكر والأنثيين معا، أو قطعه ثم قطعهما، لزمه ديتان','568','714','0');
INSERT INTO [t6534] VALUES (738,'٦١٨ - إذا قطع رجل ذكر خنثى مشكل وأنثييه وشفريه عمدا، فقال الخنثى: لا أعفو عن القصاص، ولا أؤخر الحق، استحق عاجلا حكومة في الشفرين','569','714','0');
INSERT INTO [t6534] VALUES (739,'٦١٩ - إذا قطع يد عبد، لزمه نصف قيمته','570','714','0');
INSERT INTO [t6534] VALUES (740,'٦٢٠ - إذا قصد إنسان أخذ مال إنسان، فلرب المال دفعه بالمقاتلة ولو أتى على نفس المدفوع، ولغيره معاونته على دفعه عن نفسه، لا ماله إن خاف قتل المدفوع','570','714','0');
INSERT INTO [t6534] VALUES (741,'٦٢١ - إذا قطع ولي القتيل يد القاتل فاندملت، لزمه ديتها','571','714','0');
INSERT INTO [t6534] VALUES (742,'٦٢٢ - إذا جنى عليه فأذهب سنه ثم عاد (، فجنى عليه آخر) فأذهبه، فعليه القصاص','571','714','0');
INSERT INTO [t6534] VALUES (743,'٦٢٣ - إذا اشترك جماعة في قتل آدمي، لزم كل واحد كفارة','572','714','0');
INSERT INTO [t6534] VALUES (744,'٦٢٤ - إذا جنى عبد المحجور عليه لفلس على عبد له آخر بما يوجب القصاص في النفس، وقلنا: موجب العمد أحد شيئين، فعفى السيد','572','714','0');
INSERT INTO [t6534] VALUES (745,'٦٢٥ - إذا جنى على المكاتب مثله، أو عبد جناية توجب القصاص فيما دون النفس، فعفى على غير مال لم يسقط','573','714','0');
INSERT INTO [t6534] VALUES (746,'٦٢٦ - إذا جنى عبد على حر موضحة مثلا، فأبرأه منها، لم يصح','573','714','0');
INSERT INTO [t6534] VALUES (747,'٦٢٧ - إذا أمر الإمام إنسانا بنزول بئر ففعل، فهلك، ضمنه الإمام','574','714','0');
INSERT INTO [t6534] VALUES (748,'٦٢٨ - إذا ذكرت امرأة عند الإمام بسوء، فأرسل إليها ليحضرها، فأسقطت جنينها، وجب ضمانه على الإمام وهل يكون في عاقلته، أو بيت المال؟ فيه الروايتان','575','714','0');
INSERT INTO [t6534] VALUES (749,'٦٢٩ - إذا أمر الإمام إنسانا بقتل آخر، فقتله معتقدا جواز قتله، فلا ضمان عليه','576','714','0');
INSERT INTO [t6534] VALUES (750,'٦٣٠ - إذا قطع الإمام أو غيره سلعة غير مولى عليه بغير إذنه، [فمات] فعلى القاطع القود','576','714','0');
INSERT INTO [t6534] VALUES (751,'٦٣١ - قلت: فلو قطعها الإمام من مولى عليه كالصبي والمجنون، لم يضمن ذكره أبو بكر','577','714','0');
INSERT INTO [t6534] VALUES (752,'٦٣٢ - إذا وقفت دابته في طريق فجنت، ضمن','577','714','0');
INSERT INTO [t6534] VALUES (753,'٦٣٣ - إذا سقط رجل في حفرة فجذب ثانيا، وجذب الثاني ثالثا، وجذب الثالث رابعا، فلما صاروا فيها قتلهم أسد أو أفعى، فدم الأول هدر وعليه دية الثاني، وعلى الثاني دية الثالثة، وعلى الثالث دية الرابع','578','714','0');
INSERT INTO [t6534] VALUES (754,'٦٣٤ - إذا استولى البغاة على بلد، فادعى أهله دفع الزكاة إليهم، قبل بغير يمين','581','714','0');
INSERT INTO [t6534] VALUES (755,'٦٣٥ - إذا نصبوا قاضيا يستحل دماء أهل العدل ومالهم، لم ينفذ حكمه، وإلا نفذ','581','714','0');
INSERT INTO [t6534] VALUES (756,'٦٣٦ - إذا شرب المسلم الخمر، أو أكل لحم الخنزير، لم يحكم بردته','582','714','0');
INSERT INTO [t6534] VALUES (757,'كتاب الحدود','583','0','0');
INSERT INTO [t6534] VALUES (758,'٦٣٧ - إذا أباحت المرأة لزوجها أمتها، فوطئها وهو محصن عالم بحرمة الوطء، عزر بمائة جلدة، ولم يجب رجمه','583','757','0');
INSERT INTO [t6534] VALUES (759,'٦٣٨ - إذا ثبت على رجل أنه زنا بأمة فلان حد، وإن لم يحضر فلان','584','757','0');
INSERT INTO [t6534] VALUES (760,'٦٣٩ - إذا شهد شهود على رجل بالزنا، استفسرهم الحاكم عن صفة الزنا، وكيف رأوا','584','757','0');
INSERT INTO [t6534] VALUES (761,'٦٤٠ - إذ شهد أربعة على رجل بالزنا، وله ولد من زوجته، فقال: لم أطأها، لم يثبت إحصانه بالولد فلا يرجم؛ لأن الولد يلحق بالإمكان، والإحصان لا يثبت إلا باليقين لا بالإمكان، فلم يكن إلحاق الولد [دالا] على وجود الوطء منه قطعا، فلهذا لم يثبت إحصانه','585','757','0');
INSERT INTO [t6534] VALUES (762,'٦٤١ - إذا أقر بالزنا، سئل عن كيفيته','586','757','0');
INSERT INTO [t6534] VALUES (763,'٦٤٢ - إذا وجب رجم مريض مرضا يرجى برؤه، لم يؤخر رجمه','587','757','0');
INSERT INTO [t6534] VALUES (764,'٦٤٣ - إذا شهد بحق، فقال المشهود عليه: هو عبد، لم تقبل الشهادة حتى تثبت حريته','587','757','0');
INSERT INTO [t6534] VALUES (765,'٦٤٤ - إذا اشترك اثنان في نقب حرز ودخوله إلا أن أحدهما أخرج النصاب، ولم يخرج الآخر شيئا، فعليهما القطع','588','757','0');
INSERT INTO [t6534] VALUES (766,'٦٤٥ - إذا نقبا حرزا وأخرج أحدهما منه نصابا، فعليه القطع','589','757','0');
INSERT INTO [t6534] VALUES (767,'٦٤٦ - إذا هتك حرزا وأخرج منه بعض النصاب وانصرف، ثم عاد فأخذ بقيته، فإن تراخى عوده فلا قطع، وإن لم يتراخ قطع','589','757','0');
INSERT INTO [t6534] VALUES (768,'٦٤٧ - إذا سرق منديلا لا يساوي نصابا، وفي طرفه دينار مشدود لم يعلم به، لم يقطع','590','757','0');
INSERT INTO [t6534] VALUES (769,'٦٤٨ - إذا سرق عبدا صغيرا من حرز، قطع','590','757','0');
INSERT INTO [t6534] VALUES (770,'٦٤٩ - إذا سرق باب دار مفتوحا أو مغلقا، قطع','591','757','0');
INSERT INTO [t6534] VALUES (771,'٦٥٠ - إذا نام حر على أمتعته، فحملهما السارق وأخذهما، لم يقطع','591','757','0');
INSERT INTO [t6534] VALUES (772,'٦٥١ - إذا سرق تأزير المسجد، أو بابه قطع، والمطالبة به للإمام','592','757','0');
INSERT INTO [t6534] VALUES (773,'٦٥٢ - إذا جحد المودع الوديعة، لم يقطع','592','757','0');
INSERT INTO [t6534] VALUES (774,'٦٥٣ - إذا أقر بسرقة نصاب لغائب، لم يقطع حتى يحضر، بل يحبس','594','757','0');
INSERT INTO [t6534] VALUES (775,'٦٥٤ - إذا أجر حرزا ثم سرق منه نصابا لمستأجره، قطع','594','757','0');
INSERT INTO [t6534] VALUES (776,'٦٥٥ - إذا سرق ويمينه مقطوعة، قطعت يسرى رجليه','595','757','0');
INSERT INTO [t6534] VALUES (777,'٦٥٦ - إذا استعار حرزا وأحرز فيه متاعه، فسرق المعير منه نصابا من مال المستعير، قطع','595','757','0');
INSERT INTO [t6534] VALUES (778,'كتاب الجهاد','597','0','0');
INSERT INTO [t6534] VALUES (779,'٦٥٧ - يجوز للغانمين ذبح الحيوان من الغنيمة وكل لحمه','597','778','0');
INSERT INTO [t6534] VALUES (780,'٦٥٨ - إذا كان مع المجاهدين جوارح للصيد، لم يجز إطعامها من الغنيمة بخلاف علف الدواب','597','778','0');
INSERT INTO [t6534] VALUES (781,'٦٥٩ - يجوز [لآحاد] المجاهدين القتال بما في الغنيمة من سلاح','598','778','0');
INSERT INTO [t6534] VALUES (782,'٦٦٠ - لا يجوز أن يقتصر في تفرقة الخمس على أحد أصنافه','598','778','0');
INSERT INTO [t6534] VALUES (783,'٦٦١ - إذا قال الإمام: من دلنا على بلد كذا، فله الجارية الفلانية التي فيه، فدلهم إنسان وفتح عنوة، وقد أسلمت الجارية فله قيمتها','598','778','0');
INSERT INTO [t6534] VALUES (784,'٦٦٢ - إذا دخل حربي بأمان دار الإسلام فمات، ورثه ورثته','599','778','0');
INSERT INTO [t6534] VALUES (785,'٦٦٣ - إذا أعتق مسلم عبدا ذميا فلحق بدار الحرب، ثم سبي، لم يسترق','600','778','0');
INSERT INTO [t6534] VALUES (786,'٦٦٤ - إذا غزا على فرس غصبه أسهم له، وكان لمالكه','600','778','0');
INSERT INTO [t6534] VALUES (787,'٦٦٥ - إذا أخذ حربي أمة مسلم وأولدها، ثم غنمها المسلمون، فجميعهم غنيمة، ولسيدها أخذها بغير عوض قبل القسمة','601','778','0');
INSERT INTO [t6534] VALUES (788,'٦٦٦ - إذا حاصر الإمام حصنا فنزلوا على حكم إنسان فحكم بقتلهم، وسبي ذراريهم، فللإمام أن يمن عليهم بترك ذلك','601','778','0');
INSERT INTO [t6534] VALUES (789,'٦٦٧ - يكره بيع أرض العشر من أهل الذمة','602','778','0');
INSERT INTO [t6534] VALUES (790,'٦٦٨ - يجوز إسقاط الخراج عن من وجب عليه','603','778','0');
INSERT INTO [t6534] VALUES (791,'٦٦٩ - إذا سألت حربية دخول دارنا واجراء أحكامنا عليها، وتصير ذمية بغير جزية جاز فإذا عقد، منع سبيها واسترقاقها','603','778','0');
INSERT INTO [t6534] VALUES (792,'٦٧٠ - ولو أخذ الإمام الجزية من المرأة، وكانت معتقدة أنها تجب عليها وجب ردها','604','778','0');
INSERT INTO [t6534] VALUES (793,'٦٧١ - إذا اتجرت ذمية أو حربية إلى دار الإسلام، لم يؤخذ منها العشر','605','778','0');
INSERT INTO [t6534] VALUES (794,'٦٧٢ - إذا اتجرت الذمية أو الحربية إلى الحجاز أخذ منهما العشر من تجارتهما','606','778','0');
INSERT INTO [t6534] VALUES (795,'٦٧٣ - الجزية عوض عن حقن دمائهم وسكناهم دارنا، فهي كالإجارة، غير أن الإجارة يجوز استعجال عوضها','606','778','0');
INSERT INTO [t6534] VALUES (796,'٦٧٤ - إذا خيف من المستأمن خيانة نبذ إليه عهده، ورد إلى مأمنه','607','778','0');
INSERT INTO [t6534] VALUES (797,'٦٧٥ - وكذا إن هادن قوما وخاف خيانتهم','607','778','0');
INSERT INTO [t6534] VALUES (798,'٦٧٦ - إذا دخل حربي إلينا بأمان مدة فانقضت، واختار المقام، وامتنع من الجزية، فهو على أمانه حتى يبلغ مأمنه','608','778','0');
INSERT INTO [t6534] VALUES (799,'٦٧٧ - إذا نقض الذمي عهده بسب النبي - صلى الله عليه وسلم - تعين قتله','609','778','0');
INSERT INTO [t6534] VALUES (800,'٦٧٨ - إذا غلب الكفار على أموال المسلمين فأخذوها، ثم غلبهم المسلمون فأخذوها منهم قهرا قبل إسلامهم، فمن وجد عين ماله قبل القسمة أخذه مجانا، وبعدها بالقيمة في إحدى الروايتين','610','778','0');
INSERT INTO [t6534] VALUES (801,'٦٧٩ - إذا أسلم حربي وفي يده شيء مما غنمه من مال المسلمين، لم يكن لربه المسلم أخذه','611','778','0');
INSERT INTO [t6534] VALUES (802,'٦٨٠ - لا يسهم للعبد وإن قاتل بإذن مولاه','612','778','0');
INSERT INTO [t6534] VALUES (803,'٦٨١ - لا يشترط أن يكون بناء الذمي مغايرا لبناء أهل البلد في القصر، بل لجيرانه','612','778','0');
INSERT INTO [t6534] VALUES (804,'كتاب الصيد والذبائح','614','0','0');
INSERT INTO [t6534] VALUES (805,'٦٨٢ - إذا أكل الكلب أو الفهد من الصيد حرم','614','804','0');
INSERT INTO [t6534] VALUES (806,'٦٨٣ - إذا سمى على سهم أو إرسال جارح إلى صيد، فأصاب غيره فقتله، أبيح أكله','614','804','0');
INSERT INTO [t6534] VALUES (807,'٦٨٤ - إذا رمى طائرا فجرحه وسقط ميتا، حل','615','804','0');
INSERT INTO [t6534] VALUES (808,'٦٨٥ - إذا نسي التسمية على الذبيحة، حل أكلها','615','804','0');
INSERT INTO [t6534] VALUES (809,'٦٨٦ - إذا أرسل كلبه ولم يسم، ثم صاح وسمى، فزاد عدوه، ثم قتل صيدا، لم يحل','616','804','0');
INSERT INTO [t6534] VALUES (810,'٦٨٧ - إذا سمى بغير العربية من يحسنها، أجزأه','617','804','0');
INSERT INTO [t6534] VALUES (811,'٦٨٨ - إذا قطع عضوا من سعيد البر وأفلت، لم يبح أكل العضو','617','804','0');
INSERT INTO [t6534] VALUES (812,'٦٨٩ - إذا رمى صيدا فعقره ولم يثبته، ثم رماه آخر فقتله، أبيح','618','804','0');
INSERT INTO [t6534] VALUES (813,'٦٩٠ - إذا رمى رجلان صيدا فأصاباه ومات، فادعى كل منهما السبق بالرمي وإثباته، لم يحل','619','804','0');
INSERT INTO [t6534] VALUES (814,'كتاب الإيمان','620','0','0');
INSERT INTO [t6534] VALUES (815,'٦٩١ - إذا حلف بالله تعالى على شيء، فقال له آخر: أنا على مثل ذلك وما أشبه هذا، يريد التزام مثل يمينه، لم تنعقد','620','814','0');
INSERT INTO [t6534] VALUES (816,'٦٩٢ - إذا قال رجل: علي عتق رقبة إن دخلت هذه الدار، فقال آخر: علي مثل يمينك إن دخلتها، لزم الثاني مثل ما لزم الأول','620','814','0');
INSERT INTO [t6534] VALUES (817,'٦٩٣ - إذا حلف بطلاق أو عتاق، فقال له آخر: يميني في يمينك ونحوه، انعقد','621','814','0');
INSERT INTO [t6534] VALUES (818,'٦٩٤ - إذا حلف: ليخرجن من هذه الدار، لم يبر حتى يخرج أهله ومتاعه','622','814','0');
INSERT INTO [t6534] VALUES (819,'٦٩٥ - إذا حلف لا يكل هذا البشر، فصار تمرا أو دبسا أو خلا، فأكله حنث، إلا أن ينوي ما دام بسرا نص عليه','622','814','0');
INSERT INTO [t6534] VALUES (820,'٦٩٦ - إذا قال لعبده: أن بعتك فأنت حر، فباعه، عتق عقيب قبول المشتري','623','814','0');
INSERT INTO [t6534] VALUES (821,'٦٩٧ - إذا قال لعبده: إن بعتك فأنت حر، فباعه، لم يعتق بالإيجاب، بل بقبول المشتري','624','814','0');
INSERT INTO [t6534] VALUES (822,'٦٩٨ - إذا قال: أول من يدخل الدار من عبيدي فهو حر، فدخلها اثنان معا، ثم ثالث وحده، لم يعتق أحد منهم','624','814','0');
INSERT INTO [t6534] VALUES (823,'٦٩٩ - إذا حلف لا يسكن دارا وهو ساكنها، ولا نية له ولا سبب ليمينه، فلم يخرج في الحال مع إمكانه، حنث','625','814','0');
INSERT INTO [t6534] VALUES (824,'٧٠٠ - إذا قال: أي غلماني بشرني بكذا فهو حر، فبشره واحد ثم آخر عتق الأول وحده','626','814','0');
INSERT INTO [t6534] VALUES (825,'٧٠١ - إذا قال: إن قتلت فلانا يوم الجمعة فعبدي حر، فضربه يوم الخميس، فمات يوم الجمعة من ضربته','626','814','0');
INSERT INTO [t6534] VALUES (826,'٧٠٢ - إذا حلف: لا كلمت زيدا وعمرا، فكلم أحدهما، لم يحنث','627','814','0');
INSERT INTO [t6534] VALUES (827,'٧٠٣ - إذا حلف: لا كلمته يوما ويومين، فكلمه في الثالث، حنث','627','814','0');
INSERT INTO [t6534] VALUES (828,'٧٠٤ - إذا حلف بالله وقال: استثنيت بقلبي، لم يقبل إلا أن يكون مظلوما','628','814','0');
INSERT INTO [t6534] VALUES (829,'٧٠٥ - إذا حلف لا يأكل طعاما اشتراه زيد، فأكل طعاما اشتراه زيد وعمرو، ففي حنثه روايتان','628','814','0');
INSERT INTO [t6534] VALUES (830,'٧٠٦ - ذكر القاضي في كتاب إبطال الحيل: إذا حلف لا أكلت شيئا أبدا، ونوى اللحم، فله نيته','629','814','0');
INSERT INTO [t6534] VALUES (831,'٧٠٧ - إذا حلف لا يدخل دارا، فدخل فضاء كان دارا، لم يحنث','630','814','0');
INSERT INTO [t6534] VALUES (832,'٧٠٨ - قد ذكرنا: أنه يحنث بدخول عرصة دار حلف لا يدخلها','630','814','0');
INSERT INTO [t6534] VALUES (833,'٧٠٩ - إذا حلف: لا فارقتك حتى أستوفي حقي منك، فهرب، لم يحنث','631','814','0');
INSERT INTO [t6534] VALUES (834,'٧١٠ - إذا حلف لا يأكل شعيرا، فأكل حنطة فيها حبات شعير، حنث','631','814','0');
INSERT INTO [t6534] VALUES (835,'٧١١ - إذا حلف: لا كلمته أشهرا، حمل على ثلاثة، كقوله أياما','633','814','0');
INSERT INTO [t6534] VALUES (836,'٧١٢ - إذا حلف بالله ليأكلن الخبز الذي في هذا السل ولا خبز فيه، أو ليشربن الماء الذي في هذا الكوز ولا ماء فيه، لم تنعقد يمينه، سواء علم بهما أو لم يعلم','633','814','0');
INSERT INTO [t6534] VALUES (837,'٧١٣ - إذا قال لأمته: إن أعطيتك كرى مقنعتك فأنت حرة، فأقرضها دراهم، فاشترت بها قطنا، وغزلته وباعته، وردت على مولاها قدر ما أعطاها، وصرفت الباقي في أجرة المقنعة، حنث نص عليه','634','814','0');
INSERT INTO [t6534] VALUES (838,'٧١٤ - إذا حلف لا يتصدق عليه، فوهبه، لم يحنث','635','814','0');
INSERT INTO [t6534] VALUES (839,'٧١٥ - إذا حلف لا يأكل هذه التمرة فاختلطت بتمر ولم تتميز، فأسقط منه تمرة وأكل الباقي، لم يحنث','635','814','0');
INSERT INTO [t6534] VALUES (840,'٧١٦ - إذا حلف لا يشرب ماء دجلة ولا نية له، فشرب منه، حنث','636','814','0');
INSERT INTO [t6534] VALUES (841,'٧١٧ - إذا حلف لا باع ثوبه من فلان بمائة، فباعه بها أو بأقل منها، حنث','636','814','0');
INSERT INTO [t6534] VALUES (842,'٧١٨ - إذا قال لزوجته: إن وهبتك شيئا فبعتيه فأنت طالق، فوهب لها شيئا فباعته، طلقت','637','814','0');
INSERT INTO [t6534] VALUES (843,'٧١٩ - إذا حلف لا يبيع فباع، فلم يقبل المشتري، لم يحنث','637','814','0');
INSERT INTO [t6534] VALUES (844,'كتاب النذر','639','0','0');
INSERT INTO [t6534] VALUES (845,'٧٢٠ - إذا قال: لله علي أن أصوم ثلاثين يوما، لم يلزمه التتابع رواية واحدة','639','844','0');
INSERT INTO [t6534] VALUES (846,'٧٢١ - إذا نذر أن يصوم كل اثنين أجزأه صوم الأثانين التي في شهر رمضان عنه وعن نذره، ولم يلزمه قضاؤها، ولا كفارة','640','844','0');
INSERT INTO [t6534] VALUES (847,'٧٢٢ - إذا قال في حال اللجاج والغضب: إن دخلت الدار فمالي صدقة، فدخلها، خير بين فعل ما التزم، وبين كفارة يمين','641','844','0');
INSERT INTO [t6534] VALUES (848,'٧٢٣ - إذا نذر الصلاة في أحد المساجد الثلاثة لزمه فعلها','642','844','0');
INSERT INTO [t6534] VALUES (849,'٧٢٤ - إذا نذر أن يصوم كل اثنين، ثم لزمه صوم شهرين متتابعين إما بظهار أو غيره، فصام عن ذلك، لزمه قضاء الأثانين اللاتي فيهما','642','844','0');
INSERT INTO [t6534] VALUES (850,'كتاب الأقضية','644','0','0');
INSERT INTO [t6534] VALUES (851,'٧٢٥ - إذا استعدى على خصم في البلد، أعدي عليه قبل تحرير الدعوى','644','850','0');
INSERT INTO [t6534] VALUES (852,'٧٢٦ - إذا قال المدعي: لا بينة لي، ثم أتى ببينة، لم تقبل','644','850','0');
INSERT INTO [t6534] VALUES (853,'٧٢٧ - إذا قال لا بينة لي، ثم أتى ببينة، لم تقبل','645','850','0');
INSERT INTO [t6534] VALUES (854,'٧٢٨ - إذا قال المدعي: لي بينة غائبة، لم يكن له ملازمة المدعى عليه، ولا مطالبته بكفيل نص عليه','645','850','0');
INSERT INTO [t6534] VALUES (855,'٧٢٩ - إذا كتب قاضي بلد إلى قاضي بلد بثبوت حق على إنسان، فأحضر المكتوب إليه المدعى عليه، وألزمه دفع الحق، فقال: برئت إليه منه، فإن كان صاحب الحق حاضرا حلف وأخذ','646','850','0');
INSERT INTO [t6534] VALUES (856,'٧٣٠ - إذا ادعى إنسان أن الحاكم حكم له بحق فلم يذكر، فشهد عدلان عنده بذلك، لم يحكم بشهادتهما','646','850','0');
INSERT INTO [t6534] VALUES (857,'٧٣١ - إذا بان للحاكم أنه خالف النص أو الإجماع، نقض حكمه','647','850','0');
INSERT INTO [t6534] VALUES (858,'٧٣٢ - إذا ادعى على رجل قتل عمد، وأقام شاهدا وحلف معه، لم يستحق قودا ولا دية','648','850','0');
INSERT INTO [t6534] VALUES (859,'٧٣٣ - إذا كان في يد رجل جارية، فادعى رجل أنها أم ولده، وأن ولدها منه حر، وأقام بذلك رجلا وامرأتين، أو شاهدا وحلف معه، حكم له بالجارية؛ لأن أم الولد مال، وإذا ملكها بالبينة ثبت أنها أم ولده بالإقرار في الملك','648','850','0');
INSERT INTO [t6534] VALUES (860,'٧٣٤ - إذا ادعى عينا في يد آخر أنها له ولفلان الغائب، وأقام بذلك بينة، انتزع نصيب المدعي من يده، وأبقي نصيب الغائب','649','850','0');
INSERT INTO [t6534] VALUES (861,'٧٣٥ - إذا ادعى أحد الوارثين: أن من تركة موروثه مالا في ذمة رجل حاضر معه، والوارث الآخر غائب، وأقام البينة بدعواه، فإن الحاكم يسمعها ويقضي للحاضر بنصيبه، دون نصيب الغائب، فمتى حضر الغائب وسأل الحاكم لحقه، حكم له، ولم يحتج إلى إعادة البينة','650','850','0');
INSERT INTO [t6534] VALUES (862,'٧٣٦ - إذا مات رجل فادعى ورثته على رجل أن من تركته دينا عليه، وشهد شاهد واحد، وحلفوا معه استحقوا، وإن حلف بعضهم استحق الحالف','651','850','0');
INSERT INTO [t6534] VALUES (863,'٧٣٧ - إذا كان لاثنين أرض فيها زرع فأرادا قسمته منفردا، فإن كان قصيلا جاز','652','850','0');
INSERT INTO [t6534] VALUES (864,'٧٣٨ - إذا حكم حاكم بشهادة شاهدين، فبانا كافرين، نقض حكمه','653','850','0');
INSERT INTO [t6534] VALUES (865,'٧٣٩ - قلت: ثم إن كان المحكوم به مالا فهو مضمون على المحكوم له، فإن كان باقيا رده، وإن كان تالفا رد بدله','653','850','0');
INSERT INTO [t6534] VALUES (866,'٧٤٠ - إذا ادعى أثمانا ولم يعين نقدها، لم تكن الدعوى محررة، ولم ينصرف إلى نقد البلد','655','850','0');
INSERT INTO [t6534] VALUES (867,'٧٤١ - إذا كان في يده دجاجة، فأثبت آخر: أن البيضة التي خرجت منها الدجاجة ملكه، قضي له بها','655','850','0');
INSERT INTO [t6534] VALUES (868,'٧٤٢ - إذا تداعيا شاة مسلوخة، في يد أحدهما أطرافها ورأسها وجلدها، وفي يد الآخر باقيها، وأقام كل منهما البينة بدعواه، قضي له بما في يد صاحبه','656','850','0');
INSERT INTO [t6534] VALUES (869,'٧٤٣ - إذا تداعيا عينا في يد ثالث فأقر بها لأحدهما ثم للآخر، دفعها إلى الأول، وقيمتها إلى الثاني','657','850','0');
INSERT INTO [t6534] VALUES (870,'٧٤٤ - إذا أتلف على إنسان عبدا، أو قال: كان به عيب فنقصت قيمته، أخذ بقول مالكه في نفيه','657','850','0');
INSERT INTO [t6534] VALUES (871,'٧٤٥ - إذا كان في يد مجهول النسب مال، فقال لمثله: مات أبي وأنت أخي، فقال؛ بل هو أبي ولست أخي، لم يقبل إنكاره','658','850','0');
INSERT INTO [t6534] VALUES (872,'٧٤٦ - قد قدمنا أنه إذا قال: مات أبي وأنت أخي، فقال: لست أخي، أنه لا يقبل إنكاره','658','850','0');
INSERT INTO [t6534] VALUES (873,'٧٤٧ - إذا ادعى نكاح امرأة فأقرت له، قبل إقرارها','659','850','0');
INSERT INTO [t6534] VALUES (874,'٧٤٨ - قد قررنا: أنه لا يقبل إقرارها لأحد المتداعيين نكاحها','660','850','0');
INSERT INTO [t6534] VALUES (875,'٧٤٩ - إذا قال: [له] علي مائة إلى سنة، قبل قوله، وكانت مؤجلة','660','850','0');
INSERT INTO [t6534] VALUES (876,'كتاب الشهادات','661','0','0');
INSERT INTO [t6534] VALUES (877,'٧٥٠ - إذا شهد أحد الشاهدين: أنه باعه داره أمس، والآخر: أنه باعه إياها اليوم، حكم بشهادتهما','661','876','0');
INSERT INTO [t6534] VALUES (878,'٧٥١ - إذا شهد أحدهما: أنه باعه داره أمس، وشهد الآخر: أنه باعه إياها اليوم، حكم بشهادتهما','661','876','0');
INSERT INTO [t6534] VALUES (879,'٧٥٢ - إذا ادعى: أن فلانا أتلف عليه ثوبه الفلاني، فشهد له شاهد: أنه أتلفه يوم الخميس، وآخر: أنه أتلفه يوم الجمعة، لم تكمل شهادتهما، ولم يحكم بها، لكن يحلف المشهود له مع أحدهما، ويستحق [قيمته]','662','876','0');
INSERT INTO [t6534] VALUES (880,'٧٥٣ - إذا شهد [اثنان]: أنه قذف فلانا يوم الخميس، واثنان: يوم الجمعة، ثبت ولزم الحد','662','876','0');
INSERT INTO [t6534] VALUES (881,'٧٥٤ - إذا شهد اثنان: أنه غصبه ثوبا معينا يوم الخميس، واثنان: أنه غصبه يوم الجمعة، لزم ضمان الثوب','663','876','0');
INSERT INTO [t6534] VALUES (882,'٧٥٥ - إذا شهد أحدهما: أن له عليه ألفا، وشهد الآخر: أن له عليه ألفين، ثبتت ألف بشاهد [ين]، ويحلف مع الآخر على استحقاق الألف الأخرى','663','876','0');
INSERT INTO [t6534] VALUES (883,'٧٥٦ - إذا شهدا: أن له عليه ألفا، ثم قال أحدهما: قضاه منها خمسمائة، بطلت شهادته','664','876','0');
INSERT INTO [t6534] VALUES (884,'٧٥٧ - إذا شهد أحدهما: أن فلانا سرق منه ثوبا قيمته خمسة، وآخر: أنه سرقه وقيمته عشرة، ثبتت الخمسة بشاهدين فيستحقها، والخمسة الأخرى بشاهد واحد، فله أن يحلف معه ويستحقها','665','876','0');
INSERT INTO [t6534] VALUES (885,'٧٥٨ - إذا ادعى على رجل: أنه غصب منه شيئا، فحلف بالطلاق ما غصب، ثم ثبت عليه بعدلين، ألزم بالخروج منه، وطلقت زوجته','665','876','0');
INSERT INTO [t6534] VALUES (886,'٧٥٩ - إذا شهد رجل وامرأتان: أنه أوضحه عمدا، لم تقبل شهادتهم','667','876','0');
INSERT INTO [t6534] VALUES (887,'٧٦٠ - إذا ادعى الزوج الخلع فأنكرته، فشهد له به رجل وامرأتان، ثبت','668','876','0');
INSERT INTO [t6534] VALUES (888,'٧٦١ - إذا شهد بالمال رجل وامرأتان، أو رجل وحلف معه المدعي، حكم بذلك','668','876','0');
INSERT INTO [t6534] VALUES (889,'٧٦٢ - إذا جرح رجل رجلا فشهد له غير عمودي نسبه، كالأخ والعم: أنه جرحه عمدا، فإن كان قبل الاندمال، لم يقبل، وإن كان بعده، قبلت','669','876','0');
INSERT INTO [t6534] VALUES (890,'٧٦٣ - إذا شهدا لموروثهما بدين وهو مريض، قبلت في أحد الوجهين بخلاف المسألة المتقدمة','669','876','0');
INSERT INTO [t6534] VALUES (891,'٧٦٤ - فإن مات من يحجبهما بعد الشهادة وقبل الحكم بها، لم يحكم بها','670','876','0');
INSERT INTO [t6534] VALUES (892,'٧٦٥ - إذا خلف ثلاثة بنين وغيرهم من البنات والزوجات، فادعى أحد البنين: أن أباه وقف هذه الدار عليه وعلى إخوته خاصة، ومن مات','670','876','0');
INSERT INTO [t6534] VALUES (893,'٧٦٦ - قلت: فلو كان دعوى الآباء: أن أبناءهم يشاركونهم، لم يحكم به حتى يحلف الأبناء','671','876','0');
INSERT INTO [t6534] VALUES (894,'٧٦٧ - إذا شهد صبي وعبد وكافر فلم تقبل، ثم أعادوها بعد الكمال، قبلت','671','876','0');
INSERT INTO [t6534] VALUES (895,'٧٦٨ - تثبت الجناية الموجبة للقصاص فيما دون النفس برجلين','672','876','0');
INSERT INTO [t6534] VALUES (896,'٧٦٩ - تصح الشهادة بالإقرار بالمجهول','673','876','0');
INSERT INTO [t6534] VALUES (897,'٧٧٠ - إذا ادعى شراء دار وتسليم الثمن، وأقام بذلك شاهدين عدلين ولم يسميا الثمن، والبائع ينكر قبضه، فشهادتهما باطلة','673','876','0');
INSERT INTO [t6534] VALUES (898,'٧٧١ - إذا شهد شاهدان: أن عمرا وهب عبده من زيد، وأقبضه إياه، وآخران: أنه وهبه من خالد، وأقبضه إياه، وجهل أسبقهما، أقرع بينهما، فمن قرع أخذه','676','876','0');
INSERT INTO [t6534] VALUES (899,'٧٧٢ - إذا سمع شاهدان رجلا يقر بشيء، جاز أن يشهدا عليه به ولو لم يقل لهما: اشهدا علي','677','876','0');
INSERT INTO [t6534] VALUES (900,'٧٧٣ - ذكر القاضي في كتاب إبطال الحيل: إذا وصى إلى رجلين فقبلا ثم مات، والوصيان يعلمان أن لرجل عليه دينا فقضاه، ثم شهدا به، لم تقبل شهادتهما','677','876','0');
INSERT INTO [t6534] VALUES (901,'٧٧٤ - إذا أوصى إلى رجلين بالنظر في أمر أطفاله، فبلغ بعضهم وفك حجره، فغصب من كل منهم شيء، فشهد الوصيان بذلك، قبلت شهادتهما للكبار، دون الصغار','678','876','0');
INSERT INTO [t6534] VALUES (902,'٧٧٥ - إذا كان في يد رجل جارية ولها ولد، فادعى رجل: أنها أم ولده وولدها منه حر، فأنكره، فشهد له بدعواه رجل وامرأتان، قضي له بالجارية، وأنها أم ولده، ولم يثبت نسب الولد منه، ولا حريته على إحدى الروايتين','678','876','0');
INSERT INTO [t6534] VALUES (903,'٧٧٦ - يثبت الوقف برجل وامرأتين، وشاهد ويمين','678','876','0');
INSERT INTO [t6534] VALUES (904,'٧٧٧ - يحد شارب النبيذ المعتقد إباحته','679','876','0');
INSERT INTO [t6534] VALUES (905,'٧٧٨ - قد تقدم: أن الشارب للنبيذ معتقدا إباحته، يحد','679','876','0');
INSERT INTO [t6534] VALUES (906,'٧٧٩ - إذا شهد العدول عند الحاكم بحق، ثم جنوا، أو عموا، أو خرسوا قبل','680','876','0');
INSERT INTO [t6534] VALUES (907,'كتاب العتق','682','0','0');
INSERT INTO [t6534] VALUES (908,'٧٨٠ - إذا أعتق موسر شركا له في عبد، عتق كله، وقوم عليه نصيب شريكه','682','907','0');
INSERT INTO [t6534] VALUES (909,'٧٨١ - إذا قال أحد الشريكين لشريكه الموسر: إذا أعتقت نصيبك فنصيبي','682','907','0');
INSERT INTO [t6534] VALUES (910,'٧٨٢ - عبد بين شريكين قيمة كل نصيب عشرة دنانير، فقال رجل لا يملك غيرها لأحدهما: أعتق نصيبك عني وعلي هذه العشرة، ففعل عتق، ولم يسر العتق إلى نصيب الشريك','683','907','0');
INSERT INTO [t6534] VALUES (911,'٧٨٣ - إذا كان لرجل نصفا عبدين قيمتهما سواء، لا مال له غيرهما، فأعتق نصيبه منهما بكلمة واحدة، عتق عليه نصيباه منهما خاصة، ولم يقوم عليه شيء آخر من باقيهما','684','907','0');
INSERT INTO [t6534] VALUES (912,'٧٨٤ - عبد بين ثلاثة، شهد اثنان منهم [على الثالث]: أنه أعتق نصيبه وهو معسر، قبلت شهادتهما','685','907','0');
INSERT INTO [t6534] VALUES (913,'٧٨٥ - عبد بين شريكين رأيا طائرا، فقال أحدهما: إن كان غرابا فنصيبه حر، وقال الآخر: إن لم يكن غرابا فنصيبه حر، وغاب ولم يعلم خبره، فأحدهما حانث بيقين','686','907','0');
INSERT INTO [t6534] VALUES (914,'٧٨٦ - إذا قال: إن كان حملك غلاما فأنت حرة، وإن كان جارية فهي حرة، فبان ذكرا وأنثى، لم يعتق واحد منهما','687','907','0');
INSERT INTO [t6534] VALUES (915,'٧٨٧ - إذا قال لعبديه: إذا جاء غد فأحدكما حر، فجاء الغد وهما في ملكه، أخرج المعتق بالقرعة','687','907','0');
INSERT INTO [t6534] VALUES (916,'٧٨٨ - فإن كانت بحالها، فباع نصف أحدهما قبل الغد، أقرع بين العبد ونصف الآخر، فإن خرجت على العبد عتق، واستقر ملكه على نصف الآخر','687','907','0');
INSERT INTO [t6534] VALUES (917,'٧٨٩ - إذا قال لعبده: إن ملكتك فأنت حر، عتق','688','907','0');
INSERT INTO [t6534] VALUES (918,'٧٩٠ - إذا قال لرجل: أعتق عبدك هذا وعلي ثمنه، فالثمن على السائل، والولاء والعتق للمسؤول','688','907','0');
INSERT INTO [t6534] VALUES (919,'٧٩١ - إذا أعتق الأمة الحامل، عتق حملها','689','907','0');
INSERT INTO [t6534] VALUES (920,'كتاب التدبير','690','0','0');
INSERT INTO [t6534] VALUES (921,'٧٩٢ - اذا كان العبد لواحد فقال: إذا مت فأنت حر، صار مدبرا','690','920','0');
INSERT INTO [t6534] VALUES (922,'٧٩٣ - إذا حدث للمدبرة أولاد (بعد تدبيرها)، ثم رجع سيدها في تدبيرها، لم يبطل التدبير في حق أولادها','691','920','0');
INSERT INTO [t6534] VALUES (923,'٧٩٤ - إذا دبر أمة ثم ولدت، ثم ماتت قبل موت سيدها، عتق الأولاد بموته','691','920','0');
INSERT INTO [t6534] VALUES (924,'٧٩٥ - إذا عتقت المدبرة بموت سيدها، ثم اختلفت هي والوارث في مال بيدها، فقال الوارث: كسبتيه قبل الموت فهو إرث، فقالت: بل بعده فهو ملكي، أخذ بقولها','692','920','0');
INSERT INTO [t6534] VALUES (925,'٧٩٦ - إذا قال: إن دخلت الدار فأنت حر بعد موتي، فدخلها في حياته صار مدبرا, ولو دخلها بعد موته، لم يعتق بحال','693','920','0');
INSERT INTO [t6534] VALUES (926,'٧٩٧ - إذا قال له: إن شئت فأنت مدبر، اختصت مشيئته بالمجلس','693','920','0');
INSERT INTO [t6534] VALUES (927,'٧٩٨ - إذا فسدت الكتابة لجهل العوض أو غيره، غلب فيها حكم الصفة، فتكون جائزة من الطرفين','695','920','0');
INSERT INTO [t6534] VALUES (928,'٧٩٩ - إذا اتفق السيد والمكاتب على فسخها، انفسخت','695','920','0');
INSERT INTO [t6534] VALUES (929,'٨٠٠ - الكتابة الفاسدة تنفسخ بجنون السيد، دون الصحيحة','696','920','0');
INSERT INTO [t6534] VALUES (930,'٨٠١ - الفاسدة تبطل بجنون السيد، دون العبد','696','920','0');
INSERT INTO [t6534] VALUES (931,'٨٠٢ - الكتابة الصحيحة لا تبطل بموت السيد','697','920','0');
INSERT INTO [t6534] VALUES (932,'٨٠٣ - إذا أدى حر عن المكاتب مال الكتابة بغير إذنه لم يرجع عليه، ذكره في المجرد','697','920','0');
INSERT INTO [t6534] VALUES (933,'٨٠٤ - إذا قال لعبده: إن أعطيتني مائة فأنت حر، فدفعها إليه عتق، وما فضل بيده لسيده','698','920','0');
INSERT INTO [t6534] VALUES (934,'٨٠٥ - ذكر القاضي في المجرد: أنه إذا تزوج المكاتب بأمة لسيده، ثم اشتراها وأتت بولد، فقال السيد: أتت به قبل أن تشتريها فهو مملوكي، وقال المكاتب: بل أتت به بعد أن اشتريتها فهو ملكي، واحتمل، ولا بينة، أخذ بقول المكاتب','698','920','0');
INSERT INTO [t6534] VALUES (935,'٨٠٦ - إذا قتل السيد مكاتبه، لم يضمنه','699','920','0');
INSERT INTO [t6534] VALUES (936,'٨٠٧ - إذا كاتب أمة بشرط إدخال ولدها في الكتابة، ثم أعتقه عتق، وسقطت حصته من مال الكتابة','699','920','0');
INSERT INTO [t6534] VALUES (937,'٨٠٨ - إذا زوج عبده من أمته فأولدها، ثم كاتبهما جميعا، وشرط دخول الولد في الكتابة، ثم قتل الولد، سقط قدر حصته من مال الكتابة عنهما','700','920','0');
INSERT INTO [t6534] VALUES (938,'٨٠٩ - إذا كاتب اثنان عبدهما، فأدى إلى أحدهما، لم يعتق نصيبه من المكاتب','700','920','0');
INSERT INTO [t6534] VALUES (939,'٨١٠ - إذا كاتب عبده على مال، ثم أعطاه كفيلا، لم تصح الكفالة','701','920','0');
INSERT INTO [t6534] VALUES (940,'٨١١ - إذا طاوعت المكاتبة سيدها على وطئها, لم يسقط مهرها ذكره القاضي عن أبي بكر','702','920','0');
INSERT INTO [t6534] VALUES (941,'٨١٢ - إذا جنى عبد المكاتب خطأ أو عمدا، واختار المكاتب فداءه، وقلنا: يلزمه فداؤه بأرش الجناية بالغا ما بلغ، فإنه يلزمه ما لم يجاوز أرشها قيمته فإن جاوزها لم يجز أن يفديه بأكثر من قيمته، بل يسلمه للبيع','702','920','0');
INSERT INTO [t6534] VALUES (942,'٨١٣ - إذا جنى عبد المكاتب جناية تتعلق برقبته وهو أجنبي منه، فله فداؤه بأقل الأمرين: من قيمته، أو أرش الجناية','703','920','0');
INSERT INTO [t6534] VALUES (943,'٨١٤ - تتعلق ديون المكاتب بذمته','703','920','0');
INSERT INTO [t6534] VALUES (944,'٨١٥ - إذا جنى المكاتب على سيده جناية تعلق أرشها برقبته، ثم عتق بالأداء، تحول أرشها إلى ذمته','704','920','0');
INSERT INTO [t6534] VALUES (945,'٨١٦ - إذا أبرأ السيد مكاتبه من مال الكتابة في مرض موته ولا مال له غير ذلك، عتق من المكاتب بقدر ثلث [الأقل من] قيمته، أو مال الكتابة','705','920','0');
INSERT INTO [t6534] VALUES (946,'٨١٧ - إذا أوصى بمال الكتابة لإنسان، عتق المكاتب بالدفع إليه، كما يعتق بالدفع إلى الوصي ليدفعه إليه','705','920','0');
INSERT INTO [t6534] VALUES (947,'٨١٨ - إذا كاتب عبده لم يكن له الفسخ إذا كان قادرا على الأداء','706','920','0');
INSERT INTO [t6534] VALUES (948,'٨١٩ - إذا استولد المكاتب أمته، فولده مملوك له','706','920','0');
INSERT INTO [t6534] VALUES (949,'٨٢٠ - لا تصير الأمة أم ولد بعلوقها بمملوك، إلا أمة المكاتب إذا علقت منه','707','920','0');
INSERT INTO [t6534] VALUES (950,'٨٢١ - إذا ولدت الأمة المشتركة منهما، فألحقت القافة ولدها بهما، ثم مات أحدهما، عتق نصفها, ولم يعتق باقيها إلا بموت الآخر، أو إعتاقه','707','920','0');
INSERT INTO [t6534] VALUES (951,'٨٢٢ - إذا أوصى لأم ولده وقيمتها ثلث ماله، بشيء قيمته ثلث ماله [ملكته بعد موته','708','920','0');
INSERT INTO [t6534] VALUES (952,'٨٢٣ - إذا جنت أم الولد فداها سيدها بأقل الأمرين: من قيمتها، أو أرش الجناية، ولا يلزمه غير ذلك رواية واحدة','709','920','0');
INSERT INTO [t6534] VALUES (953,'٨٢٤ - إذا قتلت أم الولد سيدها، عتقت','710','920','0');
INSERT INTO [t6534] VALUES (954,'٨٢٥ - إذا أعتق أم ولده ولها أولاد من زوج أو زنا ولدتهم بعد الاستيلاد عتقت، ولم يعتق أولادها إلا بموت السيد أو عتقه','710','920','0');
INSERT INTO [t6534] VALUES (955,'قائمة المراجع','712','0','0');
INSERT INTO [t6534] VALUES (956,'أولا: المخطوطة','712','955','0');
INSERT INTO [t6534] VALUES (957,'ثانيا: المطبوعة','714','955','0');
