INSERT INTO [t6549] VALUES (1,'زاده الله علوا وتأييدا وزاد من يؤازره بالحير ويحثه عليه توفيقا وتسديدا ثم إنه أعز الله نصره صرف همته العالية إلى نصرة دين الله وقمع أعداء الله بعد ما تقرر للكافة حسن إعتقاداه بتقرير خطباء أهل مملكته على لعن من إستوجب اللعن من أهل البدعة ببدعته وأيس','79','0','0');
INSERT INTO [t6549] VALUES (2,'قال فصاح أبو العباس القاضي وبكى فقال قد نعى إلي نفسي قال الشيخ أبو الوليد فمات القاضي أبو العباس في تلك السنة قال الحاكم أبو عبد الله فلما رويت هذه الحكاية كتبوها وكان ممن كتبها شيخ أديب فقيه فلما كان في المجلس الثاني قال لي بعض الحاضرين إن هذا الشيخ','191','0','0');
INSERT INTO [t6549] VALUES (3,'إلى أن قال أنا من علمت فلا تظني غيره صعب على خطب الزمان الأصعب لكنني طوع لكل خريدة رود الشباب وكل خود خرعب من كل ساجية الجفون كأنما ترنو إذا نظرت بعيني ربرب بيضاء أخلصها النعيم كأنما يجلو مجردها حشاشة مقضب ملكت محبات القلوب','202','0','0');
INSERT INTO [t6549] VALUES (4,'ومنهم أبو علي الدقاق النيسابوري شيخ أبي القسم القشيري رحمه الله','204','0','0');
INSERT INTO [t6549] VALUES (5,'ثم يدعو ويقول أللهم اسقنا قال فما أتم ثلاثا حتى سقينا كأفواه القرب ومنهم القاضي أبو عمر محمد بن الحسين البسطامي رحمه الله','214','0','0');
INSERT INTO [t6549] VALUES (6,'ومنهم أبو الحسن بن ما شاذة الأصبهاني رحمه الله','217','0','0');
INSERT INTO [t6549] VALUES (7,'ومنهم أبو الحسن النعيمي البصري رحمه الله','228','0','0');
INSERT INTO [t6549] VALUES (8,'رواها أبو بكر الخطيب عن محمد بن الصوري عن النعيمي والله أعلم','230','0','0');
INSERT INTO [t6549] VALUES (9,'ومنهم الإمام أبو الحسن الطبري المعروف بالكيا رحمه الله','266','0','0');
INSERT INTO [t6549] VALUES (10,'ومنهم الإمام أبو حامد الطوسي الغزالي رحمه الله','269','0','0');
INSERT INTO [t6549] VALUES (11,'من إنشاد الأستاذ الإمام أبي القسم عبد الكريم بن هوازن القشيري ثم إنه جرى على لسان ولدك محمد في أثناء إنشاده بين يدي رسول الله صلى الله عليه وسلم هذه القصيدة شيء فقال له رسول الله صلى الله عليه وسلم كيف قلت كالمستدرك عليه فرجع إلى أبيات قبلها فأنشدها','301','0','0');
INSERT INTO [t6549] VALUES (12,'لعلمت أن أحدا لم يسلم من ألسنة الطعانين ولم يخل بعض الكبار من','399','0','0');
