INSERT INTO [t6615] VALUES (1,'مولد رسول الله صلى الله عليه وسلم ووفاته','4','0','0');
INSERT INTO [t6615] VALUES (2,'سنة اثنتين','8','0','0');
INSERT INTO [t6615] VALUES (3,'غزوة بواط قال ابن إسحاق وفيها غزا بواط من ناحية رضوى في شهر ربيع الآخر يعترض عير قريش فلم يلق كيدا وقال علي بن محمد خرج يوم الثلاثاء لثلاث خلون من شهر ربيع الآخر ورجع لعشر وهي على ثلاث مراحل من المدينة من طريق الشام','9','0','0');
INSERT INTO [t6615] VALUES (4,'غزوة سفوان قال ابن إسحاق ثم خرج في طلب كرز بن جابر وكان اغار على سرح المدينة فخرج رسول الله صلى الله عليه وسلم في طلبه حتى بلغ سفوان من ناحية بدر في جمادى الآخرة ورجع في رجب ولم يلق كيدا وهي غزوة بدر الأولى','9','0','0');
INSERT INTO [t6615] VALUES (5,'غزوة بدر الكبرى قال ابن إسحاق وبلغ رسول الله صلى الله عليه وسلم أن أبا سفيان أقبل من الشام في عير لقريش فخرج في طلبه','9','0','0');
INSERT INTO [t6615] VALUES (6,'غزوة الكدر','10','0','0');
INSERT INTO [t6615] VALUES (7,'غزوة السويق قال ابن إسحاق خرج في ذي الحجة قال ابن إسحاق فحدثني محمد بن جعفر بن الزبير ويزيد بن رومان أن أبا سفيان حين رجع إلى مكة ورجع فل قريش نذر ألا يمس رأسه ماء من جنابة حتى يغزو محمدا فخرج في مائتي راكب من قريش وخرج رسول الله صلى الله عليه وسلم في','11','0','0');
INSERT INTO [t6615] VALUES (8,'من بني المطلب من قريش ثم من بني المطلب بن عبد مناف بن قصي عبيدة بن الحارث بن المطلب قتله عتبة بن ربيعة قطع رجله فمات بالصفراء','11','0','0');
INSERT INTO [t6615] VALUES (9,'ومن بني زهرة عمير بن أبي وقاص قتله عمرو بن عبد ود وذو الشمالين بن عبد عمرو بن نضلة حليف لهم من خزاعة قتله أسامة الجشمي','11','0','0');
INSERT INTO [t6615] VALUES (10,'من بني عدي ومن بني عدي بن كعب عاقل بن البكير حليف لهم من بني ليث قتله مالك بن زهير ومهجع مولى عمر بن الخطاب أتاه سهم فقتله','12','0','0');
INSERT INTO [t6615] VALUES (11,'استشهاد أبي أنسة قال علي بن محمد عن عبد العزيز بن أبي ثابت عن داود بن حصين عن عكرمة عن ابن عباس قال استشهد يوم بدر أبو أنسة مولى رسول الله صلى الله عليه وسلم','12','0','0');
INSERT INTO [t6615] VALUES (12,'من بني الخزرج ومن بني الخزرج يزيد بن الحارث الذي يقال له ابن فسحم قتله نوفل بن معاوية','12','0','0');
INSERT INTO [t6615] VALUES (13,'من بني حبيب ومن بني حبيب بن عبد حارثة رافع بن المعلى قتله عكرمة بن أبي جهل','12','0','0');
INSERT INTO [t6615] VALUES (14,'من بني عدي ومن بني عدي بن النجار حارثة بن سراقة بن الحارث قتله حبان بن العرقة بسهم وهو يشرب من الحوض','13','0','0');
INSERT INTO [t6615] VALUES (15,'سرية عبيدة إلى سيف البحر حدثنا خليفة قال نا بكر عن ابن إسحاق ووهب عن أبيه عن ابن إسحاق قال أول سرية بعث فيها عبيدة بن الحارث في شهر ربيع الأول في ثمانين راكبا ويقال ستين من المهجرين ليس فيهم من الأنصار أحد فبلغ سيف البحر حتى بلغ أحيا ماء بالحجاز بأسفل','13','0','0');
INSERT INTO [t6615] VALUES (16,'سرية حمزة إلى سيف البحر قال علي بن محمد أول سرية بعثها رسول صلى الله عليه وسلم حمزة بن عبد المطلب في شهر ربيع الأول من سنة أثنتين إلى سيف البحر من ناحية بالعيص من أرض جهينة','14','0','0');
INSERT INTO [t6615] VALUES (17,'سرية سعد بن مالك قال ابن إسحاق ثم سرية سعد بن مالك في ثمانية رهط من المهاجرين فخرج حتى أتى الخرار من أرض الحجاز فلم يلق كيدا قال ابن إسحاق وبعث','14','0','0');
INSERT INTO [t6615] VALUES (18,'بعث عبد الله بن غالب الليثي قال ابن إسحاق وبعث رسول الله صلى الله عليه وسلم عبد الله بن غالب الليثي في شوال','15','0','0');
INSERT INTO [t6615] VALUES (19,'تجسس خبر عير أبي سفيان','15','0','0');
INSERT INTO [t6615] VALUES (20,'صرف القبلة إلى الكعبة وفي هذه السنة وهي سنة اثنتين صرفت القبلة يقول الله لنبيه محمد صلى الله عليه وسلم','15','0','0');
INSERT INTO [t6615] VALUES (21,'حصل بني قينقاع قال ابن إسحاق وكان فيما بين ذلك من غزواته أمر بني قينقاع قال ابن إسحاق فحدثني عاصم بن عمر بن قتادة أن بني قينقاع كانوا أول يهود نقضوا ما بينهم وبين رسول الله صلى الله عليه وسلم فيما بين بدر وأحد فحاصرهم رسول الله صلى الله عليه وسلم حتى','18','0','0');
INSERT INTO [t6615] VALUES (22,'غزوة أحد','19','0','0');
INSERT INTO [t6615] VALUES (23,'تسمية من استشهد مع رسول الله صلى الله عليه وسلم يوم أحد','20','0','0');
INSERT INTO [t6615] VALUES (24,'من بني أمية ومن بني امية بن عبد شمس عبد الله بن جحش بن رئاب حليل لهم من بني أسد بن خزيمة','20','0','0');
INSERT INTO [t6615] VALUES (25,'من بني عبد الدار ومن بني عبد الدار مصعب بن عمير بن هاشم قتله ابن قمئة الليثي','21','0','0');
INSERT INTO [t6615] VALUES (26,'من بني ظفر ومن بني ظفر يزيد بن حاطب بن أمية بن رافع رجل','21','0','0');
