INSERT INTO [t6615] VALUES (28,'من بني السلم ومن بني السلم ابن امرئ القيس بن مالك بن الأوس خيثمة أبو سعد بن خيثمة رجل','22','0','0');
INSERT INTO [t6615] VALUES (29,'من بني معاوية ومن بني معاوية بن مالك سبيع بن حاطب بن الحارث بن قيس بن هيشة','22','0','0');
INSERT INTO [t6615] VALUES (30,'من بني عمرو بن مبذول ومن بني عمرو بن مبذول أبو هبيرة بن الحارث بن علقمة بن عمرو بن ثقف بن مالك بن مبذول وعمرو بن مطرف بن علقمة بن عمرو رجلان','23','0','0');
