INSERT INTO [t6615] VALUES (116,'فتح حلوان والماهات وما سبذان حدثني محمد بن معاوية قال قال أبو عبيدة عن السمري وجه سعد جرير بن عبد الله البجلي إلى حلوان بعد جلولاء فافتتحها عنوة','91','0','0');
INSERT INTO [t6615] VALUES (117,'فتح جند يسابور والسوس وفيها افتتح جند يسابور والسوس صلحهم صالحههم أبو موسى ثم رجع إلى الأهواز','92','0','0');
INSERT INTO [t6615] VALUES (118,'فتح تستر وحدثني الوليد بن هشام عن ابيه عن جده قال افتتح ابو موسى عامة رامهرمز ثم سار أبو موسى إلى تستر فأقام عليها','92','0','0');
INSERT INTO [t6615] VALUES (119,'فتح ريشهر قال ابو عبيدة وفيها حاصر هرم بن حيان أهل ريشهر فرأى ملكهم امرأة تأكل ولدها فقال الآن أصالح العرب فصالح هرما على أن خلى لهم المدينة','93','0','0');
