INSERT INTO [t6615] VALUES (250,'من بني مازن ومن بني مازن بن النجار عمرو بن تميم بن غزية ونعمان بن عمرو بن سعد ابن عمرو بن غزية وسعد وجعفر ابنا أبي داود بن عمير بن مالك وعبد الله بن زيد بن عاصم وابنه أبو حسن وعبد الله بن حارث بن عبد الله بن كعب وأخواه','200','0','0');
INSERT INTO [t6615] VALUES (251,'من بني الحرث ومن بني الحرث بن الخزرج عبد الرحمن بن خبيب بن إساف ومحمد بن عبد الرحمن بن خالد بن إساف ومحمد وعبد الله ابنا خالد بن إساف وعبيد الله بن أنيس بن سكن بن إساف وسعد بن كليب بن إساف ومحمد ويحيى وعبد الله بنو ثابت بن قيس بن شماس وأبو نعيم بن أبي','201','0','0');
