INSERT INTO [t6615] VALUES (256,'ابن الزبير يرفض مبايعة يزيد قال أبو الحسن عن رجل من أهل مكة عن صالح بن كيسان عن عبد العزيز بن مروان قال بعث يزيد ابن عضاه الأشعري إلى ابن الزبير يدعوه لبيعته ومعه جامعة','203','0','0');
