INSERT INTO [t6624] VALUES (2,'بسم الله الرحمن الرحيم','1','1','0');
INSERT INTO [t6624] VALUES (3,'لا يعلم إلا من التوراة والتوراة مختلفة على ثلاثة نسخ كما سيأتي وما بين وفاة موسى إلى ابتداء ملك بختنصر يعلم من المنجمين قال أبو عيسى ويعلم من قرانات زحل والمشترى في المثلثات وهم أيضا مختلفون في ذلك ويعلم أيضا من سفر قضاة بني','2','1','0');
INSERT INTO [t6624] VALUES (4,'الفصل الأول','7','1','0');
INSERT INTO [t6624] VALUES (5,'في عمود التواريخ القديمة وذكر الأنبياء عليهم السلام على الترتيب','7','1','0');
INSERT INTO [t6624] VALUES (6,'آدم وبنيه إلى نوح','7','1','0');
INSERT INTO [t6624] VALUES (7,'أن الله تعالى خلق آدم من قبضة قبضها من جميع الأرض فجاء بنو آدم على قدر الأرض منهم الأسود والأحمر والأبيض وبين ذلك ومنهم السهل والحزن وبين ذلك آدم أي من أديم الأرض خلق الله جسده وتركه أربعين ليلة وقيل أربعين سنة ملقى بغير روح','7','1','0');
INSERT INTO [t6624] VALUES (8,'ذكر نوح وولده','9','1','0');
INSERT INTO [t6624] VALUES (9,'لمضي ألف وإحدى وثمانين للطوفان جملة أعمار المذكورين عاش سام ستمائة فتكون وفاته بعد وفاة نوح بمائة وخمسين سنة وعاش أرفخشذ أربعمائة وخمسا وستين وقينان أربعمائة وثلاثين وشالخ أربعمائة وستين وغابر أربعمائة وأربعا وستين وفالغ','10','1','0');
INSERT INTO [t6624] VALUES (10,'ذكر هود وصالح','11','1','0');
INSERT INTO [t6624] VALUES (11,'ذكر إبراهيم','11','1','0');
INSERT INTO [t6624] VALUES (12,'هو إبراهيم بن تارخ وهو آزر بن ناخور بن ساروغ بن أرغو بن فالغ بن غابر بن','11','1','0');
INSERT INTO [t6624] VALUES (13,'أنها أمثال منها أيها المسلط المغرور إني لم أبعثك لتجمع الدنيا بعضها على بعض ولكن بعثتك لترد عني دعوة المظلوم فإني لا أردها ولو كانت من كافر وعلى العاقل أن يكون بصيرا بزمانه مقبلا على شانه حافظا للسانه ومن عد كلامه من عمله قل كلامه إلا','13','1','0');
INSERT INTO [t6624] VALUES (14,'وبناء إبراهيم الكعبة بعد ما مضى مائة سنة من عمره فبين ذلك وبين الهجرة ألفان وسبعمائة وثلاث وتسعون سنة تقريبا وأرسل الله إسماعيل إلى قبائل اليمن وإلى العماليق وزوج إسماعيل ابنته من ابن أخيه العيص بن إسحاق وعاش إسماعيل مائة وسبعة','14','1','0');
INSERT INTO [t6624] VALUES (15,'قد عد من أمة الروم لأنه من ولد العيص هو أيوب بن موص بن رازح بن العيص بن إسحاق بن إبراهيم وزوجة أيوب رحمة وكان لأيوب البثنة من أعمال دمشق ملكا وأموالا عظيمة فابتلي بذهاب الأموال وبالفقر وهو على عبادته وشكره ثم ابتلي في جسده حتى','14','1','0');
INSERT INTO [t6624] VALUES (16,'ذكر يوسف عليه السلام','15','1','0');
INSERT INTO [t6624] VALUES (17,'ذكر حكام بني إسرائيل ثم ملوكهم','18','1','0');
INSERT INTO [t6624] VALUES (18,'عثنئال بعين مهملة وثاء مثلثة ساكنة ونون مكسورة ومثناة تحت مهموزة وألف ولام وبعده أكثر بنو إسرائيل المعاصي وعبدوا الأصنام فسلط عليهم عغلون ملك ماب من ولد لوط واستعبدهم فاستغاثوا إلى الله تعالى وبقوا تحت مضايقته ثمان عشرة سنة','19','1','0');
INSERT INTO [t6624] VALUES (19,'ذكر زكريا ويحيى عليهما السلام','28','1','0');
INSERT INTO [t6624] VALUES (20,'ذكر عيسى ابن مريم عليهما السلام','28','1','0');
INSERT INTO [t6624] VALUES (21,'خمسمائة وخمس وأربعون سنة تقريبا وكانت ولادة المسيح أيضا لمضي ثلاث وثلاثين سنة من أول ملك أغسطس ولمضي إحدى وعشرين سنة من غلبته على قلوبطرا لأن أغسطس لمضي اثنتي عشرة سنة من ملكه سار من رومية وملك ديار مصر وقتل قلوابطرا ملكه اليونان وبعد إحدى','30','1','0');
