INSERT INTO [t6624] VALUES (27,'لأربع وعشرين سنة من ملكه ولذلك ولد النبي','42','1','0');
INSERT INTO [t6624] VALUES (28,'من مكة إلى المدينة وهلك برويز لمضي خمس سنين وستة أشهر وخمسة عشر يوما للهجرة لأن من السنة الثانية والأربعين من ملك أنوشروان وهي سنة مولد','43','1','0');
INSERT INTO [t6624] VALUES (29,'إلى نصف السنة الثالثة والثلاثين من ملك برويز وهي عام الهجرة ثلاثا وخمسين سنة بيانه أن رسول الله','44','1','0');
INSERT INTO [t6624] VALUES (30,'وهو ابن ثلاث وخمسين سنة فيكون له','44','1','0');
INSERT INTO [t6624] VALUES (31,'الفصل الثالث','46','1','0');
INSERT INTO [t6624] VALUES (32,'في ذكر فراعنة مصر ثم ملوك اليونان ثم الروم','46','1','0');
INSERT INTO [t6624] VALUES (33,'هو وهب سارة هاجر كان يسكن الفرما ثم ملكت بعده أخته جورساق ثم بعدها زلفا بنت مامون سمع عمالقة الشام بضعفها فغزوها وملكوا مصر وصارت الدولة للعمالقة والذي أخذ منها الملك الوليد بن دبيع العملاقي عابد البقرة قتله أسد في صيده وقيل هو أول','46','1','0');
INSERT INTO [t6624] VALUES (34,'منه بأن التقطته آسية امرأة فرعون وحمته منه وتزعم اليهود أن بنت فرعون هي التي التقطته لا زوجته والأصح أنها زوجته كما نطق القرآن ولما أظهر الآيات لفرعون وسلم إليه بني إسرائيل وسار بهم ندم ولحقهم عند بحر القلزم فضرب موسى بعصاه البحر فصار فيه اثنا','47','1','0');
