INSERT INTO [t6624] VALUES (39,'وأسلم ثم صارت اليمن للإسلام','56','1','0');
INSERT INTO [t6624] VALUES (40,'أول ملك من العرب بأرض الجزيرة مالك بن فهم بن غنم بن دوس بن عدنان بن عبد الله بن وهران بن كعب بن الحارث بن كعب بن مالك بن النضر بن الأزد من ولد كهلان بن سبا بن يشجب بن يعرب بن قحطان كان ملك مالك في أيام ملوك الطوائف قبل الأكاسرة','56','1','0');
INSERT INTO [t6624] VALUES (41,'ذكر ابتداء ملك اللخميين','57','1','0');
INSERT INTO [t6624] VALUES (42,'ثم ملك بعده أخوه المنذر بن المنذر ثم ابنه النعمان بن المنذر بن المنذر بن ماء السماء وكنيته أبو قابوس وهو الذي تنصر وأمه سلمى بنت وائل بن عطية الصائغ من أهل فدك وملك اثنتين وعشرين سنة وقتله كسرى برويز وسبب قتله وقعة ذي قار بين الفرس','58','1','0');
