INSERT INTO [t6624] VALUES (477,'قصد هولاكو الشام','562','414','0');
INSERT INTO [t6624] VALUES (478,'سلطنة الحلبي بدمشق','570','414','0');
INSERT INTO [t6624] VALUES (479,'قبض الملك السعيد وعود التتر','570','414','0');
INSERT INTO [t6624] VALUES (480,'ففرغ في أربع سنين والله أعلم وفيها في تاسع عشر ربيع الآخر هلك هولاكو بن طلو بن جنكيزخان وترك خمسة عشر ابنا وملك بعده ابنه أبغا البلاد التي كانت بيد أبيه وهي أقليم خراسان وكرسيه نيسابور وإقليم عراق العجم وتعرف ببلاد الجبل وكرسيه','577','414','0');
INSERT INTO [t6624] VALUES (481,'وأماننا لأخينا السلطان الملك المظفر شمس الدين يوسف بن عمر صاحب اليمن إننا راعون له ولأولاده مسالمون من سالمهم معادون من عاداهم ونحو ذلك وأرسل إليه هدية من أسلاب التتر وخيلهم وفيها مات منكوتمر بن هولاكو بن طلو بن جنكيزخان بجزيرة','588','414','0');
INSERT INTO [t6624] VALUES (482,'فتح حموص وغيرها','601','414','0');
INSERT INTO [t6624] VALUES (483,'ذكر المتجددات بعد الكسرة','606','414','0');
INSERT INTO [t6624] VALUES (484,'قدوم قبجق إلى حماه','611','414','0');
INSERT INTO [t6624] VALUES (485,'ثم دخلت سنة خمس وسبعمائة في المحرم منها أرسل قرا سنقر نائب حلب مع مملوكه قشتمر عسكرا إلى سيس وكان قشتمر ضعيف العقل مشتغلا بالخمر فاستهان بالعدو فجمع صاحب سيس سنباط من الأرمن والفرنج والتتر ووصلوا إلى غزة وقاتلوهم قرب إياس فانهزم','612','414','0');
INSERT INTO [t6624] VALUES (486,'ألقى الله في قلوبهم الرعب وهزمهم قلت ما ذكروا المصطفى بسوء إلا وسيق البلا إليهم فحبه رحمة علينا وسبه نقمة عليهم وقاسى العسكر في هدم الأبراج مشقة فإنها كانت مكلبة بحديد ورصاص وعرض السور ذراعا بالنجاري ونقبت','630','414','0');
INSERT INTO [t6624] VALUES (487,'أشعارا وما أرق قوله لا تسأل يا حبيب قلبي ما تم علي في هواكا العرض فقد صلوت عنه والنفس جعلتها فداكا وقوله دو بيت يا عصر شبابي المفدي أرأيت ما أسرع ما بعدت عني ونأيت قد كنت مساعدي على كيت وكيت واليوم','632','414','0');
INSERT INTO [t6624] VALUES (488,'فجاء نقيب الحكم بدر الدين محمد بن نجم الدين إسحاق وأزاح العقرب عن كم النبي','635','414','0');
INSERT INTO [t6624] VALUES (489,'لا تشد الرحال إلا إلى ثلاثة مساجد مع اعترافه بأن الزيارة بلا شد رحل قربة فشنعوا عليه بها وكتب فيها جماعة بأنه يلزم من منعه شائبة تنقيص للنبوة فيكفر بذلك وأفتى عدة بأنه مخطئ بذلك خطأ المجتهدين المغفور لهم ووافقه جماعة وكبرت القضية','645','414','0');
INSERT INTO [t6624] VALUES (490,'أمر بقتل الكلاب مرة ثم صح أنه نهى عن قتلها قال واستقر الشرع عليه على التفصيل المعروف فأمر بقتل الأسود إليهم وكان هذا في الإبتداء وهو الآن منسوخ هذا كلام إمام الحرمين ولا مزيد على تحقيقه والله أعلم','648','414','0');
INSERT INTO [t6624] VALUES (491,'رد عليه السلام من الحجرة وقال وعليك السلام يا مهنا ثم عاد إلى الفوعة وأقام بها إلى أن توفي رحمه الله تعالى في المحرم سنة أربع وثمانين وستمائة','668','414','0');
INSERT INTO [t6624] VALUES (492,'نبينا وعليه وسلم فارتاب في ذلك فأقدم على فتح الباب المذكور بعد أن نهي عن ذلك فوجد بابا عليه تأزير رخام أبيض ووجد في ذلك تابوت رخام أبيض فوقه رخامة تبيضاء مربعة فرفعت الرخامة عن التابوت فإذا فيها بعض جمجمة فهرب الحاضرون هيبة لها ثم رد','671','414','0');
INSERT INTO [t6624] VALUES (493,'فيمن صام الدهر لا صام ولا أفطر على أنه دعاء عليه وفي حق من نذور ولم يتضرر خمسة أقوال الوجوب وهو إختيار أكثر الأصاحب والإباحة والكراهة والتحريم وفي حق من يتضرر بأن تفوته السنن أو الاجتماع بالأهل ثلاثة أقوال التحريم والكراهة والإباحة ولا يجيء','676','414','0');
INSERT INTO [t6624] VALUES (494,'مالك عن نافع عن ابن عمر وابن عباس وابن عمر رضي الله عنهم عن نبينا سيد المرسلين محمد بن عبد الله بن عبد المطلب','677','414','0');
INSERT INTO [t6624] VALUES (495,'وفيه ورد الخبر إلى حلب أن الشيخ تقي الدين علي بن السبكي تولى قضاء القضاة الشافعية بدمشق المحروسة بعد أن حدث الخطيب بدر الدين محمد بن القاضي جلال الدين نفسه بذلك وجزم به وقبل الهناء فقال فيه بعض أهل دمشق قد سبك السبكي قلب الخطيب','681','414','0');
INSERT INTO [t6624] VALUES (496,'وفيها في العشرين من شهر رجب توفي بجبرين الشيخ محمد بن الشيخ نبهان كان له القبول التام عند الخاص والعام وناهيك أن طشتمر حمص أخضر على قوة نفسه وشممه وقف على زاويته بجبرين حصة من قرية حريثان لها مغل جيد وبالجملة فكأنما ماتت بموته مكارم','693','414','0');
INSERT INTO [t6624] VALUES (497,'حسن الخظ وله نظم كان كاتبا ثم صار داوندار قبجق بحماه ثم شاد الدواوين بحلب ثم حاجبا بها ثم دواتدار الملك الناصر ثم نائبا بالإسكندرية ثم أميرا بحلب وشاد المال والوقف ثم أميرا بطرابلس رحمه الله تعالى وفيها في شعبان بلغنا وفاة الشيخ','695','414','0');
INSERT INTO [t6624] VALUES (498,'فمن أعدى الأول استرسل ثعبانه وانساب وسمي طاعون الأنساب وهو سادس طاعون وقع في الإسلام وعندي أنه الموتان الذي أنذر به نبينا عليه أفضل الصلاة والسلام كان وكان أعوذ بالله ربي من شر طاعون النسب باروده المستعلي قد طار في الأقطار','706','414','0');
