INSERT INTO [t6744] VALUES (5,'في المنام وقال له ما معناه إنه من قرأ عليه دخل الجنة وقد أخذ عنه لذلك غير واحد من أهل العلم وقال الخزرجي في طبقات أهل اليمن كان فقيها عالما صالحا عارفا بالفقه والحديث والتفسير واللغة والنحو والعروض قرأ النحو على ابن بصيبص','62','1','0');
INSERT INTO [t6744] VALUES (6,'أبو عبد الله من قرطبة قال ابن الفرضي كان الغالب عليه حفظ اللغة ورواية الحديث ثقة مأمونا ولم يكن عنده كبير علم بالفقه رحل فحج ودخل البصرة فسمع من بندار وغيره من أهل الحديث ولقي بها أبا حاتم السجستاني والعباس بن الفرج','160','1','0');
INSERT INTO [t6744] VALUES (7,'في المنام فقال لي اصطلح مع محمد البكري مات سنة أربع وسبعين وسبعمائة وهو يصلي الصبح','204','1','0');
INSERT INTO [t6744] VALUES (8,'فهو مولى مولى مولى ثم ادعى أبو بكرة انه ثقفي وادعى سليمان أنه تميمي وادعى ابن مناذر أنه من بني صبيرة بن يربوع فهو دعي مولى دعي مولى دعي وهذا مما لم يجتمع في غيره','250','1','0');
INSERT INTO [t6744] VALUES (9,'باب الأحمدين','291','1','0');
INSERT INTO [t6744] VALUES (10,'وكتاب حلية الفقهاء ومسائل في اللغة يغالي بها الفقهاء ومنه اقتبس الحريري صاحب المقامات ذلك الأسلوب ووضع المسائل الفقهية في المقامة الحربية وهي مائة مسألة وغير ذلك قال الذهبي مات سنة خمس وتسعين وثلاثمائة بالري وهو أصح ما','352','1','0');
INSERT INTO [t6744] VALUES (11,'تلك الليلة فقال لي أقرئ أبا العباس مني السلام وقل له أنت صاحب العلم المستطيل قال لي أبو عمر الزاهد سئل ثعلب عن شيء فقال لا أدري فقيل له أتقول لا أدري وإليك تضرب اكباد الإبل من كل بلد فقال لو كان لأمك بعدد ما لا أدري','397','1','0');
