INSERT INTO [t674] VALUES (1,'١ - قال صاحب كتاب كليلة ودمنة: يقال من صفة الناسك الوقار والاستتار بالقنوع ورفض الشهوات للتخلي من الأحزان وترك إخافة الناس لئلا يخافهم.','1','0','0');
INSERT INTO [t674] VALUES (2,'٢ - قال صاحب الكتاب: ويقال الأخلاط في الإنسان أربعة متعادية متغالبة تغذوهن الحياة، والحياة إلى نفاد، كالصنم المفصلة أعضاؤه يجمعها مسمار واحد، فإذا نزع المسمار تساقطت.','1','0','0');
INSERT INTO [t674] VALUES (3,'٣ - قال صاحب الكتاب: يقال الجامع للأهل والأقارب بغير اقتصاد كالدخنة تحترق ويذهب بريحها غيرها.','1','0','0');
INSERT INTO [t674] VALUES (4,'٤ - قال صاحب الكتاب: الدنيا كالماء الملح الذي كلما ازداد منه صاحبه شربا ازداد عطشا.','1','0','0');
INSERT INTO [t674] VALUES (5,'٥ - قال صاحب الكتاب: أو كالعظم يصيبه الكلب يجد فيه رائحة اللحم فيطلبها فتدمي فاه فلا يزداد لها طلبا الا ازداد لفيه ادماء.','1','0','0');
INSERT INTO [t674] VALUES (6,'٦ - قال صاحب الكتاب: أو كاللعقة من العسل وفي أسفلها سم ذعاف، فلذائقها حلاوة عاجلة وفي أسفلها سم ناقع.','1','0','0');
INSERT INTO [t674] VALUES (7,'٧ - قال صاحب الكتاب: أو كدودة الإبريسم التي كلما ازدادت على نفسها لفا ازدادت من الخروج بعدا.','1','0','0');
INSERT INTO [t674] VALUES (8,'٨ - قال صاحب الكتاب: ويقال العاقل حقيق أن لا يغفل أمر آخرته والتزود لها، فإن الموت يأتي بغتة وليس بينه وبين أحد أجل معلوم.','1','0','0');
INSERT INTO [t674] VALUES (9,'٩ - قال صاحب الكتاب: ويقال العاقل يعد أبويه أصدقاء وإخوته رفقاء وأقاربه غرماء ويعد نفسه فريدا وحيدا.','1','0','0');
INSERT INTO [t674] VALUES (10,'١٠ - قال صاحب الكتاب: يقال يعد من البهائم من لم يكن له همة إلا بطنه أو فرجه من كان.','1','0','0');
INSERT INTO [t674] VALUES (11,'١١ - قال صاحب الكتاب: ويقال من عاش غير خامل ذا فضل على نفسه واصحابه فهو وان قل عمره طويل العمر.','1','0','0');
INSERT INTO [t674] VALUES (12,'١٢ - قال صاحب الكتاب: من كان عيشه في ضيق ووحدة وقلة خير على نفسه وعلى الناس فهو وان طال عمره قصير العمر.','2','0','0');
INSERT INTO [t674] VALUES (13,'١٣ - قال صاحب الكتاب: الارتفاع من ضعة المنزلة إلى شرفها عسير.','2','0','0');
INSERT INTO [t674] VALUES (14,'١٤ - قال صاحب الكتاب: لا يواظب على باب السلطان أحد فيطرح عنه الأنف ويحتمل الأذى ويكظم الغيظ ويرفق بالناس الا بلغ حاجته من السلطان.','2','0','0');
INSERT INTO [t674] VALUES (15,'١٥ - قال صاحب الكتاب: ويقال في أمور ثلاثة لا يجترئ عليها الا أهوج ولا يسلم الاقليل؛ صحبة السلطان وركوب البحر وشرب السم للتجربة.','2','0','0');
INSERT INTO [t674] VALUES (16,'١٦ - قال صاحب الكتاب: ويقال شبهت العلماء السلطان بالجبل الصعب الذي فيه جميع الثمار الطيبة وهو معدن السباع وجميع الهوام.','2','0','0');
INSERT INTO [t674] VALUES (17,'١٧ - قال صاحب الكتاب: ويقال لن للعدو حتى تمكن الفرصة.','2','0','0');
INSERT INTO [t674] VALUES (18,'١٨ - قال صاحب الكتاب: يقال من سكرات السلطان الرضا عمن يستوجب السخط والسخط عمن يستوجب الرضا.','2','0','0');
INSERT INTO [t674] VALUES (19,'١٩ - قال صاحب الكتاب: ويقال من ضم الدر والياقوت بالرصاص فليس ذلك تقصيرا بهما ولكنه جهل ممن فعله.','2','0','0');
INSERT INTO [t674] VALUES (20,'٢٠ - قال صاحب الكتاب: ويقال لا تحقرن رجلا صغير المنزلة فان الصغير ربما عظم وبلغ، كالعقب من الميتة يؤخذ فيعمل منه القوس والوتر والترس فيحتاج إليه الملك في بعض حالاته من لهو أو بأس.','2','0','0');
INSERT INTO [t674] VALUES (21,'٢١ - قال صاحب الكتاب: ويقال العاقل لا يدع استشارة عدو إذا كان ذا رأي في الأمر الذي يشركه في ضره أو نفعه.','2','0','0');
INSERT INTO [t674] VALUES (22,'٢٢ - قال صاحب الكتاب: يقال إذا لقي اللاقي عدوه في المواطن التي يعلم أنه غير ناج منه فيها كان حقيقيا بالمقاتلة كرما وحفاظا.','3','0','0');
INSERT INTO [t674] VALUES (23,'٢٣ - قال صاحب الكتاب: ويقال الرجال ثلاثة حليم وحازم وعاجز؛ فالحليم إذا نزل به الأمر من البلاء لم يدهش ولم يذهب قلبه شعاعا ولم يعي برأيه وحيلته التي بها يرجو المخرج، والحازم الذي يتقدم في العدة للامر يتخوفه قبل نزوله به، والعاجز لا يزال في تردد وتحير والتباس حتى يهلك.','3','0','0');
INSERT INTO [t674] VALUES (24,'٢٤ - قال صاحب الكتاب: ويقال من هذا الذي بلغ من الدنيا جسيما فلم يبطر.','3','0','0');
INSERT INTO [t674] VALUES (25,'٢٥ - قال صاحب الكتاب: ويقال من تبع الهوى فلم يعثر ومن جاور النساء فلم يفتن ومن صاحب الأشرار فسلم ومن خدم الملوك فلم يعطب.','3','0','0');
INSERT INTO [t674] VALUES (26,'٢٦ - قال صاحب الكتاب: ومن صحب الملوك فدام له منهم الاحسان.','3','0','0');
INSERT INTO [t674] VALUES (27,'٢٧ - قال صاحب الكتاب: ومن طلب معروف اللئام فلم يهن.','3','0','0');
INSERT INTO [t674] VALUES (28,'٢٨ - قال صاحب الكتاب: ويقال مصاحبة الأشرار تورث صاحبها سوء ظن الأخيار.','3','0','0');
INSERT INTO [t674] VALUES (29,'٢٩ - قال صاحب الكتاب: ويقال العجب كل العجب من رجل تطلب رضاه قلا يرضى، وأعجب من ذلك أن ترضيه فيسخط.','3','0','0');
INSERT INTO [t674] VALUES (30,'٣٠ - قال صاحب الكتاب: إذا كانت الموجدة عن علة كان الرضا موجودا، وإذا كانت عن غير علة كان معدوما.','3','0','0');
INSERT INTO [t674] VALUES (31,'٣١ - قال صاحب الكتاب: ويقال من التمس الرخص من الإخوان عند المشاورة ومن الأطباء عند العلة ومن الفقهاء عند الشبهة لم يخطأ الرأي وازداد علة ومرضا.','3','0','0');
INSERT INTO [t674] VALUES (32,'٣٢ - قال صاحب الكتاب: يقال ربما كان الهلاك في بعض ما يقسم من الفضل كالشجرة الطيبة الثمرة هلاكها في طيب ثمرتها، وكالطاووس الذي ربما كان ذنبه وبالا عليه عندما يحتاج إلى الخفة فيثقله ويقعد به حتى يهلكه وهو حسنه.','3','0','0');
INSERT INTO [t674] VALUES (33,'٣٣ - قال صاحب الكتاب: ويقال الفرس الجواد القوي الوطيء الفاره يطال اقتعاده وإتعابه لفضل ما عنده فيكون في سبب هلاكه.','3','0','0');
INSERT INTO [t674] VALUES (34,'٣٤ - قال صاحب الكتاب: ويقال ذو المروءة ربما هلاكه في مروءته لاحتماله ولكظمه مالا يكظم عليه.','4','0','0');
INSERT INTO [t674] VALUES (35,'٣٥ - قال صاحب الكتاب: ويقال من بذل نصيحته وشفعته لمن لا يستمع منه ومن لا يشكر له إن سمع، كان كمن يبذر زرعه في السباخ.','4','0','0');
INSERT INTO [t674] VALUES (36,'٣٦ - قال صاحب الكتاب: ويقال الماء ألين من القول بالحجر أشد من القلب.','4','0','0');
INSERT INTO [t674] VALUES (37,'٣٧ - قال صاحب الكتاب: ويقال النجدة يدركها الزلل مع خطأ الرأي والرأي يجزئ بلا نجدة والبأس والنجدة لا يستغنيان عن الرأي.','4','0','0');
INSERT INTO [t674] VALUES (38,'٣٨ - قال صاحب الكتاب: ويقال لا خير في القول إلا مع الفعل ولا في الفقه إلا مع الورع ولا في الصدقة إلا مع النية.','4','0','0');
INSERT INTO [t674] VALUES (39,'٣٩ - قال صاحب الكتاب: ويقال الأدب يذهب غي السكر ويزيد الأنوك سكرا كما ان النهار يزيد كل ذي بصر بصرا ويزيد الخفاش وأمثاله عمى.','4','0','0');
INSERT INTO [t674] VALUES (40,'٤٠ - قال صاحب الكتاب: ويقال العاقل لا يبطر لمنزلة أصابها وإن عظم ذلك كالجبل الذي لا تزلزله الرياح وإن اشتدت وعظمت عليه.','4','0','0');
INSERT INTO [t674] VALUES (41,'٤١ - قال صاحب الكتاب: ويقال من الخرق والحمق أن يعامل الإخوان بغير وفاء ويطلب الفوز بالرياء ومودة النساء بالغلظة ونفع الناس بمضرة الناس وبلوغ الفضل بالخفض والدعة.','4','0','0');
INSERT INTO [t674] VALUES (42,'٤٢ - قال صاحب الكتاب: ويقال لا تلتمس تقويم ما لا يستقيم ولا تعالجن تأديب من لا يتأدب فإن الحجر لا يجرب بالسيوف والعظم لا يعالج انحناؤه ولا تقويمه.','5','0','0');
INSERT INTO [t674] VALUES (43,'٤٣ - قال صاحب الكتاب: ويقال رب متحمل أوقعه تمحله في ورطة ومن لم يتثبت في الحيل اوقعته حيلته في أشد مما يحتال له والحيلة اجزى من القوة.','5','0','0');
INSERT INTO [t674] VALUES (44,'٤٤ - قال صاحب الكتاب: ويقال عذوبة الأنهار ما لم تنته إلى البحار وصلاح أهل البيت ما لم يدخل بينهم مفسد وتقارب الإخوان ما لم يدخل بينهم السعاة.','5','0','0');
INSERT INTO [t674] VALUES (45,'٤٥ - قال صاحب الكتاب: ويقال لا يود رجل رجلا ولا يبغضه إلا وجد له الآخر مثل ذلك علم أو لم يعلم.','5','0','0');
INSERT INTO [t674] VALUES (46,'٤٦ - قال صاحب الكتاب: ويقال السبب الذي يدرك به العاجز حاجته هو الذي يحول بين الحازم وبين طلبته.','5','0','0');
INSERT INTO [t674] VALUES (47,'٤٧ - قال صاحب الكتاب: يقال المودة بين الصالحين بطيء انقطاعها سريع اتصالها مثل ذلك مثل كوز الذهب البطيء الانكسار الهين الإصلاح.','5','0','0');
INSERT INTO [t674] VALUES (48,'٤٨ - قال صاحب الكتاب: ويقال الكريم يود عن لقائه مرة واحدة ومعرفة يوم، واللئيم لا يصل أحدا إلا عن رغبة أو رهبة.','6','0','0');
INSERT INTO [t674] VALUES (49,'٤٩ - قال صاحب الكتاب: ويقال أهل الدنيا يتعاطون فيما بينهم خلتين يتواصلون عليهما: ذات النفس وذات اليد.','6','0','0');
INSERT INTO [t674] VALUES (50,'٥٠ - قال صاحب الكتاب: ويقال من كان إنما يصنع المعروف التماس الجزاء فإنما مثله مثل الصياد يبذل من إلقائه الحب للطير لا يريد به نفعها ولكنه يريد بذلك نفع نفسه.','6','0','0');
INSERT INTO [t674] VALUES (51,'٥٢ - قال صاحب الكتاب: ويقال الخرس خير من البيان بالكذب والعي خير من الهذر والفاقة خير من السعة من أموال الناس والحرص والشره يؤديان أصحابهما إلى البلاء.','6','0','0');
INSERT INTO [t674] VALUES (52,'٥٣ - قال صاحب الكتاب: ويقال لا حسب مثل الخلق ولا غنى مثل القنوع وأحق ما صبر عليه ما ليس إلى غيره سبيل.','6','0','0');
INSERT INTO [t674] VALUES (53,'٥٤ - قال صاحب الكتاب: ويقال يختبر ذو البأس عند اللقاء وذو الأمانة عند الأخذ والإعطاء والإخوان عند النوائب والأهل والولد عند الفاقة.','6','0','0');
INSERT INTO [t674] VALUES (54,'٥٥ - قال صاحب الكتاب: ويقال قلما ظفر أحد ببغي وقلما حرص على النساء فلم يفتضح وقلما أكثر الطعام فلم يتخم وقلما ابتلي بوزراء السوء فلم يهلك.','6','0','0');
INSERT INTO [t674] VALUES (55,'٥٧ - قال صاحب الكتاب: ويقال إذا لم يستطع الرجل نيل عظيم الا باحتمال صغير كان حقيقا باحتماله.','7','0','0');
INSERT INTO [t674] VALUES (56,'٥٨ - قال صاحب الكتاب: ويقال ذو العقل يقل الكلام ويبالغ في الفعل ويعترف بالزلة ويتأتى في الأمور قبل الإقدام عليها ويستقيل هفوة غفلته بعقله كالذي يعثر بالأرض وعليها ينهض ويستمر.','7','0','0');
INSERT INTO [t674] VALUES (57,'٥٩ - قال صاحب الكتاب: ويقال لا يبلغ البلاء من ذي الرأي مجهود عقله ولا الرجاء مبلغا يبطره ويسكره ويعمي عليه أمره.','7','0','0');
INSERT INTO [t674] VALUES (58,'٦٠ - قال صاحب الكتاب: ويقال ليس أحد أبعد من الخير من اثنين منزلتهما واحدة وصفتهما مختلفة.','7','0','0');
INSERT INTO [t674] VALUES (59,'٦١ - قال صاحب الكتاب: ويقال الكريم لا يكون إلا شكورا غير حقود تنسيه الخلة من الإحسان الكثير من الإساءة.','7','0','0');
INSERT INTO [t674] VALUES (60,'٦٢ - قال صاحب الكتاب: ويقال اعجل العقوبات عقوبة الغدر واليمين الكاذبة ومن إذا تضرع إليه وسئل العفو لم يفعل.','7','0','0');
INSERT INTO [t674] VALUES (61,'٦٣ - قال صاحب الكتاب: ويقال الغادر مأخوذ بغدره وإن قصرت عنه عقوبة العاجل لم تقصر عنه عقوبة الآجل حتى إن العقوبة قد تدرك الأعقاب وأعقاب الأعقاب.','8','0','0');
INSERT INTO [t674] VALUES (62,'٦٤ - قال صاحب الكتاب: ويقال من كان ذا عقل كان على إماتة حقده أحرص منه على تربيته.','8','0','0');
INSERT INTO [t674] VALUES (63,'٦٥ - قال صاحب الكتاب: ويقال الأقدار وإن كانت مقدورة فليست تمنع الحازم من توقي المخوف والاحتراس مما يحترس منه.','8','0','0');
INSERT INTO [t674] VALUES (64,'٦٦ - قال صاحب الكتاب: ويقال الفاقة بلاء والسقم بلاء والغربة بلاء ورأس البلاء الهرم.','8','0','0');
INSERT INTO [t674] VALUES (65,'٦٧ - قال صاحب الكتاب: ويقال ليس أحد أعلم بما في نفس الموجع الحران ممن ذاق مثل ما به.','8','0','0');
INSERT INTO [t674] VALUES (66,'٦٨ - قال صاحب الكتاب: ويقال لا خير فيمن لا يستطيع كتمان ما في نفسه وإماتته حتى لا يذكر منه شيئا بل لا يكون له في نفسه موقع.','8','0','0');
INSERT INTO [t674] VALUES (67,'٦٩ - قال صاحب الكتاب: ويقال العاقل لا يخيف أحدا ما استطاع ولا يقيم على خوف وهو يجد مذهبا.','8','0','0');
INSERT INTO [t674] VALUES (68,'٧٠ - قال صاحب الكتاب: ويقال خلال من لزمهن بلغنه ما يريد وقربن له البعيد وآنسنه في الوحشة وعرفنه في الغربة ولين له المعيشة وأكثرن له الإخوان: كف الأذى وحسن السيرة وسعة الخلق وحسن الأدب والاقتصاد في العمل ومجانبة الريب.','8','0','0');
INSERT INTO [t674] VALUES (69,'٧١ - قال صاحب الكتاب: ويقال شر الأمور التي لا تتفق وشر الأزواج التي لا تواني وشر الولد العاصي وشر الإخوان الخاذل وشر الملوك الذي يخافه البريء وشر البلاد بلد لا أمن فيه.','8','0','0');
INSERT INTO [t674] VALUES (70,'٧٢ - قال صاحب الكتاب: ويقال ربما اتعظ الجاهل واعتبر بما يصيبه من المكروه من مثله فيرتدع أن يصيب أحدا بمثل ذلك.','9','0','0');
INSERT INTO [t674] VALUES (71,'٧٣ - قال صاحب الكتاب: ويقال اصبر من غيرك على ما مثل ما صبر عليه غيرك منك، فانه يقال كما تدين تدان.','9','0','0');
INSERT INTO [t674] VALUES (72,'٧٤ - قال صاحب الكتاب: ويقال من عمل بغير العدل والحق، انتقم منه وأديل عليه.','9','0','0');
INSERT INTO [t674] VALUES (73,'٧٥ - قال صاحب الكتاب: ويقال صحبة الأخيار تورث الخير وصجبة الاشرار تورث الشر ومثل ذلك مثل الريح التي إذا مرت بالنتن حملت نتنا ولإذا مرت بالطيب حملت طيبا.','9','0','0');
INSERT INTO [t674] VALUES (74,'٧٦ - قال صاحب الكتاب: ويقال أشياء لا ثبات لها ولا بقاء: خلة الأشرار وظل الغمام ومودة النساء والثناء الكاذب والتخلق.','9','0','0');
INSERT INTO [t674] VALUES (75,'٧٧ - قال صاحب الكتاب: ويقال تذكر الأحزان كالجرح المندمل تصيبه الضربة فيجتمع على صاحبه ألمان: ألم الضربة وألم انتقاض الجرح.','9','0','0');
INSERT INTO [t674] VALUES (76,'٧٨ - قال صاحب الكتاب: ويقال من تكلف من الأعمال ما ليس من عمله أوشك أن يضيع عمله.','10','0','0');
INSERT INTO [t674] VALUES (77,'٧٩ - قال صاحب الكتاب: ويقال الحر الكريم تنسيه الخلة الواحدة من الإحسان ألف خلة من الإساءة.','10','0','0');
INSERT INTO [t674] VALUES (78,'٨٠ - قال صاحب الكتاب: ويقال اللئيم يكفر ألف حسنة بسيئة واحدة تؤتى إليه.','10','0','0');
INSERT INTO [t674] VALUES (79,'٨١ - قال صاحب الكتاب: ويقال صاحب الدنيا يطلب ثلاثة أمور لن يدركها إلا بأربعة أشياء.','10','0','0');
INSERT INTO [t674] VALUES (80,'٨٢ - قال صاحب الكتاب: ويقال العاقل لايرحم من يخاف والحازم ربما أبعد الرجل الذي ثم أدناه لما يعلم عنده من الغناء والإجزاء فعل المتكاره على الدواء البشع الكريه رجاء منفعته.','11','0','0');
INSERT INTO [t674] VALUES (81,'٨٣ - قال صاحب الكتاب: يقال لا ينبغي للعاقل أن يغفل عن علم ما في نفس أهله وولده وإخوانه وأصدقائه في كل لحظة وحركة وكلمة وفي القيام والقعود وفي كل حالة، فإن هذه كلها شواهد لا يخفى معها ما تجن له القلوب.','11','0','0');
INSERT INTO [t674] VALUES (82,'٨٤ - قال صاحب الكتاب: ويقال لا يمنعن ذا العقل عداوة عدوه من مقاربته وإيناسه إذا طمع منه في دفع مخوف أو في جر مرغوب.','11','0','0');
INSERT INTO [t674] VALUES (83,'٨٥ - قال صاحب الكتاب: ويقال كثير من المودة ربما تحولت بغضاء وكثير من البغضاء ربما تحول مودة عن حوادث العلل والامور، وذو الرأي يحدث لما يحدث من ذلك رأيا.','11','0','0');
INSERT INTO [t674] VALUES (84,'٨٦ - قال صاحب الكتاب: ويقال ليس كل من أسأت إليه ينبغي ان تتخوف غشه وعدوانه وتيأس من نصيحته ومودته، ولكن ينبغي ان تنزل الناس في ذلك منازلهم على اختلاف طبقاتهم فإن منهم من إذا ظفرت بقطيعته فالرأي ان تغتنم ذلك وتمتنع من معاودته ومنهم من لا ينبغي تركه على حال من الأحوال.','11','0','0');
INSERT INTO [t674] VALUES (85,'٨٧ - قال صاحب الكتاب: ويقال خصال العاقل حقيق بالنظر فيهن والاحتيال لهن.','12','0','0');
INSERT INTO [t674] VALUES (86,'٨١ - قال صاحب الكتاب: ويقال الزم ذا العقل واسترسل إليه وإياك وفراقه ولا عليك أن تصحب العاقل وإن كان غير محمود الكرم.','12','0','0');
INSERT INTO [t674] VALUES (87,'٨٩ - قال صاحب الكتاب: ويقال افضل البر الرحمة وأفضل المودة الاسترسال وأفضل العقل ما يكون مما لا يكون وأفضل السرور طيب النفس وأفضل القنوع حسن الانصراف عما لا سبيل إليه.','12','0','0');
INSERT INTO [t674] VALUES (88,'٩٠ - قال صاحب الكتاب: لا ينبغي للملتمس العاقل ان يلتمس من الدنيا فوق الكفاف الذي يدفع به الأذى والحاجة عن نفسه وذلك يسير انما هو المطعم والمشرب إذا أعين بسعة بلد وسخاء نفس.','12','0','0');
INSERT INTO [t674] VALUES (89,'٩١ - قال صاحب الكتاب: ويقال لو ان رجلا وهبت له الدنيا وما فيها لم ينتفع إلا بالذي يدفع الحاجة عن نفسه.','13','0','0');
INSERT INTO [t674] VALUES (90,'٩٢ - قال صاحب الكتاب: ويقال الرجل ذو المروءة قد يكرم على غيرغنى كالأسد الذي يهاب وإن كان رابضا.','13','0','0');
INSERT INTO [t674] VALUES (91,'٩٣ - قال صاحب الكتاب: ويقال المال زيادة في القوة والرأي وليس الإخوان والأهل والأعوان إلا مع المال.','13','0','0');
INSERT INTO [t674] VALUES (92,'٩٤ - قال صاحب الكتاب: ويقال من لا مال له لا إخوان له ومن لا إخوان له لا أهل له ومن لا أهل له لا ولد ومن لا ولد له لا ذكر له ومن لا ذكر له لا عقل له ومن لا عقل له لا دنيا له ولا آخرة له، لأن الرجل إذا أصابه الضر قطعه إخوانه وشنأه أهله ورفضه ذوو رحمه وفقد عقله وخمل ذكره واضطرته المعيشة والتماس الرزق إلى التغرير بنفسه ودينه، فتهلك نفسه ويخسر آخرته ودنياه.','13','0','0');
INSERT INTO [t674] VALUES (93,'٩٥ - قال صاحب الكتاب: ويقال الشجرة النابتة في السباخ المأكولة الثمر والورق أحسن حالا من الفقير المحتاج.','13','0','0');
INSERT INTO [t674] VALUES (94,'٩٦ - قال صاحب الكتاب: ويقال الفقر داعية لصاحبه إلى مقت الناس وهو مسلبة للعقل والمروءة ومضلة للرأي ومذهبة للعلم والأدب ومجمعة للبلايا ومعدن للتهم.','13','0','0');
INSERT INTO [t674] VALUES (95,'٩٧ - قال صاحب الكتاب: ويقال إذا افتقر الرجل اتهمه من كان له مؤتمنا وأساء به الظن من كان يحسنه فيه فان أذنب غيره اتهم وكان لسوء الظن موضعا.','14','0','0');
INSERT INTO [t674] VALUES (96,'٩٨ - قال صاحب الكتاب: ويقال ليس في الغني من خلة تحمد إلا وهي في الفقير تذم.','14','0','0');
INSERT INTO [t674] VALUES (97,'٩٩ - قال صاحب الكتاب: ويقال الفاقة تعدل الموت بل الموت خير من الفاقة التي تضطر صاحبها إلى المسألة ولا سيما أن تضطره إلى مسألى اللئام.','14','0','0');
INSERT INTO [t674] VALUES (98,'١٠٠ - قال صاحب الكتاب: ويقال لإدخال الكريم يده في فم التنين فيستخرج منه سما قانلا يقتات به أحب عليه من مسألة اللئيم الأنوك.','14','0','0');
INSERT INTO [t674] VALUES (99,'١٠١ - قال صاحب الكتاب: ويقال من ابتلي بفرقه الإخوان والأحبة والغربة وابتلي بفاقة تضطره إلى المسألة فالحياة له موت وفي الموت له راحة.','14','0','0');
INSERT INTO [t674] VALUES (100,'١٠٢ - قال صاحب الكتاب: ويقال العداوة الباطنة الظاهرة الصداقة أشد ضررا من العداوة الظاهرة.','14','0','0');
INSERT INTO [t674] VALUES (101,'١٠٣ - قال صاحب الكتاب: ويقال العاقل يفي لمن صالح بما جعل له ولا يثق لنفسه بمثل ذلك من عدوه الذي لا تؤمن غائلته في بعد ولا قرب ويحترس منه كيف كان وما استطاع.','14','0','0');
INSERT INTO [t674] VALUES (102,'١٠٤ - قال صاحب الكتاب: ويقال العاقل إذا رجا نفع العدو اظهر له الصداقة، وإذا خاف ضرره أظهر له العداوة.','14','0','0');
INSERT INTO [t674] VALUES (103,'١٠٥ - قال صاحب الكتاب: ويقال ربما قطع الصديق صديقه الذي كان يصله فلا يخاف غائلته لأن أصل أمره لم يكن مبتدئا عن عداوة.','14','0','0');
INSERT INTO [t674] VALUES (104,'١٠٦ - قال صاحب الكتاب: ويقال من كان أصل أمره عداوة ثم أحدث صداقة لحاجة حملته على ذلك، كان صاحبه جديرا بالاحتراس منه لأنه إذا بلغ حاجته عاد إلى أصل أمره كالماء الذي يسخن بالنار فإذا أبعد عنها عاد إلى البرودة.','15','0','0');
INSERT INTO [t674] VALUES (105,'١٠٧ - قال صاحب الكتاب: ويقال لا تأمن عدوا رجع إليك بنفسه ووده، وإن أوليته من البر واللطف والإكرام ما ظننت انك قد سللت به سخيمته ولو خلطته بنفسك.','15','0','0');
INSERT INTO [t674] VALUES (106,'١٠٨ - قال صاحب الكتاب: ويقال العدو الضعيف أقرب إلى السلامة من العدو القوي إذا احترس منه ولم يغتر به من القوي إذا اغتر بالعدو الضعيف واسترسل إليه.','15','0','0');
INSERT INTO [t674] VALUES (107,'١٠٩ - قال صاحب الكتاب: ويقال العاقل يصانع عدوه إذا اضطر إليه ويظهر له ودا ويعجل الانصراف عنه إذا وجد إلى ذلك سبيلا.','15','0','0');
INSERT INTO [t674] VALUES (108,'١١٠ - قال صاحب الكتاب: ويقال للأحقاد في القلوب مواقع موجعة ناكية والألسن لا تصدق على القلوب.','15','0','0');
INSERT INTO [t674] VALUES (109,'١١١ - قال صاحب الكتاب: ويقال الأحقاد مخوفة حيث كانت وأشدها وأخوفها ما كان في أنفس الملوك لأن الملوك يدينون بالانتقام ويرون الطلب بالوتر مكرمة وفخرا.','15','0','0');
INSERT INTO [t674] VALUES (110,'١١٢ - قال صاحب الكتاب: ويقال لا ينبغي للعاقل أن يغتر بسكون الحقد، فانما الحقد في القلب مثل الجمر المكتن ما لم يجد حطبا.','16','0','0');
INSERT INTO [t674] VALUES (111,'١١٣ - قال صاحب الكتاب: ويقال أكيس الأقوام من لم يلتمس الأمر بالقتال ما وجد إلى غيره سبيلا.','16','0','0');
INSERT INTO [t674] VALUES (112,'١١٤ - قال صاحب الكتاب: ويقال لا تتهاون بالضعيف من أمر العداوة فان الحشيش الضعيف يجمع فيقتل منه حبل وثيق لو شد به فيل مغتلم أوثقه.','16','0','0');
INSERT INTO [t674] VALUES (113,'١١٥ - قال صاحب الكتاب: ويقال لو ان امرءا توسد النار وافترش الافاعي والتحف العقارب كان أحق أن يهنئه النوم عليها من قرب صاحب ملازم ذي عداوة يريد به نفسه.','16','0','0');
INSERT INTO [t674] VALUES (114,'١١٦ - قال صاحب الكتاب: ويقال الضرس المأكول لا يزال صاحبه منه في ألم حتى يقلعه.','16','0','0');
INSERT INTO [t674] VALUES (115,'١١٧ - قال صاحب الكتاب: ويقال الطعام إذا غثت منه النفس وجاشت فالراحة منه قذفه.','16','0','0');
INSERT INTO [t674] VALUES (116,'١١٨ - قال صاحب الكتاب: ويقال العدو المخوف دواؤه فقده.','16','0','0');
INSERT INTO [t674] VALUES (117,'١١٩ - قال صاحب الكتاب: ويقال ليس للعدو الحنق الذي لا يطاق ولا تمكن الفرصة فيه إلا الهرب منه.','16','0','0');
INSERT INTO [t674] VALUES (118,'١٢٠ - قال صاحب الكتاب: ويقال إذا لم تقو على العدو فالفدية امثل.','16','0','0');
INSERT INTO [t674] VALUES (119,'١٢١ - قال صاحب الكتاب: ويقال الاصطبار على الغربة والضنك في المعيشة خير من الخضوع للعدو الخسيس.','16','0','0');
INSERT INTO [t674] VALUES (120,'١٢٢ - قال صاحب الكتاب: ويقال قارب عدوك بعض المقاربة تنل حاجتك إليه.','17','0','0');
INSERT INTO [t674] VALUES (121,'١٢٣ - قال صاحب الكتاب: ويقال الحازم لا يأمن عدوه على حال: فإنه إن كان بعيدا لم يأمن مغاورته، وان كان قريبا لم يأمن مناجزته، وان كان مطردا لم يأمن كرته، وان كان وحيدا لم يأمن مكره.','17','0','0');
INSERT INTO [t674] VALUES (122,'١٢٤ - قال صاحب الكتاب: ويقال لكل حريق مطفئ فللنار الماء وللسم الترياق وللعشق الالتقاء وللحزن الصبر ونار الحقد لا يطفئها شيء.','17','0','0');
INSERT INTO [t674] VALUES (123,'١٢٥ - قال صاحب الكتاب: ويقال من أقلعت عنه الحمى استراح قلبه، ومن وضع عنه الحمل الثقيل اراح متنه ومن أمن عدوه أمن ليله وثلج صدره.','17','0','0');
INSERT INTO [t674] VALUES (124,'١٢٦ - قال صاحب الكتاب: ويقال العاقل وان كان واثقا بقوته ورأيه لا يحمله ذلك على ان يجني على نفسه عداوة وبغضة، ولا يغتر بعداوة ضعيف اتكالا على ما عنده من القوة والرأي كما أن الطبيب وان كان عنده الترياق وادوية السموم لا يحمله ما عنده من ذلك على شرب السم اتكالا عليها.','17','0','0');
INSERT INTO [t674] VALUES (125,'١٢٧ - قال صاحب الكتاب: ويقال إنما يستخرج ما عند الرجال ولاتهم وما عند الجنود قادتهم وما في الدين والتأويل علماؤه.','17','0','0');
INSERT INTO [t674] VALUES (126,'١٢٨ - قال صاحب الكتاب: ويقال كثرة العمال إذا لم يكونوا مجزين مضرة بالعمل فان العمل ليس رجاؤه بالكثير منهم ولكن بالقليل من صالحيهم، كالرجل الذي يحمل حجرا ثقيلا فيجهد نفسه ولا يصيب به ثمنا، وآخر يحمل الياقوت فلا يثقله ولا يجهد نفسه ويصيب به أكثر من امله.','18','0','0');
INSERT INTO [t674] VALUES (127,'١٢٩ - قال صاحب الكتاب: ويقال الرجل يحذره السلطان إذا كان قد أطيلت جفوته أو كان شرها حريصا أو كان أجرم جرما مع نظراء له فعفي عنهم دونه، أو عوقبوا جميعا فبلغ منه ما لم يبلغ منهم، أو كان قد أبلى بلاء حسنا مع نظراء له ففضلوا عليه في المنزلة والجاه، أو كان غير موثوق به في الدين، أو كان يخاف في شيء مما ينفعه عند السلطان ضررا، أو كان لعدو السلطان موادا.','18','0','0');
INSERT INTO [t674] VALUES (128,'١٣٠ - قال صاحب الكتاب: ويقال يؤتى الملك من ست خصال: من الحرمان والفتنة والهوى والفظاظة والزمان والخرق.','18','0','0');
INSERT INTO [t674] VALUES (129,'١٣١ - قال صاحب الكتاب: ويقال إذا عرف الملك من الرعية والصحابة رجلا قد ساواه في الرأي والهيبة والمنزلة والمال والتبع فليصرعه فانه إن لم يفعل به ذلك كان هو المصروع.','19','0','0');
INSERT INTO [t674] VALUES (130,'١٣٢ - قال صاحب الكتاب: ويقال خير الاخوان أقلهم مصانعة في النصيحة.','19','0','0');
INSERT INTO [t674] VALUES (131,'١٣٣ - قال وخير النساء الموافقة.','19','0','0');
INSERT INTO [t674] VALUES (132,'١٣٤ - قال: وخير الاعمال احلاها عاقبة وخير البر صلة الرحم وخير الثناء ما كان على أفواه الاخيار.','19','0','0');
INSERT INTO [t674] VALUES (133,'١٣٥ - قال صاحب الكتاب: يقال أشرف السلطنة ما لم يخالطها بطر.','19','0','0');
INSERT INTO [t674] VALUES (134,'١٣٦ - قال صاحب الكتاب: ويقال أغنى الأغنياء من لم يكن للحرص أسيرا.','19','0','0');
INSERT INTO [t674] VALUES (135,'١٣٧ - قال صاحب الكتاب: ويقال خير الأصدقاء من ترك المزاح.','19','0','0');
INSERT INTO [t674] VALUES (136,'١٣٨ - قال صاحب الكتاب: ويقال خير الاخلاق ما كان اعونها على الورع.','19','0','0');
INSERT INTO [t674] VALUES (137,'١٣٩ - قال صاحب الكتاب: ويقال لا تعلن عقوبة من لم يعلن ذنبه فإن لكل ذنب عقوبة، فالذنب السر عقوبة السر ولذنب الاعلان عقوبة الاعلان.','19','0','0');
INSERT INTO [t674] VALUES (138,'١٤٠ - قال صاحب الكتاب: ويقال السلطان إذا كان صالحا ووراءه وزراء سوء منعت الخاصة خيره والعامة عدله.','20','0','0');
INSERT INTO [t674] VALUES (139,'١٤١ - قال صاحب الكتاب: ويقال يصيب الملك الظفر بالحزم والحزم باجالة الرأي والرأي بتحصين الأسرار.','20','0','0');
INSERT INTO [t674] VALUES (140,'١٤٢ - قال صاحب الكتاب: يطلع على أسرار الملوك من قبل أربعة وجوه: من قبل النساء أو من قبل صاحب الرأي أو من قبل الحشم أو من الرسل المستعلمين الخبر.','20','0','0');
INSERT INTO [t674] VALUES (141,'١٤٣ - قال صاحب الكتاب: ويقال من حصن سره كان له من تحصينه أمران: إما ظفر بما يريد وإما السلامة من عيبه وضرره، ان أخطأه ما كان يؤمل من الظفر.','20','0','0');
INSERT INTO [t674] VALUES (142,'١٤٤ - قال صاحب الكتاب: ويقال إذا كان الملك محصنا للأسرار متخيرا للوزراء مهيبا عند العامة بعيدا من أن يعلم ما في نفسه أحد، لا يضيع عنده حسن بلاء ولا يسلم منه ذو جرم وكان مقدرا لما يفيد وما ينفق كان خليقا بأن لا يسلب صالح ما أوتي.','20','0','0');
INSERT INTO [t674] VALUES (143,'١٤٥ - قال صاحب الكتاب: ويقال من السر ما يدخل فيه الرهط، ومنه ما يدخل فيه الرجىن، ومنه ما يدخل فيه الواحد، ومنه ما لا ينبغي أن يطلع عليه أحد.','20','0','0');
INSERT INTO [t674] VALUES (144,'١٤٦ - قال صاحب الكتاب: ويقال يعتبر المرسل برأي رسوله ونفاذه.','20','0','0');
INSERT INTO [t674] VALUES (145,'١٤٧ - قال صاحب الكتاب: ويقال لا يطمعن ذو الكبر في الثناء الحسن ولا ذو الخب في كثرة الصديق ولا السيئ الادب في الشرف ولا الشحيح في البر ولا الحريص في قلة الذنوب ولا الملك المختال المتهاون في ثبات ملكه.','21','0','0');
INSERT INTO [t674] VALUES (146,'١٤٨ - قال صاحب الكتاب: ويقال إذا لم يكن في مملكة الملك قرة أعين رعيته فمثله كمثل زنمة العنز التي يصيبها السخلة فلا تجد فيها شيئا من الخير.','21','0','0');
INSERT INTO [t674] VALUES (147,'١٤٩ - قال صاحب الكتاب: ويقال امور الملك كبيرة ومن يحتاج إليه فيها من العمال والأعوان مثل ذلك.','21','0','0');
INSERT INTO [t674] VALUES (148,'١٥٠ - قال صاحب الكتاب: ويقال تنقل الناس من بعض المنازل إلى بعض وتركهم ما ألزموه وجرت لهم المعايش فيه والتماس الطبقة السفلى مراتب الطبقة العليا، مضرة للملوك وانتشار من الأمور وفساد من الأدب ومنازعة من اللئيم للكريم والأشياء تجري من ذلك على مثال حتى تنتهي إلى الخطر الجسيم.','21','0','0');
INSERT INTO [t674] VALUES (149,'١٥١ - قال صاحب الكتاب: ويقال خصال ثلاثة يزداد بها ما بين الاخوان لطفا واسترسالا: منها الزيارة في الرحال والمؤاكلة والنظر إلى الحشم والأهل.','22','0','0');
INSERT INTO [t674] VALUES (150,'١٥٢ - قال صاحب الكتاب: ويقال لا يكثر الرجل على إخوانه حمل المؤونات فيبرمهم ويؤذيهم.','22','0','0');
INSERT INTO [t674] VALUES (151,'١٥٣ - قال صاحب الكتاب: يقال الصديق صديقان: صديق طباع وصديق اضطرار وكلاهما يلتمس المنافع ويحترس من المضار.','22','0','0');
INSERT INTO [t674] VALUES (152,'١٥٤ - قال صاحب الكتاب: ويقال من اتخذ صديقا وأضاع رب صداقته حرم مسرة الإخاء ويئس من منفعة الإخوان.','22','0','0');
INSERT INTO [t674] VALUES (153,'١٥٥ - قال صاحب الكتاب: ويقال نعم العون على تسلية الهموم وتسكين النفس عند البلاء لقاء الأخ أخاه وإفضاء كل منهما بسره إلى صاحبه.','22','0','0');
INSERT INTO [t674] VALUES (154,'١٥٦ - قال صاحب الكتاب: ويقال من لم يركب الأهوال لم ينل الرغائب ومن ترك الأمر الذي لعله أن يبلغه حاجته مخافة ما لعله يوقاه فليس ينال جسيما.','22','0','0');
INSERT INTO [t674] VALUES (155,'١٥٧ - قال صاحب الكتاب: ويقال أعمال ثلاثة لا يستطيعها أحد الا بمعونة من ارتفاع همة وعظم حظر: صحبة الملوك وتجارة البحر ومناجزة العدو.','22','0','0');
INSERT INTO [t674] VALUES (156,'١٥٨ - قال صاحب الكتاب: ويقال الرجل العاقل لا يرى إلا في مكانين ولا يليق به غيرهما: إما مع الملوك مكرما وإما مع النساك في مساجدهم متبتلا.','23','0','0');
INSERT INTO [t674] VALUES (157,'١٥٩ - قال صاحب الكتاب: ويقال المال وسائر متاع الدنيا سريع إقباله إذا أقبل سريع ذهابه إذا ذهب مثل الكرة التي هي سريع ارتفاعها سريع وقوعها.','23','0','0');
INSERT INTO [t674] VALUES (158,'١٦٠ - قال صاحب الكتاب: ويقال لا يزال المرء مستمرا ما لم يعثر فإذا عثر مرة في جادة لج به العثار ولو مشى في جدد.','23','0','0');
INSERT INTO [t674] VALUES (159,'١٦١ - قال صاحب الكتاب: ويقال من غالب الملك المصنوع له الحازم الأريب الذي لا تبطره السراء ولا تدهشه الضراء فحينه يحدوه إلى ذلك.','23','0','0');
INSERT INTO [t674] VALUES (160,'١٦٢ - قال صاحب الكتاب: ويقال معاداة الملوك كالسيل بالليل لا تدري من أين يأتيك ولا كيف تتقيه.','23','0','0');
INSERT INTO [t674] VALUES (161,'١٦٣ - قال صاحب الكتاب: ويقال ويل لمن ابتلي بصحبة الملوك الذين لا يكرم عليهم صاحبهم إلا أن يطمعوا منه في غنى أو يحتاجوا إليه، فعند ذلك يقربونه ويكرمونه.','23','0','0');
INSERT INTO [t674] VALUES (162,'١٦٤ - قال صاحب الكتاب: ويقال اغلط الناس أكثرهم مستجيرا وسائلا منجحا.','23','0','0');
INSERT INTO [t674] VALUES (163,'١٦٥ - قال صاحب الكتاب: ويقال لا غنيا من لم يشارك في ماله ولا يعد عيشا ما عيش في بغضة وسوء ثناء.','23','0','0');
