INSERT INTO [t6874] VALUES (1,'خطبة المؤلف','1','0','0');
INSERT INTO [t6874] VALUES (2,'مداواة النفوس وإصلاح الأخلاق لذة العاقل بتمييزه ولذة العالم بعلمه ولذة الحكيم بحكمته ولذة المجتهد لله عز وجل باجتهاده أعظم من لذة الآكل بأكله والشارب بشربه والواطئ بوطئه والكاسب بكسبه واللاعب بلعبه والآمر بأمره وبرهان ذلك أن الحكيم والعاقل والعالم','3','0','0');
INSERT INTO [t6874] VALUES (3,'العقل والراحة وهو اطراح المبالاة بكلام الناس واستعمال المبالاة بكلام الخالق عز وجل بل هذا باب العقل والراحة كلها من قدر أنه يسلم من طعن الناس وعيبهم فهو مجنون من حقق النظر وراض نفسه على السكون إلى الحقائق وإن آلمتها في أول صدمة كان اغتباطه بذم الناس','7','0','0');
INSERT INTO [t6874] VALUES (4,'العلم لو لم يكن من فضل العلم إلا أن الجهال يهابونك ويجلونك وأن العلماء يحبونك ويكرمونك لكان ذلك سببا إلى وجوب طلبه فكيف بسائر فضائله في الدنيا والآخرة ولو لم يكن من نقص الجهل إلا أن صاحبه يحسد العلماء ويغبط نظراءه من الجهال لكان ذلك سببا إلى وجوب','11','0','0');
INSERT INTO [t6874] VALUES (5,'الأخلاق والسير إحرص على أن توصف بسلامة الجانب وتحفظ من أن توصف بالدهاء فيكثر المتحفظون منك حتى ربما أضر ذلك بك وربما قتلك وطن نفسك على ما تكره يقل همك إذا أتاك ويعظم سرورك ويتضاعف إذا أتاك ما تحب مما لم تكن قدرته إذا تكاثرت الهموم سقطت كلها الغادر يفي','16','0','0');
INSERT INTO [t6874] VALUES (6,'الإخوان والصداقة والنصيحة إستبقاك من عاتبك وزهد فيك من استهان بسيئاتك العتاب للصديق كالسبك للسبيكة فإما تصفو وإما تطير من طوى من إخوانك سره الذي يعنيك دونك أخون لك ممن أفشى سرك لأن من أفشى سرك فإنما خانك فقط ومن طوى سره دونك منهم فقد خانك واستخونك لا','30','0','0');
INSERT INTO [t6874] VALUES (7,'المحبة وأنواعها المحبة كلها جنس واحد ورسمها أنها الرغبة في المحبوب وكراهة منافرته والرغبة في المقارضة منه بالمحبة وإنما قدر الناس أنها تختلف من أجل اختلاف الأغراض فيها وإنما اختلفت الأغراض من أجل اختلاف الأطماع وتزايدها وضعفها أو انحسامها فتكون المحبة','41','0','0');
INSERT INTO [t6874] VALUES (8,'أقوال في المحبة من امتحن بقرب من يكره كمن امتحن ببعد من يحب ولا فرق إذا دعا المحب في السلو فإجابته مضمونة ودعوته مجابة إقنع بمن عندك يقنع بك من عندك السعيد في المحبة هو من ابتلي بمن يقدر أن يلقي عليه قفله ولا تلحقه في مواصلته تبعة من الله عز وجل ولا','44','0','0');
INSERT INTO [t6874] VALUES (9,'صباحة الصور وأنواعها وقد سئلت عن تحقيق الكلام فيها فقلت الحلاوة رقة المحاسن ولطف الحركات وخفة الإشارات وقبول النفس لأعراض الصور وإن لم تكن ثم صفات ظاهرة القوام جمال كل صفة على حدتها ورب جميل الصفات على انفراد كل صفة منها بارد الطلعة غير مليح ولا حسن','46','0','0');
INSERT INTO [t6874] VALUES (10,'الأخلاق والعادات التلون المذموم هو التنقل من زي متكلف لا معنى له إلى زي آخر مثله في التكلف وفي أنه لا معنى له ومن حال لا معنى لها إلى حال لا معنى لها بلا سبب يوجب ذلك وأما من استعمل من الزي ما أمكنه مما به إليه حاجة وترك التزيد مما لا يحتاج إليه فهذا','47','0','0');
INSERT INTO [t6874] VALUES (11,'أدواء الأخلاق الفاسدة ومداواتها من امتحن بالعجب فليفكر في عيوبه فإن أعجب بفضائله فليفتش ما فيه من الأخلاق الدنيئة فإن خفيت عليه عيوبه جملة حتى يظن أنه لا عيب فيه فليعلم أن مصيبته إلى الأبد وأنه أتم الناس نقصا وأعظمهم عيوبا وأضعفهم تمييزا وأول ذلك أنه','56','0','0');
INSERT INTO [t6874] VALUES (12,'غرائب أخلاق النفس ينبغي للعاقل أن لا يحكم بما يبدو له من استرحام الباكي المتظلم وتشكيه وشدة تلويه وتقلبه وبكائه فقد وقفت من بعض من يفعل هذا على يقين أنه الظالم المعتدي المفرط الظلم ورأيت بعض المظلومين ساكن الكلام معدوم التشكي مظهرا لقلة المبالاة فيسبق','76','0','0');
INSERT INTO [t6874] VALUES (13,'تطلع النفس إلى ما يستر عنها من كلام مسموع أو شيء مرئي أو إلى المدح وبقاء الذكر هذا أمران لا يكاد يسلم منهما أحد إلا ساقط الهمة جدا أو من راض نفسه الرياضة التامة وقمع قوة نفسه الغضبية قمعا كاملا أو عانى مدواة شره النفس إلى سماع كلام تستر به عنها أو','78','0','0');
INSERT INTO [t6874] VALUES (14,'حضور مجالس العلم إذا حضرت مجلس علم فلا يكن حضورك إلا حضور مستزيد علما وأجرا لا حضور مستغن بما عندك طالبا عثرة تشيعها أو غريبة تشنعها فهذه أفعال الأرذال الذين لا يفلحون في العلم أبدا فإذا حضرتها على هذه النية فقد حصلت خيرا على كل حال وإن لم تحضرها على','82','0','0');
INSERT INTO [t6874] VALUES (15,'قال أبو محمد إن أبا الأسود إنما قصد بالإنكار المجيء بما نهي عنه المرء وأنه يتضاعف قبحه منه مع نهيه عنه فقد أحسن كما قال الله تعالى أتأمرون الناس بالبر وتنسون أنفسكم ولا يظن بأبي الأسود إلا هذا وأما أن يكون نهى عن النهي عن الخلق المذموم فنحن','85','0','0');
