INSERT INTO [t7038] VALUES (569,'من رآني يعني في النوم فقد رأى الحق ومعناه عند الصوفية ما يفهم ما قال العارف النامي الشيخ عبد الرحمن الجامي قدس سره السامي شعر خود كفت هر انكس كه مرا ديد خدا ديد يعني بود آئينه حق روى محمد ورواه الترمذي','795','418','0');
INSERT INTO [t7038] VALUES (570,'من رآني في وقت النوم فقد رآني ذاتي فإنه تمثل له ذاتي بصورة مناسبة للوقت لهدايته فإن الشيطان لا يتمثل بي أي بشبهي وفي صورة مضافة إلي ولا يخدع الرابي بإلقاء أنه رسول الله عز وجل','796','418','0');
