INSERT INTO [t7038] VALUES (707,'على نوعين خلافة كبرى وخلافة صغرى أما الخلافة الكبرى فهي خلافة الباطن وهي خاصة بأمير المؤمنين علي كرم الله وجهه وأما الخلافة الصغرى فهي خلافة الظاهر التي اختلفت حولها الأمة فأهل السنة والجماعة اعتبروها في أمير المؤمنين أبي بكر الصديق','955','655','0');
INSERT INTO [t7038] VALUES (708,'فقال له الرسول هل شربت من هذا الماء وقال من شرب من هذا الماء فقد سرى علمي فيه انتهى لذلك ولهذا السبب فقد دعى الرسول','956','655','0');
