INSERT INTO [t7038] VALUES (791,'وقال أليس بينكم من يقول الشعر فقام أحدهم وقال قد لسغت حية الهوى كبدي فلا طبيب له ولا راقي إلا الحبيب الذي شغفت به فعنده رقيتي وترياقي وقد ترجمه عن أصله بالعربية إلى الفارسية الجامي قدس سره السامي عند سماع النبي عليه','1012','655','0');
INSERT INTO [t7038] VALUES (792,'عقرب وهو يصلي فلما فرغ قال لعن الله العقرب لا تدع مصليا ولا غيره ثم دعا بماء وملح وجعل يمسح عليها ويقرأ قل يا أيها الكافرون وقل أعوذ برب الفلق وقل أعوذ برب الناس انتهى ولمداواة سم العقرب هناك أدوية كثيرة لكنها','1013','655','0');
