INSERT INTO [t7038] VALUES (797,'من التحميد للمبالغة في الحمد يقال فلان أحمده إذا أثنى عليه بجلائل صفاته وأحمدته ذا وجدته محمودا ويقال له هذا الرجل محمود فإذا بلغ النهاية في ذلك وتكامل في المحاسن والمناقب فهو محمد وإنما سمي عليه السلام محمدا لكثرة خصاله المحمودة وسيكثر حمده','1015','655','0');
INSERT INTO [t7038] VALUES (798,'وسر ذلك أن المحاسن الظاهرة آيات على المحاسن الباطنة والأخلاق الزكية ولا أكمل منه','1015','655','0');
INSERT INTO [t7038] VALUES (799,'وإلا لما طاقت أعين الصحابة رضي الله تعالى عنهم النظر إليه','1015','655','0');
