INSERT INTO [t7038] VALUES (801,'ولد يوم الاثنين من شهر ربيع الأول واختلفو هل هو في اليوم الثاني أو الثامن أم العاشر أم الثاني عشر فهذه أربعة أقوال مشهورة وتوفي','1016','655','0');
INSERT INTO [t7038] VALUES (802,'كانت وقفته بعرفات بالجمعة في السنة العاشرة إجماعا فإذا كان كذلك لا يتصور وقوع يوم الاثنين في ثاني عشر من ربيع الأول من السنة التي بعدها وذلك مطرد في كل سنة يكون الوقفة قبله بالجمعة على كل تقدير من تمام الشهور ونقصانها وتمام بعضها ونقصان بعضها','1016','655','0');
