INSERT INTO [t7478] VALUES (214,'باب ما جاء في قتل كعب بن الأشرف [١] وكفاية الله عز وجل رسوله صلى الله عليه وسلم والمسلمين شره','1284','183','0');
INSERT INTO [t7478] VALUES (215,'جماع أبواب غزوة أحد [١] باب ذكر التاريخ لوقعة أحد','1298','179','0');
INSERT INTO [t7478] VALUES (216,'باب ذكر ما أرى رسول الله صلى الله عليه وسلم في منامه من شأن الهجرة وأحد وما جاء الله به من الفتح بعد','1300','215','0');
INSERT INTO [t7478] VALUES (217,'باب سياق قصة خروج النبي صلى الله عليه وسلم إلى أحد وكيف كانت الوقعة','1303','215','0');
INSERT INTO [t7478] VALUES (218,'باب ذكر عدد المسلمين يوم أحد وعدد المشركين، وقول الله عز وجل: وإذ غدوت من أهلك تبوئ المؤمنين مقاعد للقتال والله سميع عليم، إذ همت طائفتان منكم أن تفشلا والله وليهما، وعلى الله فليتوكل المؤمنون ٣: ١٢١ - ١٢٢ [١] وقوله: فما لكم في المنافقين فئت','1317','215','0');
INSERT INTO [t7478] VALUES (219,'باب كيف كان الخروج إلى أحد والقتال بين المسلمين والمشركين يومئذ','1321','215','0');
INSERT INTO [t7478] VALUES (220,'باب تحريض النبي صلى الله عليه وسلم أصحابه على القتال يوم أحد وثبوت من عصمه الله - عز وجل -','1329','215','0');
INSERT INTO [t7478] VALUES (221,'باب ما ذكر في المغازي من وقوع عين قتادة بن النعمان على وجنته ورد رسول الله صلى الله عليه وسلم عينه إلى مكانها وعودها إلى حالها','1348','215','0');
INSERT INTO [t7478] VALUES (222,'باب الملكين الذين كانا يقاتلان عن رسول الله صلى الله عليه وسلم يوم أحد ويدفعان عنه وعصمة الله تعالى إياه عن القتل كما وعده بقوله: والله يعصمك من الناس ٥: ٦٧ [١]','1351','215','0');
INSERT INTO [t7478] VALUES (223,'باب شدة رسول الله صلى الله عليه وسلم في البأس، وتصديق الله عز وجل قوله في أبي بن خلف، وما أصابه يوم أحد من الجراح في سبيل الله - عز وجل -','1355','215','0');
INSERT INTO [t7478] VALUES (224,'باب قول الله عز وجل ولقد صدقكم الله وعده إذ تحسونهم بإذنه، حتى إذا فشلتم وتنازعتم في الأمر ٣: ١٥٢ [١] الآية. وقول الله - عز وجل - إذ تصعدون ولا تلوون على أحد والرسول يدعوكم في أخراكم فأثابكم غما بغم لكيلا تحزنوا على ما فاتكم ولا ما أصابكم و','1364','215','0');
INSERT INTO [t7478] VALUES (225,'باب عدد من استشهد من المسلمين يوم أحد وعدد من قتل من المشركين يومئذ','1373','215','0');
INSERT INTO [t7478] VALUES (226,'باب ما جرى بعد انقضاء الحرب وذهاب المشركين في أمر القتلى والجرحى ومن أجاد الحرب وما ظهر من الآثار في حال الشهداء على طريق الاختصار','1379','215','0');
INSERT INTO [t7478] VALUES (227,'باب قول الله عز وجل: ولا تحسبن الذين قتلوا في سبيل الله أمواتا بل أحياء عند ربهم يرزقون، فرحين بما آتاهم الله من فضله ٣: ١٦٩ - ١٧٠ [١] الآية، وما ورد في فضل شهداء أحد، وزيارة قبورهم على سبيل الاختصار.','1400','215','0');
INSERT INTO [t7478] VALUES (228,'باب قول الله عز وجل إن الذين تولوا منكم يوم التقى الجمعان إنما استزلهم، الشيطان ببعض ما كسبوا ولقد عفا الله عنهم إن الله غفور حليم ٣: ١٥٥ [١] .','1407','215','0');
INSERT INTO [t7478] VALUES (229,'باب خروج النبي صلى الله عليه وسلم إلى حمراء الأسد [١] وقول الله عز وجل الذين استجابوا لله والرسول من بعد ما أصابهم القرح للذين أحسنوا منهم واتقوا أجر عظيم ٣: ١٧٢ [٢]','1409','215','0');
