INSERT INTO [t7478] VALUES (376,'باب ما جاء في قدوم كعب بن زهير [١] على النبي صلى الله عليه وسلم بعد ما رجع إلى المدينة زمن الفتح','2177','343','0');
INSERT INTO [t7478] VALUES (377,'جماع أبواب غزوة تبوك.','2182','342','0');
INSERT INTO [t7478] VALUES (378,'باب ذكر التاريخ لغزوة تبوك، وتأهب رسول الله صلى الله عليه وسلم وأصحابه رضي الله عنهم للخروج إليه وما روي في تجهيز عثمان بن عفان رضي الله عنه ذلك الجيش واستخلاف النبي صلى الله عليه وسلم علي بن أبي طالب [رضي الله عنه] [١] على المدينة وتخلف من ت','2182','377','0');
INSERT INTO [t7478] VALUES (379,'باب لحوق أبي ذر رضي الله عنه وأبي خيثمة [رضي الله عنه] [١] برسول الله صلى الله عليه وسلم بعد خروجه، وما ظهر فيما روي من قوله عند مجيئهما وإخباره عن حال أبي ذر وقت وفاته من آثار النبوة','2191','377','0');
INSERT INTO [t7478] VALUES (380,'باب سبب تسمية غزوة تبوك بالعسرة وما ظهر بدعاء النبي صلى الله عليه وسلم في بقية الأزواد وفي الماء وإخباره عن قول المنافقين [١] في غيبته ثم بموضع ناقته من آثار النبوة.','2197','377','0');
INSERT INTO [t7478] VALUES (381,'باب ورود النبي صلى الله عليه وسلم في مسيره على حجر ثمود ونهيه عن الدخول على أهله وخبره عن قوم يأتي الله بهم لا يدفعون عن أنفسهم شيئا، فكان كما قال.','2203','377','0');
INSERT INTO [t7478] VALUES (382,'باب إخبار النبي صلى الله عليه وسلم عن وقت إتيانهم عين تبوك، وما ظهر في ذلك، وفي وضوئه من تلك العين حتى كثر ماؤها وفيما قال لمعاذ فكان كما قال من آثار النبوة.','2206','377','0');
INSERT INTO [t7478] VALUES (383,'باب خرص [١] النبي صلى الله عليه وسلم [في مسيره] [٢] وإخباره عن الريح التي تهب تلك الليلة، ودعائه للذي خنق، وما ظهر في كل واحد منها من آثار النبوة.','2208','377','0');
INSERT INTO [t7478] VALUES (384,'باب ما روي في خطبته [صلى الله عليه وسلم] بتبوك','2211','377','0');
INSERT INTO [t7478] VALUES (385,'باب صلاة النبي صلى الله عليه وسلم بتبوك، ودعائه على من مر بين يديه، وما ظهر في ذلك من آثار النبوة.','2213','377','0');
INSERT INTO [t7478] VALUES (386,'باب ما روي في صلاته بتبوك على معاوية بن معاوية الليثي [١]- رضي الله عنه - في اليوم الذي مات فيه بالمدينة','2215','377','0');
INSERT INTO [t7478] VALUES (387,'باب ذكر كتابه ليحنة [١] بن رؤبة [٢] وكتابه لأهل جرباء [٣] وأذرح [٤] وهو بتبوك.','2217','377','0');
INSERT INTO [t7478] VALUES (388,'باب بعث النبي صلى الله عليه وسلم خالد بن الوليد إلى أكيدر دومة [١] ، وما ظهر في إخباره عن وجوده وهو يصيد البقر من آثار النبوة','2220','377','0');
INSERT INTO [t7478] VALUES (389,'باب ما روي في سبب خروج النبي صلى الله عليه وسلم إلى تبوك وسبب رجوعه إن صح الخبر فيه.','2224','377','0');
INSERT INTO [t7478] VALUES (390,'باب رجوع النبي صلى الله عليه وسلم من تبوك، وأمره بهدم مسجد الضرار، ومكر المنافقين به في الطريق وعصمة الله تعالى إياه واطلاعه عليه، وما ظهر في ذلك من آثار النبوة.','2226','377','0');
INSERT INTO [t7478] VALUES (391,'باب تلقي الناس رسول الله صلى الله عليه وسلم حين قدم من غزوة تبوك وما قال في المخلفين [من الأعراب] [١] بعذر والمخلفين بغير عذر','2235','377','0');
INSERT INTO [t7478] VALUES (392,'حديث أبي لبابة وأصحابه','2240','377','0');
INSERT INTO [t7478] VALUES (393,'حديث كعب بن مالك وصاحبيه رضي الله عنهم','2243','377','0');
INSERT INTO [t7478] VALUES (394,'باب ما جاء في مرض عبد الله بن أبي بن سلول ووفاته بعد رجوع النبي صلى الله عليه وسلم من غزوة تبوك','2255','377','0');
INSERT INTO [t7478] VALUES (395,'باب قصة ثعلبة بن حاطب وما ظهر فيها من الآثار.','2259','377','0');
INSERT INTO [t7478] VALUES (396,'باب حجة أبي بكر الصديق رضي الله [تعالى] [١] عنه بأمر النبي صلى الله عليه وسلم سنة تسع، ونزول سورة براءة بعد خروجه، وبعث رسول الله صلى الله عليه وسلم علي ابن أبي طالب [رضي الله عنه] [٢] ليقرأها على الناس','2263','377','0');
INSERT INTO [t7478] VALUES (397,'باب قدوم وفد ثقيف وهم أهل الطائف على رسول الله صلى الله عليه وسلم وتصديق ما قال في غزوة ابن مسعود الثقفي رضي الله عنه ثم إجابة الله [تعالى] [١] دعاءه في هداية ثقيف','2269','377','0');
INSERT INTO [t7478] VALUES (398,'باب تعليم النبي صلى الله عليه وسلم عثمان بن أبي العاص الثقفي رضي الله عنه ما كان سببا لشفائه ودعائه له حتى فارقه الشيطان وذهب عنه النسيان.','2277','377','0');
INSERT INTO [t7478] VALUES (399,'جماع أبواب وفود العرب إلى رسول الله صلى الله عليه وسلم [١]','2279','342','0');
INSERT INTO [t7478] VALUES (400,'باب وفد عطارد بن حاجب في بني تميم','2283','399','0');
INSERT INTO [t7478] VALUES (401,'باب وفد بني عامر [١] ودعاء النبي صلى الله عليه وسلم على عامر بن الطفيل وكفاية الله تعالى شره، وشر أربد بن قيس بعد أن عصم منها نبيه صلى الله عليه وسلم، وما ظهر في ذلك من آثار النبوة','2288','399','0');
INSERT INTO [t7478] VALUES (402,'باب وفد عبد القيس [١] وإخبار النبي صلى الله عليه وسلم بطلوعهم قبل قدومهم','2293','399','0');
INSERT INTO [t7478] VALUES (403,'باب وفد بني حنيفة [١]','2300','399','0');
INSERT INTO [t7478] VALUES (404,'باب رؤيا رسول الله صلى الله عليه وسلم في الأسود العنسي ومسيلمة الكذابين، وتصديق الله سبحانه رؤياه وما ظهر في ذلك من آثار النبوة.','2304','399','0');
INSERT INTO [t7478] VALUES (405,'باب وفد طيء [١] منهم زيد الخيل وعدي ابن حاتم وما قال لزيد وإخباره صلى الله عليه وسلم عديا ببعض ما يكون بعده وما ظهر فيه من آثار النبوة','2307','399','0');
INSERT INTO [t7478] VALUES (406,'باب قدوم جرير بن عبد الله البجلي [١] على النبي صلى الله عليه وسلم وإخباره أصحابه فيما بين خطبته بدخوله على صفته ثم دعائه له حين بعثه في رجال من أحمس إلى ذي الخلصة وما ظهر في كل واحد منهما [من] آثار النبوة','2316','399','0');
INSERT INTO [t7478] VALUES (407,'باب قدوم وائل بن حجر [١]','2319','399','0');
INSERT INTO [t7478] VALUES (408,'باب قدوم الأشعريين وأهل اليمن','2321','399','0');
INSERT INTO [t7478] VALUES (409,'باب قدوم الحكم بن حزن [١] وحكاية صفة خطبته [صلى الله عليه وسلم] يوم الجمعة','2324','399','0');
INSERT INTO [t7478] VALUES (410,'باب قدوم زياد بن الحارث الصدائي [١] على النبي صلى الله عليه وسلم، وما روي في قصته من خروج الماء من بين أصبعي رسول الله صلى الله عليه وسلم، وما ظهر في البئر التي شكا إليه قلة مائها ببركة دعائه من آثار النبوة','2325','399','0');
INSERT INTO [t7478] VALUES (411,'باب ما جاء في قدوم عبد الرحمن بن أبي عقيل [١] على النبي صلى الله عليه وسلم','2328','399','0');
INSERT INTO [t7478] VALUES (412,'باب قصة دوس [١] والطفيل بن عمرو رضي الله عنه وما ظهر بين عينيه من النور ثم في رأس سوطه، وما كان في رؤياه وفي دعاء النبي صلى الله عليه وسلم من براهين الشريعة','2329','399','0');
INSERT INTO [t7478] VALUES (413,'باب قصة مزينة ومسألتهم [١] وظهور البركة في التمر الذي منه أعطاهم عمر بن الخطاب رضي الله عنه.','2335','399','0');
INSERT INTO [t7478] VALUES (414,'باب قدوم فروة بن مسيك المرادي [١] وعمرو بن معدي كرب، وقدوم الأشعث بن قيس في وفد كندة على النبي صلى الله عليه وسلم','2338','399','0');
INSERT INTO [t7478] VALUES (415,'باب قدوم صرد بن عبد الله [١] على النبي صلى الله عليه وسلم في وفد من الأسد [٢] وإسلامه ورجوعه إلى جرش وقدوم رجلين من جرش على النبي صلى الله عليه وسلم وإخباره إياهما بإصابة صرد قومهما في الساعة التي أصابهم فيها، وما ظهر في ذلك من آثار النبوة.','2342','399','0');
INSERT INTO [t7478] VALUES (416,'باب قدوم ضمام بن ثعلبة [١] على رسول الله صلى الله عليه وسلم','2344','399','0');
INSERT INTO [t7478] VALUES (417,'باب قدوم معاوية بن حيدة [١] القشيري ودخوله [٢] على النبي صلى الله عليه وسلم، وإجابة الله عز وجل دعاء رسول الله صلى الله عليه وسلم حتى ألجأه إلى القدوم عليه.','2348','399','0');
INSERT INTO [t7478] VALUES (418,'باب قدوم طارق بن عبد الله [١] وأصحابه على النبي صلى الله عليه وسلم وقول المرأة التي كانت معهم في رسول الله صلى الله عليه وسلم.','2350','399','0');
INSERT INTO [t7478] VALUES (419,'باب وفد نجران [١] وشهادة الأساقفة لنبينا صلى الله عليه وسلم بأنه النبي الذي كانوا ينتظرونه، وامتناع من امتنع منهم من الملاعنة وما ظهر في ذلك من آثار النبوة.','2352','399','0');
INSERT INTO [t7478] VALUES (420,'باب بعث رسول الله صلى الله عليه وسلم علي بن أبي طالب رضي الله عنه إلى أهل نجران، وبعثه إلى اليمن بعد خالد بن الوليد رضي الله عنه','2364','399','0');
INSERT INTO [t7478] VALUES (421,'باب بعث معاذ بن جبل وأبي موسى الأشعري رضي الله عنهما إلى اليمن وما ظهر في قول رسول الله صلى الله عليه وسلم لمعاذ ثم في رؤيا معاذ بن جبل من براهين الشريعة.','2371','399','0');
INSERT INTO [t7478] VALUES (422,'باب ذكر فروة بن عمرو الجذامي [١]','2379','399','0');
INSERT INTO [t7478] VALUES (423,'باب بعث خالد بن الوليد رضي الله عنه إلى بني الحارث بن كعب','2381','399','0');
INSERT INTO [t7478] VALUES (424,'كتاب رسول الله صلى الله عليه وسلم لعمرو بن حزم إلى اليمن','2383','399','0');
INSERT INTO [t7478] VALUES (425,'باب قدوم تميم الداري على النبي صلى الله عليه وسلم واخباره اياه بأمر الجساسة [١] وما سمع من الدجال في خروج النبي صلى الله عليه وسلم وإيمان من آمن به','2386','399','0');
INSERT INTO [t7478] VALUES (426,'باب ما روي في قدوم هامة بن هيم بن لاقيس بن إبليس على النبي صلى الله عليه وسلم وإسلامه','2388','399','0');
INSERT INTO [t7478] VALUES (427,'باب ما روي في التقاء النبي صلى الله عليه وسلم بالياس عليه السلام وإسناد حديثه ضعيف والله أعلم','2391','399','0');
INSERT INTO [t7478] VALUES (428,'باب ما روي في سماعه كلام الخضر عليه السلام. وإسناده ضعيف','2393','399','0');
INSERT INTO [t7478] VALUES (429,'باب ما جاء في قصة وصي عيسى بن مريم [١] عليه السلام وظهوره في زمن عمر بن الخطاب رضي الله عنه ان صحت الرواية.','2395','399','0');
INSERT INTO [t7478] VALUES (430,'باب ما جاء في شأن [سيدنا] [١] إبراهيم بن النبي صلى الله عليه وسلم ووفاته وذلك قبل حجة الوداع.','2399','399','0');
INSERT INTO [t7478] VALUES (431,'باب حجة الوداع [١]','2402','399','0');
INSERT INTO [t7478] VALUES (432,'باب ما جاء في نعي النبي صلى الله عليه وسلم نفسه إلى الناس في حجة الوداع وذلك حين نزل عليه قوله عز وجل: إذا جاء نصر الله والفتح ١١٠: ١ إلى آخر السورة وقوله: اليوم أكملت لكم دينكم ٥: ٣ الآية ثم إخباره في خطبته بأن الشيطان قد يئس أن يعبد بأرضكم و','2415','399','0');
INSERT INTO [t7478] VALUES (433,'باب [ما جاء في] [١] انصراف النبي صلى الله عليه وسلم من حجة الوداع.','2421','399','0');
INSERT INTO [t7478] VALUES (434,'باب عدد حجات رسول الله صلى الله عليه وسلم وعمره','2423','399','0');
INSERT INTO [t7478] VALUES (435,'باب عدد غزوات رسول الله صلى الله عليه وسلم وعدد سراياه [١] .','2427','399','0');
INSERT INTO [t7478] VALUES (436,'باب ما جاء في تحدث رسول الله صلى الله عليه وسلم بنعمة ربه عز وجل لقوله تعالى: وأما بنعمة ربك فحدث ٩٣: ١١ [١] وما جاء في خصائصه على طريق الاختصار فقد ذكرنا في كتاب النكاح من كتاب السنن ما خص به من الأحكام','2440','399','0');
INSERT INTO [t7478] VALUES (437,'باب ما جاء في التخيير بين الأنبياء','2461','399','0');
INSERT INTO [t7478] VALUES (438,'المجلد السادس','2472','0','0');
INSERT INTO [t7478] VALUES (439,'جماع أبواب دلائل النبوة سوى ما مضى في هذا الكتاب ما ظهر منها على نبينا محمد صلى الله عليه وسلم من وقت الولادة إلى أن بعث بالرسالة ثم من وقت الرسالة إلى وقت الهجرة ثم من وقت الهجرة إلى آخر مغازيه المعروفة وأسفاره المشهورة مؤرخا بتواريخه المنقولة و','2472','438','0');
INSERT INTO [t7478] VALUES (440,'باب انقياد الشجر لنبينا محمد صلى الله عليه وسلم وما جمع الخبر المنقول فيه من ذكر خروج الماء من بين أصابعه وغير ذلك من علامات [١] النبوة.','2473','439','0');
INSERT INTO [t7478] VALUES (441,'باب مشي العذق الذي دعاه محمد صلى الله عليه وسلم إليه حتى وقف بين يديه ثم رجوعه إلى مكانه بإذنه وما في ذلك من دلائل النبوة.','2479','439','0');
INSERT INTO [t7478] VALUES (442,'باب ذكر المعجزات الثلاث التي شهدهن جابر بن عبد الله الأنصاري وغيره في الشجرتين والصبي والجمل، وما [كان] [١] في كل واحد منهن من آثار النبوة.','2484','439','0');
INSERT INTO [t7478] VALUES (443,'باب ذكر البعير الذي سجد للنبي صلى الله عليه وسلم وأطاع أهله بعد ما امتنع عليهم ببركته.','2494','439','0');
INSERT INTO [t7478] VALUES (444,'باب ذكر الوحش الذي كان يقبل ويدبر فإذا أحس برسول الله صلى الله عليه وسلم ربض فلم يترمرم [١] .','2497','439','0');
INSERT INTO [t7478] VALUES (445,'باب ما جاء في الحمرة التي فجعت ببيضتها أو بفرخيها، فشكت إلى النبي صلى الله عليه وسلم حالها.','2498','439','0');
INSERT INTO [t7478] VALUES (446,'باب ما جاء في كلام الظبية التي فجعت بخشفها [١] وشهادتها لنبينا صلى الله عليه وسلم بالرسالة','2500','439','0');
INSERT INTO [t7478] VALUES (447,'باب ما جاء في شهادة الضب لنبينا صلى الله عليه وسلم بالرسالة وما ظهر في ذلك من دلالات النبوة.','2502','439','0');
INSERT INTO [t7478] VALUES (448,'باب ما جاء في مجيء الذئب مجلس النبي رسول الله صلى الله عليه وسلم يطلب شيئا.','2505','439','0');
INSERT INTO [t7478] VALUES (449,'باب ما في كلام الذئب وشهادته لنبينا صلى الله عليه وسلم بالرسالة وما ظهر في ذلك من دلالات النبوة.','2507','439','0');
INSERT INTO [t7478] VALUES (450,'باب ما جاء في تسخير الله عز وجل الأسد"لسفينة" مولى رسول الله صلى الله عليه وسلم كرامة لرسول الله صلى الله عليه وسلم وما روي في معناه.','2511','439','0');
INSERT INTO [t7478] VALUES (451,'باب ما جاء في معجزة أخرى ظهرت له في مولاه سفينة وبذلك سمي سفينة','2513','439','0');
INSERT INTO [t7478] VALUES (452,'باب ما جاء في المجاهد في سبيل الله الذي بعث حماره بعد ما نفق.','2514','439','0');
INSERT INTO [t7478] VALUES (453,'باب ما جاء في المهاجرة إلى النبي صلى الله عليه وسلم التي أحيا الله تعالى بدعائها ولدها بعد ما مات، وما جاء في الكرامات التي ظهرت على العلاء بن الحضرمي وأصحابه.','2516','439','0');
INSERT INTO [t7478] VALUES (454,'باب ما جاء في شهادة الميت لرسول الله صلى الله عليه وسلم بالرسالة والقائمين بعده بالخلافة، والرواية في ذلك صحيحة ثابتة وفي ذلك دلالة ظاهرة من دلالات النبوة.','2521','439','0');
INSERT INTO [t7478] VALUES (455,'باب ما جاء في شهادة الرضيع والأبكم لنبينا صلى الله عليه وسلم بالرسالة إن صحت فيه الرواية.','2525','439','0');
INSERT INTO [t7478] VALUES (456,'باب ما جاء في تسبيح الطعام الذي كانوا يأكلونه مع نبينا [محمد] [١] صلى الله عليه وسلم وما في ذلك من آثار النبوة.','2528','439','0');
INSERT INTO [t7478] VALUES (457,'باب ما جاء في تسبيح الحصيات في كف النبي صلى الله عليه وسلم، ثم في كف بعض أصحابه.','2530','439','0');
INSERT INTO [t7478] VALUES (458,'باب ما جاء في حنين الجذع الذي كان يخطب عنده رسول الله صلى الله عليه وسلم حين جاوزه إلى المنبر، وقد مضى بعض طرقه عند [ذكر] [١] اتخاذ المنبر وفي ذلك دلالة ظاهرة من دلالات النبوة.','2532','439','0');
INSERT INTO [t7478] VALUES (459,'باب [ما جاء في] [١] وجود رائحة الطيب من كل طريق سلكه نبينا صلى الله عليه وسلم وسجود الحجر والشجر الذي يمر عليه له ومجه مسكا أو أطيب من المسك في الدلو الذي [كان] [٢] يشرب منه.','2535','439','0');
INSERT INTO [t7478] VALUES (460,'باب ما جاء في تأمين أسكفة الباب وحوائط البيت على دعاء نبينا محمد صلى الله عليه وسلم لعمه العباس - رضي الله عنه - ولبني عمه إن صحت الرواية.','2537','439','0');
INSERT INTO [t7478] VALUES (461,'باب ما جاء في رؤية النبي صلى الله عليه وسلم أصحابه وراء ظهره.','2539','439','0');
INSERT INTO [t7478] VALUES (462,'باب ما جاء في البرقة التي برقت لابني ابنة رسول الله صلى الله عليه وسلم حين خرجا من عنده حتى مشيا في ضوئها كرامة للنبي صلى الله عليه وسلم.','2542','439','0');
INSERT INTO [t7478] VALUES (463,'باب ما جاء في إضاءة عصى الرجلين من أصحاب النبي صلى الله عليه وسلم حتى خرجا من عنده في ليلة مظلمة حتى مشيا في ضوئها كرامة لنبي الله صلى الله عليه وسلم، وما روي في إضاءة عصى أبي عبس، ثم ما جاء في إضاءة أصابع حمزة بن عمرو الأسلمي حتى جمعوا ظهورهم.','2543','439','0');
INSERT INTO [t7478] VALUES (464,'باب ما جاء في الكرامة التي ظهرت على تميم الداري رضي الله عنه شرفا للمصطفى صلى الله عليه وسلم وتنويها باسم من آمن به','2546','439','0');
INSERT INTO [t7478] VALUES (465,'باب ما جاء في التمثال الذي وضع عليه رسول الله صلى الله عليه وسلم فأذهبه الله عز وجل [١] .','2547','439','0');
INSERT INTO [t7478] VALUES (466,'جماع أبواب دعوات نبينا صلى الله عليه وسلم المستجابة في الأطعمة والأشربة وبركاته التي ظهرت فيما دعا فيه وغير ذلك من دعواته على طريق الاختصار فلا سبيل إلى نقل جميعها لما فيه من الإكثار.','2548','438','0');
INSERT INTO [t7478] VALUES (467,'باب ما جاء في ظهور بركته في الشاة التي لم يكن فيها لبن حتى نزل لها لبن، وقد مضى ذلك في ذكر نزوله بمخيمتي أم معبد [١] ونزوله قبل ذلك بالأغنام التي كان يرعاها ابن أم معبد.','2549','466','0');
INSERT INTO [t7478] VALUES (468,'باب ما جاء في دعائه لأهله وهو يريد نفسه ومن في نفقته بالكفاف من الرزق فرزقوا ذلك وصبروا عليه.','2552','466','0');
INSERT INTO [t7478] VALUES (469,'باب ما جاء في دعوة أبي طلحة الأنصاري - رضي الله عنه - رسول الله صلى الله عليه وسلم وما ظهر في طعامه ببركة رسول الله صلى الله عليه وسلم من آثار النبوة.','2553','466','0');
INSERT INTO [t7478] VALUES (470,'باب ما جاء في القصعة التي كانت تمد من السماء وما ظهر فيها من آثار النبوة','2558','466','0');
INSERT INTO [t7478] VALUES (471,'باب ما جاء في دعوة أبي أيوب الأنصاري رضي الله عنه وما ظهر في طعامه ببركة رسول الله صلى الله عليه وسلم من آثار النبوة','2559','466','0');
INSERT INTO [t7478] VALUES (472,'باب ما جاء في البركة التي ظهرت في الشاة التي اشتراها من الاعرابي','2560','466','0');
INSERT INTO [t7478] VALUES (473,'باب ما ظهر في النخل التي غرسها النبي صلى الله عليه وسلم لسلمان الفارسي رضي الله عنه وأطعمت من سنته من آثار النبوة، واستبرائه عند قدومه عليه، وما وصف له من حاله','2562','466','0');
INSERT INTO [t7478] VALUES (474,'باب ما جاء في دعاء النبي صلى الله عليه وسلم أهل الصفة على لبن يسير وما ظهر في ذلك من آثار النبوة','2566','466','0');
INSERT INTO [t7478] VALUES (475,'باب ما جاء في البركة التي ظهرت في الطعام الذي قدم في دار أبي بكر الصديق رضي الله عنه إلى أضيافه في زمان النبي صلى الله عليه وسلم','2568','466','0');
INSERT INTO [t7478] VALUES (476,'باب ما جاء في دعاء المرأة بالرزق في زمان النبي صلى الله عليه وسلم، ودعاء الآخر برد إبله وابنه عليه، وقول الله عز وجل ومن يتق الله يجعل له مخرجا ويرزقه من حيث لا يحتسب ٦٥: ٢ - ٣','2570','466','0');
INSERT INTO [t7478] VALUES (477,'باب ما جاء في دعائه لابنته فاطمة عليهما السلام وما ظهر فيه من الإجابة','2573','466','0');
INSERT INTO [t7478] VALUES (478,'باب ما جاء في مزود ابي هريرة رضي الله عنه وما ظهر فيه ببركة دعاء النبي صلى الله عليه وسلم من آثار النبوة.','2574','466','0');
INSERT INTO [t7478] VALUES (479,'باب ما جاء في امتلاء النحي [١] الذي أهريق ما فيه','2577','466','0');
INSERT INTO [t7478] VALUES (480,'باب ما ظهر فيما خلف رسول الله صلى الله عليه وسلم على عائشة - رضي الله عنها - من الشعير، وفيما اعطى الرجل من الشعير، وفيما بقي عند المرأة من السمن في العكة، التي كانت تهدي منها للنبي صلى الله عليه وسلم، وفيما أهدت تلك المرأة الأخرى إليه من السمن','2578','466','0');
INSERT INTO [t7478] VALUES (481,'باب ما جاء في القوم الذين كانوا لا يشبعون فأمرهم رسول الله صلى الله عليه وسلم بالاجتماع على الطعام وتسمية الله تعالى عليه ففعلوا فشبعوا.','2584','466','0');
INSERT INTO [t7478] VALUES (482,'باب ما ظهر في بقية أزواد القوم ببركة دعاء النبي صلى الله عليه وسلم من الزيادة وآثار النبوة.','2585','466','0');
INSERT INTO [t7478] VALUES (483,'باب فيما ظهر من الكرامات على أم شريك في هجرتها إلى رسول الله صلى الله عليه وسلم وما ظهر من دلالات النبوة في العكة التي أهدتها له.','2588','466','0');
INSERT INTO [t7478] VALUES (484,'باب ما جاء في ما ظهر على أم أيمن مولاة رسول الله صلى الله عليه وسلم وحاضنته من الكرامات في هجرتها.','2590','466','0');
INSERT INTO [t7478] VALUES (485,'باب ما جاء فيما ظهر على أبي أمامة حين بعث رسولا إلى قومه من الكرامات.','2591','466','0');
INSERT INTO [t7478] VALUES (486,'باب ما جاء في إجابة الله تعالى دعاء رسول الله صلى الله عليه وسلم حين ضافه ضيف ولم يكن عنده شيء.','2593','466','0');
INSERT INTO [t7478] VALUES (487,'باب ما ظهر في مزادتي المرأة ببركة دعاء رسول الله [١] صلى الله عليه وسلم من الزيادة وآثار النبوة. قد مضى بعض طرق هذا الحديث في آخر غزوة خيبر','2595','466','0');
INSERT INTO [t7478] VALUES (488,'باب حديث الميضأة وما ظهر في ذلك من آثار النبوة ودلالات الصدق قد مضى في ذلك حديث سليمان بن المغيرة عن ثابت عن عبد الله بن رباح عن ابي قتادة ومن ذلك الوجه أخرجه مسلم في الصحيح','2597','466','0');
INSERT INTO [t7478] VALUES (489,'باب ما ظهر في البئر التي كانت بقباء من بركته صلى الله عليه وسلم','2601','466','0');
INSERT INTO [t7478] VALUES (490,'باب ما جاء في الشاة التي ظهرت فحلبت فأروت ثم ذهبت فلم توجد','2602','466','0');
INSERT INTO [t7478] VALUES (491,'باب استسقاء النبي صلى الله عليه وسلم واجابة الله تعالى إياه في سقياه، ثم دعائه بالكشف حين شكوا اليه كثرة المطر، واجابة الله تعالى إياه فيما دعاه وما ظهر في ذلك من آثار النبوة','2604','466','0');
INSERT INTO [t7478] VALUES (492,'باب استسقاء أمير المؤمنين عمر بن الخطاب رضي الله عنه بعم رسول الله صلى الله عليه وسلم وإجابة الله تعالى في سقياهم','2612','466','0');
INSERT INTO [t7478] VALUES (493,'باب ما جاء في استسقاء أنس بن مالك [رضي الله عنه] [١] خادم رسول الله صلى الله عليه وسلم لأرضه','2613','466','0');
INSERT INTO [t7478] VALUES (494,'باب دعاء النبي صلى الله عليه وسلم في التمر الموروث عن عبد الله بن عمرو بن حرام رضي الله عنه حتى قضى منه دينه وكأنه لم ينقص منه شيء وما ظهر في ذلك من آثار النبوة','2614','466','0');
INSERT INTO [t7478] VALUES (495,'باب دعاء النبي صلى الله عليه وسلم في بعير جابر بن عبد الله وقد أعيا حتى صار ببركة دعائه في أول الركب، وما ظهر فيه وفي فرس أبي طلحة بركوبه وفي دابة جعيل الأشجعي، وفي ناقة الفتى ببركته من آثار النبوة.','2616','466','0');
INSERT INTO [t7478] VALUES (496,'باب دعاء النبي صلى الله عليه وسلم للمرأة التي كانت تصرع وتنكشف بالعافية إن لم تصبر أو بأن لا تنكشف إن صبرت ولها الجنة وما ظهر في ذلك من آثار النبوة.','2621','466','0');
INSERT INTO [t7478] VALUES (497,'باب ما جاء في استئذان الحمى على رسول الله صلى الله عليه وسلم وإرساله إياها إلى أهل قباء لتكون لهم كفارة، وظهور ما ظهر في ذلك من آثار النبوة.','2623','466','0');
INSERT INTO [t7478] VALUES (498,'باب ما جاء في رشه على جابر بن عبد الله من وضوئه حتى عقل بعد ما كان لا يعقل.','2627','466','0');
INSERT INTO [t7478] VALUES (499,'باب ما جاء في أمره بالغسل للمعين، وما ظهر فيه من الشفاء.','2628','466','0');
INSERT INTO [t7478] VALUES (500,'باب ما جاء في أمره الرجل الذي شكا إليه استطلاق بطن أخيه بسقي العسل، وما جعل الله تعالى فيه من الشفاء، وليس ذلك من الطب بسبيل.','2629','466','0');
INSERT INTO [t7478] VALUES (501,'باب ما في تعليمه الضرير ما كان فيه شفاؤه حين لم يصبر وما ظهر في ذلك من آثار النبوة','2631','466','0');
INSERT INTO [t7478] VALUES (502,'باب ما جاء في تعليمه عائشة رضي الله عنها دعاء الحمى فقالته فذهبت','2634','466','0');
INSERT INTO [t7478] VALUES (503,'باب ما جاء في دعائه لصاحب القرحة حتى صح وبرئت القرحة','2635','466','0');
INSERT INTO [t7478] VALUES (504,'باب ما جاء في الدعاء الذي علمه أبا بكر رضي الله عنه في الدين فدعا به فقضى الله عنه دينه','2636','466','0');
INSERT INTO [t7478] VALUES (505,'باب ما جاء في نفثه في عينين كانتا مبيضتين لا يبصر صاحبهما بهما حتى أبصر','2638','466','0');
INSERT INTO [t7478] VALUES (506,'باب في نفثه صلى الله عليه وسلم في يد محمد بن حاطب وقد احترقت حتى برئت','2639','466','0');
INSERT INTO [t7478] VALUES (507,'باب ما جاء في نفثه في كف شرحبيل الجعفي ووضع كفه على السلعة التي كانت بكفه حتى ذهبت','2641','466','0');
INSERT INTO [t7478] VALUES (508,'باب ما جاء في تفله في جراحة خبيب بن إساف ويقال: ابن يسار [١] ، وبرئهه [٢]','2643','466','0');
INSERT INTO [t7478] VALUES (509,'باب ما جاء في دعائه لعلي بن أبي طالب رضي الله عنه ولغيره بالشفاء وإجابة الله تعالى له فيما دعاه.','2644','466','0');
INSERT INTO [t7478] VALUES (510,'باب ما جاء في المرأتين اللتين اغتابتا وهما صائمتان، وما ظهر في ذلك من آثار النبوة ودلالة صدق القرآن، وفيه حديث الصبي الذي كان يجن فدعا له فخرج من جوفه جرو أسود.','2651','466','0');
INSERT INTO [t7478] VALUES (511,'باب ما جاء في دعاء النبي صلى الله عليه وسلم لأبي بن كعب [١]- رضي الله عنه - حين شك في القراءة وإجابة الله تعالى له فيما دعاه في الحال','2653','466','0');
INSERT INTO [t7478] VALUES (512,'باب ما جاء في دعاء رسول الله صلى الله عليه وسلم لسعد بن أبي وقاص - رضي الله عنه - باستجابة الدعاء، وما ظهر من إجابة الله تعالى دعاء رسوله فيه','2654','466','0');
INSERT INTO [t7478] VALUES (513,'باب ما جاء في دعائه لعبد الله بن عباس - رضي الله عنه - بالفقه في الدين والعلم بالتأويل وإجابة الله دعاءه فيه','2657','466','0');
INSERT INTO [t7478] VALUES (514,'باب دعاؤه لأنس بن مالك الأنصاري رضي الله عنه بكثرة المال والولد وإجابة الله تعالى له فيه.','2659','466','0');
INSERT INTO [t7478] VALUES (515,'باب ما جاء في دعائه صلى الله عليه وسلم بالبركة لحمل أم سليم من أبي طلحة','2663','466','0');
INSERT INTO [t7478] VALUES (516,'باب ما جاء في إشارته على أبي هريرة الدوسي رضي الله عنه وغيره بما يكون سببا للحفظ وإجابة أبي هريرة رضي الله عنه إليه، وتحقيق الله سبحانه قول رسول الله صلى الله عليه وسلم وما ظهر فيه من آثار النبوة.','2666','466','0');
INSERT INTO [t7478] VALUES (517,'باب ما جاء في دعائه لأم أبي هريرة بالهداية وإجابة الله تعالى له فيها.','2668','466','0');
INSERT INTO [t7478] VALUES (518,'باب ما جاء في الشاب الذي لم ينفتح لسانه بالشهادة عند الموت، حتى رضيت عنه والدته.','2670','466','0');
INSERT INTO [t7478] VALUES (519,'باب ما جاء في اليهودي الذي شمته فقال له: هداك الله، فأسلم إن صح.','2672','466','0');
INSERT INTO [t7478] VALUES (520,'باب ما جاء في دعائه صلى الله عليه وسلم للسائب [بن يزيد] [١] رضي الله عنه، وما ظهر فيه ببركة دعائه من الآثار.','2673','466','0');
INSERT INTO [t7478] VALUES (521,'باب ما روي في شأن اليهودي الذي أخذ من لحية النبي صلى الله عليه وسلم وما ظهر ذلك من آثار النبوة','2675','466','0');
INSERT INTO [t7478] VALUES (522,'باب ما جاء في شأن أبي زيد: عمرو بن أخطب الأنصاري [١] رضي الله عنه ودعائه له وما ظهر في ذلك من آثار النبوة','2676','466','0');
INSERT INTO [t7478] VALUES (523,'باب ما جاء في مسحه صلى الله عليه وسلم رأس محمد بن أنس [١] ، وحنظلة [٢] ، وعينيهما، وما ظهر في ذلك من آثار النبوة','2678','466','0');
INSERT INTO [t7478] VALUES (524,'باب ما روي في شأن قتادة بن ملحان وما ظهر على وجهه ببركة مسح النبي صلى الله عليه وسلم إياه من النور','2682','466','0');
INSERT INTO [t7478] VALUES (525,'باب ما جاء في دعائه صلى الله عليه وسلم لعبد الرحمن بن عوف رضي الله عنه بالبركة فكثر ماله حتى صولحت امرأة من نسائه من ربع الثمن على ثمانين ألفا.','2683','466','0');
INSERT INTO [t7478] VALUES (526,'باب ما جاء في دعائه صلى الله عليه وسلم لعروة البارقي [١] في البركة في بيعه وطهورها بعده في ذلك، وكذلك في تجارة عبد الله بن جعفر بن أبي طالب','2685','466','0');
INSERT INTO [t7478] VALUES (527,'باب ما جاء في دعائه صلى الله عليه وسلم بالبركة لأمته في بكورها','2687','466','0');
INSERT INTO [t7478] VALUES (528,'باب في دعائه صلى الله عليه وسلم لعبد الله بن هشام بالبركة وظهورها بعده','2688','466','0');
INSERT INTO [t7478] VALUES (529,'باب ما روي في دعائه بإذهاب البرد عن أهل مسجده وإجابة الله تعالى دعاءه','2689','466','0');
INSERT INTO [t7478] VALUES (530,'باب ما جاء في تفله في فم عبد الله بن عامر بن كريز، وما أصابه من بركته','2690','466','0');
INSERT INTO [t7478] VALUES (531,'باب ما جاء في تفله في أفواه المرتضعين يوم عاشوراء فتكفوا به إلى الليل','2691','466','0');
INSERT INTO [t7478] VALUES (532,'باب ما جاء في تحنيكه محمد بن ثابت بن قيس بن شماس وبزاقه في فيه وما ظهر في ذلك ببركته من الآثار','2692','466','0');
INSERT INTO [t7478] VALUES (533,'باب ما جاء في دعائه لزوجين أحدهما يبغض الآخر بالألفة واستجابة الله دعاءه فيهما','2693','466','0');
INSERT INTO [t7478] VALUES (534,'باب ما جاء في شأن من شكا إليه الصداع','2695','466','0');
INSERT INTO [t7478] VALUES (535,'باب ما جاء في دعائه لنابغة [١] وإجابة الله - تعالى - له فيما دعاه به','2697','466','0');
INSERT INTO [t7478] VALUES (536,'باب ما جاء في دعائه - صلى الله عليه وسلم - لأبي أمامة [١] وأصحابه حين سأل الدعاء بالشهادة بالسلامة وإصابة الغنيمة فكان كما دعاه','2699','466','0');
INSERT INTO [t7478] VALUES (537,'باب ما جاء في دعائه صلى الله عليه وسلم لأهل اليمن والشام والعراق بالهداية وما ظهر فيه من الإجابة.','2701','466','0');
INSERT INTO [t7478] VALUES (538,'باب ما جاء في دعائه صلى الله عليه وسلم على من أكل بشماله ودعائه على من كان يختلج بوجهه وغيرهما وما ظهر في كل واحد منهما من آثار النبوة.','2703','466','0');
INSERT INTO [t7478] VALUES (539,'باب ما جاء في قوله للرجل: ضرب [الله] [١] عنقه في سبيل الله، فقتل الرجل في سبيل الله.','2709','466','0');
INSERT INTO [t7478] VALUES (540,'باب ما روي في دعائه صلى الله عليه وسلم على من كذب عليه','2710','466','0');
INSERT INTO [t7478] VALUES (541,'باب ما جاء في دعائه على من احتكر بالجذام وإجابة الله - تعالى - دعاءه فيمن احتكر في زمان عمر - رضي الله عنه -.','2711','466','0');
INSERT INTO [t7478] VALUES (542,'باب ما جاء في دعائه ربه - عز وجل - فيما سحر به وإجابة الله - سبحانه - إياه فيما دعاه.','2712','466','0');
INSERT INTO [t7478] VALUES (543,'باب ما جاء في قلنسوة خالد بن الوليد واستنصاره بما جعل فيها من شعر رسول الله صلى الله عليه وسلم.','2714','466','0');
INSERT INTO [t7478] VALUES (544,'باب ما جاء في استنصار رسول الله صلى الله عليه وسلم بأسماء الله - تعالى - على ركانة [١] في المصارعة ونصرة الله - تعالى - إياه عليه وما روي في تلك القصة من آثار النبوة.','2715','466','0');
INSERT INTO [t7478] VALUES (545,'باب ما جاء في قوله صلى الله عليه وسلم للرماة ارموا وأنا مع ابن الأذرع وما ظهر في ذلك من الآثار','2720','466','0');
INSERT INTO [t7478] VALUES (546,'باب ما جاء في أسماعه صلى الله عليه وسلم خطبته العوائق في خدورهن [١] وهو في موضعه من المسجد','2721','466','0');
INSERT INTO [t7478] VALUES (547,'جماع أبواب أسئلة اليهود وغيرهم واستبرائهم عن أحوال النبي صلى الله عليه وسلم وإسلام من هدي إلى الإسلام منهم','2723','438','0');
INSERT INTO [t7478] VALUES (548,'باب مسائل عبد الله بن سلام [١] رضي الله عنه، وإسلامه حين عرف صدق رسول الله صلى الله عليه وسلم في رسالته','2724','547','0');
INSERT INTO [t7478] VALUES (549,'باب مسائل الحبر، ومعرفته اصابة النبي صلى الله عليه وسلم في جواب مسألته وصدقه في نبوته','2727','547','0');
INSERT INTO [t7478] VALUES (550,'باب ما جاء في مسائل عصابة من اليهود ومعرفة اصابته فيما قال','2730','547','0');
INSERT INTO [t7478] VALUES (551,'باب ما جاء في مسائل اليهوديين ومعرفتهما بصدق النبي صلى الله عليه وسلم في نبوته','2732','547','0');
INSERT INTO [t7478] VALUES (552,'باب رجوعهم إلى النبي صلى الله عليه وسلم في عقوبة الزاني وما ظهر من ذلك من كتمانهم ما أنزل الله تعالى في التوراة من حكمه وصفة نبيه عليه السلام [١]','2733','547','0');
INSERT INTO [t7478] VALUES (553,'باب ما جاء في اليهودي الذي اعترف بصفة النبي صلى الله عليه وسلم في التوراة واسلم عند موته. واليهودي الذي اعترف بوجود صفته حين ناشده','2736','547','0');
INSERT INTO [t7478] VALUES (554,'باب ما جاء في قول الله عز وجل قل إن كانت لكم الدار الآخرة عند الله خالصة من دون الناس فتمنوا الموت إن كنتم صادقين ٢: ٩٤ [١] وإخبار الله تعالى بأنهم لن يتمنوه ابدا فكان كما اخبر، وما روي من احتراق من يهزأ بالأذان ويدعو على المؤذن بالاحتراق','2738','547','0');
INSERT INTO [t7478] VALUES (555,'باب ما جاء في تعجب الحبر الذي سمعه يقرأ سورة يوسف لموافقتها ما في التوراة وسؤال من سأله عن أسماء النجوم التي رآها ساجدة له','2740','547','0');
INSERT INTO [t7478] VALUES (556,'باب مطلب أسماء النجوم التي سجدت ليوسف [عليه السلام] [٢] .','2741','547','0');
INSERT INTO [t7478] VALUES (557,'باب استبراء زيد بن سعنة أحوال النبي صلى الله عليه وسلم حتى إذا وقف عليها وأبصر علامات النبوة فيها أسلم وانقاد','2742','547','0');
INSERT INTO [t7478] VALUES (558,'باب ما روي فيما أصاب من خالف أمره في الرحيل [١]','2746','547','0');
INSERT INTO [t7478] VALUES (559,'باب ما روي في إخباره بما أصاب المشرك - الذي سأل عن كيفية الله سبحانه - من العذاب','2747','547','0');
INSERT INTO [t7478] VALUES (560,'باب ما روي فيما أصاب الذي كذب عليه، وقوله للذين بعثهما إليه: ولا أراكما تدركانه فلم يدركاه','2748','547','0');
INSERT INTO [t7478] VALUES (561,'باب ما جاء في إخباره صلى الله عليه وسلم بأسماء المنافقين وصدقه في ذلك','2750','547','0');
INSERT INTO [t7478] VALUES (562,'باب ما روي في إخباره صلى الله عليه وسلم [الرجل] [١] الذي وصف بالاجتهاد في العبادة بما حدثته نفسه وبغير ذلك من حاله','2751','547','0');
INSERT INTO [t7478] VALUES (563,'باب ما جاء في إخباره المرأة الصائمة بما كان من شأنها في حفظ لسانها','2753','547','0');
INSERT INTO [t7478] VALUES (564,'باب ما جاء في وعده من استعف بالإعفاف ومن استغنى بالإغناء ووجود صدقه في أبي سعيد الخدري وغيره','2754','547','0');
INSERT INTO [t7478] VALUES (565,'باب ما روي في إخبار النبي صلى الله عليه وسلم السائل بما أراد أن يسأله عنه قبل سؤاله','2756','547','0');
INSERT INTO [t7478] VALUES (566,'باب إخباره صلى الله عليه وسلم عن قبر أبي رغال وما فيه من الذهب','2761','547','0');
INSERT INTO [t7478] VALUES (567,'باب ما جاء في إخباره صلى الله عليه وسلم [١] عن امر السفينة','2762','547','0');
INSERT INTO [t7478] VALUES (568,'باب ما جاء في اللحم الذي صار حجرا وإخبار النبي صلى الله عليه وسلم عن سببه فكان كما قال','2763','547','0');
INSERT INTO [t7478] VALUES (569,'باب ما جاء في إخباره بإسلام ابي الدرداء فكان كما اخبر صلى الله عليه وسلم','2765','547','0');
INSERT INTO [t7478] VALUES (570,'باب ما جاء في إخباره بحال من نحر نفسه فكان كما اخبر صلى الله عليه وسلم','2766','547','0');
INSERT INTO [t7478] VALUES (571,'باب ما جاء في إشارته إلى ما صار إليه امر ماعز بن مالك','2767','547','0');
INSERT INTO [t7478] VALUES (572,'باب ما جاء في إخباره من قال في نفسه شعرا في الشكاية عن ولده بذلك إن صحت الرواية','2768','547','0');
INSERT INTO [t7478] VALUES (573,'باب ما جاء في إخباره صاحب الجنبذة بصنيعه. وما ثبت عن ابن عمر انهم كانوا يتقون الكلام والانبساط مخافة ان ينزل فيهم القرآن بما قالوا وفعلوا','2770','547','0');
INSERT INTO [t7478] VALUES (574,'باب ما جاء في إخباره عوف بن مالك بما كان منه في نحر الجزور','2772','547','0');
INSERT INTO [t7478] VALUES (575,'باب امتناع النبي صلى الله عليه وسلم عن أكل الشاة التي أخذت بغير إذن مالكها، وما ظهر في ذلك من حفظ الله تعالى رسوله صلى الله عليه وسلم عن أكل الحرام','2774','547','0');
INSERT INTO [t7478] VALUES (576,'باب ما جاء في إخباره عن السحابة التي مطرت بواد باليمن','2775','547','0');
INSERT INTO [t7478] VALUES (577,'جماع أبواب إخبار النبي صلى الله عليه وسلم بالكوائن بعده، وتصديق الله جل ثناؤه رسوله صلى الله عليه وسلم في جميع ما وعده','2776','438','0');
INSERT INTO [t7478] VALUES (578,'باب إخبار النبي صلى الله عليه وسلم أصحابه بإتمام الله تعالى أمره وإظهاره دينه وتصديق الله سبحانه قوله، قال الله - عز وجل -: هو الذي أرسل رسوله بالهدى ودين الحق ليظهره على الدين كله ولو كره المشركون ٩: ٣٣.','2779','577','0');
INSERT INTO [t7478] VALUES (579,'باب قول الله - عز وجل -: وعد الله الذين آمنوا منكم وعملوا الصالحات ليستخلفنهم في الأرض كما استخلف الذين من قبلهم وليمكنن لهم دينهم الذي ارتضى لهم وليبدلنهم من بعد خوفهم أمنا يعبدونني لا يشركون بي شيئا ومن كفر بعد ذلك فأولئك هم الفاسقون ٢٤: ٥٥ [','2781','577','0');
INSERT INTO [t7478] VALUES (580,'باب ما جاء في إخبار النبي صلى الله عليه وسلم عن خلفاء يكونون بعده فكانوا','2802','577','0');
INSERT INTO [t7478] VALUES (581,'باب ما جاء في إخباره عن ملوك يكونون بعد الخلفاء فكانوا كما أخبر صلى الله عليه وسلم','2803','577','0');
INSERT INTO [t7478] VALUES (582,'باب في إخباره صلى الله عليه وسلم عن مدة الخلافة بعده، ثم تكون ملكا فكان كما أخبر','2805','577','0');
INSERT INTO [t7478] VALUES (583,'باب ما جاء في إخباره بأن الله تعالى يأبى ثم المؤمنون أن يكون بعده الخليفة إلا أبا بكر وإن لم يستخلفه في غير الصلاة نصا فكان كما أخبر','2807','577','0');
INSERT INTO [t7478] VALUES (584,'باب ما جاء في إخباره عن رؤياه - ورؤيا الأنبياء عليهم السلام وحي - بقصر مدة أبي بكر بعده وزيادة مدة عمر بن الخطاب بعد أبي بكر فكانا كما أخبر','2808','577','0');
INSERT INTO [t7478] VALUES (585,'باب ما جاء في الإخبار عن الولاة بعده وما وقع من الفتنة في آخر عهد عثمان، ثم في أيام علي - رضي الله عنهما - حتى لم يستقم له أمر الولاية كما استقام لأصحابه واغتمام النبي صلى الله عليه وسلم بذلك','2810','577','0');
INSERT INTO [t7478] VALUES (586,'باب ما جاء في إخباره عن صدق أبي بكر في إيمانه وشهادته لعمر وعثمان بالشهادة فاستشهدا بعده كما أخبر، مع ما فيه من امره الجبل بالثبوت بعد الرجفة وضربه إياه برجله فسكن','2814','577','0');
INSERT INTO [t7478] VALUES (587,'باب ما جاء في إخباره عن صدق أبي بكر في تصديقه وشهادته لعمر وعثمان وعلي وطلحة والزبير بالشهادة فاستشهدوا كما أخبر','2816','577','0');
INSERT INTO [t7478] VALUES (588,'باب ما جاء في دعائه لعكاشة بن محصن وإدراكه الشهادة ببركة دعائه وظهور دلالات الصدق فيما أخبر عن حاله','2817','577','0');
INSERT INTO [t7478] VALUES (589,'باب ما جاء في إخباره عن حال ثابت بن قيس بن شماس رضي الله عنه وشهادته له بالشهادة والجنة فقتل شهيدا يوم مسيلمة في عهد أبي بكر الصديق رضي الله عنه وما ظهر في رؤيا من رآه من الآثار','2818','577','0');
INSERT INTO [t7478] VALUES (590,'باب ما جاء في إخباره صلى الله عليه وسلم بكفاية الله تعالى عباده شر الأسود العنسي ومسيلمة الكذابين فقتلا جميعا','2822','577','0');
INSERT INTO [t7478] VALUES (591,'باب ما جاء في تحذيره الرجوع إلى الكفر بعد الإيمان وإخباره بالتبديل الذي وجد بعد وفاته حتى قاتلهم أبو بكر الصديق - رضي الله عنه - بمن ثبت على دينه من أهل الإسلام','2824','577','0');
INSERT INTO [t7478] VALUES (592,'باب ما جاء في إخباره صلى الله عليه وسلم بأن المسلمين [١] لا يعبدون الشيطان في جزيرة العرب - يريد أصحابه فمن بعدهم فكان كما قال. ثم كان ما اخبر به من التحريش بينهم في آخر أيامه','2827','577','0');
INSERT INTO [t7478] VALUES (593,'باب ما جاء في إخباره ابنته بوفاته وبأنها أول اهل بيته لحوقا به فكانا كما أخبر','2828','577','0');
INSERT INTO [t7478] VALUES (594,'باب إخباره بما يرجع إليه مقال سهيل بن عمرو بن عبد شمس [١] ورجوعه إلى ذلك فكان كما اخبر','2831','577','0');
INSERT INTO [t7478] VALUES (595,'باب ما جاء في إخبار النبي صلى الله عليه وسلم عن حال البراء بن مالك الأنصاري [١] بأنه ممن لو أقسم على الله لأبره وتصديق الله جل ثناؤه قول رسول الله صلى الله عليه وسلم فيه رضى الله عنه','2832','577','0');
INSERT INTO [t7478] VALUES (596,'باب ما جاء في إخبار النبي صلى الله عليه وسلم بمحدثين كانوا في الأمم وأنه إن يكن في أمته منهم احد فعمر بن الخطاب فكان كما أخبر','2833','577','0');
INSERT INTO [t7478] VALUES (597,'باب ما جاء في إخبار النبي صلى الله عليه وسلم بمن يكون أسرع لحوقا به زوجاته [١] فكان كما أخبر','2835','577','0');
INSERT INTO [t7478] VALUES (598,'باب ما جاء في إخبار النبي صلى الله عليه وسلم بأن خبر التابعين أويس القرني [١] ووصفه إياه وقدومه على امير المؤمنين عمر بن الخطاب رضي الله عنه على الصفة التي ذكرها رسول الله صلى الله عليه وسلم وما ظهر في ذلك من آثار النبوة','2839','577','0');
INSERT INTO [t7478] VALUES (599,'باب ما روي في إخباره بأنه يكون في أمته رجل يقال له صلة بن أشيم [١] فكان بعد وفاته على صفته','2843','577','0');
INSERT INTO [t7478] VALUES (600,'باب ما جاء في إخباره بولادة غلام بعده لعلي بن أبي طالب رضي الله عنه وإذنه إياه في أن يسميه باسمه ويكنيه بكنيته فكان ذلك في محمد ابن الحنفية [١]','2844','577','0');
INSERT INTO [t7478] VALUES (601,'باب في إخباره أم ورقة [١] بأنها تدرك [٢] الشهادة فاستشهدت في عهد عمر ابن الخطاب - رضي الله عنه','2845','577','0');
INSERT INTO [t7478] VALUES (602,'باب ما جاء في إخبار النبي صلى الله عليه وسلم بالطاعون الذي وقع بالشام في أصحابه في عهد عمر بن الخطاب - رضي الله عنه -','2847','577','0');
INSERT INTO [t7478] VALUES (603,'باب ما جاء في إخبار النبي صلى الله عليه وسلم بالفتنة التي تموج موج البحر وأنها لا تكون في أيام أبي بكر وعمر رضي الله عنهما حتى يكسر بابها وكسر بابها قتل عمر رضي الله عنه','2850','577','0');
INSERT INTO [t7478] VALUES (604,'باب ما جاء في إخبار النبي صلى الله عليه وسلم بالبلوى التي أصابت عثمان بن عفان رضي الله عنه والفتنة التي ظهرت في أيامه والعلامة التي دلت على قبره وقبر صاحبيه [رضي الله عنهما]','2852','577','0');
INSERT INTO [t7478] VALUES (605,'باب ما جاء في إخباره عبد الله بن مسعود رضي الله عنه وغيره بأنهم يدركون أقواما يصلون الصلاة لغير وقتها وما ظهر من صدقه فيما قال. وما جاء في إخباره عما لأطفال عقبة بن أبي معيط وظهور آثار صدقه فيما أخبر','2860','577','0');
INSERT INTO [t7478] VALUES (606,'باب ما جاء في إخباره عن حال ابي ذر - رضي الله عنه - عند موته وما أوصاه به من الخروج عن المدينة عند ظهور الفتن','2865','577','0');
INSERT INTO [t7478] VALUES (607,'باب ما جاء في إخباره عن حال أبي الدرداء [١] رضي الله عنه وانه يموت قبل وقوع الفتن فكان كما اخبر - وما جاء في رؤيا عامر بن ربيعة','2867','577','0');
INSERT INTO [t7478] VALUES (608,'باب ما جاء في إخباره بالفتن التي ظهرت في [آخر] [١] ايام عثمان بن عفان وفي ايام علي بن ابي طالب رضي الله عنهما - وان القتل للموقن منهم كفارة، واختياره لمحمد بن مسلمة البدري [٢]- رضي الله عنه - وغيره ان يكفوا، ثم إخباره بأن محمد بن مسلمة لا تضره','2869','577','0');
INSERT INTO [t7478] VALUES (609,'باب [١] ما جاء في إخباره بأن واحدة من أمهات المؤمنين تنبح عليها كلاب الحوأب وما روي في إشارته على علي - رضي الله عنه - بأن يرفق بها وما روي في توبتها من خروجها وتلهفها على ما خفي عليها من ذلك وكونها من أهل الجنة مع زوجها - صلى الله عليه وسلم، و','2874','577','0');
INSERT INTO [t7478] VALUES (610,'باب ما جاء في إخباره عن قتال الزبير مع علي رضي الله عنهما وترك الزبير قتاله حين ذكره','2878','577','0');
INSERT INTO [t7478] VALUES (611,'باب ما روي في إخباره صلى الله عليه وسلم عن قتل زيد بن صوحان [١] شهيدا فكان كما أخبر. قتل يوم الجمل','2880','577','0');
INSERT INTO [t7478] VALUES (612,'باب ما جاء في إخباره [صلى الله عليه وسلم] [١] باقتتال فئتين عظيمتين تكون بينهما مقتلة عظيمة ودعواهما واحدة يريد - والله أعلم - دعوى الإسلام فكان كما أخبر في حرب صفين','2882','577','0');
INSERT INTO [t7478] VALUES (613,'باب ما جاء في إخباره عن الفئة الباغية منهما بما جعله علامة لمعرفتهم','2884','577','0');
INSERT INTO [t7478] VALUES (614,'باب [١] ما جاء في إخباره عن الحكمين اللذين بعثا في زمان علي - رضي الله عنه -','2887','577','0');
INSERT INTO [t7478] VALUES (615,'باب ما جاء في إخباره بأن مارقة تمرق بين هاتين الطائفتين تقتلهم أولى الطائفتين بالحق فكان كما أخبر. خرج أهل النهروان وقتلهم أولى الطائفتين بالحق','2888','577','0');
INSERT INTO [t7478] VALUES (616,'باب ما جاء في إخباره بخروجهم وسيماهم والمخدج الذي فيهم [وأجر من قتلهم] [١] واسم من قتل المخدج منهم وإشارته على علي رضي الله عنه بقتالهم وما ظهر بوجود الصدق في إخباره من آثار النبوة','2890','577','0');
INSERT INTO [t7478] VALUES (617,'باب ما جاء في إخباره زوجته ميمونة بنت الحارث انها لا تموت بمكة فماتت بسرف سنة ثمان وثلاثين','2901','577','0');
INSERT INTO [t7478] VALUES (618,'باب ما روى في إخباره بتأمير علي - رضي الله عنه - وقتله فكانا كما أخبر','2902','577','0');
