INSERT INTO [t7478] VALUES (621,'باب ما جاء في إخبار النبي صلى الله عليه وسلم بناس من أمته يركبون البحر غزاة في سبيل الله كالملوك على الأسرة. وشهادته بأن أم حرام بنت ملحان منهم - وتصديق الله سبحانه قوله في زمن معاوية بن أبي سفيان ...','2914','577','0');
INSERT INTO [t7478] VALUES (622,'باب ما جاء في إخباره بتكلم رجل من أمته بعد موته من خير التابعين فكان كما أخبر','2918','577','0');
