INSERT INTO [t7478] VALUES (633,'باب ما جاء في إخباره بمن يكون بعده من الكذابين وإشارته إلى من يكون منهم من ثقيف فكان كما أخبر','2944','577','0');
INSERT INTO [t7478] VALUES (634,'باب ما جاء في إخباره بالمبير الذي يخرج من ثقيف وتصديق الله سبحانه قوله في الحجاج بن يوسف الثقفي غفر الله لنا ولجميع المسلمين','2949','577','0');
INSERT INTO [t7478] VALUES (635,'باب ما جاء في إخباره [صلى الله عليه وسلم] [١] بالشر الذي يكون بعد الخير الذي جاء به، ثم بالخير الذي يكون بعد ذلك، ثم بالشر الذي يكون بعده. وما يستدل به على إخباره بعمر بن عبد العزيز - رضي الله عنه - وإشارته إلى ما ظهر من عدله وإنصافه في ولايته','2954','577','0');
