INSERT INTO [t7478] VALUES (646,'باب ما جاء في إخباره بما دعا لأمته وبما أجيب فيه وبما لم يجب وبما كان يخاف عليهم منه وبأن السيف إذا وضع فيهم لم يرفع عنهم وبما وقع من الردة والكذابين وبطائفة من أمته لا يزالون على الحق ظاهرين حتى يأتي أمر الله وصدقه في جميع ما أخبر به صلى ال','2990','577','0');
INSERT INTO [t7478] VALUES (647,'باب ما جاء في إخباره بكون المعادن وأنه يكون فيها من شرار خلق الله عز وجل فكان كما أخبر','2994','577','0');
