INSERT INTO [t7478] VALUES (711,'باب ما روى في الأمان من السرق والحرق','3132','684','0');
INSERT INTO [t7478] VALUES (712,'باب ما جاء في مصارعة امير المؤمنين عمر بن الخطاب (رضي الله عنه) شيطانا لقيه.','3134','684','0');
INSERT INTO [t7478] VALUES (713,'باب ما جاء في قتال عمار بن ياسر مع الجن، وإخبار النبي صلى الله عليه وسلم عنه','3135','684','0');
INSERT INTO [t7478] VALUES (714,'باب ما جاء في سؤال إبليس عن الدين ليشكك [١] الناس فيه','3136','684','0');
INSERT INTO [t7478] VALUES (715,'باب ما ظهر على من ارتد عن الإسلام في وقت النبي صلى الله عليه وسلم ومات على ردته من النكال، ثم من قتل من شهد بالحق من ذلك، وما في كل واحد منهما من دلائل النبوة.','3137','684','0');
INSERT INTO [t7478] VALUES (716,'باب ما أعطي الأنبياء من الآيات وما أعطي نبينا محمد صلى الله عليه وسلم من الآية الكبرى، التي عجز عنها قومه، حتى آمن عليها من أراد الله به منهم خيرا.','3140','684','0');
INSERT INTO [t7478] VALUES (717,'باب ما جاء في نزول القرآن وهو نزول الملك بما حفظ من كلام الله - عز وجل - إلى السماء الدنيا، ثم نزوله به مفصلا على نبينا صلى الله عليه وسلم من وقت البعث إلى حال الوفاة [صلى الله عليه وسلم]','3142','684','0');
INSERT INTO [t7478] VALUES (718,'باب تتابع الوحي عليه في آخر عمره','3144','684','0');
INSERT INTO [t7478] VALUES (719,'باب آخر سورة نزلت جميعا وما فيها من نعيه صلى الله عليه وسلم','3145','684','0');
INSERT INTO [t7478] VALUES (720,'باب آخر سورة نزلت وآخر آية نزلت فيما قال البراء بن عازب، ثم فيما قال غيره','3147','684','0');
INSERT INTO [t7478] VALUES (721,'باب ذكر السور التي نزلت بمكة والتي نزلت بالمدينة','3153','684','0');
INSERT INTO [t7478] VALUES (722,'باب ما جاء في عرض القرآن على النبي صلى الله عليه وسلم في كل عام مرة، وعرضه عليه في العام الذي قبض فيه مرتين','3157','684','0');
INSERT INTO [t7478] VALUES (723,'باب ما جاء في تأليف القرآن [١] ، وقوله عز وجل إنا نحن نزلنا الذكر، وإنا له لحافظون ١٥: ٩ [٢] وما ظهر من الآيات فيما نسخ من رسمه وفيما لم ينسخ منه','3158','684','0');
INSERT INTO [t7478] VALUES (724,'جماع أبواب مرض رسول الله صلى الله عليه وسلم ووفاته وما ظهر في ذلك من آثار النبوة، ودلالات الصدق.','3172','662','0');
INSERT INTO [t7478] VALUES (725,'باب ما جاء في نعي رسول الله صلى الله عليه وسلم نفسه إلى أبي مويهبة مولاه [١] ، وإخباره إياه بما اختاره لنفسه فيما خير فيه.','3173','724','0');
INSERT INTO [t7478] VALUES (726,'باب ما جاء في نعيه نفسه صلى الله عليه وسلم إلى ابنته فاطمة رضي الله عنها، وإخباره إياها بأنها أول أهل بيته به لحوقا، فكان كما قال.','3175','724','0');
INSERT INTO [t7478] VALUES (727,'باب ما جاء في إشارته إلى عائشة رضي الله عنها في ابتداء مرضه بما يشبه النعي، ثم إخباره إياها بحضور أجله وما في حديثها من أنه صلى الله عليه وسلم توفي شهيدا','3179','724','0');
INSERT INTO [t7478] VALUES (728,'باب ما جاء في استئذانه [أزواجه] [١] في أن يمرض في بيت عائشة - رضي الله عنها -، ثم ما جاء في اغتساله وخروجه إلى الناس، وصلاته بهم وخطبته إياهم ونعيه نفسه إليهم، وإشارته إلى أمن الناس عليه في صحبته، وماله ليدلهم بذلك على عظم شأنه وكبر محله [علي','3184','724','0');
INSERT INTO [t7478] VALUES (729,'باب ما روي في خطبة رسول الله صلى الله عليه وسلم من بذله نفسه وماله بحق إن كان لأحد قبله حتى يلقى الله تعالى، وليست لأحد عنده مظلمة، وما ذكر بها لعمر بن الخطاب (رضي الله عنه) .','3190','724','0');
INSERT INTO [t7478] VALUES (730,'باب ما جاء في همه بأن يكتب لأصحابه كتابا حين اشتد به الوجع يوم الخميس، ثم بدا له اعتمادا على ما وعده الله - تعالى - من حفظ دينه، وإظهار أمره [صلى الله عليه وسلم]','3192','724','0');
INSERT INTO [t7478] VALUES (731,'باب ما جاء في أمره، حين اشتد به المرض - أبا بكر الصديق رضي الله عنه أن يصلي بالناس','3197','724','0');
INSERT INTO [t7478] VALUES (732,'باب ما جاء في آخر صلاة صلاها رسول الله صلى الله عليه وسلم بالناس، من أولها إلى آخرها، وأول صلاة أمر أبا بكر الصديق أن يصليها بالناس، والصلاة التي حضرها حين وجد من نفسه خفة وصلاة أبي بكر بهم فيما بينهما أياما','3200','724','0');
INSERT INTO [t7478] VALUES (733,'باب ما جاء في تقرير النبي صلى الله عليه وسلم أبا بكر على آخر صلاة صلاها بالناس في حياته وإشارته إليهم بإتمامها خلفه. وارتضائه صنيعهم، وذلك في صلاة الفجر من يوم الإثنين، وهو اليوم الذي توفي فيه، وقول من زعم أنه خرج، فصلى منها ركعة خلف أبي بكر ب','3205','724','0');
INSERT INTO [t7478] VALUES (734,'باب ما يؤثر عنه صلى الله عليه وسلم من ألفاظه في مرض موته، وما جاء في حاله عند وفاته.','3214','724','0');
INSERT INTO [t7478] VALUES (735,'باب ما يستدل به على ان النبي صلى الله عليه وسلم لم يستخلف أحدا بعينه، ولم يوص إلى أحد بعينه، في أمر أمته، وإنما نبه على الخلافة بما ذكرنا من امر الصلاة','3232','724','0');
INSERT INTO [t7478] VALUES (736,'باب ذكر الحديث الذي روي عن ابن مسعود [رضي الله عنه] [١] عن النبي صلى الله عليه وسلم في نعيه نفسه إلى أصحابه. وما أوصاهم به، وإسناده ضعيف بالمرة','3242','724','0');
INSERT INTO [t7478] VALUES (737,'باب ما جاء في الوقت واليوم والشهر [والسنة] [١] التي توفي فيها رسول الله صلى الله عليه وسلم، وفي مدة مرضه.','3244','724','0');
