INSERT INTO [t7478] VALUES (745,'باب ما جاء في موضع قبر رسول الله صلى الله عليه وسلم','3270','724','0');
INSERT INTO [t7478] VALUES (746,'باب ما جاء في صفة قبر النبي صلى الله عليه وسلم وصاحبيه','3274','724','0');
INSERT INTO [t7478] VALUES (747,'باب ما جاء في عظم المصيبة التي نزلت بالمسلمين بوفاة رسول الله صلى الله عليه وسلم','3276','724','0');
INSERT INTO [t7478] VALUES (748,'باب معرفة أهل الكتاب بوفاة رسول الله صلى الله عليه وسلم قبل وقوع الخبر إليهم بما يجدونه مكتوبا عندهم في التوراة والإنجيل، بصفته، وصورته، وما ظهر في ذلك من آثار النبوة.','3281','724','0');
