INSERT INTO [t754] VALUES (1,'بسم الله الرحمن الرحيم','1','0','0');
INSERT INTO [t754] VALUES (2,'ولد أبو الطيب','2','0','0');
INSERT INTO [t754] VALUES (3,'١','2','0','0');
INSERT INTO [t754] VALUES (4,'أبلى الهوى أسفا يوم النوى بدني ... وفرق الهجر بين الجفن والوسن','2','3','0');
INSERT INTO [t754] VALUES (5,'روح تردد في مثل الخلال إذا ... أطارت الريح عنه الثوب لم يبن','2','3','0');
INSERT INTO [t754] VALUES (6,'كفى بجسمي نحولا أنني رجل ... لولا مخاطبتي إياك لم ترني','3','3','0');
INSERT INTO [t754] VALUES (7,'بأبي من وددته فافترقنا ... وقضى الله بعد ذاك اجتماعا','3','3','0');
INSERT INTO [t754] VALUES (8,'فافترقنا حولا فلما التقينا ... كان تسليمه علي وداعا','3','3','0');
INSERT INTO [t754] VALUES (9,'أهلا بدار سباك أغيدها ... أبعد ما بان عنك خردها','3','3','0');
INSERT INTO [t754] VALUES (10,'ظلت بها تنطوي على كبد ... نضيجة فوق خلبها يدها','3','3','0');
INSERT INTO [t754] VALUES (11,'يا حاديى عيسها وأحسبني ... أوجد ميتا قبيل أفقدها','4','3','0');
INSERT INTO [t754] VALUES (12,'قفا قليلا بها علي فلا ... أقل من نظرة أزودها','4','3','0');
INSERT INTO [t754] VALUES (13,'ففي فؤاد المحب نار هوى ... أحر نار الجحيم أبردها','4','3','0');
INSERT INTO [t754] VALUES (14,'شاب من الهجر فرق لمته ... فصار مثل الدمقس أسودها','4','3','0');
INSERT INTO [t754] VALUES (15,'بانوا بخرعوبة لها كفل ... يكاد عند القيام يقعدها','4','3','0');
INSERT INTO [t754] VALUES (16,'ربحلة اسم مقبلها ... سجلة أبيض مجردها','4','3','0');
INSERT INTO [t754] VALUES (17,'يا عاذل العاشقين دع فئة ... أضلها الله كيف ترشدها','5','3','0');
INSERT INTO [t754] VALUES (18,'ليس يحيك الملام في همم ... أقربها منك عنك أبعدها','5','3','0');
INSERT INTO [t754] VALUES (19,'بيئس الليالي سهرت من طربي ... شوقا إلى من يبيت يرقدها','5','3','0');
INSERT INTO [t754] VALUES (20,'أحييتها والدموع تنجدني ... شؤونها والظلام ينجدها','5','3','0');
INSERT INTO [t754] VALUES (21,'لا ناقتي تقبل الرديف ولا ... بالسوط يوم الرهان أجهدها','5','3','0');
INSERT INTO [t754] VALUES (22,'شراكها كورها ومشفرها ... زمامها والشسوع مقودها','5','3','0');
INSERT INTO [t754] VALUES (23,'أشد عصف الرياح يسبقه ... تحتي من خطوبها تأيدها','5','3','0');
INSERT INTO [t754] VALUES (24,'في مثل ظهر المجن متصل ... بمثل بطن المجن قرددها','5','3','0');
INSERT INTO [t754] VALUES (25,'مرتميات بنا إلى ابن عبيد ... الله غيطانها وفدفدها','6','3','0');
INSERT INTO [t754] VALUES (26,'إلى فتى يصدر الرماح وقد ... أنهلها في القلوب موردها','6','3','0');
INSERT INTO [t754] VALUES (27,'له أياد إلي سابقة ... أعد منها ولا أعددها','6','3','0');
INSERT INTO [t754] VALUES (28,'يعطى فلا مطله يكدرها ... به ولا منه ينكدها','6','3','0');
INSERT INTO [t754] VALUES (29,'خير قريش أبا وأمجدها ... أكثرها نائلا وأجودها','6','3','0');
INSERT INTO [t754] VALUES (30,'أطعنها بالقناة أضربها ... بالسيف جحجاحها مسودها','6','3','0');
INSERT INTO [t754] VALUES (31,'أفرسها فارسا وأطولها ... باعا ومغوارها وسيدها','6','3','0');
INSERT INTO [t754] VALUES (32,'تاج لوي بن غالب وبه ... سما لها فرعها ومحتدها','6','3','0');
INSERT INTO [t754] VALUES (33,'شمس ضحاها هلال ليلتها ... در تقاصيرها زبرجدها','6','3','0');
INSERT INTO [t754] VALUES (34,'يا ليت بي ضربة أتيح لها ... كما أتيحت له محمدها','7','3','0');
INSERT INTO [t754] VALUES (35,'أثر فيها وفي الحديد وما ... أثر في وجهه مهندها','7','3','0');
INSERT INTO [t754] VALUES (36,'فاغتبطت إذ رأت تزينها ... بمثله والجراح تحسدها','7','3','0');
INSERT INTO [t754] VALUES (37,'وأيقن الناس أن زارعها ... بالمكر في قلبه سيحصدها','7','3','0');
INSERT INTO [t754] VALUES (38,'أصبح حساده وانفسهم ... يحدرها خوفه ويصعدها','7','3','0');
INSERT INTO [t754] VALUES (39,'تبكى على الأنصل الغمود إذا ... أنذرها أنه يجردها','7','3','0');
INSERT INTO [t754] VALUES (40,'لعلمها أنها تصير دما ... وأنه في الرقاب يغمدها','7','3','0');
INSERT INTO [t754] VALUES (41,'أطلقها فالعدو من جزع ... يذمها والصديق يحمدها','7','3','0');
INSERT INTO [t754] VALUES (42,'تنقد النار من مضاربها ... وصب ماء الرقاب يخمدها','7','3','0');
INSERT INTO [t754] VALUES (43,'إذا أضل الهمام مهجته ... يوما فأطرافهن منشدها','7','3','0');
INSERT INTO [t754] VALUES (44,'قد أجمعت هذه الخليقة لي ... أنك يا ابن النبي أوحدها','8','3','0');
INSERT INTO [t754] VALUES (45,'وأنك بالأمس كنت محتلما ... شيخ معد وأنت أمردها','8','3','0');
INSERT INTO [t754] VALUES (46,'وكم وكم نعمة مجللة ... ربيتها كان منك مولدها','8','3','0');
INSERT INTO [t754] VALUES (47,'وكم وكم حاجة سمحت لها ... أقرب مني إلي موعدها','8','3','0');
INSERT INTO [t754] VALUES (48,'ومكرمات مشت على قدم ال ... بر إلي منزلي ترددها','8','3','0');
INSERT INTO [t754] VALUES (49,'أقر جلدي بها علي فلا ... أقدر حتى الممات أمجدها','8','3','0');
INSERT INTO [t754] VALUES (50,'فعد بها لا عدمتها أبدا ... خير صلات الكريم أعودها','8','3','0');
INSERT INTO [t754] VALUES (51,'لا تحسن الوفرة حتى ترى ... منشورة الضفرين يوم القتال','8','3','0');
INSERT INTO [t754] VALUES (52,'على فتى معتقل صعدة ... يعلها من كل وافي السبال','8','3','0');
INSERT INTO [t754] VALUES (53,'لقد أصبح الجرذ المستغير ... أسير المنايا صريع العطب','8','3','0');
INSERT INTO [t754] VALUES (54,'رماه الكناني والعامري ... وتلاه للوجه فعل العرب','8','3','0');
INSERT INTO [t754] VALUES (55,'كلا الرجلين أتلى قتله ... فأيكما غل حر السلب','9','3','0');
INSERT INTO [t754] VALUES (56,'وأيكما كان من خلفه ... فإن به عضة في الذنب','9','3','0');
INSERT INTO [t754] VALUES (57,'لما نسبت فكنت ابنا لغير أب ... ثم اختبرت فلم ترجع إلى أدب','9','3','0');
INSERT INTO [t754] VALUES (58,'سميت بالذهبي اليوم تسمية ... مشتقة من ذهاب العقل لا الذهب','9','3','0');
INSERT INTO [t754] VALUES (59,'ملقب بك ما لقبت ويك به ... يا أيها اللقب الملقى على اللقب','9','3','0');
INSERT INTO [t754] VALUES (60,'كفي أراني ويك لومك ألوما ... هم أقام على فواد أنجما','9','3','0');
INSERT INTO [t754] VALUES (61,'وخيال جسم لم يخل له الهوى ... لحما فيخله السقام ولا دما','9','3','0');
INSERT INTO [t754] VALUES (62,'وخفوق قلب لو رأيت لهيبه ... يا جنتي لظننت فيه جهنما','9','3','0');
INSERT INTO [t754] VALUES (63,'وإذا سحابة صد حب أبرقت ... تركت حلاوة كل حب علقما','9','3','0');
INSERT INTO [t754] VALUES (64,'يا وجه داهية الذي لولاك ما ... أكل الضنا جسدي ورض الأعظما','9','3','0');
INSERT INTO [t754] VALUES (65,'إن كان أغناها السلو فإنني ... أمسيت من كبدي ومنها معدما','9','3','0');
INSERT INTO [t754] VALUES (66,'غصن على نقوى فلاة نابت ... شمس النهار تقل ليلا مظلما','10','3','0');
INSERT INTO [t754] VALUES (67,'لم تجمع الأضداد في متشابه ... إلا لتجعلني لغرمي مغنما','10','3','0');
INSERT INTO [t754] VALUES (68,'كصفات أوحدنا أبي الفضل التي ... بهرت فانطق واصفيه فافحما','10','3','0');
INSERT INTO [t754] VALUES (69,'يعطيك مبتدئا فإن أعجلته ... أعطاك معتذرا كمن قد أجرما','10','3','0');
INSERT INTO [t754] VALUES (70,'نصر الفعال على المطال كأنما ... خال السؤال على النوال محرما','10','3','0');
INSERT INTO [t754] VALUES (71,'يا ايها الملك المصفى جوهرا ... من ذات ذي الملكوت أسمى من سما','10','3','0');
INSERT INTO [t754] VALUES (72,'نور تظاهر فيك لاهوتيه ... فتكاد تعلم علم ما لم يعلما','10','3','0');
INSERT INTO [t754] VALUES (73,'ويهم فيك إذا نطقت فصاحة ... من كل عضو منك أن يتكلما','10','3','0');
INSERT INTO [t754] VALUES (74,'أنا مبصر وأظن أني نائم ... من كان يحلم بالآله فأحلما','10','3','0');
INSERT INTO [t754] VALUES (75,'كبر العيان على حتى إنه ... صار اليقين من العيان توهما','11','3','0');
INSERT INTO [t754] VALUES (76,'يا من لجود يديه في أمواله ... نقم تعود على اليتامى أنعما','11','3','0');
INSERT INTO [t754] VALUES (77,'حتى يقول الناس ماذا عاقلا ... ويقول بيت المال ماذا مسلما','11','3','0');
INSERT INTO [t754] VALUES (78,'إذكار مثلك ترك إذكاري له ... إذ لا تريد لما أريد مترجما','11','3','0');
INSERT INTO [t754] VALUES (79,'محبي قيام ما لذالكم النصل ... بريا من الجرحى سليما من القتل','11','3','0');
INSERT INTO [t754] VALUES (80,'أرى من فرندى قطعة في فرنده ... وجودة ضرب الهام في جودة الصقل','11','3','0');
INSERT INTO [t754] VALUES (81,'وخضرة ثوب العيش في الخضرة التي ... أرتك أحمرار الموت في مدرج النمل','11','3','0');
INSERT INTO [t754] VALUES (82,'أمط عنك تشبيهي بما وكأنه ... فما أحد فوقي وما أحد مثلي','12','3','0');
INSERT INTO [t754] VALUES (83,'فذرني وإياه وطرفي وذابلي ... نكن واحدا يلقى الورى وأنظرن فعلي','12','3','0');
INSERT INTO [t754] VALUES (84,'إلى أي حين أنت في زي محرم ... وحتى مت في شقوة وإلى كم','12','3','0');
INSERT INTO [t754] VALUES (85,'وإلا تمت تحت السيوف مكرما ... تمت وتقاسي الذل غير مكرم','12','3','0');
INSERT INTO [t754] VALUES (86,'وثب واثقا بالله وثبة ماجد ... يرى الموت في الهيجا جنى النحل في الفم','12','3','0');
INSERT INTO [t754] VALUES (87,'أحيي وأيسر ما قاسيت ما قتلا ... والبين جار على ضعفي وما عدلا','12','3','0');
INSERT INTO [t754] VALUES (88,'والوجد يقوى كما تقوى النوى أبدا ... والصبر ينحل في جسمي كما نحلا','13','3','0');
INSERT INTO [t754] VALUES (89,'لولا مفارقة الأحباب ما وجدت ... لها المنايا إلى أرواحنا سبلا','13','3','0');
INSERT INTO [t754] VALUES (90,'بما بجفنيك من سحر صلى دنفا ... يهوى الحياة وأما إن صددت فلا','13','3','0');
INSERT INTO [t754] VALUES (91,'إلا يشب فلقد شابت له كبد ... شيبا إذا خضبته سلوة نصلا','13','3','0');
INSERT INTO [t754] VALUES (92,'يجن شوقا فلولا أن رائحة ... تزوره في رياح الشرق ما عقلا','13','3','0');
INSERT INTO [t754] VALUES (93,'ها فأنظري أو فظني بي ترى حرقا ... من لم يذق طرفا منها فقد وألا','13','3','0');
INSERT INTO [t754] VALUES (94,'عل الأمير يرى ذلي فيشفع لي ... إلى التي تركتني في الهوى مثلا','13','3','0');
INSERT INTO [t754] VALUES (95,'أيقنت أن سعيدا طالب بدمي ... لما بصرت به بالرمح معتقلا','13','3','0');
INSERT INTO [t754] VALUES (96,'وأنني غير محص فضل والده ... ونائل دون نيلي وصفه زحلا','13','3','0');
INSERT INTO [t754] VALUES (97,'قيل بمنبج مثواه ونائله ... في الأفق يسأل عمن غيره سألا','13','3','0');
INSERT INTO [t754] VALUES (98,'يلوح بدر الدجى في صحن غرته ... ويحمل الموت في الهيجاء إن حملا','14','3','0');
INSERT INTO [t754] VALUES (99,'ترابه في كلاب كحل أعينها ... وسيفه في جناب يسبق العذلا','14','3','0');
INSERT INTO [t754] VALUES (100,'مهذ! ب الجد يستسقي الغمام به ... حلو كأن على أخلاقه عسلا','14','3','0');
INSERT INTO [t754] VALUES (101,'لنوره في سماء الفخر مخترق ... لو صاعد الفكر فيه الدهر ما نزلا','14','3','0');
INSERT INTO [t754] VALUES (102,'هو الأمير الذي بادت تميم به ... قدما وساق إليها حينها الأجلا','14','3','0');
INSERT INTO [t754] VALUES (103,'لما رأته وخيل النصر مقبلة ... والحرب غير عوان أسلموا الحللا','14','3','0');
INSERT INTO [t754] VALUES (104,'وضاقت الأرض حتى كان هاربهم ... إذا رأى غير شيء ظنه رجلا','14','3','0');
INSERT INTO [t754] VALUES (105,'فبعده وإلى ذا اليوم لو ركضت ... بالخيل في لهوات الطفل ما سعلا','15','3','0');
INSERT INTO [t754] VALUES (106,'فقد تركت الأولى لاقيتهم جزرا ... وقد قتلت الأولى لم تلقهم وجلا','15','3','0');
INSERT INTO [t754] VALUES (107,'كم مهمة قذف قلب الدليل به ... قلب المحب قضاني بعد ما مطلا','15','3','0');
INSERT INTO [t754] VALUES (108,'عقدت بالنجم طرفي في مفاوزه ... وحر وجهي بحر الشمس إذا أفلا','15','3','0');
INSERT INTO [t754] VALUES (109,'أنكحت صم حصاها خف يعملة ... تغشمرت بي إليك السهل والجبلا','15','3','0');
INSERT INTO [t754] VALUES (110,'لو كنت حشو قميصي فوق نمرقها ... سمعت للجن في غيطانها زجلا','15','3','0');
INSERT INTO [t754] VALUES (111,'حتى وصلت بنفس مات أكثرها ... وليتني عشت منها بالذي فضلا','15','3','0');
INSERT INTO [t754] VALUES (112,'أرجوا نداك ولا أخشى المطال به ... يا من إذا وهب الدنيا فقد بخلا','15','3','0');
INSERT INTO [t754] VALUES (113,'كم قتيل كما قتلت شهيد ... ببياض الطلا وورد الخدود','16','3','0');
INSERT INTO [t754] VALUES (114,'وعيون المها ولا كعيون ... فتكت بالمتيم المعمود','16','3','0');
INSERT INTO [t754] VALUES (115,'در در الصبي أأيام تجري ... ر ذيولي بدار الاثلة عودي','16','3','0');
INSERT INTO [t754] VALUES (116,'عمرك الله هل رأيت بدوار ... طلعت في براقع وعقود','16','3','0');
INSERT INTO [t754] VALUES (117,'راميات بأسهم ريشها الهد ... ب تشق القلوب قبل الجلود','16','3','0');
INSERT INTO [t754] VALUES (118,'يرتشفن من فمي رشفات ... هن فيه أحلى من التوحيد','16','3','0');
INSERT INTO [t754] VALUES (119,'كال خمصانة أرق من الخم ... ر بقلب أقسى من الجلمود','16','3','0');
INSERT INTO [t754] VALUES (120,'ذات فرع كأنما ضرب العن ... بر فيه ماء ورد وعود','16','3','0');
INSERT INTO [t754] VALUES (121,'حالك كالغداف جثل دجوج ... ي أثيث جعد بلا تجعيد','16','3','0');
INSERT INTO [t754] VALUES (122,'تحمل المسك من غدائرها الري ... ح وتفتر عن شتيت برود','16','3','0');
INSERT INTO [t754] VALUES (123,'جمعت بين جسم أحمد والسق ... م وبين الجفون والتسهيد','17','3','0');
INSERT INTO [t754] VALUES (124,'هذه مهجتي لديك لحيني ... فانقصى من عذابها أو فريدي','17','3','0');
INSERT INTO [t754] VALUES (125,'أهل ما بي من الضني بطل صي ... د بتصفيف طرة وبجيد','17','3','0');
INSERT INTO [t754] VALUES (126,'كل شيء من الدمآء حرام ... شربه ما خلا دم العنقود','17','3','0');
INSERT INTO [t754] VALUES (127,'فاسقنيها فدى لعينيك نفسي ... من غزال وطارفي وتليدي','17','3','0');
INSERT INTO [t754] VALUES (128,'شيب رأسي وذلتي ونحولي ... ودموعي على هواك شهودي','17','3','0');
INSERT INTO [t754] VALUES (129,'أي يوم سررتني بوصال ... لم ترعني ثلاثة بصدود','17','3','0');
INSERT INTO [t754] VALUES (130,'ما مقامي بدار نخلة إلا ... كمقام المسيح بين اليهود','17','3','0');
INSERT INTO [t754] VALUES (131,'مفرشي صهوة الحسان ولك ... ن قميصي مسرودة من حديد','17','3','0');
INSERT INTO [t754] VALUES (132,'لأمة فاضة أضاة دلاص ... أحكمت نسجها يدا داؤد','17','3','0');
INSERT INTO [t754] VALUES (133,'أين فضلي إذا قنعت من الده ... ر بعيش معجل التنكيد','17','3','0');
INSERT INTO [t754] VALUES (134,'ضاق صدري وطال في طلب الرز ... ق قيامي وقل عنه قعودي','17','3','0');
INSERT INTO [t754] VALUES (135,'ابدا أقطع البلاد ونجمي ... في نحوس وهمتي في سعود','17','3','0');
INSERT INTO [t754] VALUES (136,'ولعلي مؤمول بعض ما أب ... لغ باللطف من عزيز حميد','17','3','0');
INSERT INTO [t754] VALUES (137,'بسري لباسه خشن القط ... ن ومروي مرو لبس القرود','18','3','0');
INSERT INTO [t754] VALUES (138,'عش عزيزا أو مت وأنت كريم ... بين طعن القنا وخفق البند','18','3','0');
INSERT INTO [t754] VALUES (139,'فرؤوس الرماح أذهب للغي ... ظ وأشفى لغل صدر الحقود','18','3','0');
INSERT INTO [t754] VALUES (140,'لا كما قد حييت غير حميد ... وإذا مت مت غير فقيد','18','3','0');
INSERT INTO [t754] VALUES (141,'فأطلب العز في لظى وذر الذل ... ولو كان في جنان الخلود','18','3','0');
INSERT INTO [t754] VALUES (142,'يقتل العاجز الجبان وقد يع ... جز عن قطع بخنق المولود','18','3','0');
INSERT INTO [t754] VALUES (143,'ويوقى الفتى المخش وقد خ ... وض في ماء لبة الصنديد','18','3','0');
INSERT INTO [t754] VALUES (144,'لا بقومي شرفت بل شرفوا بي ... وبنفسي فخرت لا بجدودي','18','3','0');
INSERT INTO [t754] VALUES (145,'وبهم فخر كل من نطق الضا ... د وعوذ الجاني وغوت الطريد','18','3','0');
INSERT INTO [t754] VALUES (146,'إن أكن معجبا فعجب عجيب ... لم يجد فوق نفسه من مزيد','18','3','0');
INSERT INTO [t754] VALUES (147,'أنا ترب الندى ورب القوافي ... وسمام العدى وغيظ الحسود','19','3','0');
INSERT INTO [t754] VALUES (148,'أنا في أمة تداركها الل ... هـ غريب تصالح في ثمود','19','3','0');
INSERT INTO [t754] VALUES (149,'قد شغل الناس كثرة الأمل ... وأنت بالمكرمات في شغل','19','3','0');
INSERT INTO [t754] VALUES (150,'تمثلوا حاتما ولو عقلوا ... لكنت في الجود غاية المثل','19','3','0');
INSERT INTO [t754] VALUES (151,'أهلا وسهلا بما بعثت به ... إيها أبا قاسم وبالرسل','19','3','0');
INSERT INTO [t754] VALUES (152,'هدية ما رأيت مهديها ... ألا رأيت العباد في رجل','19','3','0');
INSERT INTO [t754] VALUES (153,'أقل ما في أقلها سمك ... يسبح في بركة من العسل','19','3','0');
INSERT INTO [t754] VALUES (154,'كيف أكافي على أجل يد ... من لا يرى أنها يد قبلي','19','3','0');
INSERT INTO [t754] VALUES (155,'أقصر فلست بزائدي ودا ... بلغ المدى وتجاوز الحدا','19','3','0');
INSERT INTO [t754] VALUES (156,'أرسلتها مملوءة كرما ... فرددتها مملوءة حمدا','19','3','0');
INSERT INTO [t754] VALUES (157,'جاءتك تطفح وهي فارغة ... مثنى بن وتظنها فردا','19','3','0');
INSERT INTO [t754] VALUES (158,'تأبى خلائقك التي شرفت ... ألا تحن وتذكر العهدا','19','3','0');
INSERT INTO [t754] VALUES (159,'لو كنت عصرا منبتا زهرا ... كنت الربيع وكانت الوردا','19','3','0');
INSERT INTO [t754] VALUES (160,'بقية قوم آذنوا ببوار ... وأنضاء أسفار كشرب عقار','20','3','0');
INSERT INTO [t754] VALUES (161,'نزلنا على حكم الرياح بمسجد ... علينا لها ثوبا حصا وغبار','20','3','0');
INSERT INTO [t754] VALUES (162,'خليلي ما هذا مناخنا لمثلنا ... شدا عليها وأرحلا بنهار','20','3','0');
INSERT INTO [t754] VALUES (163,'ولا تنكرا عصف الرياح فإنها ... قرى كل ضيف بات عند سوار','20','3','0');
INSERT INTO [t754] VALUES (164,'أرق على أرق ومثلي يأرق ... وجوى يزيد وعبرة تترقرق','20','3','0');
INSERT INTO [t754] VALUES (165,'جهد الصبابة أن تكون كما أرى ... عين مسهدة وقلب يخفق','20','3','0');
INSERT INTO [t754] VALUES (166,'ما لاح برق أو ترنم طائر ... إلا انثنيت ولي فؤاد شيق','20','3','0');
INSERT INTO [t754] VALUES (167,'جربت من نار الهوى ما تنطفي ... نار الغضا وتكل عما تحرق','20','3','0');
INSERT INTO [t754] VALUES (168,'وعذلت أهل العشق حتى ذقته ... فعجبت كيف يموت من لا يعشق','20','3','0');
INSERT INTO [t754] VALUES (169,'وعذرتهم وعرفت ذنبي أنني ... عيرتهم فلقيت فيه ما لقوا','20','3','0');
INSERT INTO [t754] VALUES (170,'أبنى أبينا نحن أهل منازل ... أبدا غراب البين فينا ينعق','20','3','0');
INSERT INTO [t754] VALUES (171,'نبكي على الدنيا وما من معشر ... جمعتهم الدنيا فلم يتفرقوا','21','3','0');
INSERT INTO [t754] VALUES (172,'أين الأكاسرة الجبابرة الأولى ... كنزوا الكنوز فما بقين ولا بقوا','21','3','0');
INSERT INTO [t754] VALUES (173,'من كل من ضاق الفضاء بجيشه ... حتى ثوى فحواه لحد ضيق','21','3','0');
INSERT INTO [t754] VALUES (174,'خرس إذا نودوا كأن لم يعلموا ... أن الكلام لهم حلال مطلق','21','3','0');
INSERT INTO [t754] VALUES (175,'فالموت آت والنفوس نفائس ... والمستغر بما لديه الأحمق','21','3','0');
INSERT INTO [t754] VALUES (176,'والمرء يأمل والحياة شهية ... والشيب أوقر والشبيبة أنزق','21','3','0');
INSERT INTO [t754] VALUES (177,'ولقد بكيت على الشباب ولمتي ... مسودة ولماء وجهي رونق','21','3','0');
INSERT INTO [t754] VALUES (178,'حذرا عليه قبل يوم فراقه ... حتى لكدت بماء جفني أشرق','21','3','0');
INSERT INTO [t754] VALUES (179,'أما بنو أوس بن معن بن الرضا ... فأعز من تحدى إليه الأينق','21','3','0');
INSERT INTO [t754] VALUES (180,'كبرت حول ديارهم لما بدت ... منها الشموس وليس فيها المشرق','21','3','0');
INSERT INTO [t754] VALUES (181,'وعجبت من أرض سحاب أكفهم ... من فوقها وصخورها لا تورق','21','3','0');
INSERT INTO [t754] VALUES (182,'وتفوح من طيب الثناء روائح ... لهم بكل مكانة تستنشق','22','3','0');
INSERT INTO [t754] VALUES (183,'مسكية النفحات إلا أنها ... وحشية بسواهم لا تعبق','22','3','0');
INSERT INTO [t754] VALUES (184,'أمريد مثل محمد في عصرنا ... لا تبلنا بطلاب ما لا يلحق','22','3','0');
INSERT INTO [t754] VALUES (185,'لم يخلق الرحمن مثل محمد ... أحدا وظني أنه لا يخلق','22','3','0');
INSERT INTO [t754] VALUES (186,'يا ذا الذي يهب الكثير وعنده ... أني عليه بأخذه أتصدق','22','3','0');
INSERT INTO [t754] VALUES (187,'أمطر على ساحب جودك ثرة ... وأنظر إلي برحمة لا أغرق','22','3','0');
INSERT INTO [t754] VALUES (188,'كذب ابن فاعلة يقول بجهله ... مات الكرام وأنت حي ترزق','22','3','0');
INSERT INTO [t754] VALUES (189,'حشاشة نفس ودعت يوم ودعوا ... فلم أدر أي الظاعنين أشيع','22','3','0');
INSERT INTO [t754] VALUES (190,'أشاروا بتسليم فجدنا بأنفس ... تسيل من الآماق والسم أدمع','22','3','0');
INSERT INTO [t754] VALUES (191,'حشاي على جمر ذكي من الهوى ... وعيناي في روض من الحسن ترتع','22','3','0');
INSERT INTO [t754] VALUES (192,'ولو حملت صم الجبال الذي بنا ... غداة افترقنا أوشكت تتصدع','22','3','0');
INSERT INTO [t754] VALUES (193,'بما جنبي التي خاض طيفها ... إلى الدياجي والخليون هج','23','3','0');
INSERT INTO [t754] VALUES (194,'أتت زائرا ما خامر الطيب ثوبها ... وكالمسك من أردانها يتضوع','23','3','0');
INSERT INTO [t754] VALUES (195,'فما جلست حتى أنثنت توسع الخطا ... كفاطمة عن درها قبل ترضع','23','3','0');
INSERT INTO [t754] VALUES (196,'فشرد إعظامي لها ما أتى بها ... من النوم والتاع الفؤاد المفتج','23','3','0');
INSERT INTO [t754] VALUES (197,'فيا ليلة ما كان أطول بتها ... وسم الأفاعي عذب ما أتجرع','23','3','0');
INSERT INTO [t754] VALUES (198,'تذلل لها واخضع على القرب والنوى ... فما عاشق من لا يذل ويخضع','23','3','0');
INSERT INTO [t754] VALUES (199,'ولا ثوب مجد غير ثوب ابن أحمد ... على أحد إلا بلؤم مرقع','23','3','0');
INSERT INTO [t754] VALUES (200,'وإن الذي حابى جديلة طيىء ... به الله يعطى من يشاء ويمنع','23','3','0');
INSERT INTO [t754] VALUES (201,'بذي كرم ما مر يوم وشمسه ... على رأس أوفى ذمة منه تطلع','23','3','0');
INSERT INTO [t754] VALUES (202,'فأرحام شعر يتصلن لدنه ... وارحام مال لا تنى تتقطع','23','3','0');
INSERT INTO [t754] VALUES (203,'فتى ألف جزء رأيه في زمانه ... أقل جزى بعضه الرأي أجمع','24','3','0');
INSERT INTO [t754] VALUES (204,'غمام علينا ممطر ليس يقشع ... ولا البرق فيه خلبا حين يلمع','24','3','0');
INSERT INTO [t754] VALUES (205,'إذا عرضت حاج إليه فنفسه ... إلى نفسه فيها شفيع مشفع','24','3','0');
INSERT INTO [t754] VALUES (206,'خبت نار حرب لم تهجها بنانه ... وأسمر عريان من القشر أصلع','24','3','0');
INSERT INTO [t754] VALUES (207,'نحيف الشوى يعدو على أم رأسه ... ويحفى ويقوى عدوه حين يقطع','24','3','0');
INSERT INTO [t754] VALUES (208,'يمج ظلاما في نهار لسانه ... ويفهم عمن قال ما ليس يسمع','24','3','0');
INSERT INTO [t754] VALUES (209,'ذباب حسام منه أنجى ضريبة ... وأعصى لمولاه وذا منه أطوع','24','3','0');
INSERT INTO [t754] VALUES (210,'بكف جواد لو حكتها سحابة ... لما فاتها في الشرق والغرب موضع','24','3','0');
INSERT INTO [t754] VALUES (211,'فصيح متى ينطق تجد كل لفظة ... أصول البراعات التي تتفرع','24','3','0');
INSERT INTO [t754] VALUES (212,'وليس كبحر الماء يشتق قعره ... إلى حيث يفنى الماء حوت وضفدع','25','3','0');
INSERT INTO [t754] VALUES (213,'أبحر يضر المعتفين وطعمه ... زعاق كبحر لا يضر وينفع','25','3','0');
INSERT INTO [t754] VALUES (214,'يتيه الدقيق الفكر في بعد غوره ... ويغرق في تياره وهو مصقع','25','3','0');
INSERT INTO [t754] VALUES (215,'ألا أيها القيل المقيم بمنبج ... وهمته فوق السماكين توضع','25','3','0');
INSERT INTO [t754] VALUES (216,'أليس عجيبا أن وصفك معجز ... وأن طنوني في معاليك تظلع','25','3','0');
INSERT INTO [t754] VALUES (217,'وأنك في ثوب وصدرك فيكما ... على أنه من ساحة الأرض أوسع','25','3','0');
INSERT INTO [t754] VALUES (218,'وقلبك في الدنيا ولو دخلت بنا ... وبالجن فيه ما درت كيف ترجع','25','3','0');
INSERT INTO [t754] VALUES (219,'ألا كل سمح غيرك اليوم باطل ... وكل مديح في سواك مضيع','25','3','0');
INSERT INTO [t754] VALUES (220,'قضاعة تعلم أني الفتى ال ... ذي ادخرت لصروف الزمان','25','3','0');
INSERT INTO [t754] VALUES (221,'ومجدي يدل بني خندف ... على أن كل كريم يماني','25','3','0');
INSERT INTO [t754] VALUES (222,'أنا أبن اللقاء أنا ابن السخاء ... أنا ابن الضراب أنا ابن الطعان','25','3','0');
INSERT INTO [t754] VALUES (223,'أنا ابن الفيافي أنا ابن القوافي ... أنا ابن السروج أنا ابن الرعان','25','3','0');
INSERT INTO [t754] VALUES (224,'طويل النجاد طويل العماد ... طويل القناة طويل السنان','25','3','0');
INSERT INTO [t754] VALUES (225,'حديد الحفاظ حديد اللحاظ ... حديد الحسام حديد الجنان','26','3','0');
INSERT INTO [t754] VALUES (226,'يسابق سيقي منايا العباد ... إليهم كأنهما في رهان','26','3','0');
INSERT INTO [t754] VALUES (227,'يرى حده غامضات القلوب ... إذا كنت في هبوة لا أراني','26','3','0');
INSERT INTO [t754] VALUES (228,'سأجعله حكما في النفوس ... ولو ناب عنه لساني كفاني','26','3','0');
INSERT INTO [t754] VALUES (229,'قفا تريا ودقي فهاتا المخائل ... ولا تخشيا خلفا لما أنا قائل','26','3','0');
INSERT INTO [t754] VALUES (230,'رماني خساس الناس من صائب أسته ... وآخر قطن من يديه الجنادل','26','3','0');
INSERT INTO [t754] VALUES (231,'ومن جاهل بي وهو يجهل جهله ... ويجهل علمي أنه بي جاهل','26','3','0');
INSERT INTO [t754] VALUES (232,'ويجهل أني مالك الأرض معسر ... وأني على ظهر السماكين راجل','26','3','0');
INSERT INTO [t754] VALUES (233,'تحقر عندي همتي كل مطلب ... ويقصر في عيني المدى المتطاول','26','3','0');
INSERT INTO [t754] VALUES (234,'وما زلت طودا لا تزول مناكبي ... إلى أن بدت للضيم في زلازل','26','3','0');
INSERT INTO [t754] VALUES (235,'فقلقلت بالهم الذي قلقل الحشا ... قلاقل عيس كلهن قلاقل','26','3','0');
INSERT INTO [t754] VALUES (236,'إذا الليل وارانا أرتنا خفافها ... بقدح الحصى ما لا ترينا المشاعل','27','3','0');
INSERT INTO [t754] VALUES (237,'كأني من الوجناء في ظهر موجة ... رمت بي بحارا ما لهن سواحل','27','3','0');
INSERT INTO [t754] VALUES (238,'يخيل لي أن البلاد مسامعي ... وأني فيها ما تقول العواذل','27','3','0');
INSERT INTO [t754] VALUES (239,'ومن يبغ ما أبغي من المجد والعلا ... تساوي المحائي عنده والمقاتل','27','3','0');
INSERT INTO [t754] VALUES (240,'ألا ليست الحاجات إلا نفوسكم ... وليس لنا إلا السيوف وسائل','27','3','0');
INSERT INTO [t754] VALUES (241,'فما وردت روح امرء روحه له ... ولا صدرت عن باخل وهو باخل','27','3','0');
INSERT INTO [t754] VALUES (242,'غثاثة عيشي أن تغث كرامتي ... وليس بغث أن تغث المآكل','27','3','0');
INSERT INTO [t754] VALUES (243,'ضيف ألم براسي غير محتشم ... والسيف أحسن فعلا منه باللمم','27','3','0');
INSERT INTO [t754] VALUES (244,'إبعد بعدت بياضا لا بياض له ... لأنت أسود في عيني من الظلم','28','3','0');
INSERT INTO [t754] VALUES (245,'بحب قاتلتي والشيب تغذيتي ... هواي طفلا وشيبي بالغ الحلم','28','3','0');
INSERT INTO [t754] VALUES (246,'فما أمر برسم لا أسائله ... ولا بذات خمار لا تريق دمي','28','3','0');
INSERT INTO [t754] VALUES (247,'تنفست عن وفاء غير منصدع ... يوم الرحيل وشعب غير ملتئم','28','3','0');
INSERT INTO [t754] VALUES (248,'قبلتها ودموعي مزج أدمعها ... وقبلتني على خوف فما لفم','28','3','0');
INSERT INTO [t754] VALUES (249,'فذقت ماء حيوة من مقبلها ... لو صاب تربا لأحيا سالف الأمم','28','3','0');
INSERT INTO [t754] VALUES (250,'ترنو إلي بعين الظبي مجشهة ... وتمسح الطل فوق الورد بالنعم','28','3','0');
INSERT INTO [t754] VALUES (251,'وريد حكمك فينا غير منصفة ... بالناس كلهم أفديك من حكم','29','3','0');
INSERT INTO [t754] VALUES (252,'أبديت مثل الذي أبديت من جزع ... ولم تجنى الذي أجننت من ألم','29','3','0');
INSERT INTO [t754] VALUES (253,'إذا لبزك ثوب الحسن أصغره ... وصرت مثلي في ثوبين من سقم','29','3','0');
INSERT INTO [t754] VALUES (254,'ليس التعلل بالآمال من أربى ... ولا القناعة بالإقلال من شيمي','29','3','0');
INSERT INTO [t754] VALUES (255,'ولا أظن بنات الدهر تتركني ... حتى تسد عليها طرقها هممي','29','3','0');
INSERT INTO [t754] VALUES (256,'لم الليالي التي أخنت على جدتي ... برقة الحال واعذرني ولا تلم','29','3','0');
INSERT INTO [t754] VALUES (257,'أرى أناسا ومحصولي على غنم ... وذكر جود ومحصولي على كلم','29','3','0');
INSERT INTO [t754] VALUES (258,'ورب مال فقيرا من مروته ... لم يثر منها كما أثرى من العدم','29','3','0');
INSERT INTO [t754] VALUES (259,'سيصحب النصل مني مثل مضربه ... وينجلي خبري عن صمة الصمم','30','3','0');
INSERT INTO [t754] VALUES (260,'لقد تصبرت حتى لات مصطبر ... فالآن أقحم حتى لات مقتحم','30','3','0');
INSERT INTO [t754] VALUES (261,'لأتركن وجوه الخيل ساهمة ... والحرب أقوم من ساق على قدم','30','3','0');
INSERT INTO [t754] VALUES (262,'والطعن يحرقها والزجر يقلقها ... حتى كأن بها ضربا من اللمم','30','3','0');
INSERT INTO [t754] VALUES (263,'قد كلمتها العوالي فهي كالحة ... كأنها الصاب معصوب على اللجم','30','3','0');
INSERT INTO [t754] VALUES (264,'بكل منصلت ما زال منتظري ... حتى أدلت له من دولة الخدم','30','3','0');
INSERT INTO [t754] VALUES (265,'شيخ يرى الصلوات الخمس نافلة ... ويستحل دم الحجاج في الحرم','30','3','0');
INSERT INTO [t754] VALUES (266,'وكلما نطحت تحت العجاج به ... أسد الكتائب رامته ولم يرم','30','3','0');
INSERT INTO [t754] VALUES (267,'تنسى البلاد بروق البحو بارقتي ... وتكتفي بالدم البحاري عن الديم','30','3','0');
INSERT INTO [t754] VALUES (268,'ردي حياض الردى يا نفس واتركي ... حياض خوف الردى للشآء والنعم','30','3','0');
INSERT INTO [t754] VALUES (269,'إن لم أذرك على الأرماح سائلة ... فلا دعيت ابن أم المجد والكرم','30','3','0');
INSERT INTO [t754] VALUES (270,'أيملك الملك والأسياف ظامئة ... والطير جائعة لحم على وضم','31','3','0');
INSERT INTO [t754] VALUES (271,'من لو رآني ماء مات من ظمأ ... ولو مثلت له في النوم لم ينم','31','3','0');
INSERT INTO [t754] VALUES (272,'ميعاد كل رقيق الشفرتين غدا ... ومن عصى من ملوك العرب والغجم','31','3','0');
INSERT INTO [t754] VALUES (273,'فإن أجابوا فما قصدي بها لهم ... وإن تولوا فما أرضى لها بهم','31','3','0');
INSERT INTO [t754] VALUES (274,'أبا سعيد جنب العتابا ... فرب رآء خطأ صوابا','31','3','0');
INSERT INTO [t754] VALUES (275,'فإنهم قد أكثروا الحجابا ... واستوقفوا لردنا البوابا','31','3','0');
INSERT INTO [t754] VALUES (276,'وإن حد الصارم القرضابا ... والذابلات السمر والعرابا','31','3','0');
INSERT INTO [t754] VALUES (277,'شوقي إليك نفى لذيذ هجوعي ... فارقتني وأقام بين ضلوعي','31','3','0');
INSERT INTO [t754] VALUES (278,'أوما وجدتم في الصراة ملوحة ... مما أرقرق في الفرات دموعي','31','3','0');
INSERT INTO [t754] VALUES (279,'ما زلت أحذر من وداعك جاهدا ... حتى اغتدى أسفي على التوديع','31','3','0');
INSERT INTO [t754] VALUES (280,'رحل العزاء برحلتي فكأنما ... أتبعته الإنفاس للتشييع','31','3','0');
INSERT INTO [t754] VALUES (281,'أي محل أرتقى ... أي عظيم أتقي','31','3','0');
INSERT INTO [t754] VALUES (282,'وكل ما قد خلق الل ... هـ وما لم يخلق','31','3','0');
INSERT INTO [t754] VALUES (283,'محتقر في همتي ... كشعرة في مفرقي','31','3','0');
INSERT INTO [t754] VALUES (284,'إذا لم تجد ما يبتر الفقر قاعدا ... فقم واطلب الشيء الذي يبتر العمرا','31','3','0');
INSERT INTO [t754] VALUES (285,'أنا عاتب لتعتبك ... متعجب لتعجبك','32','3','0');
INSERT INTO [t754] VALUES (286,'إذ كنت حين لقيتني ... متوجعا لتغيبك','32','3','0');
INSERT INTO [t754] VALUES (287,'فشغلت عن رد السلا ... م وكان شغلي عنك بك','32','3','0');
INSERT INTO [t754] VALUES (288,'أنصر بجودك ألفاظا تركت بها ... في الشرق والغرب من عاداك مكبوتا','32','3','0');
INSERT INTO [t754] VALUES (289,'فقد نظرتك حتى حان مرتحلي ... وذا الوداع فكن أهلا لما شيتا','32','3','0');
INSERT INTO [t754] VALUES (290,'حاشا الرقيب فخانته ضمائره ... وغيض الدمع فأنهلت بوادره','32','3','0');
INSERT INTO [t754] VALUES (291,'وكاتم الحب يوم البين منتهك ... وصاحب الدمع لاتخفى سرائره','32','3','0');
INSERT INTO [t754] VALUES (292,'لولا ظباء عدي ما شقيت بهم ... ولا بربربهم لولا جاذره','32','3','0');
INSERT INTO [t754] VALUES (293,'من كل أحور في أنيابه شنب ... خمر يخامرها مسك تخامره','32','3','0');
INSERT INTO [t754] VALUES (294,'نعج محاجره دعج نواظره ... حمر غفائره سود غدائره','32','3','0');
INSERT INTO [t754] VALUES (295,'أعارني قسم جفنيه وحملني ... من الهوى ثقل ما تحوى مآزره','32','3','0');
INSERT INTO [t754] VALUES (296,'يا من تحكم في نفسي فعذبني ... ومن فؤادي على قتلي يضافره','33','3','0');
INSERT INTO [t754] VALUES (297,'بعودة الدولة الغراء ثانية ... سلوت عنك ونام الليل ساهره','33','3','0');
INSERT INTO [t754] VALUES (298,'من بعد ما كان ليلى لا صباح له ... كأن أول يوم الحشر آخره','33','3','0');
INSERT INTO [t754] VALUES (299,'غاب الأمير فغاب الخير عن بلد ... كادت لفقد اسمه تبكي منابره','33','3','0');
INSERT INTO [t754] VALUES (300,'قد اشتكت وحشة الأحياء أربعه ... وخبرت عن أسى الموتى مقابره','33','3','0');
INSERT INTO [t754] VALUES (301,'حتى إذا عقدت فيه القباب له ... أهل لله باديه وحاضره','33','3','0');
INSERT INTO [t754] VALUES (302,'وجددت فرحا لا الغم يطرده ... ولا الصبابة في قلب تجاوره','33','3','0');
INSERT INTO [t754] VALUES (303,'إذا خلت منك حمص لا خلت أبدا ... فلا سقاها من الوسمي باكره','33','3','0');
INSERT INTO [t754] VALUES (304,'دخلتها وشعاع الشمس متقد ... ونور وجهك بين الخيل باهره','33','3','0');
INSERT INTO [t754] VALUES (305,'في قيلق من حديد لو قذفت به ... صرف الزمان لما دارت دوائره','33','3','0');
INSERT INTO [t754] VALUES (306,'تمضي المواكب والأبصار شاخصة ... منها إلى الملك الميمون طائره','33','3','0');
INSERT INTO [t754] VALUES (307,'قد حرن في بشر في تاجه قمر ... في درعه أسد تدمى أظافره','33','3','0');
INSERT INTO [t754] VALUES (308,'حلو خلائقه شوس حقائقه ... تحصى الحصى قبل أن تحصى مآثره','33','3','0');
INSERT INTO [t754] VALUES (309,'تضيق عن جيشه الدنيا ولو رحبت ... كصدره لم تبن فيها عساكره','33','3','0');
INSERT INTO [t754] VALUES (310,'إذا تغلغل فكر المرء في طرف ... من مجده غرقت فيه خواطره','34','3','0');
INSERT INTO [t754] VALUES (311,'تحمى السيوف على أعدائه معه ... كأنهن بنوه أو عشائره','34','3','0');
INSERT INTO [t754] VALUES (312,'إذا انتضاها لحرب لم تدع جسدا ... إلا وباطنه للعين ظاهره','34','3','0');
INSERT INTO [t754] VALUES (313,'فقد تيقن أن الحق في يده ... وقد وثقن بأن الله ناصره','34','3','0');
INSERT INTO [t754] VALUES (314,'تركن هام بني عوف وثعلبة ... على رؤوس بلا ناس مغافرة','34','3','0');
INSERT INTO [t754] VALUES (315,'فخاض بالسيف بحر الموت خلفهم ... وكان منه إلى الكعبين زاخره','34','3','0');
INSERT INTO [t754] VALUES (316,'كم من دم رويت منه أسنته ... ومهجة ولغت فيها بواتره','34','3','0');
INSERT INTO [t754] VALUES (317,'وحائن لعبت سمر الرماح به ... والعيش هاجره والنسر زائرة','34','3','0');
INSERT INTO [t754] VALUES (318,'من قال لست بخير الناس كلهم ... فجهله بك عند الناس عاذره','34','3','0');
INSERT INTO [t754] VALUES (319,'أو شك أنك فرد في زمانهم ... بلا نظير ففي روحي أخاطره','34','3','0');
INSERT INTO [t754] VALUES (320,'يا من ألوذ به فيما أومله ... ومن أعوذ به مما أحاذره','34','3','0');
INSERT INTO [t754] VALUES (321,'ومن توهمت أن البحر راحته ... جوداص وأن عطاياها جواهره','34','3','0');
INSERT INTO [t754] VALUES (322,'لا يجبر الناس عظما أنت كاسره ... ولا يهيضون عظما أنت جابره','34','3','0');
INSERT INTO [t754] VALUES (323,'لا يجبر الناس عظم ما كسروا ... ولا يهيضون عظم ما جبروا','34','3','0');
INSERT INTO [t754] VALUES (324,'إرحم شباب فتى اودت بجدته ... يد البلا وذنى في السجن ناضره','34','3','0');
INSERT INTO [t754] VALUES (325,'عزيز أسى من داؤه الحدق النجل ... عياء به مات المحبون من قبل','35','3','0');
INSERT INTO [t754] VALUES (326,'فمن شاء فلينظر إلى فمنظري ... نذير إلى من ظن أن الهوى سهل','35','3','0');
INSERT INTO [t754] VALUES (327,'وما هي إلا لحظة بعد لحظة ... إذا نزلت في قلبه رحل العقل','35','3','0');
INSERT INTO [t754] VALUES (328,'جرى حبها مجرى دمي في مفاصلي ... فأصبح لي عن كل شغل بها شغل','35','3','0');
INSERT INTO [t754] VALUES (329,'سبتني بدل ذات حسن يزينها ... تكحل عينيها وليس لها كحل','35','3','0');
INSERT INTO [t754] VALUES (330,'كأن لحاظ العين في فتكه بنا ... رقيب تعدى أو عدو له دخل','35','3','0');
INSERT INTO [t754] VALUES (331,'ومن جسدي لم يترك السقم شعرة ... فما فوقها إلا وفيها له فعل','35','3','0');
INSERT INTO [t754] VALUES (332,'إذا عذلوا فيها أجبت بأنة ... حبيبتا قلبا فؤادا هيا جمل','35','3','0');
INSERT INTO [t754] VALUES (333,'كأن رقيبا منك سد مسامعي ... عن العذل حتى ليس يدخلها العذل','35','3','0');
INSERT INTO [t754] VALUES (334,'كأن سهاد العين يعشق مقلتي ... فبينهما في كل هجر لنا وصل','35','3','0');
INSERT INTO [t754] VALUES (335,'أحب التي في البدر منها مشابه ... وأشكو إلى من لا يصاب له شكل','35','3','0');
INSERT INTO [t754] VALUES (336,'إلى واحد الدنيا إلى ابن محمد ... شجاع الذي لله ثم له الفضل','36','3','0');
INSERT INTO [t754] VALUES (337,'إلى الثمر الحلو الذي ضيء له ... فروع وقحان بن هود لها أصل','36','3','0');
INSERT INTO [t754] VALUES (338,'إلى سيد لو بشر الله أمة ... بغير نبي بشرتنا به الرسل','36','3','0');
INSERT INTO [t754] VALUES (339,'إلى القابض الأرواح والضيغم الذي ... تحدث عن وقفاته الخيل والرجل','36','3','0');
INSERT INTO [t754] VALUES (340,'إلى رب مال كلما شت شمله ... تجمع في تشتيته للعلي شمل','36','3','0');
INSERT INTO [t754] VALUES (341,'همام إذا ما فارق الغمد سيفه ... وعاينته لم تدر أيهما النصل','36','3','0');
INSERT INTO [t754] VALUES (342,'رأيت ابن أم الموت لو أن بأسه ... فشى بين أهل الأرض لا نقطع النسل','36','3','0');
INSERT INTO [t754] VALUES (343,'على سابح موج المنايا بنحره ... غداة كأن النبل في صدره وبل','36','3','0');
INSERT INTO [t754] VALUES (344,'وكم عين قرن حذقت لنزاله ... فلم تغض غلا والسنان لها كحل','36','3','0');
INSERT INTO [t754] VALUES (345,'إذا قيل رفقا قال للحلم موضع ... وحلم الفتى في غير موضعه جهل','36','3','0');
INSERT INTO [t754] VALUES (346,'ولولا تولي نفسه حمل حلمه ... عن الأرض لأنهدت وناء بها الحمل','37','3','0');
INSERT INTO [t754] VALUES (347,'تباعدت الآمال عن كل مقصد ... وضاق بها إلا إلى بابك السبل','37','3','0');
INSERT INTO [t754] VALUES (348,'ونادى الندى بالنائمين عن السرى ... فاسمعهم هبوا فقد هلك البخل','37','3','0');
INSERT INTO [t754] VALUES (349,'وحالت عطايا كفه دون وعده ... فليس له إنجاز وعد ولا مطل','37','3','0');
INSERT INTO [t754] VALUES (350,'وأقرب من تحديدها رد فائت ... وأيسر من إحصائها القطر والرمل','37','3','0');
INSERT INTO [t754] VALUES (351,'وما تنقم الأيام ممن وجوهها ... لأخمصه في كل نائبة نعل','37','3','0');
INSERT INTO [t754] VALUES (352,'وما عزة فيها مراد أراده ... وإن عز إلا أن يكون له مثل','37','3','0');
INSERT INTO [t754] VALUES (353,'كفى ثعلا فخرا بأنك منهم ... ودهر لأن أمسيت من أهله أهل','37','3','0');
INSERT INTO [t754] VALUES (354,'وويل لنفس حاولت منك غرة ... وطوبى لعين ساعة منك لا تخلو','37','3','0');
INSERT INTO [t754] VALUES (355,'فما بفقير شام برقك فاقة ... ولا في بلاد أنت صيبها محل','38','3','0');
INSERT INTO [t754] VALUES (356,'أليوم عهدكم فأين الموعد ... هيهات ليس ليوم عهدكم غد','38','3','0');
INSERT INTO [t754] VALUES (357,'الموت أقرب مخلبا من بينكم ... والعيش أبعد منكم لا تبعدوا','38','3','0');
INSERT INTO [t754] VALUES (358,'إن التي سفكت دمي بجفونها ... لمتدر أن دمي الذي تتقلد','38','3','0');
INSERT INTO [t754] VALUES (359,'قالت وقد رأت اصفراري من به ... وتنهدت فأجبتها المتنهد','38','3','0');
INSERT INTO [t754] VALUES (360,'فمضت وقد صبغ الحياء بياضها ... لوني كما صبغ اللجين العسجد','38','3','0');
INSERT INTO [t754] VALUES (361,'فرأيت قرن الشمس في قمر الدجى ... متأودا غصن به يتأود','38','3','0');
INSERT INTO [t754] VALUES (362,'عدوية بدوية من دونها ... سلب النفوس ونار حرب توقد','38','3','0');
INSERT INTO [t754] VALUES (363,'وهواجل وصواهل ومناصل ... وذوابل وتوعد وتهدد','38','3','0');
INSERT INTO [t754] VALUES (364,'أبلت مودتها الليالي بعدنا ... ومشى عليها الدهر وهو مقيد','38','3','0');
INSERT INTO [t754] VALUES (365,'أبرحت يا مرض الجفون بممرض ... مرض الطبيب له وعيد العود','39','3','0');
INSERT INTO [t754] VALUES (366,'فله بنو عبد العزيز بن الرضى ... ولكل ركب عيسهم والفدفد','39','3','0');
INSERT INTO [t754] VALUES (367,'أعطى فقلت لجوده ما يقتني ... وسطا فقلت لسيفه ما يولد','39','3','0');
INSERT INTO [t754] VALUES (368,'وتحيرت فيه الصفات لأنها ... ألفت طرائقه عليها تبعد','39','3','0');
INSERT INTO [t754] VALUES (369,'في كل معترك كلي مفرية ... يذممن منه ما الأسنة تحمد','39','3','0');
INSERT INTO [t754] VALUES (370,'نقم على نقم الزمان يصبها ... نعم على النعم التي لا تجحد','39','3','0');
INSERT INTO [t754] VALUES (371,'في شأنه ولسانه وبنانه ... وجنانه عجب لمن يتفقد','39','3','0');
INSERT INTO [t754] VALUES (372,'أسد دم الأسد الهزبر خضابه ... موت فريص الموت منه يرعد','39','3','0');
INSERT INTO [t754] VALUES (373,'ما منبج مذ غبت إلا مقلة ... سهدت ووجهك نومها والإثمد','39','3','0');
INSERT INTO [t754] VALUES (374,'فالليل حين قدمت فيها أبيض ... والصبح منذ رحلت عنها أسود','40','3','0');
INSERT INTO [t754] VALUES (375,'مازلت تدنو وهي تعلو عزة ... حتى توارى في ثراها الفرقد','40','3','0');
INSERT INTO [t754] VALUES (376,'أرض لها شرف سواها مثلها ... لو كان مثلك في سواها يوجد','40','3','0');
INSERT INTO [t754] VALUES (377,'أبدى العداة بك السرور كأنهم ... فرحوا وعندهم المقيم المقعد','40','3','0');
INSERT INTO [t754] VALUES (378,'قطعتهم حسدا أراهم ما بهم ... فتقطعوا حسدا لمن لا يحسد','40','3','0');
INSERT INTO [t754] VALUES (379,'حتى انثنوا ولو أن حر قلوبهم ... في قلب هاجرة لذاب الجلمد','40','3','0');
INSERT INTO [t754] VALUES (380,'نظر العلوج فلم يروا من حولهم ... لما رأوك وقيل هذا السيد','40','3','0');
INSERT INTO [t754] VALUES (381,'بقيت جموعهم كأنك كلها ... وبقيت بينهم كأنك مفرد','40','3','0');
INSERT INTO [t754] VALUES (382,'لهفان يستوبي بك الغضب الورى ... لو لم ينهنهك الحجي والسودد','40','3','0');
INSERT INTO [t754] VALUES (383,'كن حيث شئت تسر إليك ركابنا ... فالأرض واحدة وأنت الأوحد','40','3','0');
INSERT INTO [t754] VALUES (384,'وصن الحسام ولا تذله فإنه ... يشكو يمينك والجماجم تشهد','40','3','0');
INSERT INTO [t754] VALUES (385,'يبس النجيع عليه وهو مجرد ... من غمده وكأنما هو مغمد','40','3','0');
INSERT INTO [t754] VALUES (386,'ريان لو قذف الذي أسقيته ... لجرى من المهجات بحر مزبد','40','3','0');
INSERT INTO [t754] VALUES (387,'ما شاركته منية في مهجة ... إلا وشفرته على يدها يد','41','3','0');
INSERT INTO [t754] VALUES (388,'إن الرزايا والعطايا والقنا ... حلفاء طي غوروا أو أنجدوا','41','3','0');
INSERT INTO [t754] VALUES (389,'صح يال جلهمة تذرك وإنما ... أشفار عينك ذابل ومهند','41','3','0');
INSERT INTO [t754] VALUES (390,'من كل أكبر من جبال تهامة ... قلبا ومن جود الغوادي أجود','41','3','0');
INSERT INTO [t754] VALUES (391,'يلقاك مرتديا بأحمر من دم ... ذهبت بخضرته الطلى والأكبد','41','3','0');
INSERT INTO [t754] VALUES (392,'حتى يشار إليك ذا مولاهم ... وهم الموالي والخليقة أعبد','41','3','0');
INSERT INTO [t754] VALUES (393,'أنى يكون أبا البرية آدم ... وأبوك والثقلان أنت محمد','41','3','0');
INSERT INTO [t754] VALUES (394,'يفنى الكلام ولا يحيط بفضلكم ... أيحيد ما يفنى بما لا ينفد','41','3','0');
INSERT INTO [t754] VALUES (395,'أهون بطول الثواء والتلف ... والسجن والقيد يا أبا دلف','41','3','0');
INSERT INTO [t754] VALUES (396,'غير اختيار قبلت برك بي ... والجوع يرضى الأسود بالجيف','41','3','0');
INSERT INTO [t754] VALUES (397,'كن أيها السجن كيف شئت فقد ... وطنت للموت نفس معترف','42','3','0');
INSERT INTO [t754] VALUES (398,'لو كان سكناي فيك منقصة ... لم يكن الدر ساكن الصدف','42','3','0');
INSERT INTO [t754] VALUES (399,'أي خدد الله ورد الخدود ... وقد قدود الحسان القدود','42','3','0');
INSERT INTO [t754] VALUES (400,'فهن أسلن دما مقلني ... وعذبن قلبي بطول الصدود','42','3','0');
INSERT INTO [t754] VALUES (401,'وكم للهوى من فتى مدنف ... وكم للنوى من قتيل شهيد','42','3','0');
INSERT INTO [t754] VALUES (402,'فوا حسرتا ما أمر الفراق ... وأعلق نيرانه بالكبود','42','3','0');
INSERT INTO [t754] VALUES (403,'وأعرى الصبابة بالعاشقين ... وأقتلها للمحب العميد','42','3','0');
INSERT INTO [t754] VALUES (404,'وألهج نفسي لغير الخنا ... بحب ذوات اللمى والنهود','42','3','0');
INSERT INTO [t754] VALUES (405,'فكانت وكن فداء الأمير ... ولا زال من نعمة في مزيد','42','3','0');
INSERT INTO [t754] VALUES (406,'لقد حال بالسيف دون الوعيد ... وحالت عطاياه دون الوعود','42','3','0');
INSERT INTO [t754] VALUES (407,'فأنجم أمواله في النحوس ... وأنجم سؤاله في السعود','42','3','0');
INSERT INTO [t754] VALUES (408,'ولو لم أخف غير أعدائه ... عليه لبشرته بالخلود','42','3','0');
INSERT INTO [t754] VALUES (409,'رمى حلبا بنواصي الخيول ... وسمر يرقن دما في الصعيد','42','3','0');
INSERT INTO [t754] VALUES (410,'وبيض مسافرة ما يقم ... ن لا في الرقاب ولا في الغمود','42','3','0');
INSERT INTO [t754] VALUES (411,'يقدن الفناء غداة اللقاء ... إلى كل جيش كثير العديد','43','3','0');
INSERT INTO [t754] VALUES (412,'فولي بأشياعه الخرشني ... كشاء أحس بزار الأسود','43','3','0');
INSERT INTO [t754] VALUES (413,'يرون من الذعر صوت الرياح ... صهيل الجياج وخفق البنود','43','3','0');
INSERT INTO [t754] VALUES (414,'فمن كالأمير ابن بنت الأمي ... ر أم من كآبائه والجدود','43','3','0');
INSERT INTO [t754] VALUES (415,'سعوا للمعالي وهم صبية ... وسادوا وجادوا وهم في المهود','43','3','0');
INSERT INTO [t754] VALUES (416,'أمالك رقى ومن شأنه ... هبات اللجين وعتق العبيد','43','3','0');
INSERT INTO [t754] VALUES (417,'دعوتك عند انقطاع الرجا ... ء والموت مني كحبل الوريد','43','3','0');
INSERT INTO [t754] VALUES (418,'دعوتك لما براني البلاء ... وأوهن رجلي ثقل الحديد','43','3','0');
INSERT INTO [t754] VALUES (419,'وقد كان مشيهما في النعال ... فقد صار مشيهما في القيود','43','3','0');
INSERT INTO [t754] VALUES (420,'وكنت من الناس في محفل ... فها أنا في محفل من قرود','43','3','0');
INSERT INTO [t754] VALUES (421,'تعجل في وجوب الحدود ... وحدي قبل وجوب الساجود','43','3','0');
INSERT INTO [t754] VALUES (422,'وقيل عدوت عل العالمي ... ن بين ولادي وبين القعود','43','3','0');
INSERT INTO [t754] VALUES (423,'فما لك تقبل زور الكلام ... وقدر الشهادة قدر الشهود','43','3','0');
INSERT INTO [t754] VALUES (424,'فلا تسمعن من الكاشحين ... ولا تعبأن بمحك اليهود','43','3','0');
INSERT INTO [t754] VALUES (425,'وكن فارقا بين دعوى أردت ... ودعوى فعلت بشأو بعيد','44','3','0');
INSERT INTO [t754] VALUES (426,'وفي جود كفيك ما جدت لي ... بنفسي ولو كنت أشقى ثمود','44','3','0');
INSERT INTO [t754] VALUES (427,'أبا عبد الإله معاذ إني ... خفي عنك في الهيجا مقامي','44','3','0');
INSERT INTO [t754] VALUES (428,'ذكرت جسيم ما طلبي وأنا ... نخاطر فيها بالمهج الجسام','44','3','0');
INSERT INTO [t754] VALUES (429,'أمثلي تأخذ النكبات منه ... ويجزع من ملاقاة الحمام','44','3','0');
INSERT INTO [t754] VALUES (430,'ولو برز الزمان إلى شخصا ... لخضب شعر مفرقه حسامي','44','3','0');
INSERT INTO [t754] VALUES (431,'وما بلغت مشيتها الليالي ... ولا سارت وفي يدها زماني','44','3','0');
INSERT INTO [t754] VALUES (432,'إذا امتلأت عيون الخيل مني ... فويل في التيقظ والمنام','44','3','0');
INSERT INTO [t754] VALUES (433,'أنا عين المسود الجحجاح ... هيجتني كلابكم بالنباح','44','3','0');
INSERT INTO [t754] VALUES (434,'أيكون الهجان غير هجان ... أما يكون الصراح غير صراح','44','3','0');
INSERT INTO [t754] VALUES (435,'جهلوني وإن عمرت قليلا ... نسبتني لهم رؤوس الرماح','44','3','0');
INSERT INTO [t754] VALUES (436,'ألذ من المدام الخندريس ... وأحلى من معاطاة الكؤوس','44','3','0');
INSERT INTO [t754] VALUES (437,'معاطاة الصفائح والعوالي ... وإقحامي خميسا في خميس','44','3','0');
INSERT INTO [t754] VALUES (438,'فموتي في الوغى عيشي لأني ... رأيت العيش في أرب النفوس','45','3','0');
INSERT INTO [t754] VALUES (439,'ولو سقيتها بيدي نديم ... أسر به لكان أبا ضبيس','45','3','0');
INSERT INTO [t754] VALUES (440,'إذا ما شربت الخمر صرفا مهنأ ... شربنا الذي ن مثله شرب الكرم','45','3','0');
INSERT INTO [t754] VALUES (441,'ألا حبذا قوم نداماهم القنا ... يسقونها ريا وساقيهم العزم','45','3','0');
INSERT INTO [t754] VALUES (442,'لأحبتي أن يملؤا ... بالصافيات الأكوبا','45','3','0');
INSERT INTO [t754] VALUES (443,'وعليهم أن يبذلوا ... وعلي أن لا أشربا','45','3','0');
INSERT INTO [t754] VALUES (444,'حتى تكون الباترات ... المسمعات فأطربا','45','3','0');
INSERT INTO [t754] VALUES (445,'أما ترى ما أراه أيها الملك ... كأننا في سماء ما لها حبك','45','3','0');
INSERT INTO [t754] VALUES (446,'ألفرقد ابنك والمصباح صاحبه ... وأنت بدر الدجى والمجلس الفلك','45','3','0');
INSERT INTO [t754] VALUES (447,'إن القوافي لم تنمك وإنما ... محقتك حتى صرت ما لايوجد','45','3','0');
INSERT INTO [t754] VALUES (448,'فكأن أذنك فوك حين سمعتها ... وكأنها مما سكرت المرقد','45','3','0');
INSERT INTO [t754] VALUES (449,'كتمت حبك حتى منك تكرمة ... ثم استوى فيك إسراري وإعلاني','45','3','0');
INSERT INTO [t754] VALUES (450,'كأنه زاد حتى فاض عن جسدي ... فصار سقمي به في جسم كتماني','45','3','0');
INSERT INTO [t754] VALUES (451,'وأخ لنا بعث الطلاق ألية ... لأعللن بهذه الخرطوم','45','3','0');
INSERT INTO [t754] VALUES (452,'فجعلت ردي عرسه كفارة ... من شربها وشربت غير أثيم','46','3','0');
INSERT INTO [t754] VALUES (453,'أظبية الوحش لولا ظبية الأنس ... لما غدوت بجد في الهوى تعس','46','3','0');
INSERT INTO [t754] VALUES (454,'ولا سقيت الثرى والمزن مخلفه ... دمعا ينشفه من لوعة نفسي','46','3','0');
INSERT INTO [t754] VALUES (455,'ولا وقفت بجسم مسى ثالثة ... ذي أرسم درس في الأرسم الدرس','46','3','0');
INSERT INTO [t754] VALUES (456,'صريع مقلتها سال دمنتها ... قتيل تكسير ذاك الجفن واللعس','46','3','0');
INSERT INTO [t754] VALUES (457,'خريدة لو رأتها الشمس ما طلعت ... ولو رآها قضيب البان لم يمس','46','3','0');
INSERT INTO [t754] VALUES (458,'ما ضاق قبلك خلخال على رشإ ... ولا سمعت بديباج على كنس','46','3','0');
INSERT INTO [t754] VALUES (459,'إن ترمني نكبات الدهر عن كثب ... ترم أمرأ غير رعديد ولا نكس','46','3','0');
INSERT INTO [t754] VALUES (460,'يفدي بنيك عبيد الله حاسدهم ... بجبهة العير يفدي حافر الفرس','46','3','0');
INSERT INTO [t754] VALUES (461,'أبا الغطارفة الحامين جارهم ... وتاركي الليث كلبا غير مفترس','47','3','0');
INSERT INTO [t754] VALUES (462,'من كل أبيض وضاح عمامته ... كأنما اشتملت نورا على قبس','47','3','0');
INSERT INTO [t754] VALUES (463,'دان بعيد محب مبغض بهج ... أغر حلو ممر لين شرس','47','3','0');
INSERT INTO [t754] VALUES (464,'ند أبي غر واف أخي ثقة ... جعد سري نه ندب رضى ندس','47','3','0');
INSERT INTO [t754] VALUES (465,'لو كان فيض يديه ماء غادية ... عز القطا في الفيافي موضع اليبس','47','3','0');
INSERT INTO [t754] VALUES (466,'أكارم حسد الأرض السماء بهم ... وقصرت كل مصر عن طرابلس','47','3','0');
INSERT INTO [t754] VALUES (467,'أي الملوك وهم قصدي أحاذره ... واي قرن وهم سيفي وهم ترسي','47','3','0');
INSERT INTO [t754] VALUES (468,'أحببت برك إذ أردت رحيلا ... فوجدت أكثر ما وجدت قليلا','47','3','0');
INSERT INTO [t754] VALUES (469,'وعلمت أنك في المكارم راغب ... صب إليها بكرة وأصيلا','47','3','0');
INSERT INTO [t754] VALUES (470,'فجعلت ما تهدي إلى هدية ... مني إليك وظرفها التأميلا','47','3','0');
INSERT INTO [t754] VALUES (471,'بر يخف على يديك قبوله ... ويكون محمله علي ثقيلا','47','3','0');
INSERT INTO [t754] VALUES (472,'هذي برزت لنا فهجت رسيسا ... ثم انصرفت وما شفيت نسيسا','48','3','0');
INSERT INTO [t754] VALUES (473,'وجعلت حظي منك حظي في الكرى ... وتركتني للفرقدين جليسا','48','3','0');
INSERT INTO [t754] VALUES (474,'قطعت ذياك الخمار بسكرة ... وأدرت من خمر الفراق كؤسا','48','3','0');
INSERT INTO [t754] VALUES (475,'إن كنت طاعنة فإن مدامعي ... تكفي مزادكم وتروي العيسا','48','3','0');
INSERT INTO [t754] VALUES (476,'حاشى لمثلك أن تكون بخيلة ... ولمثل وجهك أن يكون عبوسا','48','3','0');
INSERT INTO [t754] VALUES (477,'ولمثل وصلك أن يكو ممنعا ... ولمثل نيلك أن يكون خسيسا','48','3','0');
INSERT INTO [t754] VALUES (478,'خود جنت بيني وبين عواذلي ... حربا وغادرت الفؤاد وطيسا','48','3','0');
INSERT INTO [t754] VALUES (479,'بيضاء يمنعها تكلم دلها ... تيها ويمنعها الحياء تميسا','49','3','0');
INSERT INTO [t754] VALUES (480,'لما وجدت دواء دائي عندها ... هانت على صفات جالينوس','49','3','0');
INSERT INTO [t754] VALUES (481,'أبقى زريق للثغور محمدا ... أبقى نفيس للنفيس نفيسا','49','3','0');
INSERT INTO [t754] VALUES (482,'إن حل فارقت الخزائن ماله ... أو سار فارقت الجسوم الروسا','49','3','0');
INSERT INTO [t754] VALUES (483,'ملك إذا عاديت نفسك عاده ... ورضيت أوحش ما كرهت أنيسا','49','3','0');
INSERT INTO [t754] VALUES (484,'الخائض الغمرات غير مدافع ... والشمري المطعن الدعيسا','49','3','0');
INSERT INTO [t754] VALUES (485,'كشفت جمهرة العباد فلم أجد ... إلا مسودا جنبه مرءوسا','49','3','0');
INSERT INTO [t754] VALUES (486,'بشر تصور غياة في آية ... تنفي الظنون وتفسد التقييسا','49','3','0');
INSERT INTO [t754] VALUES (487,'وبه يضن على البرية لا بها ... وعليه منها لا عليها يوسا','49','3','0');
INSERT INTO [t754] VALUES (488,'لو كان ذو القرنين أعمل رأيه ... لما أتى الظلمات صرن شموسا','49','3','0');
INSERT INTO [t754] VALUES (489,'أو كان صادف راس عازر سيفه ... في يوم معركة لأعيى عيسى','49','3','0');
INSERT INTO [t754] VALUES (490,'او كان لج البحر مثل يمينه ... ما انشق حتى جاز فيه موسى','50','3','0');
INSERT INTO [t754] VALUES (491,'أو كان للنيران ضوء جبينه ... عبدت فصار العالمون مجوسا','50','3','0');
INSERT INTO [t754] VALUES (492,'لما سمعت به سمعت بواحد ... ورأيته فرأيت منه خميسا','50','3','0');
INSERT INTO [t754] VALUES (493,'ولحظت أنمله فسلن مواهبا ... ولمست منصله فسال نفوسا','50','3','0');
INSERT INTO [t754] VALUES (494,'يا من نلوذ من الزمان بظله ... أبدا ونطرد باسمه إبليسا','50','3','0');
INSERT INTO [t754] VALUES (495,'صدر المخبر عنك دونك وصفه ... من بالعراق يراك في طرسوسا','50','3','0');
INSERT INTO [t754] VALUES (496,'فإذا طلبت فريسة فارقته ... وإذا خدرت تخذته عريسا','50','3','0');
INSERT INTO [t754] VALUES (497,'إني نثرت عليك درا فانتقد ... كثر المدلس فاحذر التدليسا','50','3','0');
INSERT INTO [t754] VALUES (498,'حجبتها عن أهل انطاكية ... وجلوتها لك فاجتليت عروسا','50','3','0');
INSERT INTO [t754] VALUES (499,'خير الطيور على القصور وشرها ... يأوي الخراب ويسكن الناووسا','50','3','0');
INSERT INTO [t754] VALUES (500,'لو جادت الدنيا فدتك بأهلها ... أو جاهدت كتبت عليك حبيسا','50','3','0');
INSERT INTO [t754] VALUES (501,'محمد بن زريق ما نرى أحدا ... إذا فقدناك يعطي قبل أن يعدا','51','3','0');
INSERT INTO [t754] VALUES (502,'فقد قصدتك والترحال مقترب ... والدار شاسعة والزاد قد نفدا','51','3','0');
INSERT INTO [t754] VALUES (503,'فخل كفك تهمي واثن وابلها ... إذا اكتفيت وإلا أغرق البلدا','51','3','0');
INSERT INTO [t754] VALUES (504,'بكيت يا ربع حتى كدت أبكيكا ... وجدت بي وبدمعي في مغانيكا','51','3','0');
INSERT INTO [t754] VALUES (505,'فعم صباحا لقد هيجت في شجنا ... وأردد تحيتناإنا محيوكا','51','3','0');
INSERT INTO [t754] VALUES (506,'بأي حكم زمان صرت متخذا ... رئم الفلا بدلا من رئم أهليكا','51','3','0');
INSERT INTO [t754] VALUES (507,'أيام فيك شموس ما انبعثن لنا ... إلا ابتعثن دما باللحظ مسفوكا','51','3','0');
INSERT INTO [t754] VALUES (508,'والعيش أخضر والأطلال مشرقة ... كأن نور عبيد الله يعلوكا','51','3','0');
INSERT INTO [t754] VALUES (509,'نجا امرء يا ابن يحيى كنت بغيته ... وخاب ركب ركاب لم يؤموكا','51','3','0');
INSERT INTO [t754] VALUES (510,'أحييت للشعراء الشعر فامتدحوا ... جميع من مدحوه بالذي فيكا','51','3','0');
INSERT INTO [t754] VALUES (511,'وعلموا الناس منك المجد واقتدروا ... على دقيق المعاني من معانيكا','51','3','0');
INSERT INTO [t754] VALUES (512,'فكن كما شئت يا من لا شبيه له ... أوكيف شئت فما خلق يدانيكا','51','3','0');
INSERT INTO [t754] VALUES (513,'شكر العفة لما أوليت أوجدني ... إلى يديك طريق العرف مسلوكا','51','3','0');
INSERT INTO [t754] VALUES (514,'وعظم قدرك في الآفاق أوهمني ... أني بقلة ما أثنيت أهجوكا','51','3','0');
INSERT INTO [t754] VALUES (515,'كفى بأنك من قحطان في شرف ... وإن فخرت فكل من مواليك','51','3','0');
INSERT INTO [t754] VALUES (516,'ولو نقصت كما قد زدت من كرم ... على الورى لرأوني مثل شانيكا','51','3','0');
INSERT INTO [t754] VALUES (517,'لبي نداك لقد نادى فاسمعني ... يفديك من رجل صحبي وأفديكا','52','3','0');
INSERT INTO [t754] VALUES (518,'ما زلت تتبع ما تولي يدا بيد ... حتى ظننت حيوتي من أياديكا','52','3','0');
INSERT INTO [t754] VALUES (519,'فإن تقل ها فعادات عرفت بها ... أولا فإنك فظننت لا يسخو بلا فوكا','52','3','0');
INSERT INTO [t754] VALUES (520,'أريقك أم ماء الغمامة أم خمر ... بفي برود وهو في كبدي جمر','52','3','0');
INSERT INTO [t754] VALUES (521,'إذا الغصن أم ذا الدعص أم أنت فتنة ... وذيا الذي قبلته البرق أم ثغر','52','3','0');
INSERT INTO [t754] VALUES (522,'رأت وجه من أهوى بليل عواذلي ... فقلن نرى شمسا وما طلع الفجر','52','3','0');
INSERT INTO [t754] VALUES (523,'رأين التي للسحر في لحظاتها ... سيوف ظباها من جمي أبدا حمر','52','3','0');
INSERT INTO [t754] VALUES (524,'تناهى سكون الحسن في حركاتها ... فليس لراء وجهها لم يمت عذر','52','3','0');
INSERT INTO [t754] VALUES (525,'إلبيك ابن يحيى بن الوليد تجاوزت ... بي البيد عيس لحمها والدم الشعر','52','3','0');
INSERT INTO [t754] VALUES (526,'نضحت بذكراكم حرارة قلبها ... فسارت وطول الأرض في عينها شبر','52','3','0');
INSERT INTO [t754] VALUES (527,'إلى ليث حرب يلحم الليث سيفه ... وبحر ندى في موجه يغرق البحر','53','3','0');
INSERT INTO [t754] VALUES (528,'وإن كان يبقى جوده من تليده ... شبيها بما يبقى من العاشق الهجر','53','3','0');
INSERT INTO [t754] VALUES (529,'فتى كل يوم يحتوي نفس ماله ... رماح المعالي لا الردينية السمر','53','3','0');
INSERT INTO [t754] VALUES (530,'تباعد ما بين السحاب وبينه ... فنائلها قطر ونائله غمر','53','3','0');
INSERT INTO [t754] VALUES (531,'ولو تنزل الدنيا على حكم كفه ... لأصبحت الدنيا وأكثرها نزر','53','3','0');
INSERT INTO [t754] VALUES (532,'أراه صغيرا قدرها عظم قدره ... فما لعظيم قدره عنده قدر','53','3','0');
INSERT INTO [t754] VALUES (533,'متى ما يشر نحو السماء بوجهه ... تخر له الشعري وينكسف البدر','53','3','0');
INSERT INTO [t754] VALUES (534,'ترى القمر الأرضي والملك الذي ... له الملك بعد الله والمجد والذكر','53','3','0');
INSERT INTO [t754] VALUES (535,'كثير سهاد العين من غير علة ... يؤرقه فيما يشرفه الفكر','53','3','0');
INSERT INTO [t754] VALUES (536,'له منن تفنى الثناء كأنما ... به أقسمت أن لا يؤدي لها شكر','53','3','0');
INSERT INTO [t754] VALUES (537,'أبا أحمد ما الفخر إلا لأهله ... وما لأمرء لم يمس من بحتر فخر','53','3','0');
INSERT INTO [t754] VALUES (538,'هم الناس إلا أنهم من مكارم ... يغنى بهم حضر ويجدو بهم سفر','53','3','0');
INSERT INTO [t754] VALUES (539,'بمن أضرب الأمثال أم من أقيسه ... إليك وأهل الدهر دونك والدهر','53','3','0');
INSERT INTO [t754] VALUES (540,'ما الشوق مقتنعا مني بذا الكمد ... حتى أكون بلا قلب ولا كبد','53','3','0');
INSERT INTO [t754] VALUES (541,'ولا الديار التي كان الحبيب بها ... تشكو إلي ولا أشكو إلى أحد','54','3','0');
INSERT INTO [t754] VALUES (542,'ما زال كل هزيم الودق ينحلها ... والسقم ينحلني حتى حكت جسدي','54','3','0');
INSERT INTO [t754] VALUES (543,'وكلما فاض دمعي غاض مصطبري ... كأن ما سال من جفني من جلدي','54','3','0');
INSERT INTO [t754] VALUES (544,'وأين من زفراتي من كلفت به ... وأين منك ابن يحيى صولة الأسد','54','3','0');
INSERT INTO [t754] VALUES (545,'لما وزنت بك الدنيا فملت بها ... وبالورى قل عندي كثرة العدد','54','3','0');
INSERT INTO [t754] VALUES (546,'ما جار في خلد الأيام لي فرح ... أبا عبادة حتى درت في خلدي','54','3','0');
INSERT INTO [t754] VALUES (547,'ملك إذا امتلأت مالا خزائنه ... أذاقها طعم ثكل الأم للولد','54','3','0');
INSERT INTO [t754] VALUES (548,'ماضي الجنان يريه الحزم قبل غد ... بقلبه ما ترى عيناه بعد غد','54','3','0');
INSERT INTO [t754] VALUES (549,'ماذا البهاء ولا ذا النور من بشر ... ولا السماح الذي فيه سماح يد','55','3','0');
INSERT INTO [t754] VALUES (550,'أي الأكف تباري الغيث ما اتفقا ... حتى إذا افترقا عادت ولم يعد','55','3','0');
INSERT INTO [t754] VALUES (551,'قد كنت أحسب أن المجد من مضر ... حتى تبحتر فهو اليوم من أدد','55','3','0');
INSERT INTO [t754] VALUES (552,'قوم إذا مطرت موتا سيوفهم ... حسبتها سحبا جادت على بلد','55','3','0');
INSERT INTO [t754] VALUES (553,'لم أجر غاية فكري منك في صفة ... إلا وجدت مداها غاية الأبد','55','3','0');
INSERT INTO [t754] VALUES (554,'جللا كما بي فليك التبريح ... أغذاء ذا الرشأ الأغن الشيح','55','3','0');
INSERT INTO [t754] VALUES (555,'لعبت بمشيته الشمول وغادرت ... صنما من الأصنام لولا الروح','56','3','0');
INSERT INTO [t754] VALUES (556,'ما باله لا حظته فتضرجت ... وجناته وفؤادي المجروح','56','3','0');
INSERT INTO [t754] VALUES (557,'ورما وما رمتا يداه فصابني ... سهم يعذب والسهام تريح','56','3','0');
INSERT INTO [t754] VALUES (558,'قرب المزار ولا مزار وإنما ... يغدو الجنان فلتقي ويروح','56','3','0');
INSERT INTO [t754] VALUES (559,'وفشت سرائرنا إليك وشفنا ... تعريضنا فبدا لك التصريح','56','3','0');
INSERT INTO [t754] VALUES (560,'لما تقطعت الحمول تقطعت ... نفسي أسى وكأنهن طلوح','57','3','0');
INSERT INTO [t754] VALUES (561,'وجلا الوداع من الحبيب محسنا ... حسن العزاء وقد جلين قبيح','57','3','0');
INSERT INTO [t754] VALUES (562,'فيد مسلمة وطرف شاخص ... وحشا يذوب ومدمع مسفوح','57','3','0');
INSERT INTO [t754] VALUES (563,'يجد الحمام ولو كوجدي لانبرى ... شجر الأراك مع الحمام ينوح','57','3','0');
INSERT INTO [t754] VALUES (564,'وأمق لو خدت الشمال براكب ... في عرضه لأناخ وهي طليح','57','3','0');
INSERT INTO [t754] VALUES (565,'نازعته قلص الركاب وركبها ... خوف الهلاك حداهم التسبيح','57','3','0');
INSERT INTO [t754] VALUES (566,'لولا الأمير مساور بن محمد ... ما جشمت خطرا ورد نصيح','57','3','0');
INSERT INTO [t754] VALUES (567,'ومتى ونت وأبو المظفر أمها ... فأتاح لي ولها الحمام متيح','57','3','0');
INSERT INTO [t754] VALUES (568,'شمنا وما حجب السماء بروقه ... وحري يجود وما مرته الريح','57','3','0');
INSERT INTO [t754] VALUES (569,'مرجو منفعة مخوف أذية ... مغبوق كأس محامد مصبوح','57','3','0');
INSERT INTO [t754] VALUES (570,'حنق على بدر اللجين وما أتت ... بإساءة وعن المسيىء صفوح','57','3','0');
INSERT INTO [t754] VALUES (571,'لو فرق الكرم المفرق ماله ... في الناس لم يكن في الزمان شحيح','57','3','0');
INSERT INTO [t754] VALUES (572,'ألغت مسامعه الملام وغادرت ... سمة على أنف اللئام تلوح','58','3','0');
INSERT INTO [t754] VALUES (573,'هذا الذي خلت القرون وذكره ... وحديثه في كتبها مشروح','58','3','0');
INSERT INTO [t754] VALUES (574,'ألبابنا بجماله مبهورة ... وسحابنا بنواله مفضوح','58','3','0');
INSERT INTO [t754] VALUES (575,'يغشى الطعان فلا يرد قناته ... مكسورة ومن الكماة صحيح','58','3','0');
INSERT INTO [t754] VALUES (576,'وعلى التراب من الدماء مجاسد ... وعلى السماء من العجاج مسوح','58','3','0');
INSERT INTO [t754] VALUES (577,'يخطو القتيل إلى القتيل أمامه ... رب الجواد وخلفه المبطوح','58','3','0');
INSERT INTO [t754] VALUES (578,'فمقيل حب محبه فرح به ... ومقيل غيظ عدوه مقروح','58','3','0');
INSERT INTO [t754] VALUES (579,'يخفى العداوة وهي غير خفية ... نظر العدو بما أسر يبوح','58','3','0');
INSERT INTO [t754] VALUES (580,'يا ابن الذي ما ضم برد كابنه ... شرفا ولا كالجد ضم ضريح','58','3','0');
INSERT INTO [t754] VALUES (581,'نفديك من سيل إذا سئل الندى ... هول إذا اختلطا دم ومسيح','58','3','0');
INSERT INTO [t754] VALUES (582,'لو كانت بحرا لم يكن لك ساحل ... أو كنت غيثا ضاق عنك اللوح','58','3','0');
INSERT INTO [t754] VALUES (583,'وخشيت منك على البلاد وأهلها ... ما كان أنذر قوم نوح نوح','58','3','0');
INSERT INTO [t754] VALUES (584,'عجز بحر فاقة ووراءه ... رزق الإله وبابك المفتوح','58','3','0');
INSERT INTO [t754] VALUES (585,'إن القريض شج بعطفي عائذ ... من أن يكون سواءك الممدوح','58','3','0');
INSERT INTO [t754] VALUES (586,'وذكى رائحة الرياض كلامها ... تبغي الثناء على الحيا فتفوح','59','3','0');
INSERT INTO [t754] VALUES (587,'جهد المقل فكيف بابن كريمة ... توليه خيرا واللسان فصيح','59','3','0');
INSERT INTO [t754] VALUES (588,'أمساور أم قرن شمس هذا ... أم ليث غاب يقدم الأستاذا','59','3','0');
INSERT INTO [t754] VALUES (589,'شم ما انتضيت فقد تركت ذبابه ... قطعا وقد ترك العباد جذاذا','59','3','0');
INSERT INTO [t754] VALUES (590,'هبك ابن يزداذ حطمت وصحبه ... أترى الورى أصحوا بنى يزداذا','59','3','0');
INSERT INTO [t754] VALUES (591,'غادرت أوجههم بحيث لقيتهم ... أقفاءهم وكبودهم افلاذا','59','3','0');
INSERT INTO [t754] VALUES (592,'في موقف وقف الحمام عليهم ... في ضنطه واستحوذ استحواذا','59','3','0');
INSERT INTO [t754] VALUES (593,'جمدت نفوسهم فلما جئتها ... أجريتها وسقيتها الفولاذا','59','3','0');
INSERT INTO [t754] VALUES (594,'لما رأوك رأوا أباك محمدا ... في جوشن وأخا أبيك معاذا','59','3','0');
INSERT INTO [t754] VALUES (595,'أعجلت ألسنهم بضرب رقابهم ... عن قولهم لا فارس إلا ذا','59','3','0');
INSERT INTO [t754] VALUES (596,'غر طلعت عليه طلعة عارض ... مطر المنايا وابلا ورذاذا','59','3','0');
INSERT INTO [t754] VALUES (597,'فغدا أسيرا قد بللت ثيابه ... بدم وبل ببوله الأفخاذا','59','3','0');
INSERT INTO [t754] VALUES (598,'سدت عليه المشرفية طرقه ... فانصاع لا حلبا ولا بغداذا','59','3','0');
INSERT INTO [t754] VALUES (599,'طلب الإمارة في الثغور ونشؤه ... ما بين كرخايا إلى كلواذا','60','3','0');
INSERT INTO [t754] VALUES (600,'فكأنه حسب الأسنة حلوة ... أو ظنها البراني والآزاذا','60','3','0');
INSERT INTO [t754] VALUES (601,'لم يلق قبلك من إذا اختلف القنا ... جعل الطعان من الطعان ملاذا','60','3','0');
INSERT INTO [t754] VALUES (602,'من لا توافقه الحياة وطيبها ... حتى يوافق عزمه الإنقاذا','60','3','0');
INSERT INTO [t754] VALUES (603,'متعودا لبس الدروع يخالها ... في البرد خزا والهواجر لاذا','60','3','0');
INSERT INTO [t754] VALUES (604,'أعجب بأخذكه وأعجب منكما ... أن لا تكون لمثله أخاذا','60','3','0');
INSERT INTO [t754] VALUES (605,'إني لأعلم واللبيب خبير ... أن الحياة وإن حرصت غرور','60','3','0');
INSERT INTO [t754] VALUES (606,'ورأيت كلا ما يعلل نفسه ... بتعلة وإلى الفناء يصير','60','3','0');
INSERT INTO [t754] VALUES (607,'أمجاور الديماس رهن قرارة ... فيها الضياء بوجهه والنور','60','3','0');
INSERT INTO [t754] VALUES (608,'ما كنت أحسب قبل دفنك في الثرى ... أن الكواكب في التراب تغور','60','3','0');
INSERT INTO [t754] VALUES (609,'ما كنت آمل قبل نعشك أن أرى ... رضوى على أيدي الرجال تسير','60','3','0');
INSERT INTO [t754] VALUES (610,'خرجوا به ولكل باك خلفه ... صعقات موسى يوم دك الطور','60','3','0');
INSERT INTO [t754] VALUES (611,'والشمس في كبد السماء مريضة ... والأرض واجفة تكاد تمور','60','3','0');
INSERT INTO [t754] VALUES (612,'وحفيف أجنحة الملائك حوله ... وعيون أهل اللاذقية صور','60','3','0');
INSERT INTO [t754] VALUES (613,'حتى أتوا جدثا كأن ضريحه ... في قلب كل موحد محفور','60','3','0');
INSERT INTO [t754] VALUES (614,'بمزود كفن البلى من ملكه ... مغف واثمد عينه الكافور','61','3','0');
INSERT INTO [t754] VALUES (615,'فيه السماحة والفصاحة والتقى ... والبأس أجمع والحجي والخير','61','3','0');
INSERT INTO [t754] VALUES (616,'كفل الثناء له برد حياته ... لما انطوى فكأنه منشور','61','3','0');
INSERT INTO [t754] VALUES (617,'فكأنما عيسى بن مريم ذكره ... وكأن عازر شخصه المقبور','61','3','0');
INSERT INTO [t754] VALUES (618,'غاضت أنامله وهن بحور ... وخبت مكايده وهن سعير','61','3','0');
INSERT INTO [t754] VALUES (619,'يبكي عليه وما استقر قراره ... في اللحد حتى صافحته الحور','61','3','0');
INSERT INTO [t754] VALUES (620,'صبرا بنى إسحاق عنه تكرما ... إن العظيم على العظيم صبور','61','3','0');
INSERT INTO [t754] VALUES (621,'فلكل مفجوع سواكم مشبه ... ولكل مفقود سواه نظير','61','3','0');
INSERT INTO [t754] VALUES (622,'أيام قائم سيفه في كفه ال ... يمنى وباع الموت عنه قصير','61','3','0');
INSERT INTO [t754] VALUES (623,'ولطال ما انهملت بماء أحمر ... في شفرتيه جماجم ونحور','61','3','0');
INSERT INTO [t754] VALUES (624,'فأعيد إخوته برب محمد ... أن يجزنوا ومحمد مسرور','61','3','0');
INSERT INTO [t754] VALUES (625,'أو يرغبوا بقصورهم عن حفرة ... حياه فيها منكر ونكير','61','3','0');
INSERT INTO [t754] VALUES (626,'نفر إذا غابت غمود سيوفهم ... عنها فآجال العداة حصور','61','3','0');
INSERT INTO [t754] VALUES (627,'وإذا لقوا جيشا تيقن أنه ... من بطن طير تنوفة محشور','61','3','0');
INSERT INTO [t754] VALUES (628,'لم تثن في طلب أعنة خليهم ... ألا وعمر طريدها مبتور','61','3','0');
INSERT INTO [t754] VALUES (629,'يممت شاسع دارهم عن نية ... إن لمحب على البعاد يزور','61','3','0');
INSERT INTO [t754] VALUES (630,'وقنعت باللقيا وأول نظرة ... إن القليل من الحبيب كثير','62','3','0');
INSERT INTO [t754] VALUES (631,'ألآل إبراهيم بعد محمد ... إلا حنين دائم وزفير','62','3','0');
INSERT INTO [t754] VALUES (632,'ما شك خابر أمرهم من بعده ... أن العزاء عليهم محظور','62','3','0');
INSERT INTO [t754] VALUES (633,'تدمى خدودهم الدموع وتنقضي ... ساعات ليلهم وهن دهور','62','3','0');
INSERT INTO [t754] VALUES (634,'أبناء عم كل ذنب لامرء ... إلا السعاية بينهم مغفور','62','3','0');
INSERT INTO [t754] VALUES (635,'طار الوشاة على صفاء ودادهم ... وكذا الذباب على الطعام يطير','62','3','0');
INSERT INTO [t754] VALUES (636,'ولقد منحت أبا الحسين مودة ... جودي بها لعدوه تبذير','62','3','0');
INSERT INTO [t754] VALUES (637,'لأي صروف الدهر فيه نعاتب ... وأي رزاياه بوتر نطالب','62','3','0');
INSERT INTO [t754] VALUES (638,'مضى من فقدنا صبرنا عند فقده ... وقد كان يعطي الصبر والصبر عازب','62','3','0');
INSERT INTO [t754] VALUES (639,'يزور الأعدى في سماء عجاجة ... أسنته في جانبيها الكواكب','62','3','0');
INSERT INTO [t754] VALUES (640,'فتسفر عنه والسيوف كأنما ... مضاربها مما انفللن ضرائب','62','3','0');
INSERT INTO [t754] VALUES (641,'طلعن شموسا والغمود مشارق ... لهن وهامات الرجال مغارب','63','3','0');
INSERT INTO [t754] VALUES (642,'مصائب شتى جمعت في مصيبة ... ولم يكفها حتى قفتها مصائب','63','3','0');
INSERT INTO [t754] VALUES (643,'رثى أبن أبينا غير ذي رحم له ... فباعدنا عنه ونحن الأقارب','63','3','0');
INSERT INTO [t754] VALUES (644,'وعرض أنا شامتون بموته ... وإلا فزارت عارضيه القواضب','63','3','0');
INSERT INTO [t754] VALUES (645,'أليس عجيبا أن بين بني أب ... لنجل يهودي تدب العقارب','63','3','0');
INSERT INTO [t754] VALUES (646,'ألا إنما كانت وفاة محمد ... دليلا على أن ليس لله غالب','63','3','0');
INSERT INTO [t754] VALUES (647,'هو البين حتى ما تأنى الحزائق ... ويا قلب حتى أنت ممن أفارق','63','3','0');
INSERT INTO [t754] VALUES (648,'وقفنا ومما زاد بثا وقوفنا ... فريقي هوى منا مشوق وشائق','63','3','0');
INSERT INTO [t754] VALUES (649,'وقد صارت الأجفان قرحى من البكا ... وصار بهارا في الخدود الشقائض','63','3','0');
INSERT INTO [t754] VALUES (650,'على ذا مضى الناس اجتماع وفرقة ... وميت ومولود وقال ووامق','63','3','0');
INSERT INTO [t754] VALUES (651,'تغير حالي والليالي بحالها ... وشبت وما شاب الزمان الغرانق','64','3','0');
INSERT INTO [t754] VALUES (652,'وليل دجوجي كأنا جلت لنا ... محياك فيه فاهتدينا السمالق','64','3','0');
INSERT INTO [t754] VALUES (653,'فما زال لولا نور وجهك جنحه ... ولا جابها الركبان لولا الأيانق','64','3','0');
INSERT INTO [t754] VALUES (654,'وهز أطار النوم حتى كأنني ... من السكر في الغرزين ثوب شبارق','64','3','0');
INSERT INTO [t754] VALUES (655,'بمن تقشعر الأرض خوفا إذا مشى ... عليها وترتج الجبال الشواعق','64','3','0');
INSERT INTO [t754] VALUES (656,'فتى كالسحاب الجون يخشى ويرتجى ... يرجى الحيا منه وتخشى الصواعق','64','3','0');
INSERT INTO [t754] VALUES (657,'ولكنها تمضي وهذا مخيم ... وتكذب أحيانا وذا الدهر صادق','64','3','0');
INSERT INTO [t754] VALUES (658,'تخلى من الدنيا لينسى فما خلت ... مغاربها من ذكره والمشارق','64','3','0');
INSERT INTO [t754] VALUES (659,'غذى الهندوانيات بالهام والطلي ... فهن مداريها وهن المخانق','64','3','0');
INSERT INTO [t754] VALUES (660,'تشقق منهن الجيوب إذا غزا ... وتخضب منهن اللحى والمفارق','64','3','0');
INSERT INTO [t754] VALUES (661,'يجنبها من حتفه عنه غافل ... ويصلي بها من نفسه منه طالق','64','3','0');
INSERT INTO [t754] VALUES (662,'يحاجي به ما ناطق وهو ساكت ... يرى ساكتا والسيف عن فيه ناطق','64','3','0');
INSERT INTO [t754] VALUES (663,'نكرتك حتى طال منك تعجبي ... ولا عجب من حسن ما الله خالق','65','3','0');
INSERT INTO [t754] VALUES (664,'كأنك في الإعطاء للمال مبغض ... وفي كل حرب للمنية عاشق','65','3','0');
INSERT INTO [t754] VALUES (665,'ألا قلما تبقى على ما بدا لها ... وحل بها منك القنا والسوابق','65','3','0');
INSERT INTO [t754] VALUES (666,'خف الله واستر ذا الجمال ببرقع ... فإن لحت ذابت في الخدور العواتق','65','3','0');
INSERT INTO [t754] VALUES (667,'سيحيى بك السمار ما لاح كوكب ... ويجدو بك السفار ما ذر شارق','65','3','0');
INSERT INTO [t754] VALUES (668,'فما ترزق الأقدار من أنت حارم ... ولا تحرم الأقدار من أنت رازق','65','3','0');
INSERT INTO [t754] VALUES (669,'ولا تفتق الأيام ما أنت راتق ... ولا ترتق الأيام ما أنت فاتق','65','3','0');
INSERT INTO [t754] VALUES (670,'لك الخير غيري رام من غيرك الغني ... وغيري بغير اللاذقية لاحق','65','3','0');
INSERT INTO [t754] VALUES (671,'هي الغرض الأقصى ورؤيتك المنى ... ومنزلك الدنيا وأنت الخلائق','65','3','0');
INSERT INTO [t754] VALUES (672,'أتنكر يا ابن إسحاق إخائي ... وتحسب ماء غيري من إناءي','65','3','0');
INSERT INTO [t754] VALUES (673,'أأنطق فيك هجرا بعد علمي ... بأنك خير من تحت السماء','65','3','0');
INSERT INTO [t754] VALUES (674,'وأكره من ذباب السيف طعما ... وأمضي في الأمور من القضاء','65','3','0');
INSERT INTO [t754] VALUES (675,'وما أرمت على العشرين سني ... فكيف مللت من طول البقاء','65','3','0');
INSERT INTO [t754] VALUES (676,'وما استغرقت وصفك في مديحي ... فأنقص منه شيئا بالهجاء','65','3','0');
INSERT INTO [t754] VALUES (677,'وهبني قلت هذا الصبح ليل ... أيعمى العالمون عن الضياء','65','3','0');
INSERT INTO [t754] VALUES (678,'تطيع الحاسدين وأنت مرء ... جعلت فداءه وهم فداءي','66','3','0');
INSERT INTO [t754] VALUES (679,'وهاجى نفسه من لم يميز ... كلامي من كلامهم الهراء','66','3','0');
INSERT INTO [t754] VALUES (680,'وإن من العجائب أن تراني ... فتعدل بي أقل من الهباء','66','3','0');
INSERT INTO [t754] VALUES (681,'وتنكر موتهم وأنا سهيل ... طلعت بموت أولاد الزناء','66','3','0');
INSERT INTO [t754] VALUES (682,'ملام النوى في ظلمها غاية الظلم ... لعل بها مثل الذي بي من السقم','66','3','0');
INSERT INTO [t754] VALUES (683,'فلو لم تغر لم تزو عني لقاءكم ... ولو لم تردكم لم تكن فيكم خصمي','66','3','0');
INSERT INTO [t754] VALUES (684,'أمنعمة بالعودة الظبية التي ... بغير ولي كان نائلها الوسمييريد بنائلها وصالها وأراد بالوسمي أول ما بدأت به وبالوتي ما بعد ذلك من الوصل يقول أنها بدأت بوصل ثم لم تعد إليه فليتها أنعمت علي برجوعها إلى الوصل مرة أخرى والوسمي أول مطر في السنة والولي الذي يليه وهو منقول من قول ذي الرمة، لني ولية تمرع جنابي فإنني، لوسمي ما أوفيت من ذاك شاكر، والمعنى من قول بشار، قد زرتني زورة في الدهر واحدة، ثنى ولا تجعليها بيضة الديك،','66','3','0');
INSERT INTO [t754] VALUES (685,'ترشفت فاها سحرة فكأنني ... ترشفت حر الوجد من بارد الظلم','66','3','0');
INSERT INTO [t754] VALUES (686,'فتاة تساوى عقدها وكلامها ... ومبسمها الدري في الحسن والنظم','66','3','0');
INSERT INTO [t754] VALUES (687,'ونكهتها والمندلي وقرقف ... معتقة صهباء في الريح والطعم','66','3','0');
INSERT INTO [t754] VALUES (688,'جفتني كأني لست انطق قومها ... واطعنهم والشهب في ثورة الدهم','67','3','0');
INSERT INTO [t754] VALUES (689,'يحاذرني حتفي كأني حتفه ... وتنكزني الأفعى فيقتلها سمي','67','3','0');
INSERT INTO [t754] VALUES (690,'طوال الردينيات يقصفها دمي ... وبيض السريجيات يقطعها لحمي','67','3','0');
INSERT INTO [t754] VALUES (691,'برتني السرى برى المدى فددنني ... أخف على المركوب من نفسي جرمي','67','3','0');
INSERT INTO [t754] VALUES (692,'وأبصر من زرقاء جو لأنني ... إذا نظرت عيناي ساواهما علمي','67','3','0');
INSERT INTO [t754] VALUES (693,'كأني دحوت الأرض من خبرتي بها ... كأن بنى الإسكندر السد من عزمي','67','3','0');
INSERT INTO [t754] VALUES (694,'لألقي ابن إسحاق الذي دق فهمه ... فأبدع حتى جل عن دقة الفهم','67','3','0');
INSERT INTO [t754] VALUES (695,'وأسمع من الفاظه اللغة التي ... يلذ بها سمعي ولو ضمنت شتمي','67','3','0');
INSERT INTO [t754] VALUES (696,'يمين بني قحكان رأس قضاعة ... عرنينها بدر النجوم بنى فهم','67','3','0');
INSERT INTO [t754] VALUES (697,'إذا بيت الأعداء كان استماعهم ... صرير العوالي قبل قعقعة اللجم','68','3','0');
INSERT INTO [t754] VALUES (698,'مدل الأعزاء المعز وإن يئن ... به يتمهم فالموتم الجابر اليتم','68','3','0');
INSERT INTO [t754] VALUES (699,'وإن تمس داء في القلوب قناته ... فممسكها منه الشفاء من العدم','68','3','0');
INSERT INTO [t754] VALUES (700,'مقلد طاغي الشفرتين محكم ... على الهام إلا أنه جائر الحكم','68','3','0');
INSERT INTO [t754] VALUES (701,'تحرج عن حقن الدماء كأنه ... يرى قتل نفس ترك رأس على جسم','68','3','0');
INSERT INTO [t754] VALUES (702,'وجدنا ابن إسحاق الحسني كجده ... على كثرة القتلى بريئا من الإثم','68','3','0');
INSERT INTO [t754] VALUES (703,'مع الحزم حتى لو تعمد تركه ... لألحقه تضييعه الحزم بالحزم','68','3','0');
INSERT INTO [t754] VALUES (704,'وفي الحرب حتى لو أراد تأخرا ... لآخره الطبع الكريم إلى القدم','68','3','0');
INSERT INTO [t754] VALUES (705,'له رحمة تحيي العظام وغضبة ... بها فضلة للجرم عن صاحب الجرم','68','3','0');
INSERT INTO [t754] VALUES (706,'ورقة وجه لو ختمت بنظرة ... على وجنتيه ما انمحى أثر الختم','68','3','0');
INSERT INTO [t754] VALUES (707,'أذاق الغواني حسنه ما أذقنني ... وعف فجازاهن عني على الصرم','68','3','0');
INSERT INTO [t754] VALUES (708,'فدى من على الغبراء أولهم أنا ... لهذا الأبي الماجد الجائد القرم','69','3','0');
INSERT INTO [t754] VALUES (709,'لقد حال بين الجن والأمن سيفه ... فما الظن بعد الجن بالعرب والعجم','69','3','0');
INSERT INTO [t754] VALUES (710,'وأرهب حتى لو تأمل درعه ... جرت جزء من غير نار ولا فحم','69','3','0');
INSERT INTO [t754] VALUES (711,'وجاد فلولا جوده غير شارب ... لقيل كريم هيجته ابنة الكرم','69','3','0');
INSERT INTO [t754] VALUES (712,'أطعناك طوع الدهر يابن ابن يوسف ... بشهوتنا والحاسدوا لك بالرغم','69','3','0');
INSERT INTO [t754] VALUES (713,'وثقنا بأن تعطى فلو لم تجد لنا ... لخلناك قد أعطيت من قوة الوهم','69','3','0');
INSERT INTO [t754] VALUES (714,'دعيت بتقريظيك في كل مجلس ... وطن الذي يدعو ثناءي عليك اسمى','69','3','0');
INSERT INTO [t754] VALUES (715,'وأطمعتني في نيل ما لا أناله ... بما نلت حتى صرت أطمع في النجم','69','3','0');
INSERT INTO [t754] VALUES (716,'إذا ما ضربت القرن ثم أجزتني ... فكل ذهبا لي مرة منه بالكلم','69','3','0');
INSERT INTO [t754] VALUES (717,'أبت لك ذمي نخوة يمينة ... ونفس بها في مازق أبدا ترمي','69','3','0');
INSERT INTO [t754] VALUES (718,'وكم قائل لو كان ذا الشخص نفسه ... لكان قراه مكمن العسكر الدهم','70','3','0');
INSERT INTO [t754] VALUES (719,'وقائلة والأرض أعنى تعجبا ... على أمرؤ يمشي بوقري من الحلم','70','3','0');
INSERT INTO [t754] VALUES (720,'عظمت فلما لم تكلم مهابة ... تواضعت وهو العظم عظما عن العظم','70','3','0');
INSERT INTO [t754] VALUES (721,'إذا ما الكأس أرعشت اليدين ... صحوت فلم تحل بيني وبيني','70','3','0');
INSERT INTO [t754] VALUES (722,'هجرت الخمر كالذهب المصفى ... فخمري ماء مزن كاللجين','70','3','0');
INSERT INTO [t754] VALUES (723,'أغار من الزجاجة وهي تجري ... على شفة الأمير أبي الحسين','70','3','0');
INSERT INTO [t754] VALUES (724,'كأن بياضها والراح فيها ... بياض محدق بسواد عين','70','3','0');
INSERT INTO [t754] VALUES (725,'أتيناه نطالبه برفد ... فطالب نفسه منه بدين','70','3','0');
INSERT INTO [t754] VALUES (726,'مرتك ابن إبراهيم صافية الخمر ... وهنئتها من شارب مسكر السكر','70','3','0');
INSERT INTO [t754] VALUES (727,'رأيت الحميا في الزجاج بكفه ... فشبهتها بالشمس في البدر في البحر','70','3','0');
INSERT INTO [t754] VALUES (728,'إذا ما ذكرنا جوده كان حاضرا ... ناي أو دنا يسعى على قدم الخضر','70','3','0');
INSERT INTO [t754] VALUES (729,'أحاد أم سداس في أحاد ... لييلتنا المنوطة بالتنادي','70','3','0');
INSERT INTO [t754] VALUES (730,'كأن بنات نعش في دجاها ... خرائد سافراتس في حداد','71','3','0');
INSERT INTO [t754] VALUES (731,'أفكر في معاقرة المنايا ... وقود الخيل مشرفة الهوادي','71','3','0');
INSERT INTO [t754] VALUES (732,'زعيما للقنا الخطي عزمي ... بسفك دم الحواضر والبوادي','71','3','0');
INSERT INTO [t754] VALUES (733,'إلى كم ذات التخلف والتواني ... وكم هذا التمادي في التمادي','71','3','0');
INSERT INTO [t754] VALUES (734,'وشغل النفس عن طلب المعالي ... ببيع الشعر في سوق الكساد','71','3','0');
INSERT INTO [t754] VALUES (735,'وما ماضي الشباب بمسترد ... ولا يوم يمر بمستعاد','71','3','0');
INSERT INTO [t754] VALUES (736,'متى لحظت بياض الشيب عيني ... فقد وجدته منها في السواد','71','3','0');
INSERT INTO [t754] VALUES (737,'متى ما أزددت من بعد التناهي ... فقد وقع انتقاصي في ازديادي','71','3','0');
INSERT INTO [t754] VALUES (738,'أأرضى أن أعيش ولا أكافي ... على ما للأمير من الأيادي','71','3','0');
INSERT INTO [t754] VALUES (739,'جزى الله المسير إليه خيرا ... وإن ترك المطايا كالمزاد','71','3','0');
INSERT INTO [t754] VALUES (740,'فلم تلق ابن إبراهيم عنسي ... وفيها قوت يوم للقراد','72','3','0');
INSERT INTO [t754] VALUES (741,'ألم يكن بيننا بلد بعيد ... فصير طوله عرض النجاد','72','3','0');
INSERT INTO [t754] VALUES (742,'وأبعد بعدنا بعد التداني ... وقرب قربنا قرب البعاد','72','3','0');
INSERT INTO [t754] VALUES (743,'فلما جئته أعلى محلي ... وأجلسني على السبع الشداد','72','3','0');
INSERT INTO [t754] VALUES (744,'تهلل قبل تسليمي عليه ... وألقي ماله قبل الوساد','72','3','0');
INSERT INTO [t754] VALUES (745,'نلومك يا علي لغير ذنب ... لأنك قد زريت على العباد','72','3','0');
INSERT INTO [t754] VALUES (746,'وأنك لا تجود على جواد ... هباتك أن يلقب الجواد','72','3','0');
INSERT INTO [t754] VALUES (747,'كأن سخاءك الإسلام تخشى ... متى ما حلت عاقبة ارتداد','72','3','0');
INSERT INTO [t754] VALUES (748,'كأن الهام في الهيجا عيون ... وقد طبعت سيوفك من رقاد','72','3','0');
INSERT INTO [t754] VALUES (749,'وقد صغت الأسنة من هموم ... فما يخطرون إلا في فؤاد','72','3','0');
INSERT INTO [t754] VALUES (750,'ويوم جلبتها شعث النواصي ... معقدة السبائب للطراد','72','3','0');
INSERT INTO [t754] VALUES (751,'وحام بها الهلاك على أناس ... لهم باللاذقية بغي عاد','72','3','0');
INSERT INTO [t754] VALUES (752,'فكان الغرب بحرا من مياه ... وكان الشرق بحرا من جياد','72','3','0');
INSERT INTO [t754] VALUES (753,'وقد خفقت لك الرايات فيه ... فظل يموج بالبيض الحداد','73','3','0');
INSERT INTO [t754] VALUES (754,'لقوك بأكبد الإبل الأبايا ... فسقتهم وحد السيف حادي','73','3','0');
INSERT INTO [t754] VALUES (755,'وقد مزقت ثوب الغي عنهم ... وقد ألبستهم ثوب الرشاد','73','3','0');
INSERT INTO [t754] VALUES (756,'فما تركوا الإمارة لاختيار ... ولا انتحلوا ودادك من وداد','73','3','0');
INSERT INTO [t754] VALUES (757,'ولا استغلوا لزهد في التعالي ... ولا انقادوا سرورا بانقياد','73','3','0');
INSERT INTO [t754] VALUES (758,'ولكن هب خوفك في حشاهم ... هبوب الريح في رجل الجراد','73','3','0');
INSERT INTO [t754] VALUES (759,'وماتوا قبل موتهم فلما ... مننت أعدتهم قبل البعد','73','3','0');
INSERT INTO [t754] VALUES (760,'غمدت صوارما لو لم يتوبوا ... محوتهم بها محو المداد','73','3','0');
INSERT INTO [t754] VALUES (761,'وما الغضب الطريف وإن تقوى ... بمنتصف من الكرم التلاد','73','3','0');
INSERT INTO [t754] VALUES (762,'ولا تغررك السنة موال ... تقلبهن أفئدة أعادي','73','3','0');
INSERT INTO [t754] VALUES (763,'وكن كالموت لا يرثي لباك ... بكى منه ويروى وهو صادي','73','3','0');
INSERT INTO [t754] VALUES (764,'فإن الجرح ينفر بعد حين ... إذا كان البناء على فساد','73','3','0');
INSERT INTO [t754] VALUES (765,'وإن الماء يجري من جماد ... وإن النار تخرج من زناد','73','3','0');
INSERT INTO [t754] VALUES (766,'وكيف يبيت مضطجعا جبان ... فرشت لجنبه شوك القتاد','73','3','0');
INSERT INTO [t754] VALUES (767,'يرى في النوم رمحك في كلاه ... ويخشى أن يراه في السهاد','73','3','0');
INSERT INTO [t754] VALUES (768,'أثرت أبا الحسين بمدح قوم ... نزلت بهم فرست بغير زاد','73','3','0');
INSERT INTO [t754] VALUES (769,'وظنوني مدحتهم قديما ... وأنت بما مدحتهم مرادي','74','3','0');
INSERT INTO [t754] VALUES (770,'وإني عنك بعد غد لغاد ... وقلبي عن فنائك غير غادي','74','3','0');
INSERT INTO [t754] VALUES (771,'محبك حيث ما اتجهت ركابي ... وضيفك حيث كنت من البلاد','74','3','0');
INSERT INTO [t754] VALUES (772,'ملث القطر أعطشها ربوعا ... وإلا فاسقها السم النقيعا','74','3','0');
INSERT INTO [t754] VALUES (773,'أسائلها عن المتديريها ... فلا تدري ولا تذري دموعا','74','3','0');
INSERT INTO [t754] VALUES (774,'لحاها الله إلا ماضييها ... زمان اللهو والخود الشموعا','74','3','0');
INSERT INTO [t754] VALUES (775,'منعمة ممنعة رداح ... يكلف لفظها الطير الوقوع','74','3','0');
INSERT INTO [t754] VALUES (776,'ترفع ثوبها الأرداف عنها ... فيبقى من وشاحيها شسوعا','74','3','0');
INSERT INTO [t754] VALUES (777,'إذا ماست رأيت لها ارتجاجا ... له لولا سواعدها نزوعا','74','3','0');
INSERT INTO [t754] VALUES (778,'تألم درزه والدرز لين ... كما تتألم العضب الصنيعا','74','3','0');
INSERT INTO [t754] VALUES (779,'ذراعاها عدوا دملجيها ... يظن ضجيعها الزند الضجيعا','74','3','0');
INSERT INTO [t754] VALUES (780,'كأن نقابها غيم رقيق ... يضي بمنعه البدر الطلوعا','74','3','0');
INSERT INTO [t754] VALUES (781,'أقول لها أكشفي ضري وقولي ... بأكثر من تدللها خضوعا','74','3','0');
INSERT INTO [t754] VALUES (782,'أخفت الله في احياء نفس ... متى عصى الإله بأن أطيعا','74','3','0');
INSERT INTO [t754] VALUES (783,'غدا بك كل خلو مسهاما ... وأصبح كل مستور خليعا','74','3','0');
INSERT INTO [t754] VALUES (784,'غدا بك كل خلو مستهاما ... وأصبح كل مستور خليعا','74','3','0');
INSERT INTO [t754] VALUES (785,'أحبك أو يقولوا جر نمل ... ثبيرا وابن إبراهيم ريعا','74','3','0');
INSERT INTO [t754] VALUES (786,'بعيد الصيت منبث السرايا ... يشيب ذكره الطفل الرضيعا','75','3','0');
INSERT INTO [t754] VALUES (787,'يغض الطرف من مكر ودهي ... كأن به وليس به خشوعا','75','3','0');
INSERT INTO [t754] VALUES (788,'إذا استعطيته ما في يديه ... فقدك سألت عن سر مذيعا','75','3','0');
INSERT INTO [t754] VALUES (789,'قبولك منه من عليه ... وإلا يبتدي يره فظيعا','75','3','0');
INSERT INTO [t754] VALUES (790,'لهون المال أفرشه أديما ... وللتفريق يكره أني ضيعا','75','3','0');
INSERT INTO [t754] VALUES (791,'إذا ضرب الأمير رقاب قوم ... فما لكرامة مد النطوعا','75','3','0');
INSERT INTO [t754] VALUES (792,'فليس بواهب إلا كثيرا ... وليس بقاتل إلا قريعا','75','3','0');
INSERT INTO [t754] VALUES (793,'وليس مؤدبا إلا بنصل ... كفى الصمصامة التعب القطيعا','75','3','0');
INSERT INTO [t754] VALUES (794,'علي ليس يمنع من مجيء ... مبارزه ويمنعه الرجوعا','75','3','0');
INSERT INTO [t754] VALUES (795,'علي قاتل البطل المفدى ... ومدله من الزرد النجيعا','75','3','0');
INSERT INTO [t754] VALUES (796,'إذا أعوج القنا في حامليه ... وجاز إلى ضلوعهم الضلوعا','75','3','0');
INSERT INTO [t754] VALUES (797,'ونالت ثارها الأكباد منه ... فأولته اندقاقا أو صدوعا','75','3','0');
INSERT INTO [t754] VALUES (798,'فحد في ملتقى الخيلين عنه ... وإن كنت الجبعثنة الشجيعا','75','3','0');
INSERT INTO [t754] VALUES (799,'إن استجرأت ترمقه بعيدا ... فأنت اسطعت شيئا ما استطيعا','75','3','0');
INSERT INTO [t754] VALUES (800,'وإن ماريتني فاركب حصانا ... ومثله تخر له صريعا','75','3','0');
INSERT INTO [t754] VALUES (801,'غمام ربما مطر انتقاما ... فأقحط ودقه البلد المريعا','75','3','0');
INSERT INTO [t754] VALUES (802,'رآني بعد ما قطع المطايا ... تيممه وقطعت القطوعا','75','3','0');
INSERT INTO [t754] VALUES (803,'فصير سيله بلدي غديرا ... وصير خيره سنتي ربيعا','76','3','0');
INSERT INTO [t754] VALUES (804,'وجاودني بأن يعطى وأحوى ... فاغرق نيله أخذي سريعا','76','3','0');
INSERT INTO [t754] VALUES (805,'أمنسي السكون وحضرموتا ... ووالدتي وكندة والسبيعا','76','3','0');
INSERT INTO [t754] VALUES (806,'قد استقصيت في سلب الأعادي ... فرج لهم من السلب الهجوما','76','3','0');
INSERT INTO [t754] VALUES (807,'إذا ما لم تسر جيشا إليهم ... اسرت إلى قلوبهم الهلوعا','76','3','0');
INSERT INTO [t754] VALUES (808,'رضوا بك كالرضى بالشيب قسرا ... وقد وخط النواصي والفروعا','76','3','0');
INSERT INTO [t754] VALUES (809,'فلا عزل وأنت بلا سلاح ... لحاظك ما تكون به منيعا','76','3','0');
INSERT INTO [t754] VALUES (810,'لو استبدلت ذهنك من حسام ... قددت به المغافر والدروعا','76','3','0');
INSERT INTO [t754] VALUES (811,'لو استفرغت جهدك في قتال ... أتيت به على الدنيا جميعا','76','3','0');
INSERT INTO [t754] VALUES (812,'سموت بهمة تسمو فتسمو ... فما تلفي بمرتبة قنوعا','76','3','0');
INSERT INTO [t754] VALUES (813,'وهبك سمحت حتى لا جواد ... فكيف علوت حتى لا رفيعا','76','3','0');
INSERT INTO [t754] VALUES (814,'أحق عاف بدمعك الهمم ... أحدث شيء عهدا بها القدم','76','3','0');
INSERT INTO [t754] VALUES (815,'وإنما الناس بالملوك وما ... تفلح عرب ملوكها عجم','76','3','0');
INSERT INTO [t754] VALUES (816,'لا أدب عندهم ولا حسب ... ولا عهود لهم ولا ذمم','76','3','0');
INSERT INTO [t754] VALUES (817,'بكل أرض وظئتها أمم ... ترعى بعبد كأنها غنم','76','3','0');
INSERT INTO [t754] VALUES (818,'يستخشن الخز حين يلمسه ... وكان يبرى بظفره القلم','76','3','0');
INSERT INTO [t754] VALUES (819,'إني وإن لمت حاسدي فما ... أنكر أنى عقوبة لهم','76','3','0');
INSERT INTO [t754] VALUES (820,'وكيف لا يحسد أمرء علم ... له على كل هامة قدم','76','3','0');
INSERT INTO [t754] VALUES (821,'يهابه أبسأ الرجال به ... وتتقي حد سيفه البهم','77','3','0');
INSERT INTO [t754] VALUES (822,'كفاني الذم أنني رجل ... أكرم مال ملكته الكرم','77','3','0');
INSERT INTO [t754] VALUES (823,'يجني الغني للئام لو عقلوا ... ما ليس يجني عليهم العدم','77','3','0');
INSERT INTO [t754] VALUES (824,'هم لأموالهم ولسن لهم ... والعار يبقى والجرح يلتئم','77','3','0');
INSERT INTO [t754] VALUES (825,'من طلب المجد فليكن كعل ... ي يهب الألف وهو يبتسم','77','3','0');
INSERT INTO [t754] VALUES (826,'ويطعن الخيل كل نافذة ... ليس لها من وحائها ألم','77','3','0');
INSERT INTO [t754] VALUES (827,'ويعرف الأمر قبل موقعه ... فما له بعد فعله ندم','77','3','0');
INSERT INTO [t754] VALUES (828,'والأمر والنهي والسلاهب وال ... بيض له والعبيد والحشم','77','3','0');
INSERT INTO [t754] VALUES (829,'والسطوات التي سمعت بها ... تكاد منها الجبال تنقصم','77','3','0');
INSERT INTO [t754] VALUES (830,'يرعيك سمعا فيه استماع إلى ال ... داعي وفيه عن الخنا صمم','77','3','0');
INSERT INTO [t754] VALUES (831,'يريك من خلقه غرائبه ... في مجده كيف تخلق النسم','77','3','0');
INSERT INTO [t754] VALUES (832,'ملت إلى من يكاد بينكما ... إن كنتما السائلين ينقسم','77','3','0');
INSERT INTO [t754] VALUES (833,'من بعد ما صيغ من مواهبه ... لمن أحب الشنوف والخدم','77','3','0');
INSERT INTO [t754] VALUES (834,'ما بذلت ما به يجود يد ... ولا تهدي لما يقول فم','77','3','0');
INSERT INTO [t754] VALUES (835,'بنو العفرني محطة الأسد ... الأسد ولكن رماحها الأجم','78','3','0');
INSERT INTO [t754] VALUES (836,'قوم بلوغ الغلام عندهم ... طعن نحور الكماة لا الحلم','78','3','0');
INSERT INTO [t754] VALUES (837,'كأنما يولد الندى معهم ... لا صغر عاذر ولا هرم','78','3','0');
INSERT INTO [t754] VALUES (838,'إذا تولوا عداوة كشفوا ... وإن تولو صنيعة كتموا','78','3','0');
INSERT INTO [t754] VALUES (839,'إذا تولوا عداوة كشفوا ... وإن تولوا صنيعة كتموا','78','3','0');
INSERT INTO [t754] VALUES (840,'تظن من فقدك اعتدادهم ... أنهم أنعموا وما علموا','78','3','0');
INSERT INTO [t754] VALUES (841,'إن برقوا فالحتوف حاضرة ... أو نطقوا فالصواب والحكم','78','3','0');
INSERT INTO [t754] VALUES (842,'أو حلفوا في الغموس واجتهدوا ... فقولهم خاب سائلي القسم','78','3','0');
INSERT INTO [t754] VALUES (843,'أو ركبوا الخيل غير مسرجة ... فإن أفخاذهم لها حزم','78','3','0');
INSERT INTO [t754] VALUES (844,'أو شهدوا الحرب لاقحا أخذوا ... من مهج الدارعين ما احتكموا','78','3','0');
INSERT INTO [t754] VALUES (845,'تشرق أعراضهم وأوجهم ... كأنها في نفوسهم شئم','78','3','0');
INSERT INTO [t754] VALUES (846,'لولاك لم أترك البحيرة وال ... غور دفىء وماءها شبم','78','3','0');
INSERT INTO [t754] VALUES (847,'والموج مثل الفحول مزبدة ... تهدر فيها وما بها قطم','78','3','0');
INSERT INTO [t754] VALUES (848,'والطير فوق الحباب تحسبها ... فرسان بلق تخونها اللجم','78','3','0');
INSERT INTO [t754] VALUES (849,'كأنها والرياح تضربها ... جيشا وغي هازم ومنهزم','78','3','0');
INSERT INTO [t754] VALUES (850,'كأنها في نهارها قمر ... حف به من جنانها ظلم','78','3','0');
INSERT INTO [t754] VALUES (851,'ناعمة الجسم لا عظام لها ... لها بنات وما لها رحم','79','3','0');
INSERT INTO [t754] VALUES (852,'يبقر عنهن بطنها أبدا ... وما تشكى وما يسيل دم','79','3','0');
INSERT INTO [t754] VALUES (853,'تغنت الطير في جوانبها ... وجادت الروض حولها الديم','79','3','0');
INSERT INTO [t754] VALUES (854,'فهي كماوية مطوقة ... جرد عنها غشاءها الأدم','79','3','0');
INSERT INTO [t754] VALUES (855,'يشينها جريها على بلد ... تشينه الأدعياء والقزم','79','3','0');
INSERT INTO [t754] VALUES (856,'أبا الحسين استمع فمدحكم ... في الفعل قبل الكلام منتظم','79','3','0');
INSERT INTO [t754] VALUES (857,'وقد توالى العهاد منه لكم ... وجادت المطرة التي تسم','79','3','0');
INSERT INTO [t754] VALUES (858,'أعيذكم من صروف دهركم ... فإنه في الكرام متهم','79','3','0');
INSERT INTO [t754] VALUES (859,'دمع جرى فقضي في الربع ما وجبا ... لهله وشفى أنى ولا كربا','79','3','0');
INSERT INTO [t754] VALUES (860,'عجنا فأذهب ما أبقى الفراق لنا ... من العقول وما رد الذي ذهبا','79','3','0');
INSERT INTO [t754] VALUES (861,'سقيته عبرات ظنها مطرا ... سوائلا من جفون ظنها سحبا','79','3','0');
INSERT INTO [t754] VALUES (862,'دار الملم لها طيف تهددني ... ليلا فما صدقت عيني ولا كذبا','79','3','0');
INSERT INTO [t754] VALUES (863,'ناءيته فدني أدنيته فنأي ... جمشته فنبا قبلته فأبي','79','3','0');
INSERT INTO [t754] VALUES (864,'هام الفؤاد بأعرابية سكنت ... بيتا من القلب لم تمدد له طنبا','79','3','0');
INSERT INTO [t754] VALUES (865,'مظلومة القد في تشبيهه غصنا ... مظلومة الريق في تشبيهه ضربا','79','3','0');
INSERT INTO [t754] VALUES (866,'بيضاء تطمع فيما تحت حلتها ... وعز ذلك مطلوبا إذا طلبا','79','3','0');
INSERT INTO [t754] VALUES (867,'كأنها الشمس يعيى كف قابضه ... شعاعها ويراه الطرف مقتربا','79','3','0');
INSERT INTO [t754] VALUES (868,'مرت بنا بين تربيها فقلت لها ... من أين جانس هذا الشادن العربا','80','3','0');
INSERT INTO [t754] VALUES (869,'فاستضحكت ثم قالت كالمغيث يرى ... ليث الشرى وهو من عجل إذا انتسبا','80','3','0');
INSERT INTO [t754] VALUES (870,'جاءت بأشجع من يسمى وأسمح من ... أعطى وأبلغ من أملي ومن كتبا','80','3','0');
INSERT INTO [t754] VALUES (871,'لو حل خاطره في مقعد لمشى ... أو جاهل لصحى أو أخرس خطبا','80','3','0');
INSERT INTO [t754] VALUES (872,'إذا بدا حجبت عينيك هيبته ... وليس يحجبه ستر إذا احتجبا','80','3','0');
INSERT INTO [t754] VALUES (873,'بياض وجه يريك الشمس حالكة ... ودر لفظ يريك الدر مخشلبا','80','3','0');
INSERT INTO [t754] VALUES (874,'وسيف عزم ترد السيف هبته ... رطب الغرار من التامور مختضبا','80','3','0');
INSERT INTO [t754] VALUES (875,'عمر العدو إذا لاقاه في رهج ... أقل من عمر ما يحوى إذا وهبا','80','3','0');
INSERT INTO [t754] VALUES (876,'توقه فإذا ما شئت تبلوه ... فكن معاديه أو كن له نشبا','80','3','0');
INSERT INTO [t754] VALUES (877,'تحلو مذاقته حتى إذا غضبا ... حالت فلو قطرت في الماء ما شربا','80','3','0');
INSERT INTO [t754] VALUES (878,'وتغبط الأرض منها حيث حال به ... وتحسد الخيل منها أيها ركبا','80','3','0');
INSERT INTO [t754] VALUES (879,'ولا يرد بفيه كف سائله ... عن نفسه ويرد الجحفل اللجبا','80','3','0');
INSERT INTO [t754] VALUES (880,'وكلما لقي الدينار صاحبه ... في ملكه افترقا من قبل يصطحبا','81','3','0');
INSERT INTO [t754] VALUES (881,'مال كأن غراب البين يرقبه ... فكلما قيل هذا مجتد نعبا','81','3','0');
INSERT INTO [t754] VALUES (882,'بحر عجائبه لم تبق في سمر ... ولا عجائب بحر بعدها عجبا','81','3','0');
INSERT INTO [t754] VALUES (883,'لايقنع ابن علي نيل منزلة ... يشكو محاولها التقصير والتعبا','81','3','0');
INSERT INTO [t754] VALUES (884,'هز اللواء بنو عجل به فغدا ... رأسا لهم وغدا كل لهم ذنبا','81','3','0');
INSERT INTO [t754] VALUES (885,'التاركين من الأشياء أهونها ... والراكبين من الأشياء من صعبا','81','3','0');
INSERT INTO [t754] VALUES (886,'مبرقعي خيلهم بالبيض متخذي ... هام الكماة على أرماحهم عذبا','81','3','0');
INSERT INTO [t754] VALUES (887,'إن المنية لو لاقتهم وقفت ... خرقاء تتهم الإقدام والهربا','81','3','0');
INSERT INTO [t754] VALUES (888,'مراتب صعدت والفكر يتبعها ... فجاز وهو على آثارها الشهبا','82','3','0');
INSERT INTO [t754] VALUES (889,'محامد نزفت شعري ليملأها ... فآل ما امتلأت منه ولا نصبا','82','3','0');
INSERT INTO [t754] VALUES (890,'مكارم لك فت العالمين بها ... من يستطيع لأمر فائت طلبا','82','3','0');
INSERT INTO [t754] VALUES (891,'لما أقمت بأنطاكية اختلفت ... إلى بالخبر الركبان في حلبا','82','3','0');
INSERT INTO [t754] VALUES (892,'فسرت نحوك لا ألوي على أحد ... أحث راحلتي الفقر والأدبا','82','3','0');
INSERT INTO [t754] VALUES (893,'أذاقني زمني بلوى شرقت بها ... لو ذاقها لبكى ما عاش وانتحبا','82','3','0');
INSERT INTO [t754] VALUES (894,'وإن عمرت جعلت الحرب والدة ... والسمهري أخا والمشرفي أبا','82','3','0');
INSERT INTO [t754] VALUES (895,'بكل أشعث يلقى الموت مبتسما ... حتى كأن له فيقتله أربا','82','3','0');
INSERT INTO [t754] VALUES (896,'قح يكاد صهيل الخيل يقذفه ... عن سرجه مرحا بالعز أو طربا','82','3','0');
INSERT INTO [t754] VALUES (897,'فالموت أعذر لي والصبر أجمل بي ... والبر أوسع والدنيا لمن غلبا','82','3','0');
INSERT INTO [t754] VALUES (898,'فؤاد ما تسليه المدام ... وعمر مثل ما تهب اللئام','82','3','0');
INSERT INTO [t754] VALUES (899,'ودهر ناسه ناس صغار ... ون كانت لهم جثث ضخام','82','3','0');
INSERT INTO [t754] VALUES (900,'ما أنا منهم بالعيش فيهم ... ولكن معدن الذهب الرغام،','82','3','0');
INSERT INTO [t754] VALUES (901,'أرانب غير أنهم ملوك ... مفتحة عيونهم نيام','82','3','0');
INSERT INTO [t754] VALUES (902,'بأجسام يحر القتل فيها ... وما أقرانها إلا الطعام','83','3','0');
INSERT INTO [t754] VALUES (903,'وخيل لا يخر لها طعين ... كأن قنا فوارسها ثمام','83','3','0');
INSERT INTO [t754] VALUES (904,'خليلك أنت لا من قلت خلى ... وإن كثر التجمل والكلام','83','3','0');
INSERT INTO [t754] VALUES (905,'ولو حيز الحفاظ بغير عقل ... تجنب عنق صيقله الحسام','83','3','0');
INSERT INTO [t754] VALUES (906,'وشبه الشيء منجذب إليه ... وأشبهما بدنيانا الطغام','83','3','0');
INSERT INTO [t754] VALUES (907,'ولو لوم يعل إلا ذو محل ... تعالى الجيش وانحظ القتام','83','3','0');
INSERT INTO [t754] VALUES (908,'ولو لم يرع إلا مسنحق ... لرتبته أسامهم المسام','83','3','0');
INSERT INTO [t754] VALUES (909,'ومن خبر الغواني فالغواني ... ضياء في بواطنه ظلام','83','3','0');
INSERT INTO [t754] VALUES (910,'ذا كان الشباب السكر والشي ... ب هما فالحيوة هي الحمام','83','3','0');
INSERT INTO [t754] VALUES (911,'وما كل بمعذور ببخل ... ولا كل على بخل يلام','83','3','0');
INSERT INTO [t754] VALUES (912,'ولم أر مثل جيراني ومثلي ... لمثلي عند مثلهم مقام','83','3','0');
INSERT INTO [t754] VALUES (913,'بأرض ما اشتهيت رأيت فيها ... فليس يفوتها إلا الكرام','83','3','0');
INSERT INTO [t754] VALUES (914,'فهلا كان نقص الأهل فيها ... وكان لأهلها منها التمام','83','3','0');
INSERT INTO [t754] VALUES (915,'بها الجبلان من فخر وصخر ... أنافا ذا المغيث وذا اللكام','83','3','0');
INSERT INTO [t754] VALUES (916,'وليست من مواطنه ولكن ... يمر بها كما مر الغمام','83','3','0');
INSERT INTO [t754] VALUES (917,'سقا الله ابن منجبة سقاني ... بدر ما لراضعه فطام','83','3','0');
INSERT INTO [t754] VALUES (918,'ومن إحدى فوائده العطايا ... ومن إحدى عطاياه الدوام','83','3','0');
INSERT INTO [t754] VALUES (919,'وقد خفى الزمان به علينا ... كسلك الدر يخفيه النظام','83','3','0');
INSERT INTO [t754] VALUES (920,'تلذ له المروة وهو تؤذى ... ومن يعشق يلذ له الغرام','84','3','0');
INSERT INTO [t754] VALUES (921,'تعلقها هوى قيس لليلى ... وواصلها وليس به سقام','84','3','0');
INSERT INTO [t754] VALUES (922,'يروع ركانة ويذوب ظرفا ... فما يدري أشيخ أم غلام','84','3','0');
INSERT INTO [t754] VALUES (923,'وتملكه المسائل في نداه ... فأما في الجدال فما يرام','84','3','0');
INSERT INTO [t754] VALUES (924,'وقبض نواله شرف وعز ... وقبض نوال بعض القوم ذام','84','3','0');
INSERT INTO [t754] VALUES (925,'أقامت في الرقاب له أياد ... هي الأصواق والناس الحمام','84','3','0');
INSERT INTO [t754] VALUES (926,'إذا عد الكرام فتلك عجل ... كما الأنواء حين تعد عام','84','3','0');
INSERT INTO [t754] VALUES (927,'يقي جبهاتهم ما في ذراهم ... إذا بشفارها حمى اللطام','84','3','0');
INSERT INTO [t754] VALUES (928,'ولو يممتهم في الحشر تجدو ... لأعطوك الذي صلوا وصاموا','84','3','0');
INSERT INTO [t754] VALUES (929,'فإن حلموا فإن الخيل فيهم ... خفاف والرماح بها عرام','84','3','0');
INSERT INTO [t754] VALUES (930,'وعندهم الجفان مكللات ... وشزر الطعن والضرب التوأم','84','3','0');
INSERT INTO [t754] VALUES (931,'نصرعهم بأعيننا حياء ... وتنبو عن وجوههم السهام','84','3','0');
INSERT INTO [t754] VALUES (932,'قبيل يحملون من المعالي ... كما حملت من الجسد العظام','84','3','0');
INSERT INTO [t754] VALUES (933,'قبيل أنت أنت وأنت منهم ... وجدك بشر الملك الهمام','84','3','0');
INSERT INTO [t754] VALUES (934,'لمن مال تمزقه العطايا ... ويشرك في رغايبه الأنام','85','3','0');
INSERT INTO [t754] VALUES (935,'ولا ندعوك صاحبه فترضى ... لأن بصحبة يجب الذمام','85','3','0');
INSERT INTO [t754] VALUES (936,'تحايده كأنك سامري ... تصافحه يد فيها جذام','85','3','0');
INSERT INTO [t754] VALUES (937,'إذا ما العالمون عروك قالوا ... أفدانا أيها الحبر الإمام','85','3','0');
INSERT INTO [t754] VALUES (938,'إذا ما المعلمون رأوك قالوا ... بهذا يعلم الجيش اللهام','85','3','0');
INSERT INTO [t754] VALUES (939,'لقد حسنت بك الأوقات حتى ... كأنك في فم الزمن ابتسام','85','3','0');
INSERT INTO [t754] VALUES (940,'وأعطيت الذي لم يعط خلق ... عليك صلوة ربك والسلام','85','3','0');
INSERT INTO [t754] VALUES (941,'لجنية أم غادة رفع السجف ... لوحشية لا ما لوحشية شنف','85','3','0');
INSERT INTO [t754] VALUES (942,'نفور عرقتها نفرة فتجاذبت ... سوالفها والحلى والخصر الردف','85','3','0');
INSERT INTO [t754] VALUES (943,'وخل منها مرطها فكأنما ... تثنى لنا خوط ولاحظنا خشف','85','3','0');
INSERT INTO [t754] VALUES (944,'زيادة شيب وهي نقص زيادتي ... وقوة عشق وهي من قوتي ضعف','85','3','0');
INSERT INTO [t754] VALUES (945,'هراقت دمي من بي من الوجد ما بها ... من الوجد بي والشوق لي ولها حلف','86','3','0');
INSERT INTO [t754] VALUES (946,'ومن كلما جردتها من ثيابها ... كساها ثيابا غيرها الشعر الوحف','86','3','0');
INSERT INTO [t754] VALUES (947,'وقابلني رمانتا غصن بانة ... يميل به بدر ويمسكه حقف','86','3','0');
INSERT INTO [t754] VALUES (948,'أكيدا لنا يا بين واصلت وصلنا ... فلا دارنا تدنو ولا عيشنا يصفو','86','3','0');
INSERT INTO [t754] VALUES (949,'أردد ويلي لو قضي الويل حاجة ... وأكثر لهفي لو شفي غلة لهف','86','3','0');
INSERT INTO [t754] VALUES (950,'ضني في الهوى كالسم في الشهد كامنا ... لذذت به جهلا وفي اللذة الحتف','86','3','0');
INSERT INTO [t754] VALUES (951,'فأفنى وما أفنته نفسي كأنما ... أبو الفرج القاضي له دونها كهف','86','3','0');
INSERT INTO [t754] VALUES (952,'قليل الكرى لو كانت البيض والقنا ... كآرائه ما أغنت البيض والزغف','86','3','0');
INSERT INTO [t754] VALUES (953,'يقول مقام الجيش تقطيب وجهه ... ويستغرق الألفاظ من لفظه حرف','86','3','0');
INSERT INTO [t754] VALUES (954,'وإن فقد الإعطاء حنت يمينه ... إليه حنين الإلف فارقه الإلف','86','3','0');
INSERT INTO [t754] VALUES (955,'أديب رست للعلم في أرض صدره ... جبال جبال الأرض في جنبها قف','86','3','0');
INSERT INTO [t754] VALUES (956,'جواد سميت في الخير والشر كفه ... سموا أود الدهر أن اسمه كف','86','3','0');
INSERT INTO [t754] VALUES (957,'وأضحى وبين الناس في كل سيد ... من الناس إلا في سيادته خلف','86','3','0');
INSERT INTO [t754] VALUES (958,'يفدونه حتى كأن دماءهم ... لجاري هواه في عروقهم تقفوا','86','3','0');
INSERT INTO [t754] VALUES (959,'وقوفين في وقفين شكر ونائل ... فنائله وقف وشكرهم وقف','86','3','0');
INSERT INTO [t754] VALUES (960,'ولما فقدنا مثله دام كشفنا ... عليه فدام الفقد وانكشف الكشف','87','3','0');
INSERT INTO [t754] VALUES (961,'وما حارت الأوهام في عظم شانه ... بأكثر مما حار في حسنه الطرف','87','3','0');
INSERT INTO [t754] VALUES (962,'ولا نال من حساده الغيظ والأذى ... بأعظم مما نال من وفره العرف','87','3','0');
INSERT INTO [t754] VALUES (963,'تفكره علم ومنطقة حكم ... وباطنه دين وظاهره ظرف','87','3','0');
INSERT INTO [t754] VALUES (964,'أمات رياح اللؤم وهي عواصف ... ومغنى العلا يودي ورسم الندا يعفو','87','3','0');
INSERT INTO [t754] VALUES (965,'فلم نر قبل ابن الحسين أصابعا ... إذا ما هطلن استحيت الديم الوطف','87','3','0');
INSERT INTO [t754] VALUES (966,'ولا ساعيا في قلة المجد مدركا ... بأفعاله ما ليس يدركه الوصف','87','3','0');
INSERT INTO [t754] VALUES (967,'ولم نر شيئا يحمل العبء حمله ... ويتسصغر الدنيا ويحمله طرف','87','3','0');
INSERT INTO [t754] VALUES (968,'ولا جلس البحر المحيط لقاصد ... ومن تحته فرش ومن فوقه سقف','87','3','0');
INSERT INTO [t754] VALUES (969,'فوا عجبا مني أحاول نعته ... وقد فنيت فيه القراطيس والصحف','87','3','0');
INSERT INTO [t754] VALUES (970,'ومن كثرة الأخبار عن مكرماته ... يمر له صنف ويأتي له صنف','87','3','0');
INSERT INTO [t754] VALUES (971,'قصدتك والراجون قصدي إليهم ... كثير ولكن ليس كالذنب الأنف','87','3','0');
INSERT INTO [t754] VALUES (972,'ولا الفضة البيضاء والتبر واحد ... نفوعان للمكدي وبينهما صرف','87','3','0');
INSERT INTO [t754] VALUES (973,'ولست بدون يرتجي الغيث دونه ... ولا منتهى الجود الذي خلفه خلف','87','3','0');
INSERT INTO [t754] VALUES (974,'ولا واحدا في ذا الورى من جماعة ... ولا البعض من كل ولكنك الضعف','88','3','0');
INSERT INTO [t754] VALUES (975,'ولا الضعف حتى يتبع الضعف ضعفه ... ولا ضعف ضعف الضعف بل مثله ألف','88','3','0');
INSERT INTO [t754] VALUES (976,'أقاضينا هذا الذي أنت أهله ... غلطت ولا الثلثان هذا ولا النصف','88','3','0');
INSERT INTO [t754] VALUES (977,'وذنبي تقصيري وما جئت مادحا ... بذنبي ولكن جئت أسأل أن تعفو','88','3','0');
INSERT INTO [t754] VALUES (978,'بابي الشموس الجانحات غواربا ... اللابسات من الحرير جلاببا','88','3','0');
INSERT INTO [t754] VALUES (979,'المنهبات قلوبنا وعقولنا ... وجناتهن الناهبات الناهبا','88','3','0');
INSERT INTO [t754] VALUES (980,'الناعمات القاتلات المحييا ... ت المبديات من الدلال غرائبا','88','3','0');
INSERT INTO [t754] VALUES (981,'حاولن تفديتي وخفن مراقبا ... فوضعن أيديهن فوق ترائبا','88','3','0');
INSERT INTO [t754] VALUES (982,'وبسمن عن برد خشيت أذيبه ... من حر أنفاسي فكنت الذائبا','88','3','0');
INSERT INTO [t754] VALUES (983,'يا حبذا المتحملون وحبذا ... واد لثمت به الغزالة كاعبا','88','3','0');
INSERT INTO [t754] VALUES (984,'كيف الرجاء من الخطوب تخلصا ... من بعد أن أنشبن في مخالبا','88','3','0');
INSERT INTO [t754] VALUES (985,'أوحدنني ووجدن حزنا واحدا ... متناهيا فجعلنه لي صاحبا','88','3','0');
INSERT INTO [t754] VALUES (986,'ونصبنني غرض الرماة تصيبني ... محن أحد من السيوف مضاربا','88','3','0');
INSERT INTO [t754] VALUES (987,'أظمتني الدنيا فلما جئتها ... مستسقيا مطرت على مصائبا','88','3','0');
INSERT INTO [t754] VALUES (988,'وحييت من خوص الركاب بأسود ... من دراش فغدوت أمشي راكبا','89','3','0');
INSERT INTO [t754] VALUES (989,'حالا متى علم ابن منصور بها ... جاء الزمان إلي منها تائبا','89','3','0');
INSERT INTO [t754] VALUES (990,'ملك سنان قناته وبنانه ... يتباريان دما وعرفا ساكبا','89','3','0');
INSERT INTO [t754] VALUES (991,'يستصغر الخطر الكبير لوفده ... ويظن دجلة ليس تكفي شاربا','89','3','0');
INSERT INTO [t754] VALUES (992,'كرما فلو حدثته عن نفسه ... بعظيم ما صنعت لظنك كاذبا','89','3','0');
INSERT INTO [t754] VALUES (993,'سل عن شجاعته وزره مسالما ... وحذار ثم حذار منه محاربا','89','3','0');
INSERT INTO [t754] VALUES (994,'فالموت تعرف بالصفات طباعه ... لم تلق خلقا ذات موتا آئبا','89','3','0');
INSERT INTO [t754] VALUES (995,'إن تلقه لا تلق غلا جحفلا ... أو قسطلا أو طاعنا أو ضاربا','89','3','0');
INSERT INTO [t754] VALUES (996,'أو هاربا أو طالبا أو راغبا ... أو راهبا أو هالكا أو نادبا','89','3','0');
INSERT INTO [t754] VALUES (997,'وإذا نظرت إلى الجبل رأيتها ... فوق السهول عواسلا وقواضبا','89','3','0');
INSERT INTO [t754] VALUES (998,'وإذا نظرت إلى السهول رأيتها ... تحت الجبال فوارسا وجنائبا','89','3','0');
INSERT INTO [t754] VALUES (999,'وعجاجة ترك الحديد سوادها ... زنجا تبسم أو قذالا شائبا','89','3','0');
INSERT INTO [t754] VALUES (1000,'فكأنما كسى النهار بها دجى ... ليل وأطلعت الرماح كواكبا','89','3','0');
INSERT INTO [t754] VALUES (1001,'قد عسكرت معها الرزايا عسكرا ... وتكتبت فيها الرجال كتائبا','89','3','0');
INSERT INTO [t754] VALUES (1002,'أسد فرائسها الأسود يقودها ... أسد تصير له الأسود ثعالبا','89','3','0');
INSERT INTO [t754] VALUES (1003,'في رتبة حجب الورى عن نيلها ... وعلا فسموه علي الحاجبا','89','3','0');
INSERT INTO [t754] VALUES (1004,'ودعوه من فرط السخاء مبذرا ... ودعوه من غصب النفوس الغاصبا','89','3','0');
INSERT INTO [t754] VALUES (1005,'هذا الذي أفنى النضار مواهبا ... وعداه قتلا والزمان تجاربا','89','3','0');
INSERT INTO [t754] VALUES (1006,'ومخيب العذال مما أملوا ... منه وليس يرد كفا خائبا','90','3','0');
INSERT INTO [t754] VALUES (1007,'هذا الذي أبصرت منه حاضرا ... مثل الذي أبصرت منه غائبا','90','3','0');
INSERT INTO [t754] VALUES (1008,'كالبدر من حيث التفت رأيته ... يهدي إلى عينيك نورا ثاقبا','90','3','0');
INSERT INTO [t754] VALUES (1009,'كالبحر يقذف للقريب جواهرا ... جودا ويبعث للبعيد سحائبا','90','3','0');
INSERT INTO [t754] VALUES (1010,'كالشمس في كبد السماء وضوءها ... يغشى البلاد مشارقا ومغاربا','90','3','0');
INSERT INTO [t754] VALUES (1011,'أمهجن الكرماء والمزري بهم ... وتروك كل كريم قوم عاتبا','90','3','0');
INSERT INTO [t754] VALUES (1012,'شادوا مناقبهم وشدت مناقبا ... وجدت مناقبهم بهن مثاليا','90','3','0');
INSERT INTO [t754] VALUES (1013,'لبيك غيظ الحاسدين الراتبا ... إنا لنخبر من يديك عجائبا','90','3','0');
INSERT INTO [t754] VALUES (1014,'تدبير ذي حنك يفكر في غد ... وهجوم غر لا يخاف عواقبا','90','3','0');
INSERT INTO [t754] VALUES (1015,'وعطاء مال لو عداه طالب ... أنفقته في أن تلاقي طالبا','90','3','0');
INSERT INTO [t754] VALUES (1016,'خذ من ثناي عليك ما أسطيعه ... لا تلزمني في الثناء الواجبا','90','3','0');
INSERT INTO [t754] VALUES (1017,'فلقد دهشت لما فعلت ودونه ... ما يدهش الملك الحفيظ الكاتبا','90','3','0');
INSERT INTO [t754] VALUES (1018,'نرى عظما بالبين والصد أعظم ... ونتهم الواشين والدمع منهم','90','3','0');
INSERT INTO [t754] VALUES (1019,'ومن لبه مع غيره كيف حاله ... ومن سره في جفنه كيف يكتم','90','3','0');
INSERT INTO [t754] VALUES (1020,'ولما التقينا والنوى ورقيبنا ... غفولان عنا ظلت أبكى وتبسم','90','3','0');
INSERT INTO [t754] VALUES (1021,'فلم أر بدرا ضاحكا قبل وجهها ... ولم تر قبلي ميتا يتكلم','90','3','0');
INSERT INTO [t754] VALUES (1022,'ظلوم كمتنيها لصب كخصرها ... ضعيف القوى من فعلها يتظلم','90','3','0');
INSERT INTO [t754] VALUES (1023,'بفرع يعيد الليل والصبح نير ... ووجه يعيد الصبح والليل مظلم','91','3','0');
INSERT INTO [t754] VALUES (1024,'فلو كان قلبي دارها كان خاليا ... ولكن جيش الشوق فيه عرمرم','91','3','0');
INSERT INTO [t754] VALUES (1025,'أثاف بها ما بالفؤاد من الصلى ... ورسم كجسمي ناحل متهدم','91','3','0');
INSERT INTO [t754] VALUES (1026,'بللت بها ردني والغيم مسعدي ... وعبرته صرف وهي عبرتي دم','91','3','0');
INSERT INTO [t754] VALUES (1027,'ولو لم يكن ما انهل في الخد من دمي ... لما كان محمرا يسيل فأسقم','91','3','0');
INSERT INTO [t754] VALUES (1028,'بنفسي الخيال الزائري بعد هجعة ... وقولته لي بعدنا الغمض تطعم','91','3','0');
INSERT INTO [t754] VALUES (1029,'سلام فلولا البخل والخوف عنده ... لقلنا أبو حفص علينا المسلم','91','3','0');
INSERT INTO [t754] VALUES (1030,'محب الندى الصابي إلى بذل ماله ... صبوا كما يصبو المحب المتيم','91','3','0');
INSERT INTO [t754] VALUES (1031,'وأقسم لولا أن في كل شعرة ... له ضيغما قلنا له أنت ضيغم','91','3','0');
INSERT INTO [t754] VALUES (1032,'أننقصه من حظه وهو زائد ... ونبخسه والبخس شيء محرم','91','3','0');
INSERT INTO [t754] VALUES (1033,'يجل عن التشبيه لا الكف لجة ... ولا هو ضرغام ولا الرأي مخذم','91','3','0');
INSERT INTO [t754] VALUES (1034,'ولا جرحه يؤسى ولا غوره يرى ... ولا حده ينبو ولا يتثلم','91','3','0');
INSERT INTO [t754] VALUES (1035,'ولا يبرم الأمر الذي هو حالل ... ولا يحلل الأمر الذي هو مبرم','91','3','0');
INSERT INTO [t754] VALUES (1036,'ولا يرمح الأذيال من جبرية ... ولا يخدم الدنيا وإياه تخدم','91','3','0');
INSERT INTO [t754] VALUES (1037,'ولا يشتهى يبقى وتفنى هباته ... ولا يسلم الأعداء منه ويسلم','91','3','0');
INSERT INTO [t754] VALUES (1038,'ألذ من الصهباء بالماء ذكره ... وأحسن من يسر تلقاه معدم','91','3','0');
INSERT INTO [t754] VALUES (1039,'وأغرب من عنقاء في الطير شكله ... وأعوز من مسترفد منه يحرم','92','3','0');
INSERT INTO [t754] VALUES (1040,'وأكثر من بعد الأيادي أياديا ... من القطر بعد القطر والوبل مثجم','92','3','0');
INSERT INTO [t754] VALUES (1041,'سنى العطايا لو رأى نوم عينه ... من اللوم آلى أنه لا يهوم','92','3','0');
INSERT INTO [t754] VALUES (1042,'ولو قال هاتوا درهما لم أجد به ... على أحد أعيى على الناس درهم','92','3','0');
INSERT INTO [t754] VALUES (1043,'ولو ضر مرأ قبله ما يسره ... لأثر فيه بأسه والتكرم','92','3','0');
INSERT INTO [t754] VALUES (1044,'يروي يكأ لفرصاد في كل غارة ... يتامى م الأغماد بيضا ويؤتم','92','3','0');
INSERT INTO [t754] VALUES (1045,'إلى اليوم ما حط الفداء سروجه ... مذ الغزو سار مسرج الخيل ملجم','92','3','0');
INSERT INTO [t754] VALUES (1046,'يشق بلاد الروم والنقع أبلق ... بأسيافه والجو بالنقع أدهم','92','3','0');
INSERT INTO [t754] VALUES (1047,'إلى الملك الطاغي فكم من كتيبة ... تساير منه حتفها وهي تعلم','92','3','0');
INSERT INTO [t754] VALUES (1048,'ومن عاتق نصراتة برزت له ... أسيلة خد عن قليل ستلطم','92','3','0');
INSERT INTO [t754] VALUES (1049,'صفوفا لليث في ليوث حصونها ... متون المذاكي والتوشيج المقوم','92','3','0');
INSERT INTO [t754] VALUES (1050,'تغيب المنايا عنهم وهو غائب ... وتقدم في ساحاتهم حين يقدم','92','3','0');
INSERT INTO [t754] VALUES (1051,'أجدك ما ينفك علن تفكه ... عم بن سليمان ومال تقسم','92','3','0');
INSERT INTO [t754] VALUES (1052,'مكافيك من أوليت دين رسوله ... يدا لا تؤدي شكرها اليد والفم','92','3','0');
INSERT INTO [t754] VALUES (1053,'محلك مقصود وشانيك مفحم ... ومثلك مفقود ونيلك خضرم','92','3','0');
INSERT INTO [t754] VALUES (1054,'وزارك بي دون الملوك تحرج ... إذا عن بحر لم يجز لي التيمم','92','3','0');
INSERT INTO [t754] VALUES (1055,'فعش لو فدى المملوك ربا بنفسه ... من الموت لم تفقد وفي الأرض مسلم','92','3','0');
INSERT INTO [t754] VALUES (1056,'أركائب الأحباب إن الأدمعا ... تطس الخدرد كما تطسن اليرمعا','92','3','0');
INSERT INTO [t754] VALUES (1057,'فاعرفن من حملت عليكن النوى ... وأمشين هونا في الأزمة خضعا','93','3','0');
INSERT INTO [t754] VALUES (1058,'قد كان يمنعني الحياء من البكا ... فاليوم يمنعه البكا أن يمنعا','93','3','0');
INSERT INTO [t754] VALUES (1059,'حتى كأن لكل عظم رنة ... في جلده ولكل عرق مدمعا','93','3','0');
INSERT INTO [t754] VALUES (1060,'وكفى بمن فضح الجداية فاضحا ... لمحبه وبمصرعي ذا مصرعا','93','3','0');
INSERT INTO [t754] VALUES (1061,'سفرت وبرقعها الفراق بصفرة ... سترت محاجرها ولم تك برقعا','93','3','0');
INSERT INTO [t754] VALUES (1062,'كشفت ثلاث ذوائب من شعرها ... في ليلة فأرت ليالي أربعا','93','3','0');
INSERT INTO [t754] VALUES (1063,'واستقبلت قمر السماء بوجهها ... فأرتني القمرين في وقت معا','93','3','0');
INSERT INTO [t754] VALUES (1064,'ردي الوصال سقى طلولك عارض ... لو كان وصلك مثله ما أقشعا','93','3','0');
INSERT INTO [t754] VALUES (1065,'زجل يريك الجو نارا والملا ... كالبحر والتلعات روضا ممرعا','93','3','0');
INSERT INTO [t754] VALUES (1066,'كبنان عبد الواحد الغدق الذي ... أروى وآمن من يشاء وأجزعا','93','3','0');
INSERT INTO [t754] VALUES (1067,'ألف المروة مذ نشا فكأنه ... سقى اللبان بها صبيا مرضعا','93','3','0');
INSERT INTO [t754] VALUES (1068,'نظمت مواهبه عليه تمائما ... فاعتادها فإذا سقطن تفزعا','93','3','0');
INSERT INTO [t754] VALUES (1069,'ترك الصنائع كالقواطع بارقا ... ت والمعالي كالعوالي شرعا','93','3','0');
INSERT INTO [t754] VALUES (1070,'متبسما لعفاته عن واضح ... تغشى لوامعه البروق اللمعا','93','3','0');
INSERT INTO [t754] VALUES (1071,'متكشفا لعداته عن سطوة ... لو حك منكبها السماء لزعزعا','93','3','0');
INSERT INTO [t754] VALUES (1072,'الحازم اليقظ الأعز العالم ال ... فطن الألد الأريجى الأروعا','93','3','0');
INSERT INTO [t754] VALUES (1073,'الكاتب اللبق الخطيب الواهب ال ... ندس اللبيب الهبرزي المصقعا','93','3','0');
INSERT INTO [t754] VALUES (1074,'نفس لها خلق الزمان لأنه ... مفنى النفوس مفرق ما جمعا','94','3','0');
INSERT INTO [t754] VALUES (1075,'ويد لها كرم الغمام لأنه ... يسقى النفوس العمارة والمكان البلقعا','94','3','0');
INSERT INTO [t754] VALUES (1076,'أبدا يصدع شعب وفر وافر ... ويلم شعب مكارم متصدعا','94','3','0');
INSERT INTO [t754] VALUES (1077,'يهتز للجدوى اهتزاز مهند ... يوم الرجاء هززته يوم الوعا','94','3','0');
INSERT INTO [t754] VALUES (1078,'يا مغنيا أمل الفقير لقاؤه ... ودعاؤه بعد الصلاة إذا دعا','94','3','0');
INSERT INTO [t754] VALUES (1079,'أقصر فلست بمقصر جزت المدى ... وبلغت حيث النجم تحتك فاربعا','94','3','0');
INSERT INTO [t754] VALUES (1080,'وحللت من شرف الفعال مواضعا ... لم يحلل الثقلان منها موضعا','94','3','0');
INSERT INTO [t754] VALUES (1081,'وحويت فضلهما وما طمع امرء ... فيه ولا طمع امرء أن يطمعا','94','3','0');
INSERT INTO [t754] VALUES (1082,'نفذ القضاء بما أردت كأنه ... لك كلما أزمعت أمرا أزمعا','94','3','0');
INSERT INTO [t754] VALUES (1083,'وأطاعك الدهر العصي كأنه ... عبد إذا ناديت لبى مرعا','94','3','0');
INSERT INTO [t754] VALUES (1084,'أكلت مفاخرك المفاخر وانثنت ... عن شاوهن مطي وصفي ظلعا','94','3','0');
INSERT INTO [t754] VALUES (1085,'وجرين جرى الشمس في أفلاكها ... فقطعن مغربها وجزن المطلعا','94','3','0');
INSERT INTO [t754] VALUES (1086,'لو نيطت الدنيا بأخرى مثلها ... لعممتها وخشيت أن لا تقنعا','94','3','0');
INSERT INTO [t754] VALUES (1087,'فمتى يكذب مدع لك فوق ذا ... والله يشهد أن حقا ما أدعى','94','3','0');
INSERT INTO [t754] VALUES (1088,'ومتى يؤدي شرح حالك ناطق ... حفظ القليل النزر مما ضيعا','94','3','0');
INSERT INTO [t754] VALUES (1089,'إن كان لا يدعى الفتى إلا كذا ... رجلا فسم الناس طرا إصبعا','94','3','0');
INSERT INTO [t754] VALUES (1090,'إن كان لا يسعى لجود ماجد ... إلا كذا فالغيث أبخل من سعى','94','3','0');
INSERT INTO [t754] VALUES (1091,'قد خلف العباس غرتك ابنه ... مرأي لنا وإلى القيامة مسمعا','94','3','0');
INSERT INTO [t754] VALUES (1092,'أجارك يا اسد الفراديس مكرم ... فتسكن نفسي أم مهان فمسلم','94','3','0');
INSERT INTO [t754] VALUES (1093,'ورائي وقدامي عداة كثيرة ... أحاذر من لص ومنك ومنهم','95','3','0');
INSERT INTO [t754] VALUES (1094,'فهل لك في حلفي على ما أريده ... فإني بأسباب المعيشة أعلم','95','3','0');
INSERT INTO [t754] VALUES (1095,'إذا لأتاك الرزق من كل وجهة ... وأثريت مما تغنمين وأغنم','95','3','0');
INSERT INTO [t754] VALUES (1096,'صلة الهجر لي وهجر الوصال ... نكساني في السقم نكس الهلال','95','3','0');
INSERT INTO [t754] VALUES (1097,'فغدا الجسم ناقصا والذي ين ... قص منه يزيد في بلبالي','95','3','0');
INSERT INTO [t754] VALUES (1098,'قف على الدمنتين بالدو من ر ... يا كخال في وجنة جنب خال','95','3','0');
INSERT INTO [t754] VALUES (1099,'بطلول كأنهن نجوم ... في عراص كأنهن ليالي','95','3','0');
INSERT INTO [t754] VALUES (1100,'ونؤي كأنهن عليه ... ن خدام خرس بسوق خدال','95','3','0');
INSERT INTO [t754] VALUES (1101,'لا تلمني فإنني أعشق العش ... اق فيها يا أعذل العذال','95','3','0');
INSERT INTO [t754] VALUES (1102,'ما تريد النوى من الحية الذ ... واق حر الفلا وبرد الظلال','95','3','0');
INSERT INTO [t754] VALUES (1103,'فهو امضى في الروع من ملك المو ... ت وأسرى في ظلمة من خيال','95','3','0');
INSERT INTO [t754] VALUES (1104,'ولحتف في العز يدنو محب ... ولعمر يطول في الذل قالي','95','3','0');
INSERT INTO [t754] VALUES (1105,'نحن ركب ملجن في زي ناس ... فوق طير لها شخوص الجمال','95','3','0');
INSERT INTO [t754] VALUES (1106,'من بنات الجديل تمشي بنا في ال ... بيد مشي الأيام في الاجل','95','3','0');
INSERT INTO [t754] VALUES (1107,'كل هوجاء للدياميم فيها ... أثر النار في سليط الذبال','95','3','0');
INSERT INTO [t754] VALUES (1108,'عامدات للبر والبحر والضر ... غامة ابن المبارك المفضال','95','3','0');
INSERT INTO [t754] VALUES (1109,'من يزره يزر سليمان في المل ... ك جلالا ويوسفا في الجمال','95','3','0');
INSERT INTO [t754] VALUES (1110,'وربيعا يضاحك الغيث فيه ... زهر الشكر من رياض المعالي','95','3','0');
INSERT INTO [t754] VALUES (1111,'نفحتنا منه الصبا بنسيم ... رد روحا في ميت الامال','96','3','0');
INSERT INTO [t754] VALUES (1112,'هم عبد الرحمن نفع الموالي ... وبوار الأعداء والأموال','96','3','0');
INSERT INTO [t754] VALUES (1113,'أكبر العيب عنده البخل والطع ... ن عليه التشبيه بالرئبال','96','3','0');
INSERT INTO [t754] VALUES (1114,'والجراحات عنده نغمات ... سبقت قبل سيبه بسؤال','96','3','0');
INSERT INTO [t754] VALUES (1115,'ذا السراج المنير هذا النقي ال ... جيب هذا بقية الأبدال','96','3','0');
INSERT INTO [t754] VALUES (1116,'فخذا ماء رجله وانضحا في ال ... مدن تأمن بوائق الزلزال','96','3','0');
INSERT INTO [t754] VALUES (1117,'وأمسحا ثوبه البقير على دا ... ئكما تشفيا من الأعلال','96','3','0');
INSERT INTO [t754] VALUES (1118,'مالئا من نواله الشرق والغر ... ب ومن خوفه قلوب الرجال','96','3','0');
INSERT INTO [t754] VALUES (1119,'قابضا كفه اليمين على الدن ... يا ولو شاء حازها بالشمال','96','3','0');
INSERT INTO [t754] VALUES (1120,'نفسه جيشه وتدبيره النص ... ر وألحاظه الظبي والعوالي','96','3','0');
INSERT INTO [t754] VALUES (1121,'وله في جماجم المال ضرب ... وقعه في جماجم الأبطال','96','3','0');
INSERT INTO [t754] VALUES (1122,'فهم لاتقائه الدهر في يو ... م نزال وليس يوم نزال','96','3','0');
INSERT INTO [t754] VALUES (1123,'رجل طينه من العنبر الور ... د وطين الرجال من صلصال','96','3','0');
INSERT INTO [t754] VALUES (1124,'فبقيات طينه لاقت الما ... ء فصارت عذوبة في الزلال','96','3','0');
INSERT INTO [t754] VALUES (1125,'وبقايا وقاره عافت النا ... س فصارت ركانة في الجبال','96','3','0');
INSERT INTO [t754] VALUES (1126,'لست ممن يغره حبك السل ... م وأن لا ترى شهود القتال','96','3','0');
INSERT INTO [t754] VALUES (1127,'ذاك شيء كفاكه عيش شاني ... ك ذليلا وقلة الأشكال','96','3','0');
INSERT INTO [t754] VALUES (1128,'واغتفار لو غير السخط منه ... جعلت هامهم نعال النعال','96','3','0');
INSERT INTO [t754] VALUES (1129,'لجياد يدخلن في الحرب أعرا ... ء ويخرجن من دم في جلال','97','3','0');
INSERT INTO [t754] VALUES (1130,'واستعار الحديد لونا وألقى ... لونه في ذوائب الأطفال','97','3','0');
INSERT INTO [t754] VALUES (1131,'أنت طورا أمر من ناقع الس ... م وطورا أحلى من السلسال','97','3','0');
INSERT INTO [t754] VALUES (1132,'إنما الناس حيث أنت وما النا ... س بناس في موضع منك خالي','97','3','0');
INSERT INTO [t754] VALUES (1133,'أمن ازديارك في الدجى الرقباء ... إذ حيث أنت من الظلام ضياء','97','3','0');
INSERT INTO [t754] VALUES (1134,'قلق المليحة وهي مسك هتكها ... ومسيرها في الليل وهي ذكاء','97','3','0');
INSERT INTO [t754] VALUES (1135,'أسفي على أسفي الذي دلهتني ... عن علمه فبه على خفاء','98','3','0');
INSERT INTO [t754] VALUES (1136,'وشكيتي فقد السقام لأنه ... قد كان لما كان لي أعضاء','98','3','0');
INSERT INTO [t754] VALUES (1137,'مثلت عينك في حشاي جراحة ... فتشابها كلتاهما نجلاء','98','3','0');
INSERT INTO [t754] VALUES (1138,'نفذت علي السابري وربما ... تندق فيه الصعدة السمراء','98','3','0');
INSERT INTO [t754] VALUES (1139,'أنا صخرة الوادي إذا ما زوحمت ... وإذا نطقت فإنني الجوازء','98','3','0');
INSERT INTO [t754] VALUES (1140,'وإذا خفيت على الغبي فعاذر ... أن لا تراني مقلة عمياء','98','3','0');
INSERT INTO [t754] VALUES (1141,'شيم الليالي أن تشكك ناقتي ... صدري بها أفضى أم البيداء','98','3','0');
INSERT INTO [t754] VALUES (1142,'فتبيت تسئد مسئدا في نيها ... إسآدها في المهمة الإنضاء','99','3','0');
INSERT INTO [t754] VALUES (1143,'أنساعها ممغوطة وخفافها ... منكوحة وطريقها عذراء','99','3','0');
INSERT INTO [t754] VALUES (1144,'يتلون الخريت من خوف التوى ... فيهاكما يتلون الحرباء','99','3','0');
INSERT INTO [t754] VALUES (1145,'بيني وبين أبي علي مثله ... شم الجبال ومثلهن رجاء','99','3','0');
INSERT INTO [t754] VALUES (1146,'وعقاب لبنان وكيف بقطعها ... وهو الشتاء وصيفهن شتاء','99','3','0');
INSERT INTO [t754] VALUES (1147,'لبس الثلوج بها على مسالكي ... فكأنها ببياضها سوداء','99','3','0');
INSERT INTO [t754] VALUES (1148,'وكذا الكريم إذا أقام ببلدة ... سال النضار بها وقام الماء','99','3','0');
INSERT INTO [t754] VALUES (1149,'من يهتدي في الفعل ما لا يهتدي ... في القول حتى يفعل الشعراء','100','3','0');
INSERT INTO [t754] VALUES (1150,'جمد القطار ولو رأته كما ترى ... بهتت فلم تتبجس الأنواء','100','3','0');
INSERT INTO [t754] VALUES (1151,'في خطه من كل قلب شهوة ... حتى كأن مداده الأهواء','100','3','0');
INSERT INTO [t754] VALUES (1152,'من يهتدي في الفعل ما لا يهتدي ... في القول حتى يفعل الشعرا','100','3','0');
INSERT INTO [t754] VALUES (1153,'في كل يوم للقوافي جولة ... في قلبه ولأذنه إصغاء','100','3','0');
INSERT INTO [t754] VALUES (1154,'وإغارة فيما احتواه كأنما ... في كل بيت فيلق شهباء','100','3','0');
INSERT INTO [t754] VALUES (1155,'من يظلم اللوماء في تكليفهم ... أن يصبحوا وهم له أكفاء','100','3','0');
INSERT INTO [t754] VALUES (1156,'ونذيمهم وبهم عرفنا فضله ... وبضدها تتبين الأشياء','100','3','0');
INSERT INTO [t754] VALUES (1157,'من نفعه في أن يهاج وضره ... في تركه لو تفطن الأعداء','101','3','0');
INSERT INTO [t754] VALUES (1158,'فالسلم يكسر من جناحي ماله ... بنواله ما تجبر الهيجاء','101','3','0');
INSERT INTO [t754] VALUES (1159,'يعطى فتعطى من لهي يده اللهى ... وترى برؤية رأيه الآراء','101','3','0');
INSERT INTO [t754] VALUES (1160,'متفرق الطعمين مجتمع القوى ... فكأنه السراء والضراء','101','3','0');
INSERT INTO [t754] VALUES (1161,'وكأنه ما لا تشاء عداته ... متمثلا لوفوده ما شاءوا','101','3','0');
INSERT INTO [t754] VALUES (1162,'يا أيها المجدي عليه روحه ... إذ ليس يأتيه لها استجداء','101','3','0');
INSERT INTO [t754] VALUES (1163,'إحمد عفاتك لا فجعت بفقدهم ... فلترك ما لم يأخذوا إعطاء','101','3','0');
INSERT INTO [t754] VALUES (1164,'لا تكثر الأموات كثرة قلة ... إلا إذا شقيت بك الأحياء','101','3','0');
INSERT INTO [t754] VALUES (1165,'والقلب لا ينشق عما تحته ... حتى تحل به لك الشحناء','102','3','0');
INSERT INTO [t754] VALUES (1166,'لم تسم يا هارون إلا بعد ما اق ... ترعت ونازعت اسمك الأحاء','102','3','0');
INSERT INTO [t754] VALUES (1167,'فغدوت واسمك فيك غير مشارك ... والناس فيما في يديك سواء','102','3','0');
INSERT INTO [t754] VALUES (1168,'لعممت حتى المدن منك ملاء ... ولفت حتى ذا الثناء لفاء','102','3','0');
INSERT INTO [t754] VALUES (1169,'ولجدت حتى كدت تبخل حائلا ... للمنتهى ومن السرور بكاء','102','3','0');
INSERT INTO [t754] VALUES (1170,'أبدات شيئا منك يعرف بدؤه ... وأعدت حتى أنكر الإبداء','102','3','0');
INSERT INTO [t754] VALUES (1171,'فالفخر عن تقصيره بكل ناكب ... والمجد من أن تستزاد براء','102','3','0');
INSERT INTO [t754] VALUES (1172,'فإذا سئلت فلا لأنك محوج ... وإذا كتمت وشت بك الآلاء','102','3','0');
INSERT INTO [t754] VALUES (1173,'وإذا مدحت فل لتكسب رفعة ... للشاكرين على الإله ثناء','102','3','0');
INSERT INTO [t754] VALUES (1174,'وإذا مطرت فلا لأنك مجدب ... يسقى الخصيب وتمطر الدأماء','102','3','0');
INSERT INTO [t754] VALUES (1175,'لم تحك نائلك السحاب وإنما ... حمت به فصبيبها الرخضاء','102','3','0');
INSERT INTO [t754] VALUES (1176,'لم تلق هذا الوجه شمس نهارنا ... إلا بوجه ليس فيه حياء','102','3','0');
INSERT INTO [t754] VALUES (1177,'فبأيما قدم سعيت إلى العلي ... أدم الهلال لأخمصيك حذاء','103','3','0');
INSERT INTO [t754] VALUES (1178,'ولك الزمان من الزمان وقاية ... ولك الحمام من الحمام فداء','103','3','0');
INSERT INTO [t754] VALUES (1179,'لو لم تكن من ذا الورى الذ منك هو ... عقمت بمولد نسلها حواء','103','3','0');
INSERT INTO [t754] VALUES (1180,'ومنزل ليس لنا بمنزل ... ولا لغير الغاديات الهطل','103','3','0');
INSERT INTO [t754] VALUES (1181,'ندى الخزاميى ذفر القرنفل ... محلل ملوحش لم يحلل','103','3','0');
INSERT INTO [t754] VALUES (1182,'عن لنا فيه مراعي مغزل ... محين النفس بعيد الموئل','103','3','0');
INSERT INTO [t754] VALUES (1183,'أغناه حسن الجيد عن لبس الحلى ... وعادة العري ن التفضل','103','3','0');
INSERT INTO [t754] VALUES (1184,'كأنه مضمخ بصندل ... معترضا بمثل قرن الأيل','103','3','0');
INSERT INTO [t754] VALUES (1185,'يحلو بين الكلب والتأمل ... فحل كلابي وثاق الأحبل','103','3','0');
INSERT INTO [t754] VALUES (1186,'عن أشدق مسوجر مسلسل ... أقب ساط شرس شمردل','103','3','0');
INSERT INTO [t754] VALUES (1187,'منها إذا يثغ له لا يغزل ... موجد الفقرة رخو المفصل','103','3','0');
INSERT INTO [t754] VALUES (1188,'له إذا أدبر لحظ المقبل ... كأنما ينظر من سجنجل','103','3','0');
INSERT INTO [t754] VALUES (1189,'يعدو إذا أحزن عدو المسهل ... إذا تلى جاء المدى وقد تلي','103','3','0');
INSERT INTO [t754] VALUES (1190,'يقعى جلوس البدوي المصطلي ... بأربع مجدولة لم تجدل','103','3','0');
INSERT INTO [t754] VALUES (1191,'فتل الأيادي ربذات الأرجل ... آثارها أمثالها في الجندل','103','3','0');
INSERT INTO [t754] VALUES (1192,'يكاد في الوثب من التفتل ... يجمع بين متنة والكلكل','104','3','0');
INSERT INTO [t754] VALUES (1193,'وبين أعلاه وبين الأسفل ... شبيه وسمى الحضار بالولي','104','3','0');
INSERT INTO [t754] VALUES (1194,'كأنه مضبر من جرول ... موثق على رماح ذبل','104','3','0');
INSERT INTO [t754] VALUES (1195,'ذي ذنب أجرد غير أعزل ... يخط في الأرض حساب الجمل','104','3','0');
INSERT INTO [t754] VALUES (1196,'كأنه من جسمه بمعزل ... لو كان يبلي السوط تحريك بلى','104','3','0');
INSERT INTO [t754] VALUES (1197,'نيل المنى وحكم نفس المرسل ... وعقلة الظبي وحتف التتفل','104','3','0');
INSERT INTO [t754] VALUES (1198,'فانبريا فذين تحت القسطل ... قل ضمن الآخر قتل الأول','104','3','0');
INSERT INTO [t754] VALUES (1199,'في هبوة كلاهما لم يذهل ... لا يتلي في ترك أن لا يأتلي','104','3','0');
INSERT INTO [t754] VALUES (1200,'مقتحما على المكان الأهول ... يخال طول البحر عرض الجدول','104','3','0');
INSERT INTO [t754] VALUES (1201,'حتى إذا قيل له نلت أفعل ... إفتر عن مدروبة كالأنصل','104','3','0');
INSERT INTO [t754] VALUES (1202,'لا تعرف العهد بصقل الصقيل ... مركبات في العذاب المنزل','104','3','0');
INSERT INTO [t754] VALUES (1203,'كأنها من سرعة في الشمأل ... كأنها من ثقل في يذبل','105','3','0');
INSERT INTO [t754] VALUES (1204,'كأنها من سعة في هوجل ... كأنه من عمله بالمقتل','105','3','0');
INSERT INTO [t754] VALUES (1205,'فحال ما للقفز للتجدل ... وصار ما في جلده في المرجل','105','3','0');
INSERT INTO [t754] VALUES (1206,'فلم يضرنا معه فقد الأجدل ... إذا بقيت سالما أبا علي','105','3','0');
INSERT INTO [t754] VALUES (1207,'أحلما نرى أم زمانا جديدا ... أما الخلق في شخص حي أعيدا','105','3','0');
INSERT INTO [t754] VALUES (1208,'تجلى لنا فأضأنا به ... كأنا نجوم لقينا سعودا','105','3','0');
INSERT INTO [t754] VALUES (1209,'رأينا ببدر وآبائه ... لبدر ولودا وبدرا وليدا','105','3','0');
INSERT INTO [t754] VALUES (1210,'طلبنا رضاه بترك الذي ... رضينا له فتركنا السجودا','105','3','0');
INSERT INTO [t754] VALUES (1211,'أمير أمير عليه الندى ... جواد بخيل بأن لا يجودا','105','3','0');
INSERT INTO [t754] VALUES (1212,'يحدث عن فضله مكرها ... كأن له منه قلبا حسودا','105','3','0');
INSERT INTO [t754] VALUES (1213,'ويقدم إلا على أن يفر ... ويقدر إلا على أن يزيدا','106','3','0');
INSERT INTO [t754] VALUES (1214,'كأن نوالك بعض القضاء ... فما تعط منه نجده جدودا','106','3','0');
INSERT INTO [t754] VALUES (1215,'وربتما حملة في الوغى ... رددت بها الذبل السمر سوادا','106','3','0');
INSERT INTO [t754] VALUES (1216,'وهول كشفت ونصل قصفت ... ورمح تركت مبادا مبيدا','106','3','0');
INSERT INTO [t754] VALUES (1217,'ومال وهبت بلا موعد ... وقرن سبقت إليه الوعيدا','106','3','0');
INSERT INTO [t754] VALUES (1218,'بهجر سيوفك أغمادها ... تمنى الطلا أن تكون الغمودا','106','3','0');
INSERT INTO [t754] VALUES (1219,'إلى الهام تصدر عن مثله ... ترى صدرا عن ورود ورودا','106','3','0');
INSERT INTO [t754] VALUES (1220,'قتلت نفوس العدى بالحدي ... د حتى قتلت بهن الحديدا','106','3','0');
INSERT INTO [t754] VALUES (1221,'فأنفدت من عيشهن البقا ... وأبقيت مما ملكت النفودا','106','3','0');
INSERT INTO [t754] VALUES (1222,'كأنك بالفقر تبغي الغنى ... وبالموت في الحرب تبقي الخلودا','107','3','0');
INSERT INTO [t754] VALUES (1223,'خلائق تهدى إلى ربها ... وآية مجد أراها العبيدا','107','3','0');
INSERT INTO [t754] VALUES (1224,'مهذبة حلوة مرة ... حقرنا البحار بها والأسودا','107','3','0');
INSERT INTO [t754] VALUES (1225,'بعيد على قربها وصفها ... تغول الظنون وتنضى القصيدا','107','3','0');
INSERT INTO [t754] VALUES (1226,'فأنت وحيد بنى آدم ... ولست لفقد نظير وحيدا','107','3','0');
INSERT INTO [t754] VALUES (1227,'أبعد نأي المليحة البخل ... في البعد ما لا تكلف الإبل','107','3','0');
INSERT INTO [t754] VALUES (1228,'ملولة ما يدوم ليس لها ... من ملل دائم بها ملل','107','3','0');
INSERT INTO [t754] VALUES (1229,'كانما قدها إذا انفتلت ... سكران من خمر طرفها ثمل','107','3','0');
INSERT INTO [t754] VALUES (1230,'يجذبها تحت خصرها عجز ... كأنه من فراقها وجل','107','3','0');
INSERT INTO [t754] VALUES (1231,'بي حر شوق إلى ترشفها ... ينفصل الصبر حين يتصل','108','3','0');
INSERT INTO [t754] VALUES (1232,'الثغر والنحر والمخلخل والم ... عصم دائي والفاحم الرجل','108','3','0');
INSERT INTO [t754] VALUES (1233,'ومهمة جبته على قدمي ... تعجز عنه العرامس الذلل','108','3','0');
INSERT INTO [t754] VALUES (1234,'بصارمي مرتد بمخبرتي ... مجتزىء بالظلام مشتمل','108','3','0');
INSERT INTO [t754] VALUES (1235,'إذا صديق نكرت جانبه ... لم تعيني في فراقه الحيل','108','3','0');
INSERT INTO [t754] VALUES (1236,'في سعة الخافقين مضطرب ... وفي بلاد من أختها بدل','108','3','0');
INSERT INTO [t754] VALUES (1237,'وفي اعتمار الأمير بدر بن عم ... ار عن الشغل بالورى شغل','108','3','0');
INSERT INTO [t754] VALUES (1238,'أصبح مالا كماله لذوي ال ... حاجة لا يبتدي ولا يسل','108','3','0');
INSERT INTO [t754] VALUES (1239,'هان على قلبه الزمان فما ... يبين فيه غم ولا جذل','108','3','0');
INSERT INTO [t754] VALUES (1240,'يكاد من طاعة الحمام له ... يقتل من ما دنا له الأجل','108','3','0');
INSERT INTO [t754] VALUES (1241,'يكاد من صحة العزيمة ما ... يفعل قبل الفعال ينفعل','108','3','0');
INSERT INTO [t754] VALUES (1242,'تعرف في عينه حقائقه ... كأنه بالذكاء مكتحل','108','3','0');
INSERT INTO [t754] VALUES (1243,'أشفق عند اتقاد فكرته ... عليه منها أخاف يشتعل','108','3','0');
INSERT INTO [t754] VALUES (1244,'أغر أعداؤه إذا سلموا ... بالهرب استكبروا الذي فعلوا','109','3','0');
INSERT INTO [t754] VALUES (1245,'يقبلهم وجه كل سابحة ... أربعها قبل طرفها تصل','109','3','0');
INSERT INTO [t754] VALUES (1246,'جرداء ملإ الحزام مجفرة ... تكون مثلي عسيبها الخصل','109','3','0');
INSERT INTO [t754] VALUES (1247,'إن أدبرت قلت لا تليل لها ... أو أقبلت قلت ما لها كفل','109','3','0');
INSERT INTO [t754] VALUES (1248,'والطعن شزر والأرض واجفة ... كأنما في فؤادها وهل','109','3','0');
INSERT INTO [t754] VALUES (1249,'قد صبغت خدها الدماء كما ... يصبغ خد الخريدة الخجل','109','3','0');
INSERT INTO [t754] VALUES (1250,'والخيل تبكي جلودها عرقا ... بأدمع ما تسحها مقل','109','3','0');
INSERT INTO [t754] VALUES (1251,'سار ولا قفر من مواكبه ... كأنما كل سبسب جبل','109','3','0');
INSERT INTO [t754] VALUES (1252,'يمنها أن يصيبها مطر ... شدة ما قد تضايق الأسل','109','3','0');
INSERT INTO [t754] VALUES (1253,'يا بدر يا بحر يا غمامة يا ... ليث الشرى يا حمام يا رجل','109','3','0');
INSERT INTO [t754] VALUES (1254,'إن البنان الذي تقلبه ... عندك في كل موضع مثل','109','3','0');
INSERT INTO [t754] VALUES (1255,'إنك من معشر إذا وهبوا ... ما دون أعمارهم فقد بخلوا','109','3','0');
INSERT INTO [t754] VALUES (1256,'قلوبهم في مضاء ما امتشقوا ... قاماتهم في تمام ما اعتقلوا','109','3','0');
INSERT INTO [t754] VALUES (1257,'أنت نقيض اسمه إذا اختلفت ... قواضب الهند والقنا الذبل','109','3','0');
INSERT INTO [t754] VALUES (1258,'أنت لعمري البدر المنير ولك ... نك في حومة الوغى زحل','109','3','0');
INSERT INTO [t754] VALUES (1259,'كتيبة لست ربها نفل ... وبلدة لست حليها عطل','109','3','0');
INSERT INTO [t754] VALUES (1260,'قصدت من شرقها ومغربها ... حتى اشتكتك الركاب والسبل','109','3','0');
INSERT INTO [t754] VALUES (1261,'لم تبق إلا قليل عافية ... قد وفدت تجتديكها العلل','110','3','0');
INSERT INTO [t754] VALUES (1262,'عذر الملومين فيك أنهما ... آس جبان ومبضع بطل','110','3','0');
INSERT INTO [t754] VALUES (1263,'مددت في راحة الطبيب يدا ... وما دري كيف يقطع الأمل','110','3','0');
INSERT INTO [t754] VALUES (1264,'إن يكن النفع ضر باطنها ... فربما ضر ظهرها القبل','110','3','0');
INSERT INTO [t754] VALUES (1265,'يشق في عرقها الفصاد ولا ... يشق في عرق جودها العذل','110','3','0');
INSERT INTO [t754] VALUES (1266,'خامره إذ مددتها جزع ... كأنه من حذاقة عجل','110','3','0');
INSERT INTO [t754] VALUES (1267,'جاز حدود اجتهاده فأتى ... غير اجتهاد لأمه الهبل','110','3','0');
INSERT INTO [t754] VALUES (1268,'أبلغ ما يطلب النجاح به ال ... طبع وعند التعمق الزلل','110','3','0');
INSERT INTO [t754] VALUES (1269,'إرث لها إنها لما ملكت ... وبالذي قد أسلت تنهمل','110','3','0');
INSERT INTO [t754] VALUES (1270,'مثلك يا بدر لا يكون ولا ... تصلح إلا لمثلك الدول','110','3','0');
INSERT INTO [t754] VALUES (1271,'بقاءي شاء ليس هم ارتحالا ... وحسن الصبر زموا لا الجمالا','110','3','0');
INSERT INTO [t754] VALUES (1272,'تولوا بغتة فكأن بينا ... تهيبني ففاجأني اغتيالا','110','3','0');
INSERT INTO [t754] VALUES (1273,'فكان مسير عيسهم ذميلا ... وسير الدمع إثرهم انهمالا','111','3','0');
INSERT INTO [t754] VALUES (1274,'كأن العيس كانت فوق جفني ... مناخات فلما ثرن سالا','111','3','0');
INSERT INTO [t754] VALUES (1275,'وحجبت النوى الظبيات عني ... وساعدت البراقع والحجالا','111','3','0');
INSERT INTO [t754] VALUES (1276,'لبسن الوشى لا متجملات ... ولكن كي يصن به الجمالا','111','3','0');
INSERT INTO [t754] VALUES (1277,'وضفرن الغدائر لا لحسن ... ولكن خفن في الشعر الضلالا','111','3','0');
INSERT INTO [t754] VALUES (1278,'بجسمي من برته فلو أصارت ... وشاحي ثقب لؤلؤة لجالا','111','3','0');
INSERT INTO [t754] VALUES (1279,'ولولا أنني في غير نوم ... لبت أظنني مني خيالا','111','3','0');
INSERT INTO [t754] VALUES (1280,'بدت قمرا ومالت خوط بان ... وفاحت عنبرا ورنت غزالا','111','3','0');
INSERT INTO [t754] VALUES (1281,'كأن الحزن مشغوف بقلبي ... فساعة هجرها يجد الوصالا','111','3','0');
INSERT INTO [t754] VALUES (1282,'كذا الدنيا على من كان قبلي ... صروف لم يدمن عليه حالا','111','3','0');
INSERT INTO [t754] VALUES (1283,'أشد الغم عندي في سرور ... تيقن عنه صاحبه انتقالا','111','3','0');
INSERT INTO [t754] VALUES (1284,'الفت ترحلي وجعلت أرضي ... قتودي والغريري الجلالا','111','3','0');
INSERT INTO [t754] VALUES (1285,'فما حاولت في أرض مقاما ... ولا أزمعت عن أرض زوالا','111','3','0');
INSERT INTO [t754] VALUES (1286,'على قلق كأن الريح تحتي ... أوجهها جنوبا أو شمالا','111','3','0');
INSERT INTO [t754] VALUES (1287,'الى البدر بن عمار الذي لم ... يكن في غرة الشهر الهلالا','112','3','0');
INSERT INTO [t754] VALUES (1288,'ولم يعظم لنقص كان فيه ... ولم يزل الأمير ولن يزالا','112','3','0');
INSERT INTO [t754] VALUES (1289,'بلا مثل وإن أبصرت فيه ... لكل مغيب حسن مثالا','112','3','0');
INSERT INTO [t754] VALUES (1290,'حسان لابن رائق المرجى ... حسام المتقي أيام صالا','112','3','0');
INSERT INTO [t754] VALUES (1291,'سنان في قناة بني معد ... بني أسد إذا دعوا النزالا','112','3','0');
INSERT INTO [t754] VALUES (1292,'أعز مغالب كفا وسيفا ... ومقدرة ومحمية وآلا','112','3','0');
INSERT INTO [t754] VALUES (1293,'وأشرف فاخر نفسا وقوما ... وأكرم منتم عما وخالا','112','3','0');
INSERT INTO [t754] VALUES (1294,'ويبقى ضعف ما قد قيل فيه ... إذا لم يترك أحد مقالا','112','3','0');
INSERT INTO [t754] VALUES (1295,'فيا ابن الطاعنين بكل لدن ... مواضع يشتكي البطل السعالا','112','3','0');
INSERT INTO [t754] VALUES (1296,'ويا ابن الضاربين بكل عضب ... من العرب الأسافل والقلالا','112','3','0');
INSERT INTO [t754] VALUES (1297,'أرى المتشاعرين غروا بذمي ... ومن ذا يحمد الداء العضالا','113','3','0');
INSERT INTO [t754] VALUES (1298,'ومن يكن ذا فم مر مريض ... يجد مرا به الماء الزلالا','113','3','0');
INSERT INTO [t754] VALUES (1299,'وقالوا هل يبلغك الثريا ... فقلت نعم إذا شئت استفالا','113','3','0');
INSERT INTO [t754] VALUES (1300,'هو المفني والمذاكي والأعادي ... وبيض الهند والسمر الطوالا','113','3','0');
INSERT INTO [t754] VALUES (1301,'وقائدها مسومة خفافا ... على حي تصبح ثقالا','113','3','0');
INSERT INTO [t754] VALUES (1302,'جوائل بالقنى مثقفات ... كأن على عواملها الذبالا','113','3','0');
INSERT INTO [t754] VALUES (1303,'إذا وطئت بأيديها صخورا ... يفئن لوطيء أرجلها رمالا','113','3','0');
INSERT INTO [t754] VALUES (1304,'جواب مسائلي أله نظير ... ولا لك في سؤالك لا ألا لا','113','3','0');
INSERT INTO [t754] VALUES (1305,'لقد أمنت بك الإعدام نفس ... تعد رجاءها إياك مالا','113','3','0');
INSERT INTO [t754] VALUES (1306,'وقد وجلت قلوب منك حتى ... غدت أوجالها فيها وجالا','113','3','0');
INSERT INTO [t754] VALUES (1307,'سرورك أن تسر الناس طرا ... تعلمهم عليك به الدلالا','113','3','0');
INSERT INTO [t754] VALUES (1308,'إذا سألوا شكرتهم عليه ... وإن سكتوا سألتهم السؤالا','113','3','0');
INSERT INTO [t754] VALUES (1309,'وأسعد من رأينا مستميح ... ينيل المستماح بأن ينالا','113','3','0');
INSERT INTO [t754] VALUES (1310,'يفارق سهمك الرجل الملاقى ... فراق القوس ما لاقى الرجالا','113','3','0');
INSERT INTO [t754] VALUES (1311,'فما تقف السهام على قرار ... كأن الريش يطلب النصالا','113','3','0');
INSERT INTO [t754] VALUES (1312,'سبقت السابقين فما تجارى ... وجاوزت العلو فما تعالا','113','3','0');
INSERT INTO [t754] VALUES (1313,'وأقسم لو صلحت يمين شيء ... لما صلح العباد له شمالا','113','3','0');
INSERT INTO [t754] VALUES (1314,'أقلب منك طرفي في سماء ... وإن طلعت كواكبها خصالا','114','3','0');
INSERT INTO [t754] VALUES (1315,'وأعجب منك كيف قدرت تنشا ... وقد أعطيت في المهد الكمالا','114','3','0');
INSERT INTO [t754] VALUES (1316,'إنما بدر بن عمار سحاب ... هطل فيه ثواب وعقاب','114','3','0');
INSERT INTO [t754] VALUES (1317,'إنما بدر رزايا وعطايا ... ومنايا وطعان وضراب','114','3','0');
INSERT INTO [t754] VALUES (1318,'ما يجيل الطرف إلا حمدته ... جهدها الأيدي وذمته الرقاب','114','3','0');
INSERT INTO [t754] VALUES (1319,'ما به قتل أعادية ولكن ... يتقي أخلاف ما ترجو الذئاب','114','3','0');
INSERT INTO [t754] VALUES (1320,'فله هيبة من لا يترجى ... وله جود مرجى لا يهاب','114','3','0');
INSERT INTO [t754] VALUES (1321,'طاعن الفرسان في الأحداق شزرا ... وعجاج الحرب للشمس نقاب','114','3','0');
INSERT INTO [t754] VALUES (1322,'باعث النفس على الهول الذي ... ما لنفس وقعت فيه إياب','114','3','0');
INSERT INTO [t754] VALUES (1323,'بأبي ريحك لا نرجسنا ذا ... وأحاديثك لا هذا الشراب','114','3','0');
INSERT INTO [t754] VALUES (1324,'ليس بالمنكر إن برزت سبقا ... غير مدفوع عن السبق العراب','114','3','0');
INSERT INTO [t754] VALUES (1325,'في الخد أن عزم الخليط رحيلا ... مطر تزيد به الخدود محولا','114','3','0');
INSERT INTO [t754] VALUES (1326,'يا نظرة نفت الرقاد وغادرت ... في حد قلبي ما حييت فلولا','114','3','0');
INSERT INTO [t754] VALUES (1327,'كانت من الكحلاء سؤلي إنما ... أجلى تمثل في فؤادي سولا','114','3','0');
INSERT INTO [t754] VALUES (1328,'أجد الجفاء على سواك مروة ... والصبر إلا في نواك جميلا','114','3','0');
INSERT INTO [t754] VALUES (1329,'وارى تدللك الكثير محببا ... وأرى قليل تدلل مملولا','114','3','0');
INSERT INTO [t754] VALUES (1330,'تشكو روادفك المطية فوقها ... شكوى التي وجدت هواك دخيلا','114','3','0');
INSERT INTO [t754] VALUES (1331,'ويغيرني جذب الزمام لقلبها ... فمها إليك كطالب تقبيلا','115','3','0');
INSERT INTO [t754] VALUES (1332,'حدق الحسان من الغواني هجن لي ... يوم الفراق صبابة وغليلا','115','3','0');
INSERT INTO [t754] VALUES (1333,'حدق يذم من القواتل غيرها ... بدر بن عمار بن إسماعيلا','115','3','0');
INSERT INTO [t754] VALUES (1334,'الفارج الكرب العظام بمثلها ... والتارك الملك العزيز ذليلا','115','3','0');
INSERT INTO [t754] VALUES (1335,'محك إذا مطل الغريم بدينه ... جعل الحسام بما أراد كفيلا','115','3','0');
INSERT INTO [t754] VALUES (1336,'نطق إذا حط الكلام لثامه ... أعطى بمنطقة القلوب عقولا','115','3','0');
INSERT INTO [t754] VALUES (1337,'أعدى الزمان سخاؤه فسخا به ... ولقد يكون به الزمان بخيلا','115','3','0');
INSERT INTO [t754] VALUES (1338,'وكأن برقا في متون غمامة ... هندية في كفه مسلولا','115','3','0');
INSERT INTO [t754] VALUES (1339,'ومحل قائمه يسيل مواهبا ... لو كن سيلا ما وجدن مسيلا','115','3','0');
INSERT INTO [t754] VALUES (1340,'رقت مضاربه فهن كأنما ... بيدين من عشق الرقاب نحولا','115','3','0');
INSERT INTO [t754] VALUES (1341,'أمعفر الليث الهزبر بسوطه ... لمن أدخرت الصارم المصقولا','115','3','0');
INSERT INTO [t754] VALUES (1342,'وقعت على الأردن منه بلية ... نضدت بها هام الرفاق تلولا','115','3','0');
INSERT INTO [t754] VALUES (1343,'ورد إذا ورد البحيرة شاربا ... ورد الفرات زئيره والنيلا','116','3','0');
INSERT INTO [t754] VALUES (1344,'متخضب بدم الفوارس لابس ... في غيله من لبدتيه غيلا','116','3','0');
INSERT INTO [t754] VALUES (1345,'ما قوبلت عيناه إلا ظنتا ... تحت الدجى نار الفريق حلول','116','3','0');
INSERT INTO [t754] VALUES (1346,'في وحدة الرهبان إلا أنه ... لا يعرف التحريم والتحليلا','116','3','0');
INSERT INTO [t754] VALUES (1347,'يطأ الثرى مترفقا من تيهه ... فكأنه آس يجس عليلا','116','3','0');
INSERT INTO [t754] VALUES (1348,'ويرد غفرته إلى يافوخه ... حتى تصير لرأسه إكليلا','116','3','0');
INSERT INTO [t754] VALUES (1349,'وتظنه مما تزمجر نفسه ... عنها لشدة غيظه مشغولا','116','3','0');
INSERT INTO [t754] VALUES (1350,'قصرت مخافته الخطا فكأنهما ... ركب الكمى جواده مشكولا','116','3','0');
INSERT INTO [t754] VALUES (1351,'ألقى فريسته وبربر دونها ... وقربت قربا خاله تطفيلا','116','3','0');
INSERT INTO [t754] VALUES (1352,'فتشابه الخلقان في إقدامه ... وتخالفا في بذلك المأكولا','116','3','0');
INSERT INTO [t754] VALUES (1353,'أسد يرى عضويه فيك كليهما ... متنا أزل وساعدا مفتولا','116','3','0');
INSERT INTO [t754] VALUES (1354,'في سرج ظامية الفصوص طمرة ... يأبى تفردها لها التمثيلا','116','3','0');
INSERT INTO [t754] VALUES (1355,'نيالة الطلبات لولا أنها ... تعطى مكان لجامها ما نيلا','116','3','0');
INSERT INTO [t754] VALUES (1356,'تندى سوالفها إذا استحضرتها ... ويظن عقد عنانها محلولا','116','3','0');
INSERT INTO [t754] VALUES (1357,'ما زال يجمع نفسه في زوره ... حتى حسبت العرض منه الطولا','117','3','0');
INSERT INTO [t754] VALUES (1358,'ويدق بالصدر الحجار كأنه ... يبغي إلى ما في الحضيض سبيلا','117','3','0');
INSERT INTO [t754] VALUES (1359,'أنف الكريم من الدنية تارك ... في عينه العدد الكثير قليلا','117','3','0');
INSERT INTO [t754] VALUES (1360,'والعار مضاض وليس بخائف ... من حتفه من خاف مما قيلا','117','3','0');
INSERT INTO [t754] VALUES (1361,'سبق التقاءكه بوثبة هاجم ... لو لم تصادمه لجازك ميلا','117','3','0');
INSERT INTO [t754] VALUES (1362,'خذلته قوته وقد كافحته ... فاستنصر التسليم والتجديلا','117','3','0');
INSERT INTO [t754] VALUES (1363,'قبضت منيته يديه وعنقه ... فكأنما صادفته مغلولا','117','3','0');
INSERT INTO [t754] VALUES (1364,'سمع ابن عمته به وبحاله ... فنجا يهرول أمس منك مهولا','117','3','0');
INSERT INTO [t754] VALUES (1365,'وأمر مما فر منه فراره ... وكقتله أن لايموت قتيلا','117','3','0');
INSERT INTO [t754] VALUES (1366,'تلف الذي اتخذ الجراءة خلة ... وعظ الذي اتخذ الفرار خليلا','117','3','0');
INSERT INTO [t754] VALUES (1367,'لو كان علمك بالإله مقسما ... في الناس ما بعث الإله رسولا','117','3','0');
INSERT INTO [t754] VALUES (1368,'لو كان لفظك فيهم ما أنزل ال ... قرآن والتوراة والأنجيلا','117','3','0');
INSERT INTO [t754] VALUES (1369,'لو كان ما تعطيهم من قبل أن ... تعطيهم لم يعرفوا التأميلا','117','3','0');
INSERT INTO [t754] VALUES (1370,'فلقد عرفت وما عرفت حقيقة ... ولقد جهلت وما جهلت خمولا','117','3','0');
INSERT INTO [t754] VALUES (1371,'نطقت بسوددك الحمام تغنيا ... وبما تجشمها الجياد صهيلا','117','3','0');
INSERT INTO [t754] VALUES (1372,'ما كل من طلب المعالي نافذا ... فيها ولا كل الرجال فحولا','117','3','0');
INSERT INTO [t754] VALUES (1373,'تهني بصور أم نهنئها بكا ... وقل الذي صور فأنت له لكا','117','3','0');
INSERT INTO [t754] VALUES (1374,'وما صغر الأردن والساحل الذي ... حبيت به إلا إلى جنب قدركا','118','3','0');
INSERT INTO [t754] VALUES (1375,'تحاسدت البلدان حتى لو أنها ... نفوس لسار الشرق والغرب نحوكا','118','3','0');
INSERT INTO [t754] VALUES (1376,'وأصبح مصر لا تكون أميره ... ولو أنه ذو مقلة وفم بكى','118','3','0');
INSERT INTO [t754] VALUES (1377,'أرى حللا مطواة حسانا ... عداني أن أراك بها اعتلالي','118','3','0');
INSERT INTO [t754] VALUES (1378,'وهبك طويتها وخرجت منها ... أتطوي ما عليك من الجمال','118','3','0');
INSERT INTO [t754] VALUES (1379,'لقد ظلت أواخرها الأعالي ... مع الأولى بجسمك في قتال','118','3','0');
INSERT INTO [t754] VALUES (1380,'تلاحظك العيون وأنت فيها ... كأن عليك أفئدة الرجال','118','3','0');
INSERT INTO [t754] VALUES (1381,'متى أحصيت فضلك في كلام ... فقد أحصيت حبات الرمال','118','3','0');
INSERT INTO [t754] VALUES (1382,'الحب ما منع الكلام الألسنا ... وألذ شكوى عاشق ما أعلنا','118','3','0');
INSERT INTO [t754] VALUES (1383,'ليست الحبيب الهاجري هجر الكرى ... من غير جرم واصلي صلة الضنا','118','3','0');
INSERT INTO [t754] VALUES (1384,'بنا فلو حليتنا لم تدر ما ... ألواننا مما امتقعن تلونا','118','3','0');
INSERT INTO [t754] VALUES (1385,'وتوقدت أنفاسنا حتى لقد ... أشفقت تحترق العواذل بيننا','118','3','0');
INSERT INTO [t754] VALUES (1386,'أفدى المودعة التي أتبعتها ... نظرا فرادى بين زفرات ثنا','118','3','0');
INSERT INTO [t754] VALUES (1387,'أنكرت طارقة الحوادث مرة ... ثم اعترفت بها فصارت ديدنا','118','3','0');
INSERT INTO [t754] VALUES (1388,'وقطعت في الدنيا الفلا وركائبي ... فيها ووقتي الضحى والموهنا','119','3','0');
INSERT INTO [t754] VALUES (1389,'فوقفت منها حيث أوقفني الندا ... وبلغت من بدر بن عمار المنى','119','3','0');
INSERT INTO [t754] VALUES (1390,'لأبي الحسين جدى يضيق وعاءه ... عنه ولو كان الوعاء الأزمنا','119','3','0');
INSERT INTO [t754] VALUES (1391,'وشجاعة أغناه عنها ذكرها ... ونهى الجبان حديثها أن يجبنا','119','3','0');
INSERT INTO [t754] VALUES (1392,'نيطت حمائله بعاتق محرب ... ما كر قط وهل يكر وما أنثنى','119','3','0');
INSERT INTO [t754] VALUES (1393,'فكأنه والطعن من قدامه ... متخوف من خلفه أن يطعنا','119','3','0');
INSERT INTO [t754] VALUES (1394,'نفت التوهم عنه دحة ذهنه ... فقضى على غيب الأمور تيقنا','119','3','0');
INSERT INTO [t754] VALUES (1395,'يتفزع الجبار من بغتاته ... فيظل في خلواته متكفنا','119','3','0');
INSERT INTO [t754] VALUES (1396,'أمضى إرادته فسوف له قد ... واستقرب الأقصى فثم له هنا','119','3','0');
INSERT INTO [t754] VALUES (1397,'يجد الحديد على بضاضة جلده ... ثوبا أخف من الحرير وألينا','119','3','0');
INSERT INTO [t754] VALUES (1398,'وأمر من فقد الأحبة عنده ... فقد السيوف الفاقدات الأجفنا','119','3','0');
INSERT INTO [t754] VALUES (1399,'لا يستكن الرعب بين ضلوعه ... يوما ولا الإحسان أن لا يحسنا','119','3','0');
INSERT INTO [t754] VALUES (1400,'مستنبط من علمه ما في غد ... فكأن ما سيكون منه دونا','120','3','0');
INSERT INTO [t754] VALUES (1401,'تتقاصر الأفهام عن إدراكه ... مثل الذي الأفلاك فيه والدني','120','3','0');
INSERT INTO [t754] VALUES (1402,'من ليس من قتلاه من طلقائه ... من ليس ممن دان ممن حينا','120','3','0');
INSERT INTO [t754] VALUES (1403,'لما قفلت من السواحل نحونا ... قفلت إليها وحشة من عندنا','120','3','0');
INSERT INTO [t754] VALUES (1404,'ارج الطريق فما مررت بموضع ... إلا أقام به الشذا مستوطنا','120','3','0');
INSERT INTO [t754] VALUES (1405,'لو تعقل الشجر التي قابلتها ... مدت محيية إليك الأغصنا','120','3','0');
INSERT INTO [t754] VALUES (1406,'سلكت تماثيل القباب الجن من ... شوق بها فأدرن فيك الأعينا','120','3','0');
INSERT INTO [t754] VALUES (1407,'طربت مراكبنا فخلنا أنها ... لولا حياء عاقها رقصت بنا','120','3','0');
INSERT INTO [t754] VALUES (1408,'أقبلت تبسم والجياد عوابس ... يخببن بالحلق المضاعف والقنا','120','3','0');
INSERT INTO [t754] VALUES (1409,'عقدت سنابكها عليها عثيرا ... لو تبتغي عنقا عليه أمكنا','120','3','0');
INSERT INTO [t754] VALUES (1410,'والأمر أمرك والقلوب خوافق ... في موقف بين المنية والمنى','120','3','0');
INSERT INTO [t754] VALUES (1411,'فعجبت حتى ما عجبت من الظبي ... ورأيت حتى ما رأيت من السنا','120','3','0');
INSERT INTO [t754] VALUES (1412,'إني أراك من المكارم عسكرا ... في عسكر ومن المعالي معدنا','120','3','0');
INSERT INTO [t754] VALUES (1413,'فطن الفؤاد لما أتيت على النوى ... ولما تركت مخافة أن تفطنا','121','3','0');
INSERT INTO [t754] VALUES (1414,'أضحى فراقك لي عليه عقوبة ... ليس الذي قاسيت فيه هينا','121','3','0');
INSERT INTO [t754] VALUES (1415,'فاغفر فدى لك واحبني من بعدها ... لتخصني بعطية منها أنا','121','3','0');
INSERT INTO [t754] VALUES (1416,'وأنه المشير عليك في بضلة ... فالحر ممتحن بأولاد الزنا','121','3','0');
INSERT INTO [t754] VALUES (1417,'وإذا الفتى طرح الكلام معرضا ... في مجلس أخذ الكلام اللذ عني','121','3','0');
INSERT INTO [t754] VALUES (1418,'ومكائد السفهاء واقعة بهم ... وعداوة الشعراء بئس المقتنى','121','3','0');
INSERT INTO [t754] VALUES (1419,'لعنت مقارنة اللئام فإنها ... ضيف يجر من الندامة ضيفنا','121','3','0');
INSERT INTO [t754] VALUES (1420,'غضب الحسود إذا لقيتك راضيا ... رزء أخف على من أن يوزنا','121','3','0');
INSERT INTO [t754] VALUES (1421,'أمسى الذي بربك كافرا ... من غيرنا معنا بفضلك مؤمنا','121','3','0');
INSERT INTO [t754] VALUES (1422,'خلت البلاد من الغزالة ليلها ... فأعاضهاك الله كي لاتحزنا','121','3','0');
INSERT INTO [t754] VALUES (1423,'أصبحت تأمر بالحجاب لخلوة ... هيهات لست على الحجاب بقادر','121','3','0');
INSERT INTO [t754] VALUES (1424,'من كان ضوء جبينه ونواله ... لم يحجبا لم يحتجب عن ناظر','121','3','0');
INSERT INTO [t754] VALUES (1425,'فإذا احتجبت فأنت غير محجب ... وإذا بطنت فأنت عين الظاهر','121','3','0');
INSERT INTO [t754] VALUES (1426,'لم تر من نادمت الأكا ... لا لسوى ودك لي ذاكا','121','3','0');
INSERT INTO [t754] VALUES (1427,'ولا لحبيها ولكنني ... أمسيت أرجوك وأخشاكا','121','3','0');
INSERT INTO [t754] VALUES (1428,'عذلت منادمة الأمير عواذلي ... في شربها وكفت جواب السائل','121','3','0');
INSERT INTO [t754] VALUES (1429,'مطرت سحاب يديك ري جوانحي ... وحملت شكرك واصطناعك حاملي','122','3','0');
INSERT INTO [t754] VALUES (1430,'فمتى أقوم بشكر ما أوليتني ... والقول فيك علو قدر القائل','122','3','0');
INSERT INTO [t754] VALUES (1431,'يا أيها الملك الذي ندماؤه ... شركاؤه في ملكه لا ملكه','122','3','0');
INSERT INTO [t754] VALUES (1432,'في كل يوم بيننا دم كرمة ... لك توبة من توبة من سفكه','122','3','0');
INSERT INTO [t754] VALUES (1433,'والصدق من شيم الكرام فنبئنا ... أمن المدام تتوب أم من تركه','122','3','0');
INSERT INTO [t754] VALUES (1434,'بدر فتى لو كان من سؤاله ... يوما توفر حظه من ماله','122','3','0');
INSERT INTO [t754] VALUES (1435,'تخير الأفعال في أفعاله ... ويقل ما يأتيه في إقباله','122','3','0');
INSERT INTO [t754] VALUES (1436,'قمرا ترى وسحابتين بموضع ... من وجهه ويمينه وشماله','122','3','0');
INSERT INTO [t754] VALUES (1437,'سفك الدماء بجوده لا بأسه ... كرما لأن الطير بعض عياله','122','3','0');
INSERT INTO [t754] VALUES (1438,'إن يفن ما يحوي فقد أبقى به ... ذكرا يزول الدهر قبل زواله','122','3','0');
INSERT INTO [t754] VALUES (1439,'قد أبت بالحاجة مقضية ... وعفت في الجلسة تطويلها','122','3','0');
INSERT INTO [t754] VALUES (1440,'أنت الذي طول بقائي به ... خير لنفسي من بقائي لها','122','3','0');
INSERT INTO [t754] VALUES (1441,'يا بدر إنك والحديث شجون ... من لم يكن لمثاله تكوين','122','3','0');
INSERT INTO [t754] VALUES (1442,'لعظمت حتى لو تكون أمانة ... ما كان مؤتمنا بها جبرين','122','3','0');
INSERT INTO [t754] VALUES (1443,'بعض البرية فوق بعض خاليا ... فإذا حضرت فكل فوق دون','122','3','0');
INSERT INTO [t754] VALUES (1444,'فدتك الخيل وهي مسومات ... وبيض الهند وهي مجردات','122','3','0');
INSERT INTO [t754] VALUES (1445,'وصفتك في قواف سائرات ... وقد بقيت وإن كثرت صفات','122','3','0');
INSERT INTO [t754] VALUES (1446,'أفاعيل الورى من قبل دهم ... وفعلك في فعالهم شيات','122','3','0');
INSERT INTO [t754] VALUES (1447,'مضى الليل والفضل الذي لك لا يمضي ... ورؤياك أحلى في العيون من الغمض','123','3','0');
INSERT INTO [t754] VALUES (1448,'على أنني طوقت منك بنعمة ... شهيد بها بعضي لغيري على بعضي','123','3','0');
INSERT INTO [t754] VALUES (1449,'سلام الذي فوق السموات عرشه ... تخص به يا خير ماش على الأرض','123','3','0');
INSERT INTO [t754] VALUES (1450,'ألم تر أيها الملك المرجى ... عجائب ما رأيت من السحاب','123','3','0');
INSERT INTO [t754] VALUES (1451,'تشكي الأرض غيبته إليه ... وترشف ماءه رشف الرضاب','123','3','0');
INSERT INTO [t754] VALUES (1452,'وأوهم أن في الشطرنج همي ... وفيك تأملي ولك انتصابي','123','3','0');
INSERT INTO [t754] VALUES (1453,'سأمضي والسلام عليك مني ... مغيبي ليلتي وغدا إيابي','123','3','0');
INSERT INTO [t754] VALUES (1454,'نال الذي نلت منه مني ... لله ما تصنع الخمور','123','3','0');
INSERT INTO [t754] VALUES (1455,'وذا انصرافي إلى محلي ... أآذن أيها الأمير','123','3','0');
INSERT INTO [t754] VALUES (1456,'وجدت المدامة غلابة ... تهيج للقلب أشواقه','123','3','0');
INSERT INTO [t754] VALUES (1457,'تسيء من المرء تأديبه ... ولكن تحسن أخلاقه','123','3','0');
INSERT INTO [t754] VALUES (1458,'وأنفس ما للفتى لبه ... وذو اللب يكره إنفاقه','123','3','0');
INSERT INTO [t754] VALUES (1459,'وقد مت أمس بها موتة ... وما يشتهي الموت من ذاقه','123','3','0');
INSERT INTO [t754] VALUES (1460,'وجارية شعرها شطرها ... محكمة نافذ أمرها','123','3','0');
INSERT INTO [t754] VALUES (1461,'تدور وفي يدها طاقة ... تضمنها مكرها شبرها','123','3','0');
INSERT INTO [t754] VALUES (1462,'فإن أسكرتنا ففي جهلها ... بما فعلته بنا عذرها','123','3','0');
INSERT INTO [t754] VALUES (1463,'جارية ما لجسمها روح ... بالقلب من حبها تباريح','123','3','0');
INSERT INTO [t754] VALUES (1464,'في يدها طاقة تشير بها ... لكل طيب من طيبها ريح','123','3','0');
INSERT INTO [t754] VALUES (1465,'سأشرب الكاس عن إشارتها ... ودمع عيني في الخد مسفوح','123','3','0');
INSERT INTO [t754] VALUES (1466,'يا ذا المعالي ومعدن الأدب ... سيدنا وابن سيد العرب','123','3','0');
INSERT INTO [t754] VALUES (1467,'أنت عليم بكل معجزة ... ولو سألنا سواك لم يجب','123','3','0');
INSERT INTO [t754] VALUES (1468,'أهذه قابلتك راقصة ... أم رفعت رجلها من التعب','123','3','0');
INSERT INTO [t754] VALUES (1469,'أن الأمير أدام الله دولته ... لفاخر كسيت فخرا به مضر','124','3','0');
INSERT INTO [t754] VALUES (1470,'في الشرب جارية من تحتها خشب ... ما كان والدها جن ولابشر','124','3','0');
INSERT INTO [t754] VALUES (1471,'قامت على فرد رجل من مهابته ... وليس تعقل ما تأتي وما تدر','124','3','0');
INSERT INTO [t754] VALUES (1472,'ما نقلت في مشيئة قدما ... ولا اشتكت من دوارها ألما','124','3','0');
INSERT INTO [t754] VALUES (1473,'لم أر شخصا من قبل رؤيتها ... يفعل أفعالها وما عزما','124','3','0');
INSERT INTO [t754] VALUES (1474,'فلا تلمها على تواقعها ... أطربها أن رأتك مبتسما','124','3','0');
INSERT INTO [t754] VALUES (1475,'زعمت أنك تنفى الظن عن أدبي ... وأنت أعظم أهل العصر مقدارا','124','3','0');
INSERT INTO [t754] VALUES (1476,'أني أنا الذهب المعروف مخبره ... يزيد في السبك للدينار دينارا','124','3','0');
INSERT INTO [t754] VALUES (1477,'برجاء جودك يطرد الفقر ... وبأن تعادي ينفد العمر','124','3','0');
INSERT INTO [t754] VALUES (1478,'فخر الزجاج بأن شربت به ... وزرت على من عافها الخمر','124','3','0');
INSERT INTO [t754] VALUES (1479,'وسلمت منها وهي تسكرنا ... حتى كأنك هابك السكر','124','3','0');
INSERT INTO [t754] VALUES (1480,'ما يرتجي أحد لمكرمة ... إلا الإله وأنت يا بدر','124','3','0');
INSERT INTO [t754] VALUES (1481,'لا افتخار إلا لمن لا يضام ... مدرك أو محارب لا ينام','124','3','0');
INSERT INTO [t754] VALUES (1482,'ليس عزما ما مرض المرء فيه ... ليس هما ما عاق عنه الظلام','124','3','0');
INSERT INTO [t754] VALUES (1483,'ذل من يغبط الذليل بعيش ... رب عيش أخف منه الحمام','124','3','0');
INSERT INTO [t754] VALUES (1484,'كل حلم أتى بغير اقتدار ... حجة لاجيء إليها اللئام','124','3','0');
INSERT INTO [t754] VALUES (1485,'من يهن يسهل الهوان عليه ... لم لجرح بميت إيلام','124','3','0');
INSERT INTO [t754] VALUES (1486,'ضاق ذرعا بأن أضيق به ذر ... عا زماني واستكمرتني الكرام','124','3','0');
INSERT INTO [t754] VALUES (1487,'واقفا تحت أخمصي قدر نفسي ... واقفا تحت أخمصي الأنام','124','3','0');
INSERT INTO [t754] VALUES (1488,'أقرارا ألذ فوق شرار ... ومراما أبغي وظلمي يرام','124','3','0');
INSERT INTO [t754] VALUES (1489,'دون أن يشرق الحجاز ونجد ... والعراقان بالقنا والشام','124','3','0');
INSERT INTO [t754] VALUES (1490,'شرق الجو بالغبار إذا سا ... ر على بن أحمد القمقام','125','3','0');
INSERT INTO [t754] VALUES (1491,'الأديب المهذب الأصيد الضر ... بالذكي الجعد السري الهمام','125','3','0');
INSERT INTO [t754] VALUES (1492,'والذي ريب دهره من أسارا ... هـ ومن حاسدي يديه الغمام','125','3','0');
INSERT INTO [t754] VALUES (1493,'يتداوى من كثرة المال والإق ... لال جودا كأن هالا سقام','125','3','0');
INSERT INTO [t754] VALUES (1494,'حسن في عيون أعدائه أق ... بح من ضيفه رأته السوام','125','3','0');
INSERT INTO [t754] VALUES (1495,'لو حمى سيدا من الموت حام ... لحماك الإجلال والإعظام','125','3','0');
INSERT INTO [t754] VALUES (1496,'وعوار لوامع دينها الح ... ل ولكن زيها الإحرام','125','3','0');
INSERT INTO [t754] VALUES (1497,'كتبت في صحائف المجد بسم ... ثم قيس وبعد قيس السلام','125','3','0');
INSERT INTO [t754] VALUES (1498,'إنما مرة بن عوف بن سعد ... جمرات لا تشتهيها النعام','125','3','0');
INSERT INTO [t754] VALUES (1499,'ليلها صبحها من النار والأص ... باح ليل من الدخان تمام','125','3','0');
INSERT INTO [t754] VALUES (1500,'همم بلغتكم رتبات ... قصرت عن بلوغها الأوهام','125','3','0');
INSERT INTO [t754] VALUES (1501,'ونفوس إذا ايرت لقتال ... نفدت قبل ينفذ الإقدام','125','3','0');
INSERT INTO [t754] VALUES (1502,'وقلوب موطنات على الرو ... ع كأن اقتحامها استسلام','125','3','0');
INSERT INTO [t754] VALUES (1503,'قائدو كل شطبة وحصان ... قد براها الإسراج والإلجام','125','3','0');
INSERT INTO [t754] VALUES (1504,'يتعثران بالرؤوس كما م ... ر بتاءات نطقه التمام','125','3','0');
INSERT INTO [t754] VALUES (1505,'طال غشيانك الكرائه حتى ... قال فيك الذي أقول الحسام','125','3','0');
INSERT INTO [t754] VALUES (1506,'وكفتك الصفائح الناس حتى ... قد كفتك الصفائح الأقلام','126','3','0');
INSERT INTO [t754] VALUES (1507,'وكفتك التجارب الفكر حتى ... قد كفاك التجارب الإلهام','126','3','0');
INSERT INTO [t754] VALUES (1508,'فارس يشتري برازك للفخ ... ر بقتل معجل لا يلام','126','3','0');
INSERT INTO [t754] VALUES (1509,'نائل منك نظرة ساقه الفق ... ر عليه لفقره إنعام','126','3','0');
INSERT INTO [t754] VALUES (1510,'خير أعضائنا الرؤس ولكن ... فضلتها بقصدك الأقدام','126','3','0');
INSERT INTO [t754] VALUES (1511,'قد لعمري أقصرت عنك وللوف ... د ازدحام وللعطايا ازدحام','126','3','0');
INSERT INTO [t754] VALUES (1512,'خفت إن صرت في يمينك أن تأ ... خذني في هباتك الأقوام','126','3','0');
INSERT INTO [t754] VALUES (1513,'ومن الرشد لم أزرك على القر ... ب على البعد يعرف الإلمام','126','3','0');
INSERT INTO [t754] VALUES (1514,'ومن الخير بطؤ سيبك عنى ... أسرع السحب في المسير الجهام','126','3','0');
INSERT INTO [t754] VALUES (1515,'قل فكم من جواهر بنظام ... ودها أنها بفيك كلام','126','3','0');
INSERT INTO [t754] VALUES (1516,'حسبك الله ما تضل عن الح ... ق ولا يهتدي إليك أثام','126','3','0');
INSERT INTO [t754] VALUES (1517,'لم لا تحذر العواقب في غي ... ر الدنايا أو ما عليك حرام','126','3','0');
INSERT INTO [t754] VALUES (1518,'كم حبيب لا عذر في اللوم فيه ... لك فيه من التقي لوام','126','3','0');
INSERT INTO [t754] VALUES (1519,'إن بعضا من القريض هذاء ... ليس شيئا وبعضه أحكام','126','3','0');
INSERT INTO [t754] VALUES (1520,'منه ما يجلب البراعة والفض ... ل ومنه ما يجلب البرسام','127','3','0');
INSERT INTO [t754] VALUES (1521,'لا تنكرن رحيلي عنك في عجل ... فإنني لرحيلي غير مختار','127','3','0');
INSERT INTO [t754] VALUES (1522,'وربما فارق الإنسان مهجته ... يوم الوغى غير قال خشية العار','127','3','0');
INSERT INTO [t754] VALUES (1523,'وقد منيت بحساد أحاربهم ... فاجعل نداك عليهم بعض انصاري','127','3','0');
INSERT INTO [t754] VALUES (1524,'عذيري من عذاري من أمور ... سكن جوانحي بدل الخدور','127','3','0');
INSERT INTO [t754] VALUES (1525,'ومبتسمات هيجاوات عصر ... عن الأسياف ليس عن الثغور','127','3','0');
INSERT INTO [t754] VALUES (1526,'ركبت مشمرا قدمي إليها ... وكل عذافر قلق الضفور','127','3','0');
INSERT INTO [t754] VALUES (1527,'أوانا في بيوت البدو رحلي ... وآونة على قتد البعير','127','3','0');
INSERT INTO [t754] VALUES (1528,'أعرض للرماح الصم نحري ... وأنصب حر وجهي للهجير','127','3','0');
INSERT INTO [t754] VALUES (1529,'وأسرى في ظلام الليل وحدي ... كأني منه في قمر منير','127','3','0');
INSERT INTO [t754] VALUES (1530,'فقل في حاجة لم أقض منها ... على تعبي بها شروي نقير','127','3','0');
INSERT INTO [t754] VALUES (1531,'ونفس لا نجيب إلى خسيس ... وعين لا تدار على نظير','127','3','0');
INSERT INTO [t754] VALUES (1532,'وكف لا تنازع من أتاني ... ينازعني سوى شرفي وخيري','127','3','0');
INSERT INTO [t754] VALUES (1533,'وقلة ناصر جوزيت عني ... بشر منك يا شر الدهور','127','3','0');
INSERT INTO [t754] VALUES (1534,'عدوي كل شيء فيك حتى ... لنحلت الأكم موغرة الصدور','127','3','0');
INSERT INTO [t754] VALUES (1535,'فلو أني حسدت على نفيس ... لجدت به لذا الجد العثور','127','3','0');
INSERT INTO [t754] VALUES (1536,'ولكني حسدت على حيوتي ... وما خير الحيوى بلا سرور','128','3','0');
INSERT INTO [t754] VALUES (1537,'فيا ابن كروس يا نصف أعمى ... وإن تفخر فيها نصف البصير','128','3','0');
INSERT INTO [t754] VALUES (1538,'تعادينا لأنا غير لكن ... وتبغضنا لأنا غير عور','128','3','0');
INSERT INTO [t754] VALUES (1539,'فلو كنت امرء يهجا هجونا ... ولكن ضاق فتر عن مسير','128','3','0');
INSERT INTO [t754] VALUES (1540,'أفاضل الناس أغراض لذا الزمن ... يخلو من الهم أخلاهم من الفطن','128','3','0');
INSERT INTO [t754] VALUES (1541,'وإنما نحن في جيل سواسية ... شر على الحر من سقم على بدن','128','3','0');
INSERT INTO [t754] VALUES (1542,'حولي بكل مكان منهم خلق ... تخطى إذا جئت في استفهامها بمن','128','3','0');
INSERT INTO [t754] VALUES (1543,'لا أقتري بلدا إلا على غرر ... ولا أمر بخلق غير مضطغن','128','3','0');
INSERT INTO [t754] VALUES (1544,'ولا أعاشر من أملاكهم أحدا ... لا أحق بضرب الرأس من وثن','128','3','0');
INSERT INTO [t754] VALUES (1545,'إني لأعذرهم مما أعنفهم ... حتى أعنف نفسي فيهم وأني','128','3','0');
INSERT INTO [t754] VALUES (1546,'فقر الجهول بلا قلب إلى أدب ... فقرالحمار بلا رأس إلى رسن','128','3','0');
INSERT INTO [t754] VALUES (1547,'ومدقعين مسبورت صحبتهم ... عارين من حلل كاسين من درن','128','3','0');
INSERT INTO [t754] VALUES (1548,'خراب بادية غرثى بطونهم ... مكن الضباب لهم زاد بلا ثمن','128','3','0');
INSERT INTO [t754] VALUES (1549,'يستخبرون فلا أعطيهم خبري ... وما يطيش لهم سهم من الظنن','129','3','0');
INSERT INTO [t754] VALUES (1550,'وخلة في جليس أتقيه بها ... كيما يرى أننا مثلان في الوهن','129','3','0');
INSERT INTO [t754] VALUES (1551,'وكلمة في طريق خفتأعربها ... فيهتدي لي فلم أقدر على اللحن','129','3','0');
INSERT INTO [t754] VALUES (1552,'قد هون الصبر عندي كل نازلة ... ولين العزم حد المركب الخشن','129','3','0');
INSERT INTO [t754] VALUES (1553,'كمن مخلص على في خوض مهلكة ... وقتلة قرنت بالذم في الجبن','129','3','0');
INSERT INTO [t754] VALUES (1554,'لايعجبن مضيما حسن بزته ... وهل تروق دفينا جودة الكفن','129','3','0');
INSERT INTO [t754] VALUES (1555,'لله حال أرجيها وتخلفني ... وأقتضي كونها دهري فيمطلني','129','3','0');
INSERT INTO [t754] VALUES (1556,'مدحت قوما وإن عشنا نظمت لهم ... قصائدا من إناث الخيل والحصن','129','3','0');
INSERT INTO [t754] VALUES (1557,'تحت العجاح قوافيها مضمرة ... إذا تنوشدن لم يدخلن في أذن','129','3','0');
INSERT INTO [t754] VALUES (1558,'فلا أحارب مدفوعا على جدر ... ولا أصالح مغرورا على دخن','129','3','0');
INSERT INTO [t754] VALUES (1559,'مخيم الجمع بالبيداء يصهره ... حر الهواجر في صم من الفتن','129','3','0');
INSERT INTO [t754] VALUES (1560,'ألقى الكرام الأولى بادوا مكارمهم ... على الخصيبي عند الفرض والسنن','129','3','0');
INSERT INTO [t754] VALUES (1561,'فهن في الحجر منه كلما عرضت ... له اليتامى بدا بالمجد والمنن','129','3','0');
INSERT INTO [t754] VALUES (1562,'قاض إذا التبس الأمران عن له ... رأي يخلص بين الماء واللبن','129','3','0');
INSERT INTO [t754] VALUES (1563,'غض الشباب بعيد فجر ليلته ... مجانب العين للفحشاء والوسن','130','3','0');
INSERT INTO [t754] VALUES (1564,'شرابه النشح لا للري يطلبه ... وطعمه لقوام الجسم لا السمن','130','3','0');
INSERT INTO [t754] VALUES (1565,'القائل الصدق فيه ما يضر به ... والواحد الحالتين السر والعلن','130','3','0');
INSERT INTO [t754] VALUES (1566,'أفعاله نسب لو لم يقل معها ... جدي الخصيب عرفنا العرق بالغصن','130','3','0');
INSERT INTO [t754] VALUES (1567,'العارض الهتن ابن العارض الهتن اب ... ن العارض الهتن ابن العارض الهتن','130','3','0');
INSERT INTO [t754] VALUES (1568,'قد صرت أول الدنيا وآخرها ... آباؤه من مغار العلم في قرن','130','3','0');
INSERT INTO [t754] VALUES (1569,'كأنهم ولدوا من قبل أن ولدوا ... وكان فهمهم أيام لم يكن','130','3','0');
INSERT INTO [t754] VALUES (1570,'الخاطرين على أعدائهم أبدا ... من المحامد في أوقى من الجنن','130','3','0');
INSERT INTO [t754] VALUES (1571,'للناظرين إلى إقباله فرح ... يزيل ما بجباه القوم من غضن','130','3','0');
INSERT INTO [t754] VALUES (1572,'كأن مال ابن عبد الله مغتر ... من راحتيه بأرض الروم واليمن','130','3','0');
INSERT INTO [t754] VALUES (1573,'لم نفتقد بك من مزن سوى لثق ... ولا من البحر غير الريح والسفن','130','3','0');
INSERT INTO [t754] VALUES (1574,'ولا من الليث إلا قبح منظره ... ومن سواه سوى ما ليس بالحسن','130','3','0');
INSERT INTO [t754] VALUES (1575,'منذ احتبيت بأنطاكية اعتدلت ... حتى كأن ذوي الأوتار في هدن','130','3','0');
INSERT INTO [t754] VALUES (1576,'ومذ مررت على أطوادها قرعت ... من السجود فلا نبت على القنن','131','3','0');
INSERT INTO [t754] VALUES (1577,'أخلت مواهبك الأسواق من صنع ... أغنى نداك عن الأعمال والمهن','131','3','0');
INSERT INTO [t754] VALUES (1578,'ذا جود من ليس من دهر على ثقة ... وزهد من ليس من دنياه في وطن','131','3','0');
INSERT INTO [t754] VALUES (1579,'وهذه هيبة لم يؤتها بشر ... وذا اقتدار لسان ليس في المنن','131','3','0');
INSERT INTO [t754] VALUES (1580,'فمر وأوم تطع قدست من جبل ... تبارك الله مجرى الروح في حضن','131','3','0');
INSERT INTO [t754] VALUES (1581,'ألا لا أري الأحداث حمدا ولا ذما ... فما بطشها جهلا وما كفها حلما','131','3','0');
INSERT INTO [t754] VALUES (1582,'إلى مثل ما كان الفتى مرجع الفتى ... يعود كما أبدى ويكرى كما أرمي','131','3','0');
INSERT INTO [t754] VALUES (1583,'لك الله من مفجوعة بحبيبها ... قتيلة شوق غير ملحقها وصما','131','3','0');
INSERT INTO [t754] VALUES (1584,'أحن إلى الكأس التي شربت بها ... وأهوى لمثواها التراب وما ضما','131','3','0');
INSERT INTO [t754] VALUES (1585,'بكيت عليها خيفة في حيوتها ... وذاق كلانا ثكل صاحبه قدما','131','3','0');
INSERT INTO [t754] VALUES (1586,'ولو قتل الهجر المحبين كلهم ... مضى بلد باق أجدت له صرما','131','3','0');
INSERT INTO [t754] VALUES (1587,'عرفت الليالي قبل ما صنعت بنا ... فلما دهتني لم تزدني بها علما','131','3','0');
INSERT INTO [t754] VALUES (1588,'منافعها ما ضر في نفع غيرها ... تغذى وتروى أن نجوع وأن تظما','131','3','0');
INSERT INTO [t754] VALUES (1589,'أتاها كتابي بعد يأس وترحة ... فماتت سرورا بي فمت بها هما','132','3','0');
INSERT INTO [t754] VALUES (1590,'حرام على قلبي السرور فإنني ... أعد الذي ماتت به بعدها سما','132','3','0');
INSERT INTO [t754] VALUES (1591,'تعجب من خطي ولفظي كأنها ... ترى بحروف السطر أغربة عصما','132','3','0');
INSERT INTO [t754] VALUES (1592,'وتلثمه حتى أصار مداده ... محاجر عينيها وأنيابها سحما','132','3','0');
INSERT INTO [t754] VALUES (1593,'رقا دمعها البحارى وجفت جفونها ... وفارق حبي قلبها بعد ما أدمى','132','3','0');
INSERT INTO [t754] VALUES (1594,'ولم يسلها إلا المنايا وإنما ... أشد من السقم الذي أذهب السقما','132','3','0');
INSERT INTO [t754] VALUES (1595,'طلبت لها حظا ففاتت وفاتني ... وقد رضيت بي لو رضيت بها قسما','132','3','0');
INSERT INTO [t754] VALUES (1596,'فأصبحت استسقي الغمام لقبرها ... وقد كنت أستسقي الوغى والقنا الصما','132','3','0');
INSERT INTO [t754] VALUES (1597,'وكنت قبيل الموت استعظم النوى ... فقد صارت الصغرى التي كانت العظمى','132','3','0');
INSERT INTO [t754] VALUES (1598,'هبيني أخذت الثار فيك من العدى ... فكيف بأخذ الثار فيك من الحمى','132','3','0');
INSERT INTO [t754] VALUES (1599,'وما انسدت الدنيا علي لضيقها ... ولكن ظرفا لا أراك به أعمى','132','3','0');
INSERT INTO [t754] VALUES (1600,'فوا أسفا ألا أكب مقبلا ... لرأسك والصدر الذي مليأ حزما','132','3','0');
INSERT INTO [t754] VALUES (1601,'وألا ألاقى روحك الطيب الذي ... كأن ذكي المسك كان له جسما','133','3','0');
INSERT INTO [t754] VALUES (1602,'ولو لم تكوني بنت أكرم والد ... لكان أباك الضخم كونك لي أما','133','3','0');
INSERT INTO [t754] VALUES (1603,'لئن لذ يوم الشامتين بموتها ... لقد ولدت مني لأنفهم رغما','133','3','0');
INSERT INTO [t754] VALUES (1604,'تغرب لا مستعظما غير نفسه ... ولا قابلا إلا لخالقه حكما','133','3','0');
INSERT INTO [t754] VALUES (1605,'يقولون لي ما أنت في كل بلدة ... وما تبتغي ما أبتغي جل أن يسمى','133','3','0');
INSERT INTO [t754] VALUES (1606,'كأن بنيهم عالمون بأنني ... جلوب إليهم من معادنه اليتما','133','3','0');
INSERT INTO [t754] VALUES (1607,'وما الجمع بين الماء والنار في يدي ... بأصعب من أن أجمع الجد والفهما','133','3','0');
INSERT INTO [t754] VALUES (1608,'ولكنني مستنصر بذبابه ... ومرتكب في كل حال به الغشا','133','3','0');
INSERT INTO [t754] VALUES (1609,'وجاعله يوم اللقاء تحيتي ... وإلا فلست السيد البطل القرما','133','3','0');
INSERT INTO [t754] VALUES (1610,'إذا فل عزمي عن مدى خوف بعده ... فأبعد شيء ممكن لم يجد عزما','133','3','0');
INSERT INTO [t754] VALUES (1611,'وإني لمن قوم كأن نفوسنا ... بها أنف أن تسكن اللحم والعظما','133','3','0');
INSERT INTO [t754] VALUES (1612,'كذا أنا يا دنيا إذا شئت فاذهبي ... ويا نفس زيدي في كرائهها قدما','133','3','0');
INSERT INTO [t754] VALUES (1613,'فلا عبرت بي ساعة لا تعرني ... ولا صحبتني مهجة تقبل الظلما','133','3','0');
INSERT INTO [t754] VALUES (1614,'يستعظمون أبياتا نأمت بها ... لاتحسدن على أن ينئم الأسدا','133','3','0');
INSERT INTO [t754] VALUES (1615,'لو أن ثم قلوبا يعقلون بها ... أنساهم الذعر مما تحتها الحسدا','133','3','0');
INSERT INTO [t754] VALUES (1616,'لك يا منازل في القلوب منازل ... أقفرت أنت وهن منعك أواهل','133','3','0');
INSERT INTO [t754] VALUES (1617,'يعلمن ذاك وما علمت وإنما ... أولا كما ببكى عليه العاقل','134','3','0');
INSERT INTO [t754] VALUES (1618,'وأنا الذي اجتلب المنية طرفه ... فمن المطالب والقتيل القاتل','134','3','0');
INSERT INTO [t754] VALUES (1619,'تخلو الديار من الظباء وعنده ... من كل تابعة خيال خاذل','134','3','0');
INSERT INTO [t754] VALUES (1620,'أللاء افتكها الجبان بمهجتي ... وأحبها قربا إلى الباخل','134','3','0');
INSERT INTO [t754] VALUES (1621,'الراميات لنا وهن نوافر ... والخاتلات لنا وهن غوافل','134','3','0');
INSERT INTO [t754] VALUES (1622,'كافأننا عن شبههن من المها ... فلهن في غير التراب حبائل','134','3','0');
INSERT INTO [t754] VALUES (1623,'من طاعني ثغر الرجال جآذر ... ومن الرماح دمالج وخلاخل','134','3','0');
INSERT INTO [t754] VALUES (1624,'ولذا أسم أغطية العيون جفونها ... من أنها عمل السيوف عوامل','134','3','0');
INSERT INTO [t754] VALUES (1625,'كم وقفة سجرتك شوقا بعد ما ... غرى الرقيب بنا ولج العاذل','134','3','0');
INSERT INTO [t754] VALUES (1626,'دون التعانق ناحلين كشكلتي ... نصب أدقهما وضم الشاكل','134','3','0');
INSERT INTO [t754] VALUES (1627,'إنعم ولذ فللأمور أواخر ... أبدا إذا كانت لهن أوائل','134','3','0');
INSERT INTO [t754] VALUES (1628,'ما دمت من أرب الحسان فإنما ... ظل الشباب عليك ظل زائل','134','3','0');
INSERT INTO [t754] VALUES (1629,'للهو آونة تمر كأ، ها ... قبل يزودها حبيب راحل','134','3','0');
INSERT INTO [t754] VALUES (1630,'جمع الزمان فما لذيذ خالص ... مما يشوب ولا سرور كامل','135','3','0');
INSERT INTO [t754] VALUES (1631,'حتى أبو الفضل بن عبد الله رؤ ... يته المنى وهي المقام الهائل','135','3','0');
INSERT INTO [t754] VALUES (1632,'ممطورة طرقي إليه دونه ... من جوده في كل قج وابل','135','3','0');
INSERT INTO [t754] VALUES (1633,'محجوبة بسرادق من هيبة ... تثني الأزمة والمطي ذوامل','135','3','0');
INSERT INTO [t754] VALUES (1634,'للشمس فيه وللسحاب وللجا ... ر وللرياح وللأسود شمائل','135','3','0');
INSERT INTO [t754] VALUES (1635,'ولديه ملعقيان والأدب المفا ... د وما الحيوة وما الممات مناهل','135','3','0');
INSERT INTO [t754] VALUES (1636,'لو لم يهب لجب الوفود حواله ... لسرى إليه قطا الفلاة الناهل','135','3','0');
INSERT INTO [t754] VALUES (1637,'يدري بما بك قبل تظهره له ... من ذهنه ويجيب قبل تسائل','135','3','0');
INSERT INTO [t754] VALUES (1638,'وتراه معترضا لها وموليا ... أحداقنا وتحار حين يقابل','135','3','0');
INSERT INTO [t754] VALUES (1639,'كلماته قضب وهن فواصل ... كل الضرائب تحتهن مفاصل','135','3','0');
INSERT INTO [t754] VALUES (1640,'هزمت مكارمه المكارم كلها ... حتى كأن المكرمات قبائل','135','3','0');
INSERT INTO [t754] VALUES (1641,'وقتلن دفرا والدهيم فما ترى ... أم الدهيم وأم دفر هابل','135','3','0');
INSERT INTO [t754] VALUES (1642,'علامة العلماء واللج الذي ... لا ينتهي ولكل لج ساحل','135','3','0');
INSERT INTO [t754] VALUES (1643,'لو طاب مولد كل حي مثله ... ولد النساء وما لهن قوابل','135','3','0');
INSERT INTO [t754] VALUES (1644,'لو بان بالكرم الجنين بيانه ... لدرت به ذكر أم أنثى الحامل','136','3','0');
INSERT INTO [t754] VALUES (1645,'ليزد بنو الحسن الشراف تواضعا ... هيهات تكتم في الظلام مشاعل','136','3','0');
INSERT INTO [t754] VALUES (1646,'ستروا الندى ستر الغراب سفاده ... فبدا وهل يخفى الرباب الهاطل','136','3','0');
INSERT INTO [t754] VALUES (1647,'جفخت وهم لا يجفخون بها بهم ... شيم على الحسب الأعز دلائل','136','3','0');
INSERT INTO [t754] VALUES (1648,'متشابهي ورع النفوس كبيرهم ... وصغيرهم عف الإزار حلال','136','3','0');
INSERT INTO [t754] VALUES (1649,'يا أفخر فإن الناس فيك ثلاثة ... مستعظم أو حاسد أو جاهل','136','3','0');
INSERT INTO [t754] VALUES (1650,'ولقد علوت فما تبالي بعد ما ... عرفوا أيحمد أم يذم القائل','136','3','0');
INSERT INTO [t754] VALUES (1651,'أثنى عليك ولو تشاء لقلت لي ... قصرت بالإمساك عني نمائل','136','3','0');
INSERT INTO [t754] VALUES (1652,'لا تجسر الفصحاء تنشد هاهنا ... بيتا ولكني الهزبر الباسل','136','3','0');
INSERT INTO [t754] VALUES (1653,'وإذا أتتك مذمتي من ناقص ... فهي الشهادة لي بأني فاضل','136','3','0');
INSERT INTO [t754] VALUES (1654,'من لي بفهم أهيل عصر يدعي ... أن يحسب الهندي فيهم باقل','136','3','0');
INSERT INTO [t754] VALUES (1655,'وأما وحقلك وهو غايرة مقسم ... للحق أنت وما سواك الباطل','137','3','0');
INSERT INTO [t754] VALUES (1656,'الطيب أنت إذا أصابك طيبه ... والماء أنت إذا اغتسلت الغاسل','137','3','0');
INSERT INTO [t754] VALUES (1657,'ما دار في الحنك اللسان وقلبت ... قلما بأحسن من نثاك أنامل','137','3','0');
INSERT INTO [t754] VALUES (1658,'قد علم البين منا البيت أجفانا ... تدمي وألف في ذا القلب أحزانا','137','3','0');
INSERT INTO [t754] VALUES (1659,'أملت ساعة ساروا كشف معصمها ... ليلبث الحي دون السير حيرانا','137','3','0');
INSERT INTO [t754] VALUES (1660,'ولو بدت لأتاهتهم فحجبها ... صون عقولهم من لحظها صانا','137','3','0');
INSERT INTO [t754] VALUES (1661,'بالواخدات وحاديها وبي قمر ... يظل من وخدها في الخدر حشيانا','137','3','0');
INSERT INTO [t754] VALUES (1662,'أما الثياب فتعرى من محاسنه ... إذا نضاها ويكسى الحسن عريانا','137','3','0');
INSERT INTO [t754] VALUES (1663,'يضمه المسك ضم المستهام به ... حتى يصير على الأعكان أعكانا','137','3','0');
INSERT INTO [t754] VALUES (1664,'قد كنت أشفق من دمعي على بصري ... فاليوم كل عزيز بعدكم هانا','137','3','0');
INSERT INTO [t754] VALUES (1665,'تهدي البوارق أخلاف المياه لكم ... وللمحب من التذكار نيرانا','137','3','0');
INSERT INTO [t754] VALUES (1666,'إذا قدمت على الأهوال شيعني ... قلب إذا شئت إن يسلاكم خانا','137','3','0');
INSERT INTO [t754] VALUES (1667,'أبدو فيسجد من بالسوء يذكرني ... ولا أعاتبه صفحا وإهوانا','137','3','0');
INSERT INTO [t754] VALUES (1668,'وهكذا كنت في أهلي وفي وطني ... إن النفيس غريب حيثما كانا','137','3','0');
INSERT INTO [t754] VALUES (1669,'محسد الفضل مكذوب على أثري ... ألقى الكمي ويلقاني إذا حانا','138','3','0');
INSERT INTO [t754] VALUES (1670,'لا أشرئب إلى ما لم يفت طمعا ... ولا أبيت على ما فات حسرانا','138','3','0');
INSERT INTO [t754] VALUES (1671,'ولا أسر بما غيري الحميد به ... ولو حملت إلى الجهر ملآنا','138','3','0');
INSERT INTO [t754] VALUES (1672,'لايجذبن ركابي نحوه أحد ... ما جمت حيا وما قلقن كيرانا','138','3','0');
INSERT INTO [t754] VALUES (1673,'لو استطعت ركبت الناس كلهم ... إلى سعيد بن عبد الله بعرانا','138','3','0');
INSERT INTO [t754] VALUES (1674,'فالعيس أعقل من قوم رأيتهم ... عما يراه من الإحسان عميانا','138','3','0');
INSERT INTO [t754] VALUES (1675,'ذاك الجواد وإن قل الجواد له ... ذاك الشجاع وإن لم يرض أقرانا','138','3','0');
INSERT INTO [t754] VALUES (1676,'ذاك المعد الذي تقنوا يداه لنا ... فلو أصيب بشيء منه عزانا','138','3','0');
INSERT INTO [t754] VALUES (1677,'خف الزمان على أطراف أنمله ... حتى توهمن للأزمان أزمانا','138','3','0');
INSERT INTO [t754] VALUES (1678,'يلقى الوغى والقنا والنازلات به ... والسيف والضيف رحب الباع جذلانا','138','3','0');
INSERT INTO [t754] VALUES (1679,'تخاله من ذكاء القلب محتميا ... ومن تكرمه والبشر نشوانا','138','3','0');
INSERT INTO [t754] VALUES (1680,'وتسحب الحبر القينات رافلة ... في جوده وتجر الخيل أرسانا','138','3','0');
INSERT INTO [t754] VALUES (1681,'يعطى المبشر بالقصاد قبلهم ... كمن يبشره بالماء عطشانا','138','3','0');
INSERT INTO [t754] VALUES (1682,'جزت بني الحسن الحسنى فإنهم ... في قومهم مثلهم في الغر عدنانا','138','3','0');
INSERT INTO [t754] VALUES (1683,'ما شيد الله من مجد لسالفهم ... إلا ونحن نراه فيهم الآنا','139','3','0');
INSERT INTO [t754] VALUES (1684,'إن كوتبوا أو لقوا أو حوربوا وجدوا ... في الخط واللفظ والهيجاء فرسانا','139','3','0');
INSERT INTO [t754] VALUES (1685,'كأن ألسنهم في النطق قد جعلت ... على رماحهم في الطعن خرصانا','139','3','0');
INSERT INTO [t754] VALUES (1686,'كأنهم يردون الموت من ظمإ ... أو نشقون من الخطى ريحانا','139','3','0');
INSERT INTO [t754] VALUES (1687,'الكائنين لمن أبغى عداوته ... أعدى العدى ولمن آخيت إخوانا','139','3','0');
INSERT INTO [t754] VALUES (1688,'خلائق لو حواها الزنج لانقلبوا ... ظمى الشفاه جعاد الشعر غرانا','139','3','0');
INSERT INTO [t754] VALUES (1689,'وأنفس يلمعيات تحبهم ... لها اضطرارا ولو أقصوك شنآنا','139','3','0');
INSERT INTO [t754] VALUES (1690,'الواضحين أبوات وأجبنة ... ووالدات وألبانا وأذهانا','139','3','0');
INSERT INTO [t754] VALUES (1691,'يا صائد الجحفل المرهوب جانبه ... إن الليوث تصيد الناس أحدانا','139','3','0');
INSERT INTO [t754] VALUES (1692,'وواهبا كل وقتس وقت نائله ... وإنما يهب الوهاب أحيانا','139','3','0');
INSERT INTO [t754] VALUES (1693,'أنت الذي سبك الأموال مكرمة ... ثم اتخذت لها السؤال خزانا','139','3','0');
INSERT INTO [t754] VALUES (1694,'عليك منك إذا أخليت مرتقب ... لم تأت في السر ما لم تأت إعلانا','139','3','0');
INSERT INTO [t754] VALUES (1695,'لا أستزيدك فيما فيك من كرم ... أنا الذي نام إن نبهت يقظانا','139','3','0');
INSERT INTO [t754] VALUES (1696,'فإن مثلك باهيت الكرام به ... ورد سخطا على الأيام رضوانا','139','3','0');
INSERT INTO [t754] VALUES (1697,'وأنت أبعدهم ذكرا وأكبرهم ... قدرا وأرفعهم في المجد بنيانا','139','3','0');
INSERT INTO [t754] VALUES (1698,'قد شرف الله أرضا أنت ساكنها ... وشرف الناس إذ سواك إنسانا','139','3','0');
INSERT INTO [t754] VALUES (1699,'سرب محاسنه حرمت ذواتها ... داني الصفات بعيد موصوفاتها','140','3','0');
INSERT INTO [t754] VALUES (1700,'أوفى فكنت إذا رميت بمقلتي ... بشرا رأيت ارق من عبراتها','140','3','0');
INSERT INTO [t754] VALUES (1701,'يستاق عيسهم أنيني خلفهم ... تتوهم الزفرات زجر حداتها','140','3','0');
INSERT INTO [t754] VALUES (1702,'وكأنها شجر بدا لكنها ... شجر جنيت الموت من ثمراتها','140','3','0');
INSERT INTO [t754] VALUES (1703,'لا سرت من إبل لو أني فوقها ... لمحت حرارة مدمعي سماتها','140','3','0');
INSERT INTO [t754] VALUES (1704,'وحملت ما حملت من هذي المها ... وحملت ما حملت من حسراتها','140','3','0');
INSERT INTO [t754] VALUES (1705,'إني على شغفي بما في خمرها ... لأعف عما في سراويلاتها','140','3','0');
INSERT INTO [t754] VALUES (1706,'وترى المروة والفتوة والأب ... وة في كل مليحة ضراتها','140','3','0');
INSERT INTO [t754] VALUES (1707,'هن الثلاث المانعاني لذتي ... في خلوتي لا الخوف من تبعاتها','141','3','0');
INSERT INTO [t754] VALUES (1708,'ومطالب فيها الهلاك اتيتها ... ثبت الجنان كأنني لم آتها','141','3','0');
INSERT INTO [t754] VALUES (1709,'ومقانب بمقانب غادرتها ... أقوات وحش كن من أقواتها','141','3','0');
INSERT INTO [t754] VALUES (1710,'أقبلتها غرر الجياد كأنما ... أيدي بني عمران في جبهاتها','141','3','0');
INSERT INTO [t754] VALUES (1711,'الثابتين فروسة كجلودها ... في ظهرها والطعن في لباتها','141','3','0');
INSERT INTO [t754] VALUES (1712,'العارفين بها كما عرفتهم ... والراكبين جدودهم أماتها','141','3','0');
INSERT INTO [t754] VALUES (1713,'فكأنها نتجت قياما تحتهم ... وكأنهم ولدوا على صهواتها','141','3','0');
INSERT INTO [t754] VALUES (1714,'إن الكرام بلا كرام منهم ... مثل القلوب بلا سويداواتها','141','3','0');
INSERT INTO [t754] VALUES (1715,'تلك النفوس الغالبات على العلا ... والمجد يغلبها على شهواتها','141','3','0');
INSERT INTO [t754] VALUES (1716,'سقيت منابتها التي سقت الورى ... بيدي أبي أيوب خير نباتها','141','3','0');
INSERT INTO [t754] VALUES (1717,'ليس التعجب من مواهب ماله ... بل من سلامتها إلى أوقاتها','142','3','0');
INSERT INTO [t754] VALUES (1718,'عجبا له حفظ العنان بأنمل ... ما حفظها الأشياء من عاداتها','142','3','0');
INSERT INTO [t754] VALUES (1719,'لو مر يركض في سطور كتابة ... أحصى بحافر مهره ميماتها','142','3','0');
INSERT INTO [t754] VALUES (1720,'يضع السنان بحيث شاء مجاولا ... حتى من الآذان في أخراتها','142','3','0');
INSERT INTO [t754] VALUES (1721,'تكبو وراءك يا ابن أحمد قرح ... ليست قوائمهن من آلاتها','142','3','0');
INSERT INTO [t754] VALUES (1722,'رعد القوارس منك في أبدانها ... أجرى من العسلان في قنواتها','142','3','0');
INSERT INTO [t754] VALUES (1723,'لا خلق أسمح منك إلا عارف ... بك رآء نفسك لم يقل لك هاتها','142','3','0');
INSERT INTO [t754] VALUES (1724,'غلت الذي حسب العشور بآية ... ترتيلك السورات من آياتها','142','3','0');
INSERT INTO [t754] VALUES (1725,'كرم تبين في كلامك ماثلا ... ويبين عتق الخيل في أصواتها','142','3','0');
INSERT INTO [t754] VALUES (1726,'أعيا زوالك عن محل نلته ... لا تخرج الأقمار عن هالاتها','142','3','0');
INSERT INTO [t754] VALUES (1727,'لا نعذل المرض الذي بك شائق ... أنت الرجال وشائق علاتها','142','3','0');
INSERT INTO [t754] VALUES (1728,'فإذا نوت سفرا إليك سبقتها ... فأضفت قبل مضافها حالاتها','142','3','0');
INSERT INTO [t754] VALUES (1729,'ومنازل الحمى الجسوم فقل لنا ... ما عذرها في تركها خيراتها','143','3','0');
INSERT INTO [t754] VALUES (1730,'أعجبتها شرفا فطال وقوفها ... لتأمل الأعضاء لا لآذاتها','143','3','0');
INSERT INTO [t754] VALUES (1731,'وبذلت ما عشقته نفسك كله ... حتى بذلت لهذه صحاتها','143','3','0');
INSERT INTO [t754] VALUES (1732,'حق الكواكب أن تزورك من علو ... وتعودك الآساد من غاباتها','143','3','0');
INSERT INTO [t754] VALUES (1733,'والجن من ستراتها والوحش من ... فلواتها والطير من وكناتها','143','3','0');
INSERT INTO [t754] VALUES (1734,'ذكر الأنام لنا فكان قصيدة ... كنت البديع الفرد من أبياتها','143','3','0');
INSERT INTO [t754] VALUES (1735,'في الناس أمثلة تدور حيوتها ... كمماتها ومماتها كحيوتها','143','3','0');
INSERT INTO [t754] VALUES (1736,'هبت النكاح حذار نسل مثلها ... حتى وفرت على النساء بناتها','143','3','0');
INSERT INTO [t754] VALUES (1737,'فاليوم صرت إلى الذي لو أنه ... ملك البرية لأستقل هباتها','143','3','0');
INSERT INTO [t754] VALUES (1738,'مسترخص نظر إليه بما به ... نظرت وعثرة رجله بدياتها','143','3','0');
INSERT INTO [t754] VALUES (1739,'أطاعن خيلا من فوارسها الدهر ... وحيدا وما قولي كذا ومعي الصبر','143','3','0');
INSERT INTO [t754] VALUES (1740,'وأشجع مني كل يوم سلامتي ... وما ثبتت إلا وفي نفسها أمر','143','3','0');
INSERT INTO [t754] VALUES (1741,'تمرست بالآفات حتى تركتها ... تقول أمات الموت أم ذعر الذعر','143','3','0');
INSERT INTO [t754] VALUES (1742,'وأقدمت أقدام الأتي كأن لي ... سوى مهجتي أو كان لي عندها وتر','143','3','0');
INSERT INTO [t754] VALUES (1743,'ذر النفس تأخذ وسعها قبل بينها ... فمفترق جاران دارهما العمر','143','3','0');
INSERT INTO [t754] VALUES (1744,'وتضريب أعناق الرجال وأن ترى ... لك الهبوات السود والعسكر المجر','144','3','0');
INSERT INTO [t754] VALUES (1745,'وتركك في الدنيا دويا كأنما ... تداول سمع المرء أنمله العشر','144','3','0');
INSERT INTO [t754] VALUES (1746,'إذا الفضل لم يرفعك عن شكر ناقص ... على هبة فالفضل فيمن له الشكر','144','3','0');
INSERT INTO [t754] VALUES (1747,'ومن ينفق الساعات في جمع ماله ... مخافة فقر فالذي فعل الفقر','144','3','0');
INSERT INTO [t754] VALUES (1748,'علي لأهل الجور كل طمرة ... عليها غلام مل حيزومه غمر','144','3','0');
INSERT INTO [t754] VALUES (1749,'يدير بأطراف الرماح عليهم ... كؤوس المنايا حيث لا تشتهي الخمر','144','3','0');
INSERT INTO [t754] VALUES (1750,'وكم من جبال جبت تشهد أنني ال ... جبال وبحر شاهد أنني البحر','144','3','0');
INSERT INTO [t754] VALUES (1751,'وخرق مكان العيس منه مكاننا ... من العيس فيه واسط الكور والظهر','144','3','0');
INSERT INTO [t754] VALUES (1752,'يخدن بنا في جوزه وكأننا ... على كرة أو أرضه معنا سفر','144','3','0');
INSERT INTO [t754] VALUES (1753,'ويوم وصلناه بليل كأنما ... على أفقه من برقه حلل حمر','145','3','0');
INSERT INTO [t754] VALUES (1754,'وليل وصلناه بيوم كأنما ... على متنه من دجنه حلل خضر','145','3','0');
INSERT INTO [t754] VALUES (1755,'وغيث ظننا تحته أن عامرا ... علا لم يمت أو في السحاب له قبر','145','3','0');
INSERT INTO [t754] VALUES (1756,'أو ابن ابنه الباقي علي بن أحمد ... يجود به لم أجز ويدي صفر','145','3','0');
INSERT INTO [t754] VALUES (1757,'وأن سحابا جوده مثل جوده ... سحاب على كل السحاب له فخر','145','3','0');
INSERT INTO [t754] VALUES (1758,'فتى لا يضم القلب همات قلبه ... ولو ضمها قلب لما ضمه صدر','145','3','0');
INSERT INTO [t754] VALUES (1759,'ولا ينفع الإمكان لولا سخاؤه ... وهل نافع لولا الأكف القنا السمر','145','3','0');
INSERT INTO [t754] VALUES (1760,'قران تلاقي الصلت فيه وعامر ... كما يتلاقى الهنداوي والنصر','145','3','0');
INSERT INTO [t754] VALUES (1761,'فجاءا به صلت الجبين معظما ... ترى الناس قلا حوله وهم كثر','145','3','0');
INSERT INTO [t754] VALUES (1762,'مفدى بآبآء الرجال سميدعا ... هو الكرم المد الذي ما له جزر','145','3','0');
INSERT INTO [t754] VALUES (1763,'وما زلت حتى قادني الشوق نحوه ... يسايرني في كل ركب له ذكر','145','3','0');
INSERT INTO [t754] VALUES (1764,'وأستكبر الأخبار قبل لقائه ... فلما التقينا صغر الخبر الخبر','145','3','0');
INSERT INTO [t754] VALUES (1765,'إليك طعنا في مدى كل صفصف ... بكل وآة كل مما لقيت نحر','145','3','0');
INSERT INTO [t754] VALUES (1766,'إذا ورمت من لسعة مرحت لها ... كأن نوالا صر في جلدها النبر','146','3','0');
INSERT INTO [t754] VALUES (1767,'فجئناك دون الشمس والبدر في النوى ... ودونك في أحوالك الشمس والبدر','146','3','0');
INSERT INTO [t754] VALUES (1768,'كأنك برد الماء لا عيش دونه ... ولو كنت برد الماء لم يكن العشر','146','3','0');
INSERT INTO [t754] VALUES (1769,'دعاني إليك العلم والحلم والحجي ... وهذا الكلام النظم والنائل النثر','146','3','0');
INSERT INTO [t754] VALUES (1770,'وما قلت من شعر تكاد بيوته ... إذا كتبت يبيض من نورها الحبر','146','3','0');
INSERT INTO [t754] VALUES (1771,'كأن المعاني في فصاحة لفظها ... نجوم الثريا أو خلائقك الزهر','146','3','0');
INSERT INTO [t754] VALUES (1772,'وجنبني قرب السلاطين مقتها ... وما يقتضيني من جماجمها النسر','146','3','0');
INSERT INTO [t754] VALUES (1773,'فإني رأيت الضر أحسن منظرا ... وأهون من مرأي صغير به كبر','146','3','0');
INSERT INTO [t754] VALUES (1774,'لساني وعيني والفؤاد وهمتي ... أود اللواتي ذا اسمها منك والشطر','146','3','0');
INSERT INTO [t754] VALUES (1775,'وما أنا وحدي قلت ذا الشعر كله ... ولكن لشعري فيك من نفسه شعر','146','3','0');
INSERT INTO [t754] VALUES (1776,'وماذا الذي فيه من الحسن رونقا ... ولكن بدا في وجهه نحوك البشر','147','3','0');
INSERT INTO [t754] VALUES (1777,'وإني ولو نلت السماء لعالم ... بأنك ما نلت الذي يوجب القدر','147','3','0');
INSERT INTO [t754] VALUES (1778,'أزالت بك الأيام عتبي كأنما ... بنوها لها ذنب وأنت لها عذر','147','3','0');
INSERT INTO [t754] VALUES (1779,'ضروب الناس عشاق ضروبا ... فأعذرهم أشفهم حببا','147','3','0');
INSERT INTO [t754] VALUES (1780,'وما سكنى سوى قتل الأعادي ... فهل من زورة تشفى القلوبا','147','3','0');
INSERT INTO [t754] VALUES (1781,'تظل الطير منها في حديث ... ترد به الصراصر والنعيبا','147','3','0');
INSERT INTO [t754] VALUES (1782,'وقد لبست دماءهم عليهم ... حدادا لم تشق لها جيوبا','147','3','0');
INSERT INTO [t754] VALUES (1783,'أدمنا طعنهم والقتل حتى ... خلطنا في عظامهم الكعوبا','147','3','0');
INSERT INTO [t754] VALUES (1784,'كأن خيولنا كانت قديما ... تسقى في قحوفهم الحليبا','147','3','0');
INSERT INTO [t754] VALUES (1785,'فمرت غير نافرة عليهم ... تدوس بنا الجماجم والتريبا','147','3','0');
INSERT INTO [t754] VALUES (1786,'يقدمها وقد خضبت شواها ... فتى ترمي الحروب به الحروبا','147','3','0');
INSERT INTO [t754] VALUES (1787,'شديد الخنزوانة لا يبالي ... أصاب إذا تنمر أم أصيبا','147','3','0');
INSERT INTO [t754] VALUES (1788,'أعزمي طال هذا الليل فانظر ... أمنك الصبح يفرق أن يؤوبا','147','3','0');
INSERT INTO [t754] VALUES (1789,'كأن الفجر حب مستزار ... يراعى من دجنته رقيبا','147','3','0');
INSERT INTO [t754] VALUES (1790,'كان نجومه حلي عليه ... وقد حذيت قوائمه الجبوبا','148','3','0');
INSERT INTO [t754] VALUES (1791,'كأن الجو قاسى ما أقاسي ... فصار سواده فيه شحوبا','148','3','0');
INSERT INTO [t754] VALUES (1792,'كأن دجاه يجذبها سهادي ... فليس تغيب إلا أن يغيبا','148','3','0');
INSERT INTO [t754] VALUES (1793,'أقلب فيه أجفاني كأني ... أعد بها على الدهر الدنوبا','148','3','0');
INSERT INTO [t754] VALUES (1794,'وما ليل بأطول من نهار ... يظل بلحظ حسادي مشوبا','148','3','0');
INSERT INTO [t754] VALUES (1795,'وما موت بأبغض من حيوة ... أرى لهم معي فيها نصيبا','148','3','0');
INSERT INTO [t754] VALUES (1796,'عرفت نوائب الحدثان حتى ... لو انتسبت لكنت لها نقيبا','148','3','0');
INSERT INTO [t754] VALUES (1797,'ولما قلت الإبل امتطينا ... إلى ابن أبي سليمان الخطوبا','148','3','0');
INSERT INTO [t754] VALUES (1798,'مطايا لا تذل لمن عليها ... ولا يبغي لها أحد ركوبا','148','3','0');
INSERT INTO [t754] VALUES (1799,'وترتع دون نبت الأرض فينا ... فما فارقتها إلا جديبا','148','3','0');
INSERT INTO [t754] VALUES (1800,'إلى ذي شيمة شغفت فؤادي ... فلولاه لقلت بها النسيبا','148','3','0');
INSERT INTO [t754] VALUES (1801,'تنازعني هواها كل نفس ... وإن لم تشبه الرشأ الربيبا','148','3','0');
INSERT INTO [t754] VALUES (1802,'عجيب في الزمان وما عجيب ... أتى من آل سيار عجيبا','148','3','0');
INSERT INTO [t754] VALUES (1803,'وشيخ في الشباب وليس شيخا ... سمي كل من بلغ المشيبا','148','3','0');
INSERT INTO [t754] VALUES (1804,'قسا فالأسد تفزع من قواه ... ورق فنحن نفزغ أن يذوبا','148','3','0');
INSERT INTO [t754] VALUES (1805,'أشد من الرياح الهوج بطشا ... وأسرع في الندى منها هبوبا','148','3','0');
INSERT INTO [t754] VALUES (1806,'وقالوا ذاك أرمي من رأينا ... فقلت رأيتهم الغرض القريبا','148','3','0');
INSERT INTO [t754] VALUES (1807,'وهل يخطى بأسهمه الرمايا ... وما يخطى بما ظن الغيوبا','148','3','0');
INSERT INTO [t754] VALUES (1808,'إذا نكبت كنانته استبنا ... بأنصلها لأنصلها ندوبا','149','3','0');
INSERT INTO [t754] VALUES (1809,'يصيب ببعضها أفواق بعض ... فلولا الكسر لاتصلت قضيبا','149','3','0');
INSERT INTO [t754] VALUES (1810,'يريك النزع بين القوس منه ... وبين رميه الهدف اللهيبا','149','3','0');
INSERT INTO [t754] VALUES (1811,'ألست ابن الأولى سعدوا وسادوا ... ولم يلدوا امرأ إلا نجيبا','149','3','0');
INSERT INTO [t754] VALUES (1812,'ونالوا ما اشتهوا بالحزم هونا ... وصاد الوحش نملهم دبيبا','149','3','0');
INSERT INTO [t754] VALUES (1813,'وما ريح الرياض لها ولكن ... كساها دفنهم في الترب طيبا','149','3','0');
INSERT INTO [t754] VALUES (1814,'أيا من عاد روح المجد فيه ... وصار زمانه البالي فتشيبا','149','3','0');
INSERT INTO [t754] VALUES (1815,'تيممني وكيلك مادحا لي ... وأنشدني من الشعر الغريبا','149','3','0');
INSERT INTO [t754] VALUES (1816,'فآجرك الإله على عليل ... بعثت إلى المسيح به طبيبا','149','3','0');
INSERT INTO [t754] VALUES (1817,'ولست بمنكر منك الهدايا ... ولكن زدتني فيها أديبا','149','3','0');
INSERT INTO [t754] VALUES (1818,'فلا زالت ديارك مشرقات ... ولا دانيت يا شمس الغروبا','149','3','0');
INSERT INTO [t754] VALUES (1819,'لأصبح آمنا فيك الرزايا ... كما أنا آمن فيك العيوبا','149','3','0');
INSERT INTO [t754] VALUES (1820,'أقل فعالي بله أكثره مجد ... وذا الجد فيه نلت أو لم أنل جد','149','3','0');
INSERT INTO [t754] VALUES (1821,'سأطلب حقي بالقنا ومشائخ ... كأنهم من طول ما التثموا مرد','150','3','0');
INSERT INTO [t754] VALUES (1822,'ثقال إذا لاقوا خفاف إذا دعوا ... كثير إذا شدوا قليل إذا عدوا','150','3','0');
INSERT INTO [t754] VALUES (1823,'وطعن كأن الطعن لا طعن عنده ... وضرب كأن النار من حره برد','150','3','0');
INSERT INTO [t754] VALUES (1824,'إذا شئت حفت بي على كل سابح ... رجال كأن الموت في فمها شهد','150','3','0');
INSERT INTO [t754] VALUES (1825,'أذم إلى هذا الزمان أهيله ... فأعلمهم فدم وأحزمهم وغد','150','3','0');
INSERT INTO [t754] VALUES (1826,'وأكرمهم كلب وأبصرهم عم ... وأسهدهم فهد وأشجعهم قرد','150','3','0');
INSERT INTO [t754] VALUES (1827,'ومن نكد الدنيا على الحر أن يرى ... عدوا له ما من صداقته بد','150','3','0');
INSERT INTO [t754] VALUES (1828,'فيا نكد الدنيا متى أنت مقصر ... عن الحر حتى لا يكون له ضد','150','3','0');
INSERT INTO [t754] VALUES (1829,'يروح ويغدو كارها لوصاله ... وتضطره الأيام والزمن النكد','150','3','0');
INSERT INTO [t754] VALUES (1830,'بقلبي وإن لم أرو منها ملالة ... وبي عن غوانيها وإن وصلت صد','150','3','0');
INSERT INTO [t754] VALUES (1831,'خليلاي دون الناس حزن وعبرة ... على فقد من أحببت ما لهما فقد','151','3','0');
INSERT INTO [t754] VALUES (1832,'تلج دموعي بالجفون كأنما ... جفوني لعيني كل باكية خد','151','3','0');
INSERT INTO [t754] VALUES (1833,'وأني لتفهيني من الماء بقية ... وأصبر عنه ما تصبر الربد','151','3','0');
INSERT INTO [t754] VALUES (1834,'وأمضى كما يمضي السنان لطيتي ... وأطوي كما تطوى المجلحة العقد','151','3','0');
INSERT INTO [t754] VALUES (1835,'وأكبر نفسي عن جزاء بغيبة ... وكل اغتياب جهد من ما له جهد','151','3','0');
INSERT INTO [t754] VALUES (1836,'وأرحم أقواما من العي والغبي ... وأعذر في بغضي لأنهم ضد','151','3','0');
INSERT INTO [t754] VALUES (1837,'ويمنعني ممن سوى ابن محمد ... أياد له عندي يضيق بها عند','151','3','0');
INSERT INTO [t754] VALUES (1838,'توالي بلا وعد لكن قبلها ... شمائله من غير وعد بها وعد','151','3','0');
INSERT INTO [t754] VALUES (1839,'سرى السيف مما تطبع الهند صاحبي ... إلى السيف مما يطبع الله لا الهند','151','3','0');
INSERT INTO [t754] VALUES (1840,'فلما رآني مقبلا هز نفسه ... إلى حسام كل صفح له حد','151','3','0');
INSERT INTO [t754] VALUES (1841,'فلم أر قبلي من مشي البحر نحوه ... ولا رجلا قامت تعانقه الأسد','151','3','0');
INSERT INTO [t754] VALUES (1842,'كان القسي العاصيات تطيعه ... هوى أو بها في غير أنمله زهد','151','3','0');
INSERT INTO [t754] VALUES (1843,'يكاد يصيب الشيء من قبل رميه ... ويمكنه في سهمه المرسل الرد','151','3','0');
INSERT INTO [t754] VALUES (1844,'وينفذه في العقد وهو مضيق ... من الشعرة السوداء والليل مسود','152','3','0');
INSERT INTO [t754] VALUES (1845,'بنفسي الذي لا يزدهي بخديعة ... وإن كثرت فيه الذرائع والقصد','152','3','0');
INSERT INTO [t754] VALUES (1846,'ومن بعده فقر ومن قربه غني ... ومن عرضه حر ومن ماله عبد','152','3','0');
INSERT INTO [t754] VALUES (1847,'ويصطنع المعروف مبتدئا به ... ويمنعه من كل من ذمه حمد','152','3','0');
INSERT INTO [t754] VALUES (1848,'ويمقر المساء عن ذكره لهم ... كأنهم في الخلق ما خلقوا بعد','152','3','0');
INSERT INTO [t754] VALUES (1849,'وتأمنه الأعداء من غير ذلة ... ولكن على قدر الذي يذنب الحقد','152','3','0');
INSERT INTO [t754] VALUES (1850,'فإن يك سيار بن مكرم أنقضى ... فإنك ماء الورد أن ذهب الورد','152','3','0');
INSERT INTO [t754] VALUES (1851,'مضى وبنوه وانفردت بفضلهم ... وألف إذا ما جمعت واحدا فرد','152','3','0');
INSERT INTO [t754] VALUES (1852,'لهم أوجه غر وأيد كريمة ... ومعرفة عد وألسنة لد','152','3','0');
INSERT INTO [t754] VALUES (1853,'وأردية خضر وملك مطاعة ... ومركوزة سمر ومقربة جرد','152','3','0');
INSERT INTO [t754] VALUES (1854,'وما عشت ما ماتوا ولا أبواهم ... تميم بن مر وابن طابخة أد','152','3','0');
INSERT INTO [t754] VALUES (1855,'فبعض الذي يبدو الذي أنا ذاكر ... وبعض الذي يخفى علي الذي يبدو','153','3','0');
INSERT INTO [t754] VALUES (1856,'الوم به من لامني في وداده ... وحق لخير الخلق من خيره الود','153','3','0');
INSERT INTO [t754] VALUES (1857,'كذا فتنحوا عن علي وطرقه ... بنى اللؤم حتى يعبر الملك الجعد','153','3','0');
INSERT INTO [t754] VALUES (1858,'فما في سجاياكم منازعة العلي ... ولا في طباع التربة المسك والند','153','3','0');
INSERT INTO [t754] VALUES (1859,'أما الفراق فإنه ما أعهد ... هو توأمي لو أن بينا يولد','153','3','0');
INSERT INTO [t754] VALUES (1860,'ولقد علمنا أننا سنطيعه ... لما علمنا أننا لا نخلد','153','3','0');
INSERT INTO [t754] VALUES (1861,'وإذا الجياد أبا البهي نقلننا ... عنكم فأردأ ما ركبت الأجود','153','3','0');
INSERT INTO [t754] VALUES (1862,'من خص بالذم الفراق فإنني ... من لا يرى في الدهر شيئا يحمد','153','3','0');
INSERT INTO [t754] VALUES (1863,'كفرندي فرند سيفي الجراز ... لذة العين عدة للبراز','153','3','0');
INSERT INTO [t754] VALUES (1864,'تحسب الماء خط في لهب النا ... ر أدق الخطوط في الأحراز','153','3','0');
INSERT INTO [t754] VALUES (1865,'كلما رمت لونه منع النا ... ظر موج كأنه منك هازي','153','3','0');
INSERT INTO [t754] VALUES (1866,'ودقيق قذى الهباء أنيق ... متوال في مستو هزهاز','153','3','0');
INSERT INTO [t754] VALUES (1867,'ورد الماء فالجوانب قدرا ... شربت والتي تليها جوازي','153','3','0');
INSERT INTO [t754] VALUES (1868,'حملته حمائل الدهر حتى ... وهي محتاجة إلى خراز','154','3','0');
INSERT INTO [t754] VALUES (1869,'فهو لا تلحق الدماء غراري ... هـ ولا عرض منتضيه المخازي','154','3','0');
INSERT INTO [t754] VALUES (1870,'يا مزيل الظلام عني وروضي ... يوم شربي ومعقلي في البراز','154','3','0');
INSERT INTO [t754] VALUES (1871,'واليماني الذي لو أسطعت كانت ... مقلتي غمده من الإعزاز','154','3','0');
INSERT INTO [t754] VALUES (1872,'إن برقي إذا برقت فعالي ... وصليل إذا صللت ارتجازي','154','3','0');
INSERT INTO [t754] VALUES (1873,'ولم أحملك معلماهكذا إلا ... لضرب الرقاب والأجواز','154','3','0');
INSERT INTO [t754] VALUES (1874,'ولقطعي بك الحديد عليها ... فكلانا لجنسه اليوم غازي','154','3','0');
INSERT INTO [t754] VALUES (1875,'سله الركض بعد وهن بنجد ... فتصدى للغيث أهل الحجاز','154','3','0');
INSERT INTO [t754] VALUES (1876,'فتمنيت مثله فكأني ... طالب لابن صالح من يوازي','154','3','0');
INSERT INTO [t754] VALUES (1877,'ليس كل السراة بالروذبار ... ي ولا كل ما يطير ببازي','154','3','0');
INSERT INTO [t754] VALUES (1878,'فارسي له من المجد تاج ... كان من جوهر على أبرواز','154','3','0');
INSERT INTO [t754] VALUES (1879,'نفسه فوق كل أصل شريف ... ولو أني له إلى الشمس عازي','154','3','0');
INSERT INTO [t754] VALUES (1880,'شغلت قلبه حسان المعالي ... عن حسان الوجوه والأعجاز','154','3','0');
INSERT INTO [t754] VALUES (1881,'وكأن الفريد والدر واليا ... قوت من لفظه وسام الركاز','154','3','0');
INSERT INTO [t754] VALUES (1882,'تقضم الجمر والحديد الأعادي ... دونه قضم سكر الأهواز','154','3','0');
INSERT INTO [t754] VALUES (1883,'بلغته البلاغة الجهد بالعف ... وونال الإسهاب بالإيجاز','154','3','0');
INSERT INTO [t754] VALUES (1884,'حامل الحرب والديات عن القو ... م وثقل الديون والأعواز','154','3','0');
INSERT INTO [t754] VALUES (1885,'كيف لا يشتكي وكيف تشكوا ... وبه لا بمن شكاها المرازي','154','3','0');
INSERT INTO [t754] VALUES (1886,'أيها الواسع الفناء وما في ... هـ مبيت لمالك المجتاز','155','3','0');
INSERT INTO [t754] VALUES (1887,'بك أضحى شبا السنة عندي ... كشبا أسوق الجراد النوازي','155','3','0');
INSERT INTO [t754] VALUES (1888,'وأنثنى عني الرديني حتى ... دار دور الحروف في هواز','155','3','0');
INSERT INTO [t754] VALUES (1889,'وبآباءك الكرام التأسي ... والتسلي عمن مضى والتعازي','155','3','0');
INSERT INTO [t754] VALUES (1890,'تركوا الأرض بعدما ذللوها ... ومشت تحتهم بلا مهماز','155','3','0');
INSERT INTO [t754] VALUES (1891,'واطاعتهم الجيوش وهيبوا ... فكلام الورى لهم كالنحاز','155','3','0');
INSERT INTO [t754] VALUES (1892,'وهجان على هجان تأيي ... ك عديد الحبوب في الأقواز','155','3','0');
INSERT INTO [t754] VALUES (1893,'صفها السير في العراء فكانت ... فوق مثل الملاء مثل الطراز','155','3','0');
INSERT INTO [t754] VALUES (1894,'وحكى في اللحوم فعلك في الوف ... ر فأودى بالعنتريس الكناز','155','3','0');
INSERT INTO [t754] VALUES (1895,'كلما جادت الظنون بوعد ... عنك جادت يداك بالإنجاز','155','3','0');
INSERT INTO [t754] VALUES (1896,'ملك منشد القريض لديه ... وأضع الثوب في يدي بزاز','155','3','0');
INSERT INTO [t754] VALUES (1897,'ولنا القول وهو أدرى بفح ... واه وأهدى فيه إلى الإعجاز','155','3','0');
INSERT INTO [t754] VALUES (1898,'ومن الناس من يجوز عليه ... شعراء كأنها الخازباز','155','3','0');
INSERT INTO [t754] VALUES (1899,'ويرى أنه البصير بهذا ... وهو في العمى ضائع العكاز','156','3','0');
INSERT INTO [t754] VALUES (1900,'كل شعر نظير قائله في ... ك وعقل المجيز مثل المجاز','156','3','0');
INSERT INTO [t754] VALUES (1901,'أماتكم من قبل موتكم الجهل ... وجركم من خفة بكم النمل','156','3','0');
INSERT INTO [t754] VALUES (1902,'وليد أبي الطيب الكلب ما لكم ... فطنتم إلى الدعوى وليس لكم عقل','156','3','0');
INSERT INTO [t754] VALUES (1903,'ولو ضربتكم منجنيقي وأصلكم ... قوي لهدتكم فكيف ولا أصل','156','3','0');
INSERT INTO [t754] VALUES (1904,'ولو كنتم ممن يدبر أمره ... لما كنتم نسل الذي ما له نسل','156','3','0');
INSERT INTO [t754] VALUES (1905,'لقد حازني وجد بمن حازه بعد ... فيا ليتني بعد ويا ليته وجد','156','3','0');
INSERT INTO [t754] VALUES (1906,'أسر بتجديد الهوى ذكر ما مضى ... وإن كان لا يبقى له الحجر الصلد','156','3','0');
INSERT INTO [t754] VALUES (1907,'سهاد أتانا منك في العين عندنا ... رقاد وقلام رعى سربكم ورد','156','3','0');
INSERT INTO [t754] VALUES (1908,'ممثلة حتى كأن لم تفارقي ... وحتى كأن اليأس من وصلك الوعد','156','3','0');
INSERT INTO [t754] VALUES (1909,'وحتى تكادي تمسحين مدامعي ... ويعبق في ثوبي من ريحك الند','156','3','0');
INSERT INTO [t754] VALUES (1910,'إذا غدرت حسناء أوفت بعهدها ... ومن عهدها أن لا يدوم لها عهد','156','3','0');
INSERT INTO [t754] VALUES (1911,'وإن عشقت كانت أشد صبابة ... وإن فركت فأذهب فما فركها قصد','156','3','0');
INSERT INTO [t754] VALUES (1912,'وإن حقدت لم يبق في قلبها رضى ... وإن رضيت لم يبق في قلبها حقد','156','3','0');
INSERT INTO [t754] VALUES (1913,'كذلك أخلاق النساء وربما ... يضل بها الهادي ويخفى بها الرشد','156','3','0');
INSERT INTO [t754] VALUES (1914,'ولكن حبا خامر القلب في الصبي ... يزيد على مر الزمان ويشتد','157','3','0');
INSERT INTO [t754] VALUES (1915,'سقى ابن علي كل مزن سقتكم ... مكافأة يغدو إليها كما تغدو','157','3','0');
INSERT INTO [t754] VALUES (1916,'لتروي كما تروى بلادا سكنتها ... وينبت فيها فوقك الفخر والمجد','157','3','0');
INSERT INTO [t754] VALUES (1917,'بمن تشخص الأبصار يوم ركوبه ... ويخرق من زحم على الرجل البرد','157','3','0');
INSERT INTO [t754] VALUES (1918,'وتلقى وما تدري البنان سلاحها ... لكثرة إيماء إليه إذا يبدو','157','3','0');
INSERT INTO [t754] VALUES (1919,'ضروب لهام الضاربي الهام في الوغى ... خفيف إذا ما أثقل الفرس اللبد','157','3','0');
INSERT INTO [t754] VALUES (1920,'بصير بأخذ الحمد في كل موضع ... ولو خبأته بين أنيابها الأسد','157','3','0');
INSERT INTO [t754] VALUES (1921,'بتأميله يغني الفتى قبل نيله ... وبالذعر من قبل المهند ينقد','157','3','0');
INSERT INTO [t754] VALUES (1922,'وسيفي لأنت السيف لا ما تسله ... لضرب ومما السيف منه لك الغمد','157','3','0');
INSERT INTO [t754] VALUES (1923,'ورمحي لأنت الرمح لا ما تبله ... نجيعا ولولا القدح لم يثقب الزند','157','3','0');
INSERT INTO [t754] VALUES (1924,'من القاسمين الشكر بيني وبينهم ... لأنهم يسدى إليهم بأن يسدوا','157','3','0');
INSERT INTO [t754] VALUES (1925,'فشكري لهم شكران شكر على الندى ... وشكر على الشكر الذي وهبوا بعد','157','3','0');
INSERT INTO [t754] VALUES (1926,'صيام بأبواب القباب جيادهم ... وأشخاصها في قلب خائفهم تعدوا','157','3','0');
INSERT INTO [t754] VALUES (1927,'وأنفسهم مبذولة لوفودهم ... وأموالهم في دار من لم يفد وفد','158','3','0');
INSERT INTO [t754] VALUES (1928,'كأن عطيات الحسين عساكر ... ففيها العبدي والمطهمة الجرد','158','3','0');
INSERT INTO [t754] VALUES (1929,'أرى القمر ابن الشمس قد لبس العلي ... رويدك حتى يلبس الشعر الخد','158','3','0');
INSERT INTO [t754] VALUES (1930,'وغال فضول الدرع من جنباتها ... على بدن قد القناة له قد','158','3','0');
INSERT INTO [t754] VALUES (1931,'وباشر ابكار المكارم أمردا ... وكان ندا آباؤه وهم مرد','158','3','0');
INSERT INTO [t754] VALUES (1932,'مدحت أباه قبله فشفى يدي ... من العدم من تشفى به الأعين الرمد','158','3','0');
INSERT INTO [t754] VALUES (1933,'حباني بأثمان السوابق دونها ... مخافة سيري أنها للنوى جند','158','3','0');
INSERT INTO [t754] VALUES (1934,'وشهوة عود إن جود يمينه ... ثناء ثناء والجواد بها فرد','158','3','0');
INSERT INTO [t754] VALUES (1935,'فلا زلت ألقي الحاسدين بمثلها ... وفي يدهم غيظ وفي يدي الرفد','158','3','0');
INSERT INTO [t754] VALUES (1936,'وعندي قباطي الهمام وماله ... وعندهم مما ظفرت به الجحد','158','3','0');
INSERT INTO [t754] VALUES (1937,'يرومون شأوى في الكلام وإنما ... يحاكي الفتى في ما خلا المنطق القرد','158','3','0');
INSERT INTO [t754] VALUES (1938,'فهم في جموع لا يراها ابن دأية ... وهم في ضجيج لا يحس به الخلد','158','3','0');
INSERT INTO [t754] VALUES (1939,'ومني استفاد الناس كل غريبة ... فجازوا بترك الذم إن لم يكن حمد','158','3','0');
INSERT INTO [t754] VALUES (1940,'وجدت عليا وابنه خير قومه ... وهم خير قوم واستوى الحر والعبد','159','3','0');
INSERT INTO [t754] VALUES (1941,'وأصبح شعري منهما في مكانه ... وفي عنق الحسناء يستحسن العقد','159','3','0');
INSERT INTO [t754] VALUES (1942,'أنا لائمي إن كنت وقت اللوائم ... علمت بما بي بين تلك المعالم','159','3','0');
INSERT INTO [t754] VALUES (1943,'ولكنني مما شدهت متيم ... كسال وقلبي بائح مثل كاتم','159','3','0');
INSERT INTO [t754] VALUES (1944,'وقفنا كأنا كل وجد قلوبنا ... تمكن من أذوادنا في القوائم','159','3','0');
INSERT INTO [t754] VALUES (1945,'ودسنا بأخفاف المطي ترابها ... فلا زلت استشفي بلثم المناسم','159','3','0');
INSERT INTO [t754] VALUES (1946,'ديار اللواتي دارهن عزيزة ... بطول القنا يحفظن لا بالتمائم','159','3','0');
INSERT INTO [t754] VALUES (1947,'حسان التثني ينقش الوشى مثله ... إذا مسن في اجسامهن النواعم','159','3','0');
INSERT INTO [t754] VALUES (1948,'ويبسمن عن جر تقلدن مثله ... كأن التراقي وشحت بالمباسم','159','3','0');
INSERT INTO [t754] VALUES (1949,'فما لي وللدنيا طلابي نجومها ... ومسعاي منها في شدوق الأراقم','159','3','0');
INSERT INTO [t754] VALUES (1950,'من الحلم أن تستعمل الجهل دونه ... إذا اتسعت في الحلم طرق المظالم','160','3','0');
INSERT INTO [t754] VALUES (1951,'وأن ترد الماء الذي شطره دم ... فتسقي إذا لم يسق من لم يزاحم','160','3','0');
INSERT INTO [t754] VALUES (1952,'ومن عرف الأيام معرفتي بها ... وبالناس روى رمحه غير راحم','160','3','0');
INSERT INTO [t754] VALUES (1953,'فليس بمرحوم إذا ظفروا به ... ولا في الردى الجاري عليهم بآثم','160','3','0');
INSERT INTO [t754] VALUES (1954,'إذا صلت لم أترك مصالا لفاتك ... وإن قلت لم أترك مقالا لعالم','160','3','0');
INSERT INTO [t754] VALUES (1955,'وإلا فخانتني القوافي وعاقني ... عن ابن عبيد الله ضعف العزائم','160','3','0');
INSERT INTO [t754] VALUES (1956,'عن المقتني بذل التلاد تلاده ... ومجتنب البخل اجتناب المحارم','160','3','0');
INSERT INTO [t754] VALUES (1957,'تمنى أعاديه محل عفاته ... وتحسد كفيه ثقال الغمائم','160','3','0');
INSERT INTO [t754] VALUES (1958,'ولا يتلقى الحرب إلا بمهجة ... معظمة مذخورة للعظائم','160','3','0');
INSERT INTO [t754] VALUES (1959,'وذي لجب لا ذو الجناح أمامه ... بناج ولا الوحش المثار بسالم','160','3','0');
INSERT INTO [t754] VALUES (1960,'تمر عليه الشمس وهي ضعيفة ... تطالعه من بين ريش القشاعم','160','3','0');
INSERT INTO [t754] VALUES (1961,'إذا ضوءها لاقى من الطير فرجة ... تدور فوق البيض مثل الدراهم','160','3','0');
INSERT INTO [t754] VALUES (1962,'ويخفى عليك الرعد والبرق فوقه ... من اللمع في حافاته والهماهم','160','3','0');
INSERT INTO [t754] VALUES (1963,'أرى دون ما بين الفرات وبرقة ... ضربا يمشي الخيل فوق الجماجم','160','3','0');
INSERT INTO [t754] VALUES (1964,'وطعن غطاريف كأن أكفهم ... عرفن الردينيات قبل المعاصم','160','3','0');
INSERT INTO [t754] VALUES (1965,'حمته على الأعداء من كل جانب ... سيوف بني طغج بن جف القماقم','160','3','0');
INSERT INTO [t754] VALUES (1966,'هم المحسنون الكر في حومة الوغى ... وأحسن منه كرهم في المكارم','161','3','0');
INSERT INTO [t754] VALUES (1967,'وهم يحسنون العفو عن كل مذنب ... ويحتملون الغرم عن كل غارم','161','3','0');
INSERT INTO [t754] VALUES (1968,'حييون إلا أنهم في نزالهم ... أقل حياء من شفار الصوارم','161','3','0');
INSERT INTO [t754] VALUES (1969,'ولولا احتقار الأسد شبهتها بهم ... ولكنها معدودة في البهائم','161','3','0');
INSERT INTO [t754] VALUES (1970,'سرى النوم عني في سراي إلى الذي ... صنائعه تسري إلى كل نائم','161','3','0');
INSERT INTO [t754] VALUES (1971,'إلى مطلق الأسرى ومختوم العدي ... ومشكى ذوي الشكوى ورغم المراغم','161','3','0');
INSERT INTO [t754] VALUES (1972,'كريم نفضت الناس لما بلغته ... كأنهم ما جف من زاد قادم','161','3','0');
INSERT INTO [t754] VALUES (1973,'وكاد سروري لا يفي بندامتي ... على تركه في عمري المتقادم','161','3','0');
INSERT INTO [t754] VALUES (1974,'وفارقت شر الأرض أهلا وتربة ... بها علوي جده غير هاشم','161','3','0');
INSERT INTO [t754] VALUES (1975,'بلى الله حساد الأمير بحلمه ... وأجلسه منهم مكان العمائم','161','3','0');
INSERT INTO [t754] VALUES (1976,'فإن لهم في سرعة الموت راحة ... وإن لهم في العيش حز الغلاصم','161','3','0');
INSERT INTO [t754] VALUES (1977,'كأنك ما جاودت من بان جوده ... عليك ولا قاتلت من لم تقاوم','161','3','0');
INSERT INTO [t754] VALUES (1978,'سقاني الخمر قولك لي بحقي ... وود لم تشبه لي بمذق','161','3','0');
INSERT INTO [t754] VALUES (1979,'يمينا لو حلفت وأنت تأتي ... على قتلي بها لضربت عنقي','161','3','0');
INSERT INTO [t754] VALUES (1980,'حييت من قسم وأفدى المقسما ... أمسى الأنام له مجلا معظما','161','3','0');
INSERT INTO [t754] VALUES (1981,'وإذا طلبت رضا الأمير بشربها ... وأخذتها فلقد تركت الأحرما','161','3','0');
INSERT INTO [t754] VALUES (1982,'ماذا يقول الذي يغني ... يا خير من تحت ذي السماء','161','3','0');
INSERT INTO [t754] VALUES (1983,'شغلت قلبي بلحظ عيني ... إليك عن حسن ذا الغناء','161','3','0');
INSERT INTO [t754] VALUES (1984,'أرى مرهفا مدهش الصيقلين ... وبابة كل غلام عتا','161','3','0');
INSERT INTO [t754] VALUES (1985,'أتأذن لي ولك السابقات ... أجربه لك في ذا الفتى','161','3','0');
INSERT INTO [t754] VALUES (1986,'يقاتلني عليك الليل جدا ... ومنصرفي له أمضى السلاح','161','3','0');
INSERT INTO [t754] VALUES (1987,'لأني كلما فارقت طرفي ... بعيد بين جفني والصباح','161','3','0');
INSERT INTO [t754] VALUES (1988,'وزيارة عن غير موعد ... كالغمض في الجفن المسهد','162','3','0');
INSERT INTO [t754] VALUES (1989,'معجت بنا فيها الجيا ... د مع الأمير أبي محمد','162','3','0');
INSERT INTO [t754] VALUES (1990,'حتى دخلنا جنة ... لو أن ساكنها مخلد','162','3','0');
INSERT INTO [t754] VALUES (1991,'خضراء حمراء الترا ... ب كأنها في خد أغيد','162','3','0');
INSERT INTO [t754] VALUES (1992,'أحببت تشبيها لها ... فوجدته ما ليس يوجد','162','3','0');
INSERT INTO [t754] VALUES (1993,'وإذا رجعت إلى الحقا ... ئق فهي واحدة لأوحد','162','3','0');
INSERT INTO [t754] VALUES (1994,'ووقت وفى بالدهر لي عند واحد ... وفي لي بأهليه وزاد كثيرا','162','3','0');
INSERT INTO [t754] VALUES (1995,'شربت على استحسان ضوء جبينه ... وزهر ترى للماء فيه خريرا','162','3','0');
INSERT INTO [t754] VALUES (1996,'غدا الناس مثليهم به لا عدمته ... وأصبح دهري في ذراه دهورا','162','3','0');
INSERT INTO [t754] VALUES (1997,'المجلسان على التمييز بينهما ... مقابلان ولكن أحسنا الأدبا','162','3','0');
INSERT INTO [t754] VALUES (1998,'إذا صعدت إلى ذا مال ذا رهبا ... وإن صعدت إلى ذا مال ذا رهبا','162','3','0');
INSERT INTO [t754] VALUES (1999,'فلم يهابك ما لا حس يردعه ... أني لأبصر من فعليهما عجبا','162','3','0');
INSERT INTO [t754] VALUES (2000,'زال النهار ونور منك يوهمنا ... أن لم يزل ولجنح الليل إجنان','162','3','0');
INSERT INTO [t754] VALUES (2001,'وإن يكن طلب البستان يمسكنا ... فرح فكل مكان مكن بستان','162','3','0');
INSERT INTO [t754] VALUES (2002,'أنشر الكباء ووجه الأمير ... وحسن الغناء وصافي الخمور','162','3','0');
INSERT INTO [t754] VALUES (2003,'فداو خماري بشربي لها ... فإني سكرت بشرب السرور','162','3','0');
INSERT INTO [t754] VALUES (2004,'تعرض لي السحاب وقد قفلنا ... فقلت إليك إن معي السحابا','162','3','0');
INSERT INTO [t754] VALUES (2005,'فشم في القبة الملك المرجى ... فأمسك بعد ما عزم انسكابا','162','3','0');
INSERT INTO [t754] VALUES (2006,'الطيب مما غنيت عنه ... كفى بقرب الأمير طيبا','162','3','0');
INSERT INTO [t754] VALUES (2007,'يبني به ربنا المعالي ... كما بكم يغفر الذنوبا','162','3','0');
INSERT INTO [t754] VALUES (2008,'يا أكرم الناس في الفعال ... وأفصح الناس في المقال','163','3','0');
INSERT INTO [t754] VALUES (2009,'إن قلت في ذا البخور سوقا ... فهكذا قلت في النوال','163','3','0');
INSERT INTO [t754] VALUES (2010,'غير مستنكر لك الإقدام ... فلمن ذا الحديث والإعلام','163','3','0');
INSERT INTO [t754] VALUES (2011,'قد علمنا من قبل أنك من لا ... يمنع الليل همه والغمام','163','3','0');
INSERT INTO [t754] VALUES (2012,'قد بلغت الذي أردت من الب ... ر ومن حق ذا الشريف عليكا','163','3','0');
INSERT INTO [t754] VALUES (2013,'وإذا لم تسر إلى الدار في وقت ... ك ذا خفت أن تسير إليكا','163','3','0');
INSERT INTO [t754] VALUES (2014,'يا من رأيت الحليم وغدا ... به وحر الملوك عبدا','163','3','0');
INSERT INTO [t754] VALUES (2015,'مال على الشراب جدا ... وأنت للمكرمات أهدا','163','3','0');
INSERT INTO [t754] VALUES (2016,'فإن تفضلت بانصرافي ... عددته من لديك رفدا','163','3','0');
INSERT INTO [t754] VALUES (2017,'لا تلومن اليهودي على ... أن يرى الشمس فلا ينكرها','163','3','0');
INSERT INTO [t754] VALUES (2018,'إنما اللوم على حاسبها ... ظلمة من بعد ما يبصرها','163','3','0');
INSERT INTO [t754] VALUES (2019,'إنما أحفظ المديح بعيني ... لا بلقبي لما أرى في الأمير','163','3','0');
INSERT INTO [t754] VALUES (2020,'من خصال إذا نظرت إليها ... نظمت لي غرائب المنثور','163','3','0');
INSERT INTO [t754] VALUES (2021,'أباعث كل مكرمة طموح ... وفارس كل سلهبة سبوح','163','3','0');
INSERT INTO [t754] VALUES (2022,'وطاعن كل نجلاء غموس ... وعاصى كل عذال نصيح','163','3','0');
INSERT INTO [t754] VALUES (2023,'سقاني الله قبل الموت يوما ... دم الأعداء من جوف الجروح','163','3','0');
INSERT INTO [t754] VALUES (2024,'أمن كل شيء بلغت المرادا ... وفي كل شأو شأوت العبادا','163','3','0');
INSERT INTO [t754] VALUES (2025,'فماذا تركت لمن لم يسد ... وماذا تركت لمن كان سادا','163','3','0');
INSERT INTO [t754] VALUES (2026,'كأن السماني إذا ما رأتك ... تصيدها تشتهي أن تصادا','163','3','0');
INSERT INTO [t754] VALUES (2027,'وشامخ من الجبال أقود ... فرد كيافوخ البعير الأصيد','163','3','0');
INSERT INTO [t754] VALUES (2028,'يسار من مضيقه والجلمد ... في مثله متن المسد المعقد','163','3','0');
INSERT INTO [t754] VALUES (2029,'زرناه للأمر الذي لم يعهد ... للصيد والنزهة والتمرد','163','3','0');
INSERT INTO [t754] VALUES (2030,'بكل مسقى الدماء أسود ... معاود مقود مقلد','163','3','0');
INSERT INTO [t754] VALUES (2031,'بكل ناب ذرب محدد ... على حفافي حنكك كالمبرد','163','3','0');
INSERT INTO [t754] VALUES (2032,'كطالب الثار وإن لم يحقد ... يقتل ما يقتله ولا يدى','163','3','0');
INSERT INTO [t754] VALUES (2033,'ينشد من ذا الخشف ما لم يفقد ... فثار من أخضر ممطور ندى','164','3','0');
INSERT INTO [t754] VALUES (2034,'فلم يكد إلا لحتف يهتدي ... ولم يقع إلا على بطن يد','164','3','0');
INSERT INTO [t754] VALUES (2035,'ولم يدع للشاعر المجود ... وصفا له عند الأمير الأمجد','164','3','0');
INSERT INTO [t754] VALUES (2036,'الملك القرم أبي محمد ... القانص الأبطال بالمهند','164','3','0');
INSERT INTO [t754] VALUES (2037,'ذي النعم الغر البوادي العود ... إذا أردت عدها لم أعدد','164','3','0');
INSERT INTO [t754] VALUES (2038,'أيا ما أحيسنها مقلة ... ولولا الملاحة لم أعجب','164','3','0');
INSERT INTO [t754] VALUES (2039,'خلوقية في خلوقيها ... سويداء من عنب الثعلب','164','3','0');
INSERT INTO [t754] VALUES (2040,'إذا نظر الباز في عطفه ... كسته شعاعا على المنكب','164','3','0');
INSERT INTO [t754] VALUES (2041,'ترك مديحك كالهجاء لنفسي ... وقليل لكل المديح الكثير','164','3','0');
INSERT INTO [t754] VALUES (2042,'غير أني تركت مقتضب الشع ... ر لأمر مثلي به معذور','164','3','0');
INSERT INTO [t754] VALUES (2043,'وسجاياك مادحاتك لا لف ... ظي وجود على كلامي يغير','164','3','0');
INSERT INTO [t754] VALUES (2044,'فسقى الله من أحب بكفي ... ك وأسقاك أيهذا الأمير','164','3','0');
INSERT INTO [t754] VALUES (2045,'ماذا الوداع وداع المواق الكمد ... هذا الوداع وداع الروح للجسد','164','3','0');
INSERT INTO [t754] VALUES (2046,'إذا السحاب زفته الريح مرتفعا ... فلا عدا الرملة البيضاء من بلد','164','3','0');
INSERT INTO [t754] VALUES (2047,'ويا فراق الأمير الرحب منزله ... إن أنت فارقتنا يوما فلا تعد','164','3','0');
INSERT INTO [t754] VALUES (2048,'أعيدوا صباحي فهو عند الكواعب ... وردوا رقادي فهو لحظ الحبائب','164','3','0');
INSERT INTO [t754] VALUES (2049,'فإن نهاري ليلة مدلهمة ... على مقلة من فقدكم في غياهب','164','3','0');
INSERT INTO [t754] VALUES (2050,'بعيدة ما بين الجفون كأنما ... عقدتم أعالي كل هدب بحاجب','164','3','0');
INSERT INTO [t754] VALUES (2051,'وأحسب أني لو هويت فراقكم ... لفارقته والدهر أخبث صاحب','165','3','0');
INSERT INTO [t754] VALUES (2052,'فيا ليت ما بيني وبين أحبتي ... من البعد ما بيني وبين المصائب','165','3','0');
INSERT INTO [t754] VALUES (2053,'أراك ظننت السلك جسمي فعقته ... عليك بدر عن لقاء الترائب','165','3','0');
INSERT INTO [t754] VALUES (2054,'ولو قلم القيت في شق رأسه ... من السقم ما غيرت في خط كاتب','165','3','0');
INSERT INTO [t754] VALUES (2055,'تخوفني دون الذي أمرت به ... ولم تدر أن العار شر العواقب','165','3','0');
INSERT INTO [t754] VALUES (2056,'ولابد من يوم أغر محجل ... يطول استماعي بعده للنوادب','165','3','0');
INSERT INTO [t754] VALUES (2057,'يهون على مثلي إذا رام حاجة ... وقوع العوالي دونها والقواضب','165','3','0');
INSERT INTO [t754] VALUES (2058,'كثير حياة المرء مثل قليلها ... يزول وباقي عيشه مثال ذاهب','165','3','0');
INSERT INTO [t754] VALUES (2059,'إليك فإني لست ممن إذا أتقى ... عضاض الأفاعي نام فوق العقارب','165','3','0');
INSERT INTO [t754] VALUES (2060,'أتاني وعيد الأدعياء وأنهم ... أعدوا لي السودان في كفر عاقب','165','3','0');
INSERT INTO [t754] VALUES (2061,'ولو صدقوا في جدهم لحذرتهم ... فهل في وحدي قولهم غير كاذب','165','3','0');
INSERT INTO [t754] VALUES (2062,'إلي لعمري قصد كل عجيبة ... كأني عجيب في عيون العجائب','166','3','0');
INSERT INTO [t754] VALUES (2063,'بأي بلاد لم أجر ذوائبي ... وأي مكان لم تطأه ركائبي','166','3','0');
INSERT INTO [t754] VALUES (2064,'كأن رحيلي كان من كف طاهر ... فأثبت كورى في ظهور المواهب','166','3','0');
INSERT INTO [t754] VALUES (2065,'فلم يبق خلق لم يردن فناءه ... وهن له شرب ورود المشارب','166','3','0');
INSERT INTO [t754] VALUES (2066,'فتى علمته نفسه وجدوده ... قراع الأعادي وابتذال الرغائب','166','3','0');
INSERT INTO [t754] VALUES (2067,'فقد غيب الشهاد عن كل موطن ... ورد إلى أوطانه كل غائب','166','3','0');
INSERT INTO [t754] VALUES (2068,'كذا الفاطميون الندى في بنانهم ... أعز امحاء من خطوط الرواجب','166','3','0');
INSERT INTO [t754] VALUES (2069,'أناس إذا لاقوا عدى فكأنما ... سلاح الذي لاقوا غبار السلاهب','166','3','0');
INSERT INTO [t754] VALUES (2070,'رموا بنواصيها القسي فجئنها ... دوامي الهوادي سالمات الجوانب','166','3','0');
INSERT INTO [t754] VALUES (2071,'أولائك أحلى من حيوة معادة ... وأكثر ذكرا من دهور الشبائب','166','3','0');
INSERT INTO [t754] VALUES (2072,'نصرت غليا يا ابنة ببواتر ... من الفعل لا فل لها في المضارب','166','3','0');
INSERT INTO [t754] VALUES (2073,'وأبهر آيات التهامي أنه ... أبوك وأجدى ما لكم من مناقب','166','3','0');
INSERT INTO [t754] VALUES (2074,'إذا لم تكن نفس النسيب كأصله ... فماذا الذي كرام المناصب','167','3','0');
INSERT INTO [t754] VALUES (2075,'وما قربت أشباه قوم أباعد ... وما بعدت أشباه قوم أقارب','167','3','0');
INSERT INTO [t754] VALUES (2076,'إذا علوي لم يكن مثل طاهر ... فما هو إلا حجة للنواصب','167','3','0');
INSERT INTO [t754] VALUES (2077,'يقولون تأثير الكواكب في الورى ... فما باله تأثيره في الكواكب','167','3','0');
INSERT INTO [t754] VALUES (2078,'علا كتد الدنيا إلى كل غاية ... تسير به سير الذلول براكب','168','3','0');
INSERT INTO [t754] VALUES (2079,'وحق له أن يسبق الناس جالسا ... ويدرك ما لم يدركوا غير طالب','168','3','0');
INSERT INTO [t754] VALUES (2080,'ويجذى عرانين الملوك وإنها ... لمن قدميه في أجل المراتب','168','3','0');
INSERT INTO [t754] VALUES (2081,'يد للزمان الجمع بيني وبينه ... لتفريقه بيني وبين النوائب','168','3','0');
INSERT INTO [t754] VALUES (2082,'هو ابن رسول الله وابن وصيه ... وشبههما شبهت بعد التجارب','168','3','0');
INSERT INTO [t754] VALUES (2083,'يرى أن ما ما بان منك لضارب ... بأقتل مما بان منك لعائب','168','3','0');
INSERT INTO [t754] VALUES (2084,'ألا أيها المال الذي قد أباده ... تعز فهذا فعله في الكتائب','168','3','0');
INSERT INTO [t754] VALUES (2085,'لعلك في وقت شغلت فؤاده ... عن الجود أو كثرت جيش محارب','168','3','0');
INSERT INTO [t754] VALUES (2086,'حملت إليه من لساني حديقة ... سقاها الحجي سقى الرياض السحائب','168','3','0');
INSERT INTO [t754] VALUES (2087,'فحييت خير ابن لخير أب بها ... لأشرف بيت في لؤي بن غالب','168','3','0');
INSERT INTO [t754] VALUES (2088,'ما للمروج الخضر والحدائق ... يشكو خلاها كثرة العوائق','168','3','0');
INSERT INTO [t754] VALUES (2089,'أقام فيها الثلج كالمرافق ... يعقد فوق السن ريق الباصق','168','3','0');
INSERT INTO [t754] VALUES (2090,'ثم مضى لا عاد من مفارق ... بقائدس من ذوبه وسائق','168','3','0');
INSERT INTO [t754] VALUES (2091,'كأنما الطخرور باغي ابق ... يأكل من نبت قصير لاصق','168','3','0');
INSERT INTO [t754] VALUES (2092,'كقشرك الحبر من المهارق ... أروده منه بكالشوذانق','168','3','0');
INSERT INTO [t754] VALUES (2093,'بمطلق اليمنى طويل الفائق ... عبل الشوى مقارب المرافق','169','3','0');
INSERT INTO [t754] VALUES (2094,'رحب اللبان نائه الطرائق ... ذي منخر رحب واطل لاحق','169','3','0');
INSERT INTO [t754] VALUES (2095,'محجل نهد كميت زاهق ... شادخة غرته كالشارق','169','3','0');
INSERT INTO [t754] VALUES (2096,'كأنها من لونه في بارق ... باق على البوغاء والشقائق','169','3','0');
INSERT INTO [t754] VALUES (2097,'للفارس الراكض منه الواثق ... خوف الجبان في فؤاد العاشق','169','3','0');
INSERT INTO [t754] VALUES (2098,'لو سابق الشمس من المشارق ... جاء إلى الغرب مجيء السابق','169','3','0');
INSERT INTO [t754] VALUES (2099,'يترك في حجارة الأبارق ... آثار قلع الحلي في المناطق','169','3','0');
INSERT INTO [t754] VALUES (2100,'لو أوردت غب سحاب صادق ... لأحسبت خوامس الأيانق','169','3','0');
INSERT INTO [t754] VALUES (2101,'إذا اللجام جاءه لطارق ... شحا له شحو الغراب الناعق','169','3','0');
INSERT INTO [t754] VALUES (2102,'كأنما الجلد لعرى الناهق ... منحدر عن سيتي جلاهق','169','3','0');
INSERT INTO [t754] VALUES (2103,'بذ المذاكي وهو في العقائق ... وزاد في الساق على النقانق','169','3','0');
INSERT INTO [t754] VALUES (2104,'وزاد في الوقع على الصواعق ... وزاد في الأذن على الخرانق','169','3','0');
INSERT INTO [t754] VALUES (2105,'وزاد في الحذر على العقاعق ... يميز الهزل من الحقائق','170','3','0');
INSERT INTO [t754] VALUES (2106,'وينذر الركب بكل سارق ... يريك خرقا وهو عين الحاذق','170','3','0');
INSERT INTO [t754] VALUES (2107,'يحك أني شاء حك الباشق ... قوبل من آفقة وآفق','170','3','0');
INSERT INTO [t754] VALUES (2108,'بين عتاق الخيل والعتائق ... فعنقه يربي على البواسق','170','3','0');
INSERT INTO [t754] VALUES (2109,'وحلقه يمكن فتر الخانق ... أعده للطعن في الفيالق','170','3','0');
INSERT INTO [t754] VALUES (2110,'والضرب في الأوجه والمفارق ... والسير في ظل اللواء الخافق','170','3','0');
INSERT INTO [t754] VALUES (2111,'يحملني والنصل ذا السفاسق ... يقطر في كمي إلى البنائق','170','3','0');
INSERT INTO [t754] VALUES (2112,'لا ألحظ الدنيا بعيني وامق ... ولا أبالي قلة الموافق','170','3','0');
INSERT INTO [t754] VALUES (2113,'أي كبت كل حاسد منافق ... أنت لنا وكلنا للخالق','170','3','0');
INSERT INTO [t754] VALUES (2114,'إذا غامرت في شرف مروم ... فلا تقنع بما دون النجوم','170','3','0');
INSERT INTO [t754] VALUES (2115,'فطعم الموت في أمر حقير ... كطعم الموت في أمر عظيم','170','3','0');
INSERT INTO [t754] VALUES (2116,'ستبكي شجوها فرسي ومهري ... صفائح دمعها ماء الجسوم','170','3','0');
INSERT INTO [t754] VALUES (2117,'قربن النار ثم نشأن فيها ... كما نشأ العذارى في النعيم','170','3','0');
INSERT INTO [t754] VALUES (2118,'وفارقن الصياقل مخلصات ... وأيديها كثيرات الكلوم','171','3','0');
INSERT INTO [t754] VALUES (2119,'يرى الجبناء أن العجز عقل ... وتلك خديعة الطبع اللئيم','171','3','0');
INSERT INTO [t754] VALUES (2120,'وكل شجاعة في المرء تغنى ... ولا مثل الشجاعة في الحكيم','171','3','0');
INSERT INTO [t754] VALUES (2121,'وكم من عائب قولا صحيحا ... وآفته من الفهم السقيم','171','3','0');
INSERT INTO [t754] VALUES (2122,'ولكن تأخذ الآذان منه ... على قدر القرائح والعلوم','171','3','0');
INSERT INTO [t754] VALUES (2123,'لهوى القلوب سريرة لا تعلم ... عرضا نظرت وخلت أني أسلم','171','3','0');
INSERT INTO [t754] VALUES (2124,'يا أخت معتنق الفوارس في الوغى ... لأخوك ثم أرق منك وأرحم','171','3','0');
INSERT INTO [t754] VALUES (2125,'يرنو إليك مع العفاف وعنده ... أن المحبوس تصيب فيما تحكم','171','3','0');
INSERT INTO [t754] VALUES (2126,'أرعتك رائعة البياض بعارضي ... ولو أنها الولى لراع الأسحم','172','3','0');
INSERT INTO [t754] VALUES (2127,'لو كان يمكنني سفرت عن الصبا ... فالشيب من للأوان تلثم','172','3','0');
INSERT INTO [t754] VALUES (2128,'ولقد رأيت الحادثات فلا أرى ... يققا يميت ولا سوادا يعصم','172','3','0');
INSERT INTO [t754] VALUES (2129,'والهم يخترم الجسيم نحافة ... ويشيب ناصية الصبي ويهرم','172','3','0');
INSERT INTO [t754] VALUES (2130,'ذو العقل يشقى في النعيم بعقله ... وأخو الجهالة في الشقاوة ينعم','172','3','0');
INSERT INTO [t754] VALUES (2131,'والناس قد نبذوا الحفاظ فمطلق ... ينسى الذي يولى وعاف يندم','172','3','0');
INSERT INTO [t754] VALUES (2132,'لا يخدعنك من عدو دمعه ... وأرحم شبابك من عدو ترحم','172','3','0');
INSERT INTO [t754] VALUES (2133,'لا يسلم الشرف الرفيع من الأذى ... حتى يراق على جوانبه الدم','173','3','0');
INSERT INTO [t754] VALUES (2134,'يؤذى القليل من اللئام بطبعه ... من لا يقل كما يقل ويلؤم','173','3','0');
INSERT INTO [t754] VALUES (2135,'والظلم في خلق النفوس فإن تجد ... ذا عفة فلعلة لا يظلم','173','3','0');
INSERT INTO [t754] VALUES (2136,'يحمى ابن كيغلغ الطريق وعرسه ... ما بين رجليها الطريق الأعظم','173','3','0');
INSERT INTO [t754] VALUES (2137,'أقم المسالح فوق شفر سكينة ... إن المنى بحلقتيها خضرم','173','3','0');
INSERT INTO [t754] VALUES (2138,'وأرفق بنفسك إن خلقك ناقص ... واستر أباك فإن أصلك مظلم','173','3','0');
INSERT INTO [t754] VALUES (2139,'وغناك مسألة وطيشك نفخة ... ورضاك فيشلة وربك درهم','173','3','0');
INSERT INTO [t754] VALUES (2140,'واحذر مناوأة الرجال فإنما ... تقوى على كمر العبيد وتقدم','173','3','0');
INSERT INTO [t754] VALUES (2141,'ومن البلية عذل من لا يرعوي ... عن غيه وخطاب من لا يفهم','173','3','0');
INSERT INTO [t754] VALUES (2142,'يمشى بأربعة على أعقابه ... تحت العلوج ومن وراء يلجم','173','3','0');
INSERT INTO [t754] VALUES (2143,'وجفونه ما تستقر كأنها ... مطروفة أو فت فيها حصرم','173','3','0');
INSERT INTO [t754] VALUES (2144,'وإذا أشار محدثا فكأنه ... قرد يقهقه أو عجوز تلطم','173','3','0');
INSERT INTO [t754] VALUES (2145,'يقلي مفارقة الأكف قذاله ... حتى يكاد على يد يتعمم','173','3','0');
INSERT INTO [t754] VALUES (2146,'وتراه أصغر ما تراه ناطقا ... ويكون أكذب ما يكون ويقسم','173','3','0');
INSERT INTO [t754] VALUES (2147,'والذل يظهر في الذليل مودة ... وأود منه لمن يود الأرقم','173','3','0');
INSERT INTO [t754] VALUES (2148,'ومن العداوة ما ينالك نفعه ... ومن الصداقة ما يضر ويؤلم','173','3','0');
INSERT INTO [t754] VALUES (2149,'أرسلت تسألني المديح سفاهة ... صفراء أضيق منك ماذا أزعم','173','3','0');
INSERT INTO [t754] VALUES (2150,'أترى القيادة في سواك تكسبا ... يا ابن الأعير وهي فيك تكرم','174','3','0');
INSERT INTO [t754] VALUES (2151,'فلشد ما جاوزت قدرك صاعدا ... ولشد ما قربت عليك الأنجم','174','3','0');
INSERT INTO [t754] VALUES (2152,'وأرغت ما لأبي العشائر خالصا ... إن الثناء لمن يزار فينعم','174','3','0');
INSERT INTO [t754] VALUES (2153,'ولمن أقمت على الهوان ببابه ... تدنو فيوجأ أخدعاك وتنهم','174','3','0');
INSERT INTO [t754] VALUES (2154,'ولمن يهين المال وهو مكرم ... ولمن يجر الجيش وهو عرمرم','174','3','0');
INSERT INTO [t754] VALUES (2155,'ولمن إذا التقت الكماة بمازق ... فنصيبه منها الكمى المعلم','174','3','0');
INSERT INTO [t754] VALUES (2156,'ولربما أطر القناة بفارس ... وثنى فقومها بآخر منهم','174','3','0');
INSERT INTO [t754] VALUES (2157,'والوجه أزهر والفؤاد مشيع ... والرمح أسمر والحسام مصمم','174','3','0');
INSERT INTO [t754] VALUES (2158,'أفعال من تلد الكرام كريمة ... وفعال من تلد الأعاجم أعجم','174','3','0');
INSERT INTO [t754] VALUES (2159,'أتاني كلام الجاهل ابن كيغلغ ... يجوب حزونا بيننا وسهولا','174','3','0');
INSERT INTO [t754] VALUES (2160,'ولو لم يكن بين ابن صفراء حائل ... وبيني سوى رمحي لكان طويلا','174','3','0');
INSERT INTO [t754] VALUES (2161,'وإسحاق مأمون على من أهانه ... ولكن تسلى بالبكاء قليلا','174','3','0');
INSERT INTO [t754] VALUES (2162,'وليس جميلا عرضه فيصونه ... وليس جميلا أن يكون جميلا','174','3','0');
INSERT INTO [t754] VALUES (2163,'ويكذب ما أذللته بهجائه ... لقد كان من قبل الهجاء ذليلا','174','3','0');
INSERT INTO [t754] VALUES (2164,'قالوا لنا مات إسحاق فقلت لهم ... هذا الدواء الذي يشفى من الحمق','174','3','0');
INSERT INTO [t754] VALUES (2165,'إن مات مات بلا فقد ولا أسف ... أو عاش عاش بلا خلق ولا خلق','174','3','0');
INSERT INTO [t754] VALUES (2166,'منه تعلم عبد شق هامته ... خون الصديق ودس الغدر في الملق','174','3','0');
INSERT INTO [t754] VALUES (2167,'حلق ألف يمين غير صادقة ... مطرودة ككعوب الرمح في نسق','174','3','0');
INSERT INTO [t754] VALUES (2168,'ما زلت أعرفه قردا بلا ذنب ... صفرا من البأس مملوا من النزق','174','3','0');
INSERT INTO [t754] VALUES (2169,'كريشة بمهب الريح ساقطة ... لا تستقر على حال من القلق','174','3','0');
INSERT INTO [t754] VALUES (2170,'تستغرق الكف فوديه ومنكبه ... وتكتسي منه ريح الجورب العرق','174','3','0');
INSERT INTO [t754] VALUES (2171,'فسائلوا قاتليه كيف مات لهم ... موتا من الضرب أو موتا من الفرق','174','3','0');
INSERT INTO [t754] VALUES (2172,'وأين موقع حد السيف من شبح ... بغير جسم ولا رأس ولا عنق','174','3','0');
INSERT INTO [t754] VALUES (2173,'لولا اللئام وشيء من مشابهه ... لكان ألأم طفل لف في خرق','174','3','0');
INSERT INTO [t754] VALUES (2174,'كلام أكثر من تلقى ومنظره ... مما يشق على الآذان والحدق','175','3','0');
INSERT INTO [t754] VALUES (2175,'روينا يا ابن عسكر الهماما ... ولم يترك نداك بنا هياما','175','3','0');
INSERT INTO [t754] VALUES (2176,'وصار أحب ما تهدي إلينا ... لغير قلي وداعك والسلاما','175','3','0');
INSERT INTO [t754] VALUES (2177,'ولم نملل تفقدك الموالي ... ولم نذمم أياديك الجساما','175','3','0');
INSERT INTO [t754] VALUES (2178,'ولكن الغيوث إذا توالت ... بأرض مسافر كره الغماما','175','3','0');
INSERT INTO [t754] VALUES (2179,'ما أهتز منه على عضو ليبتره ... إلا اتقاه بترس من تجلده','175','3','0');
INSERT INTO [t754] VALUES (2180,'ذم الزمان إليه من أحبته ... ما ذم من بدره في حمد أحمده','175','3','0');
INSERT INTO [t754] VALUES (2181,'شمس إذا الشمس لاقته على فرس ... تردد النور فيها من تردده','175','3','0');
INSERT INTO [t754] VALUES (2182,'إن يقبح الحسن إلا عند طلعته ... فالعبد يقبح إلا عند سيده','175','3','0');
INSERT INTO [t754] VALUES (2183,'قالت عن الرفد طب نفسا فقلت لها ... لا يصدر الحر إلا بعد مورده','175','3','0');
INSERT INTO [t754] VALUES (2184,'لم أعرف الخير إلا مذ عرفت فتى ... لم يولد الجود إلا عند مولده','175','3','0');
INSERT INTO [t754] VALUES (2185,'نفس تصغر نفس الدهر من كبر ... لها نهى كهله في سن أمرده','175','3','0');
INSERT INTO [t754] VALUES (2186,'أتراها لكثرة العشاق ... تحسب الدمع خلقة في المآقي','175','3','0');
INSERT INTO [t754] VALUES (2187,'كيف ترثي التي ترى كل جفن ... راءها غير جفنها غير راقى','175','3','0');
INSERT INTO [t754] VALUES (2188,'أنت منا فتنت نفسك لكن ... ك عوفيت من ضني واشتياق','176','3','0');
INSERT INTO [t754] VALUES (2189,'حلت دون المزار فاليوم لو زر ... ت لحال النحول دون العناق','176','3','0');
INSERT INTO [t754] VALUES (2190,'إن لحظا أدمته وأدمنا ... كان عمدا لنا وحتف اتفاق','176','3','0');
INSERT INTO [t754] VALUES (2191,'لو عدا عنك غير هجرك بعد ... لأرار الرسيم مخ المناقي','176','3','0');
INSERT INTO [t754] VALUES (2192,'ولسرنا ولو وصلنا عليها ... مثل أنفاسنا على الأمارق','176','3','0');
INSERT INTO [t754] VALUES (2193,'ما بنا من هوى العيون اللواتي ... لون أشفارهن لون الحداق','176','3','0');
INSERT INTO [t754] VALUES (2194,'قصرت مدة الليالي المواضي ... فأطالت بها الليالي البواقي','176','3','0');
INSERT INTO [t754] VALUES (2195,'كاثرث نائل الأمير من الما ... ل بما نولت من الإبراق','176','3','0');
INSERT INTO [t754] VALUES (2196,'ليس إلا أبا العشائر خلق ... ساد هذا الأنام باستحقاق','176','3','0');
INSERT INTO [t754] VALUES (2197,'طاعن الطعنة التي تطعن الفي ... لق بالذعر والدم المهراق','176','3','0');
INSERT INTO [t754] VALUES (2198,'ذات فرع كأنها في حشى المخ ... بر عنها من شدة الإطراق','176','3','0');
INSERT INTO [t754] VALUES (2199,'ضارب الهام في الغبا روما ير ... هب أن يشرب الذي هو ساقي','177','3','0');
INSERT INTO [t754] VALUES (2200,'فوق شقاء للأشق مجال ... بين أرساغها وبين الصفاق','177','3','0');
INSERT INTO [t754] VALUES (2201,'ما رآها مكذب الرسل إلا ... صدق القول في صفات البراق','177','3','0');
INSERT INTO [t754] VALUES (2202,'همه في ذوي الأسنة لا في ... ها وأطرافها لها كالنطاق','177','3','0');
INSERT INTO [t754] VALUES (2203,'ثاقب العقل ثابت الحلم لا يق ... در مرء له على إقلاق','177','3','0');
INSERT INTO [t754] VALUES (2204,'يا بني الحارث بن لقمان لا تع ... دمكم في الوغى متون العتاق','177','3','0');
INSERT INTO [t754] VALUES (2205,'بعثوا الرعب في قلوب الأعادي ... فكأن القتال قبل التلاقي','177','3','0');
INSERT INTO [t754] VALUES (2206,'وتكاد الظبي لما عودوها ... تنتضي نفسها إلى الأعناق','177','3','0');
INSERT INTO [t754] VALUES (2207,'وإذا أشفق الفوارس من وق ... ع القنا أشفقوا من الإشفاق','177','3','0');
INSERT INTO [t754] VALUES (2208,'كل ذمر يزيد في الموت حسنا ... كبدور تمامها في المحاق','177','3','0');
INSERT INTO [t754] VALUES (2209,'جاعل درعه منيته إن ... لم يكن دونها من العار واقي','177','3','0');
INSERT INTO [t754] VALUES (2210,'كرم خشن الجوانب منهم ... فهو كالماء في الشفار الرقاق','177','3','0');
INSERT INTO [t754] VALUES (2211,'ومعال إذا ادعاها سواهم ... لزمته جناية السراق','178','3','0');
INSERT INTO [t754] VALUES (2212,'يا ابن من كلما بدوت بدا لي ... غائب الشخص حاضر الأخلاق','178','3','0');
INSERT INTO [t754] VALUES (2213,'لو تنكرت في المكر لقوم ... حلفوا أنك انبه بالطلاق','178','3','0');
INSERT INTO [t754] VALUES (2214,'كيف يقوى بكفك الزند والآف ... اق فيها كالكف في الآفاق','178','3','0');
INSERT INTO [t754] VALUES (2215,'قل نفع الحديد فيك فما يل ... قاك إلا من سيفه من نفاق','178','3','0');
INSERT INTO [t754] VALUES (2216,'إلف هذا الهواء أوقع في الأن ... فس أن الحمام مر المذاق','178','3','0');
INSERT INTO [t754] VALUES (2217,'والأسى قبل فرقة الروح عجز ... والأسى لا يكون بعد الفراق','178','3','0');
INSERT INTO [t754] VALUES (2218,'كم ثراء فرجت بالرمح عنه ... كان من بخل أهله في وثاق','178','3','0');
INSERT INTO [t754] VALUES (2219,'والغنى في يد اللئيم قبيح ... قدر قبح الكريم في الإملاق','178','3','0');
INSERT INTO [t754] VALUES (2220,'ليس قولي في شمس فعلك كالشم ... س ولكن في الشمس كالإشراق','178','3','0');
INSERT INTO [t754] VALUES (2221,'شاعر المجد خدنه شاعر اللف ... ظ كلانا رب المعاني الدقاق','178','3','0');
INSERT INTO [t754] VALUES (2222,'لم تزل تسمع المديح ولك ... ن صهيل الجياد غير النهاق','178','3','0');
INSERT INTO [t754] VALUES (2223,'ليت لي مثل جد ذا الدهر ... في الأدهر أو رزقه من الأرزاق','178','3','0');
INSERT INTO [t754] VALUES (2224,'أنت فيه وكان كل زمان ... يشتهي بعض ذا على الخلاق','179','3','0');
INSERT INTO [t754] VALUES (2225,'وبنية من خيزران ضمنت ... بطيخة نبتت بنار في يد','179','3','0');
INSERT INTO [t754] VALUES (2226,'نظم الأمير لها قلادة لؤلؤ ... كفعاله وكلامه في المشهد','179','3','0');
INSERT INTO [t754] VALUES (2227,'كالكاس باشرها بالمزاج فأبرزت ... زبدا يدور على شراب أسود','179','3','0');
INSERT INTO [t754] VALUES (2228,'وسوداء منظوم عليها لآلىء ... لها صورة البطيخ وهي من الند','179','3','0');
INSERT INTO [t754] VALUES (2229,'كأن بقايا عنبر فوق رأسها ... طلوع رواعى الشيب في الشعر الجعد','179','3','0');
INSERT INTO [t754] VALUES (2230,'ما أنا والخمر وبطيخة ... سوداء في قشر من الخيزران','179','3','0');
INSERT INTO [t754] VALUES (2231,'يشغلني عنها وعن غيرها ... توطئتي النفس ليوم الطعان','179','3','0');
INSERT INTO [t754] VALUES (2232,'وكل نجلاء لها صائك ... يخضب ما بين يدي والسنان','179','3','0');
INSERT INTO [t754] VALUES (2233,'مبيتي من دمشق على فراش ... حشاه لي بحر حشاي حاشى','179','3','0');
INSERT INTO [t754] VALUES (2234,'لقي ليل كعين الظبي لونا ... وهم كالحميا في المشاش','179','3','0');
INSERT INTO [t754] VALUES (2235,'وشوق كالتوقد في فؤاد ... كجمر في جوانح كالمحاش','179','3','0');
INSERT INTO [t754] VALUES (2236,'سقى الدم كل نصل غير ناب ... وروى كل رمح غير راش','179','3','0');
INSERT INTO [t754] VALUES (2237,'فإن الفارس المنعوت خفت ... لمنصله الفوارس كالرياش','179','3','0');
INSERT INTO [t754] VALUES (2238,'فقد أضحى أبا الغمرات يكنى ... كأن أبا العشائر غير فاش','179','3','0');
INSERT INTO [t754] VALUES (2239,'وقد نسي الحسين بما يسمى ... ردي الأبطال أو غيث العطاش','179','3','0');
INSERT INTO [t754] VALUES (2240,'لقوه حاسرا في درع ضرب ... دقيق النسج ملتهب الحواشي','180','3','0');
INSERT INTO [t754] VALUES (2241,'كأن على الجماجم منه نار ... وأيدي القوم أجنحة الفراش','180','3','0');
INSERT INTO [t754] VALUES (2242,'كأن جواري المهجات ماء ... يعاودها المهند من عطاش','180','3','0');
INSERT INTO [t754] VALUES (2243,'فولوا بين ذي روح مفات ... وذي رمق وذي عقل مطاش','180','3','0');
INSERT INTO [t754] VALUES (2244,'ومنعفر لنصل السيف فيه ... توارى الضب خاف من احتراش','180','3','0');
INSERT INTO [t754] VALUES (2245,'يدمى بعض أيدي الخيل بعضا ... وما بعجاية أثر ارتهاش','180','3','0');
INSERT INTO [t754] VALUES (2246,'ورائعها وحيد لم يرعه ... تباعد جيشه والمستجاش','180','3','0');
INSERT INTO [t754] VALUES (2247,'كأن تلوى النشاب فيه ... تلوى الخوص في سعف العشاش','180','3','0');
INSERT INTO [t754] VALUES (2248,'ونهب نفوس أهل النهب أولى ... بأهل المجد من نهب القماش','180','3','0');
INSERT INTO [t754] VALUES (2249,'تشارك في الندام إذا نزلنا ... بطان لا تشارك في الجحاش','180','3','0');
INSERT INTO [t754] VALUES (2250,'ومن قبل النطاح وقبل يأنى ... يبين لك النعاج من الكباش','180','3','0');
INSERT INTO [t754] VALUES (2251,'فيا بحر البحور ولا أورى ... ويا بدر البدور ولا أحاشي','180','3','0');
INSERT INTO [t754] VALUES (2252,'كانك ناظر في كل قلب ... ولا يخفى عليك محل غاش','180','3','0');
INSERT INTO [t754] VALUES (2253,'أأصبر عنك لم تبخل بشيء ... ولم تقبل علي كلام واش','181','3','0');
INSERT INTO [t754] VALUES (2254,'وكيف وأنت في الرؤساء عندي ... عتيق الطير ما بين الخشاش','181','3','0');
INSERT INTO [t754] VALUES (2255,'فما خاشيك للتكذيب راج ... ولا راجيك للتخييب خاشى','181','3','0');
INSERT INTO [t754] VALUES (2256,'تطاعن كل خيل سرت فيها ... ولو كانوا النبيط على الجحاش','181','3','0');
INSERT INTO [t754] VALUES (2257,'أرى الناس الظلام وأنت نور ... وإني فيهم لإليك عاش','181','3','0');
INSERT INTO [t754] VALUES (2258,'بليت بهم بلاء الورد يلقى ... أنوفا هن أولى بالخشاش','181','3','0');
INSERT INTO [t754] VALUES (2259,'عليك إذا هزلت مع الليالي ... وحولك حين تسمن في هراش','181','3','0');
INSERT INTO [t754] VALUES (2260,'أتى خبر الأمير فقيل كروا ... فقلت نعم ولو لحقوا بشاش','181','3','0');
INSERT INTO [t754] VALUES (2261,'يقودهم إلى الهيجا لجوج ... يسن قتاله والكر ناش','181','3','0');
INSERT INTO [t754] VALUES (2262,'وأسرجت الكميت فناقلت بي ... على إعقاقها وعلى غشاشي','181','3','0');
INSERT INTO [t754] VALUES (2263,'من المتمردات تذب عنها ... برمحي كل طائرة الرشاش','181','3','0');
INSERT INTO [t754] VALUES (2264,'ولو عقرت لبلغني إليه ... حديث عنه يحمل كل ماشي','182','3','0');
INSERT INTO [t754] VALUES (2265,'إذا ذكرت مواقفه لحاف ... وشيك فما ينكس لانتقاش','182','3','0');
INSERT INTO [t754] VALUES (2266,'يزيل مخافة المصبور عنه ... ويلهي ذا الفياش عن الفياش','182','3','0');
INSERT INTO [t754] VALUES (2267,'وما وجد اشتياق كاشتياقي ... ولا عرف انكماش كانكماشي','182','3','0');
INSERT INTO [t754] VALUES (2268,'فسرت إليك في طلب المعالي ... وسار سواي في طلب المعاش','182','3','0');
INSERT INTO [t754] VALUES (2269,'وطائرة تتبعها المنايا ... على آثارها زجل الجناح','182','3','0');
INSERT INTO [t754] VALUES (2270,'كأن الريش منه في سهام ... على جسد تجسم من رياح','182','3','0');
INSERT INTO [t754] VALUES (2271,'كأن رؤس أقلام غلاظ ... مسحن بريش جؤجؤه الصحاح','182','3','0');
INSERT INTO [t754] VALUES (2272,'فأقعصها بحجن تحت صفر ... لها فعل الأسنة والرماح','182','3','0');
INSERT INTO [t754] VALUES (2273,'فقلت لكل حي يوم موت ... وإن حرص النفوس على الفلاح','182','3','0');
INSERT INTO [t754] VALUES (2274,'أتنكر ما نطقت به يديها ... وليس بمنكر سبق الجواد','182','3','0');
INSERT INTO [t754] VALUES (2275,'أراكض معوصات القول قسرا ... فأقتلها وغيري في الطراد','182','3','0');
INSERT INTO [t754] VALUES (2276,'لئن كان أحسن في وصفها ... لقد ترك الحسن في الوصف لك','183','3','0');
INSERT INTO [t754] VALUES (2277,'لأنك بحر وإن البحار ... لتأنف من حال هذي البرك','183','3','0');
INSERT INTO [t754] VALUES (2278,'كأنك سيفك لا ما ملك ... ت يبقى لديك ولا ما ملكت','183','3','0');
INSERT INTO [t754] VALUES (2279,'فأكثر من جريها ما وهبت ... وأكثر من مائها ما سفك','183','3','0');
INSERT INTO [t754] VALUES (2280,'أسأت وأحسنت عن قدرة ... ودرت على الناس دور الفلك','183','3','0');
INSERT INTO [t754] VALUES (2281,'لا تحسبوا ربعكم ولا طلله ... أول حي فراقكم قتله','183','3','0');
INSERT INTO [t754] VALUES (2282,'قد تلفت قبله النفوس بكم ... وأكثرت في هواكم العذله','183','3','0');
INSERT INTO [t754] VALUES (2283,'خلا وفيه أهل وأوحشنا ... وفيه صرم مروح إبله','183','3','0');
INSERT INTO [t754] VALUES (2284,'لو سار ذاك الحبيب عن فلك ... ما رضى الشمس برجه بدله','183','3','0');
INSERT INTO [t754] VALUES (2285,'أحبه والهوى وأدؤره ... وكل حب صبابة ووله','183','3','0');
INSERT INTO [t754] VALUES (2286,'ينصرها الغيث وهي ظامئة ... إلى سواه وسحبها هطله','183','3','0');
INSERT INTO [t754] VALUES (2287,'وا حربا منك يا جدايتها ... مقيمة فاعلمي ومرتحله','183','3','0');
INSERT INTO [t754] VALUES (2288,'لو خلط المسك والعبير بها ... ولست فيها لخلتها تفله','183','3','0');
INSERT INTO [t754] VALUES (2289,'أنا ابن من بعضه يفوق ابا ال ... باحث والنجل بعض من نجله','183','3','0');
INSERT INTO [t754] VALUES (2290,'وإنما يذكر الجدود لهم ... من نفروه وأنفدوا حيله','183','3','0');
INSERT INTO [t754] VALUES (2291,'فخرا لعضب أروح مشتمله ... وسمهري أروح معتقله','183','3','0');
INSERT INTO [t754] VALUES (2292,'وليفخر الفخر إذ غدوت به ... مرتديا خيره ومنتعله','184','3','0');
INSERT INTO [t754] VALUES (2293,'أنا الذي بين الإله به الأقد ... ار والمرء حيثما جعله','184','3','0');
INSERT INTO [t754] VALUES (2294,'جوهرة تفرح الشراف بها ... وغضة لا تسيغها السفله','184','3','0');
INSERT INTO [t754] VALUES (2295,'إن الكذاب الذي أكاد به ... أهون عندي من الذي نقله','184','3','0');
INSERT INTO [t754] VALUES (2296,'فلا مبال ولا مداج ولا ... وإن ولا عاجز ولا تكله','184','3','0');
INSERT INTO [t754] VALUES (2297,'ودارع سفته فخر لقا ... في الملتقى والعجاج والعجله','184','3','0');
INSERT INTO [t754] VALUES (2298,'وسامع رعته بقافية ... يحار فيها المنقح القوله','184','3','0');
INSERT INTO [t754] VALUES (2299,'وربما أشهد الطعام معي ... من لا يساوي الخبز الذي أكله','184','3','0');
INSERT INTO [t754] VALUES (2300,'ويظهر الجهل بي وأعرفه ... والدر در برغم من جهله','184','3','0');
INSERT INTO [t754] VALUES (2301,'مستحييا من أبي العشائر أن ... أسحب في غير أرضه حلله','184','3','0');
INSERT INTO [t754] VALUES (2302,'أسحبها عنده لدى ملك ... ثيابه من جليسه وجله','184','3','0');
INSERT INTO [t754] VALUES (2303,'وبيض غلمانه كنائله ... أول محمول سيبه الحمله','184','3','0');
INSERT INTO [t754] VALUES (2304,'ما لي لا أمدح الحسين ولا ... أبذل مثل الود الذي بذله','184','3','0');
INSERT INTO [t754] VALUES (2305,'أأخفت العين عنده خبرا ... أم بلغ الكيذبان ما أمله','184','3','0');
INSERT INTO [t754] VALUES (2306,'أم ليس ضراب كل جمجمة ... منخوة ساعة الوغى زعله','184','3','0');
INSERT INTO [t754] VALUES (2307,'وصاحب الجود ما يفارقه ... لو كان للجود منطق عذله','185','3','0');
INSERT INTO [t754] VALUES (2308,'وراكب الهول لا يفتره ... لو كان للهول محزم هزله','185','3','0');
INSERT INTO [t754] VALUES (2309,'وفارس الأحمر المكلل في ... طيىء المشرع القنا قبله','185','3','0');
INSERT INTO [t754] VALUES (2310,'لما رأت وجهه خيولهم ... أقسم بالله لا رأت كفله','185','3','0');
INSERT INTO [t754] VALUES (2311,'فأكبروا فعله وأصغره ... أكبر من فعله الذي فعله','185','3','0');
INSERT INTO [t754] VALUES (2312,'القاتل الواصل الكميل فلا ... بعض جميل عن بعضه شغله','185','3','0');
INSERT INTO [t754] VALUES (2313,'فواهب والرماح تشجره ... وطاعن والهبات متصله','185','3','0');
INSERT INTO [t754] VALUES (2314,'وكلما آمن البلاد سرى ... وكلما خيف منزل نزله','185','3','0');
INSERT INTO [t754] VALUES (2315,'وكلما جاهر العدو ضحى ... أمكن حتى كأنه ختله','185','3','0');
INSERT INTO [t754] VALUES (2316,'يحتقر البيض واللدان إذا ... سن عليه الدلاص أو نثله','185','3','0');
INSERT INTO [t754] VALUES (2317,'قد هذبت فهمه الفقهة لي ... وهذبت شعري الفصاحة له','185','3','0');
INSERT INTO [t754] VALUES (2318,'فصرت كالسيف حامدا يده ... لا يحمد السيف كل من حمله','185','3','0');
INSERT INTO [t754] VALUES (2319,'أعن إذني تهب الريح رهوا ... ويسري كلما شئت الغمام','185','3','0');
INSERT INTO [t754] VALUES (2320,'ولكن الغمام له طباع ... والدهر لفظ وأنت معناه','185','3','0');
INSERT INTO [t754] VALUES (2321,'والجود عين وفيك ناظرها ... والناس باع وفيك يمناه','185','3','0');
INSERT INTO [t754] VALUES (2322,'أفدي الذي كل مازق حرج ... أغبر فرسانه تحاماه','185','3','0');
INSERT INTO [t754] VALUES (2323,'أعلى قناة الحسين أوسطها ... فيه وأعلى الكمي رجلاه','186','3','0');
INSERT INTO [t754] VALUES (2324,'تنشد اثوابنا مدائحه ... بالسن ما لهن أفواه','186','3','0');
INSERT INTO [t754] VALUES (2325,'إذا مررنا على الأصم بها ... أغنته عن مسمعيه عيناه','186','3','0');
INSERT INTO [t754] VALUES (2326,'سبحان من خار للكواكب بال ... بعد ولو نلن كن جدواه','186','3','0');
INSERT INTO [t754] VALUES (2327,'لو كان ضوء الشموس في يده ... لضاعه جوده وأفناه','186','3','0');
INSERT INTO [t754] VALUES (2328,'يا راحلا كل من يودعه ... مودع دينه ودنياه','186','3','0');
INSERT INTO [t754] VALUES (2329,'إن كان فيما نراه من كرم ... فيك مزيد فزادك الله','186','3','0');
INSERT INTO [t754] VALUES (2330,'قالوا ألم تكنه فقلت لهم ... ذلك عي إذا وصفناه','186','3','0');
INSERT INTO [t754] VALUES (2331,'لا يتوفى أبو العشائر من ... ليس معاني الورى كمعناه','186','3','0');
INSERT INTO [t754] VALUES (2332,'أفرس من تسبح الجياد به ... وليس إلا الحديد أمواه','186','3','0');
INSERT INTO [t754] VALUES (2333,'به وبمثله شق الصفوف ... وزلت عن مباشرها الحتوف','186','3','0');
INSERT INTO [t754] VALUES (2334,'فدعه لقى فإنك من كرام ... جواشنها الأسنة والسيوف','187','3','0');
INSERT INTO [t754] VALUES (2335,'لام أناس أبا العشائر في ... جود يديه بالعين والورق','187','3','0');
INSERT INTO [t754] VALUES (2336,'وإنما قيل لم خلقت كذا ... وخالق الخلق خالق الخلق','187','3','0');
INSERT INTO [t754] VALUES (2337,'قالوا ألم تكفه سماحته ... حتى بنى بيته على الطرق','187','3','0');
INSERT INTO [t754] VALUES (2338,'فقلت إن الفتى شجاعته ... تريه في الشح صورة الفرق','187','3','0');
INSERT INTO [t754] VALUES (2339,'الشمس قد حلت السماء وما ... يحجبها بعدها عن الحدق','187','3','0');
INSERT INTO [t754] VALUES (2340,'بضرب هام الكماة تم له ... كسب الذي يكسبون بالملق','187','3','0');
INSERT INTO [t754] VALUES (2341,'كن لجة أيها السماح فقد ... آمنه سيفه من الغرق','187','3','0');
INSERT INTO [t754] VALUES (2342,'ومنتسب عندي إلى من أحبه ... وللنبل حولي من يديه حفيف','187','3','0');
INSERT INTO [t754] VALUES (2343,'فهيج من شوقي وما من مذله ... حننت ولكن الكريم ألوف','187','3','0');
INSERT INTO [t754] VALUES (2344,'فكل وداج لا يدوم على الأذى ... دوام ودادي للحسين ضعيف','187','3','0');
INSERT INTO [t754] VALUES (2345,'فإن يكن الفعل الذي ساء واحدا ... فأفعاله اللائي سررن ألوف','187','3','0');
INSERT INTO [t754] VALUES (2346,'ونفسي له نفسي الفداء لنفسه ... ولكن بعض المالكين عنيف','187','3','0');
INSERT INTO [t754] VALUES (2347,'وفاءكما كالربع أشجاه طاسمه ... بأن تسعدا والدمع أشفاه ساجمه','187','3','0');
INSERT INTO [t754] VALUES (2348,'وما أنا إلا عاشق كل عاشق ... أعق خليليه الصفيين لائمه','188','3','0');
INSERT INTO [t754] VALUES (2349,'وقد يتزيا بالهوى غير أهله ... ويستصحب الإنسان من لا يلائمه','188','3','0');
INSERT INTO [t754] VALUES (2350,'بليت بلى الأطلال إن لم أقف بها ... وقوف شحيح ضاع في الترب خاتمه','188','3','0');
INSERT INTO [t754] VALUES (2351,'كئيبا توقاني العواذل في الهوى ... كما يتوقى ريض الخيل حازمه','189','3','0');
INSERT INTO [t754] VALUES (2352,'قفى تغرم الأولى من اللحظ مهجتي ... بثانية والمتلف الشيء غارمه','189','3','0');
INSERT INTO [t754] VALUES (2353,'سقاك وحيانا بك الله إنما ... على العيس نور والخدور كمائمه','189','3','0');
INSERT INTO [t754] VALUES (2354,'وما حاجة الأظعان حولك في الدجى ... إلى قمر ما واجد لك عادمه','189','3','0');
INSERT INTO [t754] VALUES (2355,'إذا ظفرت منك العيون بنظرة ... أثاب بها معيى المطي ورازمه','190','3','0');
INSERT INTO [t754] VALUES (2356,'حبيب كأن الحسن كان يحبه ... فآثره أو جار في الحسن قاسمه','190','3','0');
INSERT INTO [t754] VALUES (2357,'تحول رماح الخط دون سبائه ... وتسبي له من كل حي كرائمه','190','3','0');
INSERT INTO [t754] VALUES (2358,'ويضحى غبار الخيل أدنى ستوره ... وآخرها نشر الكباء الملازمه','190','3','0');
INSERT INTO [t754] VALUES (2359,'وما استغربت عيني فراقا رأيته ... ولا علمتني غير ما القلب عالمه','190','3','0');
INSERT INTO [t754] VALUES (2360,'فلا يتهمني الكاشحون فإنني ... رعيت الردى حتى حلت لي علاقمه','190','3','0');
INSERT INTO [t754] VALUES (2361,'مشب الذي يبكي الشباب مشيبه ... فكيف توقيه وبانيه هادمه','190','3','0');
INSERT INTO [t754] VALUES (2362,'وتكلمة العيش الصبي وعقيبه ... وغائب لون العارضين وقادمه','191','3','0');
INSERT INTO [t754] VALUES (2363,'وما خضب الناس البياض لأنه ... قبيح ولكن أحسن الشعر فاحمه','191','3','0');
INSERT INTO [t754] VALUES (2364,'وأحسن من ماء الشبيبة كله ... حيا بارق في فازة أنا شائمه','191','3','0');
INSERT INTO [t754] VALUES (2365,'عليها رياض لم تحكها سحابة ... وأغصان دوح لم تغن حمائمة','191','3','0');
INSERT INTO [t754] VALUES (2366,'وفوق حواشي كل ثوب موجه ... من الدر سمط لم يثقبه ناظمه','191','3','0');
INSERT INTO [t754] VALUES (2367,'ترى حيوان البر مصطلحا بها ... يحارب ضد ضده ويسالمه','191','3','0');
INSERT INTO [t754] VALUES (2368,'إذا ضربته الريح ماج كأنه ... تجول مذاكيه وتدأى ضراغمه','191','3','0');
INSERT INTO [t754] VALUES (2369,'تقبل أفواه الملوك بساطه ... ويكبر عنها كمه وبراجمه','191','3','0');
INSERT INTO [t754] VALUES (2370,'قياما لمن يشفى من الداء كيه ... ومن بين أذني كل قرم مواسمه','191','3','0');
INSERT INTO [t754] VALUES (2371,'قبائعها تحت المرافق هيبة ... وأنفذ مما في الجفون عزائمه','191','3','0');
INSERT INTO [t754] VALUES (2372,'له عسكرا خيل وطير إذا رمى ... بها عسكرا لم يبق إلا جماجمه','191','3','0');
INSERT INTO [t754] VALUES (2373,'أجلتها من كل طاغ ثيابه ... وموطئها من كل باغ ملاغمه','192','3','0');
INSERT INTO [t754] VALUES (2374,'فقد مل ضوء الصبح مما تغيره ... ومل سواد الليل مما تزاحمه','192','3','0');
INSERT INTO [t754] VALUES (2375,'ومل القنا مما تدق صدوره ... ومل حديد الهند ما تلاطمه','192','3','0');
INSERT INTO [t754] VALUES (2376,'سحاب من العقبان يزحف تحتها ... سحاب إذا استسقت سقتها صوارمه','192','3','0');
INSERT INTO [t754] VALUES (2377,'سلكت صروف الدهر حتى لقيته ... على ظهر عزم مؤيدات قوائمه','192','3','0');
INSERT INTO [t754] VALUES (2378,'مهالك لم تصحب بها الذئب نفسه ... ولا حملت فيها الغراب قوادمه','192','3','0');
INSERT INTO [t754] VALUES (2379,'فأبصرت بدرا لا يرى البدر مثله ... وخاطبت بحرا لا يرى العبر عائمه','192','3','0');
INSERT INTO [t754] VALUES (2380,'غضبت له لما رأيت صفاته ... بلا واصف والشعر تهذي طماطمه','192','3','0');
INSERT INTO [t754] VALUES (2381,'وكنت إذا يممت أرضا بعيدة ... سريت فكنت السر والليل كاتمه','192','3','0');
INSERT INTO [t754] VALUES (2382,'لقد سل سيف الدولة المجد معلما ... فلا المجد مخفيه ولا الضرب ثالمه','192','3','0');
INSERT INTO [t754] VALUES (2383,'على عاتق الملك الأعز نجاده ... وفي يد جبار السموات قائمه','193','3','0');
INSERT INTO [t754] VALUES (2384,'تحاربه الأعداء وهي عبيده ... وتدخر الأموال وهي غنائمه','193','3','0');
INSERT INTO [t754] VALUES (2385,'ويستكبرون الدهر والدهر دونه ... ويستعظمون الموت والموت خادمه','193','3','0');
INSERT INTO [t754] VALUES (2386,'وإن الذي سمى عليا لمنصف ... وإن الذي سماه سيفا لظالمه','193','3','0');
INSERT INTO [t754] VALUES (2387,'وما كل سيف يقطع الهام حده ... وتقطع لزبات الزمان مكارمه','193','3','0');
INSERT INTO [t754] VALUES (2388,'أين أزمعت أيهذا الهمام ... نحن نبت الربى وأنت الغمام','193','3','0');
INSERT INTO [t754] VALUES (2389,'نحن من ضايق الزمان له في ... ك وخانته قربك الأيام','193','3','0');
INSERT INTO [t754] VALUES (2390,'في سبي العلى قتالك والسل ... م وهذا المقام والإجذام','193','3','0');
INSERT INTO [t754] VALUES (2391,'ليت أنا إذا ارتحلت لك الخي ... ل وأنا إذا نزلت الخيام','193','3','0');
INSERT INTO [t754] VALUES (2392,'كل يوم لك ارتحال جديد ... ومسير للمجد فيه مقام','193','3','0');
INSERT INTO [t754] VALUES (2393,'وإذا كانت النفوس كبارا ... تعبت في مرادها الأجسام','193','3','0');
INSERT INTO [t754] VALUES (2394,'٢','194','0','0');
INSERT INTO [t754] VALUES (2395,'وكذا تطلع البدور علينا ... وكذا تقلق البحور العظام','194','2394','0');
INSERT INTO [t754] VALUES (2396,'ولنا عادة الجميل من الصب ... ر لو أنا سوى نواك نسام','194','2394','0');
INSERT INTO [t754] VALUES (2397,'كل عيش ما لم تطبه حمام ... كل شمس ما لم تكنها ظلام','194','2394','0');
INSERT INTO [t754] VALUES (2398,'أزل الوحشة التي عندنا يا ... من به يأنس الخميس اللهام','194','2394','0');
INSERT INTO [t754] VALUES (2399,'والذي يشهد الوغى ساكن القل ... ب كأن القتال فيها ذمام','194','2394','0');
INSERT INTO [t754] VALUES (2400,'والذي يضرب الكتائب حتى ... تتلاقى الفهاق والأقدام','194','2394','0');
INSERT INTO [t754] VALUES (2401,'وإذا حل ساعة بمكان ... فأذاه على الزمان حرام','194','2394','0');
INSERT INTO [t754] VALUES (2402,'والذي تنبت البلاد سرور ... والذي يمطر السحاب مدام','194','2394','0');
INSERT INTO [t754] VALUES (2403,'كلما قيل قد تناهى أرانا ... كرما ما اهتدت إليه الكرام','194','2394','0');
INSERT INTO [t754] VALUES (2404,'وكفاحا تكع عنه الأعادي ... وارتياحا تحار فيه الأنام','194','2394','0');
INSERT INTO [t754] VALUES (2405,'إنما هيبته المؤل سيف ال ... دولة الملك في القلوب حسام','194','2394','0');
INSERT INTO [t754] VALUES (2406,'فكثير من الشجاع التوقي ... وكثير من البليغ السلام','194','2394','0');
INSERT INTO [t754] VALUES (2407,'رويدك ايها الملك الجليل ... تأن وعده مما تنيل','194','2394','0');
INSERT INTO [t754] VALUES (2408,'وجودك بالمقام ولو قليلا ... فما فيما تجود به قليل','194','2394','0');
INSERT INTO [t754] VALUES (2409,'لأكبت حاسدا وأرى عدوا ... كأنهما وداعك والرحيل','194','2394','0');
INSERT INTO [t754] VALUES (2410,'ويهدأ ذا السحاب فقد شككنا ... أتغلب أم حياه لكم قبيل','195','2394','0');
INSERT INTO [t754] VALUES (2411,'وكنت أعيب عذلا في سماح ... فها أنا في السماح له عذول','195','2394','0');
INSERT INTO [t754] VALUES (2412,'وما أخشى نبوك عن طريق ... وسيف الدولة الماضي الصقيل','195','2394','0');
INSERT INTO [t754] VALUES (2413,'وكل شواه غطريف تمنى ... لسيرك أن مفرقها السبيل','195','2394','0');
INSERT INTO [t754] VALUES (2414,'ومثل العمق مملوؤا دماء ... مشت بك في مجاريه الخيول','195','2394','0');
INSERT INTO [t754] VALUES (2415,'إذا اعتاد الفتى خوض المنايا ... فأهون ما يمر به الوحول','195','2394','0');
INSERT INTO [t754] VALUES (2416,'ومن أمر الحصون فما عصته ... أطاعته الحزونة والسهول','195','2394','0');
INSERT INTO [t754] VALUES (2417,'أتخفر كل من رمت الليالي ... وتنشر كل من دفن الخمول','195','2394','0');
INSERT INTO [t754] VALUES (2418,'وندعوك الحسام وهل حسام ... يعيش به من الموت القتيل','195','2394','0');
INSERT INTO [t754] VALUES (2419,'وما للسيف إلا القطع فعل ... وأنت القاطع البر الوصول','195','2394','0');
INSERT INTO [t754] VALUES (2420,'وأنت الفارس القوال صبرا ... وقد فنى التكلم والصهيل','195','2394','0');
INSERT INTO [t754] VALUES (2421,'يحيد الرمح عنك وفيه قصد ... ويقصر أن ينال وفيه طول','195','2394','0');
INSERT INTO [t754] VALUES (2422,'ولو قدر السنان على لسان ... لقال لك السنان كما أقول','195','2394','0');
INSERT INTO [t754] VALUES (2423,'ولو جاز الخلود خلدت فردا ... ولكن ليس للدنيا خليل','195','2394','0');
INSERT INTO [t754] VALUES (2424,'نعد المشرفية والعوالي ... وتقتلنا المنون بلا قتال','195','2394','0');
INSERT INTO [t754] VALUES (2425,'ونرتبط البسوابق مقربات ... وما ينجين من خبب الليالي','195','2394','0');
INSERT INTO [t754] VALUES (2426,'ومن لم يعشق الدنيا قديما ... ولا كن لا سبيل إلى الوصال','195','2394','0');
INSERT INTO [t754] VALUES (2427,'نصيبك في حياتك من حبيب ... نصيبك في منامك من خيال','196','2394','0');
INSERT INTO [t754] VALUES (2428,'رماني الدهر بالأرزاء حتى ... فؤادي في غشاء من نبال','196','2394','0');
INSERT INTO [t754] VALUES (2429,'فصرت إذا أصابتني سهام ... تكسرت النصال على النصال','196','2394','0');
INSERT INTO [t754] VALUES (2430,'وهان فماابالي بالرزايا ... لأني ما انتفعت بأن أبالي','196','2394','0');
INSERT INTO [t754] VALUES (2431,'وهذا أول الناعين طرا ... لأول ميتة في ذا الجلال','196','2394','0');
INSERT INTO [t754] VALUES (2432,'كأن الموت لم يفجع بنفس ... ولم يخطر لمخلوق ببال','196','2394','0');
INSERT INTO [t754] VALUES (2433,'صلوة الله خالقنا حنوط ... على الوجه المكفن بالجمال','196','2394','0');
INSERT INTO [t754] VALUES (2434,'على المدفون قبل الترب صونا ... وقبل اللحد في كرم الخلال','196','2394','0');
INSERT INTO [t754] VALUES (2435,'فإن له ببطن الأرض شخصا ... جديدا ذكرناه وهو بالي','196','2394','0');
INSERT INTO [t754] VALUES (2436,'وما أحد يخلد في البرايا ... بل الدنيا تؤول إلى زوال','196','2394','0');
INSERT INTO [t754] VALUES (2437,'أطاب النفس أنك مت ... موتا تمنته البواقي والخوالي','196','2394','0');
INSERT INTO [t754] VALUES (2438,'وزلت ولم ترى يوما كريها ... تسر الروح فيه بالزوال','196','2394','0');
INSERT INTO [t754] VALUES (2439,'رواق العز فوقك مسبطر ... وملك علي ابنك في كمال','196','2394','0');
INSERT INTO [t754] VALUES (2440,'سقى مثواك غاد في الغوادي ... نظير نوال كفك في النوال','197','2394','0');
INSERT INTO [t754] VALUES (2441,'لساحيه على الأجداث حفش ... كأيدي الخيل أبصرت المخالي','197','2394','0');
INSERT INTO [t754] VALUES (2442,'أسائل عنك بعدك كل مجد ... وما عهدي بمجد عنك خالي','197','2394','0');
INSERT INTO [t754] VALUES (2443,'يمر بقبرك العافي فيبكي ... ويشغله البكاء عن السؤال','197','2394','0');
INSERT INTO [t754] VALUES (2444,'وما أهداك للجدوى عليه ... لو أنك تقدرين على فعال','197','2394','0');
INSERT INTO [t754] VALUES (2445,'بعيشك هل سلوت فإن قلبي ... وإن جانبت أرضك غير سالي','197','2394','0');
INSERT INTO [t754] VALUES (2446,'نزلت على الكراهة في مكان ... بعدت على النعامي والشمال','197','2394','0');
INSERT INTO [t754] VALUES (2447,'تحجب عنك رائحة الخزامى ... وتمنع منك أنداء الطلال','197','2394','0');
INSERT INTO [t754] VALUES (2448,'بدار كل ساكنها غريب ... طويل الهجر منبت الحبال','197','2394','0');
INSERT INTO [t754] VALUES (2449,'حصان مثل ماء المزن فيه ... كتوم السر صادقة المقال','197','2394','0');
INSERT INTO [t754] VALUES (2450,'يعللها نطاسي الشكايا ... وواحدها نطاسي المعالي','197','2394','0');
INSERT INTO [t754] VALUES (2451,'إذا وصفوا له داء بثغر ... سقاه أسنة الأسل الطوال','197','2394','0');
INSERT INTO [t754] VALUES (2452,'وليست كالإناث ولا اللواتي ... تعد لها القبور من الحجال','197','2394','0');
INSERT INTO [t754] VALUES (2453,'ولا من في جنازتها تجار ... يكون وداعها نفض النعال','197','2394','0');
INSERT INTO [t754] VALUES (2454,'مشى الأمراء حوليها حفاة ... كأن المرو من زف الرئال','197','2394','0');
INSERT INTO [t754] VALUES (2455,'وأبرزت الخدور مخبآت ... يضعن النقس أمكنة الغوالي','198','2394','0');
INSERT INTO [t754] VALUES (2456,'أتتهن المصيبة غافلات ... فدمع الحزن في دمع الدلال','198','2394','0');
INSERT INTO [t754] VALUES (2457,'ولو كان النساء كمن فقدنا ... لفضلت النساء على الرجال','198','2394','0');
INSERT INTO [t754] VALUES (2458,'وما التأنيث لاسم الشمس عيب ... ولا التذكير فجر للهلال','198','2394','0');
INSERT INTO [t754] VALUES (2459,'وأفجع من فقدنا من وجدنا ... قبيل الفقد مفقود المثال','198','2394','0');
INSERT INTO [t754] VALUES (2460,'يدفن بعضنا بعضا ويمشي ... أواخرنا على هام الأوالي','198','2394','0');
INSERT INTO [t754] VALUES (2461,'وكم عين مقبلة النواحي ... كحيل بالجنادل والرمال','198','2394','0');
INSERT INTO [t754] VALUES (2462,'ومغض كان لا يغضى لخطب ... وبال كان يفكر في الهزال','198','2394','0');
INSERT INTO [t754] VALUES (2463,'أسيف الدولة استنجد بصبر ... وكيف بمثل صبرك للجبال','198','2394','0');
INSERT INTO [t754] VALUES (2464,'وأنت تعلم الناس التعزي ... وخوض الموت في الحرب السجال','198','2394','0');
INSERT INTO [t754] VALUES (2465,'وحالات الزمان عليك شتى ... وحالك واحد في كل حال','198','2394','0');
INSERT INTO [t754] VALUES (2466,'فلا غيضت بحارك يا جموما ... على علل الغرائب والدخال','198','2394','0');
INSERT INTO [t754] VALUES (2467,'رأيتك في الذين أرى ملوكا ... كأنك مستقيم في محال','198','2394','0');
INSERT INTO [t754] VALUES (2468,'فإن تفق الأنام وأنت منهم ... فإن المسك بعض دم الغزال','198','2394','0');
INSERT INTO [t754] VALUES (2469,'إلى م طماعية العاذل ... ولا رأى في الحب للعاقل','199','2394','0');
INSERT INTO [t754] VALUES (2470,'يراد من القلب نسيانكم ... وتأبى الطباع على الناقل','199','2394','0');
INSERT INTO [t754] VALUES (2471,'وإني لأعشق من عشقكم ... نحولي وكل فتى ناحل','199','2394','0');
INSERT INTO [t754] VALUES (2472,'ولو زلتم ثم لم أبككم ... بكيت على حبي الزائل','199','2394','0');
INSERT INTO [t754] VALUES (2473,'أينك خدي دموعي وقد ... جرت منه في مسلك سابل','199','2394','0');
INSERT INTO [t754] VALUES (2474,'وهبت السلو لمن لامني ... وبت من الشوق في شاغل','199','2394','0');
INSERT INTO [t754] VALUES (2475,'كأن الجفون على مقلتي ... ثياب شققن على ثاكل','199','2394','0');
INSERT INTO [t754] VALUES (2476,'ولو كنت في أسر غير الهوى ... ضمنت ضمان أبي وائل','199','2394','0');
INSERT INTO [t754] VALUES (2477,'فدى نفسه بضمان النضار ... وأعطى صدور القنا الذابل','199','2394','0');
INSERT INTO [t754] VALUES (2478,'ومناهم الخيل مجنوبة ... فجئن بكل فتى باسل','199','2394','0');
INSERT INTO [t754] VALUES (2479,'كأن خلاص أبي وائل ... معاودة القمر الآفل','199','2394','0');
INSERT INTO [t754] VALUES (2480,'دعا فسمعت وكم ساكت ... على البعد عندك كالقائل','199','2394','0');
INSERT INTO [t754] VALUES (2481,'فلبيته بك في جحفل ... له ضامن وبه كافل','199','2394','0');
INSERT INTO [t754] VALUES (2482,'خرجن من النقع في عارض ... ومن عرق الركض في وابل','200','2394','0');
INSERT INTO [t754] VALUES (2483,'ولما نشفن لقين السياط ... بمثل صفا البلد الماحل','200','2394','0');
INSERT INTO [t754] VALUES (2484,'شفن بخمس إلى من طلب ... ن قبل الشفون إلى نازل','200','2394','0');
INSERT INTO [t754] VALUES (2485,'فدانت مرافقهن البرى ... على ثقة بالدم الغاسل','200','2394','0');
INSERT INTO [t754] VALUES (2486,'وما بين كاذتي المستغير ... كما بين كاذتي البائل','200','2394','0');
INSERT INTO [t754] VALUES (2487,'فلقين كل ردينية ... ومصبوحة لبن الشائل','200','2394','0');
INSERT INTO [t754] VALUES (2488,'وجيش إمام على ناقة ... صحيح الإمامة في الباطل','200','2394','0');
INSERT INTO [t754] VALUES (2489,'فأقبلن ينحزن قدامه ... نوافر كالنحل والعاسل','200','2394','0');
INSERT INTO [t754] VALUES (2490,'فلما بدوت لأصحابه ... رأت أسدها آكل الآكل','200','2394','0');
INSERT INTO [t754] VALUES (2491,'بضرب يعمهم جائر ... له فيهم قسمة العادل','200','2394','0');
INSERT INTO [t754] VALUES (2492,'وطعن يجمع شذانهم ... كما اجتمعت درة الحافل','200','2394','0');
INSERT INTO [t754] VALUES (2493,'إذا ما نظرت إلى فارس ... تحير عن مذهب الراجل','200','2394','0');
INSERT INTO [t754] VALUES (2494,'فظل يخضب منها اللحى ... فتى لا يعيد على الناصل','200','2394','0');
INSERT INTO [t754] VALUES (2495,'ولا يستغيث إلى ناصر ... ولا يتضعضع من خاذل','200','2394','0');
INSERT INTO [t754] VALUES (2496,'ولا يزع الطرف عن مقدم ... ولا يرجع الطرف عن هائل','200','2394','0');
INSERT INTO [t754] VALUES (2497,'إذا طلب التبل لم يشأه ... وإن كان دينا على ماطل','200','2394','0');
INSERT INTO [t754] VALUES (2498,'خذوا ما أتاكم به واعذرواد ... فإن الغنيمة في العاجل','201','2394','0');
INSERT INTO [t754] VALUES (2499,'وإن كان أعجبكم عامكم ... فعودوا إلى حمص في قابل','201','2394','0');
INSERT INTO [t754] VALUES (2500,'فإن الحسام الخضيب الذي ... قتلتم به في يد القاتل','201','2394','0');
INSERT INTO [t754] VALUES (2501,'يجود بمثل الذي رمتم ... ولم تدركوه على السائل','201','2394','0');
INSERT INTO [t754] VALUES (2502,'أمام الكتيبة تزهى به ... مكان السنان من العامل','201','2394','0');
INSERT INTO [t754] VALUES (2503,'وإني لأعجب من آمل ... قتالا بكم على بازل','201','2394','0');
INSERT INTO [t754] VALUES (2504,'أقال له الله لا تلقهم ... بماض على فرس حائل','201','2394','0');
INSERT INTO [t754] VALUES (2505,'إذا ما ضربت به هامة ... براها وغناك في الكاهل','201','2394','0');
INSERT INTO [t754] VALUES (2506,'وليس بأول ذي همة ... دعته لما ليس بالنائل','201','2394','0');
INSERT INTO [t754] VALUES (2507,'يشمر للج عن ساقه ... ويغمره الموج في الساحل','201','2394','0');
INSERT INTO [t754] VALUES (2508,'أما للخلافة من مشفق ... على سيف دولتها الفاصل','201','2394','0');
INSERT INTO [t754] VALUES (2509,'يقد عداها بلا ضارب ... ويسري إليهم بلا حامل','201','2394','0');
INSERT INTO [t754] VALUES (2510,'تركت جماجمهم في النقا ... وما يتحصلن للناخل','201','2394','0');
INSERT INTO [t754] VALUES (2511,'وأنبت منهم ربيع السباع ... فأثنت بأحسانك الشامل','201','2394','0');
INSERT INTO [t754] VALUES (2512,'وعدت إلى حلب ظافرا ... كعود الحلي إلى العاطل','201','2394','0');
INSERT INTO [t754] VALUES (2513,'ومثل الذي دسته حافيا ... يؤثر في قدم الناعل','202','2394','0');
INSERT INTO [t754] VALUES (2514,'وكم لك من خبر شائع ... له شية الأبلق الجائل','202','2394','0');
INSERT INTO [t754] VALUES (2515,'ويوم شهاب بنية الردى ... بغيض الحضور إلى الواغل','202','2394','0');
INSERT INTO [t754] VALUES (2516,'تفك العناة وتغنى العفاة ... وتغفر للمذنب الجاهل','202','2394','0');
INSERT INTO [t754] VALUES (2517,'فهنأك النصر معطيكه ... وأرضاه سعيك في الاجل','202','2394','0');
INSERT INTO [t754] VALUES (2518,'فذي الدار أخون من مومس ... وأخدع من كفة الحابل','202','2394','0');
INSERT INTO [t754] VALUES (2519,'تفاني الرجال على حبها ... ولا يحصلون على طائل','202','2394','0');
INSERT INTO [t754] VALUES (2520,'أعلى الممالك ما يبني على الأسل ... والطعن عند محبيهن كالقبل','202','2394','0');
INSERT INTO [t754] VALUES (2521,'وما تقر سيوف في ممالكها ... حتى تقلقل دهرا قبل في القلل','202','2394','0');
INSERT INTO [t754] VALUES (2522,'مثل الأمير بغي أمرا فقربه ... طول الرماح وأيدي الخيل والإبل','202','2394','0');
INSERT INTO [t754] VALUES (2523,'وعزمة بعثتها همة زحل ... من تحتها بمكان الترب من زحل','202','2394','0');
INSERT INTO [t754] VALUES (2524,'على الفرات أعاصير وفي حلب ... توحش لملقى النصر مقتبل','202','2394','0');
INSERT INTO [t754] VALUES (2525,'تتلو أسنته الكتب التي نفذت ... ويجعل الخيل أبدالا من الرسل','202','2394','0');
INSERT INTO [t754] VALUES (2526,'يلقى الملوك فلا يلقى سوى جزر ... وما أعدوا فلا يلقى سوى نفل','202','2394','0');
INSERT INTO [t754] VALUES (2527,'صان الخليفة بالأبطال مهجته ... صيانة الذكر الهندي بالخلل','202','2394','0');
INSERT INTO [t754] VALUES (2528,'الفاعل الفعل لم يفعل لشدته ... والقائل القول لم يترك ولم يقل','202','2394','0');
INSERT INTO [t754] VALUES (2529,'والباعث الجيش قد غالت عجاجته ... ضوء النهار فصار الظهر كالطفل','203','2394','0');
INSERT INTO [t754] VALUES (2530,'الجو أضيق ما لاقاه ساطعها ... ومقلة الشمس فيه أحير المقل','203','2394','0');
INSERT INTO [t754] VALUES (2531,'ينال أبعد منها وهي ناظرة ... فما تقابله إلا على وجل','203','2394','0');
INSERT INTO [t754] VALUES (2532,'قد عرض السيف دون النازلات به ... وظاهر الحزم بين النفس والغيل','203','2394','0');
INSERT INTO [t754] VALUES (2533,'ووكل الظن بالأسرار فانكشفت ... له ضمائر أهل السهل والجبل','203','2394','0');
INSERT INTO [t754] VALUES (2534,'هو الشجاع يعد البخل من جبن ... وهو الجواد يعد الجبن من بخل','203','2394','0');
INSERT INTO [t754] VALUES (2535,'يعود من كل فتح غير مفتخر ... وقد أغذ إليه غير محتفل','203','2394','0');
INSERT INTO [t754] VALUES (2536,'ولا يجير عليه الدهر بغيته ... ولا تحصن درع مهجة البطل','203','2394','0');
INSERT INTO [t754] VALUES (2537,'إذا خلعت على عرض له حللا ... وجدتها منه في أبهى من الحلل','203','2394','0');
INSERT INTO [t754] VALUES (2538,'بذي الغباوة من إنشادها ضرر ... كما تضر رياح الورد بالجعل','203','2394','0');
INSERT INTO [t754] VALUES (2539,'لقد رأت كل عين منك مالئها ... وجربت خير سيف خيرة الدول','203','2394','0');
INSERT INTO [t754] VALUES (2540,'فما تكشفك الأعداء عن ملل ... من الحروب ولا الآراء عن زلل','204','2394','0');
INSERT INTO [t754] VALUES (2541,'وكم رجال بلا أرض لكثرتهم ... تركت جمعهم أرضا بلا رجل','204','2394','0');
INSERT INTO [t754] VALUES (2542,'ما زال طرفك يجري في دمائهم ... حتى مشى بك مشى الشارب الثمل','204','2394','0');
INSERT INTO [t754] VALUES (2543,'يا من يسير وحكم الناظرين له ... فيما يراه وحكم القلب في الجذل','204','2394','0');
INSERT INTO [t754] VALUES (2544,'إن السعادة فيما أنت فاعله ... وفقت مرتحلا أو غير مرتحل','204','2394','0');
INSERT INTO [t754] VALUES (2545,'أجر الجياد على ما كنت مجريها ... وخذ بنفسك في أخلاقك الأول','204','2394','0');
INSERT INTO [t754] VALUES (2546,'فلا هجمت بها إلا على ظفر ... ولا وصلت بها إلا إلى أمل','204','2394','0');
INSERT INTO [t754] VALUES (2547,'سر حل حيث تحله النوار ... وأراد فيك مرادك المقدار','204','2394','0');
INSERT INTO [t754] VALUES (2548,'وإذا ارتحلت فشيعتك سلامة ... حيث اتجهت وديمة مدرار','204','2394','0');
INSERT INTO [t754] VALUES (2549,'وأراك دهرك ما تحاول في العدى ... حتى كأن صروفه أنصار','204','2394','0');
INSERT INTO [t754] VALUES (2550,'وصدرت أغنم صادر عن مورد ... مرفوعة لقدومك الأبصار','204','2394','0');
INSERT INTO [t754] VALUES (2551,'أنت الذي بجح الزمان بذكره ... وتزينت بحديثه الأسمار','204','2394','0');
INSERT INTO [t754] VALUES (2552,'وإذا تنكر فالفناء عقابه ... وإذا عفا فعطاؤه الأعمار','204','2394','0');
INSERT INTO [t754] VALUES (2553,'وله وإن هب الملوك مواهب ... در الملوك لدرها أغبار','204','2394','0');
INSERT INTO [t754] VALUES (2554,'لله قلبك ما يخاف من الردى ... ويخاف أن يدنو إليك العار','204','2394','0');
INSERT INTO [t754] VALUES (2555,'وتحيد عن طبع الخلائق كله ... ويحيد عنك الجحفل الجرار','204','2394','0');
INSERT INTO [t754] VALUES (2556,'يا من يعز على الأعزة جاره ... ويذل في سطواته الجبار','205','2394','0');
INSERT INTO [t754] VALUES (2557,'كن حيث شئت فما تحول تنوفة ... دون اللقاء ولا يشط مزار','205','2394','0');
INSERT INTO [t754] VALUES (2558,'وبدون ما أنا من ودادك مضمر ... ينضى المطي ويقرب المستار','205','2394','0');
INSERT INTO [t754] VALUES (2559,'أن الذي خلفت خلفي ضائع ... ما لي على قلقي غليه خيار','205','2394','0');
INSERT INTO [t754] VALUES (2560,'وإذا صحبت فكل ماء مشرب ... لولا العيال وكل أرض دار','205','2394','0');
INSERT INTO [t754] VALUES (2561,'إذن الأمير بأن أعود إليهم ... صلة تسير بذكرها الأشعار','205','2394','0');
INSERT INTO [t754] VALUES (2562,'بنا منك فوق الرمل ما بك في الرمل ... وهذا الذي يضني كذاك الذي يبلي','205','2394','0');
INSERT INTO [t754] VALUES (2563,'كأنك أبصرت الذي بي وخفته ... إذا عشت فاخترت الحمام على الثكل','205','2394','0');
INSERT INTO [t754] VALUES (2564,'تركت خدود الغانيات وفوقها ... دموع تذيب الحسن في الأعين النجل','205','2394','0');
INSERT INTO [t754] VALUES (2565,'تبل الثرى سودا من المسك وحده ... وقد قطرت حمرا على الشعر الجثل','205','2394','0');
INSERT INTO [t754] VALUES (2566,'فإن تكن في قبر فإنك في الحشا ... وإن تك طفلا فالأسى ليس بالطفل','205','2394','0');
INSERT INTO [t754] VALUES (2567,'ألست من القوم الذي من رماحهم ... نداهم ومن قتلاهم مهجة البخل','205','2394','0');
INSERT INTO [t754] VALUES (2568,'بمولودهم صمت اللسان كغيره ... ولكن في أعطافه منطق الفضل','206','2394','0');
INSERT INTO [t754] VALUES (2569,'تسليهم علياؤهم عن مصابهم ... ويشغلهم كسب الثناء عن الشغل','206','2394','0');
INSERT INTO [t754] VALUES (2570,'أقل بلاء بالرزايا من القنا ... وأقدم بين الجحفلين من النبل','206','2394','0');
INSERT INTO [t754] VALUES (2571,'عزاءك سيف الدولة المقتدى به ... فإنك نصل والشدائد للنصل','206','2394','0');
INSERT INTO [t754] VALUES (2572,'مقيم من الهيجاء في كل منزل ... كأنك من كل الصوارم في أهل','206','2394','0');
INSERT INTO [t754] VALUES (2573,'ولم أرى أعصى منك للحزن عبرة ... وأثبت عقلا والقلوب بلا عقل','206','2394','0');
INSERT INTO [t754] VALUES (2574,'تخون المنايا عهده في سليله ... وتنصره بين الفوارس والرجل','206','2394','0');
INSERT INTO [t754] VALUES (2575,'ويبقى على مر الحوادث صبره ... ويبدو كما يبدو الفرند على الصقل','206','2394','0');
INSERT INTO [t754] VALUES (2576,'ومن كان ذا نفس كنفسك حرة ... ففيه لها مغن له مسلى','206','2394','0');
INSERT INTO [t754] VALUES (2577,'وما الموت إلا سارق دق شخصه ... يصول بلا كف ويسعى بلا رجل','206','2394','0');
INSERT INTO [t754] VALUES (2578,'يرد أبو الشبل الخميس عن ابنه ... ويسلمه عند الولادة للنمل','206','2394','0');
INSERT INTO [t754] VALUES (2579,'بنفسي وليد عاد من بعد حمله ... إلى بطن أم لا تطرق بالحمل','206','2394','0');
INSERT INTO [t754] VALUES (2580,'بدا وله وعد السحابة بالروى ... وصد وفينا غلة البلد المحل','207','2394','0');
INSERT INTO [t754] VALUES (2581,'وقد مدت الخيل العتاق عيونها ... إلى وقت تبديل الركاب من النعل','207','2394','0');
INSERT INTO [t754] VALUES (2582,'وريع له جيش العدو وما مشى ... وجاشت له الحرب الضروس وما تغلى','207','2394','0');
INSERT INTO [t754] VALUES (2583,'أيفطمه التوراب قبل فطامه ... ويأكله قبل البلوغ إلى الأكل','207','2394','0');
INSERT INTO [t754] VALUES (2584,'وقبل يرى من جوده ما رأيته ... ويسمع فيه ما سمعت من العذل','207','2394','0');
INSERT INTO [t754] VALUES (2585,'ويلقى كما تلقى من السلم والوغى ... ويسمى كما تسمى مليكا بلا مثل','207','2394','0');
INSERT INTO [t754] VALUES (2586,'توليه أوساط البلاد رماحه ... وتمنعه أطرافهن من العزل','207','2394','0');
INSERT INTO [t754] VALUES (2587,'نبكي لموتانا على غير رغبة ... تفوت من الدنيا ولا موهب جزل','207','2394','0');
INSERT INTO [t754] VALUES (2588,'إذا ما تأملت الزمان وصرفه ... تيقنت أن الموت ضرب من القتل','207','2394','0');
INSERT INTO [t754] VALUES (2589,'هل الولد المحبوب إلا تعلة ... وهل خلوة الحسناء إلا أذى البعل','207','2394','0');
INSERT INTO [t754] VALUES (2590,'وقد ذقت حلواء النين على الصبا ... فلا تحسبني قلت ما قلت عن جهل','208','2394','0');
INSERT INTO [t754] VALUES (2591,'وما تسع الأزمان علمي بأمرها ... وما تحسن الأيام تكتب ما أملي','208','2394','0');
INSERT INTO [t754] VALUES (2592,'وما الدهر أهل أن تؤمل عنده ... حيوة وأن يشتاق فيه إلى النسل','208','2394','0');
INSERT INTO [t754] VALUES (2593,'موقع الخيل من نداك طفيف ... ولو أن الجياد فيها ألوف','208','2394','0');
INSERT INTO [t754] VALUES (2594,'ومن اللفظ لفظة تجمع الوص ... ف وذاك المطهم المعروف','208','2394','0');
INSERT INTO [t754] VALUES (2595,'ما لنا في الندى عليك اختيار ... كل ما يمنح الشريف شريف','208','2394','0');
INSERT INTO [t754] VALUES (2596,'اخترت دهماء تين يا مطر ... ومن له في الفضائل الخير','208','2394','0');
INSERT INTO [t754] VALUES (2597,'وربما قالت العيون وقد ... يصدق فيها ويكذب النظر','208','2394','0');
INSERT INTO [t754] VALUES (2598,'أنت الذي لو يعاب في ملإ ... ما عيب إلا بأنه بشر','208','2394','0');
INSERT INTO [t754] VALUES (2599,'وأن إعطاءه الصوارم والخي ... ل وسمر الرماح والعكر','208','2394','0');
INSERT INTO [t754] VALUES (2600,'فاضح أعدائه كأنهم ... له يقلون كلما كثروا','209','2394','0');
INSERT INTO [t754] VALUES (2601,'أعاذك الله من سهامهم ... ومخطىء من رميه القمر','209','2394','0');
INSERT INTO [t754] VALUES (2602,'فعلت بنا فعل السماء بأرضه ... خلع الأمير وحقه لم نقضه','209','2394','0');
INSERT INTO [t754] VALUES (2603,'فكأن صحة نسجها من لفظه ... وكأن حسن نقائها من عرضه','209','2394','0');
INSERT INTO [t754] VALUES (2604,'وإذا وكلت إلى كريم رأيه ... في الجود بأن مذيقه من محضه','209','2394','0');
INSERT INTO [t754] VALUES (2605,'لا الحلم جاد به ولا بمثاله ... لولا ادكار وداعه وزياله','209','2394','0');
INSERT INTO [t754] VALUES (2606,'إن المعيد لنا المنام خياله ... كانت إعادته خيال خياله','209','2394','0');
INSERT INTO [t754] VALUES (2607,'بتنا يناولنا المدام بكفه ... من ليس يخطر أن نراه بباله','210','2394','0');
INSERT INTO [t754] VALUES (2608,'نجني الكواكب من قلائد جيدة ... وننال عين الشمس من خلخاله','210','2394','0');
INSERT INTO [t754] VALUES (2609,'بنتم عن العين القريحة فيكم ... وسكنتم ظن الفؤاد الواله','210','2394','0');
INSERT INTO [t754] VALUES (2610,'فدنوتم ودنوكم من عنده ... وسمحتم وسماحكم من ماله','210','2394','0');
INSERT INTO [t754] VALUES (2611,'إني لأبغض طيف من أحببته ... إذ كان يهجرنا زمان وصاله','210','2394','0');
INSERT INTO [t754] VALUES (2612,'مثل الصبابة والكآبة والأسى ... فارقته فحدثن من ترحاله','210','2394','0');
INSERT INTO [t754] VALUES (2613,'وقد استقدت من الهوى وأذقته ... من عفتي ما ذقت من بلباله','210','2394','0');
INSERT INTO [t754] VALUES (2614,'ولقد ذخرت لكل أرض ساعة ... تستجفل الضرغام عن أشباله','210','2394','0');
INSERT INTO [t754] VALUES (2615,'تلقى الوجوه بها الوجوه وبينها ... ضرب يجول الموت في أجواله','211','2394','0');
INSERT INTO [t754] VALUES (2616,'ولقد خبأت من الكلام سلافه ... وسقيت من نادمت من جرياله','211','2394','0');
INSERT INTO [t754] VALUES (2617,'وإذا تعثرت الجياد بسهله ... برزت غير معثر بجباله','211','2394','0');
INSERT INTO [t754] VALUES (2618,'وحكمت في البلد العراء بناعج ... معتاده مجتابه مغتاله','211','2394','0');
INSERT INTO [t754] VALUES (2619,'يمشي كما عدت المطي وراءه ... ويزيد وقت جمامها وكلاله','211','2394','0');
INSERT INTO [t754] VALUES (2620,'وتراع غير معقلات حوله ... فيفوتها متجفلا بعقاله','211','2394','0');
INSERT INTO [t754] VALUES (2621,'فغدا النجاع وراح في أخفافه ... وغدا المراح وراح في إرقاله','211','2394','0');
INSERT INTO [t754] VALUES (2622,'وشركت دولة هاشم في سيفها ... وشققت خيس الملك عن ريباله','211','2394','0');
INSERT INTO [t754] VALUES (2623,'عن ذا الذي حرم الليوث كماله ... ينسى الفريسة خوفه بجماله','211','2394','0');
INSERT INTO [t754] VALUES (2624,'وتواضع الأمراء حول سريره ... ويرى المحبة وهي من آكاله','211','2394','0');
INSERT INTO [t754] VALUES (2625,'ويميت قبل قتاله ويبش قب ... ل نواله وينيل قبل سؤاله','211','2394','0');
INSERT INTO [t754] VALUES (2626,'أن الرياح إاذ عمدن لناخر ... أغناه مقبلها عن استعجاله','211','2394','0');
INSERT INTO [t754] VALUES (2627,'أعطى ومن على الملوك بعفوه ... حتى تساوى الناس في إفضاله','211','2394','0');
INSERT INTO [t754] VALUES (2628,'وإذا غنوا بعطائه عن هزه ... وإلى فأغنى أن يقولوا واله','211','2394','0');
INSERT INTO [t754] VALUES (2629,'وكأنما جدواه من إكثاره ... حسد لسائله على إقلاله','211','2394','0');
INSERT INTO [t754] VALUES (2630,'غرب النجوم فغرن دون همومه ... وطلعن حين طلعن دون مناله','211','2394','0');
INSERT INTO [t754] VALUES (2631,'والله يسعد كل يوم جده ... ويزيد من أعدائه في إله','212','2394','0');
INSERT INTO [t754] VALUES (2632,'لو لم تكن تجري على أسيافه ... مهجاتهم لجرت على إقباله','212','2394','0');
INSERT INTO [t754] VALUES (2633,'لم يتركوا أثرا عليه من الوغا ... إلا دماءهم على سرباله','212','2394','0');
INSERT INTO [t754] VALUES (2634,'فلمثله جمع العرمرم نفسه ... وبمثله انفصمت عرى اقتاله','212','2394','0');
INSERT INTO [t754] VALUES (2635,'يا أيها القمر المباهي وجهه ... لا تكذبن فلست من أشكاله','212','2394','0');
INSERT INTO [t754] VALUES (2636,'وإذا طما البحر المحيط فقل له ... دع ذا فإنك عاجز عن حاله','212','2394','0');
INSERT INTO [t754] VALUES (2637,'وهب الذي ورث الجدود وما رأى ... أفعالهم لأبن بلا أفعاله','212','2394','0');
INSERT INTO [t754] VALUES (2638,'حتى إذا فنى التراث سوى العلى ... قصد العداة من القنا بطواله','212','2394','0');
INSERT INTO [t754] VALUES (2639,'وبأرعن لبس العجاج إليهم ... فوق الحديد وجر من أذياله','212','2394','0');
INSERT INTO [t754] VALUES (2640,'فكأنما قذى النهار بنقعه ... أو غض عنه الطرف من إجلاله','212','2394','0');
INSERT INTO [t754] VALUES (2641,'الجيش جيشك غير أنك جيشه ... في قلبه ويمينه وشماله','212','2394','0');
INSERT INTO [t754] VALUES (2642,'ترد الطعان المر عن فرسانه ... وتنازل الأبطال عن أبطاله','212','2394','0');
INSERT INTO [t754] VALUES (2643,'كل يريد رجال لحياته ... يا من يريد حياته لرجاله','212','2394','0');
INSERT INTO [t754] VALUES (2644,'دون الحلاوة في الزمان مرارة ... لا تختطي إلا على أهواله','213','2394','0');
INSERT INTO [t754] VALUES (2645,'فلذاك جاوزها على وحده ... وسعى بمنصله إلى آماله','213','2394','0');
INSERT INTO [t754] VALUES (2646,'أنا منك بين فضائل ومكارم ... ومن ارتياحك في غمام دائم','213','2394','0');
INSERT INTO [t754] VALUES (2647,'ومن احتقارك كل من تحبوبه ... فيما ألاحظه بعيني حالم.','213','2394','0');
INSERT INTO [t754] VALUES (2648,'إن الخليفة لم يسمك سيفها ... حتى بلاك فكنت عين الصارم','213','2394','0');
INSERT INTO [t754] VALUES (2649,'فإذا تتوج كنت درة تاجه ... وإذا تختم كنت فص الخاتم','213','2394','0');
INSERT INTO [t754] VALUES (2650,'وإذا انتضاك على العدى في معرك ... هلكوا وضاقت كفه بالقائم','213','2394','0');
INSERT INTO [t754] VALUES (2651,'أبدى سخاؤك عجز كل مشمر ... في وصفه وأضاق ذرع الكاتم','213','2394','0');
INSERT INTO [t754] VALUES (2652,'أيدري الربع أي دم أراقا ... واي قلوب هذا الركب شاقا','213','2394','0');
INSERT INTO [t754] VALUES (2653,'لنا ولأهله أبدا قلوب ... تلاقى في جسوم ما تلاقي','213','2394','0');
INSERT INTO [t754] VALUES (2654,'وما عفت الرياح له محلا ... عفاه من حدا بهم وساقا','213','2394','0');
INSERT INTO [t754] VALUES (2655,'فليت هوى الأحبة كان عدلا ... فحمل كل قلب ما أطاقا','213','2394','0');
INSERT INTO [t754] VALUES (2656,'نظرت إليهم والعين شكري ... فصارت كلها للدمع مأقا','213','2394','0');
INSERT INTO [t754] VALUES (2657,'وقد أخذ التمام البدر فيهم ... وأعطاني من السقم المحاقا','213','2394','0');
INSERT INTO [t754] VALUES (2658,'وبين الفرع والقدمين نور ... يقود بلا أزمتها النياقا','213','2394','0');
INSERT INTO [t754] VALUES (2659,'وطرف إن سقى العشاق كأسا ... بها نقص سقانيها دهاقا','214','2394','0');
INSERT INTO [t754] VALUES (2660,'وخصر تثبت الأبصار فيه ... كأن عليه من حدق نطاقا','214','2394','0');
INSERT INTO [t754] VALUES (2661,'سلى عن سيرتي فرسي وسيفي ... ورمحي والهملعة الدفاقا','214','2394','0');
INSERT INTO [t754] VALUES (2662,'تركنا من وراء العيس نجدا ... ونكبنا السماوة والعراقا','214','2394','0');
INSERT INTO [t754] VALUES (2663,'فما زالت ترى والليل داج ... لسيف الدولة الملك ائتلاقا','214','2394','0');
INSERT INTO [t754] VALUES (2664,'أدلتها رياح المسك منه ... إذا فتحت مناخرها انتشاقا','214','2394','0');
INSERT INTO [t754] VALUES (2665,'أباح الوحش يا وحش الأعادي ... فلم تتعرضين له الرفاقا','214','2394','0');
INSERT INTO [t754] VALUES (2666,'ولو تبعت ما طرحت قناه ... لكفك عن رذايانا وعاقا','214','2394','0');
INSERT INTO [t754] VALUES (2667,'ولو سرنا إليه في طريق ... من النيران لم نخف احتراقا','214','2394','0');
INSERT INTO [t754] VALUES (2668,'إمام للأئمة من قريش ... إلى من يتقون له شقاقا','214','2394','0');
INSERT INTO [t754] VALUES (2669,'يكون لهم إذا غضبوا حساما ... وللهيجاء حين تقوم ساقا','214','2394','0');
INSERT INTO [t754] VALUES (2670,'فلا تستنكرون له ابتساما ... إذا فهق المكر دما وضاقا','214','2394','0');
INSERT INTO [t754] VALUES (2671,'وقد ضمنت له المهج العوالي ... وحمل همه الخيل العتاقا','214','2394','0');
INSERT INTO [t754] VALUES (2672,'إذا أنعلن في آثار قوم ... وإن بعدوا جعلنهم طراقا','214','2394','0');
INSERT INTO [t754] VALUES (2673,'وإن نقع الصريخ إلى مكان ... نصبن له مؤللة دقاقا','215','2394','0');
INSERT INTO [t754] VALUES (2674,'فكان الطعن بينهما جوابا ... وكان اللبث بينهما فواقا','215','2394','0');
INSERT INTO [t754] VALUES (2675,'ملاقية نواصيها المنايا ... معاودة فوراسها العناقا','215','2394','0');
INSERT INTO [t754] VALUES (2676,'تبيت رماحه فوق الهوادي ... وقد ضرب العجاج لها رواقا','215','2394','0');
INSERT INTO [t754] VALUES (2677,'تميل كأن في الأبطال خمرا ... عللن به اصطباحا واغتباقا','215','2394','0');
INSERT INTO [t754] VALUES (2678,'تعجبت المدام وقد حساها ... فلم يسكر وجاد فما أفاقا','215','2394','0');
INSERT INTO [t754] VALUES (2679,'وزنا قيمة الدهماء منه ... ووفينا القيان به الصداقا','215','2394','0');
INSERT INTO [t754] VALUES (2680,'وحاشا لأرتياحك أن يباري ... وللكرم الذي لك أن يباقي','215','2394','0');
INSERT INTO [t754] VALUES (2681,'ولكنا نداعب منك قرما ... تراجعت القروم له حقاقا','215','2394','0');
INSERT INTO [t754] VALUES (2682,'فتى لا تسلب القتلى يداه ... ويسلب عفوه الأسرى الوثاقا','215','2394','0');
INSERT INTO [t754] VALUES (2683,'ولم تأت الجميل إلى سهوا ... ولم أظفر به منك استراقا','215','2394','0');
INSERT INTO [t754] VALUES (2684,'فأبلغ حاسدي عليك أني ... كبا برق يحاول بي لحاقا','215','2394','0');
INSERT INTO [t754] VALUES (2685,'وهل تغنى الرسائل في عدو ... إذا ما لم ين ظبي رقاقا','216','2394','0');
INSERT INTO [t754] VALUES (2686,'إذا ما الناس جربهم لبيب ... فإني قد أكلتهم وذاقا','216','2394','0');
INSERT INTO [t754] VALUES (2687,'فلم أر ودهم إلا خداعا ... ولم أر دينهم إلا نفاقا','216','2394','0');
INSERT INTO [t754] VALUES (2688,'يقصر عن يمينك كل بحر ... وعما لم تلقه ما ألاقا','216','2394','0');
INSERT INTO [t754] VALUES (2689,'ولولا قدرة الخلاق قلنا ... أعمدا كان خلقك أم وفاقا','216','2394','0');
INSERT INTO [t754] VALUES (2690,'فلا حطت لك الهيجاء سرجا ... ولا ذاقت لك الدنيا فراقا','216','2394','0');
INSERT INTO [t754] VALUES (2691,'ما سدكت علة بمولود ... أكرم من تغلب بن داود','216','2394','0');
INSERT INTO [t754] VALUES (2692,'يأنف من ميتة الفراش وقد ... حل به أصدق المواعيد','216','2394','0');
INSERT INTO [t754] VALUES (2693,'ومثله أنكر الممات على ... غير سروج السوابح القود','216','2394','0');
INSERT INTO [t754] VALUES (2694,'بعد عثار القنا بلبته ... وضربه أرؤس الصناديد','216','2394','0');
INSERT INTO [t754] VALUES (2695,'وخوضه غمر كل مهلكة ... للذمر فيها فؤاد رعديد','216','2394','0');
INSERT INTO [t754] VALUES (2696,'فإن صبرنا فإننا صبر ... وإن بكينا فغير مردود','216','2394','0');
INSERT INTO [t754] VALUES (2697,'وإن جزعنا له فلا عجب ... ذا الجزر في البحر غير معهود','216','2394','0');
INSERT INTO [t754] VALUES (2698,'أين الهبات التي يفرقها ... على الزرافات والمواحيد','216','2394','0');
INSERT INTO [t754] VALUES (2699,'سالم أهل الوداد بعدهم ... يسلم للحزن لا لتخليد','216','2394','0');
INSERT INTO [t754] VALUES (2700,'فما ترجى النفوس من زمن ... أحمد حاليه غير محمود','216','2394','0');
INSERT INTO [t754] VALUES (2701,'إن نيوب الزمان تعرفني ... أنا الذي طال عجمها عودي','217','2394','0');
INSERT INTO [t754] VALUES (2702,'وفي ما قارع الخطوب وما ... آنسني بالمصائب السود','217','2394','0');
INSERT INTO [t754] VALUES (2703,'ما كنت عنه إذ استغاثك يا ... سيف بني هاشم بمغمود','217','2394','0');
INSERT INTO [t754] VALUES (2704,'يا أكرم الأكرمين يا ملك الأم ... لاك طرا يا أصيد الصيد','217','2394','0');
INSERT INTO [t754] VALUES (2705,'قد مات من قبلها فأنشره ... وقع قنا الخط في اللغاديد','217','2394','0');
INSERT INTO [t754] VALUES (2706,'ورميك الليل بالجنود وقد ... رميت أجفانهم بتسهيد','217','2394','0');
INSERT INTO [t754] VALUES (2707,'فصبحتهم رعالها شربا ... بين ثبات إلى عباديد','217','2394','0');
INSERT INTO [t754] VALUES (2708,'تحمل أغمادها الفداء لهم ... فانتقدوا الضرب كالأخاديد','217','2394','0');
INSERT INTO [t754] VALUES (2709,'موقعه في فراش هامهم ... وريحه في مناخر السيد','217','2394','0');
INSERT INTO [t754] VALUES (2710,'أفنى الحياة التي وهبت له ... في شرف شاكرا وتسويد','217','2394','0');
INSERT INTO [t754] VALUES (2711,'سقيم جسم صحيح مكرمة ... منجود كرب غياث منجود','217','2394','0');
INSERT INTO [t754] VALUES (2712,'ثم غدى قده الحمام وما ... تخلص منه يمين مصفود','217','2394','0');
INSERT INTO [t754] VALUES (2713,'لا ينقص الهالكون من عدد ... منه على مضيق البيد','217','2394','0');
INSERT INTO [t754] VALUES (2714,'تهب في ظهرها كتائبه ... هبوب أرواحها المراويد','217','2394','0');
INSERT INTO [t754] VALUES (2715,'أول حرف من اسمه كتبت ... سنابك الخيل في الجلاميد','217','2394','0');
INSERT INTO [t754] VALUES (2716,'مهما يعزي الفتى الأمير به ... فلا بإقدامه ولا الجود','218','2394','0');
INSERT INTO [t754] VALUES (2717,'ومن منانا بقاؤه أبدا ... حتى يعزي بكل مولود','218','2394','0');
INSERT INTO [t754] VALUES (2718,'لا عدم المشيع المشيع ... ليت الرياح صنع ما تصنع','218','2394','0');
INSERT INTO [t754] VALUES (2719,'بكرن ضرأ وبكرت تنفع ... وسجسج أنت وهن زعزع','218','2394','0');
INSERT INTO [t754] VALUES (2720,'وواحد أنت وهن أربع ... وأنت نبع والملوك خروع','218','2394','0');
INSERT INTO [t754] VALUES (2721,'لعيني كل يوم منك حظ ... تحير منه في أمر عجاب','218','2394','0');
INSERT INTO [t754] VALUES (2722,'حمالة ذا الحسام على حسام ... وموقع ذا السحاب على سحاب','218','2394','0');
INSERT INTO [t754] VALUES (2723,'تجف الأرض من هذا الرباب ... ويخلق ما كساها من ثياب','218','2394','0');
INSERT INTO [t754] VALUES (2724,'وما ينفك منك الدهر رطبا ... وما ينفك غيثك في انسكاب','218','2394','0');
INSERT INTO [t754] VALUES (2725,'تسايرك السواري والغوادي ... مسايرة الأحباء الطراب','218','2394','0');
INSERT INTO [t754] VALUES (2726,'تفيد الجود منك فتحتذيه ... وتعجز عن خلائقك العذاب','218','2394','0');
INSERT INTO [t754] VALUES (2727,'أنا بالوشاة إذا ذكرتك أشبه ... تأتي الندى فيشاع عنك فتكره','218','2394','0');
INSERT INTO [t754] VALUES (2728,'فإذا رأيتك دون عرض عارضا ... أيقنت أن الله يبغي نصره','218','2394','0');
INSERT INTO [t754] VALUES (2729,'رب نجيع بسيف الدولة انسفكا ... ورب قافية غاظت به ملكا','219','2394','0');
INSERT INTO [t754] VALUES (2730,'من يعرف الشمس لا ينكر مطالعها ... أو يبصر الخيل لا يستكرم الرمكا','219','2394','0');
INSERT INTO [t754] VALUES (2731,'تسر بالمال بعض المال تملكه ... إن البلاد وإن العالمين لكا','219','2394','0');
INSERT INTO [t754] VALUES (2732,'يؤمم ذا السيف آماله ... ولا يفعل السيف أفعاله','219','2394','0');
INSERT INTO [t754] VALUES (2733,'إذا سار في مهمة عمه ... وإن سار في جبل طاله','219','2394','0');
INSERT INTO [t754] VALUES (2734,'وأنت بما نلتنا مالك ... يثمر من ماله ماله','219','2394','0');
INSERT INTO [t754] VALUES (2735,'كأنك ما بيننا ضيغم ... يرشح للفرس اشباله','219','2394','0');
INSERT INTO [t754] VALUES (2736,'لقد نسبوا الخيام إلى علاء ... أبيت قبوله كل الإباء','219','2394','0');
INSERT INTO [t754] VALUES (2737,'وما سلمت فوقك للثريا ... ولا سلمت فوقك للسماء','219','2394','0');
INSERT INTO [t754] VALUES (2738,'وقد أوحشت أرض الشام حتى ... سلبت ربوعها ثوب البهاء','219','2394','0');
INSERT INTO [t754] VALUES (2739,'تنفس والعواصم منك عشر ... فيعرف طيب ذلك في الهواء','219','2394','0');
INSERT INTO [t754] VALUES (2740,'أغلب الحيزين ما كنت فيه ... وولى النماء من تنميه','219','2394','0');
INSERT INTO [t754] VALUES (2741,'ذا الذي أنت جده وأبوه ... دنية دون جده وأبيه','220','2394','0');
INSERT INTO [t754] VALUES (2742,'ألا أذن فما أذكرت ناسي ... ولا لينت قلبا وهو قاسي','220','2394','0');
INSERT INTO [t754] VALUES (2743,'ولا شغل الأمير عن المعالي ... ولا عن ذكر خالقه بكاس','220','2394','0');
INSERT INTO [t754] VALUES (2744,'فدنياك أهدى الناس سهما إلى قلبي ... واقتلهم للدارعين بلا حرب','220','2394','0');
INSERT INTO [t754] VALUES (2745,'تفرد بالأحكام في أهله الهوى ... فأنت جميل الخلف مستحسن الكذب','220','2394','0');
INSERT INTO [t754] VALUES (2746,'وإني لممنوع المقاتل في الوغى ... وإن كنت مبذول المقاتل في الحب','220','2394','0');
INSERT INTO [t754] VALUES (2747,'ومن خلقت عيناك بين جفونه ... أصاب الحدور السهل في المرتقى الصعب','220','2394','0');
INSERT INTO [t754] VALUES (2748,'إذا كان مدح فالنسيب المقدم ... أكل فصيح قال شعرا متيم','220','2394','0');
INSERT INTO [t754] VALUES (2749,'لحب ابن عبد الله أولى فإنه ... به يبدأ الذكر الجميل ويختم','220','2394','0');
INSERT INTO [t754] VALUES (2750,'أطعت الغواني قبل مطمح ناظري ... إلى منظر يصغرن عنه ويعظم','220','2394','0');
INSERT INTO [t754] VALUES (2751,'تعرضسيف الدولة الدهر كله ... يطبق في أوصاله ويصمم','220','2394','0');
INSERT INTO [t754] VALUES (2752,'فجاز له حتى على الشمس حكمه ... وبان له حتى على البدر ميسم','221','2394','0');
INSERT INTO [t754] VALUES (2753,'كأن العدى في أرضهم خلفاؤه ... فإن شاء حازوها وإن شاء سلموا','221','2394','0');
INSERT INTO [t754] VALUES (2754,'ولا كتب إلا المشرفية عنده ... ولا رسل إلا الخميس العرمرم','221','2394','0');
INSERT INTO [t754] VALUES (2755,'ولم يخل من نصر له من له يد ... ولم يخل من شكر له من له فم','221','2394','0');
INSERT INTO [t754] VALUES (2756,'ولم يخل من أسمائه عود منبر ... ولم يخل دينار ولم يخل درهم','221','2394','0');
INSERT INTO [t754] VALUES (2757,'ضروب وما بين الحسامين ضيق ... بصير وما بين الشجاعين مظلم','221','2394','0');
INSERT INTO [t754] VALUES (2758,'تبارى نجوم القذف في كل ليلة ... نجوم له منهن ورد وأدهم','221','2394','0');
INSERT INTO [t754] VALUES (2759,'يطأن من الأبطال من لا حملنه ... ومن قصد المران ما لا يقوم','221','2394','0');
INSERT INTO [t754] VALUES (2760,'فهن مع السيدان في البر عسل ... وهن مع النينان في البحر عوم','221','2394','0');
INSERT INTO [t754] VALUES (2761,'وهن مع الغزلان في الواد كمن ... وهن مع العقبان في النيق حوم','221','2394','0');
INSERT INTO [t754] VALUES (2762,'إذا جلب الناس الوشيج فإنه ... بهن وفي لباتهن يحطم','221','2394','0');
INSERT INTO [t754] VALUES (2763,'بغرته في الحرب والسلم والحجى ... وبذل اللهى والحمد والمجد معلم','221','2394','0');
INSERT INTO [t754] VALUES (2764,'يقر له بالفضل من لا يؤده ... ويقضي له بالسعد من لا ينجم','222','2394','0');
INSERT INTO [t754] VALUES (2765,'أجار على الأيام حتى ظننته ... تطالبه بالرد عاد وجرهم','222','2394','0');
INSERT INTO [t754] VALUES (2766,'ضلالا لهذي الريح ماذا تريده ... وهديا لهذا السيل ماذا يؤمم','222','2394','0');
INSERT INTO [t754] VALUES (2767,'الم يسال الوبل الذي رام ثنينا ... فيخبره عنك الحديد المثلم','222','2394','0');
INSERT INTO [t754] VALUES (2768,'ولما تلقاك السحاب بصوبه ... تلقاه أعلى منه كعبا وأكرم','222','2394','0');
INSERT INTO [t754] VALUES (2769,'فباشر وجها طالما باشر القنا ... وبل ثيابا طالما بلها الدم','222','2394','0');
INSERT INTO [t754] VALUES (2770,'تلاك وبعض الغيث تبع بعضه ... من الشام يتلو الحاذق المتعلم','222','2394','0');
INSERT INTO [t754] VALUES (2771,'فزار التي زارت بك الخيل قبرها ... وجشمه الشوق الذي تتجشم','222','2394','0');
INSERT INTO [t754] VALUES (2772,'ولما عرضت الجيش كان بهاؤه ... على الفارس المرخى الذؤابة منهم','222','2394','0');
INSERT INTO [t754] VALUES (2773,'حواليه بجر للتجافيف مائج ... يسير به طود من الخيل ايهم','222','2394','0');
INSERT INTO [t754] VALUES (2774,'تساوت به الأقطار حتى كأنه ... يجمع أشتات الجبال وينظم','222','2394','0');
INSERT INTO [t754] VALUES (2775,'وكل فتى للحرب فوق جبينه ... من الضرب سطر بالأسنة معجم.','222','2394','0');
INSERT INTO [t754] VALUES (2776,'يمد يديه في المفاضة ضيغم ... وعينيه من تحت التريكة أرقم','222','2394','0');
INSERT INTO [t754] VALUES (2777,'كأجناسها راياتها وشعارها ... وما لبسته والسلاح المسمم','222','2394','0');
INSERT INTO [t754] VALUES (2778,'وأدبها طول القتال فطرفه ... يشير إليها من بعيد فتهم','222','2394','0');
INSERT INTO [t754] VALUES (2779,'تجاوبه فعلا وما تسمع الوحا ... ويسمعها لحظا وما يتكلم','223','2394','0');
INSERT INTO [t754] VALUES (2780,'تجانف عن ذات اليمين كأنها ... ترق لميافارقين وترحم','223','2394','0');
INSERT INTO [t754] VALUES (2781,'ولو زحمتها بالمناكب زحمة ... درت أي سوريها الضعيف المهدم','223','2394','0');
INSERT INTO [t754] VALUES (2782,'على كل طاو تحت طاو كأنه ... من الدم يسقى أو من اللحم يطعم','223','2394','0');
INSERT INTO [t754] VALUES (2783,'لها في الوغى زي الفوارس فوقها ... فكل حصان دارع متلثم','223','2394','0');
INSERT INTO [t754] VALUES (2784,'وما ذاك بخلا بالنفوس على القنا ... ولكن صدم الشرق بالشر احزم','223','2394','0');
INSERT INTO [t754] VALUES (2785,'أتحسب بيض الهند أصلك أصلها ... وأنك منها سآء ما تتوهم','223','2394','0');
INSERT INTO [t754] VALUES (2786,'إذا نحن سميناك خلنا سيوفنا ... من التيه في أغمادها تتبسم','223','2394','0');
INSERT INTO [t754] VALUES (2787,'ولم نر ملكا قط يدعى بدونه ... فيرضى ولكن يجهلون وتحلم','223','2394','0');
INSERT INTO [t754] VALUES (2788,'أخذت على الأرواح كل ثنية ... من العيش تعطى من تشاء وتحرم','223','2394','0');
INSERT INTO [t754] VALUES (2789,'فلا موت إلا من سنانك يتقي ... ولا رزق إلا من يمينك يقسم','223','2394','0');
INSERT INTO [t754] VALUES (2790,'أينفع في الخيمة العذل ... وتشمل من دهرها يشمل','224','2394','0');
INSERT INTO [t754] VALUES (2791,'وتعلو الذي زحل تحته ... محال لعمرك ما تسأل','224','2394','0');
INSERT INTO [t754] VALUES (2792,'فلم لا تلوم الذي لامها ... وما فص خاتمه يذبل','224','2394','0');
INSERT INTO [t754] VALUES (2793,'تضيق بشخصك أرجاؤها ... ويركض في الواحد الجحفل','224','2394','0');
INSERT INTO [t754] VALUES (2794,'وتقصر ما كنت في جوفها ... وتركز فيها القنا الذبل','224','2394','0');
INSERT INTO [t754] VALUES (2795,'وكيف تقوم على راحة ... كأن البحار لها أنمل','224','2394','0');
INSERT INTO [t754] VALUES (2796,'فليت وقارك فرقته ... وحملت أرضك ما تحمل','224','2394','0');
INSERT INTO [t754] VALUES (2797,'فصار الأنام به سادة ... وسدتهم بالذي يفضل','224','2394','0');
INSERT INTO [t754] VALUES (2798,'رأت لو نورك في لونها ... كلون الغزالة لا يغسل','224','2394','0');
INSERT INTO [t754] VALUES (2799,'وأن لها شرفا باذخا ... وأن الخيام بها تخجل','224','2394','0');
INSERT INTO [t754] VALUES (2800,'فلا تنكرن لها صرعة ... فمن فرح النفس ما يقتل','224','2394','0');
INSERT INTO [t754] VALUES (2801,'ولو بلغ الناس ما بلغت ... لخانتهم حولك الأرجل','224','2394','0');
INSERT INTO [t754] VALUES (2802,'ولما أمرت بتطنيبها ... أشيع بأنك لا ترحل','224','2394','0');
INSERT INTO [t754] VALUES (2803,'فما اعتمد الله تقويضها ... ولكن أشار بما تفعل','224','2394','0');
INSERT INTO [t754] VALUES (2804,'وعرف أنك من همه ... وأنك في نصره ترفل','224','2394','0');
INSERT INTO [t754] VALUES (2805,'فما العاندون وما أثلوا ... وما الحاسدون وما قالوا','224','2394','0');
INSERT INTO [t754] VALUES (2806,'هم يطلبون فمن أدركوا ... وهم يكذبون فمن يقبل','225','2394','0');
INSERT INTO [t754] VALUES (2807,'وهم يتمنون ما يشتهون ... ومن دونه جدك المقبل','225','2394','0');
INSERT INTO [t754] VALUES (2808,'وملمومة زرد ثوبها ... ولكنه بالقنا مخمل','225','2394','0');
INSERT INTO [t754] VALUES (2809,'يفاجىء جيشا بها حينه ... وينذر جيشا بها القسطل','225','2394','0');
INSERT INTO [t754] VALUES (2810,'جعلتك بالقلب لي عدة ... لأنك باليد ولا تجعل','225','2394','0');
INSERT INTO [t754] VALUES (2811,'لقد رفع الله من دولة ... لها منك يا سيفها منصل','225','2394','0');
INSERT INTO [t754] VALUES (2812,'فإن طبعت قبلك المرهفات ... فإنك من قبلها المقصل','225','2394','0');
INSERT INTO [t754] VALUES (2813,'وإن جاد قبلك قوم مضوا ... فإنك في الكرم الأول','225','2394','0');
INSERT INTO [t754] VALUES (2814,'وكيف تقصر في غاية ... وأمك من ليثها مشبل','225','2394','0');
INSERT INTO [t754] VALUES (2815,'وقد ولدتك فقال الورى ... ألم تكن الشمس ولا تنجل','225','2394','0');
INSERT INTO [t754] VALUES (2816,'فتبا لدين عبيد النجوم ... ومن يدعى أنها تعقل','225','2394','0');
INSERT INTO [t754] VALUES (2817,'وقد عرفتك فما بالها ... تراك تراها ولا تنزل','225','2394','0');
INSERT INTO [t754] VALUES (2818,'ولو بتما عند قدريكما ... لبت وأعلاكما الأسفل','225','2394','0');
INSERT INTO [t754] VALUES (2819,'أنلت عبادك ما أملوا ... أنالك ربك ما تأمل','225','2394','0');
INSERT INTO [t754] VALUES (2820,'لهذا اليوم بعد غد أريج ... ونار في العدو لها أجيج','226','2394','0');
INSERT INTO [t754] VALUES (2821,'تبيت بها الحواصن آمنات ... ويسلم في مسالكها الحجيج','226','2394','0');
INSERT INTO [t754] VALUES (2822,'فلا زالت عداتك حيث كانت ... فرائس أيها الأسد المهيج','226','2394','0');
INSERT INTO [t754] VALUES (2823,'عرفتك والصفوف معبيات ... وأنت بغير سيفك لا تعيج','226','2394','0');
INSERT INTO [t754] VALUES (2824,'ووجه البحر يعرف من بعيد ... إذا يسجو فكيف إذا يموج','226','2394','0');
INSERT INTO [t754] VALUES (2825,'بأرض تهلك الأشواط فيها ... إذا ملئت من الركض الفروج','226','2394','0');
INSERT INTO [t754] VALUES (2826,'تحاول نفس ملك الروم فيها ... فتقديه رعيته العلوج','226','2394','0');
INSERT INTO [t754] VALUES (2827,'أبالغمرات توعدنا النصارى ... ونحن نجومها وهي البروج','226','2394','0');
INSERT INTO [t754] VALUES (2828,'وفينا السيف حملته صدوق ... إذا لاقى وغارته لجوج','226','2394','0');
INSERT INTO [t754] VALUES (2829,'نعوذه من العيان بأسا ... ويكثر بالدعاء له الضجيج','226','2394','0');
INSERT INTO [t754] VALUES (2830,'رضينا والدمستق غير راض ... بما حكم القواضب والوشيج','226','2394','0');
INSERT INTO [t754] VALUES (2831,'وإن يقدم فقد زرنا سمندو ... وإن يحجم فموعدنا الخليج','226','2394','0');
INSERT INTO [t754] VALUES (2832,'غيري بأكثر هذا الناس ينخدع ... إن قاتلوا جبنوا أو حدثوا شجعوا','226','2394','0');
INSERT INTO [t754] VALUES (2833,'أهل الحفيظة إلا أن تجربهم ... وفي التجارب بعد الغي ما يزع','226','2394','0');
INSERT INTO [t754] VALUES (2834,'وما الحيوة ونفسي بعد ما علمت ... أن الحيوة كما لا تشتهي طبع','227','2394','0');
INSERT INTO [t754] VALUES (2835,'ليس الجمال لوجه صح مارنه ... أنف العزيز بقطع العز يجتدع','227','2394','0');
INSERT INTO [t754] VALUES (2836,'أأطرح المجد عن كتفي وأطلبه ... وأترك الغيث في غمدي وأنتجع','227','2394','0');
INSERT INTO [t754] VALUES (2837,'والمشرفية لا زالت مشرفة ... دواء كل كريم أو هي الوجع','227','2394','0');
INSERT INTO [t754] VALUES (2838,'وفارس الخيل من خفت فوقرها ... في الدرب والدم في أعطافها دفع','227','2394','0');
INSERT INTO [t754] VALUES (2839,'وأوحدته وما في قلبه قلق ... وأغضبته وما في لفظه قذع','227','2394','0');
INSERT INTO [t754] VALUES (2840,'بالجيش يمتنع السادات كلهم ... والجيش بابن أبي الهيجاء يمتنع','227','2394','0');
INSERT INTO [t754] VALUES (2841,'قاد المقانب أقصى شربها نهل ... على الشكيم وأدنى سيرها سرع','227','2394','0');
INSERT INTO [t754] VALUES (2842,'لايعتقي بلد مسراه عن بلد ... كالموت ليس له ري ولا شبع','227','2394','0');
INSERT INTO [t754] VALUES (2843,'حتى أقام على أرباض خرشنة ... تشقى به الروم والصلبان والبيع','227','2394','0');
INSERT INTO [t754] VALUES (2844,'للسبي ما نكحوا والقتل ما ولدوا ... والنهب ما جمعوا والنار ما زرعوا','227','2394','0');
INSERT INTO [t754] VALUES (2845,'مخلى له المرج منصوبا بصارخة ... له المنابر مشهودا بها الجمع','227','2394','0');
INSERT INTO [t754] VALUES (2846,'يطمع الطير فيهم طول أكلهم ... حتى تكاد على أحيائهم تقع','227','2394','0');
INSERT INTO [t754] VALUES (2847,'ولو رآه حواريوهم لبنوا ... على محبته الشرع الذي شرعوا','227','2394','0');
INSERT INTO [t754] VALUES (2848,'ذم الدمستق عينيه وقد طلعت ... سود الغمام فظنوا أنها قزع','227','2394','0');
INSERT INTO [t754] VALUES (2849,'فيها الكماة التي مفطومها رجل ... على الجياد التي حوليها جذع','228','2394','0');
INSERT INTO [t754] VALUES (2850,'يذرى اللقان غبارا في مناخرها ... وفي حناجرها من ألس جرع','228','2394','0');
INSERT INTO [t754] VALUES (2851,'كأنما تتلقاهم لتسلكهم ... فالطعن يفتح في الأجواف ما تسع','228','2394','0');
INSERT INTO [t754] VALUES (2852,'تهدي نواظرها والحرب مظلمة ... من الأسنة نار والقنا شمع','228','2394','0');
INSERT INTO [t754] VALUES (2853,'دون السهام ودون القر طافحة ... على نفوسهم المقورة المزع','228','2394','0');
INSERT INTO [t754] VALUES (2854,'إذا دعا العلج علجا حال بينهما ... أظمى تفارق منه أختها الضلع','228','2394','0');
INSERT INTO [t754] VALUES (2855,'أجل من ولد الفقاس منكتف ... إذ فاتهن وأمضى منه منصرع','228','2394','0');
INSERT INTO [t754] VALUES (2856,'وما نجا من شفار البيض منفلت ... نجا ومنهن في أحشائه فزع','228','2394','0');
INSERT INTO [t754] VALUES (2857,'يباشر الأمن دهرا وهو مختبل ... ويشرب الخمر دهرا وهو ممتقع','228','2394','0');
INSERT INTO [t754] VALUES (2858,'كم من حشاشة بطريق تضمنها ... للباترات أمين ما له ورع','228','2394','0');
INSERT INTO [t754] VALUES (2859,'يقاتل الخطو عنه حين يطلبه ... ويطرد النوم عنه حين يضطجع','228','2394','0');
INSERT INTO [t754] VALUES (2860,'تغدو المنايا فلا تنفك واقفة ... حتى يقول لها عودي فتندفع','228','2394','0');
INSERT INTO [t754] VALUES (2861,'قل للدمستق أن المسلمين لكم ... خانوا الأمير فجازاهم بما صنعوا','228','2394','0');
INSERT INTO [t754] VALUES (2862,'وجدتموهم نياما في دمائكم ... كأن قتلاكم إياهم فجعوا','229','2394','0');
INSERT INTO [t754] VALUES (2863,'ضعفي تعف الأعادي عن مثالهم ... من الأعادي وإن هموا بهم نزعوا','229','2394','0');
INSERT INTO [t754] VALUES (2864,'لا تحسبوا من أسرتم كان ذا رمق ... فليس يأكل إلا الميتة الضبع','229','2394','0');
INSERT INTO [t754] VALUES (2865,'هلا على عقب الوادي وقد صعدت ... أسد تمر فرادى ليس تجتمع','229','2394','0');
INSERT INTO [t754] VALUES (2866,'تشقكم بقناها كل سلهبة ... والضرب يأخذ منكم فوق ما يدع','229','2394','0');
INSERT INTO [t754] VALUES (2867,'وإنما عرض الله الجنود بكم ... لكي يكونوا بلا فسل إذا رجعوا','229','2394','0');
INSERT INTO [t754] VALUES (2868,'فكل غزو إليكم بعد ذا فله ... وكل غاز لسيف الدولة التبع','229','2394','0');
INSERT INTO [t754] VALUES (2869,'تمشي الكرام على آثار غيرهم ... وأنت تخلق ما تأتي وتبتدع','229','2394','0');
INSERT INTO [t754] VALUES (2870,'وهي يشينك وقت كنت فارسه ... وكان غيرك فيه العاجز الضرع','229','2394','0');
INSERT INTO [t754] VALUES (2871,'من كان فوق محل الشمس موضعه ... فليس يرفعه شيء ولا يضع','229','2394','0');
INSERT INTO [t754] VALUES (2872,'لم يسلم الكر في الأعقاب مهجته ... إن كان أسلمها الأصحاب والشيع','229','2394','0');
INSERT INTO [t754] VALUES (2873,'ليت الملوك على الأقدار معطية ... فلم يكن لدني عندها طمع','229','2394','0');
INSERT INTO [t754] VALUES (2874,'رضيت منهم بأن زرت الوغى فرأوا ... وأن قرعت حبيك البيض فاستمعوا','229','2394','0');
INSERT INTO [t754] VALUES (2875,'لقد أباحك غشا في معاملة ... من كنت منه بغير الصدق تنتفع','229','2394','0');
INSERT INTO [t754] VALUES (2876,'الدهر معتذر والسيف منتظر ... وأرضهم لك مصطاف مرتبع','230','2394','0');
INSERT INTO [t754] VALUES (2877,'وما الجبال لنصران بحامية ... ولو تنر فيها الأعصم الصدع','230','2394','0');
INSERT INTO [t754] VALUES (2878,'وما حمدتك في هول ثبت له ... حتى بلوتك والأبطال تمتصع','230','2394','0');
INSERT INTO [t754] VALUES (2879,'فقد يظن شجاعا من به خرق ... وقد يظن جبانا من به زمع','230','2394','0');
INSERT INTO [t754] VALUES (2880,'إن السلاح جميع الناس يحمله ... وليس كل ذوات المخلب السبع','230','2394','0');
INSERT INTO [t754] VALUES (2881,'نزور ديارا ما نحب لها مغنى ... ونسأل فيها غير سكانها الإذنا','230','2394','0');
INSERT INTO [t754] VALUES (2882,'نقود إليها الآخذات لنا المدى ... عليها الكماة المحسنون بها الظنا','230','2394','0');
INSERT INTO [t754] VALUES (2883,'ونصفي الذي يكنى أبا الحسن الهوى ... ونرضى الذي يسمى الإله ولا يكنا','230','2394','0');
INSERT INTO [t754] VALUES (2884,'وقد علم الروم الشقيون أننا ... إذا ما تركنا أرضهم خلفنا عدنا','230','2394','0');
INSERT INTO [t754] VALUES (2885,'وأنا إذا ما الموت صرح في الوغى ... لبسنا إلى حاجاتنا الضرب والطعنا','230','2394','0');
INSERT INTO [t754] VALUES (2886,'قصدنا له قصد الحبيب لقاؤه ... إلينا وقلنا للسيوف هلمنا','230','2394','0');
INSERT INTO [t754] VALUES (2887,'وخيل حشوناها الأسنة بعدما ... تكدسن من هنا علينا ومن هنا','230','2394','0');
INSERT INTO [t754] VALUES (2888,'ضربن إلينا بالسياط جهالة ... فلما تعارفنا ضربن بها عنا','230','2394','0');
INSERT INTO [t754] VALUES (2889,'تعد القرى والمس بنا الجيش لمسة ... تبار إلى ما تشتهي يدك اليمنى','230','2394','0');
INSERT INTO [t754] VALUES (2890,'فقد بردت فوق اللقان دماؤهم ... ونحن أناس نتبع البارد السخنا','230','2394','0');
INSERT INTO [t754] VALUES (2891,'وإن كنت سيف الدولة العضب فيهم ... فدعنا نكن قبل الضراب القنا اللدنا','231','2394','0');
INSERT INTO [t754] VALUES (2892,'فنحن الأولى لا نأتلي لك نصرة ... وأنت الذي لو أنه وحده أغنى','231','2394','0');
INSERT INTO [t754] VALUES (2893,'يقيك الردى من يبتغي عندك العلي ... ومن قال لا أرضي من العيش بالأدنى','231','2394','0');
INSERT INTO [t754] VALUES (2894,'فلولاك لم تجر الدماء ولا اللهى ... ولم يك للدنيا ولا أهلها معنى','231','2394','0');
INSERT INTO [t754] VALUES (2895,'وما الخوف إلا ما تخوفه الفتى ... ولا الأمن إلا ما رآ الفتى أمنا','231','2394','0');
INSERT INTO [t754] VALUES (2896,'عواذل ذات الخال في حواسد ... وإن ضجيع الخود مني لماجد','231','2394','0');
INSERT INTO [t754] VALUES (2897,'يرد يدا عن ثوبها وهو قادر ... ويعصي الهوى في طيفها وهو راقد','231','2394','0');
INSERT INTO [t754] VALUES (2898,'متى يشتفي من لاعج الشوق في الحشا ... محب لها في قربه متباعد','231','2394','0');
INSERT INTO [t754] VALUES (2899,'إذا كنت تخشى العار في كل خلوة ... فلم تتصباك الحسان الخرائد','231','2394','0');
INSERT INTO [t754] VALUES (2900,'ألح على السقم حتى ألفته ... ومل طبيبي جانبي والعوائد','231','2394','0');
INSERT INTO [t754] VALUES (2901,'مررت على دار الحبيب فحمحمت ... جوادي وهل تشجو الجياد المعاهد','231','2394','0');
INSERT INTO [t754] VALUES (2902,'وما تنكر الدهماء من رسم منزل ... سقتها ضريب الشول فيها الولائد','232','2394','0');
INSERT INTO [t754] VALUES (2903,'أهم بشيء والليالي كأنها ... تطاردني عن كونه وأطارد','232','2394','0');
INSERT INTO [t754] VALUES (2904,'وحيدا من الخلان في كل بلدة ... إذا عظم المطلوب قل المساعد','232','2394','0');
INSERT INTO [t754] VALUES (2905,'وتسعدني في غمرة بعد غمرة ... سبوح لها منها عليها شواهد','232','2394','0');
INSERT INTO [t754] VALUES (2906,'تثنى على قدر الطعان كأنما ... مفاصلها تحت الرماح مراود','232','2394','0');
INSERT INTO [t754] VALUES (2907,'وأورد نفسي والمهند في يدي ... موارد لا يصدرن من لا يجالد','232','2394','0');
INSERT INTO [t754] VALUES (2908,'ولكن إذا لم يحمل القلب كفه ... على حالة لم يحمل الكف ساعد','232','2394','0');
INSERT INTO [t754] VALUES (2909,'خليلي إني لا أرى غير شاعر ... فلم منهم الدعوى ومني القصائد','232','2394','0');
INSERT INTO [t754] VALUES (2910,'فلا تعجبا إن السيوف كثيرة ... ولكن سيف الدولة اليوم واحد','232','2394','0');
INSERT INTO [t754] VALUES (2911,'له من كريم الطبع في الحرب منتض ... ومن عادة الإحسان والصفح غامد','232','2394','0');
INSERT INTO [t754] VALUES (2912,'ولما رأيت الناس دون محله ... تيقنت أن الدهر للناس ناقد','232','2394','0');
INSERT INTO [t754] VALUES (2913,'أحقهم بالسيف من ضرب الطلى ... وبالأمر من هانت عليه الشدائد','232','2394','0');
INSERT INTO [t754] VALUES (2914,'وأشقى بلاد الله ما الروم أهلها ... بهذا وما فيها لمجدك جاحد','232','2394','0');
INSERT INTO [t754] VALUES (2915,'شننت بها الغارات حتى تركتها ... وجفن الذي خلف الفرنجة ساهد','233','2394','0');
INSERT INTO [t754] VALUES (2916,'مخضبة والقوم صرعى كأنها ... وإن لم يكونوا ساجدين مساجد','233','2394','0');
INSERT INTO [t754] VALUES (2917,'تنكسهم والسابقات جبالهم ... وتطعن فيهم والرماح المكايد','233','2394','0');
INSERT INTO [t754] VALUES (2918,'وتضربهم هبرا وقد سكنوا الكدى ... كما سكنت بطن التراب الأساود','233','2394','0');
INSERT INTO [t754] VALUES (2919,'وتضحى الحصون المشمخرات في الذرى ... وخيلك في أعناقهن قلائد','233','2394','0');
INSERT INTO [t754] VALUES (2920,'عصفن بهم يوم اللقان وسقنهم ... بهنزيط حتى أبيض بالسبي آمد','233','2394','0');
INSERT INTO [t754] VALUES (2921,'وألحقن بالصفصاف سابور فانهوى ... وذات الردى أهلاهما والجلامد','233','2394','0');
INSERT INTO [t754] VALUES (2922,'وغلس في الوادي بن مشيع ... مبارك ما تحت اللثامين عابد','233','2394','0');
INSERT INTO [t754] VALUES (2923,'فتى يشتهي طول البلاد ووقته ... تضيق به أوقاته والمقاصد','233','2394','0');
INSERT INTO [t754] VALUES (2924,'أخو غزوات ما تغب سيوفه ... رقابهم إلا وسيحان جامد','233','2394','0');
INSERT INTO [t754] VALUES (2925,'فلم يبق إلا من حماها من الظبا ... لما شفيتها والثدي النواهد','233','2394','0');
INSERT INTO [t754] VALUES (2926,'تبكي عليهن البطاريق في الدجى ... وهن لدينا ملقيات كواسد','233','2394','0');
INSERT INTO [t754] VALUES (2927,'بذا قضت الأيام ما بين أهلها ... مصائب قوم عند قوم فوائد','233','2394','0');
INSERT INTO [t754] VALUES (2928,'ومن شرف الإقدام أنك فيهم ... على القتل موموق كأنك شاكد','233','2394','0');
INSERT INTO [t754] VALUES (2929,'وإن دما أجريته بك فاخر ... وأن فؤادا رعته لك حامد','233','2394','0');
INSERT INTO [t754] VALUES (2930,'وكل يرى طرق الشجاعة والندى ... ولكن طبع النفس للنفس قائد','234','2394','0');
INSERT INTO [t754] VALUES (2931,'نهبت من الأعمار ما لو حويته ... لهنئت الدنيا بأنك خالد','234','2394','0');
INSERT INTO [t754] VALUES (2932,'فأنت حسام الملك والله ضارب ... وأنت لواء الدين والله عاقد','234','2394','0');
INSERT INTO [t754] VALUES (2933,'وأنت أبو الهيجا ابن حمدان يا ابنه ... تشابه مولود كريم ووالد','234','2394','0');
INSERT INTO [t754] VALUES (2934,'وحمدان حمدون وحمدون حارث ... وحارث لقمان ولقمان راشد','234','2394','0');
INSERT INTO [t754] VALUES (2935,'أولائك أنياب الخلافة كلها ... وسائر أملاك البلاد الزوائد','234','2394','0');
INSERT INTO [t754] VALUES (2936,'أبك يا شمس الزمان وبدره ... وإن لامني فيك السهى والفراقد','234','2394','0');
INSERT INTO [t754] VALUES (2937,'وذاك لأن الفضل عندك باهر ... وليس لأن العيش عندك بارد','234','2394','0');
INSERT INTO [t754] VALUES (2938,'فإن قليل الحب بالعقل صالح ... وأن كثير الحب بالجهل فاسد','234','2394','0');
INSERT INTO [t754] VALUES (2939,'لا يحزن الله الأمير فإنني ... لآخذ من حالاته بنصيب','234','2394','0');
INSERT INTO [t754] VALUES (2940,'ومن سر أهل الأرض ثم بكى أسى ... بكى بعيون سرها وقلوب','234','2394','0');
INSERT INTO [t754] VALUES (2941,'وأني وإن كان الدفين حبيبه ... حبيب إلى قلبي حبيب حبيبي','235','2394','0');
INSERT INTO [t754] VALUES (2942,'وقد فارق الناس الأحبة قبلنا ... وأعيا دواء الموت كل طبيب','235','2394','0');
INSERT INTO [t754] VALUES (2943,'سبقنا إلى الدنيا فلو عاش أهلها ... منعنا بها من جيئة وذهوب','235','2394','0');
INSERT INTO [t754] VALUES (2944,'تملكها الآتي تملك سالب ... وفارقها الماضي فراق سليب','235','2394','0');
INSERT INTO [t754] VALUES (2945,'ولا فضل فيها للشجاعة والندى ... وصبر الفتى لولا لقاء شعوب','235','2394','0');
INSERT INTO [t754] VALUES (2946,'وأوفى حيوة الغابرين لصاحب ... حيوة أمرى خانته بعد مشيب','235','2394','0');
INSERT INTO [t754] VALUES (2947,'لأبقى يماك في حشاي صبابة ... إلى كر تركي النجار جليب','235','2394','0');
INSERT INTO [t754] VALUES (2948,'وما كل وجه أبيض بمبارك ... ولا كل جفن ضيق بنجيب','235','2394','0');
INSERT INTO [t754] VALUES (2949,'لئن ظهرت فينا عليه كآبة ... لقد ظهرت في حد كل قضيب','235','2394','0');
INSERT INTO [t754] VALUES (2950,'وفي كل قوس كل يوم تناضل ... وفي كل طرف كل يوم ركوب','235','2394','0');
INSERT INTO [t754] VALUES (2951,'يعز عليه أن يخل بعادة ... وتدعو لأمر وهو غير مجيب','235','2394','0');
INSERT INTO [t754] VALUES (2952,'وكنت إذا أبصرته لك قائما ... نظرت إلى ذي لبدتين أديب','235','2394','0');
INSERT INTO [t754] VALUES (2953,'فإن يكن العلق النفيس فقدته ... فمن كف متلاف أغر وهوب','235','2394','0');
INSERT INTO [t754] VALUES (2954,'كأن الردى عادس على كل ماجد ... إذا لم يعوذ مجده بعيوب','235','2394','0');
INSERT INTO [t754] VALUES (2955,'ولولا أيادي الدهر في الجمع بيننا ... غفلنا فلم نشعر له بذنوب','236','2394','0');
INSERT INTO [t754] VALUES (2956,'وللترك للإحسان خير لمحسن ... إذا جعل الإحسان غير ربيب','236','2394','0');
INSERT INTO [t754] VALUES (2957,'وإن الذي أمست نزار عبيده ... غني عن استعباده لغريب','236','2394','0');
INSERT INTO [t754] VALUES (2958,'كفى بصفاء الود رقا لمثله ... وبالقرب منه مفخرا لنسيب','236','2394','0');
INSERT INTO [t754] VALUES (2959,'فعوض سيف الدولة الأجر إنه ... أجل مثاب من أجل مثيب','236','2394','0');
INSERT INTO [t754] VALUES (2960,'فتى الخيل قد بل النجيع نحورها ... يطاعن في ضنك المقام عصيب','236','2394','0');
INSERT INTO [t754] VALUES (2961,'يعاف خيام الريط في غزواته ... فما خيمه إلا غبار حروب','236','2394','0');
INSERT INTO [t754] VALUES (2962,'علينا لك الإسعاد إن كان نافعا ... فما خيمه إلا غبار حروب','236','2394','0');
INSERT INTO [t754] VALUES (2963,'فرب كئيب ليس تندى جفونه ... ورب كثير الدمع غير كئيب','236','2394','0');
INSERT INTO [t754] VALUES (2964,'تسل بفكر في أبيك فإنما ... بكيت فكان الضحك بعد قريب','236','2394','0');
INSERT INTO [t754] VALUES (2965,'إذا استقبلت نفس الكريم مصابها ... بخبث ثنت فاستدبرته بطيب','236','2394','0');
INSERT INTO [t754] VALUES (2966,'وللواجد المكروب من زفراته ... سكون عزاء أو سكون لغوب','236','2394','0');
INSERT INTO [t754] VALUES (2967,'وكم لك جدا لم تر العين وجهه ... فلم تجر في آثاره بغروب','237','2394','0');
INSERT INTO [t754] VALUES (2968,'فدتك نفوس الحاسدين فإنها ... معذبة في حضرة ومغيب','237','2394','0');
INSERT INTO [t754] VALUES (2969,'وفي تعب من يحسد الشمس نورها ... ويجهد أن يأتي لها بضريب','237','2394','0');
INSERT INTO [t754] VALUES (2970,'فديناك من ربع وإن زدتنا كربا ... فإنك كنت الشرق للشمس والغربا','237','2394','0');
INSERT INTO [t754] VALUES (2971,'وكيف عرفنا رسم من لم تدع لنا ... فؤادا لعرفان الرسوم ولا لبا','237','2394','0');
INSERT INTO [t754] VALUES (2972,'نزلنا عن الأكوار نمشي كرامة ... لمن بان عنه أن نلم به ركبا','237','2394','0');
INSERT INTO [t754] VALUES (2973,'نذم السحاب الغر في فعلها به ... ونعرض عنها كلما طلعت عتبا','237','2394','0');
INSERT INTO [t754] VALUES (2974,'ومن صحب الدنيا طويلا تقلبت ... على عينه حتى يرى صدقها كذبا','237','2394','0');
INSERT INTO [t754] VALUES (2975,'وكيف التذاذي بالأصائل والضحى ... إذا لم يعد ذاك النسيم الذي هبا','237','2394','0');
INSERT INTO [t754] VALUES (2976,'ذكرت به وصلا كأن لم أفز به ... وعيشا كأني كنت أقطعه وثبا','237','2394','0');
INSERT INTO [t754] VALUES (2977,'وفتانة العينين قتالة الهوى ... إذا نفحت شيخا روائحها شبا','237','2394','0');
INSERT INTO [t754] VALUES (2978,'لها بشر الدر الذي قلدت به ... ولم أر بدرا قبلها قلد الشهبا','238','2394','0');
INSERT INTO [t754] VALUES (2979,'فيا شوق ما أبقى ويا لي من النوى ... ويا دمع ما أجرى ويا قلب ما أصبا','238','2394','0');
INSERT INTO [t754] VALUES (2980,'لقد لعب البين المشت بها وبي ... وزودني في السير ما زود الضبا','238','2394','0');
INSERT INTO [t754] VALUES (2981,'ومن تكن الأسد الضواري جدوده ... يكن ليله صبحا ومطعمه غصبا','238','2394','0');
INSERT INTO [t754] VALUES (2982,'ولست أبالي بعد إدراكي العلى ... أكان تراثا ما تناولت أما كسبا','238','2394','0');
INSERT INTO [t754] VALUES (2983,'فرب غلام علم المجد نفسه ... كتعليم سيف الدولة الدولة الضربا','238','2394','0');
INSERT INTO [t754] VALUES (2984,'إذا الدولة أستكفت به في ملمة ... كفاها فكان السيف والكف والقلبا','238','2394','0');
INSERT INTO [t754] VALUES (2985,'تهاب سيوف الهند وهي حدائد ... فكيف إذا كانت نزارية عربا','238','2394','0');
INSERT INTO [t754] VALUES (2986,'ويرهب ناب الليث والليث وحده ... فكيف إذا كان الليوث له صحبا','238','2394','0');
INSERT INTO [t754] VALUES (2987,'ويخشى عباب البحر والحبر ساكن ... فكيف بمن يغشى البلاد إذا عبا','238','2394','0');
INSERT INTO [t754] VALUES (2988,'عليم بأسرار الديانات واللغى ... له خطرات تفضح الناس والكتابا','238','2394','0');
INSERT INTO [t754] VALUES (2989,'فبوركت من غيث كأن جلودنا ... به تنبت الديباج والوشى والعصبا','238','2394','0');
INSERT INTO [t754] VALUES (2990,'ومن واهب جزلا ومن زاجر هلا ... ومن هاتك درعا ومن ناثر قصبا','239','2394','0');
INSERT INTO [t754] VALUES (2991,'هنيأ لأهل الثغر رأيك فيهم ... وأنك حزب الله صرت لهم حزبا','239','2394','0');
INSERT INTO [t754] VALUES (2992,'وأنك رعت الدهر فيها وريبه ... فإن شك فليحدث بساحتها خطبا','239','2394','0');
INSERT INTO [t754] VALUES (2993,'فيوما بخيل تطرد الروم عنهم ... ويوما بجود تطرد الفقر والجدبا','239','2394','0');
INSERT INTO [t754] VALUES (2994,'سراياك تترى والدمستق هارب ... وأصحابه قتلى وأمواله نهبا','239','2394','0');
INSERT INTO [t754] VALUES (2995,'أتى مرعشا يستقرب البعد مقبلا ... وأدبر إذ اقبلت يستبعد القربا','239','2394','0');
INSERT INTO [t754] VALUES (2996,'كذا يترك الأعداء من يكره القنا ... ويقفل من كانت غنيمته رعبا','239','2394','0');
INSERT INTO [t754] VALUES (2997,'وهل رد عنه باللقان وقوفه ... صدور العوالي والمطهمة القبا','239','2394','0');
INSERT INTO [t754] VALUES (2998,'مضى بعد ما التف الرماحان ساعة ... كما يتلقى الهدب في الرقدة الهدبا','239','2394','0');
INSERT INTO [t754] VALUES (2999,'ولكنه ولى وللطعن سورة ... إذا ذكرتها نفسه لمس الجنبا','239','2394','0');
INSERT INTO [t754] VALUES (3000,'وخلى العذارى والبطاريق والقرى ... وشعث النصارى والقرابين والصلبا','239','2394','0');
INSERT INTO [t754] VALUES (3001,'أرى كلنا يبغي الحيوة لنفسه ... حريصا عليها مستهاما بها صبا','239','2394','0');
INSERT INTO [t754] VALUES (3002,'فحب الجبان النفس أورده البقا ... وحب الشجاع النفس أورده الحربا','239','2394','0');
INSERT INTO [t754] VALUES (3003,'ويختلف الرزقان والفعل واحد ... إلى أن ترى إحسان هذا لذا ذنبا','240','2394','0');
INSERT INTO [t754] VALUES (3004,'فأضحت كأن السور من فوق بدئه ... إلى الأرض قد شق الكواكب والتربا','240','2394','0');
INSERT INTO [t754] VALUES (3005,'تصد الرياح الهوج عنها مخافة ... وتفزع فيها الطير أن تلقط الحبا','240','2394','0');
INSERT INTO [t754] VALUES (3006,'وتردى الجياد الجرد فوق جبالها ... وقد ندف الصنبر في طرقها العطبا','240','2394','0');
INSERT INTO [t754] VALUES (3007,'كفى عجبا أن يعجب الناس أنه ... بنى مرعشا تبا لآرائهم تبا','240','2394','0');
INSERT INTO [t754] VALUES (3008,'وما الفرق ما بين الأنام وبينه ... إذا حذر المحذور واستصعب الصعبا','240','2394','0');
INSERT INTO [t754] VALUES (3009,'لأمر أعدته الخلاقة للعدى ... وسمته دون العالم الصارم العضبا','240','2394','0');
INSERT INTO [t754] VALUES (3010,'ولم تفترق عنه الأسنة رحمة ... ولم تترك الشأم الأعادي له حبا','240','2394','0');
INSERT INTO [t754] VALUES (3011,'ولكن نفاها عنه غير كريمة ... كريم النثا ما سب قط ولا سبا','240','2394','0');
INSERT INTO [t754] VALUES (3012,'وجيش يثنى كل طود كأنه ... خريق رياح واجهت غصنا رطبا','241','2394','0');
INSERT INTO [t754] VALUES (3013,'كأن نجوم الليل خافت مغاره ... فمدت عليها من عجاجته حجبا','241','2394','0');
INSERT INTO [t754] VALUES (3014,'فمن كان يرضى اللؤم والكفر ملكه ... فهذا الذي يرضى المكارم والربا','241','2394','0');
INSERT INTO [t754] VALUES (3015,'ثياب كريم ما يصون حسانها ... إذا نشرت كان الهبات صوانها','241','2394','0');
INSERT INTO [t754] VALUES (3016,'ترينا صناع الروم فيها ملوكها ... وتجلو علينا نفسها وقيانها','241','2394','0');
INSERT INTO [t754] VALUES (3017,'ولم يكفها تصويرها الخيل وحدها ... فصورت الأشياء إلا زمانها','241','2394','0');
INSERT INTO [t754] VALUES (3018,'وما أدخرتها قدرة في مصور ... سوى أنها من أنطقت حيوانها','241','2394','0');
INSERT INTO [t754] VALUES (3019,'وسمراء يستغوي الفوارس قدها ... ويذكرها كراتها وطعانها','241','2394','0');
INSERT INTO [t754] VALUES (3020,'ردينية تمت فكاد نباتها ... يركب فيها زجها وسنانها','241','2394','0');
INSERT INTO [t754] VALUES (3021,'وأم عتيق خاله دون عمه ... رأى حسنها من أعجبته فعانها','241','2394','0');
INSERT INTO [t754] VALUES (3022,'إذا سايرته باينته وبانها ... وشانته في عين البصير وزانها','241','2394','0');
INSERT INTO [t754] VALUES (3023,'فأين التي لم يأمن الخيل شرها ... وشرى ولا تعطى سواي أمانها','241','2394','0');
INSERT INTO [t754] VALUES (3024,'وأين التي لا ترجع الرمح خائبا ... إذا خفضت يسرى يدي عنانها','241','2394','0');
INSERT INTO [t754] VALUES (3025,'وما لي ثناء لا أراك مكانه ... فهل لك نعمى لا تراني مكانها','241','2394','0');
INSERT INTO [t754] VALUES (3026,'وا حر قلباه ممن قلبه شبم ... ومن بجسمي وحالي عنده سقم','241','2394','0');
INSERT INTO [t754] VALUES (3027,'ما لي أكتم حبا قد برى جسدي ... وتدعي حب سيف الدولة الأمم','242','2394','0');
INSERT INTO [t754] VALUES (3028,'إن كان يجمعنا حب لغرته ... فليت أنا بقدر الحب نقتسم','242','2394','0');
INSERT INTO [t754] VALUES (3029,'قد زرته وسيوف الهند مغمدة ... وقد نظرت إليه والسيوف دم','242','2394','0');
INSERT INTO [t754] VALUES (3030,'فكان أحسن خلق الله كلهم ... وكان أحسن ما في الأحسن الشيم','242','2394','0');
INSERT INTO [t754] VALUES (3031,'فوت العدو الذي يممته ظفر ... في طيه أسف في طيه نعم','242','2394','0');
INSERT INTO [t754] VALUES (3032,'قد ناب عنك شديد الخوف واصطنعت ... لك المهابة ما لا تصنع البهم','242','2394','0');
INSERT INTO [t754] VALUES (3033,'ألزمت نفسك شيئا ليس يلزمها ... أن لا تواريهم أرض ولا علم','242','2394','0');
INSERT INTO [t754] VALUES (3034,'أكلما رمت جيشا فانثني هربا ... تصرفت بك في آثاره الهمم','242','2394','0');
INSERT INTO [t754] VALUES (3035,'عليك هزمهم في كل معترك ... وما عليك بهم عار إذا انهزموا','242','2394','0');
INSERT INTO [t754] VALUES (3036,'أما ترى ظفرا حلوا سوى ظفر ... تصافحت فيه بيض الهند واللمم','242','2394','0');
INSERT INTO [t754] VALUES (3037,'يا أعدل الناس إلا في معاملتي ... فيك الخاصم وأنت الخصم والحكم','242','2394','0');
INSERT INTO [t754] VALUES (3038,'أعيذها نظرات منك صادقة ... أن تحسب الشحم فيمن شحمه ورم','242','2394','0');
INSERT INTO [t754] VALUES (3039,'وما انتفاع أخي الدنيا بناظره ... إذا استوت عنده الأنوار والظلم','242','2394','0');
INSERT INTO [t754] VALUES (3040,'أنا الذي نظر الأعمى إلى أدبي ... وأسمعت كلماتي من به صمم','242','2394','0');
INSERT INTO [t754] VALUES (3041,'أنام ملء جفوني عن شواردها ... ويسهر الخلق جراها ويختصم','242','2394','0');
INSERT INTO [t754] VALUES (3042,'وجاهل مده في جهله ضحكي ... حتى أتته يد فراسة وفم','243','2394','0');
INSERT INTO [t754] VALUES (3043,'إذا رأيت نيوب الليث بارزة ... فلا تظنن أن الليث يبتسم','243','2394','0');
INSERT INTO [t754] VALUES (3044,'ومهجة مهجتي من هم صاحبها ... أدركتها بجواد ظهره حرم','243','2394','0');
INSERT INTO [t754] VALUES (3045,'رجلاه في الركض رجل واليدان يد ... وفعله ما تريد الكف والقدمث','243','2394','0');
INSERT INTO [t754] VALUES (3046,'ومرهف صرت بين الجحفلين به ... حتى ضربت وموج الموت يلتطم','243','2394','0');
INSERT INTO [t754] VALUES (3047,'فالخيل والليل والبيداء تعرفني ... والحرب والضرب والقرطاس والقلم','243','2394','0');
INSERT INTO [t754] VALUES (3048,'صحبت في الفلوات والوحش منفردا ... حتى تعجب من القور والأكم','243','2394','0');
INSERT INTO [t754] VALUES (3049,'يا من يعز علينا أن نفارقهم ... وجداننا كل شيء بعدكم عدم','243','2394','0');
INSERT INTO [t754] VALUES (3050,'ما كان اخلقنا منكم بتكرمة ... لو أن أمركم من أمرنا أمم','243','2394','0');
INSERT INTO [t754] VALUES (3051,'إن كان سركم ما قال حاسدنا ... فما لجر إذا أرضاكم ألم','243','2394','0');
INSERT INTO [t754] VALUES (3052,'وبيننا لو رعيتم ذاك معرفة ... إن المعارف في أهل النهى ذمم','243','2394','0');
INSERT INTO [t754] VALUES (3053,'كم تطلبون لنا عيبا فيعجزكم ... ويكره الله ما تأتون والكرم','243','2394','0');
INSERT INTO [t754] VALUES (3054,'ما أبعد العيب والنقصان من شرفي ... أنا الثريا وذان الشيب والهرم','243','2394','0');
INSERT INTO [t754] VALUES (3055,'ليت الغمام الذي عندي صواعقه ... يزيلهن إلى من عنده الديم','243','2394','0');
INSERT INTO [t754] VALUES (3056,'أرى النوى تقتضيني كل مرحلة ... لا تستقل بها الوخادة الرسم','244','2394','0');
INSERT INTO [t754] VALUES (3057,'لئن تركن ضميرا عن ميامننا ... ليحدثن لمن ودعتهم ندم','244','2394','0');
INSERT INTO [t754] VALUES (3058,'إذا ترحلت عن قومس وقد قدروا ... أن لا تفارقهم فالراحلون هم','244','2394','0');
INSERT INTO [t754] VALUES (3059,'شر البلاد مكان لا صديق به ... وشر ما يكسب الإنسان ما يصم','244','2394','0');
INSERT INTO [t754] VALUES (3060,'وشر ما قنصته راحتي قنص ... شهب البزاة سواء فيه والرخم','244','2394','0');
INSERT INTO [t754] VALUES (3061,'بأي لفظ تقول الشعر زعنفة ... تجوز عندك لا عرب ولا عجم','244','2394','0');
INSERT INTO [t754] VALUES (3062,'هذا عتابك إلا أنه مقة ... قد ضمن الدر إلا أنه كلم','244','2394','0');
INSERT INTO [t754] VALUES (3063,'أسامري ضحكة كر راء ... فطنت وأنت أغبى الأغبياء','244','2394','0');
INSERT INTO [t754] VALUES (3064,'صغرت عن المديح فقلت أهجي ... كأنك ما صغرت عن الهجاء','244','2394','0');
INSERT INTO [t754] VALUES (3065,'وما فكرت قبلك في محال ... ولا جربت سيفي في هباء','244','2394','0');
INSERT INTO [t754] VALUES (3066,'ألا ما لسيف الدولة اليوم عاتبا ... فداه الورى أمضى السيوف مضاربا','244','2394','0');
INSERT INTO [t754] VALUES (3067,'وما لي إذا ما اشتقت أبصرت دونه ... تنايف لا أشتاقها وسبابها','244','2394','0');
INSERT INTO [t754] VALUES (3068,'وقد كان يدني مجلسي من سمائه ... أحادث فيها بدرها والكواكبأ','244','2394','0');
INSERT INTO [t754] VALUES (3069,'أهذا جزاء الصدق إن كنت صادقا ... أهذا جزاء الكذب إن كنت كاذبا','244','2394','0');
INSERT INTO [t754] VALUES (3070,'وإن كان ذنبي كل ذنب فإنه ... محا الذنب كل المحو من جاء تائبا','245','2394','0');
INSERT INTO [t754] VALUES (3071,'أجاب دمعي ما الداعي سوى طلل ... دعا فلباه قبل الركب والإبل','245','2394','0');
INSERT INTO [t754] VALUES (3072,'طللت بين أصيحابي اكفكفه ... وظل يسفح بين العذبر والعذل','245','2394','0');
INSERT INTO [t754] VALUES (3073,'أشكو النوى ولهم من عبرتي عجب ... كذاك كنت وما أشكو سوى الكلل','245','2394','0');
INSERT INTO [t754] VALUES (3074,'وما صبابة مشتاق على أمل ... من اللقاء كمشتاق بلا أمل','245','2394','0');
INSERT INTO [t754] VALUES (3075,'متى تزر قوم من تهوى زيارتها ... لا يتحفوك بغير البيض والأسل','245','2394','0');
INSERT INTO [t754] VALUES (3076,'والهجر أقتل لي ما أراقبه ... أنا الغريق فما خوفي من البلل','245','2394','0');
INSERT INTO [t754] VALUES (3077,'ما بال كل فؤاد في عشيرتها ... به الذي بي وما بي غير منتقل','245','2394','0');
INSERT INTO [t754] VALUES (3078,'مطاعة اللحظ في الألحاظ مالكة ... لمقلتيها عظيم الملك في المقل','245','2394','0');
INSERT INTO [t754] VALUES (3079,'تشبه الخفرات الآنسات بها ... في مشيها فينلن الحسن بالحيل','245','2394','0');
INSERT INTO [t754] VALUES (3080,'قد ذقت شدة أيامي ولذتها ... فما حصلت على صاب ولا عسل','245','2394','0');
INSERT INTO [t754] VALUES (3081,'وقد أراني الشباب الروح في بدني ... وقد أراني المشيب الروح في بدلي','245','2394','0');
INSERT INTO [t754] VALUES (3082,'وقد طرقت فتاة الحي مرتديا ... بصاحب غير عزهاة ولا غزل','245','2394','0');
INSERT INTO [t754] VALUES (3083,'فبات بين تراقينا ندافعه ... وليس يعلم بالشكوى، ولا القبل','246','2394','0');
INSERT INTO [t754] VALUES (3084,'ثم اغتدى وبه من ردعها أثر ... على ذؤابته والجفن والخلل','246','2394','0');
INSERT INTO [t754] VALUES (3085,'لا أكسب الذكر إلا من مضاربه ... أو من سنان أصم الكعب معتدل','246','2394','0');
INSERT INTO [t754] VALUES (3086,'جاج الأمير به لي في مواهبه ... فزانها وكساني الدرع في الحلل','246','2394','0');
INSERT INTO [t754] VALUES (3087,'ومن علي بن عبد الله معرفتي ... بحمله من كعبد الله أو كعلي','246','2394','0');
INSERT INTO [t754] VALUES (3088,'معطى الكواعب والجرد السلاهب وال ... تبيض القواضب والعسالة الذبل','246','2394','0');
INSERT INTO [t754] VALUES (3089,'ضاق الزمان ووجه الأرض عن ملك ... ملء الزمان وملء السهل والجبل','246','2394','0');
INSERT INTO [t754] VALUES (3090,'فنحن في جذل والروم في وجل ... والبر في شغل والبحر في خجل','246','2394','0');
INSERT INTO [t754] VALUES (3091,'من تغلب الغالبين الناس منصبه ... ومن عدي أعادي الجبن والبخل','246','2394','0');
INSERT INTO [t754] VALUES (3092,'والمدح لأبن أبي الهيجاء تنجده ... بالجاهلية عين العي والخطل','246','2394','0');
INSERT INTO [t754] VALUES (3093,'ليت المدائح تستوفي مناقبه ... فما كليب وأهل الأعصر الأول','246','2394','0');
INSERT INTO [t754] VALUES (3094,'خذ ما تراه ودع شيئا سمعت به ... في طلعة الشمس ما يغنيك عن زحل','246','2394','0');
INSERT INTO [t754] VALUES (3095,'وقد وجدت مجال القول ذا سعة ... فإن وجدت لسانا قائلا فقل','246','2394','0');
INSERT INTO [t754] VALUES (3096,'إن الهمام الذي فخر الأنام به ... خير السيوف بكفي خيرة الدول','246','2394','0');
INSERT INTO [t754] VALUES (3097,'تمسى الأماني صرعى دون مبلغه ... فما يقول لشيء ليت ذلك لي','246','2394','0');
INSERT INTO [t754] VALUES (3098,'أنظر إذا اجتمع السيفان في رهج ... إلى اختلافهما في الخلق والعمل','246','2394','0');
INSERT INTO [t754] VALUES (3099,'هذا المعد لريب الدهر منصلتا ... أعد هذا لرأس الفارس البطل','246','2394','0');
INSERT INTO [t754] VALUES (3100,'فالعرب منه من الكدري طائرة ... والروم طائرة منه مع الحجل','247','2394','0');
INSERT INTO [t754] VALUES (3101,'وما الفرار إلى الأجبال من أسد ... تمشى النعام به في معقل الوعل','247','2394','0');
INSERT INTO [t754] VALUES (3102,'جاز الدروب إلى ما خلف خرشنة ... وزال عنها وذاك الروع لم يزل','247','2394','0');
INSERT INTO [t754] VALUES (3103,'وكلما حلمت عذراء عندهم ... فإنما حلمت بالسبي والجمل','247','2394','0');
INSERT INTO [t754] VALUES (3104,'إن كنت ترضى بأن يعطوا الجزي بذلوا ... منها رضاك ومن للعور بالحول','247','2394','0');
INSERT INTO [t754] VALUES (3105,'ناديت مجدك في شعري وقد صدرا ... يا غير منتحل في غير منتحل','247','2394','0');
INSERT INTO [t754] VALUES (3106,'بالشرق والغرب أقوام نحبهم ... فطالعاهم وكونا أبلغ الرسل','247','2394','0');
INSERT INTO [t754] VALUES (3107,'وعرفاهم بأني في مكارمه ... أقلب الطرف بين الخيل والخول','247','2394','0');
INSERT INTO [t754] VALUES (3108,'يا أيها المحسن المشكور من جهتي ... والشكر من قبل الإحسان لا قبلي','247','2394','0');
INSERT INTO [t754] VALUES (3109,'ما كان نومي إلا فوق معرفتي ... بأن رأيك لا يؤتى من الزلل','247','2394','0');
INSERT INTO [t754] VALUES (3110,'أل أنل أقطع احمل عل سل أعد ... زد هش بش تفضل أدن سر صل','247','2394','0');
INSERT INTO [t754] VALUES (3111,'لعل عتبك محمود عواقبه ... فربما صحت الأجسام بالعلل','248','2394','0');
INSERT INTO [t754] VALUES (3112,'وما سمعت ولا غيري بمقتدر ... أذب منك لزور القول عن رجل','248','2394','0');
INSERT INTO [t754] VALUES (3113,'لأن حلمك حلم لا تكلفه ... ليس التكحل في العينين كالكحل','248','2394','0');
INSERT INTO [t754] VALUES (3114,'أنت الجواد بلا من ولا كدر ... ولا مطال ولا وعد ولا مذل','248','2394','0');
INSERT INTO [t754] VALUES (3115,'وما ثناك كلام الناس عن كرم ... ومن يسد طريق العارض الهطل','248','2394','0');
INSERT INTO [t754] VALUES (3116,'أنت الشجاع إذا ما لم تطأ فرس ... غير السنور والأشلاء والقلل','248','2394','0');
INSERT INTO [t754] VALUES (3117,'ورد بعض القنا بعضا مقارعة ... كأنه من نفوس القوم في جدل','248','2394','0');
INSERT INTO [t754] VALUES (3118,'لا زلت تضرب من عاداك عن عرض ... بعاجل النصر في مستأجر الأجل','248','2394','0');
INSERT INTO [t754] VALUES (3119,'إن هذا الشعر في الشعر ملك ... سار فهو الشمس والدنيا فلك','248','2394','0');
INSERT INTO [t754] VALUES (3120,'عدل الرحمن فيه بيننا ... فقضى باللفظ لي والحمد لك','248','2394','0');
INSERT INTO [t754] VALUES (3121,'فإذا مر بأذني حاسد ... صار ممن كان حيا فهلك','248','2394','0');
INSERT INTO [t754] VALUES (3122,'أقل أنل أن صن أحمل عل سل أعد ... زد هش بش هب اغفر ادن سر صل','248','2394','0');
INSERT INTO [t754] VALUES (3123,'وهذا دعاء لو سكت كفيته ... لأني سألت الله فيك وقد فعل','249','2394','0');
INSERT INTO [t754] VALUES (3124,'شديد البعد من شرب الشمول ... ترنج الهند أو طلع النخيل','249','2394','0');
INSERT INTO [t754] VALUES (3125,'ولكن كل شيء فيه طيب ... لديك من الدقيق إلى الجليل','249','2394','0');
INSERT INTO [t754] VALUES (3126,'وميدان الفصاحة والقوافي ... وممتحن الفوارس والخيول','249','2394','0');
INSERT INTO [t754] VALUES (3127,'أتيت بمنطق العرب الأصيل ... وكان بقدر ما عاينت قيلي','249','2394','0');
INSERT INTO [t754] VALUES (3128,'فعارضه كلام كان منه ... بمنزل النساء من البعول','249','2394','0');
INSERT INTO [t754] VALUES (3129,'وهذا الدر مأمون التشظي ... وأنت السيف مأمون الفلول','249','2394','0');
INSERT INTO [t754] VALUES (3130,'وليس يصح في الأفهام شيء ... إذا احتاج النهار إلى دليل','249','2394','0');
INSERT INTO [t754] VALUES (3131,'لقيت العفاة بآمالها ... وزرت العداة بآجالها','249','2394','0');
INSERT INTO [t754] VALUES (3132,'وأقبلت الروم تمشي إلي ... ك بين الليوث وأشبالها','249','2394','0');
INSERT INTO [t754] VALUES (3133,'إذا رأت الأسد مسبية ... فأين تفر بأطفالها','250','2394','0');
INSERT INTO [t754] VALUES (3134,'لعينيك ما يلقى الفؤاد وما لقي ... وللشوق ما لم يبق مني وما بقي','250','2394','0');
INSERT INTO [t754] VALUES (3135,'وما كنت ممن يدخل العشق قلبه ... ولكن من ينظر جفونك يعشق','250','2394','0');
INSERT INTO [t754] VALUES (3136,'وبين الرضا والسخط والقرب والنوى ... مجال لدمع المقلة المترقرق','250','2394','0');
INSERT INTO [t754] VALUES (3137,'وأحلى الهوى ما شك في الوصل ربه ... وفي الهجر فهو الدهر يرجو ويتقي','250','2394','0');
INSERT INTO [t754] VALUES (3138,'وغضبي من الإدلال سكرى من الصبا ... شفعت إليها من شبابي بريق','250','2394','0');
INSERT INTO [t754] VALUES (3139,'وأشنب معسول الثنيات واضح ... سترت فمي عنه فقبل مفرقي','250','2394','0');
INSERT INTO [t754] VALUES (3140,'وأجياد غزلان كجيدك زرنني ... فلم أتبين عاطلا من مطوق','250','2394','0');
INSERT INTO [t754] VALUES (3141,'وما كل من يهوى يعف إذا خلى ... عفافي ويرضى الحب والخيل تلتقي','250','2394','0');
INSERT INTO [t754] VALUES (3142,'سقى الله أيام الصبي ما يسرها ... ويفعل فعل البابلي المعتق','250','2394','0');
INSERT INTO [t754] VALUES (3143,'إذا ما لبست الدهر مستمعا به ... تخرقت والملبوس لم يتخرق','250','2394','0');
INSERT INTO [t754] VALUES (3144,'ولم أر كالألحاظ يم رحليهم ... بعثن بكل القتل من كل مشفق','250','2394','0');
INSERT INTO [t754] VALUES (3145,'أدرن عيونا حائرات كأنها ... مركبة أحداقها فوق زيبق','251','2394','0');
INSERT INTO [t754] VALUES (3146,'عشية يعدونا عن النظر البكا ... وعن لذة التوديع خوف التفريق','251','2394','0');
INSERT INTO [t754] VALUES (3147,'نودعهم والبين فينا كأنه ... قنا ابن أبي الهيجاء في قلب فيلق','251','2394','0');
INSERT INTO [t754] VALUES (3148,'قواض مواض نسج داؤد عندها ... إذا وقعت فيه كنسج الحذرنق','251','2394','0');
INSERT INTO [t754] VALUES (3149,'هواد لأملاك الجيوش كأنها ... تخير أرواح الكماة وتنتقى','251','2394','0');
INSERT INTO [t754] VALUES (3150,'تقد عليهم كل درع وجوشن ... وتفري إليهم كل سور وخندق','251','2394','0');
INSERT INTO [t754] VALUES (3151,'يغير بها بين اللقان وواسط ... ويركزها بين الفرات وجلق','251','2394','0');
INSERT INTO [t754] VALUES (3152,'ويرجعها حمرا كأن صحيحها ... يبكي دما من رحمة المتدقق','251','2394','0');
INSERT INTO [t754] VALUES (3153,'فلا تبلغاه ما أقول فإنه ... شجاع متى يذكر له الطعن يشتق','251','2394','0');
INSERT INTO [t754] VALUES (3154,'ضروب بأطراف السيوف بنانه ... لعوب بأطراف الكلام المشقق','251','2394','0');
INSERT INTO [t754] VALUES (3155,'كسائله من يسأل الغيث قطرة ... كعاذله من قال للفلك أرفق','252','2394','0');
INSERT INTO [t754] VALUES (3156,'لقد جدت حتى جدت في كل ملة ... وحتى أتاك الحمد من كل منطق','252','2394','0');
INSERT INTO [t754] VALUES (3157,'رأى ملك الروم ارتياحك للندى ... فقام مقام المجتدى المتملق','252','2394','0');
INSERT INTO [t754] VALUES (3158,'وخلى الرماح السمهرية صاغرا ... لأذرب منه بالطعان وأحذق','252','2394','0');
INSERT INTO [t754] VALUES (3159,'وكاتب من أرض بعيد مرامها ... قريب على خيل حواليك سبق','252','2394','0');
INSERT INTO [t754] VALUES (3160,'وقد سار في مسراك منها رسوله ... فما سار إلا وق هام مفلق','252','2394','0');
INSERT INTO [t754] VALUES (3161,'فلما جنا اخفى عليه مكانه ... شعاع الحديد البارق المتألق','252','2394','0');
INSERT INTO [t754] VALUES (3162,'وأقبل يمشي في البساط فما درى ... إلى الحبر يسعى أم إلى البدر يرتقي','252','2394','0');
INSERT INTO [t754] VALUES (3163,'ولم يثنك الأعداء عن مهجاتهم ... بمثل خضوع في كلام منمق','252','2394','0');
INSERT INTO [t754] VALUES (3164,'وكنت إذا كاتبته قبل هذه ... كتبت إليه في قذال الدمستق','252','2394','0');
INSERT INTO [t754] VALUES (3165,'فإن تعطه بعض الأمان فسائل ... وإن تعطه حد الحسام فأخلق','252','2394','0');
INSERT INTO [t754] VALUES (3166,'وهل ترك البيض الصوارم منهم ... حبيسا لفاد أو رقيقا لمعتق','252','2394','0');
INSERT INTO [t754] VALUES (3167,'لقد وردوا ورد القطا شفراتها ... ومروا عليها رزدقا بعد رزدق','252','2394','0');
INSERT INTO [t754] VALUES (3168,'بلغت بسيف الدولة النور رتبة ... أنرت بها ما بين غرب ومشرق','252','2394','0');
INSERT INTO [t754] VALUES (3169,'إذا شاء أن يلهو بلحية أحمق ... أراه غباري ثم قال له الحق','252','2394','0');
INSERT INTO [t754] VALUES (3170,'وما كمد الحساد شيئا قصدته ... ولكنه من يزحم البحر يغرق','253','2394','0');
INSERT INTO [t754] VALUES (3171,'ويمتحن الناس الأمير برأيه ... ويغضى على علم بكل ممخرق','253','2394','0');
INSERT INTO [t754] VALUES (3172,'وإطراق طرف العين ليس بنافع ... إذا كان طرف القلب ليس بمطرق','253','2394','0');
INSERT INTO [t754] VALUES (3173,'فيا أيها المطلوب جاوره تمتنع ... ويا أيها المحروم يممه ترزق','253','2394','0');
INSERT INTO [t754] VALUES (3174,'ويا أجبن الفرسان صاحبه تجتري ... ويا أشجع الشجعان فارقه تفرق','253','2394','0');
INSERT INTO [t754] VALUES (3175,'إذا سعت الأعداء في كيد مجده ... سعى مجده في جده سعى محنق','253','2394','0');
INSERT INTO [t754] VALUES (3176,'وما ينصر الفضل المبين على العدى ... إذا لم يكن فضل السعيد الموفق','253','2394','0');
INSERT INTO [t754] VALUES (3177,'وصفت لنا ولم نره سلاحا ... كأنك واصف وقت النزال','253','2394','0');
INSERT INTO [t754] VALUES (3178,'وأن البيض صف على دروع ... فشوق من رآه إلى القتال','253','2394','0');
INSERT INTO [t754] VALUES (3179,'فلو أطفأت نارك تا لديه ... قرأت الخط في سود الليالي','253','2394','0');
INSERT INTO [t754] VALUES (3180,'ولو لحظ الدمشتق حافتيه ... لقلب رأيه حالا لحال','253','2394','0');
INSERT INTO [t754] VALUES (3181,'إن استحسنت وهو على بساط ... فأحسن ما يكون على الرجال','253','2394','0');
INSERT INTO [t754] VALUES (3182,'وإن بها وإن به لنقصا ... وأنت لها النهاية في الكمال','253','2394','0');
INSERT INTO [t754] VALUES (3183,'أحسن ما يخضب الحديد به ... وخاضبيه النجيع والغضب','253','2394','0');
INSERT INTO [t754] VALUES (3184,'فلا تشينه بالنضار فما ... يجتمع الماء فيه الذهب','253','2394','0');
INSERT INTO [t754] VALUES (3185,'قد سمعنا ما قلت في الأحلام ... وأنلناك بدرة في المنام','254','2394','0');
INSERT INTO [t754] VALUES (3186,'وانتبهنا كما انتبهت بالا ش ... يء وكان النوال قدر الكلام','254','2394','0');
INSERT INTO [t754] VALUES (3187,'أيها المشتكى إذا رقد الإع ... دام لا رقدة مع الإعدام','254','2394','0');
INSERT INTO [t754] VALUES (3188,'افتح الجفن واترك القول في الن ... وم وميز خطاب سيف الأنام','254','2394','0');
INSERT INTO [t754] VALUES (3189,'الذي ليس عنه مغن ولا من ... هـ بديل ولا لما رام حامي','254','2394','0');
INSERT INTO [t754] VALUES (3190,'كل آبائه كرام بن الدن ... يا ولكنه كريم الكرام','254','2394','0');
INSERT INTO [t754] VALUES (3191,'عذل العواذل حول قلبي التائه ... وهوى الأحبة منه في سودائه','254','2394','0');
INSERT INTO [t754] VALUES (3192,'يشكو الملام إلى اللوائم حره ... ويصد حين يلمن عن برحائه','254','2394','0');
INSERT INTO [t754] VALUES (3193,'وبمهجتي يا عاذلي الملك الذي ... أسخطت كل الناس في إرضائه','254','2394','0');
INSERT INTO [t754] VALUES (3194,'إن كان قد ملك القلوب فإنه ... ملك الزمان بأرضه وسمائه','254','2394','0');
INSERT INTO [t754] VALUES (3195,'الشمس من حساده والنصر من ... قرنائه والسيف من أسمائه','254','2394','0');
INSERT INTO [t754] VALUES (3196,'أين الثلاثة من ثلاث خلاله ... من حسنه وإبائه ومضائه','254','2394','0');
INSERT INTO [t754] VALUES (3197,'مضت الدهور وما أتين بمثله ... ولقد أتى وعجزن عن نظرائه','254','2394','0');
INSERT INTO [t754] VALUES (3198,'القلب أعلم يا عذول بدائه ... وأحق منك بجفنه وبمائه','255','2394','0');
INSERT INTO [t754] VALUES (3199,'فومن أحب لأعصينك في الهوى ... قسما به وبحسنه وبهائه','255','2394','0');
INSERT INTO [t754] VALUES (3200,'أأحبه وأحب فيه ملامة ... إن الملامة فيه من أعدائه','255','2394','0');
INSERT INTO [t754] VALUES (3201,'عجب الوشاة من اللحاة وقولهم ... دع ما نراك ضعفت عن إخفائه','255','2394','0');
INSERT INTO [t754] VALUES (3202,'ما الخل إلا من أود بقلبه ... وأرى بطرف لا يرى بسوائه','255','2394','0');
INSERT INTO [t754] VALUES (3203,'إن المعين على الصبابة بالأسى ... أولى برحمة ربها وأخائه','255','2394','0');
INSERT INTO [t754] VALUES (3204,'مهلا فإن العذل من أسقامه ... وترفقا فالسمع من أعضائه','255','2394','0');
INSERT INTO [t754] VALUES (3205,'وهب الملامة في اللذاذة كالكرى ... مطرودة بسهاده وبكائه','255','2394','0');
INSERT INTO [t754] VALUES (3206,'لا تعذر المشتاق في أشواقه ... حتى تكون حشاك في أحشائه','255','2394','0');
INSERT INTO [t754] VALUES (3207,'أن القتيل مضرجا بدموعه ... مثل القتيل مضرجا بدمائه','255','2394','0');
INSERT INTO [t754] VALUES (3208,'والعشق كالمعشوق يعذب قربه ... للمبتلي وينال من حوبائه','255','2394','0');
INSERT INTO [t754] VALUES (3209,'لو قلت للدنف الحزين فديته ... مما به لأغرته بفدائه','255','2394','0');
INSERT INTO [t754] VALUES (3210,'وقى الأمير هوى العيون فإنه ... ما لا يزول ببأسه وسخائه','256','2394','0');
INSERT INTO [t754] VALUES (3211,'يستأسر البطل الكمي بنظرة ... ويحول بين فؤاده وعزائه','256','2394','0');
INSERT INTO [t754] VALUES (3212,'فأتيت من فوق الزمان وتحته ... متصلصلا وأمامه وورائه','256','2394','0');
INSERT INTO [t754] VALUES (3213,'من للسيوف بأن تكون سميه ... في أصله وفرنده ووفائه','256','2394','0');
INSERT INTO [t754] VALUES (3214,'طبع الحديد فكان من أجناسه ... وعلي المطبوع من آبائه','256','2394','0');
INSERT INTO [t754] VALUES (3215,'رضاك رضائي الذي أوثر ... وسرك سري فما أظهر','256','2394','0');
INSERT INTO [t754] VALUES (3216,'كفتك المروة ما تتقي ... وأمنك الود ما تحذر','256','2394','0');
INSERT INTO [t754] VALUES (3217,'وسركم في الحشا ميت ... إذا أنشر السر لا ينشر','256','2394','0');
INSERT INTO [t754] VALUES (3218,'وإفشاء ما أنا مستودع ... من الغدر والحر لا يغدر','256','2394','0');
INSERT INTO [t754] VALUES (3219,'إذا ما قدرت على نطقة ... فإني على تركها أقدر','256','2394','0');
INSERT INTO [t754] VALUES (3220,'أصرف نفسي كما أشهي ... وأملكها والقنا أحمر','256','2394','0');
INSERT INTO [t754] VALUES (3221,'دواليك يا سيفها دولة ... وأمرك يا خير من يأمر','256','2394','0');
INSERT INTO [t754] VALUES (3222,'أتاني رسولك مستعجلا ... فلباه شعري الذي أذخر','256','2394','0');
INSERT INTO [t754] VALUES (3223,'ولو كان يوم وغى قاتما ... للباه سيفي والأشقر','256','2394','0');
INSERT INTO [t754] VALUES (3224,'فلا غفل الدهر عن أهله ... فإنك عين بها ينظر','256','2394','0');
INSERT INTO [t754] VALUES (3225,'أرى ذلك القرب صار أزورارا ... وصار طويل السلام اختصارا','256','2394','0');
INSERT INTO [t754] VALUES (3226,'تركتني اليوم في خجلة ... أموت مرارا وأحيا مرارا','256','2394','0');
INSERT INTO [t754] VALUES (3227,'أسارقك اللحظ مستحييا ... وأزجر في الخيل مهري سرارا','257','2394','0');
INSERT INTO [t754] VALUES (3228,'وأعلم أني إذا ما اعتذرت ... إليك أراد اعتذاري اعتذارا','257','2394','0');
INSERT INTO [t754] VALUES (3229,'كفرت مكارمك الباهرات ... إن كان ذلك مني اختيارا','257','2394','0');
INSERT INTO [t754] VALUES (3230,'ولكن حمى الشعر إلا القلي ... ل هم حمى النوم إلا غرار','257','2394','0');
INSERT INTO [t754] VALUES (3231,'وما أنا أسقمت جمسي به ... ولا أنا أضرمت في القلب نارا','257','2394','0');
INSERT INTO [t754] VALUES (3232,'فلا تلزمني صروف الزمان ... إلى أساء وإياي ضارا','257','2394','0');
INSERT INTO [t754] VALUES (3233,'وعندي لك الشرد السائرا ... ت لا يختصصن من الأرض دارا','257','2394','0');
INSERT INTO [t754] VALUES (3234,'قواف إذا سرن من مقولي ... وثبن الجبال وخضن البحارا','257','2394','0');
INSERT INTO [t754] VALUES (3235,'فلو خلق الناس من دهرهم ... لكانوا الظلام وكنت النهارا','257','2394','0');
INSERT INTO [t754] VALUES (3236,'ولي فيك ما لم يقل قائل ... وما لم يسر قمر حيث سارا','257','2394','0');
INSERT INTO [t754] VALUES (3237,'أشدهم في الندى هزة ... وأبعدهم في عدو مغارا','257','2394','0');
INSERT INTO [t754] VALUES (3238,'سما بك همي فوق الهموم ... فلست أعد يسارا يسارا','257','2394','0');
INSERT INTO [t754] VALUES (3239,'ومن كنت بحرا له يا عل ... ي لم يقبل الدر إلا كبارا','257','2394','0');
INSERT INTO [t754] VALUES (3240,'ليالي بعد الظاعنين شكول ... طوال وليل العاشقين طويل','257','2394','0');
INSERT INTO [t754] VALUES (3241,'بين لي البدر الذي لا أريده ... ويخفين بدرا ما إليه سبيل','257','2394','0');
INSERT INTO [t754] VALUES (3242,'وما عشت من بعد الأحبة سلوة ... ولكنني للنائبات حمول','257','2394','0');
INSERT INTO [t754] VALUES (3243,'وإن رحيلا واحدا حال بيننا ... وفي الموت من بعد الرحيل رحيل','257','2394','0');
INSERT INTO [t754] VALUES (3244,'إذا كان شم الروح أدنى إليكم ... فلا برحتني روضة وقبول','257','2394','0');
INSERT INTO [t754] VALUES (3245,'وما شرقي بالماء إلا تذكرا ... لماء به أهل الحبيب نزول','258','2394','0');
INSERT INTO [t754] VALUES (3246,'يحرمه لمع الأسنة فوقه ... فليس لظمآن إليه وصول','258','2394','0');
INSERT INTO [t754] VALUES (3247,'أما في النجوم السائرات وغيرها ... لعيني على ضوء الصباح دليل','258','2394','0');
INSERT INTO [t754] VALUES (3248,'ألم ير هذا الليل عينيك رؤيتي ... فتظهر فيه رقة ونحول','258','2394','0');
INSERT INTO [t754] VALUES (3249,'لقيت بدرب القلة الفجر لقية ... شفت كمدي والليل فيه قتيل','258','2394','0');
INSERT INTO [t754] VALUES (3250,'ويوما كأن الحسن فيه علامة ... بعثت بها والشمس منك رسول','258','2394','0');
INSERT INTO [t754] VALUES (3251,'وما قبل سيف الدولة اتار عاشق ... ولا طلبت عند الظلام ذحول','258','2394','0');
INSERT INTO [t754] VALUES (3252,'ولكنه يأتي بكل غريبة ... تروق على استغرابها وتهول','258','2394','0');
INSERT INTO [t754] VALUES (3253,'رمى الدرب بالجرد الجياد إلى العدى ... وما علموا أن السهام خيول','259','2394','0');
INSERT INTO [t754] VALUES (3254,'شوائل تشوال العقارب بالقنا ... لها مرح من تحته وصهيل','259','2394','0');
INSERT INTO [t754] VALUES (3255,'وما هي إلا خطرة عرضت له ... بحران لبتها قنا ونصول','259','2394','0');
INSERT INTO [t754] VALUES (3256,'همام إذا ما هم أمضى همومه ... بأرعن وطأ الموت فيه ثقيل','259','2394','0');
INSERT INTO [t754] VALUES (3257,'وخيل براها الركض في كل بلدة ... إذا عرست فيها فليس تقيل','259','2394','0');
INSERT INTO [t754] VALUES (3258,'فلما تجلى من دلوك وصنجة ... علت كل طودس رأيه ورعيل','259','2394','0');
INSERT INTO [t754] VALUES (3259,'على طرق فيها على الطرق رفعة ... وفي ذكرها عند الأنيس خمول','259','2394','0');
INSERT INTO [t754] VALUES (3260,'فما شعروا حتى رأوها مغيرة ... قباحا وأما خلقها فجميل','259','2394','0');
INSERT INTO [t754] VALUES (3261,'سحائب يمطرن الحديد عليهم ... فكل مكان بالسيوف غسيل','259','2394','0');
INSERT INTO [t754] VALUES (3262,'وأمسى السبايا بنتحبن بعرقة ... كأن جيوب الثاكلات ذيول','259','2394','0');
INSERT INTO [t754] VALUES (3263,'وعادت فظنوها بموزار قفلا ... وليس لها إلا الدخول قفول','259','2394','0');
INSERT INTO [t754] VALUES (3264,'فخاضت نجيع الجمع خوضا كأنه ... بكل نجيع لم تخضه كفيل','259','2394','0');
INSERT INTO [t754] VALUES (3265,'تسايرها النيران في كل مسلك ... به القوم صرعى والديار طلول','259','2394','0');
INSERT INTO [t754] VALUES (3266,'وكرت فمرت في دماء ملطية ... ملطية أم للبنين ثكول','259','2394','0');
INSERT INTO [t754] VALUES (3267,'وأضعفن ما كلفنه من قباقب ... فأضحى كأن الماء فيه عليل','259','2394','0');
INSERT INTO [t754] VALUES (3268,'ورعن بنا قلب الفرات كأنما ... تخر عليه بالرجال سيول','259','2394','0');
INSERT INTO [t754] VALUES (3269,'يطارد فيه موجه كل سابح ... سواء عليه غمرة ومسيل','259','2394','0');
INSERT INTO [t754] VALUES (3270,'تراه كأن الماء مر بجسمه ... وأقبل راس وحده وتليل','259','2394','0');
INSERT INTO [t754] VALUES (3271,'وفي بطن هنزيط وسمنين للظبا ... وصعم القنا ممن أبدن بديل','259','2394','0');
INSERT INTO [t754] VALUES (3272,'طلعن عليهم طلعة يعرفونها ... لها غرر ما تنقضي وحجول','260','2394','0');
INSERT INTO [t754] VALUES (3273,'تمل الحصون الشم طول نزالنا ... فتلقى إلينا أهلها وتزول','260','2394','0');
INSERT INTO [t754] VALUES (3274,'وبتن بحصن الران رزحى من الوجى ... وكل عزيز للأمير ذليل','260','2394','0');
INSERT INTO [t754] VALUES (3275,'وفي كل نفس ما خلاه ملالة ... وفي كل سيف ما خلاه فلول','260','2394','0');
INSERT INTO [t754] VALUES (3276,'ودون سميساط المطامير والملا ... وأودية مجهولة وهجول','260','2394','0');
INSERT INTO [t754] VALUES (3277,'لبسن الدجى فيها إلى أرض مرعش ... وللرةم خطب في البلاد جليل','260','2394','0');
INSERT INTO [t754] VALUES (3278,'فلما رأوه وحده قبل جيشه ... دروا أنكل العالمين فضول','260','2394','0');
INSERT INTO [t754] VALUES (3279,'وأن رماح الخط عنه قصيرة ... وأن حديد الهند عنه كليل','260','2394','0');
INSERT INTO [t754] VALUES (3280,'فأوردهم صدر الحصان وسيفه ... فتى بأسه مثل العطاء جزيل','260','2394','0');
INSERT INTO [t754] VALUES (3281,'جواد على العلات بالمال كله ... ولكنه بالدارعين بخيل','260','2394','0');
INSERT INTO [t754] VALUES (3282,'فودع قتلاهم وشيع فلهم ... بضرب حزون البيض فيه سهول','260','2394','0');
INSERT INTO [t754] VALUES (3283,'على قلب قسطنطين منه تعجب ... وإن كان في ساقيه منه كبول','260','2394','0');
INSERT INTO [t754] VALUES (3284,'لعلك يوما يا دمستق عائد ... فكم هارب مما إليه يؤول','260','2394','0');
INSERT INTO [t754] VALUES (3285,'نجوت بإحدى مهجتيك جريحة ... وخلفت إحدى مهجتيك تسيل','260','2394','0');
INSERT INTO [t754] VALUES (3286,'أتسلم للخطية أبنك هاربا ... ويسكن في الدنيا إليك خليل','260','2394','0');
INSERT INTO [t754] VALUES (3287,'بوجهك ما أنساكه من مرشة ... نصيرك منها رنة وعويل','260','2394','0');
INSERT INTO [t754] VALUES (3288,'أعركم طول الجيوش وعرضها ... علي شروب للجيوش أكول','261','2394','0');
INSERT INTO [t754] VALUES (3289,'إذا لم يكن لليث إلا فريسة ... غداه ولم ينفعك أنك فيل','261','2394','0');
INSERT INTO [t754] VALUES (3290,'إذا الطعن لم تدخلك فيه شجاعة ... هي الطعن لم يدخلك فيه عذول','261','2394','0');
INSERT INTO [t754] VALUES (3291,'فإن تكن الأيام أبصرن صوله ... فقد علم الأيام كيف تصول','261','2394','0');
INSERT INTO [t754] VALUES (3292,'فدتك ملوك لم تسم مواضيا ... فإنك ماضي الشفرتين صقيل','261','2394','0');
INSERT INTO [t754] VALUES (3293,'إذا كان بعض الناس سيفا لدولة ... ففي الناس بوقات لها طبول','261','2394','0');
INSERT INTO [t754] VALUES (3294,'أنا السابق الهادي إلى ما أقوله ... إذ القول قبل القائلين مقول','261','2394','0');
INSERT INTO [t754] VALUES (3295,'وما لكلام الناس فيما يريبني ... أصول ولا للقائليه أصول','261','2394','0');
INSERT INTO [t754] VALUES (3296,'أعادي على ما يوجب الحب للفتى ... وأهدأ والأفكار في تجول','261','2394','0');
INSERT INTO [t754] VALUES (3297,'سوى وجع الحساد داو فإنه ... إذا حل في قلب فليس يحول','261','2394','0');
INSERT INTO [t754] VALUES (3298,'ولا تطمعن من حاسد في مودة ... وإن كنت تبديها له وتنيل','261','2394','0');
INSERT INTO [t754] VALUES (3299,'وإنا لنلقي الحادثات بأنفس ... كثير الرزايا عندهن قليل','261','2394','0');
INSERT INTO [t754] VALUES (3300,'يهون علينا أن تصاب جسومنا ... وتسلم أعراض لنا وعقول','261','2394','0');
INSERT INTO [t754] VALUES (3301,'فتيها وفخرا تغلب ابنة وائل ... فأنت لخير الفاخرين قبيل','261','2394','0');
INSERT INTO [t754] VALUES (3302,'يغم عليا أن يموت عدوه ... إذا لم تغله بالأسنة غول','261','2394','0');
INSERT INTO [t754] VALUES (3303,'شريك المنايا والنفوس غنيمة ... فكل ممات لم يمته غلول','261','2394','0');
INSERT INTO [t754] VALUES (3304,'فإن تكن الدولات قسما فإنها ... لمن ورد الموت الزؤام تدول','261','2394','0');
INSERT INTO [t754] VALUES (3305,'لمن هو الدنيا على النفس ساعة ... وللبيض في هام الكماة صليل','261','2394','0');
INSERT INTO [t754] VALUES (3306,'بأدني ابتسام منك تحي القرائح ... وتقوى من الجسم الضعيف الجوارح','261','2394','0');
INSERT INTO [t754] VALUES (3307,'ومن ذا الذي يقضي حقوقك كلها ... ومن ذا الذي سوى من تسامح','262','2394','0');
INSERT INTO [t754] VALUES (3308,'وقد تقبل العذر الخفي تكرما ... فما بال عذري واقفا وهو واضح','262','2394','0');
INSERT INTO [t754] VALUES (3309,'وإن محالا إذ بك العيش أن أرى ... وجسمك معتل وجسمي صالح','262','2394','0');
INSERT INTO [t754] VALUES (3310,'وما كان ترك الشعر إلا لأنه ... تقصر عن مدح الأمير المدائح','262','2394','0');
INSERT INTO [t754] VALUES (3311,'أيدري ما أرابك من يريب ... وهل ترقى إلى الفلك الخطوب','262','2394','0');
INSERT INTO [t754] VALUES (3312,'وجسمك فوق همة كل داء ... فقرب أقلها منه عجيب','262','2394','0');
INSERT INTO [t754] VALUES (3313,'يجمشك الزمان هوى وحبا ... وقد يؤذي من المقة الحبيب','262','2394','0');
INSERT INTO [t754] VALUES (3314,'وكيف تعلك الدنيا بشيء ... وأنت بعلة الدنيا طبيب','262','2394','0');
INSERT INTO [t754] VALUES (3315,'وكيف تنوبك الشكوى بداء ... وأنت المستغاث لما ينوب','262','2394','0');
INSERT INTO [t754] VALUES (3316,'مللت مقام يوم ليس فيه ... طعان صادق ودمق صبيب','262','2394','0');
INSERT INTO [t754] VALUES (3317,'وأنت الملك تمرضه الحشايا ... لهمته وتشفيه الحروب','262','2394','0');
INSERT INTO [t754] VALUES (3318,'وما بك غير حبك أن تراها ... وعثيرها لأرجلها جنيب','262','2394','0');
INSERT INTO [t754] VALUES (3319,'محجلة لها أرض الأعادي ... وللسمر المناخر والجنوب','262','2394','0');
INSERT INTO [t754] VALUES (3320,'فقرطها الأعنة راجعات ... فإن بعيد ما طلمت قريب','262','2394','0');
INSERT INTO [t754] VALUES (3321,'إذا داء هفا بقراط عنه ... فلم يعرف لصاحبه ضريب','262','2394','0');
INSERT INTO [t754] VALUES (3322,'بسيف الدولة الوضاء تمسي ... جفوني تحت شمس ما تغيب','263','2394','0');
INSERT INTO [t754] VALUES (3323,'فأغزو من غزا وبه اقتداري ... وأرمي من رمى وبه أصيب','263','2394','0');
INSERT INTO [t754] VALUES (3324,'وللحساد عذر أن يشحوا ... على نظري إليه وأن يذوبوا','263','2394','0');
INSERT INTO [t754] VALUES (3325,'فإني قد وصلت إلى مكان ... عليه تحسد الحدق القلوب','263','2394','0');
INSERT INTO [t754] VALUES (3326,'فديت بماذا يسر الرسول ... وأنت الصحيح بذا لا العليل','263','2394','0');
INSERT INTO [t754] VALUES (3327,'عواقب هذا تسوء العدو ... وتثبت فيهم وهذا يزول','263','2394','0');
INSERT INTO [t754] VALUES (3328,'إذا اعتل سيف الدولة اعتلت الأرض ... ومن فوقها والناس والكرم المحض','263','2394','0');
INSERT INTO [t754] VALUES (3329,'وكيف انتفاعي بالرقاد وإنما ... بعلته يعتل في الأعين الغمض','263','2394','0');
INSERT INTO [t754] VALUES (3330,'شفاك الذي يشفي بجودك خلقه ... فإنك بحر كل بحر له بعض','263','2394','0');
INSERT INTO [t754] VALUES (3331,'المجد عوفي إذ عوفيت والكرم ... وزال عنك إلى أعدائك الألم','263','2394','0');
INSERT INTO [t754] VALUES (3332,'صحت بصحتك الغارات وابتهجت ... بها المكارم وانهلت بها الديم','263','2394','0');
INSERT INTO [t754] VALUES (3333,'وراجع الشمس نور كان فارقها ... كأنما فقده في جسمها سقم','263','2394','0');
INSERT INTO [t754] VALUES (3334,'ولاح برقك لي من عارضي ملك ... ما يسقط الغيث إلا حيث يبتسم','263','2394','0');
INSERT INTO [t754] VALUES (3335,'يسمى الحسام وليست من مشابهة ... وكيف يشتبه المخدوم والخدم','263','2394','0');
INSERT INTO [t754] VALUES (3336,'تفرد العرب في الدنيا بمحتده ... وشارك الغرب في إحسانه العجم','263','2394','0');
INSERT INTO [t754] VALUES (3337,'وأخلص الله للإسلام نصرته ... وإن تقلب في آلائه الأمم','263','2394','0');
INSERT INTO [t754] VALUES (3338,'وما أخصك في برء بتهنئة ... إذا سلمت فكل الناس قد سلموا','263','2394','0');
INSERT INTO [t754] VALUES (3339,'الصوم والفطر والأعياد والعصر ... منيرة بك حتى الشمس والقمر','263','2394','0');
INSERT INTO [t754] VALUES (3340,'ترى الأهلة وجها عم نائله ... وما يخص به من جونها البشر','264','2394','0');
INSERT INTO [t754] VALUES (3341,'ما الدهر عندك إلا روضة أنف ... يا من شمائله في دهره زهر','264','2394','0');
INSERT INTO [t754] VALUES (3342,'ما ينتهي لك في أيامه كرم ... فلا انتهى لك في أعوامه عمر','264','2394','0');
INSERT INTO [t754] VALUES (3343,'فإن حظك من تكرارها شرف ... وحظ غيرك منها الشيب والكبر','264','2394','0');
INSERT INTO [t754] VALUES (3344,'حجب ذا البحر بحار دونه ... يذمها الناس ويحمدونه','264','2394','0');
INSERT INTO [t754] VALUES (3345,'يا ماء هل حسدتنا معينه ... أم اشتهيت أن ترى قرينه','264','2394','0');
INSERT INTO [t754] VALUES (3346,'أم انجعت للغنى يمينه ... أم زرته مكثرا قطينه','264','2394','0');
INSERT INTO [t754] VALUES (3347,'أم جئته مخندقا حصونه ... أن الجياد والقنا يكفينه','264','2394','0');
INSERT INTO [t754] VALUES (3348,'يا رب لج جعلت سفينه ... وعازب الروض توفت عونه','264','2394','0');
INSERT INTO [t754] VALUES (3349,'وذي جنون أذهبت جنونه ... وشرب كأس أكثرت رنينه','264','2394','0');
INSERT INTO [t754] VALUES (3350,'وأبدلت غناءه أنينه ... وضيغم أولجها عرينه','264','2394','0');
INSERT INTO [t754] VALUES (3351,'وملك أوطأها جبينه ... يقودها مسهدا جفونه','264','2394','0');
INSERT INTO [t754] VALUES (3352,'مباشرا بنفسه شؤونه ... مشرفا بطعنه طعينه','264','2394','0');
INSERT INTO [t754] VALUES (3353,'عفيف ما في ثوبه مأمونه ... أبيض ما في تاجه ميمونه','264','2394','0');
INSERT INTO [t754] VALUES (3354,'بحر يكون كل بحر نونه ... شمس تمنى الشمس أن تكونه','264','2394','0');
INSERT INTO [t754] VALUES (3355,'إن تدع يا سيف لتستعينه ... يجبك قبل أ، تتم سينه','264','2394','0');
INSERT INTO [t754] VALUES (3356,'أدام من أعدائه تمكينه ... من صان منهم نفسه ودينه','264','2394','0');
INSERT INTO [t754] VALUES (3357,'لكل امرء من دهره ما تعودا ... وعادات سيف الدولة الطعن في العدا','264','2394','0');
INSERT INTO [t754] VALUES (3358,'وأن يكذب الإرجاف عنه بضده ... ويمسي بما تنوي أعاديه أسعدا','264','2394','0');
INSERT INTO [t754] VALUES (3359,'ورب مريد ضره ضر نفسه ... وهاد إليه الجيش أهدى وما هدى','264','2394','0');
INSERT INTO [t754] VALUES (3360,'ومستكبر لم يعرف الله ساعة ... رأي سيفه في كفه فتشهدا','265','2394','0');
INSERT INTO [t754] VALUES (3361,'هو البحر غص فيه إذا كان ساكنا ... على الدر واحذره إذا كان مزيدا','265','2394','0');
INSERT INTO [t754] VALUES (3362,'فإني رأيت البحر يعثر بالفتى ... وهذا الذي يأتي الفتى متعمدا','265','2394','0');
INSERT INTO [t754] VALUES (3363,'تظل ملوك الأرض خاشعة له ... تفارقه هلكي وتلقاه سجدا','265','2394','0');
INSERT INTO [t754] VALUES (3364,'وتحيى له المال الصوارم والقنا ... ويقتل ما تحيي التبسم والحدا','265','2394','0');
INSERT INTO [t754] VALUES (3365,'ذكي تظنيه طليعة عينه ... يرى قلبه في يومه ما ترى غدا','265','2394','0');
INSERT INTO [t754] VALUES (3366,'وصول إلى المستصعبات بخيله ... فلو كان قرن الشمس ماء لأوردا','265','2394','0');
INSERT INTO [t754] VALUES (3367,'لذلك سمى ابن الدمستق يومه ... مماتا وسماه الدمستق مولدا','265','2394','0');
INSERT INTO [t754] VALUES (3368,'سريت إلى جيحان من أرض آمد ... ثلاثا لقد أدناك ركض وأبعدا','265','2394','0');
INSERT INTO [t754] VALUES (3369,'فولى وأعطاك ابنه وجيوشه ... جميعا ولم يعط الجميع ليحمدا','265','2394','0');
INSERT INTO [t754] VALUES (3370,'عرضت له دون الحيوة وطرفه ... وأبصر سيف الله منك مجردا','265','2394','0');
INSERT INTO [t754] VALUES (3371,'وما طلبت رزق الأسنة غيره ... ولكن قسطنطين كان له الفدا','265','2394','0');
INSERT INTO [t754] VALUES (3372,'فاصبح يجتاب المسوح مخافة ... وقد كان يجتاب الدلاص المسردا','265','2394','0');
INSERT INTO [t754] VALUES (3373,'ويمشي به العكاز في الدير تائبا ... وما كان يرضى مشي أشقر أجردا','265','2394','0');
INSERT INTO [t754] VALUES (3374,'وما تاب حتى غادر الكر وجهه ... جريحا وخلى جفنه النقع أرمدا','266','2394','0');
INSERT INTO [t754] VALUES (3375,'فلو كان ينجي من علي ترهب ... ترهبت الأملاك مثنى وموحدا','266','2394','0');
INSERT INTO [t754] VALUES (3376,'وكل امرىء في الشرق والغرب بعدها ... يعد له ثوبا من الشعر اسودا','266','2394','0');
INSERT INTO [t754] VALUES (3377,'هنيئا لك العيد الذي أنت عيده ... وعيد لمن سمى وضحى وعيدا','266','2394','0');
INSERT INTO [t754] VALUES (3378,'ولا زالت الأعياد لبسك بعده ... تسلم مخروقا وتعطي مجددا','266','2394','0');
INSERT INTO [t754] VALUES (3379,'فذا اليوم في الأيام مثلك في الورى ... كما كنت فيهم أوحدا كان أوحدا','266','2394','0');
INSERT INTO [t754] VALUES (3380,'هو الجد حتى تفضل العين أختها ... وحتى يكون اليوم لليوم سيدا','266','2394','0');
INSERT INTO [t754] VALUES (3381,'فوا عجبا من دائل أنت سيفه ... تصيده الضرغام فيما تصيدا','266','2394','0');
INSERT INTO [t754] VALUES (3382,'ومن يجعل الضرغام للصيد بازه ... تصيده الضرغام فيما تصيدا','266','2394','0');
INSERT INTO [t754] VALUES (3383,'رأيتك محض الحلم في محض قدرة ... ولو شئت كان الحلم منك المهندا','266','2394','0');
INSERT INTO [t754] VALUES (3384,'وما قتل الأحرار كالعفو عنهم ... ومن لك بالحر الذي يحفظ اليدا','266','2394','0');
INSERT INTO [t754] VALUES (3385,'إذا أنت أكرمت الكريم ملكته ... وإن أنت أكرمت اللئيم تمردا','266','2394','0');
INSERT INTO [t754] VALUES (3386,'ووضع الندى في موضع السيف بالعلي ... مضر كوضع السيف في موضع الندى','266','2394','0');
INSERT INTO [t754] VALUES (3387,'ولكن تفوق الناس رأيا وحكمة ... كما فقتهم حالا ونفسا ومحتدا','266','2394','0');
INSERT INTO [t754] VALUES (3388,'يدق على الأفكار ما أنت فاعل ... فيترك ما يخفى ويوخذ ما بدا','266','2394','0');
INSERT INTO [t754] VALUES (3389,'أزل حسد الحساد عني بكبتهم ... فأنت الذي صيرتهم لي حسدا','267','2394','0');
INSERT INTO [t754] VALUES (3390,'إذا شد زندي حسن رأيك في يدي ... ضربت بنصل يقطع الهام مغمدا','267','2394','0');
INSERT INTO [t754] VALUES (3391,'وما أنا إلا سمهري حملته ... فزين معروضا وراع مسددا','267','2394','0');
INSERT INTO [t754] VALUES (3392,'وما الدهر إلا من رواة قلائدي ... إذا قلت شعرا أصبح الدهر منشدا','267','2394','0');
INSERT INTO [t754] VALUES (3393,'فسار به من لا يسير مشمرا ... وغنى به من لا يغني مغردا','267','2394','0');
INSERT INTO [t754] VALUES (3394,'أجزني إذا أنشدت شعرا فإنما ... بشعري أتاك المادحون مرددا','267','2394','0');
INSERT INTO [t754] VALUES (3395,'ودع كل صوت بعد صوتي فإنني ... أنا الصائح المحكي والآخر الصدى','267','2394','0');
INSERT INTO [t754] VALUES (3396,'تركت السرى خلفي لمن قل ماله ... وأنعلت أفراسي بنعماك عسجدا','267','2394','0');
INSERT INTO [t754] VALUES (3397,'وقيدت نفسي في ذراك محبة ... ومن وجد الإنسان قيدا تقيدا','267','2394','0');
INSERT INTO [t754] VALUES (3398,'إذا سأل الإنسان أيامه الغنى ... وكنت على بعد جعلتك موعدا','268','2394','0');
INSERT INTO [t754] VALUES (3399,'إن كنت عن خير الأنام سائلا ... فخيرهم أكثرهم فضائلا','268','2394','0');
INSERT INTO [t754] VALUES (3400,'من أنت منهم يا همام وائلا ... الطاعنين في الوغا أوائلا','268','2394','0');
INSERT INTO [t754] VALUES (3401,'والعاذلين في الندى العواذلا ... قد فضلوا بفضلك القبائلا','268','2394','0');
INSERT INTO [t754] VALUES (3402,'ظلم لذا اليوم وصف قبل رؤيته ... لا يصدق الوصف حتى يصدق النظر','268','2394','0');
INSERT INTO [t754] VALUES (3403,'تزاحم الجيش حتى لم يجد سببا ... إلى بساطك لي سمع ولا بصر','268','2394','0');
INSERT INTO [t754] VALUES (3404,'فكنت أشهد مختص وأغيبه ... معاينا وعياني كله خبر','268','2394','0');
INSERT INTO [t754] VALUES (3405,'اليوم يرفع ملك الروم ناظره ... لأن عفوك عنه عنده ظفر','268','2394','0');
INSERT INTO [t754] VALUES (3406,'فإن أجبت بشيء عن رسائله ... فلا يزال على الأملاك يفتخر','268','2394','0');
INSERT INTO [t754] VALUES (3407,'قد استراحت إلى وقت رقابهم ... من السيوف وباقي القوم ينتظر','268','2394','0');
INSERT INTO [t754] VALUES (3408,'وقد تبدلها بالقوم غيرهم ... لكي تجم رؤس القوم والقصر','268','2394','0');
INSERT INTO [t754] VALUES (3409,'تشبيه جودك بالأمطار غادية ... جود لكفك ثان ناله المطر','268','2394','0');
INSERT INTO [t754] VALUES (3410,'تكسب الشمس منك النور طالعة ... كما تكسب منها نورها القمر','269','2394','0');
INSERT INTO [t754] VALUES (3411,'دروع لملك الروم هذي الرسائل ... يرد بها عن نفسه ويشاغل','269','2394','0');
INSERT INTO [t754] VALUES (3412,'هي الزرد الضافي عليه ولفظها ... عليك ثناء سابغ وفضائل','269','2394','0');
INSERT INTO [t754] VALUES (3413,'وأنى اهتدى هذا الرسول بأرضه ... وما سكنت مذ سرت فيها القساطل','269','2394','0');
INSERT INTO [t754] VALUES (3414,'ومن أي ماء كان يسقي جياده ... ولم تصف من مزج الدماء المناهل','269','2394','0');
INSERT INTO [t754] VALUES (3415,'أتاك يكاد الرأس يجحد عنقه ... وتنقد تحت الذعر منه المفاصل','269','2394','0');
INSERT INTO [t754] VALUES (3416,'يقوم تقويم السماطين مشيه ... إليك إذا ما عوجته الأفاكل','269','2394','0');
INSERT INTO [t754] VALUES (3417,'فقاسمك العينين منه ولحظه ... سميك والخل الذي لا يزايل','269','2394','0');
INSERT INTO [t754] VALUES (3418,'وأبصر منك الرزق والرزق مطمع ... وابصر منه الموت والموت هائل','269','2394','0');
INSERT INTO [t754] VALUES (3419,'وقبل ما قبل الترب قبله ... وكل كمي واقف متضائل','269','2394','0');
INSERT INTO [t754] VALUES (3420,'وأسعد مشتاق وأظفر طالب ... همام إلى تقبيل كمك واصل','269','2394','0');
INSERT INTO [t754] VALUES (3421,'مكان تمناه الشفاه ودونه ... صدور المذاكي والرماح الذوابل','269','2394','0');
INSERT INTO [t754] VALUES (3422,'فما بلغته ما أراد كرامة ... عليك ولكن لم يخب لك سائل','269','2394','0');
INSERT INTO [t754] VALUES (3423,'وأكبر منه همة بعثت به ... إليك العدى واستنظرته الجحافل','269','2394','0');
INSERT INTO [t754] VALUES (3424,'فاقبل من أصحابه وهو مرسل ... وعاد إلى أصحابه وهو عاذل','269','2394','0');
INSERT INTO [t754] VALUES (3425,'تحير في سيف ربيعة أصله ... وطابعه الرحمن والمجد صاقل','269','2394','0');
INSERT INTO [t754] VALUES (3426,'وما لونه مما تحصل مقلة ... ولا حدة مما تجس الأنامل','269','2394','0');
INSERT INTO [t754] VALUES (3427,'إذا عاينتك الرسل هانت نفوسها ... عليها وما جاءت به والمراسل','269','2394','0');
INSERT INTO [t754] VALUES (3428,'رجا الروم من ترجى النوافل كلها ... لديه ولا ترجى لديه الطوائل','269','2394','0');
INSERT INTO [t754] VALUES (3429,'فإن كان خوف القتل والأسر ساقهم ... فقد فعلوا ما القتل والأسر فاعل','269','2394','0');
INSERT INTO [t754] VALUES (3430,'فخافوك حتى ما لقتل زيادة ... وجاؤوك حتى ما تراد السلاسل','270','2394','0');
INSERT INTO [t754] VALUES (3431,'أرى كل ي ملك إليك مصيره ... كأنك بحر والملوك جداول','270','2394','0');
INSERT INTO [t754] VALUES (3432,'إذا مطرت ومنك سحائب ... فوابلهم طل وطلك وابل','270','2394','0');
INSERT INTO [t754] VALUES (3433,'كريم متى استوهبت ما أنت راكب ... وقد لقحت حرب فإنك نازل','270','2394','0');
INSERT INTO [t754] VALUES (3434,'إذا الجود أعط الناس ما أنت مالك ... ولا تعطين الناس ما أنا قائل','270','2394','0');
INSERT INTO [t754] VALUES (3435,'أفي كل يوم تحت ضبني شويعر ... ضعيف يقاويني قصير يطاول','270','2394','0');
INSERT INTO [t754] VALUES (3436,'لساني بنطقي صامت عنه عادل ... وقلبي بصمتي ضاحك منه هازل','270','2394','0');
INSERT INTO [t754] VALUES (3437,'وأتعب من ناداك من لا تجيبه ... وأغيظ من عاداك من لا تشاكل','270','2394','0');
INSERT INTO [t754] VALUES (3438,'وما التيه طبي فيهم غير أنني ... بغيض إلي الجاهل المتعاقل','270','2394','0');
INSERT INTO [t754] VALUES (3439,'وأكثر تيهي أنني بك واثق ... وأكثر مالي أنني لك أمل','270','2394','0');
INSERT INTO [t754] VALUES (3440,'لعل لسيف الدولة القرم هبة ... يعيش بها حق ويهلك باطل','270','2394','0');
INSERT INTO [t754] VALUES (3441,'رميت عداه بالقوافي وفضله ... وهن الغوازي السالمات القواتل','270','2394','0');
INSERT INTO [t754] VALUES (3442,'وقد زعموا أن النجوم خوالد ... ولو حاربته ناح فيها الثواكل','270','2394','0');
INSERT INTO [t754] VALUES (3443,'وما كان أدناها له لو أرادها ... وألطفها لو أنه المتناول','270','2394','0');
INSERT INTO [t754] VALUES (3444,'قريب عليه كل ناء على الورى ... إذا لثمته بالغبار القنابل','270','2394','0');
INSERT INTO [t754] VALUES (3445,'تدبر شرق الأرض والغرب كفه ... وليس لها وقتا عن الجود شاغل','270','2394','0');
INSERT INTO [t754] VALUES (3446,'يتبع هراب الرجال مراده ... فمن فر حربا عارضته الغوائل','271','2394','0');
INSERT INTO [t754] VALUES (3447,'ومن فر من إحسانه حسدا له ... تلقاه منه حيث ما سار نائل','271','2394','0');
INSERT INTO [t754] VALUES (3448,'فتى لا يرى إحسانه وهو كامل ... له كاملا حتى يرى وهو شامل','271','2394','0');
INSERT INTO [t754] VALUES (3449,'إذا العرب العرباء رازت نفوسها ... فأنت فتاها والمليك الحلاحل','271','2394','0');
INSERT INTO [t754] VALUES (3450,'أطاعتك في أرواحها وتصرفت ... بأمرك التفت عليك القبائل','271','2394','0');
INSERT INTO [t754] VALUES (3451,'وكل أنابيب القنا مدد له ... وما تنكت الفرسان إلا العوامل','271','2394','0');
INSERT INTO [t754] VALUES (3452,'رأيتك لو لم يقتض الطعن في الوغى ... إليك انقيادا لاقتضته الشمائل','271','2394','0');
INSERT INTO [t754] VALUES (3453,'ومن لم تعلمه لك الذل نفسه ... من الناس طرا علمته المناصل','271','2394','0');
INSERT INTO [t754] VALUES (3454,'لنا ملك لا يطعنم النوم همه ... ممات لحي أو حيوة لميت','271','2394','0');
INSERT INTO [t754] VALUES (3455,'ويكبر أ، تقذى بشيء جفونه ... إذا ما رأته خلة بك قرت','271','2394','0');
INSERT INTO [t754] VALUES (3456,'جزى الله عني سيف الدولة هاشم ... فإن نداه الغمر سيفي ودولتي','271','2394','0');
INSERT INTO [t754] VALUES (3457,'بغيرك راعيا عبث الذئاب ... وغيرك صارما ثلم الضراب','271','2394','0');
INSERT INTO [t754] VALUES (3458,'وتملك أنفس الثقلين طرا ... فكيف تحوز أنفسها كلاب','271','2394','0');
INSERT INTO [t754] VALUES (3459,'وما تركوك معصية ولكن ... يعاف الورد والموت الشراب','271','2394','0');
INSERT INTO [t754] VALUES (3460,'طلبتهم على الأمواه حتى ... تخوف أن تفتشه السحاب','271','2394','0');
INSERT INTO [t754] VALUES (3461,'فبت لياليا لا نوم فيها ... تخب بك المسومة العراب','271','2394','0');
INSERT INTO [t754] VALUES (3462,'يهز الجيش حولك جانبيه ... كما نفضت جناحيها العقاب','271','2394','0');
INSERT INTO [t754] VALUES (3463,'وتسأل عنهم الفوات حتى ... أجابك بعضها وهم الجواب','271','2394','0');
INSERT INTO [t754] VALUES (3464,'فقاتل عن حريمهم وفروا ... ندى كفيك والنسب القراب','272','2394','0');
INSERT INTO [t754] VALUES (3465,'وحفظك فيهم سلفي معد ... وأنهم العشائر والصحاب','272','2394','0');
INSERT INTO [t754] VALUES (3466,'تكفكف عنهم صم العوالي ... وقد شرقت بظعنهم الشعاب','272','2394','0');
INSERT INTO [t754] VALUES (3467,'وأسقطت الأجنة في الولايا ... وأجهضت الحوائل والسقاب','272','2394','0');
INSERT INTO [t754] VALUES (3468,'وعمرو في ميامنهم عور ... وكعب في مياسرهم كعاب','272','2394','0');
INSERT INTO [t754] VALUES (3469,'وقد خذلت أبو بكر بنيها ... وخاذلها قريط والضباب','272','2394','0');
INSERT INTO [t754] VALUES (3470,'إذا ما سرت في آثار قوم ... تخاذلت الجماجم والرقاب','272','2394','0');
INSERT INTO [t754] VALUES (3471,'فعدن كما أخذن مكرمات ... عليهن القلائد والملاب','272','2394','0');
INSERT INTO [t754] VALUES (3472,'يثبنك بالذي أوليت شكرا ... وأين من الذي تولى الثواب','272','2394','0');
INSERT INTO [t754] VALUES (3473,'وليس مصيرهن إليك شينا ... ولا في صونهن لديك عاب','272','2394','0');
INSERT INTO [t754] VALUES (3474,'ولا في فقدهن بني كلاب ... إذا أبصرن غرتك إغتراب','272','2394','0');
INSERT INTO [t754] VALUES (3475,'وكيف يتم بأسك في أناس ... تصيبهم فيولمك المصاب','272','2394','0');
INSERT INTO [t754] VALUES (3476,'ترفق أيها المولى عليهم ... فإن الرفق بالجاني عتاب','272','2394','0');
INSERT INTO [t754] VALUES (3477,'وإنهم عبيدك حيث كانوا ... إذا تدعو لحادثة أجابوا','272','2394','0');
INSERT INTO [t754] VALUES (3478,'وعين المخطئين هم وليسوا ... بأول معشر خطئوا فتابوا','272','2394','0');
INSERT INTO [t754] VALUES (3479,'وأنت حيوتهم غضبت عليهم ... وهجر حيوتهم لهم عقاب','272','2394','0');
INSERT INTO [t754] VALUES (3480,'وما جهلت أياديك البوادي ... ولكن ربما خفي الصواب','273','2394','0');
INSERT INTO [t754] VALUES (3481,'وكم ذنب مولده دلال ... وكم ذنب مولده اقتراب','273','2394','0');
INSERT INTO [t754] VALUES (3482,'وجرم جره سفهاء قوم ... فحل بغير جارمه العذاب','273','2394','0');
INSERT INTO [t754] VALUES (3483,'فإن هابوا بجرمهم عليا ... فقد يرجو عليا من يهاب','273','2394','0');
INSERT INTO [t754] VALUES (3484,'وإن يك سيف دولة غير قيس ... فمنه جلود قيس والثياب','273','2394','0');
INSERT INTO [t754] VALUES (3485,'وتحت ربابه نبتوا وأثوا ... وفي أيامه كثروا وطابوا','273','2394','0');
INSERT INTO [t754] VALUES (3486,'وتحت لوائه ضربوا الأعادي ... وذل لهم من العرب الصعاب','273','2394','0');
INSERT INTO [t754] VALUES (3487,'ولو غير الأمير غزا كلابا ... ثناه عن شموسهم ضباب','273','2394','0');
INSERT INTO [t754] VALUES (3488,'ولاقى دون ثأيهم طعانا ... يلاقي عنده الذئب الغراب','273','2394','0');
INSERT INTO [t754] VALUES (3489,'وخيلا تغتذى ريح الموامي ... ويكفيها من الماء السراب','273','2394','0');
INSERT INTO [t754] VALUES (3490,'ولكن ربهم أسرى إليهم ... فما نفع الوقوف ولا الذهاب','273','2394','0');
INSERT INTO [t754] VALUES (3491,'ولا ليل أجن ولا نهار ... ولا خيل حملن ولا ركاب','273','2394','0');
INSERT INTO [t754] VALUES (3492,'رميتهم ببحر من حديد ... له في البر خلفهم عباب','273','2394','0');
INSERT INTO [t754] VALUES (3493,'فمساهم وبسطهم حرير ... وصبحهم وبسطهم تراب','273','2394','0');
INSERT INTO [t754] VALUES (3494,'ومن في كفه منهم قناة ... كمن في كفه منهم خضاب','273','2394','0');
INSERT INTO [t754] VALUES (3495,'بنو قتلى أبيك بأرض نجد ... ومن أبقى وأبقته الحراب','273','2394','0');
INSERT INTO [t754] VALUES (3496,'عفا عنهم وأعتقهم صغارا ... وفي أعناق أكثرهم سخاب','273','2394','0');
INSERT INTO [t754] VALUES (3497,'فكلكم أتى مأتى أبيه ... وكل فعال كلكم عجاب','273','2394','0');
INSERT INTO [t754] VALUES (3498,'كذا فيسر من طلب الأعادي ... ومثل سراك فليكن الطلاب','274','2394','0');
INSERT INTO [t754] VALUES (3499,'على قدر أهل العزم تأتي العزائم ... وتأتي على قدر الكرام المكارم','274','2394','0');
INSERT INTO [t754] VALUES (3500,'وتعظم في عين الصغير صغارها ... وتصغر في عين العظيم العظائم','274','2394','0');
INSERT INTO [t754] VALUES (3501,'يكلف سيف الدولة الجيش همه ... وقد عجزت عنه الجيوش الخضارم','274','2394','0');
INSERT INTO [t754] VALUES (3502,'ويطلب عند الناس ما عند نفسه ... وذلك ما لا تدعيه الضراغم','274','2394','0');
INSERT INTO [t754] VALUES (3503,'يفدي أتم عمرا سلاحه ... نسور الملا أحداثها والقشاعم','274','2394','0');
INSERT INTO [t754] VALUES (3504,'وما ضرها خلق بغير مخالب ... وقد خلقت أسيافه والقوائم','274','2394','0');
INSERT INTO [t754] VALUES (3505,'هل الحدث الحمراء تعرف لونها ... وتعلم أي الساقيين الغمائم','274','2394','0');
INSERT INTO [t754] VALUES (3506,'سقتها الغمام الغر قبل نزوله ... فلما دنا منها سقتها الجماجم','274','2394','0');
INSERT INTO [t754] VALUES (3507,'بناها فأعلى والقنا يقرع القنا ... وموج المنايا حولها متلاطم','274','2394','0');
INSERT INTO [t754] VALUES (3508,'وكان بها مثل الجنون فأصبحت ... ومن جثث القتلى عليها تمتئم','274','2394','0');
INSERT INTO [t754] VALUES (3509,'طريدة دهر ساقها فرددتها ... على الدين بالخطى والدهر راغم','274','2394','0');
INSERT INTO [t754] VALUES (3510,'تفيت الليالي كل شيء أخذته ... وهن لما يأخذن منك غوارم','274','2394','0');
INSERT INTO [t754] VALUES (3511,'إذا كان ما تنويه فعلا مضارعا ... مضى قبل أن تلقي عليه الجوازم','275','2394','0');
INSERT INTO [t754] VALUES (3512,'وكيف ترجى الروم والروس هدمها ... وذا الطعن آساس لها ودعائم','275','2394','0');
INSERT INTO [t754] VALUES (3513,'وقد حاكموها والمنايا حواكم ... فما مات مظلوم ولا عاش ظالم','275','2394','0');
INSERT INTO [t754] VALUES (3514,'أتوك يجرون الحديد كأنهم ... سروا بجياد ما لهن قوائم','275','2394','0');
INSERT INTO [t754] VALUES (3515,'إذا برقوا لم تعرف البيض منهم ... ثيابهم من مثلها والعمائم','275','2394','0');
INSERT INTO [t754] VALUES (3516,'خميس بشرق الأرض والغرب زحفه ... وفي أذن الجوزاء منه زمازم','275','2394','0');
INSERT INTO [t754] VALUES (3517,'تجمع فيه كل لسن وأمة ... فما تفهم الحداث إلا التراجم','275','2394','0');
INSERT INTO [t754] VALUES (3518,'فلله وقت ذوب الغش ناره ... فلم يبق إلا صارم أو ضبارم','275','2394','0');
INSERT INTO [t754] VALUES (3519,'تقطع ما لا يقطع الدرع والقنا ... وفر من الفرسان من لا يصادم','275','2394','0');
INSERT INTO [t754] VALUES (3520,'وقفت وما في الموت شك لواقف ... كأنك في جفن الردى وهو نائم','275','2394','0');
INSERT INTO [t754] VALUES (3521,'تمر بك الأبطال كلمى هزيمة ... ووجهك وضاح وثغرك باسم','276','2394','0');
INSERT INTO [t754] VALUES (3522,'تجاوزت مقدار الشجاعة والنهى ... إلى قول قوم أنت بالغيب عالم','276','2394','0');
INSERT INTO [t754] VALUES (3523,'ضممت جناحيهم على القلب ضمة ... تموت الخوافي تحتها والقوادم','276','2394','0');
INSERT INTO [t754] VALUES (3524,'بضرب أتى الهامات والنصر غائب ... وصار إلى اللبات والنصر قادم','276','2394','0');
INSERT INTO [t754] VALUES (3525,'حقرت الردينيات حتى طرحتها ... وحتى كأن السيف للرمح شاتم','276','2394','0');
INSERT INTO [t754] VALUES (3526,'ومن طلب الفتح الجليل فإنما ... مفاتيحه البيض الخفاف الصوارم','276','2394','0');
INSERT INTO [t754] VALUES (3527,'نثرتهم فوق الأحيدب كله ... كما نثرت فوق العروس الدراهم','276','2394','0');
INSERT INTO [t754] VALUES (3528,'تدوس بك الخيل الوكور على الذرى ... وقد كثرت حول الوكور المطاعم','277','2394','0');
INSERT INTO [t754] VALUES (3529,'تظن فراخ الفتخ أنك زرتها ... بأماتها وهي العتاق الصلادم','277','2394','0');
INSERT INTO [t754] VALUES (3530,'إذا زلقت مشيتها ببطونها ... كما تتمشى في الصعيد الأراقم','277','2394','0');
INSERT INTO [t754] VALUES (3531,'أفي كل يوم ذا الدمستق مقدم ... قفاه على الإقدام للوجه لائم','277','2394','0');
INSERT INTO [t754] VALUES (3532,'أينكر ريح الليث حتى يذوقه ... وقد عرفت ريح الليوث البهائم','277','2394','0');
INSERT INTO [t754] VALUES (3533,'وقد فجعته بابنه وابن صهره ... وبالصهر حملاتث الأمير الغواشم','277','2394','0');
INSERT INTO [t754] VALUES (3534,'مضى يشكر الأصحاب في فوته الظبا ... لما شغلتها هامهم والمعاصم','277','2394','0');
INSERT INTO [t754] VALUES (3535,'ويفهم صوت المشرفية فيهم ... على أن اصوات السيوف أعاجم','277','2394','0');
INSERT INTO [t754] VALUES (3536,'يسر بما أعطاك لا من جهالة ... ولكن مغنوما نجا منك غانم','277','2394','0');
INSERT INTO [t754] VALUES (3537,'ولست مليكا هازما لنظيره ... ولكنك التوحيد للشرك هازم','277','2394','0');
INSERT INTO [t754] VALUES (3538,'تشرف عدنان به لا ربيعة ... وتفتخر الدنيا به لا العواصم','277','2394','0');
INSERT INTO [t754] VALUES (3539,'لك الحمد في الدر الذي لي لفظه ... فإنك معطيه وإني ناظم','277','2394','0');
INSERT INTO [t754] VALUES (3540,'وأني لتعدوبي عطاياك في الوغا ... فلا أنا مذموم ولا أنت نادم','277','2394','0');
INSERT INTO [t754] VALUES (3541,'على كل طيار إليها برجله ... إذا وقعت في مسمعيه الغماغم','277','2394','0');
INSERT INTO [t754] VALUES (3542,'ألا أيها السيف الذي لست مغمدا ... ولا فيك مرتاب ولا منك عاصم','277','2394','0');
INSERT INTO [t754] VALUES (3543,'هنيأ لضرب الهام والمجد والعلى ... وراجيك والإسلام أنك سالم','278','2394','0');
INSERT INTO [t754] VALUES (3544,'ولام لا يقي الرحمن حديك ما وقى ... وتغليقه هام العدى بك دائم','278','2394','0');
INSERT INTO [t754] VALUES (3545,'أراع كذا كل الأنام همام ... وسح له رسل الملوك غمام','278','2394','0');
INSERT INTO [t754] VALUES (3546,'ودانت له الدنيا فأصبح جالسا ... وأيامها فيما يريد قيام','278','2394','0');
INSERT INTO [t754] VALUES (3547,'إذا زار سيف الدولة الروم غازيا ... كفاها لمام لو كفاه لمام','278','2394','0');
INSERT INTO [t754] VALUES (3548,'فتى تتبع الأزمان في الناس خطوه ... لكل زمان في يديه زمام','278','2394','0');
INSERT INTO [t754] VALUES (3549,'تنام لديك الرسل أمنا وغبطة ... وأجفان رب الرسل ليس تنام','278','2394','0');
INSERT INTO [t754] VALUES (3550,'حذارا لمعروري الجياد فجاءة ... إلى الطعن قبلا ما لهن لجام','278','2394','0');
INSERT INTO [t754] VALUES (3551,'تعطف فيه والأعنة شعرها ... وتضرب فيه والسياط كلام','278','2394','0');
INSERT INTO [t754] VALUES (3552,'وما تنفع الخيل الكرام ولا القنا ... إذا لم يكن فوق الكرام كرام','278','2394','0');
INSERT INTO [t754] VALUES (3553,'إلى كم ترد الرسل عما أتوا له ... كأنهم فيما وهبت ملام','278','2394','0');
INSERT INTO [t754] VALUES (3554,'فإن كنت لا تعطي الذمام طواعة ... فعوذ الأعادي بالكريم ذمام','278','2394','0');
INSERT INTO [t754] VALUES (3555,'وإن نفوسا أممتك منيعة ... وإن دماء أملتك حرام','278','2394','0');
INSERT INTO [t754] VALUES (3556,'إذا خاف ملك من مليك اجرته ... وسيفك خافوا والجوار تسام','278','2394','0');
INSERT INTO [t754] VALUES (3557,'لهم عنك بالبيض الخفاف تفرق ... وحولك بالكتب اللطاف زحام','278','2394','0');
INSERT INTO [t754] VALUES (3558,'تغر حلاوات النفوس قلوبها ... فتختار بعض العيش وهو حمام','278','2394','0');
INSERT INTO [t754] VALUES (3559,'وشر الحمامين الزؤامين عيشة ... يذلك الذي يختاره ويضام','278','2394','0');
INSERT INTO [t754] VALUES (3560,'فلو كان صلحا لم يكن بشفاعة ... ولكنه ذل لهم وغرام','278','2394','0');
INSERT INTO [t754] VALUES (3561,'ومن لفرسان الثغور عليهم ... بتبليغهم ما لا يكاد يرام','279','2394','0');
INSERT INTO [t754] VALUES (3562,'كتائب جاؤوا خاضعين فأقدموا ... ولو لم يكونو خائفين لخاموا','279','2394','0');
INSERT INTO [t754] VALUES (3563,'وعزت قديما في ذراك خيولهم ... وعزوا وعامت في نداك وعاموا','279','2394','0');
INSERT INTO [t754] VALUES (3564,'على وجهك الميمون في كل غارة ... صلوة توالي منهم وسلام','279','2394','0');
INSERT INTO [t754] VALUES (3565,'وكل أناس يتبعون إمامهم ... وأنت لأهل المكرمات إمام','279','2394','0');
INSERT INTO [t754] VALUES (3566,'ورب جواب عن كتاب بعثنه ... وعنوانه للناظرين قتام','279','2394','0');
INSERT INTO [t754] VALUES (3567,'تضيق به البيداء من قبل نشره ... وما فض بالبيدءا عنه ختام','279','2394','0');
INSERT INTO [t754] VALUES (3568,'حروف هجاء الناس فيه ثلاثة ... جوادق ورمح ذابل وحسام','279','2394','0');
INSERT INTO [t754] VALUES (3569,'أذا الحرب قد اتبعتها فاله ساعة ... ليغمد نصل أو يحل حزام','279','2394','0');
INSERT INTO [t754] VALUES (3570,'وإن طال أعمار الرماح بهدنة ... فإن الذي يعمرن عندك عام','279','2394','0');
INSERT INTO [t754] VALUES (3571,'وما زلت تفنى السمر وهي كثيرة ... وتفنى بهن الجيش وهو لهام','279','2394','0');
INSERT INTO [t754] VALUES (3572,'متى عاود الجالون عاودت أرضهم ... وفيها رقاب للسيوف وهام','279','2394','0');
INSERT INTO [t754] VALUES (3573,'وربوا لك الأولاد حتى تصيبها ... وقد كعبت بنت وشب غلام','279','2394','0');
INSERT INTO [t754] VALUES (3574,'جرى معك الجارون حتى إذا انتهوا ... إلى الغاية القصوى جريت وقاموا','279','2394','0');
INSERT INTO [t754] VALUES (3575,'فليس لشمس مذ أنرت إنارة ... وليس لبدر مذ تممت تمام','279','2394','0');
INSERT INTO [t754] VALUES (3576,'تذكرت ما بين العذيب وبارق ... مجر عوالينا ومجرى السوابق','279','2394','0');
INSERT INTO [t754] VALUES (3577,'وصحبة قوم يذبحون قنيصهم ... بفضلات ما قد كسروا في المفارق','280','2394','0');
INSERT INTO [t754] VALUES (3578,'وليلا توسدنا الثوية تحته ... كأن ثراها عنبر في المرافق','280','2394','0');
INSERT INTO [t754] VALUES (3579,'بلاد إذا زار الحسان بغيرها ... حصى تربها ثقينه للمخانق','280','2394','0');
INSERT INTO [t754] VALUES (3580,'سقتني بها القطربلي مليحة ... على كاذب من وعدها ضوء صادق','280','2394','0');
INSERT INTO [t754] VALUES (3581,'سهاد لأجفان وشمس لناظر ... وسقم لأبدان ومسك لناشق','280','2394','0');
INSERT INTO [t754] VALUES (3582,'وأغيد يهوى نفسه كل عاقل ... عفيف ويهوى جسمه كل فاسق','280','2394','0');
INSERT INTO [t754] VALUES (3583,'أديب إذا ما جس أوتار مزهر ... بلا كل سمع عن سواها بعائق','280','2394','0');
INSERT INTO [t754] VALUES (3584,'يحدث عما بين عاد وبينه ... وصدغاه في خدي غلام مراهق','280','2394','0');
INSERT INTO [t754] VALUES (3585,'وما الحسن في وجه الفتى شرفا له ... إذا لم يكن في فعله والخلائق','280','2394','0');
INSERT INTO [t754] VALUES (3586,'وما بلد الإنسان غير الموافق ... ولا أهله الأدنون غير الأصدق','281','2394','0');
INSERT INTO [t754] VALUES (3587,'وجائزة دعوى المحبة والهوى ... وإنك ان لا يخفى كلام المنافق','281','2394','0');
INSERT INTO [t754] VALUES (3588,'برأي من انقادت عقيل إلى الردى ... وإشمات مخلوق وإسخاط خالق','281','2394','0');
INSERT INTO [t754] VALUES (3589,'أرادوا عليا بالذي يعجز الورى ... ويوسع قتل الجحفل المتضايق','281','2394','0');
INSERT INTO [t754] VALUES (3590,'فما بسطوا كفا إلى غير قاطع ... ولا حملوا رأسا إلى غير فلقها','281','2394','0');
INSERT INTO [t754] VALUES (3591,'لقد أقدموا لو صادفوا غير آخذ ... وقد هربوا لو صادفوا غير لاحق','281','2394','0');
INSERT INTO [t754] VALUES (3592,'ولما كسا كعبا ثيابا طغوا بها ... رمى كل ثوب من سنان بخارق','281','2394','0');
INSERT INTO [t754] VALUES (3593,'ولما سقى الغيث الذي كفروا به ... سقى غيره في غير تلك البوارق','281','2394','0');
INSERT INTO [t754] VALUES (3594,'أتاهم بها حشو العجاجة والقنا ... سنابكها تحشو بطون الحمالق','281','2394','0');
INSERT INTO [t754] VALUES (3595,'عوابس حلي يابس الماء حزمها ... فن على أوساطها كالمناطق','281','2394','0');
INSERT INTO [t754] VALUES (3596,'فليت أبا الهيجا يرى خلف تدمر ... طوال العوالي في طوال السمالق','281','2394','0');
INSERT INTO [t754] VALUES (3597,'وسوق علي من معد وغيرها ... قبائل لا تعطى القفى لسائق','281','2394','0');
INSERT INTO [t754] VALUES (3598,'قشير وبلعجلان فيها خفية ... كرائين في ألفاظ ألثغ ناطق','281','2394','0');
INSERT INTO [t754] VALUES (3599,'تخليهم النسوان غير فوارك ... وهم خلوا النسوان غير طوالق','281','2394','0');
INSERT INTO [t754] VALUES (3600,'يفرق ما بين الكماة وبينها ... بطعن يسلي حره كل عاشق','281','2394','0');
INSERT INTO [t754] VALUES (3601,'أتى الظعن حتى ما تطير رشاشة ... من الخيل إلا في نحور العواتق','282','2394','0');
INSERT INTO [t754] VALUES (3602,'بكل فلاة تنكر الإنس أرضها ... ظعائن حمر الحلي حمر الأيانق','282','2394','0');
INSERT INTO [t754] VALUES (3603,'وملمومة سيفية ربيعة ... تصيح الحصى فيها صياح اللقالق','282','2394','0');
INSERT INTO [t754] VALUES (3604,'بعيدة أطراف القنا من أصوله ... قريبة بين البيض غبر اليلامق','282','2394','0');
INSERT INTO [t754] VALUES (3605,'نهاها وأغناها عن النهب جوده ... فما تبتغي إلا حماة الحقائق','282','2394','0');
INSERT INTO [t754] VALUES (3606,'توهمها الأعراب سورة مترف ... تذكره البيداء ظل السرادق','282','2394','0');
INSERT INTO [t754] VALUES (3607,'فذكرتهم بالماء ساعة غبرت ... سماوة كلب في أنوف الحزائق','282','2394','0');
INSERT INTO [t754] VALUES (3608,'وكانوا يرعون الملوك بأن بدوا ... وأن نبتت في الماء نبت الغلافق','282','2394','0');
INSERT INTO [t754] VALUES (3609,'فهاجوك أهدى في الفلا من نجومه ... وأبدى بيوتا من أداحي النقانق','282','2394','0');
INSERT INTO [t754] VALUES (3610,'وأصبر عن أمواهه من ضبابه ... وآلف منها مقلة للودائق','282','2394','0');
INSERT INTO [t754] VALUES (3611,'وكان هديرا من فحول تركتها ... مهلبة الأذناب خرس الشقاشق','282','2394','0');
INSERT INTO [t754] VALUES (3612,'فما حرموا بالركض خيلك راحة ... ولكن كفاها البر قطع الشواهق','283','2394','0');
INSERT INTO [t754] VALUES (3613,'ولا شغلوا صم القنا بقلوبهم ... عن الركز لكن عن قلوب الدماسق','283','2394','0');
INSERT INTO [t754] VALUES (3614,'ألم يحذروا مسخ الذي يمسخ العدى ... ويجعل أيدي الأسد أيدي الخرانق','283','2394','0');
INSERT INTO [t754] VALUES (3615,'وقد عاينوه في سواهم وربما ... أرى مارقا في الحرب مصرع مارق','283','2394','0');
INSERT INTO [t754] VALUES (3616,'تعود أن لا تقضم الحب خيله ... إذا الهام لم ترفع جنوب العلائق','283','2394','0');
INSERT INTO [t754] VALUES (3617,'ولا ترد الغدران إلا وماؤها ... من الدم كالريحان تحت الشقائق','283','2394','0');
INSERT INTO [t754] VALUES (3618,'لوفد نمير كان أرشد منهم ... وقد طردوا الأظعان طرد الوسائق','283','2394','0');
INSERT INTO [t754] VALUES (3619,'أعدوا رماحا من خضوع وطاعنوا ... بها الجيش حتى رد غرب الفيالق','283','2394','0');
INSERT INTO [t754] VALUES (3620,'فلم أر أرمى منه غير مخاتل ... وأسرى إلى الأعداء غير مسارق','283','2394','0');
INSERT INTO [t754] VALUES (3621,'تصيب المجانيق العظام بكفه ... دقائق قد أعيت قسى البنادق','283','2394','0');
INSERT INTO [t754] VALUES (3622,'طوال قنا تطاعنها قصار ... وقطرك في ندى ووغى بحار','284','2394','0');
INSERT INTO [t754] VALUES (3623,'وفيك إذا جنى الجاني أناة ... تظن كرامة وهي احتقار','284','2394','0');
INSERT INTO [t754] VALUES (3624,'وأخذ للحواضر والبوادي ... بضبط لم تعوده نزار','284','2394','0');
INSERT INTO [t754] VALUES (3625,'تشممه شميم الوحش إنسا ... وتنكره فيعروها نفار','284','2394','0');
INSERT INTO [t754] VALUES (3626,'وما انقادت لغيرك في زمان ... فتدري ما المقادة والصغار','284','2394','0');
INSERT INTO [t754] VALUES (3627,'وأفرحت المقاود ذفرييها ... وصعر خدها هذا العذار','284','2394','0');
INSERT INTO [t754] VALUES (3628,'وأطمع عامر البقيا عليها ... ونزقها احتمالك والوقار','284','2394','0');
INSERT INTO [t754] VALUES (3629,'وغيرها التراسل والتشاكي ... وأعجبها التلبب والمغار','284','2394','0');
INSERT INTO [t754] VALUES (3630,'جياد تعجز الأرسان عنها ... وفرسان تضيق بها الديار','284','2394','0');
INSERT INTO [t754] VALUES (3631,'وكانت بالتوقف عن رداها ... نفوسا في رداها تستشار','284','2394','0');
INSERT INTO [t754] VALUES (3632,'وكنت السيف قائمه إليهم ... وفي الأعداء حدك والغرار','284','2394','0');
INSERT INTO [t754] VALUES (3633,'فأمست بالبدية شفرتاه ... وأمسى خلف قائمه الحيار','284','2394','0');
INSERT INTO [t754] VALUES (3634,'وكان بنو كلاب حيث كعب ... فخافوا أن يصيروا حيث صاروا','284','2394','0');
INSERT INTO [t754] VALUES (3635,'تلقوا عز مولاهم بذل ... وسار إلى بني كعب وساروا','284','2394','0');
INSERT INTO [t754] VALUES (3636,'فأقبلها المروج مسومات ... ضوامر لا هزال ولا شيار','284','2394','0');
INSERT INTO [t754] VALUES (3637,'تثير على سليمى مسبطرا ... تناكر تحته لولا الشعار','285','2394','0');
INSERT INTO [t754] VALUES (3638,'عجاجا تعثر العقبان فيه ... كأن الجو وعث أو خبار','285','2394','0');
INSERT INTO [t754] VALUES (3639,'وظل الطعن في الخيلين خلسا ... كأن الموت بينهما اختصار','285','2394','0');
INSERT INTO [t754] VALUES (3640,'فلزهم الطراد إلى قتال ... أحذ سلاحهم فيه الفرار','285','2394','0');
INSERT INTO [t754] VALUES (3641,'مضوا متسابقي الأعضاء فيه ... لأرؤسهم بأرجلهم عثار','285','2394','0');
INSERT INTO [t754] VALUES (3642,'يشلهم بكل أقب نهد ... لفارسه على الخيل الخيار','285','2394','0');
INSERT INTO [t754] VALUES (3643,'وكل أصم يعسل جانباه ... على الكعبين منه دم ممار','285','2394','0');
INSERT INTO [t754] VALUES (3644,'يغادر كل ملتفت إليه ... ولبته لثعلبه وجار','285','2394','0');
INSERT INTO [t754] VALUES (3645,'إذا صرف النهار الضوء عنهم ... دجى ليلان ليل والغبار','285','2394','0');
INSERT INTO [t754] VALUES (3646,'وإن جنح الظلام إنجاب عنهم ... أضاء المشرفية والنهار','285','2394','0');
INSERT INTO [t754] VALUES (3647,'يبكي خلفهم دثر بكاه ... رغاء أو ثواج أو يعار','285','2394','0');
INSERT INTO [t754] VALUES (3648,'غطى بالعثير البيداء حتى ... تحيرت المتالي والعشار','285','2394','0');
INSERT INTO [t754] VALUES (3649,'ومروا بالجباة يضم فيها ... كلا الجيشين من نقع إزار','286','2394','0');
INSERT INTO [t754] VALUES (3650,'وجاؤوا الصحصحان بلا سروج ... وقد سقط العمامة والخمار','286','2394','0');
INSERT INTO [t754] VALUES (3651,'وأرهقت العذارى مردفات ... وأوطئت الأصيبية الصغار','286','2394','0');
INSERT INTO [t754] VALUES (3652,'وقد نزح العوير فلا عوير ... ونهيا والبييضة والجفار','286','2394','0');
INSERT INTO [t754] VALUES (3653,'وليس بغير تدمر مستغاث ... وتدمر كاسمها لهم دمار','286','2394','0');
INSERT INTO [t754] VALUES (3654,'أرادوا أن يديروا الرأي فيها ... فصبحهم برأي لا يدار','286','2394','0');
INSERT INTO [t754] VALUES (3655,'وجيش كلما حاروا بأرض ... وأقبل أقبلت فيه تحار','286','2394','0');
INSERT INTO [t754] VALUES (3656,'يحف أغر لا قود عليه ... ولادية تساق ولا اعتذار','286','2394','0');
INSERT INTO [t754] VALUES (3657,'تريق سيوفه مهج الأعادي ... وكل دم أراقته جبار','286','2394','0');
INSERT INTO [t754] VALUES (3658,'وكانوا الأسد ليس لها مصال ... على طير وليس لها مطار','286','2394','0');
INSERT INTO [t754] VALUES (3659,'إذا فاتوا الرماح تناولتهم ... بأرماح من العطش القفار','286','2394','0');
INSERT INTO [t754] VALUES (3660,'يرون الموت قداما وخلفا ... فيختارون والموت اضطرار','286','2394','0');
INSERT INTO [t754] VALUES (3661,'إذا سلك السماوة غير هاد ... فقتلاهم لعينيه منار','286','2394','0');
INSERT INTO [t754] VALUES (3662,'ولو لم يبق لم تعش البقايا ... وفي الماضي لمن بقي اعتبار','286','2394','0');
INSERT INTO [t754] VALUES (3663,'إذا لم يرع سيدهم عليهم ... فمن يرعى عليهم أو يغار','286','2394','0');
INSERT INTO [t754] VALUES (3664,'تفرقهم وإياه السجايا ... ويجمعهم وإياه النجار','286','2394','0');
INSERT INTO [t754] VALUES (3665,'ومال بها على أرك وعرض ... وأهل الرقتين لها مزار','286','2394','0');
INSERT INTO [t754] VALUES (3666,'وأجفل بالفرات بنو نمير ... وزارهم الذي زأروا خوار','286','2394','0');
INSERT INTO [t754] VALUES (3667,'فهم حزق على الخابور صرعى ... بهم من شرب غيرهم خمار','287','2394','0');
INSERT INTO [t754] VALUES (3668,'فلم يسرح لهم في الصبح مال ... ولم توقد لهم بالليل نار','287','2394','0');
INSERT INTO [t754] VALUES (3669,'حذرا فتى إذا لم يرض عنهم ... فليس بنافع لهم الحذار','287','2394','0');
INSERT INTO [t754] VALUES (3670,'تبيت وفودهم تسري إليه ... وجدواه التي سألوا اغتفار','287','2394','0');
INSERT INTO [t754] VALUES (3671,'فخلفهم برد البيض عنهم ... وهامهم له معهم معار','287','2394','0');
INSERT INTO [t754] VALUES (3672,'هم ممن أذم لهم عليه ... كريم العرق والحسب النضار','287','2394','0');
INSERT INTO [t754] VALUES (3673,'فأصبح بالعواصم مستقرا ... وليس لبحر نائله قرار','287','2394','0');
INSERT INTO [t754] VALUES (3674,'وأضحى ذكره في كل أرض ... تدار على الغناء به العقار','287','2394','0');
INSERT INTO [t754] VALUES (3675,'تخر له القبائل ساجدات ... وتحمده الأسنة والشفار','287','2394','0');
INSERT INTO [t754] VALUES (3676,'كأن شعاع عين الشمس فيه ... ففي أبصارنا عنه انكسار','287','2394','0');
INSERT INTO [t754] VALUES (3677,'فمن طلب الطعان فذا علي ... وخيل الله والأسل الحرار','287','2394','0');
INSERT INTO [t754] VALUES (3678,'يراه الناس حيث رأته كعب ... بأرض ما لنازلها استتار','287','2394','0');
INSERT INTO [t754] VALUES (3679,'يوسطه المفاوز كل يوم ... طلاب الطاعنين لا الإنتظار','287','2394','0');
INSERT INTO [t754] VALUES (3680,'تصاهل خيله متجاوبات ... وما من عادة الخيل السرار','287','2394','0');
INSERT INTO [t754] VALUES (3681,'بنو كعب وما أثرت فيهم ... يد لم يدمها إلا السوار','287','2394','0');
INSERT INTO [t754] VALUES (3682,'بها من قطعه ألم ونقص ... وفيها من جلالته افتخار','287','2394','0');
INSERT INTO [t754] VALUES (3683,'لهم حق بشركك في نزار ... وأدنى الشرك في أصل جوار','287','2394','0');
INSERT INTO [t754] VALUES (3684,'لعل بنيهم لبنيك جند ... فأولى قرح الخيل المهار','287','2394','0');
INSERT INTO [t754] VALUES (3685,'وأنت أبر من لو عق أفنى ... وأعفى من عقوبته البوار','287','2394','0');
INSERT INTO [t754] VALUES (3686,'وأقدر من يهيجه انتصار ... وأحلم من يحلمه اقتدار','288','2394','0');
INSERT INTO [t754] VALUES (3687,'وما في سطوة الأرباب عيب ... ولا في ذلة العبدان عار','288','2394','0');
INSERT INTO [t754] VALUES (3688,'أيا راميا يصمى فؤاد مرامه ... تربى عداه ريشها لسهامه','288','2394','0');
INSERT INTO [t754] VALUES (3689,'أسير إلى أقطاعه في ثيابه ... على طرفه من داره بحسامه','288','2394','0');
INSERT INTO [t754] VALUES (3690,'وما مطرتنيه من البيض والقنا ... وروم العبدى هاطلات غمامه','288','2394','0');
INSERT INTO [t754] VALUES (3691,'فتى يهب الإقليم بالمال والقرى ... ومن فيه من فرسانه وكرامه','288','2394','0');
INSERT INTO [t754] VALUES (3692,'ويجعل ما خولته من نواله ... جزاء لما خولته من كلامه','288','2394','0');
INSERT INTO [t754] VALUES (3693,'فلازالت الشمس التي في سمائه ... مطالعة الشمس التي في لثامه','288','2394','0');
INSERT INTO [t754] VALUES (3694,'فلازال يجتاز البدور بوجهه ... تعجب من نقصانها وتمامه','288','2394','0');
INSERT INTO [t754] VALUES (3695,'إن يكن صبر ذي الرزية فضلا ... تكن الأفضل الأعز الأجلا','288','2394','0');
INSERT INTO [t754] VALUES (3696,'أنت يا فوق أن تعزى عن الأح ... باب فوق الذي يعزيك عقلا','288','2394','0');
INSERT INTO [t754] VALUES (3697,'وبألفاظك اهتدى فإذا ع ... زاك قال الذي له قلت قبلا','288','2394','0');
INSERT INTO [t754] VALUES (3698,'قد بلوت الخطوب مرا وحلوا ... وسلكت الزمان حزنا وسهلا','288','2394','0');
INSERT INTO [t754] VALUES (3699,'وقتلت الزمان علما فما يغ ... رب قولا ولا يجدد فعلا','288','2394','0');
INSERT INTO [t754] VALUES (3700,'أجد الحزن فيك حفظا وعقلا ... وأراه في الخلق ذعرا وجهلا','288','2394','0');
INSERT INTO [t754] VALUES (3701,'لك إلف يجره وإذا ما ... كرم الأصل كان للإلف أصلا','289','2394','0');
INSERT INTO [t754] VALUES (3702,'ووفاء نبت فيه ولكن ... لم يزل للوفاء أهلك أهلا','289','2394','0');
INSERT INTO [t754] VALUES (3703,'إن خير الدموع عونا لدمع ... بعثته رعية فاستهلا','289','2394','0');
INSERT INTO [t754] VALUES (3704,'أين ذي الرقة التي في الحر ... ب إذا استنكره الحديد وصلا','289','2394','0');
INSERT INTO [t754] VALUES (3705,'أين خلفتها غداة لقيت ال ... روم والهام بالصوارم تفلى','289','2394','0');
INSERT INTO [t754] VALUES (3706,'قاسمتك المنون شخصين جورا ... جعل القسم نفسه فيك عدلا','289','2394','0');
INSERT INTO [t754] VALUES (3707,'فإذا قست ما أخذن بما أغ ... درن سرى على الفؤاد وسلى','289','2394','0');
INSERT INTO [t754] VALUES (3708,'وتيقنت أن حظك أوفى ... وتبينت أن جدك أعلى','289','2394','0');
INSERT INTO [t754] VALUES (3709,'ولعمري لقد شغلت المنايا ... بالأعادي فكيف يطلبن شغلا','289','2394','0');
INSERT INTO [t754] VALUES (3710,'وكم انتشت بالسيوف من الده ... ر أسيرا وبالنوال مقلا','289','2394','0');
INSERT INTO [t754] VALUES (3711,'عدها نصرة عليه فلما ... صال ختلا رآه أدرك تبلا','289','2394','0');
INSERT INTO [t754] VALUES (3712,'كذبته ظنونه أنت تبلي ... هـ وتبقى في نعمة ليس تبلى','289','2394','0');
INSERT INTO [t754] VALUES (3713,'ولقد رامك العداة كما را ... م فلم يجرحوا لشخصك ظلا','289','2394','0');
INSERT INTO [t754] VALUES (3714,'ولقد رمت بالسعادة بعضا ... من نفوس العدى فأدركت كلا','290','2394','0');
INSERT INTO [t754] VALUES (3715,'قارعت رمحك الرماح ولكن ... ترك الرامحين رمحك عزلا','290','2394','0');
INSERT INTO [t754] VALUES (3716,'لو يكون الذي وردت من الف ... جعة طعنا أوردته الخيل قبلا','290','2394','0');
INSERT INTO [t754] VALUES (3717,'ولكشفت ذا الحنين بضرب ... طالما كشف الكروب وجلى','290','2394','0');
INSERT INTO [t754] VALUES (3718,'خطبة للحمام ليس لها ر ... د وإن كانت المسماة ثكلا','290','2394','0');
INSERT INTO [t754] VALUES (3719,'وإذا لم تجد من الناس كفوا ... ذات خدر أرادت الموت بعلا','290','2394','0');
INSERT INTO [t754] VALUES (3720,'ولذيذ الحيوة أنفس في النف ... س وأشهى من أن يمل وأحلى','290','2394','0');
INSERT INTO [t754] VALUES (3721,'وإ االشيخ قال أف فما م ... ل حيوة وإنما الضعف ملا','290','2394','0');
INSERT INTO [t754] VALUES (3722,'آلة العيش صحة وشباب ... فإذا وليا عن المرء ولى','290','2394','0');
INSERT INTO [t754] VALUES (3723,'أبدا تسترد ما تهب الدن ... يا فيا ليت جودها كان بخلا','290','2394','0');
INSERT INTO [t754] VALUES (3724,'فكفت كون فرحة تورث الغ ... م وخل يغادر الوجد خلا','290','2394','0');
INSERT INTO [t754] VALUES (3725,'وهي معشوقة على الغدر لا تح ... فظ عهدا ولا تتمم وصلا','290','2394','0');
INSERT INTO [t754] VALUES (3726,'كل دمع يسيل منها عليها ... وبفك اليدين عنها تخلى','290','2394','0');
INSERT INTO [t754] VALUES (3727,'شيم الغانيات فيها فلا أد ... رى لذا أنث اسمها الناس أم لا','290','2394','0');
INSERT INTO [t754] VALUES (3728,'يا مليك الورى المفرق محيا ... ومماتا فيهم وعزا وذلا','290','2394','0');
INSERT INTO [t754] VALUES (3729,'قلد الله دولة سيفها أن ... ت حساما بالمكرمات محلى','290','2394','0');
INSERT INTO [t754] VALUES (3730,'فبه أغنت الموالي بذلا ... وبه أفنت الأعادي قتلا','290','2394','0');
INSERT INTO [t754] VALUES (3731,'وإذا اهتز للندى كان بحرا ... وإذا اهتز للوغا كان نصلا','290','2394','0');
INSERT INTO [t754] VALUES (3732,'وإذا الأرض أظلمت كان شمسا ... وإذا الإرض أمحلت كان وبلا','290','2394','0');
INSERT INTO [t754] VALUES (3733,'وهو الضارب الكتيبة والطع ... نة تغلو والضرب أغلى وأغلى','290','2394','0');
INSERT INTO [t754] VALUES (3734,'أيها الباهر العقول فما تد ... رك وصفا أتعبت فكري فملا','291','2394','0');
INSERT INTO [t754] VALUES (3735,'من تعاطى تشبها بكل أعيا ... هـ ومن سار في طريقك ضلا','291','2394','0');
INSERT INTO [t754] VALUES (3736,'فإذا ما اشتهى خلودك داع ... قال لا زلت أو ترى لك مثلا','291','2394','0');
INSERT INTO [t754] VALUES (3737,'ذي المعالي فليعلون من تعالى ... هكذا هكذا وإلا فلا لا','291','2394','0');
INSERT INTO [t754] VALUES (3738,'شرف ينطح النجوم بروقي ... هـ وعز يقلقل الأجبالا','291','2394','0');
INSERT INTO [t754] VALUES (3739,'حال أعدائنا عظيم وسيف ال ... دولة ابن السيوف أعظم حالا','291','2394','0');
INSERT INTO [t754] VALUES (3740,'كلما أعجلوا النذير مسيرا ... أعجلتهم جياده الإعجالا','291','2394','0');
INSERT INTO [t754] VALUES (3741,'فأتتهم خوارق الأرض ما تح ... مل إلا الحديد والأبطالا','291','2394','0');
INSERT INTO [t754] VALUES (3742,'خافيات الألوان قد نسج النق ... ع عليها براقعا جلالا','291','2394','0');
INSERT INTO [t754] VALUES (3743,'حالفته صدورها والعوالي ... لخوضن دونه الأهوالا','291','2394','0');
INSERT INTO [t754] VALUES (3744,'ولتمضن حيث لا يجد الرم ... ح مدارا ولا الحصان مجالا','291','2394','0');
INSERT INTO [t754] VALUES (3745,'لا ألوم ابن لاون ملك الرو ... م وإن كان ما تمنى محالا','291','2394','0');
INSERT INTO [t754] VALUES (3746,'أقلقته بنية بين أذني ... هـ وبان بغي السماء فنالا','291','2394','0');
INSERT INTO [t754] VALUES (3747,'كلما رام حطها اتسع البن ... ى فغطى جبينه والقذالا','291','2394','0');
INSERT INTO [t754] VALUES (3748,'يجمع الروم والصقالب والبل ... غر فيها وتجمع الآجالا','291','2394','0');
INSERT INTO [t754] VALUES (3749,'وتوافيهم بها في القنا السم ... ر كما وافت العطاش الصلالا','292','2394','0');
INSERT INTO [t754] VALUES (3750,'قصدوا هدم سورها فبنوه ... وأتوا كي يقصروه فطالا','292','2394','0');
INSERT INTO [t754] VALUES (3751,'وساتجروا مكايد الحرب حتى ... تركوها لها عليهم وبالا','292','2394','0');
INSERT INTO [t754] VALUES (3752,'رب أمر أتاك لا تحمد الف ... عال فيه وتحمد الأفعالا','292','2394','0');
INSERT INTO [t754] VALUES (3753,'وقسي رميت عنها فردت ... في قلوب الرماة عنك النصالا','292','2394','0');
INSERT INTO [t754] VALUES (3754,'أخذوا الطرق يقطعون بها الرس ... ل فكان انقطاعها إرسالا','292','2394','0');
INSERT INTO [t754] VALUES (3755,'وهم البحر ذو الغوارب إلا ... أنه صار عند بحرك آلا','292','2394','0');
INSERT INTO [t754] VALUES (3756,'ما مضوا لم يقاتلوك ولك ... ن القتال الذي كفاك القتالا','292','2394','0');
INSERT INTO [t754] VALUES (3757,'والذي قطع الرقاب من الضر ... ب بكفيك قطع الآمالا','292','2394','0');
INSERT INTO [t754] VALUES (3758,'والثبات الذي أجادوا قديما ... علم الثابتين ذا الإجفالا','292','2394','0');
INSERT INTO [t754] VALUES (3759,'نزلوا في مصارع عرفوها ... يندبون الأعمام والأخوالا','292','2394','0');
INSERT INTO [t754] VALUES (3760,'تحمل الريح بينهم شعر الها ... م تدذرى عليهم الأوصالا','292','2394','0');
INSERT INTO [t754] VALUES (3761,'تنذر الجسم أن يقيم لديها ... وتريه لكل عضو مثالا','292','2394','0');
INSERT INTO [t754] VALUES (3762,'أبصروا الطعن في القلوب دراكا ... قبل أن يبصروا الرماح خيالا','292','2394','0');
INSERT INTO [t754] VALUES (3763,'فإذا حاولت طعانك خيل ... أبصرت أذرع القنا أميالا','292','2394','0');
INSERT INTO [t754] VALUES (3764,'٣','293','0','0');
INSERT INTO [t754] VALUES (3765,'بسط الرعب في اليمين يمينا ... فتولوا وفي الشمال شمالا','293','3764','0');
INSERT INTO [t754] VALUES (3766,'ينفض الروع أيديا ليس تدرى ... أسيوفا حملن أم أغلالا','293','3764','0');
INSERT INTO [t754] VALUES (3767,'ووجوها أخافها منك وجه ... تركت حسنها له والجمالا','293','3764','0');
INSERT INTO [t754] VALUES (3768,'والعيان الجلي يحدث للظ ... ن زوالا وللمراد انتقالا','293','3764','0');
INSERT INTO [t754] VALUES (3769,'وإذا ما خلا الجبان بأرض ... طلب الطعن وحده والنزالا','293','3764','0');
INSERT INTO [t754] VALUES (3770,'أقسموا لا رأوك إلا بلقب ... طالما غرت العيون الرجالا','293','3764','0');
INSERT INTO [t754] VALUES (3771,'أي عين تأملتك فلاقت ... ك وطرف رنا إليك فآلا','293','3764','0');
INSERT INTO [t754] VALUES (3772,'ما يشك اللعين في أخذك الجي ... ش فهل يبعث الجيوش نوالا','293','3764','0');
INSERT INTO [t754] VALUES (3773,'ما لمن ينصب الحبائل في الأر ... ض ومرجاه أن يصيد الهلالا','293','3764','0');
INSERT INTO [t754] VALUES (3774,'إن دون التي على الدرب والأح ... دب والنهر مخلطا مزيالا','293','3764','0');
INSERT INTO [t754] VALUES (3775,'غصب الدهر والملوك عليها ... فبناها في وجنة الدهر خالا','294','3764','0');
INSERT INTO [t754] VALUES (3776,'فهي تمشي مشي العروس اختيالا ... وتثنى على الزمان دلالا','294','3764','0');
INSERT INTO [t754] VALUES (3777,'وحماها بكل مطرد الأك ... عب جور الزمان والأوجالا','294','3764','0');
INSERT INTO [t754] VALUES (3778,'وظبي تعرف الحرام من الح ... ل فقد أفنت الدماء حلالا','294','3764','0');
INSERT INTO [t754] VALUES (3779,'في خميس من الأسود بئيس ... يفترسن النفوس والأموالا','294','3764','0');
INSERT INTO [t754] VALUES (3780,'إنما أنفس الأنيس سباع ... يتفارسن جهرة واغتيالا','294','3764','0');
INSERT INTO [t754] VALUES (3781,'من أطاق التماس شيء غلابا ... واغتصابا لم يلتمسه سؤالا','294','3764','0');
INSERT INTO [t754] VALUES (3782,'كل غاد لحاجة يتمنى ... أن يكون الغضنفر الرئبالا','294','3764','0');
INSERT INTO [t754] VALUES (3783,'رأيتك توسع الشعراء نيلا ... حديثهم المولد والقديما','294','3764','0');
INSERT INTO [t754] VALUES (3784,'فتعطى من بقي مالا جسيما ... وتعطي من مضى شرفا عظيما','294','3764','0');
INSERT INTO [t754] VALUES (3785,'سمعتك منشدا بيتي زياد ... نشيدا مثل منشده كريما','294','3764','0');
INSERT INTO [t754] VALUES (3786,'فما أنكرت موضعه ولكن ... غبطت بذاك أعظمه الرميما','294','3764','0');
INSERT INTO [t754] VALUES (3787,'ذكر الصبا ومرابع الآرام ... جلبت حمامي قبل وقت حمامي','294','3764','0');
INSERT INTO [t754] VALUES (3788,'دمن تكاثرت الهموم علي في ... عرصاتها كتكاثر اللوام','294','3764','0');
INSERT INTO [t754] VALUES (3789,'وكأن كل سحابة وقفت بها ... تبكي بعيني عروة بن حزام','294','3764','0');
INSERT INTO [t754] VALUES (3790,'ولطالما أفنيت ريق كعابها ... فيها وافنت بالعتاب كلامي','294','3764','0');
INSERT INTO [t754] VALUES (3791,'قد كنت تهزأ بالفراق مجانة ... وتجر ذيلي شرة وعرام','294','3764','0');
INSERT INTO [t754] VALUES (3792,'ليس القباب على الركاب وإنما ... هن الحياة ترحلت بسلام','295','3764','0');
INSERT INTO [t754] VALUES (3793,'ليت الذي خلق النوى جعل الحصى ... لخفافهن مفاصلي وعظامي','295','3764','0');
INSERT INTO [t754] VALUES (3794,'متلاحظين نسح ماء شؤوننا ... حذرا من الرقباء في الأكمام','295','3764','0');
INSERT INTO [t754] VALUES (3795,'أرواحنا انهملت وعشنا بعدها ... من بعد ما قطرت على الأقدام','295','3764','0');
INSERT INTO [t754] VALUES (3796,'لو كن يوم جرين كن كصبرنا ... عند الرحيل لكن غير سجام','295','3764','0');
INSERT INTO [t754] VALUES (3797,'لم يتركوا لي صاحبا غير الأسى ... وذميل ذعلبة كفحل نعام','295','3764','0');
INSERT INTO [t754] VALUES (3798,'وتعذر الأحرار صير ظهرها ... إلا إليك على فرج حرام','295','3764','0');
INSERT INTO [t754] VALUES (3799,'أنت الغريبة في زمان أهله ... ولدت مكارمهم لغير تمام','295','3764','0');
INSERT INTO [t754] VALUES (3800,'أكثرت من بذل النوال ولم تزل ... علما على الإفضال والإنعام','295','3764','0');
INSERT INTO [t754] VALUES (3801,'صغرت كل كبيرة وكبرت عن ... لكأنه وعددت سن غلام','295','3764','0');
INSERT INTO [t754] VALUES (3802,'ورفلت في حلل الثناء وإنما ... عدم الثناء نهاية الإعدام','295','3764','0');
INSERT INTO [t754] VALUES (3803,'عيب عليك ترى بسيف في الوغا ... ما يصنع الصمصام بالصمصام','295','3764','0');
INSERT INTO [t754] VALUES (3804,'إن كان مثلك كان أو هو كائن ... فبرئت حينئذ من الإسلام','295','3764','0');
INSERT INTO [t754] VALUES (3805,'ملك زهت بمكانه أيامه ... حتى افتخرن به على الأيام','295','3764','0');
INSERT INTO [t754] VALUES (3806,'وتخاله سلب الورى من حلمه ... أحلامهم فهم بلا أحلام','295','3764','0');
INSERT INTO [t754] VALUES (3807,'وإذا امتحنت تكشفت عزماته ... عن أوحدي النقص والإبرام','295','3764','0');
INSERT INTO [t754] VALUES (3808,'وإذا سألت بنانه عن نيله ... لم يرض بالدنيا قضاء ذمام','295','3764','0');
INSERT INTO [t754] VALUES (3809,'مهلا ألا لله ما صنع القنا ... في عمرو حاب وضبة الأغتام','296','3764','0');
INSERT INTO [t754] VALUES (3810,'لما تحكمت الأسنة فيهم ... جارت وهن يجرن في الأحكام','296','3764','0');
INSERT INTO [t754] VALUES (3811,'فتركتهم خلل البيوت كأنما ... غضبت رؤسهم على الأجسام','296','3764','0');
INSERT INTO [t754] VALUES (3812,'أحجار ناسس فوق أرض من دم ... ونجوم بيض في سماء قتام','296','3764','0');
INSERT INTO [t754] VALUES (3813,'وذراع كل أبي فلان كنية ... حالت فصاحبها أبو الأيتام','296','3764','0');
INSERT INTO [t754] VALUES (3814,'عهدي بمعركة الأمير وخيله ... في النقع محجمة عن الإحجام','296','3764','0');
INSERT INTO [t754] VALUES (3815,'صلى الإله عليك غير مودع ... وسقى ثري أبويك صوب غمام','296','3764','0');
INSERT INTO [t754] VALUES (3816,'وكساك ثوب مهابة من عنده ... وأراك وجه شقيقك القمقام','296','3764','0');
INSERT INTO [t754] VALUES (3817,'فلقد رمى بلد العدو بنفسه ... في روق أرعن كالغطم لهام','296','3764','0');
INSERT INTO [t754] VALUES (3818,'قوم تفرست المنايا فيكم ... فرأت لكم في الحرب صبر كرام','296','3764','0');
INSERT INTO [t754] VALUES (3819,'تالله ما علم امرؤ لولاكم ... كيف السخاء وكيف ضرب الهام','296','3764','0');
INSERT INTO [t754] VALUES (3820,'الرأي قبل شجاعة الشجعان ... هو أول وهي المحل الثاني','296','3764','0');
INSERT INTO [t754] VALUES (3821,'فغذا هما اجتمعا لنفس مرة ... بلغت من العلياء كل مكان','296','3764','0');
INSERT INTO [t754] VALUES (3822,'ولربما طعن الفتى أقرانه ... بالرأي قبل تطاعن الأقران','296','3764','0');
INSERT INTO [t754] VALUES (3823,'لولا العقول لكان أدنى ضيغم ... أدنى إلى شرف من الإنسان','296','3764','0');
INSERT INTO [t754] VALUES (3824,'ولما تفاضلت النفوس ودبرت ... أيدي الكماة عوالي المران','296','3764','0');
INSERT INTO [t754] VALUES (3825,'لولا سمي سيوفه ومضاؤه ... لما سللن لكن كالأجفان','296','3764','0');
INSERT INTO [t754] VALUES (3826,'خاض الحمام بهن حتى ما درى ... أمن إحتقار ذاك أم نسيان','297','3764','0');
INSERT INTO [t754] VALUES (3827,'وسعى فقصر عن مداه في العلى ... أهل الزمان وأهل كل زمان','297','3764','0');
INSERT INTO [t754] VALUES (3828,'تخذوا المجالس في البيوت وعنده ... أن السروج مجالس الفتيان','297','3764','0');
INSERT INTO [t754] VALUES (3829,'وتوهموا اللعب الوغا والطعن في ال ... هيجاء غير الطعن في الميدان','297','3764','0');
INSERT INTO [t754] VALUES (3830,'قاد الجياد إلى الطعان ولم يقد ... إلا إلى العادات والأوطان','297','3764','0');
INSERT INTO [t754] VALUES (3831,'كل ابن سابقة يغير بحسنه ... في قلب صاحبه على الأحزان','297','3764','0');
INSERT INTO [t754] VALUES (3832,'إن خليت ربطت بآداب الوغا ... فدعاؤها يغني عن الأرسان','297','3764','0');
INSERT INTO [t754] VALUES (3833,'في جحفل ستر العيون غباره ... فكأنما يبصرن بالآذان','297','3764','0');
INSERT INTO [t754] VALUES (3834,'يرمي بها البلد البعيد مظفر ... كل البعيد له قريب دان','297','3764','0');
INSERT INTO [t754] VALUES (3835,'فكأن أرجلها بتربة منبج ... يطرحن أيديها بحصن الران','297','3764','0');
INSERT INTO [t754] VALUES (3836,'حتى عبرن بأرسناس سوابحا ... ينشرن فيه عمائم الفرسان','297','3764','0');
INSERT INTO [t754] VALUES (3837,'يقمصن في مثل المدى من بارد ... يذر الفحول وهن كالخصيان','297','3764','0');
INSERT INTO [t754] VALUES (3838,'والماء بين عجاجتين مخلص ... تتفرقان به وتلتقيان','297','3764','0');
INSERT INTO [t754] VALUES (3839,'ركض الأمير وكاللجين حبابه ... وثنى الأعنة وهو كالعقيان','297','3764','0');
INSERT INTO [t754] VALUES (3840,'فتل الحبال من الغدائر فوقه ... وبنى السفين له من الصلبان','297','3764','0');
INSERT INTO [t754] VALUES (3841,'وحشاه عادية بغير قوائم ... عقم البطون حوالك الألوان','297','3764','0');
INSERT INTO [t754] VALUES (3842,'تأتي بما سبت الخيول كأنها ... تحت الحسان مرابض الغزلان','297','3764','0');
INSERT INTO [t754] VALUES (3843,'بحر تعود أن يذم لأهله ... من دهره وطوارق الحدثان','297','3764','0');
INSERT INTO [t754] VALUES (3844,'فتركته وإذا أذم من الورى ... راعاك واستثنى بني حمدان','297','3764','0');
INSERT INTO [t754] VALUES (3845,'ألمخفرين بكل أبيض صارم ... ذمم الدروع على ذوي التيجان','298','3764','0');
INSERT INTO [t754] VALUES (3846,'متصعلكين على كثافة ملكهم ... متواضعين على عظيم الشان','298','3764','0');
INSERT INTO [t754] VALUES (3847,'يتقيلون ظلال كل مطهم ... أجل الظليم وربقة السرحان','298','3764','0');
INSERT INTO [t754] VALUES (3848,'خضعت لمنصلك المناصل عنوة ... وأذل دينك سائر الأديان','298','3764','0');
INSERT INTO [t754] VALUES (3849,'وعلى الدروب وفي الرجوع غضاضة ... والسير ممتنع من الإمكان','298','3764','0');
INSERT INTO [t754] VALUES (3850,'والطرق ضيقة المسالك بالقنا ... والكفر مجتمع على الإيمان','298','3764','0');
INSERT INTO [t754] VALUES (3851,'نظروا إلى زبر الحديد كأنما ... يصعدن بين مناكب العقبان','298','3764','0');
INSERT INTO [t754] VALUES (3852,'وفوارس يحيى الحمام نفوسها ... فكأنها ليست من الحيوان','298','3764','0');
INSERT INTO [t754] VALUES (3853,'ما زلت تضربهم دراكا في الذرى ... ضربا كأن السيف فيه اثنان','298','3764','0');
INSERT INTO [t754] VALUES (3854,'خص الجماجم والوجوه كأنما ... جاءت إليك جسومهم بأمان','298','3764','0');
INSERT INTO [t754] VALUES (3855,'فرموا بما يرمون عنه وأدبروا ... يطؤون كل حنية مرنان','298','3764','0');
INSERT INTO [t754] VALUES (3856,'يغشاهم مطر السحاب مفصلا ... بمهند ومثقف وسنان','298','3764','0');
INSERT INTO [t754] VALUES (3857,'حرموا الذي أملوا وأدرك منهم ... آماله من عاد بالحرمان','298','3764','0');
INSERT INTO [t754] VALUES (3858,'وإذا الرماح شغلن مجهة ثائر ... شغلته مهجته عن الإخوان','298','3764','0');
INSERT INTO [t754] VALUES (3859,'هيهات عاق عن العواد قواضب ... كثر القتيل بها وقل العاني','299','3764','0');
INSERT INTO [t754] VALUES (3860,'ومهذب أمر المنايا فيهم ... فأطعنه في طاعة الرحمان','299','3764','0');
INSERT INTO [t754] VALUES (3861,'قد سودت شجر الجبال شعورهم ... فكأن فيه مسفة الغربان','299','3764','0');
INSERT INTO [t754] VALUES (3862,'وجرى على الورق النجيع القاني ... فكأنه النارنج في الأغصان','299','3764','0');
INSERT INTO [t754] VALUES (3863,'إن السيوف مع الذين قلوبهم ... كقلوبهن إذا التقى الجمعان','299','3764','0');
INSERT INTO [t754] VALUES (3864,'تلقى الحسام على جراءة حده ... مثل الجبان بكف كل جبان','299','3764','0');
INSERT INTO [t754] VALUES (3865,'رعفت بك العرب العماد وصيرت ... قمم الملوك مواقد النيران','299','3764','0');
INSERT INTO [t754] VALUES (3866,'أنساب فخرهم إليك وإنما ... أنساب أصلهم إلى عدنان','299','3764','0');
INSERT INTO [t754] VALUES (3867,'يا من يقتل من أراد بسيفه ... أصبحت من قتلاك بالإحسان','299','3764','0');
INSERT INTO [t754] VALUES (3868,'فإذا رأيتك حار دونك ناظري ... وإذا مدحتك حار فيك لساني','299','3764','0');
INSERT INTO [t754] VALUES (3869,'عقبى اليمين على عقبى الوغى ندم ... ماذا يزيدك في إقدامك القسم','299','3764','0');
INSERT INTO [t754] VALUES (3870,'وفي اليمين على ما أنت واعده ... ما دل أنك في الميعاد متهم','299','3764','0');
INSERT INTO [t754] VALUES (3871,'آلي الفتى ابن شمشقيق فأحنثه ... فتى من الضرب ينسى عنده الكلم','299','3764','0');
INSERT INTO [t754] VALUES (3872,'وفاعل ما اشتهى يغنيه عن حلف ... على الفعال حضور الفعل والكرم','299','3764','0');
INSERT INTO [t754] VALUES (3873,'كل السيوف إذا طال الضراب بها ... يمسها غير سيف الدولة السأم','299','3764','0');
INSERT INTO [t754] VALUES (3874,'لو كلت الخيل حتى لاتحمله ... تحملته إلى أعدائه الهمم','299','3764','0');
INSERT INTO [t754] VALUES (3875,'أين البطاريق والحلف الذي حلفوا ... بمفرق الملك والزعم الذي زعموا','299','3764','0');
INSERT INTO [t754] VALUES (3876,'ولي صوارمه إكذاب قولهم ... فهن ألسنة أفواهها القمم','299','3764','0');
INSERT INTO [t754] VALUES (3877,'نواطق مخبرات في جماجمهم ... عنه بما جعلوا منه وما علموا','299','3764','0');
INSERT INTO [t754] VALUES (3878,'الراجع الخيل محفاة مقودة ... من كل مثل وبار أهلها إرام','300','3764','0');
INSERT INTO [t754] VALUES (3879,'كتل بطريق المغرور ساكنها ... بأن دارك قنسرون والأجم','300','3764','0');
INSERT INTO [t754] VALUES (3880,'وظنهم أنك المصباح في حلب ... إذا قصدت سواها عادها الظلم','300','3764','0');
INSERT INTO [t754] VALUES (3881,'والشمس يعنون إلا أنهم جهلوا ... والموت يدعون إلا أنهم وهموا','300','3764','0');
INSERT INTO [t754] VALUES (3882,'فلم تتم سروج فتح ناظرها ... إلا وجيشك في جفنيه مزدحم','300','3764','0');
INSERT INTO [t754] VALUES (3883,'والنقع يأخذ حرانا وبقعتها ... والشمس تسفر أحيانا وتلتثم','300','3764','0');
INSERT INTO [t754] VALUES (3884,'سحب تمر بحصن الران ممسكة ... وما بها البخل لولا أنها نقم','300','3764','0');
INSERT INTO [t754] VALUES (3885,'جيش كأنك في ارض تطاوله ... فالأرض لا أمم والجيش لا أمم','300','3764','0');
INSERT INTO [t754] VALUES (3886,'إذا مضى علم منها بدا علم ... وإن مضى علم منه بدا علم','300','3764','0');
INSERT INTO [t754] VALUES (3887,'وشرب أحمت الشعري شكائمها ... ووسمتها على آنافها الحكم','300','3764','0');
INSERT INTO [t754] VALUES (3888,'حتى وردن بمسنين بحيرتها ... تنش بالماء في أشداقها اللجم','300','3764','0');
INSERT INTO [t754] VALUES (3889,'وأصبحت بقورى هنزيط جائلة ... ترعى الظبا في خصيب نبته اللمم','300','3764','0');
INSERT INTO [t754] VALUES (3890,'فما تركن بها خلدا له بصر ... تحت التراب ولا بازا له قدم','300','3764','0');
INSERT INTO [t754] VALUES (3891,'فلا هزبرا له من درعه لبد ... ولا مهاة لها من شبهها حشم','300','3764','0');
INSERT INTO [t754] VALUES (3892,'ترمي على شفرات الباترات بهم ... مكامن الأرض والغيطان والأكم','301','3764','0');
INSERT INTO [t754] VALUES (3893,'وجاوزوا أرسناسا معصمين به ... وكيف يعصمهم ما ليس ينعصم','301','3764','0');
INSERT INTO [t754] VALUES (3894,'وما يصدك عن بحر لهم سعة ... وما يردك عن طود لهم شمم','301','3764','0');
INSERT INTO [t754] VALUES (3895,'ضربته بصدور الخيل حاملة ... قوما إذا تلفوا قدما فقد سلموا','301','3764','0');
INSERT INTO [t754] VALUES (3896,'تجفل الموج عن لبات خيلهم ... كما تجفل تحت الغارة النعم','301','3764','0');
INSERT INTO [t754] VALUES (3897,'عبرت تقدمهم فيه وفي بلد ... سكانه رمم مسكونها حمم','301','3764','0');
INSERT INTO [t754] VALUES (3898,'وفي أكفهم النار التي عبدت ... قبل المجوس إلى ذا اليوم تضطرم','301','3764','0');
INSERT INTO [t754] VALUES (3899,'هندية إن تصغر معشرا صغروا ... بحدها أو تعظم معشرا عظموا','301','3764','0');
INSERT INTO [t754] VALUES (3900,'قاسمتها تل بطريق فكان لها ... أبطالها ولك الأطفال والحرم','301','3764','0');
INSERT INTO [t754] VALUES (3901,'تلقى بهم زبد التيار مقربة ... على جحافلها من نضحه رثم','301','3764','0');
INSERT INTO [t754] VALUES (3902,'دهم فوارسها ركاب أبطنها ... مكدودة وبقوم لا بها الألم','301','3764','0');
INSERT INTO [t754] VALUES (3903,'من الجياد التي كدت العدو بها ... وما لها خلق منها ولا شيم','301','3764','0');
INSERT INTO [t754] VALUES (3904,'نتاج رأيك في وقت على عجل ... كلفظ حرف وعاه سامع فهم','301','3764','0');
INSERT INTO [t754] VALUES (3905,'وقد تمنوا غداة الدرب في لجب ... أن يبصروك فلما أبصروك عموا','301','3764','0');
INSERT INTO [t754] VALUES (3906,'صدمتهم بخميس أنت غرته ... وسمهريته في وجهه غمم','301','3764','0');
INSERT INTO [t754] VALUES (3907,'فكان أثبت ما فيهم جسومهم ... يسقطن حولك والأرواح تنهزم','301','3764','0');
INSERT INTO [t754] VALUES (3908,'والأعوجية ملء الطرق خلفهم ... والمشرفية ملء اليوم فوقهم','301','3764','0');
INSERT INTO [t754] VALUES (3909,'إذا توافقت الضربات صاعدة ... توافقت قلل في الجو تصطدم','301','3764','0');
INSERT INTO [t754] VALUES (3910,'وأسلم ابن شمشقيق أليته ... ألا أنثنى فهو ينأى وهي تبتسم','302','3764','0');
INSERT INTO [t754] VALUES (3911,'لا يأمل النفس الأقصى لمهجته ... فيسرق النفس الأدنى ويغتنم','302','3764','0');
INSERT INTO [t754] VALUES (3912,'ترد عنه قنا الفرسان سابغة ... صوب الأسنة في أثنائها ديم','302','3764','0');
INSERT INTO [t754] VALUES (3913,'تخط فيها العوالي ليس تنفذها ... كأن كل سنان فوقها قلم','302','3764','0');
INSERT INTO [t754] VALUES (3914,'فلا سقى الغيث ما واراه من شجر ... لو زل عنه لوارت شخصه الرخم','302','3764','0');
INSERT INTO [t754] VALUES (3915,'ألهى الممالك عن فخر قفلت به ... شرب المدامة والأوتار والنغم','302','3764','0');
INSERT INTO [t754] VALUES (3916,'مقلدا فوق شكر الله ذا شطب ... لا تستدام بأمضى منهما النعم','302','3764','0');
INSERT INTO [t754] VALUES (3917,'ألقت إليك دماء الروم طاعتها ... فلو دعوت بلا ضرب أجاب دم','302','3764','0');
INSERT INTO [t754] VALUES (3918,'يسابق القتل فيهم كل حادثة ... فما يصيبهم موت ولا هرم','302','3764','0');
INSERT INTO [t754] VALUES (3919,'نفت رقاد علي عن محاجره ... نفس يفرج نفسا غيرها الحلم','302','3764','0');
INSERT INTO [t754] VALUES (3920,'القائم الملك الهادي الذي شهدت ... قيامه وهداه العرب والعجم','302','3764','0');
INSERT INTO [t754] VALUES (3921,'إبن المعفر في نجد فوارسها ... بسيفه وله كوفان والحرم','302','3764','0');
INSERT INTO [t754] VALUES (3922,'لا تطلبن كريما بعد رؤيته ... إن الكرام بأسخاهم يدا ختموا','302','3764','0');
INSERT INTO [t754] VALUES (3923,'ولا تبال بشعر بعد شاعره ... قد أفسد القول حتى أحمد الصمم','302','3764','0');
INSERT INTO [t754] VALUES (3924,'فارقتكم فإذا ما كان عندكم ... قبل الفراق أذى بعد الفراق يد','302','3764','0');
INSERT INTO [t754] VALUES (3925,'إذا تذكرت ما بيني وبينكم ... أعان قلبي على الشوق الذي أجد','302','3764','0');
INSERT INTO [t754] VALUES (3926,'يا أخت خير أخ يا بنت خير أب ... كناية بهما عن أشرف النسب','302','3764','0');
INSERT INTO [t754] VALUES (3927,'أجل قدرك أن تسمى مؤبنة ... ومن يصفك فقد سماك للعرب','302','3764','0');
INSERT INTO [t754] VALUES (3928,'لا يملك الطرب المحزون منطقه ... ودمعه وهما في قبضة الطرب','303','3764','0');
INSERT INTO [t754] VALUES (3929,'غدرت يا موت كم أفنيت من عدد ... بمن أصبت وكم أسكت من لجب','303','3764','0');
INSERT INTO [t754] VALUES (3930,'وكم صحبت أخاها في منازلة ... وكم سألت فلم يبخل ولم تخب','303','3764','0');
INSERT INTO [t754] VALUES (3931,'طوى الجزيرة حتى جاءني خبر ... فزعت فيه بآمالي إلى الكذب','303','3764','0');
INSERT INTO [t754] VALUES (3932,'حتى إذا لم يدع لي صدقه أملا ... شرقت بالدمع حتى كاد يشرق بي','303','3764','0');
INSERT INTO [t754] VALUES (3933,'تعثرت به في الإفواه ألسنها ... والرد في الطرق والأقلام في الكتب','303','3764','0');
INSERT INTO [t754] VALUES (3934,'كأن فعلة لم تملأ مواكبها ... ديار بكر ولم تخلع ولم تهب','303','3764','0');
INSERT INTO [t754] VALUES (3935,'ولم ترد حيوة بعد تولية ... ولم تغث داعيا بالويل والحرب','303','3764','0');
INSERT INTO [t754] VALUES (3936,'أرى العراق طويل الليل مذ نعيت ... فكيف ليل فتى الفتيان في حلب','303','3764','0');
INSERT INTO [t754] VALUES (3937,'يظن أن فؤادي غير ملتهب ... وأن دمع جفوني غير منسكب','303','3764','0');
INSERT INTO [t754] VALUES (3938,'بلى وحرمة من كانت مراعية ... لحرمة المجد والقصاد والأدب','303','3764','0');
INSERT INTO [t754] VALUES (3939,'ومن مضت غير موروث خلائقها ... وإن مضت يدها موروثة النشب','303','3764','0');
INSERT INTO [t754] VALUES (3940,'وهمها في العلى والمجد ناشئة ... وهم أترابها في اللهو واللعب','303','3764','0');
INSERT INTO [t754] VALUES (3941,'يعلمن حين تحيى حسن مبسمها ... وليس يعلم إلا الله بالشنب','304','3764','0');
INSERT INTO [t754] VALUES (3942,'مسرة في قلوب الطيب مفرقها ... وحسرة في قلوب البيض واليلب','304','3764','0');
INSERT INTO [t754] VALUES (3943,'إذا رأى ورآها رأس لابسه ... رأى المقانع أعلى منه في الرتب','304','3764','0');
INSERT INTO [t754] VALUES (3944,'وإن تكن خلقت أنثى لقد خلقت ... كريمة غير أنثى العقل والحسب','304','3764','0');
INSERT INTO [t754] VALUES (3945,'وإن تكن تغلب الغلباء عنصرها ... فإن في الخمر معنى ليس في العنب','304','3764','0');
INSERT INTO [t754] VALUES (3946,'فليت طالعة الشمسين غائبة ... وليت غائبة الشمسين لم تغب','304','3764','0');
INSERT INTO [t754] VALUES (3947,'وليت عين التي آب النهار بها ... فداء عين التي زالت ولم تؤب','304','3764','0');
INSERT INTO [t754] VALUES (3948,'فما تقلد بالياقوت مشبهها ... ولا تقلد بالهندية القضب','304','3764','0');
INSERT INTO [t754] VALUES (3949,'ولا ذكرت جميلا من صنائعها ... إلا بكيت ولا ود بلا سبب','304','3764','0');
INSERT INTO [t754] VALUES (3950,'قد كان كل حجاب دون رؤيتها ... فما قنعت لها يا أرض بالحجب','304','3764','0');
INSERT INTO [t754] VALUES (3951,'ولا رأيت عيون الإنس تدركها ... فهل حسدت عليها أعين الشهب','304','3764','0');
INSERT INTO [t754] VALUES (3952,'وهل سمعت سلاما لي ألم بها ... فقد أطلت وما سلمت من كثب','304','3764','0');
INSERT INTO [t754] VALUES (3953,'وكيف يبلغ موتانا التي دفنت ... وقد يقصر عن أحيائنا الغيب','304','3764','0');
INSERT INTO [t754] VALUES (3954,'يا أحسن الصبر زر أولى القلوب بها ... وقل لصاحبه يا أنفع السحب','304','3764','0');
INSERT INTO [t754] VALUES (3955,'وأكرم الناس لا مستثنيا أحدا ... من الكرام سوى آبائك النجب','304','3764','0');
INSERT INTO [t754] VALUES (3956,'قد كان قاسمك الشخصين دهرهما ... فعاش درهما المفدي بالذهب','305','3764','0');
INSERT INTO [t754] VALUES (3957,'ما كان أقصر وقتا كان بينهما ... كأنه الوقت بين الورد والقرب','305','3764','0');
INSERT INTO [t754] VALUES (3958,'جزاك ربك بالأحزان مغفرة ... فحزن كل أخي حزن أخو الغضب','305','3764','0');
INSERT INTO [t754] VALUES (3959,'وأنتم نفر تسخو نفوسكم ... بما يهبن ولا يسخون بالسب','305','3764','0');
INSERT INTO [t754] VALUES (3960,'حللتم من ملوك الناس كلهم ... محل سمر القنا من سائر القصب','305','3764','0');
INSERT INTO [t754] VALUES (3961,'فلا تنلك اليالي إن أيديها ... إذا ضربن كسرن النبع بالغرب','305','3764','0');
INSERT INTO [t754] VALUES (3962,'ولا يعن عدوا أنت قاهره ... فإنهن يصدن الصقر بالخرب','305','3764','0');
INSERT INTO [t754] VALUES (3963,'وإن سررن بمحبوب فجعن به ... وقد أتينك في الحالين بالعجب','305','3764','0');
INSERT INTO [t754] VALUES (3964,'وربما أحتسب الإنسان غايتها ... وفاجأته بأمر غير محتسب','305','3764','0');
INSERT INTO [t754] VALUES (3965,'وما قضى أحد منها لبانته ... ولا انتهى أرب إلا إلى أرب','305','3764','0');
INSERT INTO [t754] VALUES (3966,'تخالف الناس حتى لا اتفاق لهم ... إلا على شجب والخلف في الشجب','305','3764','0');
INSERT INTO [t754] VALUES (3967,'فقيل تخلص نفس المرء سالمة ... وقيل تشرك جسم المرء في العطب','305','3764','0');
INSERT INTO [t754] VALUES (3968,'ومن تفكر في الدنيا ومهجته ... أقامه الفكر بين العجز والتعب','305','3764','0');
INSERT INTO [t754] VALUES (3969,'ما لنا كلنا جو يا رسول ... أنا أهوى وقلبك المتبول','305','3764','0');
INSERT INTO [t754] VALUES (3970,'كلما عاد من بعثت إليه ... غار مني وخان فيما يقول','305','3764','0');
INSERT INTO [t754] VALUES (3971,'أفسدت بيننا الأمانات عينا ... ها وخانت قلوبهن العقول','306','3764','0');
INSERT INTO [t754] VALUES (3972,'تشتكي ما اشتكيت من ألم الشو ... ق إليها والشوق حيث النحول','306','3764','0');
INSERT INTO [t754] VALUES (3973,'وإذا خامر الهوى قلب صب ... فعليه لكل عين دليل','306','3764','0');
INSERT INTO [t754] VALUES (3974,'زودينا من حسن وجهك ما دا ... م فحسن الوجوه حال تحول','306','3764','0');
INSERT INTO [t754] VALUES (3975,'وصلينا نصلك في هذه الدن ... يا فإن المقام فيها قليل','306','3764','0');
INSERT INTO [t754] VALUES (3976,'من رآها بعينها شاقه القط ... ان فيها كما تشوق الحمول','306','3764','0');
INSERT INTO [t754] VALUES (3977,'إن تريني أدمت بعد بياض ... فحميد من القناة الذبول','306','3764','0');
INSERT INTO [t754] VALUES (3978,'صحبتني على الفلاة فتاة ... عادة اللون عندها التبديل','306','3764','0');
INSERT INTO [t754] VALUES (3979,'سترتك الحجال غنها ولكن ... بك منها من اللمى تقبيل','306','3764','0');
INSERT INTO [t754] VALUES (3980,'مثلها أنت لوحتني وأسقم ... ت وزادت أبهاكما العطبول','306','3764','0');
INSERT INTO [t754] VALUES (3981,'نحن أدرى وقد سألنا بنجد ... أطويل طريقنا أم يطول','306','3764','0');
INSERT INTO [t754] VALUES (3982,'وكثير من السؤال اشتياق ... وكثير من رده تعليل','306','3764','0');
INSERT INTO [t754] VALUES (3983,'لا أقمنا على مكان وإن طا ... ب ولا يمكن المكان الرحيل','306','3764','0');
INSERT INTO [t754] VALUES (3984,'كلما رحبت بنا الروض قلنا ... حلب قصدنا وأنت السبيل','306','3764','0');
INSERT INTO [t754] VALUES (3985,'فيك مرعى جيادنا والمطايا ... وإليها وجيفنا والذميل','306','3764','0');
INSERT INTO [t754] VALUES (3986,'والمسمون بالأمير كثير ... والأمير الذي بها المأمول','306','3764','0');
INSERT INTO [t754] VALUES (3987,'الذي زلت عنه شرقا وغربا ... ونداه مقابلي ما يزول','306','3764','0');
INSERT INTO [t754] VALUES (3988,'فإذا العذل في الندى زار سمعا ... ففداه العذول والمعذول','307','3764','0');
INSERT INTO [t754] VALUES (3989,'وموال تحييهم من يديه ... نعم غيرهم بها مقتول','307','3764','0');
INSERT INTO [t754] VALUES (3990,'فرس سابق ورمح طويل ... ودلاص زغف وسيف صغيل','307','3764','0');
INSERT INTO [t754] VALUES (3991,'كلما صبحت ديار عدو ... قال تلك الغيوث هذي السيول','307','3764','0');
INSERT INTO [t754] VALUES (3992,'دهمته تطاير الزرد المح ... كم عنه كما يطير النسيل','307','3764','0');
INSERT INTO [t754] VALUES (3993,'تنقص الخيل خيله قنص الوح ... ش ويستأسر الخميس الرعيل','307','3764','0');
INSERT INTO [t754] VALUES (3994,'وإذا الحرب أعرضت زعم الهو ... ل لعينيه أنه تهويل','307','3764','0');
INSERT INTO [t754] VALUES (3995,'وإذا صح فالزمان صحيح ... وإذا اعتل فالزمان عليل','307','3764','0');
INSERT INTO [t754] VALUES (3996,'وإذا غاب وجهه عن مكان ... فبه من نثاه وجه جميل','307','3764','0');
INSERT INTO [t754] VALUES (3997,'ليس إلاك يا علي همام ... سيفه دون عرضه مسلول','307','3764','0');
INSERT INTO [t754] VALUES (3998,'كيف لا يأمن العراق ومصر ... وسراياك دونها والخيول','307','3764','0');
INSERT INTO [t754] VALUES (3999,'لو تحرفت عن طريق الأعادي ... ربط السدر خيلهم والنخيل','307','3764','0');
INSERT INTO [t754] VALUES (4000,'ودرى من أعزه الدفع عنه ... فيهما أنه الحقير الذليل','307','3764','0');
INSERT INTO [t754] VALUES (4001,'أنت طول الحياة للروم غاز ... فمتى الوعد أن يكون القفول','307','3764','0');
INSERT INTO [t754] VALUES (4002,'وسوى الروم خلف ظهرك روم ... فعلى أي جانبيك تميل','307','3764','0');
INSERT INTO [t754] VALUES (4003,'قعد الناس كلهم عن مساعي ... ك وقامت بها القنا والنصول','307','3764','0');
INSERT INTO [t754] VALUES (4004,'ما الذي عنده تدار المنايا ... كالذي عنده تدار الشمول','308','3764','0');
INSERT INTO [t754] VALUES (4005,'لست أرضى بأن تكون جوادا ... وزماني بأن أراك بخيل','308','3764','0');
INSERT INTO [t754] VALUES (4006,'نغص البعد عند قرب العطايا ... مرتعي مخصب وجسمي هزيل','308','3764','0');
INSERT INTO [t754] VALUES (4007,'إن تبوأت غير دنياي دارا ... وأتاني نيل فأنت المنيل','308','3764','0');
INSERT INTO [t754] VALUES (4008,'من عبيدي إن عشت لي ألف كافو ... ر ولي من نداك ريف ونيل','308','3764','0');
INSERT INTO [t754] VALUES (4009,'ما أبالي إذا اتقتك الرزايا ... من دهته خبولها والحبول','308','3764','0');
INSERT INTO [t754] VALUES (4010,'فهمت الكتاب أبر الكتب ... فسمعا لأمر أمير العرب','308','3764','0');
INSERT INTO [t754] VALUES (4011,'وطوعا له وابتهاجا به ... وإن قصر الفعل عما وجب','308','3764','0');
INSERT INTO [t754] VALUES (4012,'وما عاقني غير خوف الوشاة ... وإن الوشايات طرق الكدب','308','3764','0');
INSERT INTO [t754] VALUES (4013,'وتكثير قومس وتقليلهم ... وتقريبهم بيننا والخبب','308','3764','0');
INSERT INTO [t754] VALUES (4014,'وقد كان ينصرهم سمعه ... وينصرني قلبه والحسب','308','3764','0');
INSERT INTO [t754] VALUES (4015,'وما قلت للبدر أنت اللجين ... ولا قلت للشمس أنت الذهب','308','3764','0');
INSERT INTO [t754] VALUES (4016,'فيقلق منه البعيد الأناة ... ويغضب منه البطيء الغضب','308','3764','0');
INSERT INTO [t754] VALUES (4017,'وما لاقني بلد بعدكم ... ولا اعتضت من رب نعماي رب','308','3764','0');
INSERT INTO [t754] VALUES (4018,'ومن ركب الثور بعد الجوا ... د أنكر أظلافه والغبب','308','3764','0');
INSERT INTO [t754] VALUES (4019,'وما قست كل ملوك البلاد ... فدع ذكر بعض بمن في حلب','308','3764','0');
INSERT INTO [t754] VALUES (4020,'ولو كنت سميتهم باسمه ... لكان الحديد وكانوا الخشب','308','3764','0');
INSERT INTO [t754] VALUES (4021,'أفي الرأي يشبه أم في السخا ... ء أم في الشجاعة أم في الأدب','308','3764','0');
INSERT INTO [t754] VALUES (4022,'مبارك الإسم أغر اللقب ... كريم الجرشي شريف النسب','308','3764','0');
INSERT INTO [t754] VALUES (4023,'أخو الحرب يخدم مما سبى ... قناه ويخلع ما سلب','308','3764','0');
INSERT INTO [t754] VALUES (4024,'إذا حاز مالا فقد حازه ... فتى لا يسر بما لا يهب','308','3764','0');
INSERT INTO [t754] VALUES (4025,'وإني لأتبع تذكاره ... صلوة الإله وسقى السحب','308','3764','0');
INSERT INTO [t754] VALUES (4026,'وأثنى عليه بآلآئه ... وأقرب منه نأي أو قرب','308','3764','0');
INSERT INTO [t754] VALUES (4027,'وإن فارقتني أمطاره ... فأكثر غدرانها ما نضب','309','3764','0');
INSERT INTO [t754] VALUES (4028,'أي سيف ربك لا خلقه ... ويا ذا المكارم لا ذا الشطب','309','3764','0');
INSERT INTO [t754] VALUES (4029,'وأبعد ذي همة همة ... وأعرف ذي رتبة بالرتب','309','3764','0');
INSERT INTO [t754] VALUES (4030,'وأطعن من مس خطية ... وأضرب من بحسام ضرب','309','3764','0');
INSERT INTO [t754] VALUES (4031,'بذا اللفظ ناداك أهل الثغور ... فلبيت والهام تحت القضب','309','3764','0');
INSERT INTO [t754] VALUES (4032,'وقد يئسوا من لذيذ الحيوة ... فعين تغور وقلب يجب','309','3764','0');
INSERT INTO [t754] VALUES (4033,'وغر الدمستق قول العدا ... ة إن عليا ثقيل وصب','309','3764','0');
INSERT INTO [t754] VALUES (4034,'وقد علمت خيله أنه ... إذا هم وهو عليل ركب','309','3764','0');
INSERT INTO [t754] VALUES (4035,'أتاهم بأوسع من أرضهم ... طوال السبيب قصار العسب','309','3764','0');
INSERT INTO [t754] VALUES (4036,'تغيب الشواهق في جيشه ... وتبدو صغارا إذا لم تغب','309','3764','0');
INSERT INTO [t754] VALUES (4037,'ولا تعبر الريح في جوجه ... إذا لم تخط القنا أو تثب','309','3764','0');
INSERT INTO [t754] VALUES (4038,'فغرق مدنهم بالجيوش ... وأخفت أصواتهم باللجب','309','3764','0');
INSERT INTO [t754] VALUES (4039,'فأخبث به طالبا قتلهم ... وأخبث به تاركا ما طلب','309','3764','0');
INSERT INTO [t754] VALUES (4040,'نأيت فقاتلهم باللقاء ... وجئت فقاتلهم بالهرب','309','3764','0');
INSERT INTO [t754] VALUES (4041,'وكانوا له الفخر لما أتى ... وكنت له العذر لما ذهب','309','3764','0');
INSERT INTO [t754] VALUES (4042,'سبقت إليهم مناياهم ... ومنفعة الغوث قبل العطب','309','3764','0');
INSERT INTO [t754] VALUES (4043,'فخروا لخالقهم سجدا ... ولو لم تغث سجدوا للصلب','309','3764','0');
INSERT INTO [t754] VALUES (4044,'وكم ذدت عنهم ردى بالردى ... وكشفت من كرب بالكرب','309','3764','0');
INSERT INTO [t754] VALUES (4045,'وقد زعموا أنه إن يعد ... يعد معه الملك المعتصب','309','3764','0');
INSERT INTO [t754] VALUES (4046,'ويستنصران الذي يعبدان ... وعندهما أنه قد صلب','309','3764','0');
INSERT INTO [t754] VALUES (4047,'ويدفع ما ناله عنهما ... فيا للرجال لهذا العجب','309','3764','0');
INSERT INTO [t754] VALUES (4048,'أرى المسلمين مع المشركي ... ن إما لعجز وإما رهب','310','3764','0');
INSERT INTO [t754] VALUES (4049,'وأنت مع الله في جانب ... قليل الرقاد كثير التعب','310','3764','0');
INSERT INTO [t754] VALUES (4050,'كأنك وحدك وحدته ... ودان البرية بابن وأب','310','3764','0');
INSERT INTO [t754] VALUES (4051,'فليت سيوفك في حاسد ... إذا ما ظهرت عليهم كئب','310','3764','0');
INSERT INTO [t754] VALUES (4052,'وليت شكاتك في جسمه ... وليتك تجزى ببغض وحب','310','3764','0');
INSERT INTO [t754] VALUES (4053,'فلو كنت تجزي به نلت من ... ك أضعف حظ بأقوى سبب','310','3764','0');
INSERT INTO [t754] VALUES (4054,'كفى بك داء أن ترى الموت شافيا ... وحسب المنايا أن يكن أمانيا','310','3764','0');
INSERT INTO [t754] VALUES (4055,'تمنيتها لما تمنيت أن ترى ... صديقا فأعيا أو عدوا مداجيا','310','3764','0');
INSERT INTO [t754] VALUES (4056,'إذا كنت ترضى أن تعيش بذلة ... فلا تستعدن الحسام اليمانيا','310','3764','0');
INSERT INTO [t754] VALUES (4057,'ولا تستطيلن الرماح لغارة ... ولا تستجيدن العتاق المذاكيا','310','3764','0');
INSERT INTO [t754] VALUES (4058,'فما ينفع الأسد الحياء من الطوى ... ولا تتقي حتى تكون ضواريا','310','3764','0');
INSERT INTO [t754] VALUES (4059,'حببتك قلبي قبل حبك من ناي ... وقد كان غدارا فكن أنت وافيا','310','3764','0');
INSERT INTO [t754] VALUES (4060,'وأعلم أن البين يشكيك بعده ... فلست فؤادي إن أيتك شاكيا','310','3764','0');
INSERT INTO [t754] VALUES (4061,'فإن دموع العين غدر بربها ... إذا كن إثر الغادرين جواريا','310','3764','0');
INSERT INTO [t754] VALUES (4062,'إذا الجود لم يرزق خلاصا من الأذى ... فلا الحمد مكسوبا ولا المال باقيا','311','3764','0');
INSERT INTO [t754] VALUES (4063,'وللنفس أخلاق تدل على الفتى ... أكان سخاء ما أتى أم تساخيا','311','3764','0');
INSERT INTO [t754] VALUES (4064,'أقل اشتياقا أيها القلب ربما ... رايتك تصفي الود من ليس جازيا','311','3764','0');
INSERT INTO [t754] VALUES (4065,'خلقت ألوفا لو رحلت إلى الصبي ... لفارقت شيبي موجع القلب باكيا','311','3764','0');
INSERT INTO [t754] VALUES (4066,'ولكن بالفسطاط بحرا أزرته ... حيوتي ونصحي والهوى والقوافيا','311','3764','0');
INSERT INTO [t754] VALUES (4067,'وجردا مددنا بين آذانها القنا ... فبتن حفافا يتبعن العواليا','311','3764','0');
INSERT INTO [t754] VALUES (4068,'تماشى بأيد كلما وافت الصفا ... نقشن به صدر البزاة حوافيا','311','3764','0');
INSERT INTO [t754] VALUES (4069,'وينظرن من سود صوادق في الدجى ... يرين بعيدات الشخوص كما هيا','311','3764','0');
INSERT INTO [t754] VALUES (4070,'وتنصب للجرس الخفي سوامعا ... يخلن مناجاة الضمير تناديا','311','3764','0');
INSERT INTO [t754] VALUES (4071,'تجاذب فرسان الصباح أعنة ... كأن على الأعناق منها أفاعيا','311','3764','0');
INSERT INTO [t754] VALUES (4072,'بعزم يسير الجسم في السرج راكبا ... به ويسير القلب في الجسم ماشيا','311','3764','0');
INSERT INTO [t754] VALUES (4073,'قواصد كافور توارك غيره ... ومن قصد البحر استقل السواقيا','311','3764','0');
INSERT INTO [t754] VALUES (4074,'فجاءت بنا إنسان عين زمانه ... وخلت بياضا خلفها ومآقيا','311','3764','0');
INSERT INTO [t754] VALUES (4075,'نجوز عليها المحسنين إلى الذي ... نرى عندهم إحسانه والأياديا','311','3764','0');
INSERT INTO [t754] VALUES (4076,'فتى ما سرينا في ظهور جدودنا ... إلى عصره إلا نرجى التلاقيا','311','3764','0');
INSERT INTO [t754] VALUES (4077,'ترفع عن عون المكارم قدره ... فما يفعل الفعلات إلا عذاريا','311','3764','0');
INSERT INTO [t754] VALUES (4078,'يبيد عداوات البغاة بلطفه ... فإن لم تبد منهم أباد الأعاديا','312','3764','0');
INSERT INTO [t754] VALUES (4079,'أبا المسك ذا الوجه الذي كنت تائقا ... إليه وذا الوقت الذي كنت راجيا','312','3764','0');
INSERT INTO [t754] VALUES (4080,'لقيت المروري والشناخيب دونه ... وجبت هجيرا يترك الماء صاديا','312','3764','0');
INSERT INTO [t754] VALUES (4081,'أبا كل طيب لا أبا المسك وحده ... وكل سحاب لا أخص الغواديا','312','3764','0');
INSERT INTO [t754] VALUES (4082,'يدل بمعنى واحد كل فاخر ... وقد جمع الرحمان فيك المعانيا','312','3764','0');
INSERT INTO [t754] VALUES (4083,'إذا كسب الناس المعالي بالندى ... فإنك تعطي في نداك المعاليا','312','3764','0');
INSERT INTO [t754] VALUES (4084,'وغير كثير أن يزورك راجل ... فيرجع ملكا للعراقين واليا','312','3764','0');
INSERT INTO [t754] VALUES (4085,'فقد تهب الجيش الذي جاء غازيا ... لسائلك الفرد الذي جاء عافيا','312','3764','0');
INSERT INTO [t754] VALUES (4086,'وتحتقر الدنيا احتقار مجرب ... يرى كل ما فيها وحاشاك فانيا','312','3764','0');
INSERT INTO [t754] VALUES (4087,'وما كنت ممن أدرك الملك بالمنى ... ولكن بأيام أشبن النواصيا','312','3764','0');
INSERT INTO [t754] VALUES (4088,'عداك تراها في البلاد مساعيا ... وأنت تراها في السماء مراقيا','312','3764','0');
INSERT INTO [t754] VALUES (4089,'لبست لها كدر العجاج كأنما ... ترى غير صاف أن ترى الجو صافيا','312','3764','0');
INSERT INTO [t754] VALUES (4090,'وقدت إليها كل أجرد سابح ... يؤديك غضبانا ويثنيك راضيا','312','3764','0');
INSERT INTO [t754] VALUES (4091,'ومخترط ماض يطيعك آمرا ... ويعصي إذا استثنيت أو صرت ناهيا','312','3764','0');
INSERT INTO [t754] VALUES (4092,'وأسمر ذي عشرين ترضاه واردا ... ويرضاك في إيراده الخيل ساقيا','313','3764','0');
INSERT INTO [t754] VALUES (4093,'كتائب ما انفكت تجوس عمائرا ... من الأرض قد جاست إليها فيافيا','313','3764','0');
INSERT INTO [t754] VALUES (4094,'غزوت بها دور الملوك فباشرت ... سنابكها هاماتهم والمغانيا','313','3764','0');
INSERT INTO [t754] VALUES (4095,'وأنت الذي تغشى الأسنة أولا ... وتأنف أن تغشى الأسنة ثانيا','313','3764','0');
INSERT INTO [t754] VALUES (4096,'إذا الهند سوت بين سيفي كريهة ... فسيفك في كف تزيل التساويا','313','3764','0');
INSERT INTO [t754] VALUES (4097,'ومن قول سام لو رآك لنسله ... فدى ابن أخي نسلي ونفسي وماليا','313','3764','0');
INSERT INTO [t754] VALUES (4098,'مدى بلغ الأستاذ أقصاه ربه ... ونفس له لم ترض إلا التناهيا','313','3764','0');
INSERT INTO [t754] VALUES (4099,'دعته فلباها إلى المجد والعلى ... وقد خالف الناس النفوس الدواعيا','313','3764','0');
INSERT INTO [t754] VALUES (4100,'فأصبح فوق العالمين يرونه ... وإن كان يدنيه التكرم نائيا','313','3764','0');
INSERT INTO [t754] VALUES (4101,'أريك الرضا لو أخفت النفس خافيا ... وما أنا عن نفسي ولا عنك راضيا','313','3764','0');
INSERT INTO [t754] VALUES (4102,'أمينا وإخلافا وغدرا وخسة ... وجبنا أشخصا لحت لي أم مخازيا','313','3764','0');
INSERT INTO [t754] VALUES (4103,'تظن ابتساماتي رجاء وغبطة ... وما أنا إلا ضاحك من رجائيا','313','3764','0');
INSERT INTO [t754] VALUES (4104,'وتعجبني رجلاك في النعل أنني ... رأيتك ذا نعل إذا كنت حافيا','313','3764','0');
INSERT INTO [t754] VALUES (4105,'وإنك لا تدري ألونك أسود ... من الجهل أم قد صار أبيض صافيا','313','3764','0');
INSERT INTO [t754] VALUES (4106,'ويذكرني تخييط كعبك شقه ... ومشيك في ثوب من الزيت عاريا','313','3764','0');
INSERT INTO [t754] VALUES (4107,'ولولا فضول الناس جئتك مادحا ... بما كنت في سري به لك هاجيا','314','3764','0');
INSERT INTO [t754] VALUES (4108,'فأصبحت مسرورا بما أنا منشد ... وإن كان بالإنشاد هجوك غاليا','314','3764','0');
INSERT INTO [t754] VALUES (4109,'فإن كنت لا خيرا أفدت فإنني ... أفدت بلحظي مشفريك الملاهيا','314','3764','0');
INSERT INTO [t754] VALUES (4110,'ومثلك يؤتى من بلاد بعيدة ... ليضحك ربات الحداد البواكيا','314','3764','0');
INSERT INTO [t754] VALUES (4111,'إما التهنئات للأكفاء ... ولمن يدني من البعداء','314','3764','0');
INSERT INTO [t754] VALUES (4112,'وأنا منك لا يهنىء عضو ... بالمسرات سائر الأعضاء','314','3764','0');
INSERT INTO [t754] VALUES (4113,'مستقل لك الديار ولو كا ... ن نجوما آجر لهذا البناء','314','3764','0');
INSERT INTO [t754] VALUES (4114,'ولو أن الذي يخر من الأم ... واه فيها من فضة بيضاء','314','3764','0');
INSERT INTO [t754] VALUES (4115,'أنت أعلى محلة أن تهني ... بمكان في الأرض أو في السماء','314','3764','0');
INSERT INTO [t754] VALUES (4116,'ولكن الناس والبلاد وما يس ... رح بين الخضراء والغبراء','314','3764','0');
INSERT INTO [t754] VALUES (4117,'وبساتينك الجياد وما تح ... مل من سمهرية سمراء','314','3764','0');
INSERT INTO [t754] VALUES (4118,'إنما يفخر الكريم أبو المس ... ك بما يبتني من العلياء','314','3764','0');
INSERT INTO [t754] VALUES (4119,'وبأيامه التي انسلخت عن ... هـ وما داره سوى الهيجاء','314','3764','0');
INSERT INTO [t754] VALUES (4120,'وبما أثرت صوارمه البي ... ض له في جماجم الأعداء','314','3764','0');
INSERT INTO [t754] VALUES (4121,'وبمسك يكنى به ليس بالمس ... ك ولكنه أريج الثناء','314','3764','0');
INSERT INTO [t754] VALUES (4122,'لابما تبتني الحواضر في الري ... ف وما يطبي قلوب النساء','314','3764','0');
INSERT INTO [t754] VALUES (4123,'نزلت إذ نزلتها الدار في أح ... سن منها من السنا والسناء','314','3764','0');
INSERT INTO [t754] VALUES (4124,'حل في منبت الرياحين منها ... منبت المكرمات والآلاء','315','3764','0');
INSERT INTO [t754] VALUES (4125,'تفضح الشمس كلما ذرت الشم ... س بشمس منيرة سوداء','315','3764','0');
INSERT INTO [t754] VALUES (4126,'إن في ثوبك الذي المجد فيه ... لضياء يزرى بكل ضياء','315','3764','0');
INSERT INTO [t754] VALUES (4127,'إنما الجلد ملبس وابيضاض ال ... نفس خير من أبيضاض القباء','315','3764','0');
INSERT INTO [t754] VALUES (4128,'كرم في شجاعة وذكاء ... في بهاء وقدرة في وفاء','315','3764','0');
INSERT INTO [t754] VALUES (4129,'من لبيض الملوك أن تبدل اللو ... ن بلون الأستاذ والسحناء','315','3764','0');
INSERT INTO [t754] VALUES (4130,'فتراها بنو الحروب بأعيا ... ن تراه بها غداة اللقاء','315','3764','0');
INSERT INTO [t754] VALUES (4131,'يا رجاء العيون في كل أرض ... لم يكن غير أن أراك رجائي','315','3764','0');
INSERT INTO [t754] VALUES (4132,'ولقد أفنت المفاوز خيلي ... قبل أن نلتقي وزادي ومائي','315','3764','0');
INSERT INTO [t754] VALUES (4133,'فارم بي ما أردت مني فإني ... أسد القلب آدمي الرواء','315','3764','0');
INSERT INTO [t754] VALUES (4134,'وفؤادي من الملوك وإن كا ... ن لساني يرى من الشعراء','315','3764','0');
INSERT INTO [t754] VALUES (4135,'من الجآذر في زي الأعاريب ... حمر الحلي والمطايا والجلابيب','315','3764','0');
INSERT INTO [t754] VALUES (4136,'إن كنت تسأل شكا في معارفها ... فمن بلاك بتسهيد وتعذيب','315','3764','0');
INSERT INTO [t754] VALUES (4137,'لا تجزني بضني بي بعدها بقر ... تجزي دموعي مسكوبا بمسكوب','315','3764','0');
INSERT INTO [t754] VALUES (4138,'سوائر ربما سارت هوادجها ... منيعة بين مطعون ومضروب','315','3764','0');
INSERT INTO [t754] VALUES (4139,'وربما وخدت أيدي المطي بها ... على نجيع من الفرسان مصبوب','315','3764','0');
INSERT INTO [t754] VALUES (4140,'كم زورة لك في الأعراب خافية ... أدهى وقد رقدوا من زورة الذيب','315','3764','0');
INSERT INTO [t754] VALUES (4141,'أزورهم وسواد الليل يشفع لي ... وأنثنى وبياض الصبح يغري بي','316','3764','0');
INSERT INTO [t754] VALUES (4142,'قد وافقوا الوحش في سكني مراتعها ... وخالفوها بتقويض وتطنيب','316','3764','0');
INSERT INTO [t754] VALUES (4143,'جيرانها وهم شر الجوار لها ... وصحبها وهم شر الأصاحيب','316','3764','0');
INSERT INTO [t754] VALUES (4144,'فؤاد كل محب في بيوتهم ... ومال كل أخيذ المال محروب','316','3764','0');
INSERT INTO [t754] VALUES (4145,'ما أوجه الحضر المستحسنات به ... كأوجه البدويات الرعابيب','316','3764','0');
INSERT INTO [t754] VALUES (4146,'حسن الحضارة مجلوب بتظرية ... وفي البداوة حسن غير مجلوب','316','3764','0');
INSERT INTO [t754] VALUES (4147,'أين المعيز من الآرام ناظرة ... وغير ناظرة في الحسن والطيب','316','3764','0');
INSERT INTO [t754] VALUES (4148,'أفدى ظياء فلاة ما عرفن بها ... مضغ الكلام ولا صبغ الحواجيب','316','3764','0');
INSERT INTO [t754] VALUES (4149,'ولا برزن من الحمام مائلة ... أوراكهن صقيلات العراقيب','316','3764','0');
INSERT INTO [t754] VALUES (4150,'ومن هوى كل من ليست مموهة ... تركت لون مشيبي غير مخضوب','316','3764','0');
INSERT INTO [t754] VALUES (4151,'ومن هوى الصدق في قولي وعادته ... رغبت عن شعر في الوجه مكذوب','316','3764','0');
INSERT INTO [t754] VALUES (4152,'ليت الحوادث باعتني الذي أخذت ... مني بحلمي الذي أعطت وتجريبي','316','3764','0');
INSERT INTO [t754] VALUES (4153,'فما الحداثة من حلم بمانعة ... قد يوجد الحلم في الشبان والشيب','316','3764','0');
INSERT INTO [t754] VALUES (4154,'ترعرع الملك الأستاذ مكتهلا ... قبل اكتهال أديبا قل تأديب','316','3764','0');
INSERT INTO [t754] VALUES (4155,'مجربا فهما من غير تجربة ... مهذبا كرما من قبل تهذيب','316','3764','0');
INSERT INTO [t754] VALUES (4156,'حتى أصاب من الدنيا نهايتها ... وهمه في ابتدآت وتشبيب','317','3764','0');
INSERT INTO [t754] VALUES (4157,'يدبر الملك من مصر إلى عدن ... إلى العراق فأرض الروم فالنوب','317','3764','0');
INSERT INTO [t754] VALUES (4158,'إذا أتتها الرياح النكب من بلد ... فما تهب بها إلا بترتيب','317','3764','0');
INSERT INTO [t754] VALUES (4159,'ولا تجاوزها شمس إذا شرقت ... إلا ومنه إذن بتغريب','317','3764','0');
INSERT INTO [t754] VALUES (4160,'يصرف الأمر فيها طين خاتمه ... ولو تطلس منه كل مكتوب','317','3764','0');
INSERT INTO [t754] VALUES (4161,'يحط كل طويل الرمح حامله ... من سرج كل طويل الباع يعبوب','317','3764','0');
INSERT INTO [t754] VALUES (4162,'كأن كل سؤال في مسامعه ... قميص يوسف في أجفان يعقوب','317','3764','0');
INSERT INTO [t754] VALUES (4163,'إذا غزته أعاديه بمسألة ... فقد غزته بجيش غير مغلوب','317','3764','0');
INSERT INTO [t754] VALUES (4164,'أو حاربته فما تنجو بتقدمة ... مما أراد ولا تنجو بتجبيب','317','3764','0');
INSERT INTO [t754] VALUES (4165,'أضرت شجاعته أقصى كتائبه ... على الحمام فما موت بمرهوب','317','3764','0');
INSERT INTO [t754] VALUES (4166,'قالوا هجرت إليه الغيث قلت لهم ... إلى غيوث يديه والشآبيب','317','3764','0');
INSERT INTO [t754] VALUES (4167,'إلى الذي تهب الدولات راحته ... ولا تمن على آثار موهوب','317','3764','0');
INSERT INTO [t754] VALUES (4168,'ولا يروع بمغدور به أحدا ... ولا يفزع موفورا بمنكوب','317','3764','0');
INSERT INTO [t754] VALUES (4169,'بلى يروع بذي جيش يجدله ... ذا مثله في أحم النقع غربيب','317','3764','0');
INSERT INTO [t754] VALUES (4170,'وجدت أنفع مال كنت أذخره ... ما في السوابق من جري وتقريب','317','3764','0');
INSERT INTO [t754] VALUES (4171,'لما رأين صروف الدهر تغدرني ... وفين لي ووفت صم الأنابيب','317','3764','0');
INSERT INTO [t754] VALUES (4172,'فتن المهالك حتى قال قائلها ... ماذا لقينا من الجرد السراحيب','318','3764','0');
INSERT INTO [t754] VALUES (4173,'تهوى بمنجرد ليست مذاهبه ... للبس ثوب ومأكول ومشروب','318','3764','0');
INSERT INTO [t754] VALUES (4174,'يرمي النجوم بعيني من يحاولها ... كأنها سلب في عين مسلوب','318','3764','0');
INSERT INTO [t754] VALUES (4175,'حتى وصلت إلى نفس محجبة ... تلقى النفوس بفضل غير محجوب','318','3764','0');
INSERT INTO [t754] VALUES (4176,'في جسم أروع صافي العقل تضحكه ... خلائق الناس إضحاك الأعاجيب','318','3764','0');
INSERT INTO [t754] VALUES (4177,'فالحمد قبل له والحمد بعد لها ... وللقنا ولإدلاجي وتأويبي','318','3764','0');
INSERT INTO [t754] VALUES (4178,'وكيف أكفر يا كافور نعمتها ... وقد بلغنك بي يا كل مطلوبي','318','3764','0');
INSERT INTO [t754] VALUES (4179,'يا أيها الملك الغاني بتسمية ... في الشرق والغرب عن وصف وتلقيب','318','3764','0');
INSERT INTO [t754] VALUES (4180,'أنت الحبيب ولكني أعوذ به ... من أن أكون محبا غير محبوب','318','3764','0');
INSERT INTO [t754] VALUES (4181,'أود من الأيام مالا توده ... وأشكو إليها بيننا وهي جنده','318','3764','0');
INSERT INTO [t754] VALUES (4182,'يباعدن حبا يجتمعن ووصله ... فكيف بحب يجتمعن وصده','318','3764','0');
INSERT INTO [t754] VALUES (4183,'أبى خلق الدنيا حبيبا تديمه ... فما طلبي منها حبيبا ترده','319','3764','0');
INSERT INTO [t754] VALUES (4184,'واسرع مفعول فعلت تغيرا ... تكلف شيء في طباعك ضده','319','3764','0');
INSERT INTO [t754] VALUES (4185,'رعى الله عيسا فارقتنا وفوقها ... مها كلها يولى بجفنيه خده','319','3764','0');
INSERT INTO [t754] VALUES (4186,'بواد به ما بالقلوب كأنه ... وقد رحلوا جيد تناثر عقده','319','3764','0');
INSERT INTO [t754] VALUES (4187,'إذا سارت الأحداج فوق نباته ... تفاوح مسك الغانيات ورنده','319','3764','0');
INSERT INTO [t754] VALUES (4188,'وحال كإحداهن رمت بلوغها ... ومن دونها غول الطريق وبعده','319','3764','0');
INSERT INTO [t754] VALUES (4189,'وأتعب خلق الله من زاد همه ... وقصر عما تشتهي النفس وجده','319','3764','0');
INSERT INTO [t754] VALUES (4190,'فلا ينحلل في المجد مالك كله ... فينحل مجد كان بالمال عقده','319','3764','0');
INSERT INTO [t754] VALUES (4191,'ودبره تدبير الذي المجد كفه ... إذا حارب الأعداء والمال زنده','319','3764','0');
INSERT INTO [t754] VALUES (4192,'فلا مجد في الدنيا لمن قل ماله ... ولا مال في الدنيا لمن قل مجده','320','3764','0');
INSERT INTO [t754] VALUES (4193,'وفي الناس من يرضى بميسور عيشه ... ومركوبه رجلاه والثوب جلده','320','3764','0');
INSERT INTO [t754] VALUES (4194,'ولكن قلبا بين جنبي ما له ... مدى ينتهي بي في مراد أحده','320','3764','0');
INSERT INTO [t754] VALUES (4195,'يرى جسمه يكسى شفوفا تربه ... فيختار أن يكسى دروعا تهده','320','3764','0');
INSERT INTO [t754] VALUES (4196,'يكلفني التهجير في كل مهمة ... عليقي مراعيه وزادي ربده','320','3764','0');
INSERT INTO [t754] VALUES (4197,'وأمضى سلاح قلد المرء نفسه ... رجاء أبى المسك الكريم وقصده','320','3764','0');
INSERT INTO [t754] VALUES (4198,'هما ناصرا من خانه كل ناصر ... وأسرة من لم يكثر النسل جده','320','3764','0');
INSERT INTO [t754] VALUES (4199,'أنا اليوم من غلمانه في عشيرة ... لنا والد منه يفديه ولده','320','3764','0');
INSERT INTO [t754] VALUES (4200,'فمن ماله مال الكبير ونفسه ... ومن ماله در الصغير ومهده','320','3764','0');
INSERT INTO [t754] VALUES (4201,'تجر القنا الخطى حول قبابه ... وتردى بنا قب الرباط وجرده','320','3764','0');
INSERT INTO [t754] VALUES (4202,'ونمتحن النشاب في كل وابل ... دوي القسي الفارسية رعده','320','3764','0');
INSERT INTO [t754] VALUES (4203,'فإن لا يكن مصر الشرى أو عرينه ... فإن الذي فيها من الناس أسده','320','3764','0');
INSERT INTO [t754] VALUES (4204,'سبائك كافور وعقيانه الذي ... بصم القنا لا بالأصابع نقده','320','3764','0');
INSERT INTO [t754] VALUES (4205,'بلاها حواليه العدو وغيره ... وجربها هزل الطراد وجده','320','3764','0');
INSERT INTO [t754] VALUES (4206,'أبو المسك لا يفنى بذنبك عفوه ... ولكنه يفنى بعذرك حقده','320','3764','0');
INSERT INTO [t754] VALUES (4207,'فيا أيها المنصور بالجد سعيه ... ويا ايها المنصور بالسعي جده','321','3764','0');
INSERT INTO [t754] VALUES (4208,'تولى الصبي عني فأخلفت طيبه ... وما ضرني لما رأيتك فقده','321','3764','0');
INSERT INTO [t754] VALUES (4209,'لقد شب في هذا الزمان كهوله ... لديك وشابت عند غيرك مرده','321','3764','0');
INSERT INTO [t754] VALUES (4210,'ألا ليت يوم السير يخبر حره ... فتسأله والليل يخبر برده','321','3764','0');
INSERT INTO [t754] VALUES (4211,'وليتك ترعاني وحيران معرض ... فتعلم أني من حسامك حده','321','3764','0');
INSERT INTO [t754] VALUES (4212,'وأني إذا حاولت أمرا أريده ... تدانت أقاصيه وهان أشده','321','3764','0');
INSERT INTO [t754] VALUES (4213,'ومازال أهل الدهر يشتبهون لي ... إليك فلما لحت لي لاح فرده','321','3764','0');
INSERT INTO [t754] VALUES (4214,'يقال إذا أبصرت جيشا وربه ... أمامك ملك رب ذا الجيش عبده','321','3764','0');
INSERT INTO [t754] VALUES (4215,'وألقى الفم الضحاك أعلم أنه ... قريب بذي الكف المفداة عهده','321','3764','0');
INSERT INTO [t754] VALUES (4216,'فزارك مني من إليك اشتياقه ... وفي الناس إلا فيك وحدك زهده','321','3764','0');
INSERT INTO [t754] VALUES (4217,'يخلف من لم يأت دراك غاية ... ويأتي ويدري أن ذلك جهده','321','3764','0');
INSERT INTO [t754] VALUES (4218,'فإن نلت ما أملت منك فربما ... شربت بماء يعجز الطير ورده','321','3764','0');
INSERT INTO [t754] VALUES (4219,'ووعدك فعل قبل وعد لأنه ... نظير فعال الصادق القول وعده','321','3764','0');
INSERT INTO [t754] VALUES (4220,'فكن في اصطناعي محسنا كمجرب ... يبن لك تقريب الجواد وشده','321','3764','0');
INSERT INTO [t754] VALUES (4221,'إذا كنت في شك من السيف فابله ... فإما تنفيه وإما تعده','321','3764','0');
INSERT INTO [t754] VALUES (4222,'وما الصارم الهندي إلا كغيره ... إذا لم يفارقه النجاد وغمده','321','3764','0');
INSERT INTO [t754] VALUES (4223,'وإنك للمشكور في كل حالة ... ول لم يكن إلا البشاشة رفده','322','3764','0');
INSERT INTO [t754] VALUES (4224,'فكل نوال كان أو هو كائن ... فلحظه طرف منك عندي نده','322','3764','0');
INSERT INTO [t754] VALUES (4225,'وإني لفي بحر من الخير أصله ... عطاياك أرجو مدها وهي مده','322','3764','0');
INSERT INTO [t754] VALUES (4226,'وما رغبتي في عسجد أستفيده ... ولكنها في مفخر أستجده','322','3764','0');
INSERT INTO [t754] VALUES (4227,'يجود به من يفضح الجود جوده ... ويجمده من يفضح الحمد حمده','322','3764','0');
INSERT INTO [t754] VALUES (4228,'فإنك ما مر النحوس بكوكب ... وقابلته إلا ووجهك سعده','322','3764','0');
INSERT INTO [t754] VALUES (4229,'يقل له القيام على الرؤوس ... وبذل المكرمات من النفوس','322','3764','0');
INSERT INTO [t754] VALUES (4230,'إذا خانته في يوم ضحوك ... فكيف تكون في يوم عبوس','322','3764','0');
INSERT INTO [t754] VALUES (4231,'أحق دار بأن تدعى مباركة ... دار مباركة الملك الذي فيها','322','3764','0');
INSERT INTO [t754] VALUES (4232,'وأجدر الدور أن تسقى بساكنها ... دار غدا الناس ستسقون أهليها','322','3764','0');
INSERT INTO [t754] VALUES (4233,'هذي منازلك الأخرى نهنئها ... فمن يمر على الأولى يسليها','322','3764','0');
INSERT INTO [t754] VALUES (4234,'إذا حللت مكانا بعد صاحبه ... جعلت فيه على ما قبله تيها','322','3764','0');
INSERT INTO [t754] VALUES (4235,'لا ينكر العقل من دار تكون بها ... فإن ريحك روح في مغانيها','322','3764','0');
INSERT INTO [t754] VALUES (4236,'أتم سعدك من لقاك أوله ... ولا أسترد حياة منك معطيها','322','3764','0');
INSERT INTO [t754] VALUES (4237,'فراق ومن فارقت غير مذمم ... وأم ومن يممت صير ميمم','322','3764','0');
INSERT INTO [t754] VALUES (4238,'وما منزل اللذات عندي بمنزل ... إذا لم أبجل عنده وأكرم','323','3764','0');
INSERT INTO [t754] VALUES (4239,'سجية نفس لا تزال مليحة ... من الضيم مرميا بها كل مخرم','323','3764','0');
INSERT INTO [t754] VALUES (4240,'رحلت فكم باك بأجفان شادن ... علي وكم باك بأجفان ضيغم','323','3764','0');
INSERT INTO [t754] VALUES (4241,'وما رب القرط المليح مكانه ... عذرت ولكن من حبيب معمم','323','3764','0');
INSERT INTO [t754] VALUES (4242,'رمى واتقى رميي ومن دون ما اتقى ... هوى كاسر كفى وقوسي وأسمهي','323','3764','0');
INSERT INTO [t754] VALUES (4243,'إذا ساء فعل المرء ساءت ظنونه ... وصدق ما يعتاده من توهم','323','3764','0');
INSERT INTO [t754] VALUES (4244,'وعادى محبيه بقول عداته ... وأصبح في ليل من الشك مظلم','323','3764','0');
INSERT INTO [t754] VALUES (4245,'أصادق نفس المرء من قبل جسمه ... وأعرفها في فعله والتكلم','323','3764','0');
INSERT INTO [t754] VALUES (4246,'وأحلم عن حلي وأعلم أنه ... متى أجزه حلما على الجهل يندم','323','3764','0');
INSERT INTO [t754] VALUES (4247,'وإن بذل الإنسان لي جود عابس ... جزيت بجود التارك المتبسم','323','3764','0');
INSERT INTO [t754] VALUES (4248,'وأهوى من الفتيان كل سميدع ... نجيب كصدر السمهري المقوم','323','3764','0');
INSERT INTO [t754] VALUES (4249,'خطت تحته العيش الفلاة وخالطت ... به الخيل كبات الخميس العرمرم','323','3764','0');
INSERT INTO [t754] VALUES (4250,'ولا عفة في سيفه وسنانه ... ولكنها في الكف والفرج والفم','323','3764','0');
INSERT INTO [t754] VALUES (4251,'وما كل هاو للجميل بفاعل ... ولا كل فاعل له بتممم','323','3764','0');
INSERT INTO [t754] VALUES (4252,'أغر بمجد قد شخصن وراءه ... إلى خلق رحب وخلق مطهم','324','3764','0');
INSERT INTO [t754] VALUES (4253,'إذا منعت منك السياسة نفسها ... فقف وقفة قدامه تتعلم','324','3764','0');
INSERT INTO [t754] VALUES (4254,'يضيق على من راءه العذر أن يرى ... ضعيف المساعي أو قليل التكرم','324','3764','0');
INSERT INTO [t754] VALUES (4255,'ومن مثل كافور إذا الخيل أحجمت ... وكان قليلا من يقول لها أقدمي','324','3764','0');
INSERT INTO [t754] VALUES (4256,'شديد ثبات الطرف والنقع واصل ... إلى لهوات الفارس المتلثم','324','3764','0');
INSERT INTO [t754] VALUES (4257,'أبا المسك أرجو منك نصرا على العدا ... وآمل عزا يخضب البيض بالدم','324','3764','0');
INSERT INTO [t754] VALUES (4258,'ويوما يغيظ الحاسدين وحالة ... أقيم الشقا فيها مقام التنعم','324','3764','0');
INSERT INTO [t754] VALUES (4259,'ولم أرج إلا أهل ذاك ومن يرد ... مواطر من غير السحائب يظلم','324','3764','0');
INSERT INTO [t754] VALUES (4260,'فلو لم تكن في مصر ما سرت نحوها ... بقلب المشوق المستهام المتيم','324','3764','0');
INSERT INTO [t754] VALUES (4261,'ولا نبحت خيلي كلاب قبائل ... كأن بها في الليل حملات ديلم','324','3764','0');
INSERT INTO [t754] VALUES (4262,'ولا أتبعت آثارنا عين قائف ... فلم تر إلا حافرا فوق منسم','324','3764','0');
INSERT INTO [t754] VALUES (4263,'وسمنا بها البيداء حتى تغمرت ... من النيل واستذرت بظل المقطم','324','3764','0');
INSERT INTO [t754] VALUES (4264,'وأبلخ يعصي باختصاصي مشيره ... عصيت بقصديه مشيري ولومي','324','3764','0');
INSERT INTO [t754] VALUES (4265,'فساق إلي العرف غير مكدر ... وسقت إليه الشكر غير مجمجم','325','3764','0');
INSERT INTO [t754] VALUES (4266,'قد اخترتك الأملاك فاختر لهم بنا ... حديث وقد حكمت رأيك فأحكم','325','3764','0');
INSERT INTO [t754] VALUES (4267,'فأحسن وجه في الورى وجه محسن ... وأيمن كف فيهم كف منعم','325','3764','0');
INSERT INTO [t754] VALUES (4268,'وأشرفهم من كان أشرف همة ... وأكثر إقداما على كل معظم','325','3764','0');
INSERT INTO [t754] VALUES (4269,'لمن تطلب الدنيا إذا لم ترد بها ... سرور محب أو مسآءة مجرم','325','3764','0');
INSERT INTO [t754] VALUES (4270,'وقد وصل المهر الذي فوق فخذه ... من اسمك ما في كل عنق ومعصم','325','3764','0');
INSERT INTO [t754] VALUES (4271,'لك الحيوان الراكب الخيل كله ... وإن كان بالنيران غير موسم','325','3764','0');
INSERT INTO [t754] VALUES (4272,'ولو كنت أدرى كم حياتي قسمتها ... وصيت ثلثيها انتظارك فاعلم','325','3764','0');
INSERT INTO [t754] VALUES (4273,'ولكن ما يمضي من الدهر فائت ... فجد لي بحظ البارد المتغنم','325','3764','0');
INSERT INTO [t754] VALUES (4274,'رضيت بما ترضى به لي محبة ... وقدت إليك النفس قود المسلم','325','3764','0');
INSERT INTO [t754] VALUES (4275,'ومثلك من كان الوسيط فؤاده ... فكلمه عني ولم أتكلم','325','3764','0');
INSERT INTO [t754] VALUES (4276,'أنوك من عبد ومن عرسه ... من حكم العبد على نفسه','325','3764','0');
INSERT INTO [t754] VALUES (4277,'ما من يرى أنك في وعده ... كمن يرى أنك ي حبسه','325','3764','0');
INSERT INTO [t754] VALUES (4278,'وإنما يظهر تحكيمه ... ليحكم الإفساد في حسه','325','3764','0');
INSERT INTO [t754] VALUES (4279,'العبد لا تفضل أخلاقه ... عن فرجه المنتن أو ضرسه','326','3764','0');
INSERT INTO [t754] VALUES (4280,'لا ينجز الميعاد في يومه ... ولا يعي ما قال في أمسه','326','3764','0');
INSERT INTO [t754] VALUES (4281,'وإنما تحتال في جذبه ... كأنك الملاح في قلسه','326','3764','0');
INSERT INTO [t754] VALUES (4282,'فلا ترج الخير عند امرىء ... مرت يد النخاس في رأسه','326','3764','0');
INSERT INTO [t754] VALUES (4283,'وإن عراك الشك في نفسه ... بحاله فانظر إلى جنسه','326','3764','0');
INSERT INTO [t754] VALUES (4284,'فقلما يلؤم في ثوبه ... إلا الذي يلؤم في غرسه','326','3764','0');
INSERT INTO [t754] VALUES (4285,'من وجد المذهب عن قدره ... ولم يجد المذهب عن قنسه','326','3764','0');
INSERT INTO [t754] VALUES (4286,'حسم الصلح ما اشتهته الأعادي ... وأذاعته ألسن الحساد','326','3764','0');
INSERT INTO [t754] VALUES (4287,'وأرادته أنفس حال تدبي ... رك ما بينها وبين المراد','326','3764','0');
INSERT INTO [t754] VALUES (4288,'صار ما أوضع المخبون فيه ... من عتاب زيادة في الوداد','326','3764','0');
INSERT INTO [t754] VALUES (4289,'وكلام الوشاة ليس على الأح ... باب سلطانه على الأضداد','326','3764','0');
INSERT INTO [t754] VALUES (4290,'إنما تنجح المقالة في المر ... ء إذا وافقت هوى في الفؤاد','326','3764','0');
INSERT INTO [t754] VALUES (4291,'ولعمري لقد هززت بما قي ... ل فألفيت أوثق الأطواد','326','3764','0');
INSERT INTO [t754] VALUES (4292,'وأشارت بما أبيت رجال ... كنت أهدي منها إلى الإرشاد','326','3764','0');
INSERT INTO [t754] VALUES (4293,'قد يصيب الفتى المشير ولم يج ... هد ويشوى الصواب بعد اجتهاد','326','3764','0');
INSERT INTO [t754] VALUES (4294,'نلت ما لا ينال بالبيض والسم ... ر وصنت الأرواح في الأجساد','326','3764','0');
INSERT INTO [t754] VALUES (4295,'ما دروا إذ رأوا فؤادك فيهم ... ساكنا أن رأيه في الطراد','326','3764','0');
INSERT INTO [t754] VALUES (4296,'ففدى رأيك الذي لم تفده ... كل رأي معلم مستفاد','326','3764','0');
INSERT INTO [t754] VALUES (4297,'وإذا الحلم لم يكن في طباع ... لم يحلم تقدم الميلاد','326','3764','0');
INSERT INTO [t754] VALUES (4298,'فبهذا ومثله سدت يا كا ... فور واقتدت كل صعب القياد','326','3764','0');
INSERT INTO [t754] VALUES (4299,'وأطاع الذي أطاعك والطا ... عة ليست خلائق الآساد','327','3764','0');
INSERT INTO [t754] VALUES (4300,'إنما أنت والدق والأب القا ... طع أحنى من واصل الأولاد','327','3764','0');
INSERT INTO [t754] VALUES (4301,'لا عدا الشر من بغي لكما الش ... ر وخص الفساد أهل الفساد','327','3764','0');
INSERT INTO [t754] VALUES (4302,'أنتما ما اتفقتما الجسم والرو ... ح فلا احتجتما إلى العواد','327','3764','0');
INSERT INTO [t754] VALUES (4303,'وإذا كان في الأنابيب خلف ... وقع الطيش في صدور الصعاد','327','3764','0');
INSERT INTO [t754] VALUES (4304,'أشمت الخلف بالشراة عداها ... وشفى رب فارس من إياد','327','3764','0');
INSERT INTO [t754] VALUES (4305,'وملوكا كأمس في القرب منا ... وكطسم وأختها في البعاد','327','3764','0');
INSERT INTO [t754] VALUES (4306,'فيكما بت عائذا فيكما من ... هـ ومن كيد كل باغ وعاد','327','3764','0');
INSERT INTO [t754] VALUES (4307,'وبلبيكما الاصيلين أن تف ... رق صم الرماح بين الجياد','327','3764','0');
INSERT INTO [t754] VALUES (4308,'هل يسرن باقيا بعد ماض ... ما يقول العداة في كل ناد','327','3764','0');
INSERT INTO [t754] VALUES (4309,'منع الود والرعاية والسو ... ددأن تبلغا إلى الأحقاد','327','3764','0');
INSERT INTO [t754] VALUES (4310,'وحقوق ترقق القلب للقل ... ب ولو ضمنت قلوب الجماد','327','3764','0');
INSERT INTO [t754] VALUES (4311,'فغدا الملك باهرا من رآه ... شاكرا ما أتيتما من سداد','327','3764','0');
INSERT INTO [t754] VALUES (4312,'فيه أيديكما على الظفر الح ... لو وأيدي قوم على الأكباد','327','3764','0');
INSERT INTO [t754] VALUES (4313,'هذه دولة المكارم والرأ ... فة والمجد والندى والأيادي','327','3764','0');
INSERT INTO [t754] VALUES (4314,'كسفت ساعة ما تكسف الشم ... س وعادت ونورها في إزدياد','328','3764','0');
INSERT INTO [t754] VALUES (4315,'يزحم الدهر ركنها عن أذاها ... بفتى مارد على المراد','328','3764','0');
INSERT INTO [t754] VALUES (4316,'متلف مخلف وفي أبي ... عالم حازم شجاع جواد','328','3764','0');
INSERT INTO [t754] VALUES (4317,'أجفل الناس عن طريق أبي المس ... ك وذلت له رقاب العباد','328','3764','0');
INSERT INTO [t754] VALUES (4318,'كيف لا يترك الطريق لسيل ... ضيق عن أتيه كل واد','328','3764','0');
INSERT INTO [t754] VALUES (4319,'أغالب فيك الشوق والشوق أغلب ... وأعجب من ذا الهجر والوصل أعجب','328','3764','0');
INSERT INTO [t754] VALUES (4320,'أما تغلط الأيام في بأن أرى ... بغيضا تنأى أو حبيبا تقرب','328','3764','0');
INSERT INTO [t754] VALUES (4321,'ولله سيري ما اقل تئية ... عشية شرقيي الدالي وغرب','328','3764','0');
INSERT INTO [t754] VALUES (4322,'عشية أحفى الناس بي من جفوته ... وأهدي الطريقين الذي أتجنب','328','3764','0');
INSERT INTO [t754] VALUES (4323,'وكم لظلام الليل عندك من يد ... تخبر أن المانوية تكذب','328','3764','0');
INSERT INTO [t754] VALUES (4324,'ويوم كليل العاشقين كمنته ... أراقب فيه الشمس أيان تغرب','328','3764','0');
INSERT INTO [t754] VALUES (4325,'وعيني إلى أذني أغر كأنه ... من الليل باق بين عينيه كوكب','328','3764','0');
INSERT INTO [t754] VALUES (4326,'له فضلة عن جسمه في إهابه ... تجيء على صدر رحيب وتذهب','329','3764','0');
INSERT INTO [t754] VALUES (4327,'شققت به الظلماء أدنى عنانه ... فيطغى وأرخيه مرارا فيلعب','329','3764','0');
INSERT INTO [t754] VALUES (4328,'وأصرع أي الوحش قفيته به ... وأنزل عه مثله حين أركب','329','3764','0');
INSERT INTO [t754] VALUES (4329,'وما الخيل إلا كالصديق قليلة ... وإن كثرت في عين من لا يجرب','329','3764','0');
INSERT INTO [t754] VALUES (4330,'إذا لم تشاهد غير حسن شياتها ... وأعضائها فالحسن عنك مغيب','329','3764','0');
INSERT INTO [t754] VALUES (4331,'لحا الله ذي الدنيا مناخا لراكب ... فكل بعيد الهم فيها معذب','329','3764','0');
INSERT INTO [t754] VALUES (4332,'ألا ليست شعري هل أقول قصدة ... فلا أشتكي فيها ولا أتعتب','329','3764','0');
INSERT INTO [t754] VALUES (4333,'وبي ما يذود الشعر عني أقله ... ولكن قلبي يابنة القوم قلب','329','3764','0');
INSERT INTO [t754] VALUES (4334,'وأخلاق كافور إذا شئت مدحه ... وإن لم أشأ تملي علي وأكتب','329','3764','0');
INSERT INTO [t754] VALUES (4335,'إذا ترك الإنسان أهلا وراءه ... ويمم كافورا فما يتغرب','329','3764','0');
INSERT INTO [t754] VALUES (4336,'فتى يملأ الأفعال رأيا وحكمة ... ونادرة أحيان يرضى ويغضب','329','3764','0');
INSERT INTO [t754] VALUES (4337,'إذا ضربت في الحرب بالسيف كفه ... تبينت أن السيف بالكف يضرب','329','3764','0');
INSERT INTO [t754] VALUES (4338,'تريد عطاياه على التبث كثرة ... وتلبث أمواه السحاب فتنضب','330','3764','0');
INSERT INTO [t754] VALUES (4339,'أبا المسك هل في الكاس فضل أناله ... فإني أغنى منذ حين ونشرب','330','3764','0');
INSERT INTO [t754] VALUES (4340,'وهبت على مقدار كفى زماننا ... ونفسي على مقدار كفيك تطلب','330','3764','0');
INSERT INTO [t754] VALUES (4341,'إذا لم تنط بي ضيعة أو ولاية ... فجودك يكسوني وشغلك يسلب','330','3764','0');
INSERT INTO [t754] VALUES (4342,'يضاحك في ذا العيد كل حبيبه ... حذائي وأبكي من أحب وأندب','330','3764','0');
INSERT INTO [t754] VALUES (4343,'أحن إلى أهلي وأهوى لقاءهم ... وأين من المشتاق عنقاء مغرب','330','3764','0');
INSERT INTO [t754] VALUES (4344,'فإن لم يكن إلا أبو المسكن أو هم ... فإنك أحلى في فؤادي وأعذب','330','3764','0');
INSERT INTO [t754] VALUES (4345,'وكل أمرىء يولي الجميل محبب ... وكان مكان ينبت العز طيب','330','3764','0');
INSERT INTO [t754] VALUES (4346,'يريد بك الحساد ما الله دافع ... وسمر العوالي والحديد المذرب','330','3764','0');
INSERT INTO [t754] VALUES (4347,'ودون الذي يبغون ما لو تخلصوا ... إلى الشيب منه عشت والطفل أشيب','330','3764','0');
INSERT INTO [t754] VALUES (4348,'إذا طلبوا جدواك أعطوا وحكموا ... وإن طلبوا الفضل الذي فيك خيبوا','330','3764','0');
INSERT INTO [t754] VALUES (4349,'ولو جاز أن يجوو علاك وهبتها ... ولكن من الأشياء ما ليس يوهب','330','3764','0');
INSERT INTO [t754] VALUES (4350,'وأظلم أهل الظلم من بات حاسدا ... لمن بات في نعمائه يتقلب','330','3764','0');
INSERT INTO [t754] VALUES (4351,'وأنت الذي ربيت ذا الملك مرضعا ... وليس له أم سواك ولا أب','330','3764','0');
INSERT INTO [t754] VALUES (4352,'وكنت له ليث العرين لشبله ... وما لك إلا الهندواتي مخلب','330','3764','0');
INSERT INTO [t754] VALUES (4353,'لقيت القنا عنه بنفس كريمة ... إلى الموت في الهيجا من العار تهرب','331','3764','0');
INSERT INTO [t754] VALUES (4354,'وقد يترك النفس التي لا تهابه ... ويخترم النفس التي تتهيب','331','3764','0');
INSERT INTO [t754] VALUES (4355,'وما عدم اللاقوك بأسا وشدة ... ولكن من لاقوا أشد وأنجب','331','3764','0');
INSERT INTO [t754] VALUES (4356,'ثناهم وبرق البيض في البيض صادق ... عليهم وبرق البيض في البيض خلب','331','3764','0');
INSERT INTO [t754] VALUES (4357,'سللت سيوفا علمت كل خاطب ... على كل عود كيف يدعو ويخطب','331','3764','0');
INSERT INTO [t754] VALUES (4358,'ويغنيك عما ينسب الناس أنه ... إليك تناهي المكرمات وتنسب','331','3764','0');
INSERT INTO [t754] VALUES (4359,'وأي قبيل يستحقك قدره ... معد بن عدنان فداك ويعرب','331','3764','0');
INSERT INTO [t754] VALUES (4360,'وما طربي لما رأيتك بدعة ... لقد كنت أرجو أن أراك فأطرب','331','3764','0');
INSERT INTO [t754] VALUES (4361,'وتعذلني فيك القوافي وهمتي ... كأني بمدح قبل مدحك مذنب','331','3764','0');
INSERT INTO [t754] VALUES (4362,'ولكن طال الطريق ولم أزل ... أفتش عن هذا الكلام وينهب','331','3764','0');
INSERT INTO [t754] VALUES (4363,'فشرق حتى ليس للشرق مشرق ... وغرب حتى ليس للغرب مغرب','331','3764','0');
INSERT INTO [t754] VALUES (4364,'إذا قلته لم يمتنع من وصوله ... جدار معلى أو خباء مطنب','331','3764','0');
INSERT INTO [t754] VALUES (4365,'بم التعلل لا أهل ولا وطن ... ولا نديم ولا كأس ولا سكن','331','3764','0');
INSERT INTO [t754] VALUES (4366,'أريد من زمني ذا أن يبلغني ... ما ليس يبلغه من نفسه الزمن','331','3764','0');
INSERT INTO [t754] VALUES (4367,'لا تلق دهرك إلا غير مكترث ... ما دام يصحب فيه روحك البدن','331','3764','0');
INSERT INTO [t754] VALUES (4368,'فما يدوم سرور ما سررت به ... ولا يريد عليك الفائت الحزن','332','3764','0');
INSERT INTO [t754] VALUES (4369,'مما أضر بأهل العشق أنهم ... هووا وما عرفوا الدنيا وما فطنوا','332','3764','0');
INSERT INTO [t754] VALUES (4370,'تفنى عيونهم دمعا وأنفسهم ... في إثر كل قبيح وجهه حسن','332','3764','0');
INSERT INTO [t754] VALUES (4371,'تحملوا حملتكم كل ناجية ... فكل بين علي اليوم مؤتمن','332','3764','0');
INSERT INTO [t754] VALUES (4372,'ما في هوادجكم من مهجتي عوض ... ن مت شوقا ولا فيها لها ثمن','332','3764','0');
INSERT INTO [t754] VALUES (4373,'يا من نعيت على بعد بمجلسه ... كل بما زعم الناعون مرتهن','332','3764','0');
INSERT INTO [t754] VALUES (4374,'كم قد قتلت وكم قد مت عندكم ... ثم انتفضت فزال القبر والكفن','332','3764','0');
INSERT INTO [t754] VALUES (4375,'قد كان شاهد دفني قولهم ... جماعة ثم ماتوا قبل من دفنوا','332','3764','0');
INSERT INTO [t754] VALUES (4376,'ما كل ما يتمنى المرء يدركه ... تجري الرياح بما لا تشتهي السفن','332','3764','0');
INSERT INTO [t754] VALUES (4377,'رأيتكم لا يصون العرض جاركم ... ولا يدر على مرعاكم اللبن','332','3764','0');
INSERT INTO [t754] VALUES (4378,'وتغضبون على من نال رفدكم ... حتى يعاقبه التنغيص والمنن','332','3764','0');
INSERT INTO [t754] VALUES (4379,'فغادر الهجر ما بيني وبينكم ... يهماء تكذب فيها العين والأذن','332','3764','0');
INSERT INTO [t754] VALUES (4380,'تحبو الرواسم من بعد الرسيم بها ... وتسأل الأرض عن أخفافها الثفن','332','3764','0');
INSERT INTO [t754] VALUES (4381,'أني أصاحب حلمي وهو بي كرم ... ولا أصاحب حلمي وهو بي جبن','332','3764','0');
INSERT INTO [t754] VALUES (4382,'ولا أقيم على مال أذل به ... ولا ألذ بما عرضي به درن','332','3764','0');
INSERT INTO [t754] VALUES (4383,'سهرت بعد رحيلي وحشة لكم ... ثم استمر مريري وارعوي الوسن','332','3764','0');
INSERT INTO [t754] VALUES (4384,'وإن بليت بود مثل ودكم ... فإنني بفراق مثله قمن','333','3764','0');
INSERT INTO [t754] VALUES (4385,'أبلى الأجلة مهري عند غيركم ... وبدل العذر بالفسطاط والرسن','333','3764','0');
INSERT INTO [t754] VALUES (4386,'عند الهمام أبي المسك الذي غرقت ... في جوده مضر الحمراء واليمن','333','3764','0');
INSERT INTO [t754] VALUES (4387,'وإن تأخر عني بعض موعده ... فما تأخر آمالي ولا تهن','333','3764','0');
INSERT INTO [t754] VALUES (4388,'هو الوفي ولكني ذكرت له ... مودة فهو يبلوها ويمتحن','333','3764','0');
INSERT INTO [t754] VALUES (4389,'صحب الناس قبلنا ذا الزمانا ... وعناهم من شأنه ما عنانا','333','3764','0');
INSERT INTO [t754] VALUES (4390,'وتولوا بغصة كلهم من ... هـ وإن سر بعضهم أحيانا','333','3764','0');
INSERT INTO [t754] VALUES (4391,'ربما تحسن الصنيع ليالي ... هـ ولكن تكدر الإحسانا','333','3764','0');
INSERT INTO [t754] VALUES (4392,'وكأنا لم يرض فينا بريب ال ... دهر حتى أعانه من أعانا','333','3764','0');
INSERT INTO [t754] VALUES (4393,'كلما أنبت الزمان قناة ... ركب المرء في القناة سنانا','333','3764','0');
INSERT INTO [t754] VALUES (4394,'ومراد النفوس أضغر من أن ... نتعادى فيه وأنا نتفانا','333','3764','0');
INSERT INTO [t754] VALUES (4395,'غير أن الفتى يلاقي المنايا ... كالحات ولا يلاقي الهوانا','333','3764','0');
INSERT INTO [t754] VALUES (4396,'ولو أن الحيوة تبقى لحي ... لعددنا أضلنا الشجعانا','333','3764','0');
INSERT INTO [t754] VALUES (4397,'وإذا لم يكن من الموت بد ... فمن العجز أن تكون جبانا','333','3764','0');
INSERT INTO [t754] VALUES (4398,'كل ما لم يكن من الصعب في الأن ... فس سهل فيها إذا هو كانا','333','3764','0');
INSERT INTO [t754] VALUES (4399,'عدوك مذموم بكل لسان ... ولو كان من أعدائك القمران','333','3764','0');
INSERT INTO [t754] VALUES (4400,'ولله سر في علاك وإنما ... كلام العدي ضرب من الهذيان','333','3764','0');
INSERT INTO [t754] VALUES (4401,'أتلتمس الأعداء بعد الذي رأت ... قيام دليلس أو وضوح بيان','334','3764','0');
INSERT INTO [t754] VALUES (4402,'رأت كل من ينوي لك الغدر يبتلي ... بغدر حيوة أو بغدر زمان','334','3764','0');
INSERT INTO [t754] VALUES (4403,'برغم شبيب فارق السيف كفه ... وكانا على العلات يصطحبان','334','3764','0');
INSERT INTO [t754] VALUES (4404,'كأن رقاب الناس قالت لسيفه ... رفيقك قيسي وأنت يماني','334','3764','0');
INSERT INTO [t754] VALUES (4405,'فإن يك إنسانا مضى لسبيله ... فإن المنايا غاية الحيوان','334','3764','0');
INSERT INTO [t754] VALUES (4406,'وما كان إلا النار في كل موضع ... تثير غبارا في مكان دخان','334','3764','0');
INSERT INTO [t754] VALUES (4407,'فنال حيوة يشتهيها عدوه ... وموتا يشهي الموت كل جبان','334','3764','0');
INSERT INTO [t754] VALUES (4408,'نفى وقع أطرافه الرماح برمحه ... ولم يخش وقع النجم والدبران','334','3764','0');
INSERT INTO [t754] VALUES (4409,'ولم يدر أن الموت فوق شواته ... معار جناح محسن الطيران','334','3764','0');
INSERT INTO [t754] VALUES (4410,'وقد قتل الأقران حتى قتلته ... بأضعف قرن في أذل مكان','334','3764','0');
INSERT INTO [t754] VALUES (4411,'أتته المنايا في طريق خفية ... على كل سمع حوله وعيان','334','3764','0');
INSERT INTO [t754] VALUES (4412,'ولو سلكت طرق السلاح لردها ... بطول يمين واتساع جنان','334','3764','0');
INSERT INTO [t754] VALUES (4413,'تقصده المقدار بين صحابه ... على ثقة من دهره وأمان','334','3764','0');
INSERT INTO [t754] VALUES (4414,'وهل ينفع الجيش الكثير التفافه ... على غير منصور وغير معان','334','3764','0');
INSERT INTO [t754] VALUES (4415,'ودي ما جنى قبل المبيت بنفسه ... ولم يده بالجمال العكنان','335','3764','0');
INSERT INTO [t754] VALUES (4416,'أتمسك ما أوليته يد عاقل ... وتمسك في كفرانه بعنان','335','3764','0');
INSERT INTO [t754] VALUES (4417,'ويركب ما أركبته من كرامة ... ويركب للعصيان ظهر حصان','335','3764','0');
INSERT INTO [t754] VALUES (4418,'ثنى يده الإحسان حتى كأنها ... وقد قبضت كانت بغير بنان','335','3764','0');
INSERT INTO [t754] VALUES (4419,'وعند من اليوم الوفاء لصاحب ... شبيب وأوفى من ترى أخوان','335','3764','0');
INSERT INTO [t754] VALUES (4420,'قضى الله يا كافور أنك أول ... وليس بقاض أن يرى لك ثاني','335','3764','0');
INSERT INTO [t754] VALUES (4421,'فما لك تختار القسي وإنما ... عن السعد يرمي دونك الثقلان','335','3764','0');
INSERT INTO [t754] VALUES (4422,'وما لك تعني بالأسنة والقنا ... وجدك طعان بغير سنان','335','3764','0');
INSERT INTO [t754] VALUES (4423,'ولم تحمل السيف الطويل نجاده ... وأنت غني عنه بالحدثان','335','3764','0');
INSERT INTO [t754] VALUES (4424,'أرد لي جميلا جدت أو لم تجد به ... فإنك ما أحببت في أتاني','335','3764','0');
INSERT INTO [t754] VALUES (4425,'لو الفلك الدوار أبغضت سعيه ... لعوقه شيء عن الدوران','335','3764','0');
INSERT INTO [t754] VALUES (4426,'ملومكما يجل عن الملام ... ووقع فعاله فوق الكلام','335','3764','0');
INSERT INTO [t754] VALUES (4427,'ذراني والفلاة بل دليل ... ووجهي والهجير بلا لثام','335','3764','0');
INSERT INTO [t754] VALUES (4428,'فإني أستريح بذي وهذا ... وأتعب بالإناخة والمقام','335','3764','0');
INSERT INTO [t754] VALUES (4429,'عيون رواحلي إن حرت عيني ... وكل بغام رازحة بغامي','335','3764','0');
INSERT INTO [t754] VALUES (4430,'فقد أرد المياه بغير هاد ... سوى عدي لها برق الغمام','336','3764','0');
INSERT INTO [t754] VALUES (4431,'يذم لمهجتي ربي وسيفي ... إذا احتاج إلى الذمام','336','3764','0');
INSERT INTO [t754] VALUES (4432,'ولا أمسي لأهل البخل ضيفا ... وليس قرى سورى مخ النعام','336','3764','0');
INSERT INTO [t754] VALUES (4433,'ولما صار ود الناس خبا ... جزيت على ابتسام بابتسام','336','3764','0');
INSERT INTO [t754] VALUES (4434,'يحب العاقلون على التصافي ... وحب الجاهلين على الوسام','336','3764','0');
INSERT INTO [t754] VALUES (4435,'وأنف من أخي لبي وأمي ... إذا ما لم أجده من الكرام','336','3764','0');
INSERT INTO [t754] VALUES (4436,'أرى الأجداد تغلبها كثيرا ... على الأولاد أخلاق اللئام','336','3764','0');
INSERT INTO [t754] VALUES (4437,'ولست بقانع من كل فضل ... بأن أعزي إلى جد همام','336','3764','0');
INSERT INTO [t754] VALUES (4438,'عجبت لمن له قد وحد ... وينبو القضم الكهام','336','3764','0');
INSERT INTO [t754] VALUES (4439,'ومن يجد الطريق إلى المعالي ... فلا يذر المطي بلا سنام','336','3764','0');
INSERT INTO [t754] VALUES (4440,'ولم أر في عيوب الناس شيئا ... كنقص القادرين على التمام','336','3764','0');
INSERT INTO [t754] VALUES (4441,'أقمت بأرض مصر فلا ورائي ... تخب بي الركاب ولا أمامي','336','3764','0');
INSERT INTO [t754] VALUES (4442,'وملني الفراش وكان جنبي ... يمل لقاءة في كل عام','336','3764','0');
INSERT INTO [t754] VALUES (4443,'قليل عائدي سقم فؤادي ... كثير حاسدي صعب مرامي','336','3764','0');
INSERT INTO [t754] VALUES (4444,'عليل الجسم ممتنع القيام ... شديد السكر من غير المدام','336','3764','0');
INSERT INTO [t754] VALUES (4445,'وزائرتي كأن بها حياء ... فليس تزور إلا في الظلام','336','3764','0');
INSERT INTO [t754] VALUES (4446,'بذلت لها المطارف والحشايا ... فعافتها وباتت في عظامي','336','3764','0');
INSERT INTO [t754] VALUES (4447,'يضيق الجلد عن نفسي وعنها ... فتوسعه بأنواع السقام','336','3764','0');
INSERT INTO [t754] VALUES (4448,'إذا ما فارقتني غسلتني ... كانا عاكفان على حرام','336','3764','0');
INSERT INTO [t754] VALUES (4449,'كأن الصبح يطردها فتجري ... مدامعها بأربعة سجام','336','3764','0');
INSERT INTO [t754] VALUES (4450,'أراقب وقتها من غير شوق ... مراقبة المشوق المستهام','337','3764','0');
INSERT INTO [t754] VALUES (4451,'ويصدق وعدها والصدق شر ... إذا ألقاك في الكرب العظام','337','3764','0');
INSERT INTO [t754] VALUES (4452,'أبنت الدهر عندي كل بنت ... فكيف وصلت أنت من الزحام','337','3764','0');
INSERT INTO [t754] VALUES (4453,'جرحت مجرحا لم يبق فيه ... مكان للسيوف وللسهام','337','3764','0');
INSERT INTO [t754] VALUES (4454,'ألا يا ليت شعر يدي أتمسى ... تصرف في عنان أو زمام','337','3764','0');
INSERT INTO [t754] VALUES (4455,'وهي أرمي هواي براقصات ... محلاة المقاود باللغام','337','3764','0');
INSERT INTO [t754] VALUES (4456,'فربتما شفيت غليل صدري ... بسير أو قناة أو حسام','337','3764','0');
INSERT INTO [t754] VALUES (4457,'وضاقت خطة فخلصت منها ... خلاص الخمر من نسج الفدام','337','3764','0');
INSERT INTO [t754] VALUES (4458,'وفارقت الحبيب بلا وداع ... وودعت بالبلاد بلا سلام','337','3764','0');
INSERT INTO [t754] VALUES (4459,'يقول لي الطبيب أكلت شيئا ... ودائك في شرابك والطعام','337','3764','0');
INSERT INTO [t754] VALUES (4460,'وما في طبه أني جواد ... أضر بجسمه طول الجمام','337','3764','0');
INSERT INTO [t754] VALUES (4461,'تعود أن يغبر في السرايا ... ويدخل من قتام في قتام','337','3764','0');
INSERT INTO [t754] VALUES (4462,'فأمسك لا يطال له فيرعى ... ولا هو في العليق ولا اللجام','337','3764','0');
INSERT INTO [t754] VALUES (4463,'فإن أمرض فما مرض اصطباري ... وإن أحمم فما حم اعتزامي','337','3764','0');
INSERT INTO [t754] VALUES (4464,'وإن اسلم فما أبقى ولكن ... سلمت من الحمام إلى الحمام','337','3764','0');
INSERT INTO [t754] VALUES (4465,'تمتع من سهاد أو رقاد ... ولا تأمل كرى تحت الرجام','337','3764','0');
INSERT INTO [t754] VALUES (4466,'فإن لثالث الحالين معنى ... سوى معنى انتباهك والمنام','337','3764','0');
INSERT INTO [t754] VALUES (4467,'منى كن لي أن البياض خضاب ... فيخفى بتبييض القرون شباب','338','3764','0');
INSERT INTO [t754] VALUES (4468,'ليالي عند البيض فوادي فتنة ... وفخر وذاك الفخر عندي عاب','338','3764','0');
INSERT INTO [t754] VALUES (4469,'فكيف أذم اليوم ما كنت أشتهي ... وأدعو بما أشكوه حين أجاب','338','3764','0');
INSERT INTO [t754] VALUES (4470,'جلا اللون عن لون هدى كل مسلك ... كما انجاب عن ضوء النهار ضباب','338','3764','0');
INSERT INTO [t754] VALUES (4471,'وفي الجسم نفس لا تشيب بشيبه ... ولو أو أن ما الوجه منه حراب','338','3764','0');
INSERT INTO [t754] VALUES (4472,'لها ظفر إن كل ظفر أعده ... وناب إذا لم يبق في الفم ناب','338','3764','0');
INSERT INTO [t754] VALUES (4473,'يغير مني الدهر ما شاء غيرها ... وأبلغ أقصى العمر وهي كعاب','338','3764','0');
INSERT INTO [t754] VALUES (4474,'وإني لنجم يهتدي صحبتي به ... إذا حال من دون النجوم سحاب','338','3764','0');
INSERT INTO [t754] VALUES (4475,'غني عن الأوطان لا يستفزني ... إلى بلد سافرت عنه إياب','338','3764','0');
INSERT INTO [t754] VALUES (4476,'وعن ذملان العيس إن سامحت به ... وإلا ففي أكوارهن عقاب','338','3764','0');
INSERT INTO [t754] VALUES (4477,'وأصدى فلا أبدى إلى الماء حاجة ... وللشمس فوق اليعملات لعاب','338','3764','0');
INSERT INTO [t754] VALUES (4478,'وللسر مني موضع لا يناله ... نديم لا يفضي إليه شراب','338','3764','0');
INSERT INTO [t754] VALUES (4479,'وللخود مني ساعة ثم بيننا ... فلاة إلى غير اللقاء تجاب','339','3764','0');
INSERT INTO [t754] VALUES (4480,'وما العشق إلا غرة وطماعة ... يعرض قلب نفسه فتصاب','339','3764','0');
INSERT INTO [t754] VALUES (4481,'وغير فؤادي للغواني رمية ... وغير بناني للزجاج ركاب','339','3764','0');
INSERT INTO [t754] VALUES (4482,'تركنا لأطراف القنا كل شهوة ... فليس لنا إلا بهن لعاب','339','3764','0');
INSERT INTO [t754] VALUES (4483,'نصرفه للطعن فوق حوادر ... قد انقصفت فيهن منه كعاب','339','3764','0');
INSERT INTO [t754] VALUES (4484,'أعز مكان في الدنى سرج سابح ... وخير جليس في الزمان كتاب','339','3764','0');
INSERT INTO [t754] VALUES (4485,'وبحر أبو المسك الخضم الذي له ... على كل بحر زخرة وعباب','339','3764','0');
INSERT INTO [t754] VALUES (4486,'تجاوز قدر المدح حتى كأنه ... بأحسن ما يثني عليه يعاب','339','3764','0');
INSERT INTO [t754] VALUES (4487,'وغالبه الأعداء ثم عنوا له ... كما غالبت بيض السيوف رقاب','339','3764','0');
INSERT INTO [t754] VALUES (4488,'وأكثر ما تلقى أبا المسك بذلة ... إذا لم يصن إلا الحديد ثياب','339','3764','0');
INSERT INTO [t754] VALUES (4489,'وأوسع ما تلقاه صدرا وخلفه ... رماء وطعن والأمام ضراب','340','3764','0');
INSERT INTO [t754] VALUES (4490,'وأنفذ ما تلقاه حكما إذا قضى ... قضاء ملوك الأرض منه غضاب','340','3764','0');
INSERT INTO [t754] VALUES (4491,'يقول إليه طاعة الناس فضله ... ولو لم يقدها نائل وعقاب','340','3764','0');
INSERT INTO [t754] VALUES (4492,'أيا أسدا في جسمه روح ضيغم ... وكم أسد أرواحهن كلاب','340','3764','0');
INSERT INTO [t754] VALUES (4493,'ويا آخذا من دهره حق نفسه ... ومثلك يعطى حقه ويهاب','340','3764','0');
INSERT INTO [t754] VALUES (4494,'لنا عند هذا الدهر حق يلطه ... وقد قل إعتاب وطال عتاب','340','3764','0');
INSERT INTO [t754] VALUES (4495,'وقد تحدث الأيام عندك شيمة ... وتنغمر الأوقات وهي يباب','340','3764','0');
INSERT INTO [t754] VALUES (4496,'ولا ملك إلا أنت والملك فضلة ... كأنك سيف فيه وهو قراب','340','3764','0');
INSERT INTO [t754] VALUES (4497,'أرى لي بقربي منك عينا قريرة ... وإن كان قربا بالبعاد يشاب','341','3764','0');
INSERT INTO [t754] VALUES (4498,'وهل نافعي أن ترفع الحجب بيننا ... ودون الذي أملت منك حجاب','341','3764','0');
INSERT INTO [t754] VALUES (4499,'أقل سلامي حب ما خف عنكم ... وأسكت كيما لا يكون جواب','341','3764','0');
INSERT INTO [t754] VALUES (4500,'وفي النفس حاجات وفيك فطانة ... سكوتي بيان عندها وخطاب','341','3764','0');
INSERT INTO [t754] VALUES (4501,'وما أنا بالباغي على الحب رشوة ... ضعيف هوى يبغي عليه ثواب','341','3764','0');
INSERT INTO [t754] VALUES (4502,'وما شئت إلا أن أدل عواذلي ... على أن رأيي في هواك صواب','341','3764','0');
INSERT INTO [t754] VALUES (4503,'وأعلم قوما خالفوني فشرقوا ... وغربت أني قد ظفرت وخابوا','341','3764','0');
INSERT INTO [t754] VALUES (4504,'جرى الخلف إلا فيك أنك واحد ... وأنك ليث والملوك ذئاب','341','3764','0');
INSERT INTO [t754] VALUES (4505,'وأنك إن قويست صحف قارىء ... ذئابا ولم يخطىء فقال ذباب','341','3764','0');
INSERT INTO [t754] VALUES (4506,'وأن مديح الناس حق وباطل ... ومدحك حق ليس فيه كذاب','341','3764','0');
INSERT INTO [t754] VALUES (4507,'إذا نلت منك الود فالمال هين ... وكل الذي فوق التراب تراب','341','3764','0');
INSERT INTO [t754] VALUES (4508,'وما كنت لولا أنت إلا مهاجرا ... له كل يوم بلدة وصحاب','341','3764','0');
INSERT INTO [t754] VALUES (4509,'ولكنك الدنيا إلي حبيبة ... فما عنك لي إلا إليك ذهاب','341','3764','0');
INSERT INTO [t754] VALUES (4510,'من أية الطرق يأتي نحوك الكرم ... أين المحاجم يا كافور والجلم','341','3764','0');
INSERT INTO [t754] VALUES (4511,'جاز الألي ملكت كفاك قدرهم ... فعرفوا بك أن الكلب فوقهم','341','3764','0');
INSERT INTO [t754] VALUES (4512,'لا شيء أقبح من فحل له ذكر ... تقوده أمة ليست لها رحم','341','3764','0');
INSERT INTO [t754] VALUES (4513,'سادات كل أناس من نفوسهم ... وسادة المسلمين الأعبد القزم','342','3764','0');
INSERT INTO [t754] VALUES (4514,'أغاية الدين أن تحفوا شواربكم ... يا أمة ضحكت من جهلها الأمم','342','3764','0');
INSERT INTO [t754] VALUES (4515,'ألا فتى يورد الهندي هامته ... كيما تزول شكوك الناس والتهم','342','3764','0');
INSERT INTO [t754] VALUES (4516,'فإنه حجة يوذي القلوب بها ... من دينه الدهر والتعطيل والقدم','342','3764','0');
INSERT INTO [t754] VALUES (4517,'ما أقدر الله أن يخزي خليفته ... ولا يصدق قوما في الذي زعموا','342','3764','0');
INSERT INTO [t754] VALUES (4518,'أما في هذه الدنيا كريم ... تزول به عن القلب الهموم','342','3764','0');
INSERT INTO [t754] VALUES (4519,'أما في هذه الدنيا مكان ... يسر بأهله الجار المقيم','342','3764','0');
INSERT INTO [t754] VALUES (4520,'تشابهت البهائم والعبدي ... علينا والموالي والصميم','342','3764','0');
INSERT INTO [t754] VALUES (4521,'وما أدري إذا داء حديث ... أصاب الناس أم داء قديم','342','3764','0');
INSERT INTO [t754] VALUES (4522,'حصلت بأرض مصر على عبيد ... كأن الحر بينهم يتيم','342','3764','0');
INSERT INTO [t754] VALUES (4523,'كأن الأسود اللابي فيهم ... غراب حوله رخم وبوم','342','3764','0');
INSERT INTO [t754] VALUES (4524,'أخذت بمدحه فرأيت لهوا ... مقالي للأحيمق يا حليم','342','3764','0');
INSERT INTO [t754] VALUES (4525,'ولما أن هجوت رأيت عيا ... مقالي لابن آوى يا لئيم','342','3764','0');
INSERT INTO [t754] VALUES (4526,'فهل من عاذر في ذا وفي ذا ... فمدفوع إلى السقم السقيم','342','3764','0');
INSERT INTO [t754] VALUES (4527,'إذا أنت الإساءة من وضيع ... ولم ألم المسيء فيمن ألوم','342','3764','0');
INSERT INTO [t754] VALUES (4528,'لو كان ذا الآكل أزوادنا ... ضيفا لأوسعناه إحسانا','343','3764','0');
INSERT INTO [t754] VALUES (4529,'لكننا في العين أضيافه ... يوسعنا زورا وبهتانا','343','3764','0');
INSERT INTO [t754] VALUES (4530,'فليته خلى لنا طرقنا ... أعانه الله وإيانا','343','3764','0');
INSERT INTO [t754] VALUES (4531,'أتحلف لا تكلفني مسيرا ... إلى بلد أحاول منه مالا','343','3764','0');
INSERT INTO [t754] VALUES (4532,'وأنت مكلفي أنبي مكانا ... وأبعد شقة وأشد حالا','343','3764','0');
INSERT INTO [t754] VALUES (4533,'إذا سرنا عن الفسطاط يوما ... فلقني الفوارس والرجالا','343','3764','0');
INSERT INTO [t754] VALUES (4534,'لتعلم قدر ما فارقت مني ... وأنك رمت من ضيمي محالا','343','3764','0');
INSERT INTO [t754] VALUES (4535,'عيد بأية حال عدت يا عيد ... بما مضى أم بأمر فيك تجديد','343','3764','0');
INSERT INTO [t754] VALUES (4536,'أما الأحبة فالبيداء دونهم ... فليت دونك بيدا دونها بيد','343','3764','0');
INSERT INTO [t754] VALUES (4537,'لولا العلي لم تجب بي ما أجوب بها ... وجناء حرف ولا جرداء قيدود','343','3764','0');
INSERT INTO [t754] VALUES (4538,'وكان أطيب من سيفي مضاجعة ... أشباه رونقه الغيد الأماليد','343','3764','0');
INSERT INTO [t754] VALUES (4539,'لم يترك الدهر من قلبي ولا كبدي ... شيئا تتيمه عين ولا جيد','343','3764','0');
INSERT INTO [t754] VALUES (4540,'يا ساقيي أخمر في كؤوسكما ... أم في كؤسكما هم وتسهيد','343','3764','0');
INSERT INTO [t754] VALUES (4541,'أصخرة أنا ما لي لا تحركني ... هذي المدام ولا هذي الأغاريد','343','3764','0');
INSERT INTO [t754] VALUES (4542,'إذا أردت كميت اللون صافية ... وجدتها وحبيب النفس مفقود','344','3764','0');
INSERT INTO [t754] VALUES (4543,'ماذا لقيت من الدنيا وأعجبها ... أنى بما أنا باك منه محسود','344','3764','0');
INSERT INTO [t754] VALUES (4544,'أمسيت أروح مثر خازنا ويدا ... أنا الغني وأموالي المواعيد','344','3764','0');
INSERT INTO [t754] VALUES (4545,'ني نزلت بكذابين ضيفهم ... عن القرى وعن الترحال محدود','344','3764','0');
INSERT INTO [t754] VALUES (4546,'جود الرجال من الأيدي وجودهم ... من اللسان فلا كانوا ولا الجود','344','3764','0');
INSERT INTO [t754] VALUES (4547,'ما يقبض الموت نفسا من نفوسهم ... إلا وفي يده من نتنها عود','344','3764','0');
INSERT INTO [t754] VALUES (4548,'من كل رخو وكاء البطن منفتق ... لا في الرجال ولا النسوان معدود','344','3764','0');
INSERT INTO [t754] VALUES (4549,'أكلما اغتال بعد السوء سيده ... أو خانه فله في مصر تمهيد','344','3764','0');
INSERT INTO [t754] VALUES (4550,'صار الخصي إمام الآبقين بها ... فالحر مستبعد والعبد معبود','344','3764','0');
INSERT INTO [t754] VALUES (4551,'نامت نواطير مصر عن ثعالبها ... فقد بشمن وما تفنى العناقيد','344','3764','0');
INSERT INTO [t754] VALUES (4552,'العبد ليس لحر صالح بأخ ... لو أنه في ثياب الحر مولود','344','3764','0');
INSERT INTO [t754] VALUES (4553,'لا تشتر العبد إلا والعصا معه ... إن العبيد لأنجاس مناكيد','344','3764','0');
INSERT INTO [t754] VALUES (4554,'ما كنت أحسبني أحيا إلى زمن ... يسيء بي فهو كلب وهو محمود','344','3764','0');
INSERT INTO [t754] VALUES (4555,'ولا توهمت أن الناس قد فقدوا ... وأن مثل أبي البيضاء موجود','344','3764','0');
INSERT INTO [t754] VALUES (4556,'وأن ذا الأسود المثقوب مشفره ... تطيعه ذي العضاريط الرعاديد','345','3764','0');
INSERT INTO [t754] VALUES (4557,'جوعان يأكل من زادي ويمسكني ... لكي يقال عظيم القدر مقصود','345','3764','0');
INSERT INTO [t754] VALUES (4558,'إن امرأ أمة حبلى تدبره ... لمستضام سخين العين مفؤود','345','3764','0');
INSERT INTO [t754] VALUES (4559,'ويلمها خطة ويلم قابلها ... لمثلها خلق المهرية القود','345','3764','0');
INSERT INTO [t754] VALUES (4560,'وعندها لذ طعم الموت شاربه ... إن المنية عند الذل قنديد','345','3764','0');
INSERT INTO [t754] VALUES (4561,'من علم الأسود والمخصي مكرمة ... أقومه البيض أم آباؤك الصيد','345','3764','0');
INSERT INTO [t754] VALUES (4562,'أم أذنه في يد النخاس دامية ... أم قدره وهو بالفلسين مردود','345','3764','0');
INSERT INTO [t754] VALUES (4563,'أولى اللئام كويفير بمعذرة ... في كل لؤم وبعض العذر تنفيد','345','3764','0');
INSERT INTO [t754] VALUES (4564,'وذاك أن الفحول البيض عاجوة ... عن الجميل فكيف الخصية السود','345','3764','0');
INSERT INTO [t754] VALUES (4565,'جزى عربا أمست ببلبيس ربها ... بمسعاتها تقرر بذاك عيونها','345','3764','0');
INSERT INTO [t754] VALUES (4566,'كراكر من قيس بن عيلان ساهرا ... جفون ظبائها للعلي وجفونها','345','3764','0');
INSERT INTO [t754] VALUES (4567,'وخص به عبد العزيز بن يوسف ... فما هو إلا غيثها ومعينها','345','3764','0');
INSERT INTO [t754] VALUES (4568,'فتى زان في عيني أقصى قبيلة ... وكم سيد في حلة لا يزينها','345','3764','0');
INSERT INTO [t754] VALUES (4569,'وإن تك طيىء كانت لئاما ... فألأمها ربيعة أو بنوه','345','3764','0');
INSERT INTO [t754] VALUES (4570,'وإن تكن طيىء كانت كراما ... فوردان لغيرهم أبوه','345','3764','0');
INSERT INTO [t754] VALUES (4571,'مررنا منه في حسمي بعبد ... يمج اللؤم منخره وفوه','345','3764','0');
INSERT INTO [t754] VALUES (4572,'أشد بعرسه عني عبيدي ... فأتلفهم ومالي أتلفوه','345','3764','0');
INSERT INTO [t754] VALUES (4573,'فإن شقيت بأيديهم جيادي ... لقد شقيت بمنصلي الوجوه','346','3764','0');
INSERT INTO [t754] VALUES (4574,'لحى الله وردانا وأما أتت به ... له كسب خنزير وخرطوم ثعلب','346','3764','0');
INSERT INTO [t754] VALUES (4575,'فما كان منه الغدر إلا دلالة ... على أنه فيه من الأم والأب','346','3764','0');
INSERT INTO [t754] VALUES (4576,'إذا كسب الإنسان من هن عرسه ... فيا لؤم إنسان ويا لؤم مكسب','346','3764','0');
INSERT INTO [t754] VALUES (4577,'أهذا اللذيا بنت وردان بنته ... هما الطالبان الرزق من شر مطلب','346','3764','0');
INSERT INTO [t754] VALUES (4578,'لقد كنت أنفي الغدر عن توس طيء ... فلا تعذلاني رب صدق مكذب','346','3764','0');
INSERT INTO [t754] VALUES (4579,'أعددت للغادرين أسيافا ... أجدع منهم بهن آنافا','346','3764','0');
INSERT INTO [t754] VALUES (4580,'لا يرحم الله أرؤسا لهم ... أطرن عن هامهن أقحافا','346','3764','0');
INSERT INTO [t754] VALUES (4581,'ما ينقم السيف غير قلتهم ... وأن تكون المئون آلافا','346','3764','0');
INSERT INTO [t754] VALUES (4582,'يا شر لحم فجعته بدم ... وزار للخامعات أجوافا','346','3764','0');
INSERT INTO [t754] VALUES (4583,'قد كنت أغنيت عن سؤالك بي ... من زجر الطير لي ومن عافا','346','3764','0');
INSERT INTO [t754] VALUES (4584,'وعدت ذا النصل من تعرضه ... وخفت لما اعترضت إخلافا','346','3764','0');
INSERT INTO [t754] VALUES (4585,'لايذكر الخير أن ذكرت ولا ... تتبعك المقلتان توكافا','346','3764','0');
INSERT INTO [t754] VALUES (4586,'إذا امرؤ راعني بغدرته ... أوردته الغاية التي خافا','346','3764','0');
INSERT INTO [t754] VALUES (4587,'بسيطة مهلا سقيت القطارا ... تركت عيون عبيدي حيارى','346','3764','0');
INSERT INTO [t754] VALUES (4588,'فظنوا النعام عليك النخيل ... فظنوا الصوار عليك المنارا','346','3764','0');
INSERT INTO [t754] VALUES (4589,'فأمسك صحبي بأكوراهم ... وقد قصد الضحك فيهم وجارا','346','3764','0');
INSERT INTO [t754] VALUES (4590,'ألا كل ماشية الخيزلي ... فدى كل ماشية الهيدبا','347','3764','0');
INSERT INTO [t754] VALUES (4591,'وكل نجاة بجاوية ... خنوف وما بي حسن المشا','347','3764','0');
INSERT INTO [t754] VALUES (4592,'ولكنهن حبال الحيوة ... وكيد العداة وميط الأذا','347','3764','0');
INSERT INTO [t754] VALUES (4593,'ضربت بها التيه ضرب القما ... ر إما لهذا وإما لذا','347','3764','0');
INSERT INTO [t754] VALUES (4594,'إذا فزعت قدمتها الجياد ... وبيض السيوف وسمر القنا','347','3764','0');
INSERT INTO [t754] VALUES (4595,'فمرت بنخل وفي ركبها ... عن العالمين وعنه غنى','347','3764','0');
INSERT INTO [t754] VALUES (4596,'وأمست تخيرنا بالنقا ... ب وأدى المياه ووادي القرى','347','3764','0');
INSERT INTO [t754] VALUES (4597,'وقلنا لها أين أرض العراق ... فقالت ونحن بتربان ها','347','3764','0');
INSERT INTO [t754] VALUES (4598,'وهبت بخسمي هبوب الدبو ... ر مستقبلات مهب الصبا','347','3764','0');
INSERT INTO [t754] VALUES (4599,'روامي الكفاف وكبد الوهاد ... وجار البويرة وادي الغضا','347','3764','0');
INSERT INTO [t754] VALUES (4600,'وجابت بسيطة جوب الردا ... ء بين النعام وبين المها','347','3764','0');
INSERT INTO [t754] VALUES (4601,'إلى عقدة الجوف حتى شفت ... بماء الجراوي بعض الصدى','347','3764','0');
INSERT INTO [t754] VALUES (4602,'ولاح لها صور والصباح ... ولا الشغور لها والضحى','348','3764','0');
INSERT INTO [t754] VALUES (4603,'ومسى الجميعي دئداؤها ... وعادى الأضارع ثم الدنا','348','3764','0');
INSERT INTO [t754] VALUES (4604,'فيا لك ليلا على أعكش ... أحم البلاد خفي الصوى','348','3764','0');
INSERT INTO [t754] VALUES (4605,'وردنا الرهيمة في جوزه ... وباقيه أكثر مما مضى','348','3764','0');
INSERT INTO [t754] VALUES (4606,'فلما أنخنا ركزنا الرما ... ح فوق مكارمنا والعلي','348','3764','0');
INSERT INTO [t754] VALUES (4607,'وبتنا نقبل أسيافنا ... ونمسحها من دماء العدى','348','3764','0');
INSERT INTO [t754] VALUES (4608,'لتعلم مصر ومن العراق ... ومن بالعواصم أني الفتى','348','3764','0');
INSERT INTO [t754] VALUES (4609,'وأني وفيت وأني أبيت ... وأني عتوت على من عتا','348','3764','0');
INSERT INTO [t754] VALUES (4610,'وما كل من قال قولا وفى ... وما كل من سيم خسفا أبي','348','3764','0');
INSERT INTO [t754] VALUES (4611,'ومن يكن قلب كقلبي له ... يشق إلى العز قلب التوى','348','3764','0');
INSERT INTO [t754] VALUES (4612,'ولا بد للقلب من آلة ... ورأي يصدع صم الصفا','348','3764','0');
INSERT INTO [t754] VALUES (4613,'وكل طريق أتاه الفتى ... على قدر الرجل فيه الخطا','348','3764','0');
INSERT INTO [t754] VALUES (4614,'ونام الخويدم عن ليلنا ... وقد نام قبل عمى لا كرى','348','3764','0');
INSERT INTO [t754] VALUES (4615,'وكان على قربنا بيننا ... مهامه من جهله والغبي','348','3764','0');
INSERT INTO [t754] VALUES (4616,'لقد كنت أحسب قبل الخص ... ي أن الرؤوس مقر النهي','348','3764','0');
INSERT INTO [t754] VALUES (4617,'ولما نظرت إلى عقله ... رأيت النهي كلها في الخصي','348','3764','0');
INSERT INTO [t754] VALUES (4618,'وماذا بمصر من المضحكات ... ولكنه ضحك كالبكا','348','3764','0');
INSERT INTO [t754] VALUES (4619,'بها نبطي من أهل السواد ... يدرس أنساب أهل الفلا','348','3764','0');
INSERT INTO [t754] VALUES (4620,'وأسود مشفره نصفه ... يقال أنه أنت بدر الدجى','349','3764','0');
INSERT INTO [t754] VALUES (4621,'وشعر مدحت به الكركد ... ن بين القريض وبين الرقى','349','3764','0');
INSERT INTO [t754] VALUES (4622,'فما كان ذلك مدحا له ... ولكنه كان هجو الورى','349','3764','0');
INSERT INTO [t754] VALUES (4623,'وقد ضل قوم بأصنامهم ... وأما بزق رياح فلا','349','3764','0');
INSERT INTO [t754] VALUES (4624,'وتلك صموت وذا ناطق ... إذا حركوه فسا أو هذى','349','3764','0');
INSERT INTO [t754] VALUES (4625,'ومن جهلت نفسه قدره ... رأى غيره منه ما لا يرى','349','3764','0');
INSERT INTO [t754] VALUES (4626,'وأسود أما القلب منه فضيق ... نخيب وأما بطنه فرحيب','349','3764','0');
INSERT INTO [t754] VALUES (4627,'يموت به غيظا على الدهر أهله ... كما مات غيظا فاتك وشبيب','349','3764','0');
INSERT INTO [t754] VALUES (4628,'أعدت على مخصاه ثم تركته ... يتبع مني الشمس وهي تغيب','349','3764','0');
INSERT INTO [t754] VALUES (4629,'إذا ما عدمت الأصل والعقل والندى ... فما لحيوة في جنابك طيب','349','3764','0');
INSERT INTO [t754] VALUES (4630,'لا خيل عندك تهديها ولا مال ... فليسعد النطق إن لم يسعد الحال','349','3764','0');
INSERT INTO [t754] VALUES (4631,'وأجز الأمير الذي نعماه فاجئة ... بغير قول ونعمى الناس أقوال','349','3764','0');
INSERT INTO [t754] VALUES (4632,'وربما جزت الإحسان موليه ... خريدة من عذارى الحي مكسال','349','3764','0');
INSERT INTO [t754] VALUES (4633,'وإن تكن محكمات الشكل تمنعني ... ظهور جري فلي فيهن تصهال','349','3764','0');
INSERT INTO [t754] VALUES (4634,'وما شكرت لأن المال فرحني ... سيان عندي إكثار وإقلال','350','3764','0');
INSERT INTO [t754] VALUES (4635,'لكن رأيت قبيحا أني جاد لنا ... وأننا بقضاء الحق بخال','350','3764','0');
INSERT INTO [t754] VALUES (4636,'فكنت منبت روض الحزن باكره ... غيث بغير سباخ الأرض هطال','350','3764','0');
INSERT INTO [t754] VALUES (4637,'غيث يبين للنظار موقعه ... أن الغيوث بما تأتيه جهال','350','3764','0');
INSERT INTO [t754] VALUES (4638,'لا يدرك المجد إلا سيد فطن ... لما يشق على السادات فعال','350','3764','0');
INSERT INTO [t754] VALUES (4639,'لا وارث جهلت يمناه ما وهبت ... ولا كسوب بغير السيف سأال','350','3764','0');
INSERT INTO [t754] VALUES (4640,'قال الزمان له قولا فأفهمه ... إن الزمان على الإمساك عذال','350','3764','0');
INSERT INTO [t754] VALUES (4641,'تدري القناة إذا اهتزت براحته ... أن الشقي بها خيل وأبطال','350','3764','0');
INSERT INTO [t754] VALUES (4642,'كفاتك ودخول الكاف منقصة ... كالشمس قلت وما للشمس أمثال','350','3764','0');
INSERT INTO [t754] VALUES (4643,'القائد الأسد غذتها براثنه ... بمثلها من عداه وهي أشبال','350','3764','0');
INSERT INTO [t754] VALUES (4644,'القاتل السيف في جسم القتيل به ... وللسيوف كما للناس آجال','350','3764','0');
INSERT INTO [t754] VALUES (4645,'تغير عنه على الغارات هيبته ... وماله بأقاصي الأرض أهمال','350','3764','0');
INSERT INTO [t754] VALUES (4646,'له من الوحش ما اختارت أسنته ... عير وهيق وخنساء وذيال','350','3764','0');
INSERT INTO [t754] VALUES (4647,'تمسى الضيوف مشهاة بعقوته ... كأن أوقاتها في الطيب آصال','350','3764','0');
INSERT INTO [t754] VALUES (4648,'لو أشتهت لحم قاريها لبادرها ... خراذل منه في الشيزي وأوصال','351','3764','0');
INSERT INTO [t754] VALUES (4649,'لا يعرف الرزء في مال ولا ولد ... إلا إذا حفز الأضياف ترحال','351','3764','0');
INSERT INTO [t754] VALUES (4650,'يروي صدى الأرض من فضلات ما شربوا ... محض اللقاح وصافي اللون سلسال','351','3764','0');
INSERT INTO [t754] VALUES (4651,'تقرى صوارمه الساعات عبط دم ... كأنما الساع قفال ونزال','351','3764','0');
INSERT INTO [t754] VALUES (4652,'تجري النفوس حواليه مخلطة ... منها عداة وأغنام وآبال','351','3764','0');
INSERT INTO [t754] VALUES (4653,'لا يحرم البعد أهل البعد نائله ... وغير عاجزة عنه الأطيفال','351','3764','0');
INSERT INTO [t754] VALUES (4654,'أمضى الفريقين في اقرانه ظبة ... والبيض هادية والسمر ضلال','351','3764','0');
INSERT INTO [t754] VALUES (4655,'يريك مخبره أضعاف منظره ... بين الرجال وفيها الماء والآل','351','3764','0');
INSERT INTO [t754] VALUES (4656,'وقد يلقبه المجنون حاسده ... إذا اختلطن وبعض العقل عقال','351','3764','0');
INSERT INTO [t754] VALUES (4657,'يرمي بها الجيش لا بد له ولها ... من شقه ولو أن الجيش أجبال','351','3764','0');
INSERT INTO [t754] VALUES (4658,'إذا العدى نشبت فيهم مخالبه ... لم يجتمع لهم حلم ورئبال','351','3764','0');
INSERT INTO [t754] VALUES (4659,'يروعهم منه دهر صرفه أبدا ... مجاهر وصروف الدهر تغتال','351','3764','0');
INSERT INTO [t754] VALUES (4660,'أناله الشرف الأعلى تقدمه ... فما الذي بتوقي ما أتى نالوا','352','3764','0');
INSERT INTO [t754] VALUES (4661,'إذا الملوك تحلت كان حليته ... مهند وأصم الكعب عسال','352','3764','0');
INSERT INTO [t754] VALUES (4662,'أبو شجاع أبو الشجعان قاطبة ... هول نمته من الهيجاء أهوال','352','3764','0');
INSERT INTO [t754] VALUES (4663,'تملك الحمد حتى ما لمفتخر ... في الحمد حاء ولا ميم ولا دال','352','3764','0');
INSERT INTO [t754] VALUES (4664,'عليه منه سرابيل مضاعفة ... وقد كفاه من الماذي سربال','352','3764','0');
INSERT INTO [t754] VALUES (4665,'وكيف أستر ما أوليت من حسن ... وقد غمرت نوالا أيها النال','352','3764','0');
INSERT INTO [t754] VALUES (4666,'لطفت رأيك في بري وتكرمتي ... أن الكريم على العلياء يحتال','352','3764','0');
INSERT INTO [t754] VALUES (4667,'حتى غدوت وللأخبار تجوال ... وللكواكب في كفيك آمال','352','3764','0');
INSERT INTO [t754] VALUES (4668,'وقد أطال ثنائي طول لابسه ... إن الثناء على التنبال تنبال','352','3764','0');
INSERT INTO [t754] VALUES (4669,'إن كنت تكبر أن تختال في بشر ... فإن قدرك في الأقدار يختال','352','3764','0');
INSERT INTO [t754] VALUES (4670,'كأن نفسك لا ترضاك صاحبها ... إلا وأنت على المفضال مفضال','352','3764','0');
INSERT INTO [t754] VALUES (4671,'ولا تعدك صوانا لمهجتها ... إلا وأنت لها في الروع بذال','352','3764','0');
INSERT INTO [t754] VALUES (4672,'لولا المشقة ساد الناس كلهم ... ألجود يفقر والإقدام قتال','352','3764','0');
INSERT INTO [t754] VALUES (4673,'وإنما يبلغ الإنسان طاقته ... ما كل ماشية بالرحل شملال','352','3764','0');
INSERT INTO [t754] VALUES (4674,'إنا لفي زمن ترك القبيح به ... من أكثر الناس إحسان وأجمال','352','3764','0');
INSERT INTO [t754] VALUES (4675,'ذكر الفتى عمره الثاني وحاجته ... ما قاته وفضول العيش أشغال','352','3764','0');
INSERT INTO [t754] VALUES (4676,'الحزن يقلق والتجمل يردع ... والدمع بينهما عصي طيع','352','3764','0');
INSERT INTO [t754] VALUES (4677,'يتنازعان دموع عين مسهد ... لهذا يجيء بها ولهذا يرجع','353','3764','0');
INSERT INTO [t754] VALUES (4678,'النوم بعد أبي شجاع نافر ... والليل معي والكواكب طلع','353','3764','0');
INSERT INTO [t754] VALUES (4679,'إني لأجبن عن فارق أحبتي ... وتحس نفسي بالحمام فأشجع','353','3764','0');
INSERT INTO [t754] VALUES (4680,'ويزيدني غضب الأعادي قسوة ... ويلم بي عتب الصديق فأجزع','353','3764','0');
INSERT INTO [t754] VALUES (4681,'تصفو الحيوة لجاهل أو غافل ... عما مضى فيها وما يتوقع','353','3764','0');
INSERT INTO [t754] VALUES (4682,'ولمن يغالط في الحقائق نفسه ... ويسومها طلب المحال فتطمع','353','3764','0');
INSERT INTO [t754] VALUES (4683,'أين الذي الهرمان من بنيانه ... ما قومه ما يومه ما المصرع','353','3764','0');
INSERT INTO [t754] VALUES (4684,'تتخلف الآثار عن أصحابها ... حينا ويدركها الفناء فتتبع','353','3764','0');
INSERT INTO [t754] VALUES (4685,'لم يرض قلب أبي شجاع مبلغ ... قبل الممات ولم يسعه موضع','353','3764','0');
INSERT INTO [t754] VALUES (4686,'كنا نظن دياره مملوؤة ... ذهبا فمات وكل دار بلقع','353','3764','0');
INSERT INTO [t754] VALUES (4687,'وإذا المكارم والصوارم والقنا ... وبنات أعوج كل شيء يجمع','353','3764','0');
INSERT INTO [t754] VALUES (4688,'المجد أخسر والمكارم صفقة ... من أن يعيش لها الكريم الأروع','353','3764','0');
INSERT INTO [t754] VALUES (4689,'والناس أنزل في زمانك منزلا ... من أن تعايشهم وقدرك أرفع','353','3764','0');
INSERT INTO [t754] VALUES (4690,'برد حشاي إن استطعت بلفظة ... فلقد تضر إذا تشاء وتنفع','354','3764','0');
INSERT INTO [t754] VALUES (4691,'ما كان منك إلى خليل قبلها ... ما يستراب به ولا ما يوجع','354','3764','0');
INSERT INTO [t754] VALUES (4692,'ولقد أراك وما تلم ملمة ... إلا نفاها عنك قلب أصمع','354','3764','0');
INSERT INTO [t754] VALUES (4693,'ويد كأن نوالها وقتالها ... فرض يحق عليك وهو تبرع','354','3764','0');
INSERT INTO [t754] VALUES (4694,'يا من يبدل كل وقت حلة ... أني رضيت بحلة لا تنزع','354','3764','0');
INSERT INTO [t754] VALUES (4695,'ما زلت تخلعها على من شاءها ... حتى لبست اليوم ما لا تخلع','354','3764','0');
INSERT INTO [t754] VALUES (4696,'ما زلت تدفع كل أمر فادح ... حتى أتى الأمر الذي لا يدفع','354','3764','0');
INSERT INTO [t754] VALUES (4697,'فظللت تنظر لا رماحك شرع ... فيما عراك ولا سيوفك قطع','354','3764','0');
INSERT INTO [t754] VALUES (4698,'بأبي الوحيد وجيشه متكاثر ... يبكي ومن شر السلاح الأدمع','354','3764','0');
INSERT INTO [t754] VALUES (4699,'ومنيت إليك يد سواء عندها ... الباز الأشهب والغراب الأبقع','354','3764','0');
INSERT INTO [t754] VALUES (4700,'من للمحافل والجحافل والسري ... فقدت بفقدك نيرا لا يطلع','354','3764','0');
INSERT INTO [t754] VALUES (4701,'ومن اتخذت على الضيوف خليفة ... ضاعوا ومثلك لا يكاد يضيع','354','3764','0');
INSERT INTO [t754] VALUES (4702,'قبحا لوجهك يا زمان فإنه ... وجه له من كل قبح برقع','354','3764','0');
INSERT INTO [t754] VALUES (4703,'أيموت مثل أبي شجاع فاتك ... ويعيش حاسده الخصي الأوكع','354','3764','0');
INSERT INTO [t754] VALUES (4704,'أيد مقطعة حوالي رأسه ... وقفا يصيح بها ألا من يصفع','354','3764','0');
INSERT INTO [t754] VALUES (4705,'أبقيت أكذب كاذب أبقيته ... وأخذت أصدق من يقول ويسمع','354','3764','0');
INSERT INTO [t754] VALUES (4706,'وتركت أنتن ريحة مذمومة ... وسلبت أطيب ريحة تتضوع','354','3764','0');
INSERT INTO [t754] VALUES (4707,'فليوم قر لكل وحش نافر ... دمه وكان كأنه يتطلع','354','3764','0');
INSERT INTO [t754] VALUES (4708,'وتصاحت ثمر السياط وخيله ... وأوت إليها سوقها والأذرع','355','3764','0');
INSERT INTO [t754] VALUES (4709,'وعفا الطراد فلا سنان راعف ... فوق القناة ولا حسام يلمع','355','3764','0');
INSERT INTO [t754] VALUES (4710,'ولى وكل مخالم ومنادم ... بعد اللزوم مشيع ومودع','355','3764','0');
INSERT INTO [t754] VALUES (4711,'من كان فيه لكل قوم ملجأ ... ولسيفه في كل قوم مرتع','355','3764','0');
INSERT INTO [t754] VALUES (4712,'إن حل في فرس ففيها ربها ... كسرى تذل له الرقاب وتخضع','355','3764','0');
INSERT INTO [t754] VALUES (4713,'أو حل في روم ففيها قيصر ... أو حل في عرب ففيها تبع','355','3764','0');
INSERT INTO [t754] VALUES (4714,'لا قلبت أيدي الفوارس بعده ... رمحا ولا حملت جوادا أربع','355','3764','0');
INSERT INTO [t754] VALUES (4715,'يذكرني فاتكا حمله ... وشيء من الند فيه اسمه','355','3764','0');
INSERT INTO [t754] VALUES (4716,'ولست بناس ولكنني ... يجدد لي ريحه شمه','355','3764','0');
INSERT INTO [t754] VALUES (4717,'وأي فتى سلبتني المنو ... ن لم تدر ما حملت أمه','355','3764','0');
INSERT INTO [t754] VALUES (4718,'ولا ما تضم إلى صدرها ... ولو علمت هالها ضمه','355','3764','0');
INSERT INTO [t754] VALUES (4719,'بمصر ملوك لهم ما له ... ولكنهم ما لهم همه','355','3764','0');
INSERT INTO [t754] VALUES (4720,'فأجود من جودهم بخله ... وأحمد من حمدهم ذمه','355','3764','0');
INSERT INTO [t754] VALUES (4721,'وأشرف من عيشهم موته ... وأنفع من وجدهم عدمه','355','3764','0');
INSERT INTO [t754] VALUES (4722,'وإن منيته عنده ... لكالخمر سقيه كرمه','355','3764','0');
INSERT INTO [t754] VALUES (4723,'فذاك الذي عبه ماؤه ... وذاك الذي ذاقه طعمه','355','3764','0');
INSERT INTO [t754] VALUES (4724,'ومن ضاقت الأرض عن نفسه ... حري أن يضيق بها جسمه','355','3764','0');
INSERT INTO [t754] VALUES (4725,'حتام نحن نساري النجم في الظلم ... وما سراه على خف ولا قدم','355','3764','0');
INSERT INTO [t754] VALUES (4726,'ولا يحس بأجفان يحس بها ... فقد الرقاد غريب بات لم ينم','356','3764','0');
INSERT INTO [t754] VALUES (4727,'تسود الشمس منا بيض أوجهنا ... ولا تسود بيض العذر واللمم','356','3764','0');
INSERT INTO [t754] VALUES (4728,'وكان حالهما في الحكم واحدة ... لو احتكمنا من الدنيا إلى حكم','356','3764','0');
INSERT INTO [t754] VALUES (4729,'وتنرك الماء لا ينفك من سفر ... ما سار في الغيم منه سار في الأدم','356','3764','0');
INSERT INTO [t754] VALUES (4730,'لا أبغض العيس لكني وقيت بها ... قلبي من الحزن أو جسمي من السقم','356','3764','0');
INSERT INTO [t754] VALUES (4731,'طردت من مصر أيديها بأرجلها ... حتى مرقن بنا من جوش والعلم','356','3764','0');
INSERT INTO [t754] VALUES (4732,'تبرى لهن نعام الدو مسرجة ... تعارض الجدل المرخاة باللجم','356','3764','0');
INSERT INTO [t754] VALUES (4733,'في غلمة أخطروا ارواحهم ورضوا ... بما لقين رضى الأيسار بالزلم','356','3764','0');
INSERT INTO [t754] VALUES (4734,'تبدو لنا كلما ألقوا عمائمهم ... عمائم خلقت سودا بلا لثم','356','3764','0');
INSERT INTO [t754] VALUES (4735,'بيض العوارض طعانون من لحقوا ... من الفوارس شلالون للنعم','356','3764','0');
INSERT INTO [t754] VALUES (4736,'قد بلغوا بقناهم فوق طاقته ... وليس يبلغ ما فيهم من الهمم','356','3764','0');
INSERT INTO [t754] VALUES (4737,'في الجاهلية ألا أن أنفسهم ... من طيبهن به في الأشهر الحرم','356','3764','0');
INSERT INTO [t754] VALUES (4738,'ناشوا الرماح وكانت غير ناطقة ... فعلموها صياح الطير في البهم','356','3764','0');
INSERT INTO [t754] VALUES (4739,'تخدي الركاب بنا بيضا مشافرها ... خضرا فراسنها في الرغل والينم','356','3764','0');
INSERT INTO [t754] VALUES (4740,'مكعومة بسياط القوم نضربها ... عن منبت العشب نبغي منبت الكرم','357','3764','0');
INSERT INTO [t754] VALUES (4741,'وأين منبته من بعد منبته ... أبي شجاع قريع العرب والعجم','357','3764','0');
INSERT INTO [t754] VALUES (4742,'لا فاتك آخر في مصر نقصده ... ولا له خلف في الناس كلهم','357','3764','0');
INSERT INTO [t754] VALUES (4743,'من لا تشابهه الأحياء في شيم ... أمسى تشابهه الأموات في الرمم','357','3764','0');
INSERT INTO [t754] VALUES (4744,'عدمته وكأني سرت أطلبه ... فما تريدني الدنيا على العدم','357','3764','0');
INSERT INTO [t754] VALUES (4745,'مازلت أضحك إبلي كلما نظرت ... إلى من اختضبت أخفافها بدم','357','3764','0');
INSERT INTO [t754] VALUES (4746,'أسيرها بين أصنام أشاهدها ... ولا أشاهد فيها عفة الصنم','357','3764','0');
INSERT INTO [t754] VALUES (4747,'حتى رجعت وأقلامي قوائل لي ... المجد للسيف ليس المجد للقلم','357','3764','0');
INSERT INTO [t754] VALUES (4748,'أكتب بنا أبدا بعد الكتاب به ... فإنما نحن للأسياف كالخدم','357','3764','0');
INSERT INTO [t754] VALUES (4749,'أسمعتني ودوائي ما أشرت به ... فإن غفلت فدائي قلة الفهم','357','3764','0');
INSERT INTO [t754] VALUES (4750,'من اقتضى بسوى الهندي حاجته ... أجاب كل سؤال عن هل بلم','357','3764','0');
INSERT INTO [t754] VALUES (4751,'توهم القوم أن العجز قربنا ... وفي التقرب ما يدعو إلى التهم','357','3764','0');
INSERT INTO [t754] VALUES (4752,'ولم تزل قلة الإنصاف قاطعة ... بين الرجال وإن كانوا ذوي رحم','357','3764','0');
INSERT INTO [t754] VALUES (4753,'فلا زيارة إلا أن تزورهم ... أيد نشأن مع المصقولة الخذم','358','3764','0');
INSERT INTO [t754] VALUES (4754,'من كل قاضية بالموت شفرته ... ما بين منتقم منه ومنتقم','358','3764','0');
INSERT INTO [t754] VALUES (4755,'صنا قوائمها عنهم وما وقعت ... مواقع اللؤم في الأيدي ولا الكزم','358','3764','0');
INSERT INTO [t754] VALUES (4756,'هون على بصر ما شق منظره ... فإنما يقظات العين كالحلم','358','3764','0');
INSERT INTO [t754] VALUES (4757,'ولا تشك إلى خلق فتشتمه ... شكوى الجريح إلى الغربان والرخم','358','3764','0');
INSERT INTO [t754] VALUES (4758,'وكن على حذر للناس تستره ... ولا يغرك منهم ثغر مبتسم','358','3764','0');
INSERT INTO [t754] VALUES (4759,'غاض الوفاء فما تلقاه عدة ... وأعوز الصدق في الأخبار والقسم','358','3764','0');
INSERT INTO [t754] VALUES (4760,'سبحان خالق نفسي كيف لذتها ... فيما النفوس تراه غاية الألم','358','3764','0');
INSERT INTO [t754] VALUES (4761,'الدهر يعجب من حلمي نوائبه ... وصبر جسمي على أحداثه الحطم','358','3764','0');
INSERT INTO [t754] VALUES (4762,'وقت يضيع وعمر ليت مدته ... في غير أمته من سالف الأمم','358','3764','0');
INSERT INTO [t754] VALUES (4763,'أتى الزمان بنوه في شبيبته ... فسرهم وأتيناه على الهرم','358','3764','0');
INSERT INTO [t754] VALUES (4764,'ما أنصف القوم ... ضبه وأمه الطرطبة','358','3764','0');
INSERT INTO [t754] VALUES (4765,'رموا برأس أبيه ... وناكوا الأم غلبه','358','3764','0');
INSERT INTO [t754] VALUES (4766,'فلا بمن مات فخر ... ولا بمن نيك رغبه','359','3764','0');
INSERT INTO [t754] VALUES (4767,'وإنما قلت ما قل ... ت رحمة لا ومحبة','359','3764','0');
INSERT INTO [t754] VALUES (4768,'وحيلة لك حتى ... عذرت لو كنت تيبه','359','3764','0');
INSERT INTO [t754] VALUES (4769,'وما عليك من القت ... ل إنما هي ضربه','359','3764','0');
INSERT INTO [t754] VALUES (4770,'وما عليك من الغ ... در إنما هي سبه','359','3764','0');
INSERT INTO [t754] VALUES (4771,'وما عليك من العا ... ر أن أمك قحبه','359','3764','0');
INSERT INTO [t754] VALUES (4772,'وما يشق على الك ... لب أن يكون ابن كلبه','359','3764','0');
INSERT INTO [t754] VALUES (4773,'ما ضرها من أتاها ... وإنما ضر صلبه','359','3764','0');
INSERT INTO [t754] VALUES (4774,'ولم ينكها ولكن ... عجانها ناك زبه','359','3764','0');
INSERT INTO [t754] VALUES (4775,'يلوم ضبة قوم ... ولا يلومون قلبه','359','3764','0');
INSERT INTO [t754] VALUES (4776,'وقلبه يتشهى ... ويلزم الجسم ذنبه','359','3764','0');
INSERT INTO [t754] VALUES (4777,'لو أبصر الجذع فعلا ... أحب في الجذع صلبه','359','3764','0');
INSERT INTO [t754] VALUES (4778,'يا أطيب الناس نفسا ... وألين الناس ركبه','359','3764','0');
INSERT INTO [t754] VALUES (4779,'وأخبث الناس أصلا ... في أخبث الأرض تربه','359','3764','0');
INSERT INTO [t754] VALUES (4780,'وأرخص الناس أما ... تبيع الفا بحبه','359','3764','0');
INSERT INTO [t754] VALUES (4781,'كل الفعول سهام ... لمريم وهي جعبه','359','3764','0');
INSERT INTO [t754] VALUES (4782,'وما على من به الدا ... ء من لقاء الأطبه','359','3764','0');
INSERT INTO [t754] VALUES (4783,'وليس بين هلوك ... وحرة غير خطبه','359','3764','0');
INSERT INTO [t754] VALUES (4784,'يا قاتلا كل ضيف ... غناه ضيح وعلبه','359','3764','0');
INSERT INTO [t754] VALUES (4785,'وخوف كل رفيق ... أباتك الليل جنبه','359','3764','0');
INSERT INTO [t754] VALUES (4786,'كذا خلقت ومن ذا ال ... ذي يغالب ربه','359','3764','0');
INSERT INTO [t754] VALUES (4787,'ومن يبالي بذم ... إذا تعود كسبه','359','3764','0');
INSERT INTO [t754] VALUES (4788,'أما ترى الخيل في النخ ... ل سربة بعد سربه','359','3764','0');
INSERT INTO [t754] VALUES (4789,'على نسائك تجلو ... أيورها منذ سنبه','359','3764','0');
INSERT INTO [t754] VALUES (4790,'وهن حولك ينظر ... ن والأحيراح رطبه','359','3764','0');
INSERT INTO [t754] VALUES (4791,'وكل غرمول بغل ... يرين يحسدن قنبه','359','3764','0');
INSERT INTO [t754] VALUES (4792,'فسل فؤادك يا ض ... ب أين خلف عجبه','359','3764','0');
INSERT INTO [t754] VALUES (4793,'وإن يخنك فعمري ... لطالما خان صحبه','360','3764','0');
INSERT INTO [t754] VALUES (4794,'وكيف ترغب فيه ... وقد تبينت رعبه','360','3764','0');
INSERT INTO [t754] VALUES (4795,'ما كنت إلا ذبابا ... نفتك عنه مذبه','360','3764','0');
INSERT INTO [t754] VALUES (4796,'وكنت تنخر تيها ... فصرت تضرط رهبه','360','3764','0');
INSERT INTO [t754] VALUES (4797,'وإن بعدنا قليلا ... حملت رمحا وحربه','360','3764','0');
INSERT INTO [t754] VALUES (4798,'وقلت ليت بكفي ... عنان جرداء شطبه','360','3764','0');
INSERT INTO [t754] VALUES (4799,'إن أوحشتك المعالي ... فإنها دار غربه','360','3764','0');
INSERT INTO [t754] VALUES (4800,'أو آنستك المخازي ... فإنها لك نسبه','360','3764','0');
INSERT INTO [t754] VALUES (4801,'وإن عرفت مرادي ... تكشفت عنك كربه','360','3764','0');
INSERT INTO [t754] VALUES (4802,'وإن جهلت مرادي ... فإنه بك أشبه','360','3764','0');
INSERT INTO [t754] VALUES (4803,'كدعواك كل يدعى صحة العقل ... ومن ذا الذي يدري بما فيه من جهل','360','3764','0');
INSERT INTO [t754] VALUES (4804,'لهنك أولى لائم بملامة ... وأحوج ممن تعذلين إلى العذل','360','3764','0');
INSERT INTO [t754] VALUES (4805,'تقولين ما في الناس مثلك عاشق ... جدي مثل من أحببته تجدي مثلي','360','3764','0');
INSERT INTO [t754] VALUES (4806,'محب كنى بالبيض عن مرهفاته ... وبالحسن في أجسامهن عن الصقل','360','3764','0');
INSERT INTO [t754] VALUES (4807,'وبالسمر عن سمر القنا غير أنني ... جناها أحبائي وأطرافها رسلي','360','3764','0');
INSERT INTO [t754] VALUES (4808,'عدمت فؤادا لم تبت فيه فضلة ... لغير الثنايا الغر والحدق النجل','360','3764','0');
INSERT INTO [t754] VALUES (4809,'فما حرمت حسناء بالهجر غبطة ... ولا بلغتها من شكا الهجر بالوصل','360','3764','0');
INSERT INTO [t754] VALUES (4810,'ذريني أنل ما لا ينال من العلي ... فصعب العلي في الصعب والسهل في السهل','360','3764','0');
INSERT INTO [t754] VALUES (4811,'تريدين لقيان المعالي رخيصة ... ولا بد دون الشهد من إبر النحل','360','3764','0');
INSERT INTO [t754] VALUES (4812,'حذرت علينا الموت والخيل تدعى ... ولم تعلمي على أي عاقبة تجلى','361','3764','0');
INSERT INTO [t754] VALUES (4813,'ولست غبينا لو شريت منيتي ... بإكرام دلار بن كشكروز لي','361','3764','0');
INSERT INTO [t754] VALUES (4814,'تمر الأنابيب الخواطر بيننا ... ونذكر إقبال الأمير فتحلولي','361','3764','0');
INSERT INTO [t754] VALUES (4815,'ولو كنت أدري أنها سبب له ... لزاد سروري بالزيادة في القتل','361','3764','0');
INSERT INTO [t754] VALUES (4816,'فلا عدمت أرض العراقين فتنة ... دعتك إليها كاشف الخوف والمحل','361','3764','0');
INSERT INTO [t754] VALUES (4817,'ظللنا إذا انبى الحديد نصولنا ... نجرد ذكرا منك أمضى من النصل','361','3764','0');
INSERT INTO [t754] VALUES (4818,'ونرمي نواصيها من أسمك في الوغى ... بأنفذ من نشابنا ومن النبل','361','3764','0');
INSERT INTO [t754] VALUES (4819,'فإن تك من بعد القتال أتيتنا ... فقد هزم الأعداء ذكرك من قبل','361','3764','0');
INSERT INTO [t754] VALUES (4820,'وما زلت أطوي الأرض قبل اجتماعنا ... على حاجة بين السنابك والسبل','361','3764','0');
INSERT INTO [t754] VALUES (4821,'ولو لم تسر سرنا إليك بأنفس ... غرائب يؤثرن الجياد على الأهل','361','3764','0');
INSERT INTO [t754] VALUES (4822,'وخيل إذا مرت بوحش وروضة ... أبت رعيها إلا ومرجلنا يغلي','361','3764','0');
INSERT INTO [t754] VALUES (4823,'ولكن رأيت القصد في الفضل شركة ... فكان لك الفضلان بالقصد والفضل','361','3764','0');
INSERT INTO [t754] VALUES (4824,'وليس الذي يتبع الوبل رائدا ... كمن جاءه في داره رائد الوبل','361','3764','0');
INSERT INTO [t754] VALUES (4825,'وما أن ممن يدعي الشوق قلبه ... ويحتج في ترك الزيارة بالشغل','362','3764','0');
INSERT INTO [t754] VALUES (4826,'أرادت كلاب أن تفوز بدولة ... لمن تركت رعى الشويهات والإبل','362','3764','0');
INSERT INTO [t754] VALUES (4827,'أبى ربها أن يترك الوحش وحدها ... وأن يؤمن الضب الخبيث من الأكل','362','3764','0');
INSERT INTO [t754] VALUES (4828,'وقاد لها دلار كل طمرة ... تنيف بخديها سحوق من النخل','362','3764','0');
INSERT INTO [t754] VALUES (4829,'وكل جواد تلطم الأرض كفه ... بأغنى عن النعل الحديد من النعل','362','3764','0');
INSERT INTO [t754] VALUES (4830,'فولت تريغ الغيث والغيث خلفت ... وتطلب ما قد كان في اليد بالرجل','362','3764','0');
INSERT INTO [t754] VALUES (4831,'تحاذر هزل المال وهي ذليلة ... وأشهد أن الذل شر من الهزل','362','3764','0');
INSERT INTO [t754] VALUES (4832,'وأهدت إلينا غير قاصدة به ... كريم السجايا يسبق القول بالفعل','362','3764','0');
INSERT INTO [t754] VALUES (4833,'تتبع آثار الرزايا بجوده ... تتبع آثار الآسنة بالفتل','362','3764','0');
INSERT INTO [t754] VALUES (4834,'شفى كل شاك سيفه ونواله ... من الداء حتى الثاكلات من الثكل','362','3764','0');
INSERT INTO [t754] VALUES (4835,'عفيف تروق الشمس صورة وجهه ... ولو نزلت شوقا لحاد إلى الظل','362','3764','0');
INSERT INTO [t754] VALUES (4836,'شجاع كأن الحرب عاشقة له ... إذا زارها فدته بالخيل والرجل','362','3764','0');
INSERT INTO [t754] VALUES (4837,'وريان لا تصدي إلى الخمر نفسه ... وعطشان لا تروي يداه من البذل','362','3764','0');
INSERT INTO [t754] VALUES (4838,'وتمليك دلار وتعظيم قدره ... دليل بوحدانية الله والعدل','363','3764','0');
INSERT INTO [t754] VALUES (4839,'وما دام دلار يهز حسامه ... فلا ناب في الدنيا لليث ولا شبل','363','3764','0');
INSERT INTO [t754] VALUES (4840,'وما دام دلار يقلب كفه ... فلا خلق من دعوى المكارم في حل','363','3764','0');
INSERT INTO [t754] VALUES (4841,'فتى لا يرجى أن تتم طهارة ... لمن لم يطهر راحتيه من البخل','363','3764','0');
INSERT INTO [t754] VALUES (4842,'فلا قطع الرحمن أصلا أتى به ... فإني رأيت الطيب الطيب الأصل','363','3764','0');
INSERT INTO [t754] VALUES (4843,'باد هواك صبرت أم لم تصبرا ... وبكاك عن لم يجر دمعك أو جرى','363','3764','0');
INSERT INTO [t754] VALUES (4844,'كم غر صبرك وابتسامك صاحبا ... لما رآك وفي الحشا ما لا يرى','363','3764','0');
INSERT INTO [t754] VALUES (4845,'أمر الفؤاد لسانه وجفونه ... فكتمنه وكفى بجسمك مخبرا','363','3764','0');
INSERT INTO [t754] VALUES (4846,'تعس المهارى غير مهري غدا ... بمصور لبس الحرير مصورا','363','3764','0');
INSERT INTO [t754] VALUES (4847,'نافست فيه صورة في ستره ... لو كنتها لخفيت حتى يظهرا','363','3764','0');
INSERT INTO [t754] VALUES (4848,'لا تترب الأيدي المقيمة فوقه ... كسرى مقام الحاجبين وقيصرا','363','3764','0');
INSERT INTO [t754] VALUES (4849,'يقيان في أحد الهوادج مقلة ... رحلت وكان له فؤادي محجرا','363','3764','0');
INSERT INTO [t754] VALUES (4850,'قد كنت أحذر بينهم من قبله ... لو كان ينفع حائنا أن يحذرا','364','3764','0');
INSERT INTO [t754] VALUES (4851,'ولو استطعت إذا اغتدت روادهم ... لمنعت كل سحابة أن تقطرا','364','3764','0');
INSERT INTO [t754] VALUES (4852,'فإذا السحاب أخو غراب فراقهم ... جعل الصياح ببينهم أن يمطروا','364','3764','0');
INSERT INTO [t754] VALUES (4853,'وإذا الجمائل ما يخدن بنفنف ... إلا شققن عليه ثوبا أخضرا','364','3764','0');
INSERT INTO [t754] VALUES (4854,'يحملن مثل الروض إلا أنها ... أسبى مهاة للقلوب وجوذرا','364','3764','0');
INSERT INTO [t754] VALUES (4855,'فبلحظها نكرت قناتي راحتي ... ضعفا وأنكر خاتماي الخنصرا','364','3764','0');
INSERT INTO [t754] VALUES (4856,'أعطى الزمان فما قبلت عطاءه ... وأراد لي فأردت أن أتخيرا','364','3764','0');
INSERT INTO [t754] VALUES (4857,'أرجان أيتها الجياد فإنه ... عزمي الذي يذر الوشيج مكسرا','364','3764','0');
INSERT INTO [t754] VALUES (4858,'لو كنت أفعل ما اشتهيت فعاله ... ما شق كوكبك العجاج الأكدرا','364','3764','0');
INSERT INTO [t754] VALUES (4859,'أمي أبا الفضل المبر أليتي ... لأيممن أجل بحر جوهرا','364','3764','0');
INSERT INTO [t754] VALUES (4860,'أفتى برؤيته الأنام وحاش لي ... من أن أكون مقصرا أو مقصرا','364','3764','0');
INSERT INTO [t754] VALUES (4861,'صغت السوار لأي كف بشرت ... بابن العميد واي عبد كبرا','364','3764','0');
INSERT INTO [t754] VALUES (4862,'إن لم تغثني خيله ورماحه ... فمتى أقود إلى الأعادي عسكرا','364','3764','0');
INSERT INTO [t754] VALUES (4863,'بأبي وأمي ناطق في لفظه ... ثمن تباع به القلوب وتشترى','364','3764','0');
INSERT INTO [t754] VALUES (4864,'من لا تريه الحرب خلقا مقبلا ... فيها ولا خلق يراه مدبرا','365','3764','0');
INSERT INTO [t754] VALUES (4865,'خنثى الفحول من الكماة بصبغه ... ما يلبسون من الحديد معصفرا','365','3764','0');
INSERT INTO [t754] VALUES (4866,'يتكسب القصب الضعيف بكفه ... شرفا على صم الرماح ومفخرا','365','3764','0');
INSERT INTO [t754] VALUES (4867,'ويبين فيما مس منه بنانه ... تيه المدل فلو مشى لتبخترا','365','3764','0');
INSERT INTO [t754] VALUES (4868,'أمن إذا ورد البلاد كتابه ... قبل الجيوش ثنى الجيوش تحيرا','365','3764','0');
INSERT INTO [t754] VALUES (4869,'أنت الوحيد إذا ارتكبت طريقة ... ومن الرديف وقد ركبت غضنفرا','365','3764','0');
INSERT INTO [t754] VALUES (4870,'قطف الرجال القول قبل نباته ... وقطفت أنت القول لما نورا','365','3764','0');
INSERT INTO [t754] VALUES (4871,'فهو المشيع بالمسامع إن مضى ... وهو المضاعف حسنه إن كررا','365','3764','0');
INSERT INTO [t754] VALUES (4872,'وإذا سكت فإن أبلغ خاطب ... قلم لك اتخذ الأصابع منبرا','365','3764','0');
INSERT INTO [t754] VALUES (4873,'ورسائل قطع العداة سحاءها ... فرأوا قنا وأسنة وسنورا','365','3764','0');
INSERT INTO [t754] VALUES (4874,'فدعاك حسدك الرئيس وأمسكوا ... ودعاك خالقك الرئيس الأكبرا','365','3764','0');
INSERT INTO [t754] VALUES (4875,'خلفت صفاتك في العيون كلامه ... كالخط يملأ مسمعي من أبصرا','365','3764','0');
INSERT INTO [t754] VALUES (4876,'أرأيت همة ناقتي في ناقة ... نقلت يدا سرحا وخفا مجمرا','365','3764','0');
INSERT INTO [t754] VALUES (4877,'تركت دخان الرمث في أوطانها ... طلبا لقوم يوقدون العنبرا','366','3764','0');
INSERT INTO [t754] VALUES (4878,'وتكرمت ركباتها عن مبرك ... تقعان فيه وليس مسكا أذفرا','366','3764','0');
INSERT INTO [t754] VALUES (4879,'فأتتك دامية الأظل كأنما ... حذيت قوائمها العقيق الحمرا','366','3764','0');
INSERT INTO [t754] VALUES (4880,'بدرت إليك يد الزمان كأنها ... وجدته مشغول اليدين مفكرا','366','3764','0');
INSERT INTO [t754] VALUES (4881,'من مبلغ الأعراب أني بعدها ... شاهدت رسطليس والإسكندرا','366','3764','0');
INSERT INTO [t754] VALUES (4882,'ومللت نحر عشارها فأضافني ... من ينحر البدر النضار لمن قرى','366','3764','0');
INSERT INTO [t754] VALUES (4883,'وسمعت بطليموس دارس كتبه ... متملكا متبديا متحضرا','366','3764','0');
INSERT INTO [t754] VALUES (4884,'ولقيت كل الفاضلين كأنما ... رد الإله نفوسهم والأعصرا','366','3764','0');
INSERT INTO [t754] VALUES (4885,'نسقوا لنا نسق الحساب مقدما ... وأتى فذلك إذ أتيت مؤخرا','366','3764','0');
INSERT INTO [t754] VALUES (4886,'يا ليت باكية شجاني دمعها ... نظرت إليك كما نظرت فتعذرا','367','3764','0');
INSERT INTO [t754] VALUES (4887,'فترى الفضيلة لا ترد فضيلة ... الشمس تشرق والسحاب كنهورا','367','3764','0');
INSERT INTO [t754] VALUES (4888,'أنا من جميع الناس أطيب منزلا ... وأسر راحلة وأربح متجرا','367','3764','0');
INSERT INTO [t754] VALUES (4889,'زحل على أن الكواكب قومه ... لو كان منك لكان أكرم معشرا','367','3764','0');
INSERT INTO [t754] VALUES (4890,'أحب امرىء حبت الأنفس ... وأطيب ما شمه معطس','367','3764','0');
INSERT INTO [t754] VALUES (4891,'ونشر الندى لكنما ... مجامره الآس والنرجس','367','3764','0');
INSERT INTO [t754] VALUES (4892,'ولسنا نرى لهبا هاجه ... فهل هاجه عزك الأقعس','367','3764','0');
INSERT INTO [t754] VALUES (4893,'فإن القيام التي حولها ... لتحسد أقدامها الأروس','367','3764','0');
INSERT INTO [t754] VALUES (4894,'جاء نوروزنا وأنت مراده ... وورت بالذي أراد زناده','368','3764','0');
INSERT INTO [t754] VALUES (4895,'هذه النظرة التي نالها من ... ك إلى مثلها من الحول زاده','368','3764','0');
INSERT INTO [t754] VALUES (4896,'ينثني عنك آخر اليوم منه ... ناظر أنت طرفه ورقاده','368','3764','0');
INSERT INTO [t754] VALUES (4897,'نحن في أرض فارس في سرور ... ذا الصباح الذي نرى ميلاده','368','3764','0');
INSERT INTO [t754] VALUES (4898,'عظمته ممالك الفرس حتى ... كل أيام عامه حساده','368','3764','0');
INSERT INTO [t754] VALUES (4899,'ما لبسنا في الأكاليل حتى ... لبستها تلاعه ووهاده','368','3764','0');
INSERT INTO [t754] VALUES (4900,'عند من لا يقاس كسرى أبو سا ... سان ملكا به ولا أولاده','368','3764','0');
INSERT INTO [t754] VALUES (4901,'عربي لسانه فلسفي ... رأيه فارسية أعياده','368','3764','0');
INSERT INTO [t754] VALUES (4902,'كلما قال نائل أنا منه ... سرف قال آخر ذا اقتصاده','369','3764','0');
INSERT INTO [t754] VALUES (4903,'كيف يرتد منكبي عن سماء ... والنجاد الذي عليه نجاده','369','3764','0');
INSERT INTO [t754] VALUES (4904,'قلدتني يمينه بحسام ... أعقبت منه واحدا أجداده','369','3764','0');
INSERT INTO [t754] VALUES (4905,'كلما استل ضاحكته إياه ... تزعم الشمس أنها أراده','369','3764','0');
INSERT INTO [t754] VALUES (4906,'مثلوه في جفنه خشية الفق ... د ففي مثل أثره إغماده','369','3764','0');
INSERT INTO [t754] VALUES (4907,'منعل لا من الحفا ذهبا يح ... مل بحرا فرنده إزباده','369','3764','0');
INSERT INTO [t754] VALUES (4908,'يقسم الفارس المدجج لا يس ... لم من شفرتيه إلا بداده','369','3764','0');
INSERT INTO [t754] VALUES (4909,'جمع الدهر حده ويديه ... وثنائي فاستجمعت آحاده','369','3764','0');
INSERT INTO [t754] VALUES (4910,'وتقلدت شامة في نداه ... جلدها منفساته وعتاده','369','3764','0');
INSERT INTO [t754] VALUES (4911,'فرستنا سوابق كن فيه ... فارقت لبده وفيها طراده','370','3764','0');
INSERT INTO [t754] VALUES (4912,'ورجت راحة بنا لا تراها ... وبلاد تسير فيها بلاده','370','3764','0');
INSERT INTO [t754] VALUES (4913,'هل لعذري إلى الهمام أبي الفض ... ل قبول سواد عيني مداده','370','3764','0');
INSERT INTO [t754] VALUES (4914,'أن من شدة الحياء عليل ... مكرمات المعله عواده','370','3764','0');
INSERT INTO [t754] VALUES (4915,'ما كفاني تقصير ما قلت فيه ... عن علاه حتى ثناه انتقاده','370','3764','0');
INSERT INTO [t754] VALUES (4916,'إنني أصيد البزاة ولك ... ن أجل النجوم لا أصطاده','371','3764','0');
INSERT INTO [t754] VALUES (4917,'رب ما لا يعبر اللفظ عنه ... والذي يضمر الفؤاد اعتقاده','371','3764','0');
INSERT INTO [t754] VALUES (4918,'ما تعودت أن أرى كأبي الفض ... ل وهذا الذي أتاه اعتياده','371','3764','0');
INSERT INTO [t754] VALUES (4919,'إن في الموج للغريق لعذرا ... واضحا أن يفوته تعداده','371','3764','0');
INSERT INTO [t754] VALUES (4920,'للندى الغلب أنه فاض والش ... عر عمادي وابن العميد عماده','371','3764','0');
INSERT INTO [t754] VALUES (4921,'نال ظني الأمور إلا كريما ... ليس لي نطقه ولا في آده','371','3764','0');
INSERT INTO [t754] VALUES (4922,'ظالم الجود كلما حل ركب ... سيم أن يحمل البحار مزاده','371','3764','0');
INSERT INTO [t754] VALUES (4923,'غمرتني فوائد شاء فيها ... أن يكون الكلام مما أفاده','371','3764','0');
INSERT INTO [t754] VALUES (4924,'ما سمعنا بمن أحب العطايا ... فاشتهى أن يكون فيها فؤاده','371','3764','0');
INSERT INTO [t754] VALUES (4925,'خلق الله أفصح الناس طرا ... في مكان أعرابه أكراده','371','3764','0');
INSERT INTO [t754] VALUES (4926,'وأحق الغيوث نفسا بحمد ... في زمان كل النفوس جراده','371','3764','0');
INSERT INTO [t754] VALUES (4927,'مثل ما أحدث النبوة في الع ... الم والبعث حين شاع فساده','372','3764','0');
INSERT INTO [t754] VALUES (4928,'زانت الليل غرة القمر الطا ... لع فيه لم يشنها سواده','372','3764','0');
INSERT INTO [t754] VALUES (4929,'كثر الفكر كيف نهدي كما أه ... دت إلى ربها الرئيس عباده','372','3764','0');
INSERT INTO [t754] VALUES (4930,'والذي عندنا من المال والخي ... ل فمنه هباته وقياده','372','3764','0');
INSERT INTO [t754] VALUES (4931,'فبعثنا بأربعين مهارا ... كل مهر ميدانه إنشاده','372','3764','0');
INSERT INTO [t754] VALUES (4932,'عدد عشته يرى الجسم فيه ... أربا لا يراه فيما يزاده','372','3764','0');
INSERT INTO [t754] VALUES (4933,'فارتبطها فإن قلبا نماها ... مربط تسبق الجياد جياده','372','3764','0');
INSERT INTO [t754] VALUES (4934,'بكتب الأنام كتاب ورد ... فدت يد كاتبه كل يد','372','3764','0');
INSERT INTO [t754] VALUES (4935,'يعبر عما له عندنا ... ويذكر من شوقه ما نجد','372','3764','0');
INSERT INTO [t754] VALUES (4936,'فأخرق رائيه ما رأى ... وأبرق ناقده ما انتقد','372','3764','0');
INSERT INTO [t754] VALUES (4937,'إذا سمع الناس ألفاظه ... خلقن له في القلوب الحسد','372','3764','0');
INSERT INTO [t754] VALUES (4938,'فقلت وقد فرس الناطقين ... كذا يفعل الأسد ابن الأسد','372','3764','0');
INSERT INTO [t754] VALUES (4939,'نسيت وما أنسى عتابا على الصد ... ولا خفرا زادت به حمرة الخد','373','3764','0');
INSERT INTO [t754] VALUES (4940,'ولا ليلة قصرتها بقصورة ... أطالت يدي في جيدها صحبة العقد','373','3764','0');
INSERT INTO [t754] VALUES (4941,'ومن لي بيوم مثل يوم كرهته ... قربت به عند الوداع من البعد','373','3764','0');
INSERT INTO [t754] VALUES (4942,'وأن لا يخص الفقد شيئا فإنني ... فقدت ولم أفقد دموعي ولا وجدي','373','3764','0');
INSERT INTO [t754] VALUES (4943,'تمن يلد المستهام بمثله ... وإن كان لا يغنى فتيلا ولا يجدي','373','3764','0');
INSERT INTO [t754] VALUES (4944,'وغيظ على الأيام كالنار في الحشا ... ولكنه غيظ الأسير على القد','373','3764','0');
INSERT INTO [t754] VALUES (4945,'فإما تريني لا أقيم ببلدة ... فآفة غمدي في دلوقي وفي حدي','373','3764','0');
INSERT INTO [t754] VALUES (4946,'يحل القنا يوم الطعان بعقوتي ... فأحرمه عرضي وأطعمه جلدي','373','3764','0');
INSERT INTO [t754] VALUES (4947,'تبدل أيامي وعيشي ومنزلي ... نجائب لا يفكرن في النحس والسعد','373','3764','0');
INSERT INTO [t754] VALUES (4948,'وأوجه فتيان حياء تلثموا ... عليهن لا خوفا من الحر والبرد','374','3764','0');
INSERT INTO [t754] VALUES (4949,'وليس حياء الوجه في الذئب شيمة ... ولكنه من شيمة الأسد الورد','374','3764','0');
INSERT INTO [t754] VALUES (4950,'إذا لم تجزهم دار قوم مودة ... أجاز القنا والخوف خير من الود','374','3764','0');
INSERT INTO [t754] VALUES (4951,'يحيدون عن هزل الملوك إلى الذي ... توفر من بين الملوك على الجد','374','3764','0');
INSERT INTO [t754] VALUES (4952,'ومن يصحب اسم ابن العميد محمد ... يسر بين أنياب الأساود والأساد','374','3764','0');
INSERT INTO [t754] VALUES (4953,'يمر من السم الوحي بعاجز ... ويعبر من أفواههن على درد','374','3764','0');
INSERT INTO [t754] VALUES (4954,'كفانا الربيع العيس من بركاته ... فجاءته لم تسمع حداء سوى الرعد','374','3764','0');
INSERT INTO [t754] VALUES (4955,'إذا ما استجبن الماء يعرض نفسه ... كرعن بسبت في إناء من الورد','374','3764','0');
INSERT INTO [t754] VALUES (4956,'كأنا أرادت شكرنا الأرض عنده ... فلم يخلنا جو هبطناه من رفد','375','3764','0');
INSERT INTO [t754] VALUES (4957,'لنا مذهب العباد في ترك غيره ... وإتيانه نبغي الرغائب بالزهد','375','3764','0');
INSERT INTO [t754] VALUES (4958,'رجونا الذي يرجون في كل جنة ... بأرجان حتى ما يئسنا من الخلد','375','3764','0');
INSERT INTO [t754] VALUES (4959,'تعرض للزوار أعناق خيله ... تعرض وحش خائفات من الطرد','375','3764','0');
INSERT INTO [t754] VALUES (4960,'وتلقى نواصيها المنايا مشيحة ... ورود قطا صم تشايحن في ورد','375','3764','0');
INSERT INTO [t754] VALUES (4961,'وتنسب أفعال السيوف نفوسها ... إليه وينسبن السيوف إلى الهند','375','3764','0');
INSERT INTO [t754] VALUES (4962,'إذا الشرفاء البيض متوا بقتوه ... أتى نسب أعلى من الأب والجد','376','3764','0');
INSERT INTO [t754] VALUES (4963,'فتى فاتت العدوى من الناس عينه ... فما أرمدت أجفانه كثرة الرمد','376','3764','0');
INSERT INTO [t754] VALUES (4964,'وخالقهم خلقا وموضعا ... فقد جل أن يعدي بشيء وأن يعدي','376','3764','0');
INSERT INTO [t754] VALUES (4965,'يغير ألوان الليالي على العدى ... بمنشورة الرايات منصورة الجند','376','3764','0');
INSERT INTO [t754] VALUES (4966,'إذا أرتقبوا صبحا رأوا قبل ضوئه ... كتائب لا يردي الصباح كما تردى','376','3764','0');
INSERT INTO [t754] VALUES (4967,'ومبثوثة لاتتقي بطليعة ... ولايحتمي منها بغور ولا نجد','376','3764','0');
INSERT INTO [t754] VALUES (4968,'يغصن إذا ما عدن في متفاقد ... من الكثر غان بالعبيد عن الحشد','376','3764','0');
INSERT INTO [t754] VALUES (4969,'حثت كل أرض تربة في غباره ... فهن عليه كالطرائق في البرد','376','3764','0');
INSERT INTO [t754] VALUES (4970,'فإن يكن المهدي من بان هديه ... فهذا وإلا فالهدي ذا فما المهدي','376','3764','0');
INSERT INTO [t754] VALUES (4971,'يعللنا هذا الزمان بذا الوعد ... ويخدع عما في يديه من النقد','377','3764','0');
INSERT INTO [t754] VALUES (4972,'هل الخير شيء ليس بالخير غائب ... أم الرشد شيء غائب ليس بالرشد','377','3764','0');
INSERT INTO [t754] VALUES (4973,'أأحزم ذي لب وأكرم ذي يد ... وأشجع ذي قلب وأرحم ذي كبد','377','3764','0');
INSERT INTO [t754] VALUES (4974,'وأحسن معتم جلوسا وركبة ... على المنبر العالي أو الفرس النهد','377','3764','0');
INSERT INTO [t754] VALUES (4975,'تفضلت الأيام بالجمع بيننا ... فلما حمدنا لم تدمنا على الحمد','377','3764','0');
INSERT INTO [t754] VALUES (4976,'جعلن وداعي واحدا لثلاثة ... جمالك والعلم المبرح والمجد','377','3764','0');
INSERT INTO [t754] VALUES (4977,'وقد كنت أدركت المنى غير أنني ... يعيرني أهلي بإدراكها وحدي','377','3764','0');
INSERT INTO [t754] VALUES (4978,'وكل شريك في السرور بمصبحي ... أرى بعده من لا يرى مثله بعدي','377','3764','0');
INSERT INTO [t754] VALUES (4979,'فجد لي بقلب أن رحلت فإنني ... أخلف قلبي عند من فضله عندي','377','3764','0');
INSERT INTO [t754] VALUES (4980,'ولو فارقت جسمي إليك حيوته ... لقلت أصابت غير مذمومة العهد','377','3764','0');
INSERT INTO [t754] VALUES (4981,'أوه بديل من قولتي واها ... لمن نأت والبديل ذكراها','377','3764','0');
INSERT INTO [t754] VALUES (4982,'أوه من لا أرى محاسنها ... وأصل واها وأوه مرآها','377','3764','0');
INSERT INTO [t754] VALUES (4983,'شامية طالما خلوت بها ... تبصر في ناظري محياها','377','3764','0');
INSERT INTO [t754] VALUES (4984,'فقبلت ناظري تغالطني ... وإنما قبلت به فاها','378','3764','0');
INSERT INTO [t754] VALUES (4985,'فليتها لا تزال آوية ... وليته لا يزال مأواها','378','3764','0');
INSERT INTO [t754] VALUES (4986,'كل جريح ترجى سلامته ... إلا فؤادا دهته عيناها','378','3764','0');
INSERT INTO [t754] VALUES (4987,'تبل خدي كلما ابتسمت ... من مطر برقه ثناياها','378','3764','0');
INSERT INTO [t754] VALUES (4988,'ما نفضت في يدي غدائرها ... جعلته في المدام أفواها','378','3764','0');
INSERT INTO [t754] VALUES (4989,'في بلد تضرب الحجال به ... على حسان لسن أشباها','378','3764','0');
INSERT INTO [t754] VALUES (4990,'لقيننا والحمول سائرة ... وهن در فذبن أمواها','378','3764','0');
INSERT INTO [t754] VALUES (4991,'كل مهاة كأن مقلتها ... تقول إياكم وإياها','378','3764','0');
INSERT INTO [t754] VALUES (4992,'فيهن من تقطر السيوف دما ... إذا لسان المحب سماها','378','3764','0');
INSERT INTO [t754] VALUES (4993,'أحب حمصا إلى خناصرة ... وكل نفس تحب محياها','378','3764','0');
INSERT INTO [t754] VALUES (4994,'حيث التقى خدها وتفاح لب ... نان وثغري على حمياها','378','3764','0');
INSERT INTO [t754] VALUES (4995,'وصفت فيها مصيف بادية ... شتوت بالصحصحان مشتاها','378','3764','0');
INSERT INTO [t754] VALUES (4996,'إن أعشبت روضة رعيناها ... أو ذكرت حلة غزوناها','378','3764','0');
INSERT INTO [t754] VALUES (4997,'أو عرضت عانة مقزعة ... صدنا بأخرى الجياد أولاها','378','3764','0');
INSERT INTO [t754] VALUES (4998,'أو عبرت هجمة بنا تركت ... تكوس بين الشروب عقراها','378','3764','0');
INSERT INTO [t754] VALUES (4999,'والخيل مطرودة وطاردة ... تجر طولي القنا وقصراها','378','3764','0');
INSERT INTO [t754] VALUES (5000,'يعجبها قتلها الكماة ولا ... ينظرها الدهر بعد قتلاها','379','3764','0');
INSERT INTO [t754] VALUES (5001,'وقد رأيت الملوك قاطبة ... وسرت حتى رأيت مولاها','379','3764','0');
INSERT INTO [t754] VALUES (5002,'ومن مناياهم براحته ... يأمرها فيهم وينهاها','379','3764','0');
INSERT INTO [t754] VALUES (5003,'أبا شجاع بفارس عضد ال ... دولة فناخسرو شهنشاها','379','3764','0');
INSERT INTO [t754] VALUES (5004,'أساميا لم تزده معرفة ... وإنما لذة ذكرناها','379','3764','0');
INSERT INTO [t754] VALUES (5005,'تقود مستحسن الكلام لنا ... كما تقود السحاب عظماها','379','3764','0');
INSERT INTO [t754] VALUES (5006,'هو النفيس الذي مواهبه ... أنفس أمواله وأسناها','379','3764','0');
INSERT INTO [t754] VALUES (5007,'لو فطنت خيله لنائله ... لم يرضها أن تراه يرضاها','379','3764','0');
INSERT INTO [t754] VALUES (5008,'لا تجد الخمر في مكارمه ... إذا انتشى خلة تلافاها','379','3764','0');
INSERT INTO [t754] VALUES (5009,'تصاحب الراح أريحيته ... فتسقط الراح دون أدناها','379','3764','0');
INSERT INTO [t754] VALUES (5010,'تسر طرباته كرائنه ... ثم تزيل السرور عقباها','379','3764','0');
INSERT INTO [t754] VALUES (5011,'بكل موهوبة مولوية ... قاطعة زيرها ومثناها','379','3764','0');
INSERT INTO [t754] VALUES (5012,'تعوم عوم القذاة في زبد ... من جود كف الأمير يغشاها','380','3764','0');
INSERT INTO [t754] VALUES (5013,'دان له شرقها ومعربها ... ونفسه تستقل دنياها','380','3764','0');
INSERT INTO [t754] VALUES (5014,'تشرق تيجانه بغرته ... إشراق ألفاظه بمعناها','380','3764','0');
INSERT INTO [t754] VALUES (5015,'تجمعت في فؤاده همم ... ملء فؤاد الزمان إحداها','380','3764','0');
INSERT INTO [t754] VALUES (5016,'فإن أتى حظها بأزمنة ... أوسع من ذا الزمان أبداها','380','3764','0');
INSERT INTO [t754] VALUES (5017,'وصارت الفيلقان واحدة ... تعثر أحياؤها بموتاها','380','3764','0');
INSERT INTO [t754] VALUES (5018,'ودارت النيرات في فلك ... تسجد أقماره لأبهاها','380','3764','0');
INSERT INTO [t754] VALUES (5019,'الفارس المتقي السلاح به ال ... مثني عليه الوغا وخيلاها','380','3764','0');
INSERT INTO [t754] VALUES (5020,'لو أنكرت من حيائها يده ... في الحرب آثارها عرفناها','380','3764','0');
INSERT INTO [t754] VALUES (5021,'وكيف تخفى التي زيادتها ... وناقع الموت بعض سيماها','380','3764','0');
INSERT INTO [t754] VALUES (5022,'الواسع العذر أن يتيه على ال ... دنيا وأبنائها وماتاها','380','3764','0');
INSERT INTO [t754] VALUES (5023,'لو كفر العاملون نعمته ... لما عدت نفسه سجاياها','380','3764','0');
INSERT INTO [t754] VALUES (5024,'كالشمس لا تبتغي بما صنعت ... منعفة عندهم ولا جاها','380','3764','0');
INSERT INTO [t754] VALUES (5025,'ول السلاطين من تولاها ... والجأ إليه تكن حدياها','380','3764','0');
INSERT INTO [t754] VALUES (5026,'ولا تغرنك الإمارة في ... غير أمير وإن بها باها','381','3764','0');
INSERT INTO [t754] VALUES (5027,'فإنما الملك رب مملكة ... قد فغم الخافقين رياها','381','3764','0');
INSERT INTO [t754] VALUES (5028,'مبتسم والوجوه عابسة ... سلم العدى عنده كهيجاها','381','3764','0');
INSERT INTO [t754] VALUES (5029,'الناس كالعابدين آلهة ... وعبده كالموحد اللاها','381','3764','0');
INSERT INTO [t754] VALUES (5030,'مغاني الشعب طيبا في المغاني ... بمنزلة الربيع من الزمان','381','3764','0');
INSERT INTO [t754] VALUES (5031,'ولكن الفتى العربي فيها ... غريب الوجه واليد واللسان','381','3764','0');
INSERT INTO [t754] VALUES (5032,'ملاعب جنة لو سار فيها ... سليمان لسار بترجمان','381','3764','0');
INSERT INTO [t754] VALUES (5033,'طبت فرساننا والخيل حتى ... خشيت وإن كرمن من الحران','381','3764','0');
INSERT INTO [t754] VALUES (5034,'غدونا تنفض الأغصان فيها ... على أعرافها مثل الجمان','381','3764','0');
INSERT INTO [t754] VALUES (5035,'فسرت وقد حجبن الشمس عني ... وجبن من الضياء بما كفاني','381','3764','0');
INSERT INTO [t754] VALUES (5036,'وألقى الشرق منها في ثيابي ... دنانيرا تفر من البنان','381','3764','0');
INSERT INTO [t754] VALUES (5037,'لها ثمر تشير إليك منها ... بأشربة وقفن بلا أواني','381','3764','0');
INSERT INTO [t754] VALUES (5038,'وأمواه تصل بها حصاها ... صليل الحلي في أيدي الغواني','381','3764','0');
INSERT INTO [t754] VALUES (5039,'ولو كانت دمشق ثنى عناني ... لبيق الثرد صيني الجفان','382','3764','0');
INSERT INTO [t754] VALUES (5040,'يلنجوجي ما رفعت لضيف ... به النيران ندى الدخان','382','3764','0');
INSERT INTO [t754] VALUES (5041,'تحل به على قلب شجاع ... وترحل منه عن قلب جبان','382','3764','0');
INSERT INTO [t754] VALUES (5042,'منازل لم يزل منها خيال ... يشيعني إلى النوبندجان','382','3764','0');
INSERT INTO [t754] VALUES (5043,'إذا غنى الحمام الورق فيها ... أجابته أغاني القيان','382','3764','0');
INSERT INTO [t754] VALUES (5044,'ومن بالشعب أحوج من حمام ... إذا غنى وناح إلى البيان','382','3764','0');
INSERT INTO [t754] VALUES (5045,'وقد يتقارب الوصفان جدا ... وموصوفاهما متابعدان','382','3764','0');
INSERT INTO [t754] VALUES (5046,'يقول بشعب بوان حصاني ... أعن هذا يسار إلى الطعان','382','3764','0');
INSERT INTO [t754] VALUES (5047,'أبوكم آدم سن المعاصي ... وعلمكم مفارقة الجنان','382','3764','0');
INSERT INTO [t754] VALUES (5048,'فقلت إذا رأيت أبا شجاع ... سلوت عن العباد وذا المكان','382','3764','0');
INSERT INTO [t754] VALUES (5049,'فإن الناس والدنيا طريق ... إلى من ما له في الخلق ثاني','383','3764','0');
INSERT INTO [t754] VALUES (5050,'لقد علمت نفسي القول فيهم ... كتعليم الطراد بلا سنان','383','3764','0');
INSERT INTO [t754] VALUES (5051,'بعضد الدولة امتنعت وعزت ... وليس لغير ذي عضد يدان','383','3764','0');
INSERT INTO [t754] VALUES (5052,'ولا قبض على البيض المواضي ... ولاحظ من السمر اللدان','383','3764','0');
INSERT INTO [t754] VALUES (5053,'دعته بمفزع الأعضاء منها ... ليوم الحرب بكر أو عوان','383','3764','0');
INSERT INTO [t754] VALUES (5054,'فما يسمى كفناخسر مسم ... ولا يكنى كفناخسر كاني','383','3764','0');
INSERT INTO [t754] VALUES (5055,'ولا تحصى فضائله بظن ... ولا الإخبار عنه ولا العيان','383','3764','0');
INSERT INTO [t754] VALUES (5056,'أروض الناس من ترب وخوف ... وأرض أبي شجاع من أمان','383','3764','0');
INSERT INTO [t754] VALUES (5057,'يذم على اللصوص لكل تجر ... ويضمن للصوارم كل جاني','383','3764','0');
INSERT INTO [t754] VALUES (5058,'إذا طلبت ودائعهم ثقات ... دفعن إلى المحاني والرعان','383','3764','0');
INSERT INTO [t754] VALUES (5059,'فباتت فوقهن بلا صحاب ... تصيح بمن يمر أما تراتي','383','3764','0');
INSERT INTO [t754] VALUES (5060,'رقاه كل أبيض مشرفي ... لكل أصم صل أفعوان','383','3764','0');
INSERT INTO [t754] VALUES (5061,'وما يرقي لهاه من نداه ... ولا المال الكريم من الهوان','383','3764','0');
INSERT INTO [t754] VALUES (5062,'حمى أطراف فارس شمري ... يحض على التباقي بالتفاني','383','3764','0');
INSERT INTO [t754] VALUES (5063,'بضرب هاج أطراب المنايا ... سوى ضرب المثالث والمثاني','384','3764','0');
INSERT INTO [t754] VALUES (5064,'كأن دم الجماجم في العناصي ... كسا البلدان ريش الحيقطان','384','3764','0');
INSERT INTO [t754] VALUES (5065,'فلو طرحت قلوب العشق فيها ... لما خافت من الحدق الحسان','384','3764','0');
INSERT INTO [t754] VALUES (5066,'ولم أر قبله شبلي هزبر ... كشبليه ولا مهري رهان','384','3764','0');
INSERT INTO [t754] VALUES (5067,'أشد تنازعا لكريم أصل ... وأشبه منظرا بأب هجان','384','3764','0');
INSERT INTO [t754] VALUES (5068,'وأكثر في مجالسه استماعا ... فلان دق رمحا في فلان','384','3764','0');
INSERT INTO [t754] VALUES (5069,'وأول رأية رأيا المعالي ... فقد علقا بها قبل الأوان','384','3764','0');
INSERT INTO [t754] VALUES (5070,'وأول لفظة سمعا وقالا ... إغاثة صارخ أو فك عاني','384','3764','0');
INSERT INTO [t754] VALUES (5071,'وكنت الشمس تبهر كل عين ... فكيف وقد بدت معها اثنتان','384','3764','0');
INSERT INTO [t754] VALUES (5072,'فعاشا عيشة القمرين يحيى ... بضوئهما ولا يتحاسدان','384','3764','0');
INSERT INTO [t754] VALUES (5073,'ولا ملكا سوى ملك الأعادي ... ولا ورثا سوى من يقتلان','384','3764','0');
INSERT INTO [t754] VALUES (5074,'وكان ابنا عدو كاثراه ... له يا أي حروف أنيسيان','384','3764','0');
INSERT INTO [t754] VALUES (5075,'دعاء كالثناء بلا رياء ... يؤديه الجنان إلى الجنان','384','3764','0');
INSERT INTO [t754] VALUES (5076,'فقد أصبحت منه في فرند ... وأصبح منك في عضب يماني','384','3764','0');
INSERT INTO [t754] VALUES (5077,'ولولا كونكم في الناس كانوا ... هراء كالكلام بلا معاني','384','3764','0');
INSERT INTO [t754] VALUES (5078,'قد صدق الورد في الذي زعما ... أنك صيرت نثره ديما','385','3764','0');
INSERT INTO [t754] VALUES (5079,'كأنما مازج الهواء به ... بحر حوى مثل مائه عنما','385','3764','0');
INSERT INTO [t754] VALUES (5080,'ناثره ناثر السيوف دما ... وكل قول يقوله حكما','385','3764','0');
INSERT INTO [t754] VALUES (5081,'والخيل قد فصل الضياع بها ... والنعم السابغات النقما','385','3764','0');
INSERT INTO [t754] VALUES (5082,'فليرنا الورد إن شكا يده ... أحسن منه من جودها سلما','385','3764','0');
INSERT INTO [t754] VALUES (5083,'وقل له لست خير ما نثرت ... وإنما عوذت بك الكرما','385','3764','0');
INSERT INTO [t754] VALUES (5084,'خوفا من العين أن تصاب بها ... أصاب عينا بها تصاب عمى','385','3764','0');
INSERT INTO [t754] VALUES (5085,'أثلث فإنا أيها الطلل ... نبكي وترزم تحتنا الإبل','385','3764','0');
INSERT INTO [t754] VALUES (5086,'أولا فلا عتب على طلل ... إن الطلول لمثلها فعل','385','3764','0');
INSERT INTO [t754] VALUES (5087,'لو كنت تنطق قلت معتذرا ... بي غير ما بك أيها الرجل','385','3764','0');
INSERT INTO [t754] VALUES (5088,'أبكاك أنك بعض من شغفوا ... لم أبك أني بعض من قتلوا','385','3764','0');
INSERT INTO [t754] VALUES (5089,'إن الذين أقمت وارتحلوا ... أيامهم لديارهم دول','385','3764','0');
INSERT INTO [t754] VALUES (5090,'الحسن يرحل كلما رحلوا ... معهم وينزل حيثما نزلوا','385','3764','0');
INSERT INTO [t754] VALUES (5091,'في مقلتي رشأ تديرهما ... بدوية فتنت بها الحلل','385','3764','0');
INSERT INTO [t754] VALUES (5092,'تشكو المطاعم طول هجرتها ... وصدودها ومن الذي تصل','385','3764','0');
INSERT INTO [t754] VALUES (5093,'ما أسأرت في القعب من لبن ... تركته وهو المسك والعسل','386','3764','0');
INSERT INTO [t754] VALUES (5094,'قالت ألا تصحو فقلت لها ... أعلمتني أن الهوى ثمل','386','3764','0');
INSERT INTO [t754] VALUES (5095,'لو أن فناخسر صبحكم ... وبرزت وحدك عاقه الغزل','386','3764','0');
INSERT INTO [t754] VALUES (5096,'وتفرقت عنكم كتائبه ... إن الملاح خوادع قتل','386','3764','0');
INSERT INTO [t754] VALUES (5097,'ما كنت فاعلة وضيفكم ... ملك الملوك وشأنك البخل','386','3764','0');
INSERT INTO [t754] VALUES (5098,'أتمنعين قرى فتفتضحي ... أم تبذلين له الذي يسل','386','3764','0');
INSERT INTO [t754] VALUES (5099,'بل لا يحل بحيث حل به ... بخل ولا خوف ولا وجل','386','3764','0');
INSERT INTO [t754] VALUES (5100,'ملك إذا ما الرمح أدركه ... طنب ذكرناه فيعتدل','386','3764','0');
INSERT INTO [t754] VALUES (5101,'إن لم يكن من قبله عجزوا ... عما يسوس به فقد غفلوا','386','3764','0');
INSERT INTO [t754] VALUES (5102,'حتى أتى الدنيا ابن بجدتها ... فشكا إليه السهل والجبل','386','3764','0');
INSERT INTO [t754] VALUES (5103,'شكوى العليل إلى الكفيل له ... ألا تمر بجسمه العلل','386','3764','0');
INSERT INTO [t754] VALUES (5104,'قالت فلا كذبت شجاعته ... أقدم فنفسك ما لها أجل','386','3764','0');
INSERT INTO [t754] VALUES (5105,'فهو النهاية إن جرى مثل ... أو قيل يوم وغى من البطل','386','3764','0');
INSERT INTO [t754] VALUES (5106,'عدد الوفود العامدين له ... دون السلاح الشكل والعقل','386','3764','0');
INSERT INTO [t754] VALUES (5107,'فلشكلهم في خيله عمل ... ولعقلهم في بخته شغل','386','3764','0');
INSERT INTO [t754] VALUES (5108,'تمسي على أيدي مواهبه ... هي أو بقيتها أو البدل','386','3764','0');
INSERT INTO [t754] VALUES (5109,'يشتاق من يده إلى سبل ... شوقا إليه ينبت الأسل','387','3764','0');
INSERT INTO [t754] VALUES (5110,'سبل تطول المكرمات به ... والمجد لا الحوذان والنفل','387','3764','0');
INSERT INTO [t754] VALUES (5111,'وإلى حصى أرض أقام بها ... بالناس من تقبيله يلل','387','3764','0');
INSERT INTO [t754] VALUES (5112,'إن لم تخالطه ضواحكهم ... فلمن تصان وتذخر القبل','387','3764','0');
INSERT INTO [t754] VALUES (5113,'في وجهه من نور خالقه ... قدر هي الآيات والرسل','387','3764','0');
INSERT INTO [t754] VALUES (5114,'وإذا الخميس أبى السجود له ... سجدت له فيه القنا الذبل','387','3764','0');
INSERT INTO [t754] VALUES (5115,'وإذا القلوب أبت حكومته ... رضيت بحكم سيوفه القلل','387','3764','0');
INSERT INTO [t754] VALUES (5116,'أرضيت وهسوذان ما حكمت ... أم تستزيد لأمك الهبل','387','3764','0');
INSERT INTO [t754] VALUES (5117,'وردت بلادك غير مغمدة ... وكأنها بين القنا شعل','387','3764','0');
INSERT INTO [t754] VALUES (5118,'والقوم في أعيانهم خزر ... والخيل في أعيانها قبل','387','3764','0');
INSERT INTO [t754] VALUES (5119,'فأتوك ليس بمن أتوا قبل ... بهم وليس بمن نأوا خلل','387','3764','0');
INSERT INTO [t754] VALUES (5120,'لم يدر من بالري أنهم ... فصلوا ولا يدري إذا قفلوا','387','3764','0');
INSERT INTO [t754] VALUES (5121,'فأتيت معتزما ولا أسد ... ومضيت منهزما ولا وعل','387','3764','0');
INSERT INTO [t754] VALUES (5122,'تعطى سلاحهم وراحهم ... ما لم يكن لتناله المقل','387','3764','0');
INSERT INTO [t754] VALUES (5123,'أسخى الملوك بنقل مملكة ... من كاد عنه الرأس ينتقل','387','3764','0');
INSERT INTO [t754] VALUES (5124,'لولا الجهالة ما دفلت إلى ... قوم غرقت وإنما تفلوا','387','3764','0');
INSERT INTO [t754] VALUES (5125,'لا أقبلوا سرا ولاظفروا ... غدرا ولا نصرتهم الغيل','388','3764','0');
INSERT INTO [t754] VALUES (5126,'لا تلق أفرس منك تعرفه ... إلا إذا ما ضاقت الحيل','388','3764','0');
INSERT INTO [t754] VALUES (5127,'لا يستحي أحد يقال له ... نضلوك آل بويه أو فضلوا','388','3764','0');
INSERT INTO [t754] VALUES (5128,'قدروا عفوا وعدوا وفوا سئلوا ... أغنوا علوا أعلوا ولوا عدلوا','388','3764','0');
INSERT INTO [t754] VALUES (5129,'فوق السماء وفوق ما طلبوا ... فمتى أرادوا غاية نزلوا','388','3764','0');
INSERT INTO [t754] VALUES (5130,'قطعت مكارمهم صوارمهم ... فإذا تعذر كاذب قبلوا','388','3764','0');
INSERT INTO [t754] VALUES (5131,'لايشهرون على مخالفهم ... سيفا يقوم مقامه العذل','388','3764','0');
INSERT INTO [t754] VALUES (5132,'فأبو علي من به قهروا ... وأبو شجاع من به كملوا','388','3764','0');
INSERT INTO [t754] VALUES (5133,'حلفت لذا بركات غرة ذا ... في المهد أن لا فاتهم أمل','388','3764','0');
INSERT INTO [t754] VALUES (5134,'آخر ما الملك معزى به ... هذا الذي أثر في قلبه','388','3764','0');
INSERT INTO [t754] VALUES (5135,'لا جزعا بل أنفا شابه ... أن يقدر الدهر على غصبه','388','3764','0');
INSERT INTO [t754] VALUES (5136,'لو درت الدنيا بما عنده ... لاستحيت الأيام من عتبه','388','3764','0');
INSERT INTO [t754] VALUES (5137,'لعلها تحسب أن الذي ... ليس لديه ليس من حزنه','388','3764','0');
INSERT INTO [t754] VALUES (5138,'وأن من بغداد دار له ... ليس مقيما في ذرى عضبه','388','3764','0');
INSERT INTO [t754] VALUES (5139,'وأن جد المرء أوطانه ... من ليس منها ليس من صلبه','388','3764','0');
INSERT INTO [t754] VALUES (5140,'أخاف أن تفطن أعداؤه ... فيجفلوا خوفا إلى قربه','389','3764','0');
INSERT INTO [t754] VALUES (5141,'لا بد للإنسان من ضجعة ... لا تقلب المضجع عن جنبه','389','3764','0');
INSERT INTO [t754] VALUES (5142,'ينسى بها ما كان من عجبه ... وما أذاق الموت من كربه','389','3764','0');
INSERT INTO [t754] VALUES (5143,'نحن بنو الموتى فما بالنا ... نعاف ما لا بد من شربه','389','3764','0');
INSERT INTO [t754] VALUES (5144,'تبخل أيدينا بأرواحنا ... على زمان هن من كسبه','389','3764','0');
INSERT INTO [t754] VALUES (5145,'فهذه الأرواح من جوه ... وهذه الأجساد من تربه','389','3764','0');
INSERT INTO [t754] VALUES (5146,'لو فكر العاشق في منتهى ... حسن الذي يسبيه لم يسبه','389','3764','0');
INSERT INTO [t754] VALUES (5147,'لم ير قرن الشمس في شرقه ... فكشت الأنفس في غربه','389','3764','0');
INSERT INTO [t754] VALUES (5148,'يموت راعي الضأن في جهله ... موتة جالينوس في طبه','389','3764','0');
INSERT INTO [t754] VALUES (5149,'وربما زاد على عمره ... وزاد في الأمن على سربه','389','3764','0');
INSERT INTO [t754] VALUES (5150,'وغاية المفرط في سلمه ... كغاية المفرط في حربه','389','3764','0');
INSERT INTO [t754] VALUES (5151,'فلا قضى حاجته طالب ... فؤاده يخفق من رعبه','389','3764','0');
INSERT INTO [t754] VALUES (5152,'أستغفر الله لشخص مضى ... كان نداه منتهى ذنبه','389','3764','0');
INSERT INTO [t754] VALUES (5153,'وكان من جدد إحسانه ... كأنه أسرف في سبه','389','3764','0');
INSERT INTO [t754] VALUES (5154,'يريد من حب العلى عيشه ... ولا يريد العيش من حبه','389','3764','0');
INSERT INTO [t754] VALUES (5155,'يحسبه دافنه وحده ... ومجده في القبر من صحبه','389','3764','0');
INSERT INTO [t754] VALUES (5156,'ويظهر التذكير في ذكره ... ويستر التأنيث في حجبه','390','3764','0');
INSERT INTO [t754] VALUES (5157,'أخت أبي خير أمير دعا ... فقال جيش للقنا لبه','390','3764','0');
INSERT INTO [t754] VALUES (5158,'يا عضد الدولة من ركنها ... أبوه والقلب أبو لبه','390','3764','0');
INSERT INTO [t754] VALUES (5159,'ومن بنوه زين آبائه ... كأنها النور على قضبه','390','3764','0');
INSERT INTO [t754] VALUES (5160,'فخرا لدهر أنت من أهله ... ومنجب أصبحت من عقبه','390','3764','0');
INSERT INTO [t754] VALUES (5161,'إن الأسى القرن فلا تحيه ... وسيفك الصبر فلا تنبه','390','3764','0');
INSERT INTO [t754] VALUES (5162,'ما كان عندي أن بدر الدجى ... يوحشه المفقود من شهبه','390','3764','0');
INSERT INTO [t754] VALUES (5163,'حاشاك أن تضعف عن حمل ما ... تحمل السائر في كتبه','390','3764','0');
INSERT INTO [t754] VALUES (5164,'وقد حملت الثقل من قبله ... فأغنت الشدة عن سحبه','390','3764','0');
INSERT INTO [t754] VALUES (5165,'يدخل صبر المرء في مدحه ... ويدخل الإشفاق في ثلبه','390','3764','0');
INSERT INTO [t754] VALUES (5166,'مثلك يثني الحزن عن صوبه ... ويسترد الدمع عن غربه','390','3764','0');
INSERT INTO [t754] VALUES (5167,'أيما لإبقاء على فضله ... أيما لتسليم إلى ربه','390','3764','0');
INSERT INTO [t754] VALUES (5168,'ولم أقل مثلك أعنى به ... سواك يا فردا بلاد مشبه','390','3764','0');
INSERT INTO [t754] VALUES (5169,'أزائر يا خيال أم عائد ... أم عند مولاك أنني راقد','390','3764','0');
INSERT INTO [t754] VALUES (5170,'ليس كما ظن غشية لحقت ... فجئتني في خلالها قاصد','390','3764','0');
INSERT INTO [t754] VALUES (5171,'عد وأعدها فحبذا تلف ... ألصق ثديي بثديها الناهد','391','3764','0');
INSERT INTO [t754] VALUES (5172,'وجدت فيه بما يشح به ... من الشتيت المؤشر البارد','391','3764','0');
INSERT INTO [t754] VALUES (5173,'إذا خيالاته أطفن بنا ... أضحكه أنني لها حامد','391','3764','0');
INSERT INTO [t754] VALUES (5174,'وقال إن كان قد قضى أربا ... منا فما بال شوقه زائد','391','3764','0');
INSERT INTO [t754] VALUES (5175,'لا أجحد الفضل ربما فعلت ... ما لم يكن فاعلا ولا واعد','391','3764','0');
INSERT INTO [t754] VALUES (5176,'ما تعرف العين فرق بينهما ... كل خيال وصاله نافد','391','3764','0');
INSERT INTO [t754] VALUES (5177,'يا طفلة الكف عبلة الساعد ... على البعير المقلد الواخد','391','3764','0');
INSERT INTO [t754] VALUES (5178,'زيدي أذى مهجتي أزدك هوى ... فأجهل الناس عاشق حاقد','391','3764','0');
INSERT INTO [t754] VALUES (5179,'حكيت يا ليل فرعها الوارد ... فاحك نواها لجفني الساهد','391','3764','0');
INSERT INTO [t754] VALUES (5180,'طال بكائي على تذكرها ... وطلت حتى كلاكما واحد','391','3764','0');
INSERT INTO [t754] VALUES (5181,'ما بال هذي النجوم حائرة ... كأنها العمي ما لها قائد','391','3764','0');
INSERT INTO [t754] VALUES (5182,'أو عصبة من ملوك ناحية ... أبو شجاع عليهم واجد','391','3764','0');
INSERT INTO [t754] VALUES (5183,'إن هربوا أدركوا وإن وقفوا ... خشوا ذهاب الطريف والتالد','391','3764','0');
INSERT INTO [t754] VALUES (5184,'فهم يرجون عفو مقتدر ... مبارك الوجه جائد ماجد','391','3764','0');
INSERT INTO [t754] VALUES (5185,'أبلج لو عاذت الحمام به ... ما خشيت راميا ولا صائد','391','3764','0');
INSERT INTO [t754] VALUES (5186,'أو رعت الوحش وهي تذكره ... ما راعها حابل ولا طارد','391','3764','0');
INSERT INTO [t754] VALUES (5187,'تهدي له كل ساعة خبرا ... عن جحفل تحت سيفه بائد','392','3764','0');
INSERT INTO [t754] VALUES (5188,'وموضعا في فتان ناجية ... يحمل في التاج هامة العاقد','392','3764','0');
INSERT INTO [t754] VALUES (5189,'يا عضدا ربه به العاضد ... وساريا يبعث القطا الهاجد','392','3764','0');
INSERT INTO [t754] VALUES (5190,'وممطر الموت والحيوة معا ... وأنت لا بارق ولا راعد','392','3764','0');
INSERT INTO [t754] VALUES (5191,'نلت وما نلت من مضرة وه ... سوذان ما نال رأيه الفاسد','392','3764','0');
INSERT INTO [t754] VALUES (5192,'يبدأ من كيده بغايته ... وإنما الحرب غاية الكائد','392','3764','0');
INSERT INTO [t754] VALUES (5193,'ماذا على من أتى محاربكم ... فذم ما اختار لو أتى وافد','392','3764','0');
INSERT INTO [t754] VALUES (5194,'بلا سلاح سوى رجائكم ... ففاز بالنصر وانثنى راشد','392','3764','0');
INSERT INTO [t754] VALUES (5195,'يقارع الدهر من يقارعكم ... على مكان المسود والسائد','392','3764','0');
INSERT INTO [t754] VALUES (5196,'وليت يومي فناء عسكره ... ولم تكن دانيا ولا شاهد','392','3764','0');
INSERT INTO [t754] VALUES (5197,'ولم يغب غائب خليفته ... جيش أبيه وجده الصاعد','392','3764','0');
INSERT INTO [t754] VALUES (5198,'وكل خطية مثقفة ... يهزها مارد على مارد','392','3764','0');
INSERT INTO [t754] VALUES (5199,'سوافك ما يدعن فاصلة ... بين طري الدماء والجاسد','392','3764','0');
INSERT INTO [t754] VALUES (5200,'إذا المنايا بدت فدعوتها ... أبدل نونا بداله الحائد','392','3764','0');
INSERT INTO [t754] VALUES (5201,'إذا درى الحصن من رماه بها ... خر لها في أساسه ساجد','392','3764','0');
INSERT INTO [t754] VALUES (5202,'ما كانت الطرم في عجاجتها ... إلا بعيرا أضله ناشد','392','3764','0');
INSERT INTO [t754] VALUES (5203,'تسأل أهل القلاع عن ملك ... قد مستخه نعامة شارد','392','3764','0');
INSERT INTO [t754] VALUES (5204,'تستوجش الأرض أن تقر به ... فكلها منكر له جاحد','393','3764','0');
INSERT INTO [t754] VALUES (5205,'فلا مشاد ولا مشيد حمى ... ولا مشيد أغنى ولا شائد','393','3764','0');
INSERT INTO [t754] VALUES (5206,'فاغتظ بقوم وهسوذ ما خلقوا ... إلا لغيظ العدو والحاسد','393','3764','0');
INSERT INTO [t754] VALUES (5207,'رأوك لما بلوك نابتة ... يأكلها قبل أهله الرائد','393','3764','0');
INSERT INTO [t754] VALUES (5208,'وخل زيا لمن يحققه ... ما كل دام جبينه عابد','393','3764','0');
INSERT INTO [t754] VALUES (5209,'إن كان لم يعمد الأمير لما ... لقيت منه فيمنه عامد','393','3764','0');
INSERT INTO [t754] VALUES (5210,'يقلقه الصبح لا يرى معه ... بشرى بفتح كأنه فاقد','393','3764','0');
INSERT INTO [t754] VALUES (5211,'والأمر لله رب مجتهد ... ما خاب إلا لأنه جاهد','393','3764','0');
INSERT INTO [t754] VALUES (5212,'ومتق والسهام مرسلة ... يحيد عن حابض إلى صارد','393','3764','0');
INSERT INTO [t754] VALUES (5213,'فلا يبل قاتل أعاديه ... أقائما نال ذاك أم قاعد','393','3764','0');
INSERT INTO [t754] VALUES (5214,'ليت ثنائي الذي أصوغ فدى ... من صيغ فيه فإنه خالد','393','3764','0');
INSERT INTO [t754] VALUES (5215,'لويته دملجا على عضد ... لدولة ركنها له والد','393','3764','0');
INSERT INTO [t754] VALUES (5216,'ما أجدر الأيام والليال ... بأن تقول ما له وما لي','393','3764','0');
INSERT INTO [t754] VALUES (5217,'لا أن يكون هكذا مقالي ... فتى بنيران الحروب صالي','394','3764','0');
INSERT INTO [t754] VALUES (5218,'منها شرابي وبها اغتسالي ... لا تخطر الفحشاء لي يببالي','394','3764','0');
INSERT INTO [t754] VALUES (5219,'لو جذب الزراد من أذيالي ... مخيرا لي صنعتي سربال','394','3764','0');
INSERT INTO [t754] VALUES (5220,'ما سمته زردا سوى سروال ... وكيف لا وإنما إدلالي','394','3764','0');
INSERT INTO [t754] VALUES (5221,'بفارس المجروح والشمال ... أبي شجاع قاتل الأبطال','394','3764','0');
INSERT INTO [t754] VALUES (5222,'ساقي كؤوس الموت والجريال ... لما أصاب القفص أمس الخالي','394','3764','0');
INSERT INTO [t754] VALUES (5223,'وقتل الكرد عن القتال ... حتى اتقت بالفر والإجفال','394','3764','0');
INSERT INTO [t754] VALUES (5224,'فهالك وطائع وجال ... فاقتنص الفرسان بالعوالي','394','3764','0');
INSERT INTO [t754] VALUES (5225,'والعتق المحدثة الصقال ... سار لصيد الوحش في الجبال','394','3764','0');
INSERT INTO [t754] VALUES (5226,'وفي رقاق الأرض والرمال ... على دماء الإنس والأوصال','394','3764','0');
INSERT INTO [t754] VALUES (5227,'منفرد المهر عن الرعال ... من عظم الهمة لا الملال','394','3764','0');
INSERT INTO [t754] VALUES (5228,'وشدة الضن لا الاستبدال ... لم يتحركن سوى انسلال','394','3764','0');
INSERT INTO [t754] VALUES (5229,'فهن يضربن على التصهال ... كل عليل فوقها مختال','394','3764','0');
INSERT INTO [t754] VALUES (5230,'يمسك فاه خشية السعال ... من مطلع الشمس إلى الزوال','394','3764','0');
INSERT INTO [t754] VALUES (5231,'فلم يئل ما طار غير آل ... وما عدا فانغل في الأدغال','394','3764','0');
INSERT INTO [t754] VALUES (5232,'وما احتمى بالماء والدحال ... من الحرام اللحم والحلال','394','3764','0');
INSERT INTO [t754] VALUES (5233,'إن النفوس عدد الآجال ... سقيا لدشت الأرزن الطوال','394','3764','0');
INSERT INTO [t754] VALUES (5234,'بين المروج الفيح والأغيال ... مجاور الخنزير للريبال','395','3764','0');
INSERT INTO [t754] VALUES (5235,'داني الخنانيص من الأشبال ... مشترف الدب على الغزال','395','3764','0');
INSERT INTO [t754] VALUES (5236,'كأن فناخسر ذا الإفضال ... خاف عليها عوز الكمال فجاءها بالفيل والفيال','395','3764','0');
INSERT INTO [t754] VALUES (5237,'فقيدت الأيل في الحبال ... طوع وهوق الخيل والرجال','395','3764','0');
INSERT INTO [t754] VALUES (5238,'تسير سير النعم الأرسال ... معتمة بيبس الأجذال','395','3764','0');
INSERT INTO [t754] VALUES (5239,'ولدن تحت أثقل الأحمال ... قد منعتهن من التفالي','395','3764','0');
INSERT INTO [t754] VALUES (5240,'لا تشرك الأجسام في الهزال ... إذا تلفتن إلى الأظلال','395','3764','0');
INSERT INTO [t754] VALUES (5241,'أرينهن أشنع الأمثال ... كأنما خلقن للإذلال','395','3764','0');
INSERT INTO [t754] VALUES (5242,'زيادة في سبة الجهال ... والعضو ليس نافعا في حال لسائر الجسم من الخبال','395','3764','0');
INSERT INTO [t754] VALUES (5243,'وأوفت الفدر من الأوعال ... مرتديات بقسي الضال','395','3764','0');
INSERT INTO [t754] VALUES (5244,'نواخس الأطراف للأكفال ... يكدن ينفذن من الآطال','395','3764','0');
INSERT INTO [t754] VALUES (5245,'لها لحى سود بلا سبال ... يصلحن للإضحاك لا الإجلال','395','3764','0');
INSERT INTO [t754] VALUES (5246,'كل أثيث نبتها متفال ... لم تغذ بالمسك ولا الغوالي','395','3764','0');
INSERT INTO [t754] VALUES (5247,'ترضى من الأدهان بالأبوال ... ومن ذكي الطيب بالدمال','395','3764','0');
INSERT INTO [t754] VALUES (5248,'لو سرحت في عارضي محتال ... لعدها من شبكات المال','395','3764','0');
INSERT INTO [t754] VALUES (5249,'بين قضاة السوء والأطفال ... شبيهة الإدبار بالإقبال لا تؤثر الوجه على القذال','396','3764','0');
INSERT INTO [t754] VALUES (5250,'فاختلفت في وابلي نبال ... من أسفل الطود ومن معال','396','3764','0');
INSERT INTO [t754] VALUES (5251,'قد أودعتها عتل الرجال ... في كل كبد كبدي نصال','396','3764','0');
INSERT INTO [t754] VALUES (5252,'فهن يهوين من القلال ... مقلوبة الأظلاف الإرقال','396','3764','0');
INSERT INTO [t754] VALUES (5253,'يرقلن في الجو على المحال ... في طرق سريعة الإيصال','396','3764','0');
INSERT INTO [t754] VALUES (5254,'ينمن فيها نيمة المكسال ... على القفي أعجل العجال','396','3764','0');
INSERT INTO [t754] VALUES (5255,'لا يتشكين من الكلال ... ولا يحاذرن من الضلال','396','3764','0');
INSERT INTO [t754] VALUES (5256,'فكان عنها سبب الترحال ... تشويق إكثار إلى إقلال','396','3764','0');
INSERT INTO [t754] VALUES (5257,'فوحش نجد منه في بلبال ... يخفن في سلمى وفي قبال','396','3764','0');
INSERT INTO [t754] VALUES (5258,'نوافر الضباب والأروال ... والخاضبات الربد والريال','396','3764','0');
INSERT INTO [t754] VALUES (5259,'والظبي والخنساء الذيال ... يسمعن من أخباره الأزوال','396','3764','0');
INSERT INTO [t754] VALUES (5260,'فحولها والعوذ والمتالي ... تود لو يتحفها بوال','396','3764','0');
INSERT INTO [t754] VALUES (5261,'يركبها بالخطم والرحال ... يؤمنها من هذه الأهوال','396','3764','0');
INSERT INTO [t754] VALUES (5262,'ويخمس العشب ولا تبالي ... وماء كل مسبل هطال','396','3764','0');
INSERT INTO [t754] VALUES (5263,'يا أقدر السفار والقفال ... لو شئت صدت الأسد بالثعالي','396','3764','0');
INSERT INTO [t754] VALUES (5264,'أو شئت غرقت العدى بالآل ... ولو جعلت موضع الإلال','397','3764','0');
INSERT INTO [t754] VALUES (5265,'لم يبق إلا طرد السعالي ... في الظلم الغائبة الهلال','397','3764','0');
INSERT INTO [t754] VALUES (5266,'على ظهور الإبل الأبال ... فقد بلغت غاية الآمال','397','3764','0');
INSERT INTO [t754] VALUES (5267,'فلم تدع منها سوى المحال ... في لا مكان عند لا منال','397','3764','0');
INSERT INTO [t754] VALUES (5268,'يا عضد الدولة والمعالي ... النسب الحلي وأنت الحالي','397','3764','0');
INSERT INTO [t754] VALUES (5269,'بالأب لا الشنف ولا الخلخال ... حليا تجلى منك بالجمال','397','3764','0');
INSERT INTO [t754] VALUES (5270,'ورب قبح وحلى ثقال ... أحسن منها الحسن في المعطال','397','3764','0');
INSERT INTO [t754] VALUES (5271,'فخر الفتى بالنفس والأفعال ... من قبله بالعم والأخوال','397','3764','0');
INSERT INTO [t754] VALUES (5272,'فدى لك من يقصر عن مداكا ... فلا ملك إذا إلا فداكا','397','3764','0');
INSERT INTO [t754] VALUES (5273,'فلو قلنا فدى لك ن يساوي ... دعونا بالبقاء لمن قلاد','397','3764','0');
INSERT INTO [t754] VALUES (5274,'وآمنا فداءك كل نفس ... وإن كانت لمملكة ملاد','397','3764','0');
INSERT INTO [t754] VALUES (5275,'ومن يظن نثر الحب جودا ... وينصب تحت ما نثر الشباد','397','3764','0');
INSERT INTO [t754] VALUES (5276,'ومن بلغ التراب به كراه ... وقد بلغت به الحال السكاكا','397','3764','0');
INSERT INTO [t754] VALUES (5277,'فلو كانت قلوبهم صديقا ... لقد كانت خلائقهم عداكا','398','3764','0');
INSERT INTO [t754] VALUES (5278,'لأنك مبغض حسبا نحيفا ... إذا أبصرت دنياه ضناكا','398','3764','0');
INSERT INTO [t754] VALUES (5279,'أروح وقد ختمت على فؤادي ... بحبك أن يحل به سواكا','398','3764','0');
INSERT INTO [t754] VALUES (5280,'وقد حملتني شكرا طويلا ... ثقيلا لا أطيق به حراكا','398','3764','0');
INSERT INTO [t754] VALUES (5281,'أحاذر أن يشق على المطايا ... فلا تمشي بنا إلا سواكا','398','3764','0');
INSERT INTO [t754] VALUES (5282,'لعل الله يجعله رحيلا ... يعين على الإقامة في ذراكا','398','3764','0');
INSERT INTO [t754] VALUES (5283,'فلو أني استطعت خفضت طرفي ... فلم أبصر به حتى أراكا','398','3764','0');
INSERT INTO [t754] VALUES (5284,'وكيف الصبر عنك وقد كفاني ... نداك المستفيض وما كفاكا','398','3764','0');
INSERT INTO [t754] VALUES (5285,'أتتركني وعين الشمس نعلي ... فتقطع مشيتي فيها الشراكا','398','3764','0');
INSERT INTO [t754] VALUES (5286,'أرى أسفي وما سرنا شديدا ... فكيف إذا غدى السير ابتراكا','398','3764','0');
INSERT INTO [t754] VALUES (5287,'وهذا الشوق قبل البين سيف ... وها أنا ما ضربت وقد أحاكا','398','3764','0');
INSERT INTO [t754] VALUES (5288,'إذا التوديع أعرض قال قلبي ... عليك الصمت لا صاحبت فاكا','398','3764','0');
INSERT INTO [t754] VALUES (5289,'ولولا أن أكثر ما تمنى ... معاودة لقلت ولا مناكا','398','3764','0');
INSERT INTO [t754] VALUES (5290,'وقد استشفيت من داء بداء ... وأقتل ما أعلك ما شفاكا','398','3764','0');
INSERT INTO [t754] VALUES (5291,'فأستر منك نجوانا وأخفي ... هموما قد أطلت لها العراكا','398','3764','0');
INSERT INTO [t754] VALUES (5292,'إذا عاصيتها كانت شدادا ... وإن طاوعتها كانت ركاكا','399','3764','0');
INSERT INTO [t754] VALUES (5293,'وكم دون الثوية من حزين ... يقول له قدومي ذا بذاكا','399','3764','0');
INSERT INTO [t754] VALUES (5294,'ومن عذب الرضاب إذا أنخنا ... يقبل رحل تروك والوراكا','399','3764','0');
INSERT INTO [t754] VALUES (5295,'يحرم أن يمس الطيب بعدي ... وقد عبق العبير به وصاكا','399','3764','0');
INSERT INTO [t754] VALUES (5296,'ويمنع ثغره من كان صب ... ويمنحه البشامة والأراكا','399','3764','0');
INSERT INTO [t754] VALUES (5297,'يحدث مقلتيه النوم عني ... فليت النوم حدث عن نداكا','399','3764','0');
INSERT INTO [t754] VALUES (5298,'وأن البدن لا يعرقن إلا ... وقد أنضى العذافرة اللكاكا','399','3764','0');
INSERT INTO [t754] VALUES (5299,'وما أرضى لمقلته بحلم ... إذا انتبهت توهمه ابتشاكا','399','3764','0');
INSERT INTO [t754] VALUES (5300,'ولا إلا بأن يصغى وأحكى ... فليتك لا يتيمه هواكا','399','3764','0');
INSERT INTO [t754] VALUES (5301,'وكم طرب المسامع ليس يدري ... أيعجب من ثنائي أم علاكا','399','3764','0');
INSERT INTO [t754] VALUES (5302,'وذاك النشر عرضك كان مسكا ... وهذا الشعر فهري والمداكا','399','3764','0');
INSERT INTO [t754] VALUES (5303,'فلا تحمدهما وأحمد هماما ... إذا لم يسم حامده عناكا','399','3764','0');
INSERT INTO [t754] VALUES (5304,'أغر له شمائل من أبيه ... غدا يلقى بنوك بها أباكا','399','3764','0');
INSERT INTO [t754] VALUES (5305,'وفي الأحباب مختص بوجد ... وآخر يدعى معه اشتراكا','400','3764','0');
INSERT INTO [t754] VALUES (5306,'إذا اشتبهت دموع في خدود ... تبين من بكى ممن تباكا','400','3764','0');
INSERT INTO [t754] VALUES (5307,'أذمت مكرمات ابي شجاع ... لعيني من نواي على أولاكا','400','3764','0');
INSERT INTO [t754] VALUES (5308,'فزل يا بعد عن أيدي ركاب ... لها وقع الأسنة في حشاكا','400','3764','0');
INSERT INTO [t754] VALUES (5309,'وأيا شئت يا طرقي فكوني ... أذاة أو نجاة أو هلاكا','400','3764','0');
INSERT INTO [t754] VALUES (5310,'فلو سرنا وفي تشرين خمس ... رأوني قبل أن يروا السماكا','400','3764','0');
INSERT INTO [t754] VALUES (5311,'يشرد يمن فناخسر عني ... قنا الأعداء والطعن الدراكا','400','3764','0');
INSERT INTO [t754] VALUES (5312,'وألبس من رضاه في طريقي ... سلاحا يذعر الأبطال شاكا','400','3764','0');
INSERT INTO [t754] VALUES (5313,'ومن أعتاض منك إذا افترقنا ... وكل الناس زور ما خلاكا','400','3764','0');
INSERT INTO [t754] VALUES (5314,'وما أنا غير سهم في هواء ... يعود ولم يجد فيه امتساكا','400','3764','0');
INSERT INTO [t754] VALUES (5315,'حين من إلهى أن يراني ... وقد فارقت دارك واصطفاها','400','3764','0');
INSERT INTO [t754] VALUES (5316,'والله سبحانه وتعالى أعلم بالصواب وإليه المرجع والمآب','401','0','0');
