INSERT INTO [t7767] VALUES (4,'إن صدق دخل الجنة','6','0','0');
INSERT INTO [t7767] VALUES (5,'الإسلام علانية، والإيمان في القلب ثم يشير بيده إلى صدره: التقوى هاهنا، التقوى','7','0','0');
INSERT INTO [t7767] VALUES (6,'لا إيمان لمن لا أمانة له','8','0','0');
INSERT INTO [t7767] VALUES (7,'الإيمان يبدأ لمظة بيضاء في القلب، كلما ازداد الإيمان ازدادت بياضا حتى يبيض القلب كله، وإن','9','0','0');
INSERT INTO [t7767] VALUES (8,'إن الرجل ليذنب الذنب فينكت في قلبه نكتة سوداء، ثم يذنب الذنب فتنكت أخرى حتى يصير لون قلبه','10','0','0');
INSERT INTO [t7767] VALUES (9,'ما نقصت أمانة عبد قط إلا نقص إيمانه','11','0','0');
