INSERT INTO [t7767] VALUES (12,'لا يغرنكم صلاة امرئ ولا صيامه، من شاء صام، ومن شاء صلى، لا دين لمن لا أمانة','14','0','0');
INSERT INTO [t7767] VALUES (13,'الإيمان يزيد وينقص، فقيل: فما زيادته؟ وما نقصانه؟ قال: إذا ذكرنا ربنا وخشيناه فذلك','15','0','0');
