INSERT INTO [t7767] VALUES (20,'إن الحياء والإيمان قرنا جميعا، فإذا رفع أحدهما رفع الآخر','22','0','0');
INSERT INTO [t7767] VALUES (21,'إني مؤمن، قال: قل: إني في الجنة ولكنا نؤمن بالله وملائكته وكتبه ورسله','23','0','0');
