INSERT INTO [t7767] VALUES (24,'أنتم المؤمنون إن شاء الله','26','0','0');
INSERT INTO [t7767] VALUES (25,'إذا سئل أحدكم أمؤمن أنت؟ فلا يشكن','27','0','0');
INSERT INTO [t7767] VALUES (26,'إذا سئل أحدكم أمؤمن أنت؟ فلا يشك في إيمانه','28','0','0');
INSERT INTO [t7767] VALUES (27,'أنا مؤمن','29','0','0');
