INSERT INTO [t7767] VALUES (34,'يخرج من النار من قال: لا إله إلا الله، وكان في قلبه من الخير ما يزن شعيرة ثم قال: يخرج من','36','0','0');
INSERT INTO [t7767] VALUES (35,'يا رسول الله أعطيتهم وتركت فلانا والله إني لأراه مؤمنا، فقال رسول الله صلى الله عليه','37','0','0');
INSERT INTO [t7767] VALUES (36,'سل تعطه يعني النبي صلى الله عليه وسلم واشفع تشفع، وادع تجب، قال: فيرفع رأسه فيقول: رب','38','0','0');
INSERT INTO [t7767] VALUES (37,'لا يزني الزاني وهو مؤمن، ولا يشرب الخمر حين يشرب وهو مؤمن، ولا ينتهب نهبة يرفع الناس فيها','39','0','0');
INSERT INTO [t7767] VALUES (38,'لا يزني الزاني حين يزني وهو مؤمن، ولا يسرق حين يسرق وهو مؤمن، ولا يشرب يعني الخمر حين','40','0','0');
INSERT INTO [t7767] VALUES (39,'لا يزني الزاني حين يزني وهو مؤمن، ولا يسرق السارق حين يسرق وهو مؤمن، ولا يشرب الخمر حين','41','0','0');
