INSERT INTO [t7767] VALUES (52,'يا مقلب القلوب ثبت قلبي على دينك قالوا: يا رسول الله، آمنا بك وبما جئت به، فهل تخاف','54','0','0');
INSERT INTO [t7767] VALUES (53,'يا مقلب القلوب ثبت قلبي على دينك. قلت: يا رسول الله، ما أكثر دعاءك يا مقلب القلوب ثبت','55','0','0');
INSERT INTO [t7767] VALUES (54,'يا مقلب القلوب ثبت قلبي على دينك قلت: يا رسول الله، إنك لتدعو بهذا الدعاء؟ قال: يا عائشة،','56','0','0');
INSERT INTO [t7767] VALUES (55,'كان يدعو بهذا الدعاء: يا مقلب القلوب ثبت قلبي على دينك','57','0','0');
INSERT INTO [t7767] VALUES (56,'ما رأيت من ناقص الدين والرأي أغلب للرجال ذوي الأمر على أمرهم من النساء. قالوا: يا أبا عبد','58','0','0');
INSERT INTO [t7767] VALUES (57,'الجواب فيه بدعة، وما يسرني أني شككت','59','0','0');
INSERT INTO [t7767] VALUES (58,'لا يزني الزاني حين يزني وهو مؤمن، ولا يسرق وهو مؤمن، ولا يشرب الخمر وهو','60','0','0');
INSERT INTO [t7767] VALUES (59,'والله إن الرجل ليصبح بصيرا، ثم يمسي ما ينظر بشفر','61','0','0');
INSERT INTO [t7767] VALUES (60,'أنت الذي تزعم أنك مؤمن؟ فقال: هل كان الناس على عهد النبي صلى الله عليه وسلم إلا على ثلاثة','62','0','0');
