INSERT INTO [t7767] VALUES (66,'لو قطعت أعضاء ما بلغت الإيمان. أو كما قال','68','0','0');
INSERT INTO [t7767] VALUES (67,'تشهد أنه مؤمن مستكمل الإيمان بريء من النفاق؟ لم أشهد، ولو شهدت لشهدت أنه في الجنة، ولو','69','0','0');
INSERT INTO [t7767] VALUES (68,'ألا أزوجك؟ ما من عبد يزني إلا نزع الله منه نور الإيمان','70','0','0');
