INSERT INTO [t7767] VALUES (129,'إنهم لا أيمان لهم} [التوبة: ١٢] فقال: لا عهد لهم','131','0','0');
INSERT INTO [t7767] VALUES (130,'لا يدخل النار إنسان في قلبه مثقال حبة من خردل من إيمان','132','0','0');
INSERT INTO [t7767] VALUES (131,'أوثق عرى الإيمان: الحب في الله، والبغض في الله','133','0','0');
INSERT INTO [t7767] VALUES (132,'الإيمان فرائض، وشرائع، وحدود، وسنن، فمن استكملها استكمل الإيمان، ومن لم يستكملها لم','134','0','0');
INSERT INTO [t7767] VALUES (133,'لابد لأهل هذا الدين من أربع: دخول في دعوة الإسلام ولابد من الإيمان، وتصديق بالله','135','0','0');
INSERT INTO [t7767] VALUES (134,'ما كانوا يقولون لعمل تركه رجل كفر غير الصلاة، فقد كانوا يقولون: تركها','136','0','0');
INSERT INTO [t7767] VALUES (135,'من شهد أنه مؤمن فليشهد أنه في الجنة؟ قال: نعم','137','0','0');
INSERT INTO [t7767] VALUES (136,'ناسا يزعمون أن المؤمنين لا يدخلون النار قال: لعمرك والله إن حشوها غير المؤمنين قال أبو','138','0','0');
